!----------------------------------------------------------------------
      subroutine advwnd(u,v,zface,nzp1,zbot,ztop,uave,vave)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 940430            ADVWND
!                J. Scire, SRC
!
! --- PURPOSE:  Compute the vertically-averaged winds (U, V) through
!               the layer ZBOT through ZTOP
!
! --- INPUTS:
!         U(mxnz) - real array - U-component of wind (m/s) for each
!                                layer
!         V(mxnz) - real array - V-component of wind (m/s) for each
!                                layer
!   ZFACE(mxnzp1) - real array - Cell face heights (m) for each layer
!            NZP1 - integer    - Number of cell face heights (NZ + 1)
!            ZBOT - real       - Height (m) of BOTTOM of averaging layer
!            ZTOP - real       - Height (m) of TOP of averaging layer
!
!     Parameters:
!           MXNZ, MXNZP1, IO6
!
! --- OUTPUT:
!            UAVE - real       - Vertically-averaged U-component of the
!                                wind (m/s)
!            VAVE - real       - Vertically-averaged V-component of the
!                                wind (m/s)
!
! --- ADVWND called by:  ADVECT
! --- ADVWND calls:      ZFIND
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
      real u(mxnz),v(mxnz)
      real zface(mxnzp1)
!
! --- Set the limits of the averaging depth to ensure overlap
! --- with the meteorological fields
      zabot=zbot
      if(zabot.lt.zface(1))zabot=zface(1)
      if(zabot.gt.zface(nzp1))zabot=zface(nzp1)
!
      zatop=ztop
      if(zatop.lt.zface(1))zatop=zface(1)
      if(zatop.gt.zface(nzp1))zatop=zface(nzp1)
!
! --- Find the met. layer containing the height "ZABOT" (adjusted ZBOT)
      call zfind(zabot,zface,nzp1,IBOT)
!
! --- Find the met. layer containing the height "ZATOP" (adjusted ZTOP)
      call zfind(zatop,zface,nzp1,ITOP)
!
! ------------------------------------
! --- Compute the layer-averaged winds
! ------------------------------------
!
      if(IBOT.eq.ITOP)then
!
! ---    Averaging layer is completely within one met. layer
         uave=u(ibot)
         vave=v(ibot)
      else
!
! ---    Averaging layer extends into two or more met. layers
! ---    Sum through each met. layer
!
! ---    Lowest met. layer containing ZABOT -- ZFACE(ibot+1) is the
! ---    height of the TOP of layer IBOT
         delz=zface(ibot+1)-zabot
         sumu=delz*u(ibot)
         sumv=delz*v(ibot)
         sumz=delz
!
! ---    Highest met. layer containing ZATOP -- ZFACE(itop) is the
! ---    height of the BOTTOM of layer ITOP
         delz=zatop-zface(itop)
         sumu=sumu+delz*u(itop)
         sumv=sumv+delz*v(itop)
         sumz=sumz+delz
!
! ---    Sum through met. layers between IBOT and ITOP
         ibotp1=ibot+1
         itopm1=itop-1
         if(itopm1.lt.ibotp1)go to 102
         do 100 i=ibotp1,itopm1
            delz=zface(i+1)-zface(i)
            sumu=sumu+delz*u(i)
            sumv=sumv+delz*v(i)
            sumz=sumz+delz
100      continue
102      continue
!
! ---    Compute the layer-averaged winds
         UAVE=sumu/sumz
         VAVE=sumv/sumz
      endif
!
      return
      end
