      subroutine advect(ldbg,ix,iy,mdom,z0m,el,dpbl,istab,ht,zbot,ztop,&
                        uadv,vadv)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            ADVECT
! ---            D. Strimaitis
!
! --- PURPOSE:  Obtain puff advection wind
!
! --- UPDATE
! --- V6.265-V6.3   100212  (DGS): add nested CALMET grids
! --- V6.263-V6.265 090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
! --- V5.74-V6.23   080204  (DGS): add screen for ZTOP<ZBOT
! --- V5.4-V5.74    040715  (DGS): add AERMET option (METFM=5)
! --- V4.0a-V5.4    000602  (DGS): add message to "stop"
! --- V4.0-V4.0a    971107  (DGS): initialize ibot & itop (METFM=4)
!                   971107  (DGS): modify search for profile levels
!                                  within puff
!                   971107  (DGS): check PROBLEM returned by XTPRF
!
! --- INPUTS:
!            LDBG - logical      - Write debug output if .TRUE.
!           IX,IY - integer      - Cell index of puff in MET grid
!            MDOM - integer      - Met grid domain index
!             Z0M - real         - Surface roughness (m)
!              EL - real         - Monin-Obukhov length (m)
!            DPBL - real         - Depth of boundary layer (m)
!           ISTAB - integer      - PG stability class
!              HT - real         - Initial puff height, at release (m)
!            ZBOT - real         - Bottom of puff (m)
!            ZTOP - real         - Top of puff (m)
!
!    Common block /COMPARM/ variables:
!         WSCALM, PLX0
!     Common Block /GEN/ variables:
!         METFM
!     Common Block /GRID/ variables:
!         NZ, NZP1, ZGPT(mxnz), ZFACE(mxnzp1)
!    Common block /METHR/ variables:
!         UMET(mxnx,mxny,mxnz,mxmetdom), VMET(mxnx,mxny,mxnz,mxmetdom)
!         PTG(2), PLEXP, SSPRF(mxprfz), WDPRF(mxprfz), ZPRF(mxprfz)
!         NZPRF
!    Parameters:
!         MXNX, MXNY, MXNZ, MXMETDOM, MXNZP1, MXPRFZ, IO6
!
! --- OUTPUT:
!            UADV - real         - Vertically-averaged U-component
!                                  of the wind (m/s)
!            VADV - real         - Vertically-averaged V-component
!                                  of the wind (m/s)
!
! --- ADVECT called by:  COMP, RISEWIND
! --- ADVECT calls:      ADVWND, POWLAW, XTPRF
!----------------------------------------------------------------------
      include 'params.puf'
      include 'comparm.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'methr.puf'
      real u(mxnz),v(mxnz)
      logical ldbg,problem
      data dtor/0.0174533/
      if(ldbg) then
         write(io6,*)
         write(io6,*)'ADVECT:      metfm =  ',metfm
         write(io6,*)'cell i,j domain ',ix,iy,mdom
         write(io6,*)'zbot,ztop,ht(release) ',zbot,ztop,ht
      endif
! --- Stop if ztop<zbot
      if(ztop.LT.zbot) then
         write(io6,*)
         write(io6,*)'ADVECT: Bottom EXCEEDS Top!'
         write(io6,*)'zbot,ztop,ht(release) ',zbot,ztop,ht
         stop 'Halted in ADVECT'
      endif
! --- Trap missing power-law exponent (substitute default)
      if(plexp.LT.-900.) then
! ---    Use default
         plx=plx0(istab)
      else
! ---    Use it
         plx=plexp
      endif
      if(metfm.LE.3) then
! ---    3-D gridded MET field:  transfer U,V to 1-D arrays
         do iz=1,nz
            u(iz)=umet(ix,iy,iz,mdom)
            v(iz)=vmet(ix,iy,iz,mdom)
         enddo
         if(metfm.EQ.2 .OR. metfm.EQ.3) then
! ---       ISC/PLUME treatment (1-layer)
! ---       Profile non-calm wind speed in layer to RELEASE height
            wsold=sqrt(u(1)**2+v(1)**2)
            if(wsold.LT.wscalm) then
               uadv=u(1)
               vadv=v(1)
            else
               call POWLAW(ht,zgpt(1),wsold,plx,ws)
               ratio=ws/wsold
               uadv=u(1)*ratio
               vadv=v(1)*ratio
            endif
            if(ldbg) then
               write(io6,*)'uadv,vadv= ',uadv,vadv
            endif
         else
! ---       Full 3-D gridded layer-averaged MET treatment
            call ADVWND(u,v,zface,nzp1,zbot,ztop,uadv,vadv)
            if(ldbg) then
               write(io6,*)'uadv,vadv= ',uadv,vadv
            endif
         endif
      elseif(metfm.EQ.4 .OR. metfm.EQ.5) then
! ---    PROFILE.DAT met data
! ---    Use scalar speed as in CTDM
         problem=.FALSE.
         if(zbot.EQ.ztop) then
! ---       Get wind at ZBOT
            call XTPRF(nzprf,ssprf,zprf,zbot,'spd',z0m,el,&
                       dpbl,istab,ptg,ws,problem)
            call XTPRF(nzprf,wdprf,zprf,zbot,'dir',z0m,el,&
                       dpbl,istab,ptg,wd,problem)
            wdrad=wd*dtor
            uadv=-ws*SIN(wdrad)
            vadv=-ws*COS(wdrad)
            if(ldbg) then
               write(io6,*)'ws,wd    = ',ws,wd
            endif
         else
! ---       Get wind at top and bottom of puff
            call XTPRF(nzprf,ssprf,zprf,ztop,'spd',z0m,el,&
                       dpbl,istab,ptg,wstop,problem)
            call XTPRF(nzprf,wdprf,zprf,ztop,'dir',z0m,el,&
                       dpbl,istab,ptg,wdtop,problem)
            toprad=wdtop*dtor
            utop=-wstop*SIN(toprad)
            vtop=-wstop*COS(toprad)
            call XTPRF(nzprf,ssprf,zprf,zbot,'spd',z0m,el,&
                       dpbl,istab,ptg,wsbot,problem)
            call XTPRF(nzprf,wdprf,zprf,zbot,'dir',z0m,el,&
                       dpbl,istab,ptg,wdbot,problem)
            botrad=wdbot*dtor
            ubot=-wsbot*SIN(botrad)
            vbot=-wsbot*COS(botrad)
            if(ldbg) then
               write(io6,*)'wstop,wsbot = ',wstop,wsbot
               write(io6,*)'wdtop,wdbot = ',wdtop,wdbot
            endif
! ---       Find range of indices of profile array within puff
            ibot=0
            itop=0
            do iz=1,nzprf
               if(zprf(iz).LT.zbot) ibot=iz+1
               if(zprf(iz).LT.ztop) itop=iz
            enddo
            if(ibot.GT.itop .OR. ibot.EQ.0) then
! ---          No profile levels are within puff
               uadv=0.5*(utop+ubot)
               vadv=0.5*(vtop+vbot)
            else
               denomi=0.5/(ztop-zbot)
! ---          Bottom edge
               usum=ubot*(zprf(ibot)-zbot)
               vsum=vbot*(zprf(ibot)-zbot)
! ---          Top edge
               usum=usum+utop*(ztop-zprf(itop))
               vsum=vsum+vtop*(ztop-zprf(itop))
! ---          Middle layer(s)
               do iz=ibot,itop
                  call XTPRF(nzprf,ssprf,zprf,zprf(iz),'spd',z0m,el,&
                             dpbl,istab,ptg,wsiz,problem)
                  call XTPRF(nzprf,wdprf,zprf,zprf(iz),'dir',z0m,el,&
                             dpbl,istab,ptg,wdiz,problem)
                  drad=wdiz*dtor
                  ui=-wsiz*SIN(drad)
                  vi=-wsiz*COS(drad)
                  if(iz.EQ.ibot) then
                     zb=zbot
                  else
                     zb=zprf(iz-1)
                  endif
                  if(iz.EQ.itop) then
                     zt=ztop
                  else
                     zt=zprf(iz+1)
                  endif
                  usum=usum+ui*(zt-zb)
                  vsum=vsum+vi*(zt-zb)
               enddo
! ---          Average
               uadv=usum*denomi
               vadv=vsum*denomi
               if(ldbg) then
                  write(io6,*)'itop,ibot   = ',itop,ibot
                  write(io6,*)'usum,vsum   = ',usum,vsum
               endif
            endif
            if(ldbg) then
               write(io6,*)'uadv,vadv= ',uadv,vadv
            endif
         endif
! ---    Results are invalid if XTPRF reported PROBLEM=TRUE
         if(PROBLEM) then
            write(io6,*) 'ADVECT:  FATAL ERROR reported when ',&
                         'extracting winds from PROFILE ---'
            write(io6,*) 'There are no valid data'
            write(*,*)
            stop 'Halted in ADVECT -- see list file'
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine windset(ht,ilayer,ix,iy,mdom,z0m,el,dpbl,istab,ws,wd)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212           WINDSET
! ---            D. Strimaitis
!
! --- PURPOSE:  Obtain wind at height HT
!
! --- UPDATE
! --- V6.265-V6.3   100212  (DGS): add nested CALMET grids
! --- V5.74-V6.265  090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
! --- V5.4-V5.74    040715  (DGS): Add AERMET option (METFM=5)
! --- V5.0-V5.4     000602  (DGS): Add message to "stop"
! --- V5.0-V5.0     980731  (DGS): Use CALM WD=180.0 for absolute
!                                  calms (ws=0.0)
! --- V4.0-V5.0     971107  (DGS): Set wd to ZERO if CALM to avoid
!                                  potential numerical problem with
!                                  ATAN2 function
!                   971107  (DGS): Use (WS.GE.WSCALM) to signal NON Calm
!                   971107  (DGS): check PROBLEM returned by XTPRF
!
! --- INPUTS:
!              HT - real         - Puff height (m)
!          ILAYER - integer      - Met layer containing HT
!           IX,IY - integer      - Cell index of puff in MET grid
!            MDOM - integer      - Met grid domain index
!             Z0M - real         - Surface roughness (m)
!              EL - real         - Monin-Obukhov length (m)
!            DPBL - real         - Depth of boundary layer (m)
!           ISTAB - integer      - PG stability class
!
!    Common block /COMPARM/ variables:
!         WSCALM, PLX0
!     Common Block /GEN/ variables:
!         METFM
!     Common Block /GRID/ variables:
!         ZGPT(mxnz)
!    Common block /METHR/ variables:
!         UMET(mxnx,mxny,mxnz,mxmetdom), VMET(mxnx,mxny,mxnz,mxmetdom)
!         PTG(2), PLEXP, SSPRF(mxprfz), WDPRF(mxprfz), ZPRF(mxprfz)
!         NZPRF
!    Parameters:
!         MXNX, MXNY, MXNZ, MXPRFZ, IO6
!
! --- OUTPUT:
!              WS - real         - Value of wind speed at HT (m/s)
!              WD - real         - Value of wind direction at HT (deg)
!
! --- WINDSET called by:  RLSMET, COMP
! --- WINDSET calls:      POWLAW, XTPRF
!----------------------------------------------------------------------
      include 'params.puf'
      include 'comparm.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'methr.puf'
      logical problem
      data rtod/57.29578/,wdcalm/180.0/
! --- Trap missing power-law exponent (substitute default)
      if(plexp.LT.-900.) then
! ---    Use default
         plx=plx0(istab)
      else
! ---    Use it
         plx=plexp
      endif
      if(metfm.LE.3) then
! ---    3-D gridded MET field
! ---    Form wind speed from components
         ws=SQRT(umet(ix,iy,ilayer,mdom)**2+vmet(ix,iy,ilayer,mdom)**2)
         if(metfm.EQ.2 .OR. metfm.EQ.3) then
! ---       Adjustment for ISC/PLUME treatment !!
! ---       Profile non-calm wind speed in layer to puff height
            if(ws.GE.wscalm) then
               wsold=ws
               call POWLAW(ht,zgpt(ilayer),wsold,plx,ws)
            endif
         endif
         if(ws.GT.0.0) then
            wd=270.-(ATAN2(vmet(ix,iy,ilayer,mdom),&
                           umet(ix,iy,ilayer,mdom)))*rtod
         else
! ---       Absolute calm
            wd=wdcalm
         endif
      elseif(metfm.EQ.4 .OR. metfm.EQ.5) then
! ---    PROFILE.DAT met data
! ---    Use scalar speed as in CTDM
         problem=.FALSE.
         call XTPRF(nzprf,ssprf,zprf,ht,'spd',z0m,el,&
                    dpbl,istab,ptg,ws,problem)
         call XTPRF(nzprf,wdprf,zprf,ht,'dir',z0m,el,&
                    dpbl,istab,ptg,wd,problem)
! ---    Results are invalid if XTPRF reported PROBLEM=TRUE
         if(PROBLEM) then
            write(io6,*) 'WINDSET:  FATAL ERROR reported when ',&
                         'extracting winds from PROFILE ---'
            write(io6,*) 'There are no valid data'
            write(*,*)
            stop 'Halted in WINDSET -- see list file.'
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine puffdz(ii,icode,lpuff,istab,ilayer,htmet,dpbl,&
                        ztop,zbot)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 080204            PUFFDZ
!                D. Strimaitis
!
! --- PURPOSE:  Computes the top and bottom of the puff/slug for
!               use in obtaining the advection wind
!
! --- UPDATE
! --- V5.4-V6.23    080204  (DGS): Add screen for ZTOP<ZBOT, and
!                                  refine logic that places a limit on
!                                  ztop that accounts for past mixing
!                                  heights that are less than HTMET.
! --- V5.0-V5.4     000602  (DGS): Add message to "stop"
! --- V5.0-V5.0     980821  (DGS): Impose lid height on ZTOP for class
!                                  D as well as A,B, and C
!
! --- INPUTS:
!               II - integer - Puff index
!            ICODE - integer - Puff code
!            LPUFF - logical - Puff if .TRUE. (SLUG if .FALSE.)
!            ISTAB - integer - Stability class
!           ILAYER - integer - Met grid layer containing puff
!            HTMET - real    - Ht at which to center puff (m)
!             DPBL - real    - Current planetary boundary layer depth (m)
!
!     Common block /GEN/ variables:
!           NSPEC
!     Common block /GRID/ variables:
!           ZFACE(mxnzp1)
!     Common block /PUFF/ variables:
!           SIGZB(mxpuff), ZIOLD(mxpuff), ZIMAX(mxpuff),
!           QM(mxspec,mxpuff), QU(mxspec,mxpuff)
!     Common block /SLUG/ variables:
!           SIGZE(mxpuff)
!     Parameters:
!           MXNZP1, MXPUFF, MXSPEC, IO6
!
! --- OUTPUT:
!             ZTOP - real    - Top of puff
!             ZBOT - real    - Bottom of puff
!
! --- PUFFDZ called by: COMP, SPLIT
! --- PUFFDZ calls:     none
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
      include 'gen.puf'
      include 'grid.puf'
      include 'puff.puf'
      include 'slug.puf'
!
      logical lpuff
!
! --- Compute the height of the bottom and top of the puff/slug
! --- at the end of previous step
      if(mod(icode,2).eq.1)then
! ---    Gaussian if ICODE=1,3,5 (puff) or 11,13,15 (slug)
! ---    (height +/- sigma-z; ignore BID)
         if(lpuff) then
! ---       PUFF
            sigmaz=sigzb(ii)
         else
! ---       SLUG
            sigmaz=0.5*(sigzb(ii)+sigze(ii))
         endif
         zbot=htmet-sigmaz
         if(zbot.LT.0.) zbot=0.0
         ztop=htmet+sigmaz
! ---    For non-stable surface layer, check for lid cap on ztop
         if(icode.NE.3 .and. icode.NE.13 .AND. istab.LE.4) then
            if(dpbl.GT.htmet) then
               ztop=amin1(ztop,dpbl)
            elseif(ziold(ii).GT.htmet) then
               ztop=amin1(ztop,ziold(ii))
            elseif(zimax(ii).GT.htmet) then
               ztop=amin1(ztop,zimax(ii))
            endif
         endif
      else
! ---    Uniformly mixed if ICODE=2,4,6 (puff) or 12,14,16 (slug)
         if(icode.eq.2.or.icode.eq.12)then
! ---       Puff is within mixed layer (ICODE=2 or 12)
            zbot=0.0
!            ztop=amax1(dpbl,zimax(ii))
            ztop=zimax(ii)
         else if(icode.eq.4.or.icode.eq.14)then
! ---       Puff is above mixed layer since release (ICODE=4,14)
            zbot=zface(ilayer)
            ztop=zface(ilayer+1)
         else if(icode.eq.6.or.icode.eq.16)then
! ---       Puff is within 2-layer, mixed structure (ICODE=6,16)
            zbot=0.0
            ztop=zimax(ii)
! ---       Revise ZBOT if mass in upper layer > 2*mass in lower
            qmtot=0.0
            qutot=0.0
            do is=1,nspec
               qmtot=qmtot+qm(is,ii)
               qutot=qutot+qu(is,ii)
            enddo
            if(qutot.GT.(2.*qmtot)) zbot=ziold(ii)*(1-2.*qmtot/qutot)
         else
            write(io6,*)'ERROR in SUBR. PUFFDZ -- Invalid value of ',&
            'ICODE -- ICODE = ',icode,' PUFF NO. (II) = ',ii
            write(*,*)
            stop 'Halted in PUFFDZ -- see list file.'
         endif
      endif
!
! --- Stop if ztop<zbot
      if(ztop.LT.zbot) then
         write(io6,*)
         write(io6,*)'PUFFDZ: Bottom EXCEEDS Top!'
         write(io6,*)'zbot,ztop,htmet ',zbot,ztop,htmet
         stop 'Halted in PUFFDZ -- see list file.'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine areaint(x,y,lwflux,z,hlid,hlidmax,&
                         zrterr,zstak,zbase,ppcf,ldbhr,&
                         ccqb,ccdq,ccizqb,ccizdq)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090710           AREAINT
!                R. Yamartino
!
! --- PURPOSE:  For a polygon AREA source, INTegrate the
!               time-averaged concentration for the case of the
!               unaged slug (i.e., IAGE=0) for a receptor at (X,Y)
!
! --- UPDATE
! --- V5.0-V6.267   090710  (DGS): add HLIDMAX for use in VCOUP
! --- V5.0-V5.0     980430  (DGS): drop dt from argument list
! --- V4.0-V5.0     971107  (DGS): add /COMPARM/ with "eps" variable for
!                                  use in CPQROMB
!
! --- INPUTS:
!
!                 X - real    - X coord. of receptor.
!                 Y - real    - Y coord. of receptor.
!            LWFLUX - logical - Receptor specific wet deposition flag
!                                .true. if calculation is to be made
!                                .false. if not.
!                 Z - real    - Z coord. of receptor.
!              HLID - real    - Relevant mixing depth (m) at receptor.
!           HLIDMAX - real    - Maximum lid height (m) for mass
!                               above HLID
!            ZRTERR - real    - Terrain elevation (m MSL) at receptor
!             ZSTAK - real    - Stack height of source of puff (m)
!             ZBASE - real    - Stack base elevation MSL (m)
!              PPCF - real    - Plume Path Coefficient
!             LDBHR - logical - Flag for debugging write-statements
!
! --- COMPARM.PUF variables used.
!             EPSAREA
! --- CURRENT.PUF variables used.
!             NSIDE, XVERT, YVERT, XV, YV
!
! --- OUTPUTS:
!
!              CCQB - real    - Coupling coefficient (s/m**3) for
!                               the source rate ,QB, at the
!                               beginning of the time step.
!              CCDQ - real    - Coupling coefficient (s/m**3) for
!                               the change in source rate, DQ, between
!                               the beginning and end of the time step.
!            CCIZQB - real    - Z-integrated coefficient (s/m**2) for
!                               the source rate ,QB, at the
!                               beginning of the time step.
!            CCIZDQ - real    - Z-integrated coefficient (s/m**2) for
!                               the change in source rate, DQ, between
!                               the beginning and end of the time step.
!
! --- AREAINT called by:  CALCSL
! --- AREAINT calls:      XY2UWCW, CWYVALS, VECMIN, VECMAX, EXTREME
!                         CPQROMB
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'comparm.puf'
      include 'current.puf'
!
      logical lwflux,ldbhr
      real xvord(mxvert)
      data sigcut/3.0/
      data zero/0.0/
!
! --- Zero out the return quantities.
      ccqb  = zero
      ccdq  = zero
      ccizqb  = zero
      ccizdq  = zero
!
! --- Transform vertex coordinates, (XVERT,YVERT), into upwind/crosswind
! --- frame as (XV,YV), relative to receptor (x,y); also compute upwind
! --- position of edge of line sources (if source-type is 5), and
! --- pass slug-length variables DXY12, RDXY12 to /SLGLIN/
      call xy2uwcw(x,y)
!
! --- Quit if receptor entirely upwind.
      xtest = vecmax(nside,xv)
      if(xtest.le.zero) then
         go to 500
      else
! ---    Quit if receptor excessively crosswind.
         call extreme(nside,yv,ymin,ymax)
! ---    Continue test if receptor is NOT directly downwind.
         if((ymin*ymax).gt.zero) then
! ---       Define smallest absolute crosswind distance.
            ymin = amin1(abs(ymin),abs(ymax))
! ---       Normalize by the largest sigma y possible
            ymin = ymin / sye1
            if(ymin.gt.sigcut) go to 500
         endif
      endif
!
! --- Order the XV vertex locations in ascending order in XVORD
! --- and determine the number of integration segments NISEG.
      xvord(  1  ) = vecmin(nside,xv)
      xlast        = vecmax(nside,xv)
      xvord(nside) = xlast
      niseg = 1
!
      do i=2,nside-1
         xbelow = xvord(i-1)
         xnext = xlast
         do j=1,nside
            xnow = xv(j)
            if(xnow.gt.xbelow) xnext = amin1(xnext,xnow)
         enddo
         xvord(i) = xnext
         if(xnext.lt.xlast) niseg = niseg + 1
      enddo
!
! --- Reduce the number of segments if intercept directly upwind.
! --- Reset NISEG to 2.
! --- Define alongwind X values
      call cwyvals(nside,yv,xv,zero,xmin,xmax,nhits)
      if(nhits.eq.2) then
         xvord(2) = amax1(xmin,zero)
         xvord(3) = xlast
         niseg = 2
      endif
!
! --- Begin loop over finite length integration segments.
! --- N.B. The maximum value of NISEG is NSIDE-1
!
      do 15 i=1,niseg
      xbelow = xvord(i)
      xnext = xvord(i+1)
!
! --- Zero out the return quantities from CPQROMB
      ss1 = zero
      ss2 = zero
      ss3 = zero
      ss4 = zero
!
! --- Quit if receptor entirely upwind of this segment.
      if(xnext.le.zero) go to 15
!
! --- Redefine segment to ignore downwind source portions.
      xbelow = amax1(xbelow,zero)
! --- Ignore integration paths smaller than 0.01 m long (arbitrary cut)
      if(xnext-xbelow.lt.0.01) go to 15
!
! === Quit if receptor excessively crosswind of all Y in this interval.
!
! --- Define crosswind Y values at start of integration segment.
      call cwyvals(nside,xv,yv,xbelow,ymin,ymax,nhits)
! --- Skip detailed test if receptor directly downwind.
      if((ymin*ymax).le.zero) go to 12
      y1 = ymin
      y2 = ymax
! --- Define crosswind Y values at end of integration segment.
      call cwyvals(nside,xv,yv,xnext,ymin,ymax,nhits)
! --- Skip detailed test if receptor directly downwind.
      if((ymin*ymax).le.zero) go to 12
! --- Define range of crosswind Y values throughout integration segment.
      ymin = amin1(ymin,y1)
      ymax = amax1(ymax,y2)
! --- Skip detailed test if receptor directly downwind.
      if((ymin*ymax).le.zero) go to 12
! --- Define smallest absolute crosswind distance.
      ymin = amin1(abs(ymin),abs(ymax))
! --- Normalize by the largest sigma y possible
      ymin = ymin / sye1
      if(ymin.gt.sigcut) go to 15
!
! --- Evaluate the integral for this portion of the regime.
   12 call cpqromb(xbelow,xnext,lwflux,z,hlid,hlidmax,&
                   zrterr,zstak,zbase,ppcf,epsarea,&
                   ss1,ss2,ss3,ss4)
      ccqb  = ccqb + ss1
      ccdq  = ccdq + ss2
      ccizqb  = ccizqb + ss3
      ccizdq  = ccizdq + ss4
! ***
      if(ldbhr) then
         write(io6,*)'AREAINT -- i = ',i
         write(io6,*)'  ccqb, ccdq = ',ccqb,ccdq
      endif
! ***
!
   15 continue
!
  500 return
      end
!----------------------------------------------------------------------
      subroutine xy2uwcw(x,y)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107           XY2UWCW
!                R. Yamartino, D. Strimaitis, SRC
!
! --- PURPOSE:  Transform the NSIDE vertex coordinates, (XVERT,YVERT),
!               relative to the receptor coordinates (X,Y),
!               into upwind/crosswind frame vectors (XV,YV).
!               Also compute upwind distance from receptor to the
!               upwind corner of the block of line sources (if modeled)
!
! --- UPDATE
! --- V4.0-V5.0     971107  (DGS): add variable line source treatment
!
!
! --- INPUTS:
!
!              X    - real    - Receptor X coordinate (m).
!              Y    - real    - Receptor Y coordinate (m).
!
!     Common Block /CURRENT/ variables:
!        XB1, YB1, ZB1, SYB1, SZB1,
!        XE1, YE1, ZE1, SYE1, SZE1,
!        XB2, YB2, ZB2, SYB2, SZB2,
!        XE2, YE2, ZE2, SYE2, SZE2,
!        IAGE, SPEEDI, SRAT, TEMIS,
!        NSIDE, XVERT, YVERT, XSHIFT
!
! --- OUTPUTS:
!
!     Common Block /CURRENT/ variables:
!             XV    - real    - Receptor/vertex upwind distances (m)
!             YV    - real    - Receptor/vertex crosswind distances (m)
!        XUPEDGE    - real    - Distance from receptor to upwind edge
!                               of block of line sources (m)
!
!     Common Block /SLGLIN/ variables:
!          DXY12    - real    - Projection of fully-extended slug length
!                               on the x-y plane (m)
!         RDXY12    - real    - Reciprocal of DXY12
!
! --- XY2UWCW called by:  AREAINT
! --- XY2UWCW calls:       none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'current.puf'
      common /SLGLIN/ dxy12,rdxy12
!
! --- Determine the length and orientation of the final slug
! --- emitted from the center of the polygon area source.
! --- XE1,YE1 are the final coordinates of the slug end-point.
! --- XB2,YB2 are the initial coordinates of the slug end-point.
! --- Note that there is no change in (XB2,YB2) over time.
      X12 = XE1 - XB2
      Y12 = YE1 - YB2
! --- DXY12 is the projection of the slug length on the x-y plane.
      DXY12 = SQRT(X12*X12 + Y12*Y12)
      RDXY12 = 1.0 / DXY12
! --- Note that the angles are defined cw (clockwise) from North.
! --- Define the cos and sin of omega.
! --- note that omega = wind direction (met.) + pi in this case !
      COSOM = Y12 * RDXY12
      SINOM = X12 * RDXY12
!
! --- Begin loop over array vertices.
      do i=1,nside
! ---    Define the vertex-receptor relative position.
         xr = xvert(i) - x
         yr = yvert(i) - y
! ---    Convert to upwind (=-downwind) and crosswind distances.
         xv(i) = -(yr * cosom  +  xr * sinom)
         yv(i) =  -xr * cosom  +  yr * sinom
      enddo
!
! --- For line sources, determine distance from upwind edge of block
! --- of line sources to this receptor
      if(istype.EQ.5 .OR. istype.EQ.6) then
! ---    Source-receptor relative position
         xr = xb2 - x
         yr = yb2 - y
! ---    Convert to upwind (=-downwind) distance
         xline = -(yr * cosom  +  xr * sinom)
! ---    Add distance between upwind edge and source
         xupedge = xline + xshift
      endif
!
      return
      end
