      include 'ln1.puf'
      include 'ln2.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'nongrd.puf'
      include 'outpt.puf'
      include 'pt1.puf'
      include 'pt2.puf'
      include 'puff.puf'
      include 'slug.puf'
      include 'tibl.puf'
      include 'vol1.puf'
      include 'vol2.puf'
      include 'wrkspc.puf'
!
      real hillr2(mxhill)
      real fracwet(mxspec),fracdry(mxspec)
      real xlam(mxspec),vd(mxspec),vdpvd(mxspec)
      real qold(mxspec),qnew(mxspec),qavg(mxspec),qavgt(mxspec)
      real qoldup(mxspec),qnewup(mxspec)
      real qbrate(mxspec),qerate(mxspec)
      real qbrateup(mxspec),qerateup(mxspec)
      real q01wet(mxspec,2),q01dry(mxspec,2),qratew(mxspec,2)
      real em2dat(mxp4,mxpt2),em4dat(mxp6,mxvol)
      real em3dat(mxp14,mxarea)
      real em5dat(mxp6,mxlines),em5grp(7,mxlngrp)
      real em6dat(mxp11,mxfl2)
      real, allocatable :: em7dat(:,:),em8dat(:,:)               ! (mxspec+3,nrd2)
      real delzi(mxnz)
      real wdivs(mxnz),wdive(mxnz)
      real q1sfc(mxspec),q1upr(mxspec)
      real smxi1(mxmetdom),smxi2(mxmetdom)
! --- 6.42_x1.1 140521
      real qcij(mxnz), tkij(mxnz), patmij(mxnz)
      logical ldb2,ldb,ldb0,ldbmet,ldbaux
      logical lpuff,lslug,lskip,lresplit
      logical lcalm,lclip,ldohill(mxhill)
      logical leof,ldiv,lctsg,lprof,ltibl
      logical lsubsam
      logical ltest
      logical lszcap
      logical lvar,lrdmet,lrdprf,lrdozn,lrdh2o2,lozn,lh2o2
      integer nsource(13)
      character*12 dtype
!
! --- Special treatment for puffs from boundary conditions
      logical lbcpuf
      character*3 cbal
!
      real o3p, h2o2p
!
      data zero/0.0/
      data fracwet/mxspec*1.0/,fracdry/mxspec*1.0/
      data vd/mxspec*0.0/,vdpvd/mxspec*1.0/
      data qold/mxspec*0.0/,qnew/mxspec*0.0/
      data qoldup/mxspec*0.0/,qnewup/mxspec*0.0/
      data qbrate/mxspec*0.0/,qerate/mxspec*0.0/
      data qbrateup/mxspec*0.0/,qerateup/mxspec*0.0/
      data qratew/mxspec*0.0,mxspec*0.0/
      data xlam/mxspec*0.0/
      data qunit/1.0/,rad/57.295778/
      data nsigma/3/
! --- Set value of transport wind speed for "calm"
      data ucalm/0.001/
! --- Set minimum value on computed potential temperature gradients
      data ptgrad0/0.005/
! --- Set initial values for 'last' date-times for met/AQ data files
      data ndhrmetb/-000100100/,ndhrmete/-000100100/
      data nsecmetb/0/,nsecmete/0/
      data ndhrprfb/-000100100/,ndhrprfe/-000100100/
      data nsecprfb/0/,nsecprfe/0/
      data ndhroznb/-000100100/,ndhrozne/-000100100/
      data nsecoznb/0/,nsecozne/0/
      data ndhrh2o2b/-000100100/,ndhrh2o2e/-000100100/
      data nsech2o2b/0/,nsech2o2e/0/
!
! ***
      write(iomesg,*)'COMPUTATIONAL PHASE'
! ***
!
!----------------------------------------------------------
! --- INITIALIZATIONS
!----------------------------------------------------------
! --- Allocate the array for reading variable source data for roads
! --- and sprays. The space recognizes the number of roads, but uses 
! --- the full MXSPEC;  it will be resized in subseqent calls.
      ALLOCATE(em7dat(mxspec+3,nrd2))
      ALLOCATE(em8dat(mxspec+3,nsp2))
! --- Set search radius (grid cells) for boundary condition puffs
! --- to impact a receptor
      rbc0=rsampbc*1000.*dgridi
      chioz=bcko3m(nmob)
      chih2o2=bckh2o2m(nmob)
      rconst=nsigma*dgridi
! --- Length of period is NSECDT in seconds
      secdt=FLOAT(nsecdt)
! --- Reciprocal of speed required to travel max allowed distance in 1
! --- timestep in s/m (XMXLEN is maximum slug length, or maximum puff
! --- step during emission, in grid units; XSAMLEN is maximum advection
! --- step of an existing puff/slug in grid units)
      xsamleni=1.0/xsamlen
! --- Loop over MET grid domains
      do md=1,ngrid
         smxi1(md)=secdt/(xmxlen*xgridm(md))
! ---    Cast smxi2 calculation to match how it was done in v5
         smxi2(md)=secdt*xsamleni*xgridmi(md)
      enddo
!
! --- Set boundary of computational grid in MET-GRID units
      xb=ibcomp-1.0
      xe=iecomp
      yb=jbcomp-1.0
      ye=jecomp
!
! --- Set factor used to distinguish "puff codes" for puffs vs. slugs
      mfact=10*mslug
!
! --- Pass default potential temperature gradient from control file
! --- variable PTG0 to hourly met variable PTG (hourly PTG is provided
! --- by RDISC and RDPLM, but not by RDMET, RDMET4)
      ptg(1)=ptg0(1)
      ptg(2)=ptg0(2)
!
! --- Set value of LCLIP:  this is passed to PUFRECS, SLGRECS, and
! --- PLGRECS to limit receptor-specific sigmas to range defined by
! --- values at the start/end of the step, when true.
!     lclip=.FALSE.
      lclip=.TRUE.
!
! --- Set End-Of-File logical to .FALSE. (met & chem files)
      leof=.FALSE.
!
! --- PROFILE heights are determined hourly, but initialize to model
! --- layers for cases where PROFILE.DAT file is not used
      nzprf=nz
      do iz=1,nz
         zprf(iz)=zgpt(iz)
      enddo
! --- Set square of maximum "radius" of hills for CTSG option
      if(MCTSG.EQ.1) then
         do ih=1,nhill
            hillr2(ih)=(dgridi*AMAX1(hilldat(10,ih),hilldat(11,ih)))**2
         enddo
      endif
! --- Set logical for incorporating vertical divergence in puff growth
      if(lcalgrd .AND. metfm.EQ.1) then
         ldiv=.TRUE.
      else
         ldiv=.FALSE.
      endif
! --- Set 1/layer-depth for wdiv calculation
      do k=1,nz
         delzi(k)=1./(zface(k+1)-zface(k))
      enddo 
! --- Set logical for applying cap to sigma-z to protect virtuals
      if(szcap_m.GT.0.0) then
         lszcap=.TRUE.
      else
         lszcap=.FALSE.
      endif
! --- Set logical for reading PROFILE.DAT as a secondary met file
! --- (e.g. for turbulence data or inversion strength)
      if(metfm.EQ.4 .OR. metfm.EQ.5) then
         lprof=.FALSE.
      elseif(mtinv.EQ.1) then
         lprof=.TRUE.
      elseif(mdisp.EQ.1 .OR. mdisp.EQ.5) then
         if(mturbvw.LT.4) then
            lprof=.TRUE.
         else
            lprof=.FALSE.
         endif
      else
         lprof=.FALSE.
      endif
! --- Set logical for using external file for ozone or H2O2
      lozn=.FALSE.
      if((mchem.EQ.1 .OR. mchem.EQ.3 .OR. mchem.EQ.4 .OR. mchem.EQ.6&
                     .OR. mchem.EQ.7).AND.  moz.EQ.1) lozn=.TRUE.
      lh2o2=.FALSE.
      if(maqchem.EQ.1 .AND.  mh2o2.EQ.1) lh2o2=.TRUE.
! --- Initialize model fields with restart file if this is
! --- a "continuation" run, and if NPUFFS>0 was found in header
      if(npuffs.GT.0 .AND.&
        (mrestart.EQ.1 .OR. mrestart.EQ.3)) call RESTARTI
! --- Set puff ID range for debug output
      ipfdeb1=ipfdeb
      ipfdeb2=ipfdeb1+npfdeb-1
!----------------------------------------------------------
! --- BASIC TIME LOOP (nsecdt per step)
!----------------------------------------------------------
      if(imesg.eq.1)then
         write(iomesg,*)'  ---   Advection Step Length (sec)',nsecdt
         write(iomesg,*)'  ---   Puff #      Advection Step'
         write(iomesg,*)
      elseif(imesg.eq.2)then
        if(msplit.GT.0) then
          write(iomesg,*)'  --- Advection Step Starting:'
          write(iomesg,*)&
                '  --- YYYYJJJHH SSSS   # Old  # Split  # Emitted'
        else
          write(iomesg,*)'  --- Advection Step Starting:'
          write(iomesg,*)'  --- YYYYJJJHH SSSS   # Old  # Emitted'
        endif
        write(iomesg,*)
      endif
!
      iarlg=IABS(irlg)
      do 1000 nn=1,iarlg
!
! --- Determine if debug data are written this period
      if(nn.ge.nn1.and.nn.le.nn2)then
         ldb=ldebug
      else
         ldb=.false.
      endif
      ldb0=ldb
!     ldbmet=ldb
      ldbmet=.FALSE.
!      ldbaux=ldb
      ldbaux=.FALSE.
! --- Determine which external data should be read for this step by
! --- comparing the time at the mid-point of the step with the
! --- period for the current data
! --- Met data
      lrdmet=.FALSE.
      call TPERIOD(0,ndathrm,nsecm,ndhrmetb,nsecmetb,ndhrmete,nsecmete,&
                   ilocate)
      if(ilocate.EQ.1) lrdmet=.TRUE.
! --- Supplementary data from PROFILE file
      lrdprf=.FALSE.
      if(lprof) then
         call TPERIOD(0,ndathrm,nsecm,ndhrprfb,nsecprfb,ndhrprfe,&
                      nsecprfe,ilocate)
         if(ilocate.EQ.1) lrdprf=.TRUE.
      endif
! --- Ozone data
      lrdozn=.FALSE.
      if(lozn) then
         call TPERIOD(0,ndathrm,nsecm,ndhroznb,nsecoznb,ndhrozne,&
                      nsecozne,ilocate)
         if(ilocate.EQ.1) lrdozn=.TRUE.
      endif
! --- H2O2 data
      lrdh2o2=.FALSE.
      if(lh2o2) then
         call TPERIOD(0,ndathrm,nsecm,ndhrh2o2b,nsech2o2b,ndhrh2o2e,&
                      nsech2o2e,ilocate)
         if(ilocate.EQ.1) lrdh2o2=.TRUE.
      endif
!
! --- Echo progress message to screen (conditional)
      if((lrdmet.OR.lrdprf.OR.lrdozn) .AND. imesg.EQ.1) print 5,nn
5     format('+',' Reading Data         ',i6)
! --- Read meteorological data
!
! --- Read data from PROFILE file for current period if needed and if
! --- not already done (LPROF=T if PROFILE.DAT must be read)
      if(lrdprf) then
         if(mprffm.EQ.1) then
            call RDPROF(ndathrm,nsecm,ldbmet,nzprf,wsprf,wdprf,&
                        tprf,zprf,ssprf,swprf,svprf,dptinvo,&
                        ndhrprfe,nsecprfe,ndhrprfb,nsecprfb,leof)
         elseif(mprffm.EQ.2) then
            call RDPROF5(ndathrm,nsecm,ldbmet,nzprf,wsprf,wdprf,&
                        tprf,zprf,ssprf,swprf,svprf,dptinvo,&
                        ndhrprfe,nsecprfe,ndhrprfb,nsecprfb,leof)
         else
            write(io6,*)
            write(io6,*) 'COMP:  Bad MPRFFM found = ',mprffm
            write(io6,*) 'Expected 1 or 2'
            write(io6,*)
            write(*,*)
            write(*,*) 'COMP:  Bad MPRFFM found = ',mprffm
            write(*,*) 'Expected 1 or 2'
            write(*,*)
            stop
         endif
!
! ---    Reset PROFILE heights to model layers if turbulence data will
! ---    be read from PLMMET.DAT
         if(mturbvw.EQ.4) then
            nzprf=nz
            do iz=1,nz
               zprf(iz)=zgpt(iz)
            enddo
         endif
      endif
! --- Read data from primary met file for current period
      if(lrdmet) then
         if(metfm.EQ.1) then
            call RDMET(ndathrm,nsecm,tmp1,nw1,ldbmet,umet,vmet,tmet,&
                       ipgt,htmix,ustar,xmonin,wstar,rmm,tempss,rhoss,&
                       qswss,irhss,ipcode,temp2d,rho2d,qsw2d,irh2d,&
                       ipcode2d,ndhrmete,nsecmete,ndhrmetb,nsecmetb,&
                       wdiv)
!
! ---       Compute vertical divergence fields for CALMET data
            if(LDIV) then
               do n=1,ngrid
                do k=nz,1,-1
                 do j=1,nym(n)
                  do i=1,nxm(n)
                     if(k.GT.1) then
                        wdiv(i,j,k,n)=delzi(k)*(wdiv(i,j,k,n)-&
                                                wdiv(i,j,k-1,n))
                     else
                        wdiv(i,j,k,n)=delzi(k)*wdiv(i,j,k,n)
                     endif
                  enddo
                 enddo
                enddo
               enddo
            endif
! ---       Also read CALMET auxiliary file if needed
            if(mlwc.EQ.1) then
               if(ldbaux) write(*,*)'BEFORE call RDAUX'
! --- 6.42_x1.1 140521
               call RDAUX(ndathrm,nsecm,tmp1,nw1,ldbaux,&
                          icldmr2d,icldmr3d,qcup,zupbot,zuptop,qc3d,&
                          ndhrmetea,nsecmetea,ndhrmetba,nsecmetba)
! ---          Estimate pressure (atm) for the CALMET layer faces
               call MAKEP3D
               if(ldbaux) write(*,*)'AFTER call RDAUX'
            endif
         elseif(metfm.EQ.2) then
            call RDISC(ndathrm,nsecm,ldbmet,umet,vmet,ipgt,htmix,ustar,&
                       xmonin,wstar,rmm,tempss,rhoss,qswss,irhss,&
                       ipcode,ptg,plexp,leof,ndhrmete,nsecmete,&
                       ndhrmetb,nsecmetb)
         elseif(metfm.EQ.3) then
            call RDPLM(ndathrm,nsecm,ldbmet,umet,vmet,svprf,ipgt,htmix,&
                       ustar,xmonin,wstar,rmm,tempss,rhoss,qswss,irhss,&
                       ipcode,ptg,plexp,leof,ndhrmete,nsecmete,&
                       ndhrmetb,nsecmetb)
         elseif(metfm.EQ.4) then
            call RDMET4(ndathrm,nsecm,ldbmet,ptg,nzprf,wsprf,wdprf,&
                       tprf,zprf,ssprf,swprf,svprf,ipgt,htmix,ustar,&
                       xmonin,wstar,rmm,tempss,rhoss,qswss,irhss,&
                       ipcode,dptinvo,leof,ndhrmete,nsecmete,&
                       ndhrmetb,nsecmetb)
         elseif(metfm.EQ.5) then
            call RDMET5(ndathrm,nsecm,ldbmet,ptg,nzprf,wsprf,wdprf,&
                       tprf,zprf,ssprf,swprf,svprf,ipgt,htmix,ustar,&
                       xmonin,wstar,rmm,tempss,rhoss,qswss,irhss,&
                       ipcode,dptinvo,leof,ndhrmete,nsecmete,&
                       ndhrmetb,nsecmetb)
         endif
! ---    Require primary met timstep to be GE model timestep
         call DELTSEC(ndhrmetb,nsecmetb,ndhrmete,nsecmete,nsecmet)
         if(nsecmet.LT.nsecdt) then
            write(io6,*)
            write(io6,*) 'Primary MET data timestep    : ',nsecmet
            write(io6,*) 'is LESS than CALPUFF timestep: ',nsecdt
            write(io6,*) 'Set CALPUFF timestep to this MET timestep'
            write(io6,*)
            write(*,*)
            write(*,*) 'Primary MET data timestep    : ',nsecmet
            write(*,*) 'is LESS than CALPUFF timestep: ',nsecdt
            write(*,*) 'Set CALPUFF timestep to this MET timestep'
            write(*,*)
            stop 'Halted in COMP'
         endif
      endif
!
! --- Disregard any DPTINVO values read from PROFILE.DAT when MTINV=0
      if(mtinv.EQ.0) dptinvo=-999.
!
! --- Read ozone data for current period (nsecozn[b,e] remain zero)
      if(lrdozn) then
         dtype='OZONE       '
         call RDAQ(io22,dtype,veroz,ndathrm,nsecm,t2btzoz,nozsta,&
                   ldbmet,ndhroznb,nsecoznb,ndhrozne,nsecozne,&
                   ozconc,leof)
      endif
!
! --- Read H2O2 data for current period
      if(lrdh2o2) then
         dtype='H2O2        '
         call RDAQ(io23,dtype,verh2o2,ndathrm,nsecm,t2btzhp,nh2o2sta,&
                   ldbmet,ndhrh2o2b,nsech2o2b,ndhrh2o2e,nsech2o2e,&
                   h2o2conc,leof)
      endif
!
! --- Compute cloud and zenith data at surface met stations
      call SUNDATA(ldbmet)
!
! --- Set background data for SOA option this hour
      if(mchem.EQ.4) call SETSOA(ndathrm)
      if(mchem.EQ.7) call SETBCKOC(ndathrm)
!
! --- Update current monthly background data
      if(mchem.GT.0) call CHEMBK(ndathrm)
!
      if(lrdmet.OR.lrdprf.OR.lrdozn.OR.lrdh2o2) then
         if(LEOF) then
            irlg=nn-1
            write(io6,*)
            write(io6,*) 'EOF reached in MET or CHEM data file'
            write(io6,*) 'Last period processed IRLG =',irlg
            write(io6,*)
            write(*,*)
            write(*,*) 'EOF reached in MET or CHEM data file'
            write(*,*) 'Last period processed IRLG =',irlg
            write(*,*)
! ---       Roll back date-time to report last period processed
            call TIMEUP(-1)
            return
         endif
      endif
! --- Initialize concentrations and dry/wet fluxes at beginning
! --- of each averaging output period (TOTAL and BC arrays)
      if(iavg.eq.1.or.mod(nn,iavg).eq.1)then
!
! ---    Set beginning time for averaging period
         nyrab =nyrb
         nmoab =nmob
         ndayab=ndayb
         njulab=njulb
         nhrab =nhrb
         nsecab=nsecb
!
! ---    Gridded receptors
         if(lsamp)then
            do i=1,nspec
               call INITR2D(zero,mxnxg,mxnyg,nxsam,nysam,tchisam(1,1,i))
               if(mdry.eq.1)call INITR2D(zero,mxnxg,mxnyg,&
                            nxsam,nysam,tdfsam(1,1,i))
               if(mwet.eq.1)call INITR2D(zero,mxnxg,mxnyg,&
                            nxsam,nysam,twfsam(1,1,i))
            enddo
         endif
         if(lsamp .AND. nbc.GT.0)then
            call INITR2D(rbc0,mxnxg,mxnyg,nxsam,nysam,rbcsam)
            do i=1,nspec
               call INITR2D(zero,mxnxg,mxnyg,nxsam,nysam,cbcsam(1,1,i))
               if(mdry.eq.1)call INITR2D(zero,mxnxg,mxnyg,&
                            nxsam,nysam,dbcsam(1,1,i))
               if(mwet.eq.1)call INITR2D(zero,mxnxg,mxnyg,&
                            nxsam,nysam,wbcsam(1,1,i))
            enddo
         endif
!
! ---    Non-gridded receptors
         if(nrec.gt.0)then
            call INITR2D(zero,mxrec,mxspec,nrec,nspec,tchirec)
            if(mdry.eq.1)call INITR2D(zero,mxrec,mxspec,nrec,nspec,&
                         tdfrec)
            if(mwet.eq.1)call INITR2D(zero,mxrec,mxspec,nrec,nspec,&
                         twfrec)
         endif
         if(nrec.gt.0 .AND. nbc.GT.0)then
            call INITAR(rbc0,nrec,rbcrec)
            call INITR2D(zero,mxrec,mxspec,nrec,nspec,cbcrec)
            if(mdry.eq.1)call INITR2D(zero,mxrec,mxspec,nrec,nspec,&
                         dbcrec)
            if(mwet.eq.1)call INITR2D(zero,mxrec,mxspec,nrec,nspec,&
                         wbcrec)
         endif
!
! ---    Complex-terrain (CTSG) receptors
         if(nctrec.gt.0)then
            call INITR2D(zero,mxrect,mxspec,nctrec,nspec,tchict)
            if(nbc.GT.0) then
               call INITAR(rbc0,nctrec,rbcct)
               call INITR2D(zero,mxrect,mxspec,nctrec,nspec,cbcct)
            endif
         endif
      endif
!
! --- Initialize mass flux arrays at beginning of each time step
      if(imflx.EQ.1) call MFLXINI
!
! --- Initialize mass balance arrays at beginning of each time step
      if(imbal.EQ.1) call MBALINI
!
! --- Compute hill-specific flow parameters (Hd, U/N) for CTSG
! --- terrain option
      if(nhill.gt.0)then
         if(metfm.EQ.4 .OR. metfm.EQ.5) then
! ---       Use 1-D PROFILE.DAT arrays
! ---       Set surface layer data (use cell 1,1 of grid domain 1)
            istab=ipgt(1,1,1)
            istab=MIN0(istab,6)
            dpbl=AMAX1(htmix(1,1,1),xminzi)
            dpbl=AMIN1(dpbl,xmaxzi)
            el=xmonin(1,1,1)
            z0m=z0(1,1,1)
            call GETPRFP(ssprf,tprf,zprf,nears,tempss,lcalgrd,&
                         istab,dpbl,el,z0m,ptg,nzprf)
            do ih=1,nhill
               call HDUN(ih)
            enddo
         else
! ---       Use 3-D MET arrays
            do ih=1,nhill
! frr (09/01) - CALMET noobs mode temp2d instead of tempss
!               call GETPRFM(ih,umet,vmet,tmet,zgpt,nears,tempss,lcalgrd,
!     &                      ipgt,ptg,nx,ny,nz,xorig,yorig,dgrid)
               call GETPRFM(ih,umet,vmet,tmet,zgpt,nears,tempss,lcalgrd,&
                            ipgt,ptg,nx,ny,nz,xorig,yorig,dgrid,&
                            i2dmet,temp2d)
               call HDUN(ih)
            enddo
         endif
      endif
! --- Initialize puff numbers to report to screen this step (imesg=2)
      numold=npuffs
      numsplty=0
      numspltz=0
!
! --- Split certain puffs/slugs to respond to wind shear
      ldb2=ldb
!     ldb2=.TRUE.
!     ldb2=.FALSE.
      if(msplit.EQ.1) then
         if(iresplit(nhrind).EQ.1) then
            lresplit=.TRUE.
         else
            lresplit=.FALSE.
         endif
         call SPLIT(ldb2,lresplit,nspec,xb,yb,xe,ye,imbal,&
                    numsplty,numspltz)
         numsplit=numsplty+numspltz
      endif
!
! --- Release new puffs/slugs for this time step
! --- NOTE: when using PRIME downwash, in/near-cavity concentrations
! ---       will be computed here within source-loop
      ldb2=ldb
!     ldb2=.TRUE.
!     ldb2=.FALSE.
      call INITPUF(ndathrb,nsecb,ndathre,nsece,nspec,secdt,smxi1,ldb2,&
                   em2dat,em4dat,em3dat,em5dat,em5grp,em6dat,em7dat,&
                   em8dat,metfm,newpuf)
      noldp=npuffs
      npuffs=npuffs+newpuf
!
! --- Identify puffs for computing FOG output in plume mode, and
! --- set up puff centerline trace and receptors for each
! --- Process just the first new puff released this step from each
! --- point source
      if(LPMODE) then
         ldb2=ldb
!        ldb2=.TRUE.
!        ldb2=.FALSE.
         nipfog=0
! ---    ZTRACE operates on current met period tables
         imet=1
         do i=1,newpuf
            ip=noldp+i
            if(irlsnum(ip).EQ.1 .AND.&
              (isrctyp(ip).EQ.1 .OR. isrctyp(ip).EQ.2)) then
! ---          Keep this puff
               nipfog=nipfog+1
               ipfog(nipfog)=ip
! ---          Compute puff height at each distance in fog arrays
               call ZTRACE(ldb2,ip,mxrfog,xrfog,zrfog(1,nipfog))
            else
! ---          Drop this puff
               ipufcd(ip)=99
            endif
         enddo
! ---    Generate discrete receptors for this hour
         call FOGREC(ldb2)
      endif
! --- Tracking message for this timestep
      if(imesg.EQ.2) then
         if(msplit.GT.0) then
            print 7,ndathrb,nsecb,numold,numsplit,newpuf
         else
            print 7,ndathrb,nsecb,numold,newpuf
         endif
      endif
7     format('+',6x,i9,i5,3i9)
!
! --- Echo splitting information to list file
      ldb2=ldb
!     ldb2=.TRUE.
!     ldb2=.FALSE.
      if(LDB2) then
         write(io6,'(a,6i9)')'#Old,split-y,split-z,emitted: ',&
                 ndathrb,nsecb,numold,numsplty,numspltz,newpuf
      endif
!
!
!*****
      if(ldb)then
         write(io6,202)nn,noldp,newpuf,npuffs
202      format(/1x,'Advection step (NN) = ',i5,2x,&
         'No. old puffs (NOLDP) = ',i6,2x,'No. new puffs (NEWPUF) = ',&
         i6,2x,'Total puffs (NPUFFS) = ',i6)
         write(io6,*)'Start of Step (YYYYJJJHH,SSSS) ',ndathrb,nsecb
         write(io6,*)'End of Step   (YYYYJJJHH,SSSS) ',ndathre,nsece
      endif
!*****
!
!----------------------------------------------------------
! --- LOOP OVER PUFFS
!----------------------------------------------------------
! --- MSOURCE Option
! ---------------------
! --- The source contribution option requires output of computed
! --- fields for EACH source as well as all sources combined.
! --- Process impacts for EACH source in turn by just using puffs
! --- emitted from that source.  This is implemented by placing a
! --- screen for the source ID into the puff loop, and executing
! --- the puff loop multiple times, once for each source.  If the
! --- source contribution option is OFF (MSOURCE=0), the puff
! --- loop is executed once, and no source screen is applied.
! --- Source Types are:
!            1 = Point         Constant Emissions
!            2 = Point         Variable Emissions
!            3 = Poly. Area    Constant Emissions
!            4 = Poly. Area    Variable Emissions
!            5 = Line          Constant Emissions
!            6 = Line          Variable Emissions
!            7 = Volume        Constant Emissions
!            8 = Grid Volume   Variable Emissions
!            9 = Boundary Condition
!           10 = Flare         Constant Emissions
!           11 = Flare         Variable Emissions
!           12 = Road          Constant Emissions
!           13 = Road          Variable Emissions
!           14 = Spray         Constant Emissions (inactive)
!           15 = Spray         Variable Emissions
! --- Set range for loops over source types and sources
      if(msource.EQ.0) then
! ---    Set loop ranges so that puff loop is executed once
         nstype=1
         nsource(1)=1
         do kk=2,15
            nsource(kk)=0
         enddo
      elseif(msource.EQ.1) then
         nstype=13
         nsource(1)=npt1
         nsource(2)=npt2
         nsource(3)=nar1
         nsource(4)=nar2
         nsource(5)=nlines
         nsource(6)=nln2
         nsource(7)=nvl1
         nsource(8)=nvl2
         nsource(9)=nbc
         nsource(10)=0
         nsource(11)=nfl2
         nsource(12)=nrd1
         nsource(13)=nrd2
         nsource(14)=0
         nsource(15)=nsp2
      endif
! --- Loop over source types
      do 1200 ktype=1,nstype
! ---  Loop over sources of the current type
       do 1100 ksource=1,nsource(ktype)
!
! ---    Initialize concentrations and dry/wet fluxes (SOURCE
! ---    contribution arrays)
! ---    Gridded receptors
         if(lsamp .AND. ktype.NE.9)then
            do i=1,nspec
               call INITR2D(zero,mxnxg,mxnyg,nxsam,nysam,chisam(1,1,i))
               if(mdry.eq.1)call INITR2D(zero,mxnxg,mxnyg,&
                            nxsam,nysam,dfsam(1,1,i))
               if(mwet.eq.1)call INITR2D(zero,mxnxg,mxnyg,&
                            nxsam,nysam,wfsam(1,1,i))
            enddo
         endif
!
! ---    Non-gridded receptors
         if(nrec.gt.0 .AND. ktype.NE.9)then
            call INITR2D(zero,mxrec,mxspec,nrec,nspec,chirec)
            if(mdry.eq.1)call INITR2D(zero,mxrec,mxspec,nrec,nspec,&
                         dfrec)
            if(mwet.eq.1)call INITR2D(zero,mxrec,mxspec,nrec,nspec,&
                         wfrec)
         endif
!
! ---    Complex-terrain (CTSG) receptors
         if(nctrec.gt.0 .AND. ktype.NE.9)then
            call INITR2D(zero,mxrect,mxspec,nctrec,nspec,chict)
         endif
         DO 900 II=1,NPUFFS
!
! ---    Skip puffs/slugs off the computational grid
         if(ipufcd(ii).ge.99)go to 900
         if(LAGE) then
! ---       Drop puffs older than age cutoff
            if(tmtotb(ii).GE.tsecmax) then
               ipufcd(ii)=99
               goto 900
            endif
         endif
!
! ---    Process just first new puff released this step from each
! ---    point source if modeling visible plume length (this should
! ---    be redundant)
         if(LPMODE) then
            ltest=.FALSE.
            do jfog=1,nipfog
               if(ii.EQ.ipfog(jfog)) ltest=.TRUE.
            enddo
            if(.NOT.LTEST) then
               ipufcd(ii)=99
               goto 900
            endif
         endif
         if(msource.EQ.1) then
! ---       Skip this puff if it is not from the current source type
            if(ktype.NE.isrctyp(ii)) goto 900
! ---       Skip this puff if it is not from the current source
            if(ktype.EQ.5 .OR. ktype.EQ.6) then
! ---          Line source may be isrcnum=1000+isrcnum (isrcnum<1000)
               ksource2=ksource+1000
               if(isrcnum(ii).NE.ksource .AND.&
                  isrcnum(ii).NE.ksource2) goto 900
            else
               if(isrcnum(ii).NE.ksource) goto 900
            endif
         endif
!
! ---    Using area-source approach for modeling NEW emissions from
! ---    LINE sources requires skipping the sampling step for slugs
! ---    released from all but the first "segment" of a line.  This
! ---    will be controlled by LSKIP.  Initialize it here.
         lskip=.FALSE.
!
! ---    Initialize flag for variable source parameters
         lvar=.FALSE.
!
! ---    Use puff position (or younger end of slug) to obtain initial
! ---    met grid domain and cell index
         call GETCELL('COMP_1      ','GRIDIJ',1,xpb(ii),ypb(ii),&
                      zero,zero,ixs,iys,mdoms)
! ***
         if(imesg.eq.1)print 8,ii,nn
8        format('+',6x,i6,10x,i6)
! ***
!
! ---    Track puffs in DEBUG mode, starting with puff IPFDEB1 and
! ---    ending with puff IPFDEB2
         if(ii.GE.ipfdeb1 .AND. ii.LE.ipfdeb2) then
            ldb=ldb0
         else
            ldb=.FALSE.
         endif
!
! ---    Retrieve puff/slug identification factor for this "puff"
! ---    (MFACT equal 0 indicates all are nominally puffs, but some
! ---    slugs are puffs if the slug indexing factor has been removed
! ---    from the puff code, and road sources always emit slugs)
         if(ipufcd(ii).GT.10) then
! ---       This is a SLUG
            mfact0=10
            lslug=.TRUE.
            lpuff=.FALSE.
            zpmean=0.5*(zpb(ii)+zpe(ii))
         else
! ---       This is a PUFF
            mfact0=0
            lpuff=.TRUE.
            lslug=.FALSE.
            zpmean=zpb(ii)
         endif
!
! ---    Assign puff code for this puff
         icode=ipufcd(ii)
!
! ---    Move puff identification into local and /CURRENT/ variables:
! ---       IPNO,   the puff number released from the source for the
! ---               current time step (local);
! ---       ISTYPE, the type of source (/CURRENT/); and
! ---       ISNUM,  the source number from which the puff was released
!                   (/CURRENT/)
         ipno=irlsnum(ii)
         istype=isrctyp(ii)
         isnum=isrcnum(ii)
! --- Retain IMET for roll-back capability to earlier versions, but set
! --- equal to 1 here so that /SRCTAB/ tables are always used
         imet=1
!c
!c ---    Compute met period index for source tabulations for this puff
!c ---    (stored in /CURRENT/, capped at MXMETSAV+1)
!         imet=1+(ipno-1+NINT(tmtotb(ii)/temit0(ii)))/
!     &           NINT(secdt/temit0(ii))
!         imet=MIN(imet,mxmetsav+1)
!         if(imet.LT.1) then
!            write(io6,*)
!            write(io6,*) 'FATAL ERROR in COMP: bad met period index'
!            write(io6,*) 'Expected IMET = 1 to MXMETSAV+1'
!            write(io6,*) 'Found    IMET = ',imet
!            write(io6,*) '     MXMETSAV = ',mxmetsav
!            write(io6,*)
!            write(*,*)
!            stop 'Halted in COMP -- See list file'
!         endif
! ---    Place source tabulations that apply to this puff into /SRCTAB/
         call SRCTABIN(ii,isrctyp(ii),isrcnum(ii),irlsnum(ii))
! ---    Assign emissions step index
         iqstep=iemstep(ii)
! ---    Place the platform height (downwash) into /CURRENT/ if this
! ---    puff is from a point source
         zplat=0.0
         if(istype.EQ.1) then
            zplat=zplatpt1(isnum)
         elseif(istype.EQ.2) then
            zplat=zplatpt2(isnum)
         endif
! ---    Set logical for treating boundary condition (BC) puffs
         lbcpuf=.FALSE.
         if(istype.EQ.9) lbcpuf=.TRUE.
!
! ---    Find the layer containing the puff before rise
         call ZFIND(ht0(ii),zface,nzp1,ilayer0)
! ---    Find the layer containing the puff after rise
         call ZFIND(zpmean,zface,nzp1,ilayer1)
! --- Road-slugs generated using cell ID at mid-point
         if((istype.EQ.12 .OR. istype.EQ.13) .AND. (ii.GT.noldp)) then
            xpmid=0.5*(xpb(ii)+xpe(ii))
            ypmid=0.5*(ypb(ii)+ype(ii))
            call GETCELL('COMP_1a     ','GRIDIJ',1,xpmid,ypmid,&
                         zero,zero,ixs,iys,mdoms)
         endif
!
! ---    Assign met parameters from cell
         istab=ipgt(ixs,iys,mdoms)
         istab=MIN0(istab,6)
         dpbl=AMAX1(htmix(ixs,iys,mdoms),xminzi)
         dpbl=AMIN1(dpbl,xmaxzi)
         el=xmonin(ixs,iys,mdoms)
         z0m=z0(ixs,iys,mdoms)
!
! ---    Get wind speed for puff at final rise for metfm=1,4,5
         ilayer=ilayer1
         htmet=zpmean
! ---    Use power law from wind at stacktop for metfm=2,3
         if(metfm.EQ.2 .OR. metfm.EQ.3) then
            htmet=ht0(ii)
            ztop=htmet
            zbot=htmet
         else
            call PUFFDZ(ii,icode,lpuff,istab,ilayer,htmet,dpbl,&
                        ztop,zbot)
         endif
         call ADVECT(ldb,ixs,iys,mdoms,z0m,el,dpbl,istab,ht0(ii),&
                     zbot,ztop,uadv,vadv)
         ws=SQRT(uadv**2+vadv**2)
         if(ws.LT.wscalm) ws=AMAX1(ws,ucalm)
!
! !!!
! WSVAR is computed in NEW-PUFF section
!c ---    Select wind speed for VARSAM as MAX of stack-top and Heff
!c ---    computed as in the variable emissions subroutines
!         call ADVECT(ldb,ixs,iys,mdoms,z0m,el,dpbl,istab,ht0(ii),
!     &               htmet,htmet,uvar,vvar)
!         wsvar=SQRT(uvar**2+vvar**2)
!         wsvar=AMAX1(ws0(ii),wsvar)
!
! ---    Determine the number & length of the sampling steps
         if(ii.le.noldp)then
!
! ---       OLD PUFF/SLUG
            iold=1
!
! ---       Distance that would be traveled during the time step
! ---       in local grid units;  sampling substeps will be
! ---       used as needed for smaller grid cells
            nsam=nint(ws*smxi2(mdoms)+1)
            nsam=min0(nsam,mxsam)
            xsami=1.0/float(nsam)
            nsstr=1
            tsamp=secdt*xsami
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'OLD PUFF'
         write(io6,*)'ii = ',ii,' nsam = ',nsam,' nsstr = ',nsstr
         write(io6,*)'tsamp = ',tsamp,' ws = ',ws
      endif
! ***
         ELSE
!
! ---       NEW PUFF/SLUG
            iold=0
!
! ---       Set starting sampling step number for this puff (constant
! ---       control-file emissions)
            nsstr=ipno
!
! ---       Select wind speed for VARSAM as MAX of stack-top and Heff
! ---       wind computed as in the variable emissions subroutines
            call ADVECT(ldb,ixs,iys,mdoms,z0m,el,dpbl,istab,ht0(ii),&
                     htmet,htmet,uvar,vvar)
            wsvar=SQRT(uvar**2+vvar**2)
            wsvar=AMAX1(ws0(ii),wsvar)
!
            if(istype.EQ.1) then
! ---          Revise nsam to allow PRIME module to create a dual
! ---          release (primary source and cavity source);  
! ---          nsam=newpt1(isnum) can be too large by factor of 2
               nsam=NINT(secdt/temit0(ii))
            elseif(istype.EQ.2) then
               lvar=.TRUE.
               call VARSAM(ldb,temit0(ii),tmtotb(ii),secdt,&
                           smxi1(mdoms),mxnew,wsvar,nsstr,nsam,&
                           tsamp2,tsampf)
! ---          (Note: VARSAM resets tmtotb=0.0 after use)
            elseif(istype.EQ.3) then
               nsam=newar1(isnum)
               if(lslug) then
! ---             Transfer vertex information and area of source to
! ---             /current/ arrays (new slug from POLYGON area)
                  nside=nvert1(isnum)
                  aream2=area1(isnum)
                  do iv=1,nside
                     xvert(iv)=xar1grd(iv,isnum)*dgrid
                     yvert(iv)=yar1grd(iv,isnum)*dgrid
                  enddo
               endif
            elseif(istype.EQ.4) then
               lvar=.TRUE.
               call VARSAM(ldb,temit0(ii),tmtotb(ii),secdt,&
                           smxi1(mdoms),mxnew,wsvar,nsstr,nsam,&
                           tsamp2,tsampf)
! ---          (Note: VARSAM resets tmtotb=0.0 after use)
               if(lslug) then
! ---             Transfer vertex information and area of source to
! ---             /current/ arrays (new slug from POLYGON area)
                  nside=nvert2(isnum)
                  aream2=area2(iqstep,isnum)
                  do iv=1,nside
                     xvert(iv)=xar2grd(iv,iqstep,isnum)*dgrid
                     yvert(iv)=yar2grd(iv,iqstep,isnum)*dgrid
                  enddo
               endif
            elseif(istype.EQ.5) then
               if(lslug) then
                  if(isnum.LT.1000) then
! ---                This puff is a NEW release from segment 1 of LINE
! ---                so polygon area source method for slugs is used
! ---                for entire line during the first sampling step.
! ---                Transfer "vertex" information and "area" of source
! ---                to /CURRENT/ arrays.
                     nside=4
! ---                Note: pass area of one segment
                     aream2=arline(isnum)/nseg(isnum)
                     do iv=1,nside
                        xvert(iv)=xvertl(iv,isnum)*dgrid
                        yvert(iv)=yvertl(iv,isnum)*dgrid
                     enddo
                  else
! ---                This slug is a NEW release from segment 2+ of LINE
! ---                so skip call to CALCSL for 1st sampling step
                     lskip=.TRUE.
! ---                Reset puff ID information
                     isnum=isnum-1000
                     isrcnum(ii)=isnum
! ---                Replace source tabulations that apply to this puff
! ---                in the DA file, using the updated source number
                     call SRCTABOUT(ii,isrctyp(ii),isrcnum(ii),&
                                    irlsnum(ii))
                  endif
               endif
               nsam=newln1(isnum)
            elseif(istype.EQ.6) then
               lvar=.TRUE.
               call VARSAM(ldb,temit0(ii),tmtotb(ii),secdt,&
                           smxi1(mdoms),mxnew,wsvar,nsstr,nsam,&
                           tsamp2,tsampf)
! ---          (Note: VARSAM resets tmtotb=0.0 after use)
               if(lslug) then
                  if(isnum.LT.1000) then
! ---                This puff is a NEW release from segment 1 of LINE
! ---                so polygon area source method for slugs is used
! ---                for entire line during the first sampling step.
! ---                Transfer "vertex" information and "area" of source
! ---                to /CURRENT/ arrays.
                     nside=4
! ---                Note: pass area of one segment
                     aream2=arline2(iqstep,isnum)/nseg2(iqstep,isnum)
                     do iv=1,nside
                        xvert(iv)=xvertl2(iv,iqstep,isnum)*dgrid
                        yvert(iv)=yvertl2(iv,iqstep,isnum)*dgrid
                     enddo
                  else
! ---                This slug is a NEW release from segment 2+ of LINE
! ---                so skip call to CALCSL for 1st sampling step
                     lskip=.TRUE.
! ---                Reset puff ID information
                     isnum=isnum-1000
                     isrcnum(ii)=isnum
! ---                Replace source tabulations that apply to this puff
! ---                in the DA file, using the updated source number
                     call SRCTABOUT(ii,isrctyp(ii),isrcnum(ii),&
                                    irlsnum(ii))
                  endif
               endif
            elseif(istype.EQ.7) then
               nsam=newvl1(isnum)
            elseif(istype.EQ.8) then
               lvar=.TRUE.
               call VARSAM(ldb,temit0(ii),tmtotb(ii),secdt,&
                           smxi1(mdoms),mxnew,wsvar,nsstr,nsam,&
                           tsamp2,tsampf)
! ---          (Note: VARSAM resets tmtotb=0.0 after use)
            elseif(istype.EQ.9) then
               nsam=newbc(isnum)
            elseif(istype.EQ.11) then                            
               lvar=.TRUE.                                       
               call VARSAM(ldb,temit0(ii),tmtotb(ii),secdt,      &
                           smxi1(mdoms),mxnew,wsvar,nsstr,nsam,  &
                           tsamp2,tsampf)                        
! ---          (Note: VARSAM resets tmtotb=0.0 after use)
            elseif(istype.EQ.12) then
               nsam=newrd1(isnum)
! ---          Roads use a new 'old' unattached slug along the line,
! ---          and there is no rise - use marker = 2
               iold=2
            elseif(istype.EQ.13) then
               lvar=.TRUE.                                       
               call VARSAM(ldb,temit0(ii),tmtotb(ii),secdt,      &
                           smxi1(mdoms),mxnew,wsvar,nsstr,nsam,  &
                           tsamp2,tsampf)                        
! ---          (Note: VARSAM resets tmtotb=0.0 after use)
! ---          Roads use a new 'old' unattached slug along the line,
! ---          and there is no rise - use marker = 2
               iold=2
             elseif(istype.EQ.15) then
               lvar=.TRUE.                                       
               call VARSAM(ldb,temit0(ii),tmtotb(ii),secdt,      &
                           smxi1(mdoms),mxnew,wsvar,nsstr,nsam,  &
                           tsamp2,tsampf)                        
! ---          (Note: VARSAM resets tmtotb=0.0 after use)
! ---          Sprays use a new 'old' unattached slug along the line,
! ---          and there is no rise - use marker = 2
               iold=2
       
            else
               write(io6,*)
               write(io6,*) 'FATAL:  Source type invalid! - ',istype
               write(io6,*)
               write(*,*)
               stop 'Halted in COMP -- see list file.'
            endif
!
            tsamp=temit0(ii)
!
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'NEW PUFF'
         write(io6,*)'ii = ',ii,' nsam = ',nsam,' nsstr = ',nsstr
         write(io6,*)'tsamp = ',tsamp,' IPNO = ',ipno
         write(io6,*)'ISNUM = ',isnum,' ISTYPE = ',istype
         if(LVAR) then
            write(io6,*)' tsamp2,tsampf  = ',tsamp2,tsampf
            write(io6,*)' ws,wsvar       = ',ws,wsvar
         endif
      endif
! ***
         ENDIF
! ---    Reciprocal of number of seconds in averaging period
         tavgi=1./(float(iavg*nsecdt))
! ---    Reciprocal of number of seconds in puff emission period
         temiti=1.0/temit0(ii)
! ---    Reciprocal of number of seconds in a sampling step
         tsampi=1.0/tsamp
! ---    Reciprocal of number of sampling steps in averaging period
         tfract=tsamp*tavgi
! ---    Sampling step may need to be adjusted as puff travels among
! ---    grid cells if speeds change sufficiently.  Assign the initial
! ---    sampling step interval to a reference variable.
         reftsamp=tsamp
         tleft=0.0
! ---    Do not use substeps if the number of sampling steps has already
! ---    reached the maximum imposed
         lsubsam=.TRUE.
         if(nsam.GE.mxsam) lsubsam=.FALSE.
!*****
      if(ldb)then
         write(io6,212)ii,iold,irlsnum(ii),isrcnum(ii),isrctyp(ii),&
                       ipufcd(ii)
         write(io6,214)xpb(ii),ypb(ii),sigyb(ii),sigzb(ii),xtotb(ii),&
          tmtotb(ii)
         if(ipufcd(ii).gt.10)write(io6,216)xpe(ii),ype(ii),&
          sigye(ii),sigze(ii),xtote(ii),tmtote(ii)
         write(io6,218)zimax(ii),ziold(ii),zpmean,xfinal(ii),&
          elbase(ii),ws0(ii),srat0(ii),temit0(ii)
         write(io6,220)'QU',(qu(n,ii),n=1,nspec)
         write(io6,220)'QM',(qm(n,ii),n=1,nspec)
212      format(5x,'Puff no.=',i7,2x,'IOLD=',i1,&
         ' (0=new,1=old,0=new,2=new road)',&
         2x,'IRLSNUM=',i8,2x,'ISRCNUM=',i8,2x,'ISRCTYP=',i8,&
         2x,'IPUFCD=',i3)
214      format(5x,'XPB=',f8.3,2x,'YPB=',f8.3,2x,'SIGYB=',f8.1,2x,&
          'SIGZB=',f8.1,2x,'XTOTB=',f8.1,2x,'TMTOTB=',f10.1)
216      format(5x,'XPE=',f8.3,2x,'YPE=',f8.3,2x,'SIGYE=',f8.1,2x,&
          'SIGZE=',f8.1,2x,'XTOTE=',f8.1,2x,'TMTOTE=',f10.1)
218      format(5x,'ZIMAX=',f6.0,2x,'ZIOLD=',f6.0,2x,'ZPMEAN=',f7.2,&
          2x,'XFINAL=',f7.1,2x,'ELBASE=',f6.1,2x,'WS0=',f6.2,2x,&
          'SRAT0=',f6.3,2x,'TEMIT0=',f7.1)
220      format(5x,a2,'=',8(f12.2,2x))
         write(io6,*)'TEMITI= ',temiti,' TFRACT=',tfract,' TSAMPI=',&
          tsampi,' TAVGI=',tavgi
      endif
!*****
! ---    Add mass of fresh emissions to total emissions this period
         if(imbal.EQ.1 .AND. (iold.EQ.0 .OR. iold.EQ.2)) then
            if(LBCPUF) then
               cbal='IBC'
            else
               cbal='INP'
            endif
            call MBALSUM(cbal,qm(1,ii),qu(1,ii),qm(1,ii),qu(1,ii))
         endif
! ---    Set starting puff O3 and H2O2 (new puff)
         if(iold.EQ.0 .OR. iold.EQ.2) then
            ldb2=.false.
            chioz=0.0
            chih2o2=0.0
            if(mchem.EQ.1 .OR. mchem.EQ.3 .OR. mchem.EQ.4 .OR.&
               mchem.EQ.6 .OR. mchem.EQ.7) then
               call GETOZ(ixs,iys,mdoms,ldb2,chioz)
               if(maqchem.EQ.1) call GETH2O2(ixs,iys,mdoms,ldb2,chih2o2)
            endif
            puffo3(ii)=chioz
            puffh2o2(ii)=chih2o2
         endif
! ---    Initialize sampling time to zero
         tsampsum=0.0
!
!----------------------------------------------------------
! ---    LOOP OVER SAMPLING STEPS
!----------------------------------------------------------
! ---    Reset number of tracking steps logged for this puff
         itrk=0
         DO 800 KSAM=NSSTR,NSAM
!
! ---    Special case for variable releases: reset the reference
! ---    timestep to tsamp2 after the first sampling step for fresh
! ---    emissions to allow larger sampling steps after a short
! ---    emission period
         if(LVAR) then
            if(iold.EQ.0 .OR. iold.EQ.2) then
               if(ksam.EQ.(nsstr+1)) reftsamp=tsamp2
               if(ksam.EQ.nsam)      reftsamp=tsampf
            endif
         endif
!
! ---    Initialize sampling step variables
         tsamp=reftsamp+tleft
! ---    Time left in current sampling step
         tleft=tsamp
!
! ---    Identify slugs that are attached to source and puffs that are
! ---    at the source at the start of the step: if puff/slug was
! ---    generated this time-step (IOLD=0), and if this is the first 
! ---    sampling step, then a slug is attached to the source for the 
! ---    sampling step, and a puff touches the source at the start
! ---    (ifree=0).
         ifree=1
         if(ksam .EQ. nsstr) ifree=iold
!
! ---    Re-entry for possible substep
500      continue
         tsampi=1.0/tsamp
         tfract=tsamp*tavgi
! --- 6.42_x1.1 140521
! ---    Drop any puffs that have zero mass now (rapid removal cases)
         qall=0.0
         do ns=1,nspec
            qall=qall+qu(ns,ii)+qm(ns,ii)
         enddo
         if(qall.LE.0.0) then
            ipufcd(ii)=99
            goto 801
         endif
! ---    Transfer met. variables at start of step needed to compute
! ---    trajectory.  Use the puff position at the end of the previous
! ---    step, or the average slug position at the end of previous step.
         if(lpuff) then
! ---       Set last position of PUFF
            xold=xpb(ii)
            yold=ypb(ii)
            zold=zpb(ii)
         else
! ---       Set last position of SLUG center
            xold=0.5*(xpb(ii)+xpe(ii))
            yold=0.5*(ypb(ii)+ype(ii))
            zold=0.5*(zpb(ii)+zpe(ii))
         endif
!
! ---    Met grid domain and cell index
         call GETCELL('COMP_2      ','GRIDIJ',1,xold,yold,&
                      zero,zero,ixs,iys,mdoms)
!
! ------------------------------------
! ---    Determine the advective winds
! ------------------------------------
         istab=ipgt(ixs,iys,mdoms)
         istab=MIN0(istab,6)
         dpbl=AMAX1(htmix(ixs,iys,mdoms),xminzi)
         dpbl=AMIN1(dpbl,xmaxzi)
         el=xmonin(ixs,iys,mdoms)
         z0m=z0(ixs,iys,mdoms)
!
         htmet=zold
         ilayer=ilayer1
!
! ---    Compute the height of the bottom and top of the puff/slug
! ---    at the end of previous step
         call PUFFDZ(ii,icode,lpuff,istab,ilayer,htmet,dpbl,&
                     ztop,zbot)
!
! ---    Compute the wind components for advection
         call ADVECT(ldb,ixs,iys,mdoms,z0m,el,dpbl,istab,ht0(ii),&
                     zbot,ztop,uadv,vadv)
! ---    Compute corresponding speed and direction
         ws=SQRT(uadv**2+vadv**2)
!
! ---    Account for wind profile on transport during rise
! ---    (applies to CALMET or PROFILE winds only, if Gaussian)
         if((MOD(icode,2).EQ.1).AND.&
            (xtotb(ii).LT.xfinal(ii))) then
            if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
! ---          Fill /CURRENT/ variables needed in GRISE
               ipnum=ii
               zfrise=zfinal(ii)-(ht0(ii)-stipdw(ii))
               xfrise=xfinal(ii)
               xshift=xshift0(ii)
! ---          Vertical spread from centerline ht at start of step
               dzb=ztop-htmet
! ---          Obtain average wind for step
               call RISEWIND(ldb,lpuff,ncount,dzb,ii,ixs,iys,mdoms,z0m,&
                             el,dpbl,istab,tsamp,uadv,vadv,ws)
            endif
         endif
!
! ---    Treat calm conditions by imposing a minimum on vadv
         if(ws.LT.wscalm) then
            lcalm=.TRUE.
            if(ABS(vadv).LT.ucalm) vadv=ucalm
            ws=AMAX1(ws,ucalm)
         else
            lcalm=.FALSE.
         endif
!
! ---    FOG analysis in PLUME mode requires advection to be shifted
! ---    to align with receptor directions
         if(LPMODE) call FOGWIND(io6,ldb,ws,uadv,vadv)
!
! ---    Compute final advection wind direction (not flow vector)
         wd=270.-rad*atan2(vadv,uadv)
         wd=amod(wd,360.)
! ---    Transport during sampling step into MET grid domain with
! ---    smaller cells requires revised step-size
         mdstep=mdoms
         if(ngrid.GT.1) then
! ---       Advance TSAMP seconds (outermost met domain grid units)
            xtest=xold+uadv*tsamp*dgridi
            ytest=yold+vadv*tsamp*dgridi
! ---       Test domain resolutions along step
            call MDOMCROSS(ldb,xold,yold,xtest,ytest,mdtest)
            if(xgridm(mdtest).LT.xgridm(mdstep)) mdstep=mdtest
         endif
! ---    Apply constraint on step-size:  Slugs that are being emitted
! ---    during the first sampling step are not altered (they are NOT
! ---    free of the source)
         fstep=1.0
         if((LPUFF .OR. (LSLUG .AND. ifree.NE.0)) .AND. LSUBSAM) then
! ---       Step distance in local grid units should not exceed XSAMLEN
            fstep=ws*tsamp*xgridmi(mdstep)*xsamleni
            if(fstep.GT.1.0) then
! ---          Substep with shorter sampling time is needed
               if(fstep.GE.2.0 .OR. ksam.LT.nsam) then
! ---             Take a full XSAMLEN this sub-step
                  tsamp=tsamp/fstep
                  tsampi=1.0/tsamp
                  tfract=tsamp*tavgi
               else
! ---             This is the last step and 1<fstep<2, so divide it
! ---             into two equal timesteps
                  tsamp=0.5*tsamp
                  tsampi=1.0/tsamp
                  tfract=tsamp*tavgi
               endif
! ---          Shorter TSAMP could change mean transport wind if puff
! ---          is still rising! (revisit RISEWIND)
               if(xtotb(ii).LT.xfinal(ii)) then
                  if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
                     call RISEWIND(ldb,lpuff,ncount,dzb,ii,ixs,iys,&
                            mdoms,z0m,el,dpbl,istab,tsamp,uadv,vadv,ws)
                  endif
               endif
            endif
         endif
         tleft=tleft-tsamp
!*****
      if(ldb)then
         write(io6,240)ksam,nsam
         write(io6,242)'BEGINNING Qs',(qu(n1,ii),n1=1,nspec)
         write(io6,244)(qm(n2,ii),n2=1,nspec)
         write(io6,245)xold,yold,ixs,iys,mdoms,mdstep,ilayer
         write(io6,246)ws,wd,dpbl
         write(io6,*)' REFTSAMP      TSAMP      TLEFT    (for substep)'
         write(io6,'(3f10.2)') reftsamp,tsamp,tleft
240      format(/10x,'Sampling step: ',i4,' of ',i4)
242      format(10x,a11,1x,'QU=  ',5(f11.1,1x))
244      format(22x,'QM=  ',5(f11.1,1x))
245      format(10x,'XOLD=',f7.3,' YOLD=',f7.3,' IXS=',i4,' IYS=',i4,&
          ' MDOMS=',i3,' MDSTEP=',i3,' ILAYER=',i3)
246      format(10x,'WS=',f7.3,' WD=',f7.3,' DPBL=',f7.1)
      endif
!*****
!
! ---    Compute the DX, DY and new coordinates of the puff (in
! ---    met. grid units).  Note that when slugs are modeled, the
! ---    new and old DX,DY refer to the distance traveled by the
! ---    newer and older ends of the slug during the step.  Furthermore,
! ---    the new X,Y represents the new position of the slug center.
! ---    First, in meters.
         dxoldm=uadv*tsamp
         dyoldm=vadv*tsamp
         dxnewm=dxoldm
         dynewm=dyoldm
         distm=ws*tsamp
! ---    Now, in met. grid units (outermost met domain)
         dxmold=dxoldm*dgridi
         dymold=dyoldm*dgridi
         xnew=xold+dxmold
         ynew=yold+dymold
! ---    Mid-pt of step
         dxmmid=0.5*dxmold
         dymmid=0.5*dymold
! ---    Set active met cell index for the mid-point of the step
         call GETCELL('COMP_3      ','GRIDIJ',1,xold,yold,&
                       dxmmid,dymmid,ixmid,iymid,mdommid)
! ---    Set active met cell index for the end of the step
         call GETCELL('COMP_4      ','GRIDIJ',1,xnew,ynew,&
                       zero,zero,ixend,iyend,mdomend)
!
! ---    Add contribution of vertical divergence to sigma-z if
! ---    Gaussian puff/slug enters a cell with acute vertical
! ---    divergence during step
         if(LDIV .AND. mod(icode,2).EQ.1) then
! ---       Find average w-divergence across puff using "wind" sub.
            do kz=1,nz
               wdivs(kz)=wdiv(ixs,iys,kz,mdoms)
               wdive(kz)=wdiv(ixend,iyend,kz,mdomend)
            enddo
            call ADVWND(wdivs,wdive,zface,nzp1,zbot,ztop,divs,dive)
            wdivmx=AMAX1(divs,dive)
            if(wdivmx.GT.cdiv(1)) then
               ftrans=1.0
               if(wdivmx.LT.cdiv(2)) ftrans=(wdivmx-cdiv(1))/&
                                            (cdiv(2)-cdiv(1))
               szdivfac=1.+ftrans*wdivmx*tsamp
               sigzb(ii)=sigzb(ii)*szdivfac
               sigze(ii)=sigze(ii)*szdivfac
            else
               szdivfac=0.0
            endif
         else
            szdivfac=0.0
         endif
!*****
         if(ldb .AND. szdivfac.GT.0.0) then
            write(io6,*) ' -----  Div triggered; szdivfac=',szdivfac
            write(io6,*) ' -----  Dw/Dz averaged over puff'
            write(io6,*) '        ixs,iys,domain     =',ixs,iys,mdoms
            write(io6,*) '        ixend,iyend,domain =',ixend,iyend,&
                                                        mdomend
            do kz=1,nz
               write(io6,*)' iz,wdiv(start),wdiv(end) =',kz,wdivs(kz),&
                                                            wdive(kz)
            enddo
            write(io6,*) '              ftrans,wdivmx =',ftrans,wdivmx
         endif
!*****
! ---    CAP
! ---    Place a cap on the size of the sigma-z at the start of the step
! ---    to keep it from growing (much) beyond a very large value
! ---    This keeps sigma within the range of the host curves
         if(lszcap) then
            sigzb(ii)=AMIN1(sigzb(ii),szcap_m)
            sigze(ii)=AMIN1(sigze(ii),szcap_m)
            if(ldb) then
               write(io6,*) ' -----  SIGMA-Z CAP (m): ',szcap_m
               write(io6,*) ' -----  sigzb,sigze = ',&
                                     sigzb(ii),sigze(ii)
            endif
         endif
! ---    Set met/dispersion data for the mid-point of the step
         istab=ipgt(ixmid,iymid,mdommid)
         istab=min0(istab,6)
         dpbl=amax1(htmix(ixmid,iymid,mdommid),xminzi)
         dpbl=amin1(dpbl,xmaxzi)
         el=xmonin(ixmid,iymid,mdommid)
         ustr=ustar(ixmid,iymid,mdommid)
         wstr=wstar(ixmid,iymid,mdommid)
         z0m=z0(ixmid,iymid,mdommid)
! ---    Assign Plume Path Coefficient (even if not used)
         ppcoef=ppc(istab)
!
! ---    Extract IRU value (rural=0, urban=1) at mid-point
         iru=0
         if(ilandu(ixmid,iymid,mdommid) .GE. iurb1 .AND.&
            ilandu(ixmid,iymid,mdommid) .LE. iurb2) iru=1
! ---    Set met cell index for subsequent use (TIBL may change index)
         ixmet=ixmid
         iymet=iymid
         idmet=mdommid
! ---    Test for sub-grid TIBL treatment
! ---------------------------------------
         ltibl=.FALSE.
         if(MSGTIBL.EQ.1) then
! ---       Store current sampling time into MASTER variable since
! ---       sub-grid TIBL module may alter it
            tsamp0=tsamp
            itibl=0
            if(zitibl(ii).LE.0.) then
! ---          Puff not currently interacting with TIBL
! ---          Check for Positive Radiation at start of step
! frr (09/01 ) 2D QSW in CALMET
               if(i2dmet.EQ.1) then
                  qsws=qsw2d(ixs,iys,mdoms)
               elseif(i2dmet.EQ.0) then
                  qsws=qswss(nears(ixs,iys,mdoms),mdoms)
               else
                  write(*,*)'Subr. COMP:  Invalid I2DMET = ',i2dmet
                  stop
               endif
               if(qsws.GT.0.0) then
                  ltibl=.TRUE.
! ---             Complete the check for initiating TIBL this step
! ---             and set parameters if TIBL is needed
! ---             Use ifree=0 if it is 2
                  if(ifree.EQ.2) then
                     ifree01=0
                  else
                     ifree01=ifree
                  endif
                  if(LTIBL) call TIBLON(ldb,ifree01,xold,yold,xnew,&
                                        ynew,ixs,iys,mdoms,ixend,&
                                        iyend,mdomend,htmet,ltibl)
               endif
               if(LTIBL) then
! ---             Store TIBL ht for next sampling step for this puff
                  if(ilandu(ixend,iyend,mdomend).GE.iwat1 .AND.&
                     ilandu(ixend,iyend,mdomend).LE.iwat2) then
! ---                Still in a water cell (must re-enter this section)
                     zitibl(ii)=-1.
                  else
                     zitibl(ii)=htibl(ntibl)
                  endif
               endif
            else
! ---          Puff currently interacting with TIBL (ZITIBL>0)
! ---          Do not override current land use cell index (TIBLGRO)
               ixr=-1
               iyr=-1
               mdr=1
! ---          Current heat flux must exceed 5 W/m**2 for further TIBL
! frr (09/01 ) 2D QSW in CALMET
               if(i2dmet.EQ.1) then
                  temps=temp2d(ixs,iys,mdoms)
               elseif(i2dmet.EQ.0) then
                  temps=tempss(nears(ixs,iys,mdoms),mdoms)
               else
                  write(*,*)'Subr. COMP:  Invalid I2DMET = ',i2dmet
                  stop
               endif
! ---          Test for heat flux using met data at start of step, 
! ---          and form based on u-star and M-O length
! ---          to conform to what is done within TIBLGRO
               ustars=AMAX1(0.0,ustar(ixs,iys,mdoms))
               hbyrcp=-ustars**3*temps/(9.8*0.4*xmonin(ixs,iys,mdoms))
               if(hbyrcp.LE.0.004) then
! ---             Turn off TIBL calculations due to small heat flux
                  ltibl=.FALSE.
                  zitibl(ii)=-1.
               elseif(zitibl(ii).GE.dpbl) then
! ---             Turn off TIBL calculations if TIBL exceeds current ZI
                  ltibl=.FALSE.
                  zitibl(ii)=-1.
               else
! ---             Compute TIBL arrays for this step
                  ltibl=.TRUE.
                  call TIBLGRO(ldb,xold,yold,xnew,ynew,ixr,iyr,mdr,&
                               zitibl(ii),-.001,-1.)
! ---             Store TIBL ht for next sampling step for this puff
                  if(ilandu(ixend,iyend,mdomend).GE.iwat1 .AND.&
                     ilandu(ixend,iyend,mdomend).LE.iwat2) then
! ---                Enters a water cell next step
                     zitibl(ii)=-1.
                  else
                     zitibl(ii)=htibl(ntibl)
                  endif
               endif
            endif
         endif
! ---    Sub-grid TIBL Sub-step
! ---    Set up data for current substep within this sampling step
600      if(LTIBL) then
            itibl=itibl+1
! ---       Set sampling time for this sub-step
            tsamp=tstibl(itibl)*tsamp0
            tsampi=1.0/tsamp
            tfract=tsamp*tavgi
! ---       Redefine step
            distm=ws*tsamp
            dxoldm=uadv*tsamp
            dyoldm=vadv*tsamp
            dxnewm=dxoldm
            dynewm=dyoldm
            dxmold=dxoldm*dgridi
            dymold=dyoldm*dgridi
            xnew=xold+dxmold
            ynew=yold+dymold
! ---       Replace surface layer properties with those
! ---       consistent with TIBL computation
            ixmet=ixtibl(itibl)
            iymet=iytibl(itibl)
            idmet=mdtibl(itibl)
            istab=ipgt(ixmet,iymet,idmet)
            istab=min0(istab,6)
            el=xmonin(ixmet,iymet,idmet)
            ustr=ustar(ixmet,iymet,idmet)
            wstr=wstar(ixmet,iymet,idmet)
            z0m=z0(ixmet,iymet,idmet)
            iru=0
            if(ilandu(ixmet,iymet,idmet) .GE. iurb1 .AND.&
               ilandu(ixmet,iymet,idmet) .LE. iurb2) iru=1
! ---       Assign Plume Path Coefficient (even if not used)
            ppcoef=ppc(istab)
! ---       Use actual TIBL height for dpbl
            dpbl=amax1(htibl(itibl),xminzi)
            dpbl=amin1(dpbl,xmaxzi)
         endif
!
! ---    Transfer surface station met. variables (flag=2)
         call EXMET(2,ixmet,iymet,idmet,tempk,qsw,irh,rhoair,&
                    cldcov,coszen)
! ---    Set land (1) water (2) index for SVMIN,SWMIN
         ilw=1
         if(ilandu(ixmet,iymet,idmet).GE.iwat1 .AND.&
            ilandu(ixmet,iymet,idmet).LE.iwat2) ilw=2
!
! ---    Transfer ozone concentration for chemistry
         ldb2=.false.
         if(mchem.EQ.1 .OR. mchem.EQ.3 .OR. mchem.EQ.4&
                       .OR. mchem.EQ.6 .OR. mchem.EQ.7)&
            call GETOZ(ixmet,iymet,idmet,ldb2,chioz)
!
! ---    Transfer H2O2 concentration for aqueous chemistry
         ldb2=.false.
         if(maqchem.EQ.1)&
            call GETH2O2(ixmet,iymet,idmet,ldb2,chih2o2)
!
! ---    Set vertical mass distribution for this step
! ---    The reflecting lid returned (HLID) is used for subsequent calc
! ---    of the vertical distribution of mass; the original height
! ---    (DPBL) is retained for all other uses
         call VMASS(ii,mfact0,nspec,istab,dpbl,hlid,icode)
! ---    Place maximum mix ht for this distribution into local variable
         hlidmax=zimax(ii)
!
! ---    For puffs above mixed layer, use stability class "JSUP" to
! ---    calculate SIGMA Y and SIGMA Z growth (if JSUP=0, or if ISTAB
! ---    is 5 or 6, use the mixed-layer stability class ISTAB)
         jdstab=istab
         if(jsup.GT.0 .AND. istab.LT.5 .AND. htmet.GT.dpbl) jdstab=jsup
!
! ---    Set turbulence velocities and activate dispersion options
! ---    (Note: idopty and idoptz will be carried in /CURRENT/)
         if(mdisp.EQ.1 .OR. mdisp.EQ.2 .OR. mdisp.EQ.5) then
! ---       Extract wind speed (m/s) at Zi
            call ZFIND(dpbl,zface,nzp1,ilayerzi)
            call WINDSET(dpbl,ilayerzi,ixmet,iymet,idmet,&
                         z0m,el,dpbl,istab,uatzi,wdzi)
            call TURBSET(ldb,ustr,el,wstr,jdstab,dpbl,z0m,htmet,&
                         uatzi,ws,wd,ixmet,iymet,idmet,ilw,&
                         tsigv,tsigw,idopty,idoptz)
         else
            idopty=mdisp
            idoptz=mdisp
            tsigv=svmin(jdstab,ilw)
            tsigw=swmin(jdstab,ilw)
         endif
! ---    Set PDF variables
         if(mpdf.EQ.1) call PDFVARS(ldb,io6,mdisp,icode,tsigw,wstr,&
                               ustr,bidfnl(ii),fb(ii),dpbl,ht0(ii),ws)
!
! ---    Set local Brunt-Vaisala frequency
         if(LCALGRD) then
! ---       Use temperature (K) gradient across puff depth (DELZ)
! ---       Minimum pot. temp. gradient is 0.005 K/m
            delz=amax1((ztop-zbot),10.)
            call PTLAPS(ixmet,iymet,idmet,zbot,ptgrad0,delz,ptgrad,t0)
            bvf=sqrt(9.80665*ptgrad/t0)
         else
! ---       Use default potential temperature lapse rate with the
! ---       surface temperature from the nearest station
            if(istab.LE.4) then
               ptgrad=0.0
            elseif(istab.EQ.5) then
               ptgrad=amax1(ptg(1),ptgrad0)
            else
               ptgrad=amax1(ptg(2),ptgrad0)
            endif
            bvf=sqrt(9.80665*ptgrad/tempk)
         endif
!
!*****
      if(ldb)then
         write(io6,*)
         write(io6,242)'AFTER VMASS ',(qu(n1,ii),n1=1,nspec)
         write(io6,244)(qm(n2,ii),n2=1,nspec)
         write(io6,*)'old,new Puff/Slug Code = ',ipufcd(ii),icode
         write(io6,*)'             zbot,ztop = ',zbot,ztop
         write(io6,*)'             uadv,vadv = ',uadv,vadv
         write(io6,*)'       xnew,ynew,distm = ',xnew,ynew,distm
         write(io6,*)' ixmet,iymet,idmet,iru = ',ixmet,iymet,idmet,iru
         write(io6,*)'dpbl,hlid,istab,jdstab = ',dpbl,hlid,istab,jdstab
         write(io6,*)'            bvf,ppcoef = ',bvf,ppcoef
      endif
!*****
!
! ---    Store current puff code into array (may change in VMASS)
         ipufcd(ii)=icode
!
! -----------------------------------------------------
! ---    Finish Setting Puff/Slug parameters
! -----------------------------------------------------
         if(LBCPUF) then
! ---       Boundary Condition (BC) PUFFS FIRST
            call SETBCPUF(ii,tsamp,distm,ws,xnew,ynew,&
                          ixmet,iymet,idmet,ldb,uavg)
         elseif(lpuff) then
!
! ---       NOW PUFFS
! ---       Compute puff position and sigmas, and update stored data.
            call SETPUF(ii,tsamp,distm,jdstab,el,tsigv,tsigw,bvf,ws,&
                        dpbl,xnew,ynew,iru,ixmet,iymet,idmet,ldb,&
                        uavg,lcalm)
!
         else
!
! ---       NOW SLUGS
! ---       Compute position of slug-ends at start and end of step,
! ---       compute sigmas at these points, and update stored data.
! ---       Use ifree=1 if it is not 0
            ifree01=MIN(1,ifree)
            call SETSLG(ii,ifree01,tsamp,iru,jdstab,el,tsigv,tsigw,&
                        bvf,ws,dpbl,dxoldm,dyoldm,dxnewm,dynewm,&
                        ixmet,iymet,idmet,ldb,uavg,lcalm)
         endif
!
!
! --------------------------------------------------------
! ---    Account for Removal and/or Conversion Processes
! --------------------------------------------------------
!
! ---    Define mass at START of sampling step (after vertical
! ---    redistribution but before any removal or conversion)
         do ipl=1,nspec
! ---       Set initial pollutant mass (g) (PUFF)
            qold(ipl)=qm(ipl,ii)
            qoldup(ipl)=qu(ipl,ii)
! ---       Set starting emissions (g/s) for each pollutant (SLUG)
            qbrate(ipl)=qm(ipl,ii)*temiti
            qbrateup(ipl)=qu(ipl,ii)*temiti
! ---       Set contribution to total average mass over step
            qavgt(ipl)=0.5*(qm(ipl,ii)+qu(ipl,ii))
! ---       Store initial masses for mass balance option
            q1sfc(ipl)=qm(ipl,ii)
            q1upr(ipl)=qu(ipl,ii)
         enddo
!
! ---    Chemical Transformation
! ------------------------------
         if(mchem.ge.1)then
            zmsl=zpb(ii)+elev(ixmid,iymid,mdommid)
            
! ---       Get cloud water mixing ratio at the same cell used for
! ---       the precip rate (ixs,iys,mdoms)
! --- 6.42_x1.1 140521
! ---       Extract complete cloud liquid water profile at this cell
            if(mlwc.EQ.1) then
               qcupij=qcup(ixs,iys,mdoms)
               zuptopij=zuptop(ixs,iys,mdoms)
               zupbotij=zupbot(ixs,iys,mdoms)
               do kz=1,nz
                  qcij(kz)=qc3d(ixs,iys,kz,mdoms)
! ---             Extract temperature and pressure profiles here also
                  tkij(kz)=tmet(ixs,iys,kz,mdoms)
                  patmij(kz)=0.5*(pmet(ixs,iys,kz,mdoms)+&
                                  pmet(ixs,iys,kz+1,mdoms))
               enddo
            else
               qcupij=-999.
               zuptopij=-999.
               zupbotij=-999.
               do kz=1,nz
                  qcij(kz)=-999.
               enddo
            endif
            if(ldb) then
               write(io6,*)
               write(io6,*)'Cloud LWC at cell(i,j,md) = ',ixs,iys,mdoms
               write(io6,*)'qcup(g/kg),zbot(m),ztop(m)= ',qcupij,&
                                                      zupbotij,zuptopij
               write(io6,*)'qcij(g/kg) =',(qcij(kz),kz=1,nz)
               write(io6,*)'tkij(K)    =',(tkij(kz),kz=1,nz)
               write(io6,*)'patmij(atm)=',(patmij(kz),kz=1,nz)
            endif
            o3p=puffo3(ii)
            h2o2p=puffh2o2(ii)
! ---       Set background NH3 for puff layer (may change in CHEM)
            if(mchem.EQ.6 .OR. mchem.EQ.7) bcknh3 = bcknh3z(ilayer1)
! --- 6.42_x1.1 140521
            call CHEM(nspec,mchem,maqchem,nhrind,tsamp,jsup,qsw,&
                      tempk,irh,rhoair,istab,hlid,o3p,h2o2p,chioz,&
                      chih2o2,qcij,tkij,patmij,qcupij,zupbotij,zuptopij,&
                      cldcov,rmm(ixs,iys,mdoms),coszen,zmsl,ldb)
            puffo3(ii)=o3p
            puffh2o2(ii)=h2o2p
            if(imbal.EQ.1) then
               cbal='TRN'
               if(LBCPUF) cbal='TBC'
               call MBALSUM(cbal,q1sfc,q1upr,qm(1,ii),qu(1,ii))
            endif
         endif
!*****
      if(ldb)then
         write(io6,242)'AFTER CHEM  ',(qu(n1,ii),n1=1,nspec)
         write(io6,244)(qm(n2,ii),n2=1,nspec)
      endif
!*****
!
! ---    Wet deposition
! ---------------------
         if(mwet.eq.1)then
            do ispec=1,nspec
               q01wet(ispec,1)=qm(ispec,ii)+qu(ispec,ii)
               qratew(ispec,1)=q01wet(ispec,1)*temiti
! ---          Store initial masses for mass balance option
               q1sfc(ispec)=qm(ispec,ii)
               q1upr(ispec)=qu(ispec,ii)
            enddo
!
            call WET(ixs,iys,mdoms,nspec,tsamp,ii,tempk,ldb,xlam,&
                     fracwet)
!
            do ispec=1,nspec
               q01wet(ispec,2)=qm(ispec,ii)+qu(ispec,ii)
               qratew(ispec,2)=q01wet(ispec,2)*temiti
            enddo
!
            if(imbal.EQ.1) then
               cbal='WET'
               if(LBCPUF) cbal='WBC'
               call MBALSUM(cbal,q1sfc,q1upr,qm(1,ii),qu(1,ii))
            endif
         endif
!*****
      if(ldb)then
         write(io6,242)'AFTER WET   ',(qu(n1,ii),n1=1,nspec)
         write(io6,244)(qm(n2,ii),n2=1,nspec)
      endif
!*****
!
! ---    Dry deposition
! ---------------------
         nspart=0
         if(mdry.eq.1)then
            do ispec=1,nspec
               q01dry(ispec,1)=qm(ispec,ii)
! ---          Store initial masses for mass balance option
               q1sfc(ispec)=qm(ispec,ii)
               q1upr(ispec)=qu(ispec,ii)
!               write(*,*)'IDRYFLG: ',idryflg(ispec)
               if(idryflg(ispec).eq.2) then
                 nspart=nspart+1
                 if(idropflg(ispec).eq.1) then
                   gdiam(nspart)=dropdia(ii)
                   gsig(nspart)=0
                 endif
               endif
!               write(*,*)'COMP gdiam: ',gdiam
            enddo
! --- Perform setup operations for dry deposition
           zgrid1=0.5*(zface(1)+zface(2))
           call DRYI(zgrid1,ldb)
!           write(io6,*)'DRYI CALL'
!           write(*,*)'COMP gdiam: ',gdiam
!
            call DRY(icode,ixs,iys,mdoms,nhrind,tsamp,ii,&
                     dpbl,hlid,tempk,qsw,ppcoef,ldb,&
                     vd,vdpvd,fracdry)
!
            do ispec=1,nspec
               q01dry(ispec,2)=qm(ispec,ii)
            enddo
!
            if(imbal.EQ.1) then
               cbal='DRY'
               if(LBCPUF) cbal='DBC'
               call MBALSUM(cbal,q1sfc,q1upr,qm(1,ii),qu(1,ii))
            endif
         endif
!*****
      if(ldb)then
         write(io6,242)'AFTER DRY   ',(qu(n1,ii),n1=1,nspec)
         write(io6,244)(qm(n2,ii),n2=1,nspec)
      endif
!*****
!
! ---    Define mass at END of sampling step (after removal & chemistry)
         do ipl=1,nspec
! ---       Set "new" pollutant mass (g) (PUFF)
            qnew(ipl)=qm(ipl,ii)
            qnewup(ipl)=qu(ipl,ii)
! ---       Set ending emissions (g/s) for each pollutant (SLUG)
            qerate(ipl)=qm(ipl,ii)*temiti
            qerateup(ipl)=qu(ipl,ii)*temiti
! ---       Add contribution to total average mass over step
            qavgt(ipl)=qavgt(ipl)+0.5*(qm(ipl,ii)+qu(ipl,ii))
         enddo
! ---    Increment timestamp to the end of this (sub)step
         call DEDAT(ndathrb,iyrss,ijulss,ihrss)
         isecss=nsecb
         tsampsum=tsampsum+tsamp
         call INCRS(io6,iyrss,ijulss,ihrss,isecss,NINT(tsampsum))
         idathrss=iyrss*100000+ijulss*100+ihrss
! ---    Write puff/slug data at sampling step to TRACKING file
         if(ldb) call TRACK(ii,idathrss,isecss,dpbl,jdstab)
! ---    Write sampling-step information to the PFTRAK.DAT file
         if(ipftrak.EQ.2) then
! ---       Puff-tracking output during sampling step
            itrk=itrk+1
            call TRAKSAM(ii,itrk,idathrss,isecss)
         endif
!
! ---    Update mass flux totals
         if(imflx.EQ.1) call MFLXCMP(ldb,lslug,nspec,qavgt)
!
! ----------------------------
! ---    Sampling of puff/slug
! ----------------------------
!
! ---    Reset LSKIP if a new slug is now free of the source
         if(lskip .AND. ifree.NE.0) lskip=.FALSE.
!
! ---    Call appropriate sampling function (BC, PUFF, or SLUG)
!
         if(LPMODE) then
! ---       Process plume data for FOG Model PLUME Mode output
            call PLMFOG(xold,yold,jdstab,&
                        iru,tsigv,tsigw,el,bvf,ws,wd,dpbl,nspec,&
                        qm(1,ii),qu(1,ii),icode,hlid,temiti,&
                        ldb,lcalm)
! ---       Drop this puff (sampled 1 time as a plume)
            ipufcd(ii)=99
! ---       Log mass dropped
            if(imbal.EQ.1) then
               cbal='OUT'
               call MBALSUM(cbal,qm(1,ii),&
                            qu(1,ii),qm(1,ii),qu(1,ii))
            endif
            go to 801
            
         elseif(LBCPUF) then
! ---       Nearest BC puff sampling function
            call CALCBC(xold,yold,xnew,ynew,qold,qnew,qoldup,qnewup,&
                        icode,hlid,hlidmax,diam0(ii),vdpvd,nspec,&
                        q01wet,q01dry,vd,fracwet,tsampi,rbc0,ldb)
         elseif(lpuff) then
! ---       Integrated puff sampling function
! ---       PUFFR is the puff radius in computational grid units at
! ---       the END of the step
            puffr=rconst*sye1
            call CALCPF(xold,yold,xnew,ynew,ppcoef,jdstab,iru,&
                        tsigv,tsigw,el,bvf,uavg,dpbl,qold,qnew,qoldup,&
                        qnewup,icode,hlid,hlidmax,puffr,vdpvd,nspec,&
                        q01wet,q01dry,vd,fracwet,tsampi,tfract,&
                        ldb,lcalm,lclip)
!
         elseif(.not.LSKIP) then
! ---       Slug sampling function
            call CALCSL(tsamp,tfract,nspec,qbrate,qerate,qbrateup,&
                        qerateup,qratew,ht0(ii),elbase(ii),ppcoef,&
                        jdstab,symin,szmin,iru,hlid,hlidmax,tsigv,&
                        tsigw,el,bvf,dpbl,xlam,vd,vdpvd,uavg,ldb,&
                        lcalm,lclip)
         endif
! -----------------------------
! ---    CTSG sampling function
! -----------------------------
! ---  NOTE 1: Deposition fluxes are not computed at CTSG receptors,
! ---          but any change in the mass in a puff/slug during the
! ---          step is reflected in the concentrations at these
! ---          receptors insofar as the average mass is used here.
! ---  NOTE 2: CTSG algorithm is not designed for CALM conditions.  A
! ---          treatment for CTSG receptors has been added to CALCPF
! ---          and CALCSL for CALM conditions, so skip call to CTSG.
! ---  NOTE 3: CTSG does not interpret puff codes;  screen out codes
! ---          3 & 13 at the start (all material above CBL)
         lctsg=.FALSE.
         if(nctrec.GT.0 .AND. .not.LCALM .AND. .not.LBCPUF) then
            if(mod(icode,10).NE.3) lctsg=.TRUE.
         endif
         if(LCTSG) then
!
! ---       Define AVERAGE mass during sampling step for each pollutant
! ---       but do not incorporate the near-surface correction factor
! ---       (vd'/vd) because center of puff may impinge on terrain
            do ipl=1,nspec
! ---          Set the average pollutant mass (g) below mixing height
               qavg(ipl)=0.5*(qold(ipl)+qnew(ipl))
            enddo
!
            if(lpuff) then
! ---          PUFFS:
!
! ---          Set puff radius (squared) at end of step to identify
! ---          receptors that can be affected (met grid units)
               puffr2=(rconst*sye1)**2
!
! ---          Identify hills that may be affected by puff this step
               do ih=1,nhill
                  scanr2=puffr2+hillr2(ih)
                  call PFSCRN(xold,yold,xnew,ynew,hilldat(1,ih),&
                              hilldat(2,ih),scanr2,d2,ldohill(ih))
               enddo
!
               ipin=ii
               qin=qunit
! ---          (Note: ttb is age at START step, but tmtotb has already
! ---           been reset to the age at the start of the NEXT step)
               ttb=tmtotb(ii)-tsamp
!
! ---          Set receptor elevation to stack-base to assure no CTADJ
! ---          modifications in call to PUFRECS here
               recht=elbase(ii)
!
! ---          Loop over complex terrain receptors
               do irin=1,nctrec
!
                  if(ldohill(ihill(irin))) then
! ---                Get receptor-specific puff information
                     xrm=xrct(irin)*dgrid
                     yrm=yrct(irin)*dgrid
                     zrm=0.0
                     call PUFRECS(lclip,xrm,yrm,zrm,jdstab,iru,&
                                  tsigv,tsigw,el,bvf,uavg,dpbl,&
                                  recht,ppcoef,lcalm,trec,frac,&
                                  syr,szr,zgrise,zpr,zrpole,&
                                  rfacsq,idrop)
! ---                PRIME downwash:  skip 'dropped' receptors in
! ---                                 cavity zone
                     if(idrop.EQ.0) then
!
! ---                Use receptor-specific puff ht in CTSG call
                     call CTSG(ldb,irin,ipin,qin,ws,wd,hlid,xb1,yb1,&
                               zgrise,jdstab,elrect(irin),ihill(irin),&
                               tsamp,ttb,szb1,sze1,syb1,sye1,szr,syr,&
                               szmin,symin,frac,concct,cflat)
!
! ---                Scale concentrations for each species
                     ctemp=concct*tfract
                     do js=1,nspec
                        chict(irin,js)=chict(irin,js)+qavg(js)*ctemp
                     enddo
                     endif
                  endif
               enddo
!
            else
! ---          SLUGS:
! ---          Determine the number of plugs needed to simulate slug
! ---          (a plug is a puff-like segment of a slug)
               if(iage.EQ.0) then
! ---             Use slug information at end of step for fresh release
                  call SLG2PUF(xe1,xe2,ye1,ye2,sye1,sye2,&
                               numpuff,qfac,xfac)
               else
! ---             Use slug information at beginning of step
                  call SLG2PUF(xb1,xb2,yb1,yb2,syb1,syb2,&
                               numpuff,qfac,xfac)
               endif
! ---  Debug Writes
       if(ldb) then
          write(io6,*)
          write(io6,*)'SLG2PUF results...........'
          write(io6,*)'Slug #, numpuff     = ',ii,numpuff
          write(io6,*)'qfac,xfac           = ',qfac,xfac
          write(io6,*)
       endif
!
! ---          Loop over plugs from younger end to older end of slug
               qin=qfac*qunit
! ---          Skip first plug of a fresh slug since it has no timestep
               nfirst=1
               if(iage.EQ.0) nfirst=2
               do np=nfirst,numpuff
!
! ---             Assign slug properties to the plug
                  ipin=ii+np
                  fac=1.0-xfac*(np-1)
! ---             Set age at START of step, and duration of the step
! ---             (Note: tmtotb and tmtote are ages of the young[2] and
! ---              old[1] ends of the slug at the END of the step)
                  ttb=tmtote(ii)+fac*(tmtotb(ii)-tmtote(ii))-tsamp
                  if(ttb.LT.0.0) then
! ---                Must be a freshly released slug!
                     stepin=ttb+tsamp
                     ttb=0.0
                     tadj=stepin/tsamp
                  else
                     stepin=tsamp
                     tadj=1.0
                  endif
! ---             Set coordinates at start and end of step (meters)
                  xpbm=xb1+fac*(xb2-xb1)
                  ypbm=yb1+fac*(yb2-yb1)
                  xpem=xe1+fac*(xe2-xe1)
                  ypem=ye1+fac*(ye2-ye1)
! ---             Set coordinates at start and end of step (met grid)
                  xbmg=xpbm*dgridi
                  ybmg=ypbm*dgridi
                  xemg=xpem*dgridi
                  yemg=ypem*dgridi
! ---             Compute sigmas at start of step (meters)
                  call PLGRECS(lclip,fac,xpbm,ypbm,jdstab,iru,&
                               tsigv,tsigw,el,bvf,uavg,dpbl,&
                               frac,sy1in,sz1in,zgrise)
! ---             Compute sigmas at end of step (meters)
                  call PLGRECS(lclip,fac,xpem,ypem,jdstab,iru,&
                               tsigv,tsigw,el,bvf,uavg,dpbl,&
                               frac,sy2in,sz2in,zgrise)
!
! ---             Set plug radius (squared) at end of step to identify
! ---             receptors that are affected (met grid units)
                  puffr2=(rconst*sy2in)**2
!
! ---             Identify hills that may be affected by plug this step
                  do ih=1,nhill
                     scanr2=puffr2+hillr2(ih)
                     call PFSCRN(xbmg,ybmg,xemg,yemg,hilldat(1,ih),&
                                 hilldat(2,ih),scanr2,d2,ldohill(ih))
                  enddo
!
! ---  Debug Writes ---
       if(ldb) then
          write(io6,*)
          write(io6,*)'Plug Loop.................'
          write(io6,*)'Slug #, Plug #, Q   = ',ii,np,qin
          write(io6,*)'fac, tstart, tstep  = ',fac,ttb,stepin
       endif
! ---  Debug Writes ---
!
! ---             Loop over complex terrain receptors
                  do irin=1,nctrec
! ---                Screen out receptors that are too far away
                     if(ldohill(ihill(irin))) then
! ---                   Get receptor-specific plug information
                        xrm=xrct(irin)*dgrid
                        yrm=yrct(irin)*dgrid
                        call PLGRECS(lclip,fac,xrm,yrm,jdstab,iru,&
                                     tsigv,tsigw,el,bvf,uavg,dpbl,&
                                     frac,syr,szr,zgrise)
!
! ---                   Use receptor-specific puff ht in CTSG call
                        call CTSG(ldb,irin,ipin,qin,ws,wd,hlid,xpbm,&
                                  ypbm,zgrise,jdstab,elrect(irin),&
                                  ihill(irin),stepin,ttb,sz1in,sz2in,&
                                  sy1in,sy2in,szr,syr,szmin,symin,frac,&
                                  concct,cflat)
!
! ---                   Scale concentrations for each species
                        ctemp=concct*tfract*tadj
                        do js=1,nspec
                           chict(irin,js)=chict(irin,js)+qavg(js)*ctemp
                        enddo
                     endif
                  enddo
               enddo
            endif
         endif
! -----------------------------
! ---    Clean up for next step
! -----------------------------
! ---    Update puff code (may change in sampling routines)
         ipufcd(ii)=icode
!
! ---    Reset ZIMAX for next step (used for uniform distribution)
         zimax(ii)=amax1(zimax(ii),hlid)
! ---    Update the reflecting lid height from "last" step
         ziold(ii)=hlid
! ---    Check if new position is off computational grid (reset code)
         if(lpuff) then
! ---       Test center of PUFF:
            if(xnew.lt.xb.or.xnew.gt.xe.or.&
               ynew.lt.yb.or.ynew.gt.ye)then
               ipufcd(ii)=99
! ---          Log mass dropped
               if(imbal.EQ.1) then
                  cbal='OUT'
                  if(LBCPUF) cbal='OBC'
                  call MBALSUM(cbal,qm(1,ii),&
                               qu(1,ii),qm(1,ii),qu(1,ii))
               endif
               go to 801
            endif
         else
! ---       Test both ends of SLUG:
            if(xpe(ii).lt.xb.or.xpe(ii).gt.xe.or.&
               ype(ii).lt.yb.or.ype(ii).gt.ye) then
! ---          Older end of slug is off the grid!
               if(xpb(ii).lt.xb.or.xpb(ii).gt.xe.or.&
                  ypb(ii).lt.yb.or.ypb(ii).gt.ye) then
! ---             Younger end is also off grid!
                  ipufcd(ii)=99
! ---             Log mass dropped
                  if(imbal.EQ.1) call MBALSUM('OUT',qm(1,ii),&
                                           qu(1,ii),qm(1,ii),qu(1,ii))
                  go to 801
               endif
            endif
         endif
! ---    Check if a slug should be converted to a puff
         if(lslug) then
! ---       This is a SLUG!
            testsy=amin1(sye2,sye1)
            testd=sl2pf*sqrt((xe1-xe2)**2+(ye1-ye2)**2)
            if(testsy.GE.testd) then
! ---          Convert to PUFF!
               ipufcd(ii)=ipufcd(ii)-mfact0
               mfact0=0
               icode=ipufcd(ii)
               lpuff=.TRUE.
               lslug=.FALSE.
               zpb(ii)=0.5*(zpb(ii)+zpe(ii))
               sigyb(ii)=0.5*(sigyb(ii)+sigye(ii))
               sigzb(ii)=0.5*(sigzb(ii)+sigze(ii))
               xtotb(ii)=0.5*(xtotb(ii)+xtote(ii))
               tmtotb(ii)=0.5*(tmtotb(ii)+tmtote(ii))
               xpb(ii)=xnew
               ypb(ii)=ynew
!*****
      if(ldb)then
         write(io6,*)'SLUG converted to PUFF at end of KSAM= ',ksam
         write(io6,*)'     SIGYB, SIGZB        = ',sigyb(ii),sigzb(ii)
         write(io6,*)'     Slug Length * SL2PF = ',testd
      endif
!*****
            endif
         endif 
! ---    End of TIBL sub-step loop
         if(LTIBL) then
            if(itibl.LT.ntibl) then
               xold=xnew
               yold=ynew
               goto 600
            else
! ---          Restore sampling step variables
               tsamp=tsamp0
               tsampi=1.0/tsamp
               tfract=tsamp*tavgi
            endif
         endif
         if(LSUBSAM) then
! ---       Check for incomplete sampling step
            if(tleft.GE.1.0 .AND. ksam.EQ.nsam) then
! ---          Last sampling step, so use up remaining sampling time
! ---          by taking another substep
               tsamp=tleft
               goto 500
            elseif(tleft.GT.tsamp) then          
! ---          Time remaining in this step exceeds the time used in
! ---          the current sub-step, so take another substep.
               tsamp=tleft
               goto 500
            endif
! ---       Otherwise, continue on to the next sampling step, and add
! ---       whatever time is left to the reference sampling step time
         endif
800      continue
! ---    Puff (ii) has been sampled, and is still in domain
! ---    Add mass to total for end of period
         if(imbal.EQ.1) then
            cbal='TOT'
            if(LBCPUF) cbal='SBC'
            call MBALSUM(cbal,qm(1,ii),qu(1,ii),qm(1,ii),qu(1,ii))
         endif
801      continue
! ---    End of timestep for current puff
         if(ipftrak.EQ.1) then
! ---       Puff-tracking output at end of timestep
            itrk=1
            call TRAKSAM(ii,itrk,ndathre,nsece)
         endif
         if(ipftrak.GT.0) call TRAKOUT(ii,ndathrb,nsecb,ndathre,nsece)
! ---    Close puff loop
900      CONTINUE
! ---  All puffs for this source have been sampled
! ---  Add contribution to Total CHIFLX arrays (mode 1)
       ichiflx=1
       if(ktype.NE.9) call TCHIFLX(ichiflx)
       if(msource.EQ.1) then
! ---     Perform source contribution output
!c ---     Add contribution to Total CHIFLX arrays (mode 1)
!          ichiflx=1
!          if(ktype.NE.9) call TCHIFLX(ichiflx)
! ---     Print results and/or store source contribution records on disk
          isrcmode=1
          if(ktype.NE.9 .AND. MOD(nn,iavg).EQ.0)&
                         call OUTPUT(nn,isrcmode,ktype,ksource)
       endif
! ---  Close source loop
1100   continue
! --- Close source-type loop
1200  continue
! --- Swap Total CHIFLX arrays into standard CHIFLX arrays (mode 2)
      ichiflx=2
!      if(msource.EQ.1) call TCHIFLX(ichiflx)
      call TCHIFLX(ichiflx)
! --- Sum contribution from the boundary condition puff that passes
! --- nearest each receptor this sampling step
      if(nbc.GT.0) call SUMBC
! --- Do Fog-related processing on concentration and temperature excess
! --- fields and report results
      if(MFOG.GT.0 .AND. MOD(nn,iavg).EQ.0) call FOGOUT     
!
! --- Print results and/or store results on disk
      ktype=0
      ksource=1
      isrcmode=0
      if(MOD(nn,iavg).EQ.0) call OUTPUT(nn,isrcmode,ktype,ksource)
! --- Write model run data for this period to restart file if selected
      if(mrestart.GE.2) then
         if(nn.EQ.iarlg) then
! ---       Restart data are written at close of run
            call RESTARTO
         elseif(nrespd.GT.0) then
! ---       Restart data are written every NRESPD periods
            if(MOD(nn,nrespd).EQ.0)call RESTARTO
         endif
      endif
! --- Increment date and time (except on last hour of run)
      if(nn.lt.iarlg) call TIMEUP(1)
!
1000  continue
!
      return
      end
!----------------------------------------------------------------------
      subroutine vmass(ii,mfact0,nspec,istab,dpbl,hlid,icode)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521             VMASS
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Assigns vertical distribution of puff/slug for current
!               sampling step (puff code, mass)
!
! --- UPDATE
!
! --- V5.741-V6.42_x1.1   140521 : Modify logic for zero mass in upper
!                                  layer to recognize case where all
!                                  mass is lost in both layers
!
! --- V5.4-V5.741   040913  (DGS): MTILT option only: increase test for
!                                  well-mixed limit 
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.0     980731  (DGS): recast 2-layer logic for Gaussian to
!                                  allow gradual fumigation
! --- V4.0-V5.0     971107  (DGS): replace ZFINAL puff/slug height array
!                                  with ZPB,ZPE arrays
!
! --- INPUTS:
!            II - integer - Current puff/slug index
!        MFACT0 - integer - ICODE offset between puffs and slugs
!                           (0 = PUFF;  10 = SLUG)
!         NSPEC - integer - Number of active species in run
!         ISTAB - integer - Stability class in surface layer
!          DPBL - real    - Depth of planetary boundary layer (current
!                           mixing height, m)
!         ICODE - integer - Code for vertical distribution
!                                1 = Puff within DPBL & Gaussian
!                                2 = Puff within DPBL & uniform
!                                3 = Puff above DPBL & Gaussian
!                                4 = Puff above DPBL & uniform
!                                5 = Puff currently above DPBL
!                                    (but previously below) & Gaussian
!                                6 = Puff currently DPBL layer
!                                    (but previously below) & uniform
!                               11 = Slug within DPBL & Gaussian
!                               12 = Slug within DPBL & uniform
!                               13 = Slug above DPBL & Gaussian
!                               14 = Slug above DPBL & uniform
!                               15 = Slug currently above DPBL
!                                    (but previously below) & Gaussian
!                               16 = Slug currently above DPBL
!                                    (but previously below) & uniform
!
!     Common block /FLAGS/ variables:
!            MTILT
!     Common block /PUFF/ variables:
!            ZIMAX(mxpuff), ZIOLD(mxpuff), SIGYB(mxpuff), SIGZB(mxpuff),
!            ZPB(mxpuff), QU(mxspec,mxpuff), QM(mxspec,mxpuff)
!     Common block /SLUG/ variables:
!            ZPE(mxpuff), SIGYE(mxpuff), SIGZE(mxpuff)
!
!     Parameters:
!            MXPUFF, MXSPEC, IO6
!
! --- OUTPUT:
!          HLID - real    - Modified mixing height: reflecting lid
!                           for vertical mass distribution (m)
!         ICODE - integer - Code for vertical distribution
!     Common block /PUFF/ variables:
!            QU(mxspec,mxpuff), QM(mxspec,mxpuff)
!
!
! --- VMASS called by: COMP
! --- VMASS calls:     none
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'flags.puf'
      include 'puff.puf'
      include 'slug.puf'
!
      logical lfatal
!
! --- Set unlimited mixing height (m)
      data ziunlm/1.0e04/
      data rtpiby2/1.2533141/, one/1.0/, half/0.5/, zero/0.0/
! --- Ratio of sigma-z to zi for well-mixed limit
      data szbyzi0/1.6/
! --- Factor to increase sz/zi0 for MTILT option
      data ftiltmix/2.0/
! --- Trap invalid puff codes here
      lfatal=.FALSE.
      if(icode.EQ.0) lfatal=.TRUE.
      if(icode.GE.7 .AND. icode.LE.10) lfatal=.TRUE.
      if(icode.GE.17 .AND. icode.LT.99) lfatal=.TRUE.
      if(LFATAL) then
         write(io6,*)'VMASS:  Invalid puff code = ',icode
         write(*,*)
         stop 'Halted in VMASS -- see list file.'
      endif
! --- Condition old mixing heights from last step
      qumass=zero
      do ipl=1,nspec
         qumass=qumass+qu(ipl,ii)
      enddo
! --- 6.42_x1.1 140521
      qmmass=zero
      do ipl=1,nspec
         qmmass=qmmass+qm(ipl,ii)
      enddo
      if(qumass.EQ.zero .AND. qmmass.GT.zero) then
! ---    No upper layer, so assign last lid height to max height to be
! ---    consistent with 1-layer structure
         zimax(ii)=ziold(ii)
      endif
! --- Set mean height of puff/slug
      if(mfact0.EQ.0) then
! ---    Puff
         zmean=zpb(ii)
      else
! ---    Slug
         zmean=half*(zpb(ii)+zpe(ii))
      endif
! --- Initialize lid height to PBL height
      hlid=dpbl
! --- If Gaussian, determine the top of equivalent uniform profile
! --- Ignore BID contribution to sigma-z
      if(mod(icode,2).EQ.1) then
         if(mfact0.EQ.0) then
! ---       Puff
            szmax=sigzb(ii)
            szmin=sigzb(ii)
            zmean=zpb(ii)
         else
! ---       Slug
            szmax=amax1(sigzb(ii),sigze(ii))
            szmin=amin1(sigzb(ii),sigze(ii))
            zmean=half*(zpb(ii)+zpe(ii))
         endif
! ---    Equivalent top of puff without influence of lid
         ptop0=zmean+rtpiby2*szmax
      endif
! --- Set the ratio of sigma-z to zi for well-mixed limit
      szbyzi=szbyzi0
      if(mtilt.GT.0) szbyzi=szbyzi0*ftiltmix
!
! ---------------------------------------------------
! --- Set vertical distribution for mixed puffs first
! ---------------------------------------------------
!
! --- CODE 4:  puff/slug above mixed layer since release, & Uniform
      if(mod(icode,10).EQ.4) then
! ---    Keep current icode until mixed layer reaches center of puff
! ---    then switch to icode=2,12 with no change to hlid, and shift
! ---    all mass to main layer
         if(hlid.GE.zmean) then
            icode=2+mfact0
            do ipl=1,nspec
               qm(ipl,ii)=qu(ipl,ii)+qm(ipl,ii)
               qu(ipl,ii)=zero
            enddo
         endif
! --- CODE 2,6:  puff/slug Uniform & within present/past mixed layer
      elseif(mod(icode,2).EQ.0) then
         if(hlid.GE.zimax(ii)) then
! ---       Current mixing height has exceeded ZIMAX, so there is a
! ---       1-layer structure:  transfer mass
            do ipl=1,nspec
               qm(ipl,ii)=qu(ipl,ii)+qm(ipl,ii)
               qu(ipl,ii)=zero
            enddo
            zimax(ii)=hlid
            ziold(ii)=hlid
            icode=2+mfact0
         else
! ---       Puff/slug mass is both above and below current mixed
! ---       layer. Calculate any transfer of mass between layers
! ---       Sign of the transfer is (+) if lower layer gains mass
! ---       from the upper layer, and (-) if upper layer gains mass.
            if(hlid.GE.ziold(ii)) then
! ---          1: lower layer growing into upper layer --
               dqfac=(hlid-ziold(ii))/(zimax(ii)-ziold(ii))
               if(dqfac.GT.one) dqfac=one
               do ipl=1,nspec
                  dq=qu(ipl,ii)*dqfac
                  qu(ipl,ii)=qu(ipl,ii)-dq
                  qm(ipl,ii)=qm(ipl,ii)+dq
               enddo
            else
! ---          2: upper layer growing into lower layer --
               qmtotal=zero
               do ipl=1,nspec
                  qmtotal=qmtotal+qm(ipl,ii)
               enddo
               if(qmtotal.EQ.zero) then
! ---             Special case of qm=0 (reset hlid to ziold)
                  hlid=ziold(ii)
               else
! ---             Mass in surface layer decreases as layer shrinks
                  dqfac=(hlid-ziold(ii))/ziold(ii)
                  if(dqfac.LT.-one) dqfac=-one
                  do ipl=1,nspec
                     dq=qm(ipl,ii)*dqfac
                     qu(ipl,ii)=qu(ipl,ii)-dq
                     qm(ipl,ii)=qm(ipl,ii)+dq
                  enddo
               endif
            endif
            ziold(ii)=hlid
            icode=6+mfact0
         endif
!
! -------------------------------------------------
! --- Set vertical distribution for Gaussian puffs
! -------------------------------------------------
!
! --- Treat STABLE surface layer case first
!
      elseif(istab.GE.5) then
! ---    Adjust puff code 3 case to code 1
         if(mod(icode,10).EQ.3) then
! ---       Place puff within unlimited stable layer
            icode=1+mfact0
            hlid=ziunlm
            zimax(ii)=ziunlm
            ziold(ii)=ziunlm
            do ipl=1,nspec
               qm(ipl,ii)=qu(ipl,ii)+qm(ipl,ii)
               qu(ipl,ii)=zero
            enddo           
         else
! ---       Make the old mixing height current to either preserve
! ---       unlimited mixing, or to retain structure from last hour
            hlid=ziold(ii)
         endif
! ---    Test for well-mixed limit and reset if found
         if((szmin/hlid).GT.szbyzi) then
! ---       Puff is mixed: change code
            icode=icode+1
         endif
! --- Now treat cases with a non-stable surface layer
!
! --- CODE 1:  puff/slug Gaussian & within current mixed layer
      elseif(mod(icode,10).EQ.1) then
! ---    Determine reflecting lid height for Gaussian distribution.
         if(hlid.LT.zmean) then
! ---       Puff above surface layer, so persist last structure and
! ---       reset code to 5
            hlid=ziold(ii)
            icode=5+mfact0
         elseif(hlid.LT.ziold(ii)) then
! ---       Mixing lid is decreasing, but puff remains within surface
! ---       layer, so do not allow reflecting lid to decrease below 
! ---       level at which puff first interacts strongly with the lid.
! ---       Test based on top-hat distribution
            ptop=AMIN1(ptop0,ziold(ii))
            hlid=AMAX1(hlid,ptop)
         endif
! ---    Reset zimax and ziold to the new lid height
         zimax(ii)=hlid
         ziold(ii)=hlid
! ---    Test for well-mixed limit
         if((szmin/hlid).GT.szbyzi) then
! ---       Puff is MIXED
            if(hlid.GT.dpbl) then
! ---          Create 2 layers: restore current mixing ht and
! ---          distribute mass above/below current mixing height
               icode=6+mfact0
               dqfac=(dpbl-hlid)/hlid
               if(dqfac.LT.-one) dqfac=-one
               do ipl=1,nspec
                  dq=qm(ipl,ii)*dqfac
                  qu(ipl,ii)=qu(ipl,ii)-dq
                  qm(ipl,ii)=qm(ipl,ii)+dq
               enddo
               ziold(ii)=dpbl
               hlid=dpbl
            else
! ---          There is only one layer
               icode=2+mfact0
            endif
         endif
! --- CODE 3:  puff/slug above mixed layer since release, & Gaussian
      elseif(mod(icode,10).EQ.3) then
! ---    Keep current values until mixed layer reaches centerline ht,
! ---    then pass mass below lid to qm, with reflections from hlid
         if(hlid.GE.zmean) then
! ---       Fraction of mass above lid is  0 < fup < 0.5
            if(hlid.GE.ptop0) then
! ---          All mass is in surface layer now --- One layer
               fup=zero
               icode=1+mfact0
               zimax(ii)=hlid
               ziold(ii)=hlid
            else
! ---          Two layers
               fup=0.5*(ptop0-hlid)/(ptop0-zmean)
               icode=5+mfact0
               zimax(ii)=ptop0
               ziold(ii)=hlid
            endif
! ---       Transfer mass from upper layer to surface layer
            do ipl=1,nspec
               qtot=qu(ipl,ii)+qm(ipl,ii)
               qu(ipl,ii)=fup*qtot
               qm(ipl,ii)=qtot-qu(ipl,ii)
            enddo
! ---       Test for well-mixed limit
            if((szmin/hlid).GT.szbyzi) icode=icode+1
         endif
! --- CODE 5:  puff/slug Gaussian with mass above & below mixing ht
      elseif(mod(icode,10).EQ.5) then
         if(hlid.GE.zimax(ii)) then
! ---       Current mixing height has exceeded ZIMAX, so there is a
! ---       1-layer structure again:  transfer mass
            do ipl=1,nspec
               qm(ipl,ii)=qu(ipl,ii)+qm(ipl,ii)
               qu(ipl,ii)=zero
            enddo
            icode=1+mfact0
            zimax(ii)=hlid
            ziold(ii)=hlid
! ---       Test for well-mixed limit
            if((szmin/hlid).GT.szbyzi) icode=icode+1
         elseif(hlid.LT.zmean) then
! ---       Surface layer is below the center of the puff; retain
! ---       old vertical structure
            hlid=ziold(ii)
! ---       Test for well-mixed limit in lower layer
            if((szmin/hlid).GT.szbyzi) icode=icode+1
         elseif(hlid.LT.ziold(ii)) then
! ---       Mixing lid is decreasing, but puff remains in surface layer;
! ---       do not allow reflecting lid to decrease in height below
! ---       level at which puff first interacts strongly with the lid.
! ---       Test based on top-hat distribution
            ptop=AMIN1(ptop0,ziold(ii))
            hlid=AMAX1(hlid,ptop)
! ---       Reset ziold to the new lid height, retain zimax (2 layers)
            ziold(ii)=hlid
! ---       Test for well-mixed limit in lower layer
            if((szmin/hlid).GT.szbyzi) icode=icode+1
         else
! ---       Mixing lid is rising, but remains less than ZIMAX
! ---       Allow growing layer to entrain mass that had partially
! ---       penetrated the top of the layer.
! ---       Sign of the transfer is (+) as lower layer gains mass
            dqfac=(hlid-ziold(ii))/(zimax(ii)-ziold(ii))
            if(dqfac.GT.one) dqfac=one
            do ipl=1,nspec
               if(qu(ipl,ii).GT.zero) then
                  dq=qu(ipl,ii)*dqfac
                  qu(ipl,ii)=qu(ipl,ii)-dq
                  qm(ipl,ii)=qm(ipl,ii)+dq
               endif
            enddo
! ---       Reset ZIOLD
            ziold(ii)=hlid
! ---       Test for well-mixed limit in lower layer
            if((szmin/hlid).GT.szbyzi) icode=icode+1
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine split(ldbhr,lresplit,nspec,xb,yb,xe,ye,imbal,&
                       numsplty,numspltz)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 101111             SPLIT
!                D. Strimaitis
!
! --- PURPOSE:  Create "new" puffs/slugs by splitting old puffs/slugs
!               in the vertical and old puffs in the horizontal when
!               appropriate for tracking effects of wind shear
!
! --- UPDATE
! --- V6.301-V6.402 101111  (DGS): Update GETCELL arguments
! --- V6.3-V6.301   100827  (DGS): horizontal splitting parameters vary
!                                  by met grid domain
! --- V5.72-V6.3    100212  (DGS): add nested CALPUFF grids
! --- V5.4-V5.72    031017  (DGS): ISRCTYP replaces IPUFID
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!                   000602  (DGS): add horizontal splitting
!
! --- INPUTS:
!         LDBHR - logical - Debug output produced (T/F)
!      LRESPLIT - logical - Reset ISPLIT to 1 to allow further splits
!                           in the vertical (T = Yes, F = No)
!         NSPEC - integer - Number of species
!         XB,YB - real    - SW corner of computational grid 
!                           (met. grid units)
!         XE,YE - real    - NE corner of computational grid 
!                           (met. grid units)
!         IMBAL - integer - Flag for tracking mass balance (1=tracked)
!
!     Common block /COMPARM/ variables:
!           NSPLIT, ROLDMAX, ZISPLIT,
!           NSPLITH, CNSPLITH(mxspec),
!           SYSPLITH(mxmetdom), SHSPLITH(mxmetdom)
!     Common Block /GRID/ variables:
!           DGRIDI, NZ, NZP1, ZFACE(mxnzp1)
!    Common block /METHR/ variables:
!           UMET(mxnx,mxny,mxnz,mxmetdom),VMET(mxnx,mxny,mxnz,mxmetdom)
!     Common block /PUFF/ variables:
!           NPUFFS, ZIOLD, ZIMAX, SIGYB
!           ISRCTYP, IPUFCD, XPB, YPB, QM, QU
!     Parameters:
!           MXPUFF, MXSPEC, MXNX, MXNY, MXNZ, MXMETDOM, MXNZP1, IO6
!
! --- OUTPUT:
!      NUMSPLTY - integer - Number of old puffs that are split in the
!                           horizontal 
!                           (initial value set to 0 before call)
!      NUMSPLTZ - integer - Number of puffs that are split in the
!                           vertical 
!                           (initial value set to 0 before call)
!
! --- SPLIT called by:  COMP
! --- SPLIT calls:      ROLLDN, SWAP, PUFFDZ, ADVWND, MBALSUM,
!                       GETCELL
!----------------------------------------------------------------------
! ---       Conditions for splitting a puff in the horizontal:
!               1)  Must be a puff (not slug)
!               2)  Must NOT be a 'boundary puff'
!               3)  Sigma-y (without BID) must exceed SYSPLITH
!               4)  Speed shear across puff must exceed SHSPLITH
!               5)  Average concentration of at least one species must
!                   exceed the corresponding CNSPLITH
! ---       Conditions for splitting a puff in the vertical:
!               1)  Puff/Slug code 6/16
!               2)  ISPLIT=1 (puff/slug has not been split this cycle)
!               3)  Mix. ht last step is at least a min. value ZISPLIT
!               4)  Mix. ht last step is a "small" fraction of ZIMAX,
!                   no larger than ROLDMAX
!               5)  Puff has finite mass in surface layer (qm)
! ---------------------------------------------------------------------
!
      include 'params.puf'
      include 'comparm.puf'
      include 'grid.puf'
      include 'methr.puf'
      include 'puff.puf'
      logical ldbhr,lresplit,lpuff
      logical lconc, lshear
      real qqu(mxspec),qqm(mxspec)
      real u(mxnz),v(mxnz)
      real sin45(8),cos45(8)
      character*3 cbal
! --- Set puff logical to true (horizontal splitting)
      data lpuff/.TRUE./
! --- Set the center-puff mass augmentation factor Fc/n
! --- for a radial spread factor of fd=2.4 (scales new sigma-y)
! ---     expval=EXP(-0.5*fd*fd)
! ---     fcbyn=(0.25-expval)/(1.0-expval)
      data fd/2.4/, fcbyn/0.205395/
      data twopi/6.2831853/
      data zero/0.0/
      data cbal/'OUT'/
! --- Set array of sines and cosines in 45 degree steps for the
! --- shear calculations
      data sin45/ 0.0,  0.707107,  1.0,  0.707107,&
                  0.0, -0.707107, -1.0, -0.707107/
      data cos45/ 1.0,  0.707107,  0.0, -0.707107,&
                 -1.0, -0.707107,  0.0,  0.707107/
! --- Set angle and mass weight for radial array of split puffs
      rnm1=FLOAT(nsplith-1)
      angle=twopi/rnm1
      weight=(1.-fcbyn)/rnm1
! ------------------------------------------
! --- Address HORIZONTAL splitting first
! ------------------------------------------
! --- Make room for split puffs: If too many puffs are needed,
! --- remove puffs that are off the grid and "roll down" arrays
! --- (this also applies to the DA file of tabulated arrays)
      np0=npuffs
      np=npuffs*(nsplith)
      if(np.GT.mxpuff) then
         call ROLLDN(np0)
! ---    Reset current number of puffs after roll-down
         np0=npuffs
      endif
! --- Loop over all existing puffs/slugs
! --------------------------------------
      do ii=1,np0
! ---  Must be an active PUFF (no slugs)
       if(ipufcd(ii).LT.11) then
!
! ---    Process only puffs released from explicit sources
! ---    (not boundary)
         if(isrctyp(ii).NE.9) then
! ---      Identify met grid domain and cell for puff position
           call GETCELL('SPLIT_1     ','GRIDIJ',1,xpb(ii),ypb(ii),&
                        zero,zero,ix,iy,md)
! ---      Apply puff-size filter
           if(sigyb(ii).GT.sysplith(md)) then
              lconc=.FALSE.
              if(ipufcd(ii).NE.4)then
! ---            (Code 4 is above mixed layer since release, and
! ---             uniform: do not split it)
! ---            Approximate puff concentration
! ---            Use 'neutral' form of PUFFDZ for puff top/bottom 
! ---            (ilayer is a dummy here)
                 iistab=4
                 iilayer=3
                 call PUFFDZ(ii,ipufcd(ii),lpuff,iistab,iilayer,&
                             zpb(ii),ziold(ii),ztop,zbot)
                 volinv=1.0/((ztop-zbot)*twopi*sigyb(ii)*sigyb(ii))
                 do is=1,nspec
                    if(((qm(is,ii)+qu(is,ii))*volinv).GT.&
                                           cnsplith(is)) then
                       lconc=.TRUE.
                       goto 101
                    endif
                 enddo
              endif
!
! ---         Apply minimum concentration filter
101           if(lconc) then
! ---           Set ring radius for obtaining shear (met. grid 1 units)
! ---           [Use the distance based on the split-puff sigmas]
! ---           Set sigma-y for the new puffs
                synew=0.5*sigyb(ii)
! ---           Set radial distance for puffs about original location
                rmet=fd*synew*dgridi
! ---           Use advection components at center
! ---           Transfer U,V to 1-D arrays
                do iz=1,nz
                   u(iz)=umet(ix,iy,iz,md)
                   v(iz)=vmet(ix,iy,iz,md)
                enddo
                call ADVWND(u,v,zface,nzp1,zbot,ztop,u0,v0)
! ---           Check enlargement rate against shear criterion
                lshear=.FALSE.
! ---           Search at 45-deg intervals starting at 0 (North)
                do iang=1,8
                   x=xpb(ii)+rmet*sin45(iang)
                   y=ypb(ii)+rmet*cos45(iang)
                   call GETCELL('SPLIT_2     ','GRIDIJ',1,x,y,&
                                zero,zero,ix,iy,md)
! ---              Transfer U,V to 1-D arrays
                   do iz=1,nz
                      u(iz)=umet(ix,iy,iz,md)
                      v(iz)=vmet(ix,iy,iz,md)
                   enddo
                   call ADVWND(u,v,zface,nzp1,zbot,ztop,ui,vi)
                   erate=sin45(iang)*(ui-u0)+cos45(iang)*(vi-v0)
                   if(erate.GT.shsplith(md)) then
                      lshear=.TRUE.
                      goto 201
                   endif
                enddo
!
! ---           Apply minimum wind shear filter
201             if(lshear) then
!                 --------------------
! ---               Split this PUFF
!                 --------------------
! *******
      if(ldbhr) then
         write(io6,*)
         write(io6,*) 'SPLIT --- initial values for puff ',ii
         write(io6,*) ' sigyb,ipufcd,ziold,zimax = '
         write(io6,*) sigyb(ii),ipufcd(ii),ziold(ii),zimax(ii)
         write(io6,*) 'ztop,zbot (m) = ',ztop,zbot
         write(io6,*) '1/Volume (1/m^3) = ',volinv
         write(io6,*) 'q-upper = ',(qu(is,ii),is=1,nspec)
         write(io6,*) 'q-lower = ',(qm(is,ii),is=1,nspec)
         write(io6,*)
         write(io6,*) 'Split criteria: shear'
         write(io6,*) 'iang,ix,iy,md = ',iang,ix,iy,md
         write(io6,*) 'xpb,ypb,u0,v0 = ',xpb(ii),ypb(ii),u0,v0
         write(io6,*) '  xi,yi,ui,vi = ',x,y,ui,vi
         write(io6,*) '   erate(m/s) = ',erate
         write(io6,*)
      endif
! ********
!
! ---             Check to see if roll-down was adequate
                  if(npuffs+nsplith.gt.mxpuff)then
                     write(io6,*) 'FATAL ERROR in subr. SPLIT'
                     write(io6,*)'Too many puffs on grid ---'
                     write(io6,*)' NPUFFS (old puffs) = ',npuffs
                     write(io6,*)' NSPLITH = ',nsplith
                     write(io6,*)' MXPUFF = ',mxpuff
                     write(*,*)
                     stop 'Halted in SPLIT -- see list file.'
                  endif
!
! ---             Swap puff data from original puff to splits
                  do i=1,nsplith-1
                     j=npuffs+i
                     call SWAP(j,ii)
                  enddo
! ---             Loop over split puffs (first puff is at old location)
! ---             and alter the sigma-y, location, and masses
! ---             Center puff:
                  sigyb(ii)=synew
                  do is=1,nspec
                     qm(is,ii)=fcbyn*qm(is,ii)
                     qu(is,ii)=fcbyn*qu(is,ii)
                  enddo
! ---             Ring puffs:
                  do i=2,nsplith
! ---                Set puff index (jj)
                     jj=npuffs+i-1
! ---                Revise puff attributes
                     sigyb(jj)=synew
                     xpb(jj)=xpb(ii)+rmet*SIN(angle*(i-2))
                     ypb(jj)=ypb(ii)+rmet*COS(angle*(i-2))
                     do is=1,nspec
                        qm(is,jj)=weight*qm(is,jj)
                        qu(is,jj)=weight*qu(is,jj)
                     enddo
! ---                Check if new position is off computational
! ---                grid (reset code)
                     if(xpb(jj).lt.xb.or.xpb(jj).gt.xe.or.&
                        ypb(jj).lt.yb.or.ypb(jj).gt.ye)then
                        ipufcd(jj)=99
! ---                   Log mass dropped
                        if(imbal.EQ.1) then
                           call MBALSUM(cbal,qm(1,jj),qu(1,jj),&
                                        qm(1,jj),qu(1,jj))
                        endif
                     endif
                  enddo
! *******
      if(ldbhr) then
         do i=1,nsplith
            jj=npuffs+i-1
            if(jj.EQ.npuffs) jj=ii
         write(io6,*) 'SPLIT --- changed values for puff ',jj
         write(io6,*) 'xpb,ypb,sigyb = ',xpb(jj),ypb(jj),sigyb(jj)
         write(io6,*) 'q-upper = ',(qu(is,jj),is=1,nspec)
         write(io6,*) 'q-lower = ',(qm(is,jj),is=1,nspec)
         write(io6,*)
         enddo
      endif
! *******
!
! ---             Update total number of puffs
                  npuffs=npuffs+nsplith-1
! ---             Update number of old puffs that are split
                  numsplty=numsplty+1
!
                endif
              endif
            endif
         endif
       endif
      enddo
! ------------------------------------------
! --- Address VERTICAL splitting next
! ------------------------------------------
! --- Make room for split puffs: If too many puffs are needed,
! --- remove puffs that are off the grid and "roll down" arrays
! --- (this also applies to the DA file of tabulated arrays)
      np0=npuffs
      np=npuffs*(nsplit)
      if(np.GT.mxpuff) then
         call ROLLDN(np0)
! ---    Reset current number of puffs after roll-down
         np0=npuffs
      endif
! --- Loop over all existing puffs/slugs
! --------------------------------------
      do ii=1,np0
       if(ipufcd(ii).LT.90) then
!
! ---    Reset ISPLIT flag to 1 if it is time to split all puffs again
         if(lresplit) isplit(ii)=1
!
! ---    Process only those puffs that are uniform in the vertical, and
! ---    above the mixing layer (but were once below)
         if(ipufcd(ii).EQ.6 .OR. ipufcd(ii).EQ.16) then
!
! ---       Check to see if roll-down was adequate
            if(npuffs+nsplit.gt.mxpuff)then
               write(io6,*) 'FATAL ERROR in subr. SPLIT'
               write(io6,*)'Too many puffs on grid for puff array -- ',&
               'NPUFFS (old puffs) = ',npuffs,' NSPLIT = ',nsplit,&
               ' MXPUFF = ',mxpuff
               write(*,*)
               stop 'Halted in SPLIT -- see list file.'
            endif
!
            if(isplit(ii).EQ.1 .AND. ziold(ii).GE.zisplit) then
               ratio=ziold(ii)/zimax(ii)
               qmtotal=0.0
               do is=1,nspec
                  qmtotal=qmtotal+qm(is,ii)
               enddo
               if(ratio.LE.roldmax .AND. qmtotal.GT.0.0) then
!                 ------------------------
! ---               Split this PUFF/SLUG
!                 ------------------------
! *******
      if(ldbhr) then
         write(io6,*)
         write(io6,*) 'SPLIT --- initial values for puff ',ii
         write(io6,*) ' isplit,ipufcd,ziold,zimax = '
         write(io6,*) isplit(ii),ipufcd(ii),ziold(ii),zimax(ii)
         write(io6,*) 'q-upper = ',(qu(is,ii),is=1,nspec)
         write(io6,*) 'q-lower = ',(qm(is,ii),is=1,nspec)
      endif
! ********
!
! ---             Reset split flag to indicate that a split has occurred
                  isplit(ii)=0
!
! ---             Swap puff/slug data from original puff to splits
                  do i=1,nsplit-1
                     j=npuffs+i
                     call SWAP(j,ii)
                  enddo
!
! ---             Set working variables
                  zlidu=zimax(ii)
                  zlidm=ziold(ii)
                  do is=1,nspec
                     qqu(is)=qu(is,ii)
                     qqm(is)=qm(is,ii)
                  enddo
                  dzui=1.0/(zlidu-zlidm)
                  dzmi=1.0/zlidm
                  zlo=zlidu
!
! ---             Loop over split puffs/slugs
                  do i=1,nsplit
!
! ---                Set puff/slug index (jj)
                     jj=npuffs+i-1
                     if(jj.EQ.npuffs) jj=ii
!
! ---                Set top and bottom of puff/slug slice
                     zhi=zlo
                     zlo=0.0
                     if(i.LT.nsplit) zlo=zhi*0.5
!
! --------------------------------------------
! ---                Set/revise puff/slug data
! --------------------------------------------
!
                     if(zlo.GE.zlidm) then
! ---                   Slice is from upper layer of original puff
                        dqfac=(zhi-zlo)*dzui
                        do is=1,nspec
                           qu(is,jj)=qqu(is)*dqfac
                           qm(is,jj)=0.0
                        enddo
                        zimax(jj)=zhi
                        ziold(jj)=zlo
!
                     elseif(zhi.LE.zlidm) then
! ---                   Slice is from lower layer of original puff
                        dqfac=(zhi-zlo)*dzmi
                        if(zlo.GT.0.0) then
                           do is=1,nspec
                              qu(is,jj)=qqm(is)*dqfac
                              qm(is,jj)=0.0
                           enddo
                           zimax(jj)=zhi
                           ziold(jj)=zlo
                        else
                           do is=1,nspec
                              qu(is,jj)=0.0
                              qm(is,jj)=qqm(is)*dqfac
                           enddo
! ---                      Change code from 6(16) to 2(12)
                           ipufcd(jj)=ipufcd(jj)-4
                           zimax(jj)=zhi
                           ziold(jj)=zhi
                        endif
!
                     elseif(zlo.EQ.0.0) then
! ---                   Lower layer is identical to lower layer of
! ---                   original puff, and upper layer is drawn from
! ---                   slice of upper layer of original puff
                        dqfac=(zhi-zlidm)*dzui
                        do is=1,nspec
                           qu(is,jj)=qqu(is)*dqfac
                        enddo
                        zimax(jj)=zhi
                        ziold(jj)=zlidm
!
                     else
! ---                   Slice straddles upper and lower layers of
! ---                   original puff
                        dqfacu=(zhi-zlidm)*dzui
                        dqfacm=(zlidm-zlo)*dzmi
                        do is=1,nspec
                           qu(is,jj)=qqu(is)*dqfacu+qqm(is)*dqfacm
                           qm(is,jj)=0.0
                        enddo
                        zimax(jj)=zhi
                        ziold(jj)=zlo
                     endif
! *******
      if(ldbhr) then
         write(io6,*) 'SPLIT --- changed values for puff ',jj
         write(io6,*) ' isplit,ipufcd,ziold,zimax = '
         write(io6,*) isplit(jj),ipufcd(jj),ziold(jj),zimax(jj)
         write(io6,*) 'q-upper = ',(qu(is,jj),is=1,nspec)
         write(io6,*) 'q-lower = ',(qm(is,jj),is=1,nspec)
      endif
! *******
                  enddo
!
! ---             Update total number of puffs
                  npuffs=npuffs+nsplit-1
! ---             Update number of old puffs that are split
                  numspltz=numspltz+1
!
               endif
            endif
         endif
       endif
      enddo
!
      return
      end
!----------------------------------------------------------------------
      subroutine initpuf(ndhrb,nsecb,ndhre,nsece,nspec,dt,smxi,ldb,&
                         em2dat,em4dat,em3dat,em5dat,em5grp,&
                         em6dat,em7dat,em8dat,metfm,newpuf)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918           INITPUF
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Invoke routines that initialize newly released
!               puff/slug variables from each source
!
! --- UPDATE
! --- v7.3.0  150717 - V7.3.0  150819 (Jelena)
!                                : Fix indexing in call for ROADS for 
!                                  runs with multiple RDEMARB.DAT
!                                : Add variable Spray sources with
!                                  SPEMARB.DAT file (CDA)
! --- TNG-7.0.0 - TNG-7.1.0  141230  (DGS)
!                                : Add variable Road sources with
!                                  RDEMARB.DAT file
! --- V6.3-TNG-7.0.0  140913  (DGS)
!                                : Add Flare sources
!                                  Add Road sources
! --- V6.268-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.26-V6.268  100108  (DGS): Fix call to RDEM2 argument MFPT2 to
!                                  select file format for current file
!                                  (needs loop IP)
! --- V6.22-V6.26   080430  (DGS): Add time period to POINTS1 call for
!                                  RISE.DAT numerical rise output option
! --- V6.101-V6.22  070921  (DGS): Enforce MXMETSAV=2 (line source
!                                  tables are currently available only
!                                  for current and previous met period)
!                   070921  (DGS): Fix bug in LN2SWAP argument list
!                                  that reversed the input and output
!                                  index pointers (created problem only
!                                  for variable line sources with more
!                                  than 1 emission step per model step)
!                   070921  (DGS): Fix bug in RDEM2 argument list that
!                                  used the undefined variable NSRC2
!                                  instead of NPT2.
! --- V6.1-V6.101   051020  (DGS): Assign LNEMARB times to arrays
! --- V5.71-V6.1    050915  (DGS): Use begin-time/end-time (seconds)
!                                  for variable emissions files
!                                  and allow coordinate projection
!                                  transformations
!                   050915  (DGS): Trap EOF in emissions files
! --- V5.4-V5.71    030528  (DGS): Add MBCON=2 option for BCON file
!                   030528  (DGS): Require non-negative emissions from
!                                  variable emissions files
! --- V5.4-V5.4     000602_6(DGS): Fix call to RDEM3 (data records for
!                                  BAEMARB.DAT files) to use the file
!                                  unit number 'io' instead of 'io17',
!                                  which was removed in CALPUFF 5.4,
!                                  Level 000602.  File unit io17
!                                  (which is zero because it is not
!                                  defined) is not open so the call to
!                                  RDEM3 generates a runtime error
!                                  which halts the run.  This affects
!                                  previous CALPUFF versions starting
!                                  with version 5.4.
! --- V5.3-V5.4     000602  (DGS): relace VOLEM.DAT with VOLEMARB.DAT
!                   000602  (DGS): allow multiple PTEMARB, BAEMARB, and
!                                  VOLEMARB files
!                   000602  (DGS): add message to "stop"
! --- V5.2-V5.3     991222  (DGS): add Boundary Condition (BC) sources
! --- V5.0-V5.0     980807  (DGS): generalize VOLEM.DAT format
!                   980807  (DGS): pass METFM to subs
!                   980430  (DGS): correct BAEMARB emissions
! --- V4.0-V5.0     971107  (DGS): add LNEMARB.DAT data and subroutines
!                   971107  (DGS): remove volume flux from PT2 input
!                                  processing
!                   971107  (DGS): replace heat flux with effective rise
!                                  velocity and diameter in AR2 input
!                   971107  (DGS): add surface elevation to AR2 input
!                   971107  (DGS): add initial sigma-z to AR2 input
!                   971107  (DGS): re-order vertex array elements
!
! --- INPUTS:
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         NSPEC - integer - Number of species modeled
! SMXI(mxmetdom)- real    - Reciprocal of speed required to travel
!                           max allowed distance in 1 timestep (s/m)
!                           (varies with size of grid cells)
!            DT - real    - Length (seconds) of a basic time step
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!    EM2DAT(nse2+4,npt2) - real array - Time-varying PTEMARB source
!                                        data
!    EM4DAT(nse4+6,nvl2) - real array - Time-varying VOLEMARB source
!                                        data
!   EM3DAT(nse3+14,nar2) - real array - Time-varying BAEMARB source
!                                        data
!    EM5DAT(nse5+6,nln2) - real array - Time-varying LNEMARB source
!                                        data for lines
!      EM5GRP(7,mxlngrp) - real array - Time-varying LNEMARB source
!                                        data for groups of lines
!  EM6DAT(nse6+11,nflr2) - real array - Time-varying FLEMARB source
!                                        data
!    EM7DAT(nse7+3,nrd2) - real array - Time-varying RDEMARB source
!                                        data
!         METFM - integer - Meteorological data format
!
!     Common block /AR1/ variables:
!           NAR1
!     Common block /AR2/ variables:
!           NAR2, NSE3, IBSRC3(mxemdat), IESRC3(mxemdat),
!           pmapar2(mxemdat),utmhemar2(mxemdat),datumar2(mxemdat),
!           lutmar2(mxemdat),llccar2(mxemdat),lpsar2(mxemdat),
!           lemar2(mxemdat),llazaar2(mxemdat),lttmar2(mxemdat),
!           iutmznar2(mxemdat),feastar2(mxemdat),fnorthar2(mxemdat),
!           rnlat0ar2(mxemdat),relon0ar2(mxemdat),
!           rnlat1ar2(mxemdat),rnlat2ar2(mxemdat),
!           NSTEP3(mxemdat),T2BTZ3(mxemdat)
!     Common block /BCS/ variables:
!           NBC
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW
!     Common block /DATASET/ variables:
!           verparb(mxemdat),veraarb(mxemdat),
!           verlarb,vervarb(mxemdat),
!           verbcon
!     Common block /FILNAM/ variables:
!           NPTDAT, NARDAT, NVOLDAT,
!           PTDAT, ARDAT, VOLDAT
!     Common block /FLAGS/ variables:
!           MSLUG, MBCON
!     Common block /FL2/ variables:
!           NFL2, NSE6, IBEG6, IEND6, MFFL2
!     Common block /GRID/ variables:
!           DGRID, DGRIDI, XORIG
!     Common block /LN1/ variables:
!           NLINES
!     Common block /LN2/ variables:
!           NLN2, NSE5, IBEG5, IEND5
!     Common block /PUFF/ variables:
!           NPUFFS
!     Common block /PT1/ variables:
!           NPT1
!     Common block /PT2/ variables:
!           MFPT2, NPT2, NSE2, IBEG2, IEND2
!     Common block /VOL1/ variables:
!           NVL1
!     Common block /VOL2/ variables:
!           NVL2, NSE4, IBEG4, IEND4, MFVL2
!     |MROAD1| variables:
!           NRDSEG1
!     |MROAD2| variables:
!           NRDSEG2,NRDSEG2B,NRDSEG2E
!           NRD2, NSE7, IBEG7, IEND7, MFRD2
!     |MROAD2| variables:
!           NSPSEG2
!           NSP2, NSE8, IBEG8, IEND8, MFSP2
!     Parameters:
!           MXPT1, MXPT2, MXLINES, MXLNGRP, MXMETSAV, MXMETDOM,
!           MXAREA, MXVOL, MXFL2, MXEMDAT, MXVERT, MXQSTEP,
!           IO6, IOPT2, IOAR2, IOVOL, IOFL2
!
! --- OUTPUT:
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!     Common block /AR2/ variables:
!            xar2grd(mxvertp1,mxarea),yar2grd(mxvertp1,mxarea),
!            area2(mxarea),refar2(mxarea),htar2(mxarea),tkar2(mxarea),
!            qhfar2(mxarea),qar2(mxspec,mxarea),
!            NSTEP3(mxemdat),
!            NDHRQB3(mxqstep,mxemdat),NSECQB3(mxqstep,mxemdat),
!            NDHRQE3(mxqstep,mxemdat),NSECQE3(mxqstep,mxemdat)
!
!
! --- INITPUF called by:  COMP
! --- INITPUF calls:      POINTS1,  RDEM2, POINTS2,
!                         LINES1 ,  RDEM5, LN2FILL, LN2SWAP, LINES2,
!                         AREAS1 ,  RDEM3,  AREAS2,
!                         VOLS   ,  RDEM4,  FLARES,   RDEM6,
!                         BCS1   , RDEMBC, RDEMBC2,
!                         ROADS  ,  RDEM7,
!                         SPRAYS ,  RDEM8,
!                         TPERIOD, GLOBE1,   GLOBE
!----------------------------------------------------------------------
!
! --- Modules
      use mroad1
      use mroad2
      use mspray2
! --- Include-files
      include 'params.puf'
! --- Set number of variables before emissions in 'EMARB' arrays
! --- as parameters
      parameter(nvarpt=4, nvarba=14, nvarln=6, nvarvl=6, nvarfl=11)
      parameter(nvarrd=3, nvarsp=14)
!
      include 'ar1.puf'
      include 'ar2.puf'
      include 'bcs.puf'
      include 'comparm.puf'
      include 'dataset.puf'
      include 'filnam.puf'
      include 'flags.puf'
      include 'fl2.puf'
      include 'grid.puf'
      include 'ln1.puf'
      include 'ln2.puf'
      include 'map.puf'
      include 'puff.puf'
      include 'pt1.puf'
      include 'pt2.puf'
      include 'vol1.puf'
      include 'vol2.puf'
!
      logical ldb,problem,lqneg
!
! --- Time-variable emission arrays (declare full dimensions here and
! --- pass as argument to subr. so that dimensions can be adjusted)
      real em2dat(nse2+nvarpt,npt2)
      real em3dat(nse3+nvarba,nar2)
      real em4dat(nse4+nvarvl,nvl2)
      real em5dat(nse5+nvarln,nln2),em5grp(7,mxlngrp)
      real em6dat(nse6+nvarfl,nfl2)
      real em7dat(nse7+nvarrd,nrd2)
      real em8dat(nse8+nvarsp,nsp2)
      integer idmap(mxlines),igrp(mxlngrp)
!
! --- Fix the number of vertices (sides) of area sources at 4
      real xvert(4),yvert(4)
      real    :: xyzb(3),xyze(3)
      real smxi(mxmetdom)
! --- For coordinate transformations
      character*8 cmapi,cmapo
      character*12 caction
      character*4 c4hem
      real*8 vecti(9),vecto(9)
! --- Scale factor for Tangential TM projection
      tmsone=1.00000
! --- Output of coord transform is to CALPUFF (x,y) - /MAP/
      iutmo=iutmzn
      if(utmhem.EQ.'S   ' .AND. iutmo.LT.900) iutmo=-iutmo
      cmapo=pmap
      if(cmapo.EQ.'TTM     ') cmapo='TM      '
! --- Initialize current number of puffs equal to the number of
! --- old puffs, and set the number of new puffs to zero;
! --- both of these will be updated within subsequent calls
      np=npuffs
      newpuf=0
!
! --- MFACT is 0 (puffs released) or 10 (slugs released)
      mfact=10*mslug
! --- Roads and Sprays emit 'old' slugs regardless of MSLUG selection
      mfact_rd=10
      mfact_sp=10
!
! --- Set logical for detecting problem with sources
      problem=.FALSE.
!
!*****
      if(ldb)then
         write(io6,11)mfact,(smxi(n),n=1,nmetdom)
11       format(/1x,'LDB output from SUBR. INITPUF'/5x,'MFACT: ',i2,&
         2x,'SMXI: ',30f10.5)
      endif
!*****
! --- Enforce MXMETSAV=2 (set in parameter file) regardless of source
! --- types in current run
      if(mxmetsav.NE.2) then
        write(io6,*)
        write(io6,*)'ERROR -- MXMETSAV must be set to 2 in PARAMS.PUF'
        write(io6,*)
        stop 'Halted in INITPUF -- See list file'
      endif
!
! ---------------------------------------------------
! --- Loop over point sources with CONSTANT emissions
! ---------------------------------------------------
      if(npt1.gt.0) then
         do i=1,npt1
            call POINTS1(i,nspec,ndhrb,nsecb,ndhre,nsece,&
                         dt,smxi,ldb,mfact,metfm,np,&
                         newpuf,problem)
         enddo
      endif
!
! ---------------------------------------------------
! --- Loop over point sources with VARIABLE emissions
! ---------------------------------------------------
      if(npt2.gt.0) then
! ---    Update variable emissions from PTEMARB files
! ---    Loop over files to check/update all sources
         do ip=1,nptdat
! ---      File unit number
           io=iopt2+ip-1
! ---      Check if last emission period used in previous step extends
! ---      into current timestep (beginning of step < end of q-period)
           iq=nstep2(ip)
           call TPERIOD(-1,ndhrb,nsecb,ndhrqb2(iq,ip),nsecqb2(iq,ip),&
                        ndhrqe2(iq,ip),nsecqe2(iq,ip),ilocate)
           if(ilocate.EQ.0) then
! ---         Reset emissions data arrays so that last emission period
! ---         of previous step is first period of current step
              if(iq.GT.1) then
                 ndhrqb2(1,ip)=ndhrqb2(iq,ip)
                 nsecqb2(1,ip)=nsecqb2(iq,ip)
                 ndhrqe2(1,ip)=ndhrqe2(iq,ip)
                 nsecqe2(1,ip)=nsecqe2(iq,ip)
                 do i=ibsrc2(ip),iesrc2(ip)
                    tstak2(1,i)=tstak2(iq,i)
                    exitw2(1,i)=exitw2(iq,i)
                    do is=1,nse2
                       qstak2(ixrem2(is),1,i)=qstak2(ixrem2(is),iq,i)
                    enddo
                 enddo
                 nstep2(ip)=1
              endif
           elseif(ilocate.EQ.1) then
! ---         First set of emissions data this step must be read in
              nstep2(ip)=0
           else
              write(io6,*)
              write(io6,*)'ERROR -- PTEMARB begin time exceeds current',&
                          ' start time'
              write(io6,*)'      -- current begin time :',ndhrb,nsecb
              write(io6,*)'      -- PTEMARB begin time :',&
                                         ndhrqb2(iq,ip),nsecqb2(iq,ip)
              stop
           endif
! ---      Are new emissions data needed?
           if(nstep2(ip).EQ.0) then
              ilocate=1
           else
              call TPERIOD(1,ndhre,nsece,ndhrqb2(1,ip),nsecqb2(1,ip),&
                           ndhrqe2(1,ip),nsecqe2(1,ip),ilocate)
           endif
! ---      Read new emissions data if needed
           if(ilocate.EQ.1) then
211           continue
              call RDEM2(io,mfpt2(ip),npt2,nse2,cid2,ibsrc2(ip),&
                         iesrc2(ip),verparb(ip),t2btz2(ip),ldb,&
                         io6,ibegh,ibegs,iendh,iends,em2dat,ieof)
              if(ieof.EQ.1) then
                 write(io6,*)
                 write(io6,*)'EOF in PTEMARB.DAT file ',ip
                 write(io6,*)'Halted at current end time :',ndhre,nsece
                 write(*,*)'EOF in PTEMARB.DAT file ',ip
                 write(*,*)'Halted at current end time :',ndhre,nsece
                 stop
              endif
! ---         Test emissions period relative to beginning of timestep
              call TPERIOD(-1,ndhrb,nsecb,ibegh,ibegs,iendh,iends,iloc)
! ---         Keep reading if emission period ends before start of step
              if(iloc.EQ.1) goto 211
! ---         Test period relative to end of timestep
              call TPERIOD(1,ndhre,nsece,ibegh,ibegs,iendh,iends,iloc)
              if(iloc.EQ.-1)then
                 write(io6,*)
                 write(io6,*)'ERROR: PTEMARB begin time later than ',&
                             'end of timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- PTEMARB start time :',ibegh,ibegs
                 stop 'Halted in INITPUF -- see list file'
              endif
! ---         Swap variable emissions data (em2dat) into /PT2/ arrays
              nstep2(ip)=nstep2(ip)+1
              iq=nstep2(ip)
              if(iq.GT.mxqstep) then
                 write(io6,*)'ERROR: PTEMARB has too many periods ',&
                             'in the current timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- PTEMARB start time :',ibegh,ibegs
                 write(io6,*)'   -- Max allowed (MXQSTEP) ',mxqstep
                 stop 'Halted in INITPUF -- see list file'
              endif
              ndhrqb2(iq,ip)=ibegh
              nsecqb2(iq,ip)=ibegs
              ndhrqe2(iq,ip)=iendh
              nsecqe2(iq,ip)=iends
! ---         Loop over sources
              do i=ibsrc2(ip),iesrc2(ip)
                 tstak2(iq,i)=em2dat(1,i)
                 exitw2(iq,i)=em2dat(2,i)
                 syipt2(iq,i)=em2dat(3,i)
                 szipt2(iq,i)=em2dat(4,i)
                 lqneg=.FALSE.
                 do is=1,nse2
                    k=is+nvarpt
                    qstak2(ixrem2(is),iq,i)=em2dat(k,i)
                    if(qstak2(ixrem2(is),iq,i).LT.0.0) lqneg=.TRUE.
                 enddo
! ---            Report problem if any emissions are negative
                 if(lqneg) then
                    write(io6,*)
                    write(io6,*)'*********  FATAL  ***********'
                    write(io6,*)'ERROR in subr. INITPUF -- Invalid ',&
                    'emission rate  (must NOT be negative)'
                    write(io6,*)' -- PTEMARB Source = ',i
                    problem=.TRUE.
                 endif
              enddo
! ---         Get another period of emissions data if the end of the
! ---         timestep is not within the period just read
              if(iloc.EQ.1) goto 211
           endif
           do i=ibsrc2(ip),iesrc2(ip)
              call POINTS2(ip,i,nspec,ndhrb,nsecb,ndhre,nsece,dt,&
                           smxi,ldb,mfact,metfm,np,newpuf,problem)
           enddo
         enddo
      endif
!
!
! ------------------------------------------------------------
! --- Loop over polygon area sources with CONSTANT emissions
! ------------------------------------------------------------
      if(nar1.gt.0) then
         do i=1,nar1
            call AREAS1(i,nspec,dt,smxi,ldb,mfact,np,&
                        newpuf,problem)
         enddo
      endif
! ----------------------------------------------------------
! --- Loop over polygon area sources with VARIABLE emissions
! ----------------------------------------------------------
      if(nar2.gt.0) then
! ---    Update variable emissions from BAEMARB files
! ---    Loop over files to check/update all sources
         do ia=1,nardat
! ---      File unit number
           io=ioar2+ia-1
! ---      Set up map projection conversion to CALPUFF coordinates
! ---      Input is from BAEMARB.DAT header - /AR2/
           iutmi=iutmznar2(ia)
           if(utmhemar2(ia).EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
           cmapi=pmapar2(ia)
           if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---      Set conversion vectors
           call GLOBE1(cmapi,iutmi,tmsone,rnlat1ar2(ia),rnlat2ar2(ia),&
                  rnlat0ar2(ia),relon0ar2(ia),&
                  feastar2(ia),fnorthar2(ia),&
                  cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                  feast,fnorth,&
                  caction,vecti,vecto)
! ---      Check if last emission period used in previous step extends
! ---      into current timestep (beginning of step < end of q-period)
           iq=nstep3(ia)
           call TPERIOD(-1,ndhrb,nsecb,ndhrqb3(iq,ia),nsecqb3(iq,ia),&
                        ndhrqe3(iq,ia),nsecqe3(iq,ia),ilocate)
           if(ilocate.EQ.0) then
! ---         Reset location & emissions data arrays so that last emiss
! ---         period of previous step is first period of current step
              if(iq.GT.1) then
                 ndhrqb3(1,ia)=ndhrqb3(iq,ia)
                 nsecqb3(1,ia)=nsecqb3(iq,ia)
                 ndhrqe3(1,ia)=ndhrqe3(iq,ia)
                 nsecqe3(1,ia)=nsecqe3(iq,ia)
                 do i=ibsrc3(ia),iesrc3(ia)
                    do iv=1,nvert2(i)+1
                       xar2grd(iv,1,i)=xar2grd(iv,iq,i)
                       yar2grd(iv,1,i)=yar2grd(iv,iq,i)
                    enddo
                    area2(1,i)=area2(iq,i)
                    htar2(1,i)=htar2(iq,i)
                    elar2(1,i)=elar2(iq,i)
                    tkar2(1,i)=tkar2(iq,i)
                    wefar2(1,i)=wefar2(iq,i)
                    refar2(1,i)=refar2(iq,i)
                    sz0ar2(1,i)=sz0ar2(iq,i)
                    do is=1,nse3
                       qar2(ixrem3(is),1,i)=qar2(ixrem3(is),iq,i)
                    enddo
                 enddo
                 nstep3(ia)=1
              endif
           elseif(ilocate.EQ.1) then
! ---         First set of emissions data this step must be read in
              nstep3(ia)=0
           else
              write(io6,*)
              write(io6,*)'ERROR: BAEMARB begin time exceeds current',&
                          ' start time'
              write(io6,*)'    -- current begin time :',ndhrb,nsecb
              write(io6,*)'    -- BAEMARB begin time :',ndhrqb3(iq,ia),&
                           nsecqb3(iq,ia)
              stop 'Halted in INITPUF -- see list file'
           endif
! ---      Are new emissions data needed?
           if(nstep3(ia).EQ.0) then
              ilocate=1
           else
              call TPERIOD(1,ndhre,nsece,ndhrqb3(1,ia),nsecqb3(1,ia),&
                           ndhrqe3(1,ia),nsecqe3(1,ia),ilocate)
           endif
! ---      Read new emissions data if needed
           if(ilocate.EQ.1) then
311           continue
              call RDEM3(io,nar2,nse3,cid3,ibsrc3(ia),iesrc3(ia),&
                         veraarb(ia),t2btz3(ia),ldb,io6,ibegh,&
                         ibegs,iendh,iends,em3dat,ieof)
              if(ieof.EQ.1) then
                 write(io6,*)
                 write(io6,*)'EOF in BAEMARB.DAT file ',ia
                 write(io6,*)'Halted at current end time :',ndhre,nsece
                 write(*,*)'EOF in BAEMARB.DAT file ',ia
                 write(*,*)'Halted at current end time :',ndhre,nsece
                 stop
              endif
! ---         Test emissions period relative to beginning of timestep
              call TPERIOD(-1,ndhrb,nsecb,ibegh,ibegs,iendh,iends,iloc)
! ---         Keep reading if emission period ends before start of step
              if(iloc.EQ.1) goto 311
! ---         Test period relative to end of timestep
              call TPERIOD(1,ndhre,nsece,ibegh,ibegs,iendh,iends,iloc)
              if(iloc.EQ.-1)then
                 write(io6,*)
                 write(io6,*)'ERROR: BAEMARB begin time later than ',&
                             'end of timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- BAEMARB start time :',ibegh,ibegs
                 stop 'Halted in INITPUF -- see list file'
              endif
! ---         Swap variable emissions data (em3dat) into /ar2/ arrays
              nstep3(ia)=nstep3(ia)+1
              iq=nstep3(ia)
              if(iq.GT.mxqstep) then
                 write(io6,*)'ERROR: BAEMARB has too many periods ',&
                             'in the current timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- BAEMARB start time :',ibegh,ibegs
                 write(io6,*)'   -- Max allowed (MXQSTEP) ',mxqstep
                 stop 'Halted in INITPUF -- see list file'
              endif
              ndhrqb3(iq,ia)=ibegh
              nsecqb3(iq,ia)=ibegs
              ndhrqe3(iq,ia)=iendh
              nsecqe3(iq,ia)=iends
! ---         Loop over sources
              do i=ibsrc3(ia),iesrc3(ia)
!
! ---            Compute & store relative vertex coordinates; accumulate
! ---            sums for computing the mean location of the area source
                 sumx=0.
                 sumy=0.
! ---            Explicitly allow ONLY 4-sided shapes!
                 nvert2(i)=4
                 do iv=1,nvert2(i)
                    xvert3=em3dat(iv,i)
                    yvert3=em3dat(iv+nvert2(i),i)
                    call GLOBE(io6,caction,datumar2(ia),vecti,datum,&
                               vecto,xvert3,yvert3,xvert(iv),yvert(iv),&
                               izone,c4hem)
                    xar2grd(iv,iq,i)=(1000.*xvert(iv)-xorig)*dgridi
                    yar2grd(iv,iq,i)=(1000.*yvert(iv)-yorig)*dgridi
                    sumx=sumx+xar2grd(iv,iq,i)
                    sumy=sumy+yar2grd(iv,iq,i)
                 enddo
! ---            Mean location of source
                 xar2grd(nvert2(i)+1,iq,i)=sumx/FLOAT(nvert2(i))
                 yar2grd(nvert2(i)+1,iq,i)=sumy/FLOAT(nvert2(i))
 
! ---            Area of 4-sided area source in m^2
                 call quadarea(xvert,yvert,area2(iq,i))
!
! ---            Height, surface elevation (m)
                 htar2(iq,i)=em3dat(9,i)
                 elar2(iq,i)=em3dat(10,i)
! ---            Effective temperature (K), velocity (m/s) and radius (m)
! ---            for rise calculation
                 tkar2(iq,i)=em3dat(11,i)
                 wefar2(iq,i)=em3dat(12,i)
                 refar2(iq,i)=em3dat(13,i)
! ---            Initial sigma-z (m)
                 sz0ar2(iq,i)=em3dat(14,i)
! ---            Emissions (g/s)
                 if(baemunit(i).EQ.'g/s') then
                    fqunit=1.00
                 elseif(baemunit(i).EQ.'g/m2/s') then
                    fqunit=area2(iq,i)
                 else
                    write(io6,*)
                    write(io6,*)&
                          'ERROR -- Invalid units in BAEMARB.DAT'
                    write(io6,*)'      -- Expected g/s or g/m2/s'
                    write(io6,*)'      -- Found: ',baemunit(i)
                    write(*,*)
                    stop 'Halted in INITPUF -- see list file.'
                 endif
                 lqneg=.FALSE.
                 do is=1,nse3
                    k=is+nvarba
                    qar2(ixrem3(is),iq,i)=em3dat(k,i)*fqunit
                    if(qar2(ixrem3(is),iq,i).LT.0.0) lqneg=.TRUE.
                 enddo
! ---            Report problem if any emissions are negative
                 if(lqneg) then
                    write(io6,*)
                    write(io6,*)'ERROR in subr. INITPUF -- Invalid ',&
                    'emission rate  (must NOT be negative)'
                    write(io6,*)' -- BAEMARB Source = ',i
                    problem=.TRUE.
                 endif
              enddo
! ---         Get another period of emissions data if the end of the
! ---         timestep is not within the period just read
              if(iloc.EQ.1) goto 311
           endif
           do i=ibsrc3(ia),iesrc3(ia)
              call AREAS2(ia,i,nspec,ndhrb,nsecb,ndhre,nsece,dt,smxi,&
                          ldb,mfact,metfm,np,newpuf,problem)
           enddo
         enddo
      endif
!
! ------------------------------------------------------------
! --- Loop over volume sources with CONSTANT emissions
! ------------------------------------------------------------
      if(nvl1.gt.0) then
         mvolume=1
         iv=1
         do i=1,nvl1
            call VOLS(mvolume,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                      dt,smxi,ldb,mfact,np,newpuf,problem)
         enddo
      endif
!
! ------------------------------------------------------------
! --- Loop over volume sources with VARIABLE emissions
! ------------------------------------------------------------
!
      if(nvl2.gt.0) then
         mvolume=2
! ---    Update variable emissions from VOLEMARB files
! ---    Loop over files to check/update all sources
         do iv=1,nvoldat
! ---      File unit number
           io=iovol+iv-1
! ---      Set up map projection conversion to CALPUFF coordinates
! ---      Input is from VOLEMARB.DAT header - /VOL2/
           iutmi=iutmznvl2(iv)
           if(utmhemvl2(iv).EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
           cmapi=pmapvl2(iv)
           if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---      Set conversion vectors
           call GLOBE1(cmapi,iutmi,tmsone,rnlat1vl2(iv),rnlat2vl2(iv),&
                  rnlat0vl2(iv),relon0vl2(iv),&
                  feastvl2(iv),fnorthvl2(iv),&
                  cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                  feast,fnorth,&
                  caction,vecti,vecto)
! ---      Check if last emission period used in previous step extends
! ---      into current timestep (beginning of step < end of q-period)
           iq=nstep4(iv)
           call TPERIOD(-1,ndhrb,nsecb,ndhrqb4(iq,iv),nsecqb4(iq,iv),&
                        ndhrqe4(iq,iv),nsecqe4(iq,iv),ilocate)
           if(ilocate.EQ.0) then
! ---         Reset location & emissions data arrays so that last emiss
! ---         period of previous step is first period of current step
              if(iq.GT.1) then
                 ndhrqb4(1,iv)=ndhrqb4(iq,iv)
                 nsecqb4(1,iv)=nsecqb4(iq,iv)
                 ndhrqe4(1,iv)=ndhrqe4(iq,iv)
                 nsecqe4(1,iv)=nsecqe4(iq,iv)
                 do i=ibsrc4(iv),iesrc4(iv)
                    xvl2grd(1,i)=xvl2grd(iq,i)
                    yvl2grd(1,i)=yvl2grd(iq,i)
                    htvl2(1,i)=htvl2(iq,i)
                    elvl2(1,i)=elvl2(iq,i)
                    sy0vl2(1,i)=sy0vl2(iq,i)
                    sz0vl2(1,i)=sz0vl2(iq,i)
                    do is=1,nse4
                      qvl2(ixrem4(is),1,i)=qvl2(ixrem4(is),iq,i)
                    enddo
                 enddo
                 nstep4(iv)=1
              endif
           elseif(ilocate.EQ.1) then
! ---         First set of emissions data this step must be read in
              nstep4(iv)=0
           else
              write(io6,*)
              write(io6,*)'ERROR: VOLEMARB begin time exceeds current',&
                          ' start time'
              write(io6,*)'   -- current begin time  :',ndhrb,nsecb
              write(io6,*)'   -- VOLEMARB begin time :',ndhrqb4(iq,iv),&
                           nsecqb4(iq,iv)
              stop 'Halted in INITPUF -- see list file'
           endif
! ---      Are new emissions data needed?
           if(nstep4(iv).EQ.0) then
              ilocate=1
           else
              call TPERIOD(1,ndhre,nsece,ndhrqb4(1,iv),nsecqb4(1,iv),&
                           ndhrqe4(1,iv),nsecqe4(1,iv),ilocate)
           endif
! ---      Read new emissions data if needed
           if(ilocate.EQ.1) then
411           continue
              call RDEM4(io,nvl2,nse4,cid4,ibsrc4(iv),iesrc4(iv),&
                         vervarb(iv),t2btz4(iv),ldb,io6,ibegh,&
                         ibegs,iendh,iends,em4dat,ieof)
              if(ieof.EQ.1) then
                 write(io6,*)
                 write(io6,*)'EOF in VOLEMARB.DAT file ',iv
                 write(io6,*)'Halted at current end time :',ndhre,nsece
                 write(*,*)'EOF in VOLEMARB.DAT file ',iv
                 write(*,*)'Halted at current end time :',ndhre,nsece
                 stop
              endif
! ---         Test emissions period relative to beginning of timestep
              call TPERIOD(-1,ndhrb,nsecb,ibegh,ibegs,iendh,iends,iloc)
! ---         Keep reading if emission period ends before start of step
              if(iloc.EQ.1) goto 411
! ---         Test period relative to end of timestep
              call TPERIOD(1,ndhre,nsece,ibegh,ibegs,iendh,iends,iloc)
              if(iloc.EQ.-1)then
                 write(io6,*)
                 write(io6,*)'ERROR: VOLEMARB begin time later than ',&
                             'end of timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- VOLEMARB start time :',ibegh,ibegs
                 stop 'Halted in INITPUF -- see list file'
              endif
! ---         Swap variable emissions data (em4dat) into /VOL2/ arrays
              nstep4(iv)=nstep4(iv)+1
              iq=nstep4(iv)
              if(iq.GT.mxqstep) then
                 write(io6,*)'ERROR: VOLEMARB has too many periods ',&
                             'in the current timestep'
                 write(io6,*)'   -- current end time    :',ndhre,nsece
                 write(io6,*)'   -- VOLEMARB start time :',ibegh,ibegs
                 write(io6,*)'   -- Max allowed (MXQSTEP) ',mxqstep
                 stop 'Halted in INITPUF -- see list file'
              endif
              ndhrqb4(iq,iv)=ibegh
              nsecqb4(iq,iv)=ibegs
              ndhrqe4(iq,iv)=iendh
              nsecqe4(iq,iv)=iends
! ---         Loop over sources
              do i=ibsrc4(iv),iesrc4(iv)
!---             Apply projection/datum translation to position
                 call GLOBE(io6,caction,datumvl2(iv),vecti,datum,&
                            vecto,em4dat(1,i),em4dat(2,i),xvl2,yvl2,&
                            izone,c4hem)
! ---            Compute relative grid coordinates
                 xvl2grd(iq,i)=(1000.*xvl2-xorig)*dgridi
                 yvl2grd(iq,i)=(1000.*yvl2-yorig)*dgridi
                 htvl2(iq,i)=em4dat(3,i)
                 elvl2(iq,i)=em4dat(4,i)
                 sy0vl2(iq,i)=em4dat(5,i)
                 sz0vl2(iq,i)=em4dat(6,i)
                 lqneg=.FALSE.
                 do is=1,nse4
                    k=is+nvarvl
                    qvl2(ixrem4(is),iq,i)=em4dat(k,i)
                    if(qvl2(ixrem4(is),iq,i).LT.0.0) lqneg=.TRUE.
                 enddo
! ---            Report problem if any emissions are negative
                 if(lqneg) then
                    write(io6,*)
                    write(io6,*)'ERROR in subr. INITPUF -- Invalid ',&
                    'emission rate  (must NOT be negative)'
                    write(io6,*)' -- VOLEMARB Source = ',i
                    problem=.TRUE.
                 endif
              enddo
! ---         Get another period of emissions data if the end of the
! ---         timestep is not within the period just read
              if(iloc.EQ.1) goto 411
            endif
            do i=ibsrc4(iv),iesrc4(iv)
               call VOLS(mvolume,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                         dt,smxi,ldb,mfact,np,newpuf,problem)
            enddo
         enddo
      endif
!
! --------------------------------------------------------------
! --- Process 1 group of line sources with CONSTANT emissions
! --------------------------------------------------------------
      if(nlines.gt.0) then
         call LINES1(nspec,dt,smxi,ldb,mfact,metfm,np,&
                     newpuf,problem)
      endif
!
! --------------------------------------------------------------------
! --- Process multiple groups of line sources with VARIABLE parameters
! --------------------------------------------------------------------
!
      if(nln2.gt.0) then
! ---    Set up map projection conversion to CALPUFF coordinates
! ---    Input is from LNEMARB.DAT header - /LN2/
         iutmi=iutmznln2
         if(utmhemln2.EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
         cmapi=pmapln2
         if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---    Set conversion vectors
         call GLOBE1(cmapi,iutmi,tmsone,rnlat1ln2,rnlat2ln2,&
                rnlat0ln2,relon0ln2,&
                feastln2,fnorthln2,&
                cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                feast,fnorth,&
                caction,vecti,vecto)
! ---    Check if last emission period used in previous step extends
! ---    into current timestep (beginning of step < end of q-period)
         iq=nstep5
         call TPERIOD(-1,ndhrb,nsecb,ndhrqb5(iq),nsecqb5(iq),&
                      ndhrqe5(iq),nsecqe5(iq),ilocate)
         if(ilocate.EQ.0) then
! ---       Reset location & emissions data arrays so that last emiss
! ---       period of previous step is first period of current step
            if(iq.GT.1) then
               call LN2SWAP(nspec,iq,1)
               nstep5=1
            endif
         elseif(ilocate.EQ.1) then
! ---       First set of emissions data this step must be read in
            nstep5=0
         else
            write(io6,*)
            write(io6,*)'ERROR: LNEMARB begin time exceeds current',&
                        ' start time'
            write(io6,*)'   -- current begin time :',ndhrb,nsecb
            write(io6,*)'   -- LNEMARB begin time :',ndhrqb5(iq),&
                         nsecqb5(iq)
            stop 'Halted in INITPUF -- see list file'
         endif
! ---    Are new emissions data needed?
         if(nstep5.EQ.0) then
            ilocate=1
         else
            call TPERIOD(1,ndhre,nsece,ndhrqb5(1),nsecqb5(1),&
                         ndhrqe5(1),nsecqe5(1),ilocate)
         endif
! ---    Read new emissions data if needed
         if(ilocate.EQ.1) then
511         continue
            call RDEM5(io19,mxlngrp,nln2,nse5,cid5,verlarb,t2btz5,&
                       ldb,io6,ibegh,ibegs,iendh,iends,lngrp,idmap,&
                       em5dat,em5grp,ieof)
            if(ieof.EQ.1) then
               write(io6,*)
               write(io6,*)'EOF in LNEMARB.DAT file '
               write(io6,*)'Halted at current end time :',ndhre,nsece
               write(*,*)'EOF in LNEMARB.DAT file '
               write(*,*)'Halted at current end time :',ndhre,nsece
               stop
            endif
! ---       Test emissions period relative to beginning of timestep
            call TPERIOD(-1,ndhrb,nsecb,ibegh,ibegs,iendh,iends,iloc)
! ---       Keep reading if emission period ends before start of step
            if(iloc.EQ.1) goto 511
! ---       Test period relative to end of timestep
            call TPERIOD(1,ndhre,nsece,ibegh,ibegs,iendh,iends,iloc)
            if(iloc.EQ.-1)then
               write(io6,*)
               write(io6,*)'ERROR: LNEMARB begin time later than ',&
                           'end of timestep'
               write(io6,*)'   -- current end time   :',ndhre,nsece
               write(io6,*)'   -- LNEMARB start time :',ibegh,ibegs
               stop 'Halted in INITPUF -- see list file'
            endif
!---        Apply projection/datum translation to positions
            iline=0
            do ig=1,lngrp
               do il=1,em5grp(1,ig)
                  iline=iline+1
                  x1=em5dat(1,iline)
                  y1=em5dat(2,iline)
                  x2=em5dat(3,iline)
                  y2=em5dat(4,iline)
                  call GLOBE(io6,caction,datumln2,vecti,datum,&
                       vecto,x1,y1,em5dat(1,iline),em5dat(2,iline),&
                       izone,c4hem)
                  call GLOBE(io6,caction,datumln2,vecti,datum,&
                       vecto,x2,y2,em5dat(3,iline),em5dat(4,iline),&
                       izone,c4hem)
               enddo
            enddo
! ---       Swap variable emissions data (em5grp,em5dat) into /ln2/
            nstep5=nstep5+1
            iq=nstep5
            if(iq.GT.mxqstep) then
               write(io6,*)'ERROR: LNEMARB has too many periods ',&
                           'in the current timestep'
               write(io6,*)'   -- current end time   :',ndhre,nsece
               write(io6,*)'   -- LNEMARB start time :',ibegh,ibegs
               write(io6,*)'   -- Max allowed (MXQSTEP) ',mxqstep
               stop 'Halted in INITPUF -- see list file'
            endif
            call LN2FILL(iq,lngrp,nln2,idmap,em5grp,em5dat,nvarln,&
                         problem)
! ---       Assign start/end times for this step
            ndhrqb5(iq)=ibegh
            nsecqb5(iq)=ibegs
            ndhrqe5(iq)=iendh
            nsecqe5(iq)=iends
! ---       Get another period of emissions data if the end of the
! ---       timestep is not within the period just read
            if(iloc.EQ.1) goto 511
         endif
! ---    Initialize number of new puffs from line sources this timestep
         do i=1,nln2
            newln2(i)=0
         enddo
! ---    Identify current line source groups
! ---    Initialize marker for groups to 0
         do k=1,mxlngrp
            igrp(k)=0
         enddo
! ---    Loop over emission periods within this timestep
         do it=1,nstep5
! ---       Loop over lines to identify groups used
            do i=1,nlines2(it)
               k=ilngrp(idline(it,i))
               igrp(k)=1
            enddo
         enddo
! ---    Process active groups
         do ig=1,mxlngrp
            if(igrp(ig).EQ.1) call LINES2(ig,nspec,ndhrb,nsecb,&
                        ndhre,nsece,dt,smxi,ldb,mfact,metfm,&
                        np,newpuf,problem)
         enddo
      endif
!
! ------------------------------------------------------------
! --- Loop over flare sources with VARIABLE emissions
! ------------------------------------------------------------
!
      if(nfl2.gt.0) then
         mflare=2
! ---    Update variable emissions from FLEMARB files
! ---    Loop over files to check/update all sources
         do iv=1,nfldat
! ---      File unit number
           io=iofl2+iv-1
! ---      Set up map projection conversion to CALPUFF coordinates
! ---      Input is from FLEMARB.DAT header - /FL2/
           iutmi=iutmznfl2(iv)
           if(utmhemfl2(iv).EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
           cmapi=pmapfl2(iv)
           if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---      Set conversion vectors
           call GLOBE1(cmapi,iutmi,tmsone,rnlat1fl2(iv),rnlat2fl2(iv),&
                  rnlat0fl2(iv),relon0fl2(iv),&
                  feastfl2(iv),fnorthfl2(iv),&
                  cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                  feast,fnorth,&
                  caction,vecti,vecto)
! ---      Check if last emission period used in previous step extends
! ---      into current timestep (beginning of step < end of q-period)
           iq=nstep6(iv)
           call TPERIOD(-1,ndhrb,nsecb,ndhrqb6(iq,iv),nsecqb6(iq,iv),&
                        ndhrqe6(iq,iv),nsecqe6(iq,iv),ilocate)
           if(ilocate.EQ.0) then
! ---         Reset location & emissions data arrays so that last emiss
! ---         period of previous step is first period of current step
              if(iq.GT.1) then
                 ndhrqb6(1,iv)=ndhrqb6(iq,iv)
                 nsecqb6(1,iv)=nsecqb6(iq,iv)
                 ndhrqe6(1,iv)=ndhrqe6(iq,iv)
                 nsecqe6(1,iv)=nsecqe6(iq,iv)
                 do i=ibsrc6(iv),iesrc6(iv)
                    xfl2grd(1,i)=xfl2grd(iq,i)
                    yfl2grd(1,i)=yfl2grd(iq,i)
                    htfl2(1,i)=htfl2(iq,i)
                    elfl2(1,i)=elfl2(iq,i)
                    sy0fl2(1,i)=sy0fl2(iq,i)
                    sz0fl2(1,i)=sz0fl2(iq,i)
                    do is=1,nse6
                      qfl2(ixrem6(is),1,i)=qfl2(ixrem6(is),iq,i)
                    enddo
                 enddo
                 nstep6(iv)=1
              endif
           elseif(ilocate.EQ.1) then
! ---         First set of emissions data this step must be read in
              nstep6(iv)=0
           else
              write(io6,*)
              write(io6,*)'ERROR: FLEMARB begin time exceeds current',&
                          ' start time'
              write(io6,*)'   -- current begin time  :',ndhrb,nsecb
              write(io6,*)'   -- FLEMARB begin time :',ndhrqb6(iq,iv),&
                           nsecqb6(iq,iv)
              stop 'Halted in INITPUF -- see list file'
           endif
! ---      Are new emissions data needed?
           if(nstep6(iv).EQ.0) then
              ilocate=1
           else
              call TPERIOD(1,ndhre,nsece,ndhrqb6(1,iv),nsecqb6(1,iv),&
                           ndhrqe6(1,iv),nsecqe6(1,iv),ilocate)
           endif
! ---      Read new emissions data if needed
           if(ilocate.EQ.1) then
611           continue
              call RDEM6(io,nfl2,nse6,cid6,ibsrc6(iv),iesrc6(iv),&
                         verflarb(iv),t2btz6(iv),ldb,io6,ibegh,&
                         ibegs,iendh,iends,em6dat,ieof)
              if(ieof.EQ.1) then
                 write(io6,*)
                 write(io6,*)'EOF in FLEMARB.DAT file ',iv
                 write(io6,*)'Halted at current end time :',ndhre,nsece
                 write(*,*)'EOF in FLEMARB.DAT file ',iv
                 write(*,*)'Halted at current end time :',ndhre,nsece
                 stop
              endif
! ---         Test emissions period relative to beginning of timestep
              call TPERIOD(-1,ndhrb,nsecb,ibegh,ibegs,iendh,iends,iloc)
! ---         Keep reading if emission period ends before start of step
              if(iloc.EQ.1) goto 611
! ---         Test period relative to end of timestep
              call TPERIOD(1,ndhre,nsece,ibegh,ibegs,iendh,iends,iloc)
              if(iloc.EQ.-1)then
                 write(io6,*)
                 write(io6,*)'ERROR: FLEMARB begin time later than ',&
                             'end of timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- FLEMARB start time :',ibegh,ibegs
                 stop 'Halted in INITPUF -- see list file'
              endif
! ---         Swap variable emissions data (em6dat) into /FL2/ arrays
              nstep6(iv)=nstep6(iv)+1
              iq=nstep6(iv)
              if(iq.GT.mxqstep) then
                 write(io6,*)'ERROR: FLEMARB has too many periods ',&
                             'in the current timestep'
                 write(io6,*)'   -- current end time    :',ndhre,nsece
                 write(io6,*)'   -- FLEMARB start time :',ibegh,ibegs
                 write(io6,*)'   -- Max allowed (MXQSTEP) ',mxqstep
                 stop 'Halted in INITPUF -- see list file'
              endif
              ndhrqb6(iq,iv)=ibegh
              nsecqb6(iq,iv)=ibegs
              ndhrqe6(iq,iv)=iendh
              nsecqe6(iq,iv)=iends
! ---         Loop over sources
              do i=ibsrc6(iv),iesrc6(iv)
!---             Apply projection/datum translation to position
                 call GLOBE(io6,caction,datumfl2(iv),vecti,datum,&
                            vecto,em6dat(1,i),em6dat(2,i),xfl2,yfl2,&
                            izone,c4hem)
! ---            Compute relative grid coordinates
                 xfl2grd(iq,i)=(1000.*xfl2-xorig)*dgridi
                 yfl2grd(iq,i)=(1000.*yfl2-yorig)*dgridi
                 htfl2(iq,i)=em6dat(3,i)
                 elfl2(iq,i)=em6dat(4,i)
                 dfl2(iq,i)=em6dat(5,i)
! ---            Do not interpret type of fluxes at this point
                 tfl2(iq,i)=em6dat(6,i)
                 ufl2(iq,i)=em6dat(7,i)
                 vfl2(iq,i)=em6dat(8,i)
                 wfl2(iq,i)=em6dat(9,i)
                 sy0fl2(iq,i)=em6dat(10,i)
                 sz0fl2(iq,i)=em6dat(11,i)
                 lqneg=.FALSE.
                 do is=1,nse6
                    k=is+nvarfl
                    qfl2(ixrem6(is),iq,i)=em6dat(k,i)
                    if(qfl2(ixrem6(is),iq,i).LT.0.0) lqneg=.TRUE.
                 enddo
! ---            Report problem if any emissions are negative
                 if(lqneg) then
                    write(io6,*)
                    write(io6,*)'ERROR in subr. INITPUF -- Invalid ',&
                    'emission rate  (must NOT be negative)'
                    write(io6,*)' -- FLEMARB Source = ',i
                    problem=.TRUE.
                 endif
              enddo
! ---         Get another period of emissions data if the end of the
! ---         timestep is not within the period just read
              if(iloc.EQ.1) goto 611
            endif
            do i=ibsrc6(iv),iesrc6(iv)
               call FLARES(mflare,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                         dt,smxi,ldb,mfact,metfm,np,newpuf,problem)
            enddo
         enddo
      endif
! ------------------------------------------------------------
! --- Loop over Road Sources with CONSTANT/SCALED emissions
! ------------------------------------------------------------
      if(nrdseg1.GT.0) then
         mroad=1
         iv=1
         do i=1,nrdseg1
            call ROADS(mroad,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                       dt,smxi,ldb,mfact_rd,np,newpuf,problem)
         enddo
      endif
! ------------------------------------------------------------
! --- Loop over road sources with VARIABLE emissions
! ------------------------------------------------------------
      if(nrd2.gt.0) then
         mroad=2
! ---    Update variable emissions from RDEMARB files
! ---    Loop over files to check/update all sources
         do iv=1,nrddat
! ---      File unit number
           io=iord2+iv-1
! ---      Check if last emission period used in previous step extends
! ---      into current timestep (beginning of step < end of q-period)
           iq=nstep7(iv)
           call TPERIOD(-1,ndhrb,nsecb,ndhrqb7(iq,iv),nsecqb7(iq,iv),&
                        ndhrqe7(iq,iv),nsecqe7(iq,iv),ilocate)
           if(ilocate.EQ.0) then
! ---         Reset emissions data arrays so that last emiss
! ---         period of previous step is first period of current step
              if(iq.GT.1) then
                 ndhrqb7(1,iv)=ndhrqb7(iq,iv)
                 nsecqb7(1,iv)=nsecqb7(iq,iv)
                 ndhrqe7(1,iv)=ndhrqe7(iq,iv)
                 nsecqe7(1,iv)=nsecqe7(iq,iv)
                 do i=ibsrc7(iv),iesrc7(iv)
                    htrd2(1,i)=htrd2(iq,i)
                    sy0rd2(1,i)=sy0rd2(iq,i)
                    sz0rd2(1,i)=sz0rd2(iq,i)
                    do is=1,nse7
                      qrd2(ixrem7(is),1,i)=qrd2(ixrem7(is),iq,i)
                    enddo
                 enddo
                 nstep7(iv)=1
              endif
           elseif(ilocate.EQ.1) then
! ---         First set of emissions data this step must be read in
              nstep7(iv)=0
           else
              write(io6,*)
              write(io6,*)'ERROR: RDEMARB begin time exceeds current',&
                          ' start time'
              write(io6,*)'   -- current begin time  :',ndhrb,nsecb
              write(io6,*)'   -- RDEMARB begin time :',ndhrqb7(iq,iv),&
                           nsecqb7(iq,iv)
              stop 'Halted in INITPUF -- see list file'
           endif
! ---      Are new emissions data needed?
           if(nstep7(iv).EQ.0) then
              ilocate=1
           else
              call TPERIOD(1,ndhre,nsece,ndhrqb7(1,iv),nsecqb7(1,iv),&
                           ndhrqe7(1,iv),nsecqe7(1,iv),ilocate)
           endif
! ---      Read new emissions data if needed
           if(ilocate.EQ.1) then
711           continue
              call RDEM7(io,nrd2,nse7,cid7,ibsrc7(iv),iesrc7(iv),&
                         verrdarb(iv),t2btz7(iv),ldb,io6,ibegh,&
                         ibegs,iendh,iends,em7dat,ieof)
              if(ieof.EQ.1) then
                 write(io6,*)
                 write(io6,*)'EOF in RDEMARB.DAT file ',iv
                 write(io6,*)'Halted at current end time :',ndhre,nsece
                 write(*,*)'EOF in RDEMARB.DAT file ',iv
                 write(*,*)'Halted at current end time :',ndhre,nsece
                 stop
              endif
! ---         Test emissions period relative to beginning of timestep
              call TPERIOD(-1,ndhrb,nsecb,ibegh,ibegs,iendh,iends,iloc)
! ---         Keep reading if emission period ends before start of step
              if(iloc.EQ.1) goto 711
! ---         Test period relative to end of timestep
              call TPERIOD(1,ndhre,nsece,ibegh,ibegs,iendh,iends,iloc)
              if(iloc.EQ.-1)then
                 write(io6,*)
                 write(io6,*)'ERROR: RDEMARB begin time later than ',&
                             'end of timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- RDEMARB start time :',ibegh,ibegs
                 stop 'Halted in INITPUF -- see list file'
              endif
! ---         Swap variable emissions data (em7dat) into |MROAD2| arrays
              nstep7(iv)=nstep7(iv)+1
              iq=nstep7(iv)
              if(iq.GT.mxqstep) then
                 write(io6,*)'ERROR: RDEMARB has too many periods ',&
                             'in the current timestep'
                 write(io6,*)'   -- current end time    :',ndhre,nsece
                 write(io6,*)'   -- RDEMARB start time :',ibegh,ibegs
                 write(io6,*)'   -- Max allowed (MXQSTEP) ',mxqstep
                 stop 'Halted in INITPUF -- see list file'
              endif
              ndhrqb7(iq,iv)=ibegh
              nsecqb7(iq,iv)=ibegs
              ndhrqe7(iq,iv)=iendh
              nsecqe7(iq,iv)=iends
! ---         Loop over sources
              do i=ibsrc7(iv),iesrc7(iv)
                 htrd2(iq,i)=em7dat(1,i)
                 sy0rd2(iq,i)=em7dat(2,i)
                 sz0rd2(iq,i)=em7dat(3,i)
                 lqneg=.FALSE.
                 do is=1,nse7
                    k=is+nvarrd
                    qrd2(ixrem7(is),iq,i)=em7dat(k,i)
                    if(qrd2(ixrem7(is),iq,i).LT.0.0) lqneg=.TRUE.
                 enddo
! !!!
!               write(io6,*)'INITPUF:  step,source    = ',iq,i
!               write(io6,*)'          species,ixrem7 = ',is,ixrem7(is)
!               write(io6,*)'          em7dat(k,i)    = ',em7dat(k,i)
!               write(io6,*)'          qrd2           = ',
!     &                                qrd2(ixrem7(is),iq,i)
! !!!
! ---            Report problem if any emissions are negative
                 if(lqneg) then
                    write(io6,*)
                    write(io6,*)'ERROR in subr. INITPUF -- Invalid ',&
                    'emission rate  (must NOT be negative)'
                    write(io6,*)' -- RDEMARB Source = ',i
                    problem=.TRUE.
                 endif
              enddo
! ---         Get another period of emissions data if the end of the
! ---         timestep is not within the period just read
              if(iloc.EQ.1) goto 711
            endif
! ---       Fixed indexing in call for ROADS for runs with multiple 
! ---       external road emission files RDEMARB.DAT, L150817
!j          do i=1,nrdseg2
            do i=nrdseg2b(iv),nrdseg2e(iv)
               call ROADS(mroad,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                         dt,smxi,ldb,mfact_rd,np,newpuf,problem)
            enddo
         enddo
      endif
! ------------------------------------------------------------
! --- Loop over spray sources with VARIABLE emissions
! ------------------------------------------------------------
      if(nsp2.gt.0) then
         mspray=2
! ---    Update variable emissions from SPEMARB files
! ---    Loop over files to check/update all sources
         do iv=1,nspdat
! --- Open scratch file for processing spray segment coordinates
           OPEN(iospx,form='unformatted',status='scratch')
           nspseg2=0
! ---      File unit number
           io=iosp2+iv-1
! ---      Set up map projection conversion to CALPUFF coordinates
! ---      Input is from SPEMARB.DAT header - |SPRAY2|
           iutmi=iutmznsp2(iv)
           if(utmhemsp2(iv).EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
           cmapi=pmapsp2(iv)
           if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---      Set conversion vectors
           call GLOBE1(cmapi,iutmi,tmsone,rnlat1sp2(iv),rnlat2sp2(iv),&
                  rnlat0sp2(iv),relon0sp2(iv),&
                  feastsp2(iv),fnorthsp2(iv),&
                  cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                  feast,fnorth,&
                  caction,vecti,vecto)
! ---      Check if last emission period used in previous step extends
! ---      into current timestep (beginning of step < end of q-period)
           iq=nstep8(iv)
           call TPERIOD(-1,ndhrb,nsecb,ndhrqb8(iq,iv),nsecqb8(iq,iv),&
                        ndhrqe8(iq,iv),nsecqe8(iq,iv),ilocate)
           if(ilocate.EQ.0) then
! ---         Reset emissions data arrays so that last emiss
! ---         period of previous step is first period of current step
              if(iq.GT.1) then
                 ndhrqb8(1,iv)=ndhrqb8(iq,iv)
                 nsecqb8(1,iv)=nsecqb8(iq,iv)
                 ndhrqe8(1,iv)=ndhrqe8(iq,iv)
                 nsecqe8(1,iv)=nsecqe8(iq,iv)
                 do i=ibsrc8(iv),iesrc8(iv)
                    htsp2(1,i)=htsp2(iq,i)
                    sy0sp2(1,i)=sy0sp2(iq,i)
                    sz0sp2(1,i)=sz0sp2(iq,i)
                    diasp2(1,i)=diasp2(iq,i)
                    dcutsp2(1,i)=dcutsp2(iq,i)
                    do is=1,nse8
                      qsp2(ixrem8(is),1,i)=qsp2(ixrem8(is),iq,i)
                    enddo
                 enddo
                 nstep8(iv)=1
              endif
           elseif(ilocate.EQ.1) then
! ---         First set of emissions data this step must be read in
              nstep8(iv)=0
           else
              write(io6,*)
              write(io6,*)'ERROR: SPEMARB begin time exceeds current',&
                          ' start time'
              write(io6,*)'   -- current begin time  :',ndhrb,nsecb
              write(io6,*)'   -- SPEMARB begin time :',ndhrqb8(iq,iv),&
                           nsecqb8(iq,iv)
              stop 'Halted in INITPUF -- see list file'
           endif
! ---      Are new emissions data needed?
           if(nstep8(iv).EQ.0) then
              ilocate=1
           else
              call TPERIOD(1,ndhre,nsece,ndhrqb8(1,iv),nsecqb8(1,iv),&
                           ndhrqe8(1,iv),nsecqe8(1,iv),ilocate)
           endif
! ---      Read new emissions data if needed
           if(ilocate.EQ.1) then
811           continue
              call RDEM8(io,nsp2,nse8,cid8,ibsrc8(iv),iesrc8(iv),&
                         versparb(iv),t2btz8(iv),ldb,io6,ibegh,&
                         ibegs,iendh,iends,em8dat,ieof)
              if(ieof.EQ.1) then
                 write(io6,*)
                 write(io6,*)'EOF in SPEMARB.DAT file ',iv
                 write(io6,*)'Halted at current end time :',ndhre,nsece
                 write(*,*)'EOF in SPEMARB.DAT file ',iv
                 write(*,*)'Halted at current end time :',ndhre,nsece
                 stop
              endif
! ---         Test emissions period relative to beginning of timestep
              call TPERIOD(-1,ndhrb,nsecb,ibegh,ibegs,iendh,iends,iloc)
! ---         Keep reading if emission period ends before start of step
              if(iloc.EQ.1) goto 811
! ---         Test period relative to end of timestep
              call TPERIOD(1,ndhre,nsece,ibegh,ibegs,iendh,iends,iloc)
              if(iloc.EQ.-1)then
                 write(io6,*)
                 write(io6,*)'ERROR: SPEMARB begin time later than ',&
                             'end of timestep'
                 write(io6,*)'   -- current end time   :',ndhre,nsece
                 write(io6,*)'   -- SPEMARB start time :',ibegh,ibegs
                 stop 'Halted in INITPUF -- see list file'
              endif
! ---         Swap variable emissions data (em8dat) into |MSPRAY2| arrays
              nstep8(iv)=nstep8(iv)+1
              iq=nstep8(iv)
              if(iq.GT.mxqstep) then
                 write(io6,*)'ERROR: SPEMARB has too many periods ',&
                             'in the current timestep'
                 write(io6,*)'   -- current end time    :',ndhre,nsece
                 write(io6,*)'   -- SPEMARB start time :',ibegh,ibegs
                 write(io6,*)'   -- Max allowed (MXQSTEP) ',mxqstep
                 stop 'Halted in INITPUF -- see list file'
              endif
              ndhrqb8(iq,iv)=ibegh
              nsecqb8(iq,iv)=ibegs
              ndhrqe8(iq,iv)=iendh
              nsecqe8(iq,iv)=iends
! ---         Loop over sources
              do i=ibsrc8(iv),iesrc8(iv)
                 htsp2(iq,i)=em8dat(4,i)
                 sy0sp2(iq,i)=em8dat(5,i)
                 sz0sp2(iq,i)=em8dat(6,i)
                 diasp2(iq,i)=em8dat(13,i)
                 dcutsp2(iq,i)=em8dat(14,i)
                 if(em8dat(4,i).ne.em8dat(10,i).or.em8dat(5,i)&
                .ne.em8dat(11,i).or.em8dat(6,i).ne.em8dat(12,i))then
                  write(io6,*)'ERROR: SPEMARB emission parameters '
                  write(io6,*)'must match for beginning and end ' 
                  write(io6,*)'of segments.'
                  write(io6,*)' ht(begin,end):',em8dat(4,i),em8dat(10,i)
                  write(io6,*)'sy0(begin,end):',em8dat(5,i),em8dat(11,i)
                  write(io6,*)'sz0(begin,end):',em8dat(6,i),em8dat(12,i)
                  stop 'Halted in INITPUF -- see list file'
                 endif
                 lqneg=.FALSE.
                 do is=1,nse8
                    k=is+nvarsp
                    qsp2(ixrem8(is),iq,i)=em8dat(k,i)
                    if(qsp2(ixrem8(is),iq,i).LT.0.0) lqneg=.TRUE.
                 enddo
! ---            Report problem if any emissions are negative
                 if(lqneg) then
                    write(io6,*)
                    write(io6,*)'ERROR in subr. INITPUF -- Invalid ',&
                    'emission rate  (must NOT be negative)'
                    write(io6,*)' -- RDEMARB Source = ',i
                    problem=.TRUE.
                 endif
! ---            Transfer endpoints to modeling coordinates
                 call GLOBE(iolst,caction,datumsp2(iv),&
                  vecti,datum,vecto,&
                  em8dat(1,i),em8dat(2,i),xyzb(1),xyzb(2),iutmz,c4hem)
                 xyzb(3)=em8dat(3,i)
                 call GRIDSPRAY(cid8(i),i,iq,1,xyzb,nspseg2)
                 call GLOBE(iolst,caction,datumsp2(iv),&
                  vecti,datum,vecto,&
                  em8dat(7,i),em8dat(8,i),xyze(1),xyze(2),iutmz,c4hem)
                 xyze(3)=em8dat(9,i)
                 call GRIDSPRAY(cid8(i),i,iq,2,xyze,nspseg2)
              enddo
! ---         Get another period of emissions data if the end of the
! ---         timestep is not within the period just read
              if(iloc.EQ.1) goto 811
            endif
! ---         Process scratch file (conditional)
              if(nspseg2.GT.0) then
               call SPRAYSEG(2)
              endif
            do i=1,nspseg2
               call SPRAYS(mspray,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                         dt,smxi,ldb,mfact_sp,np,newpuf,problem)
            enddo
         enddo
      endif
! ----------------------------------------------------------------
! --- Loop over all boundary condition sources (1-hour steps)
! ----------------------------------------------------------------
      if(nbc.gt.0) then
         if(ntypebc2.GT.0) then
! ---       Check if it is time to update variable concentrations
            if(ndhre.lt.ibegbc)then
               write(io6,*)
               write(io6,*)'ERROR -- BCON date/time exceeds current'
               write(io6,*)'      -- current date/time :',ndhre
               write(io6,*)'      -- BCON date/time    :',ibegbc
               write(*,*)
               problem=.true.
            elseif(ndhre.gt.iendbc)then
! ---          Obtain BCON data for current model date/time
               if(mbcon.EQ.1) then
                  call RDEMBC(ldb,ndhre)
               elseif(mbcon.EQ.2) then
                  call RDEMBC2(ldb,ndhre)
               else
                 write(io6,*)
                 write(io6,*)'ERROR -- BCON option is invalid'
                 write(io6,*)'      -- expected : 1 or 2'
                 write(io6,*)'      -- MBCON    :',mbcon
                 write(*,*)
                 problem=.true.
               endif
            endif
         endif
! ---    Loop over all boundary segments and define new puffs
         do i=1,nbc
            call BCS1(i,nspec,dt,ldb,np,newpuf,problem)
         enddo
      endif
!
!
      if(PROBLEM) then
         write(*,*)
         stop 'Halted in INITPUF -- see list file.'
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine areas1(i,nspec,dthr,xtmp1,ldbhr,mfact,np,&
                        newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230            AREAS1
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               polygon AREA SOURCES with constant emissions.
!
! --- UPDATE
! --- V6.42_x1.1 - TNG-7.1.0  141230  (DGS)
!                                : Replace VEMFAC with QFACTOR subr.
!                                  for emissions scaling
!                                : Scale-factors for all sources in
!                                  one table array
! --- V6.402-V6.42_x1.1 140521   : Fix debug list-file output list
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
! --- V6.265-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.261-V6.265 090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
! --- V6.22-V6.261  080520  (DGS): Place rise tables in DA file
! --- V5.72-V6.22   070921  (DGS): Change J to I in debug output
! --- V5.7-V5.72    031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.4-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
! --- V5.1-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.1     990729  (DGS): enforce minimum sigma-y at source
! --- V5.0-V5.0     980918  (DGS): initial sigma-y is zero
! --- V5.0-V5.0     980515  (DGS): ZITIBL array added
! --- V5.0-V5.0     980304  (DGS): drop any new puffs if emissions = 0 
! --- V5.0-V5.0     980304  (DGS): place source sigmas in SY0,SZ0
! --- V4.0-V5.0     971107  (DGS): add variable emissions factor call
!                                  to function VEMFAC
!                   971107  (DGS): reverse arguments of vertex arrays
! --- V4.0-V5.0     971107  (DGS): ZPB,ZPE arrays added
!
! --- INPUTS:
!             I - integer - Area source index
!         NSPEC - real    - Number of species modeled
!          DTHR - real    - Length (seconds) of a basic time step
! XTMP1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!         LDBHR - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, WSCALM, PLX0
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MHFTSZ
!     Common block /GRID/ variables:
!           DGRID, IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /METHR/ variables:
!           TEMPSS(mxss,,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP
!     Common block /METHD/ variables:
!           I2DMET
!     Common block /PUFF/ variables:
!           NPUFFS
!     Common block /AR1/ variables:
!           XAR1GRD(mxvertp1,mxarea),YAR1GRD(mxvertp1,mxarea),
!           HTAR1(mxarea),ELAR1(mxarea),SZ0AR1(mxarea),NVERT1(mxarea),
!           QAR1(mxspec,mxarea),NEWAR1(mxarea), NSAR1,
!           IDSFAR1(mxspec,mxarea),IXREFAR1(mxspar)
!     |MQSCALE| variables:
!           NQSFTAB,IQSFTYPE(nqsftab),QSFTAB(mxqsf,nqsftab)
!     Parameters:
!           MXNX, MXNY, MXNZ,MXMETDOM, MXNZP1, MXSPEC, MXAREA, IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /AR1/ variables:
!           NEWAR1(mxarea)
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!
! --- AREAS1 called by:  INITPUF
! --- AREAS1 calls:      RLSMET, ROLLDN, SYAREA, SWAP, INJECT, 
!                        ZEROTAB, SRCTABOUT, GETCELL, QFACTOR
!----------------------------------------------------------------------
! --- Modules
      use mqscale
      include 'params.puf'
      include 'comparm.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methr.puf'
      include 'ar1.puf'
      include 'puff.puf'
      include 'slug.puf'
! frr (09/01)
      include 'methd.puf'
!
      real xtmp1(mxmetdom)
      real q(mxspec)
      logical ldbhr,problem
      data zero/0.0/
! ***
      if(ldbhr) then
         write(io6,*)
         write(io6,*) 'AREAS1:  Source Number    ',i
      endif
! ***
! --- Control-file area sources are Type = 3
      itype=3
!
! --- Determine the MET grid 1 (outermost) point closest to the source
      ixs=1.0+xar1grd(nvert1(i)+1,i)
      iys=1.0+yar1grd(nvert1(i)+1,i)
!
! --- Source off the computational grid -- write a FATAL message
      if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
         jecomp)then
         write(io6,20)i,ixs,iys
20       format(/1x,'FATAL -- an area source with constant emissions ',&
         'is off the computational grid'/1x,'source no. (i) = ',i6,2x,&
         'nearest grid point (ixs,iys) = (',i5,',',i5,')')
         problem=.TRUE.
         return
      endif
! --- Now determine the met. grid point closest to the source
      call GETCELL('AREAS1      ','GRIDIJ',1,xar1grd(nvert1(i)+1,i),&
                   yar1grd(nvert1(i)+1,i),zero,zero,ixs,iys,md)
!
! --- Extract release height wind speed & other met. variables
      htrel=htar1(i)
      call rlsmet(ldbhr,ixs,iys,md,htrel,mfact,&
                  mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                  ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
!
! frr (09/01) new calmet format (2D temp)
      if(i2dmet.EQ.1) then
         temp=temp2d(ixs,iys,md)
      elseif(i2dmet.EQ.0) then
         temp=tempss(issta,md)
      else
         write(*,*)'Subr. AREAS1:  Invalid I2DMET = ',i2dmet
         stop
      endif
! --- Trap missing power-law exponent (substitute default)
      if(plexp.LT.-900.) then
! ---    Use default
         plx=plx0(istab)
      else
! ---    Use it
         plx=plexp
      endif
! --- Swap emission rate for source 'i' into 1D array and scale
      qtot=0.0
      do is=1,nspec
         q(is)=qar1(is,i)
! ---    Apply emissions scale-factor (conditional)
         if(nsar1.GT.0) then
! ---       Pointer to scale factor tables
            k=idsfar1(is,i)
            if(k.GT.0) then
               kk=ixrefar1(k)
               call QFACTOR(io6,iqsftype(kk),itype,ws,istab,temp,iqsf)
               q(is)=q(is)*qsftab(iqsf,kk)
            endif
         endif
         qtot=qtot+q(is)
      enddo
! --- Return without generating new puffs if all species have zero
! --- mass in puff
      if(qtot.EQ.0.0) then
! ***
         if(ldbhr) then
            write(io6,*)
            write(io6,*) 'No emissions this step from Source ',i
            write(io6,*)
         endif
! ***
         return
      endif
! --- Initialize source tabulation specifications to zero
      call ZEROTAB
!
! --- Compute the number of puffs/slugs released this hour
! --- from this source
      npnew=ws*xtmp1(md)+1
      npnew=max0(npnew,1)
      npnew=min0(npnew,mxnew)
      newar1(i)=npnew
      newpuf=newpuf+npnew
! --- If puff arrays are full, remove puffs that are off the grid
! --- and "roll down" arrays to make room for new puffs
! --- (this also applies to the DA file of tabulated arrays)
      if(npuffs+newpuf.gt.mxpuff) call rolldn(np)
! --- Check to see if roll-down was adequate
      if(npuffs+newpuf.gt.mxpuff)then
         write(io6,*) ' ERROR in subr. AREAS1'
         write(io6,*)'Too many puffs on grid for array dimensions -- ',&
         'NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
         ' MXPUFF = ',mxpuff
         write(*,*)
         stop 'Halted in AREAS1 -- see list file.'
      endif
!
! --- DT is the time (sec) over which each puff is emitted
      dt=dthr/float(npnew)
!
! --- Set variance associated with buoyancy-enhanced growth @ final rise
      xf=0.0
      rise=0.0
      bidsq=0.0
! --- Set downwash flag to zero (no building downwash)
      idw=0
! --- Set final plume height
      heff=htar1(i)
! --- Set sigma y, sigma z at the source
      szsrc=amax1(szmin,sz0ar1(i))
      call SYAREA(xar1grd(1,i),yar1grd(1,i),nvert1(i),dgrid,flow,ldbhr,&
                  sysrc)
      sysrc=amax1(symin,sysrc)
!******
      if(ldbhr)then
! --- 6.42_x1.1 140521
         write(io6,204)i,ilayer,ixs,iys,md,ws,istab,dpbl,issta,npnew,&
          newpuf,dt
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,2x,&
          'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'NPNEW: ',i4,2x,&
          'NEWPUF: ',i5/5x,'DT: ',f7.2)
!
         write(io6,210)idw,heff,rise,xf,sysrc,szsrc
210      format(5x,'IDW: ',i5,2x,'HEFF: ',f8.2,2x,'RISE : ',f8.2,2x,&
          'XF : ',f8.2,2x,'SYSRC: ',f8.2,2x,'SZSRC: ',f8.2)
!
         write(io6,208)i,np,iru,dt,sqrts
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,&
          'IRU: ',i2,2x,'DT: ',f7.2,2x,'SQRTS: ',f7.3)
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnew
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call swap(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xar1grd(nvert1(i)+1,i)
         ypb(np)=yar1grd(nvert1(i)+1,i)
         zitibl(np)=-1.0
         elbase(np)=elar1(i)
         sigyb(np)=symin
         sigzb(np)=szsrc
! ---    Final plume rise results
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xf
         bidfnl(np)=bidsq
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=htar1(i)
         temit0(np)=dt
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations
         idw0(np)=idw
         heff20(np)=heff
         sy0(np)=sysrc
         sz0(np)=szsrc
! ---    Emission period index for this puff
         iemstep(np)=1
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from this source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =3 for const. area sources)
         irlsnum(np)=j
         isrcnum(np)=i
         isrctyp(np)=itype
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    Note that "oldest" end of slug is at the source, as is the
! ---    "newest end; oldest end moves away and grows during the step,
! ---    while newest end remains fixed in size at the source.
!
         if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---       Set coordinates of oldest end of slug (in met. grid units)
            xpe(np)=xpb(np)
            ype(np)=ypb(np)
            xtote(np)=xtotb(np)
            tmtote(np)=tmtotb(np)
! ---       Set sigmas for oldest end of slug
            sigye(np)=sigyb(np)
            sigze(np)=sigzb(np)
         endif
!
! -------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! -------------------------------------------------------------------
! ---    Set partial penetration variables to null values
         fmix=1.0
         hmax=dpbl
         call INJECT(np,nspec,dt,mfact0,q,heff,hmax,dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer NULL tabulated arrays record to DA file
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
!
      return
      end
