      subroutine areas2(ia,i,nspec,ndhrb,nsecb,ndhre,nsece,dtsec,smxi1,&
                        ldb,mfact,metfm,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521            AREAS2
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               BUOYANT polygon AREA SOURCES.
!
! --- UPDATE
! --- V6.402-V6.42_x1.1 140521   : Fix debug list-file output list
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
