! --- V6.1-V6.22    070921  (DGS): Change J to I in debug output
!                   070921  (DGS): Replace intrinsic function AMIN0()
!                                  with MIN() when both result and
!                                  arguments are integers
! --- V5.74-V6.1    050915  (DGS): generalize treatment of time for
!                                  variable emissions files --
!                                  resolve time to seconds, and
!                                  allow variable emissions timesteps
!                           (DGS): adopt partial penetration adjustment
! --- V5.72-V5.74   040715  (DGS): add METFM=5 (AERMET)
! --- V5.7-V5.72    031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.4-V5.7     030402  (DGS): add MPRIME, SYSRC, SZSRC, HTR to
!                                  NUMRISE call (not used here)
! --- V5.1-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.1     990729  (DGS): enforce minimum sigma-y at source
! --- V5.0-V5.0     980918  (DGS): initial sigma-y is zero
! --- V5.0-V5.0     980807  (DGS): use final rise CALMET/PROFILE winds 
!                                  to set release step
! --- V5.0-V5.0     980515  (DGS): ZITIBL array added
! --- V5.0-V5.0     980430  (DGS): assign PROBLEM (compiler warning)
! --- V5.0-V5.0     980304  (DGS): place source sigmas in SY0,SZ0
! --- V4.0-V5.0     971107  (DGS): remove heat flux from AR2 input
!                                  processing
!                   971107  (DGS): add ground elevation and initial
!                                  sigma-z to AR2 input
! --- V4.0-V5.0     971107  (DGS): ZPB,ZPE arrays added
!
! --- INPUTS:
!            IA - integer - BAEMARB.DAT file index
!             I - integer - Area source index
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         DTSEC - real    - Length (seconds) of a basic time step
! SMXI1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!         METFM - integer - Meteorological data format
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /AR2/ variables:
!           NTR0
!           NSTEP3(mxemdat),NDHRQB3(mxqstep,mxemdat),
!           NSECQB3(mxqstep,mxemdat),NDHRQE3(mxqstep,mxemdat),
!           NSECQE3(mxqstep,mxemdat),XAR2GRD(mxvertp1,mxqstep,mxarea),
!           YAR2GRD(mxvertp1,mxqstep,mxarea),HTAR2(mxqstep,mxarea),
!           ELAR2(mxqstep,mxarea),TKAR2(mxqstep,mxarea),
!           REFAR2(mxqstep,mxarea),WEFAR2(mxqstep,mxarea),
!           SZ0AR2(mxqstep,mxarea),
!           QAR2(mxspec,mxqstep,mxarea),AREA2(mxqstep,mxarea),
!           NVERT2(mxarea),NEWAR2(mxarea)
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, WSCALM, PLX0
!     Common block /DISPDAT/ variables:
!           JSUP
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MHFTSZ, MPARTLBA
!     Common block /GRID/ variables:
!           DGRID, IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /METHD/ variables:
!           ELEV(mxnx,mxny), I2DMET
!     Common block /METHR/ variables:
!           TEMPSS(mxss,,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP, PTG(2), DPTINVO
!     Common block /PUFF/ variables:
!           NPUFFS
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXSPEC, MXAREA, MXQSTEP, MXEMDAT,
!           MXRISE, MXMETDOM, IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /AR2/ variables:
!           NEWAR2(mxarea)
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!     Common block /SRCTAB/ variables:
!           NTR,XTR(mxrise),ZTR(mxrise),RTR(mxrise),HTR(mxrise)
!
! --- AREAS2 called by:  INITPUF
! --- AREAS2 calls:      RLSMET, ROLLDN, NUMMET, NUMRISE, NUMCLIP,
!                        DELTSEC, SYAREA, SWAP, INJECT, PTLAPS, PRFPP,
!                        NRISEHD, ZEROTAB, SRCTABOUT, GETCELL
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      include 'ar2.puf'
      include 'comparm.puf'
      include 'dispdat.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
      include 'slug.puf'
      include 'srctab.puf'
!
      real smxi1(mxmetdom)
      real q(mxspec)
      logical ldb,problem
      data twobypi/0.63662/
      data zero/0.0/
! --- Set minimum allowed potential temperature gradient PTGRAD0,
      data ptgrad0/.005/
! --- Set source type (4 for buoyant areas)
      data itype4/4/
! --- No PRIME downwash for NUMRISE call
      data mprime/0/
! --- Set dummy initial sigmas for NUMRISE call (used only with PRIME)
      data sydum/1.0/, szdum/1.0/
!
! ***
      if(ldb) then
         write(io6,*)
         write(io6,*) 'AREAS2:  Source Number    ',i
      endif
! ***
!
! --- Initialize number of new puffs from this source, this timestep
      newar2(i)=0
! --- Loop over emission periods within this timestep
      do 100 it=1,nstep3(ia)
!
! --------------------------------------------------------------------
! ---    Do not generate puff for this step if all emissions are ZERO!
! --------------------------------------------------------------------
         qsum=0.0
         do is=1,nspec
            qsum=qsum+qar2(is,it,i)
         enddo
         if(qsum.EQ.0.0) goto 100
! ---    Initialize source tabulation arrays to zero
         call ZEROTAB
! ---    Determine MET grid 1 (outermost) point closest to the source
         ixs=1.0+xar2grd(nvert2(i)+1,it,i)
         iys=1.0+yar2grd(nvert2(i)+1,it,i)
!
!
! ---    Source off the computational grid -- write a FATAL message
         if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.&
            iys.gt.jecomp)then
            write(io6,20)i,it,ixs,iys
20       format(/1x,'FATAL -- an area source with variable ',&
         'emissions is off the computational grid'/&
         1x,'source no. (i) = ',i6,2x,'emis. per. (it) = ',i6,2x,&
         'nearest grid point (ixs,iys) = (',i5,',',i5,')')
            problem=.TRUE.
            goto 100
         endif
! ---    Now determine the met. grid point closest to the source
         call GETCELL('AREAS2      ','GRIDIJ',1,&
                      xar2grd(nvert2(i)+1,it,i),&
                      yar2grd(nvert2(i)+1,it,i),zero,zero,ixs,iys,md)
!
! --------------------------------
! --- Numerical Plume Rise Section
! --------------------------------
!
! ---    Construct met. profiles at this grid cell
         call NUMMET(ixs,iys,md,ldb)
!
! ---    Prepare data header record for RISE.DAT file
! ---    (control for output to scratch file is within subroutine)
         call NRISEHD(itype4,i,ndhrb,nsecb,ndhre,nsece,ndhrqb3(it,ia),&
              nsecqb3(it,ia),ndhrqe3(it,ia),nsecqe3(it,ia),elar2(it,i),&
              htar2(it,i),tkar2(it,i),refar2(it,i),wefar2(it,i),&
              xar2grd(nvert2(i)+1,it,i),yar2grd(nvert2(i)+1,it,i))
!
! --     Tabulate plume rise in arrays
         ntr=ntr0
         call NUMRISE(mprime,ldb,htar2(it,i),tkar2(it,i),&
                   refar2(it,i),wefar2(it,i),sydum,szdum,&
                   ntr,xtr,ztr,rtr,htr)
         rise=ztr(ntr)-htar2(it,i)
         htrel=htar2(it,i)
!
! ---    Extract stack height wind speed & other met. variables
         call RLSMET(ldb,ixs,iys,md,htrel,mfact,&
                     mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,ustr,&
                     wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
! ---    Set sigma y, sigma z at the source
         szsrc=AMAX1(szmin,sz0ar2(it,i))
         call SYAREA(xar2grd(1,it,i),yar2grd(1,it,i),nvert2(i),dgrid,&
                     flow,ldb,sysrc)
         sysrc=AMAX1(symin,sysrc)
!
! ---    Revise height for met data: choose height midway between
! ---    release (typically near ground for area sources) and minimum
! ---    of final rise and PBL depth (only if release is below PBL)
         if(mpartlba.EQ.1 .AND. htrel.LT.dpbl) then
            htmet=0.5*(htrel+AMIN1(dpbl,ztr(ntr)))
            call RLSMET(ldb,ixs,iys,md,htmet,mfact,&
                     mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,ustr,&
                     wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
         endif
         if(i2dmet.EQ.1) then
            tempk=temp2d(ixs,iys,md)
         elseif(i2dmet.EQ.0) then
            tempk=tempss(issta,md)
         else
            write(*,*)'Subr. AREAS2:  Invalid I2DMET = ',i2dmet
            stop
         endif
! ---    Trap missing power-law exponent (substitute default)
         if(plexp.LT.-900.) then
! ---       Use default
            plx=plx0(istab)
         else
! ---       Use it
            plx=plexp
         endif
!
! ---    Adjust for partial penetration of elevated stable layer
! ---    Calculate buoyancy flux (9.80665 m/s**2 = g)
         dtemp=tkar2(it,i)-tempk
         dtemp=amax1(dtemp,0.0)
         fluxb=(9.80665*refar2(it,i)**2*wefar2(it,i)/tkar2(it,i))*dtemp
         if(mpartlba.EQ.1 .and.&
            istab.LE.4 .and. fluxb.GT.0.0) then
! ---       Set thickness (m) of transition region above mixing height
            dzinv=30.
! ---       Estimate potential temperature gradient above mixed layer
            if(LCALGRD) then
! ---          Use temperatures provided by met model
               htinv=amax1(dpbl,htrel)
               call PTLAPS(ixs,iys,md,htinv,ptgrad0,dzinv,ptginv,tmix)
            else
! ---          Use default potential temperature lapse rate
               if(jsup.LE.4) then
                  ptginv=ptgrad0
               elseif(jsup.EQ.5) then
                  ptginv=amax1(ptg0(1),ptgrad0)
               else
                  ptginv=amax1(ptg0(2),ptgrad0)
               endif
            endif
! ---       Estimate potential temperature change across inversion
            if(dptinvo.GT.0.0) then
! ---          Use observed value provided in met file
               dptinv=dptinvo
            else
               dptinv=ptginv*dzinv
            endif
            call PRFPP(rise,fluxb,ws,htrel,dpbl,tempk,ptginv,&
                       dptinv,fmix,hmax)
! ---       Clip rise table at new final height
            call NUMCLIP(mxrise,htrel,rise,ntr,xtr,ztr,rtr,htr)
            if(ldb) then
               write(io6,*)'AREAS2 Partial Penetration --'
               write(io6,*)'ws,ptginv,dptinv =',ws,ptginv,dptinv
               write(io6,*)'fmix,hmax,ntr    =',fmix,hmax,ntr
               write(io6,*)'Revised rise rise trajectory:'
               write(io6,*)'    I          XTR        ZTR'
               do ir=1,ntr
                 write(io6,'(i5,2x,2f11.4)')ir,xtr(ir),ztr(ir)
               enddo
               write(io6,*)
            endif
!
         else
! ---       Set partial penetration variables to null values
            fmix=1.0
            hmax=dpbl
         endif
!
! ---    Set variance associated with buoyancy-enhanced growth @ final
! ---    rise
         xf=xtr(ntr)
         bidsq=twobypi*rtr(ntr)**2
! ---    Set downwash flag to zero (no building downwash)
         idw=0
! ---    Set final plume height
         heff=ztr(ntr)
!
! ---------------------------------------------------------
! ---    Compute number of puffs for this emissions period
! ---------------------------------------------------------
! ---    Use CALMET/PROFILE winds at final rise to set release/sampling
! ---    data
         if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
         call rlsmet(ldb,ixs,iys,md,heff,mfact,&
                     mfact0fr,ilayerfr,wsfr,flowfr,ivecfr,istab,&
                     dpbl,el,ustr,wstr,tsigvfr,tsigwfr,iru,issta,&
                     sqrtsfr,idoptyfr,idoptzfr)
            wsnew=AMAX1(ws,wsfr)
         else
            wsnew=ws
         endif
! ---    Start-time for emission period during timestep
         if(ndhrqb3(it,ia).LT.ndhrb) then
            ndhr1=ndhrb
            nsec1=nsecb
         elseif(ndhrqb3(it,ia).EQ.ndhrb .AND.&
                nsecqb3(it,ia).LT.nsecb) then
            ndhr1=ndhrb
            nsec1=nsecb
         else
            ndhr1=ndhrqb3(it,ia)
            nsec1=nsecqb3(it,ia)
         endif
! ---    End-time for emission period during timestep
         if(ndhrqe3(it,ia).GT.ndhre) then
            ndhr2=ndhre
            nsec2=nsece
         elseif(ndhrqe3(it,ia).EQ.ndhre .AND.&
                nsecqe3(it,ia).GT.nsece) then
            ndhr2=ndhre
            nsec2=nsece
         else
            ndhr2=ndhrqe3(it,ia)
            nsec2=nsecqe3(it,ia)
         endif
! ---    Duration of emission period within timestep (seconds)
         call DELTSEC(ndhr1,nsec1,ndhr2,nsec2,ntemit)
         temit=FLOAT(ntemit)
! ---    Apply transport constraints
         npnewt=wsnew*smxi1(md)*temit/dtsec+1
         npnewt=MIN(npnewt,mxnew)
         temit=temit/FLOAT(npnewt)
! ---    Update puff numbers
         newpuf=newpuf+npnewt
         newar2(i)=newar2(i)+npnewt
!
! ---    If puff arrays are full, remove puffs that are off the grid
! ---    and "roll down" arrays to make room for new puffs
! ---    (this also applies to the DA file of tabulated arrays)
         if(npuffs+newpuf.gt.mxpuff) call rolldn(np)
! ---    Check to see if roll-down was adequate
         if(npuffs+newpuf.gt.mxpuff)then
            write(io6,*) ' ERROR in subr. AREAS2'
            write(io6,*)'Too many puffs on grid for array dimensions',&
            ' -- NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
            ' MXPUFF = ',mxpuff
            stop
         endif
!
!******
      if(ldb)then
! --- 6.42_x1.1 140521
         write(io6,204)i,ilayer,ixs,iys,md,ws,istab,dpbl,issta,npnewt,&
          newpuf,temit
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,2x,&
          'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'NPNEWT: ',i4,2x,&
          'NEWPUF: ',i5/5x,'DT: ',f7.2)
!
         write(io6,210)idw,heff,rise,xf,sysrc,szsrc
210      format(5x,'IDW: ',i5,2x,'HEFF: ',f8.2,2x,'RISE : ',f8.2,2x,&
          'XF : ',f8.2,2x,'SYSRC: ',f8.2,2x,'SZSRC: ',f8.2)
!
         write(io6,208)i,np,iru,temit,sqrts
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,&
          'IRU: ',i2,2x,'DT: ',f7.2,2x,'SQRTS: ',f7.3)
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current step's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnewt
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call swap(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xar2grd(nvert2(i)+1,it,i)
         ypb(np)=yar2grd(nvert2(i)+1,it,i)
         zitibl(np)=-1.0
         elbase(np)=elar2(it,i)
         sigyb(np)=symin
         sigzb(np)=szsrc
! ---    Final plume rise results
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xf
         bidfnl(np)=bidsq
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=htar2(it,i)
         temit0(np)=temit
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations
         idw0(np)=idw
         heff20(np)=heff
         sy0(np)=sysrc
         sz0(np)=szsrc
! ---    Place end-time of emission period for this puff into
! ---    TMTOTB(mxpuff) array, expressed as a fraction of the timestep
! ---    This will be used in COMP when computing the sampling steps
! ---    used to advect the freshly emitted puff during the remainder
! ---    of the timestep, and then reset.
! ---    Seconds from start of emissions period to end of timestep
         call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
         tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
! ---    Emission period index for this puff
         iemstep(np)=it
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from a source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =4 for buoyant area sources)
         jj=newar2(i)-npnewt+j
         irlsnum(np)=jj
         isrcnum(np)=i
         isrctyp(np)=4
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    Note that "oldest" end of slug is at the source, as is the
! ---    "newest end; oldest end moves away and grows during the step,
! ---    while newest end remains fixed in size at the source.
!
         if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---       Set coordinates of oldest end of slug (in met. grid units)
            xpe(np)=xpb(np)
            ype(np)=ypb(np)
            xtote(np)=xtotb(np)
            tmtote(np)=tmtotb(np)
! ---       Set sigmas for oldest end of slug
            sigye(np)=sigyb(np)
            sigze(np)=sigzb(np)
         endif
!
! -------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! -------------------------------------------------------------------
! ---    Swap emission rate for source 'i' into 1D array
         do ispec=1,nspec
            q(ispec)=qar2(ispec,it,i)
         enddo
         call INJECT(np,nspec,temit,mfact0,q,heff,hmax,dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer tabulated arrays record to DA file using full
! ---    MXRISE dimension
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
! --- End loop over emission periods within this timestep
100   continue
!
      return
      end
!----------------------------------------------------------------------
      subroutine syarea(xargrd,yargrd,nvert,dgrid,vecrad,ldbhr,&
                        sysrc)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107            SYAREA
!                D. Strimaitis,   SRC
!
! --- PURPOSE:  For a polygon area source, determine the effective
!               initial sigma-y based on the projection of the source
!               in the crosswind direction for this met period.
!
! --- UPDATE
! --- V4.0-V5.0     971107  (DGS): change coordinate arrays for
!                                  verticies so that single array dim.
!                                  can be used
!
! --- INPUTS:
!
!         XARGRD(mxvert) - real    - X coordinates (m) of the vertices of
!                                    the polygon area source.
!         YARGRD(mxvert) - real    - Y coordinates (m) of the vertices of
!                                    the polygon area source.
!                  NVERT - integer - # vertices of the polygon area source.
!                  DGRID - real    - MET grid cell length  (m)
!                 VECRAD - real    - Flow vector (radians)
!                  LDBHR - logical - Debug control
!
!
! --- OUTPUTS:
!
!                  SYSRC - real    - Effective initial sigma-y (m) for
!                                    source
!
!
! --- SYAREA called by:  AREAS1, AREAS2
! --- SYAREA calls:      none
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      real xargrd(mxvert),yargrd(mxvert)
      logical ldbhr
      data piby2/1.5707963/,rt2pi/2.5066283/
!
! --- Transform vertex coordinates into upwind/crosswind frame,
!     keeping track of the max & min crosswind coordinates
!
! --- Rotation angle (CW)
      rot=-vecrad+piby2
      sinrot=sin(rot)
      cosrot=cos(rot)
! --- New crosswind coordinate of vertex #1
      yprime=-sinrot*xargrd(1)+cosrot*yargrd(1)
      ymin=yprime
      ymax=yprime
! --- Process rest of vertices
      do iv=2,nvert
         yprime=-sinrot*xargrd(iv)+cosrot*yargrd(iv)
         ymin=amin1(ymin,yprime)
         ymax=amax1(ymax,yprime)
      enddo
!
! --- Compute initial sigma-y as L/SQRT(2*pi)
      sysrc=(ymax-ymin)*dgrid/rt2pi
!
      if(LDBHR) then
         write(io6,*) 'SYAREA -- '
         write(io6,*) 'ymin,ymax (met grid) = ',ymin,ymax
         write(io6,*) 'coord rotation (rad) = ',rot
         write(io6,*) 'initial sigma-y (m)  = ',sysrc
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine vols(mvolume,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,dtsec,&
                      smxi1,ldb,mfact,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230              VOLS
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               VOLUME SOURCES prescribed in the control-file, or
!               VOLUME SOURCES prescribed in VOLEMARB.DAT
!
! --- UPDATE
! --- V6.42_x1.1 - TNG-7.1.0  141230  (DGS)
!                                : Place ZEROTAB call within loop over
!                                  emission steps
!                                : Replace VEMFAC with QFACTOR subr.
!                                  for emissions scaling
!                                : Scale-factors for all sources in
!                                  one table array
! --- V6.402-V6.42_x1.1 140521   : Fix debug list-file output list
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
! --- V6.265-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.261-V6.265 090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
! --- V6.22-V6.261  080520  (DGS): Place rise tables in DA file
! --- V6.1-V6.22    070921  (DGS): Change J to I in debug output
!                   070921  (DGS): Revise MVOLUME if-loop error traps
!                   070921  (DGS): Set TMTOTB variable for variable
!                                  emissions file (not usually zero)
!                   070921  (DGS): Replace intrinsic function AMIN0()
!                                  with MIN() when both result and
!                                  arguments are integers
! --- V5.72-V6.1    050915  (DGS): generalize treatment of time for
!                                  variable emissions files --
!                                  resolve time to seconds, and
!                                  allow variable emissions timesteps
! --- V5.7-V5.72    031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.4-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
! --- V5.0-V5.4     000602  (DGS): combine VOLS1 & VOLS2
!                   000602  (DGS): add message to "stop"
! --- V5.0-V5.0     980515  (DGS): ZITIBL array added
! --- V5.0-V5.0     980304  (DGS): drop any new puffs if emissions = 0 
! --- V5.0-V5.0     980304  (DGS): place source sigmas in SY0,SZ0
! --- V4.0-V5.0     971107  (DGS): add variable emissions factor call
!                                  to function VEMFAC
! --- V4.0-V5.0     971107  (DGS): ZPB,ZPE arrays added
!
! --- INPUTS:
!       MVOLUME - integer - Flag for origin of volume source data
!                            1 = Control file (/vol1/)
!                            2 = VOLEMARB.DAT file (/vol2/)
!            IV - integer - VOLEMARB.DAT file index
!             I - integer - Volume source index
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         DTSEC - real    - Length (seconds) of a basic time step
! SMXI1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, WSCALM, PLX0
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MHFTSZ
!     Common block /GRID/ variables:
!           IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /METHR/ variables:
!           TEMPSS(mxss,,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP
!     Common block /METHD/ variables:
!           I2DMET
!     Common block /PUFF/ variables:
!           NPUFFS
!     |MQSCALE| variables:
!           NQSFTAB,IQSFTYPE(nqsftab),QSFTAB(mxqsf,nqsftab)
!     Common block /VOL1/ variables:
!           XVL1GRD(mxvol),YVL1GRD(mxvol),HTVL1(mxvol),
!           ELVL1(mxvol),SY0VL1(mxvol),SZ0VL1(mxvol),
!           QVL1(mxspec,mxvol),NEWVL1(mxvol),NSVL1,
!           IDSFVL1(mxspec,mxvol),IXREFVL1(mxspvl)
!     Common block /VOL2/ variables:
!           NSTEP4(mxemdat),NDHRQB4(mxqstep,mxemdat),
!           NSECQB4(mxqstep,mxemdat),NDHRQE4(mxqstep,mxemdat),
!           NSECQE4(mxqstep,mxemdat),
!           XVL2GRD(mxqstep,mxvol),YVL2GRD(mxqstep,mxvol),
!           HTVL2(mxqstep,mxvol),ELVL2(mxqstep,mxvol),
!           SY0VL2(mxqstep,mxvol),SZ0VL2(mxqstep,mxvol),
!           QVL2(mxspec,mxqstep,mxvol),NEWVL2(mxvol)
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXSPEC, MXVOL, MXQSTEP, MXEMDAT,
!           MXMETDOM, IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /VOL1/ variables:
!           NEWVL1(mxvol)
!     Common block /VOL2/ variables:
!           NEWVL2(mxvol)
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!
! --- VOLS called by:  INITPUF
! --- VOLS calls:      RLSMET, ROLLDN, SWAP, INJECT
!                      ZEROTAB, SRCTABOUT, GETCELL, QFACTOR
!----------------------------------------------------------------------
! --- Modules
      use mqscale
      include 'params.puf'
      include 'comparm.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methr.puf'
      include 'vol1.puf'
      include 'vol2.puf'
      include 'puff.puf'
      include 'slug.puf'
! frr (09/01)
      include 'methd.puf'
!
      real smxi1(mxmetdom)
      real q(mxspec)
      logical ldb,problem
      character*16 vltype
      data zero/0.0/
! --- Initialize number of new puffs from this source, this timestep
      npnew=0
! --- Set source emission period range and type, and screen MVOLUME
      if(mvolume.EQ.1) then
         nt=1
         itype=7
      elseif(mvolume.EQ.2) then
         nt=nstep4(iv)
         itype=8
      else
         write(io6,*)
         write(io6,*)'VOLS:  Invalid volume source type: ',mvolume
         write(io6,*)'       Valid types are 1 (control file)'
         write(io6,*)'                       2 (VOLEMARB.DAT)'
         write(*,*)
         stop 'Halted in VOLS -- see list file.'
      endif
! --- Loop over emission periods within this timestep
      do 100 it=1,nt
! ---    Initialize source tabulation specifications to zero
         call ZEROTAB
! ---    Assign variables for current source
         if(mvolume.EQ.1) then
            vltype=' Control File '
            xvlgrd=xvl1grd(i)
            yvlgrd=yvl1grd(i)
            heff=htvl1(i)
            elvl=elvl1(i)
            szsrc=amax1(szmin,sz0vl1(i))
            sysrc=amax1(symin,sy0vl1(i))
            do is=1,nspec
               q(is)=qvl1(is,i)
            enddo
         elseif(mvolume.EQ.2) then
            vltype=' VOLEMARB.DAT '
            xvlgrd=xvl2grd(it,i)
            yvlgrd=yvl2grd(it,i)
            heff=htvl2(it,i)
            elvl=elvl2(it,i)
            szsrc=amax1(szmin,sz0vl2(it,i))
            sysrc=amax1(symin,sy0vl2(it,i))
            do is=1,nspec
               q(is)=qvl2(is,it,i)
            enddo
         else
            write(*,*)'Subr. VOLS:  Invalid MVOLUME = ',mvolume
            stop
         endif
!
! ***
      if(ldb) then
         write(io6,*)
         write(io6,*) 'VOLS: Source Number ',i,'  Source Type ',vltype
         if(mvolume.EQ.2) write(io6,*) '   Emissions period ',it
      endif
! ***
!
! ---    Determine MET grid 1 (outermost) point closest to the source
         ixs=1.0+xvlgrd
         iys=1.0+yvlgrd
!
! ---    Source off the computational grid -- write a FATAL message
         if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
            jecomp)then
            write(io6,20) vltype,i,ixs,iys
20          format(/1x,'FATAL -- a volume source of Type ',a16,&
            'is off the computational grid'/1x,'source no. (i) = ',&
            i6,2x,'nearest grid point (ixs,iys) = (',i5,',',i5,')')
            problem=.TRUE.
            goto 100
         endif
! ---    Now determine the met. grid point closest to the source
         call GETCELL('VOLS        ','GRIDIJ',1,xvlgrd,yvlgrd,&
                      zero,zero,ixs,iys,md)
!
! ---    Extract release height wind speed & other met. variables
         call rlsmet(ldb,ixs,iys,md,heff,mfact,&
                     mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                     ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,&
                     idoptz)
!
! frr (09/01) new calmet format (2D temp)
         if(i2dmet.EQ.1) then
            temp=temp2d(ixs,iys,md)
         elseif(i2dmet.EQ.0) then
            temp=tempss(issta,md)
         else
            write(*,*)'Subr. VOLS:  Invalid I2DMET = ',i2dmet
            stop
         endif
! ---    Trap missing power-law exponent (substitute default)
         if(plexp.LT.-900.) then
! ---       Use default
            plx=plx0(istab)
         else
! ---       Use it
            plx=plexp
         endif
! --------------------------------------------------------------------
! ---    Do not generate puff for this step if all emissions are ZERO!
! --------------------------------------------------------------------
! ---    Adjust TYPE-1 emissions for source 'i' and obtain total
         qtot=0.0
         do is=1,nspec
            if(mvolume.EQ.1 .AND. nsvl1.GT.0) then
! ---          Apply emissions scale-factor (conditional)
! ---          Pointer to scale factor tables
               k=idsfvl1(is,i)
               if(k.GT.0) then
                  kk=ixrefvl1(k)
                  call QFACTOR(io6,iqsftype(kk),&
                               itype,ws,istab,temp,iqsf)
                  q(is)=q(is)*qsftab(iqsf,kk)
               endif
            endif
            qtot=qtot+q(is)
         enddo
         if(qtot.EQ.0.0) then
! ***
            if(ldb) then
               write(io6,*)
               write(io6,*) 'No emissions this step from Source ',i
               write(io6,*)
            endif
! ***
            goto 100
         endif
! ---    Establish emitting time
         if(mvolume.EQ.1) then
            ndhr1=ndhrb
            nsec1=nsecb
            ndhr2=ndhre
            nsec2=nsece
         elseif(mvolume.EQ.2) then
! ---       Start-time for emission period during timestep
            if(ndhrqb4(it,iv).LT.ndhrb) then
               ndhr1=ndhrb
               nsec1=nsecb
            elseif(ndhrqb4(it,iv).EQ.ndhrb .AND.&
                   nsecqb4(it,iv).LT.nsecb) then
               ndhr1=ndhrb
               nsec1=nsecb
            else
               ndhr1=ndhrqb4(it,iv)
               nsec1=nsecqb4(it,iv)
            endif
! ---       End-time for emission period during timestep
            if(ndhrqe4(it,iv).GT.ndhre) then
               ndhr2=ndhre
               nsec2=nsece
            elseif(ndhrqe4(it,iv).EQ.ndhre .AND.&
                   nsecqe4(it,iv).GT.nsece) then
               ndhr2=ndhre
               nsec2=nsece
            else
               ndhr2=ndhrqe4(it,iv)
               nsec2=nsecqe4(it,iv)
            endif
         else
            write(*,*)'Subr. VOLS:  Invalid MVOLUME = ',mvolume
            stop
         endif
! ---    Duration of emission period within timestep (seconds)
         call DELTSEC(ndhr1,nsec1,ndhr2,nsec2,ntemit)
         temit=FLOAT(ntemit)
! ---    Apply transport constraints
         npnewt=ws*smxi1(md)*temit/dtsec+1
         npnewt=MIN(npnewt,mxnew)
         temit=temit/FLOAT(npnewt)
! ---    Update puff numbers
         npnew=npnew+npnewt
         newpuf=newpuf+npnewt
         if(mvolume.EQ.1) then
            newvl1(i)=npnew
         elseif(mvolume.EQ.2) then
            newvl2(i)=npnew
         else
            write(*,*)'Subr. VOLS:  Invalid MVOLUME = ',mvolume
            stop
         endif
! ---    If puff arrays are full, remove puffs that are off the grid
! ---    and "roll down" arrays to make room for new puffs
! ---    (this also applies to the DA file of tabulated arrays)
         if(npuffs+newpuf.gt.mxpuff) call rolldn(np)
! ---    Check to see if roll-down was adequate
         if(npuffs+newpuf.gt.mxpuff)then
            write(io6,*) ' ERROR in subr. VOLS'
            write(io6,*)'Too many puffs on grid for array dimensions',&
            ' -- NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
            ' MXPUFF = ',mxpuff
            write(*,*)
            stop 'Halted in VOLS -- see list file.'
         endif
!
! ---    Set variance associated with buoyancy-enhanced growth @ final
! ---    rise
         xf=0.0
         rise=0.0
         bidsq=0.0
! ---    Set downwash flag to zero (no building downwash)
         idw=0
!******
      if(ldb)then
! --- 6.42_x1.1 140521
         write(io6,204)i,ilayer,ixs,iys,md,ws,istab,dpbl,issta,npnewt,&
          newpuf,temit
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,2x,&
          'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'NPNEWT: ',i4,2x,&
          'NEWPUF: ',i5/5x,'DT: ',f7.2)
!
         write(io6,210)idw,heff,rise,xf,sysrc,szsrc
210      format(5x,'IDW: ',i5,2x,'HEFF: ',f8.2,2x,'RISE : ',f8.2,2x,&
          'XF : ',f8.2,2x,'SYSRC: ',f8.2,2x,'SZSRC: ',f8.2)
!
         write(io6,208)i,np,iru,temit,sqrts
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,&
          'IRU: ',i2,2x,'DT: ',f7.2,2x,'SQRTS: ',f7.3)
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnewt
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call swap(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xvlgrd
         ypb(np)=yvlgrd
         zitibl(np)=-1.0
         elbase(np)=elvl
         sigyb(np)=sysrc
         sigzb(np)=szsrc
! ---    Final plume rise results
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xf
         bidfnl(np)=bidsq
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=heff
         temit0(np)=temit
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations (downwash)
         idw0(np)=idw
         heff20(np)=heff
         sy0(np)=sysrc
         sz0(np)=szsrc
         if(mvolume.EQ.2) then
! ---       Place end-time of emission period for this puff into
! ---       TMTOTB(mxpuff) array, expressed as fraction of the timestep
! ---       This will be used in COMP when computing the sampling steps
! ---       used to advect the freshly emitted puff during remainder
! ---       of the timestep, and then reset.
! ---       Seconds from start of emissions period to end of timestep
            call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
            tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
         endif
! ---    Emission period index for this puff
         iemstep(np)=it
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from a source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =7 for control file volume sources)
! ---    (Type =8 for VOLEMARB.DAT volume sources)
         jj=npnew-npnewt+j
         irlsnum(np)=jj
         isrcnum(np)=i
         isrctyp(np)=itype
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    Note that "oldest" end of slug is at the source, as is the
! ---    "newest end; oldest end moves away and grows during the step,
! ---    while newest end remains fixed in size at the source.
!
         if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---       Set coordinates of oldest end of slug (in met. grid units)
            xpe(np)=xpb(np)
            ype(np)=ypb(np)
            xtote(np)=xtotb(np)
            tmtote(np)=tmtotb(np)
! ---       Set sigmas for oldest end of slug
            sigye(np)=sigyb(np)
            sigze(np)=sigzb(np)
         endif
!
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
! ---    Set partial penetration variables to null values
         fmix=1.0
         hmax=dpbl
         call inject(np,nspec,temit,mfact0,q,heff,hmax,dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer NULL tabulated arrays record to DA file
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
! --- End loop over emission periods within this timestep
100   continue
!
      return
      end
!----------------------------------------------------------------------
      subroutine points1(i,nspec,ndhrb,nsecb,ndhre,nsece,dthr,&
                         xtmp1,ldbhr,mfact,metfm,np,&
                         newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           POINTS1
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               POINT SOURCES with constant emissions
!
! --- UPDATE
!
! --- V6.42_x1.1 - TNG-7.1.0  141230 (DGS)
!                                : Replace VEMFAC with QFACTOR subr.
!                                  for emissions scaling
!                                : Scale-factors for all sources in
!                                  one table array
!                                : Allow roughness adjustment (MROUGH)
!                                  of RURAL PG sigmas to be used with
!                                  CALMET gridded fields
!                                : Set BID for PRIME case where primary
!                                  plume is not in wake (no wake table)
!                                : Add explicit treatment of partial
!                                  penetration with PRIME downwash only
!                                  when plume does NOT enter wake
!                                : Adjust final rise with ISC downwash
!                                  when using partial penetration and/or
!                                  wind shear options
! --- V6.402-V6.42_x1.1 140521   : Add calls to WAKE_CSIG to set
!                                  dispersion data for release ht and
!                                  for building top
! --- V6.302-V6.402 101111  (DGS): Update GETCELL arguments
! --- V6.3-V6.302   100917  (DGS): Add idownw as a screen for debug
!                                  writes so that unassigned downwash
!                                  variables are not written to list
!                                  file
! --- V6.268-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.267-V6.268 100108  (DGS): Set BID to zero for PRIME sources as
!                                  wake tables now include BID, and add
!                                  HTR to CAV_CONC call
! --- V6.266-V6.267 090710  (DGS): Add maximum lid height for mass above
!                                  Zi to CAV_CONC arguments for later 
!                                  use in VCOUP
! --- V6.265-V6.266 090619  (DGS): Do not adjust Briggs plume rise for
!                                  shear when distance to final rise is
!                                  zero (near-calm)
!                           (DGS): Write more digits for TSTAK, DTEMP
!                                  debug output
! --- V6.263-V6.265 090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
! --- V6.261-V6.263 080827  (DGS): Modify logic for assigning partial
!                                  penetration parameters to conform
!                                  to implementation prior to version
!                                  6.26.  This affects the combination
!                                  of Briggs rise and PRIME downwash.
! --- V6.26-V6.261  080520  (DGS): Place rise tables in DA file
! --- V6.222-V6.26  080430  (DGS): Add RISE.DAT output calls
!                                  (requires timestamp in arg list)
!                   080430  (DGS): Add numerical rise option
! --- V6.2-V6.22    070921  (DGS): Initialize variables
!                                  INDEX = 0
! --- V5.75-V6.2    070629  (DGS): Initialize variables
!                                  HB, HW, HEFF2, ZLY, RINIT = 0.0
!                   070629  (DGS): Fix debug output index from J to I
!                   070629  (DGS): Fix name of IRU used in PRIME section
!                                  when the meteorology at building-ht
!                                  is extracted (IRUB was not defined)
! --- V5.74-V5.75   050225  (DGS): add platform adjustment to ISC
!                                  building downwash (MBDW=1)
!                   050225  (DGS): Add DPBL arg to SETCSIG for TAULY
! --- V5.72-V5.74   040715  (DGS): add METFM=5 (AERMET)
! --- V5.7-V5.72    031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.5-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
!                   030402  (DGS): implement PRIME downwash option
! --- V5.4-V5.5     010730  (DGS): store initial sigmas from control
!                                  file in SYSRC0 and SZSRC0 for use
!                                  in downwash zone
!                   010730  (DGS): revise building-array index to match
!                                  ISC
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!                   000602  (DGS): add MFOG option
! --- V5.0-V5.3     991222b (DGS): reset momentum flux using FMFAC
!                   991222b (DGS): pass momentum flux to STKTIP
! --- V5.0-V5.0     980807  (DGS): use final rise CALMET/PROFILE winds 
!                                  to set release step
! --- V5.0-V5.0     980515  (DGS): ZITIBL array added
! --- V5.0-V5.0     980304  (DGS): drop any new puffs if emissions = 0 
!                   980304  (DGS): use initial sigmay,z arrays from 
!                                  control file
!                   980304  (DGS): place source sigmas in SY0,SZ0
! --- V4.0-V5.0     971107  (DGS): add variable emissions factor call
!                                  to function VEMFAC
!                   971107  (DGS): updated /PT1/ variables used (doc)
! --- V4.0-V5.0     971107  (DGS): ZPB,ZPE arrays added
!                   971107  (DGS): add observed inversion strength
!
! --- INPUTS:
!             I - integer - Point source index
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!          DTHR - real    - Length (seconds) of a basic time step
! XTMP1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!         LDBHR - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!         METFM - integer - Meteorological data format
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, PTG0(2), WSCALM, TBD, PLX0
!     Common block /DISPDAT/ variables:
!           JSUP
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MTIP, MSHEAR, MPARTL, MHFTSZ, MFOG, MBDW,
!           MRISE
!     Common block /FOG/ variables:
!           TXSMXFOG
!     Common block /GRID/ variables:
!           IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /METHD/ variables:
!           LCALGRD, I2DMET
!     Common block /METHR/ variables:
!           TEMPSS(mxss,,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP, PTG(2), DPTINVO
!     Common block /PUFF/ variables:
!           NPUFFS
!     |MQSCALE| variables:
!           NQSFTAB,IQSFTYPE(nqsftab),QSFTAB(mxqsf,nqsftab)
!     Common block /PT1/ variables:
!           XPT1GRD(mxpt1),YPT1GRD(mxpt1),HTSTAK(mxpt1),
!           ELSTAK(mxpt1),DIAM(mxpt1),EXITW(mxpt1),TSTAK(mxpt1),
!           IDOWNW(mxpt1),QSTAK(mxspec,mxpt1),NPT1,
!           SYIPT1(mxpt1),SZIPT1(mxpt1),FMFPT1(mxpt1),
!           BWIDTH(36,mxpt1),BHT(36,mxpt1),
!           BLN1(36,mxpt1),XBADJ1(36,mxpt1),YBADJ1(36,mxpt1),
!           ZPLATPT1(mxpt1),
!           NSPT1,IDSFPT1(mxspec,mxpt1),IXREFPT1(mxsppt1)
!     Parameters:
!           MXNX, MXNY, MXNZ,MXMETDOM,  MXNZP1, MXPT1, MXSPEC, MXRISE,
!           IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /FOG/ variables:
!           TXSMXFOG
!     Common block /PT1/ variables:
!           NEWPT1(mxpt1)
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!     Common block /SRCTAB/ variables:
!           NTR, NWK, NCV,
!           XTR(mxrise),ZTR(mxrise),RTR(mxrise),HTR(mxrise),
!           XWK(mxrise),SYWK(mxrise),SZWK(mxrise),DRWK(mxrise),
!           XCV(mxrise),SYCV(mxrise),SZCV(mxrise)
!           
!
! --- POINTS1 called by:  INITPUF
! --- POINTS1 calls:      RLSMET, ROLLDN, PTLAPS, SETCSIG, SIGTY,
!                         SIGTZ, STKTIP, DWSIGS, PRSS, PRFIN, PRM,
!                         PRFINSH, PRFPP, NUMMET, NUMRISE, NUMCLIP,
!                         WAKE_INI, WAKE_FIN, CAV_CONC, WAKE_DBG,
!                         NRISEHD, SWAP, INJECT,
!                         ZEROTAB, SRCTABOUT, GETCELL
!                         WAKE_CSIG, QFACTOR
!----------------------------------------------------------------------
! --- Modules
      use mqscale
      include 'params.puf'
      include 'comparm.puf'
      include 'csigma.puf'
      include 'dispdat.puf'
      include 'flags.puf'
      include 'fog.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
      include 'pt1.puf'
      include 'slug.puf'
      include 'srctab.puf'
!
      real xtmp1(mxmetdom)
      real q(mxspec),qcav(mxspec)
      logical ldbhr,problem
      logical LBRIGGS
! --- PRIME logicals to configure primary (LPRM) and cavity (LCAV) puff
      logical lprm,lcav
!
      data rt2/1.4142136/, rt2pi/2.5066283/, piby4/0.7853982/
! --- Set minimum allowed potential temperature gradient PTGRAD0,
      data ptgrad0/.005/
! --- Set minimum allowed wind speed for building downwash
      data wsdw0/1.0/
! --- Set source type (1 for POINTS1)
      data itype/1/
      data zero/0.0/
! --- Initialize local ISC building downwash variables
      index=0
      hb=0.0
      hw=0.0
      heff2=0.0
      zly=0.0
      rinit=0.0
! --- Set specific plume rise variables for Briggs and Numerical
! --- rise to clarify treatment
! --- Briggs
      xfinm_B=0.0
      xfinb_B=0.0
      zfinm_B=0.0
      zfinb_B=0.0
      xf_B=0.0
      rise_B=0.0
      fmix_B=1.0
      hmax_B=0.0
      bidsq_B=0.0
      heff_B=0.0
! --- Numerical
      xfinm_N=0.0
      xfinb_N=0.0
      zfinm_N=0.0
      zfinb_N=0.0
      xf_N=0.0
      rise_N=0.0
      fmix_N=1.0
      hmax_N=0.0
      bidsq_N=0.0
      heff_N=0.0
!
! ***
      if(ldbhr) then
         write(io6,*)
         write(io6,*) 'POINTS1:  Source Number    ',i
      endif
! ***
!
! --- Determine the MET grid 1 (outermost) point closest to the stack
      ixs1=1.0+xpt1grd(i)
      iys1=1.0+ypt1grd(i)
!
! --- Source off the computational grid -- write a FATAL message
      if(ixs1.lt.ibcomp.or.ixs1.gt.iecomp.or.&
         iys1.lt.jbcomp.or.iys1.gt.jecomp)then
         write(io6,20)i,ixs1,iys1
20       format(/1x,'FATAL -- a point source with constant emissions ',&
         'is off the computational grid'/1x,'source no. (i) = ',i6,2x,&
         'nearest grid point (ixs,iys) = (',i5,',',i5,')')
         problem=.TRUE.
         return
      endif
! --- Now determine the met. grid point closest to the stack
      call GETCELL('POINTS1     ','GRIDIJ',1,xpt1grd(i),ypt1grd(i),&
                   zero,zero,ixs,iys,md)
! --- Extract stack height wind speed & other met. variables;
! --- and set CALM configuration if triggered
      call rlsmet(ldbhr,ixs,iys,md,htstak(i),mfact,&
                  mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                  ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
!
! frr (09/01) new calmet format (2D temp)
      if(i2dmet.EQ.1) then
         temp=temp2d(ixs,iys,md)
      elseif(i2dmet.EQ.0) then
         temp=tempss(issta,md)
      else
         write(*,*)'Subr. POINTS1:  Invalid I2DMET = ',i2dmet
         stop
      endif
! --- Trap missing power-law exponent (substitute default)
      if(plexp.LT.-900.) then
! ---    Use default
         plx=plx0(istab)
      else
! ---    Use it
         plx=plexp
      endif
! --- Swap emission rate for source 'i' into 1D array and scale
      qtot=0.0
      do is=1,nspec
         q(is)=qstak(is,i)
! ---    Apply emissions scale-factor (conditional)
         if(nspt1.GT.0) then
! ---       Pointer to scale factor tables
            k=idsfpt1(is,i)
            if(k.GT.0) then
               kk=ixrefpt1(k)
               call QFACTOR(io6,iqsftype(kk),itype,ws,istab,temp,iqsf)
               q(is)=q(is)*qsftab(iqsf,kk)
            endif
         endif
         qtot=qtot+q(is)
      enddo
! --- Return without generating new puffs if all species have zero
! --- mass in puff
      if(qtot.EQ.0.0) then
! ***
         if(ldbhr) then
            write(io6,*)
            write(io6,*) 'No emissions this step from Source ',i
            write(io6,*)
         endif
! ***
         return
      endif
! --- Initialize source tabulation arrays to zero
      call ZEROTAB
! --- Set volume flux from stack
      vf0=piby4*exitw(i)*(diam(i))**2
! --- Set temperature excess at release
! frr(09/01)
!      dtemp=tstak(i)-tempss(issta)
      dtemp=tstak(i)-temp
!      dtemp=amax1(dtemp,0.01)
      dtemp=amax1(dtemp,0.0)
      if(MFOG.GT.0) then
! ---    Place the temperature excess emission rate (K-m^3/s) in Q(2)
! ---    Note that nspec should be 2 for FOG option
         q(2)=dtemp*vf0
! ---    Maximum temperature excess used to compute RH will be limited
! ---    to the minimum temperature excess among FOG sources
         txsmxfog=AMIN1(dtemp,txsmxfog)
      endif
!
! -------------------------------------------------------------
! --- Compute the final plume rise and distance to final rise
! -------------------------------------------------------------
! --- Calculate fluxes
! --- Buoyancy flux:  2.4516625 = g/4., where g = 9.80665 m/s**2
      fluxb=(2.4516625*diam(i)**2*exitw(i)/tstak(i))*dtemp
! --- Momentum flux
! frr(09/01)
!      fluxm=0.25*(exitw(i)*diam(i))**2*tempss(issta)/tstak(i)
      fluxm=0.25*(exitw(i)*diam(i))**2*temp/tstak(i)
! --- Apply vertical momentum flux factor of zero or one
! --- Use simple test for ZERO instead of forming the product:
! ---     fluxm=fluxm*fmfpt1(i)
      if(fmfpt1(i).LT.0.5) fluxm=0.0
!
! --- Adjustment for stack-tip downwash (NOT for bldg downwash!)
      if(mtip.EQ.1) then
         call stktip(htstak(i),diam(i),fluxm,exitw(i),ws,tipdw)
      else
         tipdw=0.0
      endif
! --- Briggs Rise (always computed)
! ---------------------------------
! --- Final plume rise (no downwash considered)
      call prfin(exitw(i),diam(i),fluxm,fluxb,ws,istab,sqrts,&
                 xfinm_B,xfinb_B,xf_B,zfinm_B,zfinb_B,rise_B)
!
! --- Compute vertical wind shear effects
      if(mshear.EQ.1 .AND. xfinb_B.GT.0.0)then
         call prfinsh(fluxb,ws,plx,htstak(i),xfinb_B,istab,sqrts,&
                      zfinsh)
! ---    Final buoyant rise is the LOWER of the Briggs & shear hts.
         zfinb_B=amin1(zfinb_B,zfinsh)
! ---    Final plume rise is the HIGHER of the momentum & buoyant hts.
         rise_B=amax1(zfinb_B,zfinm_B)
      endif
!
! --- Adjust for partial penetration of elevated stable layer
!     if(mpartl.EQ.1 .and. istab.LE.3 .and. htstak(i).LT.dpbl) then
      if(mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0) then
! ---    Set thickness (m) of transition region above mixing height
         dzinv=30.
! ---    Estimate potential temperature gradient above mixed layer
         if(LCALGRD) then
! ---       Use temperatures provided by met model
            htinv=amax1(dpbl,htstak(i))
            call ptlaps(ixs,iys,md,htinv,ptgrad0,dzinv,ptginv,tmix)
         else
! ---       Use default potential temperature lapse rate
            if(jsup.LE.4) then
               ptginv=ptgrad0
            elseif(jsup.EQ.5) then
               ptginv=amax1(ptg0(1),ptgrad0)
            else
               ptginv=amax1(ptg0(2),ptgrad0)
            endif
         endif
! ---    Estimate potential temperature change across inversion
         if(dptinvo.GT.0.0) then
! ---       Use observed value provided in met file
            dptinv=dptinvo
         else
            dptinv=ptginv*dzinv
         endif
         call prfpp(rise_B,fluxb,ws,htstak(i),dpbl,temp,ptginv,&
                    dptinv,fmix_B,hmax_B)
! ---    Reset component final rise heights for use in gradual rise
         if(zfinb_B.GT.rise_B) zfinb_B=rise_B
         if(zfinm_B.GT.rise_B) zfinm_B=rise_B
!
      else
! ---    Set partial penetration variables to null values
         fmix_B=1.0
         hmax_B=dpbl
      endif
!
! --- Set variance associated with buoyancy-enhanced growth @ final rise
      bidsq_B=(rise_B/3.5)**2
!
! --- Set final plume height
      heff_B=htstak(i)-tipdw+rise_B
!
! --- Set selected data in /CSIGMA/ for sigma calls in ISC downwash
      call SETCSIG(idopty,idoptz,iru,ws,istab,el,sqrts,&
                   tsigv,tsigw,symin,szmin,heff_B,dpbl,ixs,iys,md)
! --- Assign Briggs adjustments to numerical rise variables as defaults
      xfinm_N=xfinm_B
      xfinb_N=xfinb_B
      zfinm_N=zfinm_B
      zfinb_N=zfinb_B
      xf_N=xf_B
      rise_N=rise_B
      fmix_N=fmix_B
      hmax_N=hmax_B
      bidsq_N=bidsq_B
      heff_N=heff_B
! --- Numerical Rise (conditional)
! --------------------------------
      if(mrise.EQ.2) then
! ---    Construct met. profiles at this grid cell
         call NUMMET(ixs,iys,md,ldbhr)
! ---    Set stack radius
         reff=0.5*diam(i)
! ---    Set effective release height
         hteff=AMAX1(0.0,htstak(i)-tipdw)
! ---    Prepare data header record for RISE.DAT file
! ---    (control for output to scratch file is within subroutine)
         call NRISEHD(itype,i,ndhrb,nsecb,ndhre,nsece,ndhrb,nsecb,&
                      ndhre,nsece,elstak(i),hteff,tstak(i),&
                      reff,exitw(i),xpt1grd(i),ypt1grd(i))
! ---    Compute plume rise
         mprime=0
! ---    Use full MXRISE points in rise table
         ntr=mxrise
         call NUMRISE(mprime,ldbhr,hteff,tstak(i),reff,exitw(i),&
                      syipt1(i),szipt1(i),ntr,xtr,ztr,rtr,htr)
         rise_N=htr(ntr)
! ---    Adjust for partial penetration of elevated stable layer
         if(mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0) then
! ---       (ptginv, dptinv computed in Briggs Rise section)
            call PRFPP(rise_N,fluxb,ws,htstak(i),dpbl,temp,ptginv,&
                       dptinv,fmix_N,hmax_N)
! ---       Clip rise table at new final height
            call NUMCLIP(mxrise,hteff,rise_N,ntr,xtr,ztr,rtr,htr)
         else
! ---       Set partial penetration variables to null values
            fmix_N=1.0
            hmax_N=dpbl
         endif
! ---    Assign final plume height and distance
         heff_N=ztr(ntr)
         xf_N=xtr(ntr)
! ---    Variance associated with buoyancy-enhanced growth @ final rise
         bidsq_N=(rise_N/3.5)**2
      endif
!
! --------------------------------------------------------------------
! --- Determine if building downwash effects on plume rise and sigmas
! --- should be considered
! --------------------------------------------------------------------
      sz0dw=0.0
      sy0dw=0.0
      xshift=0.0
      xshift2=0.0
      lprm=.FALSE.
      lcav=.FALSE.
! --- Initialize downwash flag to zero (no building downwash)
! --- Note:  idw=0  no downwash
! ---        idw=1  Huber-Snyder model
! ---        idw=2  Schulman-Scire model
! ---        idw=3  PRIME model (puff released from primary source)
! ---        idw=4  PRIME model (puff released from cavity source)
      idw=0
      if(idownw(i).EQ.1 .AND. ws.GE.wsdw0 .AND. mbdw.EQ.2) then
!        --------------------------------
!        --- PRIME Downwash Section
!        --------------------------------
         mprime=1
! --- 6.42_x1.1 140521
! ---    Store dispersion data for release height for later use
         if(mrise.EQ.2) then
            zht=heff_N
         else
            zht=heff_B
         endif
         call WAKE_CSIG(idopty,idoptz,iru,ws,istab,el,&
                        sqrts,tsigv,tsigw,symin,szmin,&
                        zht,dpbl,ixs,iys,md,'RELEASE ')
! ---    Get direction-specific building data (direction at stack ht)
         index=INT(0.1*FLOAT(ivec)+0.4999)
         if(index.eq.0)index=36
         dsbh=bht(index,i)
         dsbw=bwidth(index,i)
         dsbl=bln1(index,i)
         xadj=xbadj1(index,i)
         yadj=ybadj1(index,i)
         hl=AMIN1(dsbh,dsbw)
! ---    Screen for GEP stack (regulatory definition)
         geptest=dsbh+1.5*hl
         if(htstak(i).LT.geptest) then
            idw=3
! ---       Set stack radius
            reff=0.5*diam(i)
! ---       Set met data at top of building
            call RLSMET(ldbhr,ixs,iys,md,dsbh,mfact,&
                        mfact0b,ilayer,wsb,flowb,ivecb,istabb,dpbl,&
                        elb,ustrb,wstrb,tsigvb,tsigwb,iru,issta,&
                        sqrtsb,idoptyb,idoptzb)
! ---       Reset /CSIGMA/ data for met data at top of building
            if(mrise.EQ.2) then
               zht=AMAX1(heff_N,dsbh)
            else
               zht=AMAX1(heff_B,dsbh)
            endif
            call SETCSIG(idoptyb,idoptzb,iru,wsb,istabb,elb,sqrtsb,&
                         tsigvb,tsigwb,symin,szmin,zht,dpbl,ixs,iys,md)
! --- 6.42_x1.1 140521
! ---       Store dispersion data for top of building for later use
            call WAKE_CSIG(idoptyb,idoptzb,iru,wsb,istabb,elb,&
                           sqrtsb,tsigvb,tsigwb,symin,szmin,&
                           zht,dpbl,ixs,iys,md,'BUILDING')
! ---       Refresh /WAKEDAT/ variables
            call WAKE_INI(ldbhr,istab,iru,dsbh,dsbw,dsbl,xadj,yadj,&
                          wsb,ws,tsigvb,tsigwb,idoptyb,idoptzb)
! ---       Construct met. profiles at this grid cell
            call NUMMET(ixs,iys,md,ldbhr)
! ---       Prepare data header record for RISE.DAT file
! ---       (control for output to scratch file is within subroutine)
            call NRISEHD(itype,i,ndhrb,nsecb,ndhre,nsece,ndhrb,nsecb,&
                         ndhre,nsece,elstak(i),htstak(i),tstak(i),&
                         reff,exitw(i),xpt1grd(i),ypt1grd(i))
! ---       Compute plume rise and wake structure
! ---       Use full MXRISE points in rise table
            ntr=mxrise
            call NUMRISE(mprime,ldbhr,htstak(i),tstak(i),reff,exitw(i),&
                         syipt1(i),szipt1(i),ntr,xtr,ztr,rtr,htr)
! ---       Pass final wake structure to source arrays (with BID)
            call WAKE_FIN(ldbhr,nwk,xwk,szwk,sywk,drwk,szw0,syw0,&
                          ncv,xcv,szcv,sycv,szc0,syc0,fqcvpt1(i))
!
! ---       Set logicals (primary & cavity) and partition mass
            if(fqcvpt1(i).LE.0.001) then
               lcav=.FALSE.
               lprm=.TRUE.
               do is=1,nspec
                  qcav(is)=0.0
               enddo
            elseif(fqcvpt1(i).GE.0.999) then
               lcav=.TRUE.
               lprm=.FALSE.
               do is=1,nspec
                  qcav(is)=q(is)
                  q(is)=0.0
               enddo
            else
               lcav=.TRUE.
               lprm=.TRUE.
               do is=1,nspec
                  qcav(is)=q(is)*fqcvpt1(i)
                  q(is)=q(is)-qcav(is)
               enddo
            endif
! ---       Check for wake table
            if(nwk.LE.1 .AND. lprm) then
! ---          Case where primary plume is not in wake (no wake table)
! ---          This source will be treated as non-DW source but using
! ---          the rise calculated by PRIME if MRISE=2 (the _N values),
! ---          or Briggs if MRISE=1 (the _B values)
! ---          Reset the puff downwash flag
! ---          ----------------------------
               idw=0
! ---          Apply partial penetration adjustments to the PRIME
! ---          numerical rise tables (conditional)
               if(mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0) then
! ---             (ptginv, dptinv computed in Briggs Rise section)
                  rise_N=htr(ntr)
                  call PRFPP(rise_N,fluxb,ws,htstak(i),dpbl,temp,&
                             ptginv,dptinv,fmix_N,hmax_N)
! ---             Clip rise table at new final height
                  call NUMCLIP(mxrise,htstak(i),rise_N,&
                               ntr,xtr,ztr,rtr,htr)
               else
! ---             Set partial penetration variables to null values
                  fmix_N=1.0
                  hmax_N=dpbl
               endif
! ---          Assign final plume height and distance
               rise_N=htr(ntr)
               heff_N=ztr(ntr)
               xf_N=xtr(ntr)
! ---          Variance associated with buoyancy-enhanced growth
! ---          @ final rise
               bidsq_N=(rise_N/3.5)**2
            else
! ---          Case where primary plume enters wake (wake table)
! ---          Assign final plume height, rise, and distance for
! ---          primary source (if any)
               rise_N=htr(ntr)
               heff_N=ztr(ntr)
               xf_N=xtr(ntr)
! ---          Reset stack-tip downwash
               tipdw=0.0
! ---          The BID has been moved into the wake tables
               bidsq_N=0.0
! ---          Set partial penetration variables to null values
               fmix_N=1.0
               hmax_N=dpbl
!
! ---          Report selected data to file for debug
               if(ldbhr) call WAKE_DBG(io6,ntr,xtr,ztr,rtr,htr,&
                                       .FALSE.,htstak(i))
! ---          Compute near-cavity concentrations for current source, 
! ---          return location of cavity source on the met grid, and
! ---          return the distance from the source to the end of the
! ---          cavity transition zone
               call CAV_CONC(ldbhr,nspec,q,qcav,xpt1grd(i),ypt1grd(i),&
                             ntr,xtr,ztr,htr,flow,dpbl,hmax_N,&
                             xcavgrd,ycavgrd,xshift,xshift2)
            endif
         endif
      endif
      if(idownw(i).GE.1 .AND. ws.GE.wsdw0 .AND. mbdw.EQ.1) then
!        --------------------------------
!        --- HS/SS Downwash Section
!        --------------------------------
! ---    Get direction-specific building width and height
         index=INT(0.1*FLOAT(ivec)+0.4999)
         if(index.eq.0)index=36
         hb=bht(index,i)
         hw=bwidth(index,i)
         hl=amin1(hb,hw)
!
! ---    Assign platform height
         zplat=0.0
         if(idownw(i).EQ.2) zplat=zplatpt1(i)
!
! ---    Calculate the momentum rise at 2*hb and test for downwash
         xarg=2.*hb
         call prm(diam(i),exitw(i),fluxm,ws,istab,sqrts,xarg,&
                 xfinm_B,zfinm_B,z2hb)
         heff2=htstak(i)+z2hb
         dwtest=hb+1.5*hl
!
         if((heff2-zplat).LE.dwtest) then
! ---       Set downwash flag to ACTIVE
            idw=1
! ---       (1)  Compute sigmas at 10*hl
            xarg10=10.*hl
            xarg10km=0.001*xarg10
            targ10=xarg10/ws
            call dwsigs(tbd,hw,hb,mhftsz,ws,htstak(i),heff2,zplat,&
                        xarg10,sy10,sz10)
!
! ---       (2)  Final Plume rise altered if Schulman-Scire (SS) active
            sstest=hb+0.5*hl
            if((htstak(i)-zplat).LT.sstest) then
! ---          Schulman-Scire downwash ACTIVE
               idw=2
! ---          Set sigmas at 3*hl
               xarg=3.*hl
               call dwsigs(tbd,hw,hb,mhftsz,ws,htstak(i),heff2,zplat,&
                           xarg,sy3,sz3)
! ---          Determine line source parameters, ZLY and RINIT
! ---          Calculate ZLY
               if((heff2-zplat).GT.(1.2*hb)) then
                  zly=0.0
               elseif(sy3.GT.sz3) then
                  zly=rt2pi*(sy3-sz3)
               else
                  zly=0.0
               endif
! ---          Calculate initial radius of plume, RINIT
               rinit=rt2*sz3
! ---          Compute SS final plume rise
! ---          Set max distance to final rise
               xarg=amax1(xfinm_B,xfinb_B)
               call prss(xarg,zly,rinit,ws,istab,sqrts,diam(i),&
                         exitw(i),fluxm,fluxb,xfinm_B,xfinb_B,rise_SS)
! ---          Add constraint that final rise is not larger than Briggs
! ---          rise with partial penetration and/or wind shear adjustment
               if((mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0)&
                   .OR. mshear.EQ.1) then
                  rise_B=AMIN1(rise_SS,rise_B)
               else
                  rise_B=rise_SS
               endif
!
! ---          Reset BID and tip downwash = 0
               bidsq_B=0.0
               tipdw=0.0
            endif
!
! ---       Set final plume height
            heff_B=htstak(i)-tipdw+rise_B
!
         endif
      endif
! --- Select appropriate rise results
! -----------------------------------
      if(idw.EQ.1 .OR. idw.EQ.2) then
! ---    ISC-type downwash for this source: assign Briggs results
         LBRIGGS=.true.
      elseif(idw.EQ.3 .OR. idw.EQ.4) then
! ---    PRIME downwash for this source: assign Numerical results
         LBRIGGS=.false.
      elseif(idw.EQ.0 .AND. mrise.EQ.1) then
! ---    No downwash for this source and Briggs rise is selected
         LBRIGGS=.true.
      elseif(idw.EQ.0 .AND. mrise.EQ.2) then
! ---    No downwash for this source and Numerical rise is selected
         LBRIGGS=.false.
      else
         write(io6,*) ' ERROR in subr. POINTS1'
         write(io6,*)'Invalid source treatment found -- ',&
         'IDW(0,1,2,3, or 4) = ',idw,' MRISE(1 or 2) = ',mrise
         write(*,*)
         stop 'Halted in POINTS1 -- see list file.'
      endif
      if(LBRIGGS) then
         xfinm=xfinm_B
         xfinb=xfinb_B
         zfinm=zfinm_B
         zfinb=zfinb_B
         xf=xf_B
         rise=rise_B
         fmix=fmix_B
         hmax=hmax_B
         bidsq=bidsq_B
         heff=heff_B
      else
         xfinm=xfinm_N
         xfinb=xfinb_N
         zfinm=zfinm_N
         zfinb=zfinb_N
         xf=xf_N
         rise=rise_N
         fmix=fmix_N
         hmax=hmax_N
         bidsq=bidsq_N
         heff=heff_N
      endif
! ---------------------------------------------------------
! --- Compute the number of puffs/slugs released this hour
! --- from this source
! ---------------------------------------------------------
! --- Use CALMET/PROFILE winds at final rise to set release/sampling
! --- data and to check MFACT0 determination (slug changed to puff
! --- only if CALM is detected at both heights)
      if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
         call rlsmet(ldbhr,ixs,iys,md,heff,mfact,&
                     mfact0fr,ilayerfr,wsfr,flowfr,ivecfr,istab,dpbl,&
                     el,ustr,wstr,tsigvfr,tsigwfr,iru,issta,sqrtsfr,&
                     idoptyfr,idoptzfr)
         mfact0=MAX0(mfact0,mfact0fr)
         wsnew=AMAX1(ws,wsfr)
      else
         wsnew=ws
      endif
!
      npnew=wsnew*xtmp1(md)+1
      npnew=max0(npnew,1)
      npnew=min0(npnew,mxnew)
!
      if(lcav.AND.lprm) then
! ---    PRIME downwash produces both a primary and a cavity source
         newpt1(i)=2*npnew
      else
         newpt1(i)=npnew
      endif
!
      newpuf=newpuf+newpt1(i)
! --- If puff arrays are full, remove puffs that are off the grid
! --- and "roll down" arrays to make room for new puffs
! --- (this also applies to the DA file of tabulated arrays)
      if(npuffs+newpuf.gt.mxpuff) call rolldn(np)
! --- Check to see if roll-down was adequate
      if(npuffs+newpuf.gt.mxpuff)then
         write(io6,*) ' ERROR in subr. POINTS1'
         write(io6,*)'Too many puff on grid for array dimensions -- ',&
         'NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
         ' MXPUFF = ',mxpuff
         write(*,*)
         stop 'Halted in POINTS1 -- see list file.'
      endif
!
! --- DT is the time (sec) over which each puff is emitted
      dt=dthr/float(npnew)
! ---------------------------
! --- FOG processing Option
! ---------------------------
      if(MFOG.GT.0) then
! ---    Compute initial sigma consistent with volume flux
         ws3= wsnew * wsnew * wsnew
         if(ws3.GE.(vf0/dt**2)) then
            sigvf=SQRT(vf0/wsnew)/rt2pi
         else
            sigvf=((vf0*dt)**.3333333)/rt2pi
         endif
      else
         sigvf=0.0
      endif
! ------------------------------------
! --- Set initial sigmas at the source
! ------------------------------------
      szini=AMAX1(sigvf,szipt1(i))
      syini=AMAX1(sigvf,syipt1(i))
      szsrc=AMAX1(szmin,szini)
      sysrc=AMAX1(symin,syini)
!
      if(idw.EQ.3) then
! ---    PRIME: initial sigma already used
         sysrc=AMAX1(symin,syw0)
         sysrc2=AMAX1(symin,syc0)
         szsrc=AMAX1(szmin,szw0)
         szsrc2=AMAX1(szmin,szc0)
      elseif(idw.GT.0) then
! ---    Downwash is active: initial sigmas at the source must reproduce
! ---    the modified sigmas at 10 hl, using ambient growth curve.  The
! ---    modified sigmas add the downwash sigmas and any other initial
! ---    sigmas in quadrature. (HS/SS)
!
! ---    (1)  Add initial sigmas to downwash sigmas at 10 hl
         if(szini.GT.0.) then
            sigz=SQRT(szini**2+sz10**2)
         else
            sigz=sz10
         endif
         if(syini.GT.0.) then
            sigy=SQRT(syini**2+sy10**2)
         else
            sigy=sy10
         endif
!
! ---    (2)  Calculate virtual time/distance for these sigmas
         call sigtz(sigz,0.0,0.0,htstak(i),szamb,v10tz,v10dz)
         call sigty(sigy,0.0,0.0,syamb,v10ty,v10dy)
!
! ---    (3)  Set corresponding sigmas at the source (sy0dw,sz0dw)
! ---    this will allow the effects of downwash on the sigmas to
! ---    influence puff size beyond 10*hl
         ydxkm=amax1(0.0,v10dy-xarg10km)
         ydt=amax1(0.0,v10ty-targ10)
         zdxkm=amax1(0.0,v10dz-xarg10km)
         zdt=amax1(0.0,v10tz-targ10)
         call sigtz(0.0,zdxkm,zdt,htstak(i),sz0dw,virtz,virdz)
         call sigty(0.0,ydxkm,ydt,sy0dw,virty,virdy)
         sysrc=amax1(symin,sy0dw)
         szsrc=amax1(szmin,sz0dw)
      endif
!*****
      if(ldbhr)then
         write(io6,204)i,ilayer,ixs,iys,md,ws,istab,dpbl,issta,npnew,&
          newpuf,dt
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,2x,&
          'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'NPNEW: ',i4,2x,&
          'NEWPUF: ',i5/5x,'DT: ',f7.2)
!
         write(io6,206)htstak(i),diam(i),exitw(i),tstak(i),&
! frr(09/01)
!    1    tempss(issta),dtemp,fluxb,fluxm,xf,heff,tipdw
          temp,dtemp,fluxb,fluxm,xf,heff,tipdw
206      format(5x,'HTSTAK: ',f6.1,2x,'DIAM: ',f6.3,2x,'EXITW: ',&
          f6.3,2x,'TSTAK: ',f8.2,2x,'TEMPSS: ',f6.2,2x,'DTEMP: ',f8.3,&
          2x,'FLUXB : ',f8.3,2x,'FLUXM: ',f6.1/5x,'XF: ',f6.1,2x,&
          'HEFF: ',f6.1,2x,'TIPDW: ',f6.1)
!
         write(io6,210)idw,heff2,zly,rinit,rise
210      format(5x,'IDW: ',i5,2x,'HEFF2: ',f8.2,2x,'ZLY : ',f8.2,2x,&
          'RINIT : ',f8.2,'RISE : ',f8.2)
         if(idownw(i).GT.0) then
            if(mbdw.EQ.2) then
               write(io6,*) 'hb,hw,index = ',dsbh,dsbw,index
               write(io6,*) 'len,[x,y]adj = ',dsbl,xadj,yadj
            else
               write(io6,*) 'hb,hw,index = ',hb,hw,index
            endif
         endif
!
         write(io6,208)i,np,idopty,idoptz,iru,sysrc,szsrc,dt,istab
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,'IDOPTY: ',i2,2x,&
          'IDOPTZ: ',i2,2x,'IRU: ',i2,/2x,'SIGYB: ',f8.2,2x,&
          'SIGZB: ',f8.2,2x,'DT: ',f7.2,2x,'ISTAB: ',i1)
         if(mfog.GT.0) write(io6,*)'TXSMXFOG = ',txsmxfog
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
      if((.NOT.lcav .AND. .NOT.lprm) .OR. lprm) then
         do 50 j=1,npnew
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call swap(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xpt1grd(i)
         ypb(np)=ypt1grd(i)
         zitibl(np)=-1.0
         elbase(np)=elstak(i)
         sigyb(np)=sysrc
         sigzb(np)=szsrc
! ---    Final plume rise results
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xf
         bidfnl(np)=bidsq
! ---    Intermediate results for calc. gradual rise
         fb(np)=fluxb
         fm(np)=fluxm
         xbfin(np)=xfinb
         xmfin(np)=xfinm
         zbfin(np)=zfinb
         zmfin(np)=zfinm
         stipdw(np)=tipdw
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=htstak(i)
         exitw0(np)=exitw(i)
         diam0(np)=diam(i)
         temit0(np)=dt
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations (downwash)
         idw0(np)=idw
         hb0(np)=hb
         hw0(np)=hw
         heff20(np)=heff2
         zly0(np)=zly
         r0(np)=rinit
         xshift0(np)=xshift
         sysrc0(np)=syini
         szsrc0(np)=szini
         sy0(np)=sysrc
         sz0(np)=szsrc
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from a source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =1 for const. point sources)
         irlsnum(np)=j
         isrcnum(np)=i
         isrctyp(np)=itype
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    Note that "oldest" end of slug is at the source, as is the
! ---    "newest end; oldest end moves away and grows during the step,
! ---    while newest end remains fixed in size at the source.
!
         if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---       Set coordinates of oldest end of slug (in met. grid units)
            xpe(np)=xpb(np)
            ype(np)=ypb(np)
            xtote(np)=xtotb(np)
            tmtote(np)=tmtotb(np)
! ---       Set sigmas for oldest end of slug
            sigye(np)=sigyb(np)
            sigze(np)=sigzb(np)
         endif
!
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
         call inject(np,nspec,dt,mfact0,q,heff,hmax,dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer tabulated arrays record to DA file using full
! ---    MXRISE dimension
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
      endif
! ---------------------------------------------------------
! --- Initialize new puffs for PRIME cavity source (idw=4)
! ---------------------------------------------------------
      if(lcav) then
      do 150 j=1,npnew
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call swap(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xcavgrd
         ypb(np)=ycavgrd
         zitibl(np)=-1.0
         elbase(np)=elstak(i)
         sigyb(np)=sysrc2
         sigzb(np)=szsrc2
         stipdw(np)=tipdw
         isplit(np)=1
! ---    Time-of-release data
! ---    Set release ht to building ht for this cavity source (puffs
! ---    are released at ZERO, but are initially advected with wind
! ---    at building ht)
         ht0(np)=dsbh
         exitw0(np)=0.0
         diam0(np)=0.0
         temit0(np)=dt
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations (downwash)
         idw0(np)=4
         hb0(np)=dsbh
         hw0(np)=dsbw
         xshift0(np)=xshift2
         sysrc0(np)=0.0
         szsrc0(np)=0.0
         sy0(np)=sysrc2
         sz0(np)=szsrc2
! ---    Emission period index for this puff
         iemstep(np)=1
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from a source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =1 for const. point sources)
         irlsnum(np)=j
         isrcnum(np)=i
         isrctyp(np)=itype
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    Note that "oldest" end of slug is at the source, as is the
! ---    "newest end; oldest end moves away and grows during the step,
! ---    while newest end remains fixed in size at the source.
!
         if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---       Set coordinates of oldest end of slug (in met. grid units)
            xpe(np)=xpb(np)
            ype(np)=ypb(np)
            xtote(np)=xtotb(np)
            tmtote(np)=tmtotb(np)
! ---       Set sigmas for oldest end of slug
            sigye(np)=sigyb(np)
            sigze(np)=sigzb(np)
         endif
!
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
         call inject(np,nspec,dt,mfact0,qcav,0.0,hmax,dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer tabulated arrays record to DA file using full
! ---    MXRISE dimension
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
150   continue
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine points2(ip,i,nspec,ndhrb,nsecb,ndhre,nsece,dtsec,&
                         smxi1,ldb,mfact,metfm,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           POINTS2
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               POINT SOURCES with VARIABLE emissions
!
! --- UPDATE
! --- TNG-7.0.0 - TNG-7.1.0  141230  (DGS)
!                                : Allow roughness adjustment (MROUGH)
!                                  of RURAL PG sigmas to be used with
!                                  CALMET gridded fields
!                                : Set BID for PRIME case where primary
!                                  plume is not in wake (no wake table)
!                                : Add explicit treatment of partial
!                                  penetration with PRIME downwash only
!                                  when plume does NOT enter wake
!                                : Adjust final rise with ISC downwash
!                                  when using partial penetration and/or
!                                  wind shear options
! --- V6.42_x1.1 - TNG-7.0.0  140913  (DGS)
!                                : Remove flare processing updates
! --- V6.42_x1.0-V6.42_x1.1 140521: Add calls to WAKE_CSIG to set
!                                  dispersion data for release ht and
!                                  for building top
! --- V6.402-V6.42_x1.0 121203   : Process FLARE data into point-source
!                                  variables and generate puffs/slugs
!                                  accordingly
! --- V6.302-V6.402 101111  (DGS): Update GETCELL arguments
! --- V6.301-V6.302 100917  (DGS): Add idownw as a screen for debug
!                                  writes so that unassigned downwash
!                                  variables are not written to list
!                                  file
! --- V6.3-V6.301   100827  (DGS): Insert v6.269 update
!    [V6.268-V6.269 100430  (DGS): Fix timestep provided to INJECT call
!                                  for the cavity source.  Incorrect
!                                  variable named was not assigned a
!                                  value, leading to zero emissions
!                                  from the cavity.]
! --- V6.269-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.267-V6.268 100108  (DGS): Set BID to zero for PRIME sources as
!                                  wake tables now include BID, and add
!                                  HTR to CAV_CONC call
! --- V6.266-V6.267 090710  (DGS): Add maximum lid height for mass above
!                                  Zi to CAV_CONC arguments for later 
!                                  use in VCOUP
! --- V6.265-V6.266 090619  (DGS): Do not adjust Briggs plume rise for
!                                  shear when distance to final rise is
!                                  zero (near-calm)
!                           (DGS): Write more digits for TSTAK, DTEMP
!                                  debug output
! --- V6.261-V6.265 090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
! --- V6.261-V6.263 080827  (DGS): Modify logic for assigning partial
!                                  penetration parameters to conform
!                                  to implementation prior to version
!                                  6.26.  This affects the combination
!                                  of Briggs rise and PRIME downwash.
! --- V6.26-V6.261  080520  (DGS): Place rise tables in DA file
! --- V6.25-V6.26   080430  (DGS): Add RISE.DAT output calls
!                                  (requires timestamp in arg list)
!                   080430  (DGS): Add numerical rise option
! --- V6.22-V6.25   080227  (DGS): Halt run if any source is not on the
!                                  computational grid (replaces a
!                                  warning)
! --- V6.2-V6.22    070921  (DGS): Initialize variables
!                                  INDEX = 0
!                   070921  (DGS): Replace intrinsic function AMIN0()
!                                  with MIN() when both result and
!                                  arguments are integers
! --- V6.0-V6.2     070629  (DGS): Initialize variables
!                                  HB, HW, HEFF2, ZLY, RINIT = 0.0
!                   070629  (DGS): Fix debug output index from J to I
!                   070629  (DGS): Fix name of IRU used in PRIME section
!                                  when the meteorology at building-ht
!                                  is extracted (IRUB was not defined)
! --- V5.75-V6.0    050915  (DGS): generalize treatment of time for
!                                  variable emissions files --
!                                  resolve time to seconds, and
!                                  allow variable emissions timesteps
! --- V5.74-V5.75   050225  (DGS): add platform adjustment to ISC
!                                  building downwash (MBDW=1)
!                   050225  (DGS): Add DPBL arg to SETCSIG for TAULY
! --- V5.72-V5.74   040715  (DGS): add METFM=5 (AERMET)
! --- V5.7-V5.72    031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.5-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
!                   030402  (DGS): implement PRIME downwash option
! --- V5.4-V5.5     010730  (DGS): store initial sigmas from PTEMARB
!                                  file in SYSRC0 and SZSRC0 for use
!                                  in downwash zone
!                   010730  (DGS): revise building-array index to match
!                                  ISC
! --- V5.4-V5.4     000602_8 (DGS): add constraint that buoyancy flux
!                                   (FLUXB) be greater than zero
!                                   to conditions that must be met for
!                                   calling partial penetration sub.
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!                   000602  (DGS): add initial sigmay,z arrays
!                   000602  (DGS): add MFOG option to process emissions
!                                  file from CTEMISS
! --- V5.0-V5.3     991222b (DGS): reset momentum flux using FMFAC
!                   991222b (DGS): pass momentum flux to STKTIP
! --- V5.0-V5.0     980807  (DGS): use final rise CALMET/PROFILE winds 
!                                  to set release step
! --- V5.0-V5.0     980515  (DGS): ZITIBL array added
! --- V5.0-V5.0     980430  (DGS): assign PROBLEM (compiler warning)
! --- V5.0-V5.0     980304  (DGS): place source sigmas in SY0,SZ0
! --- V4.0-V5.0     971107  (DGS): modify partial penetration logic to
!                                  be consistent with POINTS1
!                   971107  (DGS): add building downwash
!                   971107  (DGS): remove volume flux from PT2 input
!                                  processing
! --- V4.0-V5.0     971107  (DGS): ZPB,ZPE arrays added
!                   971107  (DGS): add observed inversion strength
!
! --- INPUTS:
!            IP - integer - PTEMARB.DAT file index
!             I - integer - Point source index
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         DTSEC - real    - Length (seconds) of a basic time step
! SMXI1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!         METFM - integer - Meteorological data format
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, PTG0(2), WSCALM, TBD, PLX0
!     Common block /DISPDAT/ variables:
!           JSUP
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MTIP, MSHEAR, MPARTL, MHFTSZ, MFOG, MBDW,
!           MRISE
!     Common block /FOG/ variables:
!           TXSMXFOG
!     Common block /GRID/ variables:
!           IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /METHD/ variables:
!           LCALGRD, I2DMET
!     Common block /METHR/ variables:
!           TEMPSS(mxss,,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP, PTG(2), DPTINVO
!     Common block /PUFF/ variables:
!           NPUFFS
!     Common block /PT2/ variables:
!           TIEM2(8,mxpt2), NPT2,
!           BHT2(36,mxpt2), BWD2(36,mxpt2),
!           BLN2(36,mxpt2),XBADJ2(36,mxpt2),YBADJ2(36,mxpt2),
!           SYIPT2(mxqstep,mxpt2),SZIPT2(mxqstep,mxpt2),ZPLATPT2(mxpt2)
!           NSTEP2(mxemdat), NDHRQB2(mxqstep,mxemdat),
!           NSECQB2(mxqstep,mxemdat),
!           NDHRQE2(mxqstep,mxemdat),NSECQE2(mxqstep,mxemdat),
!           TSTAK2(mxqstep,mxpt2),EXITW2(mxqstep,mxpt2),
!           QSTAK2(mxspec,mxqstep,mxpt2)
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXMETDOM, MXQSTEP, MXPT2, MXRISE,
!           IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /FOG/ variables:
!           TXSMXFOG
!     Common block /PT2/ variables:
!           NEWPT2(mxqstep,mxpt2)
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!     Common block /SRCTAB/ variables:
!           NTR, NWK, NCV,
!           XTR(mxrise),ZTR(mxrise),RTR(mxrise),HTR(mxrise),
!           XWK(mxrise),SYWK(mxrise),SZWK(mxrise),DRWK(mxrise),
!           XCV(mxrise),SYCV(mxrise),SZCV(mxrise)
!
! --- POINTS2 called by:  INITPUF
! --- POINTS2 calls:      RLSMET, ROLLDN, PTLAPS, SETCSIG, SIGTY,
!                         SIGTZ, STKTIP, DWSIGS, PRSS, PRFIN, PRM,
!                         PRFINSH, PRFPP, NUMMET, NUMRISE, NUMCLIP,
!                         WAKE_INI, WAKE_FIN, CAV_CONC, WAKE_DBG,
!                         NRISEHD, SWAP, INJECT, DELTSEC,
!                         ZEROTAB, SRCTABOUT, GETCELL
!                         WAKE_CSIG
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      include 'comparm.puf'
      include 'csigma.puf'
      include 'dispdat.puf'
      include 'flags.puf'
      include 'fog.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
      include 'pt2.puf'
      include 'slug.puf'
      include 'srctab.puf'
!
      real smxi1(mxmetdom)
      real q(mxspec),qcav(mxspec)
      logical ldb,problem
      logical LBRIGGS
! --- PRIME logicals to configure primary (LPRM) and cavity (LCAV) puff
      logical lprm,lcav
!
      data rt2/1.4142136/, rt2pi/2.5066283/, piby4/0.7853982/
! --- Set minimum allowed potential temperature gradient PTGRAD0,
      data ptgrad0/.005/
! --- Set minimum allowed wind speed for building downwash
      data wsdw0/1.0/
! --- Set source type (2 for POINTS2)
      data itype/2/
      data zero/0.0/
! --- Initialize local ISC building downwash variables
      index=0
      hb=0.0
      hw=0.0
      heff2=0.0
      zly=0.0
      rinit=0.0
! --- Transfer selected data from TIEM2 array for clarity
      xgrd=tiem2(1,i)
      ygrd=tiem2(2,i)
      htstak=tiem2(3,i)
      diam=tiem2(4,i)
      elstak=tiem2(5,i)
      idownw=NINT(tiem2(6,i))
      fmfpt2=tiem2(7,i)
!
! --- Initialize number of new puffs from this source, this timestep
      newpt2(i)=0
!
! ***
      if(ldb) then
         write(io6,*)
         write(io6,*) 'POINTS2:  Source Number    ',i
         write(io6,*) '--------'
      endif
! ***
!
! --- Do not generate puffs for this source if all emissions are ZERO!
      qsum=0.0
      do it=1,nstep2(ip)
         do is=1,nspec
            qsum=qsum+qstak2(is,it,i)
         enddo
      enddo
      if(qsum.EQ.0.0) return
!
! --- Determine the MET grid 1 (outermost) point closest to the stack
      ixs=1.0+xgrd
      iys=1.0+ygrd
!
! --- Source off the computational grid -- write a FATAL message
      if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
         jecomp)then
         write(io6,20)i,ixs,iys
20       format(/1x,'FATAL -- a point source with variable emissions',&
         ' is off the computational grid'/1x,'source no. (i) = ',i6,2x,&
         'nearest grid point (ixs,iys) = (',i5,',',i5,')')
         problem=.TRUE.
         return
      endif
! --- Now determine the met. grid point closest to the stack
      call GETCELL('POINTS2     ','GRIDIJ',1,xgrd,ygrd,zero,zero,&
                   ixs,iys,md)
!
! --- Extract stack height wind speed & other met. variables
! --- and set CALM configuration if triggered
      call RLSMET(ldb,ixs,iys,md,htstak,mfact,&
                  mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                  ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
!
! frr (09/01) new calmet format (2D temp)
      if(i2dmet.EQ.1) then
         temp=temp2d(ixs,iys,md)
      elseif(i2dmet.EQ.0) then
         temp=tempss(issta,md)
      else
         write(*,*)'Subr. POINTS2:  Invalid I2DMET = ',i2dmet
         stop
      endif
 
! --- Trap missing power-law exponent (substitute default)
      if(plexp.LT.-900.) then
! ---    Use default
         plx=plx0(istab)
      else
! ---    Use it
         plx=plexp
      endif
! --- Compute the number of puffs/slugs released this timestep
! --- from this source, consistent with transport constraints
      npnewt=ws*smxi1(md)
! --- Add in the number of puffs/slugs needed to resolve changes in
! --- the emissions data, for conservative estimate
      npnewt=npnewt+nstep2(ip)
      newpuft=newpuf+npnewt
! --- If puff arrays are full, remove puffs that are off the grid
! --- and "roll down" arrays to make room for new puffs
! --- (this also applies to the DA file of tabulated arrays)
      if(npuffs+newpuft.gt.mxpuff) call ROLLDN(np)
! --- Check to see if roll-down was adequate
      if(npuffs+newpuft.gt.mxpuff)then
         write(io6,*) ' ERROR in subr. POINTS2'
         write(io6,*)'Too many puff on grid for array dimensions -- ',&
         'NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuft,&
         ' MXPUFF = ',mxpuff
         stop
      endif
! --- Loop over emission periods within this timestep
      do 100 it=1,nstep2(ip)
!
! --------------------------------------------------------------------
! ---    Do not generate puff for this step if all emissions are ZERO!
! --------------------------------------------------------------------
         qsum=0.0
         do is=1,nspec
            qcav(is)=0.0
            q(is)=qstak2(is,it,i)
            qsum=qsum+q(is)
         enddo
         if(qsum.EQ.0.0) goto 100
! ---    Place source data for this step into local variables
         tstak=tstak2(it,i)
         exitw=exitw2(it,i)
! ---    Initialize source tabulation arrays to zero
         call ZEROTAB
! ---    Set specific plume rise variables for Briggs and Numerical
! ---    rise to clarify treatment
! ---    Briggs
         xfinm_B=0.0
         xfinb_B=0.0
         zfinm_B=0.0
         zfinb_B=0.0
         xf_B=0.0
         rise_B=0.0
         fmix_B=1.0
         hmax_B=0.0
         bidsq_B=0.0
         heff_B=0.0
! ---    Numerical
         xfinm_N=0.0
         xfinb_N=0.0
         zfinm_N=0.0
         zfinb_N=0.0
         xf_N=0.0
         rise_N=0.0
         fmix_N=1.0
         hmax_N=0.0
         bidsq_N=0.0
         heff_N=0.0
! -------------------------------------------------------------
! --- Compute the final plume rise and distance to final rise
! -------------------------------------------------------------
! ---    Calculate fluxes
         dtemp=tstak-temp
         dtemp=amax1(dtemp,0.0)
! ---    Buoyancy flux:  2.4516625 = g/4., where g = 9.80665 m/s**2
         fluxb=(2.4516625*diam**2*exitw/tstak)*dtemp
! ---    Momentum flux
         fluxm=0.25*(exitw*diam)**2*temp/tstak
! ---    Apply vertical momentum flux factor of zero or one
! ---    Use simple test for ZERO instead of forming the product:
! ---    fluxm=fluxm*fmfpt2
         if(fmfpt2.LT.0.5) fluxm=0.0
!
! ---    Adjustment for stack-tip downwash (NOT for bldg downwash!)
         if(mtip.EQ.1) then
            call stktip(htstak,diam,fluxm,exitw,ws,tipdw)
         else
            tipdw=0.0
         endif
! --- Briggs Rise (always computed)
! ---------------------------------
! ---    Final plume rise (no downwash considered)
         call prfin(exitw,diam,fluxm,fluxb,ws,istab,sqrts,&
                    xfinm_B,xfinb_B,xf_B,zfinm_B,zfinb_B,rise_B)
!
! ---    Compute vertical wind shear effects
         if(mshear.EQ.1 .AND. xfinb_B.GT.0.0)then
            call prfinsh(fluxb,ws,plx,htstak,xfinb_B,istab,sqrts,&
             zfinsh)
! ---        Final buoyant rise is the LOWER of the Briggs & shear hts.
             zfinb_B=amin1(zfinb_B,zfinsh)
! ---        Final plume rise is the HIGHER of momentum & buoyant hts.
             rise_B=amax1(zfinb_B,zfinm_B)
         endif
!
! ---    Adjust for partial penetration of elevated stable layer
!        if(mpartl.EQ.1 .and. istab.LE.3 .and. htstak.LT.dpbl) then
         if(mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0) then
! ---       Set thickness (m) of transition region above mixing height
            dzinv=30.
! ---       Estimate potential temperature gradient above mixed layer
            if(LCALGRD) then
! ---          Use temperatures provided by met model
               htinv=amax1(dpbl,htstak)
               call ptlaps(ixs,iys,md,htinv,ptgrad0,dzinv,ptginv,tmix)
            else
! ---          Use default potential temperature lapse rate
               if(jsup.LE.4) then
                  ptginv=ptgrad0
               elseif(jsup.EQ.5) then
                  ptginv=amax1(ptg0(1),ptgrad0)
               else
                  ptginv=amax1(ptg0(2),ptgrad0)
               endif
            endif
! ---       Estimate potential temperature change across inversion
            if(dptinvo.GT.0.0) then
! ---          Use observed value provided in met file
               dptinv=dptinvo
            else
               dptinv=ptginv*dzinv
            endif
            call prfpp(rise_B,fluxb,ws,htstak,dpbl,temp,ptginv,&
                       dptinv,fmix_B,hmax_B)
! ---       Reset component final rise heights for use in gradual rise
            if(zfinb_B.GT.rise_B) zfinb_B=rise_B
            if(zfinm_B.GT.rise_B) zfinm_B=rise_B
!
         else
! ---       Set partial penetration variables to null values
            fmix_B=1.0
            hmax_B=dpbl
         endif
!
! ---    Set variance associated with buoyancy-enhanced growth at
! ---    final rise
         bidsq_B=(rise_B/3.5)**2
!
! ---    Set final plume height
         heff_B=htstak-tipdw+rise_B
!
! ---    Set selected data in /CSIGMA/ for sigma calls
         call SETCSIG(idopty,idoptz,iru,ws,istab,el,sqrts,&
                      tsigv,tsigw,symin,szmin,heff_B,dpbl,ixs,iys,md)
! ---    Assign Briggs adjustments to numerical rise variables as
! ---    defaults
         xfinm_N=xfinm_B
         xfinb_N=xfinb_B
         zfinm_N=zfinm_B
         zfinb_N=zfinb_B
         xf_N=xf_B
         rise_N=rise_B
         fmix_N=fmix_B
         hmax_N=hmax_B
         bidsq_N=bidsq_B
         heff_N=heff_B
! ---    Numerical Rise (conditional)
! -------------------------------------
         if(mrise.EQ.2) then
! ---       Construct met. profiles at this grid cell
            call NUMMET(ixs,iys,md,ldb)
! ---       Set stack radius
            reff=0.5*diam
! ---       Set effective release height
            hteff=AMAX1(0.0,htstak-tipdw)
! ---       Prepare data header record for RISE.DAT file
! ---       (control for output to scratch file is within subroutine)
            call NRISEHD(itype,i,ndhrb,nsecb,ndhre,nsece,&
                         ndhrqb2(it,ip),nsecqb2(it,ip),ndhrqe2(it,ip),&
                         nsecqe2(it,ip),elstak,hteff,tstak,reff,exitw,&
                         xgrd,ygrd)
! ---       Compute plume rise
            mprime=0
! ---       Use full MXRISE points in rise table
            ntr=mxrise
            call NUMRISE(mprime,ldb,hteff,tstak,reff,exitw,syipt2(it,i),&
                         szipt2(it,i),ntr,xtr,ztr,rtr,htr)
            rise_N=htr(ntr)
! ---       Adjust for partial penetration of elevated stable layer
            if(mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0) then
! ---          (ptginv, dptinv computed in Briggs Rise section)
               call PRFPP(rise_N,fluxb,ws,htstak,dpbl,temp,ptginv,&
                          dptinv,fmix_N,hmax_N)
! ---          Clip rise table at new final height
               call NUMCLIP(mxrise,hteff,rise_N,ntr,xtr,ztr,rtr,htr)
            else
! ---          Set partial penetration variables to null values
               fmix_N=1.0
               hmax_N=dpbl
            endif
! ---       Assign final plume height and distance
            heff_N=ztr(ntr)
            xf_N=xtr(ntr)
! ---       Variance associated with buoyancy-enhanced growth
! ---       @ final rise
            bidsq_N=(rise_N/3.5)**2
         endif
! --------------------------------------------------------------------
! --- Determine if building downwash effects on plume rise and sigmas
! --- should be considered
! --------------------------------------------------------------------
         sz0dw=0.0
         sy0dw=0.0
         xshift=0.0
         xshift2=0.0
         lprm=.FALSE.
         lcav=.FALSE.
! ---    Initialize downwash flag to zero (no building downwash)
! ---    Note:  idw=0  no downwash
! ---           idw=1  Huber-Snyder model
! ---           idw=2  Schulman-Scire model
! ---           idw=3  PRIME model (puff released from primary source)
! ---           idw=4  PRIME model (puff released from cavity source)
         idw=0
         if(idownw.EQ.1 .AND. ws.GE.wsdw0 .AND. mbdw.EQ.2) then
!        --------------------------------
!        --- PRIME Downwash Section
!        --------------------------------
            mprime=1
! ---       Store dispersion data for release height for later use
            if(mrise.EQ.2) then
               zht=heff_N
            else
               zht=heff_B
            endif
            call WAKE_CSIG(idopty,idoptz,iru,ws,istab,el,&
                           sqrts,tsigv,tsigw,symin,szmin,&
                           zht,dpbl,ixs,iys,md,'RELEASE ')
! ---       Get direction-specific building data (direction at stack ht)
            index=INT(0.1*FLOAT(ivec)+0.4999)
            if(index.eq.0)index=36
            dsbh=bht2(index,i)
            dsbw=bwd2(index,i)
            dsbl=bln2(index,i)
            xadj=xbadj2(index,i)
            yadj=ybadj2(index,i)
            hl=AMIN1(dsbh,dsbw)
! ---       Screen for GEP stack (regulatory definition)
            geptest=dsbh+1.5*hl
            if(htstak.LT.geptest) then
               idw=3
! ---          Set stack radius
               reff=0.5*diam
! ---          Set met data at top of building
               call RLSMET(ldb,ixs,iys,md,dsbh,mfact,&
                        mfact0b,ilayer,wsb,flowb,ivecb,istabb,dpbl,&
                        elb,ustrb,wstrb,tsigvb,tsigwb,iru,issta,&
                        sqrtsb,idoptyb,idoptzb)
! ---          Reset /CSIGMA/ data for met data at top of building
               if(mrise.EQ.2) then
                  zht=AMAX1(heff_N,dsbh)
               else
                  zht=AMAX1(heff_B,dsbh)
               endif
               call SETCSIG(idoptyb,idoptzb,iru,wsb,istabb,elb,sqrtsb,&
                         tsigvb,tsigwb,symin,szmin,zht,dpbl,ixs,iys,md)
! ---          Store dispersion data for top of building for later use
               call WAKE_CSIG(idoptyb,idoptzb,iru,wsb,istabb,elb,&
                              sqrtsb,tsigvb,tsigwb,symin,szmin,&
                              zht,dpbl,ixs,iys,md,'BUILDING')
! ---          Refresh /WAKEDAT/ variables
               call WAKE_INI(ldb,istab,iru,dsbh,dsbw,dsbl,xadj,yadj,&
                          wsb,ws,tsigvb,tsigwb,idoptyb,idoptzb)
! ---          Construct met. profiles at this grid cell
               call NUMMET(ixs,iys,md,ldb)
! ---          Prepare data header record for RISE.DAT file
! ---          (control for output to scratch file is within subroutine)
               call NRISEHD(itype,i,ndhrb,nsecb,ndhre,nsece,&
                         ndhrqb2(it,ip),nsecqb2(it,ip),ndhrqe2(it,ip),&
                         nsecqe2(it,ip),elstak,htstak,tstak,&
                         reff,exitw,xgrd,ygrd)
! ---          Compute plume rise and wake structure
! ---          Use full MXRISE points in rise table
               ntr=mxrise
               call NUMRISE(mprime,ldb,htstak,tstak,reff,exitw,&
                      syipt2(it,i),szipt2(it,i),ntr,xtr,ztr,rtr,htr)
! ---          Pass final wake structure to source arrays with BID
               call WAKE_FIN(ldb,nwk,xwk,szwk,sywk,drwk,szw0,syw0,&
                             ncv,xcv,szcv,sycv,szc0,syc0,fqcvpt2(i))
!
! ---          Set logicals (primary & cavity) and partition mass
               if(fqcvpt2(i).LE.0.001) then
                  lcav=.FALSE.
                  lprm=.TRUE.
                  do is=1,nspec
                     qcav(is)=0.0
                  enddo
               elseif(fqcvpt2(i).GE.0.999) then
                  lcav=.TRUE.
                  lprm=.FALSE.
                  do is=1,nspec
                     qcav(is)=q(is)
                     q(is)=0.0
                  enddo
               else
                  lcav=.TRUE.
                  lprm=.TRUE.
                  do is=1,nspec
                     qcav(is)=q(is)*fqcvpt2(i)
                     q(is)=q(is)-qcav(is)
                  enddo
               endif
! ---          Check for wake table
               if(nwk.LE.1 .AND. lprm) then
! ---             Case where primary plume is not in wake (no wake table)
! ---             This source will be treated as non-DW source but using
! ---             the rise calculated by PRIME if MRISE=2 (the _N values),
! ---             or Briggs if MRISE=1 (the _B values)
! ---             Reset the puff downwash flag
! ---             ----------------------------
                  idw=0
! ---             Apply partial penetration adjustments to the PRIME
! ---             numerical rise tables (conditional)
                  if(mpartl.EQ.1 .and. istab.LE.4 .and.&
                     fluxb.GT.0.0) then
! ---                (ptginv, dptinv computed in Briggs Rise section)
                     rise_N=htr(ntr)
                     call PRFPP(rise_N,fluxb,ws,htstak,dpbl,temp,&
                                ptginv,dptinv,fmix_N,hmax_N)
! ---                Clip rise table at new final height
                     call NUMCLIP(mxrise,htstak,rise_N,&
                                  ntr,xtr,ztr,rtr,htr)
                  else
! ---                Set partial penetration variables to null values
                     fmix_N=1.0
                     hmax_N=dpbl
                  endif
! ---             Assign final plume height and distance
                  rise_N=htr(ntr)
                  heff_N=ztr(ntr)
                  xf_N=xtr(ntr)
! ---             Variance associated with buoyancy-enhanced growth
! ---             @ final rise
                  bidsq_N=(rise_N/3.5)**2
               else
! ---             Case where primary plume enters wake (wake table)
! ---             Assign final plume height, rise, and distance for
! ---             primary source (if any)
                  rise_N=htr(ntr)
                  heff_N=ztr(ntr)
                  xf_N=xtr(ntr)
! ---             Reset stack-tip downwash
                  tipdw=0.0
! ---             The BID has been moved into the wake tables
                  bidsq_N=0.0
! ---             Set partial penetration variables to null values
                  fmix_N=1.0
                  hmax_N=dpbl
!
! ---             Report selected data to file for debug
                  if(ldb) call WAKE_DBG(io6,ntr,xtr,ztr,rtr,htr,&
                                       .FALSE.,htstak)
! ---             Compute near-cavity concentrations for current source, 
! ---             return location of cavity source on the met grid, and
! ---             return the distance from the source to the end of the
! ---             cavity transition zone
                  call CAV_CONC(ldb,nspec,q,qcav,xgrd,ygrd,&
                                ntr,xtr,ztr,htr,flow,dpbl,hmax_N,&
                                xcavgrd,ycavgrd,xshift,xshift2)
               endif
            endif
         endif
         if(idownw.GE.1 .AND. ws.GE.wsdw0 .AND. mbdw.EQ.1) then
!        --------------------------------
!        --- HS/SS Downwash Section
!        --------------------------------
! ---       Set direction-specific building width and height
            index=INT(0.1*FLOAT(ivec)+0.4999)
            if(index.eq.0)index=36
            hb=bht2(index,i)
            hw=bwd2(index,i)
            hl=amin1(hb,hw)
!
! ---       Assign platform height
            zplat=0.0
            if(idownw.EQ.2) zplat=zplatpt2(i)
! ---       Calculate the momentum rise at 2*hb and test for downwash
            xarg=2.*hb
            call prm(diam,exitw,fluxm,ws,istab,sqrts,xarg,&
                     xfinm_B,zfinm_B,z2hb)
            heff2=htstak+z2hb
            dwtest=hb+1.5*hl
!
            if((heff2-zplat).LE.dwtest) then
! ---          Set downwash flag to ACTIVE
               idw=1
! ---          (1)  Compute downwash sigmas at 10*hl for setting up
! ---          virtual sigmas at the source
               xarg10=10.*hl
               xarg10km=0.001*xarg10
               targ10=xarg10/ws
               call dwsigs(tbd,hw,hb,mhftsz,ws,htstak,heff2,zplat,&
                           xarg10,sy10,sz10)
!
! ---          (2)  Alter Final Plume rise if Schulman-Scire (SS) active
               sstest=hb+0.5*hl
               if((htstak-zplat).LT.sstest) then
! ---             Schulman-Scire downwash ACTIVE
                  idw=2
! ---             Set sigmas at 3*hl
                  xarg=3.*hl
                  call dwsigs(tbd,hw,hb,mhftsz,ws,htstak,heff2,zplat,&
                              xarg,sy3,sz3)
! ---             Determine line source parameters, ZLY and RINIT
! ---             Calculate ZLY
                  if((heff2-zplat).GT.(1.2*hb)) then
                     zly=0.0
                  elseif(sy3.GT.sz3) then
                     zly=rt2pi*(sy3-sz3)
                  else
                     zly=0.0
                  endif
! ---             Calculate initial radius of plume, RINIT
                  rinit=rt2*sz3
! ---             Compute SS final plume rise
! ---             Set max distance to final rise
                  xarg=amax1(xfinm_B,xfinb_B)
                  call prss(xarg,zly,rinit,ws,istab,sqrts,diam,&
                         exitw,fluxm,fluxb,xfinm_B,xfinb_B,rise_SS)
! ---             Add constraint that final rise is not larger than
! ---             Briggs rise with partial penetration and/or wind
! ---             shear adjustment
                  if((mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0)&
                      .OR. mshear.EQ.1) then
                     rise_B=AMIN1(rise_SS,rise_B)
                  else
                     rise_B=rise_SS
                  endif
!
! ---             Reset BID and tip downwash = 0
                  bidsq_B=0.0
                  tipdw=0.0
               endif
!
! ---          Set final plume height
               heff_B=htstak-tipdw+rise_B
!
            endif
         endif
! ---    Select appropriate rise results
! --------------------------------------
         if(idw.EQ.1 .OR. idw.EQ.2) then
! ---       ISC-type downwash for this source: assign Briggs results
            LBRIGGS=.true.
         elseif(idw.EQ.3 .OR. idw.EQ.4) then
! ---       PRIME downwash for this source: assign Numerical results
            LBRIGGS=.false.
         elseif(idw.EQ.0 .AND. mrise.EQ.1) then
! ---       No downwash for this source and Briggs rise is selected
            LBRIGGS=.true.
         elseif(idw.EQ.0 .AND. mrise.EQ.2) then
! ---       No downwash for this source and Numerical rise is selected
            LBRIGGS=.false.
         else
            write(io6,*) ' ERROR in subr. POINTS2'
            write(io6,*)'Invalid source treatment found -- ',&
            'IDW(0,1,2,3, or 4) = ',idw,' MRISE(1 or 2) = ',mrise
            write(*,*)
            stop 'Halted in POINTS2 -- see list file.'
         endif
         if(LBRIGGS) then
            xfinm=xfinm_B
            xfinb=xfinb_B
            zfinm=zfinm_B
            zfinb=zfinb_B
            xf=xf_B
            rise=rise_B
            fmix=fmix_B
            hmax=hmax_B
            bidsq=bidsq_B
            heff=heff_B
         else
            xfinm=xfinm_N
            xfinb=xfinb_N
            zfinm=zfinm_N
            zfinb=zfinb_N
            xf=xf_N
            rise=rise_N
            fmix=fmix_N
            hmax=hmax_N
            bidsq=bidsq_N
            heff=heff_N
         endif
! ---------------------------------------------------------
! ---    Compute number of puffs for this emissions period
! ---------------------------------------------------------
! ---    Use CALMET/PROFILE winds at final rise to set release/sampling
! ---    data and to check MFACT0 determination (slug changed to puff
! ---    only if CALM is detected at both heights)
         if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
            call RLSMET(ldb,ixs,iys,md,heff,mfact,&
                     mfact0fr,ilayerfr,wsfr,flowfr,ivecfr,istab,dpbl,&
                     el,ustr,wstr,tsigvfr,tsigwfr,iru,issta,sqrtsfr,&
                     idoptyfr,idoptzfr)
            mfact0=MAX0(mfact0,mfact0fr)
            wsnew=AMAX1(ws,wsfr)
         else
            wsnew=ws
         endif
! ---    Start-time for emission period during timestep
         if(ndhrqb2(it,ip).LT.ndhrb) then
            ndhr1=ndhrb
            nsec1=nsecb
         elseif(ndhrqb2(it,ip).EQ.ndhrb .AND.&
                nsecqb2(it,ip).LT.nsecb) then
            ndhr1=ndhrb
            nsec1=nsecb
         else
            ndhr1=ndhrqb2(it,ip)
            nsec1=nsecqb2(it,ip)
         endif
! ---    End-time for emission period during timestep
         if(ndhrqe2(it,ip).GT.ndhre) then
            ndhr2=ndhre
            nsec2=nsece
         elseif(ndhrqe2(it,ip).EQ.ndhre .AND.&
                nsecqe2(it,ip).GT.nsece) then
            ndhr2=ndhre
            nsec2=nsece
         else
            ndhr2=ndhrqe2(it,ip)
            nsec2=nsecqe2(it,ip)
         endif
! ---    Duration of emission period within timestep (seconds)
         call DELTSEC(ndhr1,nsec1,ndhr2,nsec2,ntemit)
         temit=FLOAT(ntemit)
! ---    Apply transport constraints
         npnewt=wsnew*smxi1(md)*temit/dtsec+1
         npnewt=MIN(npnewt,mxnew)
         temit=temit/FLOAT(npnewt)
! ---    PRIME downwash produces both a primary and a cavity source
         new=npnewt
         if(lcav.AND.lprm) new=2*npnewt
! ---    Update puff numbers
! ---    Individual puff release-steps
         newpt2(i)=newpt2(i)+npnewt
! ---    Total new puffs
         newpuf=newpuf+new
! ---    If puff arrays are full, remove puffs that are off the grid
! ---    and "roll down" arrays to make room for new puffs
! ---    (this also applies to the DA file of tabulated arrays)
         if(npuffs+newpuf.gt.mxpuff) call rolldn(np)
! ---    Check to see if roll-down was adequate
         if(npuffs+newpuf.gt.mxpuff)then
            write(io6,*) ' ERROR in subr. POINTS2'
            write(io6,*)'Too many puff on grid for array dimensions',&
            ' -- NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
            ' MXPUFF = ',mxpuff
            write(*,*)
            stop 'Halted in POINTS2 -- see list file.'
         endif
! ---------------------------
! --- FOG processing Option
! ---------------------------
         if(MFOG.GT.0) then
! ---       Screen for minimum temperature excess
            txsmxfog=AMIN1(txsmxfog,dtemp)
! ---       Compute initial sigma consistent with volume flux
            vf0=piby4*exitw*diam**2
            ws3= wsnew * wsnew * wsnew
            if(ws3.GE.(vf0/temit**2)) then
               sigvf=SQRT(vf0/wsnew)/rt2pi
            else
               sigvf=((vf0*temit)**.3333333)/rt2pi
            endif
         else
            sigvf=0.0
         endif
! ------------------------------------
! --- Set initial sigmas at the source
! ------------------------------------
         szini=AMAX1(sigvf,szipt2(it,i))
         syini=AMAX1(sigvf,syipt2(it,i))
         szsrc=AMAX1(szmin,szini)
         sysrc=AMAX1(symin,syini)
         if(idw.EQ.3) then
! ---       PRIME: set values without initial sigma option
            sysrc=AMAX1(symin,syw0)
            sysrc2=AMAX1(symin,syc0)
            szsrc=AMAX1(szmin,szw0)
            szsrc2=AMAX1(szmin,szc0)
      
         elseif(idw.GT.0) then
! ---       Downwash is active: initial sigmas at source must reproduce
! ---       the modified sigmas at 10 hl, using ambient growth curve.
! ---       The modified sigmas add the downwash sigmas and any other
! ---       initial sigmas in quadrature.
!
! ---       (1)  Add initial sigmas to downwash sigmas at 10 hl
            if(szini.GT.0.) then
               sigz=SQRT(szini**2+sz10**2)
            else
               sigz=sz10
            endif
            if(syini.GT.0.) then
               sigy=SQRT(syini**2+sy10**2)
            else
               sigy=sy10
            endif
!
! ---       (2)  Calculate virtual time/distance for these sigmas
            call sigtz(sigz,0.0,0.0,htstak,szamb,v10tz,v10dz)
            call sigty(sigy,0.0,0.0,syamb,v10ty,v10dy)
!
! ---       (3)  Set corresponding sigmas at the source (sy0dw,sz0dw)
! ---       this will allow the effects of downwash on the sigmas to
! ---       influence puff size beyond 10*hl
            ydxkm=amax1(0.0,v10dy-xarg10km)
            ydt=amax1(0.0,v10ty-targ10)
            zdxkm=amax1(0.0,v10dz-xarg10km)
            zdt=amax1(0.0,v10tz-targ10)
            call sigtz(0.0,zdxkm,zdt,htstak,sz0dw,virtz,virdz)
            call sigty(0.0,ydxkm,ydt,sy0dw,virty,virdy)
            sysrc=amax1(symin,sy0dw)
            szsrc=amax1(szmin,sz0dw)
         endif
!******
      if(ldb)then
         write(io6,*)
         write(io6,*)'Variable emissions step: ',it
         write(io6,204)i,ilayer,ixs,iys,md,ws,istab,dpbl,&
          issta,npnewt,newpuf,temit
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,&
          2x,'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'NPNEWT: ',i4,2x,&
          'NEWPUF: ',i5/5x,'DT: ',f7.2)
!
         write(io6,206)htstak,diam,exitw,tstak,&
!frr 1    tempss(issta),dtemp,fluxb,fluxm,xf,heff,tipdw
          temp,dtemp,fluxb,fluxm,xf,heff,tipdw
206      format(5x,'HTSTAK: ',f6.1,2x,'DIAM: ',f6.3,2x,'EXITW: ',&
          f6.3,2x,'TSTAK: ',f8.2,2x,'TEMPSS: ',f6.2,2x,'DTEMP: ',f8.3,&
          2x,'FLUXB : ',f8.3,2x,'FLUXM: ',f6.1/5x,'XF: ',f6.1,2x,&
          'HEFF: ',f6.1,2x,'TIPDW: ',f6.1)
!
         write(io6,210)idw,rise
210      format(5x,'IDW: ',i5,2x,'RISE : ',f8.2)
         if(idownw.GT.0) then
            if(mbdw.EQ.2) then
               write(io6,*) 'hb,hw,index = ',dsbh,dsbw,index
               write(io6,*) 'len,[x,y]adj = ',dsbl,xadj,yadj
            else
               write(io6,*) 'hb,hw,index = ',hb,hw,index
            endif
         endif
!
         write(io6,208)i,np,iru,sysrc,szsrc,temit,istab
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,&
          'IRU: ',i2,2x,'SIGYB: ',f8.2,2x,'SIGZB: ',f8.2,2x/&
          5x,'DT: ',f7.2,2x,'ISTAB: ',i1)
         if(mfog.GT.0) write(io6,*)'TXSMXFOG = ',txsmxfog
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         if((.NOT.lcav .AND. .NOT.lprm) .OR. lprm) then
            do 50 j=1,npnewt
            np=np+1
!
! ---      Use SWAP to start with all puff/slug variables equal to zero
            call swap(np,0)
!
! ---       Fill non-zero values
            xpb(np)=xgrd
            ypb(np)=ygrd
            zitibl(np)=-1.0
            elbase(np)=elstak
            sigyb(np)=sysrc
            sigzb(np)=szsrc
! ---       Final plume rise results
            zfinal(np)=heff
            zpb(np)=heff
            zpe(np)=heff
            xfinal(np)=xf
            bidfnl(np)=bidsq
! ---       Intermediate results for calc. gradual rise
            fb(np)=fluxb
            fm(np)=fluxm
            xbfin(np)=xfinb
            xmfin(np)=xfinm
            zbfin(np)=zfinb
            zmfin(np)=zfinm
            stipdw(np)=tipdw
            isplit(np)=1
! ---       Time-of-release data
            ht0(np)=htstak
            exitw0(np)=exitw
            diam0(np)=diam
            temit0(np)=temit
            ws0(np)=ws
            istab0(np)=istab
            sqrts0(np)=sqrts
            iru0(np)=iru
            sigv0(np)=tsigv
            sigw0(np)=tsigw
            el0(np)=el
            plexp0(np)=plx
! ---       Set vector/scalar ws ratio to 1.0
            srat0(np)=1.0
! ---       Additional time-of-release parameters needed for
! ---       receptor-specific calculations (downwash)
            idw0(np)=idw
            hb0(np)=hb
            hw0(np)=hw
            heff20(np)=heff2
            zly0(np)=zly
            sysrc0(np)=syini
            szsrc0(np)=szini
            r0(np)=rinit
            xshift0(np)=xshift
            sy0(np)=sysrc
            sz0(np)=szsrc
! ---       Place end-time of emission period for this puff into
! ---       TMTOTB(mxpuff) array, expressed as fraction of the timestep
! ---       This will be used in COMP when computing the sampling steps
! ---       used to advect the freshly emitted puff during remainder
! ---       of the timestep, and then reset.
! ---       Seconds from start of emissions period to end of timestep
            call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
            tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
! ---       Emission period index for this puff
            iemstep(np)=it
! ---       Set puff identity where IRLSNUM is the puff number released
! ---       from a source this time step, ISRCNUM is the source number
! ---       of type ISRCTYP from which the puff was released.
! ---       (Type =2 for point sources with VARIABLE emiss.)
            jj=newpt2(i)-npnewt+j
            irlsnum(np)=jj
            isrcnum(np)=i
            isrctyp(np)=2
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---       Note that "oldest" end of slug is at the source, as is the
! ---       "newest end; oldest end moves away and grows during the
! ---       step, while newest end remains fixed in size at the source.
!
            if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---          Set coordinates of oldest end of slug (met. grid units)
               xpe(np)=xpb(np)
               ype(np)=ypb(np)
               xtote(np)=xtotb(np)
               tmtote(np)=tmtotb(np)
! ---          Set sigmas for oldest end of slug
               sigye(np)=sigyb(np)
               sigze(np)=sigzb(np)
            endif
!
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
            call inject(np,nspec,temit,mfact0,q,heff,hmax,dpbl,fmix,&
                        istab)
! ------------------------------------------------------------------
! ---       Transfer tabulated arrays record to DA file using full
! ---       MXRISE dimension
! ------------------------------------------------------------------
            call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
!
50          continue
         endif
! ------------------------------------------------------------
! ---    Initialize new puffs for PRIME cavity source (idw=4)
! ------------------------------------------------------------
         if(lcav) then
         do 150 j=1,npnewt
            np=np+1
!
! ---       Use SWAP to start with all puff/slug variables equal to zero
            call swap(np,0)
!
! ---       Fill non-zero values
            xpb(np)=xcavgrd
            ypb(np)=ycavgrd
            zitibl(np)=-1.0
            elbase(np)=elstak
            sigyb(np)=sysrc2
            sigzb(np)=szsrc2
            stipdw(np)=tipdw
            isplit(np)=1
! ---       Time-of-release data
! ---       Set release ht to building ht for this cavity source (puffs
! ---       are released at ZERO, but are initially advected with wind
! ---       at building ht)
            ht0(np)=dsbh
            exitw0(np)=0.0
            diam0(np)=0.0
            temit0(np)=temit
            ws0(np)=ws
            istab0(np)=istab
            sqrts0(np)=sqrts
            iru0(np)=iru
            sigv0(np)=tsigv
            sigw0(np)=tsigw
            el0(np)=el
            plexp0(np)=plx
! ---       Set vector/scalar ws ratio to 1.0
            srat0(np)=1.0
! ---       Additional time-of-release parameters needed for
! ---       receptor-specific calculations (downwash)
            idw0(np)=4
            hb0(np)=dsbh
            sysrc0(np)=0.0
            szsrc0(np)=0.0
            hw0(np)=dsbw
            xshift0(np)=xshift2
            sy0(np)=sysrc2
            sz0(np)=szsrc2
! ---       Place end-time of emission period for this puff into
! ---       TMTOTB(mxpuff) array, expressed as fraction of the timestep
! ---       This will be used in COMP when computing the sampling steps
! ---       used to advect the freshly emitted puff during remainder
! ---       of the timestep, and then reset.
! ---       Seconds from start of emissions period to end of timestep
            call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
            tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
! ---       Emission period index for this puff
            iemstep(np)=it
! ---       Set puff identity where IRLSNUM is the puff number released
! ---       from a source this time step, ISRCNUM is the source number
! ---       of type ISRCTYP from which the puff was released.
! ---       (Type =2 for point sources with VARIABLE emiss.)
            jj=newpt2(i)-npnewt+j
            irlsnum(np)=jj
            isrcnum(np)=i
            isrctyp(np)=itype
!
! ----------------------------------------------------------
! ---       Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---       Note that "oldest" end of slug is at the source, as is the
! ---       "newest end; oldest end moves away and grows during step,
! ---       while newest end remains fixed in size at the source.
!
            if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---          Set coordinates of oldest end of slug (met. grid units)
               xpe(np)=xpb(np)
               ype(np)=ypb(np)
               xtote(np)=xtotb(np)
               tmtote(np)=tmtotb(np)
! ---          Set sigmas for oldest end of slug
               sigye(np)=sigyb(np)
               sigze(np)=sigzb(np)
            endif
!
! ---------------------------------------------------------------------
! ---       Inject emissions into proper layer (above/below mixing ht.)
! ---------------------------------------------------------------------
            call inject(np,nspec,temit,mfact0,qcav,0.0,hmax,dpbl,fmix,&
                        istab)
! ------------------------------------------------------------------
! ---       Transfer tabulated arrays record to DA file using full
! ---       MXRISE dimension
! ------------------------------------------------------------------
            call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
150      continue
         endif
! --- End loop over emission periods within this timestep
100   continue
!
      return
      end
!----------------------------------------------------------------------
      subroutine lines1(nspec,dthr,xtmp1,ldbhr,mfact,metfm,np,&
                        newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230            LINES1
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               LINE SOURCES with constant emissions
!
! --- UPDATE
! --- V6.42_x1.1 - TNG-7.1.0  141230  (DGS)
!                                : Replace VEMFAC with QFACTOR subr.
!                                  for emissions scaling
!                                : Scale-factors for all sources in
!                                  one table array
!                                : Allow roughness adjustment (MROUGH)
!                                  of RURAL PG sigmas to be used with
!                                  CALMET gridded fields
! --- V6.402-V6.42_x1.1 140521   : Fix debug list-file output list
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
! --- V6.265-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.261-V6.265 090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
!                           (DGS): Apply minimum floor to the downwash
!                                  sigmas stored in SY0,SZ0 arrays
!                           (DGS): initialize variables written in debug
!                                  output that may not be computed in
!                                  loop over lines (thalf,xhalfkm,
!                                  syhalf,szhalf)
! --- V5.75-V6.261  080520  (DGS): Place rise tables in DA file
! --- V5.74-V5.75   050225  (DGS): Add DPBL arg to SETCSIG for TAULY
! --- V5.72-V5.74   040715  (DGS): add METFM=5 (AERMET)
! --- V5.7-V5.72    031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.4-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.0     980807  (DGS): use final rise CALMET/PROFILE winds 
!                                  to set release step
! --- V5.0-V5.0     980515  (DGS): ZITIBL array added
! --- V5.0-V5.0     980304  (DGS): drop any new puffs if emissions = 0 
! --- V4.0-V5.0     971107  (DGS): add variable emissions factor call
!                                  to function VEMFAC
!                   971107  (DGS): alter calling arg for SETLINE, used
!                                  for both LINES1 and LINES2
! --- V4.0-V5.0     971107  (DGS): ZPB,ZPE arrays added
!
! --- INPUTS:
!         NSPEC - real    - Number of species modeled
!          DTHR - real    - Length (seconds) of a basic time step
! XTMP1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!         LDBHR - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!         METFM - integer - Meteorological data format
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, WSCALM, PLX0
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MTIP, MHFTSZ, MSHEAR
!     Common block /GRID/ variables:
!           DGRID, IBCOMP, IECOMP, JBCOMP, JECOMP
!     |MQSCALE| variables:
!           NQSFTAB,IQSFTYPE(nqsftab),QSFTAB(mxqsf,nqsftab)
!     Common block /LN1/ variables:
!           NLINES,XL,HBL,WBL,WML,DXL,FPRIMEL,WSEP,FPTOT,FBPT,
!           XLBEGGRD(mxlines),YLBEGGRD(mxlines),XLENDGRD(mxlines),
!           YLENDGRD(mxlines),HSL(mxlines),BELEVL(mxlines),
!           XLBAR,YLBAR,ORIENTL,MXNSEG,QTL(mxspec,mxlines),NSLN1,
!           IDSFLN1(mxspec,mxlines),IXREFLN1(mxspln),
!           NLRISE
!     Common block /METHR/ variables:
!           TEMPSS(mxss,,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP
!     Common block /METHD/ variables:
!           I2DMET
!     Common block /PUFF/ variables:
!           NPUFFS
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXLINES, MXSPEC, MXRISE,
!           MXMETDOM, IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /LN1/ variables:
!           NEWLN1(mxline), NSEG(mxlines),
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!     Common block /SRCTAB/ variables:
!           NTR, XTR(mxrise), ZTR(mxrise)
!
! --- LINES1 called by:  INITPUF
! --- LINES1 calls:      RLSMET, ROLLDN, HEFTRAN, SIGTY, SIGTZ, SETLINE,
!                        SETCSIG, SWAP, INJECT, ZEROTAB,
!                        SRCTABOUT, GETCELL, QFACTOR
!----------------------------------------------------------------------
! --- Modules
      use mqscale
      include 'params.puf'
      include 'comparm.puf'
      include 'csigma.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methr.puf'
      include 'ln1.puf'
      include 'puff.puf'
      include 'slug.puf'
! frr(09/01)
      include 'methd.puf'
      include 'srctab.puf'
      real xtmp1(mxmetdom)
      real q(mxspec)
      logical ldbhr,problem,lcalm
! --- Set minimum allowed wind speed for building downwash (.GE.1)
      data wsdw0/1.0/
      data oneby2pi/0.1591549/,rt2pi/2.5066283/,rt2bypi/0.7978846/
      data zero/0.0/
! --- Control-file line sources are Type = 5
      itype=5
! ***
      if(ldbhr) then
         write(io6,*)
         write(io6,*) 'LINES1:  Processing 1 Group of ',nlines,' lines'
      endif
! ***
!
! -------------------------------------------------------------------
! --- Get met data for block of line sources
! -------------------------------------------------------------------
!
! --- Determine MET grid 1 (outermost) point closest to the CENTER of
! --- the array of line sources
      ixs=1.0+xlbar
      iys=1.0+ylbar
!
! --- Source off the computational grid -- write a FATAL message
      if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
         jecomp)then
         i=1
         write(io6,20)i,ixs,iys
20       format(/1x,'FATAL -- a line source with constant emissions ',&
         'is off the computational grid'/1x,'source no. (i) = ',i6,2x,&
         'nearest grid point (ixs,iys) = (',i5,',',i5,')')
         problem=.TRUE.
         return
      endif
! --- Now determine the met. grid point closest to the source
      call GETCELL('LINES1      ','GRIDIJ',1,xlbar,ylbar,zero,zero,&
                   ixs,iys,md)
! --- Initialize source tabulation arrays to zero
      call ZEROTAB
!
! --- Extract wind speed & other met. variables;
! --- and set CALM configuration if triggered
      call rlsmet(ldbhr,ixs,iys,md,hbl,mfact,&
                  mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                  ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
!
! frr (09/01) new calmet format (2D temp)
      if(i2dmet.EQ.1) then
         temp=temp2d(ixs,iys,md)
      elseif(i2dmet.EQ.0) then
         temp=tempss(issta,md)
      else
         write(*,*)'Subr. LINES1:  Invalid I2DMET = ',i2dmet
         stop
      endif
! --- Trap missing power-law exponent (substitute default)
      if(plexp.LT.-900.) then
! ---    Use default
         plx=plx0(istab)
      else
! ---    Use it
         plx=plexp
      endif
! --- Set logical to identify calm conditions
      if(ws.LT.wscalm) then
         lcalm=.TRUE.
      else
         lcalm=.FALSE.
      endif
!
! --- Calculate the virtual times and sigmas at Heffter transition
      uavg=amax1(.5,ws)
!
! --- Set selected data in /CSIGMA/ for sigma calls
      call SETCSIG(idopty,idoptz,iru,uavg,istab,el,sqrts,&
                  tsigv,tsigw,symin,szmin,hbl,dpbl,ixs,iys,md)
!
      if(mhftsz.EQ.0)then
         szh=syh
         call sigty(syh,zero,zero,dum,thfty,dhfty)
      else
         call heftran(1,hsl(1),symin,szmin,zero,zero,zero,zero)
      endif
!
! --------------------------------------------------------------------
! --- Calculate modified plume rise parameters for group, and tabulate
! --- rise from point of full buoyancy (XFB) to point of final rise
! --------------------------------------------------------------------
!
! --- Find angle between flow and orientation of line sources (theta)
      theta=flow-orientl
!
! --- Compute attributes of the line sources for this meteorology,
! --- and update rise tabulation
      ntr=nlrise
      call setline(mshear,theta,ws,istab,sqrts,plx,nlines,xl,&
                   hbl,wml,fprimel,wsep,fptot,fbpt,ntr,&
                   xle,xld,rzero,xtr,ztr)
!
! --- Set trig. factors for flow direction
      sinf=sin(flow)
      cosf=cos(flow)
!
! --- Find the along-flow coordinate that defines the line source
! --- element furthest upwind: (YY0GRD in met grid units)
! --- Rotate coord. system to place the yy-axis along the flow (line 1)
      yybeg=xlbeggrd(1)*sinf+ylbeggrd(1)*cosf
      yyend=xlendgrd(1)*sinf+ylendgrd(1)*cosf
      yy0grd=amin1(yybeg,yyend)
! --- Continue with other lines, if used
      do i=2,nlines
         yy=xlbeggrd(i)*sinf+ylbeggrd(i)*cosf
         yy0grd=amin1(yy0grd,yy)
         yy=xlendgrd(i)*sinf+ylendgrd(i)*cosf
         yy0grd=amin1(yy0grd,yy)
      enddo
! --- Use CALMET/PROFILE winds at final rise to set release/sampling
! --- data
      if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
         heff=hbl+ztr(ntr)
         call rlsmet(ldbhr,ixs,iys,md,heff,mfact,&
                     mfact0fr,ilayerfr,wsfr,flowfr,ivecfr,istab,dpbl,el,&
                     ustr,wstr,tsigvfr,tsigwfr,iru,issta,sqrtsfr,&
                     idoptyfr,idoptzfr)
         wsnew=AMAX1(ws,wsfr)
      else
         wsnew=ws
      endif
!
!******
      if(ldbhr)then
! --- 6.42_x1.1 140521
         write(io6,204) ilayer,ixs,iys,md,ws,istab,dpbl,issta,theta
204      format(5x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,2x,&
          'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'THETA: ',f5.1)
!
         write(io6,206) xtr(1),ztr(1),xle,xld,xtr(ntr),ztr(ntr)
206      format(5x,'XFB : ',f6.1,2x,'ZFB : ',f6.1,2x,'XLE : ',&
          f6.1,2x,'XLD : ',f6.1,2x,'XFIN0: ',f6.1,2x,'ZFIN0: ',f6.1)
!
      endif
!*****
!
! --------------------------------------------------
! --- Loop over lines
! --------------------------------------------------
!
      do 100 i=1,nlines
!
! --- Swap emission rate for source 'i' into 1D array and scale
      qtot=0.0
      do is=1,nspec
         q(is)=qtl(is,i)
! ---    Apply emissions scale-factor (conditional)
         if(nsln1.GT.0) then
! ---       Pointer to scale factor tables
            k=idsfln1(is,i)
            if(k.GT.0) then
               kk=ixrefln1(k)
               call QFACTOR(io6,iqsftype(kk),itype,ws,istab,temp,iqsf)
               q(is)=q(is)*qsftab(iqsf,kk)
            endif
         endif
         qtot=qtot+q(is)
      enddo
! --- Skip to next line if all species have zero mass in puff
      if(qtot.EQ.0.0) then
! ***
         if(ldbhr) then
            write(io6,*)
            write(io6,*) 'No emissions this step from Source ',i
            write(io6,*)
         endif
! ***
         goto 100
      endif
! --- Rotate coord. system to place the yy-axis along the flow
      xxbeg=xlbeggrd(i)*cosf-ylbeggrd(i)*sinf
      xxend=xlendgrd(i)*cosf-ylendgrd(i)*sinf
      yybeg=xlbeggrd(i)*sinf+ylbeggrd(i)*cosf
      yyend=xlendgrd(i)*sinf+ylendgrd(i)*cosf
! --- Find cross-wind and along-wind length of this line in meters
      xcrossm=abs(xxbeg-xxend)*dgrid
      yalongm=abs(yybeg-yyend)*dgrid
! --- Compute the number of puffs/slugs released this hour
! --- from this line source
      npnew=wsnew*xtmp1(md)+1
      npnew=max0(npnew,1)
      npnew=min0(npnew,mxnew)
      newln1(i)=npnew
!
! --- DT is the time (sec) over which each puff is emitted
      dt=dthr/float(npnew)
! ---------------------------------------------------------
! --- Compute number of segments needed for one line (NSEG)
! ---------------------------------------------------------
      if(mslug.EQ.0 .OR. lcalm) then
! ---    Puffs are released, so use all MXNSEG segments
         thalf=0.
         xhalfkm=0.
         syhalf=0.
         szhalf=0.
         nsegy=0
         nsegz=0
         nseg(i)=mxnseg
      else
! ---    Slugs are released
!
! ---    Calculate the virtual times and sigmas at Heffter transition
         if(mhftsz.EQ.0)then
            szh=syh
            call sigty(syh,zero,zero,dum,thfty,dhfty)
         else
            call heftran(1,hsl(i),syab1,szab1,zero,zero,zero,zero)
         endif
! ---    Set NSEGY as function of transport speed, sigma-y,
! ---    and cross-wind length of line (make it an ODD integer)
         thalf=0.5*dt
         xhalfkm=0.001*uavg*thalf
         call sigty(zero,xhalfkm,thalf,syhalf,dum,dum)
         nsegy=nint(xcrossm/(rt2pi*syhalf))
         iodd=mod(nsegy,2)
         if(iodd.EQ.0) nsegy=nsegy+1
! ---    Set NSEGZ as function of transport speed, sigma-z, release ht,
! ---    and along-wind change in rise (make it an ODD integer)
         call sigtz(zero,xhalfkm,thalf,hsl(i),szhalf,dum,dum)
         nsegz=nint(2.0*ztr(1)/szhalf)
         iodd=mod(nsegz,2)
         if(iodd.EQ.0) nsegz=nsegz+1
! ---    Take the larger of the two
         nseg(i)=MAX0(nsegz,nsegy)
! ---    Cap result at MXNSEG
         nseg(i)=MIN0(nseg(i),mxnseg)
      endif
! --- Line releases npnew clouds this hour, with nseg elements
! --- in each cloud
      newpuf=newpuf+npnew*nseg(i)
! --- If puff arrays are full, remove puffs that are off the grid
! --- and "roll down" arrays to make room for new puffs
! --- (this also applies to the DA file of tabulated arrays)
      if(npuffs+newpuf.gt.mxpuff) call rolldn(np)
! --- Check to see if roll-down was adequate
      if(npuffs+newpuf.gt.mxpuff)then
         write(io6,*) ' ERROR in subr. LINES1'
         write(io6,*)'Too many puffs on grid for array dimensions -- ',&
         'NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
         ' MXPUFF = ',mxpuff
         write(*,*)
         stop 'Halted in LINES1 -- see list file.'
      endif
!
! --------------------------------------------------------------------
! --- Calculate building downwash effects on initial plume sigmas
! --------------------------------------------------------------------
! --- Initialize downwash variables to zero (no building downwash)
      sz0dw=0.0
      sy0dw=0.0
      idw=0
      if(ws.GT.wsdw0 .AND. .NOT.lcalm) then
! ---    Set downwash flag to ACTIVE
         idw=1
! ---    Use BLP calculation of downwash sigmas
         sz0dw=rt2bypi*rzero
         sy0dw=0.5*sz0dw
      endif
!
! --- Set initial sigma y, sigma z for puff/slug, including effective
! --- spread due to length of line source element (add length effects
! --- and downwash effects in quadrature)
! --- Crosswind variance: 0.25*(line width)**2/(2pi)
      if(lcalm) then
         xlen2=(xcrossm**2+yalongm**2)/(nseg(i)**2)
      else
         xlen2=(xcrossm/nseg(i))**2
      endif
      sy2=0.25*oneby2pi*xlen2+sy0dw*sy0dw
      sysrc=amax1(symin,sqrt(sy2))
! --- Alongwind variance: 0.25*(line length * rise rate)**2/(2pi)
      if(lcalm) then
         sz2=0.0
         szsrc=szmin
      else
         sz2=0.25*oneby2pi*((yalongm/nseg(i))*&
                            ztr(1)/xtr(1))**2+sz0dw*sz0dw
         szsrc=amax1(szmin,sqrt(sz2))
      endif
!******
      if(ldbhr)then
         write(io6,*)
         write(io6,*) 'Line Source # ',i
         write(io6,*)
         write(io6,*) 'LINES1:  thalf,xhalfkm = ',thalf,xhalfkm
         write(io6,*) '          syhalf,nsegy = ',syhalf,nsegy
         write(io6,*) '          szhalf,nsegz = ',szhalf,nsegz
         write(io6,*) '                  nseg = ',nseg(i)
         write(io6,208) xcrossm,npnew,newpuf,dt,&
                        idw,rzero,sysrc,szsrc
208      format(5x,'XCROSSM: ',f6.1,2x,&
          'NPNEW: ',i4,2x,'NEWPUF: ',i5,2x,'DT: ',f7.2,2x/5x,&
          'IDW: ',i3,2x,'RZERO: ',f6.1,2x,'SYSRC: ',f6.1,2x,&
          'SZSRC: ',f6.1)
      endif
!*****
!
! -------------------------------------------------------------
! --- Loop over "point" sources (each segment) along line
! -------------------------------------------------------------
!
! --- Set up the distance increments along the line
      dxgrd=(xlendgrd(i)-xlbeggrd(i))/nseg(i)
      dxgrdby2=0.5*dxgrd
      dygrd=(ylendgrd(i)-ylbeggrd(i))/nseg(i)
      dygrdby2=0.5*dygrd
      do 90 ipt=1,nseg(i)
! ---  Set location in met grid units
       xptgrd=xlbeggrd(i)+(ipt-1)*dxgrd+dxgrdby2
       yptgrd=ylbeggrd(i)+(ipt-1)*dygrd+dygrdby2
! ---  Set the shift in distance (m) of this point from the upwind edge
       yyptgrd=xptgrd*sinf+yptgrd*cosf
       xshift=(yyptgrd-yy0grd)*dgrid
       if(xshift.LT.0.0) xshift=0.0
       if(xshift.GT.xtr(1)) xshift=xtr(1)
! ---  Set the final height and distance to final rise for this puff
       if(lcalm) then
          xshift=0.0
          xfrise=0.0
          heff=hsl(i)+ztr(ntr)
       else
          if(xtr(1).LE.xtr(ntr)) then
! ---        Case 1:  final rise reached beyond XFB
             heff=hsl(i)+ztr(ntr)-xshift*ztr(1)/xtr(1)
             xfrise=xtr(ntr)-xshift
          else
! ---        Case 2:  final rise reached before XFB
             size=1.0-xshift/xtr(1)
             heff=hsl(i)+ztr(ntr)*size
             xfrise=xtr(ntr)*size
          endif
       endif
!
!******
      if(ldbhr)then
         write(io6,*) 'Element #  ',ipt
         write(io6,210) hsl(i),heff,xfrise,xshift,xptgrd,yptgrd
210      format(5x,'HSL : ',f6.1,2x,'HEFF: ',f6.1,'XFRISE: ',f6.1,2x,&
          'XSHIFT: ',f6.1,2x,'XPTGRD: ',f8.4,2x,'YPTGRD: ',f8.4)
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnew
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call swap(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xptgrd
         ypb(np)=yptgrd
         zitibl(np)=-1.0
         elbase(np)=belevl(i)
         sigyb(np)=sysrc
         sigzb(np)=szsrc
! ---    Final plume rise results for puff
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xfrise
! ---    Intermediate results for calc. gradual rise
         fb(np)=fbpt
! ---    Set xbfin and zbfin to negatives to signal use of final
! ---    rise if calm
         if(lcalm) then
            xbfin(np)=-1.0
            zbfin(np)=-1.0
         else
            xbfin(np)=xtr(ntr)
            zbfin(np)=ztr(ntr)
         endif
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=hsl(i)
         temit0(np)=dt
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations (downwash - point sources)
         idw0(np)=idw
         hb0(np)=hsl(i)
         hw0(np)=xcrossm
         heff20(np)=hsl(i)
         zly0(np)=xld
         r0(np)=rzero
! ---    Additional data for line sources
         xshift0(np)=xshift
!         sy0(np)=sy0dw
!         sz0(np)=sz0dw
         sy0(np)=AMAX1(sy0dw,symin)
         sz0(np)=AMAX1(sz0dw,szmin)
! ---    Emission period index for this puff
         iemstep(np)=1
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from a source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =5 for const. line sources)
         irlsnum(np)=j
         isrcnum(np)=i
         isrctyp(np)=itype
!
! ---    !-NOTE 1-!
! ---    The puff number really refers to the number of emission
! ---    steps, which equals the puff number when ONE puff is released
! ---    from a source each emission step.  But many puffs are released
! ---    each step when simulating a line source (distributed along
! ---    the line).  Subsequent use is made of IRLSNUM in controlling
! ---    sampling functions, which depend on the emission steps, so we
! ---    explicitly place the emission-step index in the array.
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    !-NOTE 2-!
! ---    OLD slugs from each segment are modeled explicitly, but
! ---    NEW releases are modelled using the numerical area-source
! ---    method for the ENTIRE line.  Therefore, we add a marker to
! ---    all slugs after segment 1, so that these can be identified
! ---    and ignored when NEW.  The marker consists of adding 1000 to
! ---    ISRCNUM, which would indicate that the puff comes from
! ---    line-source ISRCNUM=1000+i.  A check is made in READCF which
! ---    stops execution if parameter (mxlines .GE. 1000) to insure
! ---    that no valid line-source ID exceeds 999.
!
         if(mslug.eq.1 .AND. .NOT.lcalm) then
!
! ---       Set PUFID marker
            if(ipt.GT.1) isrcnum(np)=isrcnum(np)+1000
!
! ---       Set coordinates of oldest end of slug (in met. grid units)
! ---       Note that "oldest" end of slug is at the source, as is the
! ---       "newest" end; oldest end moves away and grows during the
! ---       step, while newest end remains fixed in size at the source.
            xpe(np)=xpb(np)
            ype(np)=ypb(np)
            xtote(np)=xtotb(np)
            tmtote(np)=tmtotb(np)
! ---       Set sigmas for oldest end of slug
            sigye(np)=sigyb(np)
            sigze(np)=sigzb(np)
         endif
!
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
! ---    Set partial penetration variables to null values
         fmix=1.0
         hmax=dpbl
! ---    Mass emitted into each line segment needs to be divided by
! ---    nseg, which is the number of segments.  Accomplish that here
! ---    by dividing the timestep by nseg.
         dtseg=dt/nseg(i)
         call inject(np,nspec,dtseg,mfact0,q,heff,hmax,dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer tabulated arrays record to DA file using full
! ---    MXRISE dimension
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
90     continue
100   continue
      return
      end
!----------------------------------------------------------------------
      subroutine rlsmet(ldbhr,ixs,iys,mdom,htr,mfact,&
                        mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                        ustr,wstr,tsigv,tsigw,iru,issta,sqrts,&
                        idopty,idoptz)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            RLSMET
!                D. Strimaitis
!
! --- PURPOSE:  Obtain release height wind speed & other met. variables
!
! --- UPDATE
! --- V6.11-V6.3    100212  (DGS): Add nested CALMET grids
! --- V5.75-V6.11   060309  (DGS): Add overwater SVMIN,SWMIN
! --- V5.7-V5.75    050225  (DGS): Add UATZI for TURBSET's AERSWV call
! --- V5.4-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.0     981228  (DGS): restore flow units
! --- V5.0-V5.0     980807  (DGS): enforce time-growth sigmas if CALM
!                   980807  (DGS): use surface stability for all hts
!                                  when stable
! --- V5.0-V5.0     980722  (DGS): SVMIN & SWMIN by stability class
! --- V5.0-V5.0     980722  (DGS): remove FLOW=0 assignment for CALMS
!                                  (use WD from WINDSET)
! --- V4.0-V5.0     971107  (DGS): use release ht = MAX(htr,z0)
!
! --- INPUTS:
!         LDBHR - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!       IXS,IYS - integer - Met grid cell index for puff
!          MDOM - integer - Met grid domain index
!           HTR - real    - Release height (m)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!
!     Common block /COMPARM/ variables:
!           XMINZI, XMAXZI, SVMIN(6,2), SWMIN(6,2), WSCALM
!     Common block /DISPDAT/ variables:
!           IURB1, IURB2, JSUP
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MDISP, MDISP2
!     Common block /GRID/ variables:
!           ZFACE(mxnzp1), NZP1, ZGPT(mxnz)
!     Common block /METHR/ variables:
!           UMET(mxnx,mxny,mxnz,mxmetdom),VMET(mxnx,mxny,mxnz,mxmetdom),
!           IPGT(mxnx,mxny,mxmetdom),HTMIX(mxnx,mxny,mxmetdom),
!           XMONIN(mxnx,mxny,mxmetdom), USTAR(mxnx,mxny,mxmetdom),
!           WSTAR(mxnx,mxny,mxmetdom), PTG(2), PLEXP,
!           TEMPSS(mxss,mxmetdom),TEMP2D(mxnx,mxny,mxmetdom)
!     Common block /METHD/ variables:
!           NEARS(mxnx,mxny,mxmetdom), ILANDU(mxnx,mxny,mxmetdom),
!           Z0(mxnx,mxny,mxmetdom),
!           NSSTA(mxmetdom), LCALGRD, I2DMET
!     Parameters:
!           MXNX, MXNY, MXNZ, MXMETDOM, MXNZP1, MXSS, IO6
!
! --- OUTPUT:
!        MFACT0 - integer - Current stepping index to identify slugs
!                           vs. puffs (puff used for calms)
!        ILAYER - integer - Met layer index for release height
!            WS - real    - Wind speed (m/s)
!          FLOW - real    - Flow direction (radians CW from N)
!          IVEC - integer - Vector flow direction (deg CW from N)
!         ISTAB - integer - Stability class (1-6)
!          DPBL - real    - Mixing height (m)
!            EL - real    - Monin-Obukhov length (m)
!          USTR - real    - Friction velocity (m/s)
!          WSTR - real    - Convective velocity scale (m/s)
!         TSIGV - real    - Lateral turbulence velocity (m/s)
!         TSIGW - real    - Vertical turbulence velocity (m/s)
!           IRU - integer - Rural/Urban indicator (rural=0, urban=1)
!         ISSTA - integer - Index of nearest surface met station
!         SQRTS - real    - Root of stability parameter (1/s);
!                           (same as Brunt-Vaisala frequency)
!        IDOPTY - integer - Current dispersion option for sigma-y
!        IDOPTZ - integer - Current dispersion option for sigma-z
!
! --- RLSMET called by:  AREAS1, AREAS2, LINES1, POINTS1, POINTS2,
!                        VOLS 
! --- RLSMET calls:      ZFIND, WINDSET, TURBSET, PTLAPS
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      include 'comparm.puf'
      include 'dispdat.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
!
      logical ldbhr
!
      data g/9.80665/
! --- Set minimum allowed potential temperature gradient PTGRAD0,
! --- and the layer above stack-top over which it is calculated DELZ
      data ptgrad0/.005/, delz/100./
! --- Set transport wind speed for "calm"
      data ucalm/0.001/
!
! --- Select properties for this cell
      istab=MIN0(ipgt(ixs,iys,mdom),6)
      dpbl=AMAX1(htmix(ixs,iys,mdom),xminzi)
      dpbl=AMIN1(dpbl,xmaxzi)
      el=xmonin(ixs,iys,mdom)
      ustr=ustar(ixs,iys,mdom)
      wstr=wstar(ixs,iys,mdom)
      z0m=z0(ixs,iys,mdom)
! --- Set land (1) water (2) index for SVMIN,SWMIN
      ilw=1
      if(ilandu(ixs,iys,mdom).GE.iwat1 .AND.&
         ilandu(ixs,iys,mdom).LE.iwat2) ilw=2
!
! --- Find the layer containing the release height (m)
      htrel=AMAX1(htr,z0m)
      call ZFIND(htrel,zface,nzp1,ilayer)
! --- Extract wind speed (m/s) and direction (deg)
      call WINDSET(htrel,ilayer,ixs,iys,mdom,z0m,el,dpbl,istab,ws,wd)
      flow=wd+180.
      if(flow.LT.0.) then
         flow=flow+360.
      elseif(flow.GE.360.) then
         flow=flow-360.
      endif
      ivec=NINT(flow)
!
! --- For puffs above mixed layer, use stability class "JSUP" to
! --- calculate SIGMA Y and SIGMA Z growth (if JSUP=0, use
! --- mixed-layer stability class)
      jdstab=istab
      if(istab.LT.5 .and. htrel.GT.dpbl .and. jsup.ne.0)jdstab=jsup
!
! --- Set turbulence velocities and activate dispersion options
      if(mdisp.EQ.1 .OR. mdisp.EQ.2 .OR. mdisp.EQ.5) then
! ---    Extract wind speed (m/s) at Zi
         call ZFIND(dpbl,zface,nzp1,ilayerzi)
         call WINDSET(dpbl,ilayerzi,ixs,iys,mdom,z0m,el,dpbl,istab,&
                         uatzi,wdzi)
         call TURBSET(ldbhr,ustr,el,wstr,jdstab,dpbl,z0m,htrel,&
                      uatzi,ws,wd,ixs,iys,mdom,ilw,&
                      tsigv,tsigw,idopty,idoptz)
      else
         idopty=mdisp
         idoptz=mdisp
         tsigw=swmin(jdstab,ilw)
         tsigv=svmin(jdstab,ilw)
      endif
!
! --- Extract IRU value (rural=0, urban=1) for source
      iru=0
      if(ilandu(ixs,iys,mdom) .GE. iurb1 .AND.&
         ilandu(ixs,iys,mdom) .LE. iurb2) iru=1
!
! --- Identify surface met station nearest source
      issta=NEARS(ixs,iys,mdom)
      if(issta.gt.nssta(mdom))then
         write(io6,*) ' ERROR in subr. RLSMET'
         write(io6,*)'ISSTA > NSSTA -- ISSTA = ',issta,' NSSTA = ',&
                      nssta(mdom)
         write(*,*)
         stop 'Halted in RLSMET -- see list file.'
      endif
!
! frr (09/01) new calmet format (2D temp)
      if(i2dmet.EQ.1) then
         temp=temp2d(ixs,iys,mdom)
      elseif(i2dmet.EQ.0) then
         temp=tempss(issta,mdom)
      else
         write(*,*)'Subr. RLSMET:  Invalid I2DMET = ',i2dmet
         stop
      endif
! --- Compute stratification parameter = Brunt-Vaisala freq.
      if(LCALGRD) then
! ---    Use temperature (K) gradient between stack-top and
! ---    DELZ above stack-top
         call PTLAPS(ixs,iys,mdom,htr,ptgrad0,delz,ptgrad,t0)
         sqrts=SQRT(g*ptgrad/t0)
      else
! ---    Use default potential temperature lapse rate with the
! ---    surface temperature from the nearest station
         if(istab.LE.4) then
            ptgrad=0.0
         elseif(istab.EQ.5) then
            ptgrad=AMAX1(ptg(1),ptgrad0)
         else
            ptgrad=AMAX1(ptg(2),ptgrad0)
         endif
         sqrts=SQRT(g*ptgrad/temp)
      endif
!
! --- If CALM is detected, make sure PUFF ID stepping index is 0 to 
! --- use PUFFS
      mfact0=mfact
      if(ws.LT.wscalm) then
         ws=AMAX1(ws,ucalm)
         mfact0=0
! ---    Reset dispersion option ("calms" use time-based sigmas)
         idopty=1
         idoptz=1
      endif
! --- Convert flow from degrees to radians
      flow=flow*.0174533
      return
      end
!----------------------------------------------------------------------
      subroutine zfind(z,zface,nzp1,ilayer)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 941215             ZFIND
!                J. Scire, SRC
!
! --- PURPOSE:  Find the vertical layer containing height "Z"
!
! --- INPUTS:
!             Z - real    - Height (m) of interest
!   ZFACE(nzp1) - real    - Array of face heights (m)
!          NZP1 - integer - Number of vertical cell faces
!                           (no. layers + 1)
!
! --- OUTPUT:
!        ILAYER - integer - Vertical containing height "Z"
!                           (ILAYER=1  returned if Z < face ht. #1)
!                           (ILAYER=NZ returned if Z > face ht. #NZP1)
!
! --- ZFIND called by:  RLSMET, ADVWND
! --- ZFIND calls:      none
!----------------------------------------------------------------------
!
      real zface(nzp1)
!
! --- Find first face height above height "Z"
      do 10 i=2,nzp1
      if(z.lt.zface(i))then
         ilayer=i-1
         go to 12
      endif
10    continue
!
! --- "Z" must be above top face
      ilayer=nzp1-1
!
12    continue
      return
      end
!----------------------------------------------------------------------
      subroutine ptlaps(i,j,md,htbase,dptmin,delz,dtheta,tht)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            PTLAPS
! ---            J. Scire, D. Strimaitis
!
! --- PURPOSE:  Calculate the potential temperature lapse rate
!               (deg. K/m) in a layer "DELZ" meters deep above the
!               a specified height, using either the temperature
!               data from the MET file, or the PROFILE.DAT file
!
! --- UPDATE
! --- V6.114-V6.3   100212  (DGS): add nested CALMET grids
! --- V5.74-V6.114  060725  (DGS): fix case where upper height exceeds
!                                  middle of top layer (ku must be nz)
! --- V5.4-V5.74    040715  (DGS): add METFM=5 (AERMET)
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V4.0-V5.0     971107  (DGS): check PROBLEM returned by XTPRF
!
! --- INPUTS:
!                  I,J - integer       - Grid cell indexes
!                   MD - integer       - Met grid domain index
!               HTBASE - real          - Height (m) of bottom of layer
!               DPTMIN - real          - Minimum potential temperature
!                                        lapse rate (deg. K/m)
!                 DELZ - real          - Depth (m) of layer through
!                                        which potential temp. lapse
!                                        rate is computed
!    Common block /COMPARM/ variables:
!         WSCALM, XMINZI, XMAXZI
!     Common Block /GEN/ variables:
!         METFM
!     Common Block /GRID/ variables:
!         NZ, ZGPT(mxnz)
!    Common block /METHR/ variables:
!         TMET(mxnx,mxny,mxnz,mxmetdom)
!         IPGT(mxnx,mxny,mxmetdom), HTMIX(mxnx,mxny,mxmetdom),
!         XMONIN(mxnx,mxny,mxmetdom)
!         PTG(2), TPRF(mxprfz), ZPRF(mxprfz), NZPRF
!    Parameters:
!         MXNX, MXNY, MXMETDOM, MXNZ, MXPRFZ, IO6
!
! --- OUTPUT:
!               DTHETA - real          - Potential temperature lapse rate
!                                        (deg. K) in "DELZ" meter layer
!                  THT - real          - Temperature at bottom of layer
!
! --- PTLAPS called by:  RLSMET, POINTS1, POINTS2, LINES1
! --- PTLAPS calls:      XTPRF
!----------------------------------------------------------------------
!
! --- include parameters
      include 'params.puf'
      include 'comparm.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'methr.puf'
!
      logical problem
!
      if(metfm.EQ.1) then
! ---    MET layers
! ---    Find level just below htbase
         kl=nz-1
         do 10 k=2,nz
            if(zgpt(k).lt.htbase) go to 10
            kl=k-1
            go to 12
10       continue
12       continue
!
! ---    Find level just above htbase+delz
! ---    Initialize to NZ
         ku=nz
         htpdz=htbase+delz
         klp1=kl+1
         do 15 k=klp1,nz
            if(zgpt(k).lt.htpdz) go to 15
            ku=k
            go to 16
15       continue
16       continue
!
! ---    If two adjacent levels were found, just take dt/dz between them
         if((ku-kl).eq.1) then
            dtheta=.0098+(tmet(i,j,ku,md)-tmet(i,j,kl,md))/&
                   (zgpt(ku)-zgpt(kl))
            tht=tmet(i,j,kl,md)
         else
! ---       Obtain temperatures (deg. K) at htbase & htbase + delz
! ---       by interpolation
            klp1=kl+1
            tht=tmet(i,j,klp1,md)-(tmet(i,j,klp1,md)-tmet(i,j,kl,md))*&
                             (zgpt(klp1)-htbase)/(zgpt(klp1)-zgpt(kl))
            kum1=ku-1
            thtp=tmet(i,j,ku,md)-(tmet(i,j,ku,md)-tmet(i,j,kum1,md))*&
                             (zgpt(ku)-htpdz)/(zgpt(ku)-zgpt(kum1))
!
! ---       Compute potential temperature lapse rate (deg. K/m)
            dtheta=(thtp-tht)/delz+0.0098
         endif
      elseif(metfm.EQ.4 .OR. metfm.EQ.5) then
! ---    PROFILE.DAT data
         problem=.FALSE.
! ---    Set current surface layer properties
         istab=ipgt(i,j,md)
         istab=MIN0(istab,6)
         dpbl=AMAX1(htmix(i,j,md),xminzi)
         dpbl=AMIN1(dpbl,xmaxzi)
         el=xmonin(i,j,md)
! ---    Do not need z0 for temperature (set to 0.1 m)
         z0m=0.1
! ---    Extract temperatures at top and bottom of layer
         call XTPRF(nzprf,tprf,zprf,htbase,'tmp',z0m,el,&
                    dpbl,istab,ptg,tht,problem)
         htop=htbase+delz
         call XTPRF(nzprf,tprf,zprf,htop,'tmp',z0m,el,&
                    dpbl,istab,ptg,thtp,problem)
! ---    Results are invalid if XTPRF reported PROBLEM=TRUE
         if(PROBLEM) then
            write(io6,*) 'PTLAPS:  FATAL ERROR reported when ',&
                         'extracting temp from PROFILE ---'
            write(io6,*) 'There are no valid data'
            write(*,*)
            stop 'Halted in PTLAPS -- see list file.'
         endif
!
! ---    Compute potential temperature lapse rate (deg. K/m)
         dtheta=(thtp-tht)/delz+0.0098
      endif
!
! --- dptmin is minimum stable pot. temp. lapse rate
      dtheta=amax1(dtheta,dptmin)
!
      return
      end
!----------------------------------------------------------------------
      subroutine inject(np,nspec,dt,mfact0,q,heff,hmax,dpbl,fmix,istab)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 980615            INJECT
!                D. Strimaitis,  SRC
!
! --- PURPOSE:   Inject emissions into proper layer (above/below
!                mixing ht.)
!
! --- UPDATE
! --- V5.0-V5.0
!          950715 - 980615  (DGS): Use 2-layer puff codes for partial
!                                  penetration
!
! --- INPUTS:
!            NP - integer - Index of puff/slug
!         NSPEC - real    - Number of species modeled
!            DT - real    - Time over which puff is emitted (s)
!        MFACT0 - integer - Stepping index to identify slugs vs puffs
!             Q - real ar.- Emission rate (g/s) for period DT
!          HEFF - real    - Effective puff ht. at final rise (m)
!          HMAX - real    - Top of upper layer (partial penetration)
!          DPBL - real    - Current mixing height (m)
!          FMIX - real    - Fraction of mass in mixed layer
!         ISTAB - integer - Stability class in surface layer
!
!     Common block /FLAGS/ variables:
!           MGAUSS
!     Common block /PUFF/ variables:
!           NPUFFS
!     Parameters:
!           MXSPEC, MXPUFF
!
! --- OUTPUT:
!
!     Common block /PUFF/ variables:
!           IPUFFCD(mxpuff),ZIMAX(mxpuff),ZIOLD(mxpuff),
!           QM(mxspec,mxpuff),QU(mxspec,mxpuff)
!
! --- INJECT called by:  AREAS1, AEREAS2, VOLS, POINTS1,
!                        POINTS2, LINES1
! --- INJECT calls:      none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'flags.puf'
      include 'puff.puf'
!
      real q(mxspec)
! --- Set unlimited mixing height (m)
      data ziunlm/1.0e04/
!
      if(fmix.NE.1.0 .and. fmix.NE.0.0) then
! ---    Partial penetration of inversion was calculated
! ------------------------------------------------------
         zimax(np)=hmax
         ziold(np)=dpbl
         do ispec=1,nspec
            qtotal=q(ispec)*dt
            qm(ispec,np)=qtotal*fmix
            qu(ispec,np)=qtotal-qm(ispec,np)
         enddo
         if(mgauss.EQ.1)then
! ---       Gaussian puff or slug within mixed layer
            ipufcd(np)=5+mfact0
         else
! ---       Uniform puff or slug within mixed layer
            ipufcd(np)=6+mfact0
         endif
!
      elseif(heff.LE.dpbl)then
! ---    Within surface layer
! ---------------------------
         do ispec=1,nspec
            qm(ispec,np)=q(ispec)*dt
            qu(ispec,np)=0.0
         enddo
         if(mgauss.EQ.1)then
! ---       Gaussian puff or slug
            ipufcd(np)=1+mfact0
            if(istab.GE.5) then
! ---          Stable regime -- set up for unlimited mixing
               zimax(np)=ziunlm
               ziold(np)=ziunlm
            else
! ---          Non-stable regime -- use current mixing height
               zimax(np)=dpbl
               ziold(np)=dpbl
            endif
         else
! ---       Uniform puff or slug within mixed layer
            ipufcd(np)=2+mfact0
            zimax(np)=dpbl
            ziold(np)=dpbl
         endif
!
      else
! ---    Above surface layer
! ---------------------------
         if(mgauss.EQ.1)then
! ---       Gaussian puff or slug above mixed layer
            if(istab.GE.5) then
! ---          Stable regime -- treat as unlimited mixed layer
               ipufcd(np)=1+mfact0
               zimax(np)=ziunlm
               ziold(np)=ziunlm
               do ispec=1,nspec
                  qm(ispec,np)=q(ispec)*dt
                  qu(ispec,np)=0.0
               enddo
            else
! ---          Non-stable regime -- place mass above mixed layer
               ipufcd(np)=3+mfact0
               zimax(np)=dpbl
               ziold(np)=dpbl
               do ispec=1,nspec
                  qm(ispec,np)=0.0
                  qu(ispec,np)=q(ispec)*dt
               enddo
            endif
         else
! ---       Uniform puff or slug above mixed layer
            ipufcd(np)=4+mfact0
            zimax(np)=dpbl
            ziold(np)=dpbl
            do ispec=1,nspec
               qm(ispec,np)=0.0
               qu(ispec,np)=q(ispec)*dt
            enddo
         endif
!
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine dwsigs(tbd,hw,hb,mhftsz,ws,hs,heff,zplat,xdown,&
                        sigy,sigz)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050225            DWSIGS
! ---            J. Scire, D. Strimaitis, SRC
!
! --- PURPOSE:  Compute the dispersion coefficients, sigma y and
!               sigma z due to building downwash effects. (Values
!               returned are the max. obtained with or without
!               building downwash)
!
! --- UPDATE
! --- V5.0-V5.75    050225  (DGS): Add platform ht adjustment
! --- V4.0-V5.0     971107  (DGS): Return "zero" sigmas for distances
!                                  within "3Hb" of source
!
! --- INPUTS:
!           TBD - real     - Variable determining switch-over pt. from
!                           Schulman-Scire scheme to Huber-Snyder
!                           scheme (SS used for Hs < Hb + TBD * HL,
!                           TBD < 0   ==> always use Huber-Synder
!                           TBD = 1.5 ==> always use Schulman-Scire
!                           TBD = 0.5 ==> ISC switch-point
!            HW - real     - Wind-direction specific building width (m)
!                            (for current wind)
!            HB - real     - Wind-direction specific building height (m)
!                            (for current wind)
!        MHFTSZ - integer  - Use Heffter sigma-z (0:NO  1:YES)
!            WS - real     - Stack height wind speed (m/s)
!            HS - real     - Stack height (m)
!          HEFF - real     - Effective stack ht. (m) accounting for
!                            momentum rise alone at a downwind distance
!                            of 2. building heights
!         ZPLAT - real     - Platform ht (m) (non-zero for structures
!                            that are elevated above the surface)
!         XDOWN - real     - Downwind distance (m) of receptor
!
! --- OUTPUT:
!          SIGY - real     - Horizontal dispersion coefficient (m)
!          SIGZ - real     - Vertical dispersion coefficient (m)
!
! --- DWSIGS called by:  PUFRECS, SLGRECS, PLGRECS, POINTS1, POINTS2
! --- DWSIGS calls:      HEFTRAN, SIGTY, SIGTZ
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'csigma.puf'
!
      sigy=0.0
      sigz=0.0
!
      hl=AMIN1(hw,hb)
      hl3=3.*hl
      hl10=10.*hl
!
! --- Return zero values for the sigmas at distances < "3Hb"
! --- Allow for precision uncertainty
      if(xdown.LT.0.99*hl3) return
! --- Valid platform height?
      if(zplat.LT.0.) stop 'DWSIGS: Platform height is negative'
! --- Adjusted stack height above platform
      hsplat=AMAX1((hs-zplat),0.0)
! --- Adjusted stack height above platform
      heplat=AMAX1((heff-zplat),0.0)
! --- Calculate the virtual times and sigmas at Heffter transition
      if(mhftsz.EQ.0)then
         szh=syh
         call sigty(syh,0.0,0.0,dum,thfty,dhfty)
      else
         call heftran(1,hs,sigy,sigz,0.0,0.0,0.0,0.0)
      endif
!
! --- Compute the downwind distance to use in the downwash eqns.
      if(xdown.le.hl3)then
         xarg=hl3
      else if(xdown.ge.hl10)then
         xarg=hl10
      else
         xarg=xdown
      endif
! --- Convert to time and distance used in dispersion formulas
      targ=xarg/ws
      xargkm=0.001*xarg
!
! --- Get "ambient" sigmas
!
      call sigty(0.0,xargkm,targ,syamb,virty,virdy)
      call sigtz(0.0,xargkm,targ,hs,szamb,virtz,virdz)
!
! --- Now get "downwash" sigmas
!
! ---------------------------------------
! --- Huber-Synder Calculations ---------
! ---------------------------------------
      xtemp=0.067*(xarg-hl3)
      sigz=0.7*hl+xtemp
!
      if(hw.ge.hb)then
! ---    Squat building
! ---    Determine if sigma y enhancement occurs
         if(heplat.le.1.2*hb)then
            if(hw.lt.5.*hb)then
               sigy=0.35*hw+xtemp
            else
               sigy=0.35*hb+xtemp
! ---          (Note: "lower bound" ISC2 option is not available)
            endif
         else
            sigy=0.0
         endif
      else
!
! ---    Tall building
! ---    Determine if sigma y enhancement occurs
         if(heplat.le.1.2*hb)then
            sigy=0.35*hw+xtemp
         else
            sigy=0.0
         endif
      endif
!
! -----------------------------------------
! --- Schulman-Scire Calculations ---------
! -----------------------------------------
      if(tbd.ge.0.0 .AND. hsplat.le.hb+tbd*hl)then
!
! ---    Determine linear scaling function for sigz
         if(heplat.le.hb)then
            a=1.0
         else if(heplat.le.hb+2.*hl)then
            a=(hb-heplat)/(2.*hl)+1.0
         else
            a=0.0
         endif
!
! ---    Scale the vertical dispersion coefficient
         sigz=a*sigz
      endif
!
! --- Select the greater of the ambient and downwash sigmas
      sigy=amax1(syamb,sigy)
      sigz=amax1(szamb,sigz)
      return
      end
!----------------------------------------------------------------------
      subroutine setline(mshear,theta,wsr,istab,sqrts,plawx,&
                         nlines,xl,hbl,wml,fprimel,wsep,fptot,fbpt,&
                         nlrise,xle,xld,r0,xrise,zrise)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090612           SETLINE
! ---            D. Strimaitis
!
! --- PURPOSE:  Perform initial processing for buoyant line sources
!               for current meteorology, and compute rise table
!
! --- UPDATE
! --- V5.0-V6.265   090612  (DGS): add check for invalid power-law exp
! --- V5.0-V5.0     981228  (DGS): add line width term to XFB for single
!                                  line and use BLP code for effective
!                                  width for parallel winds
! --- V4.0-V5.0     971107  (DGS): add all io to argument list to allow
!                                  use with variable line sources as
!                                  well as constant lines
!
! --- INPUTS:
!        MSHEAR - integer      - Flag for including wind shear in rise
!                                calcs. (0=NO shear;  1=shear)
!         THETA - real         - Angle between wind and axis of
!                                line source (radians)
!           WSR - real         - Wind speed at release height (m/s)
!         ISTAB - integer      - Stability class (PG)
!         SQRTS - real         - Square root of stability parameter
!                                  (sqrt(s)=(g*(dtheta/dz)/tair)**0.5)
!         PLAWX - real         - Wind speed profile power law exponent
!        NLINES - integer      - Number of line sources
!            XL - real         - Average line source length (m)
!           HBL - real         - Average line source height (m)
!           WML - real         - Average line source width (m)
!       FPRIMEL - real         - Average buoyancy parameter/line
!                                in units of (m**4/s**3)
!          WSEP - real         - Average separation between lines
!         FPTOT - real         - Total buoyancy parameter
!          FBPT - real         - "Point Source" buoyancy parameter
!        NLRISE - integer      - Number of points used to tabulate
!                                plume rise from block of lines
!
! --- OUTPUT:
!           XLE - real     - Cross-wind effective line length (m)
!           XLD - real     - Effective downwash length (m)
!            R0 - real     - Initial dilution radius (m)
! XRISE(mxrise) - real     - Tabulated distances (m) from source
! ZRISE(mxrise) - real     - Tabulated rise heights (m) above source
!
! --- SETLINE called by:  LINES1, LINES2
! --- SETLINE calls:      ROOT3
!----------------------------------------------------------------------
!
      include 'params.puf'
      real rt3(3)
      real xrise(mxrise),zrise(mxrise)
! --- (beta=0.6)
      data beta/0.6/, beta2/0.36/, betai/1.6666667/, pibeta/1.8849556/
      data pi/3.1415927/, twopi/6.2831853/
      data half/0.5/, third/0.3333333/
! --- Set a minimum wind speed to use for calculating final rise (m/s)
      data wsrise/1.0/
! --- Trap invalid power-law exponent
      if(plawx.LE.0.0) then
         write(io6,*)
         write(io6,*)'SETLINE:  Invalid power-law exponent found'
         write(io6,*)'          PLAWX = ',plawx
         stop 'Halted in SETLINE --- see list file'
      endif
! --- Calculate sine & cosine, taking results in first quadrant
      cost=abs(cos(theta))
      sint=abs(sin(theta))
      ws=amax1(wsr,wsrise)
      ws3=ws*ws*ws
! ---------------------------------------------------------------------
! --- Get the downwind distance to full buoyancy (XFB) and the
! --- effective line length (XLE) for current meteorology
! ---------------------------------------------------------------------
! --- Set xfb
      xfb=xl*cost+(nlines-1)*wsep*sint
      if(nlines.EQ.1) xfb=xfb+wml*sint
      xfb2=xfb*xfb
! --- Set virtual line length for winds parallel to line sources (wmlv)
      if(nlines.EQ.1) then
         wmlv=wml
      else
! ---    Height at which plumes from adjacent lines interact (hi)
         hi=half*wsep*betai
! ---    Distance at which adjacent plumes interact (xi)
! ---    First, assume that xi < xl:
         xibyxl3=ws3*(hi/xl)**3*((twopi*beta2*xl/fprimel)*&
                                 (1.+3.*wml/(pibeta*hi)))
         if(xibyxl3.GT.1.) then
            xi=half*xl*(1.+third*SQRT(12.*xibyxl3-3.))
            wmlv=fptot*(third*xl**2+xi*(xi-xl))/(2.*beta*ws3*hi**2)-&
                        third*pibeta*hi
         else
            wmlv=fptot*(third*xl**2*xibyxl3)/(2.*beta*ws3*hi**2)-&
                        third*pibeta*hi
         endif
      endif
! --- Set effective line length for current wind direction
      xle=xl*sint+wmlv*cost
! ---------------------------------------------------------------------
! --- Effective building downwash length (XLD) and initial radius (R0)
! ---------------------------------------------------------------------
      xld=xle*sint
      r0=amin1(hbl,xld)
! ---------------------------------------------------------------------
! --- Effective wind speed if shear effect on rise is included
! ---------------------------------------------------------------------
! --- Note: do not apply shear to case where wind speed is less than
! ---       the minimum speed "wsrise"
      if(mshear.EQ.1 .AND. wsr.GE.wsrise) then
         if(istab.LE.4) then
! ---       "Neutral"
            eps=2.0+3.0*plawx
            epsi=1.0/eps
! ---       Get distance to final rise (no shear)
            if(fbpt.LE.55.) then
               xfin0=3.5*14*fbpt**.625
            else
               xfin0=3.5*34.49*fbpt**.4
            endif
! ---       Get shear-modified final rise
            zeps=(0.25*(eps*xfin0)**2)*(fptot*hbl**(3.0*plawx))/&
                  ((2.0+plawx)*beta*xl*ws3)
            zshear=zeps**epsi
! ---       Get effective wind speed
            ws3=(xfin0/zshear)**2*fptot/(2.0*beta*xl)
            ws=ws3**third
! ---       Effective speed must be no less than wsr (speed at release)
            if(ws.LT.wsr) then
               ws=wsr
               ws3=ws*ws*ws
            endif
         else
! ---       "Stable"
            twopp=2.0+plawx
            twoppi=1.0/twopp
            s=sqrts**2
! ---       Get shear-modified final rise
            zshear=( twopp*fptot*hbl**plawx/(beta*xl*ws*s) )**twoppi
! ---       Get effective wind speed
            ws=2.0*fptot/(beta*xl*s*zshear*zshear)
! ---       Effective speed must be no less than wsr (speed at release)
            if(ws.LT.wsr) then
               ws=wsr
            else
               ws3=ws*ws*ws
            endif
         endif
      endif
! ---------------------------------------------------------------------
! --- Final rise & distance to final rise from the most upwind point
! --- "zfin0,xfin0"
! ---------------------------------------------------------------------
      aa3=1.0
      aa2=(3.*betai)*(xle/pi+r0)
      aa1=(3.*r0/beta2)*(2.*xld/pi+r0)
      aa00=-0.5*fbpt/(beta2*ws3)
      if(istab.LE.4) then
! ---    "Neutral"
         if(fbpt.LE.55.) then
            xfin0=3.5*14*fbpt**.625
         else
            xfin0=3.5*34.49*fbpt**.4
         endif
         if(xfin0.LT.xfb) then
            aa0=aa00*xfin0**3/xfb
         else
            aa0=aa00*(xfb2+3.*xfin0*(xfin0-xfb))
         endif
         call root3(aa3,aa2,aa1,aa0,nr,rt3)
! ---    Pick largest root
         zfin0=rt3(1)
         do i=2,nr
            zfin0=amax1(zfin0,rt3(i))
         enddo
      else
! ---    "Stable"
         s=sqrts**2
         aa0=-6.*fbpt/(beta2*ws*s)
         call root3(aa3,aa2,aa1,aa0,nr,rt3)
! ---    Pick the largest real root
         zfin0=rt3(1)
         do i=2,nr
            zfin0=amax1(zfin0,rt3(i))
         enddo
! ---    Now use neutral transient rise eqn. to get distance to zfin0
! ---    First, assume that xfin0 < xfb:
         ws2bys=ws*ws/s
         xfin0=(12.0*ws2bys*xfb)**third
         if(xfin0.GT.xfb) then
! ---       Must be beyond xfb
            xfin0=0.5*(xfb+sqrt(16.0*ws2bys-xfb2*third))
         endif
! ---    Distance should not be greater than result without downwash
         xfin=ws*pi/sqrts
         xfin0=amin1(xfin0,xfin)
      endif
! ---------------------------------------------------------------------
! --- Rise at XFB from the most upwind point (ZFB)
! ---------------------------------------------------------------------
      aa0=aa00*xfb2
      call root3(aa3,aa2,aa1,aa0,nr,rt3)
! --- Pick largest root
      zfb=rt3(1)
      do i=2,nr
         zfb=amax1(zfb,rt3(i))
      enddo
      zfb=amin1(zfb,zfin0)
!----------------------------------------------------------------------
! --- Fill in rise table between xfb and xfin0, inclusive
!----------------------------------------------------------------------
! --- Fill in new information for current met.
      xrise(1)=xfb
      zrise(1)=zfb
      xrise(nlrise)=xfin0
      zrise(nlrise)=zfin0
      dx=(xfin0-xfb)/(nlrise-1)
      if(xfb.GE.xfin0) then
! ---    Final rise reached before XFB -- just plug final rise values
         do ir=2,nlrise-1
            xrise(ir)=xrise(ir-1)+dx
            zrise(ir)=zfb
         enddo
      else
! ---    Final rise reached beyond XFB -- compute gradual rise values
         do ir=2,nlrise-1
            xibl=xrise(ir-1)+dx
            aa0=aa00*(xfb2+3.0*(xibl*xibl-xfb*xibl))
            call root3(aa3,aa2,aa1,aa0,nr,rt3)
! ---       Pick largest root
            zibl=rt3(1)
            do i=2,nr
               zibl=amax1(zibl,rt3(i))
            enddo
            zrise(ir)=amin1(zibl,zfin0)
            xrise(ir)=xibl
         enddo
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine rolldn(np)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 950715            ROLLDN
!                D. Strimaitis,  SRC
!
! --- PURPOSE:  Identifies inactive puff/slug indices, and "rolls down"
!               arrays to free space for new puffs/slugs
!
! --- INPUTS:
!            NP - integer - Index of last puff/slug initialized
!
!     Common block /PUFF/ variables:
!           NPUFFS, IPUFCD
!     Common block /OUTPT/ variables:
!           IMESG
!     Parameters:
!           MXPUFF
!
! --- OUTPUT:
!            NP - integer - Revised index of last puff/slug initialized
!     Common block /PUFF/ variables:
!           NPUFFS
!
! --- ROLLDN called by: AREAS1, LINES1, POINTS1, POINTS2, VOLS,
!                       SPLIT, RESTARTO
! --- ROLLDN calls:     SWAP
!----------------------------------------------------------------------
! --- NOTE:
!     Inactive indices are searched in ascending order, and are filled
!     with next active "old" puff/slug in descending order.  Once gaps
!     in the "old" locations have been filled, the new puffs/slugs
!     initialized this timestep are added to the top of the arrays,
!     retaining the order.
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'puff.puf'
      include 'outpt.puf'
!
! --- Identify number of NEW puffs/slugs already initialized
      nnew=np-npuffs
!
! --- Set starting index for ascending search for inactive puffs
      inact=1
!
! --- Loop over "old" puffs/slugs in descending order
      do j=npuffs,1,-1
         if(ipufcd(j).LT.99) then
! ---       Active puff found: use this to replace next inactive puff
            do i=inact,j
               if(ipufcd(i).EQ.99) then
! ---             Inactive puff found, replace it and EXIT do-i
                  call swap(i,j)
                  goto 10
               endif
            enddo
! ---       Inactive puff NOT found, replacement complete: EXIT do-j
            goto 50
10          inact=i
         endif
      enddo
50    continue
! --- Reset number of "old" puffs/slugs
      ntop=npuffs
      npuffs=j
!
! --- Inactive indices have been filled; place new puffs already
! --- initialized at the top of the arrays
      do ii=1,nnew
         i=npuffs+ii
         j=ntop+ii
         call swap(i,j)
      enddo
!
! --- Reset the index for the last puff/slug initialized
      np0=np
      np=npuffs+nnew
! --- Report activity to screen (controlled by IMESG)
      if(imesg.GT.0) then
         nlost=np0-np
         print 9,nlost
9        format('+',44x,'-',i8,' inactive puffs removed')
      endif
      return
      end
