!----------------------------------------------------------------------
      subroutine lines2(ig,nspec,ndhrb,nsecb,ndhre,nsece,dtsec,&
                         smxi1,ldb,mfact,metfm,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230            LINES2
!                D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               LINE SOURCES with variable parameters (one group
!               processed)
!
! --- UPDATE
! --- V6.402 - TNG-7.1.0  141230  (DGS)
!                                : Allow roughness adjustment (MROUGH)
!                                  of RURAL PG sigmas to be used with
!                                  CALMET gridded fields
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
! --- V6.265-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.261-V6.265 090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
!                           (DGS): Apply minimum floor to the downwash
!                                  sigmas stored in SY0,SZ0 arrays
! --- V6.22-V6.261  080520  (DGS): Place rise tables in DA file
! --- V6.1-V6.22    070921  (DGS): initialize variables written in debug
!                                  output that may not be computed in
!                                  loop over lines (thalf,xhalfkm,
!                                  syhalf,szhalf)
!                   070921  (DGS): Replace intrinsic function AMIN0()
!                                  with MIN() when both result and
!                                  arguments are integers
! --- V5.75-V6.1    050915  (DGS): generalize treatment of time for
!                                  variable emissions files --
!                                  resolve time to seconds, and
!                                  allow variable emissions timesteps
! --- V5.74-V5.75   050225  (DGS): Add DPBL arg to SETCSIG for TAULY
! --- V5.72-V5.74   040715  (DGS): add METFM=5 (AERMET)
! --- V5.4-V5.72    031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.0     980807  (DGS): use final rise CALMET/PROFILE winds 
!                                  to set release step
! --- V5.0-V5.0     980515  (DGS): ZITIBL array added
! --- V4.0-V5.0     971107  (DGS): ZPB,ZPE arrays added
!
! --- INPUTS:
!            IG - integer - Index for line source group processed
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         DTSEC - real    - Length (seconds) of a basic time step
! SMXI1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!         METFM - integer - Meteorological data format
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, WSCALM, PLX0
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MTIP, MHFTSZ, MSHEAR
!     Common block /GRID/ variables:
!           DGRID, IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /LN2/ variables:
!           NLRISE2,NLINES2(mxqstep),
!           NL2(mxqstep,mxlngrp),XL2(mxqstep,mxlngrp),
!           HBL2(mxqstep,mxlngrp),WBL2(mxqstep,mxlngrp),
!           WML2(mxqstep,mxlngrp),DXL2(mxqstep,mxlngrp),
!           FPRIMEL2(mxqstep,mxlngrp),WSEP2(mxqstep,mxlngrp),
!           FPTOT2(mxqstep,mxlngrp),FBPT2(mxqstep,mxlngrp),
!           ILNGRP(mxqstep,mxlines),XL2BEGGRD(mxqstep,mxlines),
!           YL2BEGGRD(mxqstep,mxlines),XL2ENDGRD(mxqstep,mxlines),
!           YL2ENDGRD(mxqstep,mxlines),HSL2(mxqstep,mxlines),
!           BELEVL2(mxqstep,mxlines),XVERTL2(4,mxqstep,mxlines),
!           YVERTL2(4,mxqstep,mxlines),XL2BAR(mxqstep,mxlngrp),
!           YL2BAR(mxqstep,mxlngrp),ORIENTL2(mxqstep,mxlngrp),
!           ARLINE2(mxqstep,mxlines),QTL2(mxspec,mxqstep,mxlines)
!           NSEG2(mxqstep,mxlines)
!
!     Common block /METHR/ variables:
!           PLEXP
!     Common block /PUFF/ variables:
!           NPUFFS
!     Parameters:
!           MXRISE, MXLINES, MXLNGRP, MXQSTEP, MXSPEC, MXMETDOM, IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /LN2/ variables:
!           NEWLN2(mxqstep,mxlines), NSEG2(mxqstep,mxlines),
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!     Common block /SRCTAB/ variables:
!           NTR, XTR(mxrise), ZTR(mxrise)
!
! --- LINES2 called by:  INITPUF
! --- LINES2 calls:      RLSMET, ROLLDN, HEFTRAN, SIGTY, SIGTZ, SETLINE,
!                        SWAP, INJECT, ZEROTAB, SRCTABOUT, GETCELL
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'comparm.puf'
      include 'csigma.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methr.puf'
      include 'ln2.puf'
      include 'puff.puf'
      include 'slug.puf'
      include 'srctab.puf'
      real smxi1(mxmetdom)
      real q(mxspec)
      logical ldb,problem,lcalm
! --- Set minimum allowed wind speed for building downwash (.GE.1)
      data wsdw0/1.0/
      data oneby2pi/0.1591549/,rt2pi/2.5066283/,rt2bypi/0.7978846/
      data zero/0.0/
! ***
      if(ldb) then
         write(io6,*)
         write(io6,*) 'LINES2:  Processing Line Group ',ig
         write(io6,*) '         # of emission periods ',nstep5
      endif
! ***
! --- Loop over emission periods within this timestep
      do 500 it=1,nstep5
! ---    Skip inactive groups this emission step
         if(nl2(it,ig).LE.0) goto 500
! -------------------------------------------------------------------
! ---    Get met data for block of line sources
! -------------------------------------------------------------------
!
! ---    Determine MET grid 1 (outermost) point closest to the CENTER
! ---    of the array of line sources
         ixs=1.0+xl2bar(it,ig)
         iys=1.0+yl2bar(it,ig)
!
! ---    Source off the computational grid -- write a FATAL message
         if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.&
            iys.lt.jbcomp.or.iys.gt.jecomp) then
            write(io6,20) ig,ixs,iys
20       format(/1x,'FATAL -- a line source with variable emissions ',&
         'is off the computational grid'/1x,'group no. (i) = ',i6,2x,&
         'nearest grid point (ixs,iys) = (',i5,',',i5,')')
            problem=.TRUE.
            return
         endif
! ---    Now determine the met. grid point closest to the source
         call GETCELL('LINES2      ','GRIDIJ',1,xl2bar(it,ig),&
                      yl2bar(it,ig),zero,zero,ixs,iys,md)
! ---    Initialize source tabulation arrays to zero
         call ZEROTAB
!
! ---    Extract wind speed & other met. variables;
! ---    and set CALM configuration if triggered
         call rlsmet(ldb,ixs,iys,md,hbl2(it,ig),mfact,&
                  mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                  ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
!
! ---    Set logical to identify calm conditions
         if(ws.LT.wscalm) then
            lcalm=.TRUE.
         else
            lcalm=.FALSE.
         endif
! ---    Trap missing power-law exponent (substitute default)
         if(plexp.LT.-900.) then
! ---       Use default
            plx=plx0(istab)
         else
! ---       Use it
            plx=plexp
         endif
! ---    Calculate the virtual times and sigmas at Heffter transition
         uavg=amax1(.5,ws)
!
! ---    Set selected data in /CSIGMA/ for sigma calls
         call SETCSIG(idopty,idoptz,iru,uavg,istab,el,sqrts,&
                  tsigv,tsigw,symin,szmin,hbl2(it,ig),dpbl,ixs,iys,md)
!
         if(mhftsz.EQ.0)then
            szh=syh
            call sigty(syh,zero,zero,dum,thfty,dhfty)
         else
            call heftran(1,hsl2(it,1),symin,szmin,zero,zero,zero,zero)
         endif
!
! --------------------------------------------------------------------
! ---    Calculate modified plume rise parameters for group, and
! ---    tabulate rise from point of full buoyancy (XFB) to point of
! ---    final rise
! --------------------------------------------------------------------
!
! ---    Find angle between flow and orientation of line sources (theta)
         theta=flow-orientl2(it,ig)
!
! ---    Compute attributes of the line sources for this meteorology,
! ---    and update rise tabulation
         ntr=nlrise2
         call setline(mshear,theta,ws,istab,sqrts,plx,nl2(it,ig),&
                   xl2(it,ig),hbl2(it,ig),wml2(it,ig),fprimel2(it,ig),&
                   wsep2(it,ig),fptot2(it,ig),fbpt2(it,ig),&
                   ntr,xle,xld,rzero,xtr,ztr)
!
! ---    Set trig. factors for flow direction
         sinf=sin(flow)
         cosf=cos(flow)
!
! ---    Find the along-flow coordinate that defines the line source
! ---    element furthest upwind: (YY0GRD in met grid units)
! ---    Rotate coord. system to place the yy-axis along the flow
! ---   (line 1)
         do il=nlines2(it),1,-1
            if(ilngrp(il).EQ.ig) il1=il
         enddo
         yybeg=xl2beggrd(it,il1)*sinf+yl2beggrd(it,il1)*cosf
         yyend=xl2endgrd(it,il1)*sinf+yl2endgrd(it,il1)*cosf
         yy0grd=amin1(yybeg,yyend)
! ---    Continue with other lines, if used
         do i=2,nl2(it,ig)
            lni=i-1+il1
            yy=xl2beggrd(it,lni)*sinf+yl2beggrd(it,lni)*cosf
            yy0grd=amin1(yy0grd,yy)
            yy=xl2endgrd(it,lni)*sinf+yl2endgrd(it,lni)*cosf
            yy0grd=amin1(yy0grd,yy)
         enddo
! ---    Use CALMET/PROFILE winds at final rise to set release/sampling
! ---    data
         if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
            heff=hbl2(it,ig)+ztr(ntr)
            call rlsmet(ldb,ixs,iys,md,heff,mfact,&
                     mfact0fr,ilayerfr,wsfr,flowfr,ivecfr,istab,dpbl,&
                     el,ustr,wstr,tsigvfr,tsigwfr,iru,issta,sqrtsfr,&
                     idoptyfr,idoptzfr)
            wsnew=AMAX1(ws,wsfr)
         else
            wsnew=ws
         endif
!
!******
      if(ldb)then
         write(io6,204) ilayer,ixs,iys,md,ws,istab,dpbl,issta,theta
204      format(5x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,2x,&
          'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'THETA: ',f5.1)
!
         write(io6,206) xtr(1),ztr(1),xle,xld,&
                        xtr(ntr),ztr(ntr)
206      format(5x,'XFB : ',f6.1,2x,'ZFB : ',f6.1,2x,'XLE : ',&
          f6.1,2x,'XLD : ',f6.1,2x,'XFIN0: ',f6.1,2x,'ZFIN0: ',f6.1)
!
      endif
!*****
! ---    Start-time for emission period during timestep
         if(ndhrqb5(it).LT.ndhrb) then
            ndhr1=ndhrb
            nsec1=nsecb
         elseif(ndhrqb5(it).EQ.ndhrb .AND.&
                nsecqb5(it).LT.nsecb) then
            ndhr1=ndhrb
            nsec1=nsecb
         else
            ndhr1=ndhrqb5(it)
            nsec1=nsecqb5(it)
         endif
! ---    End-time for emission period during timestep
         if(ndhrqe5(it).GT.ndhre) then
            ndhr2=ndhre
            nsec2=nsece
         elseif(ndhrqe5(it).EQ.ndhre .AND.&
                nsecqe5(it).GT.nsece) then
            ndhr2=ndhre
            nsec2=nsece
         else
            ndhr2=ndhrqe5(it)
            nsec2=nsecqe5(it)
         endif
! ---    Duration of emission period within timestep (seconds)
         call DELTSEC(ndhr1,nsec1,ndhr2,nsec2,ntemit)
         temit=FLOAT(ntemit)
! ---    Apply transport constraints
         npnewt=wsnew*smxi1(md)*temit/dtsec+1
         npnewt=MIN(npnewt,mxnew)
         temit=temit/FLOAT(npnewt)
         if(ldb)then
            write(io6,*)
            write(io6,*) 'Emissions start:',ndhrqb5(it),nsecqb5(it)
            write(io6,*) 'Emissions   end:',ndhrqe5(it),nsecqe5(it)
            write(io6,*) 'Puff step start:',ndhrb,nsecb
            write(io6,*) 'Puff step   end:',ndhre,nsece
            write(io6,*) 'Use       start:',ndhr1,nsec1
            write(io6,*) 'Use         end:',ndhr2,nsec2
         endif
!
! --------------------------------------------------
! ---    Loop over lines
! --------------------------------------------------
!
         do 100 ii=1,nlines2(it)
         i=idline(it,ii)
! ---    Skip line sources that are not in current group
         if(ilngrp(i).NE.ig) goto 100
! ---    Skip lines that have no emissions this step
         qsum=0.0
         do is=1,nspec
            qsum=qsum+qtl2(is,it,i)
         enddo
         if(qsum.EQ.0.0) goto 100
! ---    Rotate coord. system to place the yy-axis along the flow
         xxbeg=xl2beggrd(it,i)*cosf-yl2beggrd(it,i)*sinf
         xxend=xl2endgrd(it,i)*cosf-yl2endgrd(it,i)*sinf
         yybeg=xl2beggrd(it,i)*sinf+yl2beggrd(it,i)*cosf
         yyend=xl2endgrd(it,i)*sinf+yl2endgrd(it,i)*cosf
! ---    Find cross-wind and along-wind length of this line in meters
         xcrossm=abs(xxbeg-xxend)*dgrid
         yalongm=abs(yybeg-yyend)*dgrid
! ---------------------------------------------------------
! ---    Compute number of segments needed for one line (NSEG)
! ---------------------------------------------------------
         if(mslug.EQ.0 .OR. lcalm) then
! ---       Puffs are released, so use all MXNSEG segments
            thalf=0.
            xhalfkm=0.
            syhalf=0.
            szhalf=0.
            nsegy=0
            nsegz=0
            nseg2(it,i)=mxnseg2
         else
! ---       Slugs are released
!
! ---       Calculate the virtual times and sigmas at the Heffter
! ---       transition
            if(mhftsz.EQ.0)then
               szh=syh
               call sigty(syh,zero,zero,dum,thfty,dhfty)
            else
               call heftran(1,hsl2(it,i),syab1,szab1,&
                            zero,zero,zero,zero)
            endif
! ---       Set NSEGY as function of transport speed, sigma-y,
! ---       and cross-wind length of line (make it an ODD integer)
            thalf=0.5*dtsec
            xhalfkm=0.001*uavg*thalf
            call sigty(zero,xhalfkm,thalf,syhalf,dum,dum)
            nsegy=nint(xcrossm/(rt2pi*syhalf))
            iodd=mod(nsegy,2)
            if(iodd.EQ.0) nsegy=nsegy+1
! ---       Set NSEGZ as function of transport speed, sigma-z, release
! ---       ht, and along-wind change in rise (make it an ODD integer)
            call sigtz(zero,xhalfkm,thalf,hsl2(it,i),szhalf,dum,dum)
            nsegz=nint(2.0*ztr(1)/szhalf)
            iodd=mod(nsegz,2)
            if(iodd.EQ.0) nsegz=nsegz+1
! ---       Take the larger of the two
            nseg2(it,i)=MAX0(nsegz,nsegy)
! ---       Cap result at MXNSEG
            nseg2(it,i)=MIN0(nseg2(it,i),mxnseg2)
         endif
! ---    Update puff numbers
! ---    Keep track of number of new puffs from each segment
         newln2(i)=newln2(i)+npnewt
! ---    Line releases npnewt clouds this emissions step, with nseg
! ---    elements in each cloud
         newpuf=newpuf+npnewt*nseg2(it,i)
! ---    If puff arrays are full, remove puffs that are off the grid
! ---    and "roll down" arrays to make room for new puffs
! ---    (this also applies to the DA file of tabulated arrays)
         if(npuffs+newpuf.gt.mxpuff) call rolldn(np)
! ---    Check to see if roll-down was adequate
         if(npuffs+newpuf.gt.mxpuff)then
            write(io6,*) ' ERROR in subr. LINES2'
            write(io6,*)'Too many puffs on grid for array dimensions',&
            ' -- NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
            ' MXPUFF = ',mxpuff
            write(*,*)
            stop 'Halted in LINES2 -- see list file.'
         endif
!
! --------------------------------------------------------------------
! ---    Calculate building downwash effects on initial plume sigmas
! --------------------------------------------------------------------
! ---    Initialize downwash variables to zero (no building downwash)
         sz0dw=0.0
         sy0dw=0.0
         idw=0
         if(ws.GT.wsdw0 .AND. .NOT.lcalm) then
! ---       Set downwash flag to ACTIVE
            idw=1
! ---       Use BLP calculation of downwash sigmas
            sz0dw=rt2bypi*rzero
            sy0dw=0.5*sz0dw
         endif
!
! ---    Set initial sigma y, sigma z for puff/slug, including
! ---    effective spread due to length of line source element
! ---    (add length effects and downwash effects in quadrature)
! ---    Crosswind variance: 0.25*(line width)**2/(2pi)
         if(lcalm) then
            xlen2=(xcrossm**2+yalongm**2)/(nseg2(it,i)**2)
         else
            xlen2=(xcrossm/nseg2(it,i))**2
         endif
         sy2=0.25*oneby2pi*xlen2+sy0dw*sy0dw
         sysrc=amax1(symin,sqrt(sy2))
! ---    Alongwind variance: 0.25*(line length * rise rate)**2/(2pi)
         if(lcalm) then
            sz2=0.0
            szsrc=szmin
         else
            sz2=0.25*oneby2pi*((yalongm/nseg2(it,i))*&
                            ztr(1)/xtr(1))**2+sz0dw*sz0dw
            szsrc=amax1(szmin,sqrt(sz2))
         endif
!
! ---    Swap emission rate for source 'i' into 1D array
         do is=1,nspec
            q(is)=qtl2(is,it,i)
         enddo
!******
      if(ldb)then
         write(io6,*)
         write(io6,*) 'Line Source # ',i
         write(io6,*) 'Emission step ',it
         write(io6,*)
         write(io6,*) 'LINES2:  thalf,xhalfkm = ',thalf,xhalfkm
         write(io6,*) '          syhalf,nsegy = ',syhalf,nsegy
         write(io6,*) '          szhalf,nsegz = ',szhalf,nsegz
         write(io6,*) '                  nseg = ',nseg2(it,i)
         write(io6,208) xcrossm,npnewt,newpuf,dtsec,&
                        idw,rzero,sysrc,szsrc
208      format(5x,'XCROSSM: ',f6.1,2x,&
          'NPNEWT: ',i4,2x,'NEWPUF: ',i5,2x,'DTSEC: ',f7.2,2x/5x,&
          'IDW: ',i3,2x,'RZERO: ',f6.1,2x,'SYSRC: ',f6.1,2x,&
          'SZSRC: ',f6.1)
      endif
!*****
!
! -------------------------------------------------------------
! ---    Loop over "point" sources (each segment) along line
! -------------------------------------------------------------
!
! ---    Set up the distance increments along the line
         dxgrd=(xl2endgrd(it,i)-xl2beggrd(it,i))/nseg2(it,i)
         dxgrdby2=0.5*dxgrd
         dygrd=(yl2endgrd(it,i)-yl2beggrd(it,i))/nseg2(it,i)
         dygrdby2=0.5*dygrd
         do 90 ipt=1,nseg2(it,i)
! ---      Set location in met grid units
           xptgrd=xl2beggrd(it,i)+(ipt-1)*dxgrd+dxgrdby2
           yptgrd=yl2beggrd(it,i)+(ipt-1)*dygrd+dygrdby2
! ---      Set shift in distance (m) of this point from the upwind edge
           yyptgrd=xptgrd*sinf+yptgrd*cosf
           xshift=(yyptgrd-yy0grd)*dgrid
           if(xshift.LT.0.0) xshift=0.0
           if(xshift.GT.xtr(1)) xshift=xtr(1)
! ---      Set final height and distance to final rise for this puff
           if(lcalm) then
             xshift=0.0
             xfrise=0.0
             heff=hsl2(it,i)+ztr(ntr)
           else
             if(xtr(1).LE.xtr(ntr)) then
! ---           Case 1:  final rise reached beyond XFB
                heff=hsl2(it,i)+ztr(ntr)-xshift*ztr(1)/xtr(1)
                xfrise=xtr(ntr)-xshift
             else
! ---           Case 2:  final rise reached before XFB
                size=1.0-xshift/xtr(1)
                heff=hsl2(it,i)+ztr(ntr)*size
                xfrise=xtr(ntr)*size
             endif
           endif
!
!******
      if(ldb)then
         write(io6,*) 'Element #  ',ipt
         write(io6,210) hsl2(it,i),heff,xfrise,xshift,xptgrd,yptgrd
210      format(5x,'HSL : ',f6.1,2x,'HEFF: ',f6.1,'XFRISE: ',f6.1,2x,&
          'XSHIFT: ',f6.1,2x,'XPTGRD: ',f8.4,2x,'YPTGRD: ',f8.4)
      endif
!*****
! ----------------------------------------------------------
! ---      Initialize current hour's new puffs for this source
! ----------------------------------------------------------
           do 50 j=1,npnewt
             np=np+1
!
! ---        Use SWAP to start with all puff/slug variables = zero
             call swap(np,0)
!
! ---        Fill non-zero values
             xpb(np)=xptgrd
             ypb(np)=yptgrd
             zitibl(np)=-1.0
             elbase(np)=belevl2(it,i)
             sigyb(np)=sysrc
             sigzb(np)=szsrc
! ---        Final plume rise results for puff
             zfinal(np)=heff
             zpb(np)=heff
             zpe(np)=heff
             xfinal(np)=xfrise
! ---        Intermediate results for calc. gradual rise
             fb(np)=fbpt2(it,ig)
! ---        Set xbfin and zbfin to negatives to signal use of final
! ---        rise if calm
             if(lcalm) then
                xbfin(np)=-1.0
                zbfin(np)=-1.0
             else
                xbfin(np)=xtr(ntr)
                zbfin(np)=ztr(ntr)
             endif
             isplit(np)=1
! ---        Time-of-release data
             ht0(np)=hsl2(it,i)
             temit0(np)=temit
             ws0(np)=ws
             istab0(np)=istab
             sqrts0(np)=sqrts
             iru0(np)=iru
             sigv0(np)=tsigv
             sigw0(np)=tsigw
             el0(np)=el
             plexp0(np)=plx
! ---        Set vector/scalar ws ratio to 1.0
             srat0(np)=1.0
! ---        Additional time-of-release parameters needed for
! ---        receptor-specific calculations (downwash - point sources)
             idw0(np)=idw
             hb0(np)=hsl2(it,i)
             hw0(np)=xcrossm
             heff20(np)=hsl2(it,i)
             zly0(np)=xld
             r0(np)=rzero
! ---        Additional data for line sources
             xshift0(np)=xshift
             sy0(np)=AMAX1(sy0dw,symin)
             sz0(np)=AMAX1(sz0dw,szmin) 
! ---    Place end-time of emission period for this puff into
! ---    TMTOTB(mxpuff) array, expressed as a fraction of the timestep
! ---    This will be used in COMP when computing the sampling steps
! ---    used to advect the freshly emitted puff during the remainder
! ---    of the timestep, and then reset.
! ---    Seconds from start of emissions period to end of timestep
             call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
             tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
! ---        Emission period index for this puff
             iemstep(np)=it
! ---        Set puff identity where IRLSNUM is puff number released
! ---        from a source this time step, ISRCNUM is the source number
! ---        of type ISRCTYP from which the puff was released.
! ---        (Type =6 for variable line sources)
             jj=newln2(i)-npnewt+j
             irlsnum(np)=jj
             isrcnum(np)=i
             isrctyp(np)=6
!
! ---    !-NOTE 1-!
! ---    The puff number really refers to the number of emission
! ---    steps, which equals the puff number when ONE puff is released
! ---    from a source each emission step.  But many puffs are released
! ---    each step when simulating a line source (distributed along
! ---    the line).  Subsequent use is made of IRLSNUM in controlling
! ---    sampling functions, which depend on the emission steps, so we
! ---    explicitly place the emission-step index in the array.
!
! ----------------------------------------------------------
! ---        Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    !-NOTE 2-!
! ---    OLD slugs from each segment are modeled explicitly, but
! ---    NEW releases are modelled using the numerical area-source
! ---    method for the ENTIRE line.  Therefore, we add a marker to
! ---    all slugs after segment 1, so that these can be identified
! ---    and ignored when NEW.  The marker consists of adding 1000 to
! ---    ISRCNUM, which would indicate that the puff comes from
! ---    line-source ISRCNUM=1000+i.  A check is made in READCF which
! ---    stops execution if parameter (mxlines .GE. 1000) to insure
! ---    that no valid line-source ID exceeds 999.
!
             if(mslug.eq.1 .AND. .NOT.lcalm) then
!
! ---          Set PUFID marker
               if(ipt.GT.1) isrcnum(np)=isrcnum(np)+1000
!
! ---          Set coordinates of oldest end of slug (met. grid units)
! ---          Note that "oldest" end of slug is at source, as is the
! ---          "newest" end; oldest end moves away and grows during the
! ---          step, while newest end remains fixed in size at source.
               xpe(np)=xpb(np)
               ype(np)=ypb(np)
               xtote(np)=xtotb(np)
               tmtote(np)=tmtotb(np)
! ---          Set sigmas for oldest end of slug
               sigye(np)=sigyb(np)
               sigze(np)=sigzb(np)
             endif
!
! --------------------------------------------------------------------
! ---        Inject emissions into proper layer (above/below mixing ht)
! --------------------------------------------------------------------
! ---        Set partial penetration variables to null values
             fmix=1.0
             hmax=dpbl
! ---        Mass emitted into each line segment needs to be divided by
! ---        nseg, which is the number of segments.  Accomplish that
! ---        here by dividing the timestep by nseg.
             dtseg=temit/nseg2(it,i)
             call inject(np,nspec,dtseg,mfact0,q,heff,hmax,dpbl,&
                         fmix,istab)
!
! ------------------------------------------------------------------
! ---      Transfer tabulated arrays record to DA file using full
! ---      MXRISE dimension
! ------------------------------------------------------------------
           call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50         continue
90       continue
100      continue
500   continue
      return
      end
!----------------------------------------------------------------------
      subroutine ln2fill(iq,lngrp,nsrc5,idmap,em5grp,em5dat,nvarln,&
                         problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 070921           LN2FILL
!                D. Strimaitis
!
! --- PURPOSE:  Fill common /LN2/ for LINE SOURCES with variable
!               parameters
!
! --- UPDATE
! --- V6.1-V6.22    070921  (DGS): initialize PROBLEM to false
! --- V5.71-V6.1    050915  (DGS): add emission substep to arrays
!                                  and update line/group mapping
! --- V5.0-V5.71    030528  (DGS): require non-negative emissions
! --- V5.0-V5.0     980430  (DGS): add number of variables before
!                                  emissions in EM5DAT as argument
!
! --- INPUTS:
!               IQ - integer - Current emission timestep index
!            LNGRP - integer - Number of groups of line sources
!                              active this period
!            NSRC5 - integer - Max number of lines in  LNEMARB file
!  EM5GRP(7,lngrp) - real ar - Time-varying LNEMARB group data
!  EM5DAT(nse5+nvarln,nsrc5)
!                  - real ar - Time-varying LNEMARB line data
!           NVARLN - integer - Number of variables in EM5DAT array
!                              before emissions
!          PROBLEM - logical - Flag indicating errors are found
!                              when true
!
!     Common block /GRID/ variables:
!           DGRID, DGRIDI, XORIG, YORIG
!
!     Common block /LN2/ variables:
!           NSE5,ILNGRP(mxlines)
!
!     Parameters:
!           MXLINES, MXLNGRP, MXSPEC
!
! --- OUTPUT:
!          PROBLEM - logical - Flag indicating errors are found
!                              when true (updated here)
!
!     Common block /LN2/ variables:
!           ILNGRP(mxlines),NLINES2(mxqstep),
!           IDLINE(mxqstep,mxlines),
!           NL2(mxqstep,mxlngrp),XL2(mxqstep,mxlngrp),
!           HBL2(mxqstep,mxlngrp),WBL2(mxqstep,mxlngrp),
!           WML2(mxqstep,mxlngrp),DXL2(mxqstep,mxlngrp),
!           FPRIMEL2(mxqstep,mxlngrp),WSEP2(mxqstep,mxlngrp),
!           FPTOT2(mxqstep,mxlngrp),FBPT2(mxqstep,mxlngrp),
!           XL2BEGGRD(mxqstep,mxlines),
!           YL2BEGGRD(mxqstep,mxlines),XL2ENDGRD(mxqstep,mxlines),
!           YL2ENDGRD(mxqstep,mxlines),HSL2(mxqstep,mxlines),
!           BELEVL2(mxqstep,mxlines),XVERTL2(4,mxqstep,mxlines),
!           YVERTL2(4,mxqstep,mxlines),XL2BAR(mxqstep,mxlngrp),
!           YL2BAR(mxqstep,mxlngrp),ORIENTL2(mxqstep,mxlngrp),
!           ARLINE2(mxqstep,mxlines),QTL2(mxspec,mxqstep,mxlines)
!
! --- LN2FILL called by:  INITPUF
! --- LN2FILL calls:      none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'grid.puf'
      include 'ln2.puf'
      real em5dat(nse5+nvarln,nsrc5),em5grp(7,lngrp)
      integer idmap(nsrc5)
      logical problem, lqneg, lok
! --- Initialize PROBLEM
      problem=.FALSE.
! --- Place the map from the active line source index to the master
! --- line source ID into this emission step
      do k=1,nsrc5
         idline(iq,k)=idmap(k)
      enddo
! --- Have all lines been assigned a group?
      lok=.TRUE.
      maxgid=0
      do k=1,nsrc5
         maxgid=MAX(maxgid,ilngrp(k))
         if(ilngrp(k).LE.0) lok=.FALSE.
      enddo
! --- Set any unassigned group IDs for these active lines
      if(.not.LOK) then
         lnumber=0
! ---    Loop over active groups
         do ig=1,lngrp
            last=lnumber
            ng=0
            keep=0
! ---       Loop over active lines in group
            do n=1,NINT(em5grp(1,ig))
               lnumber=lnumber+1
               k=idmap(lnumber)
               if(ilngrp(k).GT.0) then
                  ng=ng+1
                  keep=ilngrp(k)
               endif
            enddo
            if(ng.EQ.0) then
! ---          No lines in this group have been assigned
               maxgid=maxgid+1
               lnumber=last
               do n=1,NINT(em5grp(1,ig))
                  lnumber=lnumber+1
                  k=idmap(lnumber)
                  ilngrp(k)=maxgid
               enddo
            elseif(ng.LT.NINT(em5grp(1,ig))) then
! ---          Some lines in this group have been assigned
               lnumber=last
               do n=1,NINT(em5grp(1,ig))
                  lnumber=lnumber+1
                  k=idmap(lnumber)
                  if(ilngrp(k).LE.0) ilngrp(k)=keep
               enddo
            endif
         enddo
      endif
! --- QA: group ID must be the same for all lines in current group
      lnumber=0
! --- Loop over active groups
      do ig=1,lngrp
         last=lnumber
! ---    Loop over active lines in group
         do n=1,NINT(em5grp(1,ig))
            lnumber=lnumber+1
            k=idmap(lnumber)
            if(n.EQ.1) then
               itest=ilngrp(k)
               k1=k
            elseif(ilngrp(k).NE.itest) then
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR in subr. LN2FILL -- Lines in ',&
                  'this input group are from different groups'
               write(io6,*)'LNEMARB Source / Group:'
               write(io6,*)cid5(k1),' / ',itest
               write(io6,*)cid5(k),' / ',ilngrp(k)
               problem=.TRUE.
            endif
         enddo
      enddo
      if(PROBLEM) return
! --- Loop over active line groups this emission step
      nlines2(iq)=0
      do ig=1,lngrp
         nlprev=nlines2(iq)
! ---    Assign group ID for this active group
         k=nlprev+1
         igid=ilngrp(k)
! ---    Assign group properties
         nl2(iq,igid)=NINT(em5grp(1,ig))
         nlines2(iq)=nlines2(iq)+nl2(iq,igid)
         xl2(iq,igid)=em5grp(2,ig)
         hbl2(iq,igid)=em5grp(3,ig)
         wbl2(iq,igid)=em5grp(4,ig)
         wml2(iq,igid)=em5grp(5,ig)
         dxl2(iq,igid)=em5grp(6,ig)
         fprimel2(iq,igid)=em5grp(7,ig)
! ---    Separation between centers of adjacent lines (average)
         wsep2(iq,igid)=wbl2(iq,igid)+dxl2(iq,igid)
! ---    Total buoyancy flux from all line sources in block
         fptot2(iq,igid)=fprimel2(iq,igid)*em5grp(1,ig)
! ---    Corresponding point-source buoyancy flux (fptot/pi)
         fbpt2(iq,igid)=fptot2(iq,igid)*0.3183099
! ---    Set half average line width for area calculations below
         wmlby2=0.5*wml2(iq,igid)
         wmlby2=FLOAT(INT(wmlby2)+1)
! ---    Loop over lines
         do i=nlprev+1,nlines2(iq)
            xl2beggrd(iq,i)=(1000.*em5dat(1,i)-xorig)*dgridi
            yl2beggrd(iq,i)=(1000.*em5dat(2,i)-yorig)*dgridi
            xl2endgrd(iq,i)=(1000.*em5dat(3,i)-xorig)*dgridi
            yl2endgrd(iq,i)=(1000.*em5dat(4,i)-yorig)*dgridi
            hsl2(iq,i)=em5dat(5,i)
            belevl2(iq,i)=em5dat(6,i)
            lqneg=.FALSE.
            do is=1,nse5
               k=is+nvarln
               qtl2(ixrem5(is),iq,i)=em5dat(k,i)
               if(qtl2(ixrem5(is),iq,i).LT.0.0) lqneg=.TRUE.
            enddo
! ---       Report problem if any emissions are negative
            if(lqneg) then
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR in subr. LN2FILL -- Invalid ',&
               'emission rate  (must NOT be negative)'
               write(io6,*)' -- LNEMARB Source = ',i
               problem=.TRUE.
            endif
            if(PROBLEM) return
!
! ---       Compute the vertex coordinates for corresponding
! ---       area-source representation
            xdiffm=(xl2beggrd(iq,i)-xl2endgrd(iq,i))*dgrid
            ydiffm=(yl2beggrd(iq,i)-yl2endgrd(iq,i))*dgrid
! ---       Let "c" denote 1/slope of line
            if(abs(ydiffm).LE.1.0e-10) then
               dx=0.0
               dy=-wmlby2
            else
               c=xdiffm/ydiffm
               dx=wmlby2/SQRT(1+c*c)
               dy=-dx*c
            endif
! ---       Convert deltas to grid units
            dxgrd=dx*dgridi
            dygrd=dy*dgridi
! ---       Pass vertices to arrays in /LN2/
            xvertl2(1,iq,i)=xl2beggrd(iq,i)-dxgrd
            yvertl2(1,iq,i)=yl2beggrd(iq,i)-dygrd
            xvertl2(2,iq,i)=xl2beggrd(iq,i)+dxgrd
            yvertl2(2,iq,i)=yl2beggrd(iq,i)+dygrd
            xvertl2(3,iq,i)=xl2endgrd(iq,i)+dxgrd
            yvertl2(3,iq,i)=yl2endgrd(iq,i)+dygrd
            xvertl2(4,iq,i)=xl2endgrd(iq,i)-dxgrd
            yvertl2(4,iq,i)=yl2endgrd(iq,i)-dygrd
! ---       Corresponding area of line source in m^2
            arline2(iq,i)=2.*wmlby2*SQRT(xdiffm*xdiffm+ydiffm*ydiffm)
         enddo
!
! ---    Find center of array of line sources, and orientation
! ---    for this group
         xbeg=0.0
         ybeg=0.0
         xend=0.0
         yend=0.0
         fac=1.0/em5grp(1,ig)
         do i=nlprev+1,nlines2(iq)
            xbeg=xbeg+xl2beggrd(iq,i)
            ybeg=ybeg+yl2beggrd(iq,i)
            xend=xend+xl2endgrd(iq,i)
            yend=yend+yl2endgrd(iq,i)
         enddo
         xbeg=xbeg*fac
         ybeg=ybeg*fac
         xend=xend*fac
         yend=yend*fac
!
         xl2bar(iq,igid)=0.5*(xbeg+xend)
         yl2bar(iq,igid)=0.5*(ybeg+yend)
         orientl2(iq,igid)=ATAN2((xbeg-xend),(ybeg-yend))
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine ln2swap(nspec,iqinp,iqout)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915           LN2FILL
!                D. Strimaitis,  EARTH TECH
!
! --- PURPOSE:  Swap variables in common /LN2/ for LINE SOURCES with
!               variable parameters, from emission step iqinp to
!               emission step iqout
!
! --- INPUTS:
!            NSPEC - integer - Number of species
!            IQINP - integer - Emission step 'input'
!            IQOUT - integer - Emission step 'output'
!
!     Common block /LN2/ variables:
!           IXREM5(mxspec)
!
!     Parameters:
!           MXLINES, MXLNGRP, MXSPEC
!
! --- OUTPUT:
!
!     Common block /LN2/ variables:
!           NDHRQB5(mxqstep),NSECQB5(mxqstep),
!           NDHRQE5(mxqstep),NSECQE5(mxqstep),
!           NLINES2(mxqstep),
!
!           NL2(mxqstep,mxlngrp),XL2(mxqstep,mxlngrp),
!           HBL2(mxqstep,mxlngrp),WBL2(mxqstep,mxlngrp),
!           WML2(mxqstep,mxlngrp),DXL2(mxqstep,mxlngrp),
!           FPRIMEL2(mxqstep,mxlngrp),WSEP2(mxqstep,mxlngrp),
!           FPTOT2(mxqstep,mxlngrp),FBPT2(mxqstep,mxlngrp),
!           XL2BAR(mxqstep,mxlngrp),YL2BAR(mxqstep,mxlngrp),
!           ORIENTL2(mxqstep,mxlngrp),
!
!           IDLINE(mxqstep,mxlines),
!           XL2BEGGRD(mxqstep,mxlines),YL2BEGGRD(mxqstep,mxlines),
!           XL2ENDGRD(mxqstep,mxlines),YL2ENDGRD(mxqstep,mxlines),
!           HSL2(mxqstep,mxlines),BELELN2(mxqstep,mxlines),
!           XVERTL2(4,mxqstep,mxlines),YVERTL2(4,mxqstep,mxlines),
!           ARLINE2(mxqstep,mxlines),NSEG2(mxqstep,mxlines),
!           QTL2(mxspec,mxqstep,mxlines),NEWLN2(mxqstep,mxlines)
!
! --- LN2SWAP called by:  INITPUF
! --- LN2SWAP calls:      none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'ln2.puf'
      integer igrp(mxlngrp)
! --- Initialize marker for groups to 0
      do k=1,mxlngrp
         igrp(k)=0
      enddo
! --- Date - time
      ndhrqb5(iqout)=ndhrqb5(iqinp)
      nsecqb5(iqout)=nsecqb5(iqinp)
      ndhrqe5(iqout)=ndhrqe5(iqinp)
      nsecqe5(iqout)=nsecqe5(iqinp)
! --- Total number of lines
      nlines2(iqout)=nlines2(iqinp)
! --- Loop over lines
      do i=1,nlines2(iqout)
         idline(iqout,i)=idline(iqinp,i)
         xl2beggrd(iqout,i)=xl2beggrd(iqinp,i)
         yl2beggrd(iqout,i)=yl2beggrd(iqinp,i)
         xl2endgrd(iqout,i)=xl2endgrd(iqinp,i)
         yl2endgrd(iqout,i)=yl2endgrd(iqinp,i)
         hsl2(iqout,i)=hsl2(iqinp,i)
         belevl2(iqout,i)=belevl2(iqinp,i)
         do k=1,4
            xvertl2(k,iqout,i)=xvertl2(k,iqinp,i)
            yvertl2(k,iqout,i)=yvertl2(k,iqinp,i)
         enddo
         arline2(iqout,i)=arline2(iqinp,i)
         nseg2(iqout,i)=nseg2(iqinp,i)
         newln2(i)=newln2(i)
         do is=1,nspec
            qtl2(ixrem5(is),iqout,i)=qtl2(ixrem5(is),iqinp,i)
         enddo
! ---    Mark group for this line
         k=ilngrp(idline(iqout,i))
         igrp(k)=1
      enddo
! --- Loop over line-group variables
      do ig=1,mxlngrp
         if(igrp(ig).EQ.0) then
            nl2(iqout,ig)=0
         else
            nl2(iqout,ig)=nl2(iqinp,ig)
            xl2(iqout,ig)=xl2(iqinp,ig)
            hbl2(iqout,ig)=hbl2(iqinp,ig)
            wbl2(iqout,ig)=wbl2(iqinp,ig)
            wml2(iqout,ig)=wml2(iqinp,ig)
            dxl2(iqout,ig)=dxl2(iqinp,ig)
            fprimel2(iqout,ig)=fprimel2(iqinp,ig)
            wsep2(iqout,ig)=wsep2(iqinp,ig)
            fptot2(iqout,ig)=fptot2(iqinp,ig)
            fbpt2(iqout,ig)=fbpt2(iqinp,ig)
            xl2bar(iqout,ig)=xl2bar(iqinp,ig)
            yl2bar(iqout,ig)=yl2bar(iqinp,ig)
            orientl2(iqout,ig)=orientl2(iqinp,ig)
         endif
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine ctadj(zterr,zht,zstak,zbase,ppcoef,zpa)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107             CTADJ
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Adjust puff-height to simulate terrain effects when
!               either the ISC terrain treatment (MCTADJ = 1) or the
!               partial plume height correction (MCTADJ = 3) is used
!
! --- UPDATE
! --- V4.0-V5.0     971107  (DGS): Allow receptors below stack base
!                                  in ISC treatment
!                   971107  (DGS): Also continue to apply partial
!                                  height eqn. with negative terrain ht
!
! --- INPUTS:
!             ZTERR - real    - Terrain elevation (m MSL) at receptor
!               ZHT - real    - Puff height before adjustments (m)
!             ZSTAK - real    - Stack height of source of puff (m)
!             ZBASE - real    - Stack base elevation MSL (m)
!            PPCOEF - real    - Plume Path Coefficient
!
!     Common Block /FLAGS/ variables:
!        MCTADJ
!     Parameters:
!        MXREC
!
!
! --- OUTPUTS:
!         ZPA - real      - Adjusted puff height above ground (m)
!
! --- CTADJ called by:  VCBAR, PUFRECS, SLGRECS, RECSPEC0
! --- CTADJ calls:      none
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'flags.puf'
      include 'nongrd.puf'
! --- Set minimum height of slug above the ground
      data zmin/0.0/
! --- Apply terrain adjustment
      if(mctadj .EQ. 1) then
! ---    ISC terrain adjustment
! ---    Chop terrain at stack-top (set relative to stack-base)
         zrterr = AMIN1(zterr-zbase,zstak)
! ---    Allow negative zrterr to pass (as in ISCST)
         zpa=AMAX1(zmin, (zht - zrterr))
      elseif(mctadj .EQ. 3) then
! ---    Partial Plume Height adjustment (e.g. half-height)
         delzr=zterr-zbase
! ---    Allow terrain below stack-base (delzr can be negative)
         zpa=zht-(1.-ppcoef)*AMIN1(zht,delzr)
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine ctadj2(r,zpold,xold,yold,xnew,ynew,szold,bvf,ws,&
                        ldbhr,strain,zpnew,fracz,lup)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 101111            CTADJ2
!                D. Strimaitis
!
! --- PURPOSE:  Compute strain-induced adjustment factor for vertical
!               dispersion, where the strain in the flow is approx.
!               from the underlying gridded terrain field.
!               Also, compute changes in puff height above ground for
!               stably stratified flow.  This is used when MCTADJ=2.
!
! --- UPDATE
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
! --- V5.2-V6.3     100212  (DGS): add nested CALMET grids
! --- V5.0-V5.2     991104a (DGS): add constraint that R <= 1.0
! --- V4.0-V5.0     971107  (DGS): clean up treatment of degenerate
!                                  cases; use approximate expressions
!                                  for functions of small argument
!                   971107  (DGS): use puff height and local terrain
!                                  relief for determining etabyh, and
!                                  use local relief in Froude number
!                   971107  (DGS): set ratio of step length to
!                                  horizontal scale of surrogate hill
!                                  equal to step rise/relief
!                   971107  (JCC): separate max/min function calls
! --- V4.0-V5.0     971107  (DGS): adjust puff height for stable flows
!
! --- INPUTS:
!           R - real      - ratio of sigma-z at the start of step
!                           to that at end of step, before accounting
!                           for strain during the step
!       ZPOLD - real      - Puff height above ground (m) at start
!        XOLD - real      - Old puff x-position (Met Grid Units)
!        YOLD - real      - Old puff y-position (Met Grid Units)
!        XNEW - real      - New puff x-position (Met Grid Units)
!        YNEW - real      - New puff y-position (Met Grid Units)
!       SZOLD - real      - Sigma-z at start of step (m)
!         BVF - real      - Current Brunt-Vaisala freq (1/s)
!          WS - real      - Current wind speed (m/s)
!       LDBHR - logical   - Debug write logical
!
!       Common block /GRID/ variables:
!             dgrid, relief(5,mxnx,mxny,mxmetdom), nx, ny
!       Parameters:
!             IO6,MXNX,MXNY,MXMETDOM
!
! --- OUTPUT:
!      STRAIN - real      - Dispersion adjustment factor for the
!                           current step
!       ZPNEW - real      - Puff height above ground (m) at end of step
!       FRACZ - real      - Fraction of step beyond which puff height is
!                           is constant (ZPNEW) for upslope flow, or up
!                           to which puff height is constant (ZPOLD) for
!                           downslope flow
!         LUP - logical   - Flag indicating upslope flow when .TRUE.
!
! --- CTADJ2 called by:  SETPUF, SETSLG
! --- CTADJ2 calls:      GETELEV, GETCELL
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'grid.puf'
      logical ldbhr,lup
      data two/2./,one/1./,half/0.5/,third/0.3333333/,zero/0.0/,&
           small/1.0e-4/
! --- Condition ratio r to be <= 1
      r=AMIN1(1.0,r)
! --- Set met cell location for old and new puff locations
      call GETCELL('CTADJ2_1    ','GRIDIJ',1,xold,yold,zero,zero,&
                   icello,jcello,mdo)
      call GETCELL('CTADJ2_2    ','GRIDIJ',1,xnew,ynew,zero,zero,&
                   icelln,jcelln,mdn)
! --- Compute horizontal distance of step, and rise along step (m)
      xstep=(xnew-xold)*dgrid
      ystep=(ynew-yold)*dgrid
      delx=SQRT(xstep**2+ystep**2)
      call GETELEV(xold,yold,elold)
      call GETELEV(xnew,ynew,elnew)
      rise=elnew-elold
      arise=abs(rise)
      if(rise.GT.zero) then
         lup=.TRUE.
      else
         lup=.FALSE.
      endif
! --- Set relief height for this trajectory
! --- ( tan(22.5)=0.4142 , tan(67.5)=2.4142)
      if(ABS(ystep).GT.2.4142*ABS(xstep)) then
! ---    North-South
         rlf=AMAX1(relief(1,icello,jcello,mdo),&
                   relief(1,icelln,jcelln,mdn))
      else
         ybyx=ystep/xstep
         if(ybyx.GT.0.4142) then
! ---       NorthEast-SouthWest
            rlf=AMAX1(relief(2,icello,jcello,mdo),&
                      relief(2,icelln,jcelln,mdn))
         elseif(ybyx.LT.-0.4142) then
! ---       SouthEast-NorthWest
            rlf=AMAX1(relief(4,icello,jcello,mdo),&
                      relief(4,icelln,jcelln,mdn))
         else
! ---       East-West
            rlf=AMAX1(relief(3,icello,jcello,mdo),&
                      relief(3,icelln,jcelln,mdn))
         endif
      endif
! --- Set peak terrain elevation near this trajectory
      eltop=AMAX1(relief(5,icello,jcello,mdo),&
                  relief(5,icelln,jcelln,mdn))
! --- Compute puff height changes and strain function
!
      if(delx.LE.small .OR. arise.LE.small) then
! ---    Treat degenerate case of no step or no slope
         dtz=zero
         slope=zero
         xbyl=zero
         dxbyl=zero
         strat=zero
         tz=one
         tzsq=one
         strain=one
         zpnew=zpold
         fracz=zero
!
      else
!
! ---    Compute the slope of the terrain along the trajectory segment
         slope=rise/delx
         aslope=ABS(slope)
! ---    Compute change in puff height above ground along step to
! ---    account for deflection of stable layer beneath puff
! ---    NOTE: puffs embedded in downslope flow are not allowed to
! ---    rise over layers below Hd
         if(bvf.GT.zero .AND. lup) then
! ---       Determine dividing-streamline elevation (m MSL)
            elds=eltop-ws/bvf
! ---       Find fraction of step at which height change either
! ---       begins or ends based on dividing-streamline
            fracz=(elds-elold)/rise
! ---       For upslope flow, add constraint that puff height is not
! ---       reduced beyond point where zp/sigz < 1.8, assuming linear
! ---       growth in sigz during step.  This is the point at which the
! ---       presence of the ground increases C(zp) by about .15% over
! ---       free-puff value, and the GLC is about 40% of C(zp).
            if(fracz.GT.zero) then
               sznew=szold/r
               fraczs=(zpold-1.8*szold)/(rise+1.8*(sznew-szold))
               fraczs=AMAX1(zero,fraczs)
               fracz=AMIN1(fraczs,fracz)
            endif
! ---       Compute puff height above ground at at end of step
            if(fracz.LE.zero) then
               zpnew=zpold
            elseif(fracz.GE.one) then
               zpnew=zpold-rise
            else
               zpnew=zpold-rise*fracz
            endif
         else
            zpnew=zpold
            fracz=zero
         endif
! ---    Set ratio of streamline height (eta) / height of the
! ---    surrogate hill (h) equal to the ratio of the "average" puff
! ---    height to the height of the local terrain relief;
! ---    condition eta/H within range 0.1 to 10.0
         zp=half*(zpold+zpnew)
         if(rlf.LE.0.1*zp) then
            etabyh=10.
         else
            etabyh=AMAX1(0.1,zp/rlf)
         endif
! ---    Compute 1/Froude number for the surrogate hill, where the local
! ---    relief is used as the hill height
         fri=bvf*rlf/ws
! ---    Limit Fr>1.0, so that 1/Fr<1.0
         fri=AMIN1(one,fri)
! ---    Set ratio of streamline height (eta) / horizontal scale of the
! ---    surrogate hill (L), and corresponding EXP(+\-)
         etabyl=two*etabyh*aslope
         if(etabyl.GE.0.1) then
           expnl=EXP(etabyl)
           expnli=one/expnl
         else
           expnl=one+etabyl
           expnli=one-etabyl
         endif
! ---    Compute the stratification parameter
         strat=fri*etabyh
         if(strat.GE.0.1) then
            sinln=SIN(strat)
            cosln=COS(strat)
         else
            sinln=strat
            cosln=one-half*strat**2
         endif
!
! ---    Find position on surrogate hill at which change in strain is
! ---    greatest
         trig=sinln/(cosln-expnl)
         if(slope.GT.zero) then
! ---       Upwind face
! ---       No stratification:
            xbyl=-SQRT(third*(one+two*aslope*(one-expnli)/etabyl))
            if(ABS(trig).GE.0.01) then
! ---          Stronger stratification:
               xbyl=xbyl+half*(one-SQRT(one+trig**2))/trig
            else
! ---          Weaker stratification:
               xbyl=xbyl-half*half*trig
            endif
         else
! ---       Downwind face
! ---       No stratification:
! ---       Use small argument approximation to *(one-expnli)/etabyl=1
            xbyl= SQRT(third*(one+two*aslope*(one-expnli)/etabyl))
            if(ABS(trig).GE.0.01) then
! ---          Stronger stratification:
               xbyl=xbyl+(one-SQRT(one+trig**2))/trig
            else
! ---          Weaker stratification:
               xbyl=xbyl-half*trig
            endif
         endif
         xbyl2=xbyl*xbyl
!
! ---    Compute strain factor at XBYL
         tzi=one+(two*aslope/(etabyl*(one+xbyl2))) *&
                 (one-expnli*(cosln-xbyl*sinln))
         tz=one/tzi
         tzsq=tz*tz
!
! ---    Set ratio of step length / length-scale of the
! ---    surrogate hill (delx/L) equal to |rise|/relief
         dxbyl=one
         if(arise.LT.rlf) dxbyl=arise/rlf
!
! ---    Compute rate of change in strain at XBYL, times the scaled
! ---    step length dxbyl
         dtz=-dxbyl*(two*aslope*tzsq/(etabyl*(one+xbyl2)**2)) *&
              (-two*xbyl+expnli*(two*xbyl*cosln+(one-xbyl2)*sinln))
!
! ---    Compute diffusion adjustment factor due to strain in the flow
         sfcnsq=exp(two*(one-tz))
         rsq=r*r
         arg=two*dtz
         exparg=EXP(-arg)
         if(ABS(arg).GT.0.05) then
            strain=rsq+sfcnsq*(one-rsq)*(one-exparg)/arg
         else
            strain=rsq+sfcnsq*(one-rsq)
         endif
         strain=SQRT(strain)
!
      endif
!
! --- DEBUG output
      if(ldbhr) then
         write(io6,*)
         write(io6,*)'CTADJ2:    r= ',r,'  delx(m)= ',delx
         write(io6,*)'xold,yold(MGU)  = ',xold,yold
         write(io6,*)'xnew,ynew(MGU)  = ',xnew,ynew
         write(io6,*)'elold,elnew(mMSL)= ',elold,elnew,'slope= ',slope
         write(io6,*)'bvf= ',bvf,' ws= ',ws,' relief =',rlf
         write(io6,*)'eltop,elds    = ',eltop,elds
         write(io6,*)'zpold,zpnew(m)= ',zpold,zpnew
         write(io6,*)'fracz,lup     = ',fracz,lup
         write(io6,*)'eta/H= ',etabyh,' delx/L= ',dxbyl
         write(io6,*)'1/Fr= ',fri,' strat= ',strat
         write(io6,*)'xbyl= ',xbyl,'  tz= ',tz,'  dtz= ',dtz
         write(io6,*)'S^2= ',sfcnsq,'  strain= ',strain
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine slugct2(x,y,lclip,rhoci,rhocf,fracsi,fracsy,fracso,&
                         xr1,yr1,xr2,yr2,zpr,fracsr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107           SLUGCT2
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Compute receptor-specific slug height and associated
!               properties, for MCTADJ=2.
!
! --- INPUTS:
!           X,Y - real    - Receptor coordinates (m)
!                           to that at end of step, before accounting
!                           for strain during the step
!         LCLIP - logical - No extrapolation of slug properties beyond
!                           footprint during step when TRUE
!   RHOCI,RHOCF - real    - Cross-slug receptor position (m) at initial
!                           and final slug locations of the step
!         FRACSI- real    - Along-slug receptor position from young end,
!                           as fraction of the slug length, at initial
!                           slug location
!  FRACSY,FRACSO- real    - Along-step receptor position, as fraction of
!                           the step length, for the young and old ends
!                           of the slug
!
!     Common Block /CURRENT/ variables:
!        XB1, YB1, ZB1,
!        XE1, YE1, ZE1, fracz1, lup1,
!        XB2, YB2, ZB2,
!        XE2, YE2, ZE2, fracz2, lup2,
!
!     Parameters:
!        IO6
!
! --- OUTPUT:
!       XR1,YR1 - real    - Coordinates (m) of old end of slug when
!                           slug interacts with receptor
!       XR2,YR2 - real    - Coordinates (m) of young end of slug when
!                           slug interacts with receptor
!           ZPR - real    - Receptor-specific slug ht above ground (m)
!        FRACSR - real    - Along-slug receptor position from young end,
!                           as fraction of the slug length, at position
!                           where slug interacts with receptor
!
! --- SLUGCT2 called by:  SLGRECS
! --- SLUGCT2 calls:      SLGFRAC
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'current.puf'
      logical lclip, ldb
      data one/1./,zero/0.0/,small/1.0e-3/
      data ldb/.FALSE./
! --- Use the cross-slug position of the receptor at the start
! --- and end of the step to find fraction of step when slug AXIS
! --- aligns with receptor (FRACSA)
      rhodif=rhocf-rhoci
      rhomax=AMAX1(ABS(rhocf),ABS(rhoci),small)
      rhotest=ABS(rhodif)/rhomax
      if(rhotest.LT.small) then
! ---    Axis does not move enough during the step, so find
! ---    the nearest approach of old or young slug-end
! ---    (where either end might be used, choose the younger)
         if(fracsy.GE.zero .AND. fracsy.LE.one) then
! ---       Young end passes receptor during step
            fracsa=fracsy
            fracsr=zero
         elseif(fracso.GE.zero .AND. fracso.LE.one) then
! ---       Old end passes receptor, but not young end
            fracsa=fracso
            fracsr=one
         elseif(fracsi.GE.zero .AND. fracsi.LE.one) then
! ---       Slug ends bracket receptor at start of step
            fracsa=zero
            fracsr=fracsi
         elseif(fracsy.GT.one) then
            if(fracso.GE.fracsy) then
! ---          Young end approaches nearer receptor at end of step
               fracsr=zero
               fracsa=fracsy
            else
! ---          Old end approaches nearer receptor at end of step
               fracsr=one
               fracsa=fracso
            endif
         elseif(fracsy.LT.zero) then
            if(fracso.LE.fracsy) then
! ---          Young end approaches nearer receptor at start of step
               fracsr=zero
               fracsa=fracsy
            else
! ---          Old end approaches nearer receptor at start of step
               fracsr=one
               fracsa=fracso
            endif
         endif
      else
! ---    Axis moves so compute fraction of step when slug AXIS
! ---    aligns with receptor
         fracsa=-rhoci/rhodif
         if(LCLIP) then
            fracsa=AMIN1(one,fracsa)
            fracsa=AMAX1(zero,fracsa)
         endif
! ---    Compute slug-end locations at FRACSA of step
         xr1=xb1+fracsa*(xe1-xb1)
         yr1=yb1+fracsa*(ye1-yb1)
         xr2=xb2+fracsa*(xe2-xb2)
         yr2=yb2+fracsa*(ye2-yb2)
! ---    Compute fractional along-slug distance to receptor from
! ---    young end (FRACSR), evaluated at FRACSA
         call SLGFRAC(x,y,xr1,yr1,xr2,yr2,rhoar,rhocr,fracsr,&
                      d12,d12i)
         if(fracsr.LT.zero) then
! ---       Use slug height at "young" end at nearest point
! ---       (reset FRACSA and FRACSR)
            fracsa=fracsy
            fracsr=zero
         elseif(fracsr.GT.one) then
! ---       Use slug height at "old" end at nearest point
! ---       (reset FRACSA and FRACSR)
            fracsa=fracso
            fracsr=one
         endif
      endif
! --- Impose clipping along transport (conditional)
      if(LCLIP) then
         fracsa=AMIN1(one,fracsa)
         fracsa=AMAX1(zero,fracsa)
      endif
! --- Compute slug-end locations at (Final) FRACSA
      xr1=xb1+fracsa*(xe1-xb1)
      yr1=yb1+fracsa*(ye1-yb1)
      xr2=xb2+fracsa*(xe2-xb2)
      yr2=yb2+fracsa*(ye2-yb2)
! --- Compute elevation of each end of slug at FRACSA
! --- Older end
      if(lup1) then
! ---    Upslope (ht may fall)
         if(fracz1.LE.zero) then
            zr1=zb1
         else
            zjump=ze1-zb1
            if(fracz1.LT.one) zjump=zjump/fracz1
            zr1=zb1+AMIN1(fracsa,fracz1)*zjump
         endif
      else
! ---    Downslope (ht may rise)
         if(fracz1.GE.one) then
            zr1=zb1
         else
            zjump=ze1-zb1
            if(fracz1.GT.zero) zjump=zjump/(one-fracz1)
            zr1=zb1+AMAX1(zero,(fracsa-fracz1))*zjump
         endif
      endif
! --- Younger end
      if(lup2) then
! ---    Upslope (ht may fall)
         if(fracz2.LE.zero) then
            zr2=zb2
         else
            zjump=ze2-zb2
            if(fracz2.LT.one) zjump=zjump/fracz2
            zr2=zb2+AMIN1(fracsa,fracz2)*zjump
         endif
      else
! ---    Downslope (ht may rise)
         if(fracz2.GE.one) then
            zr2=zb2
         else
            zjump=ze2-zb2
            if(fracz2.GT.zero) zjump=zjump/(one-fracz2)
            zr2=zb2+AMAX1(zero,(fracsa-fracz2))*zjump
         endif
      endif
! --- Interpolate slug height between ends
      zpr=zr2+fracsr*(zr1-zr2)
! --- Debug output section
      if(ldb) then
         write(io6,*)'SLUGCT2:'
         write(io6,*)'zpr,zr1,zr2   = ',zpr,zr1,zr2
         write(io6,*)'fracsr,fracsa = ',fracsr,fracsa
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine puffct2(frac,xr1,yr1,zpr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107           PUFFCT2
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Compute receptor-specific puff height for MCTADJ=2.
!
! --- INPUTS:
!          FRAC - real    - Along-step receptor position, as fraction of
!                           the step length
!
!     Common Block /CURRENT/ variables:
!        XB1, YB1, ZB1,
!        XE1, YE1, ZE1, fracz1, lup1
!
!     Parameters:
!        IO6
!
! --- OUTPUT:
!       XR1,YR1 - real    - Puff coordinates (m) when puff interacts
!                           with receptor
!           ZPR - real    - Receptor-specific puff ht above ground (m)
!
! --- PUFFCT2 called by:  PUFRECS
! --- PUFFCT2 calls:      SLGFRAC
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'current.puf'
      data one/1./,zero/0.0/
! --- Compute puff location at FRAC
      xr1=xb1+frac*(xe1-xb1)
      yr1=yb1+frac*(ye1-yb1)
! --- Compute elevation at FRAC
      if(lup1) then
! ---    Upslope (ht may fall)
         if(fracz1.LE.zero) then
            zpr=zb1
         else
            zjump=ze1-zb1
            if(fracz1.LT.one) zjump=zjump/fracz1
            zpr=zb1+AMIN1(frac,fracz1)*zjump
         endif
      else
! ---    Downslope (ht may rise)
         if(fracz1.GE.one) then
            zpr=zb1
         else
            zjump=ze1-zb1
            if(fracz1.GT.zero) zjump=zjump/(one-fracz1)
            zpr=zb1+AMAX1(zero,(frac-fracz1))*zjump
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine getelev(x,y,z)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 101111           GETELEV
!
! --- PURPOSE:  Employs bilinear interpolation to obtain terrain
!               elevation at a point within the MET grid.
!
! --- UPDATE
! --- V6.301-V6.402 101111  (DGS): Update GETCELL arguments
! --- V6.3-V6.301   100827  (DGS): Allow nested grid domains that do not
!                                  align with cells in parent domain
! --- V5.0-V6.3     100212  (DGS): Add nested CALMET grids
!
! --- INPUTS:
!           X - real      - x-position (Met Grid 1 Units)
!           Y - real      - y-position (Met Grid 1 Units)
!
!       Common block /METHD/ variables:
!             ELEV(mxnx,mxny,mxmetdom), NXM(mxmetdom), NYM(mxmetdom)
!       Common block /GRID/ variables:
!             nx,ny
!       Common block /GRIDNEST/ variables:
!             NESTFAC(mxmetdom), RNXNEST0(mxmetdom), RNYNEST0(mxmetdom)
!       Parameters:
!             mxnx, mxny, mxmetdom
!
! --- OUTPUT:
!           Z - real      - Terrain elevation (m MSL)
!
! --- GETELEV called by: ELEVI, VCBAR, PUFRECS, CTADJ2
! --- GETELEV calls:     GETCELL
!----------------------------------------------------------------------
!  NOTE:  This routine interpolates among elevations assigned to the
!         CENTER of cells in the MET grid, and assumes that the
!         terrain varies linearly between adjacent CENTERS.
!         Interpolation is done within a single MET grid.
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'methd.puf'
      include 'grid.puf'
      include 'gridnest.puf'
      data zero/0.0/
! --- Get the MET grid domain of the cell that contains this point
      call GETCELL('GETELEV     ','GRIDIJ',1,x,y,zero,zero,i,j,md)
! --- Trap case of single grid-cell
      if(nxm(md).eq.1 .AND. nym(md).eq.1) then
         z=elev(1,1,md)
         return
      endif
! --- Compute point coordinates in MET grid md coordinates
      xmd=x*nestfac(md)-rnxnest0(md)
      ymd=y*nestfac(md)-rnynest0(md)
! --- Compute cell x-index of nearest lower left grid-point
! --- (ll denotes lower left-grid point)
      if(nxm(md).eq.1) then
         ixll=1
         ixllp1=1
      else
         ixll=(xmd+0.5)
! ---    Shift index if needed to stay within grid
         nxm1=nxm(md)-1
         ixll=max0(ixll,1)
         ixll=min0(ixll,nxm1)
! ---    Other corners
         ixllp1=ixll+1
      endif
!
! --- Compute cell y-index of nearest lower left grid-point
! --- (ll denotes lower left-grid point)
      if(nym(md).eq.1) then
         iyll=1
         iyllp1=1
      else
         iyll=(ymd+0.5)
! ---    Shift index if needed to stay within grid
         nym1=nym(md)-1
         iyll=max0(iyll,1)
         iyll=min0(iyll,nym1)
! ---    Other corners
         iyllp1=iyll+1
      endif
!
! --- Position of point relative to the ll grid-point (grid units)
      t=(xmd-(ixll-0.5))
      onemt=1.-t
      u=(ymd-(iyll-0.5))
      onemu=1.-u
! -- Interpolated value
      z=onemt*onemu*elev(ixll,iyll,md)&
        +t*onemu*elev(ixllp1,iyll,md)&
        +t*u*elev(ixllp1,iyllp1,md)&
        +u*onemt*elev(ixll,iyllp1,md)
      return
      end
!----------------------------------------------------------------------
      subroutine trelief(ldb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212           TRELIEF
!                D. Strimaitis
!
! --- PURPOSE:  Compute local relief of the terrain for each cell in
!               the meteorological grid, and also the peak elevation
!               associated with this relief.  The relief height and
!               peak terrain elevation are used for MCTADJ = 2
!
! --- UPDATE
! --- V5.0-V6.3     100212  (DGS): Add nested CALMET grids
!
! --- INPUTS:
!         LDB - logical   - Debug write logical
!
!       Common block /GRIDNEST/ variables:
!             NGRID
!       Common block /METHD/ variables:
!             ELEV(MXNX,MXNY,MXMETDOM), NXM(MXMETDOM), NYM(MXMETDOM)
!       Parameters:
!             IO6,MXNX,MXNY,MXMETDOM
!
! --- OUTPUT:
!       Common block /GRID/ variables:
!             RELIEF(5,mxnx,mxny,mxmetdom)
!
! --- TRELIEF called by:  SETUP
! --- TRELIEF calls:      TERAVG
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'grid.puf'
      include 'gridnest.puf'
      include 'methd.puf'
      logical ldb
      data zero/0.0/, one/1.0/, beta/1.0/
! --- BETA is the criterion for identifying the "end-point" of the
! --- height profile associated with a cell; this test is applied to
! --- the ratio of the slope for the current position to the original
! --- slope, so that if the ratio is outside the range
! --- BETA to 1+BETA, the segment of the profile is completed
! --- Loop over MET grid domains
      do md=1,ngrid
! ---    Loop over each cell in the grid
! ---------------------------------------
         do j=1,nym(md)
         do i=1,nxm(md)
! ---       Initial peak elevation equals cell elevation
            rise=zero
! ---       Process 4 orientations (1:N/S, 2:NE/SW, 3:E/W, 4:SE/NW)
! ---       N/S
! ---------------
! ---       Direction of Increasing y-cell index
! ---       Reliefi > 0 if terrain increases in this direction
            reliefi=zero
            if(j.LT.nym(md)) then
               delh1=elev(i,j+1,md)-elev(i,j,md)
               reliefi=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i,j+1,md)
                  do jj=j+1,nym(md)-1,1
                     elavg0=elavg
                     call TERAVG(ldb,i,j,i,jj+1,md,elavg)
                     delh=elavg-elavg0
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefi=reliefi+delh
                     else
                        goto 110
                     endif
                  enddo
               endif
            endif
110         continue
! ---       Direction of Decreasing y-cell index
! ---       Reliefd > 0 if terrain decreases in this direction
            reliefd=zero
            if(j.GT.1) then
               delh1=-(elev(i,j-1,md)-elev(i,j,md))
               reliefd=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i,j-1,md)
                  do jj=j-1,2,-1
                     elavg0=elavg
                     call TERAVG(ldb,i,j,i,jj-1,md,elavg)
                     delh=-(elavg-elavg0)
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefd=reliefd+delh
                     else
                        goto 120
                     endif
                  enddo
               endif
            endif
120         continue
            rplus=ABS(reliefi+reliefd)
            rminus=ABS(reliefi-reliefd)
            if(rplus.GT.rminus) then
               relief(1,i,j,md)=rplus
            else
               relief(1,i,j,md)=AMAX1(ABS(reliefi),ABS(reliefd))
            endif
            rise=AMAX1(rise,reliefi)
            rise=AMAX1(rise,-reliefd)
! ---       NE/SW
! ---------------
! ---       Direction of Increasing x,y-cell index
! ---       Reliefi > 0 if terrain increases in this direction
            reliefi=zero
            if(j.LT.nym(md) .AND. i.LT.nxm(md)) then
               delh1=elev(i+1,j+1,md)-elev(i,j,md)
               reliefi=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i+1,j+1,md)
! ---             Limit do-loop range using index nearest edge of grid
                  nxy=MIN0((nxm(md)-i),(nym(md)-j))
                  do k=1,nxy-1
                     ii=i+k
                     jj=j+k
                     elavg0=elavg
                     call TERAVG(ldb,i,j,ii+1,jj+1,md,elavg)
                     delh=elavg-elavg0
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefi=reliefi+delh
                     else
                        goto 210
                     endif
                  enddo
               endif
            endif
210         continue
! ---       Direction of Decreasing x,y-cell index
! ---       Reliefd > 0 if terrain decreases in this direction
            reliefd=zero
            if(j.GT.1 .AND. i.GT.1) then
               delh1=-(elev(i-1,j-1,md)-elev(i,j,md))
               reliefd=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i-1,j-1,md)
! ---             Limit do-loop range using index nearest edge of grid
                  nxy=MIN0(i,j)-1
                  do k=1,nxy-1
                     ii=i-k
                     jj=j-k
                     elavg0=elavg
                     call TERAVG(ldb,i,j,ii-1,jj-1,md,elavg)
                     delh=-(elavg-elavg0)
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefd=reliefd+delh
                     else
                        goto 220
                     endif
                  enddo
               endif
            endif
220         continue
            rplus=ABS(reliefi+reliefd)
            rminus=ABS(reliefi-reliefd)
            if(rplus.GT.rminus) then
               relief(2,i,j,md)=rplus
            else
               relief(2,i,j,md)=AMAX1(ABS(reliefi),ABS(reliefd))
            endif
            rise=AMAX1(rise,reliefi)
            rise=AMAX1(rise,-reliefd)
! ---       E/W
! ---------------
! ---       Direction of Increasing x-cell index
! ---       Reliefi > 0 if terrain increases in this direction
            reliefi=zero
            if(i.LT.nxm(md)) then
               delh1=elev(i+1,j,md)-elev(i,j,md)
               reliefi=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i+1,j,md)
                  do ii=i+1,nxm(md)-1,1
                     elavg0=elavg
                     call TERAVG(ldb,i,j,ii+1,j,md,elavg)
                     delh=elavg-elavg0
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefi=reliefi+delh
                     else
                        goto 310
                     endif
                  enddo
               endif
            endif
310         continue
! ---       Direction of Decreasing x-cell index
! ---       Reliefd > 0 if terrain decreases in this direction
            reliefd=zero
            if(i.GT.1) then
               delh1=-(elev(i-1,j,md)-elev(i,j,md))
               reliefd=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i-1,j,md)
                  do ii=i-1,2,-1
                     elavg0=elavg
                     call TERAVG(ldb,i,j,ii-1,j,md,elavg)
                     delh=-(elavg-elavg0)
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefd=reliefd+delh
                     else
                        goto 320
                     endif
                  enddo
               endif
            endif
320         continue
            rplus=ABS(reliefi+reliefd)
            rminus=ABS(reliefi-reliefd)
            if(rplus.GT.rminus) then
               relief(3,i,j,md)=rplus
            else
               relief(3,i,j,md)=AMAX1(ABS(reliefi),ABS(reliefd))
            endif
            rise=AMAX1(rise,reliefi)
            rise=AMAX1(rise,-reliefd)
! ---       SE/NW
! ---------------
! ---       Direction of Increasing x, decreasing y-cell index
! ---       Reliefi > 0 if terrain increases in this direction
            reliefi=zero
            if(j.GT.1 .AND. i.LT.nxm(md)) then
               delh1=elev(i+1,j-1,md)-elev(i,j,md)
               reliefi=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i+1,j-1,md)
! ---             Limit do-loop range using index nearest edge of grid
                  nxy=MIN0((nxm(md)-i),(j-1))
                  do k=1,nxy-1
                     ii=i+k
                     jj=j-k
                     elavg0=elavg
                     call TERAVG(ldb,i,j,ii+1,jj-1,md,elavg)
                     delh=elavg-elavg0
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefi=reliefi+delh
                     else
                        goto 410
                     endif
                  enddo
               endif
            endif
410         continue
! ---       Direction of Decreasing x, increasing y-cell index
! ---       Reliefd > 0 if terrain decreases in this direction
            reliefd=zero
            if(j.LT.nym(md) .AND. i.GT.1) then
               delh1=-(elev(i-1,j+1,md)-elev(i,j,md))
               reliefd=delh1
               if(ABS(delh1).GT.zero) then
                  elavg=elev(i-1,j+1,md)
! ---             Limit do-loop range using index nearest edge of grid
                  nxy=MIN0((i-1),(nym(md)-j))
                  do k=1,nxy-1
                     ii=i-k
                     jj=j+k
                     elavg0=elavg
                     call TERAVG(ldb,i,j,ii-1,jj+1,md,elavg)
                     delh=-(elavg-elavg0)
                     rr=ABS(delh/delh1-one)
                     if(rr.LE.beta) then
                        reliefd=reliefd+delh
                     else
                        goto 420
                     endif
                  enddo
               endif
            endif
420         continue
            rplus=ABS(reliefi+reliefd)
            rminus=ABS(reliefi-reliefd)
            if(rplus.GT.rminus) then
               relief(4,i,j,md)=rplus
            else
               relief(4,i,j,md)=AMAX1(ABS(reliefi),ABS(reliefd))
            endif
            rise=AMAX1(rise,reliefi)
            rise=AMAX1(rise,-reliefd)
! ---       Identify peak elevation near this cell
            relief(5,i,j,md)=elev(i,j,md)+rise
         enddo
         enddo
         if(ldb) then
! ---       Write out a subset of the terrain difference fields
            write(io6,*)
            write(io6,*)'TRELIEF: terrain near center of met grid'
            write(io6,*)'         i, j, RELIEF(N/NE/E/SE), PEAK in m ='
            i0=nxm(md)/2
            do j=nym(md),1,-1
!            do i=i0,i0+1
               i=i0
               write(io6,'(2i4,5f10.1)')i,j,(relief(k,i,j,md),k=1,5)
!            enddo
            enddo
            ibeg=MAX0(1,i0-10)
            iend=MIN0(nxm(md),ibeg+19)
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'TRELIEF: PEAK in local RELIEF'
            write(io6,*)'E-W Cell Index  -----'
            write(io6,'(5x,20i5)') (ii,ii=ibeg,iend)
            write(io6,*)
            do jj=nym(md),1,-1
              write(io6,'(i3,2x,20f5.0)')jj,(relief(5,ii,jj,md),&
                    ii=ibeg,iend)
            enddo
            write(io6,*)
         endif
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine teravg(ldb,i0,j0,i,j,md,elavg)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            TERAVG
!                D. Strimaitis
!
! --- PURPOSE:  Compute average elevation of a strip of cells containing
!               cell (i,j), which lie across a 45 degree sector centered
!               on the direction from cell (i0,j0) to cell (i,j).
!
! --- UPDATE
! --- V5.0-V6.3     100212  (DGS): Add nested CALMET grids
!
! --- INPUTS:
!         LDB - logical   - Debug write logical
!       I0,J0 - integer   - Cell at origin of sector
!         I,J - integer   - Cell at center of sector direction
!          MD - integer   - MET grid domain for [I,J,I0,J0]
!
!       Common block /METHD/ variables:
!             ELEV(MXNX,MXNY,MXMETDOM), NXM(MXMETDOM), NXM(MXMETDOM)
!       Parameters:
!             IO6,MXNX,MXNY,MXMETDOM
!
! --- OUTPUT:
!       ELAVG - real      - Average elevation in strip of cells which
!                           is perpendicular to sector direction, and
!                           which includes cell (i,j)
!
! --- TERAVG called by:  TRELIEF
! --- TERAVG calls:      none
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'methd.puf'
      logical ldb
      data zero/0.0/, tan225/0.4142136/
      if(i0.EQ.i) then
! ---    Process N/S sector orientation
! ---    Select E/W strip of cells that lie in 45 degree sector
         jdel=IABS(j-j0)
         idel=INT(FLOAT(jdel)*tan225)
         ixmin=MAX0(1,i-idel)
         ixmax=MIN0(nxm(md),i+idel)
         nsum=0
         tsum=zero
         do ix=ixmin,ixmax
            nsum=nsum+1
            tsum=tsum+elev(ix,j,md)
         enddo
         elavg=tsum/nsum
      elseif(j0.EQ.j) then
! ---    Process E/W sector orientation
! ---    Select N/S strip of cells that lie in 45 degree sector
         idel=IABS(i-i0)
         jdel=INT(FLOAT(idel)*tan225)
         jymin=MAX0(1,j-jdel)
         jymax=MIN0(nym(md),j+jdel)
         nsum=0
         tsum=zero
         do jy=jymin,jymax
            nsum=nsum+1
            tsum=tsum+elev(i,jy,md)
         enddo
         elavg=tsum/nsum
      elseif((j-j0).EQ.(i-i0)) then
! ---    Process NE/SW sector orientation
! ---    Select NW/SE strip of cells that lie in 45 degree sector
         kdiag=IABS(i-i0)
         kdiag=INT(FLOAT(kdiag)*tan225)
         jymin=MAX0(1,j-kdiag)
         jymax=MIN0(nym(md),j+kdiag)
         ixmin=MAX0(1,i-kdiag)
         ixmax=MIN0(nxm(md),i+kdiag)
! ---    Set edge of valid domain
         kleft=-MIN0((i-ixmin),(jymax-j))
         krght=MIN0((ixmax-i),(j-jymin))
! ---    Sum the elevations in the strip
         nsum=zero
         tsum=zero
         do k=kleft,krght
            ii=i+k
            jj=j-k
            nsum=nsum+1
            tsum=tsum+elev(ii,jj,md)
         enddo
! ---    ADD half-step cells (one half-step further along direction)
         nx2=2*nxm(md)
         ny2=2*nym(md)
         i02=2*i0
! ---    Increment i2,j2 one "half-cell" along direction
         i2=2*i+1
         j2=2*j+1
         if(i.LT.i0) i2=i2-2
         if(j.LT.j0) j2=j2-2
         kdiag=IABS(i2-i02)
         kdiag=INT(FLOAT(kdiag)*tan225)
         jymin=MAX0(2,j2-kdiag)
         jymax=MIN0(ny2,j2+kdiag)
         ixmin=MAX0(2,i2-kdiag)
         ixmax=MIN0(nx2,i2+kdiag)
! ---    Set edge of valid domain
         kleft=-MIN0((i2-ixmin),(jymax-j2))
         krght=MIN0((ixmax-i2),(j2-jymin))
! ---    Sum the elevations in the strip
         do k=kleft,krght
            ii=i2+k
            jj=j2-k
! ---       Process only full-cell positions
            if(MOD(ii,2).EQ.0 .AND. MOD(jj,2).EQ.0) then
               ii=ii/2
               jj=jj/2
               nsum=nsum+1
               tsum=tsum+elev(ii,jj,md)
            endif
         enddo
! ---    Average the elevations in the strip
         elavg=tsum/nsum
      elseif((j-j0).EQ.-(i-i0)) then
! ---    Process NW/SE orientation
! ---    Select SW/NE strip of cells that lie in 45 degree sector
         kdiag=IABS(i-i0)
         kdiag=INT(FLOAT(kdiag)*tan225)
         jymin=MAX0(1,j-kdiag)
         jymax=MIN0(nym(md),j+kdiag)
         ixmin=MAX0(1,i-kdiag)
         ixmax=MIN0(nxm(md),i+kdiag)
! ---    Set edge of valid domain
         kleft=-MIN0((i-ixmin),(j-jymin))
         krght=MIN0((ixmax-i),(jymax-j))
! ---    Sum the elevations in the strip
         nsum=zero
         tsum=zero
         do k=kleft,krght
            ii=i+k
            jj=j+k
            nsum=nsum+1
            tsum=tsum+elev(ii,jj,md)
         enddo
! ---    ADD half-step cells (one half-step further along direction)
         nx2=2*nxm(md)
         ny2=2*nym(md)
         i02=2*i0
! ---    Increment i2,j2 one "half-cell" along direction
         i2=2*i+1
         j2=2*j+1
         if(i.LT.i0) i2=i2-2
         if(j.LT.j0) j2=j2-2
         kdiag=IABS(i2-i02)
         kdiag=INT(FLOAT(kdiag)*tan225)
         jymin=MAX0(2,j2-kdiag)
         jymax=MIN0(ny2,j2+kdiag)
         ixmin=MAX0(2,i2-kdiag)
         ixmax=MIN0(nx2,i2+kdiag)
! ---    Set edge of valid domain
         kleft=-MIN0((i2-ixmin),(j2-jymin))
         krght=MIN0((ixmax-i2),(jymax-j2))
! ---    Sum the elevations in the strip
         do k=kleft,krght
            ii=i2+k
            jj=j2+k
! ---       Process only full-cell positions
            if(MOD(ii,2).EQ.0 .AND. MOD(jj,2).EQ.0) then
               ii=ii/2
               jj=jj/2
               nsum=nsum+1
               tsum=tsum+elev(ii,jj,md)
            endif
         enddo
! ---    Average the elevations in the strip
         elavg=tsum/nsum
      endif
! --- Debug Output Section
      if(LDB) then
         if(i.EQ.j .AND. (j.LE.3 .OR. j.GE.(nym(md)-2))) then
            write(io6,*)'TERAVG:    i0,j0,i,j= ',i0,j0,i,j
            write(io6,*)'          MET Domain= ',md
            write(io6,*)'          elavg,nsum= ',elavg,nsum
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine walltrap(ldb,syb,sye,z,xb,yb,xe,ye,bvf,ws,itrap)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 101111          WALLTRAP
!                D. Strimaitis
!
! --- PURPOSE:  Compute local valley width at height "z" above cell
!               elevation for position at beginning and end of step;
!               then limit growth of sigma-y during step if valley
!               width is small enough (used for MCTADJ = 2)
!
! --- UPDATE
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
! --- V5.0-V6.3     100212  (DGS): Add nested CALMET grids
!
! --- INPUTS:
!         LDB - logical   - Debug write logical
!     SYB,SYE - real      - Sigma-y at beginning and end of step (m)
!           Z - real      - Puff height above ground (m)
!       XB,YB - real      - Beginning puff position (Met Grid Units)
!       XE,YE - real      - Ending puff position (Met Grid Units)
!         BVF - real      - Brunt-Vaisala Frequency (1/s)
!          WS - real      - Wind Speed (m/s)
!
!       Common block /GRID/ variables:
!             RELIEF(5,mxnx,mxny,mxmetdom)
!             IVALW(mxvalz,mxnx,mxny,mxmetdom), DZVAL
!       Common block /METHD/ variables:
!             ELEV(MXNX,MXNY,MXMETDOM), XGRIDM(MXMETDOM)
!       Parameters:
!             IO6,MXNX,MXNY,MXVALZ,MXMETDOM
!
! --- OUTPUT:
!         SYE - real      - Sigma-y at end of step (m)    [revised]
!       ITRAP - integer   - Flag denotes when SYE changes
!                           (0:  No Change;  1:  Change)
!
! --- WALLTRAP called by:  SETPUF, SETSLG
! --- WALLTRAP calls:      GETCELL
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'grid.puf'
      include 'methd.puf'
      logical ldb
      data zero/0.0/, one/1.0/
! --- Criterion for trapping is ratio RCRIT=sigma-y/L at which area
! --- under the Gaussian curve equals area of top-hat of width L, with
! --- the same peak concentration
      data rcrit/0.4/
! --- Initialize data
      itrap=0
      syeold=sye
! --- No Trapping if puff is above the dividing streamline
      if(bvf.LE.zero) goto 500
! --- Set met cell location for old and new puff locations
      call GETCELL('WALLTRAP_1  ','GRIDIJ',1,xb,yb,zero,zero,&
                   ixb,jyb,mdb)
      call GETCELL('WALLTRAP_2  ','GRIDIJ',1,xe,ye,zero,zero,&
                   ixe,jye,mde)
! --- Determine dividing-streamline elevation (m MSL)
! --- Set peak terrain elevation near this step
      eltop=AMAX1(relief(5,ixb,jyb,mdb),relief(5,ixe,jye,mde))
      elds=eltop-ws/bvf
! --- Set minimum puff elevation (m MSL)
      elpuff=AMIN1(elev(ixb,jyb,mdb),elev(ixe,jye,mde))+z
      if(elpuff.LE.elds) then
! ---    Perform trapping calculations
! ------------------------------------
! ---    Compute valley widths at "z"
         zgrid=z/dzval
         izbot=INT(zgrid)
         if(izbot.EQ.0) then
            izbot=1
            dzfac=zero
         else
            dzfac=zgrid-FLOAT(izbot)
         endif
         iztop=izbot+1
! ---    No trapping if puff is above valley
         if(izbot.GE.mxvalz) goto 500
! ---    No trapping if puff not in valley at either beginning
! ---    or end of step
         if(ivalw(iztop,ixb,jyb,mdb).EQ.-1) goto 500
         if(ivalw(iztop,ixe,jye,mde).EQ.-1) goto 500
         if(ivalw(izbot,ixb,jyb,mdb).EQ.-1) goto 500
         if(ivalw(izbot,ixe,jye,mde).EQ.-1) goto 500
! ---    Interpolate valley width(m) at beginning and end
         wb=xgridm(mdb)*((one-dzfac)*FLOAT(ivalw(izbot,ixb,jyb,mdb))+&
                               dzfac*FLOAT(ivalw(iztop,ixb,jyb,mdb)))
         we=xgridm(mdb)*((one-dzfac)*FLOAT(ivalw(izbot,ixe,jye,mde))+&
                               dzfac*FLOAT(ivalw(iztop,ixe,jye,mde)))
! ---    Check for trapping criterion at ends of step
         rb=syb/wb
         re=sye/we
         if(AMAX1(rb,re).LE.rcrit) then
! ---       No trapping within step
            goto 500
         elseif(AMIN1(rb,re).GE.rcrit) then
! ---       Full trapping within step
            itrap=1
            f=zero
            frac=-999
            sye=syb
            goto 500
         else
! ---       Trapping for only part of step
! ---       Assume linear changes over step, and compute fraction of
! ---       step at which RCRIT criterion is met
            frac=AMAX1(zero,(syb-rcrit*wb)/((we-wb)*rcrit-(sye-syb)))
            itrap=1
            if(re.GE.rcrit) then
               f=frac
            elseif(rb.GE.rcrit) then
               f=one-frac
            endif
            sye=syb+f*(sye-syb)
         endif
      endif
! --- Report DEBUG information
500   if(LDB .AND. itrap.EQ.1) then
         write(io6,*)
         write(io6,*)'WALLTRAP:      z,bvf,ws = ',z,bvf,ws
         write(io6,*)'ixb,jyb,mdb,ixe,jye,mde = ',ixb,jyb,mdb,&
                                                  ixe,jye,mde
         write(io6,*)'         Valley  wb, we = ', wb, we
         write(io6,*)'           syb,sye(old) = ',syb,syeold
         write(io6,*)'      f, frac ,sye(new) = ',f,frac,sye
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine vwidth(ldb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            VWIDTH
!                D. Strimaitis
!
! --- PURPOSE:  Compute local valley width in met grid cell units
!               as a function of height at each cell
!
! --- UPDATES
! --- V5.4-V6.3    100212  (DGS): Add nested CALMET grids
! --- V5.2-V5.4    000602  (DGS): add message to "stop"
! --- V5.1-V5.2    991104  (JSS): Error messages written to list
!                                 file as well as to screen
! --- V5.0  971107-980304  (DGS): replace NX with NY in range check for
!                                 y-indices (bug)
!                          (DGS): change AMAX1(int) to MAX0(int) etc.
!
! --- INPUTS:
!         LDB - logical   - Debug write logical
!
!       Common block /GRIDNEST/ variables:
!             NGRID
!       Common block /METHD/ variables:
!             ELEV(MXNX,MXNY,MXMETDOM),NX(MXMETDOM),NY(MXMETDOM)
!       Parameters:
!             IO6,MXNX,MXNY,MXMETDOM
!
! --- OUTPUT:
!       Common block /GRID/ variables:
!             IVALW(mxvalz,mxnx,mxny,mxmetdom), DZVAL
!
! --- VWIDTH called by:  SETUP
! --- VWIDTH calls:      none
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      include 'grid.puf'
      include 'gridnest.puf'
      include 'methd.puf'
      logical ldb,lwidth
      data big/9000./
      data dzmin/10./, qahit/0.5/
! --- DZMIN is the minimum delta-z used in resolving width as a
! --- function of height
! --- QAHIT if min fraction of cells in a strip that constitutes a wall
! --- Loop over MET grid domains
      do md=1,ngrid
! --- Initialize widths, and search elevations for max/min values (m)
         zmax=-big
         zmin=big
         do j=1,nym(md)
         do i=1,nxm(md)
            if(elev(i,j,md).GT.zmax) zmax=elev(i,j,md)
            if(elev(i,j,md).LT.zmin) zmin=elev(i,j,md)
            do k=1,mxvalz
               ivalw(k,i,j,md)=-1
            enddo
         enddo
         enddo
! ---    Set initial height interval
         dzval=(zmax-zmin)/FLOAT(mxvalz)
1        dzval=AMAX1(dzmin,dzval)
! ---    Loop over heights above the cell elevation
         do k=1,mxvalz
! ---       Did the valley disappear too soon?  --- Increase resolution
            if(k.GT.1 .AND. .NOT.lwidth .AND. dzval.GT.dzmin) then
! ---          Recalculate the height increment and start over
               dzval=delz/FLOAT(mxvalz)
               do j0=1,nym(md)
               do i0=1,nxm(md)
                  do k0=1,mxvalz
                     ivalw(k0,i0,j0,md)=-1
                  enddo
               enddo
               enddo
               goto 1
            endif
            delz=k*dzval
            lwidth=.FALSE.
! ---       Loop over each cell in the grid
! ------------------------------------------
            do j=1,nym(md)
            do i=1,nxm(md)
! ---          Do not process this height for this cell if no valley
! ---          width had been found at the previous height
               if(k.GT.1) then
                  if(ivalw(k-1,i,j,md).EQ.-1) goto 500
               endif
! ---          Set target elevation for cell (i,j)
               ztarget=elev(i,j,md)+delz
! ---          Find # cells from (i,j) to terrain that exceeds target
! ---         in each of 4 quadrants (N, S, E, W)
               lookNS=-1
               lookEW=-1
! ---          N
! ---------------
! ---          Direction of Increasing y-cell index
! ---          March in cell-steps to North of cell (i,j)
               do jy=j+1,nym(md)
                  jdel=jy-j
! ---             Look at E-W strip within 90 degree sector about N
                  ixmin=MAX0(1,i-jdel)
                  ixmax=MIN0(nxm(md),i+jdel)
! !!!             iqa=NINT(FLOAT(ixmax-ixmin)*qahit)
                  iqa=NINT(FLOAT(ixmax-ixmin)*qahit)+1
                  ihit=0
                  do ix=ixmin,ixmax
                     if(elev(ix,jy,md).GE.ztarget) then
                        ihit=ihit+1
                        if(ihit.EQ.iqa) then
                           lookNS=jy
                           goto 100
                        endif
                     endif
                  enddo
               enddo
100            continue
! ---          If target elevation was not found to the north, skip the
! ---          look to the south for the "other side" of the valley
               if(lookNS.LT.0) goto 200
! ---          S
! ---------------
! ---          Direction of Decreasing y-cell index
! ---          March in cell-steps to South of cell (i,j)
               do jy=j-1,1,-1
                  jdel=j-jy
! ---             Look at E-W strip within 90 degree sector about S
                  ixmin=MAX0(1,i-jdel)
                  ixmax=MIN0(nxm(md),i+jdel)
! !!!             iqa=NINT(FLOAT(ixmax-ixmin)*qahit)
                  iqa=NINT(FLOAT(ixmax-ixmin)*qahit)+1
                  ihit=0
                  do ix=ixmin,ixmax
                     if(elev(ix,jy,md).GE.ztarget) then
                        ihit=ihit+1
                        if(ihit.EQ.iqa) then
                           lookNS=lookNS-jy
                           goto 200
                        endif
                     endif
                  enddo
               enddo
! ---          Reset to "null" value
               lookNS=-1
200            continue
! ---          E
! ---------------
! ---          Direction of Increasing x-cell index
! ---          March in cell-steps to East of cell (i,j)
               do ix=i+1,nxm(md)
                  idel=ix-i
! ---             Look at N-S strip within 90 degree sector about E
                  jymin=MAX0(1,j-idel)
                  jymax=MIN0(nym(md),j+idel)
! !!!             iqa=NINT(FLOAT(jymax-jymin)*qahit)
                  iqa=NINT(FLOAT(jymax-jymin)*qahit)+1
                  ihit=0
                  do jy=jymin,jymax
                     if(elev(ix,jy,md).GE.ztarget) then
                        ihit=ihit+1
                        if(ihit.EQ.iqa) then
                           lookEW=ix
                           goto 300
                        endif
                     endif
                  enddo
               enddo
300            continue
! ---          If target elevation was not found to the east, skip
! ---          look to the west for the "other side" of the valley;
               if(lookEW.LT.0) goto 400
! ---          W
! ---------------
! ---          Direction of Decreasing x-cell index
! ---          March in cell-steps to West of cell (i,j)
               do ix=i-1,1,-1
                  idel=i-ix
! ---             Look at N-S strip within 90 degree sector about W
                  jymin=MAX0(1,j-idel)
                  jymax=MIN0(nym(md),j+idel)
! !!!             iqa=NINT(FLOAT(jymax-jymin)*qahit)
                  iqa=NINT(FLOAT(jymax-jymin)*qahit)+1
                  ihit=0
                     do jy=jymin,jymax
                     if(elev(ix,jy,md).GE.ztarget) then
                        ihit=ihit+1
                        if(ihit.EQ.iqa) then
                           lookEW=lookEW-ix
                           goto 400
                        endif
                     endif
                  enddo
               enddo
! ---          Reset to "null" value
               lookEW=-1
400            continue
! ---          Set cross-valley width at this elevation, for this cell,
! ---          equal to the minimum NS or EW span
               itest=lookNS*lookEW
               if(itest.LT.0) then
! ---             Only one of the directions produced a valley width, so
! ---             take the maximum to select the valid width
                  ivalw(k,i,j,md)=MAX0(lookNS,lookEW)
               else
! ---             Both are valid or both are invalid, so take the minimum
                  ivalw(k,i,j,md)=MIN0(lookNS,lookEW)
               endif
               if(ivalw(k,i,j,md).GT.1) lwidth=.TRUE.
! ---          Test width
               if(ivalw(k,i,j,md).NE.-1 .AND. ivalw(k,i,j,md).LT.2)then
                  write(io6,*) 'FATAL Problem in VWIDTH'
                  write(io6,*) '-- invalid valley width in MET grid ',md
                  write(io6,*) '-- i,j,k,width = ',i,j,k,ivalw(k,i,j,md)
                  write(*,*)
                  stop 'Halted in VWIDTH -- see list file.'
               endif
500            continue
            enddo
            enddo
! ---    End Loop over heights
         enddo
         if(ldb) then
! ---       Write out key setup info
            write(io6,*)
            write(io6,*)'VWIDTH: Information for MET grid: ',md
            write(io6,*)'         zmax,zmin = ',zmax,zmin
            write(io6,*)'      mxvalz,dzval = ',mxvalz,dzval
            do k=1,mxvalz
! ---          Write out a subset of the valley width fields
               delz=dzval*k
               i0=nxm(md)/2
               ibeg=MAX0(1,i0-10)
               iend=MIN0(nxm(md),ibeg+19)
               write(io6,*)
               write(io6,*)
               write(io6,*)'VWIDTH: Valley Width(cells) at Height(m) ',&
                            delz
               write(io6,*)'E-W Cell Index  -----'
               write(io6,'(5x,20i4)') (ii,ii=ibeg,iend)
               write(io6,*)
               do jj=nym(md),1,-1
                 write(io6,'(i3,2x,20i4)') jj,(ivalw(k,ii,jj,md),&
                                           ii=ibeg,iend)
               enddo
            enddo
         endif
! --- End loop over MET grid domains
      enddo
      return
      end
!-----------------------------------------------------------------------
      subroutine axis(ht,aax,bax)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 940831               AXIS
! ---            D. Strimaitis, SRC
!
!  PURPOSE:     Given a contour height above the grid-plane,
!               subroutine returns the major and minor axis lengths
!               of the hill.
!
!  ARGUMENTS:
!     PASSED:   ht              contour height above grid-plane (m)  [r]
!   RETURNED:   aax,bax         major, minor axis lengths (m)        [r]
!
!  CALLING ROUTINES:    CTPAR
!
!  EXTERNAL ROUTINES:   none
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      real ax(2)
      if(ht .LE. small) then
         aax=axmax(1,ih)
         bax=axmax(2,ih)
!  Restrict axis lengths so that defined major axis never becomes less
!  than defined minor axis
      if(bax .GT. aax) bax=aax
         return
      endif
!  Scale height of contour by the relief height of the hill
      htbyh=ht/h
      do 10 i=1,2
        ax(i)=scale(i,ih)*((one-htbyh)/(htbyh+&
             (scale(i,ih)/axmax(i,ih))**expo(i,ih)))**(one/expo(i,ih))
10    continue
      aax=ax(1)
      bax=ax(2)
!  Restrict axis lengths so that defined major axis never becomes less
!  than defined minor axis
      if(bax .GT. aax) bax=aax
      return
      end
!-----------------------------------------------------------------------
      subroutine bi(afac,argm,argp,b)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015                 BI
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes the quantity B(E,Eo) for the special case in
!               which sigma-z is virtually constant
!
! ARGUMENTS:
!    PASSED:    afac            1/(2*sz**2)  (m**-2)                 [r]
!               argm            Eo-length  (m)                       [r]
!               argp            Eo+length  (m)                       [r]
!  RETURNED:    b               B(E,Eo)                              [r]
!
! CALLING ROUTINES:     UPPER, LOWER
!
! EXTERNAL ROUTINES:
!-----------------------------------------------------------------------
      INCLUDE 'const.puf'
      exarg1=afac*argm**2
      exarg2=afac*argp**2
      term1=zero
      term2=zero
      if(exarg1 .LT. expmax) term1=EXP(-exarg1)
      if(exarg2 .LT. expmax) term2=EXP(-exarg2)
      b=two*(term1+term2)
      return
      end
!-----------------------------------------------------------------------
      subroutine bji(ak,dm,dp,d0,eoimh,eoiph,ej,bj)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015                BJI
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes the quantity B(E,Eo)
!
! ARGUMENTS:
!    PASSED:    ak              1/sqrt2*sze*szo*szpt  (m**-3)        [r]
!               dm              szpt**2 (Eo-Hd)  (m**3)              [r]
!               dp              szpt**2 (Eo+Hd)  (m**3)              [r]
!               d0              ak*sze**2 (zlid-Hd)  (m**3)          [r]
!               eoimh           Eo-Hd  (m)                           [r]
!               eoiph           Eo+Hd  (m)                           [r]
!               ej              E  (m)                               [r]
!  RETURNED:    bj              B(E,Eo)                              [r]
!
! CALLING ROUTINES:     UPPER
!
! EXTERNAL ROUTINES:    ERF
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      dum=half/szesq
      akd1=ak*(dm+szosq*ej)
      akd2=ak*(dm-szosq*ej)
      akd3=ak*(dp-szosq*ej)
      akd4=ak*(dp+szosq*ej)
      exarg1=dum*(ej-eoimh)*(ej-eoimh)
      exarg2=dum*(ej+eoimh)*(ej+eoimh)
      exarg3=dum*(ej+eoiph)*(ej+eoiph)
      exarg4=dum*(ej-eoiph)*(ej-eoiph)
      term1=zero
      term2=zero
      term3=zero
      term4=zero
      if(exarg1 .LT. expmax) term1=EXP(-exarg1)*(ERF(d0-akd1)+ERF(akd1))
      if(exarg2 .LT. expmax) term2=EXP(-exarg2)*(ERF(d0-akd2)+ERF(akd2))
      if(exarg3 .LT. expmax) term3=EXP(-exarg3)*(ERF(d0+akd3)-ERF(akd3))
      if(exarg4 .LT. expmax) term4=EXP(-exarg4)*(ERF(d0+akd4)-ERF(akd4))
      bj=term1+term2+term3+term4
      return
      end
!-----------------------------------------------------------------------
      subroutine bjil(ak,b1,ej,eoi,bj)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015               BJIL
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes the quantity B(E,Eo) for LOWER
!
! ARGUMENTS:
!    PASSED:    ak              1/sqrt2*sz*szo*szp  (m**-3)          [r]
!               b1              Hd*sz**2  (m**3)                     [r]
!               eoi             Eo  (m)                              [r]
!               ej              E  (m)                               [r]
!  RETURNED:    bj              B(E,Eo)                              [r]
!
! CALLING ROUTINES:     LOWER
!
! EXTERNAL ROUTINES:    ERF
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      dum=half/szsq
      b2=ej*szosq
      b3=eoi*szpsq
      exarg1=dum*(ej-eoi)*(ej-eoi)
      exarg2=dum*(ej+eoi)*(ej+eoi)
      term1=zero
      term2=zero
      arg1p=ak*(b1+b2+b3)
      arg1m=ak*(b1-b2-b3)
      arg2p=ak*(b1+b2-b3)
      arg2m=ak*(b1-b2+b3)
      if(exarg1 .LT. expmax) term1=EXP(-exarg1)*(ERF(arg1p)+ERF(arg1m))
      if(exarg2 .LT. expmax) term2=EXP(-exarg2)*(ERF(arg2p)+ERF(arg2m))
      bj=term1+term2
      return
      end
!-----------------------------------------------------------------------
      subroutine ctinit(dgrid,mhillin,nhill,hilldat,nctrec,xrct,yrct)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521             CTINIT
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Transfers data that are needed for the subgrid-scale
!               terrain option to common/CTPASS/, and initializes
!               data passed in common/CONST/.
!
! ARGUMENTS:
!    PASSED:
!               dgrid           spacing of cells in met grid (m)    [r]
!               mhillin         method for input of hill terrain    [i]
!                                     and receptor data
!                                     1 = Created by CTDM processor
!                                         programs (in CTDM format)
!                                     2 = Created by OPTHILL
!                                         processor & read in Input
!                                         Groups (6b, 6c)
!               nhill           number of subgrid-scale terrain     [i]
!                                     features
!               hilldat         hill data from control file        [ra]
!               nctrec          number of special complex terrain   [i]
!                                     receptors
!               xrct            X coordinate of complex terrain    [ra]
!                                     receptor (met. grid units)
!               yrct            Y coordinate of complex terrain    [ra]
!                                     receptor (met. grid units)
!
!  RETURNED:    (if CTDM receptor file is used)
!               xrct            X coordinate of complex terrain    [ra]
!                                     receptor (met. grid units)
!               yrct            Y coordinate of complex terrain    [ra]
!                                     receptor (met. grid units)
!
! CALLING ROUTINES:     SETUP
!
! EXTERNAL ROUTINES:    INPREC, INPTER
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
!  Dimension arrays passed through the argument list
      real hilldat(11,mxhill),xrct(mxrect),yrct(mxrect)
!  Set constants that are passed in common
      zero=0.
      one=1.
      two=2.
      three=3.
      four=4.
      half=0.5
      twoby3=two/three
      rt2=SQRT(two)
      pi=3.1415927
      rtpi=SQRT(pi)
      pii=one/pi
      rtpii=one/rtpi
      piby2=half*pi
      twopi=two*pi
      dtor=pi/180.
      small=.000001
      expmax=69.
      alphai=1.20
      hslfac=0.05
      epsint=0.01
      epsrefl=0.01
      mxrefl=50
!  Set the number of points along the trajectory to the maximum allowed
!  by the parameter MXTPTS in this implementation
      ntpts=mxtpts
!  Transfer flag to /CTPASS/ variable for type of terrain data
      mhillt=mhillin
!  Transfer hill information.  (These are placed in common/CTPASS/)
!              mhillt: method for hill data input
!                      1 = HILL.DAT file from CTDM
!                      2 = Input Subgroup 6b (OPTHILL)
      if(mhillt.eq.1) then
! ---    Read and process data from HILL.DAT (CTDM Terrain Processor)
         call INPTER
         do i=1,nhill
! ---       Change height reference from m (MSL) to m above hill base
            do inz=1,nzh(i)
               zh(inz,i)=zh(inz,i)-zgrid(i)
            enddo
         enddo
      elseif(mhillt.eq.2) then
! ---    Swap data from arrays filled from input file (Subgroup 6b)
         do i=1,nhill
! ---       Compute (xc,yc) as meters from MET grid origin
            xc(i)=dgrid*hilldat(1,i)
            yc(i)=dgrid*hilldat(2,i)
            thetah(i)=hilldat(3,i)
            zgrid(i)=hilldat(4,i)
            relief(i)=hilldat(5,i)
            expo(1,i)=hilldat(6,i)
            expo(2,i)=hilldat(7,i)
            scale(1,i)=hilldat(8,i)
            scale(2,i)=hilldat(9,i)
            axmax(1,i)=hilldat(10,i)
            axmax(2,i)=hilldat(11,i)
         enddo
      endif
!  Transfer receptor information.  (placed in common/CTPASS/)
!              mhillt: method for receptor data input
!                      1 = HILLRCT.DAT file from CTDM
!                      2 = Input Subgroup 6c
      if(mhillt.eq.1) then
! ---    Read & process data from HILLREC.DAT (CTDM Receptor Generator)
         call INPREC
! ---    Transfer receptor information to common/CTSGDAT/: change
! ---    distance units from meters to MET grid
         do i=1,nctrec
            xrct(i)= xrctm(i)/dgrid
            yrct(i)= yrctm(i)/dgrid
         enddo
      elseif(mhillt.eq.2) then
! ---    Transfer receptor information: change distance units from MET
! ---    grid to meters from MET grid origin (placed in common/CTPASS/)
         do i=1,nctrec
            xrctm(i)=dgrid*xrct(i)
            yrctm(i)=dgrid*yrct(i)
         enddo
      endif
!  Initialize variables
      ip=0
      ih=0
      tstart=0.
      return
      end
!-----------------------------------------------------------------------
      subroutine ctpar
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107              CTPAR
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes parameters for complex terrain option which
!               depend only on specification of puff and hill data.
!
!
! MODIFICATION:  1.  Changes made during EPA-sponsored upgrade to the
!                    FLOW algorithm have been made here.  This is now
!                    consistent with INPUFF 3.0 (10/93).
!
!                2.  Option added to accept hill information in CTDM
!                    format, or in original OPTHILL format, controlled
!                    by variable MHILLT:
!                               1 = CTDM format
!                               2 = OPTHILL format
!
!                3.  Modify method of computing and incorporating shear
!                    (this differs from INPUFF 3.0 (10/93)
!
! --- UPDATE
! --- V4.0-V5.0     971107  (DGS): Speed shear modification is applied
!                                  only when speed at hilltop is at
!                                  least 1/10 puff speed
!
! ARGUMENTS:    none
!
! CALLING ROUTINES:     CTSG
!
! EXTERNAL ROUTINES:    ROTATE, AXIS, KLOSE, MUNU, IREG, HILROT, SPEED,
!                       PATH, XINTRP
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      INCLUDE 'flvar.puf'
      real mu,nu
!  Define parameters used in CTDM flow algorithm
! *** Begin Modifications 7/4/91. Sigma Research Corp.  DGS.
!     data b0n/1.25/,rnlz/0.69315/
! *** Restore rnlz ('92 Modification).  Sigma Research Corp.  DGS.
      data rnlz/0.5/
! *** End Modifications 7/4/91. Sigma Research Corp.  DGS.
!  Shift puff position to hill-centered coordinate system, with
!       x-axis along the flow.
      xpc=xp-xc(ih)
      ypc=yp-yc(ih)
      rotflo=three*piby2-thetaw*dtor
      call ROTATE(xpc,ypc,rotflo,xpf,ypf)
!  Get axis lengths for hill below Hd at the lesser of Hd and puff ht.
      ht=AMIN1(zpuff,hd)
      if(mhillt .EQ. 1) then
! ---    CTDM format hill information
!        Use the ellipse parameters already fit to the hill, and select
!        the values for height 'ht' as in CTDMPLUS.  Note that the
!        center of the ellipse, and its orientation are taken from the
!        polynomial hill that was fit to the entire terrain element.
!        (Section modified from CTDMPLUS)
!         GET MAJOR, MINOR AXIS FOR THE HILL BELOW hd
!         KLOW IS THE ARRAY INDEX TO THE HEIGHT CLOSEST TO (BUT LESS
!         THAN) ht
          KLOW = KLOSE( ZH(1,ih), NZH(ih), ht )
          IF( KLOW .EQ. 0 ) KLOW = 1
          BASEHW = ZH(KLOW,ih)
          IF( KLOW .EQ. NZH(ih) ) THEN
!             THE CRITICAL HT IS ABOVE LAST CONTOUR VALUE
!             DO NOT INTERPOLATE, BUT USE INVERSE POLYNOMIAL FORMULA
!
!             COMPUTE MAJOR & MINOR AXIS LENGTHS: INTERPOLATE BETWEEN
!             MAJAXW, MINAXW AND 0.0 IF ABOVE THE LAST CONTOUR
!
              FRACT =  1.0 - (ht - BASEHW)/(h - BASEHW)
              aax = MAJAXW(KLOW,ih) * FRACT
              bax = MINAXW(KLOW,ih) * FRACT
          ELSE
!             LINEARLY INTERPOLATE BETWEEN TWO VALUES
              aax = XINTRP( ZH(KLOW,ih), ZH(KLOW+1,ih),  ht,&
                             MAJAXW(KLOW,ih), MAJAXW(KLOW+1,ih))
              bax = XINTRP( ZH(KLOW,ih), ZH(KLOW+1,ih),  ht,&
                             MINAXW(KLOW,ih), MINAXW(KLOW+1,ih))
          ENDIF
      elseif(mhillt .EQ. 2) then
! ---    OPTHILL format hill information
         call AXIS(ht,aax,bax)
      endif
      baxi=one/bax
!  ----- Compute attributes of flow below Hd -----
!  Make sure that initial puff position used to define source streamline
!  is far enough away from hill
!     xpfi=AMIN1(xpf,-two*aax)
      xpfi=AMIN1(xpf,-100.*aax)
!  Find alpha-w (wind direction CW from major axis of ellipse)
!  (note that alpha-w has a range of 0-360 degrees)
      alfw=(thetaw-thetah(ih))*dtor
      if(alfw .LT. zero) alfw=alfw+twopi
!  Find beta (angle CCW from flow direction to "beta direction")
      r=aax*baxi
      top=r*SIN(alfw)
      bot=COS(alfw)
      alfs=ATAN2(top,bot)
!  (make sure that alpha-s has a range of 0-360 degrees)
      if(alfs .LT. zero) alfs=alfs+twopi
      beta=alfw-alfs
!  Find impingement pt. in ellipse coord. system (x along major axis)
      xoe=aax*COS(-alfw)
      yoe=bax*SIN(-alfw)
!  Rotate ellipse system to place x-axis along the flow direction
      rotang=pi-alfw
      call ROTATE(xoe,yoe,rotang,xof,yof)
!  Find the x-coord of the impingement pt. in the beta system
!       (note that this is the boundary between regions 1 and 2,
!        and the negative is the boundary between regions 2 and 3
!        in the flow below Hd)
      call ROTATE(xof,yof,beta,xob,yob)
!  Find puff position in beta coordinate system
      call ROTATE(xpf,ypf,beta,xpb,ypb)
!  Find d, the distance between the puff SL and stagnation SL
!  - Find initial puff position in ellipse coord system
      call ROTATE(xpfi,ypf,-rotang,xpei,ypei)
!  - Convert to elliptical coordinates
      call MUNU(0,xpei*baxi,ypei*baxi,r,mu,nu)
!  - Compute d
      d=(aax+bax)*SINH(mu)*SIN(nu+alfw)
!  ----- Compute attributes of flow above Hd -----
!  Determine boundaries between regions above Hd
      sbeta=SIN(beta)
      cbeta=COS(beta)
      x12f=(xob-ypf*sbeta)/cbeta
      x23f=(-xob-ypf*sbeta)/cbeta
!  Compute time-of-travel from puff at the start of the time-step to
!       the boundary(1,2).  This applies to the flow both above and
!       below Hd.  Note that t12p < 0 when puff is already past the
!       (1,2) boundary.
      x12p=x12f-xpf
      t12p=x12p/u
!  Compute the age of puff at the impingement point (timpg)
      timpg=tstart+t12p
!  Compute sigmas at the impingement point
      tlow=AMAX1(timpg,zero)
! old szo=SIGMA(tlow,sz1,tstart,sz2,tstep,szr,frac)
      szimpg=SIGMA(2,zpuff,u,tlow,sz1,tstart,sz2,tstep,szr,frac)
      szimpg=AMAX1(szmn,szimpg)
! old syo=SIGMA(tlow,sy1,tstart,sy2,tstep,syr,frac)
      syimpg=SIGMA(1,zpuff,u,tlow,sy1,tstart,sy2,tstep,syr,frac)
      syimpg=AMAX1(symn,syimpg)
!  Obtain Gaussian hill length scale for each axis of hill (above Hd)
      halfht=half*(h+hd)
      call AXIS(halfht,axa,axb)
      ah=axa*alphai
      bh=axb*alphai
!  Determine angle between the minor axis of the hill and the flow
!  in radians.  Note that use of psi is invariant to shifts of +/- pi
      psi=(thetaw-thetah(ih))*dtor-piby2
!  Set variables contained in common FLVARS:
!  Cut-off hill height and Froude number above Hd
      hh=h-hd
      fr=ubyn/hh
!  Find parameters for rotated hill
      call HILROT(ah,bh,psi)
!  Adapt section of code from CTDM subroutine FLOW ----------
!  (Note: speed shear ALF is no longer passed on to FLOPUFF)
! *** COMPUTE NEEDED LENGTH SCALE FACTORS.
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
! *** Restore LZI ('92 Modification). Sigma Research Corp.
      LZI = SQRT(HALF * (LXI2 + LYI2) ) / RNLZ
!     lzi = sqrt( lxi2 + lyi2 )
!  LN and LNI are now computed in flopuff
!     LN  = HALF * rtpi * LZ
!     LNI = ONE / LN
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
      LZ  = ONE / LZI
!
!     COMPUTE THE HILL ASYMMETRY FACTOR HASYM. ....EQN. A-19B
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
! *** Restore HASYM ('92 Modification). Sigma Research Corp.
      HASYM = SQRT(1.0+LX*LX*LYI2)
!     hasym = one
      IF(HASYM .GT. rt2) HASYM = rt2
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
!
! *** Begin shear modifications (1996).  EARTH TECH -- DGS
! --- Find speed shear from 1/2 the cut-off hill height to 1.5 the
! --- cut-off hill height
      hlo=half*(h+hd)
      hhi=two*h-hlo
      alf=(SPEED(hhi-hd)-SPEED(hlo-hd))/hh
      if(alf.LT.zero) alf=zero
! --- Modify neutral length scale, using shear and wind speed at hilltop
! --- provided speed at top is at least 1/10 puff speed
      speedhh=SPEED(hh)
      if(speedhh.GT.0.1*u) then
         lni=lzi+alf/speedhh
      else
         lni=lzi
      endif
      ln=one/lni
! *** End shear modifications (1996).  EARTH TECH -- DGS
!
! *** COMPUTE CORRECTED STRATIFICATION, S.
      BVUI = zero
      IF(ubyn .LT. 999.) BVUI=one/ubyn
      BVUI2 = BVUI * BVUI
      S = BVUI * HASYM
      S2 = S*S
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
!  -B0N2 is not used, and B0,B02 are computed in FLOPUFF
!     B0N2 = B0N * B0N
!     B0 = S * LZ * RTPII * B0N
!     B02 = B0 * B0
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
! *** Begin shear modifications (1996).  EARTH TECH -- DGS
!     Compute B0, and B02 here once again
      b0=s*ln
      b02=b0*b0
! *** End shear modifications (1996).  EARTH TECH -- DGS
!  End section of code from CTDM subroutine FLOW ********************
!  Define streamline height above Hd for obtaining distortion factors.
!  Use puff-center height above Hd, but set minimum value to hslfac
!  times the cut-off hill height (HH).
      hslmin=hslfac*hh
      hsl=AMAX1(zpuff-hd,hslmin)
!  Do not allow the ht of streamline to exceed upper limit compatible
!  with the flow routine
      hslmax=hh+piby2*ubyn/hasym
      hsl=AMIN1(hsl,hslmax)
!  Set up array of distortion factors at ntpts points along puff
!  trajectory, centered at x=xmf, covering a range of 3*el.
!  The length el is one half the distance between points of
!  intersection of the line y=0 (the centerplane of the flow over the
!  hill) with the ellipse that forms the base hill below Hd.
!  But xmf is the mid-point of the intersection of the puff trajectory
!  (without deflection) and the ellipse.  Note that at(1,-)=th, and
!  at(2,-)=tl; tu will be obtained from 1/(th*tl)
      spsi=SIN(psi)
      cpsi=COS(psi)
      spsi2=spsi*spsi
      cpsi2=cpsi*cpsi
      aax2=aax*aax
      bax2=bax*bax
      c1=(aax2-bax2)*spsi*cpsi
      c2=bax2*spsi2+aax2*cpsi2
      c2i=one/c2
      c4=aax2*bax2
      xmf=-c1*ypf*c2i
      el=SQRT(c4*c2i)
      xspace=three*el/(ntpts-1)
      xbegin=xmf-half*(ntpts-1)*xspace
      xend=xbegin+three*el
!  Set a flag so that factors = 1 if trajectory passes to side of hill
      iflag=0
      if(ypf*ypf .LT. c2) iflag=1
!  Loop over points
!  Set factors for first and last points equal to 1
      k=1
      at(1,k)=one
      at(2,k)=one
      k=ntpts
      at(1,k)=one
      at(2,k)=one
!  Set initial guess for streamline position upwind of hill before
!  entering loop.  PATH will use "current" ysl,zsl values when searching
!  for the streamline position at the next x-value.
      ysl=ypf
      zsl=hsl
      do 10 k=2,ntpts-1
         at(1,k)=one
         at(2,k)=one
         x=(k-1)*xspace+xbegin
         if(iflag .NE. 1) then
            ysl=ypf
            zsl=hsl
         else
!           call PATH(at(1,k),at(2,k),dum,x,ypf,hsl,ysl,zsl)
            call PATH(at(1,k),at(2,k),dum,x,ypf,hsl,xspace,ysl,zsl)
! *** Diagnostic write statement:
!           if(at(1,k) .LE. zero .OR. at(2,k) .LE. zero)
!    1                                 print *,k,(at(i,k),i=1,2)
         endif
10    continue
! --- Report DEBUG information
      if(ldb) then
         write(idebug,*)
         write(idebug,*) 'CTPAR: Flow below Hd ----'
         write(idebug,*) 'Height for axes (m) = ',ht
         write(idebug,*) 'Axis lengths (m)    = ',aax,bax
         write(idebug,*) 'Dist to imp pt (m)  = ',x12p
         write(idebug,*) 'Time to imp pt (s)  = ',timpg
         write(idebug,*) 'Sigma y,z at imp pt = ',syimpg,szimpg
         write(idebug,*) 'Crosswind y to SSL  = ',d
         write(idebug,*)
         write(idebug,*) 'CTPAR: Flow above Hd ----'
         write(idebug,*) 'Axis lengths (m)    = ',ah,bh
         write(idebug,*) 'Upper Froude No     = ',fr
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine ctrec
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521              CTREC
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes receptor position in various coordinate
!               systems and obtains travel times from regional bounds.
!
! ARGUMENTS:    none
!
! CALLING ROUTINES:     CTSG
!
! EXTERNAL ROUTINES:    ROTATE, IREG
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
!  Shift receptor position to hill-centered coordinate system with
!       x-axis along the flow
      xrc=xr-xc(ih)
      yrc=yr-yc(ih)
      call ROTATE(xrc,yrc,rotflo,xrf,yrf)
!  Find receptor position in ellipse coordinate system
      call ROTATE(xrf,yrf,-rotang,xre,yre)
!  Reset receptor location to the surface of the elliptic cylinder
!  if it lies outside of the ellipse (RMU > 0), by changing RMU and
!  computing a new X,Y (Adapted from CTDM+ modification!)
!  Compute the elliptic coordinates of the receptor
      if(zrec.LT. hd) then
         call MUNU(0,xre*baxi,yre*baxi,r,rmu,rnu)
         if(rmu .GT. 0) then
           rsq=r*r
           if(rsq .LT. one) rsq=one
           dum1=SQRT(rsq-one)
           if(dum1 .NE. 0.0) then
! ---        Not a circle (dum1 = 0)
             rmu0=ALOG((r+one)/dum1)
             sinnu=SIN(rnu)
             cosnu=COS(rnu)
             sinhmu=SINH(rmu0)
             coshmu=COSH(rmu0)
             xre=(dum1*coshmu*cosnu)/baxi
             yre=(dum1*sinhmu*sinnu)/baxi
           else
! ---        For a circle centered at (0,0), decrease XRE,YRE by
! ---        the ratio of the radial distance to the ellipse divided
! ---        by the radial distance to (XRE,YRE)
             factor=one/(SQRT(xre*xre+yre*yre)*baxi)
             xre=xre*factor
             yre=yre*factor
           endif
!  Recalculate receptor location with x-axis along the flow
           call ROTATE(xre,yre,rotang,xrf,yrf)
         endif
      endif
!  Find receptor position in beta coordinate system
      call ROTATE(xrf,yrf,beta,xrb,yrb)
!  Determine regions above and below Hd
      iregu=IREG(xrf,x12f,x23f)
      iregl=IREG(xrb,xob,-xob)
!  Compute time-of-travel from boundary(1,2) to receptor
!  - Upper flow:
      t12ru=(xrf-x12f)/u
!  - Lower flow:
      t12rl=(xrb-xob)/(u*cbeta)
      return
      end
!-----------------------------------------------------------------------
      subroutine ctsg(ldbin,irin,ipin,qin,uin,dirin,zlidin,xpin,ypin,&
                      zpin,iscin,zrin,ihin,stepin,tin,sz1in,sz2in,&
                      sy1in,sy2in,szrin,syrin,szmnin,symnin,frin,&
                      conc,cflat)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521               CTSG
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Main subroutine that controls computation of
!               concentrations on sub-grid-scale terrain features
!
! ARGUMENTS:
!   PASSED:     ldbin           debug logical                        [l]
!               irin            receptor number                      [i]
!               ipin            puff number                          [i]
!               qin             mass in puff (g)                     [r]
!               uin,dirin       wind speed, direction (m/s, deg CCW) [r]
!               zlidin          mixing lid (m)                       [r]
!               xpin,ypin,zpin  puff coordinates (m) at start of step[r]
!               iscin           stability class                      [i]
!               zrin            receptor height (m)                  [r]
!               ihin            hill ID                              [i]
!               stepin          time step (s)                        [r]
!               tin             age of puff at start of time-step (s)[r]
!               sz1in,sy1in     puff sigmas at start of time-step (m)[r]
!               sz2in,sy2in     puff sigmas at end of time-step (m)  [r]
!               szmnin,symnin   minimum value for sigmas (m)         [r]
!               frin            interpolation fraction for receptor  [r]
!                               position (0:start, 1:end of step)
!               szrin,syrin     puff sigmas at the receptor (m)      [r]
!   RETURNED:   conc            concentration (g/m**3)               [r]
!               cflat           concentration (g/m**3) without hill  [r]
!
! CALLING ROUTINES:     (main)
!
! EXTERNAL ROUTINES:    CTPAR, CTREC, PUFFC, UPPER, LOWER
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      logical ldbin
!  Check to see if CTSG has just been called for the same time period,
!  the same puff, and the same hill:
      isame=0
      if(tstart .EQ. tin) then
         if(ip .EQ. ipin) then
            if(ih .EQ. ihin) then
               isame=1
            endif
         endif
      endif
!  Transfer debug unit number to CTPASS variable
      idebug=io6
!  Pass argument list into common CTPASS
      ldb=ldbin
      ir=irin
      ip=ipin
      q=qin
      u=uin
      thetaw=dirin
      zlid=zlidin
      xp=xpin
      yp=ypin
      zpuff=zpin
      isc=iscin
      xr=xrctm(irin)
      yr=yrctm(irin)
      zrec=zrin
      ih=ihin
      tstep=stepin
      tstart=tin
      sz1=sz1in
      sz2=sz2in
      sy1=sy1in
      sy2=sy2in
      frac=frin
      szr=szrin
      syr=syrin
      szmn=szmnin
      symn=symnin
!  Calculate diffusivities for the time-step
      if(frac.EQ.zero .OR. frac.EQ.one) then
! ---    Use diffusivity over full step
         twokz=(sz2**2-sz1**2)/tstep
         twoky=(sy2**2-sy1**2)/tstep
      elseif(frac.GT.half) then
! ---    Use diffusivity from receptor position to end of step
         timefac=1./((one-frac)*tstep)
         twokz=(sz2**2-szr**2)*timefac
         twoky=(sy2**2-syr**2)*timefac
      else
! ---    Use diffusivity from start of step to receptor position
         timefac=1./(frac*tstep)
         twokz=(szr**2-sz1**2)*timefac
         twoky=(syr**2-sy1**2)*timefac
      endif
      if(twokz.LT.small) twokz=0.0
      if(twoky.LT.small) twoky=0.0
!  Report argument list in DEBUG mode, and the inferred diffusivity
      if(ldb .AND. irin.eq.1) then
         write(io6,*)
         write(io6,*)'CTSG input data'
         write(io6,*)' ir = ',ir,' ip = ',ip,' q = ',q,&
        ' u = ',u,' wd = ',thetaw,' zlid = ',zlid,' xp = ',xp,&
        ' yp = ',yp,' zpuff = ',zpuff,' isc = ',isc,&
        ' ih = ',ih,' tstep = ',tstep,' tstart = ',tstart,&
        ' frac = ',frac,&
        ' sz1 = ',sz1,' sz2 = ',sz2,' sy1 = ',sy1,&
        ' sy2 = ',sy2,' szr = ',szr,' syr = ',syr,&
        ' symn = ',symn,' szmn = ',szmn,&
        ' hd = ',hda(ih),' u/N = ',ubyna(ih),&
        ' h = ',relief(ih),' 2Kz = ',twokz,' 2Ky = ',twoky
      endif
!  Adjust height of receptor from height above sea level to
!  height above base-plane for current hill.
      zrec=zrec-zgrid(ih)
!  If current hill, puff, or time step differs from the last, compute
!  terrain-related parameters for puff trajectory.
      if(isame .EQ. 0) then
         hd=hda(ih)
         ubyn=ubyna(ih)
         h=relief(ih)
         call CTPAR
      endif
!  Compute receptor-related parameters
      call CTREC
!  Compute concentration
!       When terrain influence is modeled by using a receptor-on-a-pole
!       the standard puff sampling algorithm is called with an argument
!       of either 1 or 2.  Argument 1 signals the use of a pole of
!       height zpole; 2 signals the same plus the use of the distance
!       (d, passed as dpole) from the puff trajectory to the stagnation
!       SL in place of the usual off-axis distance to the receptor.
      cu=zero
      cl=zero
      if(zrec .GT. hd) then
         if(zlid .GT. hd) then
            if(iregu .EQ. 1) then
               zpole=hd
               call PUFFC(1,zpole,zero,cu)
            else
               call UPPER(cu)
            endif
            if(iregl .NE. 1 .and. hd .GT. zero) call LOWER(cl)
         endif
      elseif(hd .GT. zero) then
! ---    Remove test for receptors near base of hill, and treat all
! ---    receptors in the same way
! ---    zmin=0.1*AMIN1(hd,zpuff)
         zpole=zrec
         dpole=d
! ---    if(zpole .LT. zmin) then
! ---       call PUFFC(1,zpole,zero,cl)
! ---    else
            if(iregl .EQ. 1) then
               call PUFFC(2,zpole,dpole,cl)
            else
               call LOWER(cl)
            endif
! ---    endif
      endif
!  Do a "flat" calc. for comparison
      call PUFFC(0,zero,zero,cflat)
      conc=cu+cl
!  Report results in DEBUG mode
      if(ldb .AND. irin.eq.1)then
         write(io6,*)
         write(io6,*)'CTSG output for receptor ',irin
         write(io6,*)'  hd = ',hd,'   u/N = ',ubyn
         write(io6,*)'conc = ',conc,' cflat = ',cflat
      endif
      return
      end
!-----------------------------------------------------------------------
      function fcty(t)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521               FCTY
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes value of integrand function denoted as fcty,
!               at time t.
!
! ARGUMENTS:
!    PASSED:    t       time-of-travel of puff (sec)                 [r]
!
! CALLING ROUTINES:     UPPER
!
! EXTERNAL ROUTINES:    TFAC
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
!  Obtain Th and Tu
      delt=t-timpg
      x=x12f+delt*u
      call TFAC(x,th,tl,tu)
!  Compute integrand
      fcty=twoky*EXP(two*(one-tl))
      return
      end
!-----------------------------------------------------------------------
      function fctz(t)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 951005               FCTZ
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes value of integrand function denoted as fctz,
!               at time t.
!
! ARGUMENTS:
!    PASSED:    t       time-of-travel of puff (sec)                 [r]
!
! CALLING ROUTINES:     UPPER
!
! EXTERNAL ROUTINES:    TFAC
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
!  Obtain Th and Tu
      delt=t-timpg
      x=x12f+delt*u
      call TFAC(x,th,tl,tu)
!  Compute integrand
      fctz=twokz*tu*tu*EXP(two*(one-th))
      return
      end
!-----------------------------------------------------------------------
      subroutine flopuff(x,y,z,eta,del,thi,tli,tu)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521            FLOPUFF
! ---            R. Yamartino, SRC
! ---            (D. Strimaitis, SRC)
!
! PURPOSE:      (adapted from CTDM subroutine FLOW)
!     COMPUTES THE PERTURBATION WINDS UP,VP,WP AT (X,Y,Z) AND
!     COMPUTES THE VERTICAL AND LATERAL DEFLECTIONS (ETA,DEL) OF THE
!     STREAMLINE THAT PASSES THROUGH THE POINT (X,Y,Z) ABOVE THE
!     SURFACE OF A ROTATED GAUSSIAN HILL.
! *** THIS CODE NOW COMPUTES IDYY, AND IXXDYY TO COMPUTE T-FACTORS.
!     TLI    = 1.0 - D(DEL)/DY = 1.0 + (IDYY + BVUI2*IXXDYY)
!     THI    = 1.0 - D(ETA)/DZ = 1.0 + IDZZ
!
! *** THIS STRATIFIED FLOW CALCULATION ACCOUNTS FOR ARBITRARY
!     STRATIFICATION, N/U, (INCLUDING NEUTRAL) IN THE NEAR FIELD
!     OF THE HILL (I.E. X < LX, Y < LY, Z << LX OR LY ).
! *** LINEAR WIND SHEAR CORRECTIONS ARE ADDED IN SUCH THAT NEUTRAL
!     DEFLECTIONS ARE CORRECT TO LOWEST ORDER IN THE SHEAR GRADIENT.
! *** U''/U = 0 IS ASSUMED BUT N/U IS COMPUTED FROM FR ABOVE HC.
!
!     MODIFICATION:  Changes made during EPA-sponsored upgrade to the
!                    FLOW algorithm have been made here.  This is now
!                    consistent with INPUFF 3.0 (10/93).
!
! ARGUMENTS:
!    PASSED:    X       X COORDINATE (POSITIVE DOWNWIND OF HILL      [r]
!                       CENTER) (m)
!               Y       Y COORDINATE (POSITIVE LEFT OF HILL          [r]
!                       CENTER) (m)
!               Z       Z COORDINATE (POSITIVE ABOVE HILL            [r]
!                       SURFACE) (m)
!  RETURNED:    ETA     VERTICAL DEFL. OF STREAMLINE PASSING         [r]
!                       (X,Y,Z) (m)
!               DEL     LATERAL DEFLECTION OF STREAMLINE PASSING     [r]
!                       (X,Y,Z) (m)
!               TLI     1.0/(TL FACTOR) AT (X,Y,Z)                   [r]
!               THI     1.0/(TH FACTOR) AT (X,Y,Z)                   [r]
!               TU      TU FACTOR AT (X,Y,Z)                         [r]
!
! CALLING ROUTINES:  UPPER, PATH
!
! EXTERNAL ROUTINES: SPEED
!----------------------------------------------------------------------C
      INCLUDE 'const.puf'
      INCLUDE 'flvar.puf'
!                                                                       FLW00470
      REAL*4 I,IDY,IDZ,IDX,IDXX,IDXY,IDXZ                              ! FLW00490
      REAL*4 IX,IXDY,IXX,IXXDY,IDYY,IDZZ,IXXDYY,ID3Y,IXXD3Y            ! FLW00500
      DATA WTB/1.25/                                                   ! FLW00530
!                                                                       FLW00540
! *** MOST OF THIS CODE COMPUTES I AND ITS INTEGRALS AND DERIVATIVES.   FLW00550
!     THE NOTATION IS SEEN IN THE EXAMPLE IXDY, WHICH IS I INTEGRATED   FLW00560
!     ONCE IN X (FROM -INFINITY TO X) AND DIFFERENTIATED IN Y.          FLW00570
!                                                                       FLW00580
! *** COMPUTE DIMENSIONLESS COORDINATES
      XDLX = X * LXI                                                   ! FLW00710
      YDLY = Y * LYI                                                   ! FLW00720
      YDLY2 = YDLY * YDLY                                              ! FLW00730
!                                                                       FLW00740
! *** COMPUTE THE MODIFIED COORDINATES XM,YM AND THEIR                  FLW00750
!     DIMENSIONLESS FORMS XMDLX,YMDLY.                                  FLW00760
      XM = X + GAM*LX*LX*Y                                             ! FLW00770
      YM = Y + GAM*LY*LY*X                                             ! FLW00780
      XMDLX = XM * LXI                                                 ! FLW00790
      XMDLX2 = XMDLX * XMDLX                                           ! FLW00800
      YMDLY = YM * LYI                                                 ! FLW00810
      YMDLY2 = YMDLY * YMDLY                                           ! FLW00820
!                                                                       FLW00830
      GAMLX = GAM * LX                                                 ! FLW00840
      GAMLX2 = GAMLX * GAMLX                                           ! FLW00850
      GAMP = ONE - GAMLX2*LY*LY                                        ! FLW00860
      GAMP2 = GAMP * GAMP                                              ! FLW00870
!                                                                       FLW00880
!     HHXY = HH * HILHGT(X,Y)   BUT COMPUTE IN CODE BELOW.              FLW00890
      ARG = XDLX*XDLX + YDLY*YDLY + 2.0*GAM*X*Y                        ! FLW00900
      IF(ARG .GT. 30.) ARG = 30.                                       ! FLW00910
      EXPARG = EXP(-ARG)                                               ! FLW00920
      HHXY = HH * EXPARG                                               ! FLW00930
!                                                                       FLW00940
! *** OBTAIN CORRECT WIND SPEED, U, USING SPEED                         FLW00950
!                                                                       FLW00960
      U = SPEED(HHXY+Z)                                                ! FLW00970
      UI = ONE / U                                                     ! FLW00980
!                                                                       FLW00990
!     COMPUTE THE SHEAR FACTORS THAT ARE NEEDED.                        FLW01000
!                                                                       FLW01010
! !!! Begin shear modifications 1996.  EARTH TECH -- DGS
!
! dgs U0 = AMIN1(SPEED(HHXY),U)                                         FLW01020
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
!     SHRF = SQRT(U0*UI)                                                FLW01030
!     SHRFDZ = -HALF * ALF * UI                                         FLW01040
!     SHRC=HALF*ALF/U0                                                  FLW01050
! dgs shrf = u / u0
! dgs ln  = lz * shrf
! dgs lni = one / ln
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
!
! *** COMPUTE THE ADDITIONAL Z SUPRESSION FACTOR FROM NEUTRAL FLOW MATCHFLW01060
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
!     ZFACI = ONE / (ONE + LNI * Z)                                     FLW01070
!     ZFACDZ = -TWO * LNI * ZFACI                                       FLW01080
      zfaci = exp(-lni * z)
! *** COMBINE WITH SHEAR FACTOR TO GIVE TOTAL Z ADJUSTMENT FACTOR.      FLW01090
!     TZFAC = SHRF * ZFACI * ZFACI                                      FLW01100
!     TZFDZ = SHRFDZ + ZFACDZ                                           FLW01110
!     TZFDZZ = TZFDZ*TZFDZ - SHRFDZ*ALF*UI - ZFACDZ*LNI*ZFACI           FLW01120
! *** Combine with shear factor to give total z adjustment factor.
! dgs b0 = s * ln
! dgs b02 = b0 * b0
! dgs tzfac = zfaci * lz  / (one + b02)
      tzfac = zfaci * ln  / (one + b02)
      tzfdz = -lni
      tzfdzz = lni*lni
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
! !!! End shear modifications 1996.  EARTH TECH -- DGS
!                                                                       FLW01130
!                                                                       FLW01260
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
!     AST = TWO * RTPII * S2 * LZ * PII * B0N2 - SHRC                   FLW01270
      ast = one
! --- ASTDS2 IS ACTUALLY AST/(S*BVUI)                                   FLW01280
!     ASTDS2 = TWO * RTPII * LZ * PII * HASYM                           FLW01290
      astds2 = one
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
!
! --- HHM IS THE COEFFICIENT OF I AT X=Y=0                              FLW01300
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
!     HHM = HH * LN / (ONE + B02)                                       FLW01310
      hhm = hh
! --- HHXYM IS THE COEFFICIENT OF I AWAY FROM HILL CREST                FLW01320
      HHXYM = HHM * EXPARG                                             ! FLW01330
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
! *** COMPUTE THE STRATIFICATION HEIGHT, SZ.                            FLW01340
      SZ = S * Z                                                       ! FLW01350
!                                                                       FLW01360
! *** COMPUTE THE VARIOUS 'ANGULAR FACTORS', AFN, AND THEIR DERIVATIVES,FLW01370
!     ADN, CONDITIONED ON THE VALUE OF SZ.                              FLW01380
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
!     IF(SZ .LE. 0.005) THEN                                            FLW01390
!        CSZ = ONE                                                      FLW01400
!        SSZ = SZ                                                       FLW01410
!        AF1 = Z                                                        FLW01420
!        AD1 = ONE                                                      FLW01430
!        ADD1 = S * SZ                                                  FLW01440
!     ELSE                                                              FLW01450
!        SI  = ONE / S                                                  FLW01460
!        CSZ = COS(SZ)                                                  FLW01470
!        SSZ = SIN(SZ)                                                  FLW01480
!        AF1 = SSZ * SI                                                 FLW01490
!        AD1 = CSZ                                                      FLW01500
!        ADD1 = -S * SSZ                                                FLW01510
!     ENDIF                                                             FLW01520
      csz = cos(sz)
      ssz = sin(sz)
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
!                                                                       FLW01530
!     AF0 = CSZ / ZFACI                                                 FLW01540
!     AD0 = LNI * CSZ  -  S * SSZ / ZFACI                               FLW01550
! *** OVERRIDE AF0=CSZ WITH AF0=ONE. 10/8/86                            FLW01560
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
!     AF0 = ONE / ZFACI                                                 FLW01570
!     AD0 =  LNI                                                        FLW01580
!     ADD0 = -S2 / ZFACI                                                FLW01590
!     AF2SHR = TWO * LNI + SHRC                                         FLW01600
!     AF2 = S * CSZ + AF2SHR * SSZ                                      FLW01610
!     AD2 = -S2 * SSZ + AF2SHR * S * CSZ                                FLW01620
!     ADD2 = -S2 * (S * CSZ + AF2SHR * SSZ)                             FLW01630
!
      af0 = csz
      ad0 =  -s * ssz
      add0 = -s2 * csz
!
      af1 = b0 * ssz
      ad1 = b0 * s * csz
      add1 = -b0 * s2 * ssz
!
      af2 = b0 * csz  +  ssz
      ad2 = -b0 * s * ssz  +  s * csz
      add2 = -s2 * (b0 * csz  +  ssz)
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
!                                                                       FLW01640
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR I.                         FLW01650
      T0 = ONE                                                         ! FLW01660
! *** Begin Modifications 7/4/91. Sigma Research Corp.  RJY.
      T1 = -AST                                                        ! FLW01670
! or  t1 = -one
! ??? T2 = -ASTDS2*XMDLX                                                FLW01680
! ---------- TRY SETTING T2=0. FOR XM LESS THAN ZERO ------------       FLW01690
!     T2 = 0.                                                           FLW01700
!     IF(XM .GE. 0.) T2 = -ASTDS2*XMDLX                                 FLW01710
      t2 = -astds2*xmdlx
! or  t2 = -xmdlx
! *** End Modifications 7/4/91. Sigma Research Corp.  RJY.
!                                                                       FLW01720
      TI0 = T0 * AF0                                                   ! FLW01730
      TI1 = T1 * AF1                                                   ! FLW01740
      TI2 = T2 * AF2                                                   ! FLW01750
      SUM = TI0 + TI1 + TI2                                            ! FLW01760
!                                                                       FLW01770
! *** COMPUTE THE BASIC QUANTITY I.   ....EQN. A-27                     FLW01780
      I = TZFAC * HHXYM * SUM                                          ! FLW01790
!                                                                       FLW01800
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IDZ.                       FLW01810
      TI0 = T0 * AD0                                                   ! FLW01820
      TI1 = T1 * AD1                                                   ! FLW01830
      TI2 = T2 * AD2                                                   ! FLW01840
      SUM = TI0 + TI1 + TI2                                            ! FLW01850
!                                                                       FLW01860
! *** COMPUTE THE DERIVATIVE OF I WITH RESPECT TO Z.  ....EQN. A-28     FLW01870
      TERM1 = TZFAC * HHXYM * SUM                                      ! FLW01880
      IDZ =  TERM1  +  TZFDZ * I                                       ! FLW01890
!                                                                       FLW01900
! *** COMPUTE THE SECOND DERIVATIVE OF I WITH RESPECT TO Z.             FLW01910
      TI0 = T0 * ADD0                                                  ! FLW01920
      TI1 = T1 * ADD1                                                  ! FLW01930
      TI2 = T2 * ADD2                                                  ! FLW01940
      SUM = TI0 + TI1 + TI2                                            ! FLW01950
!                                                                       FLW01960
! *** COMPUTE THE SECOND DERIVATIVE OF I WITH RESPECT TO Z. ....EQN.A-29FLW01970
      IDZZ = TZFAC * HHXYM * SUM  +  TWO * TZFDZ * TERM1  +  TZFDZZ * I! FLW01980
!                                                                       FLW01990
!                                                                       FLW02000
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IDX AND IDXZ.              FLW02010
!     T0 = ZERO                                                         FLW02020
!     T1 = ZERO                                                         FLW02030
      T2 = -ASTDS2 * LXI                                               ! FLW02040
!                                                                       FLW02050
!     TI0 = T0 * AF0                                                    FLW02060
!     TI1 = T1 * AF1                                                    FLW02070
      TI2 = T2 * AF2                                                   ! FLW02080
!     SUM = TI0 + TI1 + TI2                                             FLW02090
      SUM = TI2                                                        ! FLW02100
!                                                                       FLW02110
! *** COMPUTE THE QUANTITY IDX.    ....EQN. A-30                        FLW02120
      TERM2 = TZFAC * HHXYM * SUM                                      ! FLW02130
      IDX = -TWO * XMDLX * LXI * I  +  TERM2                           ! FLW02140
!                                                                       FLW02150
!     NOW COMPUTE THE D/DZ TERMS.                                       FLW02160
!     TI0 = T0 * AD0                                                    FLW02170
!     TI1 = T1 * AD1                                                    FLW02180
      TI2 = T2 * AD2                                                   ! FLW02190
!     SUM = TI0 + TI1 + TI2                                             FLW02200
      SUM = TI2                                                        ! FLW02210
!                                                                       FLW02220
! *** COMPUTE THE DERIVATIVE OF IDX WITH RESPECT TO Z.  ....EQN. A-32   FLW02230
      TERM3 = TZFAC*HHXYM*SUM                                          ! FLW02240
      IDXZ = -TWO*XMDLX*LXI*IDZ + TZFDZ*TERM2 + TERM3                  ! FLW02250
!                                                                       FLW02260
!                                                                       FLW02270
! *** COMPUTE THE QUANTITIES NEEDED FOR IDXX.                           FLW02280
!     T0 = ZERO                                                         FLW02290
!     T1 = ZERO                                                         FLW02300
!     T2 = ZERO                                                         FLW02310
!                                                                       FLW02320
!     TI0 = T0 * AF0                                                    FLW02330
!     TI1 = T1 * AF1                                                    FLW02340
!     TI2 = T2 * AF2                                                    FLW02350
!     SUM = TI0 + TI1 + TI2                                             FLW02360
      SUM = ZERO                                                       ! FLW02370
!                                                                       FLW02380
! *** COMPUTE THE QUANTITY IDXX.  ....EQN. A-31                         FLW02390
      IDXX = -TWO * LXI2 * (ONE + TWO * XMDLX2) * I  -                 &! FLW02400
             FOUR * XMDLX * LXI * IDX  +  TZFAC * HHXYM * SUM          ! FLW02410
!                                                                       FLW02420
!                                                                       FLW02430
!                                                                       FLW02440
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IDY.                       FLW02450
!     T0 = ZERO                                                         FLW02460
!     T1 = ZERO                                                         FLW02470
      T2 = -ASTDS2 * GAMLX                                             ! FLW02480
!                                                                       FLW02490
!     TI0 = T0 * AF0                                                    FLW02500
!     TI1 = T1 * AF1                                                    FLW02510
      TI2 = T2 * AF2                                                   ! FLW02520
!     SUM = TI0 + TI1 + TI2                                             FLW02530
      SUM = TI2                                                        ! FLW02540
!                                                                       FLW02550
! *** COMPUTE THE QUANTITY IDY.   ....EQN. A-33                         FLW02560
      IDY = -TWO * LYI * YMDLY * I  +  TZFAC * HHXYM * SUM             ! FLW02570
!                                                                       FLW02580
! *** COMPUTE THE QUANTITY IDYY.  ....EQN. A-34                         FLW02590
      IDYY = -TWO * LYI2 * (ONE + TWO * YMDLY2) * I  -                 &! FLW02600
             FOUR * YMDLY * LYI * IDY                                  ! FLW02610
!                                                                       FLW02620
! *** COMPUTE THE QUANTITY ID3Y.  ....EQN. A-35                         FLW02630
      ID3Y = -8.0 * LYI3 * YMDLY * I  -  FOUR * LYI * YMDLY * IDYY  -  &! FLW02640
             TWO * LYI2 * (THREE + TWO * YMDLY2) * IDY                 ! FLW02650
!                                                                       FLW02660
!                                                                       FLW02670
! *** COMPUTE THE QUANTITIES NEEDED FOR IDXY.                           FLW02680
!     T0 = ZERO                                                         FLW02690
!     T1 = ZERO                                                         FLW02700
!     T2 = ZERO                                                         FLW02710
!                                                                       FLW02720
!     TI0 = T0 * AF0                                                    FLW02730
!     TI1 = T1 * AF1                                                    FLW02740
!     TI2 = T2 * AF2                                                    FLW02750
!     SUM = TI0 + TI1 + TI2                                             FLW02760
      SUM = ZERO                                                       ! FLW02770
!                                                                       FLW02780
! *** COMPUTE THE QUANTITY IDXY.     ....EQN. A-36                      FLW02790
      IDXY = -TWO * (GAM  +  TWO * XM * YM * LXI2 * LYI2) * I  -       &! FLW02800
             TWO * XMDLX * LXI * IDY  -                                &! FLW02810
             TWO * YMDLY * LYI * IDX  +  TZFAC * HHXYM * SUM           ! FLW02820
!                                                                       FLW02830
!                                                                       FLW02840
! ****************************************************************      FLW02850
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IXXDY.                     FLW02860
!     NOTE THAT IXX IS ALSO NEEDED FOR THIS.                            FLW02870
!                                                                       FLW02880
! --- COMPUTE THE GNX AND GNXX FACTORS.                                 FLW02890
! $$$ G0X = HALF * rtpi * ( ONE + ERF(XMDLX) )                          FLW02900
! --- INSERT THE FOLLOWING FIX TO KILL LAT. DEFL. GROWTH. 10/1/86       FLW02910
      G0X = HALF * rtpi * ( ONE - ERF( ABS(XMDLX) )  )                 ! FLW02920
!                                                                       FLW02930
      IF(XMDLX2 .GT.30.) XMDLX2 = 30.                                  ! FLW02940
      G1X = -HALF * EXP(-XMDLX2)                                       ! FLW02950
      G0XX = XMDLX * G0X - G1X                                         ! FLW02960
      G1XX = -HALF * G0X                                               ! FLW02970
!                                                                       FLW02980
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IXX.                       FLW02990
      T0 = G0XX                                                        ! FLW03000
      T1 = -G0XX                                                       ! FLW03010
      T1 = AST * T1                                                    ! FLW03020
      T2 = -ASTDS2*G1XX                                                ! FLW03030
!                                                                       FLW03040
      TI0 = T0 * AF0                                                   ! FLW03050
      TI1 = T1 * AF1                                                   ! FLW03060
      TI2 = T2 * AF2                                                   ! FLW03070
      SUM = TI0 + TI1 + TI2                                            ! FLW03080
!                                                                       FLW03090
! *** COMPUTE THE QUANTITY IXX.      ....EQN. A-42                      FLW03100
!     QX = YDLY2 - GAMLX2 * Y * Y                                       FLW03110
      QX = YDLY2 * GAMP                                                ! FLW03120
      IF(QX .GT.30.) QX = 30.                                          ! FLW03130
      EFAC = EXP(-QX)                                                  ! FLW03140
      HHLX2E = HHM * LX * LX * EFAC                                    ! FLW03150
      IXX = TZFAC * HHLX2E * SUM                                       ! FLW03160
!                                                                       FLW03170
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IXXDY.                     FLW03180
      T0 = GAMLX*G0X                                                   ! FLW03190
      T1 = -GAMLX*G0X                                                  ! FLW03200
      T1 = AST * T1                                                    ! FLW03210
      T2 = -ASTDS2*GAMLX*G1X                                           ! FLW03220
!                                                                       FLW03230
      TI0 = T0 * AF0                                                   ! FLW03240
      TI1 = T1 * AF1                                                   ! FLW03250
      TI2 = T2 * AF2                                                   ! FLW03260
      SUM = TI0 + TI1 + TI2                                            ! FLW03270
!                                                                       FLW03280
! *** COMPUTE THE QUANTITY IXXDY.     ....EQN. A-44                     FLW03290
      IXXDY = -TWO * LYI2 * Y * GAMP * IXX  +  TZFAC * HHLX2E * SUM    ! FLW03300
!                                                                       FLW03310
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IX.                        FLW03320
      T0 = +G0X                                                        ! FLW03330
      T1 = -G0X                                                        ! FLW03340
      T1 = AST * T1                                                    ! FLW03350
      T2 = -ASTDS2*G1X                                                 ! FLW03360
!                                                                       FLW03370
      TI0 = T0 * AF0                                                   ! FLW03380
      TI1 = T1 * AF1                                                   ! FLW03390
      TI2 = T2 * AF2                                                   ! FLW03400
      SUM = TI0 + TI1 + TI2                                            ! FLW03410
!                                                                       FLW03420
! *** COMPUTE THE QUANTITY IX.     ....EQN. A-41                        FLW03430
      IX = TZFAC * HHLX2E * LXI * SUM                                  ! FLW03440
!                                                                       FLW03450
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IXDY.                      FLW03460
!     HHJ = GAMLX * EXP(-XMDLX2) * XMDLX**J  BUT SEE G1X                FLW03470
      HH0 = -TWO * G1X * GAMLX                                         ! FLW03480
      HH1 = HH0 * XMDLX                                                ! FLW03490
      HH2 = HH1 * XMDLX                                                ! FLW03500
      T0 = HH0                                                         ! FLW03510
      T1 = -HH0                                                        ! FLW03520
      T1 = AST * T1                                                    ! FLW03530
      T2 = -ASTDS2*HH1                                                 ! FLW03540
!                                                                       FLW03550
      TI0 = T0 * AF0                                                   ! FLW03560
      TI1 = T1 * AF1                                                   ! FLW03570
      TI2 = T2 * AF2                                                   ! FLW03580
      SUM = TI0 + TI1 + TI2                                            ! FLW03590
!                                                                       FLW03600
! *** COMPUTE THE QUANTITY IXDY.        ....EQN. A-45                   FLW03610
      IXDY = -TWO * LYI2 * Y * GAMP * IX  +  TZFAC * HHLX2E * LXI * SUM! FLW03620
!                                                                       FLW03630
!                                                                       FLW03640
! *** COMPUTE THE QUANTITY IXXDYY       ....EQN. A-46                   FLW03650
!     (NOTE THAT IXXDYY USES THE SAME SUM AS IXDY)                      FLW03660
      IXXDYY = -TWO * LYI2 * GAMP * IXX * (ONE + TWO*GAMP*YDLY2) -     &! FLW03670
                FOUR * LYI2 * Y * GAMP * IXXDY  +                      &! FLW03680
                TZFAC * HHLX2E * GAMLX * SUM                           ! FLW03690
!                                                                       FLW03700
!                                                                       FLW03710
! *** COMPUTE THE VARIOUS INTEGRAL TERMS FOR IXXD3Y.                    FLW03720
      T0 = -TWO * HH1                                                  ! FLW03730
      T1 = +TWO * HH1                                                  ! FLW03740
      T1 = AST * T1                                                    ! FLW03750
      T2 = -ASTDS2 * (HH0 - TWO*HH2)                                   ! FLW03760
!                                                                       FLW03770
      TI0 = T0 * AF0                                                   ! FLW03780
      TI1 = T1 * AF1                                                   ! FLW03790
      TI2 = T2 * AF2                                                   ! FLW03800
      SUM = TI0 + TI1 + TI2                                            ! FLW03810
!                                                                       FLW03820
! *** COMPUTE THE QUANTITY IXXD3Y.        ....EQN. A-47                 FLW03830
      IXXD3Y = -FOUR*LYI3*GAMP2*IXX*YDLY*(THREE+TWO*GAMP*YDLY2) -      &! FLW03840
                6.0 * LYI2 * GAMP * IXXDY * (ONE+TWO*GAMP*YDLY2)  -    &! FLW03850
                6.0 * LYI * GAMP * YDLY * IXXDYY  +                    &! FLW03860
                TZFAC * HHLX2E * GAMLX2 * SUM                          ! FLW03870
!                                                                       FLW03880
! ************************************************************          FLW03890
! *** COMPUTE THE STREAMLINE DEFLECTIONS.                               FLW03900
! *** COMPUTE THE VERTICAL DEFLECTION AS -(D/DZ) I.  ....EQN. A-22A     FLW03910
      ETA = -IDZ                                                       ! FLW03920
! *** NOW COMPUTE THE LATERAL DEFLECTION AS          ....EQN. A-22E     FLW03930
!     DEL = -(D/DY)(IDY+BVUI2*IXX) = -(IDY + BVUI2*IXXDY)               FLW03940
! *** AND COMPUTE ITS FIRST AND SECOND DERIVATIVES                      FLW03950
      TERM3 = BVUI2 * IXXDY                                            ! FLW03960
      DEL = -(IDY + TERM3)                                             ! FLW03970
      DELDY = -IDYY - BVUI2*IXXDYY                                     ! FLW03980
      DELDYY = -ID3Y - BVUI2*IXXD3Y                                    ! FLW03990
! *** COMPUTE CORRECTION FACTOR FOR LATERAL DEFLECTIONS                 FLW04000
      DELC = ONE + ABS(DELDY)                                          ! FLW04010
      DELCOR = DELC                                                    ! FLW04020
      IF(ABS(DEL).LT.0.001*LY) THEN                                    ! FLW04030
         DELCOR = ONE                                                  ! FLW04040
      ELSEIF(ABS(DELDYY*LY) .GT. 0.001) THEN                           ! FLW04050
         EPS = WTB*DELDYY*DEL                                          ! FLW04060
         ARGRT = DELC*DELC-TWO*EPS                                     ! FLW04070
         RTDELC = 0.                                                   ! FLW04080
         IF(ARGRT .GE. 0.) RTDELC = SQRT(ARGRT)                        ! FLW04090
         IF(RTDELC .NE. DELC) DELCOR = EPS/(DELC-RTDELC)               ! FLW04100
      ENDIF                                                            ! FLW04110
! *** APPLY CORRECTION FACTOR                                           FLW04120
      DEL = DEL / DELCOR                                               ! FLW04130
!                                                                       FLW04140
! *** COMPUTE THE RECIPROCAL OF THE T FACTORS      ....EQNS. A-22F,G    FLW04150
! *** NOTE THAT THE T FACTORS COULD GO TO INFINITY.                     FLW04160
      TLI = ONE - DELDY/DELCOR                                         ! FLW04170
      THI = ONE + IDZZ                                                 ! FLW04180
!                                                                       FLW04190
! ****************************************************************      FLW04200
!                                                                       FLW04210
! *** COMPUTE THE PERTURBATION VELOCITIES.   ...EQNS. A-22B,C,D         FLW04220
!                                                                       FLW04230
! *** NOW COMPUTE THE ALONG-WIND VELOCITY PERTURBATION AS               FLW04240
!     UP/U = -(IDXX + (BV*UI)**2 * I )                                  FLW04250
!     NOTE THAT THIS IS JUST THE NEGATIVE OF THE PERTURBATION           FLW04260
!     PRESSURE DIVIDED BY RHO(0)*U**2.                                  FLW04270
      TERM3 = BVUI2 * I                                                ! FLW04280
      UP = -(IDXX + TERM3)                                             ! FLW04290
! *** COMPUTE THE NON-LINEAR PERTURBATION ALA HUNT ET AL.               FLW04300
!!!!!!UPNL = ( -ONE + SQRT( ABS(ONE + TWO*UP) )  ) * U                  FLW04310
      UP = UP * U                                                      ! FLW04320
!                                                                       FLW04330
! *** NOW COMPUTE THE LATERAL VELOCITY PERTURBATION AS                  FLW04340
!     VP/U = -(IDXY + (BV*UI)**2 * IXDY)                                FLW04350
      TERM3 = BVUI2 * IXDY                                             ! FLW04360
      VP = -(IDXY + TERM3) * U                                         ! FLW04370
!                                                                       FLW04380
! *** NOW COMPUTE THE VERTICAL VELOCITY PERTURBATION AS -(D/DZ) IDX.    FLW04390
      WP = -IDXZ * U                                                   ! FLW04400
!                                                                       FLW04410
! ***********************************************************           FLW04420
!                                                                       FLW04430
! *** COMPUTE THE "SPEED-UP" FACTOR TU                                  FLW04440
      UTOT=SQRT( (U+UP)*(U+UP) + VP*VP + WP*WP )                       ! FLW04450
      TU = UTOT*UI                                                     ! FLW04460
!                                                                       FLW04470
! ***********************************************************           FLW04480
!                                                                       FLW04490
      RETURN                                                           ! FLW04500
      END                                                              ! FLW04510
!-----------------------------------------------------------------------
!      subroutine getprfm(ihill,umet,vmet,tmet,zgpt,nears,tempss,lcalgrd,
!     &                   istab,ptg,nx,ny,nz,xorig,yorig,dgrid)
      subroutine getprfm(ihill,umet,vmet,tmet,zgpt,nears,tempss,lcalgrd,&
                         istab,ptg,nx,ny,nz,xorig,yorig,dgrid,&
                         i2dmet,temp2d)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 101111            GETPRFM
! ---            D. Strimaitis
!
! PURPOSE:      Passes vertical profiles of wind speed and the Brunt-
!               Vaisala Frequency into common for use by CTSG-related
!               routines. Uses MET arrays.
!
! UPDATE:
!
! --- V6.3-V6.402   101111  (DGS): Update GETCELL arguments
! --- V5.7-V6.3     100212  (DGS): Add nested CALMET grids
! --- V5.5-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
!
! ARGUMENTS:
!    PASSED:    ihill   hill index number                           [i]
!               umet    wind field array of u-components (m/s)     [ra]
!               vmet    wind field array of v-components (m/s)     [ra]
!               tmet    temperature array (K)                      [ra]
!               zgpt    heights of data levels above surface (m)   [ra]
!               nears   array for met station nearest grid pt.     [ia]
!               tempss  temperatures at surface stations           [ra]
!               lcalgrd indicates temp profile available (T)        [l]
!               istab   stability class field                      [ia]
!               ptg     default pot. temp gradient (K/m) for the   [ra]
!                       two stable classes (E,F)
!               nx      number of grid-points in x-dir.             [i]
!               ny      number of grid-points in y-dir.             [i]
!               nz      number of grid-points in vertical           [i]
!               xorig   x-coordinate of the origin of met grid (m)  [r]
!               yorig   y-coordinate of the origin of met grid (m)  [r]
!               dgrid   spacing of grid-cells in met grid (m)       [r]
!               temp2d  temperatures at CALMET gridpoints          [ra]
!               i2dmet  2D CALMET surface arrays                    [i]
!                        0 = 2d sfc met NOT available
!                        1 = 2d sfc met available
!  RETURNED:    none
!
! CALLING ROUTINES:     COMP
!
! EXTERNAL ROUTINES:    NEARS, GETCELL
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      real umet(mxnx,mxny,mxnz,mxmetdom),vmet(mxnx,mxny,mxnz,mxmetdom),&
           tmet(mxnx,mxny,mxnz,mxmetdom),zgpt(mxnz),&
           tempss(mxss,mxmetdom),ptg(2)
      integer istab(mxnx,mxny,mxmetdom),nears(mxnx,mxny,mxmetdom)
      logical lcalgrd
! frr(09/01)
      real temp2d(mxnx,mxny,mxmetdom)
!  Obtain met data at the grid-point nearest the center of the hill.
!  Find the grid-point indices (ig,jg) of the cell in which the
!  center of the hill is located.  The minimum value of each index
!  should be 1, and the maximum value should be either nx or ny.
      xhill=xc(ihill)-xorig
      yhill=yc(ihill)-yorig
      call GETCELL('GETPRFM     ','GRID_M',1,xhill,yhill,zero,zero,&
                   ig,jg,md)
!  BVF default is determined from stability class and near-surface T
      if(istab(ig,jg,md) .LE. 4) then
         ptgrad=0.0
      elseif(istab(ig,jg,md) .EQ. 5) then
         ptgrad=ptg(1)
      else
         ptgrad=ptg(2)
      endif
! frr (09/01) new calmet format (2D temp)
      if(i2dmet.EQ.1) then
         temp=temp2d(ig,jg,md)
      elseif(i2dmet.EQ.0) then
!        Temperature at surface met station nearest hill
         issta=nears(ig,jg,md)
         temp=tempss(issta,md)
      else
         write(*,*)'Subr. GETPRFM:  Invalid I2DMET = ',i2dmet
         stop
      endif
      dfault=SQRT(9.80665*ptgrad/temp)
!  Assign variables to common.  Note that the B-V frequency is set
!  to zero if the temperature structure in a layer is not stably
!  stratified.
      nlev=nz
      do nl=1,nlev
         z(nl)=zgpt(nl)
!  Special case of nz=1:  met data may be from ISC-type input, so use
!  the wind speed as measured, not as scaled to zgpt.  The measured
!  speed was saved as "level 2".
         nlws=nl
         if(nz.EQ.1) nlws=nlws+1
         ws(nl)=SQRT(umet(ig,jg,nlws,md)**2+vmet(ig,jg,nlws,md)**2)
         if(LCALGRD) then
            abvf(nl)=0.
            if(nl .GT. 1) then
               sbvf=(9.80665/tmet(ig,jg,nl-1,md))&
                    *((tmet(ig,jg,nl,md)-tmet(ig,jg,nl-1,md))&
                    /(zgpt(nl)-zgpt(nl-1))+.0098)
               if(sbvf .GT. 0.) abvf(nl)=SQRT(sbvf)
            endif
         else
            abvf(nl)=dfault
         endif
      enddo
!  Set the B-V freq of the lowest layer equal to that in the adjacent
!  layer just above it (LCALGRD only)
      if(LCALGRD) abvf(1)=abvf(2)
      return
      end
!-----------------------------------------------------------------------
      subroutine getprfp(ssprf,tprf,zprf,nears,tempss,lcalgrd,&
                         istab,dpbl,el,z0m,ptg,nzprf)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 000602            GETPRFP
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Passes vertical profiles of wind speed and the Brunt-
!               Vaisala Frequency into common for use by CTSG-related
!               routines. Uses PROFILE.DAT arrays. (METFM=4)
!
! --- UPDATE
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V4.0-V5.0     971107  (DGS): check PROBLEM returned by XTPRF
!
! ARGUMENTS:
!    PASSED:   ssprf    scalar wind speed profile (m/s)            [ra]
!               tprf    temperature profile (K)                    [ra]
!               zprf    heights of data levels above surface (m)   [ra]
!               nears   array for met station nearest grid pt.     [ia]
!               tempss  temperatures at surface stations           [ra]
!               lcalgrd indicates temp profile available (T)        [l]
!               istab   stability class                            [ia]
!               dpbl    Boundary layer depth (m)                    [r]
!               el      Monin-Obukhov length (m)                    [r]
!               z0m     surface roughness length (m)                [r]
!               ptg     default pot. temp gradient (K/m) for the   [ra]
!                       two stable classes (E,F)
!               nzprf   number of profile levels in vertical        [i]
!  RETURNED:    none
!
! CALLING ROUTINES:     COMP
!
! EXTERNAL ROUTINES:    NEARS
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      real ssprf(mxprfz),tprf(mxprfz),zprf(mxprfz),t(mxprfz),&
           tempss(mxss),ptg(2)
      integer nears(mxnx,mxny)
      logical lcalgrd,problem
!  Obtain met data at the grid-point nearest the center of the hill.
!  Because METFM=4 provides data at one location, draw fields from (1,1)
      ig=1
      jg=1
!  Surface met station nearest hill
      issta=nears(ig,jg)
!  BVF default is determined from stability class and near-surface T
      if(istab .LE. 4) then
         ptgrad=0.0
      elseif(istab .EQ. 5) then
         ptgrad=ptg(1)
      else
         ptgrad=ptg(2)
      endif
      dfault=SQRT(9.80665*ptgrad/tempss(issta))
!  Assign variables to common.  Note that the B-V frequency is set
!  to zero if the temperature structure in a layer is not stably
!  stratified.
      problem=.FALSE.
      nlev=nzprf
      do nl=1,nlev
         z(nl)=zprf(nl)
         call XTPRF(nzprf,ssprf,zprf,z(nl),'spd',z0m,el,&
                    dpbl,istab,ptg,ws(nl),problem)
         call XTPRF(nzprf,tprf,zprf,z(nl),'tmp',z0m,el,&
                    dpbl,istab,ptg,t(nl),problem)
         if(LCALGRD) then
            abvf(nl)=0.
            if(nl .GT. 1) then
               sbvf=(9.80665/t(nl-1))*((t(nl)-t(nl-1))&
                    /(zprf(nl)-zprf(nl-1))+.0098)
               if(sbvf .GT. 0.) abvf(nl)=SQRT(sbvf)
            endif
         else
            abvf(nl)=dfault
         endif
      enddo
! --- Results are invalid if XTPRF reported PROBLEM=TRUE
      if(PROBLEM) then
         write(io6,*) 'GETPRFP:  FATAL ERROR reported when ',&
                      'extracting temp from PROFILE ---'
         write(io6,*) 'There are no valid data'
         write(*,*)
         stop 'Halted in GETPRFP -- see list file.'
      endif
!  Set the B-V freq of the lowest layer equal to that in the adjacent
!  layer just above it (LCALGRD only)
      if(LCALGRD) abvf(1)=abvf(2)
      return
      end
!-----------------------------------------------------------------------
      subroutine hdun(ihill)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 000602               HDUN
! ---            D. Strimaitis, SRC
!
! --- UPDATES
! --- V5.2-V5.4    000602  (DGS): add message to "stop"
! --- V5.1-V5.2    991104  (JSS): Error messages written to list
!                                 file as well as to screen
!
! PURPOSE:      Compute dividing-streamline height (Hd) and U/N above Hd.
!               Note that Hd is height above the grid-plane (m).
!
! ARGUMENTS:
!    PASSED:    ihill           hill ID number                       [i]
!  RETURNED:    none
!
! CALLING ROUTINES:     (main)
!
! EXTERNAL ROUTINES:    none
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      real lhs(mxlev),rhs(mxlev)
!  Identify height of hill above the grid-plane elevation
      h=relief(ihill)
!  Treat case of a single layer first (Hd = H - U/N)
      if(nlev .EQ. 1) then
         if(abvf(1) .EQ. zero) then
            hda(ihill)=zero
            ubyna(ihill)=999.
            return
         else
            ubyna(ihill)=ws(1)/abvf(1)
            hda(ihill)=AMAX1(zero,h-ubyna(ihill))
            return
         endif
      endif
!  Initialize working arrays
      do nl=1,nlev
         lhs(nl)=0.0
         rhs(nl)=0.0
      enddo
!  Find the first level whose elevation exceeds the top of the hill
      do nl=nlev,1,-1
         if(z(nl) .GT. h) ilev=nl
      enddo
!  Set up array of left-hand-side values of eqn. for hd
!  and check for neutral/unstable lapse rates throughout profile,
!  or "zero" wind speeds
      bvfsum=zero
      sumlhs=0.0
      do nl=1,ilev-1
         lhs(nl)=half*ws(nl)*ws(nl)
         sumlhs=sumlhs+lhs(nl)
         bvfsum=bvfsum+abvf(nl+1)
      enddo
      sumlhs=sumlhs+lhs(ilev)
      if(bvfsum .LE. zero) then
         hda(ihill)=zero
         ubyna(ihill)=999.
         return
      elseif(sumlhs.LE.0.0) then
         hda(ihill)=h
         ubyna(ihill)=zero
         return
      endif
!  Set up array of right-hand-side values of eqn. for hd
      zm=half*(z(ilev-1)+h)
      rhs(ilev-1)=abvf(ilev)*abvf(ilev)*((h-zm)*&
                  (h-z(ilev-1)))
      do nl=ilev-2,1,-1
         zm=half*(z(nl+1)+z(nl))
         rhs(nl)=rhs(nl+1)+&
                 abvf(nl+1)*abvf(nl+1)*((h-zm)*(z(nl+1)-z(nl)))
      enddo
!  Identify top of layer that contains hd
      it=ilev
      do nl=ilev-1,1,-1
         if(lhs(nl) .GE. rhs(nl)) it=nl
      enddo
!  Compute hd
      ib=it-1
      if(ib.EQ.0) then
         b=ws(it)/z(it)
         a=zero
      else
         b=(ws(it)-ws(ib))/(z(it)-z(ib))
         a=ws(it)-b*z(it)
      endif
      b2=b*b
      bvf2=abvf(it)*abvf(it)
!  Top of the layer containing Hd is the lesser of z(it) and H
      TOP=AMIN1(h,z(it))
      qa=b2-bvf2
      qb=two*(a*b+bvf2*h)
      qc=a*a-two*rhs(it)-bvf2*TOP*(two*h-TOP)
      qb2=qb*qb
      qac4=4.*qa*qc
      if(qac4 .LE. qb2) then
         deter=SQRT(qb2-qac4)
         hda(ihill)=(-qb+deter)/(two*qa)
         hda(ihill)=AMAX1(one,hda(ihill))
      else
         write(io6,*)'HDUN: Fatal Problem - IMAGINARY SOLUTION'
         write(io6,*)'Hill Ht                    = ',h
         write(io6,*)'Top of layer containing Hd = ',top
         write(io6,*)'b^2, 4ac                   = ',qb2,qac4
         write(io6,*)'Profile of Speed and B-V Freq.'
         do i=1,nlev
            write(io6,*)'z, ws, |BVF| = ',z(i),ws(i),abvf(i)
            write(io6,*)'z, LHS, RHS  = ',z(i),lhs(i),rhs(i)
         enddo
         write(*,*)
         stop 'Halted in HDUN -- see list file.'
      endif
!  Compute weighted speed and Brunt-Vaisala freq above top of layer
      ubar=zero
      bvfbar=zero
      do nl=it+1,ilev
         wt=z(nl)-z(nl-1)
         ubar=ubar+half*(ws(nl)+ws(nl-1))*wt
         bvfbar=bvfbar+abvf(nl)*wt
      enddo
!  Add contribution from layer between hd and z(it)
      wt=z(it)-hda(ihill)
      ubar=ubar+half*(ws(it)+a+b*hda(ihill))*wt
      bvfbar=bvfbar+abvf(it)*wt
!  Compute U/N
      ubyna(ihill)=ubar/bvfbar
      return
      end
!-----------------------------------------------------------------------
      real function hilhgt(x,y)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015             HILHGT
! ---            R. Yamartino, SRC
!
! PURPOSE:      (adapted from CTDM)
!               COMPUTES THE FRACTIONAL HILL HEIGHT AT
!               THE POSITION (X,Y) FOR A ROTATED GAUSSIAN HILL.
!
! ARGUMENTS:
!    PASSED:    X,Y     LOCATION AT WHICH HEIGHT IS NEEDED  (M)      [r]
!
! CALLING ROUTINES: PATH
!
! EXTERNAL ROUTINES: NONE
!-----------------------------------------------------------------------
      INCLUDE 'flvar.puf'
!                                                                       HHT00260
!       DEFINE ARGUMENTS                                                HHT00270
      REAL      X, Y                                                   ! HHT00280
!                                                                       HHT00290
!       DEFINE LOCAL VARIABLES                                          HHT00300
      REAL      Q, XDLX, YDLY                                          ! HHT00310
!                                                                       HHT00320
      XDLX = X * LXI                                                   ! HHT00340
      YDLY = Y * LYI                                                   ! HHT00350
      Q = XDLX*XDLX + YDLY*YDLY + 2.0*GAM*X*Y                          ! HHT00360
!  NOTE THAT HILROT PROVIDES CROSSTERM GAM FOR A ROTATED HILL           HHT00370
      IF(Q .GT. 30.) Q = 30.                                           ! HHT00380
      HILHGT = EXP(-Q)                                                 ! HHT00390
!                                                                       HHT00400
      RETURN                                                           ! HHT00410
      END                                                              ! HHT00420
!-----------------------------------------------------------------------
      subroutine hilrot(ah,bh,psi)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015             HILROT
! ---            R. Yamartino, SRC
!
! PURPOSE:      (adapted from CTDM)
!     COMPUTES THE NEEDED LENGTH SCALES LX AND LY AND THE
!     FACTOR GAM FOR A GAUSSIAN HILL HAVING MAJOR AXIS AH, MINOR AXIS
!     BH, AND ROTATED CCW BY AN ANGLE PSI.
!     WHEN PSI=0 THE MINOR AXIS IS ORIENTED ALONG THE X-AXIS (OR FLOW
!     DIRECTION) AND THE MAJOR AXIS LIES ALONG THE Y-AXIS.
!
! ARGUMENTS:
!    PASSED:    AH,BH   MAJOR AND MINOR HILL SEMI-AXIS LENGTHS (M)   [r]
!               PSI     ANGLE OF ROTATION (RADIANS)                  [r]
!  RETURNED:    NONE
!
! CALLING ROUTINES: CTPAR
!
! EXTERNAL ROUTINES: NONE
!----------------------------------------------------------------------
      real lai2,lbi2
      INCLUDE 'flvar.puf'
!                                                                       HRT00370
      CPSI = COS(PSI)                                                  ! HRT00390
      CPSI2 = CPSI*CPSI                                                ! HRT00400
      SPSI = SIN(PSI)                                                  ! HRT00410
      SPSI2 = SPSI*SPSI                                                ! HRT00420
      LAI2 = 1.0 / (AH*AH)                                             ! HRT00430
      LBI2 = 1.0 / (BH*BH)                                             ! HRT00440
!                                                                       HRT00450
      GAM = (LBI2-LAI2)*CPSI*SPSI                                      ! HRT00460
      LXI2 = CPSI2*LBI2 + SPSI2*LAI2                                   ! HRT00470
      lxi=SQRT(lxi2)
      lx=1.0/lxi
      LYI2 = CPSI2*LAI2 + SPSI2*LBI2                                   ! HRT00490
      lyi=SQRT(lyi2)
      ly=1.0/lyi
      lyi3=lyi2*lyi
!                                                                       HRT00510
      RETURN                                                           ! HRT00520
      END                                                              ! HRT00530
!-----------------------------------------------------------------------
        subroutine inprec
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521             INPREC
!
! HISTORY: This subroutine is adapted from the CTDMPLUS model with
!          modifications to allow its use within CALPUFF.  Data needed
!          for CTSG are extracted from CTDM arrays.
!
! PURPOSE: THIS ROUTINE IS RESPONSIBLE FOR READING THE RECEPTOR DATA
!
! LIMITATIONS:
!       A MAXIMUM OF 'mxrect' RECEPTORS
!
! ARGUMENTS
!   PASSED:     none
!   Common block /PARAMS/ variables:
!               mxrect          maximum number of CTSG receptors     [r]
!   Common block /CTSGDAT/ variables:
!               xhill2m         convert user lengths to meters       [r]
!               zhill2m         convert user heights to meters       [r]
!               xctdmkm         x-origin(km) of CTDM in CALPUFF sys  [r]
!               yctdmkm         y-origin(km) of CTDM in CALPUFF sys  [r]
!               xmetct0m        x-origin(m) of CTDM from SW MET grid [r]
!               ymetct0m        y-origin(m) of CTDM from SW MET grid [r]
!
!   RETURNED:   none
!               (Data arrays are loaded into /CTPASS/ and /CTSGDAT/)
!   Common block /CTSGDAT/ variables:
!               ihill(mxrect)   Hill number associated with each    [ia]
!                               complex terrain receptor
!               elrect(mxrect)  Ground elevation (m MSL) of each    [ra]
!                               complex terrain receptor
!               nctrec          number of CTSG receptors             [i]
!   Common block /CTPASS/ variables:
!                xrctm(mxrect)  CTSG receptor coordinates in meters [ra]
!                yrctm(mxrect)  CTSG receptor coordinates in meters [ra]
!
! I/O:
!
!       LINE  VARIABLE  COLUMNS FORMAT  DESCRIPTION
!
!         1      NAME      1-16   A16   RECEPTOR NAME
!                   X     21-30   F10.0 X-COORD (USER HORIZONTAL UNITS)
!                   Y     31-40   F10.0 Y-COORD (USER HORIZONTAL UNITS)
!                  zZ     41-50   F10.0 HT ABOVE GROUND (USER VERTICAL
!                                               UNITS)
!                  GE     51-60   F10.0 GROUND ELEVATION (USER VERTICAL
!                                               UNITS)
!                  NH     61-65     I5  HILL NUMBER FOR THIS RECEPTOR
!
!
! CALLING ROUTINES: CTINIT
!
!-----------------------------------------------------------------------
!
      include   'params.puf'
      include   'ctpass.puf'
      include   'ctsgdat.puf'
!
!       DEFINE LOCAL VARIABLES
        REAL    X, Y, zZ, GE
        INTEGER NH, NR, NO, YES, FLAT
        CHARACTER*16    NAME
        DATA    NO/0/, YES/1/, FLAT/0/
!
        NR = 0
        NRFLAT = NO
! ---   'Use' nrflat (compiler warning)
        nrflat=nrflat
!
!  Set local i/o unit variables
        inrec=io29
        iout=io6
!  Write initial header to the list-file
        write(iout,5020)
        WRITE(IOUT,6000)
!       LOOP ON RECEPTOR INPUT LINES, TERMINATE ON EOF
100     CONTINUE
            READ(INREC, 1010, END=900) NAME,X,Y,zZ,GE,NH
!           RECEPTORS ON FLAT TERRAIN FLAG
            IF(NH .EQ. FLAT) then
               NRFLAT = YES
            else
               NR = NR + 1
               IF(NR .GT. mxrect) GO TO 991
               WRITE(IOUT, 6020) NR,NAME,X,Y,zZ,GE,NH
! ---          Set receptor data in /CTSGDAT/
               elrect(NR)=ge*zhill2m
               ihill(NR) = NH
! ---          Compute receptor location as meters in MET grid
! ---          for /CTPASS/
               xrctm(NR)=X*xhill2m + xmetct0m
               yrctm(NR)=Y*xhill2m + ymetct0m
            endif
        GO TO 100
!       END RECEPTOR INPUT LOOP
900     nctrec = NR
!       FOOTNOTE
        WRITE(IOUT,6030) xhill2m,zhill2m
        write(iout,6040) xctdmkm,yctdmkm
        write(iout,*)'   NOTE:  All receptors are placed on the ground!'
        RETURN
!
!       ERROR SECTION
!
991     WRITE(IOUT,9910) nr,mxrect
        write(*,*)
        stop 'Halted in INPREC -- see list file.'
!       FORMAT SECTION
1010    FORMAT(A16,4X,4F10.0,I5)
!
5020  format(1x,//'INFORMATION PROVIDED IN CTDM RECEPTOR FILE'//)
!
6000    FORMAT(/,27X,'      RECEPTOR INFORMATION',//,&
        ' REC   IDENTIFICATION    EAST      NORTH  HEIGHT  ABOVE   ',&
        'GRD LVL',/,&
        ' NO.                     COORD     COORD  LOCAL GRD LVL  ',&
        'ELEVATION     HILL',/,&
        '                           (USER UNITS)    (USER UNITS) ',&
        '(USER UNITS)  NUMBER',/,&
        ' ---  ----------------  -------- -------- -------------  ',&
        '----------   ------')
6010    FORMAT(' ')
6020    FORMAT(I4,2X,A16,1X,F9.2,F9.2,5X,F7.1,5X,F7.1,7X,I2)
6030    FORMAT(' ---------------------------------------------',/,&
        '   MULTIPLY HORIZONTAL USER UNITS BY:',1PE10.3,' TO CONVERT ',&
                'TO METERS',/,&
        '   MULTIPLY VERTICAL USER UNITS BY:',1PE10.3,' TO CONVERT ',&
                'TO METERS')
6040    FORMAT(/,&
        '   ADD  ',1PE10.3,' KILOMETERS to reference X-Coordinate ',&
                'to CALPUFF coordinate system',/,&
        '   ADD  ',1PE10.3,' KILOMETERS to reference Y-Coordinate ',&
                'to CALPUFF coordinate system')
9910    FORMAT(//' ***** RECEPTOR INPUT ERROR *****'/' INVALID ',&
        ' NUMBER OF RECEPTORS FOUND   : nr     =',I4,/'         ',&
        ' NUMBER OF RECEPTORS ALLOWED : mxrect =',I4)
        END
!-----------------------------------------------------------------------
        subroutine inpter
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521             INPTER
!
! HISTORY: This subroutine is taken from the CTDMPLUS model with few
!          modifications to allow its use within CALPUFF.  Data needed
!          for CTSG are extracted from CTDM arrays.
!
! PURPOSE: THIS ROUTINE IS RESPONSIBLE FOR READING THE TERRAIN
!          INFORMATION FOR EACH HILL.
!
! ASSUMPTIONS: THIS FIRST CONTOUR HEIGHT IS LESS THAN OR EQUAL TO THE
!               LOWEST STACK BASE ELEVATION
!
! LIMITATIONS:
!       A MAXIMUM OF 'MXHILL' HILLS
!       A MAXIMUM OF 'MXCNTR HEIGHTS FOR HILL SHAPE INFORMATION
!
! ARGUMENTS:
!   PASSED:     none
!   Common block /CTSGDAT/ variables:
!               xhill2m         convert user lengths to meters       [r]
!               zhill2m         convert user heights to meters       [r]
!               xctdmkm         x-origin(km) of CTDM in CALPUFF sys  [r]
!               yctdmkm         y-origin(km) of CTDM in CALPUFF sys  [r]
!               xmetct0m        x-origin(m) of CTDM from SW MET grid [r]
!               ymetct0m        y-origin(m) of CTDM from SW MET grid [r]
!               nhill           number of hills stated in input file [i]
!
!   RETURNED:   none
!               (Data arrays are loaded into /CTPASS/ and /CTSGDAT/)
!
!
! I/O:
!  INPUT: UNIT=INTERR
!
!       THE FOLLOWING INDICATE THE GROUP OF INPUT LINES EXPECTED
!       FOR EACH HILL.
!
!       LINE  VARIABLE  COLUMNS FORMAT  DESCRIPTION
!         1      NH        6- 7   I2    HILL NUMBER
!                                         FOR THIS RECEPTOR GROUP
!                NZ        9-10   I2    NUMBER OF HEIGHTS FOR WHICH
!                                         CONTOUR AND CUTOFF HILL SHAPE
!                                         INFORMATION IS GIVEN
!                HTP      21-30  F10.0  HEIGHT OF TOP OF HILL
!                                         ABOVE GRID ZERO, USER UNITS
!                HILNAM   41-80   A40   DESCRIPTION FOR THIS HILL
!
!       NEXT NZH LINES, ONE FOR EACH BEST-FIT ELLIPSE
!
!                  ZH      1-10  F10.0  ELEVATION OF BEST-FIT ELLIPSE,
!                                       USER COORDINATES
!                 XHW     11-20  F10.0  X-COORD (USER HORIZONTAL UNITS)
!                                       OF ELLIPSE CENTER
!                 YHW     21-30  F10.0  Y-COORD (USER HORIZONTAL UNITS)
!                                       OF ELLIPSE CENTER
!              MAJORW     31-40  F10.0  ORIENTATION OF MAJOR AXIS OF
!                                       ELLIPSE (DEGREES CLOCKWISE FROM
!                                       NORTH)
!              MAJAXW     41-50  F10.0  LENGTH OF MAJOR SEMIAXIS, USER
!                                       COORDINATES
!              MINAXW     51-60  F10.0  LENGTH OF MINOR SEMIAXIS, USER
!                                       COORDINATES
!
!       NEXT NZH LINES, ONE FOR EACH CUT-OFF HILL
!
!                  ZH      1-10  F10.0  ELEVATION OF BASE OF CUT-OFF
!                                       HILL, USER COORDINATES
!                 XHL     11-20  F10.0  X-COORD (USER HORIZONTAL UNITS)
!                                       OF CENTER OF CUT-OFF HILL
!                 YHL     21-30  F10.0  Y-COORD (USER HORIZONTAL UNITS)
!                                       OF CENTER OF CUT-OFF HILL
!              MAJORL     31-40  F10.0  ORIENTATION OF MAJOR AXIS OF
!                                       CUT-OFF HILL (DEGREES CLOCKWISE
!                                       FROM NORTH)
!              EXPOMA     41-50  F10.0  EXPONENT IN INVERSE POLYNOMIAL
!                                       FUNCTION FOR CUT-OFF HILL SHAPE
!                                       FOR MAJOR AXIS CROSS SECTION
!              EXPOMI     51-60  F10.0  EXPONENT IN INVERSE POLYNOMIAL
!                                       FUNCTION FOR CUT-OFF HILL SHAPE
!                                       FOR MINOR AXIS CROSS SECTION
!              SCALMA     61-70  F10.0  SCALE LENGTH IN INVERSE
!                                       POLYNOMIAL FUNCTION FOR CUT-OFF
!                                       HILL SHAPE FOR MAJOR AXIS CROSS
!                                       SECTION
!              SCALMI     71-80  F10.0  SCALE LENGTH IN INVERSE
!                                       POLYNOMIAL FUNCTION FOR CUT-OFF
!                                       HILL SHAPE FOR MINOR AXIS CROSS
!                                       SECTION
!
!
!       THIS GROUP OF INPUT LINES SHOULD BE REPEATED FOR EACH HILL.
!
!
! CALLING ROUTINES:  CTINIT
!
! EXTERNAL ROUTINES: none
!
!-----------------------------------------------------------------------
!
      include   'params.puf'
      include   'ctpass.puf'
      include   'ctsgdat.puf'
!
!       DEFINE LOCAL VARIABLES
        REAL    EL, HTP, SMALL
        INTEGER NH, NZ, I
!
        INTEGER         NHILLS, HILNAM(10,MXHILL)
        REAL            THS(MXHILL),&
                        XHW(MXCNTR,MXHILL), YHW(MXCNTR,MXHILL),&
                        MAJORW(MXCNTR,MXHILL),&
                        XHL(MXCNTR,MXHILL), YHL(MXCNTR,MXHILL),&
                        MAJORL(MXCNTR,MXHILL),&
                        EXPOMA(MXCNTR,MXHILL), EXPOMI(MXCNTR,MXHILL),&
                        SCALMA(MXCNTR,MXHILL), SCALMI(MXCNTR,MXHILL)
!      (CTPASS contains the remaining CTDM variables ---
!                       NZH(MXHILL), ZH(MXCNTR,MXHILL),
!                       MAJAXW(MXCNTR,MXHILL), MINAXW(MXCNTR,MXHILL)  )
!
        DATA    SMALL/0.00001/
!
        NHILLS = 0
!
!  Set local i/o unit variables
      interr=io28
      iout=io6
!  Write initial header to the list-file
      write(iout,5020)
!
! --- Add test for number of hills greater than MXHILL
100   if(nhills.EQ.mxhill) then
! ---   This read should find an end-of-file (and transfer to 500)
        READ( INTERR, 1010, ERR=500, END=500) NH
! ---   Oops... too many hills in this file
        nhills=nhills+1
        write(iout,1100) nhills,mxhill
1100    format(//,10x,'NUMBER OF HILLS = ',i4,&
            '; EXCEEDS MAXIMUM ALLOWABLE (MXHILL) = ',i4)
        write(*,*)
        stop 'Halted in INPTER -- see list file.'
      else
        READ( INTERR, 1010, ERR=9000, END=500) NH, NZ, HTP,&
                                (HILNAM(I,NHILLS+1),I=1,10)
1010    FORMAT(5X,I2,1X,I2,10X,F10.0,10X,10A4)
        NHILLS = NHILLS + 1
!       CHECK FOR OUT OF SEQUENCE
        IF( NH .NE. NHILLS ) THEN
            WRITE( IOUT, 1110) NHILLS, NH
1110    FORMAT(//,10X,'HILL NUMBER OF OUT SEQUENCE--',I2,' WAS ',&
        'EXPECTED; ',I2,' WAS READ')
            write(*,*)
            stop 'Halted in INPTER -- see list file.'
        ENDIF
!       CHECK FOR ARRAY OUT OF BOUNDS
        IF( NZ .GT. mxcntr ) THEN
            WRITE(IOUT,1120) NZ,mxcntr
1120    FORMAT(//,10X,'NUMBER OF CONTOURS/CUT-OFF HILLS = ',I4,&
            '; EXCEEDS MAXIMUM ALLOWABLE (MXCNTR) = ',i4)
            write(*,*)
            stop 'Halted in INPTER -- see list file.'
        ENDIF
!
        WRITE( IOUT, 6020 ) NH, (HILNAM(I,NH),I=1,10), HTP
        WRITE( IOUT, 6030 ) (HILNAM(I,NH),I=1,10)
      endif
!
!       READ VARIABLES FOR WRAP: ELLIPSE INFORMATION
!
        DO 200 I = 1,NZ
            READ(INTERR,1040,ERR=9000) ZH(I,nh),XHW(I,NH),YHW(I,NH),&
                                MAJORW(I,NH),MAJAXW(I,NH),MINAXW(I,NH)
1040        FORMAT(6F10.0)
            WRITE(IOUT,6040) ZH(I,nh),XHW(I,NH),YHW(I,NH),MAJORW(I,NH),&
                                MAJAXW(I,NH),MINAXW(I,NH)
200     CONTINUE
!
!       READ VARIABLES FOR LIFT: CUT-OFF HILL INFORMATION
!
        WRITE(IOUT,6050) (HILNAM(I,NH),I=1,10)
        DO 300 I = 1,NZ
            READ(INTERR,1050,ERR=9000) EL,XHL(I,NH),YHL(I,NH),MAJORL(I,&
                NH),EXPOMA(I,NH),EXPOMI(I,NH),SCALMA(I,NH),SCALMI(I,NH)
1050        FORMAT(8F10.0)
            IF(ABS(EL-ZH(I,nh)).GT.SMALL) THEN
                WRITE(IOUT,1140) I,EL,I,ZH(I,nh)
1140            FORMAT(//,10X,'CUT-OFF HILL BASE # ',I2,' = ',F7.2,&
                        '; DOES NOT AGREE WITH THE CORRESPONDING ',&
                        'CONTOUR # ',I2,' HEIGHT OF ',F7.2)
                write(*,*)
                stop 'Halted in INPTER -- see list file.'
            ENDIF
            WRITE(IOUT,6060) EL,XHL(I,NH),YHL(I,NH),MAJORL(I,NH),&
                                EXPOMA(I,NH),EXPOMI(I,NH),&
                                SCALMA(I,NH),SCALMI(I,NH)
300     CONTINUE
!
!       CONVERT TO METERS USING xhill2m AND zhill2m, and shift origin
!       to place hill in CALPUFF MET GRID system
!
        NZH(NH) = NZ
        THS(NH) = HTP * zhill2m
        DO 400 I = 1,NZ
            ZH(I,NH) = ZH(I,NH) * zhill2m
            XHW(I,NH) = XHW(I,NH) * xhill2m + xmetct0m
            YHW(I,NH) = YHW(I,NH) * xhill2m + ymetct0m
            XHL(I,NH) = XHL(I,NH) * xhill2m + xmetct0m
            YHL(I,NH) = YHL(I,NH) * xhill2m + ymetct0m
            MAJAXW(I,NH) = MAJAXW(I,NH) * xhill2m
            MINAXW(I,NH) = MINAXW(I,NH) * xhill2m
            SCALMA(I,NH) = SCALMA(I,NH) * xhill2m
            SCALMI(I,NH) = SCALMI(I,NH) * xhill2m
400     CONTINUE
!       READ NEXT HILL INFO
        GO TO 100
!       END OF TERRAIN INPUT
500     CONTINUE
        WRITE(IOUT,6100) xhill2m,zhill2m
        write(iout,6110) xctdmkm,yctdmkm
        WRITE (IOUT,6091)
!
! --- CTSG Section for "LIFT" description ---
!  Transfer hill information to common/CTPASS/.  Note that the hill
!  description parameters for the polynomial hill are those for the
!  entire hill.  The ellipse information selected later for the flow
!  below Hd will depend on the hourly value of the dividing streamline.
!  The array index for the contour at the base of the hill is found
!  by calling KLOSE with a third argument equal to zero.
! --- Set the number of hills (in /CTSGDAT/) to that found(nhills)
      nhill=nhills
      do i=1,nhill
         klow=KLOSE(zh(1,i),nzh(i),zero)
         if(klow .EQ. 0) klow=1
         xc(i)=xhl(klow,i)
         yc(i)=yhl(klow,i)
         thetah(i)=majorl(klow,i)
         expo(1,i)=expoma(klow,i)
         expo(2,i)=expomi(klow,i)
         scale(1,i)=scalma(klow,i)
         scale(2,i)=scalmi(klow,i)
         axmax(1,i)=1000.*scale(1,i)
         axmax(2,i)=1000.*scale(1,i)
! ---    Set zgrid equal to the base contour elevation
         zgrid(i)=zh(klow,i)
! ---    Change height reference from m (MSL) to m above hill base
         relief(i)=ths(i)-zgrid(i)
!
! ---    Swap selected data to arrays in Subgroup 6b from input file
         hilldat(3,i) = thetah(i)
         hilldat(4,i) = zgrid(i)
         hilldat(5,i) = relief(i)
         hilldat(6,i) = expo(1,i)
         hilldat(7,i) = expo(2,i)
         hilldat(8,i) = scale(1,i)
         hilldat(9,i) = scale(2,i)
         hilldat(10,i) = axmax(1,i)
         hilldat(11,i) = axmax(2,i)
!
      enddo
!  Note that axmax is set to a number that is large compared to the
!  scale of the major axis of the hill (scale(1,i)).  This makes the
!  specification of the inverse polynomial hill consistent between
!  the CTDM and the CTSG formulations.
        RETURN
!
!       TERMINAL ERROR
9000    WRITE(IOUT,9005)
9005    FORMAT(//,10X,'ERROR IN DATA READ IN HILL INPUT')
        GO TO 9999
9999    write(*,*)
        stop 'Halted in INPTER -- see list file.'
!
5020  format(1x,//'INFORMATION PROVIDED IN THE CTDM HILL FILE'//)
!
6020    FORMAT(/' HILL #',I2,'  ',10A4,'HILL TOP:',F7.1,&
                ' (USER UNITS)')
6030    FORMAT(/' BEST FIT ELLIPSE INFORMATION FOR WRAP: ',10A4,/&
        '  CONTOUR  X-COORD   Y-COORD   MAJOR  AXIS   ELLIPSE AXIS ',&
        'LENGTHS',/,&
        '   HEIGHT    (HILL CENTER)     AZIM. FROM N    MAJOR      ',&
        'MINOR',/,&
        '  -------  --------  --------  ------------  --------   ',&
        '--------')
6040    FORMAT(F9.1,2F10.3,F11.1,2X,2F11.3)
6050    FORMAT(/' Hc CUT-OFF HILL INFORMATION FOR LIFT:  ',10A4,/,&
        '  CONTOUR  X-COORD  Y-COORD  MAJOR  AXIS  <--- INVERSE ',&
        'POLYNOMIAL VARIABLES --->',/,&
        '   HEIGHT    (HILL CENTER)  AZIM. FROM N  MAJ EXP MIN EXP   ',&
        'MAJ SCALE  MIN SCALE',/,&
        '  -------  -------- ------- ------------  ------- -------   ',&
        '---------  ---------')
6060    FORMAT(F9.1,F10.3,F9.3,F10.1,F11.3,F8.3,F12.3,F11.3)
6091    FORMAT(/)
6100    FORMAT(' ---------------------------------------------',/,&
        '   MULTIPLY HORIZONTAL USER UNITS BY:',1PE10.3,' TO CONVERT ',&
                'TO METERS',/,&
        '   MULTIPLY VERTICAL USER UNITS BY:',1PE10.3,' TO CONVERT ',&
                'TO METERS')
6110    FORMAT(/,&
        '   ADD  ',1PE10.3,' KILOMETERS to reference X-Coordinate ',&
                'to CALPUFF coordinate system',/,&
        '   ADD  ',1PE10.3,' KILOMETERS to reference Y-Coordinate ',&
                'to CALPUFF coordinate system')
        END
!-----------------------------------------------------------------------
      integer function ireg(x,x12,x23)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015               IREG
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Determines position of a point relative to boundaries
!               of the CTSG modeling regions.
!
! ARGUMENTS:
!    PASSED:    x       position (coordinate normal to regions)      [r]
!               x12     boundary upwind of hill                      [r]
!               x23     boundary downwind of hill                    [r]
!  RETURNED:    ireg    region (1, 2, or 3)                          [i]
!
! CALLING ROUTINES:     CTREC, CTPAR
!
! EXTERNAL ROUTINES:    none
!-----------------------------------------------------------------------
      ireg=2
      if(x .LT. x12) then
         ireg=1
      elseif(x .GT. x23) then
         ireg=3
      endif
      return
      end
!-----------------------------------------------------------------------
      integer function klose(XA,NA,X)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 940831              KLOSE
! ---            Adopted from CTDMPLUS without modification
!
! PURPOSE: THIS FUNCTION RETURNS THE POSITION OF THE DATA VALUE IN ARRAY
!               XA WHICH IS NEAREST TO THE VALUE X BUT LESS THAN X
!
! ASSUMPTIONS:
!       ARRAY XA IS SORTED IN ASCENDING ORDER
!
! LIMITATIONS:  IF XA ARE ALL > X, SET KLOSE = 0
!
! ARGUMENTS
!  PASSED:
!       XA      REAL    ARRAY OF VALUES TO BE SEARCHED
!       NA      INT     DIMENSION OF XA
!       X       REAL    VALUE TO BE SEARCHED FOR
!  RETURNED FUNCTION VALUE:
!       KLOSE   INT     ARRAY SUBSCRIPT OF NEAREST VALUE TO X
!
! I/O: NONE
!
! CALLING ROUTINES: INPTER, CTPAR
!
! EXTERNAL ROUTINES: NONE
!
! INTRINSIC FUNCTIONS: ABS
!
! COMMON BLOCKS: NONE
!
!-----------------------------------------------------------------------
!
!       DEFINE ARGUMENTS
        INTEGER NA
        REAL    XA(NA), X
!
!       IF NO XA VALUES ARE LESS THAN X, SET KLOSE = 0
!
        IF( XA(1) .GT. X ) THEN
                KLOSE = 0
        ELSE IF( XA(1) .EQ. X ) THEN
                KLOSE = 1
        ELSE
                DO  10  KLOSE = NA, 1, -1
                        IF( XA(KLOSE) .LT. X ) RETURN
10              CONTINUE
        ENDIF
        RETURN
        END
!-----------------------------------------------------------------------
      subroutine lower(c)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521              LOWER
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes concentrations due to puff material in lower
!               segment of the flow (below Hd).
!
! ARGUMENTS:
!    PASSED:    none
!  RETURNED:    c               concentration  (g/m**3)              [r]
!
! CALLING ROUTINES:     CTSG
!
! EXTERNAL ROUTINES:    MUNU, SIGMA, ERF
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
!  Initialize concentration that is returned
      c=zero
!  Determine if puff travels along the same or opposite side of the hill
!       from the receptor
      call MUNU(1,xre*baxi,yre*baxi,r,rmu,rnu)
      side=SIGN(one,SIN(rnu+alfw))
!  Compute the age of puff at the receptor (tup)
      tup=timpg+t12rl
!  If the age of the puff at the receptor is not positive, concentration
!  is zero!
      if(tup .LE. 0.) return
!  Set receptor-specific sigmas; note that these sigmas are constrained
!  by the puff-sigmas at the start and end of the step
!  Receptor may be shifted below Hd, so compute estimate here
!     sz=szr
!     sy=syr
! old sz=SIGMA(tup,sz1,tstart,sz2,tstep,szr,frac)
      sz=SIGMA(2,zpuff,u,tup,sz1,tstart,sz2,tstep,szr,frac)
      sz=AMAX1(sz1,sz)
      sz=AMIN1(sz2,sz)
! old sy=SIGMA(tup,sy1,tstart,sy2,tstep,syr,frac)
      sy=SIGMA(1,zpuff,u,tup,sy1,tstart,sy2,tstep,syr,frac)
      sy=AMAX1(sy1,sy)
      sy=AMIN1(sy2,sy)
      szsq=sz*sz
      sysq=sy*sy
!  Assign sigmas at the impingement point
      szo=szimpg
      szosq=szo*szo
      syo=syimpg
      syosq=syo*syo
!  Compute sigma-prime squared (both y and z)
      szpsq=szsq-szosq
      sypsq=sysq-syosq
!  Reset impingement sigmas if they exceed receptor-specific values --
!  this turns off hill-effects as the puff has not yet reached the hill
      if(szpsq.LE.zero) then
         szo=sz
         szosq=szsq
         szpsq=zero
         szp=zero
      else
         szp=SQRT(szpsq)
      endif
      if(sypsq.LE.zero) then
         syo=sy
         syosq=sysq
         sypsq=zero
         syp=zero
      else
         syp=SQRT(sypsq)
      endif
!  Puff is mixed in the vertical if sigmaz upwind of the hill (szo)
!  exceeds 1.6 times the mixing height (zmix).  Set mixing flag to 1.
      imix=0
      if(szo .GE. 1.6*zlid) imix=1
!  Compute the concentration on the trajectory ("centerline")
      t1mtr=-(t12p+t12rl)
      t2mtr=tstep+t1mtr
      dum=u/(rt2*sy)
      co=q*(ERF(t2mtr*dum)-ERF(t1mtr*dum))/(tstep*four*pi*u*sy*sz)
      if(co.LE.zero) return
!  Compute the horizontal distribution factor
      argy1=half*(d*d/sysq)
      if(argy1 .LT. expmax) then
         argy2=(d*syp)/(rt2*syo*sy)
         fy=EXP(-argy1)*(one+side*ERF(argy2))
!  Compute the vertical distribution factor
!  - If puff is mixed in the vertical, set fz to rt2pi*sz/zlid and
!    calculate concentration.
         if(imix .EQ. 1) then
            fz=rt2pi*sz/zlid
            c=co*fy*fz
            return
         endif
         twofz=zero
         if(szp .GT. small) then
            ak=one/(rt2*sz*szo*szp)
            b1=hd*szsq
!  - First term (no reflections from mixing lid); i=1, j=1:
            ejp=zrec
            eoip=zpuff
            call BJIL(ak,b1,ejp,eoip,bj)
            twofz=bj
!  - Skip reflections when contribution without reflections is zero
            if(twofz .EQ. zero) goto 501
!  - Reflections just from lid upwind of hill; j=1, i>1:
            ejp=zrec
            do 100, i=2,mxrefl
               eoip=two*(i-1)*zlid+zpuff
               eoim=eoip-two*zpuff
               call BJIL(ak,b1,ejp,eoip,bjp)
               call BJIL(ak,b1,ejp,eoim,bjm)
               add=bjp+bjm
               twofz=twofz+add
               if(add/twofz .LT. epsrefl) goto 101
100         continue
!  - Reflections just from lid over and downwind of hill; i=1, j>1:
101         eoip=zpuff
            do 200, j=2,mxrefl
               ejp=two*(j-1)*zlid+zrec
               ejm=ejp-two*zrec
               call BJIL(ak,b1,ejp,eoip,bjp)
               call BJIL(ak,b1,ejm,eoip,bjm)
               add=bjp+bjm
               twofz=twofz+add
               if(add/twofz .LT. epsrefl) goto 201
200         continue
!  - Reflections throughout; i>1, j>1:
!  - Loop on i>1:
201         do 400 i=2,mxrefl
               eoip=two*(i-1)*zlid+zpuff
               eoim=eoip-two*zpuff
!  - Loop on j>1:
               do 300 j=1,mxrefl
                  ejp=two*(j-1)*zlid+zrec
                  ejm=ejp-two*zrec
                  call BJIL(ak,b1,ejp,eoip,bjpp)
                  call BJIL(ak,b1,ejm,eoip,bjmp)
                  call BJIL(ak,b1,ejp,eoim,bjpm)
                  call BJIL(ak,b1,ejm,eoim,bjmm)
                  add=bjpp+bjmp+bjpm+bjmm
                  twofz=twofz+add
                  if(add/twofz .LT. epsrefl) then
                     if(j .GT. 1) goto 400
                     goto 501
                  endif
300            continue
400         continue
         else
!  - Sigma-z is not growing, szp is very small and equations simplify
!  - Only j=1 terms contribute (no reflection from lid over hill)
!  - 2Fz is zero for all receptors above Hd
            if(zrec .LE. hd) then
               ak=half/szsq
               ejp=zrec
               eoip=zpuff
               eoipmj=eoip-ejp
               eoippj=eoip+ejp
               call BI(ak,eoipmj,eoippj,twofz)
               if(twofz.EQ.zero) goto 501
!  - Reflections from lid upwind of hill; i>1:
               do 500 i=2,mxrefl
                  eoip=two*(i-1)*zlid+zpuff
                  eoim=eoip-two*zpuff
                  eoipmj=eoip-ejp
                  eoippj=eoip+ejp
                  eoimmj=eoim-ejp
                  eoimpj=eoim+ejp
                  call BI(ak,eoipmj,eoippj,bip)
                  call BI(ak,eoimmj,eoimpj,bim)
                  add=bip+bim
                  twofz=twofz+add
                  if(add/twofz .LE. epsrefl) goto 501
500            continue
            endif
         endif
501      fz=half*twofz
!  Compute concentration
         c=co*fy*fz
      endif
!
      if(ldb) then
         write(idebug,*)
         write(idebug,*)'LOWER: co,fy,fz =',co,fy,fz
         write(idebug,*)'(START) syo,szo =',syimpg,szimpg
         write(idebug,*)'(USED)  syo,szo =',syo,szo
         write(idebug,*)'(START) syr,szr =',syr,szr
         write(idebug,*)'(USED)  syr,szr =',sy,sz
         write(idebug,*)'       co,fy,fz =',co,fy,fz
         write(idebug,*)'t12p,t12rl,tstep=',t12p,t12rl,tstep
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine munu(muflag,xbi,ybi,r,mu,nu)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 910620              MUNU
! ---            D. Strimaitis, SRC
!
!  PURPOSE:     Computes the elliptical coordinates of a point that is
!               referenced to the system centered on the ellipse, with
!               x-axis along the major axis.
!
!  ARGUMENTS:
!     PASSED:   muflag          flag signalling no mu calculation    [i]
!                               (1: do not calculate mu)
!               (xbi,ybi)       scaled coord (x/bax,y/bax)           [r]
!               r               ratio of axes aax/bax                [r]
!   RETURNED:   (mu,nu)         elliptical coordinates               [r]
!
!  CALLING ROUTINES:    CTPAR, LOWER
!
!  EXTERNAL ROUTINES:   none
!----------------------------------------------------------------------
      INCLUDE 'const.puf'
      real mu,nu
!
      absx=ABS(xbi)
      absy=ABS(ybi)
      xsq=xbi*xbi
      ysq=ybi*ybi
!  If point lies very near an axis, place it on the axis
      if(absx .LT. small) then
         xbi=zero
         absx=zero
      endif
      if(absy .LT. small) then
         ybi=zero
         absy=zero
      endif
!  If axes are nearly equal, treat hill as a symmetric hill
      rsq=r*r
      rp1=r+one
      rsqm1=rsq-one
      if(rsqm1 .LT. small) then
         rsqm1=zero
         rp1=two
      endif
      rdsq=xsq+ysq
!     rd=SQRT(rdsq)
!  ---- Compute nu -----------
!  Special cases
      if(xbi .EQ. zero) then
         nu=piby2
         if(ybi .LT. zero) nu=three*piby2
      elseif(ybi .EQ. zero) then
         nu=zero
         if(xbi .LT. zero) nu=pi
      elseif(rsqm1 .EQ. zero) then
         nu=ATAN2(ybi,xbi)
      else
!  General case
         capb=rdsq/rsqm1-one
         ac=-ybi*ybi/rsqm1
         twoa=two
         snusq=(-capb+SQRT(capb*capb-four*ac))/twoa
         if(snusq .LT. small) then
            nu=zero
         else
            arg=SQRT(snusq)
            if((one-arg) .LT. zero) arg=one
            nu=ASIN(arg)
            if(xbi .LT. zero) nu=pi-nu
            if(ybi .LT. zero) nu=-nu
         endif
      endif
      cosnu=COS(nu)
      sinnu=SIN(nu)
      if(muflag .NE. 1) then
!  ---- Compute mu -----------
!  Special cases
         if(ybi .EQ. zero .AND. xbi .EQ. zero) then
            mu=zero
         elseif(abs(sinnu) .LE. small) then
            mu=ALOG((absx+SQRT(xsq-rsqm1))/rp1)
         elseif(abs(cosnu) .LE. small) then
            mu=ALOG((absy+SQRT(ysq+rsqm1))/rp1)
         else
!  General case
            mu=ALOG((xbi/cosnu+ybi/sinnu)/rp1)
         endif
      else
         mu=0.
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine path(th,tl,tu,x,yi,zi,delx,y3,z3)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521               PATH
! ---            D. Strimaitis, SRC
!
! PURPOSE:     (adapted from CTDM)
!     COMPUTES THE POSITION OF THE STREAMLINE THAT PASSES THROUGH THE
!     POINT AT (-INFINITY,YI,ZI) FOR ANY X ASSUMING A GAUSSIAN HILL
!     FUNCTION H(a,b)=HH*EXP(-(a/AH)**2-(b/BH)**2).  LOCAL DEFORMATION
!     FACTORS Th,Tl,Tu ARE COMPUTED AT (X,Y,Z).
!     NOTE THAT THE X-AXIS IS ALIGNED WITH THE INCIDENT FLOW.
!
!     MODIFICATION:  A root solver from the SLATEC package is called if
!                    the simple linear iteration is slow or fails
!
! ARGUMENTS:
!    PASSED:    X               DISTANCE ALONG FLOW AT WHICH         [r]
!                               STREAMLINE COORDINATES ARE FOUND (M)
!               YI,ZI           STREAMLINE COORD. FAR FROM HILL (M)  [r]
!               DELX            INCREMENT FOR STREAMLINE TRACE (M)   [r]
!  RETURNED:    TH,TL,TU        LOCAL DEFORMATION OR STRAIN FACTORS  [r]
!               Y3,Z3           STREAMLINE COORD. AT X (M)           [r]
!
! CALLING ROUTINES:     CTPAR
!
! EXTERNAL ROUTINES:    FLOPUFF, HILHGT
!                       PATH0, XSTF, SNSQE, R1MACH
!-----------------------------------------------------------------------
!
      INCLUDE 'const.puf'
      INCLUDE 'flvar.puf'
! --- Local common to pass current 'constants' from PATH to PATH0
      common /flvar2/ xx,yyi,zzi
!         xx:  along-flow coordinate where streamline is sampled
!        yyi:  lateral position of streamline far from hill
!        zzi:  vertical position of streamline far from hill
! --- Set up variables for root solver
      real yz(2),fvec(2),wa(20)
      real xgd(2),ygd(2),zgd(2)
      external PATH0
      data ijac/2/, ndim/2/, nprint/6/, lwa/20/
!     - ijac=2      compute Jacobian internally
!     - ndim=2      number of dimensions
!     - nprint=6
!     - lwa=20      dimension for work array  (wa)
!                   lwa=1+(3*ndim*ndim+13*ndim)/2
! --- Set TOL to the square root of the machine precision.
      tol=SQRT(R1MACH(4))
      xx=x
      yyi=yi
      zzi=zi
! --- Set up variables for linear solver
      DATA CRIT/0.005/                                                 ! PTH00380
      DSMALL=.01*HH                                                    ! PTH00390
      YSTEP=0.5*LY                                                     ! PTH00400
      ZSTEP=0.5*HH                                                     ! PTH00410
      NITER=0                                                          ! PTH00580
      mxiter=4
! --- Save values from last call
      zlast=z3
      ylast=y3
! --- Use original position and the values from last call to path for
! --- for the 2 initial guesses for the linear iteration
      y1=y3
      z1=z3
      y2=yi
      z2=zi
      if(y1 .EQ. y2) y2=dsmall+y2
      if(z1 .EQ. z2) z2=dsmall+z2
      if(z1.LT.zero) z1=zero
      if(z2.LT.zero) z2=zero
! *** COMPUTE THE STREAMLINE DEFLECTIONS FOR THE FIRST GUESSES          PTH00500
!       ETA IS THE VERTICAL DEFLECTION, DEL IS THE LATERAL DEFLECTION   PTH00510
!       H1 IS ELEVATION OF SURFACE BENEATH THE FIRST-GUESS POSITION     PTH00520
!       H2 IS ELEVATION OF SURFACE BENEATH THE SECOND-GUESS POSITION    PTH00530
      CALL FLOPUFF(X,Y1,Z1,ETA1,DEL1,THI,TLI,TU)                       ! PTH00540
      H1=HH*HILHGT(X,Y1)                                               ! PTH00550
      CALL FLOPUFF(X,Y2,Z2,ETA2,DEL2,THI,TLI,TU)                       ! PTH00560
      H2=HH*HILHGT(X,Y2)                                               ! PTH00570
! --- Compute the scaled errors
      er1=ABS((y1-del1-yi)/ly)+ABS((z1+h1-eta1-zi)/hh)
      er2=ABS((y2-del2-yi)/ly)+ABS((z2+h2-eta2-zi)/hh)
!                                                                       PTH00620
! *** ITERATE UNTIL THE STREAMLINE IS FOUND TO WITHIN CRIT * HH         PTH00630
! *** NEW GUESS                                                         PTH00640
!                                                                       PTH00650
! ---------------------------------------------------
! --- Section for call to linear solver
! ---------------------------------------------------
! --- Compute new estimate: y3,z3
10    DENY=Y1-Y2+DEL2-DEL1                                             ! PTH00660
      if(ABS(y2-y1) .LE. small .OR. ABS(deny) .LT. small) then
! ---    Go right to non-linear root solver
         goto 40
      ELSE                                                             ! PTH00690
         Y3=(Y1*(YI+DEL2)-Y2*(YI+DEL1))/DENY                           ! PTH00700
! *** RESTRICT RATE OF CHANGE OF POSITION ESTIMATE                      PTH00710
         STEP=Y3-Y2                                                    ! PTH00720
         IF(ABS(STEP) .GT. YSTEP) Y3=Y2+SIGN(YSTEP,STEP)               ! PTH00730
      ENDIF                                                            ! PTH00740
      DENZ=Z1+H1-ETA1+ETA2-Z2-H2                                       ! PTH00750
      if(ABS(z2-z1) .LE. small .OR. ABS(denz) .LT. small) then
! ---    Go right to non-linear root solver
         goto 40
      ELSE                                                             ! PTH00780
         Z3=(Z1*(ZI+ETA2-H2)-Z2*(ZI+ETA1-H1))/DENZ                     ! PTH00790
! --- Do not allow a streamline height below surface of hill
         z3=AMAX1(zero,z3)
! *** RESTRICT RATE OF CHANGE OF POSITION ESTIMATE                      PTH00800
         STEP=Z3-Z2                                                    ! PTH00810
         IF(ABS(STEP) .GT. ZSTEP) Z3=Z2+SIGN(ZSTEP,STEP)               ! PTH00820
      ENDIF                                                            ! PTH00830
      NITER=NITER+1                                                    ! PTH00840
!                                                                       PTH00880
!     COMPUTE QUANTITIES NOW FOR THE NEW GUESS.                         PTH00970
      CALL FLOPUFF(X,Y3,Z3,ETA3,DEL3,THI,TLI,TU)                       ! PTH00980
      H3=HH*HILHGT(X,Y3)                                               ! PTH00990
! *** COMPUTE THE ERROR IN THE SOLUTION SCALED BY HH                    PTH01000
      ERRY=ABS((Y3-DEL3-YI)/LY)                                        ! PTH01010
      ERRZ=ABS((Z3+H3-ETA3-ZI)/HH)                                     ! PTH01020
!                                                                       PTH01030
! *** QUIT IF ERROR CRITERIA SATISFIED.                                 PTH01040
      IF(ERRY .LT. CRIT .AND. ERRZ .LT. CRIT) GO TO 50                 ! PTH01050
! --- Update last estimate (keep previous best estimate in '1')
      if(er1.GT.er2) then
      Y1=Y2                                                            ! PTH01060
      Z1=Z2                                                            ! PTH01070
      H1=H2                                                            ! PTH01080
      ETA1=ETA2                                                        ! PTH01090
      DEL1=DEL2                                                        ! PTH01100
      endif
      Y2=Y3                                                            ! PTH01110
      Z2=Z3                                                            ! PTH01120
      H2=H3                                                            ! PTH01130
      ETA2=ETA3                                                        ! PTH01140
      DEL2=DEL3                                                        ! PTH01150
      er2=errz+erry
! --- Quit linear iteration approach if convergence is too slow
      if(niter.GT.mxiter) goto 40
      GO TO 10                                                         ! PTH01160
                                                                       ! PTH01170
40    continue
! ----------------------------------------------------
! --- Section for call to non-linear root solver SNSQE
! ----------------------------------------------------
! --- Set control variable to allow recovery from convergence errors
      call XSETF(0)
! --- Pass last value of (y,z) to solver as first guess; could use
! --- best result from linear solver, but this could pose problem
! --- since method failed
      yz(1)=ylast
      yz(2)=zlast
      call SNSQE(path0,jac,ijac,ndim,yz,fvec,tol,nprint,info,wa,lwa)
! --- Transfer solution to output variables, and call FLOPUFF again
! --- to obtain deflections and strain data
      y3=yz(1)
      z3=yz(2)
      call flopuff(x,y3,z3,eta3,del3,thi,tli,tu)
      h3=hh*hilhgt(x,y3)
! --- Interrogate INFO to see if this solution is valid
      if(info.EQ.2 .OR. info.EQ.4) then
! ---    PROBLEM!  Test against convergence criteria above
         yii=y3-del3
         zii=z3-eta3+h3
         erry=ABS((yii-yi)/ly)
         errz=ABS((zii-zi)/hh)
         if(erry.GT.crit .OR. errz.GT.crit) then
! ---       Recovery for failed procedure:
! ---       Search for 2 "good" points in the vicinity of x, and
! ---       interpolate y,z at x.
! ---       Shift x in increments of .1 DELX (10% of the increment
! ---       used to map streamline along the flow)
            delshft=0.1*delx
            ngood=0
! ---       First, get 1 point further downwind (search out to 3 DELX)
            do ishft=1,30
               shft=ishft*delshft
               xx=x+shft
               yz(1)=ylast
               yz(2)=zlast
               call SNSQE(path0,jac,ijac,ndim,yz,fvec,tol,nprint,&
                          info,wa,lwa)
               if(info.EQ.1) then
                  ngood=ngood+1
                  xgd(ngood)=xx
                  ygd(ngood)=yz(1)
                  zgd(ngood)=yz(2)
                  if(ngood.EQ.1) goto 45
               endif
            enddo
! ---       Now, get needed points further upwind (up to 3 DELX)
45          do ishft=1,30
               shft=ishft*delshft
               xx=x-shft
               yz(1)=ylast
               yz(2)=zlast
               call SNSQE(path0,jac,ijac,ndim,yz,fvec,tol,nprint,&
                          info,wa,lwa)
               if(info.EQ.1) then
                  ngood=ngood+1
                  xgd(ngood)=xx
                  ygd(ngood)=yz(1)
                  zgd(ngood)=yz(2)
                  if(ngood.EQ.2) goto 46
               endif
            enddo
! ---       Code should not reach this point!!
            write(*,*)
            stop 'PATH: convergence procedures failed'
! ---       Interpolate from nearest 2 points along streamline
46          xfac=(x-xgd(1))/(xgd(1)-xgd(2))
            y3=ygd(1)+(ygd(1)-ygd(2))*xfac
            z3=zgd(1)+(zgd(1)-zgd(2))*xfac
! ---       Call FLOPUFF again to obtain deflections and strain data
            call flopuff(x,y3,z3,eta3,del3,thi,tli,tu)
            h3=hh*hilhgt(x,y3)
         endif
      endif
   50 CONTINUE                                                         ! PTH01180
!                                                                       PTH01200
! *** COMPUTE T-FACTORS: IN MOST CASES, TL IS A RESIDUAL                PTH01210
!                                                                       PTH01220
      TH=1./THI                                                        ! PTH01230
      TL=THI/TU                                                        ! PTH01240
!                                                                       PTH01250
!     IF A LONG RIDGE, CONSIDER CALCULATED VALUE OF TL.  IF ASPECT RATIOPTH01260
!     IS 3 OR LESS, KEEP TL AS A RESIDUAL.  IF ASPECT RATIO EXCEEDS 3,  PTH01270
!     USE A COMBINATION OF RESIDUAL VALUE AND CALCULATED VALUE AS A     PTH01280
!     FUNCTION OF ASPECT RATIO                                          PTH01290
!                                                                       PTH01300
      ASPECT = LY/LX                                                   ! PTH01310
      IF(ASPECT.GT.3.0 .AND. ABS(1.0 - TLI) .LT. 0.1) THEN             ! PTH01320
          TLRES = TL                                                   ! PTH01330
          TLCALC = 1.0/TLI                                             ! PTH01340
          WEIGHT = ((ASPECT - 3.0)/ASPECT) ** 0.25                     ! PTH01350
          TL = TLCALC * WEIGHT + TLRES * (1.0 - WEIGHT)                ! PTH01360
          TU = THI/TL                                                  ! PTH01370
      ENDIF                                                            ! PTH01380
      RETURN                                                           ! PTH01390
      END                                                              ! PTH01400
!-----------------------------------------------------------------------
      subroutine path0(n,yz,fvec,iflag)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 980430              PATH0
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes difference between actual upstream location
!               of streamline in the y-z plane, and corresponding
!               position of approximated streamline.  When the right
!               estimate of (y,z) at a given x is chosen (this is the
!               vector yz), the difference between the location of the
!               actual streamline and the estimated streamline is zero.
!
! --- UPDATE
! --- V5.0-V5.0     980430  (DGS): assign iflag (compiler warning)
!
! ARGUMENTS:
!   PASSED:     n       number of elements in input vector (yz)      [i]
!               yz      (y,z) guess for streamline location at x    [ra]
!               iflag   control flag for SNSQ (not modified here)    [i]
!   RETURNED:   fvec    corresponding difference from actual        [ra]
!                       streamline location far upwind
!               iflag   control flag for SNSQ (not modified here)    [i]
!
! CALLING ROUTINES:     SNSQ
!
! EXTERNAL ROUTINES:    FLOPUFF, HILHGT
!-----------------------------------------------------------------------
      INCLUDE 'flvar.puf'
! --- Local common to pass current 'constants' from PATH to PATH0
      common /flvar2/ xx,yyi,zzi
!       xx:  along-flow coordinate at point where streamline is sampled
!      yyi:  lateral position of streamline far from hill
!      zzi:  vertical position of streamline far from hill
      real yz(n),fvec(n)
! --- Retain iflag value
      iflag=iflag
! --- Compute streamline deflections eta(vertical) and del(lateral) to
! --- bring streamline from (inf,yi',zi') to (x,yz(1),yz(2))
! --- Do not allow streamline height below hill surface
      if(yz(2).LT.0.0) yz(2)=0.0
      call flopuff(xx,yz(1),yz(2),eta,del,thi,tli,tu)
! --- Compute the inferred upwind location (yi',zi')
      h=hh*hilhgt(xx,yz(1))
      yip=yz(1)-del
      zip=yz(2)-eta+h
! --- Functions to be zeroed are differences yi-yi' and zi-zi'
      fvec(1)=yyi-yip
      fvec(2)=zzi-zip
      return
      end
!-----------------------------------------------------------------------
      subroutine puffc(imode,zpole,dpole,c)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521              PUFFC
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes concentrations for those receptors that are
!               modeled as a "receptor-on-a-pole" (including cases in
!               which terrain effects are negligible)
!
! ARGUMENTS:
!   PASSED:     imode   mode of operation; 0 = flat, 1 = pole calc,  [i]
!                       2 = pole calc plus stagnation streamline offset
!               zpole   height of "pole" (m)                         [r]
!               dpole   lateral offset from stagnation streamline (m)[r]
!   RETURNED:   c       concentration (g/m**3)                       [r]
!
! CALLING ROUTINES:     CTSG
!
! EXTERNAL ROUTINES:    ERF
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
!  Initialize concentration that is returned
      c=zero
!  Make sure that receptor height is consistent with mode selection
      if(imode .EQ. 0) zpole=0.0
!  Compute elapsed time from when the puff was emitted to when it
!  reaches the recepter (the age of the puff when centered at the
!  receptor ("tup")
      tup=timpg+t12rl
!  If the age of the puff at the recepter is not positive, concentration
!  is zero!
      if(tup .LE. 0.) return
!  Compute needed sigmas
      sz=szr
      sy=syr
      szsq=sz*sz
      sysq=sy*sy
!  Puff is mixed in the vertical if sigma-z exceeds 1.6 times the
!  mixing height (zlid).  Set mixing flag to 1.
      imix=0
      if(sz .GE. 1.6*zlid) imix=1
!  Compute the concentration on the trajectory ("centerline")
      t1mtr=-(t12p+t12rl)
      t2mtr=tstep+t1mtr
      dum=u/(rt2*sy)
!     co=q*(ERF(t2mtr*dum)-ERF(t1mtr*dum))/(tstep*four*pi*u*sy*sz)
      co=q*(ERFDIF((t2mtr*dum),(t1mtr*dum)))/(tstep*four*pi*u*sy*sz)
      if(co .EQ. 0.0) then
         c=0.0
         return
      endif
!  Compute the horizontal distribution factor
      dely=yrf-ypf
      if(imode .EQ. 2) dely=dpole
      delysq=dely*dely
      argy=half*(delysq/sysq)
      if(argy .LT. expmax) then
         fy=EXP(-argy)
!  Compute the vertical distribution factor
!  - If puff is mixed in the vertical, set fz equal to rt2pi*sz/zlid
!    and compute concentration.
         if(imix .EQ. 1) then
            fz=rt2pi*sz/zlid
            c=co*fy*fz
            return
         endif
!  - Section for no mixing lid
         zplus=zpuff+zpole
         zminus=zpuff-zpole
         dum=half/szsq
         argz1=dum*zminus**2
         argz2=dum*zplus**2
         term1=zero
         term2=zero
         if(argz1 .LT. expmax) term1=EXP(-argz1)
         if(argz2 .LT. expmax) term2=EXP(-argz2)
         fz=term1+term2
!  - Section for reflections from lid
         if(fz .GT. 0.) then
            do 10 i=1,mxrefl
               twoizl=two*i*zlid
               argz1=dum*(twoizl-zminus)**2
               argz2=dum*(twoizl-zplus)**2
               argz3=dum*(twoizl+zminus)**2
               argz4=dum*(twoizl+zplus)**2
               term1=zero
               term2=zero
               term3=zero
               term4=zero
               if(argz1 .LT. expmax) term1=EXP(-argz1)
               if(argz2 .LT. expmax) term2=EXP(-argz2)
               if(argz3 .LT. expmax) term3=EXP(-argz3)
               if(argz4 .LT. expmax) term4=EXP(-argz4)
               fzo=fz
               fz=fz+term1+term2+term3+term4
               test=(fz-fzo)/fz
               if(test .LT. epsrefl) goto 11
10          continue
         endif
!  Compute concentration
11       c=co*fy*fz
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine qatr(xl,xu,eps,ndim,fct,y,ier,aux)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015               QATR
!
! PURPOSE:      Integration routine adapted from the IBM SSP program
!               DQATR.  Modified for single precision.
!
! ARGUMENTS:
!    PASSED:    xl,xu   lower and upper limits of integration        [r]
!               eps     fractional error used to define convergence  [r]
!               ndim    dimension of array aux                       [i]
!               fct     external function (integrand)
!               aux     working array, passed to allow variable dim. [r]
!  RETURNED:    y       value of integral                            [r]
!               ier     status flag at termination                   [i]
!
! CALLING ROUTINES:     UPPER
!
! EXTERNAL ROUTINES:    none
!-----------------------------------------------------------------------
!  NOTES: status flags denote the following --
!               ier=0   value of integral converged to within eps
!               ier=1   value of integral is diverging
!               ier=2   value of integral did not converge to within
!                       eps before ndim limit was reached
      EXTERNAL fct
      dimension aux(1)
      integer*2 i,ii,ji,j,jj
      half=0.5
!  Preparations for Romberg loop
      aux(1)=half*(fct(xl)+fct(xu))
      h=xu-xl
      y=h*aux(1)
      if(ndim .LE. 1) then
         ier=2
         return
      elseif(h .EQ. 0.) then
         ier=0
         return
      endif
      hh=h
      delt2=0.
      p=1.
      jj=1
!  Initialize flag for integer*2 limit: jj cannot exceed 32,000
      lstop=0
      do 7 i=2,ndim
         y=aux(1)
         delt1=delt2
         hd=hh
         hh=half*hh
         p=half*p
         x=xl+hh
         sm=0.
!  Integer*2 limit: jj cannot exceed 32,000
         if(lstop .EQ. 1) then
            write(6,1010)
1010        format(2X,'ERROR FROM QATR - VARIABLE jj EXCEEDED 32,000')
            write(*,*)
            stop 'Halted in QATR -- see list file.'
         endif
         if(jj .GT. 16000) lstop=1
         do 3 j=1,jj
            sm=sm+fct(x)
            x=x+hd
3        continue
         aux(i)=half*aux(i-1)+p*sm
!  A new approximation to the integral is computed by means
!       of the trapezoidal rule
!  Start of Rombergs extrapolation method
         q=1.
         ji=i-1
         do 4 j=1,ji
            ii=i-j
            q=q+q
            q=q+q
            aux(ii)=aux(ii+1)+(aux(ii+1)-aux(ii))/(q-1.)
4        continue
!  End of Romberg step
         delt2=ABS(y-aux(1))
         if(i .GE. 5) then
!  Modification for cases in which function = 0 over interval
            if(y .EQ. 0.) then
               ier=0
               return
            elseif(delt2/y .LE. eps) then
               ier=0
               y=h*aux(1)
               return
            elseif(delt2 .GE. delt1)then
               ier=1
               y=h*y
               return
            endif
         endif
7     jj=jj+jj
      ier=2
      y=h*aux(1)
      return
      end
!-----------------------------------------------------------------------
      subroutine rotate(x,y,ang,xp,yp)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 901015             ROTATE
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Rotate coordinate system through angle "ang"
!               to map point (x,y) into point (xp,yp).
!
! ARGUMENTS:
!    PASSED:    x,y     coordinates before rotation                  [r]
!               ang     rotation angle (radians)                     [r]
!  RETURNED:    xp,yp   coordinates after rotation                   [r]
!
! CALLING ROUTINES:     CTPAR, CTREC
!
! EXTERNAL ROUTINES:    none
!-----------------------------------------------------------------------
!  Note: ang is the angle measured CCW from the x-axis to the xp-axis
      s=SIN(ang)
      c=COS(ang)
      xp=x*c+y*s
      yp=-x*s+y*c
      return
      end
!-----------------------------------------------------------------------
      function sigma(itype,zpuff,ws,t,s1,tstart,s2,tstep,sr,frac)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 000602              SIGMA
! ---            D. Strimaitis, SRC
!
! --- UPDATES
! --- V5.2-V5.4    000602  (DGS): add message to "stop"
! --- V5.1-V5.2    991104  (JSS): Error messages written to list
!                                 file as well as to screen
!
! PURPOSE:      Computes a value of sigma for a given travel-time,
!               using the CALPUFF sigma subroutines, starting from
!               the nearest of three known values.  The effect of
!               buoyancy-induced growth is treated as a virtual time,
!               as in CTDM.
!
! ARGUMENTS:
!    PASSED:    itype   1:sigma-y  2:sigma-z                         [i]
!               zpuff   puff height above ground (m)                 [r]
!               ws      puff advection speed (m/s)                   [r]
!               t       puff age (s)                                 [r]
!               s1      sigma at start of time-step (m)              [r]
!               tstart  puff age at start of time-step (s)           [r]
!               s2      sigma at end of time-step (m)                [r]
!               tstep   duration of time-step (s)                    [r]
!               sr      sigma at some intermediate point (m)         [r]
!               frac    fraction of tstep to intermediate point      [r]
!    Parameters: IO6
!  RETURNED:    sigma   sigma at time t (m)                          [r]
!
! CALLING ROUTINES:     UPPER, LOWER
!
! EXTERNAL ROUTINES:    SIGTY, SIGTZ
!-----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Declare arrays for sorted age(time) and sigmas
      real a(3),s(3)
      data zero/0.0/
! --- When sigma does not grow, just return the value at start of step
      if(s1 .EQ. s2) then
         sigma=s1
      else
! ---    Find age nearest time t
         a(1)=tstart+frac*tstep
         s(1)=sr
         a(2)=tstart
         s(2)=s1
         a(3)=tstart+tstep
         s(3)=s2
         index=1
         adelt=ABS(t-a(1))
         do i=2,3
            if(ABS(t-a(i)).LT.adelt) then
               index=i
               adelt=ABS(t-a(i))
            endif
         enddo
!
! ---    Set advection speed to km/s
         wskps=0.001*ws
!
! ---    Convert corresponding sigma to a virtual time, and then
! ---    compute the sigma at t via 'forward' call to sigma routine
         if(itype.EQ.1) then
! ---       Sigma-y
            call sigty(s(index),zero,zero,sy,vty,vdy)
            tsec=AMAX1(zero,vty+(t-a(index)))
            dkm=AMAX1(zero,vdy+(t-a(index))*wskps)
            if(tsec.GT.zero) then
               call sigty(zero,dkm,tsec,sigma,dum1,dum2)
            else
               sigma=zero
            endif
         elseif(itype.EQ.2) then
! ---       Sigma-z
            call sigtz(s(index),zero,zero,zpuff,sz,vtz,vdz)
            tsec=AMAX1(zero,vtz+(t-a(index)))
            dkm=AMAX1(zero,vdz+(t-a(index))*wskps)
            if(tsec.GT.zero) then
               call sigtz(zero,vdz,vtz,zpuff,sigma,dum1,dum2)
            else
               sigma=zero
            endif
         else
            write(io6,*) 'SIGMA:  itype not valid --  ',itype
            write(*,*)
            stop 'Halted in SIGMA -- see list file.'
         endif
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine slg2puf(x1,x2,y1,y2,sy1,sy2,npuff,qfac,xfac)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521            SLG2PUF
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Subroutine determines the number of puffs required to
!               simulate GLC's due to a slug (prepares for CTSG call).
!               Assumes that separation between puffs is fixed, and is
!               set by a mean sigma-y along the slug.
!
! ARGUMENTS:
!   PASSED:     x1,x2           x-coord. of old(1) and new(2) ends   [r]
!                               of slug
!               y1,y2           y-coord. of old(1) and new(2) ends   [r]
!                               of slug
!               sy1,sy2         sigma-y at the old(1) and new(2) ends[r]
!                               of the slug
!   RETURNED:   npuff           number of surrogate puffs            [i]
!               qfac            fraction of slug mass in each puff   [r]
!               xfac            puff separation, expressed as a      [r]
!                               fraction of the length of the slug
!
! CALLING ROUTINES:     COMP
!
! EXTERNAL ROUTINES:    none
!-----------------------------------------------------------------------
! Note: Assume that slug is partitioned into N puffs, so that there are
!       N-1 segments between the puffs.  Set the separation between the
!       puffs equal to the "mean" sigma-y
!-----------------------------------------------------------------------
      data half/0.5/, one/1.0/
! --- Compute the distance between the two "ends" of the slug
      slen=SQRT((x1-x2)**2+(y1-y2)**2)
! --- Set a min. sigma-y of "1" for the younger end ...
      sy22=AMAX1(sy2,one)
! --- Compute the mean sigma-y
! --- Arithmetic mean
      sy=half*(sy1+sy22)
! --- Geometric mean
!     sy=SQRT(sy1*sy22)
! --- Assume N equally-spaced puffs, where N is at least 2
      npuff=NINT(slen/sy)+1
      if(npuff.LT.2) npuff=2
      nseg=npuff-1
      qfac=one/FLOAT(npuff)
      xfac=one/FLOAT(nseg)
      return
      end
!-----------------------------------------------------------------------
      function speed(htmhd)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107              SPEED
! ---            D. Strimaitis, SRC
!
!
! PURPOSE:      Computes wind speed at the height provided
!
! --- UPDATE
! --- V4.0-V5.0     971107  (DGS): Minimum speed returned is ws(1)
!
! ARGUMENTS:
!    PASSED: htmhd      height at which speed is needed  (m)         [r]
!                       minus the height of dividing streamline
!
! CALLING ROUTINES: CTPAR, FLOPUFF
!
! EXTERNAL ROUTINES: NONE
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'ctpass.puf'
!  Treat single layer first
      if(nlev .EQ. 1) then
         speed=ws(1)
         return
      endif
!  Add Hd to get height consistent with profile array
      ht=htmhd+hd
!  Find the layer that contains ht
      do i=1,nlev
         iztop=i
         if(ht .LE. z(i)) goto 11
      enddo
11    if(iztop.EQ.1) then
         h1=0.0
         u1=0.0
      else
         h1=z(iztop-1)
         u1=ws(iztop-1)
      endif
      h2=z(iztop)
      u2=ws(iztop)
!  Use linear interpolation to find speed at ht
      a=(u2-u1)/(h2-h1)
      b=u1-a*h1
      speed=a*ht+b
!  Do not allow speeds less than value for layer 1
      speed=AMAX1(speed,ws(1))
      return
      end
!-----------------------------------------------------------------------
      subroutine tfac(x,th,tl,tu)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521               TFAC
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Interpolates flow distortion factors at position x along
!               the trajectory of the puff.
!
! ARGUMENTS:
!    PASSED:    x       position along the flow (puff trajectory);   [r]
!                       the origin lies at the center of the hill (m)
!  RETURNED: th,tl,tu   terrain-induced flow distortion factors      [r]
!
! CALLING ROUTINES:     FCTZ, FCTY
!
! EXTERNAL ROUTINES:    none
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
      if(x.GT.xbegin .AND. x.LT.xend) then
!  Find array index just upwind of x    (npos)
         nposm1=INT((x-xbegin)/xspace)
         npos=nposm1+1
         nposp1=npos+1
!  Find value of x at npos
         xnpos=xbegin+xspace*nposm1
!  Find distortion factors at x
         xfac=(x-xnpos)/xspace
         th=xfac*(at(1,nposp1)-at(1,npos))+at(1,npos)
         tl=xfac*(at(2,nposp1)-at(2,npos))+at(2,npos)
         tu=one/(th*tl)
      else
         tu=one
         tl=one
         th=one
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine upper(c)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521              UPPER
! ---            D. Strimaitis, SRC
!
! PURPOSE:      Computes concentrations due to puff material in the
!               upper sgment of the flow (above Hd).
!
! ARGUMENTS:
!    PASSED:    none
!  RETURNED:    c               concentration (g/m**3)               [r]
!
! CALLING ROUTINES:     CTSG
!
! EXTERNAL ROUTINES:    FLOPUFF, QATR, FCTZ, FCTY, SIGMA, ERF,
!                       BJI, BI
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'const.puf'
      INCLUDE 'ctpass.puf'
!  Set up for call to QATR:
      real aux(300)
      EXTERNAL fctz,fcty
      ndim=300
      eps=.01
!  Initialize concentration that is returned
      c=zero
!  Compute the age of puff at the receptor (tup)
      tup=timpg+t12ru
!  If the age of the puff at the recepter is not positive, concentration
!  is zero!
      if(tup .LE. zero) return
!  Puff age at start of time-integration is not negative
      tlow=AMAX1(timpg,zero)
!  Assign sigmas at impingement point
      szo=szimpg
      szosq=szo*szo
      syo=syimpg
      syosq=syo*syo
!  Reset impingement sigmas if they exceed receptor-specific values --
!  this turns off hill-effects as the puff has not yet reached the hill
      szsq=szr*szr
      sysq=syr*syr
      if(szosq.GE.szsq .OR. syosq.GE.sysq) then
         szo=szr
         szosq=szsq
         syo=syr
         syosq=sysq
      endif
!  Puff is mixed in the vertical if sigmaz upwind of the hill (szo)
!  exceeds 1.6 times the mixed layer height (zlid).  Set mixing
!  flag to 1.
      imix=0
      if(szo .GE. 1.6*zlid) imix=1
!  Compute (sigz**2-sigzo**2)/Tz**2, denoted as szptsq and
!       also (sigy**2-sigyo**2)/Ty**2, denoted as syptsq
!  Assume no growth if growth is less than "small"
      szptsq=zero
      szpt=zero
      syptsq=zero
      zdif=szr-szo
!  - set zdif equal to zero to avoid line integral evaluation when
!    puff is mixed in the vertical
      if(imix .EQ. 1) zdif=zero
      ydif=syr-syo
      if(zdif .GT. small) then
         call QATR(tlow,tup,eps,ndim,fctz,szptsq,ier,aux)
         szpt=SQRT(szptsq)
      endif
      if(ydif .GT. small) then
         call QATR(tlow,tup,eps,ndim,fcty,syptsq,ier,aux)
      endif
      szesq=szosq+szptsq
      sze=SQRT(szesq)
      syesq=syosq+syptsq
      sye=SQRT(syesq)
!  Compute the concentration on the trajectory ("centerline")
      t1mtr=-(t12p+t12ru)
      t2mtr=tstep+t1mtr
      dum=u/(rt2*sye)
!     co=q*(ERF(t2mtr*dum)-ERF(t1mtr*dum))/(tstep*four*pi*u*sye*sze)
      co=q*(ERFDIF((t2mtr*dum),(t1mtr*dum)))/(tstep*four*pi*u*sye*sze)
      if(co.LE.zero) return
!  Compute the horizontal distribution factor
      call FLOPUFF(xrf,yrf,0.0,zeta,ydel,dum1,dum2,dum3)
      del=yrf-ydel-ypf
      argy=half*(del*del/syesq)
      if(argy .LT. expmax) then
         fy=EXP(-argy)
!  Compute the vertical distribution factor
!  - If puff is mixed in the vertical, set fz equal to rt2pi*sze/zlid
!    and calculate concentration
         if(imix .EQ. 1) then
            fz=rt2pi*sze/zlid
            c=co*fy*fz
            return
         endif
         twofz=zero
         zlmhd=zlid-hd
         ak=half/szesq
         if(szpt .GT. zero) then
            ak=one/(rt2*sze*szo*szpt)
            d0=ak*szesq*zlmhd
         endif
!  - First sum, i=1:
         eoip=zpuff
         eoipmh=eoip-hd
         eoipph=eoip+hd
!  When plume doesn't grow, loop over j is not needed
         if(szpt .EQ. zero) then
            call BI(ak,eoipmh,eoipph,bp)
            twofz=twofz+bp
!  - No impact on i=1 means that plume does not reach receptor!
            if(twofz.EQ.0.0) return
         else
            dpm=eoipmh*szptsq
            dpp=eoipph*szptsq
!  - Loop on j:
            do 200 j=1,mxrefl
               aj=AMIN0(2,j)
               ej=two*(j-1)*zlmhd
               call BJI(ak,dpm,dpp,d0,eoipmh,eoipph,ej,bjp)
               add=aj*bjp
               twofz=twofz+add
!  - No impact on i=j=1 means that plume does not reach receptor!
               if(twofz.EQ.0.0) return
               if(add/twofz .LT. epsrefl) goto 201
200         continue
         endif
!  - Loop on i>1:
201      do 400 i=2,mxrefl
            eoip=zpuff+two*zlid*(i-1)
            eoipmh=eoip-hd
            eoipph=eoip+hd
            eoim=-zpuff+two*zlid*(i-1)
            eoimmh=eoim-hd
            eoimph=eoim+hd
!  When plume doesn't grow, loop over j is not needed
            if(szpt .EQ. zero) then
               call BI(ak,eoipmh,eoipph,bp)
               call BI(ak,eoimmh,eoimph,bm)
               add=(bp+bm)
               twofz=twofz+add
               if(add/twofz .LT. epsrefl) goto 401
            else
               dmm=eoimmh*szptsq
               dpm=eoipmh*szptsq
               dpp=eoipph*szptsq
               dmp=eoimph*szptsq
!  - Loop on j:
               do 300 j=1,mxrefl
                  aj=AMIN0(2,j)
                  ej=two*(j-1)*zlmhd
                  call BJI(ak,dpm,dpp,d0,eoipmh,eoipph,ej,bjp)
                  call BJI(ak,dmm,dmp,d0,eoimmh,eoimph,ej,bjm)
                  add=(aj*(bjp+bjm))
                  twofz=twofz+add
                  if(add/twofz .LT. epsrefl) then
                     if(j .GT. 1) goto 400
                     goto 401
                  endif
300            continue
            endif
400      continue
401      fz=half*twofz
!  Compute concentration
         c=co*fy*fz
      endif
      return
      end
!-----------------------------------------------------------------------
      real function xintrp(x1,x2,x,y1,y2)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 940831             XINTRP
! ---            Adopted from CTDMPLUS without modification
!
! FUNCTION: XINTRP
!
! PURPOSE: THIS FUNCTION LINEARLY INTERPOLATES BETWEEN TWO POINTS
!               (Y1,Y2) GIVEN THREE OTHER POINTS (X1,X2,X) WHERE
!               X1 AND X2 ARE ENDPOINTS AND X IS BETWEEN X1 AND X2
!
! ASSUMPTIONS: X IS BETWEEN X1 AND X2
!
! REMARKS: IF X1 = X2 THEN THE MIDPOINT BETWEEN Y1 AND Y2 IS RETURNED
!
! ARGUMENTS:
!  PASSED:
!       X1,X2   REAL    ENDPOINTS OF FIRST LINE
!       X       REAL    POINT BETWEEN X1 AND X2
!       Y1,Y2   REAL    ENDPOINTS OF LINE TO BE INTERPOLATED
!
! FUNCTION VALUE:
!       XINTER  REAL    INTERPOLATED VALUE BETWEEN Y1 AND Y2
!
! CALLING ROUTINES: ctpar
!
! EXTERNAL ROUTINES: NONE
!
! INTRINSIC FUNCTIONS: NONE
!
! COMMON BLOCKS: NONE
!
!-----------------------------------------------------------------------
!
!       DEFINE ARGUMENTS
        REAL X1, X2, X, Y1, Y2
!       DEFINE LOCAL VARIABLES
        REAL DX
!       START
        DX = X2 - X1
        IF( DX .NE. 0.0 ) THEN
                XINTRP = Y1 + (Y2-Y1) * (X-X1)/DX
         ELSE
                XINTRP = (Y1+Y2) * 0.5
        ENDIF
        RETURN
        END
!----------------------------------------------------------------------
      subroutine dry(icode,ix,iy,md,nhrind,tsamp,jp,dpbl,hlid,&
                     tempk,qsw,ppcoef,ldbhr,vd,vdpvd,fracdry)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212               DRY
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Compute dry deposition velocities for each modeled
!               species using a resistance model or user-specified
!               diurnally varying values.  Also compute mass removed
!               during step.
!
! --- UPDATES:
! --- V6.22 070921 - V6.3 100212
!                   (DGS): Add nested CALMET grids
! --- V5.0 950715 - V6.22 070921
!                   (DGS): Explicitly set LCALM in call to VCBAR to
!                          false to disable single-point sampling
!                          (LCALM was not previously assigned so this
!                          perpetuates the current treatment)
!
! --- INPUTS:
!
!             ICODE - integer - Puff status code (1,2 = Puff within
!                               mixed layer and [Gaussian, uniform] in
!                               vertical; 3,4 = Puff above mixed layer
!                               and [Gaussian, uniform]; 5,6 = Same as
!                               3, 4 except puff was previously within
!                               mixed layer; 11-16 = Same as 1-6 except
!                               applies slugs; 99 = Off comp. grid)
!                IX - integer - X index of closest grid point to
!                               puff/slug center
!                IY - integer - Y index of closest grid point to
!                               puff/slug center
!                MD - integer - MET Grid Domain index
!            NHRIND - integer - Current hour (01-24)
!             TSAMP - real    - Sampling step (s)
!                JP - integer - Puff number of current puff
!              DPBL - real    - Current depth of the boundary layer (m)
!                               (mixing height at nearest grid point)
!              HLID - real    - Current reflecting lid height (m) used
!                               to calculate the vertical distribution
!             TEMPK - real    - Air temperature (deg. K)
!               QSW - real    - Short-wave radiation (W/m**2)
!            PPCOEF - real    - Plume Path Coefficient for partial
!                               height correction (MCTADJ = 3)
!             LDBHR - logical - Variable controlling debug write
!                               statements
!
!     Common Block /DISPDAT/ variables:
!        CONK1, CONK2
!     Common Block /DRYDEP/ variables:
!        IDRYFLG(mxspec), ZREF, IVEG, VDUSER(24,mxspec)
!     Common Block /GEN/ variables:
!        NSPEC
!     Common Block /GRID/ variables:
!        NX, NY
!     Common Block /METHD/ variables:
!        Z0(mxnx,mxny,mxmetdom), ILANDU(mxnx,mxny,mxmetdom),
!        XLAI(mxnx,mxny,mxmetdom), IWAT1, IWAT2
!     Common Block /METHR/ variables:
!        UMET(mxnx,mxny,mxnz,mxmetdom), VMET(mxnx,mxny,mxnz,mxmetdom),
!        USTAR(mxnx,mxny,mxmetdom), XMONIN(mxnx,mxny,mxmetdom)
!     Common Block /PUFF/ variables:
!        QM(mxspec,mxpuff)
!     Parameters:
!        MXNX, MXNY, MXNZ, MXPUFF, MXSPEC, MXNZP1, MXSS,
!        MXUS, MXPS, MXMETDOM, IO6
!
! --- OUTPUT:
!         VD(nspec) - real    - Deposition velocities (m/s) before
!                               considering overall boundary layer
!                               resistance
!      VDPVD(nspec) - real    - Ratio of vd'/vd, where vd' is the
!                               deposition velocity after applying
!                               the overall boundary layer resistance
!    FRACDRY(nspec) - real    - Fraction of puff mass remaining after
!                               consideration of dry removal effects
!     Common Block /PUFF/ variables:
!        QM(mxspec,mxpuff)
!
! --- DRY called by:  COMP
! --- DRY calls:      VDCOMP, VCBAR
!----------------------------------------------------------------------
! --- Note:  Mass depletion requires the evaluation of the vertical
!            coupling coefficient.  Make sure that the use of the
!            mixing height is consistent with CALCPF and CALCSL!
!----------------------------------------------------------------------
!
! --- Include PARAMETERS
      include 'params.puf'
!
! --- Include common blocks
      include 'dispdat.puf'
      include 'drydep.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
!
      real vd(nspec),vdpvd(nspec),fracdry(nspec)
      logical ldbhr,ldb2
      logical lcalmf
! --- Explicitly disable single-point VCBAR sampling by setting local
! --- variable to false ("calm" sampling was never fully implemented)
      lcalmf=.false.
! ***
      ldb2=.false.
      if(ldb2)then
         write(io6,*)
         write(io6,*)'SUBR. DRY -- Inputs and local variables'
         write(io6,*)'IX        = ',ix,'  IY = ',iy
         write(io6,*)'MET Grid  = ',md
         write(io6,*)'ICODE     = ',icode
         write(io6,*)'NHRIND    = ',nhrind
         write(io6,*)'VDUSER    = ',(vduser(nhrind,n),n=1,nspec)
         write(io6,*)'UMET      = ',umet(ix,iy,1,md)
         write(io6,*)'VMET      = ',vmet(ix,iy,1,md)
         write(io6,*)'Z0        = ',z0(ix,iy,md)
         write(io6,*)'USTAR     = ',ustar(ix,iy,md)
         write(io6,*)'WSTAR     = ',wstar(ix,iy,md)
         write(io6,*)'DPBL      = ',dpbl
         write(io6,*)'HLID      = ',hlid
         write(io6,*)'XMONIN    = ',xmonin(ix,iy,md)
         write(io6,*)'ILANDU    = ',ilandu(ix,iy,md)
         write(io6,*)'IWAT1     = ',iwat1,'  IWAT2 = ',iwat2
         write(io6,*)'XLAI      = ',xlai(ix,iy,md)
         write(io6,*)'QSW       = ',qsw
         write(io6,*)'PPCOEF    = ',ppcoef
         write(io6,*)'TEMPK     = ',tempk
      endif
! ***
!
! --- Initialize output variables
      diffk=0.0
      do i=1,nspec
         vd(i)=0.0
         vdpvd(i)=1.0
         fracdry(i)=1.0
      enddo
!
! --- Extract puff distribution code
! --- (IICODE is 1-6 or 99)
      if(icode.EQ.99) goto 100
      iicode=icode
      if(iicode.ge.11.and.iicode.le.16) iicode=iicode-10
!
! --- Skip vd calculations if puff is above mixed layer and uniform
      if(iicode.eq.4) goto 100
!
! --- Determine state of the vegetation -- NOTE: negative land use
! --- indicates irrigated land
      if(ilandu(ix,iy,md).lt.0.or.&
        (ilandu(ix,iy,md).ge.iwat1.and.ilandu(ix,iy,md).le.iwat2))then
! ---    IRRIGATED or WATER-- assume vegetation is active and unstressed
         ivegij=1
      else
! ---    UNIRRIGATED -- use user-specified vegetation state
         ivegij=iveg
      endif
!
! --- Compute deposition velocities using the resistance model or
!     user-specified, diurnally-varying values
      ldb2=.false.
      call vdcomp(idryflg,nspec,zref,ivegij,umet(ix,iy,1,md),&
       vmet(ix,iy,1,md),ilandu(ix,iy,md),iwat1,iwat2,z0(ix,iy,md),&
       tempk,ustar(ix,iy,md),xmonin(ix,iy,md),xlai(ix,iy,md),qsw,&
       vduser,nhrind,ldb2,vd)
!
! --- Compute ratio of vd'/vd to account for overall boundary layer
! --- resistance if puff/slug is or was below mixed layer height AND
! --- is uniformly mixed in the vertical (use current mixing height)
      if(iicode.eq.2.or.iicode.eq.6) then
!
         eustar=ustar(ix,iy,md)
         ewstar=wstar(ix,iy,md)
!
! ---    Compute boundary layer diffusivity
         if(ewstar.gt.0.0)then
!
! ---       Unstable conditions -- diffusivity is maximum of
! ---       stable/unstable values
            diffk=amax1(conk2*ewstar*dpbl,conk1*eustar*dpbl)
         else
!
! ---       Neutral/stable
            diffk=conk1*eustar*dpbl
         endif
!
! ---    Minimum value of dz to prevent numerical problems
         dz=amax1(dpbl-zref,0.00001)
!
         do 95 j=1,nspec
! ---    Compute vd'/vd ratio only for species using resistance model
! ---    (User-specified deposition velocities are NOT modified)
         if(idryflg(j).eq.1.or.idryflg(j).eq.2)then
            vdpvd(j)=diffk/(diffk+vd(j)*dz)
         endif
95       continue
      endif
!
! --- Compute fraction remaining and adjust puff mass
      if(iicode.eq.2.or.iicode.eq.6)then
!
! ---    Puff is uniform in the vertical
         const=tsamp/hlid
!
      else
!
! ---    Puff is Gaussian in the vertical
         mfact0=icode-iicode
         f1=vcbar(jp,mfact0,hlid,ppcoef,lcalmf)
         const=tsamp*f1
      endif
!
! --- Determine the fraction of pollutant mass remaining after
! --- dry removal
      do 97 j=1,nspec
      fracdry(j)=exp(-vdpvd(j)*vd(j)*const)
      qm(j,jp)=fracdry(j)*qm(j,jp)
97    continue
!*****
100   if(ldbhr)then
         write(io6,111)diffk,(vd(n),n=1,nspec)
         write(io6,112)(vdpvd(n),n=1,nspec)
         write(io6,113)(fracdry(n),n=1,nspec)
111      format(15x,'DRY REMOVAL -- diffk = ',f10.3/&
                17x,'vd (m/s)      = ',2x,5(f11.6,1x))
112      format(17x,'vdpvd         = ',2x,5(f11.6,1x))
113      format(17x,'Fraction left = ',2x,5(f11.3,1x))
      endif
!*****
!
      return
      end
!----------------------------------------------------------------------
      subroutine vdcomp(idryflg,nspec,zref,iveg,u10,v10,ilu,iwat1,&
       iwat2,z0,tdegk,ustar,el,xlai,qsw,vduser,nhrind,ldb,vd)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 040611            VDCOMP
!                J. Scire, SRC
!
! --- PURPOSE:  Compute deposition velocities for each species
!               deposited for a single grid cell
!
! --- UPDATE
! --- V5.0 980304 - V5.73 040611
!                   (DGS): Rename NINT to NPSINT
! --- V5.0 960521 - V5.0 980304
!                   (DGS): Set minimum calculated z0 over water as in
!                          CALMET
!
! --- INPUTS:
!
!
!    IDRYFLG(nspec) - integer - Dry deposition method flags
!                                    0=not deposited,
!                                    1=resistance model - gas,
!                                    2=resistance model - particle,
!                                    3=user specified dep. velocities
!             NSPEC - integer - Total number of species
!                               (deposited + non-deposited species)
!              ZREF - real    - Reference height (m) for computing
!                               the atmospheric resistance
!              IVEG - integer - State of the vegetation in current cell
!                                    1 = vegetation active & unstressed
!                                    2 = vegetation active & stressed
!                                    3 = vegetation inactive
!               U10 - real    - U-component (m/s) of winds at 10 m
!                               height (Used only if in water cells)
!               V10 - real    - V-component (m/s) of winds at 10 m
!                               height (Used only if in water cells)
!               ILU - integer - Land use category for current grid cell
!      IWAT1, IWAT2 - integer - Range of land use categories defining
!                               water (IWAT1 to IWAT2)
!                Z0 - real    - Surface roughness length (m)
!             TDEGK - real    - Air temperature (deg. K)
!             USTAR - real    - Surface friction velocity (m/s)
!                EL - real    - Monin-Obukhov length (m)
!              XLAI - real    - Leaf area index
!               QSW - real    - Short-wave radiation at ground (W/m**2)
! VDUSER(24,mxspec) - real    - Diurnal cycle of user-specified
!                               deposition velocities (m/s) for hour
!                               ending 1-24
!            NHRIND - integer - Current hour index (1-24)
!               LDB - logical - Flag determining if debug write
!                               statements are activated
!     Common Block /DRYGAS/ variables:
!        PDIFF(mxspec),RM(mxspec),RGG(mxspec),RGW1(mxspec),
!        RCUT(mxspec),RD1(mxspec),BMIN,BMAX,QSWMAX,PCONST
!     Common Block /DRYPART/ variables:
!        SC23(mxint,mxpdep),VGRAV(mxint,mxpdep),TSTOP(mxint,mxpdep),
!        FRACT(mxint,mxpdep),VAIRMS
!     Parameters:
!        MXSPEC, MXPDEP, MXINT, IO6
!
! --- OUTPUT:
!         VD(nspec) - real    - Deposition velocities (m/s)
!
! --- VDCOMP called by:  DRY
! --- VDCOMP calls:      VDP
!----------------------------------------------------------------------
!
! --- Include PARAMETERS
      include 'params.puf'
!
! --- Include common block /DRYGAS/  -- Gas Deposition
      include 'drygas.puf'
!
! --- Include common block /DRYPART/ -- Particle Deposition
      include 'drypart.puf'
!
      real vd(nspec),vduser(24,mxspec)
      integer idryflg(nspec)
      logical lwater,ldb
!
      data vk/0.4/,usmin/1.e-5/,ramin/1.0/
! --- Minimum z0(m) over water is 2.0e-06, which corresponds to a 10m
! --- wind speed minimum of 1m/s
      data ws0/1.0/
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. VDCOMP -- INPUTS'
         write(io6,*)'NSPEC   = ',nspec
         write(io6,*)'IDRYFLG = ',(idryflg(n),n=1,nspec)
         write(io6,*)'IVEG    = ',iveg
         write(io6,*)'U10     = ',u10,' v10 = ',v10
         write(io6,*)'ILU     = ',ilu,' IWAT1 = ',iwat1,' IWAT2 = ',&
          iwat2
         write(io6,*)'Z0      = ',z0,' XLAI = ',xlai
         write(io6,*)'TDEGK   = ',tdegk,' USTAR = ',ustar,' EL = ',el,&
          ' QSW = ',qsw
         write(io6,*)'NHRIND  = ',nhrind
         write(io6,*)'VDUSER  = ',(vduser(nhrind,n),n=1,nspec)
      endif
! ***
!
! --- Pass user-supplied deposition velocities to VD array and exit
! --- if no computations are needed
! --- LOOP OVER SPECIES
      numsp=0
      do j=1,nspec
         if(idryflg(j).eq.0)then
! ---       NO DEPOSITION
            vd(j)=0.0
            numsp=numsp+1
         elseif(idryflg(j).eq.3)then
! ---       USER-SPECIFIED DEPOSITION
            vd(j)=vduser(nhrind,j)
            numsp=numsp+1
         endif
      enddo
      if(numsp.eq.nspec) return
!
! --- Continue on if computations are needed for some species
!
! --- Determine if this grid cell is a water or land cell
      if(ilu.ge.iwat1.and.ilu.le.iwat2)then
         lwater=.true.
! ---    Compute wind-driven roughness length
         z0m=2.0e-6
         wstest=ABS(u10)+ABS(v10)
         if(wstest.GT.ws0) then
            ws=sqrt(u10**2+v10**2)
            if(ws.GT.ws0) z0m=2.0e-6*ws**2.5
         endif
      else
         lwater=.false.
         z0m=z0
      endif
!
! --- Compute atmospheric resistance
! --- Stability-dependent psi function (heat)
      elabs=abs(el)
      if(el.lt.0.0.and.elabs.lt.9990.)then
         ratio=-zref/el
         if(ratio.gt.1.0)ratio=1.0
         zmel=alog(ratio)
         psih=exp(0.598+0.39*zmel-0.090*zmel*zmel)
      else if(elabs.ge.9990.)then
         psih=0.0
      else
         ratio=zref/el
         if(ratio.gt.1.0)ratio=1.0
         psih=-5.*ratio
      endif
!
! --- Use minimum value of USTAR to avoid numerical problems
!     when USTAR near zero
      ustarr=amax1(ustar,usmin)
!
! --- Calculate atmospheric resistance
      ra=(alog(zref/z0m)-psih)/(vk*ustarr)
      ra=amax1(ra,ramin)
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'LWATER = ',lwater
         write(io6,*)'Z0M    = ',z0m
         write(io6,*)'PSIH   = ',psih
         write(io6,*)'USTARR = ',ustarr
         write(io6,*)'RA     = ',ra
      endif
! ***
!
! --- LOOP OVER SPECIES
      nsp=0
      do 100 j=1,nspec
!
! --- Compute deposition velocity for species deposited
      if(idryflg(j).eq.0 .OR. idryflg(j).eq.3)then
! ---    DEPOSITION ALREADY ASSIGNED
         go to 100
      elseif(idryflg(j).eq.2)then
! ---    PARTICLE DEPOSITION
         nsp=nsp+1
         call vdp(sc23(1,nsp),vgrav(1,nsp),tstop(1,nsp),fract(1,nsp),&
         npsint,ra,vairms,ustarr,ldb,vd(j))
         go to 100
      endif
!
! --- GAS DEPOSITION
!
! --- Compute the deposition layer resistance, RD
! --- (RD1 is d1*SC**d2/vk -- computed in setup routine)
      RD=RD1(j)/ustarr
!
! --- Compute resistance directly to ground or water, RG
      if(lwater)then
!
! ---    Water cell (RGW1 computed in setup routine as
! ---                RGW1 = HENRY/(ALPHAS * D3 * USTAR)
         RG=RGW1(j)/ustarr
      else
!
! ---    Land cell (RG computed in setup routine)
         RG=RGG(j)
      endif
!
! --- Stomatal pore resistance (RS)
      if(iveg.eq.1)then
!
! ---    Vegetation is active & unstressed (IVEG=1)
!        (B = stomatal pore opening (m), BMIN = minimum stomatal
!        opening, BMAX = maximum stomatal opening, fr is the approx.
!        fraction of peak short-wave solar radiation available for a
!        particular hour)
!
! ---    Temperature effects -- If T > 35 deg. C, stomata fully
! ---    open to allow evaporative cooling -- but only if unstressed --
! ---    If T < 10 deg. C, stomata closed due to decreased metabolic
! ---    activity)
         if(tdegk.gt.308.)then
! ---       T > 35 deg. C
            B=BMAX
         else if(tdegk.lt.283.)then
! ---       T < 10 deg. C
            B=BMIN
         else
            fr=qsw/qswmax
            fr=amax1(0.0,fr)
            fr=amin1(1.0,fr)
            B=BMAX*fr+BMIN*(1.-fr)
         endif
!
         RS=pconst/(B*pdiff(j))
      else if(iveg.eq.2)then
!
! ---    Vegetation is active and stressed (IVEG=2)
!        (Stomatal opening is at its minimum size)
         RS=pconst/(BMIN*pdiff(j))
      else
!
! ---    Vegetation is inactive (IVEG=3)
         RS=9.9e9
      endif
!
! --- Internal foliage resistance (RF)
!     (RM is the mesophyll resistance)
      RF=RS+RM(j)
!
! --- Compute canopy resistance
      RC=1.0/(XLAI/RF+XLAI/RCUT(j)+1.0/RG)
!
! --- Deposition velocity is the inverse of the sum of the
! --- atmospheric, deposition layer, and canopy resistances
      vd(j)=1.0/(ra+rd+rc)
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. VDCOMP -- OUTPUT'
         write(io6,*)'SPECIES (J) = ',j
         write(io6,*)'RD          = ',rd
         write(io6,*)'RC          = ',rc
         write(io6,*)'VD(j)       = ',vd(j)
         write(io6,*)'RS          = ',rs
         write(io6,*)'RF          = ',rf
         write(io6,*)'RG          = ',rg
         write(io6,*)'B           = ',b
      endif
!
100   continue
!
      return
      end
!----------------------------------------------------------------------
      subroutine vdp(sc23,vgrav,tstop,fract,npsint,ra,vairms,ustar,&
       ldb,vd)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 040611               VDP
!                J. Scire, SRC
!
! --- UPDATE
! --- V5.0 960628 - V5.73 040611
!                   (DGS): Rename NINT to NPSINT
!
! --- MODIFICATION  6/26/96 (DGS): all mass is in first size when gsig
!                                  is less than or equal to 1.0 um, so
!                                  only do first interval if
!                                  fract(1) = 1.0
!
! --- PURPOSE:  Compute a mass-weighted particle deposition
!               velocity for a single size distribution
!
! --- INPUTS:
!
!      SC23(npsint) - real    - Schmidt number ** DCONST4
!     VGRAV(npsint) - real    - Gravitational settling velocity (m/s)
!     TSTOP(npsint) - real    - Stopping time (s)
!     FRACT(npsint) - real    - Mass fraction in each size interval
!            NPSINT - integer - Number of size intervals used to
!                               evaluate the effective deposition
!                               velocity
!                RA - real    - Atmospheric resistance (s/m)
!            VAIRMS - real    - Viscosity of air (m**2/s)
!             USTAR - real    - Friction velocity (m/s)
!               LDB - logical - Flag determining if debug write
!                               statements are activated
!     Parameters:
!        IO6
!
! --- OUTPUT:
!                VD - real    - Mass-weighted deposition velocity (m/s)
!
! --- VDP called by:  VDCOMP
! --- VDP calls:      none
!----------------------------------------------------------------------
!
! --- Include PARAMETERS
      include 'params.puf'
!
      real sc23(npsint),vgrav(npsint),tstop(npsint),fract(npsint)
      logical ldb
!
      save xmin
      data xmin/-37./
!
! ***
!      write(*,*)'SUBROUTINE VDP'
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. VDP -- Inputs'
         write(io6,*)'SC23    = ',sc23
         write(io6,*)'VGRAV   = ',vgrav
         write(io6,*)'TSTOP   = ',tstop
         write(io6,*)'FRACT   = ',fract
         write(io6,*)'NPSINT  = ',npsint
         write(io6,*)'RA      = ',ra
         write(io6,*)'VAIRMS  = ',vairms
         write(io6,*)'USTAR   = ',ustar
      endif
! ***
      vd=0.0
      t1=ustar*ustar/vairms
!
! --- Reset number of intervals to 1 if all mass is in first interval
      if(fract(1).EQ.1.0) then
         intrvl=1
      else
         intrvl=npsint
      endif
!
! --- LOOP OVER SIZE INTERVALS
      do 10 i=1,intrvl
!
      st=tstop(i)*t1
!
! --- prevent underflows (min. 10. ** (-37.)
! *** xinert=10.**(-3./st)
      xpon=-3./st
      if(xpon.lt.xmin)then
         xinert=10.**(xmin)
      else
         xinert=10.**(xpon)
      endif
!
! --- Deposition layer resistance (s/m)
      rd=1.0/(ustar*(sc23(i)+xinert))
!
! --- Deposition velocity for this current interval
      vdi=1.0/(ra+rd+ra*rd*vgrav(i))+vgrav(i)
!
! --- Effective deposition velocity is weighted average of value
! --- in each size interval
      vd=vd+vdi*fract(i)
! ***
      if(ldb)then
         write(io6,*)'I       = ',i,' RD = ',rd,' VDI = ',vdi
      endif
! ***
!
10    continue
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'VD      = ',vd
      endif
! ***
!
      return
      end
!----------------------------------------------------------------------
      subroutine rdvd
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100917              RDVD
!                J. Scire
!
! --- PURPOSE:  Read a file containing a diurnal cycle of 24 hourly
!               deposition velocities (m/s) for selected species
!
! --- UPDATE
! --- V6.267-V6.302 100917  (DGS): Activate MXSPEC=35
! --- V5.7-V6.267   090710  (DGS): Increase MXSPEC=20 to MXSPEC=24
!
! --- V5.5-V5.7
!        000602_7 - 030402  (DGS): MXVAR relocated to PARAMS.CAL
! --- V5.5         000602_7 (JSS): Fix call to READIN to allow
!                                  up to MXSPEC species to have
!                                  user-specified dep. velocities
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!
! --- INPUTS:
!     Common block /GEN/ variables
!           NSPEC, CSPEC(mxspec)
!     Common block /DRYDEP/ variables
!           IDRYFLG(mxspec), NVDUSER
!     Parameters: MXVAR, MXSPEC, IO6, IO20
!
! --- OUTPUT:
!     Common block /DRYDEP/ variables
!           VDUSER(24,mxspec)
!
! --- RDVD called by: DRYI
! --- RDVD calls:     READIN
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
      include 'params.cal'
!
      integer ivleng(mxvar),ivtype(mxvar)
      character*12 cvdic(mxvar)
      logical lecho
!
      include 'gen.puf'
      include 'drydep.puf'
!
      data cvdic/mxvar*' '/,ivleng/mxvar*0/,ivtype/mxvar*0/
      data lecho/.true./
!
! --- Initialize user-specified vd array with missing value
! --- indicators
      do 10 j=1,mxspec
      do 10 i=1,24
      vduser(i,j)=-9.
10    continue
!
      if(nvduser.eq.0)go to 999
!
! --- Set up the variable dictionary and array length vector
      do 20 i=1,nspec
      if(idryflg(i).eq.3)then
         cvdic(i)=cspec(i)
         ivleng(i)=24
         ivtype(i)=1
      endif
20    continue
!
! --- read the formatted file
      write(io6,32)
32    format(///1x,13('----------')//5x,'USER-SPECIFIED DEPOSITION ',&
       'VELOCITY FILE (VD.DAT)'//)
!
! --- The following call is set for MXSPEC=35
      call readin(cvdic,ivleng,ivtype,io20,io6,lecho,&
       vduser(1,1),vduser(1,2),vduser(1,3),vduser(1,4),vduser(1,5),&
       vduser(1,6),vduser(1,7),vduser(1,8),vduser(1,9),vduser(1,10),&
       vduser(1,11),vduser(1,12),vduser(1,13),vduser(1,14),vduser(1,15),&
       vduser(1,16),vduser(1,17),vduser(1,18),vduser(1,19),vduser(1,20),&
       vduser(1,21),vduser(1,22),vduser(1,23),vduser(1,24),vduser(1,25),&
       vduser(1,26),vduser(1,27),vduser(1,28),vduser(1,29),vduser(1,30),&
       vduser(1,31),vduser(1,32),vduser(1,33),vduser(1,34),vduser(1,35),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum)
!
! --- The following call is set for MXSPEC=24
! *** call readin(cvdic,ivleng,ivtype,io20,io6,lecho,
! ***1 vduser(1,1),vduser(1,2),vduser(1,3),vduser(1,4),vduser(1,5),
! ***2 vduser(1,6),vduser(1,7),vduser(1,8),vduser(1,9),vduser(1,10),
! ***3 vduser(1,11),vduser(1,12),vduser(1,13),vduser(1,14),vduser(1,15),
! ***4 vduser(1,16),vduser(1,17),vduser(1,18),vduser(1,19),vduser(1,20),
! ***5 vduser(1,21),vduser(1,22),vduser(1,23),vduser(1,24),
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***8 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***9 idum)
!
! --- The following call is set for MXSPEC=5
! *** call readin(cvdic,ivleng,ivtype,io20,io6,lecho,
! ***1 vduser(1,1),vduser(1,2),vduser(1,3),vduser(1,4),vduser(1,5),
! ***2 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***3 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***4 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***5 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6 idum,idum,idum,idum,idum,idum,idum)
!
! --- check that values for every required species have been read
      ierr=0
      do 50 j=1,nspec
!
      isperr=0
      if(idryflg(j).eq.3)then
         do 45 i=1,24
         if(vduser(i,j).lt.0.0)then
            ierr=1
            isperr=1
         endif
45       continue
!
! ---    Error if any hour for this deposited species is missing
         if(isperr.eq.1)then
            write(io6,47)cspec(j),(vduser(n,j),n=1,24)
47          format(/1x,'ERROR in subr. RDVD -- VD values not found ',&
            'for species: ',a12,/1x,'VDUSER = ',12f10.5/10x,12f10.5/)
         endif
      endif
50    continue
!
      if(ierr.eq.1) then
         write(*,*)
         stop 'Halted in RDVD -- see list file.'
      endif
!
999   continue
      return
      end
!----------------------------------------------------------------------
      subroutine dryi(zgrid1,ldb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 040611              DRYI
!                J. Scire, SRC
!
! --- PURPOSE:  Setup routine for dry deposition module.
!               Initialization and time-invariant calculations
!               performed.
!
! --- UPDATE
! --- V5.4 000602 - V5.73 040611
!                   (DGS): Rename NINT to NPSINT
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
! --- MODIFICATION  6/26/96 (DGS): place an upper limit of 10 m on zref
!
! --- INPUTS:
!
!            ZGRID1 - real    - Height (m) of the lowest grid point
!                               (i.e., approx. 10 m)
!               LDB - logical - Flag determining if debug write
!                               statements are activated
!     Common Block /DRYGAS/ variables:
!        PDIFF(mxspec),ALPHAS(mxspec),REACT(mxspec),RM(mxspec),
!        HENRY(mxspec),RCUTR,RGR,REACTR,DCONST1,DCONST2,DCONST3
!     Common Block /DRYPART/ variables:
!        GDIAM(mxpdep),GSIG(mxpdep),RHO(mxpdep),NPSINT,DCONST4
!     Common Block /DRYDEP/ variables:
!        IDRYFLG(mxspec),ZREF
!     Common block /GEN/ variables
!           NSPEC, CSPEC(mxspec), NSDD
!     Parameters:
!        MXSPEC, MXPDEP, MXINT, IO6
!
! --- OUTPUT:
!     Common Block /DRYGAS/ variables:
!        PDIFF(mxspec),RM(mxspec),RCUTR,RGR,RGG(mxspec),RGW1(mxspec),
!        RCUT(mxspec),RD1(mxspec)
!     Common Block /DRYPART/ variables:
!        NSPART,PDIAM(mxint,mxpdep),SC23(mxint,mxpdep),
!        VGRAV(mxint,mxpdep),TSTOP(mxint,mxpdep),FRACT(mxint,mxpdep),
!        VAIRMS
!     Common Block /DRYDEP/ variables:
!        VDUSER(24,mxspec),NVDUSER
!
! --- DRYI called by:  SETUP
! --- DRYI calls:      VDP1, RDVD
!----------------------------------------------------------------------
!
! --- Include PARAMETERS
      include 'params.puf'
!
! --- Include common block /DRYGAS/  -- Gas Deposition
      include 'drygas.puf'
!
! --- Include common block /DRYPART/ -- Particle Deposition
      include 'drypart.puf'
!
! --- Include common block /DRYDEP/ -- Deposition mode flags
      include 'drydep.puf'
!
! --- Include common block /GEN/    -- General run flags
      include 'gen.puf'
!
      logical ldb
!
      data vair/0.15e-4/,vk/0.4/
!
      if(nspec.gt.mxspec)then
         write(io6,*)'ERROR in SUBR. VD1 -- NSPEC is too large -- ',&
        'NSPEC = ',nspec,' MXSPEC = ',mxspec
         write(*,*)
         stop 'Halted in VD1 -- see list file.'
      endif
!
      if(npsint.gt.mxint)then
         write(io6,*)'ERROR in SUBR. VD1 -- NPSINT is too large -- ',&
        'NPSINT = ',npsint,' MXINT = ',mxint
         write(*,*)
         stop 'Halted in VD1 -- see list file.'
      endif
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. VD1 -- INPUTS'
         write(io6,*)'IDRYFLG          = ',(idryflg(n),n=1,nspec)
         write(io6,*)'NSPEC            = ',nspec
         write(io6,*)'PDIFF (cm**2/s)  = ',(pdiff(n),n=1,nspec)
         write(io6,*)'ALPHAS           = ',(alphas(n),n=1,nspec)
         write(io6,*)'REACT            = ',(react(n),n=1,nspec)
         write(io6,*)'RM (s/cm)        = ',(rm(n),n=1,nspec)
         write(io6,*)'HENRY            = ',(henry(n),n=1,nspec)
         write(io6,*)'RCUTR (s/cm)     = ',rcutr,' RGR = ',rgr,&
         ' REACTR = ',reactr
         write(io6,*)'DCONST1 = ',dconst1,' DCONST2 = ',dconst2,&
         ' DCONST3 = ',dconst3
      endif
! ***
!
! --- Count the number of deposited species
      LL=0
      do 15 i=1,nspec
      if(idryflg(i).ge.1)then
         LL=LL+1
      endif
15    continue
!
! --- Number of deposited species must be <= total no. species
      if(LL.ne.nsdd)then
         write(io6,17)LL,nsdd,(idryflg(n),n=1,nspec)
17       format(/1x,'ERROR in subr. VD1 -- LL .NE. NSDD -- LL = ',i5,&
         3x,'NSDD = ',i5,3x,'IDRYFLG = ',100i2)
         write(*,*)
         stop 'Halted in VD1 -- see list file.'
      endif
!
! --- Count the number of species with user-specified deposition
! --- velocities
      nvduser=0
      do 20 i=1,nspec
      if(idryflg(i).eq.3)then
         nvduser=nvduser+1
      endif
20    continue
!
! --- Read diurnal cycles of user-specified deposition velocities
! --- for pollutants flagged with IDRYFLG(i)=3
      call rdvd
!
      if(ldb)then
         write(io6,*)
         write(io6,*)'NVDUSER = ',nvduser
         do 133 i=1,nspec
         write(io6,132)i,cspec(i),(vduser(n,i),n=1,24)
132      format(1x,'NO.: ',i3,' Species: ',a12,2x,12f8.5/&
                                              33x,12f8.5)
133      continue
      endif
!
! --- Convert length units of input deposition variables from cm to m
!
      do 10 i=1,nspec
!
! --- IDRYFLG -  Flags indicating which species are deposited
!                (0=no, 1=yes-gas, 2=yes-particle, 3=yes-user specified
!                deposition velocities)
      if(idryflg(i).eq.0.or.idryflg(i).eq.3)go to 10
!
! --- Pollutant diffusivity (cm**2/s to m**2/s)
      pdiff(i)=pdiff(i)*1.e-4
!
! --- Mesophyll resistance from s/cm to s/m
      rm(i)=rm(i)*1.e2
10    continue
!
! --- Reference cuticle resistance (s/cm to s/m)
      rcutr=rcutr*1.e2
!
! --- Reference ground resistance (s/cm to s/m)
      rgr=rgr*1.e2
!
! --- Set reference height to height of the
! --- lowest grid point
      zref=AMIN1(zgrid1,10.)
!
! --- Compute time-invariant parameters
      nspart=0
      do 110 i=1,nspec
      if(idryflg(i).eq.0.or.idryflg(i).eq.3)then
         go to 110
      else if(idryflg(i).eq.2)then
!
! ---    PARTICLE DEPOSITION
         nspart=nspart+1
         if(nspart.gt.mxpdep)then
            write(io6,*)'ERROR IN SUBR. VD1 -- Too many particle ',&
            'species -- NSPART = ',nspart,' MXPDEP = ',mxpdep
            write(*,*)
            stop 'Halted in VD1 -- see list file.'
         endif
!
         call vdp1(gdiam(nspart),gsig(nspart),rho(nspart),npsint,&
         dconst4,ldb,pdiam(1,nspart),sc23(1,nspart),vgrav(1,nspart),&
         tstop(1,nspart),fract(1,nspart),vairms)
         go to 110
      endif
!
! --- GAS DEPOSITION
!
! --- Schmidt number = viscosity of air/(diffusivity of the pollutant)
!     (vair = viscosity of air at 20 deg. C = 0.15e-4 m**2/s)
      sc=vair/pdiff(i)
!
! --- Time-invariant term of deposition layer resistance
      rd1(i)=dconst1*sc**dconst2/vk
!
! --- Cuticle resistance
      ratio=reactr/react(i)
      rcut(i)=rcutr*ratio
!
! --- Ground resistance
      rgg(i)=rgr*ratio
!
! --- Time-invariant term of "ground" resistance to water
      rgw1(i)=henry(i)/(alphas(i)*dconst3)
110   continue
!
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. VD1 -- OUTPUT'
         write(io6,*)'PDIFF (m**2/s)  = ',(pdiff(n),n=1,nspec)
         write(io6,*)'RM (s/m)        = ',(rm(n),n=1,nspec)
         write(io6,*)'RCUTR (s/m)     = ',rcutr,' RGR = ',rgr
         write(io6,*)'RGG (s/m)       = ',(rgg(n),n=1,nspec)
         write(io6,*)'RGW1 (s/m)      = ',(rgw1(n),n=1,nspec)
         write(io6,*)'RCUT (s/m)      = ',(rcut(n),n=1,nspec)
         write(io6,*)'RD1 (s/m)       = ',(rd1(n),n=1,nspec)
         write(io6,*)'ZREF (m)        = ',zref
      endif
! ***
      return
      end
!----------------------------------------------------------------------
      subroutine vdp1(gdiam,gsig,rho,npsint,dconst4,ldb,pdiam,sc23,&
       vgrav,tstop,fract,vairms)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 040611              VDP1
!                J. Scire, SRC
!
! --- PURPOSE:  Setup routine for PARTICLE dry deposition.
!               Initialization and time-invariant calculations
!               performed.
!
! --- UPDATE
! --- V5.4 000602 - V5.73 040611
!                   (DGS): Rename NINT to NPSINT
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!
! --- MODIFICATION  6/26/96 (DGS): put all mass into first size when
!                                  gsig is less than or equal to 1.0 um
!
! --- INPUTS:
!
!             GDIAM - real    - Geometric mass mean diameter (microns)
!              GSIG - real    - Geometric standard deviation of the
!                               distribution (microns)
!               RHO - real    - Particle density (g/cm**3)
!            NPSINT - integer - Number of size intervals used to
!                               evaluate the effective deposition
!                               velocity
!           DCONST4 - real    - Empirical constant in particle
!                               deposition layer resistance eqn.
!               LDB - logical - Flag determining if debug write
!                               statements are activated
!     Parameters:
!        IO6
!
! --- OUTPUT:
!       PDIAM(npsint) - real    - Geometric mean diameter (microns)
!                               of each interval
!      SC23(npsint) - real    - Schmidt number ** DCONST4
!     VGRAV(npsint) - real    - Gravitational settling velocity (m/s)
!     TSTOP(npsint) - real    - Stopping time (s)
!     FRACT(npsint) - real    - Mass fraction in each size interval
!            VAIRMS - real    - Viscosity of air (m**2/s)
!
! --- VDP1 called by:  DRYI
! --- VDP1 calls:      ERF
!----------------------------------------------------------------------
!
! --- Include PARAMETERS
      include 'params.puf'
!
      real pdiam(npsint),sc23(npsint),vgrav(npsint),tstop(npsint),&
           fract(npsint)
      logical ldb
!
      save a1,a2,a3,xmfp,pi,vcon,xk,temp,vair,g
!
      data a1/1.257/,a2/0.4/,a3/0.55/,xmfp/6.5e-6/
      data pi/3.1415927/,vcon/1.81e-4/,xk/1.38e-16/
      data temp/293.15/
      data vair/0.15/,g/981./
!
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. VDP1 -- INPUTS'
         write(io6,*)'GDIAM (um)       = ',gdiam
         write(io6,*)'GSIG (um)        = ',gsig
         write(io6,*)'RHO (g/cm**3)    = ',rho
         write(io6,*)'DCONST4          = ',dconst4
         write(io6,*)'NPSINT           = ',npsint
      endif
! ***
      if(npsint.gt.1 .AND. gsig.GT.1.0)then
!
! ---    Setup size intervals from -4*sigma to +4*sigma from mean
         xincr=8.0/float(npsint)
!
! ---    Constant 1.414214 = sqrt(2)
         xtmp1=1.414214*alog(gsig)
!
! ---    DLOW is the diameter of the lower end of the interval
!        (set equal to DHIGH within loop)
         dhigh=gdiam*gsig**(-4)
      endif
!
! --- LOOP over "NPSINT" size intervals
      do 10 i=1,npsint
!
      if(npsint.gt.1 .AND. gsig.GT.1.0)then
         dlow=dhigh
!
! ---    DHIGH is the diameter of the high end of the interval
         dhigh=gdiam*gsig**(-4.+xincr*float(i))
!
! ---    Compute the area under the normal curve within this interval
         fract(i)=0.5*(erf(alog(dhigh/gdiam)/xtmp1)-&
                       erf(alog(dlow/gdiam)/xtmp1))
!
! ---    Compute the geometric mean diameter (microns) of the interval
         pdiam(i)=exp(0.5*alog(dlow*dhigh))
!
      else if(npsint.eq.1)then
!
! ---    Only one size interval
         fract(i)=1.0
         pdiam(i)=gdiam
!
      else if(gsig.le.1.0)then
! ---    Only one effective particle size; put all mass in first
         fract(i)=0.0
         if(i.eq.1) fract(i)=1.0
         pdiam(i)=gdiam
!
      else
         write(io6,*)'ERROR in Subr. VDP1 -- Invalid value of NPSINT ',&
         '-- NPSINT = ',npsint
         write(*,*)
         stop 'Halted in VDP1 -- see list file.'
      endif
!
! --- Slip correction factor
      diamcm=1.e-4*pdiam(i)
      scf=1.+2.0*xmfp*(a1+a2*exp(-a3*diamcm/xmfp))/diamcm
!
! --- Stokes friction coefficient
      sfc=3.*pi*vcon*diamcm/scf
!
! --- Diffusivity (cm**2/s)
      diff=xk*temp/sfc
! ***
      if(ldb)then
         write(io6,*)'i = ',i,' diamcm = ',diamcm,' scf = ',scf,&
         ' sfc = ',sfc,' diff = ',diff
      endif
! ***
!
! --- schmidt number
! --- (vair = viscosity of air at 20 deg. c = 0.15 cm**2/s)
      sc23(i)=(vair/diff)**(-dconst4)
!
! --- gravitational settling velocity (m/s)
      vgrav(i)=0.01*rho*g*diamcm**2*scf/(18.*vcon)
!
! --- stopping times
      tstop(i)=vgrav(i)/(0.01*g)
10    continue
!
! --- convert viscosity of air (at 20. deg. c) from cm**2/s to m**2/s
      vairms=1.e-4*vair
! ***
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. VDP1 -- Outputs'
         write(io6,*)'PDIAM  = ',pdiam
         write(io6,*)'SC23   = ',sc23
         write(io6,*)'VGRAV  = ',vgrav
         write(io6,*)'TSTOP  = ',tstop
         write(io6,*)'FRACT  = ',fract
         write(io6,*)'VAIRMS = ',vairms
      endif
! ***
!
      return
      end
!----------------------------------------------------------------------
      subroutine exmet(iflag,ixs,iys,md,tempk,qsw,irh,rhoair,&
                       cc,czen)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212             EXMET
!                J. Scire
!
! --- PURPOSE:  Find the closest non-missing value of air temperature,
!               short-wave radiation, relative humidity, and air
!               density, cloud cover, and cosine of solar angle
!               to a particular grid point.
!               NOTE: if IFLAG = 1, only temperature is returned
!                     if IFLAG = 2, values for all six variables
!                                   are returned
!
! --- UPDATE
! --- V5.7-V6.3     100212  (DGS): Add nested CALMET grids
!                   100212  (DGS): Do not let returned station ID
!                                  from FINDR/FINDI overwrite the
!                                  station ID from the NEARS array
! --- V5.4-V5.7     030402  (FRR): Add 2D met arrays (i2dmet) of
!                                  RHO,QSW,RH,TEMP if
!                                  available in CALMET 
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.0     980304  (DGS): add clouds & sun angle
!
! --- INPUTS:
!            IFLAG - integer - Flag determining which variables are
!                              computed ( 1 ==> TEMPK only,
!                              2 ==> TEMP, QSW, IRH, RHOAIR)
!              IXS - integer - X index of the closest met. grid point
!                              to the puff/slug center
!              IYS - integer - Y index of the closest met. grid point
!                              to the puff/slug center
!               MD - integer - MET grid domain index
!
!     Common block /METHD/ variables:
!           NXM(mxmetdom), NYM(mxmetdom), NEARS(mxnx,mxny,mxmetdom),
!           XSSTA(mxss,mxmetdom),YSSTA(mxss,mxmetdom), NSSTA(mxmetdom),
!           XGRIDM(mxmetdom), LCALGRD, I2DMET
!           
!     Common block /METHR/ variables:
!           TMET(mxnx,mxny,mxnz,mxmetdom), TEMPSS(mxss,mxmetdom),
!            RHOSS(mxss,mxmetdom), QSWSS(mxss,mxmetdom), 
!            IRHSS(mxss,mxmetdom), CCSS(mxss,mxmetdom),
!            CZENSS(mxss,mxmetdom)
! frr (09/01)
!           TEMP2D(mxnx,mxny,mxmetdom), RHO2D(mxnx,mxny,mxmetdom),
!           QSW2D(mxnx,mxny,mxmetdom), IRH2D(mxnx,mxny,mxmetdom),
!           CC2D(mxnx,mxny,mxmetdom), CZEN2D(mxnx,mxny,mxmetdom)
!          
!     Parameters:
!           MXNX, MXNY, MXNZ, MXSS, MXUS, MXPS, IO6
!
! --- OUTPUT:
!            TEMPK - real    - Air temperature (deg. K)
!              QSW - real    - Short-wave solar radiation (W/m**2)
!              IRH - integer - Relative humidity (percent)
!           RHOAIR - real    - Air density (kg/m**3)
!               CC - real    - Cloud cover (tenths)
!             CZEN - real    - Cosine of solar zenith angle
!
! --- EXMET called by:  COMP
! --- EXMET calls:      FINDR, FINDI
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      logical ldb2
!
      include 'methd.puf'
      include 'methr.puf'
!
! --- Missing value indicators for integer, real variables
      data imiss/9999/,xmiss/9999./
!
! --- Compute grid point coordinates (m) relative to current
! --- met. grid origin
      xgrd=(float(ixs)-0.5)*xgridm(md)
      ygrd=(float(iys)-0.5)*xgridm(md)
!
!
! frr (09/01)- Use 2D arrays if available in CALMET
       if(i2dmet.EQ.1) then
          tempk=temp2d(ixs,iys,md)
          if(iflag.eq.2)then
            qsw=qsw2d(ixs,iys,md)
            irh=irh2d(ixs,iys,md)
            rhoair=rho2d(ixs,iys,md)
            cc=cc2d(ixs,iys,md)
            czen=czen2d(ixs,iys,md)
          else
! ---       Flag variables other than temperature as missing
            qsw=xmiss
            irh=imiss
            rhoair=xmiss
            cc=xmiss
            czen=xmiss
         endif
       elseif(i2dmet.EQ.0) then
! ---    ISTA is the surface met. station closest to grid point
! ---    (IXS,IYS) in met grid domain MD
         ista=nears(ixs,iys,md)
!
! ---    AIR TEMPERATURE (deg. K)
         if(lcalgrd)then
! ---    Use gridded temp. data, if available
            tempk=tmet(ixs,iys,1,md)
         else
! ---       Use air temp. at closest surface station
            tempk=tempss(ista,md)
! ---       Find temp. at nearest station with non-missing data
            if(tempk.ge.xmiss)then
               call findr(xssta(1,md),yssta(1,md),nssta(md),&
                          tempss(1,md),xgrd,ygrd,ista2,tempk,ierr)
!
               if(ierr.eq.1)then
                  write(io6,*)'ERROR in subr. EXMET -- All surface ',&
                  'temp. data missing -- IERR = ',ierr
                  write(io6,*)'TEMPSS = ',(tempss(n,md),n=1,nssta(md))
                  write(*,*)
                  stop 'Halted in EXMET -- see list file.'
               endif
            endif
         endif
!
         if(iflag.eq.2)then
!
! ---       SHORT-WAVE RADIATION (W/m**2)
            qsw=qswss(ista,md)
            if(qsw.ge.xmiss)then
               call findr(xssta(1,md),yssta(1,md),nssta(md),&
                          qswss(1,md),xgrd,ygrd,ista2,qsw,ierr)
!
               if(ierr.eq.1)then
                  write(io6,*)'ERROR in subr. EXMET -- All surface ',&
                  'short-wave radiation data missing -- IERR = ',ierr
                  write(io6,*)'QSWSS = ',(qswss(n,md),n=1,nssta(md))
                  write(*,*)
                  stop 'Halted in EXMET -- see list file.'
               endif
            endif
!
! ---       RELATIVE HUMIDITY (percent)
            irh=irhss(ista,md)
            if(irh.ge.imiss)then
               call findi(xssta(1,md),yssta(1,md),nssta(md),&
                          irhss(1,md),xgrd,ygrd,ista2,irh,ierr)
!
               if(ierr.eq.1)then
                  write(io6,*)'ERROR in subr. EXMET -- All surface ',&
                  'relative humidity data missing -- IERR = ',ierr
                  write(io6,*)'IRHSS = ',(irhss(n,md),n=1,nssta(md))
                  write(*,*)
                  stop 'Halted in EXMET -- see list file.'
               endif
            endif
!
! ---       AIR DENSITY (kg/m**3)
            rhoair=rhoss(ista,md)
            if(rhoair.ge.xmiss)then
               call findr(xssta(1,md),yssta(1,md),nssta(md),&
                          rhoss(1,md),xgrd,ygrd,ista2,rhoair,ierr)
!
               if(ierr.eq.1)then
                  write(io6,*)'ERROR in subr. EXMET -- All surface ',&
                  'air density data missing -- IERR = ',ierr
                  write(io6,*)'RHOSS = ',(rhoss(n,md),n=1,nssta(md))
                  write(*,*)
                  stop 'Halted in EXMET -- see list file.'
               endif
            endif
!
! ---       CLOUD COVER (tenths)
            cc=ccss(ista,md)
            if(cc.ge.xmiss)then
               call findr(xssta(1,md),yssta(1,md),nssta(md),&
                          ccss(1,md),xgrd,ygrd,ista2,cc,ierr)
!
               if(ierr.eq.1)then
                  write(io6,*)'ERROR in subr. EXMET -- All surface ',&
                  'cloud cover data missing -- IERR = ',ierr
                  write(io6,*)'CCSS = ',(ccss(n,md),n=1,nssta(md))
                  write(*,*)
                  stop 'Halted in EXMET -- see list file.'
               endif
            endif
!
! ---       Cosine of solar zenith angle
            czen=czenss(ista,md)
            if(czen.ge.xmiss)then
               call findr(xssta(1,md),yssta(1,md),nssta(md),&
                          czenss(1,md),xgrd,ygrd,ista2,czen,ierr)
!
               if(ierr.eq.1)then
                  write(io6,*)'ERROR in subr. EXMET -- All surface ',&
                  'solar zenth data missing -- IERR = ',ierr
                  write(io6,*)'CZENSS = ',(czenss(n,md),n=1,nssta(md))
                  write(*,*)
                  stop 'Halted in EXMET -- see list file.'             !      
               endif
            endif
!
         else
!
! ---       Flag variables other than temperature as missing
            qsw=xmiss
            irh=imiss
            rhoair=xmiss
            cc=xmiss
            czen=xmiss
         endif
!
!*****
         ldb2=.false.
         if(ldb2)then
            write(io6,*)
            write(io6,*)'SUBR. EXMET -- iflag = ',iflag,' ixs = ',ixs,&
            ' iys = ',iys,' dgrid = ',xgridm(md),' ista = ',ista
           write(io6,*)'tempk = ',tempk,' qsw = ',qsw,' irh = ',irh,&
            ' rhoair = ',rhoair
            write(io6,*)'cc = ',cc,' czen = ',czen
         endif
!*****
      else
         write(*,*)'Subr. EXMET:  Invalid I2DMET = ',i2dmet
         stop
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine findr(xsta,ysta,nsta,dat,x,y,ista,value,ierr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 900228             FINDR
!                J. Scire, SRC
!
! --- PURPOSE:  Find the closest station to a specified (X,Y)
!               coordinate that has non-missing data and pass
!               the real variable at that station back to the
!               calling routine
!
! --- INPUTS:
!       XSTA(nsta) - real    - Array of X coordinates (m) for each
!                              station relative to the met. grid origin
!                              at (0., 0.)
!       YSTA(nsta) - real    - Array of Y coordinates (m) for each
!                              station relative to the met. grid origin
!                              at (0., 0.)
!             NSTA - integer - Number of stations
!        DAT(nsta) - real    - Values of the variable at each station
!                              (NOTE: 9999. used as a missing value
!                              indicator)
!                X - real    - Reference X coordinate (m)
!                Y - real    - Reference Y coordinate (m)
!
! --- OUTPUT:
!             ISTA - integer - Station number of closest station
!            VALUE - real    - Value of from DAT array for closest
!                              station "ISTA"
!             IERR - integer - Error code (0=no error, 1=all data
!                              missing, so VALUE = missing value
!                              indicator
!
! --- FINDR called by:  EXMET, GETOZ
! --- FINDR calls:      none
!----------------------------------------------------------------------
!
      real xsta(nsta),ysta(nsta),dat(nsta)
      data xmax/1.e38/,xmiss/9999./
!
      ista=0
      value=xmiss
      dmin2=xmax
!
      do 10 i=1,nsta
!
! --- Compute the (distance)**2 to each station with non-missing data
      if(dat(i).lt.xmiss)then
         dist2=(xsta(i)-x)**2+(ysta(i)-y)**2
!
! ---    Keep track of the closest station to the reference point
         if(dist2.lt.dmin2)then
            dmin2=dist2
            ista=i
         endif
      endif
10    continue
!
      if(ista.eq.0)then
         ierr=1
         go to 999
      endif
!
! --- Extract the value at closest station from the data array
      value=dat(ista)
      ierr=0
!
999   continue
      return
      end
!----------------------------------------------------------------------
      subroutine findi(xsta,ysta,nsta,idat,x,y,ista,ivalue,ierr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 900228             FINDI
!                J. Scire, SRC
!
! --- PURPOSE:  Find the closest station to a specified (X,Y)
!               coordinate that has non-missing data and pass
!               the integer variable at that station back to the
!               calling routine
!
! --- INPUTS:
!       XSTA(nsta) - real    - Array of X coordinates (m) for each
!                              station relative to the met. grid origin
!                              at (0., 0.)
!       YSTA(nsta) - real    - Array of Y coordinates (m) for each
!                              station relative to the met. grid origin
!                              at (0., 0.)
!             NSTA - integer - Number of stations
!       IDAT(nsta) - integer - Values of the variable at each station
!                              (NOTE: 9999 used as a missing value
!                              indicator)
!                X - real    - Reference X coordinate (m)
!                Y - real    - Reference Y coordinate (m)
!
! --- OUTPUT:
!             ISTA - integer - Station number of closest station
!           IVALUE - real    - Value of from IDAT array for closest
!                              station "ISTA"
!             IERR - integer - Error code (0=no error, 1=all data
!                              missing, so VALUE = missing value
!                              indicator
!
! --- FINDI called by:  EXMET
! --- FINDI calls:      none
!----------------------------------------------------------------------
!
      real xsta(nsta),ysta(nsta)
      integer idat(nsta)
      data xmax/1.e38/,imiss/9999/
!
      ista=0
      ivalue=imiss
!
      dmin2=xmax
!
      do 10 i=1,nsta
!
! --- Compute the (distance)**2 to each station with non-missing data
      if(idat(i).lt.imiss)then
         dist2=(xsta(i)-x)**2+(ysta(i)-y)**2
!
! ---    Keep track of the closest station to the reference point
         if(dist2.lt.dmin2)then
            dmin2=dist2
            ista=i
         endif
      endif
10    continue
!
      if(ista.eq.0)then
         ierr=1
         go to 999
      endif
!
! --- Extract the value at closest station from the data array
      ivalue=idat(ista)
      ierr=1
!
999   continue
      return
      end
!----------------------------------------------------------------------
      subroutine fin(itest)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108               FIN
! ---            J. Scire
!
! --- PURPOSE:  Run termination routine -- compute runtime,
!               write last day processed
!
! --- UPDATE
! --- V6.111-V6.268 100108  (DGS): Add argument to WARN call
! --- V6.1-V6.111   060329  (DGS): Revise text for last period time
! --- V5.721-V6.1   050915  (DGS): resolve times to the second and use
!                                  explicit begin-time/end-time format
! --- V5.7-V5.721   040503  (DGS): Add warning function (call WARN)
! --- V5.5-V5.7     030402  (DGS): Add list file unit to JULDAY call
!                                  Change rdate, rdate2 to include
!                                  YYYY format for year (MM-DD-YYYY)
! --- V5.0-V5.2     991104  (DGS): YYYY year format
!
! --- INPUTS:
!          ITEST - integer - Flag indicating if execution is to
!                            include COMPUTATIONAL phase
!                            (ITEST = 1 to STOP program and skip
!                                       the COMPUTATIONAL phase
!                             ITEST = 2 to CONTINUE execution to
!                                       include computations)
!       Common block /DATEHR/
!          nyrb, nmob, ndayb, njulb, nhrb, nsecb
!          nyre, nmoe, ndaye, njule, nhre, nsece
!       Common block /QA/
!          rdate, rtime, rcpu
!       Common block /OUTPT/
!          imesg
!       Parameters: IO6, IOMESG
!
! --- OUTPUT:  none
!
! --- FIN called by:  MAIN
! --- FIN calls:      DATETM, JULDAY, DELTT, YR4C, WARN
!----------------------------------------------------------------------
!
! --- include parameters
      include 'params.puf'
!
      character*8 rtime2
      character*10 rdate2
!
      include 'datehr.puf'
      include 'qa.puf'
      include 'outpt.puf'
! --- Process any warnings collected during the run
      call WARN('FIN         ',0.,0.)
!
      write(iomesg,*)'TERMINATION PHASE'
!
! --- Write last day/hour processed
      if(ITEST.eq.2)then
         write(io6,5)nyre,nmoe,ndaye,njule,nhre,nsece
5        format(//2x,'LAST PERIOD PROCESSED ENDS AT:'/5x,&
                '  Year: ',i4,2x,'Month: ',i2,3x,'Day: ',i2,3x,&
                'Julian day: ',i3,3x,'Hour: ',i2,3x,'Second: ',i4)
      else
!
! ---    TEST mode -- COMPUTATIONAL phase skipped
         write(io6,12)
12       format(/1x,13('----------')//1x,&
         'Completion of CALPUFF test mode run -- run terminating ',&
         'normally'//1x,13('----------'))
      endif
!
! --- get system date & time at end of run
      call datetm(rdate2,rtime2,rcpu)
!
! --- compute runtime
      read(rtime(1:2),10)ihr1
      read(rtime(4:5),10)imin1
      read(rtime(7:8),10)isec1
10    format(i2)
      t1=ihr1*3600.+imin1*60.+isec1
!
      read(rtime2(1:2),10)ihr2
      read(rtime2(4:5),10)imin2
      read(rtime2(7:8),10)isec2
      t2=ihr2*3600.+imin2*60.+isec2
!
      if(rdate.eq.rdate2)then
         delt=t2-t1
      else
         read(rdate(1:2),10)imo1
         read(rdate(4:5),10)iday1
         read(rdate(7:10),'(i4)')iyr1
         call julday(io6,iyr1,imo1,iday1,ijul1)
!
         read(rdate2(1:2),10)imo2
         read(rdate2(4:5),10)iday2
         read(rdate2(7:10),'(i4)')iyr2
         call julday(io6,iyr2,imo2,iday2,ijul2)
!
! ---    compute no. hours from beg. of first hour of run to
! ---    ending hour of ending day of the run
         call deltt(iyr1,ijul1,ihr1,iyr2,ijul2,ihr2,idelhr)
!
! ---    adjust for minutes and seconds
         delt=idelhr*3600.-imin1*60.-isec1+imin2*60.+isec2
      endif
! --- On the PC, the runtime and CPU time are the same
! --- (DATETM provides RCPU = 0.0 on the PC)
      if(rcpu.EQ.0.0)rcpu=delt
! --- Report current date
      write(io6,1402)rtime2,rdate2,delt,rcpu
1402  format(//2x,'End of run -- Clock time: ',a8/&
               2x,'                    Date: ',a10//&
               2x,'      Elapsed Clock Time: ',f10.1,' (seconds)'//&
               2x,'                CPU Time: ',f10.1,' (seconds)')
!
      return
      end
!----------------------------------------------------------------------
      subroutine met1(kg,lecho)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 121203              MET1
!                J. Scire
!
! --- PURPOSE:  Read header records of a CALMET meteorological
!               data file
!
! --- UPDATE
!
! --- V6.301-6.42_x1.0 121203  : Change IBSMETX,IESMETX,IESMET
!                                  assignments from scalar to array
!
! --- V6.3-V6.301   100827  (DGS): Allow nested CALMET files to have
!                                  different time-periods
! --- V6.1-V6.3     100212  (DGS): Add nested CALMET grids; include temp
!                                  workspace rather than passing in arg
! --- V5.7-V6.1     050915  (DGS): Add 'dataset 2.1' file format, and
!                                  use c*16 variable VERCMET in new
!                                  /DATASET/ common to identify version.
!                                  (DVERSM not used).  Implement full
!                                  begin/end time processing.
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4 call
!                   030402  (DGS): Change (f6) to (f6.0)
!                   030402  (DGS): Add echo of CALMET control file (new
!                                  header format)
!                   030402  (DGS): Add DVERSM (dataset version) to 
!                                  /METHD/ and use throughout code to
!                                  identify CALMET.DAT format, and
!                                  compute I2DMET here
! --- V5.4-V5.5     010730  (JSS): include IWAT2 on list file output
! --- V5.4-V5.4     000602_4(DGS): pass base time zone into /METHD/
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
!                   000602  (DGS): add version check for record '2a'
! --- V5.0-V5.2     991104  (DGS): YYYY year format
! --- V5.0-V5.0     990228a (DGS): add IEOF to RDR2D arguments
! --- V5.0-V5.0     980304  (DGS): accept new/old CALMET headers
! --- V4.0-V5.0     971107  (DGS): pass start-time from met file
!                                  for use if METRUN=1
!
! --- INPUTS:
!            KG - integer       - Current CALMET grid (e.g., nesting)
!         LECHO - logical       - Control variable for output of header
!                                 record information
!
!     Common block /DATASET/ variables:
!           VERCMET(mxmetdom)
!     Common block /WRKSPC/ variables:
!           tmp1(mxnx,mxny),nw1
!
!     Parameters:
!           MXMETDOM, MXNX, MXNY, MXNZP1, MXSS, MXUS, MXPS, IO6
!
! --- OUTPUT:
!     Common block /METHD/ variables:
!           NXM(mxmetdom),NYM(mxmetdom),NZM,XGRIDM(mxmetdom),
!           XGRIDMI(mxmetdom),XORIGM(mxmetdom),YORIGM(mxmetdom),
!           XBTZM,NSSTA(mxmetdom),NPSTA(mxmetdom),
!           NLU,IWAT1,IWAT2,ZFACEM(mxnzp1),
!           XSSTA(mxss,mxmetdom),YSSTA(mxss,mxmetdom),
!           XUSTA(mxus,mxmetdom),YUSTA(mxus,mxmetdom),
!           XPSTA(mxps,mxmetdom),YPSTA(mxps,mxmetdom),
!           Z0(mxnx,mxny,mxmetdom),ILANDU(mxnx,mxny,mxmetdom),
!           ELEV(mxnx,mxny,mxmetdom),XLAI(mxnx,mxny,mxmetdom),
!           NEARS(mxnx,mxny,mxmetdom),LCALGRD,
!           i2dmet, itimes
!           ibymet(mxmetdom),ibmmet,(mxmetdom)ibdmet(mxmetdom),
!           ibjdmet(mxmetdom),ibhmet(mxmetdom),ibsmet(mxmetdom)
!           ieymet(mxmetdom),iemmet(mxmetdom),iedmet(mxmetdom),
!           iejdmet(mxmetdom),iehmet(mxmetdom),iesmet(mxmetdom)
!           levmet, llconfm, xlat1m, xlat2m, rlat0m, rlon0m
!           iutmznm,feastm,fnorthm,pmapm,datumm,datenm,utmhemm
!     Common block /NESTHD/ variables:
!           levmetx,dversmx,nzmx,
!           xbtzmx,xlat0mx,xlon0mx,llconfmx,rlat0mx,rlon0mx,xlat1mx,
!           xlat2mx,nlux,iwat1x,iwat2x,zfacemx(mxnzp1),lcalgrdx,
!           i2dmetx,itimesx,itimeprfx,
!           iutmznmx,feastmx,fnorthmx,pmapmx,datummx,datenmx,utmhemmx
!
! --- MET1 called by:  SETUP, NEXTFIL
! --- MET1 calls:      RDR1D, METQA, YR4
!                      INCR, GRDAY, JULDAY
!                      RDR2D, RDI2D, OUT, NESTHDX, NESTQAX
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common block /METHD/ -- CALMET header record data
      include 'methd.puf'
! --- Include common block /NESTHD/ -- CALMET header record data from
! ---                                  nested grids for QA
      include 'nesthd.puf'
! --- Include common block /DATASET/  -- Input file dataset version
      include 'dataset.puf'
! --- Include common block /WRKSPC/  -- Working arrays
      include 'wrkspc.puf'
!
      character*80 title(3)
      character*70 messag
      character*8 vermet,clab1,clab2
      character*8 clabexp,clabex1,clabex2
      logical ldate,lecho
! --- Control for printing entire CALMET control file
      logical lmetcf
! --- Local Variables
      character*8 axbtzm
      character*16 dataset,dataver
      character*33 blank33
      character*64 datamod
      character*80 doc1
      character*132 comment1,blank
      data ldate/.false./
      data lmetcf/.false./
      data blank33/'                                 '/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
      if(lecho) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'CALMET Control file information: Grid ',kg
         write(io6,*)'------------------------------------------'
         write(io6,*)
      endif
! --- Modify MET1 to process header of all CALMET nested grids
! --- The first is the master (outermost)
      io=io7+(kg-1)
! --- Set dataset version as a real  (not currently used)
      read(vercmet(kg),'(f16.0)') dversm
! --- Read records
! -------------------
!
      if(vercmet(kg).EQ.'2.1') then
! ---   Dataset 2.1 with comment records and begin/end times
! ----------------------------------------------------------
        itimes=1
        i2dmet=1
!
! ---   Record #1 - File Declaration -- 24 words
        read(io) dataset,dataver,datamod
        if(lecho) write(io6,'(2a16,a64)') dataset,dataver,datamod
!
! ---   Record #2 - Number of comment lines -- 1 word
        read(io) ncom
! ---   Loop over comment records
        do i=1,ncom
          comment1=blank
          read(io) comment1
          if(i.EQ.1) then
! ---       Save model version line
            doc1=comment1(1:80)
          elseif(i.LE.4) then
! ---       Save 3 title lines
            title(i-1)=comment1(1:80)
          endif
          if(lmetcf) write(io6,*) comment1
        enddo
        if(lecho .AND. .NOT.lmetcf) then
           write(io6,*)
           write(io6,*) doc1
           write(io6,*) title(1)
           write(io6,*) title(2)
           write(io6,*) title(3)
           write(io6,*)
        endif
!
! ---   record #NCOM+3 - run control parameters -- 39 words
        read(io)&
         ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg),ibsmet(kg),&
         ieymet(kg),iemmet(kg),iedmet(kg),iehmet(kg),iesmet(kg),&
         axbtzm,irlgmet,irtype,nxm(kg),nym(kg),nzm,&
         xgridm(kg),xorigm(kg),yorigm(kg),iwfcod,&
         nssta(kg),nusta(kg),npsta(kg),nowsta(kg),&
         nlu,iwat1,iwat2,lcalgrd,pmapm,datumm,datenm,&
         feastm,fnorthm,utmhemm,iutmznm,&
         rnlat0m,relon0m,xlat1m,xlat2m
        call YR4(io6,ibymet(kg),ierrb)
        call YR4(io6,ieymet(kg),ierre)
        if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in MET1'
        call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
        call JULDAY(io6,ieymet(kg),iemmet(kg),iedmet(kg),iejdmet(kg))
! ---   Process character AXBTZM into real XBTZM
        call UTCBASR(axbtzm,xbtzm)
        rlat0m=rnlat0m
        rlon0m=-relon0m
      elseif(vercmet(kg).EQ.'2.0') then
! ---   Dataset 2.0 with comment records
! --------------------------------------
        itimes=0
        i2dmet=1
!
! ---   Record #1 - File Declaration -- 24 words
        read(io) dataset,dataver,datamod
        if(lecho) write(io6,'(2a16,a64)') dataset,dataver,datamod
!
! ---   Record #2 - Number of comment lines -- 1 word
        read(io) ncom
! ---   Loop over comment records
        do i=1,ncom
          comment1=blank
          read(io) comment1
          if(i.EQ.1) then
! ---       Save model version line
            doc1=comment1(1:80)
          elseif(i.LE.4) then
! ---       Save 3 title lines
            title(i-1)=comment1(1:80)
          endif
          if(lmetcf) write(io6,*) comment1
        enddo
        if(lecho .AND. .NOT.lmetcf) then
           write(io6,*)
           write(io6,*) doc1
           write(io6,*) title(1)
           write(io6,*) title(2)
           write(io6,*) title(3)
           write(io6,*)
        endif
!
! ---   record #NCOM+3 - run control parameters -- 37 words
        read(io)&
         ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg),ibtzm,irlgmet,&
         irtype,nxm(kg),nym(kg),nzm,xgridm(kg),xorigm(kg),yorigm(kg),&
         iwfcod,nssta(kg),nusta(kg),npsta(kg),nowsta(kg),nlu,iwat1,&
         iwat2,lcalgrd,pmapm,datumm,datenm,feastm,fnorthm,&
         utmhemm,iutmznm,rnlat0m,relon0m,xlat1m,xlat2m
        call YR4(io6,ibymet(kg),ierrb)
        if(ierrb.NE.0) stop 'Halted in MET1'
! ---   Place integer IBTZM into real XBTZM
        xbtzm=FLOAT(ibtzm)
        rlat0m=rnlat0m
        rlon0m=-relon0m
! ---   Convert start time from end to beginning of first hour
        call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
        call INCR(io6,ibymet(kg),ibjdmet(kg),ibhmet(kg),-1)
        call GRDAY(io6,ibymet(kg),ibjdmet(kg),ibmmet(kg),ibdmet(kg))
! --- V6.42_x1.0, Level 121203
        ibsmet(kg)=0
! ---   Find end of run (irlgmet is integer hours)
        ieymet(kg)=ibymet(kg)
        iemmet(kg)=ibmmet(kg)
        iedmet(kg)=ibdmet(kg)
        iejdmet(kg)=ibjdmet(kg)
        iehmet(kg)=ibhmet(kg)
        call INCR(io6,ieymet(kg),iejdmet(kg),iehmet(kg),irlgmet)
        call GRDAY(io6,ieymet(kg),iejdmet(kg),iemmet(kg),iedmet(kg))
! --- V6.42_x1.0, Level 121203
        iesmet(kg)=0
      else
! ---   Read older CALMET header records
! --------------------------------------
        itimes=0
        i2dmet=0
!
! ---   record #1 - run title -- 60 words
        read(io)title
!
! ---   record #2 - run control parameters -- 26 words
! ---   (vermet, levmet are both 8 bytes)
        read(io) vermet,levmet,&
         ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg),ibtzm,&
         irlgmet,irtype,nxm(kg),nym(kg),nzm,xgridm(kg),xorigm(kg),&
         yorigm(kg),iutmznm,iwfcod,nssta(kg),nusta(kg),npsta(kg),&
         nowsta(kg),nlu,iwat1,iwat2,lcalgrd
        if(lecho) then
           write(io6,104)vermet,levmet
104        format(1x,'CALMET Version: ',a8,3x,'Level: ',a8)
        endif
! ---   Place integer IBTZM into real XBTZM
        xbtzm=FLOAT(ibtzm)
! ---   Enforce YYYY format
        call YR4(io6,ibymet(kg),ierrb)
        if(ierrb.NE.0) then
           write(*,*)
           stop 'Halted in MET1'
        endif
!
! ---   New record -- #3 - additional run control data -- 8 words
! ---   Null values are assigned in block data
! ---   This record was introduced in CALMET Version 5.0 (980304)
        if(vercmet(kg).EQ.'1.5') then
           read(io)xlat0m,xlon0m,llconfm,conecm,xlat1m,xlat2m,&
                    rlat0m,rlon0m
        endif
!
! ---   Recast map projection information
        if(LLCONFM) then
           pmapm='LCC     '
        else
           pmapm='UTM     '
           UTMHEMM='N   '
           if(xlat0m.LT.0.) UTMHEMM='S   '
        endif
! ---   Set datum to unknown
        datumm='unknown'
! ---   Convert start time from end to beginning of first hour
        call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
        call INCR(io6,ibymet(kg),ibjdmet(kg),ibhmet(kg),-1)
        call GRDAY(io6,ibymet(kg),ibjdmet(kg),ibmmet(kg),ibdmet(kg))
! --- V6.42_x1.0, Level 121203
        ibsmet(kg)=0
! ---   Find end of run (irlgmet is integer hours)
        ieymet(kg)=ibymet(kg)
        iemmet(kg)=ibmmet(kg)
        iedmet(kg)=ibdmet(kg)
        iejdmet(kg)=ibjdmet(kg)
        iehmet(kg)=ibhmet(kg)
        call INCR(io6,ieymet(kg),iejdmet(kg),iehmet(kg),irlgmet)
        call GRDAY(io6,ieymet(kg),iejdmet(kg),iemmet(kg),iedmet(kg))
! --- V6.42_x1.0, Level 121203
        iesmet(kg)=0
      endif
! --- Compute inverse grid cell length
      xgridmi(kg)=1./xgridm(kg)
!
      if(LECHO)then
         write(io6,*)
         write(io6,*)
         write(io6,*)'-- Data begin at:'
         write(io6,*)' IBYR    = ',ibymet(kg)
         write(io6,*)' IBMO    = ',ibmmet(kg)
         write(io6,*)' IBDY    = ',ibdmet(kg)
         write(io6,*)' IBHR    = ',ibhmet(kg)
         write(io6,*)' IBSEC   = ',ibsmet(kg)
         write(io6,*)'-- Data end at:'
         write(io6,*)' IEYR    = ',ieymet(kg)
         write(io6,*)' IEMO    = ',iemmet(kg)
         write(io6,*)' IEDY    = ',iedmet(kg)
         write(io6,*)' IEHR    = ',iehmet(kg)
         write(io6,*)' IESEC   = ',iesmet(kg)
         write(io6,*)
         write(io6,*)' XBTZM   = ',xbtzm
         write(io6,*)' IRLG    = ',irlgmet
         write(io6,*)' IRTYPE  = ',irtype
         write(io6,*)' LCALGRD = ',lcalgrd
         write(io6,*)' PMAP    = ',pmapm
         write(io6,*)' DATUM   = ',datumm
         write(io6,*)' NIMADATE= ',datenm
         write(io6,*)' FEASTM  = ',feastm
         write(io6,*)' FNORTHM = ',fnorthm
         if(PMAPM.EQ.'UTM     ') then
            write(io6,*)' IUTMZN  = ',iutmznm
            write(io6,*)' UTMHEM  = ',utmhemm
         else
            write(io6,*)' XLAT1M  = ',xlat1m
            write(io6,*)' XLAT2M  = ',xlat2m
            write(io6,*)' RLAT0M  = ',rlat0m
            write(io6,*)' RLON0M  = ',rlon0m
         endif
         write(io6,*)' NXM     = ',nxm(kg)
         write(io6,*)' NYM     = ',nym(kg)
         write(io6,*)' NZM     = ',nzm
         write(io6,*)' XGRIDM  = ',xgridm(kg)
         write(io6,*)' XGRIDMI = ',xgridmi(kg)
         write(io6,*)' XORIGM  = ',xorigm(kg)
         write(io6,*)' YORIGM  = ',yorigm(kg)
         write(io6,*)' IWFCOD  = ',iwfcod
         write(io6,*)' NSSTA   = ',nssta(kg)
         write(io6,*)' NUSTA   = ',nusta(kg)
         write(io6,*)' NPSTA   = ',npsta(kg)
         write(io6,*)' NOWSTA  = ',nowsta(kg)
         write(io6,*)' NLU     = ',nlu
         write(io6,*)' IWAT1   = ',iwat1
         write(io6,*)' IWAT2   = ',iwat2
      endif
!
! --- Check that enough workspace has been allocated
      nec=nxm(kg)*nym(kg)
      if(nw1.lt.nec)then
         write(io6,*)'ERROR in subr. MET1 -- insufficient workspace ',&
         'allocated to work arrays -- Allocated (NW1) = ',nw1,&
         '   Required (NEC) = ',nec
         write(*,*)
         stop 'Halted in MET1 -- see list file.'
      endif
!
      nzmp1=nzm+1
      if(mxnzp1.lt.nzmp1)then
         write(io6,*)'ERROR in subr. MET1 -- dimension of ZFACEM ',&
         'array is too small for no. layers in MET file'
         write(io6,*)'MXNZP1 = ',mxnzp1,' NZMP1 = ',nzmp1
         write(*,*)
         stop 'Halted in MET1 -- see list file.'
      endif
! -------------------------------------
!
! --- record #NCOM+4 - cell face heights (NZ + 1 words)
      call rdr1d(io,itimes,zfacem,nzmp1,clab1,idum,idum,idum,idum)
!
      if(LECHO)then
         write(io6,203)(zfacem(n),n=1,nzmp1)
203      format(1x,'ZFACEM  = ',10(f9.3,', ')/)
      endif
!
! --- Check that record label matches expected label
      if(clab1.ne.'ZFACE')then
         clabexp='ZFACE'
         go to 999
      endif
! -------------------------------------
!
! ************************************************
! --- Perform QA checks on header record variables
! ************************************************
      if(kg.EQ.1) then
! ---    Compare control file inputs with outermost met grid
         call metqa
! ---    Transfer selected header variables from /METHD/ into /NESTHD/
! ---    for QA checks on nested files
         call NESTHDX
      else
! ---    Compare selected header variables from /METHD/ with /NESTHD/
         call NESTQAX
      endif
      nums=nssta(kg)+nusta(kg)+npsta(kg)
      if(lecho .AND. nums.GT.0) then
         write(io6,*)'Met Station Locations (Met Grid meters)'
      endif
!
! --- records #NCOM+5 & 6 - x, y coordinates of surface stations
! --- (NSSTA words each record)
      if(nssta(kg).gt.0)then
         call rdr1d(io,itimes,xssta(1,kg),nssta(kg),clab1,&
                    idum,idum,idum,idum)
         call rdr1d(io,itimes,yssta(1,kg),nssta(kg),clab2,&
                    idum,idum,idum,idum)
         if(LECHO)then
            write(io6,*)clab1,' = ',(xssta(n,kg),n=1,nssta(kg))
            write(io6,*)clab2,' = ',(yssta(n,kg),n=1,nssta(kg))
         endif
!
! ---    Check that record labels matches expected labels
         if(clab1.ne.'XSSTA'.or.clab2.ne.'YSSTA')then
            clabex1='XSSTA'
            clabex2='YSSTA'
            go to 1999
         endif
      endif
! -------------------------------------
!
! --- records #NCOM+7 & 8 - x, y coordinates of upper air stations
! --- (NUSTA words each record)
      if(nusta(kg).gt.0)then
         call rdr1d(io,itimes,xusta(1,kg),nusta(kg),clab1,&
                    idum,idum,idum,idum)
         call rdr1d(io,itimes,yusta(1,kg),nusta(kg),clab2,&
                    idum,idum,idum,idum)
         if(LECHO)then
            write(io6,*)clab1,' = ',(xusta(n,kg),n=1,nusta(kg))
            write(io6,*)clab2,' = ',(yusta(n,kg),n=1,nusta(kg))
         endif
!
! ---    Check that record labels matches expected labels
         if(clab1.ne.'XUSTA'.or.clab2.ne.'YUSTA')then
            clabex1='XUSTA'
            clabex2='YUSTA'
            go to 1999
         endif
      endif
! -------------------------------------
!
! --- records #NCOM+9 & 10 - x, y coordinates of precipitation stations
! --- (NPSTA words each record)
      if(npsta(kg).gt.0)then
         call rdr1d(io,itimes,xpsta(1,kg),npsta(kg),clab1,&
                    idum,idum,idum,idum)
         call rdr1d(io,itimes,ypsta(1,kg),npsta(kg),clab2,&
                    idum,idum,idum,idum)
         if(LECHO)then
            write(io6,*)clab1,' = ',(xpsta(n,kg),n=1,npsta(kg))
            write(io6,*)clab2,' = ',(ypsta(n,kg),n=1,npsta(kg))
         endif
!
! ---    Check that record labels matches expected labels
         if(clab1.ne.'XPSTA'.or.clab2.ne.'YPSTA')then
            clabex1='XPSTA'
            clabex2='YPSTA'
            go to 1999
         endif
      endif
! -------------------------------------
!
! --- record #NCOM+11 - surface roughness lengths (NX * NY words)
      call rdr2d(io,itimes,z0(1,1,kg),tmp1,mxnx,mxny,nxm(kg),nym(kg),&
                 clab1,idum,idum,idum,idum,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'MET1: Unexpected EOF in CALMET header'
      endif
      if(LECHO)then
         messag='Surface roughness lengths (m)'
         messag(63:70)=clab1
         call out(z0(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- Check that record label matches expected label
      if(clab1.ne.'Z0')then
         clabexp='Z0'
         go to 999
      endif
! -------------------------------------
!
! --- record #NCOM+12 - land use categories (NX * NY words)
      call rdi2d(io,itimes,ilandu(1,1,kg),tmp1,mxnx,mxny,nxm(kg),&
                 nym(kg),clab1,idum,idum,idum,idum)
      if(LECHO)then
         messag='Land use categories'
         messag(63:70)=clab1
         call out(xdum,ilandu(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- Check that record label matches expected label
      if(clab1.ne.'ILANDU')then
         clabexp='ILANDU'
         go to 999
      endif
! -------------------------------------
!
! --- record #NCOM+13 - elevations (NX * NY words)
      call rdr2d(io,itimes,elev(1,1,kg),tmp1,mxnx,mxny,nxm(kg),nym(kg),&
                 clab1,idum,idum,idum,idum,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'MET1: Unexpected EOF in CALMET header'
      endif
      if(LECHO)then
         messag='Terrain heights (m)'
         messag(63:70)=clab1
         call out(elev(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- Check that record label matches expected label
      if(clab1.ne.'ELEV')then
         clabexp='ELEV'
         go to 999
      endif
! -------------------------------------
!
! --- record #NCOM+14 - leaf area index (NX * NY words)
      call rdr2d(io,itimes,xlai(1,1,kg),tmp1,mxnx,mxny,nxm(kg),nym(kg),&
                 clab1,idum,idum,idum,idum,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'MET1: Unexpected EOF in CALMET header'
      endif
      if(LECHO)then
         messag='Leaf area index'
         messag(63:70)=clab1
         call out(xlai(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- Check that record label matches expected label
      if(clab1.ne.'XLAI')then
         clabexp='XLAI'
         go to 999
      endif
! -------------------------------------
!
! --- record #NCOM+15 - nearest surface station to each grid point
!                  (NX * NY words)
! frr (09/01)
      if (nssta(kg).ge.1) then
         call rdi2d(io,itimes,nears(1,1,kg),tmp1,mxnx,mxny,nxm(kg),&
                    nym(kg),clab1,idum,idum,idum,idum)
         if(LECHO)then
           messag='Nearest surface station no. to each grid point'
           messag(63:70)=clab1
           call out(xdum,nears(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
         endif
!
! ---    Check that record label matches expected label
         if(clab1.ne.'NEARS')then
            clabexp='NEARS'
            go to 999
         endif
      endif
! -------------------------------------
!
      return
!
! --- Write error messages -- incorrect record label read
999   continue
      write(io6,1001)clab1,clabexp
1001  format(/1x,'ERROR in subr. MET1 -- incorrect record label ',&
       'read from MET data file'//1x,'Label read     = ',a8/&
                                     1x,'Label expected = ',a8)
      write(*,*)
      stop 'Halted in MET1 -- see list file.'
1999  continue
      write(io6,2001)clab1,clab2,clabex1,clabex2
2001  format(/1x,'ERROR in subr. MET1 -- incorrect record label ',&
       'read from MET data file'//&
       1x,'Labels read     = ',a8,2x,a8/&
       1x,'Labels expected = ',a8,2x,a8)
      write(*,*)
      stop 'Halted in MET1 -- see list file.'
!
      end
!----------------------------------------------------------------------
      subroutine metqa
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212             METQA
!                J. Scire, SRC
!
! --- PURPOSE:  Perform consistency checks of data read from the
!               CALMET header records with control file inputs
!
! --- UPDATE
! --- V6.112-V6.3   100212  (DGS): add nested CALMET grids
! --- V6.1-V6.112   060412  (DGS): remove check on time that improperly
!                                  stopped run with multiple CALMET
!                                  files
! --- V5.71-V6.1    050915  (DGS): add time begin/end checks
!                                  resolved to seconds
! --- V5.7-V5.71    030528  (DGS): remove old UTM check that does
!                                  not screen out PMAP.NE.UTM cases
! --- V5.4-V5.7     030402  (DGS): add map projection and datum
! --- V5.4-V5.4     000602_4(DGS): add base time zone check
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!
! --- INPUTS:
!     Common block /DATEHR/ variables:
!           XBTZ
!     Common block /GEN/ variables:
!           IBDATHR, IBSEC, IEDATHR, IESEC, METFM, METRUN
!     Common block /GRID/ variables:
!           NX, NY, NZ, DGRID, XORIG, YORIG, ZFACE(mxnzp1)
!     Common block /MAP/ variables:
!           lutm,llcc,lps,lem,llaza,lttm,
!           iutmzn,feast,fnorth,
!           rlat0,rlon0,xlat1,xlat2,
!           pmap,utmhem,datum,daten
!     Common block /METHD/ variables:
!           NXM, NYM, NZM, XGRIDM, XORIGM, YORIGM, XBTZM,
!           NSSTA, NUSTA, NPSTA, ZFACEM(mxnzmp1)
!           iutmznm,feastm,fnorthm,
!           rlat0m,rlon0m,xlat1m,xlat2m,
!           pmapm,utmhemm,datumm,
!           dversm
!     Parameters:
!           MXMETDOM, MXNZP1, MXNZMP1, MXSS, MXUS, MXPS, IO6
!
! --- OUTPUT:
!         none
!
! --- METQA called by:  MET1, MET2, MET3, MET4
! --- METQA calls:      LRSAME, TPERIOD
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'datehr.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'map.puf'
      include 'methd.puf'
      logical lrsame
! --- Initialize error flags
      ierr=0
      iwarn=0
! --- Compare outermost met grid domain with control file grid
      kg=1
! --- Check for missing base time zone (met file) and replace with
! --- CALPUFF base time zone
      if(xbtzm.LT.-900.) then
         xbtzm=xbtz
         iwarn=1
         write(io6,*)
         write(io6,*)'WARNING  subr. METQA -- Unknown Time Zone'
         write(io6,*)'  Met file does not indicate time zone of data'
         write(io6,*)'  CALPUFF assumes met data are Time Zone ',xbtz
         write(io6,*)
      endif
! --- No. X, Y grid cells
      if(nx.ne.nxm(kg))then
         write(io6,102)
102      format(/1x,'ERROR in subr. METQA -- MET header record ',&
         'data is inconsistent with the control file input')
         write(io6,*)'NX = ',nx,' NXM = ',nxm(kg)
         ierr=1
      endif
      if(ny.ne.nym(kg))then
         write(io6,102)
         write(io6,*)'NY = ',ny,' NYM = ',nym(kg)
         ierr=1
      endif
      if(nz.ne.nzm)then
         write(io6,102)
         write(io6,*)'NZ = ',nz,' NZM = ',nzm
         ierr=1
      endif
!
! --- Vertical face heights
      nzp1=nz+1
      do 35 i=1,nzp1
      if(abs(zface(i)-zfacem(i)).gt.0.1)then
         write(io6,102)
         write(io6,*)'I         = ',i
         write(io6,*)'ZFACE(i)  = ',zface(i)
         write(io6,*)'ZFACEM(i) = ',zfacem(i)
         ierr=1
      endif
35    continue
!
! --- Grid size
      if(abs(xgridm(kg)-dgrid).gt.0.1)then
         write(io6,102)
         write(io6,*)'DGRID = ',dgrid,' XGRIDM = ',xgridm(kg)
         ierr=1
      endif
!
! --- Grid reference coordinates
      if(abs(xorigm(kg)-xorig).gt.1.0)then
         write(io6,102)
         write(io6,*)'XORIG = ',xorig,' XORIGM = ',xorigm(kg)
         ierr=1
      endif
      if(abs(yorigm(kg)-yorig).gt.1.0)then
         write(io6,102)
         write(io6,*)'YORIG = ',yorig,' YORIGM = ',yorigm(kg)
         ierr=1
      endif
!
! --- MXSS in CALPUFF parameter file must be at least as large as
!     the number of surface stations used in the CALMET run
      if(nssta(kg).gt.mxss)then
         write(io6,106)nssta(kg),mxss
106      format(/1x,'ERROR in subr. METQA -- NSSTA read from ',&
         'MET header record is greater than MXSS in the CALPUFF ',&
         'parameter file'/1x,'NSSTA = ',i6,3x,'MXSS = ',i6)
         ierr=1
      endif
      if(nusta(kg).gt.mxus)then
         write(io6,116)nusta(kg),mxus
116      format(/1x,'ERROR in subr. METQA -- NUSTA read from ',&
         'MET header record is greater than MXUS in the CALPUFF ',&
         'parameter file'/1x,'NUSTA = ',i6,3x,'MXUS = ',i6)
         ierr=1
      endif
      if(npsta(kg).gt.mxps)then
         write(io6,126)npsta(kg),mxps
126      format(/1x,'ERROR in subr. METQA -- NPSTA read from ',&
         'MET header record is greater than MXPS in the CALPUFF ',&
         'parameter file'/1x,'NPSTA = ',i6,3x,'MXPS = ',i6)
         ierr=1
      endif
!
! --- Base time zone
      if(xbtzm.ne.xbtz)then
         write(io6,102)
         write(io6,*)'XBTZ(control file) = ',xbtz
         write(io6,*)'IBTZM(Met file)    = ',xbtzm
         ierr=1
      endif
! --- Projection checks
      if(pmapm.NE.pmap) then
         write(io6,102)
         write(io6,*)'         Map projection PMAP does not match'
         write(io6,*)'         Met file    : ',pmapm
         write(io6,*)'         Control file: ',pmap
         ierr=1
      endif
      if(LUTM) then
         if(iutmznm.NE.iutmzn)then
            write(io6,102)
            write(io6,*)'         UTM zone does not match'
            write(io6,*)'         Met file    : ',iutmznm
            write(io6,*)'         Control file: ',iutmzn
            ierr=1
         endif
         if(utmhemm.NE.utmhem)then
            write(io6,102)
            write(io6,*)'         UTM Hemisphere does not match'
            write(io6,*)'         Met file    : ',utmhemm
            write(io6,*)'         Control file: ',utmhem
            ierr=1
         endif
      else
! ---    Check false origin variables
         if(.not.LRSAME(0.0001,feastm,feast))then
            write(io6,102)
            write(io6,*)'         FEAST does not match'
            write(io6,*)'         Met file    : ',feastm
            write(io6,*)'         Control file: ',feast
            ierr=1
         endif
         if(.not.LRSAME(0.0001,fnorthm,fnorth))then
            write(io6,102)
            write(io6,*)'         FNORTH does not match'
            write(io6,*)'         Met file    : ',fnorthm
            write(io6,*)'         Control file: ',fnorth
            ierr=1
         endif
! ---    Check lat/lon variables
         if(.not.LRSAME(0.0001,rlat0m,rlat0))then
            write(io6,102)
            write(io6,*)'         RLAT0 does not match'
            write(io6,*)'         Met file    : ',rlat0m
            write(io6,*)'         Control file: ',rlat0
            ierr=1
         endif
         if(.not.LRSAME(0.0001,rlon0m,rlon0))then
            write(io6,102)
            write(io6,*)'         RLON0 does not match'
            write(io6,*)'         Met file    : ',rlon0m
            write(io6,*)'         Control file: ',rlon0
            ierr=1
         endif
         if(LLCC) then
            if(.not.LRSAME(0.0001,xlat1m,xlat1))then
               write(io6,102)
               write(io6,*)'         XLAT1 does not match'
               write(io6,*)'         Met file    : ',xlat1m
               write(io6,*)'         Control file: ',xlat1
               ierr=1
            endif
            if(.not.LRSAME(0.0001,xlat2m,xlat2))then
               write(io6,102)
               write(io6,*)'         XLAT2 does not match'
               write(io6,*)'         Met file    : ',xlat2m
               write(io6,*)'         Control file: ',xlat2
               ierr=1
            endif
         endif
      endif
! --- DATUM
      if(dversm.LT.1.) then
! ---    Assign CALPUFF datum to met datumm
         datumm=datum
         iwarn=1
         write(io6,*)
         write(io6,*)'WARNING  subr. METQA -- Unknown DATUM'
         write(io6,*)'  Met file does not indicate DATUM of data'
         write(io6,*)'  CALPUFF assumes met DATUM is ',datum
         write(io6,*)
      elseif(datumm.NE.datum)then
         write(io6,102)
         write(io6,*)'         DATUM does not match'
         write(io6,*)'         Met file    : ',datumm
         write(io6,*)'         Control file: ',datum
         ierr=1
      endif
! --- 060412:  remove this check as it is not configured for multiple
! ---          met files, and check made on data records should catch
! ---          problems
!c --- Met data must not start after beginning of simulation
!      if(metrun.EQ.0) then
!         kdathr=ibymet*100000+ibjdmet*100+ibhmet
!         call TPERIOD(1,kdathr,ibsmet,ibdathr,ibsec,iedathr,iesec,iloc)
!         if(iloc.NE.-1) then
!            ierr=1
!            write(io6,*)
!            write(io6,*)'ERROR in subr. METQA -- Met data begin late'
!            write(io6,*)'     Start of simulation: ',ibdathr,ibsec
!            write(io6,*)'     Start of met data  : ',kdathr,ibsmet
!         endif
!      endif
!
!c --- Check end of met data when ITIMES.NE.0, or when using CALMET data
!      if(metrun.EQ.0 .AND. (itimes.NE.0 .OR. metfm.EQ.1)) then
!         kdathr=ieymet*100000+iejdmet*100+iehmet
!         call TPERIOD(-1,kdathr,iesmet,ibdathr,ibsec,iedathr,iesec,iloc)
!         if(iloc.NE.1) then
!            write(io6,*)
!            write(io6,*)'WARNING  subr. METQA -- Met data end early'
!            write(io6,*)'     End of simulation: ',iedathr,iesec
!            write(io6,*)'     End of met data  : ',kdathr,iesmet
!            write(io6,*)
!         endif
!      endif
! --- Warning notice
      if(iwarn.eq.1) then
         write(*,*)
         write(*,*)'METQA WARNINGS are generated!'
         write(*,*)'Review messages written to the list file'
         write(*,*)
      endif
! --- Stop execution if any errors encountered
      if(ierr.eq.1) then
         write(*,*)
         stop 'Halted in METQA -- see list file.'
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine rdmet(idathrm,isecm,wrk1,nwork,lecho,umet,vmet,tmet,&
       ipgt,htmix,ustar,xmonin,wstar,rmm,tempss,rhoss,qswss,irhss,&
       ipcode,temp2d,rho2d,qsw2d,irh2d,ipcode2d,kdathre,ksece,kdathrb,&
       ksecb,wdiv)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100827             RDMET
!                J. Scire
!
! --- PURPOSE:  Read CALMET meteorological data for one hour
!
! --- UPDATE
! --- V6.3-V6.301   100827  (DGS): Allow nested CALMET files to have
!                                  different time-periods
! --- V6.112-V6.3   100212  (DGS): add nested CALMET grids
! --- V6.1-V6.112   060412  (DGS): add message to "stop" when checking
!                                  for expected time period
! --- V5.7-V6.1     050915  (DGS): allow for begin/end time format
!                                  resolved to seconds, and YYYY
!                                  format used for year (retain
!                                  compatibility with older CALMET)
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4 call
!                   030119  (FRR): bug fix to make sure ipcode2d data
!                                  is read in when npsta=-1
!
! ---               090101  (FRR): read CALMET records V5.3, level 010901
! frr (09/01)
! ---  Different format after CALMET version V5.3 - Level 010901
! ---  NOOBS mode allowed (no upper and possibly no surface stations)
! ---  2D records of sf Temp, RH, IPCODE, RHO, QS (previously 1-D)
! ---  Precipitation data if NPSTA>0 or NPSTA=-1 (prognostic rain data)
! ---
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.2     991104  (DGS): YYYY format used for year
! --- V5.0-V5.0     990228a (DGS): add IEOF to RDR2D arguments and add
!                                  NEXTFIL to open next CALMET file
! --- V5.0-V5.0     990228  (DGS): correct dimension in call to RDR2D
!                                  for w-field
! --- V4.0-V5.0     971107  (DGS): store w for vertical divergence field
!                   971107  (DGS): add record-skip logic from COMP to
!                                  find current date-time
!
! --- INPUTS:
!       IDATHRM - integer       - Mid-pt date/hour of required
!                                 data (YYYYJJJHH)
!         ISECM - integer       - Mid-pt second of required data (SSSS)
!   WRK1(nwork) - real array    - Work array of length "NWORK" words
!         NWORK - integer       - Dimension of work array -- NOTE:
!                                 NWORK must be at least as large as
!                                 the max. of (NXM * NYM or NSSTA)
!         LECHO - logical       - Flag controlling printing of input
!                                 meteorological data fields
!    Common block /METHD/ variables:
!         NXM, NYM, NZM, NSSTA, NPSTA, LCALGRD, I2DMET, ITIMES
!    Common block /GRIDNEST/ variables:
!         ngrid
!
!    Parameters:
!         MXNX, MXNY, MXMETDOM, MXSS, MXNZMP1, IO6, IO7
!
! --- OUTPUT:
!
!     UMET(mxnx,mxny,mxnz,mxmetdom) - real    - U component of the wind (m/s)
!                                    at each grid point
!     VMET(mxnx,mxny,mxnz,mxmetdom) - real    - V-component of the wind (m/s)
!     TMET(mxnx,mxny,mxnz,mxmetdom) - real    - 3-D temperature field (deg. K)
!          IPGT(mxnx,mxny,mxmetdom) - integer - PGT stability class
!         HTMIX(mxnx,mxny,mxmetdom) - real    - Mixing height (m)
!         USTAR(mxnx,mxny,mxmetdom) - real    - Surface friction velocity (m/s)
!        XMONIN(mxnx,mxny,mxmetdom) - real    - Monin-Obukhov length (m)
!         WSTAR(mxnx,mxny,mxmetdom) - real    - Convective velocity scale (m/s)
!           RMM(mxnx,mxny,mxmetdom) - real    - Precipitation rate (mm/hr)
!          TEMPSS(mxss,mxmetdom) - real    - Air temperature (deg. K) at
!                                    each surface met. station
!           RHOSS(mxss,mxmetdom) - real    - Air density (kg/m**3)
!           QSWSS(mxss,mxmetdom) - real    - Short wave solar radiation
!                                    (W/m**2)
!           IRHSS(mxss,mxmetdom) - integer - Relative humidity (percent)
!          IPCODE(mxss,mxmetdom) - integer - Precipitation code at each surface
!                                    station
!        TEMP2D(mxnx,mxny,mxmetdom) - real    - Air temperature (deg. K)
!         RHO2D(mxnx,mxny,mxmetdom) - real    - Air density (kg/m**3)
!         QSW2D(mxnx,mxny,mxmetdom) - real    - Short wave solar radiation
!                                    (W/m**2)
!         IRH2D(mxnx,mxny,mxmetdom) - integer - Relative humidity (percent)
!      IPCODE2D(mxnx,mxny,mxmetdom) - integer - Precipitation code 
!                KDATHR - integer - Date of hour of data (YYYYJJJHH)
!                KDATHR - integer - Date of hour of data (YYYYJJJHH)
!               KDATHRE - integer - End date/hour of data (YYYYJJJHH)
!                 KSECE - integer - End second of data (SSSS)
!               KDATHRB - integer - Begin date/hour of data (YYYYJJJHH)
!                 KSECB - integer - Begin second of data (SSSS)
!     WDIV(mxnx,mxny,mxnz,mxmetdom) - real    - Vertical divergence dw/dz; this
!                                    array holds the w-data here
!
! --- RDMET called by:  COMP
! --- RDMET calls:      RDR1D, RDR2D, RDI1D, RDI2D, INCR, OUT,
!                       NEXTFIL, TPERIOD, YR4
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
      include 'gridnest.puf'
      include 'methd.puf'
!
      real umet(mxnx,mxny,mxnz,mxmetdom),vmet(mxnx,mxny,mxnz,mxmetdom)
      real tmet(mxnx,mxny,mxnz,mxmetdom)
      real htmix(mxnx,mxny,mxmetdom),ustar(mxnx,mxny,mxmetdom)
      real xmonin(mxnx,mxny,mxmetdom)
      real wstar(mxnx,mxny,mxmetdom),rmm(mxnx,mxny,mxmetdom)
      real wrk1(nwork)
! frr (09/01)in noobs mode, nssta can be = 0 => dimension with mxss
!     real tempss(nssta),rhoss(nssta),qswss(nssta)
      real tempss(mxss,mxmetdom),rhoss(mxss,mxmetdom)
      real qswss(mxss,mxmetdom)
      real wdiv(mxnx,mxny,mxnz,mxmetdom)
!
! frr (09/01) - additional 2D arrays for temp,rh,rho,ipcode,qsw
      real temp2d(mxnx,mxny,mxmetdom),rho2d(mxnx,mxny,mxmetdom)
      real qsw2d(mxnx,mxny,mxmetdom)
      integer irh2d(mxnx,mxny,mxmetdom),ipcode2d(mxnx,mxny,mxmetdom)
! --- Declare dummy arrays as scalars since they are only used as
! --- placeholders in OUT argument list for input arrays that are 
! --- not used (this may provoke a compiler error)
      real    rdum
      integer idum
! frr (09/01)in noobs mode, nssta can be = 0 => dimension with mxss
!     integer ipgt(mxnx,mxny,mxmetdom),irhss(nssta),ipcode(nssta)
      integer ipgt(mxnx,mxny,mxmetdom),irhss(mxss,mxmetdom)
      integer ipcode(mxss,mxmetdom)
!
      character*70 messag
      character*8 clabel
      character*8 clabexp
!
      logical lecho,ldate
!
      data ldate/.false./
! --- Process each CALMET grid in turn to fill met arrays
! -------------------------------------------------------
      do kg=1,ngrid
! --- CALMET.DAT file unit for current grid domain
      io=io7+(kg-1)
! --- Check that enough workspace has been allocated
      nec=max0(nxm(kg)*nym(kg),nssta(kg))
      if(nwork.lt.nec)then
         write(io6,*)'ERROR in subr. RDMET -- insufficient workspace ',&
         'allocated to work arrays -- Allocated (NWORK) = ',nwork,&
         '   Required (NEC) = ',nec
         write(*,*)
         stop 'Halted in RDMET -- see list file.'
      endif
! --- Begin reading data records for one time period
1     continue
!
! --- Track date-hr of end of this period to check for problems in file
      kdathre=0
      ksece=0
!
! --- read the U, V wind components
      do 10 iz=1,nzm
      call rdr2d(io,itimes,umet(1,1,iz,kg),wrk1,mxnx,mxny,&
                 nxm(kg),nym(kg),clabel,&
                 ndathrb1,nsecb1,ndathre1,nsece1,ieof)
      if(ieof.EQ.1) then
! ---    Reached end of CALMET.DAT file; check for another file
         call NEXTFIL(kg,ifound)
         if(ifound.EQ.1) then
            goto 1
         else
            write(*,*)
            write(*,*)'RDMET: Unexpected EOF in CALMET records'
            write(*,*)'       for CALMET Grid Domain ',kg
            write(*,*)'Target  Date-time: ',idathrm,isecm
            write(*,*)'Reading Date-time: ',ndathrb1,nsecb1
            write(*,*)'               to: ',ndathre1,nsece1
            stop 
         endif
      endif
!
! --- Check that record label matches expected label
      clabexp='U-LEV'
      write(clabexp(6:8),'(i3)')iz
      if(clabel.ne.clabexp)go to 999
!
      call rdr2d(io,itimes,vmet(1,1,iz,kg),wrk1,mxnx,mxny,&
                 nxm(kg),nym(kg),clabel,&
                 ndathrb2,nsecb2,ndathre2,nsece2,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'RDMET: Unexpected EOF in CALMET records'
      endif
      clabexp(1:1)='V'
      if(clabel.ne.clabexp)go to 999
!
! --- Use the W wind component (conditional) for w-divergence
      if(lcalgrd)then
         call rdr2d(io,itimes,wdiv(1,1,iz,kg),wrk1,mxnx,mxny,&
                    nxm(kg),nym(kg),clabel,&
                    ndathrb3,nsecb3,ndathre3,nsece3,ieof)
         if(ieof.EQ.1) then
            write(*,*)
            stop 'RDMET: Unexpected EOF in CALMET records'
         endif
         clabexp(1:5)='WFACE'
         if(clabel.ne.clabexp)go to 999
         if(ndathre2.ne.ndathre3 .OR. nsece2.ne.nsece3)go to 2999
      endif
!
! --- Check for consistency of date/time at end of period
      if(ndathre1.ne.ndathre2 .OR. nsece1.ne.nsece2)go to 2999
      if(kdathre.eq.0)then
! ---    first vertical level
         kdathre=ndathre1
         ksece=nsece1
      else if(ndathre1.ne.kdathre .OR. nsece1.ne.ksece)then
! ---    date/time does not match value for previous layer
         ndathre=ndathre1
         nsece=nsece1
         go to 3999
      endif
10    continue
!
! --- Read the 3-D temperature field
      if(lcalgrd)then
         clabexp='T-LEV'
         do 12 iz=1,nzm
         call rdr2d(io,itimes,tmet(1,1,iz,kg),wrk1,mxnx,mxny,&
                    nxm(kg),nym(kg),clabel,&
                    ndathrb,nsecb,ndathre,nsece,ieof)
         if(ieof.EQ.1) then
            write(*,*)
            stop 'RDMET: Unexpected EOF in CALMET records'
         endif
!
         write(clabexp(6:8),'(i3)')iz
         if(clabel.ne.clabexp)go to 999
         if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
12       continue
      endif
!
! --- read other 2-D meteorological fields
!
! --- PGT stability class
      call rdi2d(io,itimes,ipgt(1,1,kg),wrk1,mxnx,mxny,&
                    nxm(kg),nym(kg),clabel,&
                    ndathrb,nsecb,ndathre,nsece)
      clabexp='IPGT'
      if(clabel.ne.clabexp)go to 999
      if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
! --- FRICTION VELOCITY
      call rdr2d(io,itimes,ustar(1,1,kg),wrk1,mxnx,mxny,&
                 nxm(kg),nym(kg),clabel,&
                 ndathrb,nsecb,ndathre,nsece,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'RDMET: Unexpected EOF in CALMET records'
      endif
      clabexp='USTAR'
      if(clabel.ne.clabexp)go to 999
      if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
! --- MIXING HEIGHT
      call rdr2d(io,itimes,htmix(1,1,kg),wrk1,mxnx,mxny,&
                 nxm(kg),nym(kg),clabel,&
                 ndathrb,nsecb,ndathre,nsece,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'RDMET: Unexpected EOF in CALMET records'
      endif
      clabexp='ZI'
      if(clabel.ne.clabexp)go to 999
      if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
! --- MONIN-OBUKHOV LENGTH
      call rdr2d(io,itimes,xmonin(1,1,kg),wrk1,mxnx,mxny,&
                 nxm(kg),nym(kg),clabel,&
                 ndathrb,nsecb,ndathre,nsece,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'RDMET: Unexpected EOF in CALMET records'
      endif
      clabexp='EL'
      if(clabel.ne.clabexp)go to 999
      if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
! --- CONVECTIVE VELOCITY SCALE
      call rdr2d(io,itimes,wstar(1,1,kg),wrk1,mxnx,mxny,&
                 nxm(kg),nym(kg),clabel,&
                 ndathrb,nsecb,ndathre,nsece,ieof)
      if(ieof.EQ.1) then
         write(*,*)
         stop 'RDMET: Unexpected EOF in CALMET records'
      endif
      clabexp='WSTAR'
      if(clabel.ne.clabexp)go to 999
      if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
! --- PRECIPITATION DATA
! frr (09/01) - Additional option: if NPSTA=-1, prognostic gridded rain data
!      if(npsta.gt.0)then
      if(npsta(kg).ne.0)then
         call rdr2d(io,itimes,rmm(1,1,kg),wrk1,mxnx,mxny,&
                    nxm(kg),nym(kg),clabel,&
                    ndathrb,nsecb,ndathre,nsece,ieof)
         if(ieof.EQ.1) then
            write(*,*)
            stop 'RDMET: Unexpected EOF in CALMET records'
         endif
         clabexp='RMM'
         if(clabel.ne.clabexp)go to 999
         if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
      endif
!
! frr (09/01)
! --- NOOBS CALMET: full 2-D fields of the following variables instead
! --- of 1-D fields (values at the surface stations only)
!           - Air temperature (deg. K),
!           - Air density (kg/m**3),
!           - Short-wave solar radiation (W/m**2),
!           - Relative humidity (percent),
!           - Precipitation code
      if(i2dmet.EQ.1) then
! ---   New CALMET output format - 2D arrays -
!       call rdr1d(io,tempss,nssta,clabel,ndathr)
        call rdr2d(io,itimes,temp2d(1,1,kg),wrk1,mxnx,mxny,&
                   nxm(kg),nym(kg),clabel,&
                   ndathrb,nsecb,ndathre,nsece,ieof)
        if(ieof.EQ.1) then
           write(*,*)
           stop 'RDMET: Unexpected EOF in CALMET records'
        endif
        clabexp='TEMPK'
        if(clabel.ne.clabexp)go to 999
        if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
!       call rdr1d(io,rhoss,nssta,clabel,ndathr)
        call rdr2d(io,itimes,rho2d(1,1,kg),wrk1,mxnx,mxny,&
                   nxm(kg),nym(kg),clabel,&
                   ndathrb,nsecb,ndathre,nsece,ieof)
        if(ieof.EQ.1) then
           write(*,*)
           stop 'RDMET: Unexpected EOF in CALMET records'
        endif
        clabexp='RHO'
        if(clabel.ne.clabexp)go to 999
        if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
!       call rdr1d(io,qswss,nssta,clabel,ndathr)
        call rdr2d(io,itimes,qsw2d(1,1,kg),wrk1,mxnx,mxny,&
                   nxm(kg),nym(kg),clabel,&
                   ndathrb,nsecb,ndathre,nsece,ieof)
        if(ieof.EQ.1) then
           write(*,*)
           stop 'RDMET: Unexpected EOF in CALMET records'
        endif
        clabexp='QSW'
        if(clabel.ne.clabexp)go to 999
        if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
!       call rdi1d(io,irhss,nssta,clabel,ndathr)
        call rdi2d(io,itimes,irh2d(1,1,kg),wrk1,mxnx,mxny,&
                   nxm(kg),nym(kg),clabel,&
                   ndathrb,nsecb,ndathre,nsece)
        clabexp='IRH'
        if(clabel.ne.clabexp)go to 999
        if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
! ---   Precipitation code at surface stations
!        if(npsta.gt.0)then
! frr 030119 - bug fix: also precipitation code if prognostic rain
!       (i.e npsta,eq.-1)
        if(npsta(kg).ne.0)then
!         call rdi1d(io,ipcode,nssta,clabel,ndathr)
          call rdi2d(io,itimes,ipcode2d(1,1,kg),wrk1,mxnx,mxny,&
                     nxm(kg),nym(kg),clabel,&
                     ndathrb,nsecb,ndathre,nsece)
          clabexp='IPCODE'
          if(clabel.ne.clabexp)go to 999
          if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
        endif
      elseif(i2dmet.EQ.0) then
! ---    read 1-D meteorological fields:
!           Air temp. (deg. K),
!           Air density (kg/m**3),
!           Short-wave solar radiation (W/m**2),
!           Relative humidity (percent),
!           Precipitation code
         call rdr1d(io,itimes,tempss(1,kg),nssta(kg),clabel,&
                    ndathrb,nsecb,ndathre,nsece)
         clabexp='TEMPK'
         if(clabel.ne.clabexp)go to 999
         if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
         call rdr1d(io,itimes,rhoss(1,kg),nssta(kg),clabel,&
                    ndathrb,nsecb,ndathre,nsece)
         clabexp='RHO'
         if(clabel.ne.clabexp)go to 999
         if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
         call rdr1d(io,itimes,qswss(1,kg),nssta(kg),clabel,&
                    ndathrb,nsecb,ndathre,nsece)
         clabexp='QSW'
         if(clabel.ne.clabexp)go to 999
         if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
         call rdi1d(io,itimes,irhss(1,kg),nssta(kg),clabel,&
                    ndathrb,nsecb,ndathre,nsece)
         clabexp='IRH'
         if(clabel.ne.clabexp)go to 999
         if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
!
! ---    Precipitation code at surface stations
         if(npsta(kg).gt.0)then
            call rdi1d(io,itimes,ipcode(1,kg),nssta(kg),clabel,&
                       ndathrb,nsecb,ndathre,nsece)
            clabexp='IPCODE'
            if(clabel.ne.clabexp)go to 999
            if(ndathre.ne.kdathre .OR. nsece.ne.ksece)go to 3999
         endif
      else
         write(*,*)'Subr. RDMET:  Invalid I2DMET = ',i2dmet
         stop
      endif
!
! --- Set date-time for period
! ---------------------------------
      kdathre=ndathre
      ksece=nsece
      if(itimes.EQ.0) then
! ---    Construct beginning time for older CALMET data
         iyy=kdathre/100000
         iyyddd=kdathre/100
         iddd=iyyddd-iyy*1000
         ihh=kdathre-iyyddd*100
         call INCR(io6,iyy,iddd,ihh,-1)
         kdathrb=iyy*100000+iddd*100+ihh
         ksecb=0
      else
         kdathrb=ndathrb
         ksecb=nsecb
      endif
! --- Enforce YYYY format for year
      ibyr=kdathrb/100000
      ibdyhr=kdathrb-ibyr*100000
      ieyr=kdathre/100000
      iedyhr=kdathre-ieyr*100000
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDMET'
      kdathrb=ibyr*100000+ibdyhr
      kdathre=ieyr*100000+iedyhr
! --- Check to see if required date-time has been read
      call TPERIOD(0,idathrm,isecm,kdathrb,ksecb,kdathre,&
                   ksece,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         go to 1
      elseif(ilocate.EQ.-1) then
         write(io6,*)'ERROR in subr. RDMET -- current time not found',&
         ' in the MET data file'
         write(io6,*)' -- Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksecb
         write(io6,*)'                           to ',kdathre,ksece
         stop 'ERROR in subr. RDMET -- current time not found'
      endif
! --- Next CALMET grid
      enddo
! ----------------------------------------------------
!
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ MET. FIELDS BEGINNING: ',kdathrb,ksecb
         write(io6,*)'------            AND ENDING: ',kdathre,ksece
! ---    Provide each CALMET grid in turn
         do kg=1,ngrid
         write(io6,*)
!
! ---    3-D fields
         do 101 iz=1,nzm
         messag='U-component (m/s) -- Level:     -- Grid: '
         write(messag(42:43),'(i2)')kg
         write(messag(29:31),'(i3)')iz
         call out(umet(1,1,iz,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag(1:1)='V'
         call out(vmet(1,1,iz,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag(1:1)='W'
         call out(wdiv(1,1,iz,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
101      continue
!
! ---    2-D fields
         messag='PGT stability class             -- Grid: '
         write(messag(42:43),'(i2)')kg
         call out(rdum,ipgt(1,1,kg),2,1,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Friction velocity (m/s)         -- Grid: '
         write(messag(42:43),'(i2)')kg
         call out(ustar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Mixing height (m)               -- Grid: '
         write(messag(42:43),'(i2)')kg
         call out(htmix(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Monin-Obukhov length (m)        -- Grid: '
         write(messag(42:43),'(i2)')kg
         call out(xmonin(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Convective velocity scale (m/s) -- Grid: '
         write(messag(42:43),'(i2)')kg
         call out(wstar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         if(npsta(kg).gt.0)then
            messag='Precipitation rate (mm/hr)      -- Grid: '
            write(messag(42:43),'(i2)')kg
            call out(rmm(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
         endif
!
! ---    1-D fields
! frr (09/01) after level 010901, 2D CALMET fields-  Before, 1D
         if(i2dmet.EQ.1) then
            messag='Surface Air Temperature (deg. K)-- Grid: '
            write(messag(42:43),'(i2)')kg
            call out(temp2d(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
            messag='Surface Air Density (Kg/m**3)   -- Grid: '
            write(messag(42:43),'(i2)')kg
            call out(rho2d(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
            messag='Short Wave Radiation (W/m**2)   -- Grid: '
            write(messag(42:43),'(i2)')kg
            call out(qsw2d(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
            messag='Surface Relative Humidity (%)   -- Grid: '
            write(messag(42:43),'(i2)')kg
            call out(rdum,irh2d(1,1,kg),2,5,ldate,messag,&
                     nxm(kg),nym(kg))
            if(npsta(kg).ne.0)then
               messag='Precipitation code              -- Grid: '
               write(messag(42:43),'(i2)')kg
               call out(rdum,ipcode2d(1,1,kg),2,5,ldate,messag,&
                        nxm(kg),nym(kg))
            endif
         elseif(i2dmet.EQ.0) then
            write(io6,*)'TEMPSS (deg. K) = ',&
                        (tempss(n,kg),n=1,nssta(kg))
            write(io6,*)'RHOSS (kg/m**3)  = ',&
                        (rhoss(n,kg),n=1,nssta(kg))
            write(io6,*)'QSWSS (W/m**2)   = ',&
                        (qswss(n,kg),n=1,nssta(kg))
            write(io6,*)'IRHSS (percent)  = ',&
                        (irhss(n,kg),n=1,nssta(kg))
            if(npsta(kg).gt.0)write(io6,*)'IPCODE           = ',&
                                      (ipcode(n,kg),n=1,nssta(kg))
         else
            write(*,*)'Subr. RDMET:  Invalid I2DMET = ',i2dmet
            stop
         endif
! ---    Provide each CALMET grid in turn (end)
         enddo
      endif
!
      return
!
! --- Write error messages -- incorrect record label read
999   continue
      write(io6,1001)clabel,clabexp
1001  format(/1x,'ERROR in subr. RDMET -- incorrect record label ',&
       'read from MET data file'//1x,'Label read     = ',a8/&
                                     1x,'Label expected = ',a8)
      write(*,*)
      stop 'Halted in RDMET -- see list file.'
! --- date/time variables do not match
2999  continue
      write(io6,*)'ERROR in subr. RDMET -- date/time variables for',&
       ' wind components do not match --  '
      write(io6,*)'   NDATHRE1,NSECE1= ',ndathre1,nsece1
      write(io6,*)'   NDATHRE2,NSECE2= ',ndathre2,nsece2
      write(io6,*)'   NDATHRE3,NSECE3= ',ndathre3,nsece3
      stop 'Halted in RDMET -- see list file.'
3999  continue
      write(io6,*)'ERROR in subr. RDMET -- date/time variables ',&
       'do not match -- '
      write(io6,*)'   NDATHRE ,NSECE = ',ndathre,nsece
      write(io6,*)'   KDATHRE ,KSECE = ',kdathre,ksece
      stop 'Halted in RDMET -- see list file.'
      end
!----------------------------------------------------------------------
      subroutine rdprof(idathrm,isecm,lecho,izp,wsprf,wdprf,tprf,&
                        zprf,ssprf,swprf,svprf,dptinvo,&
                        kdathre,kse,kdathrb,ksb,leof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090612            RDPROF
!                D. Strimaitis
!
! --- PURPOSE:  Read meteorological data for one hour from CTDM
!               PROFILE file
!
! --- UPDATE
! --- V6.1-V6.265   090612  (DGS): Screen use of end-date variables
!                                  when not read from file to avoid
!                                  use of undefined variables (iey)
!                           (DGS): Apply TCLOCK to individual date-time
!                                  variables instead of undefined
!                                  dathr variable
! --- V5.7-V6.1     050915  (DGS): allow for begin/end time format
!                                  resolved to seconds
!                                  (retain compatibility with older
!                                  PROFILE versions)
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4, JULDAY,
!                                  GRDAY, INCR
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.2     991104  (DGS): YYYY format used for year
! --- V4.0-V5.0     971107  (DGS): add EOF recovery
!                           (DGS): add record-skip logic from COMP to
!                                  find current date-time
!                           (DGS): skip blank records
!                           (DGS): allow inversion strength in PROFILE
!
! --- INPUTS:
!       IDATHRM - integer - Mid-pt date/hour of required data (YYYYJJJHH)
!         ISECM - integer - Mid-pt second of required data (SSSS)
!         LECHO - logical - Flag controlling printing of input
!                           meteorological data fields
!        PTG(2) - real    - Default potential temperature grad.
!                           for stable classes (deg. K/m)
!
!    Common block /METHD/ variables:
!         NXM, NYM, NZM, NSSTA, NPSTA, ZFACEM(mxnzp1), LCALGRD,
!         IMIXCTDM, ISIGMAV, ITIMEPRF
!    Parameters:
!         MXNX, MXNY, MXNZ, MXNZP1, MXPRFZ, IO6
!
! --- OUTPUT:
!
!                    IZP - integer - Number of profile levels
!          WSPRF(mxprfz) - real    - 1-D vector wind speed profile (m/s)
!          WDPRF(mxprfz) - real    - 1-D vector wind dir. profile (deg)
!           TPRF(mxprfz) - real    - Temperature profile (deg. K)
!           ZPRF(mxprfz) - real    - Heights for data in profile (m)
!          SSPRF(mxprfz) - real    - 1-D scalar speed profile (m/s)
!          SWPRF(mxprfz) - real    - 1-D sigma-w profile (m/s)
!          SVPRF(mxprfz) - real    - 1-D sigma-v profile (m/s)
!                DPTINVO - real    - Inversion strength at top of CBL
!                                    (jump in potential temperature K)
!                KDATHRE - integer - End date/hour of data (YYYYJJJHH)
!                    KSE - integer - End second of data (SSSS)
!                KDATHRB - integer - Begin date/hour of data (YYYYJJJHH)
!                    KSB - integer - Begin second of data (SSSS)
!                   LEOF - logical - End-Of-File flag
!
! --- RDPROF called by:  COMP, RDMET4
! --- RDPROF calls:      JULDAY, INCR, GRDAY, INITAR,
!                        INITAI, OUT, INITR2D, INITI2D,
!                        LSTAB, XTPRF, TPERIOD, YR4, TCLOCK
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'methd.puf'
!
! --- Profile data arrays
      real wsprf(mxprfz),wdprf(mxprfz),tprf(mxprfz),zprf(mxprfz)
      real ssprf(mxprfz),swprf(mxprfz),svprf(mxprfz)
!
      character aline*132
!
      logical lecho,problem,leof
!
      data dtor/.0174533/
! --- Set logical to halt program if problem is found
1     problem=.FALSE.
! -------------------------------------------------------------------
!  -- Read records for this hour from CTDM PROFILE file
! -------------------------------------------------------------------
2     izp=0
10    izp=izp+1
! --- Do not let profile levels exceed MXPRFZ; discard extra levels
      if(izp.GT.mxprfz) izp=mxprfz
! --- First level of profile may contain the strength of inversion
! --- at top of CBL, so read first line as character variable and
! --- try to extract DPTINVO using internal read
      if(izp.EQ.1) then
         read(io31,'(a132)',end=999) aline
         if(itimeprf.EQ.1) then
            read(aline,*,end=15) iy,im,id,ih,ksb,iey,iem,ied,ieh,kse,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp),wsprf(izp),dptinvo
         else
            read(aline,*,end=15) iy,im,id,ih,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp),wsprf(izp),dptinvo
         endif
         goto 16
! ---    Inversion strength is not provided; return missing value
15       dptinvo=-999.
16       continue
      else
         if(itimeprf.EQ.1) then
            read(io31,*,end=999) iy,im,id,ih,ksb,iey,iem,ied,ieh,kse,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp),wsprf(izp)
         else
            read(io31,*,end=999) iy,im,id,ih,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp),wsprf(izp)
         endif
      endif
!
! --- Convert svprf to sigma-v (m/s) if given in degrees
      if(isigmav.EQ.0) then
         if(svprf(izp).GT.0.0) then
            if(wsprf(izp).GT.0.0) then
! ---          Definition!
               svprf(izp)=svprf(izp)*dtor*wsprf(izp)
            elseif(ssprf(izp).GT.0.0) then
! ---          Approximation!
               svprf(izp)=svprf(izp)*dtor*ssprf(izp)
            else
               svprf(izp)=-999.
            endif
         endif
      endif
!
! --- Construct vector/scalar average wind, as in CTDM, if needed
      if(wsprf(izp).LE.0.0) then
         if(ssprf(izp).GT.0.0) then
! ---       Estimate vector mean from scalar mean speed
            if(svprf(izp).GT.0) then
               sigth=svprf(izp)/ssprf(izp)
! ---          Sigma-theta cannot exceed 103.9 degrees (1.81334 radians)
               sigth=AMIN1(sigth,1.81334)
               e=SIN(sigth)*(1.0-0.073864*sigth)
               wsprf(izp)=ssprf(izp)*SQRT(1.0-e*e)
            else
               wsprf(izp)=ssprf(izp)
            endif
         endif
      elseif(ssprf(izp).LE.0.0) then
! ---    Estimate scalar mean from vector mean speed
         sigth=svprf(izp)/wsprf(izp)
! ---    Sigma-theta cannot exceed 103.9 degrees (1.81334 radians)
         sigth=AMIN1(sigth,1.81334)
         if(sigth.GT.0) then
            e=SIN(sigth)*(1.0-0.073864*sigth)
            ssprf(izp)=wsprf(izp)/SQRT(1.0-e*e)
         else
            ssprf(izp)=wsprf(izp)
         endif
      endif
!
! --- Are there more levels in this profile? (go to 10 if yes)
      if(izp.EQ.1) then
         itest1=iy+im+id+ih
         itest2=itest1
         if(ilast.NE.1) goto 10
      elseif(ilast.EQ.1) then
         itest2=iy+im+id+ih
      else
         goto 10
      endif
! -------------------------------------------------------------------
! --- Enforce YYYY format for year
      call YR4(io6,iy,ierr)
      if(ierr.NE.0) stop 'Halted in RDPROF'
      if(itimeprf.EQ.1) then
         call YR4(io6,iey,ierr)
         if(ierr.NE.0) stop 'Halted in RDPROF'
      endif
! --- Form date-time variable for last record read from PROFILE file
! --- Assign proper time period variables (augment old format)
! --- PROFILE.DAT used a 1-24 clock (hour ending).
      if(itimeprf.EQ.1) then
         call julday(io6,iy,im,id,ijul)
         call julday(io6,iey,iem,ied,iejul)
      else
         ksb=0
         kse=0
! ---    Set time at the beginning of the hour (subtract 1 hour)
         ih=ih-1
         call julday(io6,iy,im,id,ijul)
! ---    Increment time 1 hour to get end time (0-23 convention)
         iey=iy
         iem=im
         ied=id
         ieh=ih
         call julday(io6,iey,iem,ied,iejul)
         call incr(io6,iey,iejul,ieh,1)
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,iy,ijul,ih,ksb)
      call TCLOCK(io6,idum,iey,iejul,ieh,kse)
! --- Form date-time variables
      kdathrb=iy*100000+ijul*100+ih
      kdathre=iey*100000+iejul*100+ieh
! --- Check to see if required date-time has been read
      call TPERIOD(0,idathrm,isecm,kdathrb,ksb,kdathre,kse,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         goto 1
      elseif(ilocate.EQ.-1)then
         write(io6,*)'ERROR in subr. RDPROF -- current time not found',&
         ' in the PROFILE data file'
         write(io6,*)' -- Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksb
         write(io6,*)'                           to ',kdathre,kse
         problem=.TRUE.
      endif
! --- Check for consistent date-time for hour among records
      if(itest1.NE.itest2) then
         write(io6,*)'RDPROF: date-time of PROFILE levels do not match'
         problem=.TRUE.
      endif
! --- Halt program now if problem was found with PROFILE
      if(PROBLEM) then
         write(io6,*)' PROFILE per. = ',kdathrb,ksb,kdathre,kse
         stop 'Halted in RDPROF -- see list file'
      endif
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ PROFILE Data- BEGINNING: ',kdathrb,ksb
         write(io6,*)'------              AND ENDING: ',kdathre,kse
         write(io6,*)
!
! ---    Profile fields
         write(io6,*)'Height(m),  Scalar Speed,  Sigma-W,  Sigma-V:'
         do iz=1,izp
            write(io6,*) zprf(iz),ssprf(iz),swprf(iz),svprf(iz)
         enddo
         write(io6,*)
         write(io6,*)'Height(m), Vector Speed, Direction, Temperature:'
         do iz=1,izp
            write(io6,*) zprf(iz),wsprf(iz),wdprf(iz),tprf(iz)
         enddo
         write(io6,*)
! ---    Also report inversion strength here
         write(io6,*)'Inversion Strength in file (K) = ',dptinvo
         write(io6,*)
      endif
!
      return
999   write(io6,*)'RDPROF: End-Of-File found in PROFILE file'
      leof=.TRUE.
      return
      end
!----------------------------------------------------------------------
      subroutine rdprof5(idathrm,isecm,lecho,izp,wsprf,wdprf,tprf,&
                        zprf,ssprf,swprf,svprf,dptinvo,&
                        kdathre,kse,kdathrb,ksb,leof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090612           RDPROF5
!                D. Strimaitis
!
! --- PURPOSE:  Read meteorological data for one hour from AERMET
!               PROFILE file.  This is RDPROF with the following
!               changes:
!               1.  Retain extended data records even though none are
!                   currently expected
!               2.  Wind speed in record is assumed to be the scalar
!                   average
!               3.  Temperature is read as degrees C.
!
! --- UPDATE
! --- V6.1-V6.265   090612  (DGS): Screen use of end-date variables
!                                  when not read from file to avoid
!                                  use of undefined variables (iey)
!                           (DGS): Apply TCLOCK to individual date-time
!                                  variables instead of undefined
!                                  dathr variable
! --- V5.74-V6.1    050915  (DGS): Allow for begin/end time format
!                                  resolved to seconds
!                                  (retain compatibility with older
!                                  PROFILE versions)
!
! --- INPUTS:
!       IDATHRM - integer - Mid-pt date/hour of required data (YYYYJJJHH)
!         ISECM - integer - Mid-pt second of required data (SSSS)
!         LECHO - logical - Flag controlling printing of input
!                           meteorological data fields
!        PTG(2) - real    - Default potential temperature grad.
!                           for stable classes (deg. K/m)
!
!    Common block /METHD/ variables:
!         NXM, NYM, NZM, NSSTA, NPSTA, ZFACEM(mxnzp1), LCALGRD,
!         IMIXCTDM, ISIGMAV, ITIMEPRF
!    Parameters:
!         MXNX, MXNY, MXNZ, MXNZP1, MXPRFZ, IO6
!
! --- OUTPUT:
!
!                    IZP - integer - Number of profile levels
!          WSPRF(mxprfz) - real    - 1-D vector wind speed profile (m/s)
!          WDPRF(mxprfz) - real    - 1-D vector wind dir. profile (deg)
!           TPRF(mxprfz) - real    - Temperature profile (deg. K)
!           ZPRF(mxprfz) - real    - Heights for data in profile (m)
!          SSPRF(mxprfz) - real    - 1-D scalar speed profile (m/s)
!          SWPRF(mxprfz) - real    - 1-D sigma-w profile (m/s)
!          SVPRF(mxprfz) - real    - 1-D sigma-v profile (m/s)
!                DPTINVO - real    - Inversion strength at top of CBL
!                                    (jump in potential temperature K)
!                KDATHRE - integer - End date/hour of data (YYYYJJJHH)
!                    KSE - integer - End second of data (SSSS)
!                KDATHRB - integer - Begin date/hour of data (YYYYJJJHH)
!                    KSB - integer - Begin second of data (SSSS)
!                   LEOF - logical - End-Of-File flag
!
! --- RDPROF5 called by:  COMP, RDMET5
! --- RDPROF5 calls:      JULDAY, INCR, GRDAY, INITAR,
!                         INITAI, OUT, INITR2D, INITI2D,
!                         LSTAB, XTPRF, TPERIOD, YR4, TCLOCK
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'methd.puf'
!
! --- Profile data arrays
      real wsprf(mxprfz),wdprf(mxprfz),tprf(mxprfz),zprf(mxprfz)
      real ssprf(mxprfz),swprf(mxprfz),svprf(mxprfz)
!
      character aline*132
!
      logical lecho,problem,leof
!
      data dtor/.0174533/
! --- Set logical to halt program if problem is found
1     problem=.FALSE.
! -------------------------------------------------------------------
!  -- Read records for this hour from AERMET PROFILE file
! -------------------------------------------------------------------
2     izp=0
10    izp=izp+1
! --- Do not let profile levels exceed MXPRFZ; discard extra levels
      if(izp.GT.mxprfz) izp=mxprfz
! --- First level of profile may contain the strength of inversion
! --- at top of CBL, so read first line as character variable and
! --- try to extract DPTINVO using internal read
      if(izp.EQ.1) then
         read(io31,'(a132)',end=999) aline
         if(itimeprf.EQ.1) then
            read(aline,*,end=15) iy,im,id,ih,ksb,iey,iem,ied,ieh,kse,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp),dptinvo
         else
            read(aline,*,end=15) iy,im,id,ih,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp),dptinvo
         endif
         goto 16
! ---    Inversion strength is not provided; return missing value
15       dptinvo=-999.
16       continue
      else
         if(itimeprf.EQ.1) then
            read(io31,*,end=999) iy,im,id,ih,ksb,iey,iem,ied,ieh,kse,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp)
         else
            read(io31,*,end=999) iy,im,id,ih,&
                                 zprf(izp),ilast,wdprf(izp),&
                                 ssprf(izp),tprf(izp),svprf(izp),&
                                 swprf(izp)
         endif
      endif
! --- Trap AERMET missings
      if(ssprf(izp).LT.0.0 .OR. ssprf(izp).GT.90.) ssprf(izp)=-999.
      if(wdprf(izp).LT.0.0 .OR. wdprf(izp).GT.900.) wdprf(izp)=-999.
      if(tprf(izp).LT.-90. .OR. tprf(izp).GT.90.) tprf(izp)=-999.
      if(svprf(izp).LT.0.0 .OR. svprf(izp).GT.90.) svprf(izp)=-99.9
      if(swprf(izp).LT.0.0 .OR. swprf(izp).GT.90.) swprf(izp)=-99.9
! --- Set vector mean speed equal to missing
      wsprf(izp)=-999.
! --- Convert temperature from degrees C to degrees K
      if(tprf(izp).GT.-900.) tprf(izp)=tprf(izp)+273.16
! --- Convert svprf to sigma-v (m/s) if given in degrees
      if(isigmav.EQ.0) then
         if(svprf(izp).GT.0.0) then
            if(wsprf(izp).GT.0.0) then
! ---          Definition!
               svprf(izp)=svprf(izp)*dtor*wsprf(izp)
            elseif(ssprf(izp).GT.0.0) then
! ---          Approximation!
               svprf(izp)=svprf(izp)*dtor*ssprf(izp)
            else
               svprf(izp)=-999.
            endif
         endif
      endif
!
! --- Construct vector/scalar average wind, as in CTDM, if needed
      if(wsprf(izp).LE.0.0) then
         if(ssprf(izp).GT.0.0) then
! ---       Estimate vector mean from scalar mean speed
            if(svprf(izp).GT.0) then
               sigth=svprf(izp)/ssprf(izp)
! ---          Sigma-theta cannot exceed 103.9 degrees (1.81334 radians)
               sigth=AMIN1(sigth,1.81334)
               e=SIN(sigth)*(1.0-0.073864*sigth)
               wsprf(izp)=ssprf(izp)*SQRT(1.0-e*e)
            else
               wsprf(izp)=ssprf(izp)
            endif
         endif
      elseif(ssprf(izp).LE.0.0) then
! ---    Estimate scalar mean from vector mean speed
         sigth=svprf(izp)/wsprf(izp)
! ---    Sigma-theta cannot exceed 103.9 degrees (1.81334 radians)
         sigth=AMIN1(sigth,1.81334)
         if(sigth.GT.0) then
            e=SIN(sigth)*(1.0-0.073864*sigth)
            ssprf(izp)=wsprf(izp)/SQRT(1.0-e*e)
         else
            ssprf(izp)=wsprf(izp)
         endif
      endif
!
! --- Are there more levels in this profile? (go to 10 if yes)
      if(izp.EQ.1) then
         itest1=iy+im+id+ih
         itest2=itest1
         if(ilast.NE.1) goto 10
      elseif(ilast.EQ.1) then
         itest2=iy+im+id+ih
      else
         goto 10
      endif
! -------------------------------------------------------------------
! --- Enforce YYYY format for year
      call YR4(io6,iy,ierr)
      if(ierr.NE.0) stop 'Halted in RDPROF5'
      if(itimeprf.EQ.1) then
         call YR4(io6,iey,ierr)
         if(ierr.NE.0) stop 'Halted in RDPROF5'
      endif
! --- Form date-time variable for last record read from PROFILE file
! --- Assign proper time period variables (augment old format)
! --- PROFILE.DAT used a 1-24 clock (hour ending).
      if(itimeprf.EQ.1) then
         call julday(io6,iy,im,id,ijul)
         call julday(io6,iey,iem,ied,iejul)
      else
         ksb=0
         kse=0
! ---    Set time at the beginning of the hour (subtract 1 hour)
         ih=ih-1
         call julday(io6,iy,im,id,ijul)
! ---    Increment time 1 hour to get end time (0-23 convention)
         iey=iy
         iem=im
         ied=id
         ieh=ih
         call julday(io6,iey,iem,ied,iejul)
         call incr(io6,iey,iejul,ieh,1)
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,iy,ijul,ih,ksb)
      call TCLOCK(io6,idum,iey,iejul,ieh,kse)
! --- Form date-time variables
      kdathrb=iy*100000+ijul*100+ih
      kdathre=iey*100000+iejul*100+ieh
! --- Check to see if required date-time has been read
      call TPERIOD(0,idathrm,isecm,kdathrb,ksb,kdathre,kse,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         goto 1
      elseif(ilocate.EQ.-1)then
         write(io6,*)'ERROR in subr. RDPROF5 -- current time not found',&
         ' in the PROFILE data file'
         write(io6,*)' -- Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksb
         write(io6,*)'                           to ',kdathre,kse
         problem=.TRUE.
      endif
! --- Check for consistent date-time for hour among records
      if(itest1.NE.itest2) then
         write(io6,*)'RDPROF5: date-time of PROFILE levels do not match'
         problem=.TRUE.
      endif
! --- Halt program now if problem was found with PROFILE
      if(PROBLEM) then
         write(io6,*)' PROFILE per. = ',kdathrb,ksb,kdathre,kse
         stop 'Halted in RDPROF5 -- see list file'
      endif
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ PROFILE Data- BEGINNING: ',kdathrb,ksb
         write(io6,*)'------              AND ENDING: ',kdathre,kse
         write(io6,*)
!
! ---    Profile fields
         write(io6,*)'Height(m),  Scalar Speed,  Sigma-W,  Sigma-V:'
         do iz=1,izp
            write(io6,*) zprf(iz),ssprf(iz),swprf(iz),svprf(iz)
         enddo
         write(io6,*)
         write(io6,*)'Height(m), Vector Speed, Direction, Temperature:'
         do iz=1,izp
            write(io6,*) zprf(iz),wsprf(iz),wdprf(iz),tprf(iz)
         enddo
         write(io6,*)
! ---    Also report inversion strength here
         write(io6,*)'Inversion Strength in file (K) = ',dptinvo
         write(io6,*)
      endif
!
      return
999   write(io6,*)'RDPROF5: End-Of-File found in PROFILE file'
      leof=.TRUE.
      return
      end
!----------------------------------------------------------------------
      subroutine met2(lecho)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 121203              MET2
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Create the header information normally contained
!               in the CALMET file (using ISCMET file).
!               Also, set the wind profile power-law exponents if
!               not provided in control file.
!
! --- UPDATE
!
! --- V6.301-6.42_x1.0 121203  : Change IBSMET assignment from scalar
!                                  to array
!
! --- V6.3-V6.301   100827  (DGS): Allow nested CALMET files to have
!                                  different time-periods
! --- V6.1-V6.3     100212  (DGS): Add nested met grid processing
!                   100212  (DGS): Change met station coordinates from
!                                  absolute to relative to grid origin
! --- V5.7-V6.1     050915  (DGS): Add 'dataset 2.1' file format, and
!                                  use c*16 variable VERISC in new
!                                  /DATASET/ common to identify version.
!                                  Implement full begin/end time
!                                  processing.
!                                  Add METQA call.
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4, JULDAY,
!                                  GRDAY, INCR
!                   030402  (DGS): add map projection, datum
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
! --- V5.0-V5.0     980430  (DGS): remove plrur,plurb (compiler warning)
! --- V5.0-V5.0     980304  (DGS): add station elevation,lat,lon
! --- V4.0-V5.0     971107  (DGS): pass start-time from met file
!                                  for use if METRUN=1
!
! --- INPUTS:
!         LECHO - logical       - Control variable for output of header
!                                 record information
!     Common block /COMPARM/ variables:
!           PLX0
!     Common block /DATASET/ variables:
!           VERISC
!     Common block /DISPDAT/ variables:
!           IURB1, IURB2
!     Common block /GRID/ variables:
!           NX, NY, NZ, DGRID, XORIG, YORIG, ZFACE(mxnzp1)
!     Common block /MAP/ variables:
!           iutmzn,feast,fnorth,
!           rlat0,rlon0,xlat1,xlat2,
!           pmap,utmhem,datum,daten
!     Common block /METHD/ variables:
!           ANEMHT, ILANDUIN, Z0IN, XLAIIN, ELEVIN, XLATIN, XLONIN
!     Common block /GRIDNEST/ variables:
!           ngrid
!     Parameters:
!           MXNZP1, MXSS, MXUS, MXPS, MXNX, MXNY, MXMETDOM, IO6
!
! --- OUTPUT:
!     Common block /COMPARM/ variables:
!           PLX0
!     Common block /METHD/ variables:
!           nxm(mxmetdom),nym(mxmetdom),nzm,
!           xgridm(mxmetdom),xgridmi(mxmetdom),
!           xorigm(mxmetdom),yorigm(mxmetdom),
!           xbtzm,rlat0m,rlon0m,xlat1m,xlat2m,nssta(mxmetdom),
!           nusta(mxmetdom),npsta(mxmetdom),nlu,iwat1,iwat2,
!           zfacem(mxnzp1),xssta(mxss,mxmetdom),yssta(mxss,mxmetdom),
!           xlatss(mxss,mxmetdom),xlonss(mxss,mxmetdom),
!           xusta(mxus,mxmetdom),yusta(mxus,mxmetdom),
!           xpsta(mxps,mxmetdom),ypsta(mxps,mxmetdom),
!           z0(mxnx,mxny,mxmetdom),ilandu(mxnx,mxny,mxmetdom),
!           elev(mxnx,mxny,mxmetdom),xlai(mxnx,mxny,mxmetdom),
!           nears(mxnx,mxny,mxmetdom),lcalgrd,itimes,
!           ibymet(mxmetdom),ibmmet(mxmetdom),ibdmet(mxmetdom),
!           ibjdmet(mxmetdom),ibhmet(mxmetdom),ibsmet(mxmetdom),
!           ieymet(mxmetdom),iemmet(mxmetdom),iedmet(mxmetdom),
!           iejdmet(mxmetdom),iehmet(mxmetdom),iesmet(mxmetdom),
!           iutmznm,feastm,fnorthm,pmapm,datumm,datenm,utmhemm
!
! --- MET2 called by:  SETUP
! --- MET2 calls:      JULDAY, INCR, GRDAY, INITR2D, OUT, INITI2D,
!                       YR4, METQA, TCLOCK
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
      character*80 title(3)
      character*70 messag
      character*3 abegin,aend
      character*4 MAP
      character*8 axbtzm
      character*8 clab1,clab2
      logical ldate,lecho
!
! --- Include common block /COMPARM/ -- Computation parameters
      include 'comparm.puf'
! --- Include common block /DATASET/  -- Input file dataset version
      include 'dataset.puf'
! --- Include common block /DISPDAT/ -- Dispersion-related data
      include 'dispdat.puf'
! --- Include common block /METHD/ -- CALMET header record data
      include 'methd.puf'
! --- Include common block /GRID/  -- Grid data from control file
      include 'grid.puf'
! --- Include common block /MAP/  -- Projection data from control file
      include 'map.puf'
! --- Include common block /GRIDNEST/  -- Nested grid configuration
      include 'gridnest.puf'
!
      data ldate/.false./
!
! --- Initialize the time zone to an invalid number (will be replaced
! --- starting with dataset 2.1)
      xbtzm=-999.9
! --- There should be only 1 met grid
      if(ngrid.NE.1) then
         write(io6,*)
         write(io6,*)'MET2:  Nested grid information is bad'
         write(io6,*)'Expected 1 met grid, Found ',ngrid
         stop 'HALTED in MET2 -- See list file'
      else
! ---    Use explicit variable for '1'
         kg=1
      endif
! --- Set first 2 title-lines
      title(1)='Constant Meteorological Fields'
      title(2)='Created for ISCMET.DAT'
! --- Process HEADER records (or extract info from data record)
      if(verisc.EQ.'2.1') then
! ---    Full version 2.1 with beginning & ending times (non-standard)
         itimes=1
         read(io7,'(a80)') title(1)
         read(io7,*) ncomm
! ---    Keep first comment record
         read(io7,'(a80)') title(2)
! ---    Skip other comment records
         do k=2,ncomm
            read(io7,*) 
         enddo
! ---    Allow map projection of 'NONE'
         read(io7,'(a4)') map
         if(map.NE.'NONE') then
            write(io6,*)'FATAL Error in MET2 -- Unsupported Map Proj.'
            write(io6,*)'Expected NONE, Found map = ',map
            stop 'Halted in MET2 -- see list file'
         endif
! ---    Time zone (hours only)
         read(io7,'(a8)') axbtzm
         if(axbtzm(7:8).NE.'00') then
            write(io6,*)'FATAL Error in MET2 -- Unsupported Time Zone'
            write(io6,*)'Expected whole hour, Found: ',axbtzm
            stop 'Halted in MET2 -- see list file'
         endif
         read(axbtzm(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in MET2 -- Unsupported Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axbtzm
            stop 'Halted in MET2 -- see list file'
         endif
         xbtzm=-FLOAT(ibtzhh)
! ---    Time period
         read(io7,*) ibymet(kg),ibmmet(kg),ibdmet(kg),&
                     ibhmet(kg),ibsmet(kg),&
                     ieymet(kg),iemmet(kg),iedmet(kg),&
                     iehmet(kg),iesmet(kg)
! ---    Enforce YYYY format for year
         call YR4(io6,ibymet(kg),ierrb)
         call YR4(io6,ieymet(kg),ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in MET2'
         call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
         call JULDAY(io6,ieymet(kg),iemmet(kg),iedmet(kg),iejdmet(kg))
! ---    Get standard ISC header record, or other comments
         read(io7,'(a80)') title(3)
      elseif(verisc.EQ.'1.6') then
! ---    Beginning & ending times are provided (non-standard)
         itimes=1
         read(io7,'(a80)') title(1)
         read(title(1),*) abegin,ibymet(kg),ibmmet(kg),ibdmet(kg),&
                          ibhmet(kg),ibsmet(kg),&
                          aend,ieymet(kg),iemmet(kg),iedmet(kg),&
                          iehmet(kg),iesmet(kg)
! ---    Enforce YYYY format for year
         call YR4(io6,ibymet(kg),ierrb)
         call YR4(io6,ieymet(kg),ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in MET2'
         call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
         call JULDAY(io6,ieymet(kg),iemmet(kg),iedmet(kg),iejdmet(kg))
! ---    Get standard ISC header record, or other comments
         read(io7,'(a80)') title(3)
      else
         itimes=0
! ---    Read ISC header record (station, year for surface and upper)
         read(io7,'(a80)') title(3)
! ---    Read date-time from first data record
         read(io7,'(4i2)') ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg)
! ---    Rewind file and skip over header record
         rewind(io7)
         read(io7,*)
! ---    Enforce YYYY format for year
         call YR4(io6,ibymet(kg),ierr)
         if(ierr.NE.0) then
            write(*,*)
            stop 'Halted in MET2'
         endif
! ---    ISCMET.DAT uses a 1-24 clock (hour ending).  Convert to time
! ---    beginning by subtracting 1 from the hour (0-23)
         ibhmet(kg)=ibhmet(kg)-1
         call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
! --- V6.42_x1.0, Level 121203
         ibsmet(kg)=0
! ---    Use "default" run length (hours) to mark the end of data
! ---    Assume this will be 5 years, so set IRLG=43848 hours
         irlgmet=43848
         ieymet(kg)=ibymet(kg)
         iemmet(kg)=ibmmet(kg)
         iedmet(kg)=ibdmet(kg)
         iejdmet(kg)=ibjdmet(kg)
         iehmet(kg)=ibhmet(kg)
         call INCR(io6,ieymet(kg),iejdmet(kg),iehmet(kg),irlgmet)
         iesmet(kg)=0
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibymet(kg),ibjdmet(kg),ibhmet(kg),&
                  ibsmet(kg))
      call TCLOCK(io6,idum,ieymet(kg),iejdmet(kg),iehmet(kg),&
                  iesmet(kg))
      call GRDAY(io6,ibymet(kg),ibjdmet(kg),ibmmet(kg),ibdmet(kg))
      call GRDAY(io6,ieymet(kg),iejdmet(kg),iemmet(kg),iedmet(kg))
!
! ---------------------------------------------------------------------
! --- record #1 - run title --
!
! --- record #2 - run control parameters --
! --- (vermet, levmet are both 8 bytes):
! ---  vermet,levmet,ibyr,ibmo,ibdy,ibhr,irtype,
! ---  nxm, nym, nzm, xgridm, xorigm, yorigm, iutmznm, iwfcod, nssta,
! ---  nusta, npsta, nowsta, nlu, iwat1, iwat2, lcalgrd
!
! --- Fill in most first
      irtype=1
      nxm(kg)=nx
      nym(kg)=ny
      nzm=nz
      xgridm(kg)=dgrid
      xgridmi(kg)=1./dgrid
      xorigm(kg)=xorig
      yorigm(kg)=yorig
      pmapm=pmap
      datumm=datum
      datenm=daten
      iutmznm=iutmzn
      utmhemm=utmhem
      rlat0m=rlat0
      rlon0m=rlon0
      xlat1m=xlat1
      xlat2m=xlat2
      feastm=feast
      fnorthm=fnorth
      iwfcod=1
      nssta(kg)=1
      nusta(kg)=1
      npsta(kg)=1
      nowsta(kg)=0
      nlu=10
      iwat1=500
      iwat2=599
      lcalgrd=.FALSE.
      if(LECHO)then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*) title(1)
         write(io6,*) title(2)
         write(io6,*) title(3)
         write(io6,*)
         write(io6,*)'-- Data begin at:'
         write(io6,*)' IBYR    = ',ibymet(kg)
         write(io6,*)' IBMO    = ',ibmmet(kg)
         write(io6,*)' IBDY    = ',ibdmet(kg)
         write(io6,*)' IBJUL   = ',ibjdmet(kg)
         write(io6,*)' IBHR    = ',ibhmet(kg)
         write(io6,*)' IBSEC   = ',ibsmet(kg)
         if(verisc.EQ.'1.0') then
            write(io6,*)'-- Data end at:  ?'
         else
            write(io6,*)'-- Data end at:'
         endif
         write(io6,*)' IEYR    = ',ieymet(kg)
         write(io6,*)' IEMO    = ',iemmet(kg)
         write(io6,*)' IEDY    = ',iedmet(kg)
         write(io6,*)' IEJUL   = ',iejdmet(kg)
         write(io6,*)' IEHR    = ',iehmet(kg)
         write(io6,*)' IESEC   = ',iesmet(kg)
         write(io6,*)
         write(io6,*)' IRTYPE  = ',irtype
         write(io6,*)' LCALGRD = ',lcalgrd
         write(io6,*)' NXM     = ',nxm(kg)
         write(io6,*)' NYM     = ',nym(kg)
         write(io6,*)' NZM     = ',nzm
         write(io6,*)' XGRIDM  = ',xgridm(kg)
         write(io6,*)' XGRIDMI = ',xgridmi(kg)
         write(io6,*)' XORIGM  = ',xorigm(kg)
         write(io6,*)' YORIGM  = ',yorigm(kg)
         write(io6,*)' PMAPM   = ',pmapm
         write(io6,*)' DATUMM  = ',datumm
         write(io6,*)' DATENM  = ',datenm
         write(io6,*)' IUTMZNM = ',iutmznm
         write(io6,*)' UTMHEMM = ',utmhemm
         write(io6,*)' XLAT1M  = ',xlat1m
         write(io6,*)' XLAT2M  = ',xlat2m
         write(io6,*)' RLAT0M  = ',rlat0m
         write(io6,*)' RLON0M  = ',rlon0m
         write(io6,*)' FEASTM  = ',feastm
         write(io6,*)' FNORTHM = ',fnorthm
         write(io6,*)' IWFCOD  = ',iwfcod
         write(io6,*)' NSSTA   = ',nssta(kg)
         write(io6,*)' NUSTA   = ',nusta(kg)
         write(io6,*)' NPSTA   = ',npsta(kg)
         write(io6,*)' NOWSTA  = ',nowsta(kg)
         write(io6,*)' NLU     = ',nlu
         write(io6,*)' IWAT1   = ',iwat1
         write(io6,*)' IWAT2   = ',iwat2
         write(io6,*)' ANEMHT  = ',anemht
      endif
!
      nzmp1=nzm+1
      if(mxnzp1.lt.nzmp1)then
         write(io6,*)'ERROR in subr. MET2 -- dimension of ZFACEM ',&
         'array is too small for no. layers in ISCMET file'
         write(io6,*)'MXNZP1 = ',mxnzp1,' NZMP1 = ',nzmp1
         write(*,*)
         stop 'Halted in MET2 -- see list file.'
      endif
!
! --- record #3 - cell face heights (NZ + 1 words)
!
! --- Grab the face heights from the control file
      do i=1,nzmp1
         zfacem(i)=zface(i)
      enddo
!
      if(LECHO)then
         write(io6,203)(zfacem(n),n=1,nzmp1)
203      format(1x,'ZFACEM  = ',10(f9.3,', ')/)
      endif
! --- Perform QA checks on header record variables
      call METQA
!
! --- records #4 & 5 - x, y coordinates of surface stations
! --- (NSSTA words each record)
      if(nssta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xssta(1,kg)=0.0
         yssta(1,kg)=0.0
         if(LECHO)then
            clab1='XSSTA'
            clab2='YSSTA'
            write(io6,*)clab1,' = ',(xssta(n,kg),n=1,nssta(kg))
            write(io6,*)clab2,' = ',(yssta(n,kg),n=1,nssta(kg))
         endif
      endif
!
! --- records #4a & 5a - lat, lon coordinates of surface stations
! --- (NSSTA words each record)
      if(nssta(kg).gt.0)then
! ---    Grab the lat,lon from control file inputs
         xlatss(1,kg)=xlatin
         xlonss(1,kg)=xlonin
         if(LECHO)then
            clab1='XLATSS'
            clab2='YLONSS'
            write(io6,*)clab1,' = ',(xlatss(n,kg),n=1,nssta(kg))
            write(io6,*)clab2,' = ',(xlonss(n,kg),n=1,nssta(kg))
         endif
      endif
!
! --- records #6 & 7 - x, y coordinates of upper air stations
! --- (NUSTA words each record)
      if(nusta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xusta(1,kg)=0.0
         yusta(1,kg)=0.0
         if(LECHO)then
            clab1='XUSTA'
            clab2='YUSTA'
            write(io6,*)clab1,' = ',(xusta(n,kg),n=1,nusta(kg))
            write(io6,*)clab2,' = ',(yusta(n,kg),n=1,nusta(kg))
         endif
      endif
!
! --- records #8 & 9 - x, y coordinates of precipitation stations
! --- (NPSTA words each record)
      if(npsta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xpsta(1,kg)=0.0
         ypsta(1,kg)=0.0
         if(LECHO)then
            clab1='XPSTA'
            clab2='YPSTA'
            write(io6,*)clab1,' = ',(xpsta(n,kg),n=1,npsta(kg))
            write(io6,*)clab2,' = ',(ypsta(n,kg),n=1,npsta(kg))
         endif
      endif
!
! --- Fill the corresponding arrays:
!
! --- record #10 - surface roughness lengths (NX * NY words)
      call initr2d(z0in,mxnx,mxny,nxm(kg),nym(kg),z0(1,1,kg))
      if(LECHO)then
         messag='Surface roughness lengths (m)'
         messag(63:70)='z0'
         call out(z0(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #11 - land use categories (NX * NY words)
      call initi2d(ilanduin,mxnx,mxny,nxm(kg),nym(kg),ilandu(1,1,kg))
      if(LECHO)then
         messag='Land use categories'
         messag(63:70)='ILANDU'
         call out(xdum,ilandu(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #12 - elevations (NX * NY words)
! --- Set grid elevation to ELEVIN from control file
      call initr2d(elevin,mxnx,mxny,nxm(kg),nym(kg),elev(1,1,kg))
      if(LECHO)then
         messag='Terrain heights (m)'
         messag(63:70)='TERR'
         call out(elev(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #13 - leaf area index (NX * NY words)
      call initr2d(xlaiin,mxnx,mxny,nxm(kg),nym(kg),xlai(1,1,kg))
      if(LECHO)then
         messag='Leaf area index'
         messag(63:70)='XLAI'
         call out(xlai(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #14 - nearest surface station to each grid point
!                  (NX * NY words)
      call initi2d(1,mxnx,mxny,nxm(kg),nym(kg),nears(1,1,kg))
      if(LECHO)then
         messag='Nearest surface station no. to each grid point'
         messag(63:70)='NEARS'
         call out(xdum,nears(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine rdisc(idathrm,isecm,lecho,umet,vmet,ipgt,htmix,ustar,&
                       xmonin,wstar,rmm,tempss,rhoss,qswss,irhss,&
                       ipcode,ptgdf,plexp,leof,kdathre,kse,kdathrb,ksb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212             RDISC
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Read ISCMET.DAT meteorological data for one hour
!
! --- UPDATE
! --- V6.1-V6.3     100212  (DGS): add nested MET grids
! --- V5.7-V6.1     050915  (DGS): allow for begin/end time format
!                                  resolved to seconds
!                                  (retain compatibility with
!                                  standard "ISCMET" versions)
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4, JULDAY,
!                                  GRDAY, INCR
! --- V5.4-V5.4     000602_4(DGS): add QAHRISC call
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
! --- V4.0-V5.0     971107  (DGS): add EOF recovery
!                   971107  (DGS): add record-skip logic from COMP to
!                                  find current date-time
!                   971107  (DGS): skip blank records
!
! --- INPUTS:
!       IDATHRM - integer       - Mid-pt date/hour of required
!                                 data (YYYYJJJHH)
!         ISECM - integer       - Mid-pt second of required data (SSSS)
!         LECHO - logical       - Flag controlling printing of input
!                                 meteorological data fields
!    Common block /COMPARM/ variables:
!         PLX0, PTG0, WSCALM
!    Common block /METHD/ variables:
!         NXM, NYM, NZM, NSSTA, NPSTA, LCALGRD, ANEMHT, ITIMES
!    Common block /DISPDAT/ variables:
!         IURB1, IURB2
!    Common block /GRID/ variables:
!         ZGPT
!    Common block /GRIDNEST/ variables:
!         ngrid
!    Parameters:
!         MXNZMP1, MXNX, MXNY, MXMETDOM, MXSS, IO6, IO7
!
! --- OUTPUT:
! 
!      UMET(mxnx,mxny,mxnz,mxmetdom)
!                        - real    - U component of the wind (m/s)
!                                    at each grid point
!      VMET(mxnx,mxny,mxnz,mxmetdom)
!                        - real    - V-component of the wind (m/s)
!           IPGT(mxnx,mxny,mxmetdom)
!                        - integer - PGT stability class
!          HTMIX(mxnx,mxny,mxmetdom)
!                        - real    - Mixing height (m)
!          USTAR(mxnx,mxny,mxmetdom)
!                        - real    - Surface friction velocity (m/s)
!         XMONIN(mxnx,mxny,mxmetdom)
!                        - real    - Monin-Obukhov length (m)
!          WSTAR(mxnx,mxny,mxmetdom)
!                        - real    - Convective velocity scale (m/s)
!            RMM(mxnx,mxny,mxmetdom)
!                        - real    - Precipitation rate (mm/hr)
!           TEMPSS(mxss,mxmetdom)
!                        - real    - Air temperature (deg. K) at
!                                    each surface met. station
!            RHOSS(mxss,mxmetdom)
!                        - real    - Air density (kg/m**3)
!            QSWSS(mxss,mxmetdom)
!                        - real    - Short wave solar radiation
!                                    (W/m**2)
!            IRHSS(mxss,mxmetdom)
!                        - integer - Relative humidity (percent)
!           IPCODE(mxss,mxmetdom)
!                        - integer - Precipitation code at each surface
!                                    station
!               PTGDF(2) - real    - Default potential temperature grad.
!                                    for stable classes (deg. K/m)
!                  PLEXP - real    - Power law exponent for wind profile
!                                    (used with single-point met file)
!                   LEOF - logical - End-Of-File flag
!                KDATHRE - integer - End date/hour of data (YYYYJJJHH)
!                    KSE - integer - End second of data (SSSS)
!                KDATHRB - integer - Begin date/hour of data (YYYYJJJHH)
!                    KSB - integer - Begin second of data (SSSS)
!
! --- RDISC called by:  COMP
! --- RDISC calls:      JULDAY, INCR, GRDAY, POWLAW, INITAR,
!                       INITAI, OUT, INITR2D, INITI2D, TPERIOD,
!                       YR4, QAHRISC, TCLOCK
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'methd.puf'
      include 'comparm.puf'
      include 'dispdat.puf'
      include 'grid.puf'
      include 'gridnest.puf'
!
      real umet(mxnx,mxny,mxnz,mxmetdom),vmet(mxnx,mxny,mxnz,mxmetdom)
      real htmix(mxnx,mxny,mxmetdom),ustar(mxnx,mxny,mxmetdom)
      real xmonin(mxnx,mxny,mxmetdom)
      real wstar(mxnx,mxny,mxmetdom),rmm(mxnx,mxny,mxmetdom)
      real tempss(mxss,mxmetdom),rhoss(mxss,mxmetdom)
      real qswss(mxss,mxmetdom)
      real ptgdf(2),plexp
!
      integer ipgt(mxnx,mxny,mxmetdom),irhss(mxss,mxmetdom)
      integer ipcode(mxss,mxmetdom)
      character*70 messag
      character plfield*5,swfield*9,rhfield*3
      logical lecho,ldate,leof
!
      data ldate/.false./
      data vonk/0.4/
! --- There should be only 1 met grid
      if(ngrid.NE.1) then
         write(io6,*)
         write(io6,*)'RDISC:  Nested grid information is bad'
         write(io6,*)'Expected 1 met grid, Found ',ngrid
         stop 'HALTED in RDISC -- See list file'
      else
! ---    Use explicit variable for '1'
         kg=1
      endif
!
! --- Determine IRU value (rural=0, urban=1) from header data
      iru=0
      if(ilandu(1,1,kg) .GE. iurb1 .AND.&
         ilandu(1,1,kg) .LE. iurb2) iru=1
1     if(itimes.EQ.1) then
! ---    ISC file with beginning & ending times
! -------------------------------------------------------------------
!  --    Read one record of met data in modified ISC format (ASCII)
         read(io7,4,end=999) iby,ibm,ibd,ibh,ksb,iey,iem,ied,ieh,kse,&
                             fvec,wspd,tmpk,kst,rmix,&
                             umix,ustr,xmon,z0m,ipc,pmmhr,dthdz,&
                             plfield,swfield,rhfield
4        format(2(4i2,i4),2f9.4,f6.1,i2,2f7.1,f9.4,f10.1,f8.4,i4,f7.2,&
                f10.5,a5,a9,a3)
! ---    Skip blank lines
         if(ibm.EQ.0 .OR. ibd.EQ.0) goto 1
! -------------------------------------------------------------------
! ---    Enforce YYYY format for year
         call YR4(io6,iby,ierrb)
         call YR4(io6,iey,ierre)
         if(ierrb.NE.0 .OR. ierrb.NE.0) stop 'Halted in RDISC'
         call julday(io6,iby,ibm,ibd,ijul)
         kdathrb=iby*100000+ijul*100+ibh
         call julday(io6,iey,iem,ied,ijul)
         kdathre=iey*100000+ijul*100+ieh
      else
! ---    Standard/extended ISC file
! -------------------------------------------------------------------
!  --    Read one record of met data in extended ISC format (ASCII)
         read(io7,5,end=999) iy,im,id,ih,fvec,wspd,tmpk,kst,rmix,&
                             umix,ustr,xmon,z0m,ipc,pmmhr,dthdz,&
                             plfield,swfield,rhfield
5        format(4i2,2f9.4,f6.1,i2,2f7.1,f9.4,f10.1,f8.4,i4,f7.2,&
                f10.5,a5,a9,a3)
! ---    Skip blank lines
         if(im.EQ.0 .OR. id.EQ.0) goto 1
! -------------------------------------------------------------------
! ---    Enforce YYYY format for year
         call YR4(io6,iy,ierrb)
         if(ierrb.NE.0) stop 'Halted in RDISC'
! ---    ISCMET.DAT uses a 1-24 clock (hour ending).  Change start
! ---    time to the beginning of the hour (subtract 1 hour)
         ih=ih-1
         call julday(io6,iy,im,id,ijul)
         kdathrb=iy*100000+ijul*100+ih
         ksb=0
! ---    Increment time 1 hour to get end time
         call incr(io6,iy,ijul,ih,1)
         kdathre=iy*100000+ijul*100+ih
         kse=0
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,kdathrb,idum,idum,idum,ksb)
      call TCLOCK(io6,kdathre,idum,idum,idum,kse)
! --- Check period of met data with mid-point of step
      call TPERIOD(0,idathrm,isecm,kdathrb,ksb,kdathre,&
                   kse,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         go to 1
      elseif(ilocate.EQ.-1) then
         write(io6,*)'ERROR in subr. RDISC -- current time not found',&
         ' in the MET data file'
         write(io6,*)' -- Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksb
         write(io6,*)'                           to ',kdathre,kse
         stop
      endif
! --- Apply QA rules to data from file
      call QAHRISC(fvec,wspd,tmpk,kst,rmix,umix,ustr,xmon,&
                   ipc,pmmhr,swfield,rhfield,ierr)
      if(ierr.NE.0) then
         write(io6,*)'ERROR in subr. RDISC -- Data are not complete ',&
         'in the ISCMET data file'
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksb
         write(io6,*)'                           to ',kdathre,kse
         write(*,*)
         stop 'Halted in RDISC -- see list file.'
      endif
! --- Set current mixing height for land use type (IRU)
      if(iru.EQ.0) then
         zmix=rmix
      else
         zmix=umix
      endif
! --- Check current value of roughness with value in /METHD/
      if(z0m.LE.0.0) then
! ---    Roughness length missing, keep previous value
         z0m=z0(1,1,kg)
      elseif(z0m.NE.z0(1,1,kg)) then
! ---    Valid roughness length changed, update array
! !!!    write(io6,*)'RDISC :  Roughness length changed'
! !!!    write(io6,*)'         Date-Hour      - ',kdathr
! !!!    write(io6,*)'         old,new z0(m)  - ',z0(1,1,kg),z0m
         call initr2d(z0m,mxnx,mxny,nxm(kg),nym(kg),z0(1,1,kg))
      endif
! --- Recover from zero u*, L provided during CALM conditions by
! --- assuming no flow (u*=0.0), and virtually no shear layer
! --- (M-O length small).
      if(ustr.LE.0.0) then
         ustr=0.0
         xmon=1.
         if(kst.LE.4) xmon=-1.
      endif
! --- Compute w* from u*,L,zmix
      wstr=0.
      if(xmon.LT.0.) then
         wstr=ustr*(-zmix/(vonk*xmon))**0.33333
      endif
! --- Get potential temperature gradient for stable conditions
      if(dthdz.LE.0.0) then
! ---    Use default potential temperature gradients from control file
         ptgdf(1)=ptg0(1)
         ptgdf(2)=ptg0(2)
      else
         ptgdf(1)=dthdz
         ptgdf(2)=ptgdf(1)
      endif
! --- Set the U, V wind component factors
      rad=fvec*.0174533
      ufac=sin(rad)
      vfac=cos(rad)
! --- Get power law exponent
      if(plfield.EQ.' ') then
! ---    Set power law value for current stability class
         if(kst.GE.7) then
            plexp=plx0(6)
         else
            plexp=plx0(kst)
         endif
      else
         read(plfield,'(bn,f5.0)') plexp
      endif
! --- Get short-wave radiation
      if(swfield.EQ.' ') then
! ---    Blank field; just set to zero
         qswrad=0.0
      else
         read(swfield,'(bn,f9.0)') qswrad
      endif
! --- Get relative humidity
      if(rhfield.EQ.' ') then
! ---    Blank field; just set to 85%
         irelhum=85
      else
         read(rhfield,'(bn,i3)') irelhum
      endif
! --- Pass to 3-D arrays
! --- Profile section
      if(wspd.GE.wscalm) then
! ---    Scale using power law (power law imposes lower speed of wscalm)
         do iz=1,nzm
            call powlaw(zgpt(iz),anemht,wspd,plexp,wsz)
            u=wsz*ufac
            call initr2d(u,mxnx,mxny,nxm(kg),nym(kg),umet(1,1,iz,kg))
            v=wsz*vfac
            call initr2d(v,mxnx,mxny,nxm(kg),nym(kg),vmet(1,1,iz,kg))
         enddo
      else
! ---    Pass the data without scaling and interpret "calms" later
         u=wspd*ufac
         v=wspd*vfac
         do iz=1,nzm
            call initr2d(u,mxnx,mxny,nxm(kg),nym(kg),umet(1,1,iz,kg))
            call initr2d(v,mxnx,mxny,nxm(kg),nym(kg),vmet(1,1,iz,kg))
         enddo
       endif
! --- Place original wind speed (without scaling) into "layer 2" for
! --- possible use;  this applies only if nzm=1
      if(nzm.EQ.1) then
         do iz=2,2
            u=wspd*ufac
            call initr2d(u,mxnx,mxny,nxm(kg),nym(kg),umet(1,1,iz,kg))
            v=wspd*vfac
            call initr2d(v,mxnx,mxny,nxm(kg),nym(kg),vmet(1,1,iz,kg))
         enddo
      endif
! --- Set the 2-D fields
      call initi2d(kst,mxnx,mxny,nxm(kg),nym(kg),ipgt(1,1,kg))
      call initr2d(ustr,mxnx,mxny,nxm(kg),nym(kg),ustar(1,1,kg))
      call initr2d(zmix,mxnx,mxny,nxm(kg),nym(kg),htmix(1,1,kg))
      call initr2d(xmon,mxnx,mxny,nxm(kg),nym(kg),xmonin(1,1,kg))
      call initr2d(wstr,mxnx,mxny,nxm(kg),nym(kg),wstar(1,1,kg))
      call initr2d(pmmhr,mxnx,mxny,nxm(kg),nym(kg),rmm(1,1,kg))
! --- Set the 1-D fields
      call initar(tmpk,nssta(kg),tempss(1,kg))
      call initar(1.28,nssta(kg),rhoss(1,kg))
      call initar(qswrad,nssta(kg),qswss(1,kg))
      call initai(irelhum,nssta(kg),irhss(1,kg))
      call initai(ipc,nssta(kg),ipcode(1,kg))
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ MET. FIELDS BEGINNING: ',kdathrb,ksb
         write(io6,*)'------            AND ENDING: ',kdathre,kse
         write(io6,*)
! ---    3-D fields
         do 101 iz=1,nzm
         messag='U-component (m/s) -- Level: '
         write(messag(29:31),'(i3)')iz
         call out(umet(1,1,iz,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag(1:1)='V'
         call out(vmet(1,1,iz,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
101      continue
!
! ---    2-D fields
         messag='PGT stability class'
         call out(xdum,ipgt(1,1,kg),2,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Friction velocity (m/s)'
         call out(ustar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Mixing height (m)'
         call out(htmix(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Monin-Obukhov length (m)'
         call out(xmonin(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Convective velocity scale (m/s)'
         call out(wstar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         if(npsta(kg).gt.0)then
            messag='Precipitation rate (mm/hr)'
            call out(rmm(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
         endif
!
! ---    1-D fields
         write(io6,*)'TEMPSS (deg. K) = ',(tempss(n,kg),n=1,nssta(kg))
         write(io6,*)'RHOSS (kg/m**3)  = ',(rhoss(n,kg),n=1,nssta(kg))
         write(io6,*)'QSWSS (W/m**2)   = ',(qswss(n,kg),n=1,nssta(kg))
         write(io6,*)'IRHSS (percent)  = ',(irhss(n,kg),n=1,nssta(kg))
         if(npsta(kg).gt.0)write(io6,*)'IPCODE           = ',&
          (ipcode(n,kg),n=1,nssta(kg))
      endif
!
      return
999   write(io6,*)'RDISC: End-Of-File found in ISCMET file'
      leof=.TRUE.
      return
      end
!----------------------------------------------------------------------
      subroutine qahrisc(fvec,wspd,tmpk,kst,rmix,umix,ustr,xmon,&
                         ipc,pmmhr,swfield,rhfield,ierr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090326           QAHRISC
!                D. Strimaitis
!
! --- PURPOSE:  QA ISCMET.DAT meteorological data for one hour
!
! --- UPDATE
! --- V5.4-V6.264  090326  (DGS): Add MCHEM=5 (no met data needed)
!
! --- INPUTS:
!          FVEC - real          - Flow vector (degrees CW from N)
!          WSPD - real          - Wind speed (m/s)
!          TMPK - real          - Temperature (K)
!           KST - integer       - Stability class
!          RMIX - real          - Rural mixing ht. (m)
!          UMIX - real          - Urban mixing ht. (m)
!          USTR - real          - Friction velocity (m/s)
!          XMON - real          - Monin-Obukhov length (m)
!           IPC - integer       - Precipitation type code (0-45)
!         PMMHR - real          - Precipitation rate (mm/hr)
!       SWFIELD - character     - Short-wave solar radiation (W/m^2)
!       RHFIELD - character     - Relative humidity (%)
!
!    Common block /FLAGS/ variables:
!         MCHEM, MWET, MDRY, MFOG
!    Parameters:
!         IO6
!
! --- OUTPUT:
!
!          IERR - integer       - Error flag (0: no errors)
!
! --- QAHRISC called by:  RDISC
! --- QAHRISC calls:      none
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'flags.puf'
      character swfield*9,rhfield*3
      ierr=0
! --- Check primary fields
      if(fvec.GE.360.1 .OR. fvec.LE.-360.1) then
         write(io6,*) 'QAHRISC --- Invalid Flow Vector (deg): ',fvec
         ierr=1
         return
      endif
      if(wspd.GE.99. .OR. wspd.LT.0.) then
         write(io6,*) 'QAHRISC --- Invalid Wind Speed (m/s): ',wspd
         ierr=1
         return
      endif
      if(tmpk.GE.375. .OR. tmpk.LE.200.) then
         write(io6,*) 'QAHRISC --- Invalid Temperature (K): ',tmpk
         ierr=1
         return
      endif
      if(kst.GT.7 .OR. kst.LE.0) then
         write(io6,*) 'QAHRISC --- Invalid Stability Class: ',kst
         ierr=1
         return
      endif
      if(rmix.LT.0. .OR. umix.LT.0.) then
         write(io6,*) 'QAHRISC --- Invalid Mixing Hts (m): ',rmix,umix
         ierr=1
         return
      endif
! --- U_star may be zero for a CALM, but always must be positive
      if(ustr.LT.0.) then
         write(io6,*) 'QAHRISC --- Invalid U_star (m/s): ',ustr
         ierr=1
         return
      elseif(ustr.EQ.0. .AND. wspd.GE.1.0 .AND. MDRY.GT.0) then
         write(io6,*) 'QAHRISC --- Invalid U_star (m/s): ',ustr
         ierr=1
         return
      endif
! --- Precip type is used for frozen/non-frozen choice, and zero is
! --- a valid value, so merely check range
      if(ipc.LT.0 .OR. ipc.GT.45) then
         write(io6,*) 'QAHRISC --- Invalid Precip Type Code: ',ipc
         ierr=1
         return
      endif
! --- Precip rate cannot be negative, and should not exceed 500mm/hr
      if(pmmhr.GE.500. .OR. pmmhr.LT.0.) then
         write(io6,*) 'QAHRISC --- Invalid Precip (mm/hr): ',pmmhr
         ierr=1
         return
      endif
! --- Extract and test short-wave radiation
      if(swfield.EQ.'         ' .AND. mchem.GT.0 .AND. mchem.NE.5) then
! ---    Blank field is not acceptable for chem transformation
         write(io6,*) 'QAHRISC --- SW Radiation is blank '
         ierr=1
         return
      else
         read(swfield,'(bn,f9.0)') qswrad
         if(qswrad.LT.0. .OR. qswrad.GT.1500.) then
            write(io6,*) 'QAHRISC --- Invalid SW Rad (W/m^2): ',qswrad
            ierr=1
            return
         endif
      endif
! --- Extract and test Relative Humidity
      if(rhfield.EQ.'   ' .AND. ((mchem.GT.0 .AND. mchem.NE.5) .OR.&
                                  mfog.GT.0)) then
! ---    Blank field is not acceptable for chem transformation
         write(io6,*) 'QAHRISC --- Relative Humidity is blank '
         ierr=1
         return
      else
         read(rhfield,'(bn,i3)') irelhum
         if(irelhum.LT.0. .OR. irelhum.GT.100.) then
            write(io6,*) 'QAHRISC --- Invalid Humidity (%): ',irelhum
            ierr=1
            return
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine met3(lecho)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 121203              MET3
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Set the header records normally contained in the
!               CALMET file (using PLMMET data file).
!               Also, set the wind profile power-law exponents if
!               not provided in control file.
!
! --- UPDATE
!
! --- V6.301-6.42_x1.0 121203  : Change IESMET assignment from scalar
!                                  to array
!
! --- V6.3-V6.301   100827  (DGS): Allow nested CALMET files to have
!                                  different time-periods
! --- V6.1-V6.3     100212  (DGS): Add nested met grid processing
!                   100212  (DGS): Change met station coordinates from
!                                  absolute to relative to grid origin
! --- V5.7-V6.1     050915  (DGS): Use c*16 variable VERPLM in new
!                                  /DATASET/ common to identify version.
!                                  Implement full begin/end time
!                                  processing.
!                                  Add METQA call.
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4, JULDAY,
!                                  GRDAY, INCR
!                   030402  (DGS): add map projection, datum
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
! --- V5.0-V5.0     980304  (DGS): add station elevation,lat,lon
! --- V4.0-V5.0     971107  (DGS): pass start-time from met file
!                                  for use if METRUN=1
!
! --- INPUTS:
!         LECHO - logical       - Control variable for output of header
!                                 record information
!
!     Common block /DATASET/ variables:
!           VERPLM
!     Common block /GRID/ variables:
!           NX, NY, NZ, DGRID, XORIG, YORIG, ZFACE(mxnzp1)
!     Common block /MAP/ variables:
!           iutmzn,feast,fnorth,
!           rlat0,rlon0,xlat1,xlat2,
!           pmap,utmhem,datum,daten
!     Common block /METHD/ variables:
!           ANEMHT, ILANDUIN, Z0IN, XLAIIN
!     Common block /GRIDNEST/ variables:
!           ngrid
!     Parameters:
!           MXNZP1, MXSS, MXUS, MXPS, MXNX, MXNY, MXMETDOM, IO6
!
! --- OUTPUT:
!     Common block /COMPARM/ variables:
!           PLX0
!     Common block /METHD/ variables:
!           nxm(mxmetdom),nym(mxmetdom),nzm,
!           xgridm(mxmetdom),xgridmi(mxmetdom),
!           xorigm(mxmetdom),yorigm(mxmetdom),
!           xbtzm,rlat0m,rlon0m,xlat1m,xlat2m,nssta(mxmetdom),
!           nusta(mxmetdom),npsta(mxmetdom),nlu,iwat1,iwat2,
!           zfacem(mxnzp1),xssta(mxss,mxmetdom),yssta(mxss,mxmetdom),
!           xlatss(mxss,mxmetdom),xlonss(mxss,mxmetdom),
!           xusta(mxus,mxmetdom),yusta(mxus,mxmetdom),
!           xpsta(mxps,mxmetdom),ypsta(mxps,mxmetdom),
!           z0(mxnx,mxny,mxmetdom),ilandu(mxnx,mxny,mxmetdom),
!           elev(mxnx,mxny,mxmetdom),xlai(mxnx,mxny,mxmetdom),
!           nears(mxnx,mxny,mxmetdom),lcalgrd,itimes,
!           ibymet(mxmetdom),ibmmet(mxmetdom),ibdmet(mxmetdom),
!           ibjdmet(mxmetdom),ibhmet(mxmetdom),ibsmet(mxmetdom),
!           ieymet(mxmetdom),iemmet(mxmetdom),iedmet(mxmetdom),
!           iejdmet(mxmetdom),iehmet(mxmetdom),iesmet(mxmetdom),
!           iutmznm,feastm,fnorthm,pmapm,datumm,datenm,utmhemm
!
! --- MET3 called by:  SETUP
! --- MET3 calls:      JULDAY, INCR, GRDAY, INITR2D, OUT, INITI2D,
!                       YR4, METQA
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
      character*80 title(3)
      character*70 messag
      character*8 clab1,clab2
      logical ldate,lecho
!
! --- Include common block /COMPARM/ -- Computation parameters
      include 'comparm.puf'
! --- Include common block /DATASET/  -- Input file dataset version
      include 'dataset.puf'
! --- Include common block /DISPDAT/ -- Dispersion-related data
      include 'dispdat.puf'
! --- Include common block /METHD/ -- CALMET header record data
      include 'methd.puf'
! --- Include common block /GRID/  -- Grid data from control file
      include 'grid.puf'
! --- Include common block /MAP/  -- Projection data from control file
      include 'map.puf'
! --- Include common block /GRIDNEST/  -- Nested grid configuration
      include 'gridnest.puf'
!
      data ldate/.false./
! --- There should be only 1 met grid
      if(ngrid.NE.1) then
         write(io6,*)
         write(io6,*)'MET3:  Nested grid information is bad'
         write(io6,*)'Expected 1 met grid, Found ',ngrid
         stop 'HALTED in MET3 -- See list file'
      else
! ---    Use explicit variable for '1'
         kg=1
      endif
!
! --- Present code recognizes only 1 dataset version (1.0)
      itimes=0
      if(verplm.NE.'1.0') then
         write(io6,*)'FATAL Error in MET3 -- bad dataset version'
         write(io6,*)'Expected PLMMET dataset version 1.0'
         write(io6,*)'Found PLMMET dataset version ',verplm
         stop 'Halted in MET3 -- see list file'
      endif
!
! --- Read PLMMET header record
! ---------------------------------------------------------------------
      read(io7,'(a80)') title(3)
!                                                              RECORD 1
! ---------------------------------------------------------------------
! --- record #1 - run title --
! --- Set three title-lines
      title(1)='Constant Meteorological Fields'
      title(2)='Created from PLMMET.DAT'
!
! --- record #2 - run control parameters --
! --- (vermet, levmet are both 8 bytes):
! ---  vermet,levmet,ibyr,ibmo,ibdy,ibhr,irtype,
! ---  nxm, nym, nzm, xgridm, xorigm, yorigm, iutmznm, iwfcod, nssta,
! ---  nusta, npsta, nowsta, nlu, iwat1, iwat2, lcalgrd
!
! --- Fill in most first
      irtype=1
      nxm(kg)=nx
      nym(kg)=ny
      nzm=nz
      xgridm(kg)=dgrid
      xgridmi(kg)=1./dgrid
      xorigm(kg)=xorig
      yorigm(kg)=yorig
      pmapm=pmap
      datumm=datum
      datenm=daten
      iutmznm=iutmzn
      utmhemm=utmhem
      rlat0m=rlat0
      rlon0m=rlon0
      xlat1m=xlat1
      xlat2m=xlat2
      feastm=feast
      fnorthm=fnorth
      iwfcod=1
      nssta(kg)=1
      nusta(kg)=1
      npsta(kg)=1
      nowsta(kg)=0
      nlu=10
      iwat1=500
      iwat2=599
      lcalgrd=.FALSE.
! --- Now read date-time from first data record (must rewind!)
! ---------------------------------------------------------------------
      read(io7,'(4i2)') ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg)
      rewind(io7)
      read(io7,'(a80)') title(3)
!                                                              RECORD 2
! ---------------------------------------------------------------------
! --- Enforce YYYY format for year
      call YR4(io6,ibymet(kg),ierr)
      if(ierr.NE.0) then
         write(*,*)
         stop 'Halted in MET3'
      endif
! --- PLMMET.DAT uses a 1-24 clock (hour ending).  Convert to time
! --- beginning by subtracting 1 from the hour (0-23)
      ibhmet(kg)=ibhmet(kg)-1
      call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
      ibsmet(kg)=0
! --- Use "default" run length (hours) to mark the end of data
! --- Assume this will be 5 years, so set IRLG=43848 hours
      irlgmet=43848
      ieymet(kg)=ibymet(kg)
      iemmet(kg)=ibmmet(kg)
      iedmet(kg)=ibdmet(kg)
      iejdmet(kg)=ibjdmet(kg)
      iehmet(kg)=ibhmet(kg)
      call INCR(io6,ieymet(kg),iejdmet(kg),iehmet(kg),irlgmet)
      call GRDAY(io6,ieymet(kg),iejdmet(kg),iemmet(kg),iedmet(kg))
! --- V6.42_x1.0, Level 121203
      iesmet(kg)=0
      if(LECHO)then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*) title(1)
         write(io6,*) title(2)
         write(io6,*) title(3)
         write(io6,*)
         write(io6,*)'-- Data begin at:'
         write(io6,*)' IBYR    = ',ibymet(kg)
         write(io6,*)' IBMO    = ',ibmmet(kg)
         write(io6,*)' IBDY    = ',ibdmet(kg)
         write(io6,*)' IBJUL   = ',ibjdmet(kg)
         write(io6,*)' IBHR    = ',ibhmet(kg)
         write(io6,*)' IBSEC   = ',ibsmet(kg)
         if(verplm.EQ.'1.0') then
            write(io6,*)'-- Data end at:  ?'
         else
            write(io6,*)'-- Data end at:'
         endif
         write(io6,*)' IEYR    = ',ieymet(kg)
         write(io6,*)' IEMO    = ',iemmet(kg)
         write(io6,*)' IEDY    = ',iedmet(kg)
         write(io6,*)' IEJUL   = ',iejdmet(kg)
         write(io6,*)' IEHR    = ',iehmet(kg)
         write(io6,*)' IESEC   = ',iesmet(kg)
         write(io6,*)
         write(io6,*)' IRTYPE  = ',irtype
         write(io6,*)' LCALGRD = ',lcalgrd
         write(io6,*)' NXM     = ',nxm(kg)
         write(io6,*)' NYM     = ',nym(kg)
         write(io6,*)' NZM     = ',nzm
         write(io6,*)' XGRIDM  = ',xgridm(kg)
         write(io6,*)' XGRIDMI = ',xgridmi(kg)
         write(io6,*)' XORIGM  = ',xorigm(kg)
         write(io6,*)' YORIGM  = ',yorigm(kg)
         write(io6,*)' PMAPM   = ',pmapm
         write(io6,*)' DATUMM  = ',datumm
         write(io6,*)' DATENM  = ',datenm
         write(io6,*)' IUTMZNM = ',iutmznm
         write(io6,*)' UTMHEMM = ',utmhemm
         write(io6,*)' XLAT1M  = ',xlat1m
         write(io6,*)' XLAT2M  = ',xlat2m
         write(io6,*)' RLAT0M  = ',rlat0m
         write(io6,*)' RLON0M  = ',rlon0m
         write(io6,*)' FEASTM  = ',feastm
         write(io6,*)' FNORTHM = ',fnorthm
         write(io6,*)' IWFCOD  = ',iwfcod
         write(io6,*)' NSSTA   = ',nssta(kg)
         write(io6,*)' NUSTA   = ',nusta(kg)
         write(io6,*)' NPSTA   = ',npsta(kg)
         write(io6,*)' NOWSTA  = ',nowsta(kg)
         write(io6,*)' NLU     = ',nlu
         write(io6,*)' IWAT1   = ',iwat1
         write(io6,*)' IWAT2   = ',iwat2
         write(io6,*)' ANEMHT  = ',anemht
      endif
!
      nzmp1=nzm+1
      if(mxnzp1.lt.nzmp1)then
         write(io6,*)'ERROR in subr. MET3 -- dimension of ZFACEM ',&
         'array is too small for no. layers in ISCMET file'
         write(io6,*)'MXNZP1 = ',mxnzp1,' NZMP1 = ',nzmp1
         write(*,*)
         stop 'Halted in MET3 -- see list file.'
      endif
!
! --- record #3 - cell face heights (NZ + 1 words)
!
! --- Grab the face heights from the control file
      do i=1,nzmp1
         zfacem(i)=zface(i)
      enddo
!
      if(LECHO)then
         write(io6,203)(zfacem(n),n=1,nzmp1)
203      format(1x,'ZFACEM  = ',10(f9.3,', ')/)
      endif
! --- Perform QA checks on header record variables
      call METQA
!
! --- records #4 & 5 - x, y coordinates of surface stations
! --- (NSSTA words each record)
      if(nssta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xssta(1,kg)=0.0
         yssta(1,kg)=0.0
         if(LECHO)then
            clab1='XSSTA'
            clab2='YSSTA'
            write(io6,*)clab1,' = ',(xssta(n,kg),n=1,nssta(kg))
            write(io6,*)clab2,' = ',(yssta(n,kg),n=1,nssta(kg))
         endif
      endif
!
! --- records #4a & 5a - lat, lon coordinates of surface stations
! --- (NSSTA words each record)
      if(nssta(kg).gt.0)then
! ---    Grab the lat,lon from control file inputs
         xlatss(1,kg)=xlatin
         xlonss(1,kg)=xlonin
         if(LECHO)then
            clab1='XLATSS'
            clab2='YLONSS'
            write(io6,*)clab1,' = ',(xlatss(n,kg),n=1,nssta(kg))
            write(io6,*)clab2,' = ',(xlonss(n,kg),n=1,nssta(kg))
         endif
      endif
!
! --- records #6 & 7 - x, y coordinates of upper air stations
! --- (NUSTA words each record)
      if(nusta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xusta(1,kg)=0.0
         yusta(1,kg)=0.0
         if(LECHO)then
            clab1='XUSTA'
            clab2='YUSTA'
            write(io6,*)clab1,' = ',(xusta(n,kg),n=1,nusta(kg))
            write(io6,*)clab2,' = ',(yusta(n,kg),n=1,nusta(kg))
         endif
      endif
!
! --- records #8 & 9 - x, y coordinates of precipitation stations
! --- (NPSTA words each record)
      if(npsta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xpsta(1,kg)=0.0
         ypsta(1,kg)=0.0
         if(LECHO)then
            clab1='XPSTA'
            clab2='YPSTA'
            write(io6,*)clab1,' = ',(xpsta(n,kg),n=1,npsta(kg))
            write(io6,*)clab2,' = ',(ypsta(n,kg),n=1,npsta(kg))
         endif
      endif
!
! --- Fill the corresponding arrays:
!
! --- record #10 - surface roughness lengths (NX * NY words)
      call initr2d(z0in,mxnx,mxny,nxm(kg),nym(kg),z0(1,1,kg))
      if(LECHO)then
         messag='Surface roughness lengths (m)'
         messag(63:70)='z0'
         call out(z0(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #11 - land use categories (NX * NY words)
      call initi2d(ilanduin,mxnx,mxny,nxm(kg),nym(kg),ilandu(1,1,kg))
      if(LECHO)then
         messag='Land use categories'
         messag(63:70)='ILANDU'
         call out(xdum,ilandu(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #12 - elevations (NX * NY words)
! --- Set grid elevation to ELEVIN from control file
      call initr2d(elevin,mxnx,mxny,nxm(kg),nym(kg),elev(1,1,kg))
      if(LECHO)then
         messag='Terrain heights (m)'
         messag(63:70)='TERR'
         call out(elev(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #13 - leaf area index (NX * NY words)
      call initr2d(xlaiin,mxnx,mxny,nxm(kg),nym(kg),xlai(1,1,kg))
      if(LECHO)then
         messag='Leaf area index'
         messag(63:70)='XLAI'
         call out(xlai(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #14 - nearest surface station to each grid point
!                  (NX * NY words)
      call initi2d(1,mxnx,mxny,nxm(kg),nym(kg),nears(1,1,kg))
      if(LECHO)then
         messag='Nearest surface station no. to each grid point'
         messag(63:70)='NEARS'
         call out(xdum,nears(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine rdplm(idathrm,isecm,lecho,umet,vmet,svprf,ipgt,htmix,&
                       ustar,xmonin,wstar,rmm,tempss,rhoss,qswss,&
                       irhss,ipcode,ptgdf,plexp,leof,&
                       kdathre,kse,kdathrb,ksb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212             RDPLM
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Read PLMMET.DAT meteorological data for one hour
!
! --- UPDATE
! --- V6.1-V6.3     100212  (DGS): add nested MET grids
! --- V5.7-V6.1     050915  (DGS): add start/end times resolved to
!                                  seconds (end time is not contained
!                                  in PLMMET)
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4, JULDAY,
!                                  GRDAY, INCR
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
! --- V5.2-V5.3     991222a (DGS): Pass data to SVPRF only if MTURBVW=4
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
! --- V5.0-V5.0     980430  (DGS): 'use' DFIELD (compiler warning)
! --- V4.0-V5.0     971107  (DGS): add EOF recovery
!                   971107  (DGS): add record-skip logic from COMP to
!                                  find current date-time
!                   971107  (DGS): skip blank records
!
! --- INPUTS:
!       IDATHRM - integer       - Mid-pt date/hour of required
!                                 data (YYYYJJJHH)
!         ISECM - integer       - Mid-pt second of required data (SSSS)
!         LECHO - logical       - Flag controlling printing of input
!                                 meteorological data fields
!    Common block /COMPARM/ variables:
!         PLX0, PTG0, WSCALM
!    Common block /METHD/ variables:
!         NXM, NYM, NZM, NSSTA, NPSTA, LCALGRD, ANEMHT
!    Common block /DISPDAT/ variables:
!         IURB1, IURB2
!    Common block /FLAGS/ variables:
!         MTURBVW
!    Common block /GRID/ variables:
!         ZGPT
!    Common block /GRIDNEST/ variables:
!         ngrid
!    Parameters:
!         MXNX, MXNY, MXNZ, MXNZMP1, MXPRFZ, MXMETDOM, MXSS, IO6, IO7
!
! --- OUTPUT:
!
!      UMET(mxnx,mxny,mxnz,mxmetdom)
!                        - real    - U component of the wind (m/s)
!                                    at each grid point
!      VMET(mxnx,mxny,mxnz,mxmetdom)
!                        - real    - V-component of the wind (m/s)
!          SVPRF(mxprfz) - real    - 1-D sigma-v profile (m/s) (NZM
!                                    levels filled with 1 level read)
!           IPGT(mxnx,mxny,mxmetdom)
!                        - integer - PGT stability class
!          HTMIX(mxnx,mxny,mxmetdom)
!                        - real    - Mixing height (m)
!          USTAR(mxnx,mxny,mxmetdom)
!                        - real    - Surface friction velocity (m/s)
!         XMONIN(mxnx,mxny,mxmetdom)
!                        - real    - Monin-Obukhov length (m)
!          WSTAR(mxnx,mxny,mxmetdom)
!                        - real    - Convective velocity scale (m/s)
!            RMM(mxnx,mxny,mxmetdom)
!                        - real    - Precipitation rate (mm/hr)
!           TEMPSS(mxss,mxmetdom)
!                        - real    - Air temperature (deg. K) at
!                                    each surface met. station
!            RHOSS(mxss,mxmetdom)
!                        - real    - Air density (kg/m**3)
!            QSWSS(mxss,mxmetdom)
!                        - real    - Short wave solar radiation
!                                    (W/m**2)
!            IRHSS(mxss,mxmetdom)
!                        - integer - Relative humidity (percent)
!           IPCODE(mxss,mxmetdom)
!                        - integer - Precipitation code at each surface
!                                    station
!               PTGDF(2) - real    - Default potential temperature grad.
!                                    for stable classes (deg. K/m)
!                  PLEXP - real    - Power law exponent for wind profile
!                                    (used with single-point met file)
!                   LEOF - logical - End-Of-File flag
!                KDATHRE - integer - End date/hour of data (YYYYJJJHH)
!                    KSE - integer - End second of data (SSSS)
!                KDATHRB - integer - Begin date/hour of data (YYYYJJJHH)
!                    KSB - integer - Begin second of data (SSSS)
!
! --- RDPLM called by:  COMP
! --- RDPLM calls:      JULDAY, INCR, GRDAY, STAB2L, POWLAW, INITAR,
!                       INITAI, OUT, INITR2D, INITI2D, TPERIOD,
!                       YR4, TCLOCK
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'methd.puf'
      include 'comparm.puf'
      include 'dispdat.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'gridnest.puf'
!
      real umet(mxnx,mxny,mxnz,mxmetdom),vmet(mxnx,mxny,mxnz,mxmetdom)
      real htmix(mxnx,mxny,mxmetdom),ustar(mxnx,mxny,mxmetdom)
      real xmonin(mxnx,mxny,mxmetdom)
      real wstar(mxnx,mxny,mxmetdom),rmm(mxnx,mxny,mxmetdom)
      real svprf(mxprfz)
      real tempss(mxss,mxmetdom),rhoss(mxss,mxmetdom)
      real qswss(mxss,mxmetdom)
      real ptgdf(2),plexp
!
      integer ipgt(mxnx,mxny,mxmetdom),irhss(mxss,mxmetdom)
      integer ipcode(mxss,mxmetdom)
!
      character*70 messag
      character buff(2),class(6),pfield*5,tfield*5,dfield*10
! --- Set character variables for "extended" data (p-code, p-rate,
! --- qswrad, relhum)
      character pcfield*3,prfield*9,swfield*9,rhfield*3
!
      logical lecho,ldate,leof
!
      data ldate/.false./
      data vonk/0.4/,dtor/0.0174533/
!
! --- Set stability class matrix
      data class/'A','B','C','D','E','F'/
! --- There should be only 1 met grid
      if(ngrid.NE.1) then
         write(io6,*)
         write(io6,*)'RDPLM:  Nested grid information is bad'
         write(io6,*)'Expected 1 met grid, Found ',ngrid
         stop 'HALTED in RDPLM -- See list file'
      else
! ---    Use explicit variable for '1'
         kg=1
      endif
! --- Set default values for "extended" data
1     qswrad=0.0
      irelhum=0
      pmmhr=0.0
      ipc=0
! -------------------------------------------------------------------
!  -- Read one record of met data in PLMMET format (ASCII)
2     read(io7,5,end=999) iy,im,id,ih,tmpc,wspd,iwd,buff,zmix,&
                          sigtha,pfield,tfield,dfield,&
! --- Extended data section
                          pcfield,prfield,swfield,rhfield
5     format(BN,4i2,f3.0,f5.0,i4,2a1,2f5.0,2a5,a10,&
! --- Extended format section
                          a3,a9,a9,a3)
!
! --- Skip blank lines
      if(im.EQ.0 .OR. id.EQ.0) goto 2
! -------------------------------------------------------------------
! --- Enforce YYYY format for year
      call YR4(io6,iy,ierr)
      if(ierr.NE.0) then
         write(*,*)
         stop 'Halted in RDPLM'
      endif
! --- PLMMET.DAT uses a 1-24 clock (hour ending).  Change start
! --- time to the beginning of the hour (subtract 1 hour)
      ih=ih-1
      call julday(io6,iy,im,id,ijul)
      kdathrb=iy*100000+ijul*100+ih
      ksb=0
! --- Increment time 1 hour to get end time
      call incr(io6,iy,ijul,ih,1)
      kdathre=iy*100000+ijul*100+ih
      kse=0
! --- Condition times
      idum=-999
      call TCLOCK(io6,kdathrb,idum,idum,idum,ksb)
      call TCLOCK(io6,kdathre,idum,idum,idum,kse)
! --- Check period of met data with mid-point of step
      call TPERIOD(0,idathrm,isecm,kdathrb,ksb,kdathre,&
                   kse,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         go to 1
      elseif(ilocate.EQ.-1) then
         write(io6,*)'ERROR in subr. RDPLM -- current time not found',&
         ' in the MET data file'
         write(io6,*)' -- Current date/hour,sec   = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksb
         write(io6,*)'                           to ',kdathre,kse
         stop 'Halted in RDPLM -- see list file'
      endif
! --- Read stability class from buff
      if(buff(1).EQ.' ') buff(1)=buff(2)
      do isc=1,6
        if(buff(1).EQ.class(isc)) kst=isc
      enddo
! --- Estimate Monin-O length from Golder relation
! --- (not available in PLMMET.DAT)
      if(kst.EQ.4) then
         xmon=9990.
      else
         call stab2l(kst,z0(1,1,kg),eli)
         xmon=1./eli
      endif
! --- Estimate u* (not available in PLMMET.DAT)
      ustr=vonk*wspd/(alog(anemht/z0(1,1,kg))-psim(xmon,anemht))
! --- Compute w* from u*,L,zmix
      wstr=0.
      if(xmon.LT.0.) then
         wstr=ustr*(-zmix/(vonk*xmon))**0.33333
      endif
! --- Convert temperature to deg. K
      tmpk=tmpc+273.16
! --- Convert sigtha (deg) to sigma-v (m/s) (note: missing sigtha
! --- corresponds to a blank field, which is read as a zero)
      if(sigtha.GT.0.0 .AND. wspd.GE.0.0) then
         sigv=sigtha*dtor*wspd
      else
         sigv=-999.
      endif
! --- Get power law exponent
      if(pfield.EQ.' ') then
! ---    Set default power law value for current stability class
         plexp=plx0(kst)
      else
         read(pfield,'(bn,f5.0)') plexp
      endif
! --- Get potential temperature gradient for stable conditions
      if(tfield.EQ.' ') then
! ---    Use default potential temperature gradients from control file
         ptgdf(1)=ptg0(1)
         ptgdf(2)=ptg0(2)
      else
         read(tfield,'(bn,f5.0)') ptgdf(1)
         ptgdf(2)=ptgdf(1)
      endif
! --- 'Use' decay field (compiler warning)
      dfield=dfield
! --- Get extended data, when provided
! --- Precipitation code
      if(pcfield.NE.' ') then
         read(pcfield,'(bn,i3)') ipc
      endif
! --- Precipitation rate
      if(prfield.NE.' ') then
         read(prfield,'(bn,f9.0)') pmmhr
      endif
! --- Short-wave radiation
      if(swfield.NE.' ') then
         read(swfield,'(bn,f9.0)') qswrad
      endif
! --- Relative humidity
      if(rhfield.NE.' ') then
         read(rhfield,'(bn,i3)') irelhum
      endif
! --- Set the U, V wind component factors
      rad=(float(iwd)+180.)*.0174533
      ufac=sin(rad)
      vfac=cos(rad)
! --- Pass to 3-D arrays
! --- Profile section
      if(wspd.GE.wscalm) then
! ---    Scale using power law (power law imposes lower speed of wscalm)
         do iz=1,nzm
            call powlaw(zgpt(iz),anemht,wspd,plexp,wsz)
            u=wsz*ufac
            call initr2d(u,mxnx,mxny,nxm(kg),nym(kg),umet(1,1,iz,kg))
            v=wsz*vfac
            call initr2d(v,mxnx,mxny,nxm(kg),nym(kg),vmet(1,1,iz,kg))
         enddo
      else
! ---    Pass the data without scaling, and treat "calms" later
         u=wspd*ufac
         v=wspd*vfac
         do iz=1,nzm
            call initr2d(u,mxnx,mxny,nxm(kg),nym(kg),umet(1,1,iz,kg))
            call initr2d(v,mxnx,mxny,nxm(kg),nym(kg),vmet(1,1,iz,kg))
         enddo
       endif
! --- Set the 2-D fields
      call initi2d(kst,mxnx,mxny,nxm(kg),nym(kg),ipgt(1,1,kg))
      call initr2d(ustr,mxnx,mxny,nxm(kg),nym(kg),ustar(1,1,kg))
      call initr2d(zmix,mxnx,mxny,nxm(kg),nym(kg),htmix(1,1,kg))
      call initr2d(xmon,mxnx,mxny,nxm(kg),nym(kg),xmonin(1,1,kg))
      call initr2d(wstr,mxnx,mxny,nxm(kg),nym(kg),wstar(1,1,kg))
      call initr2d(pmmhr,mxnx,mxny,nxm(kg),nym(kg),rmm(1,1,kg))
! --- Set the 1-D fields
      if(mturbvw.EQ.4) call initar(sigv,nzm,svprf)
      call initar(tmpk,nssta(kg),tempss(1,kg))
      call initar(1.28,nssta(kg),rhoss(1,kg))
      call initar(qswrad,nssta(kg),qswss(1,kg))
      call initai(irelhum,nssta(kg),irhss(1,kg))
      call initai(ipc,nssta(kg),ipcode(1,kg))
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ MET. FIELDS BEGINNING: ',kdathrb,ksb
         write(io6,*)'------            AND ENDING: ',kdathre,kse
         write(io6,*)
!
! ---    3-D fields
         do 101 iz=1,nzm
         messag='U-component (m/s) -- Level: '
         write(messag(29:31),'(i3)')iz
         call out(umet(1,1,iz,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag(1:1)='V'
         call out(vmet(1,1,iz,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
101      continue
!
! ---    2-D fields
         messag='PGT stability class'
         call out(xdum,ipgt(1,1,kg),2,1,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Friction velocity (m/s)'
         call out(ustar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Mixing height (m)'
         call out(htmix(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Monin-Obukhov length (m)'
         call out(xmonin(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Convective velocity scale (m/s)'
         call out(wstar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         if(npsta(kg).gt.0)then
            messag='Precipitation rate (mm/hr)'
            call out(rmm(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
         endif
!
! ---    1-D fields
         write(io6,*)'SVPRF (m/s)     = ',(svprf(n),n=1,nzm)
         write(io6,*)'TEMPSS (deg. K) = ',(tempss(n,kg),n=1,nssta(kg))
         write(io6,*)'RHOSS (kg/m**3)  = ',(rhoss(n,kg),n=1,nssta(kg))
         write(io6,*)'QSWSS (W/m**2)   = ',(qswss(n,kg),n=1,nssta(kg))
         write(io6,*)'IRHSS (percent)  = ',(irhss(n,kg),n=1,nssta(kg))
         if(npsta(kg).gt.0)write(io6,*)'IPCODE           = ',&
          (ipcode(n,kg),n=1,nssta(kg))
      endif
!
      return
999   write(io6,*)'RDPLM: End-Of-File found in PLMMET file'
      leof=.TRUE.
      return
      end
!----------------------------------------------------------------------
      subroutine met4(metfm,lecho)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521             MET4
!                D. Strimaitis
!
! --- PURPOSE:  Create the header information normally contained
!               in the CALMET file, using PROFILE file.  Also, skip
!               any header records in the SURFACE file when available.
!
! --- UPDATE
!
! --- x1.0-6.42_x1.1 140521      : Assign both ITIMES and ITIMEPRF
!                                  time format flags, and read/pass
!                                  time zone
! --- V6.402-6.42_x1.0 121203  : Change IESMET assignment from scalar
!                                  to array
!
! --- V6.301-V6.402 101111  (DGS): Change LCALGRD assignment from F to
!                                  T (erroneously assigned F in V6.3)
! --- V6.3-V6.301   100827  (DGS): Allow nested CALMET files to have
!                                  different time-periods
! --- V6.1-V6.3     100212  (DGS): Add nested met grid processing
!                   100212  (DGS): Change met station coordinates from
!                                  absolute to relative to grid origin
! --- V5.7-V6.1     050915  (DGS): Add 'dataset 2.1' file format, and
!                                  use c*16 variable VERISC in new
!                                  /DATASET/ common to identify version.
!                                  Implement full begin/end time
!                                  processing.
!                                  Add METQA call.
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4, JULDAY,
!                                  GRDAY, INCR
!                   030402  (DGS): add map projection, datum
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
! --- V5.0-V5.0     980304  (DGS): add station elevation,lat,lon
! --- V4.0-V5.0     971107  (DGS): pass start-time from met file
!                                  for use if METRUN=1
!
! --- INPUTS:
!         METFM - integer       - Met data file type
!                                 1 = CALMET/AUSMET binary format
!                                     (CALMET.DAT, AUSMET.DAT)
!                                 2 = ISC ASCII file (ISCMET.DAT)
!                                 3 = AUSPLUME ASCII file (PLMMET.DAT)
!                                 4 = CTDM PROFILE & SURFACE files
!                                 5 = AERMET PROFILE & SURFACE files
!         LECHO - logical       - Control variable for output of header
!                                 record information
!     Common block /DATASET/ variables:
!           VERPRF, VERSFC
!     Common block /GRID/ variables:
!           NX, NY, NZ, DGRID, XORIG, YORIG, ZFACE(mxnzp1)
!     Common block /MAP/ variables:
!           iutmzn,feast,fnorth,
!           rlat0,rlon0,xlat1,xlat2,
!           pmap,utmhem,datum,daten
!     Common block /METHD/ variables:
!           ANEMHT, ILANDUIN, Z0IN, XLAIIN
!     Common block /GRIDNEST/ variables:
!           ngrid
!     Parameters:
!           MXNZP1, MXSS, MXUS, MXPS, MXMETDOM, IO6
!
! --- OUTPUT:
!     Common block /METHD/ variables:
!           nxm(mxmetdom),nym(mxmetdom),nzm,
!           xgridm(mxmetdom),xgridmi(mxmetdom),
!           xorigm(mxmetdom),yorigm(mxmetdom),
!           xbtzm,rlat0m,rlon0m,xlat1m,xlat2m,nssta(mxmetdom),
!           nusta(mxmetdom),npsta(mxmetdom),nlu,iwat1,iwat2,
!           zfacem(mxnzp1),xssta(mxss,mxmetdom),yssta(mxss,mxmetdom),
!           xlatss(mxss,mxmetdom),xlonss(mxss,mxmetdom),
!           xusta(mxus,mxmetdom),yusta(mxus,mxmetdom),
!           xpsta(mxps,mxmetdom),ypsta(mxps,mxmetdom),
!           z0(mxnx,mxny,mxmetdom),ilandu(mxnx,mxny,mxmetdom),
!           elev(mxnx,mxny,mxmetdom),xlai(mxnx,mxny,mxmetdom),
!           nears(mxnx,mxny,mxmetdom),lcalgrd,itimes,
!           itimeprf,
!           ibymet(mxmetdom),ibmmet(mxmetdom),ibdmet(mxmetdom),
!           ibjdmet(mxmetdom),ibhmet(mxmetdom),ibsmet(mxmetdom),
!           ieymet(mxmetdom),iemmet(mxmetdom),iedmet(mxmetdom),
!           iejdmet(mxmetdom),iehmet(mxmetdom),iesmet(mxmetdom),
!           iutmznm,feastm,fnorthm,pmapm,datumm,datenm,utmhemm
!
! --- MET4 called by:  SETUP
! --- MET4 calls:      JULDAY, INCR, GRDAY, INITR2D, OUT, INITI2D,
!                       YR4, METQA, TCLOCK
! --- V6.42_x1.1, Level 140521
!                      UTCBASR
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
      character*80 title(3)
      character*70 messag
      character*8 clab1,clab2
! --- V6.42_x1.1, Level 140521
! --- read time zone of SURFACE and PROFILE (should match)
      character*8 astimezone,aptimezone
      character*3 abegin,aend
      character*4 map
      logical ldate,lecho
!
! --- Include common block /CSIGMA/ -- Dispersion coeff. data
      include 'csigma.puf'
! --- Include common block /DATASET/  -- Input file dataset version
      include 'dataset.puf'
! --- Include common block /METHD/ -- CALMET header record data
      include 'methd.puf'
! --- Include common block /GRID/  -- Grid data from control file
      include 'grid.puf'
! --- Include common block /MAP/  -- Projection data from control file
      include 'map.puf'
! --- Include common block /GRIDNEST/  -- Nested grid configuration
      include 'gridnest.puf'
!
      data ldate/.false./
! --- There should be only 1 met grid
      if(ngrid.NE.1) then
         write(io6,*)
         write(io6,*)'MET4:  Nested grid information is bad'
         write(io6,*)'Expected 1 met grid, Found ',ngrid
         stop 'HALTED in MET4 -- See list file'
      else
! ---    Use explicit variable for '1'
         kg=1
      endif
!
! --- Position the SURFACE file at the first data record, since
! --- header information from the PROFILE file will be used to fill
! --- the /METHD/ common
! --- V6.42_x1.1, Level 140521
      itimes=0
      if(versfc.NE.'0.0') then
! ---    SURFACE file has been opened
         if(versfc.EQ.'2.1') then
! ---       Full version 2.1 with beginning & ending times (non-std)
! --- V6.42_x1.1, Level 140521
            itimes=1
            read(io32,*)
            read(io32,*) ncomm
! ---       Skip comment records
            do k=1,ncomm
               read(io32,*) 
            enddo
! ---       Allow map projection of 'NONE'
            read(io32,'(a4)') map
            if(map.NE.'NONE') then
              write(io6,*)'FATAL Error in MET4 -- Unsupported Map Proj'
              write(io6,*)'in the SURFACE file '
              write(io6,*)'Expected NONE, Found map = ',map
              stop 'Halted in MET4 -- see list file'
            endif
! --- V6.42_x1.1, Level 140521
            read(io32,*) astimezone
! ---       Process character zone into real XBTZM
            call UTCBASR(astimezone,xbtzm)
            read(io32,*)
         elseif(versfc.EQ.'1.6') then
! --- V6.42_x1.1, Level 140521
            itimes=1
! ---       Skip beginning & ending times
            read(io32,*)
         endif
! ---    Discard final header record of AERMET SURFACE.DAT
         if(metfm.EQ.5) read(io32,*)
      endif
! --- record #1 - run title --
! --- Set three title-lines
      title(1)='Constant Meteorological Fields'
      title(2)='Created for PROFILE.DAT'
      title(3)=' '
! --- Process PROFILE HEADER records (or extract info from data record)
      if(verprf.EQ.'2.1') then
! ---    Full version 2.1 with beginning & ending times (non-standard)
! --- V6.42_x1.1, Level 140521
         itimeprf=1
         read(io31,'(a80)') title(1)
         read(io31,*) ncomm
! ---    Keep first comment record
         read(io31,'(a80)') title(2)
! ---    Skip other comment records
         do k=2,ncomm
            read(io31,*) 
         enddo
! ---    Allow map projection of 'NONE'
         read(io31,'(a4)') map
         if(map.NE.'NONE') then
            write(io6,*)'FATAL Error in MET4 -- Unsupported Map Proj'
            write(io6,*)'in the PROFILE file '
            write(io6,*)'Expected NONE, Found map = ',map
            stop 'Halted in MET4 -- see list file'
         endif
! --- V6.42_x1.1, Level 140521
         read(io31,*) aptimezone
         if(aptimezone.NE.astimezone) then
            write(io6,*)
            write(io6,*)'ERROR in MET4:  Time zones must match'
            write(io6,*)'SURFACE file is zone = ',astimezone
            write(io6,*)'PROFILE file is zone = ',aptimezone
            stop 'HALT in MET4 -- see list file'
         endif
         read(io31,*)&
            ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg),ibsmet(kg),&
            ieymet(kg),iemmet(kg),iedmet(kg),iehmet(kg),iesmet(kg)
! ---    Enforce YYYY format for year
         call YR4(io6,ibymet(kg),ierrb)
         call YR4(io6,ieymet(kg),ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in MET4'
         call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
         call JULDAY(io6,ieymet(kg),iemmet(kg),iedmet(kg),iejdmet(kg))
      elseif(verprf.EQ.'1.6') then
! ---    Beginning & ending times are provided (non-standard)
! --- V6.42_x1.1, Level 140521
         itimeprf=1
         read(io31,'(a80)') title(1)
         read(title(1),*) abegin,ibymet(kg),ibmmet(kg),ibdmet(kg),&
                          ibhmet(kg),ibsmet(kg),&
                          aend,ieymet(kg),iemmet(kg),iedmet(kg),&
                          iehmet(kg),iesmet(kg)
! ---    Enforce YYYY format for year
         call YR4(io6,ibymet(kg),ierrb)
         call YR4(io6,ieymet(kg),ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in MET4'
         call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
         call JULDAY(io6,ieymet(kg),iemmet(kg),iedmet(kg),iejdmet(kg))
      else
! --- V6.42_x1.1, Level 140521
         itimeprf=0
! ---    Extract date-time from first record (must rewind)
         read(io31,*) ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg)
         rewind(io31)
! ---    Enforce YYYY format for year
         call YR4(io6,ibymet(kg),ierrb)
         if(ierrb.NE.0) stop 'Halted in MET4'
! ---    PROFILE.DAT uses a 1-24 clock (hour ending).  Convert to time
! ---    beginning by subtracting 1 from the hour (0-23)
         ibhmet(kg)=ibhmet(kg)-1
         call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
         ibsmet(kg)=0
! ---    Use "default" run length (hours) to mark the end of data
! ---    Assume this will be 5 years, so set IRLG=43848
         irlgmet=43848
         ieymet(kg)=ibymet(kg)
         iemmet(kg)=ibmmet(kg)
         iedmet(kg)=ibdmet(kg)
         iejdmet(kg)=ibjdmet(kg)
         iehmet(kg)=ibhmet(kg)
         call INCR(io6,ieymet(kg),iejdmet(kg),iehmet(kg),irlgmet)
         call GRDAY(io6,ieymet(kg),iejdmet(kg),iemmet(kg),iedmet(kg))
! --- V6.42_x1.0, Level 121203
         iesmet(kg)=0
         call JULDAY(io6,ibymet(kg),ibmmet(kg),ibdmet(kg),ibjdmet(kg))
         call JULDAY(io6,ieymet(kg),iemmet(kg),iedmet(kg),iejdmet(kg))
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibymet(kg),ibjdmet(kg),ibhmet(kg),&
                  ibsmet(kg))
      call TCLOCK(io6,idum,ieymet(kg),iejdmet(kg),iehmet(kg),&
                  iesmet(kg))
      call GRDAY(io6,ibymet(kg),ibjdmet(kg),ibmmet(kg),ibdmet(kg))
      call GRDAY(io6,ieymet(kg),iejdmet(kg),iemmet(kg),iedmet(kg))
!
! --- record #2 - run control parameters --
! --- (vermet, levmet are both 8 bytes):
! ---  vermet,levmet,ibyr,ibmo,ibdy,ibhr,irtype,
! ---  nxm, nym, nzm, xgridm, xorigm, yorigm, iutmznm, iwfcod, nssta,
! ---  nusta, npsta, nowsta, nlu, iwat1, iwat2, lcalgrd
!
! --- Fill in most first
      irtype=1
      nxm(kg)=nx
      nym(kg)=ny
      nzm=nz
      xgridm(kg)=dgrid
      xgridmi(kg)=1./dgrid
      xorigm(kg)=xorig
      yorigm(kg)=yorig
      pmapm=pmap
      datumm=datum
      datenm=daten
      iutmznm=iutmzn
      utmhemm=utmhem
      rlat0m=rlat0
      rlon0m=rlon0
      xlat1m=xlat1
      xlat2m=xlat2
      feastm=feast
      fnorthm=fnorth
      iwfcod=1
      nssta(kg)=1
      nusta(kg)=1
      npsta(kg)=1
      nowsta(kg)=0
      nlu=10
      iwat1=500
      iwat2=599
      lcalgrd=.TRUE.
      if(LECHO)then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*) title(1)
         write(io6,*) title(2)
         write(io6,*) title(3)
         write(io6,*)
         write(io6,*)'-- Data begin at:'
         write(io6,*)' IBYR    = ',ibymet(kg)
         write(io6,*)' IBMO    = ',ibmmet(kg)
         write(io6,*)' IBDY    = ',ibdmet(kg)
         write(io6,*)' IBJUL   = ',ibjdmet(kg)
         write(io6,*)' IBHR    = ',ibhmet(kg)
         write(io6,*)' IBSEC   = ',ibsmet(kg)
         if(verprf.EQ.'1.0') then
            write(io6,*)'-- Data end at:  ?'
         else
            write(io6,*)'-- Data end at:'
         endif
         write(io6,*)' IEYR    = ',ieymet(kg)
         write(io6,*)' IEMO    = ',iemmet(kg)
         write(io6,*)' IEDY    = ',iedmet(kg)
         write(io6,*)' IEJUL   = ',iejdmet(kg)
         write(io6,*)' IEHR    = ',iehmet(kg)
         write(io6,*)' IESEC   = ',iesmet(kg)
         write(io6,*)
         write(io6,*)' ISIGMAV = ',isigmav
         if(isigmav.EQ.0) then
            write(io6,*)'     (read sigma-theta and convert to sigma-v)'
         else
            write(io6,*)'     (read sigma-v)'
         endif
         write(io6,*)' IRTYPE  = ',irtype
         write(io6,*)' LCALGRD = ',lcalgrd
         write(io6,*)' NXM     = ',nxm(kg)
         write(io6,*)' NYM     = ',nym(kg)
         write(io6,*)' NZM     = ',nzm
         write(io6,*)' XGRIDM  = ',xgridm(kg)
         write(io6,*)' XGRIDM  = ',xgridm(kg)
         write(io6,*)' YORIGM  = ',yorigm(kg)
         write(io6,*)' PMAPM   = ',pmapm
         write(io6,*)' DATUMM  = ',datumm
         write(io6,*)' DATENM  = ',datenm
         write(io6,*)' IUTMZNM = ',iutmznm
         write(io6,*)' UTMHEMM = ',utmhemm
         write(io6,*)' XLAT1M  = ',xlat1m
         write(io6,*)' XLAT2M  = ',xlat2m
         write(io6,*)' RLAT0M  = ',rlat0m
         write(io6,*)' RLON0M  = ',rlon0m
         write(io6,*)' FEASTM  = ',feastm
         write(io6,*)' FNORTHM = ',fnorthm
         write(io6,*)' IWFCOD  = ',iwfcod
         write(io6,*)' NSSTA   = ',nssta(kg)
         write(io6,*)' NUSTA   = ',nusta(kg)
         write(io6,*)' NPSTA   = ',npsta(kg)
         write(io6,*)' NOWSTA  = ',nowsta(kg)
         write(io6,*)' NLU     = ',nlu
         write(io6,*)' IWAT1   = ',iwat1
         write(io6,*)' IWAT2   = ',iwat2
      endif
!
      nzmp1=nzm+1
!
! --- record #3 - cell face heights (NZ + 1 words)
!
! --- Grab the face heights from the control file
      do i=1,nzmp1
         zfacem(i)=zface(i)
      enddo
!
      if(LECHO)then
         write(io6,203)(zfacem(n),n=1,nzmp1)
203      format(1x,'ZFACEM  = ',10(f9.3,', ')/)
      endif
! --- Perform QA checks on header record variables
      call METQA
!
! --- records #4 & 5 - x, y coordinates of surface stations
! --- (NSSTA words each record)
      if(nssta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xssta(1,kg)=0.0
         yssta(1,kg)=0.0
         if(LECHO)then
            clab1='XSSTA'
            clab2='YSSTA'
            write(io6,*)clab1,' = ',(xssta(n,kg),n=1,nssta(kg))
            write(io6,*)clab2,' = ',(yssta(n,kg),n=1,nssta(kg))
         endif
      endif
!
! --- records #4a & 5a - lat, lon coordinates of surface stations
! --- (NSSTA words each record)
      if(nssta(kg).gt.0)then
! ---    Grab the lat,lon from control file inputs
         xlatss(1,kg)=xlatin
         xlonss(1,kg)=xlonin
         if(LECHO)then
            clab1='XLATSS'
            clab2='YLONSS'
            write(io6,*)clab1,' = ',(xlatss(n,kg),n=1,nssta(kg))
            write(io6,*)clab2,' = ',(xlonss(n,kg),n=1,nssta(kg))
         endif
      endif
!
! --- records #6 & 7 - x, y coordinates of upper air stations
! --- (NUSTA words each record)
      if(nusta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xusta(1,kg)=0.0
         yusta(1,kg)=0.0
         if(LECHO)then
            clab1='XUSTA'
            clab2='YUSTA'
            write(io6,*)clab1,' = ',(xusta(n,kg),n=1,nusta(kg))
            write(io6,*)clab2,' = ',(yusta(n,kg),n=1,nusta(kg))
         endif
      endif
!
! --- records #8 & 9 - x, y coordinates of precipitation stations
! --- (NPSTA words each record)
      if(npsta(kg).gt.0)then
! ---    Coordinates relative to the grid origin
         xpsta(1,kg)=0.0
         ypsta(1,kg)=0.0
         if(LECHO)then
            clab1='XPSTA'
            clab2='YPSTA'
            write(io6,*)clab1,' = ',(xpsta(n,kg),n=1,npsta(kg))
            write(io6,*)clab2,' = ',(ypsta(n,kg),n=1,npsta(kg))
         endif
      endif
!
! --- Fill the corresponding arrays:
!
! --- record #10 - surface roughness lengths (NX * NY words)
      call initr2d(z0in,mxnx,mxny,nxm(kg),nym(kg),z0(1,1,kg))
      if(LECHO)then
         messag='Surface roughness lengths (m)'
         messag(63:70)='z0'
         call out(z0(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #11 - land use categories (NX * NY words)
      call initi2d(ilanduin,mxnx,mxny,nxm(kg),nym(kg),ilandu(1,1,kg))
      if(LECHO)then
         messag='Land use categories'
         messag(63:70)='ILANDU'
         call out(xdum,ilandu(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #12 - elevations (NX * NY words)
! --- Set grid elevation to ELEVIN from control file
      call initr2d(elevin,mxnx,mxny,nxm(kg),nym(kg),elev(1,1,kg))
      if(LECHO)then
         messag='Terrain heights (m)'
         messag(63:70)='TERR'
         call out(elev(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #13 - leaf area index (NX * NY words)
      call initr2d(xlaiin,mxnx,mxny,nxm(kg),nym(kg),xlai(1,1,kg))
      if(LECHO)then
         messag='Leaf area index'
         messag(63:70)='XLAI'
         call out(xlai(1,1,kg),idum,1,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
! --- record #14 - nearest surface station to each grid point
!                  (NX * NY words)
      call initi2d(1,mxnx,mxny,nxm(kg),nym(kg),nears(1,1,kg))
      if(LECHO)then
         messag='Nearest surface station no. to each grid point'
         messag(63:70)='NEARS'
         call out(xdum,nears(1,1,kg),2,5,ldate,messag,nxm(kg),nym(kg))
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine MET4PROF(lecho)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521          MET4PROF
!                D. Strimaitis   Earth Tech
!
! --- PURPOSE:  Read the header information from CTDM/AERMET PROFILE
!               file if present (MET4PROF is needed if PROFILE.DAT
!               is used for turbulence data or inversion strength data
!               only)
!
! --- UPDATE
!
! --- x6.1-6.42_x1.1 140521      : Read/check/pass time zone
!
! --- INPUTS:
!         LECHO - logical       - Control variable for output of header
!                                 record information
!
!     Common block /DATASET/ variables:
!           VERPRF
!
! --- V6.42_x1.1, Level 140521
!     Common block /METHD/ variables:
!           xbtzm
!
!     Parameters:
!           IO6, IO31
!
! --- OUTPUT:
!     Common block /METHD/ variables:
!           itimeprf
!
! --- MET4PROF called by:  SETUP
! --- MET4PROF calls:      JULDAY, YR4, TCLOCK
! --- V6.42_x1.1, Level 140521
!                          UTCBASR
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- V6.42_x1.1, Level 140521
! --- Read time zone of PROFILE file (should match other met data)
      character*8 aptimezone
      character*4 map
      character*3 abegin,aend
      logical lecho
!
! --- Include common block /DATASET/  -- Input file dataset version
      include 'dataset.puf'
! --- Include common block /METHD/ -- CALMET header record data
      include 'methd.puf'
! --- Process HEADER records (or extract info from data record)
      if(verprf.EQ.'2.1') then
! ---    Full version 2.1 with beginning & ending times (non-standard)
         itimeprf=1
! ---    Skip dataset record
         read(io31,*)
         read(io31,*) ncomm
! ---    Skip comment records
         do k=1,ncomm
            read(io31,*) 
         enddo
! ---    Allow map projection of 'NONE'
         read(io31,'(a4)') map
         if(map.NE.'NONE') then
            write(io6,*)'FATAL Error in MET4PROF -- ',&
                        'Unsupported Map Proj'
            write(io6,*)'in the PROFILE file '
            write(io6,*)'Expected NONE, Found map = ',map
            stop 'Halted in MET4PROF -- see list file'
         endif
! --- V6.42_x1.1, Level 140521
         read(io31,*) aptimezone
! ---    Process character zone into real XPTZM
         call UTCBASR(aptimezone,xptzm)
! ---    Must match time zone of primary met data
         if(xbtzm.GT.-900. .AND. xbtzm.NE.xptzm) then
            write(io6,*)
            write(io6,*)'ERROR in MET4PROF -- ',&
                        'Met time zones do not match'
            write(io6,*)'IBTZM(PROFILE)     = ',xptzm
            write(io6,*)'IBTZM(Met file)    = ',xbtzm
            stop 'HALT in MET4PROF -- see list file'
         else
            xbtzm=xptzm
         endif
         read(io31,*) ibyt,ibmt,ibdt,ibht,ibst,&
                      ieyt,iemt,iedt,ieht,iest
! ---    Enforce YYYY format for year
         call YR4(io6,ibyt,ierrb)
         call YR4(io6,ieyt,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in MET4PROF'
         call JULDAY(io6,ibyt,ibmt,ibdt,ibjdt)
         call JULDAY(io6,ieyt,iemt,iedt,iejdt)
      elseif(verprf.EQ.'1.6') then
! ---    Beginning & ending times are provided (non-standard)
         itimeprf=1
         read(io31,*) abegin,ibyt,ibmt,ibdt,ibht,ibst,&
                      aend,ieyt,iemt,iedt,ieht,iest
! ---    Enforce YYYY format for year
         call YR4(io6,ibyt,ierrb)
         call YR4(io6,ieyt,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in MET4PROF'
         call JULDAY(io6,ibyt,ibmt,ibdt,ibjdt)
         call JULDAY(io6,ieyt,iemt,iedt,iejdt)
      else
         itimeprf=0
! ---    Extract date-time from first record (must rewind)
         read(io31,*) ibyt,ibmt,ibdt,ibht
         rewind(io31)
! ---    Enforce YYYY format for year
         call YR4(io6,ibyt,ierrb)
         if(ierrb.NE.0) stop 'Halted in MET4PROF'
! ---    PROFILE.DAT uses a 1-24 clock (hour ending).  Convert to time
! ---    beginning by subtracting 1 from the hour (0-23)
         ibht=ibht-1
         call JULDAY(io6,ibyt,ibmt,ibdt,ibjdt)
         ibst=0
! ---    Use "default" run length (hours) to mark the end of data
! ---    Assume this will be 5 years, so set IRLG=43848
         irlgt=43848
         ieyt=ibyt
         iemt=ibmt
         iedt=ibdt
         iejdt=ibjdt
         ieht=ibht
         call INCR(io6,ieyt,iejdt,ieht,irlgt)
         call GRDAY(io6,ieyt,iejdt,iemt,iedt)
         iest=0
         call JULDAY(io6,ibyt,ibmt,ibdt,ibjdt)
         call JULDAY(io6,ieyt,iemt,iedt,iejdt)
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyt,ibjdt,ibht,ibst)
      call TCLOCK(io6,idum,ieyt,iejdt,ieht,iest)
      call GRDAY(io6,ibyt,ibjdt,ibmt,ibdt)
      call GRDAY(io6,ieyt,iejdt,iemt,iedt)
      if(LECHO)then
         write(io6,*)
         write(io6,*)
         write(io6,*)' -------------------------------------------'
         write(io6,*)' ------- PROFILE HEADER INFORMATION --------'
         write(io6,*)' -------------------------------------------'
         write(io6,*)
         write(io6,*)'-- Data begin at:'
         write(io6,*)' IBYR    = ',ibyt
         write(io6,*)' IBMO    = ',ibmt
         write(io6,*)' IBDY    = ',ibdt
         write(io6,*)' IBJUL   = ',ibjdt
         write(io6,*)' IBHR    = ',ibht
         write(io6,*)' IBSEC   = ',ibst
         if(verprf.NE.'1.0') then
            write(io6,*)'-- Data end at:'
            write(io6,*)' IEYR    = ',ieyt
            write(io6,*)' IEMO    = ',iemt
            write(io6,*)' IEDY    = ',iedt
            write(io6,*)' IEJUL   = ',iejdt
            write(io6,*)' IEHR    = ',ieht
            write(io6,*)' IESEC   = ',iest
         else
            write(io6,*)'-- Data end at:  ?'
         endif
         write(io6,*)
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine rdmet4(idathrm,isecm,lecho,ptg,izp,wsprf,wdprf,tprf,&
                        zprf,ssprf,swprf,svprf,ipgt,htmix,ustar,xmonin,&
                        wstar,rmm,tempss,rhoss,qswss,irhss,ipcode,&
                        dptinvo,leof,kdathre,kse,kdathrb,ksb)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 171103            RDMET4
!                D. Strimaitis
!
! --- PURPOSE:  Read meteorological data for one hour from CTDM
!               PROFILE and SURFACE files
!
! --- UPDATE
! --- Version 7.3.0, Level 150915 to Version 7.3.1, Level 171103
!     1) When reading AERMOD or CTDM PROFILE.DAT files, fill missing
!        levels.  This will avoid calculations in numerical rise 
!        for PRIME from using missing data flags.  After fills all
!        will be complete for temperature, WS, WD and Scalar WS.
!        Issue arises when multi-level on-site data is used in AERMET
!        and levels are not complete for all variables.  For instance
!        records are present for 2 meter temperature and 10 meter winds.
!        MODIFIED: RDMET4, RDMET5
!     2) Modified reads for AERMOD and CTDM PROFILE.DAT to identify 
!        multi-level profiles where all records are CALM.  Previously
!        CALM profiles were only properly identified for cases with
!        only 1 layer.
!        Issue and Fix contributed by Eric Delisle, SNC-Lavalin 
! --- V6.3-6.42_x1.1 140521      : Use ITIMES flag for processing times
!                                  from the SURFACE file
! --- V6.268-V6.3   100212  (DGS): add nested MET grids
! --- V6.1-V6.268   100108  (DGS): declare PTG as 1-D real
! --- V5.7-V6.1     050915  (DGS): allow for begin/end time format
!                                  resolved to seconds.
!                                  (retain compatibility with standard
!                                  PROFILE and SURFACE version)
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4, JULDAY,
!                                  GRDAY, INCR
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
! --- V4.0-V5.0     971107  (DGS): add EOF recovery
!                   971107  (DGS): initialize data as missing for non-
!                                  measurement levels
!                   971107  (DGS): add record-skip logic from COMP to
!                                  find current date-time
!                                  and act on PROBLEMs found in XTPRF
!                                  for records that are not skipped
!                   971107  (DGS): skip blank records
!                   971107  (DGS): allow inversion strength in PROFILE
!                   971107  (DGS): move PROFILE.DAT reads into RDPROF
!
! --- INPUTS:
!       IDATHRM - integer       - Mid-pt date/hour of required
!                                 data (YYYYJJJHH)
!         ISECM - integer       - Mid-pt second of required data (SSSS)
!         LECHO - logical       - Flag controlling printing of input
!                                 meteorological data fields
!        PTG(2) - real          - Default potential temperature grad.
!                                 for stable classes (deg. K/m)
!    Common block /METHD/ variables:
!         NXM, NYM, NZM, NSSTA, NPSTA, ZFACEM(mxnzp1), LCALGRD,
!         IMIXCTDM, ISIGMAV, ITIMES, ITIMEPRF
!    Common block /GRIDNEST/ variables:
!         ngrid
!    Parameters:
!         MXNX, MXNY, MXNZ, MXNZP1, MXPRFZ, MXMETDOM, MXSS, IO6
!
! --- OUTPUT:
!
!                    IZP - integer - Number of profile levels
!          WSPRF(mxprfz) - real    - 1-D vector wind speed profile (m/s)
!          WDPRF(mxprfz) - real    - 1-D vector wind dir. profile (deg)
!           TPRF(mxprfz) - real    - Temperature profile (deg. K)
!           ZPRF(mxprfz) - real    - Heights for data in profile (m)
!          SSPRF(mxprfz) - real    - 1-D scalar speed profile (m/s)
!          SWPRF(mxprfz) - real    - 1-D sigma-w profile (m/s)
!          SVPRF(mxprfz) - real    - 1-D sigma-v profile (m/s)
!           IPGT(mxnx,mxny,mxmetdom)
!                        - integer - PGT stability class
!          HTMIX(mxnx,mxny,mxmetdom)
!                        - real    - Mixing height (m)
!          USTAR(mxnx,mxny,mxmetdom)
!                        - real    - Surface friction velocity (m/s)
!         XMONIN(mxnx,mxny,mxmetdom)
!                        - real    - Monin-Obukhov length (m)
!          WSTAR(mxnx,mxny,mxmetdom)
!                        - real    - Convective velocity scale (m/s)
!            RMM(mxnx,mxny,mxmetdom)
!                        - real    - Precipitation rate (mm/hr)
!  TEMPSS(mxss,mxmetdom) - real    - Air temperature (deg. K) at
!                                    each surface met. station
!   RHOSS(mxss,mxmetdom) - real    - Air density (kg/m**3)
!   QSWSS(mxss,mxmetdom) - real    - Short wave solar radiation
!                                    (W/m**2)
!   IRHSS(mxss,mxmetdom) - integer - Relative humidity (percent)
!  IPCODE(mxss,mxmetdom) - integer - Precipitation code at each surface
!                                    station
!                DPTINVO - real    - Inversion strength at top of CBL
!                                    (jump in potential temperature K)
!                   LEOF - logical - End-Of-File flag
!                KDATHRE - integer - End date/hour of data (YYYYJJJHH)
!                    KSE - integer - End second of data (SSSS)
!                KDATHRB - integer - Begin date/hour of data (YYYYJJJHH)
!                    KSB - integer - Begin second of data (SSSS)
!
! --- RDMET4 called by:  COMP
! --- RDMET4 calls:      JULDAY, INCR, GRDAY, INITAR,
!                        INITAI, OUT, INITR2D, INITI2D,
!                        LSTAB, XTPRF, RDPROF, TPERIOD, YR4, TCLOCK
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'methd.puf'
      include 'comparm.puf'
      include 'dispdat.puf'
      include 'grid.puf'
      include 'gridnest.puf'
!
! --- Profile data arrays
      real wsprf(mxprfz),wdprf(mxprfz),tprf(mxprfz),zprf(mxprfz)
      real ssprf(mxprfz),swprf(mxprfz),svprf(mxprfz)
! --- Real 2-D fields
      real htmix(mxnx,mxny,mxmetdom),ustar(mxnx,mxny,mxmetdom)
      real xmonin(mxnx,mxny,mxmetdom)
      real wstar(mxnx,mxny,mxmetdom),rmm(mxnx,mxny,mxmetdom)
! --- Real 1-D fields
      real tempss(mxss,mxmetdom),rhoss(mxss,mxmetdom)
      real qswss(mxss,mxmetdom)
      real ptg(2)
!
      integer ipgt(mxnx,mxny,mxmetdom),irhss(mxss,mxmetdom)
      integer ipcode(mxss,mxmetdom)
!
      character messag*70,aline*132
!
      logical lecho,ldate,problem,leof,lcalmprof
!
      data ldate/.false./
      data vonk/0.4/,third/0.3333333/
! --- There should be only 1 met grid
      if(ngrid.NE.1) then
         write(io6,*)
         write(io6,*)'RDMET4:  Nested grid information is bad'
         write(io6,*)'Expected 1 met grid, Found ',ngrid
         stop 'HALTED in RDMET4 -- See list file'
      else
! ---    Use explicit variable for '1'
         kg=1
      endif
! --- Set logical to halt program if problem is found
1     problem=.FALSE.
! -------------------------------------------------------------------
!  -- Read one record of met data from CTDM SURFACE file
! -------------------------------------------------------------------
! --- Read entire line as character variable
4     read(io32,'(a132)',end=998) aline
! --- Try to extract fields for an extended record (internal read)
      if(itimes.EQ.1) then
         read(aline,*,end=5) iby,ibm,ibd,ibjul,ibh,ksb,&
                             iey,iem,ied,iejul,ieh,kse,&
                             ziobs,zipre,ustr,xmon,z0m,&
                             ipc,pmmhr,qswrad,irelhum
      else
         read(aline,*,end=5) iey,iem,ied,iejul,ieh,&
                             ziobs,zipre,ustr,xmon,z0m,&
                             ipc,pmmhr,qswrad,irelhum
      endif
      goto 6
! --- Extended data are incomplete or missing; use default values
5     qswrad=0.0
      irelhum=85
      pmmhr=0.0
      ipc=0
6     continue
!
! --- Skip blank lines
      if(itimes.EQ.1) then
         if(ibm.EQ.0 .OR. ibd.EQ.0) goto 4
      else
         if(iem.EQ.0 .OR. ied.EQ.0) goto 4
      endif
! -------------------------------------------------------------------
! --- Process date-time information
! --- V6.42_x1.1, Level 140521
      if(itimes.EQ.1) then
! ---    Enforce YYYY format for year
         call YR4(io6,iby,ierrb)
         call YR4(io6,iey,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDMET4'
         kdathrb=iby*100000+ibjul*100+ibh
         kdathre=iey*100000+iejul*100+ieh
      else
! ---    Enforce YYYY format for year
         call YR4(io6,iey,ierrb)
         if(ierrb.NE.0) stop 'Halted in RDMET4'
! ---    SURFACE.DAT uses a 1-24 clock (hour ending).  Change
! ---    time to the beginning of the hour (subtract 1 hour)
         ih=ieh-1
         call julday(io6,iey,iem,ied,ijul)
         kdathrb=iey*100000+ijul*100+ih
         ksb=0
! ---    Increment time 1 hour to get end time
         call incr(io6,iey,ijul,ih,1)
         kdathre=iey*100000+ijul*100+ih
         kse=0
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,kdathrb,idum,idum,idum,ksb)
      call TCLOCK(io6,kdathre,idum,idum,idum,kse)
! --- Check period of met data with mid-point of step
      call TPERIOD(0,idathrm,isecm,kdathrb,ksb,kdathre,&
                   kse,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         go to 1
      elseif(ilocate.EQ.-1) then
         write(io6,*)'ERROR in subr. RDMET4 -- current time not found',&
         ' in the SURFACE data file'
         write(io6,*)' --   Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksb
         write(io6,*)'                           to ',kdathre,kse
         stop 'Halted in RDMET4 -- see list file.'
      endif
! --- Set current mixing height
      if(imixctdm.EQ.0) then
         zmix=zipre
         if(zmix.LT.0.0) zmix=ziobs
      else
         zmix=ziobs
         if(zmix.LT.0.0) zmix=zipre
      endif
! --- Check current value of roughness with value in /METHD/
      if(z0m.LE.0.0) then
! ---    Roughness length missing, keep previous value
         z0m=z0(1,1,kg)
      elseif(z0m.NE.z0(1,1,kg)) then
! ---    Valid roughness length changed, update array
! !!!    write(io6,*)'RDMET4:  Roughness length changed'
! !!!    write(io6,*)'         Date-Hour      - ',ksdathr
! !!!    write(io6,*)'         old,new z0(m)  - ',z0(1,1,kg),z0m
         call initr2d(z0m,mxnx,mxny,nxm(kg),nym(kg),z0(1,1,kg))
      endif
! --- Recover from missing u*, L by assuming that there is no
! --- stability influence, and no flow .... CALM.  This is likely
! --- because METPRO will give non-missing surface layer parameters
! --- for "real" data.  However, if onsite tower data are missing,
! --- and the offsite data are "zero" for speed and possibly direction,
! --- then the period must be treated as a calm.
      if(ustr.LE.0.0) then
         write(io6,*)'RDMET4: U-star missing in SURFACE'
         write(io6,*)'        Period Beginning:',kdathrb,ksb
         write(io6,*)'        Period Ending   :',kdathre,kse
         write(io6,*)'        Assuming CALM condition'
         ustr=0.0
         xmon=9999.
      endif
! --- Compute w* from u*,L,zmix; estimate PG class using Golder(1972)
      if(zmix.LT.0.0) then
         write(io6,*)'RDMET4: Mixing height missing in SURFACE'
         write(io6,*)'        Cannot fill in a computed w-star'
         problem=.TRUE.
      else
         wstr=0.
         if(xmon.LT.0.) then
            wstr=ustr*(-zmix/(vonk*xmon))**third
         endif
         kst=LSTAB(xmon,z0m)
      endif
! -------------------------------------------------------------------
!  -- Read records for this hour from CTDM PROFILE file
! -------------------------------------------------------------------
      call RDPROF(idathrm,isecm,lecho,izp,wsprf,wdprf,tprf,zprf,&
                  ssprf,swprf,svprf,dptinvo,&
                  ldathre,lse,ldathrb,lsb,leof)
      if(leof) return
! --- Check for consistent date-time for period among records
      if(kdathre.NE.ldathre .OR. kse.NE.lse) then
         write(io6,*)'RDMET4: PROFILE does not match SURFACE period'
         problem=.TRUE.
      endif
! --- Halt program now if problem was found with SURFACE or PROFILE
      if(PROBLEM) then
         write(io6,*)' PROFILE per. = ',ldathrb,lsb,ldathre,lse
         write(io6,*)' SURFACE per. = ',kdathrb,ksb,kdathre,kse
         stop 'Halted in RDMET4 -- see list file'
      endif
! --- Check for calms over the whole CTDM profile
      lcalmprof=.false.
      icalm=0
      do i=1,izp
        if(wsprf(i).LE.0.0.AND.ssprf(i).LE.0.0) icalm=icalm+1
      enddo
      if(icalm==izp) lcalmprof=.true.
! --- Add levels at .25, .5, and 1.0 times the mixing height as in
! --- CTDM+, and add level at top of model, if mxprfz will allow it
! --- Do not add CTDM+ levels if mixing height is less than 10 m
      nznew=4
      if(zmix.LT.10.0) nznew=1
      nznew=MIN0(nznew,mxprfz-izp)
      if(nznew.GT.0) then
         do iz=1,nznew
! ---       Set new level
            iznew=izp+iz
            if(iz.EQ.1) then
               zprf(iznew)=zfacem(nz+1)
            elseif(iz.EQ.2) then
               zprf(iznew)=zmix
            else
               zprf(iznew)=.5*zprf(iznew-1)
            endif
! ---       Set initial values to missing
            wsprf(iznew)=-999.
            ssprf(iznew)=-999.
            wdprf(iznew)=-999.
            tprf(iznew)=-999.
            swprf(iznew)=-999.
            svprf(iznew)=-999.
! ---       Reset logical for reporting problems
            problem=.FALSE.
! ---       Extract value at this level, and add to top of profile
            if(lcalmprof) then
! ---          Only speed available is calm from offsite; do not profile
               wsprf(iznew)=AMAX1(MAXVAL(wsprf),MAXVAL(ssprf))
               ssprf(iznew)=wsprf(iznew)
            else
               call XTPRF(izp,wsprf,zprf,zprf(iznew),'spd',z0m,xmon,&
                       zmix,kst,ptg,wsprf(iznew),problem)
               call XTPRF(izp,ssprf,zprf,zprf(iznew),'spd',z0m,xmon,&
                       zmix,kst,ptg,ssprf(iznew),problem)
            endif
            call XTPRF(izp,wdprf,zprf,zprf(iznew),'dir',z0m,xmon,&
                    zmix,kst,ptg,wdprf(iznew),problem)
            call XTPRF(izp,tprf,zprf,zprf(iznew),'tmp',z0m,xmon,&
                    zmix,kst,ptg,tprf(iznew),problem)
! ---       Need at least 1 valid level of spd, dir, and tmp
            if(PROBLEM) then
               write(io6,*)'ERROR in subr. RDMET4 -- speed, dir, or ',&
                    'temperature missing at all levels in PROFILE'
               write(io6,*)'        Period Beginning:',ldathrb,lsb
               write(io6,*)'        Period Ending   :',ldathre,lse
               write(*,*)
               stop 'Halted in RDMET4 -- see list file.'
            endif
            call XTPRF(izp,swprf,zprf,zprf(iznew),'sig',z0m,xmon,&
                    zmix,kst,ptg,swprf(iznew),problem)
            call XTPRF(izp,svprf,zprf,zprf(iznew),'sig',z0m,xmon,&
                    zmix,kst,ptg,svprf(iznew),problem)
         enddo
! --     Sort profile levels
         izp=iznew
         call STPRF(nznew,izp,zprf,wsprf,wdprf,tprf,swprf,svprf,ssprf)
      endif
! -- FILL MISSING LEVELS FOR TEMPERATURE AND WINDS
! -- REV CdA
      do iz=1,izp
        if(wsprf(iz).LT.0.0) then
           call XTPRF(izp,wsprf,zprf,zprf(iz),'spd',z0m,xmon,&
                    zmix,kst,ptg,wsprf(iz),problem)
           call XTPRF(izp,wdprf,zprf,zprf(iz),'dir',z0m,xmon,&
                    zmix,kst,ptg,wdprf(iz),problem)
        endif
        if(ssprf(iz).LT.0.0) then
           call XTPRF(izp,ssprf,zprf,zprf(iz),'spd',z0m,xmon,&
                    zmix,kst,ptg,ssprf(iz),problem)
        endif
        if(tprf(iz).LT.0.0) then
            call XTPRF(izp,tprf,zprf,zprf(iz),'tmp',z0m,xmon,&
                    zmix,kst,ptg,tprf(iz),problem)
        endif
      enddo 
! -- REV CdA
! --- Set the 2-D fields
      call initi2d(kst,mxnx,mxny,nxm(kg),nym(kg),ipgt(1,1,kg))
      call initr2d(ustr,mxnx,mxny,nxm(kg),nym(kg),ustar(1,1,kg))
      call initr2d(zmix,mxnx,mxny,nxm(kg),nym(kg),htmix(1,1,kg))
      call initr2d(xmon,mxnx,mxny,nxm(kg),nym(kg),xmonin(1,1,kg))
      call initr2d(wstr,mxnx,mxny,nxm(kg),nym(kg),wstar(1,1,kg))
      call initr2d(pmmhr,mxnx,mxny,nxm(kg),nym(kg),rmm(1,1,kg))
! --- Pass surface temperature to TMPK (assume z=z0m)
      call XTPRF(izp,tprf,zprf,z0m,'tmp',z0m,xmon,zmix,kst,&
                 ptg,tmpk,problem)
! --- Set the 1-D fields
      call initar(tmpk,nssta(kg),tempss(1,kg))
      call initar(1.28,nssta(kg),rhoss(1,kg))
      call initar(qswrad,nssta(kg),qswss(1,kg))
      call initai(irelhum,nssta(kg),irhss(1,kg))
      call initai(ipc,nssta(kg),ipcode(1,kg))
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ MET. FIELDS BEGINNING: ',kdathrb,ksb
         write(io6,*)'------            AND ENDING: ',kdathre,kse
         write(io6,*)
!
! ---    2-D fields
         messag='PGT stability class'
         call out(xdum,ipgt(1,1,kg),2,1,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Friction velocity (m/s)'
         call out(ustar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Mixing height (m)'
         call out(htmix(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Monin-Obukhov length (m)'
         call out(xmonin(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Convective velocity scale (m/s)'
         call out(wstar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         if(npsta(kg).gt.0)then
            messag='Precipitation rate (mm/hr)'
            call out(rmm(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
         endif
!
! ---    1-D fields
         write(io6,*)'TEMPSS (deg. K) = ',(tempss(n,kg),n=1,nssta(kg))
         write(io6,*)'RHOSS (kg/m**3)  = ',(rhoss(n,kg),n=1,nssta(kg))
         write(io6,*)'QSWSS (W/m**2)   = ',(qswss(n,kg),n=1,nssta(kg))
         write(io6,*)'IRHSS (percent)  = ',(irhss(n,kg),n=1,nssta(kg))
         if(npsta(kg).gt.0)write(io6,*)'IPCODE           = ',&
          (ipcode(n,kg),n=1,nssta(kg))
      endif
!
      return
998   write(io6,*)'RDMET4: End-Of-File found in SURFACE file'
      leof=.TRUE.
      return
      end
!----------------------------------------------------------------------
      subroutine rdmet5(idathrm,isecm,lecho,ptg,izp,wsprf,wdprf,tprf,&
                        zprf,ssprf,swprf,svprf,ipgt,htmix,ustar,xmonin,&
                        wstar,rmm,tempss,rhoss,qswss,irhss,ipcode,&
                        dptinvo,leof,kdathre,kse,kdathrb,ksb)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 171103            RDMET5
!                D. Strimaitis
!
! --- PURPOSE:  Read meteorological data for one hour from AERMET
!               PROFILE and SURFACE files (processing follows that
!               in RDMET4)
!
! --- UPDATE
! --- Version 7.3.0, Level 150915 to Version 7.3.1, Level 171103
!     1) When reading AERMOD or CTDM PROFILE.DAT files, fill missing
!        levels.  This will avoid calculations in numerical rise 
!        for PRIME from using missing data flags.  After fills all
!        will be complete for temperature, WS, WD and Scalar WS.
!        Issue arises when multi-level on-site data is used in AERMET
!        and levels are not complete for all variables.  For instance
!        records are present for 2 meter temperature and 10 meter winds.
!        MODIFIED: RDMET4, RDMET5
!     2) Modified reads for AERMOD and CTDM PROFILE.DAT to identify 
!        multi-level profiles where all records are CALM.  Previously
!        CALM profiles were only properly identified for cases with
!        only 1 layer.
!        Issue and Fix contributed by Eric Delisle, SNC-Lavalin 
! --- V6.3-6.42_x1.1 140521      : Use ITIMES flag for processing times
!                                  from the SURFACE file
! --- V6.268-V6.3   100212  (DGS): add nested MET grids
! --- V6.1-V6.268   100108  (DGS): declare PTG as 1-D real
! --- V5.74-V6.1    050915  (DGS): allow for begin/end time format
!                                  resolved to seconds.
!                                  (retain compatibility with standard
!                                  PROFILE and SURFACE version)
!
! --- INPUTS:
!       IDATHRM - integer       - Mid-pt date/hour of required
!                                 data (YYYYJJJHH)
!         ISECM - integer       - Mid-pt second of required data (SSSS)
!         LECHO - logical       - Flag controlling printing of input
!                                 meteorological data fields
!        PTG(2) - real          - Default potential temperature grad.
!                                 for stable classes (deg. K/m)
!    Common block /METHD/ variables:
!         NXM, NYM, NZM, NSSTA, NPSTA, ZFACEM(mxnzp1), LCALGRD,
!         IMIXCTDM, ISIGMAV, ITIMES, ITIMEPRF
!    Common block /GRIDNEST/ variables:
!         ngrid
!    Parameters:
!         MXNX, MXNY, MXNZ, MXNZP1, MXPRFZ, MXMETDOM, MXSS, IO6
!
! --- OUTPUT:
!
!                    IZP - integer - Number of profile levels
!          WSPRF(mxprfz) - real    - 1-D vector wind speed profile (m/s)
!          WDPRF(mxprfz) - real    - 1-D vector wind dir. profile (deg)
!           TPRF(mxprfz) - real    - Temperature profile (deg. K)
!           ZPRF(mxprfz) - real    - Heights for data in profile (m)
!          SSPRF(mxprfz) - real    - 1-D scalar speed profile (m/s)
!          SWPRF(mxprfz) - real    - 1-D sigma-w profile (m/s)
!          SVPRF(mxprfz) - real    - 1-D sigma-v profile (m/s)
!           IPGT(mxnx,mxny,mxmetdom)
!                        - integer - PGT stability class
!          HTMIX(mxnx,mxny,mxmetdom)
!                        - real    - Mixing height (m)
!          USTAR(mxnx,mxny,mxmetdom)
!                        - real    - Surface friction velocity (m/s)
!         XMONIN(mxnx,mxny,mxmetdom)
!                        - real    - Monin-Obukhov length (m)
!          WSTAR(mxnx,mxny,mxmetdom)
!                        - real    - Convective velocity scale (m/s)
!            RMM(mxnx,mxny,mxmetdom)
!                        - real    - Precipitation rate (mm/hr)
!  TEMPSS(mxss,mxmetdom) - real    - Air temperature (deg. K) at
!                                    each surface met. station
!   RHOSS(mxss,mxmetdom) - real    - Air density (kg/m**3)
!   QSWSS(mxss,mxmetdom) - real    - Short wave solar radiation
!                                    (W/m**2)
!   IRHSS(mxss,mxmetdom) - integer - Relative humidity (percent)
!  IPCODE(mxss,mxmetdom) - integer - Precipitation code at each surface
!                                    station
!                DPTINVO - real    - Inversion strength at top of CBL
!                                    (jump in potential temperature K)
!                   LEOF - logical - End-Of-File flag
!
! --- RDMET5 called by:  COMP
! --- RDMET5 calls:      JULDAY, INCR, GRDAY, INITAR,
!                        INITAI, OUT, INITR2D, INITI2D,
!                        LSTAB, XTPRF, RDPROF5, TPERIOD, YR4, TCLOCK
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'methd.puf'
      include 'comparm.puf'
      include 'dispdat.puf'
      include 'grid.puf'
      include 'gridnest.puf'
!
! --- Profile data arrays
      real wsprf(mxprfz),wdprf(mxprfz),tprf(mxprfz),zprf(mxprfz)
      real ssprf(mxprfz),swprf(mxprfz),svprf(mxprfz)
! --- Real 2-D fields
      real htmix(mxnx,mxny,mxmetdom),ustar(mxnx,mxny,mxmetdom)
      real xmonin(mxnx,mxny,mxmetdom)
      real wstar(mxnx,mxny,mxmetdom),rmm(mxnx,mxny,mxmetdom)
! --- Real 1-D fields
      real tempss(mxss,mxmetdom),rhoss(mxss,mxmetdom)
      real qswss(mxss,mxmetdom)
      real ptg(2)
!
      integer ipgt(mxnx,mxny,mxmetdom),irhss(mxss,mxmetdom)
      integer ipcode(mxss,mxmetdom)
!
      character messag*70,aline*132
!
      logical lecho,ldate,problem,leof,lcalmprof
!
      data ldate/.false./
      data vonk/0.4/,third/0.3333333/
! --- There should be only 1 met grid
      if(ngrid.NE.1) then
         write(io6,*)
         write(io6,*)'RDMET5:  Nested grid information is bad'
         write(io6,*)'Expected 1 met grid, Found ',ngrid
         stop 'HALTED in RDMET5 -- See list file'
      else
! ---    Use explicit variable for '1'
         kg=1
      endif
! --- Set logical to halt program if problem is found
1     problem=.FALSE.
! -------------------------------------------------------------------
!  -- Read one record of met data from AERMET SURFACE file
! -------------------------------------------------------------------
! --- Read entire line as character variable
4     read(io32,'(a132)',end=998) aline
! --- Extract fields for regular record (internal read)
      if(itimes.EQ.1) then
         read(aline,*) iby,ibm,ibd,ibjul,ibh,ksb,&
                       iey,iem,ied,iejul,ieh,kse,&
                       hflx,ustr,wstr,vptgup,ziconv,zimech,xmon,z0m,&
                       brat,alb,wsa,wda,za,tmpk,zt
      else
         read(aline,*) iey,iem,ied,iejul,ieh,&
                       hflx,ustr,wstr,vptgup,ziconv,zimech,xmon,z0m,&
                       brat,alb,wsa,wda,za,tmpk,zt
      endif
! --- Use default values for parameters that are not in file
5     qswrad=0.0
      irelhum=85
      pmmhr=0.0
      ipc=0
6     continue
!
! --- Skip blank lines
      if(itimes.EQ.1) then
         if(ibm.EQ.0 .OR. ibd.EQ.0) goto 4
      else
         if(iem.EQ.0 .OR. ied.EQ.0) goto 4
      endif
! -------------------------------------------------------------------
! --- Process date-time information
! --- V6.42_x1.1, Level 140521
      if(itimes.EQ.1) then
! ---    Enforce YYYY format for year
         call YR4(io6,iby,ierrb)
         call YR4(io6,iey,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDMET5'
         kdathrb=iby*100000+ibjul*100+ibh
         kdathre=iey*100000+iejul*100+ieh
      else
! ---    Enforce YYYY format for year
         call YR4(io6,iey,ierrb)
         if(ierrb.NE.0) stop 'Halted in RDMET5'
! ---    SURFACE.DAT uses a 1-24 clock (hour ending).  Change
! ---    time to the beginning of the hour (subtract 1 hour)
         ih=ieh-1
         call julday(io6,iey,iem,ied,ijul)
         kdathrb=iey*100000+ijul*100+ih
         ksb=0
! ---    Increment time 1 hour to get end time
         call incr(io6,iey,ijul,ih,1)
         kdathre=iey*100000+ijul*100+ih
         kse=0
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,kdathrb,idum,idum,idum,ksb)
      call TCLOCK(io6,kdathre,idum,idum,idum,kse)
! --- Check period of met data with mid-point of step
      call TPERIOD(0,idathrm,isecm,kdathrb,ksb,kdathre,&
                   kse,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         go to 1
      elseif(ilocate.EQ.-1) then
         write(io6,*)'ERROR in subr. RDMET5 -- current time not found',&
         ' in the SURFACE data file'
         write(io6,*)' --   Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksb
         write(io6,*)'                           to ',kdathre,kse
         stop 'Halted in RDMET5 -- see list file.'
      endif
! --- Set w-star to 0.0 rather than -9.0 when the heat flux is negative
      if(hflx.LT.0.0) wstr=0.0
! --- Set current mixing height (max of convective and mechanical)
      zmix=AMAX1(ziconv,zimech)
! --- Check current value of roughness with value in /METHD/
      if(z0m.LE.0.0) then
! ---    Roughness length missing, keep previous value
         z0m=z0(1,1,kg)
      elseif(z0m.NE.z0(1,1,kg)) then
! ---    Valid roughness length changed, update array
! !!!    write(io6,*)'RDMET5:  Roughness length changed'
! !!!    write(io6,*)'         Date-Hour      - ',ksdathr
! !!!    write(io6,*)'         old,new z0(m)  - ',z0(1,1,kg),z0m
         call initr2d(z0m,mxnx,mxny,nxm(kg),nym(kg),z0(1,1,kg))
      endif
! --- Recover from missing u*, L by assuming that there is no
! --- stability influence, and no flow .... CALM.  This is likely
! --- because METPRO will give non-missing surface layer parameters
! --- for "real" data.  However, if onsite tower data are missing,
! --- and the offsite data are "zero" for speed and possibly direction,
! --- then the period must be treated as a calm.
      if(ustr.LE.0.0) then
         write(io6,*)'RDMET5: U-star missing in SURFACE'
         write(io6,*)'        Period Beginning:',kdathrb,ksb
         write(io6,*)'        Period Ending   :',kdathre,kse
         write(io6,*)'        Assuming CALM condition'
         ustr=0.0
         xmon=9999.
      endif
! --- Estimate PG class using Golder(1972)
      kst=LSTAB(xmon,z0m)
! -------------------------------------------------------------------
!  -- Read records for this hour from AERMET PROFILE file
! -------------------------------------------------------------------
      call RDPROF5(idathrm,isecm,lecho,izp,wsprf,wdprf,tprf,zprf,&
                   ssprf,swprf,svprf,dptinvo,&
                   ldathre,lse,ldathrb,lsb,leof)
      if(leof) return
! --- Check for consistent date-time for period among records
      if(kdathre.NE.ldathre .OR. kse.NE.lse) then
         write(io6,*)'RDMET5: PROFILE does not match SURFACE period'
         problem=.TRUE.
      endif
! --- Halt program now if problem was found with SURFACE or PROFILE
      if(PROBLEM) then
         write(io6,*)' PROFILE per. = ',ldathrb,lsb,ldathre,lse
         write(io6,*)' SURFACE per. = ',kdathrb,ksb,kdathre,kse
         stop 'Halted in RDMET5 -- see list file'
      endif
! --- Check for calms over entire Profile
      lcalmprof=.false.
      icalm=0
      do i=1,izp
        if(wsprf(i).LE.0.0.AND.ssprf(i).LE.0.0) icalm=icalm+1
      enddo
      if(icalm==izp) lcalmprof=.true.
! --- Add levels at .25, .5, and 1.0 times the mixing height as in
! --- CTDM+, and add level at top of model, if mxprfz will allow it
! --- Do not add CTDM+ levels if mixing height is less than 10 m
      nznew=4
      if(zmix.LT.10.0) nznew=1
      nznew=MIN0(nznew,mxprfz-izp)
      if(nznew.GT.0) then
         do iz=1,nznew
! ---       Set new level
            iznew=izp+iz
            if(iz.EQ.1) then
               zprf(iznew)=zfacem(nz+1)
            elseif(iz.EQ.2) then
               zprf(iznew)=zmix
            else
               zprf(iznew)=.5*zprf(iznew-1)
            endif
! ---       Set initial values to missing
            wsprf(iznew)=-999.
            ssprf(iznew)=-999.
            wdprf(iznew)=-999.
            tprf(iznew)=-999.
            swprf(iznew)=-999.
            svprf(iznew)=-999.
! ---       Reset logical for reporting problems
            problem=.FALSE.
! ---       Extract value at this level, and add to top of profile
            if(lcalmprof) then
! ---          Only speed available is calm from offsite; do not profile
               wsprf(iznew)=AMAX1(MAXVAL(wsprf),MAXVAL(ssprf))
               ssprf(iznew)=wsprf(iznew)
            else
               call XTPRF(izp,wsprf,zprf,zprf(iznew),'spd',z0m,xmon,&
                       zmix,kst,ptg,wsprf(iznew),problem)
               call XTPRF(izp,ssprf,zprf,zprf(iznew),'spd',z0m,xmon,&
                       zmix,kst,ptg,ssprf(iznew),problem)
            endif
            call XTPRF(izp,wdprf,zprf,zprf(iznew),'dir',z0m,xmon,&
                    zmix,kst,ptg,wdprf(iznew),problem)
            call XTPRF(izp,tprf,zprf,zprf(iznew),'tmp',z0m,xmon,&
                    zmix,kst,ptg,tprf(iznew),problem)
! ---       Need at least 1 valid level of spd, dir, and tmp
            if(PROBLEM) then
               write(io6,*)'ERROR in subr. RDMET5 -- speed, dir, or ',&
                    'temperature missing at all levels in PROFILE'
               write(io6,*)'        Period Beginning:',ldathrb,lsb
               write(io6,*)'        Period Ending   :',ldathre,lse
               write(*,*)
               stop 'Halted in RDMET5 -- see list file.'
            endif
            call XTPRF(izp,swprf,zprf,zprf(iznew),'sig',z0m,xmon,&
                    zmix,kst,ptg,swprf(iznew),problem)
            call XTPRF(izp,svprf,zprf,zprf(iznew),'sig',z0m,xmon,&
                    zmix,kst,ptg,svprf(iznew),problem)
         enddo
! --     Sort profile levels
         izp=iznew
         call STPRF(nznew,izp,zprf,wsprf,wdprf,tprf,swprf,svprf,ssprf)
      endif
! -- FILL MISSING LEVELS FOR TEMPERATURE AND WINDS
! -- REV CdA
      do iz=1,izp
        if(wsprf(iz).LT.0.0) then
           call XTPRF(izp,wsprf,zprf,zprf(iz),'spd',z0m,xmon,&
                    zmix,kst,ptg,wsprf(iz),problem)
           call XTPRF(izp,wdprf,zprf,zprf(iz),'dir',z0m,xmon,&
                    zmix,kst,ptg,wdprf(iz),problem)
        endif
        if(ssprf(iz).LT.0.0) then
           call XTPRF(izp,ssprf,zprf,zprf(iz),'spd',z0m,xmon,&
                    zmix,kst,ptg,ssprf(iz),problem)
        endif
        if(tprf(iz).LT.0.0) then
            call XTPRF(izp,tprf,zprf,zprf(iz),'tmp',z0m,xmon,&
                    zmix,kst,ptg,tprf(iz),problem)
        endif
      enddo 
! -- REV CdA
! --- Set the 2-D fields
      call initi2d(kst,mxnx,mxny,nxm(kg),nym(kg),ipgt(1,1,kg))
      call initr2d(ustr,mxnx,mxny,nxm(kg),nym(kg),ustar(1,1,kg))
      call initr2d(zmix,mxnx,mxny,nxm(kg),nym(kg),htmix(1,1,kg))
      call initr2d(xmon,mxnx,mxny,nxm(kg),nym(kg),xmonin(1,1,kg))
      call initr2d(wstr,mxnx,mxny,nxm(kg),nym(kg),wstar(1,1,kg))
      call initr2d(pmmhr,mxnx,mxny,nxm(kg),nym(kg),rmm(1,1,kg))
!c --- Pass surface temperature to TMPK (assume z=z0m)
!      call XTPRF(izp,tprf,zprf,z0m,'tmp',z0m,xmon,zmix,kst,
!     &           ptg,tmpk,problem)
! --- Set the 1-D fields
      call initar(tmpk,nssta(kg),tempss(1,kg))
      call initar(1.28,nssta(kg),rhoss(1,kg))
      call initar(qswrad,nssta(kg),qswss(1,kg))
      call initai(irelhum,nssta(kg),irhss(1,kg))
      call initai(ipc,nssta(kg),ipcode(1,kg))
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ MET. FIELDS BEGINNING: ',kdathrb,ksb
         write(io6,*)'------            AND ENDING: ',kdathre,kse
         write(io6,*)
!
! ---    2-D fields
         messag='PGT stability class'
         call out(xdum,ipgt(1,1,kg),2,1,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Friction velocity (m/s)'
         call out(ustar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Mixing height (m)'
         call out(htmix(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Monin-Obukhov length (m)'
         call out(xmonin(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         messag='Convective velocity scale (m/s)'
         call out(wstar(1,1,kg),idum,1,5,ldate,messag,&
                  nxm(kg),nym(kg))
         if(npsta(kg).gt.0)then
            messag='Precipitation rate (mm/hr)'
            call out(rmm(1,1,kg),idum,1,5,ldate,messag,&
                     nxm(kg),nym(kg))
         endif
!
! ---    1-D fields
         write(io6,*)'TEMPSS (deg. K) = ',(tempss(n,kg),n=1,nssta(kg))
         write(io6,*)'RHOSS (kg/m**3)  = ',(rhoss(n,kg),n=1,nssta(kg))
         write(io6,*)'QSWSS (W/m**2)   = ',(qswss(n,kg),n=1,nssta(kg))
         write(io6,*)'IRHSS (percent)  = ',(irhss(n,kg),n=1,nssta(kg))
         if(npsta(kg).gt.0)write(io6,*)'IPCODE           = ',&
          (ipcode(n,kg),n=1,nssta(kg))
      endif
!
      return
998   write(io6,*)'RDMET5: End-Of-File found in SURFACE file'
      leof=.TRUE.
      return
      end
!----------------------------------------------------------------------
      subroutine stprf(n,izp,zprf,wsprf,wdprf,tprf,swprf,svprf,ssprf)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521             STPRF
!                D. Strimaitis   SRC
!
! --- PURPOSE:  Sort arrays of PROFILE data
!
! --- INPUTS:
!             N - integer       - number of new levels at top of
!                                 data arrays
!           IZP - integer       - total number of levels in data arrays
!          ZPRF - real array    - heights above ground for met data
!         WSPRF - real array    - wind speed (m/s)
!         WDPRF - real array    - wind direction (deg)
!          TPRF - real array    - temperature (K)
!         SWPRF - real array    - sigma-w (m/s)
!         SVPRF - real array    - sigma-v (m/s)
!         SSPRF - real array    - scalar avg. wind speed (m/s)
!
! --- OUTPUT:
!           IZP - integer       - total number of levels in data arrays
!          ZPRF - real array    - heights above ground for met data
!         WSPRF - real array    - vector avg. wind speed (m/s)
!         WDPRF - real array    - wind direction (deg)
!          TPRF - real array    - temperature (K)
!         SWPRF - real array    - sigma-w (m/s)
!         SVPRF - real array    - sigma-v (m/s)
!         SSPRF - real array    - scalar avg. wind speed (m/s)
!
! --- STPRF called by:  RDMET4, RDMET5
! --- STPRF calls    :  none
!----------------------------------------------------------------------
!
      real zprf(izp),wsprf(izp),wdprf(izp),tprf(izp)
      real swprf(izp),svprf(izp),ssprf(izp)
! --- Local arrays
      real znew(4),wsnew(4),wdnew(4),tnew(4)
      real swnew(4),svnew(4),ssnew(4)
! --- Set the N new values aside (up to 4)
      itop=izp-n
      do i=1,n
         j=itop+i
         znew(i)=zprf(j)
         tnew(i)=tprf(j)
         wsnew(i)=wsprf(j)
         wdnew(i)=wdprf(j)
         swnew(i)=swprf(j)
         svnew(i)=svprf(j)
         ssnew(i)=ssprf(j)
      enddo
! --- Find places for new values
      do j=1,n
         do i=itop,1,-1
! ---       (Drop any duplicate profile entry ...)
            if(zprf(i).EQ.znew(j)) goto 20
! ---       (Place is found when profile height first becomes less)
            if(zprf(i).LT.znew(j)) then
               insert=i+1
               goto 10
            endif
         enddo
! ---    Current znew is less than all zprf
         insert=1
10       continue
! ---    Shift values above "insert" up in the array
         itop=itop+1
         do i=itop,insert+1,-1
            zprf(i)=zprf(i-1)
            tprf(i)=tprf(i-1)
            wsprf(i)=wsprf(i-1)
            wdprf(i)=wdprf(i-1)
            swprf(i)=swprf(i-1)
            svprf(i)=svprf(i-1)
            ssprf(i)=ssprf(i-1)
         enddo
! ---    Place new values into profile at insert
         zprf(insert)=znew(j)
         tprf(insert)=tnew(j)
         wsprf(insert)=wsnew(j)
         wdprf(insert)=wdnew(j)
         swprf(insert)=swnew(j)
         svprf(insert)=svnew(j)
         ssprf(insert)=ssnew(j)
20       continue
      enddo
! --- Profile arrays now contain ITOP levels
      izp=itop
      return
      end
!----------------------------------------------------------------------
      subroutine xtprf(nzp,array,zarray,zht,ctype,z0m,el,zmix,kst,&
                       ptg,value,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 971107             XTPRF
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Extract value from PROFILE at a specified height,
!               using CTDM profiling constructs when needed
!
! --- UPDATE
! --- V4.0-V5.0     971107  (DGS): place lower bound on wind speed
!                                  equal to WSCALM (any elevation),
!                                  provided nearest speeds not less
!                                  than WSCALM; min speed = WSCALM/10
!                   971107  (DGS): separate max/min function calls
!                   971107  (DGS): remove IO unit from arg list
!                   971107  (DGS): alter CTDM temperature extrapolation
!                                  above top of "tower"
!                   971107  (DGS): do not extrapolate turbulence above
!                                  mixing lid for CBL
!
! --- INPUTS:
!           NZP - integer       - number of levels in data arrays
!         ARRAY - real array    - meteorological data array
!        ZARRAY - real array    - heights above ground for met data
!           ZHT - real          - height for retrieval
!         CTYPE - character     - type of data
!                                 sig: sigma-v or sigma-w
!                                 tmp: temperature
!                                 dir: wind direction
!                                 spd: wind speed (vector or scalar avg)
!           Z0M - real          - roughness length (m)
!            EL - real          - Monin-Obukhov length (m)
!          ZMIX - real          - mixing height (m)
!           KST - integer       - stability class
!           PTG - real array    - default potential temperature gradient
!                                 for (1) class E and (2) class F
!         VALUE - real          - default value if unavailable
!       PROBLEM - logical       - trouble flag initialized outside call
!
!    Common block /COMPARM/ variables:
!         WSCALM
!
! --- OUTPUT:
!         VALUE - real          - value at zht
!       PROBLEM - logical       - trouble flag (.TRUE. if problem found)
!
! --- XTPRF called by:  RDMET4,PTLAPS,TURBSET,ADVECT,WINDSET,GETPRFP
! --- XTPRF calls    :  PSIM
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'comparm.puf'
!
      real array(nzp),zarray(nzp),ptg(2)
      character*3 ctype
      logical problem,lctdm
! --- Set switch for extrapolating temperature above sounding like CTDM
      data lctdm/.FALSE./
      data zero/0.0/,drylaps/-.0098/
! --- Find index for valid data above/below zht (valid data are assumed
! --- to be greater than or equal to zero)
      itop=nzp+1
      ibot=0
      itopp1=itop
      ibotm1=ibot
! --- Level above
      do iz=nzp,1,-1
         if(zarray(iz).GT.zht .AND. array(iz).GE.zero) then
            itopp1=itop
            itop=iz
         endif
      enddo
! --- Level below
      do iz=1,itop-1
         if(zarray(iz).LE.zht .AND. array(iz).GE.zero) then
            ibotm1=ibot
            ibot=iz
         endif
      enddo
! --- Set default temperature gradients
      if(ctype.EQ.'tmp') then
! ---    Set gradient in the surface layer
         if(kst.LE.4) then
            tgradsl=drylaps
         else
            tgradsl=ptg(kst-4)+drylaps
         endif
! ---    Set gradient aloft equal to half the dry adiabatic lapse rate
         tgradup=0.5*drylaps
! ---    Reset gradient for CTDM extrapolation above mixing height
         if(LCTDM) tgradup=AMAX1(tgradsl,ptg(1)+drylaps)
      endif
! --- Set surface layer height for CTDM wind speed profiling
! --- For convective case, slht is the larger of 2L or zmix/10,
! --- but no greater than 200 m
      if(ctype.EQ.'spd') then
         if(el.LT.zero .AND. el.GT.-100.) then
            slht=AMAX1(2.*ABS(el),0.1*zmix)
            slht=AMIN1(slht,200.)
         else
            slht=zmix
         endif
      endif
! --- Case 1:  no valid data are found; return initial value
      if(ibot.EQ.0 .AND. itop.GT.nzp) then
         problem=.TRUE.
! --- Case 2:  zht is below first valid level of profile
      elseif(ibot.EQ.0) then
         if(ctype.EQ.'dir' .OR. ctype.EQ.'sig') then
! ---       Use first valid value
            value=array(itop)
         elseif(ctype.EQ.'tmp') then
            if(itopp1.LE.nzp) then
! ---          Use T gradient from first 2 valid levels
               scale=(zht-zarray(itop))/(zarray(itopp1)-zarray(itop))
               diff=array(itopp1)-array(itop)
               value=array(itop)+diff*scale
            else
! ---          Use first valid T and default surface layer T gradient
               value=array(itop)+(zht-zarray(itop))*tgradsl
            endif
         elseif(ctype.EQ.'spd') then
            if(array(itop).LT.wscalm) then
               value=array(itop)
            elseif(zht.GT.slht) then
! ---          Speed is constant above surface layer
               value=array(itop)
            else
! ---          Speed follows similarity theory within surface layer
               ztop=AMIN1(zarray(itop),slht)
               psitop=PSIM(el,ztop)
               ztop0=ALOG(ztop/z0m)
               psiht=PSIM(el,zht)
               zht0=ALOG(AMAX1(1.0,zht/z0m))
               value=array(itop)*(zht0-psiht)/(ztop0-psitop)
! ---          Smallest speed allowed from profiling is WSCALM
               value=AMAX1(value,wscalm)
            endif
         endif
! --- Case 3:  zht is equal to zarray(ibot)
      elseif(zarray(ibot).EQ.zht) then
         value=array(ibot)
! --- Case 4:  zht is above last valid level of profile
      elseif(itop.GT.nzp) then
         if(ctype.EQ.'dir') then
! ---       Use last valid value
            value=array(ibot)
         elseif(ctype.EQ.'sig') then
! ---       Use last valid value unless zht is above the CBL
            if(zht.LE.zmix .OR. el.GE.zero) then
               value=array(ibot)
            else
! ---          Treat as missing as in Case 1
               problem=.TRUE.
            endif
         elseif(ctype.EQ.'tmp') then
            if(ibotm1.LE.0) then
! ---          There is only 1 valid Temp: use with default gradients
! ---          Compute Temp at mixing ht
               if(zarray(ibot).GE.zmix) then
                  dtdz=tgradup
               else
                  dtdz=tgradsl
               endif
               tmix=array(ibot)+(zmix-zarray(ibot))*dtdz
! ---          Use gradient from mixing ht to zht
               if(zht.GE.zmix) then
                  dtdz=tgradup
               else
                  dtdz=tgradsl
               endif
               value=tmix+(zht-zmix)*dtdz
            else
! ---          Extrapolate based on upper T gradient (last 2 T's)
! ---          Compute gradient from last 2 valid levels
               dtdz=(array(ibot)-array(ibotm1))/&
                    (zarray(ibot)-zarray(ibotm1))
               if(LCTDM) then
! ---             Use CTDM extrapolation methods
                  if(dtdz.GT.drylaps) then
! ---                "Stable"
! ---                Extend last gradient to zht regardless of lid
                     value=array(ibot)+dtdz*(zht-zarray(ibot))
                  else
! ---                "Non-stable"
! ---                Use adiabat to mix lid, then stable gradient above
                     dtdz=drylaps
                     value=array(ibot)+dtdz*(zht-zarray(ibot))
                     delzmix=zht-zmix
                     if(zarray(ibot).LE.zmix .AND. delzmix.GT.zero) then
                        value=value+delzmix*(tgradup-dtdz)
                     endif
                  endif
               else
! ---             Use modified extrapolation method
                  dtdz=AMAX1(drylaps,dtdz)
                  if(zht.LE.zmix) then
! ---                Below lid, so use measured gradient
                     value=array(ibot)+dtdz*(zht-zarray(ibot))
                  elseif(zarray(ibot).GT.zmix) then
! ---                Above lid, and dtdz either lies above or straddles
! ---                lid; use dtdz as best estimate of gradient aloft
                     value=array(ibot)+dtdz*(zht-zarray(ibot))
                  else
! ---                Above lid and measurements are below lid
! ---                First extend T to mixing ht using measured dtdz
                     tmix=array(ibot)+dtdz*(zmix-zarray(ibot))
! ---                Now extrapolate using gradient aloft
                     if(el.GT.zero) then
! ---                   Stable SBL: dtdz aloft may be less than default
                        dtdz=AMIN1(dtdz,tgradup)
                     else
                        dtdz=tgradup
                     endif
                     value=tmix+(zht-zmix)*dtdz
                  endif
               endif
            endif
         elseif(ctype.EQ.'spd') then
            if(array(ibot).LT.wscalm) then
               value=array(ibot)
            elseif(zarray(ibot).GT.slht) then
! ---          Speed is constant above surface layer
               value=array(ibot)
            else
! ---          Speed follows similarity theory within surface layer
               psibot=PSIM(el,zarray(ibot))
               zbot0=ALOG(zarray(ibot)/z0m)
               zval=AMIN1(zht,slht)
               psiht=PSIM(el,zval)
               zval0=ALOG(zval/z0m)
               value=array(ibot)*(zval0-psiht)/(zbot0-psibot)
! ---          Smallest speed returned from profiling is WSCALM
               value=AMAX1(wscalm,value)
            endif
         endif
! --- Case 5:  zht is between two valid levels
      else
! ---    Use linear interpolation
         scale=(zht-zarray(ibot))/(zarray(itop)-zarray(ibot))
         diff=array(itop)-array(ibot)
         if(ctype.EQ.'dir') then
! ---       Correct direction difference greater than 180 degrees
            if(diff.GT.180.0) then
               diff=diff-360.0
            elseif(diff.LT.-180.0) then
               diff=diff+360.0
            endif
         endif
         value=array(ibot)+diff*scale
      endif
! --- Final restriction on wind speed: it cannot be zero
      if(ctype.EQ.'spd') value=AMAX1(value,(0.1*wscalm))
!
      return
      end
!----------------------------------------------------------------------
      subroutine powlaw(ht,zmeas,ws,p,wsht)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090612            POWLAW
! ---            J. Scire
!
! --- PURPOSE:  Extrapolate wind speed to height HT using a power
!               law profile adjustment factor
!
! --- UPDATES:
! --- V5.0-V6.265   090612  (DGS): add check for invalid power-law exp
!
! --- INPUTS:
!              HT - real         - New height (m)
!           ZMEAS - real         - Old (measurement) height (m)
!              WS - real         - Measured wind speed (m/s)
!               P - real         - Power law exponent
!
!    Common block /COMPARM/ variables:
!         WSCALM
!
! --- OUTPUT:
!            WSHT - real         - Value of wind speed at HT (m/s)
!
! --- POWLAW called by:  RDISC, RDPLM, ADVECT, WINDSET
! --- POWLAW calls:      none
!----------------------------------------------------------------------
      include 'params.puf'
      include 'comparm.puf'
! --- Trap invalid power-law exponent
      if(p.LE.0.0) then
         write(io6,*)
         write(io6,*)'POWLAW:  Invalid power-law exponent found'
         write(io6,*)'          P = ',p
         stop 'Halted in POWLAW --- see list file'
      endif
!
! --- Compute the wind speed at HT using the ISC2 convention,
! --- i.e., assume the wind speed is constant below either 10 m OR the
! --- measurement height, whichever is lower
      if(ht.ge.10.0)then
         wsht=ws*(ht/zmeas)**p
      else if(zmeas.gt.10.0)then
         wsht=ws*(10.0/zmeas)**p
      else
         wsht=ws
      endif
!
! --- Wind speed is not allowed to be less than WSCALM m/s
      wsht=amax1(wsht,wscalm)
!
      return
      end
!----------------------------------------------------------------------
      integer function lstab(el,zr0)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521             LSTAB
!
! --- PURPOSE:  Calculate a PG class given the Monin-Obukhov length
!               and the surface roughness from Golder's 1972 curves
!
! --- Taken from CTDMplus subroutine of the same name
!
! ASSUMPTIONS: THE DIVIDING LINES BETWEEN CATEGORIES ARE ASSUMED TO BE
!               LINEAR.
!
! LIMITATIONS: THIS FUNCTION IS ONLY VALID FOR 0.01 <= Z0 <= 0.5(M).
!              HOWEVER, RESULTS ARE EXTENDED TO OTHER VALUES OF Z0 BY
!              USING Z0 = 0.01 IF Z0 < 0.01 M, AND BY USING Z0 = 0.5
!              IF Z0 > 0.5 M.
!
! --- INPUTS:
!       EL      REAL    MONIN-OBUKHOV LENGHT (M)
!       ZR0     REAL    SURFACE ROUGHNESS LENGTH (M)
!
! --- OUTPUT:
!       LSTAB   INT     P-G STABILITY CATEGORY 1=A, 2=B, ETC.
!
! CALLING ROUTINES: SEQMOD
!
! EXTERNAL ROUTINES: NONE
!
! INTERNAL FUNCTIONS:
!       XL - EQUATION OF DIVIDING LINE BETWEEN P-G STABILITY CLASSES
!
! INTRINSIC FUNCTIONS: ALOG
!
! REFERENCES:
!       GOLDER, D. (1972): RELATIONS AMONG STABILITY PARAMETERS IN THE
!                       SURFACE LAYER, BOUNDARY-LAYER METEOROLOGY, 3:56.
!
!-----------------------------------------------------------------------
!
        REAL            EL, XEL, XL, Z0, ZR0
!
!
        XL(Y,XM,B)=XM/(ALOG(Y)-B)
!
        Z0 = ZR0
        IF(Z0 .GT. 0.5) Z0 = 0.5
        IF(Z0 .LT. 0.01) Z0 = 0.01
        IF(EL .LT. 0.0) THEN
            XEL = -EL
            IF(XEL .LE. XL(Z0,-70.0,4.35)) THEN
!               STABILITY A
                LSTAB=1
              ELSE IF(XEL .LE. XL(Z0,-85.2,0.502)) THEN
!               STABILITY B
                LSTAB=2
              ELSE IF(XEL .LE. XL(Z0,-245.,0.050)) THEN
!               STABILITY C
                LSTAB=3
              ELSE
!               STABILITY D
                LSTAB=4
            ENDIF
          ELSE
            IF(EL .GE. XL(Z0,-327.,0.627)) THEN
!               STABILITY D
                LSTAB=4
              ELSE IF(EL .GE. XL(Z0,-70.0,0.295)) THEN
!               STABILITY E
                LSTAB=5
              ELSE
!               STABILITY F
                LSTAB=6
            ENDIF
        ENDIF
!
        RETURN
        END
!----------------------------------------------------------------------
      subroutine stab2l(istab,zr0,eli)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521            STAB2L
! ---            D. Strimaitis
!
! --- PURPOSE:  Calculate a Monin-Obukhov length given the PG class
!               and the surface roughness from Golder's 1972 curves
!               as presented in Seinfeld, 1986 (Atm. Chem. & Phys. of
!               Air Poll.)
!
! LIMITATIONS: THIS FUNCTION IS VALID FOR 0.01 <= Z0 <= 0.5(M).
!              HOWEVER, RESULTS ARE EXTENDED TO OTHER VALUES OF Z0 BY
!              USING Z0 = 0.01 IF Z0 < 0.01 M, AND BY USING Z0 = 0.5
!              IF Z0 > 0.5 M.
!
! --- INPUTS:
!       ISTAB - integer    - P-G stability category 1=A, 2=B, ETC.
!         ZR0 - real       - Surface roughness length (m)
!
! --- OUTPUT:
!         ELI - real       - 1/Monin-Obukhov length (1/m)
!
! --- STAB2L called by:  RDPLM
! --- STAB2L calls:      none
!----------------------------------------------------------------------
      real a(6),b(6)
      data a/-.096,-.037,-.002,0.0,.004,.035/
      data b/.029,.029,.018,0.0,-.018,-.036/
      z0 = zr0
      if(z0.GT.0.5) z0 = 0.5
      if(z0.LT.0.01) z0 = 0.01
      eli=a(istab)+b(istab)*ALOG10(z0)
      return
      end
!----------------------------------------------------------------------
      function psim(el,zref)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 960521              PSIM
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Compute the Businger-Dyer surface layer PSI function
! ---           for momentum
!
! --- INPUTS:
!          EL - real       - Monin-Obukhov length (m)
!        ZREF - real       - Reference height (m)
!
! --- OUTPUT:
!        PSIM - real       - Surface layer PSI function for momentum
!
! --- PSIM called by:  RDPLM, XTPRF
! --- PSIM calls:      none
!----------------------------------------------------------------------
      data piby2/1.5707963/
      elabs=abs(el)
      if(el.lt.0.0.and.elabs.lt.9990.) then
! ---    Convective
         ratio=zref/elabs
         if(ratio.gt.1.0) ratio=1.0
         xsq=SQRT(1.+15.*ratio)
         x=SQRT(xsq)
         term1=2.*ALOG(0.5*(1.+x))
         term2=ALOG(0.5*(1.+xsq))
         term3=-2.*ATAN(x)+piby2
         psim=term1+term2+term3
      elseif(elabs.ge.9990.) then
! ---    Neutral
         psim=0.0
      else
! ---    Stable
         ratio=zref/el
         if(ratio.gt.1.0) ratio=1.0
         psim=-4.7*ratio
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine numrise(mprm,ldbhr,h,teff,reff,weff,sigy0,sigz0,ntr,&
                         xtr,ztr,rtr,htr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108           NUMRISE
!                X.(J.) Zhang, J. Scire
!
! --- PURPOSE:  Compute plume rise using a numerical solution to the
!               Non-Boussinesq conservation laws.  Model allows:
!
!               (1) arbitrary ambient temperature stratifications
!               (2) arbitrary uni-directional wind stratification
!               (3) any size of finite emission source
!               (4) is free of the Boussinesq approximation
!               (5) radiative heat loss
!
! --- UPDATE
! --- V6.26-V6.268  100108  (DGS): Add call to WAKE_ADDBID to add
!                                  BID to PRIME-source sigmas upwind
!                                  of entering the wake
! --- V6.2-V6.26    080430  (DGS): Add numerical rise output to
!                                  RISE.DAT file
! --- V5.725-V6.2   070629  (DGS): Initialize dzds=0.0 (debug output)
! --- V5.71-V5.725  050128  (DGS): initialize first element of MXNW
!                                  arrays to stack conditions to avoid
!                                  problems with the NN pointer being
!                                  old
! --- V5.7-V5.71    030528  (DGS): modify args of MIN to be integer
! --- V5.4-V5.7     030402  (DGS): add PRIME-related features to model
!                                  vertical streamline deflection near
!                                  buildings, to compute diffusion
!                                  (sigmas) in bldg wake, and to
!                                  determine plume/cavity interaction
!                   030402  (DGS): Revise the plume rise-angle criterion
!                                  for wake effects from 45 degrees to
!                                  20 degrees (default -- actual value
!                                  is set in block data and READ62)
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.1-V5.2     991104  (DGS): revise distance to final momentum rise
! --- V5.0-V5.1     990729  (DGS): allow for zero rise (weff=0) and
!                                  also use W<0 to stop rise instead
!                                  of PHI<0 for stable regime
! --- V4.0-V5.0     971107  (DGS): replace heat flux (KW) with input
!                                  temperature, exit velocity, and
!                                  radius
!
! --- INPUTS:
!          MPRM - integer       - Flag for PRIME building downwash calc
!                                 0 = no PRIME calculations
!                                 1 = PRIME method ACTIVE
!         LDBHR - logical       - Flag for debug write statements
!             H - real          - Release height (m)
!          TEFF - real          - Effective temperature of release (K)
!          REFF - real          - Effective radius of release (m)
!          WEFF - real          - Effective exit velocity (m/s)
!         SIGY0 - real          - Initial sigma-y of the source (m)
!         SIGZ0 - real          - Initial sigma-z of the source (m)
!           NTR - integer       - Number of points in trajectory passed
!                                 back to calling program (final point
!                                 is "final rise")
!
!     Common block /AMBIENT/ variables:
!           NZA,UAMB(mxprfz),RAMB(mxprfz),DEDZ(mxprfp1),TAMB(mxprfz),
!           ZFACEA(mxprfp1),TAMB0,RAMB0
!     Common block /NUMPARM/ variables:
!           ZMIN, DS0, NSTEP, SLAST, GRAVI, PHICHECK
!     Common block /OUTPT/ variables:
!           INRISE
!     Common block /WAKEDAT/ variables:
!           HB, WB, XLB, RB, HR, XLR, XLC, XBADJ, YBADJ
!     Parameters:
!           MXNW, MXPRFZ, MXPRFP1, MXENT, MXENTP1, IO6, IOX
!
! --- OUTPUT:
!        XTR(ntr) - real          - Downwind distance (m)
!        ZTR(ntr) - real          - Plume centerline height (m)
!        RTR(ntr) - real          - Plume radius (m)
!        HTR(ntr) - real          - Plume rise (m) without streamline
!                                   adjustments (for BID when PRIME
!                                   building downwash is used)
!
! --- NUMRISE called by:  AREAS2, POINTS1, POINTS2,
! --- NUMRISE calls:      ZMET,LUMP,RATE,MARCHING,UNLUMP
!                         ZSTREAM, POSITION, WAKE_U
!                         WAKE_DRDX, WAKE_DFSN, WAKE_FQC,
!                         NRISEOUT, WAKE_ADDBID
!----------------------------------------------------------------------
! --- Notation --- in (KG,M,S) units
!               NN:     Number of points along rise trajectory
!               X:      PLUME LOCATION (downwind)
!               Y:      PLUME LOCATION (crosswind)
!               Z:      PLUME HEIGHT
!               H:      Release height (flame ht., stack ht.)
!               ZE:     PLUME EQUILIBRIUM HEIGHT
!               S:      LENGTH ALONG PLUME CENTERLINE
!               R:      PLUME RADIUS
!               U:      PLUME HORIZONTAL VELOCITY
!               W:      PLUME VERTICAL VELOCITY
!               USC:    VELOCITY ALONG PLUME CENTERLINE
!               PHI:    ANGLE BETWEEN PLUME TRAJECTORY AND GROUND
!              DEN:     PLUME DENSITY
!               TP:     PLUME TEMPERATURE
!               ua:     HORIZONTAL WIND SPEED
!               dudz:   WIND SHEAR
!               ta:     AMBIENT TEMPERATURE
!               dpdz:   AMBIENT POTENTIAL TEMPERATURE GRADIENT
!               ra:     AMBIENT DENSITY
!               zmin:   Minimum plume centerline height (m)
!               ds:     Step size (m) in the numerical plume rise calc.
!               nstep:  Reporting frequency of numerical calc.
!               slast:  Termination distance (m) of plume rise calc.
!               gravi:  Acceleration due to gravity (m/s**2)
!----------------------------------------------------------------------
! --- Include files
      include 'params.puf'
      include 'numparm.puf'
      include 'ambient.puf'
      include 'outpt.puf'
      include 'wakedat.puf'
      COMMON /PLU/ S,X,Y,Z,R,U,V,W,USC,PHI,DEN,TP
      REAL XTR(ntr),ZTR(ntr),RTR(ntr),HTR(ntr)
      REAL XT(mxnw),ZT(mxnw),RT(mxnw)
! --- Array for trajectory height without streamline modifications
      real Z0T(mxnw)
      DIMENSION RHS(7),RHSTEMP(7),F(7),FTEMP(7)
      logical ldbhr,ldb,ldbnn,ldbu,linwake
! --- Use LDB as a local switch for more extensive debug output
      ldb=ldbhr
! !!! ldb=.FALSE.
! !!! ldbu=ldb
      ldbu=.FALSE.
! --- Initial number or output records for RISE.DAT file
      kriseout=0
      X=0.
      Y=0.
      Z=AMAX1(H,ZMIN)
      S=0.
      R=REFF
      RSQ=R*R
      U=0.000002
      tp=teff
      w=weff
! --- Additional variables for PRIME module
! -----------------------------------------
      linwake=.FALSE.
      drdxa=0.0
      ipositn=4
! --- Define the crosswind velocity component = zero
      dyds=0.0
      v=0.0
! --- Introduce ZCUMUL to track change in the vertical coordinate of
! --- the trajectory that arises from streamline inclination from
! --- horizontal. (This adjustment is applied outside of the system of
! --- rise equations out to a distance of 15R from downwind face of
! --- the building)
      zcumul=0.0
! --- Assign initial stepping length
      ds=ds0
! --- INDIRECT VARIABLES
      USC=SQRT(U*U+W*W)
      PHI=ATAN(W/U)
! --- PARAMETERS
      NP=NSTEP
      XNP=FLOAT(NP)
      nnp=1
! --- Initialize first location in rise storage arrays to one step (ds)
! --- and no rise; this should be overwritten with actual rise results.
      NN=1
      XT(NN)=ds
      ZT(NN)=h
      Z0T(NN)=h
      RT(NN)=reff
! --- Get met. variables at release height
      call ZMET(z,ua0,ra,ta,dudz0,dpdz)
! --- Compute initial buoyancy flux (m**4/s**3)
      deltat=amax1(tp-ta,0.0)
      fb=gravi*w*rsq*deltat/tp
! --- Compute momentum flux (m**4/s**2)
      fm=ta*w**2*rsq/tp
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    Apply reduction in wake wind speed
         r15src=xbadj+(xLb+15.*Rb)
         xb=x-xbadj
         yb=y-ybadj
         zb=AMAX1(z,zmin)
         call POSITION(xb,yb,zb,ipositn)
         ufac=1.0
         dufac=0.0
         if(ipositn.LT.4) call WAKE_U(.FALSE.,xb,yb,zb,ufac,dufac)
         ua=ufac*ua0
         dudz=ufac*dudz0+dufac*ua0
      else
         ua=ua0
         dudz=dudz0
      endif
      uam=AMAX1(ua,1.0)
! --- Final neutral rise distance
      if(fb.le.0.0)then
! ---    No buoyancy, momentum only
         if(weff.LE.0.0) then
! ---       No rise for this source
            xmax=0.0
            x=ds
            z=h
            zc=z
            r=reff
            zfin=z
            z0fin=z
            xfin=x
            rfin=r
            nn=1
            XT(NN)=X
            ZT(NN)=Z
            Z0T(NN)=Z
            RT(NN)=R
            goto 97
         else
! ---       Use final rise formula for jets, subject to Briggs (1969)
! ---       constraint that w/u is greater than or equal to 4
            wbyu=w/uam
            wbyu=AMAX1(wbyu,4.0)
            xmax=8.*r*wbyu*(1.+3./wbyu)**2
         endif
      else if(fb.lt.55)then
! ---    Buoyancy flux < 55 m**4/s**3
         wbyu=w/uam
         wbyu=AMAX1(wbyu,4.0)
         xmaxm=8.*r*wbyu*(1.+3./wbyu)**2
         xmaxb=49.*fb**0.625
         xmax=AMAX1(xmaxm,xmaxb)
      else
! ---    Buoyancy flux .GE. 55 m**4/s**3
         xmax=119.*fb**0.4
      endif
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    Use Briggs neutral rise to identify "minimal rise" cases
! ---    Compute Briggs neutral final rise
         if(xmax.EQ.0.0) then
            znf=0.0
         elseif(fb.LE.0.0) then
! ---       No buoyancy, momentum only
            znf=6.*r*w/uam
         elseif(fb.LT.55.) then
! ---       Buoyancy flux < 55 m**4/s**3
            znf=21.425*(fb**0.75)/uam
         else
! ---       Buoyancy flux .GE. 55 m**4/s**3
            znf=38.71*(fb**0.60)/uam
         endif
! ---    Set minimum rise to 0.1 m
         znf0=AMAX1(0.1,znf)
! ---    Guard against step length greater than likely rise
         dmin=0.5*znf0
         if(ds.GT.dmin) then
            ds=dmin
            if(ldb) then
               write(io6,*)'NUMRISE - initial step reset'
               write(io6,*)'znf,ds0,ds  :',znf,ds0,ds
            endif
         endif
      endif
! --- START PLUME RISE MARCHING LOOP
! ----------------------------------------------------
      DEN=RA*TA/tp
      call LUMP(ua,ta,f)
999   continue
! --- Set local debug logical
      if(nnp.LT.150) then
         ldbnn=ldb
      else
         ldbnn=.FALSE.
      endif
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    Define coordinates of plume relative to bldg.-defined origin
         xb=x-xbadj
         yb=y-ybadj
         zb=AMAX1(z+zcumul,zmin)
! ---    Obtain mean streamline slopes here (within 15R of building)
         dxds=0.0
         dzdx=0.0
         dzds=0.0
         dzstrm=0.0
         call POSITION(xb,yb,zb,ipositn)
         if(ipositn.GT.2 .AND. x.LE.r15src) then
            call ZSTREAM(hb,wb,xLb,rb,xLr,hr,xb,yb,zb,dzdx)
            dxds=U/USC
            dzds=dzdx*dxds
            dzstrm=dzds*ds
         endif
      endif
! --- Compute RHS of rate equations for this location
      call RATE(ua,dudz,ra,dpdz,ta,drdxa,rhs)
! --- PREDICTOR MARCHING
! ----------------------------------------------------
      call MARCHING(f,ftemp,rhs,ds)
      call UNLUMP(ua,ta,ra,ftemp)
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    Extract met and apply reduction in wake wind speed
         zb=AMAX1(z+zcumul+dzstrm,zmin)
         call ZMET(zb,ua0,ra,ta,dudz0,dpdz)
         call POSITION(xb,yb,zb,ipositn)
         ufac=1.0
         dufac=0.0
         if(ipositn.LT.4) call WAKE_U(ldbu,xb,yb,zb,ufac,dufac)
         ua=ufac*ua0
         dudz=ufac*dudz0+dufac*ua0
      else
         z=AMAX1(z,zmin)
         call ZMET(z,ua,ra,ta,dudz,dpdz)
      endif
      call RATE(ua,dudz,ra,dpdz,ta,drdxa,rhstemp)
! --- CORRECTOR
! ----------------------------------------------------
! *** DO I=1,7 (location 7 is lateral term which is always zero)
      DO I=1,6
         RHS(I)=0.5*(RHSTEMP(I)-RHS(I))
      ENDDO
      call MARCHING(ftemp,f,rhs,ds)
      call UNLUMP(ua,ta,ra,f)
! --- Process new position
! ----------------------------------------------------
      S=S+DS
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    Compute incremental change in plume height to account for
! ---    streamline ascent/descent, and add to cumulative change
         zcumul=zcumul+dzstrm
! ---    Apply cumulative adjustment to plume height
         zc=AMAX1(z+zcumul,zmin)
! ---    Define coordinates of plume relative to bldg.-defined origin
         xb=x-xbadj
         yb=y-ybadj
         zb=zc
         call POSITION(xb,yb,zb,ipositn)
      else
         z=AMAX1(z,zmin)
! ---    Assign height to PRIME variables (zc,zb) to simplify calls
         zc=z
         zb=z
      endif
! --- RISE.DAT output
      if(inrise.EQ.1) then
         kriseout=kriseout+1
         write(iox,*)s,x,y,zc,r,usc,u,v,w,den,tp,ua,ra,ta,dudz,dpdz
      endif
! --- Numerical procedure may result in small negative downwind
! --- distance:  reset to zero and go to next step
      if(x.LT.0.0) then
         x=0.0
         nnp=nnp-1
         goto 96
      endif
! --- Write debug output if in debug mode
! ---------------------------------------
      if(ldbnn)then
         if(mprm.EQ.1) then
! ---       PRIME module
            if(mod(nnp,1000).eq.1) then
               write(io6,*)'NUMRISE  inputs : '
               write(io6,*)' TEFF,REFF,WEFF = ',teff,reff,weff
               write(io6,112)
            endif
            deltaz=zc-h
            write(io6,114)nnp,x,y,zc,r,u,v,w,usc,phi,den,tp,ua,ra,ta,&
                          dudz,dpdz,dzds,dyds,ipositn,deltaz
         else
            if(mod(nnp,50).eq.1) then
               write(io6,*)'NUMRISE  inputs : '
               write(io6,*)' TEFF,REFF,WEFF = ',teff,reff,weff
               write(io6,122)
            endif
            deltaz=z-h
            write(io6,124)nnp,x,z,r,u,v,w,usc,phi,den,tp,ua,ra,ta,&
                          dudz,dpdz,deltaz
         endif
      endif
112   format(1x,2x,'NNP',7x,'X',6x,'Y',6x,'Z',6x,'R',6x,'U',5x,'V',&
         6x,'W',4x,'USC',5x,'PHI',4x,'DEN',5x,'TP',4x,'UA',5x,'RA',5x,&
         'TA',4x,'DUDZ',5x,'DPDZ',3x,'DZDS',3x,'DYDS',2x,'IPOS',&
         1x,'DELTAZ')
114   format(1x,i5,f9.2,3f7.2,4f7.2,&
          f8.4,f6.3,f7.2,f6.2,f7.3,f7.2,f9.4,&
          f8.4,2f7.4,i5,f7.3)
122      format(1x,2x,'NNP',8x,'X',7x,'Z',7x,'R',5x,'U',5x,'V',&
         5x,'W',3x,'USC',5x,'PHI',3x,'DEN',5x,'TP',4x,'UA',5x,'RA',5x,&
         'TA',4x,'DUDZ',4x,'DPDZ',3x,'DELTAZ')
124      format(1x,i5,f9.2,2f8.3,4f6.3,&
          f8.5,f6.3,f7.2,f6.2,f7.3,f7.2,f8.5,&
          f8.5,f7.3)
! ---------------------------------------
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    When trajectory inclination falls below PHICHECK radians,
! ---    ignoring streamline descent, check for wake influence
         if(phi.LE.phicheck .AND. ipositn.LT.4) then
            if(.not.LINWAKE) then
! ---          Plume centerline has just entered wake
               linwake=.TRUE.
               xbi=xb
! ---          Use unadjusted rise for BID
               base=AMAX1(H,ZMIN)
               rise=AMAX1(0.0,z-base)
               bidsq=(rise/3.5)**2
               if(ldbhr) then
                  write(io6,*)'NUMRISE call to WAKE_DFSN'
                  write(io6,*)'x,y,z,z+zcum:',x,y,z,zc
                  write(io6,*)'ds,u,w      :',ds,u,w
                  write(io6,*)'xb,phi,phicheck:',xb,phi,phicheck
                  write(io6,*)'rise,bidsq  :',rise,bidsq
                  write(io6,*)'sz0,sy0     :',sigz0,sigy0
               endif
! ---          Compute table of sigmas and growth rate in wake region
               call WAKE_DFSN(ldbhr,xbi,bidsq,sigz0,sigy0)
            endif
! ---       Select plume radius growth rate for this location
            call WAKE_DRDX(x,drdxa)
         endif
      endif
! --- Store any NNP<NP point in NN=1 array location in case rise ends
! --- too quickly to reach the first scheduled point
      if(nnp.LT.np) then
         NN=1
         XT(NN)=X
         ZT(NN)=zc
         Z0T(NN)=z
         RT(NN)=R
      endif
! --- Update path arrays
      if(float(NNP/NP).eq.float(NNP)/XNP) THEN
         NN=NNP/NP
         if(nn.gt.mxnw)then
            write(io6,*)'Error in Subr. NUMRISE -- NN too large -- ',&
            'NN = ',nn,' MXNW = ',mxnw
         write(*,*)
         stop 'Halted in NUMRISE -- see list file.'
         endif
         XT(NN)=X
         ZT(NN)=zc
         Z0T(NN)=z
         RT(NN)=R
! --- CHECK FOR PLUME EQUILIBRIUM HEIGHT
         IF(x .ge. xmax) THEN
            ZFIN=zc
            Z0FIN=z
            XFIN=X
            RFIN=R
            GOTO 97
         ENDIF
      ENDIF
! --- Extract met and apply reduction in wake wind speed
96    call ZMET(zb,ua0,ra,ta,dudz0,dpdz)
      if(mprm.EQ.1) then
! ---    PRIME module
         ufac=1.0
         dufac=0.0
         if(ipositn.LT.4) call WAKE_U(.FALSE.,xb,yb,zb,ufac,dufac)
         ua=ufac*ua0
         dudz=ufac*dudz0+dufac*ua0
      else
         ua=ua0
         dudz=dudz0
      endif
! --- Next increment
      NNP=NNP+1
! --- Stop rise at local maximum (excludes streamline descent effects)
      if(w.lt.0.0)then
         zfin=zc
         z0fin=z
         xfin=x
         rfin=r
         go to 97
      endif
! --- Adjust ds toward ds0 for next step
      if(ds.LT.ds0) ds=AMIN1(ds0,2.*ds)
      IF(S.LT.SLAST) GOTO 999
! --- END OF PLUME RISE MARCHING LOOP
! ----------------------------------------------------
      ZFIN=zc
      Z0FIN=z
      XFIN=X
      RFIN=R
97    CONTINUE
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    Complete trajectory out to "15R" if required, to account for
! ---    streamline slope (vertical only)
         xfin0=xfin
         x15r=r15src-xfin0
         nnleft=mxnw-nn
         if(x15r.GT.0.0) then
! ---       Check for room in arrays
            if(nnleft.LE.0)then
               write(io6,*)'Error in Subr. NUMRISE -- NN too large ',&
               '-- NN = ',nn,' MXNW = ',mxnw
               write(io6,*)'Cannot complete trajectory out to 15R'
               write(*,*)
               stop 'Halted in NUMRISE -- see list file.'
            endif
! ---       Set stepsize
            dsfin=nstep*ds
            dx15r=x15r/FLOAT(nnleft)
            dx15r=AMAX1(dsfin,dx15r)
! ---       Set range for additional steps
            n15r=MIN(NINT(x15r/dx15r),nnleft)
            nbeg=nn+1
            nend=nn+n15r
            do in=nbeg,nend
! ---          Define coordinates of plume relative to bldg.
               xbb=xt(in-1)-xbadj
               xbe=xbb+dx15r
               yb=y-ybadj
               zb=zt(in-1)
! ---          Obtain mean streamline slope
               dzdx=0.0
               call POSITION(xbb,yb,zb,ipos)
               if(ipos.GT.2) then
                  call ZSTREAM(hb,wb,xLb,rb,xLr,hr,xbb,yb,zb,dzdxb)
                  call ZSTREAM(hb,wb,xLb,rb,xLr,hr,xbe,yb,zb,dzdxe)
                  dzdx=0.5*(dzdxb+dzdxe)
               endif
               xt(in)=xt(in-1)+dx15r
               zt(in)=AMAX1(zmin,zt(in-1)+dzdx*dx15r)
               z0t(in)=z0t(in-1)
               rt(in)=rfin
               zcumul=zcumul+dzdx*dx15r
! ---          Check for wake entry if this has not already happened
               if(.not.LINWAKE) then
                  if(ipos.LT.4) then
! ---                Plume centerline has just entered wake
                     linwake=.TRUE.
! ---                Set "internal" variable names
                     x=xt(in)
                     z=zt(in)-zcumul
                     xbi=x-xbadj
! ---                Use unadjusted rise for BID
                     base=AMAX1(H,ZMIN)
                     rise=AMAX1(0.0,z-base)
                     bidsq=(rise/3.5)**2
! ---                Debug output
                     if(ldbhr) then
                        write(io6,*)'NUMRISE call to WAKE_DFSN'
                        write(io6,*)'x,z,z+zcum:',x,z,zt(in)
                        write(io6,*)'xb,phi      :',xbi,phi
                        write(io6,*)'rise,bidsq  :',rise,bidsq
                        write(io6,*)'sz0,sy0     :',sigz0,sigy0
                        write(io6,*)
                     endif
! ---                Tabulate sigmas and growth rate in wake region
                     call WAKE_DFSN(ldbhr,xbi,bidsq,sigz0,sigy0)
                  endif
! ---             Select plume radius growth rate for this location
                  call WAKE_DRDX(x,drdxa)
               endif
            enddo
! ---       Update nn and reset "fin" data
            nn=nend
            xfin=xt(nn)
            zfin=zt(nn)
            z0fin=z0t(nn)
         endif
      endif
! --- Construct trajectory arrays for calling program
      if(nn.GT.ntr) then
! ---    Sample a subset of the nn points
         xtr(ntr)=xfin
         ztr(ntr)=zfin
         rtr(ntr)=rfin
         htr(ntr)=z0fin-h
         if(nn.LE.2*ntr) then
! ---       Fill elements with nearest values
            deln=FLOAT(nn)/FLOAT(ntr)
            do in=1,ntr-1
               jn=in*deln
               xtr(in)=xt(jn)
               ztr(in)=zt(jn)
               rtr(in)=rt(jn)
               htr(in)=z0t(jn)-h
            enddo
         else
! ---       Use sliding step-size to sample nearfield more frequently
            deln=2.*FLOAT(nn-ntr)/FLOAT(ntr*(ntr-1))
            rn=0.0
            do in=1,ntr-1
               rn=rn+1.0+(in-1)*deln
               jn=rn
               xtr(in)=xt(jn)
               ztr(in)=zt(jn)
               rtr(in)=rt(jn)
               htr(in)=z0t(jn)-h
            enddo
         endif
      else
! ---    Fill elements directly
         do in=1,nn
            xtr(in)=xt(in)
            ztr(in)=zt(in)
            rtr(in)=rt(in)
            htr(in)=z0t(in)-h
         enddo
! ---    Fill excess elements with final rise properties
         do it=nn+1,ntr
            xtr(it)=xfin
            ztr(it)=zfin
            rtr(it)=rfin
            htr(it)=z0fin-h
         enddo
      endif
! --- Restore step size (may have changed)
      ds=ds0
      if(mprm.EQ.1) then
! ---    PRIME module
! ---    Use final form of tabulated rise array to add BID to
! ---    sigmas in the wake table upwind of xi
         call WAKE_ADDBID(ldbhr,ntr,xtr,htr)
! ---    Determine maximum fraction of plume captured in cavity
         if(LINWAKE .AND. xbi.LT.(xLb+xLR)) then
! ---       Plume centerline enters wake boundary before clearing cavity
            call WAKE_FQC(ldbhr,xbi,xtr,ztr,ntr)
         else
            fqcav=0.0
         endif
      endif
      if(ldbhr) then
         delzfin=zfin-h
         write(io6,*)
         write(io6,*)'      Initial Plume Temperature = ',teff
         write(io6,*)'             Buoyancy flux (FB) = ',fb
         write(io6,*)'             Momentum flux (FM) = ',fm
         write(io6,*)'  Neutral dist. to final rise   = ',xmax
         write(io6,*)'Total distance tabulated (XFIN) = ',xfin
         write(io6,*)'      Final plume height (ZFIN) = ',zfin
         write(io6,*)'     Final plume rise (DELZFIN) = ',delzfin
         write(io6,*)'      Final plume radius (RFIN) = ',rfin
         if(mprm.EQ.1) then
            write(io6,*)'  Calc distance to final rise   = ',xfin0
            write(io6,*)'Distance from final rise to 15R = ',x15r
            write(io6,*)'Cumul. streamline adj. (ZCUMUL) = ',zcumul
            write(io6,*)
            write(io6,*)'    Fraction of plume in CAVITY = ',fqcav
            write(io6,*)
         endif
         write(io6,*)
!
! ---    Write the arrays passed back to the calling routine
         write(io6,28)
28       format(/4x,'I',10x,'XTR',8x,'ZTR',8x,'RTR',8x,'HTR'/)
         do i=1,ntr
            write(io6,32)i,xtr(i),ztr(i),rtr(i),htr(i)
32          format(i5,3x,4(f10.4,1x))
         enddo
         write(io6,*)
      endif
! --- Process and write RISE.DAT data records
! --- Transfer current rise data from scratch file to RISE.DAT file
      if(inrise.EQ.1) call NRISEOUT(kriseout,mprm)
      RETURN
      END
!----------------------------------------------------------------------
      subroutine numclip(mxrise,htstak,rise,ntr,xtr,ztr,rtr,htr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 080430           NUMCLIP
!                D. Strimaitis
!
! --- PURPOSE:  Clip numerical rise tables at reduced rise
!
! --- UPDATE
! --- V6.0-V6.26    080430  (DGS): Add rise table to arrays that are
!                                  modified
!
! --- INPUTS:
!         mxrise - integer    - Size declared for rise tables
!         htstak - real       - Height of release above ground (m)
!           rise - real       - Height of rise above release (m)
!            ntr - real       - Number of entries in trajectory
!            xtr - real       - Distance from source (m)
!            ztr - real       - Height above ground (m)
!            rtr - real       - Radius of plume (m)
!            htr - real       - Rise height from release (m)
!
!
! --- OUTPUT:
!            ntr - real       - Number of entries in trajectory
!            xtr - real       - Distance from source (m)
!            ztr - real       - Height above ground (m)
!            rtr - real       - Radius of plume (m)
!            htr - real       - Rise height from release (m)
!
!
! --- NUMCLIP called by:  AREAS2, POINTS1, POINTS2
! --- NUMCLIP calls:      none
!----------------------------------------------------------------------
!
      real xtr(mxrise),ztr(mxrise),rtr(mxrise),htr(mxrise)
! --- Set final height above ground
      zfin=htstak+rise
! --- Terminate rise tables at zfin
      ntop=ntr
      if(zfin.LT.ztr(ntr)) then
         do i=2,ntop
            if(zfin.LE.ztr(i)) then
               ntr=i
               frac=(zfin-ztr(i-1))/(ztr(i)-ztr(i-1))
               ztr(i)=zfin
               xtr(i)=xtr(i-1)+frac*(xtr(i)-xtr(i-1))
               rtr(i)=rtr(i-1)+frac*(rtr(i)-rtr(i-1))
               htr(i)=htr(i-1)+frac*(htr(i)-htr(i-1))
               goto 10
            endif
         enddo
      endif
10    return
      end
!----------------------------------------------------------------------
      subroutine nummet(ixs,iys,md,ldbhr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            NUMMET
!                X.(J.) Zhang, D. Strimaitis, J. Scire
!
! --- PURPOSE:  Prepare meteorological data for numerical plume rise
!
! --- UPDATE
! --- V6.265-V6.3   100212  (DGS): Add nested CALMET grids
! --- V6.2-V6.265   090612  (DGS): PLEXP is explicitly assigned a
!                                  missing value in BLOCK DATA and
!                                  must be be replaced with default if
!                                  it remains missing here
! --- V5.74-V6.2    070629  (DGS): Assign PTGRAD value to default so it
!                                  is always available for debug output
! --- V5.7-V5.74    040715  (DGS): Add METFM=5 (AERMET)
! --- V5.4-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
! --- V5.0-V5.4     000602  (DGS): add message to "stop"
! --- V4.0-V5.0     971107  (JSS): add logic to use METFM=2,3 formats
!                           (DGS): add logic to use METFM=4 format
!
! --- INPUTS:
!       IXS,IYS - integer       - Met cell index nearest source
!            MD - integer       - Met grid domain index
!         LDBHR - logical       - Debug switch controlling debug output
!
!     Common block /AMBIENT/ variables:
!           NZA,NZAP1,ZFACEA(mxprfp1),ZGPTA(mxprfz) : for METFM=1,2,3
!           ADIA,PTGRAD0
!     Common block /COMPARM/ variables:
!           PLX0
!     Common block /NUMPARM/ variables:
!           RGAS,GRAVI
!     Common block /METHD/ variables:
!           LCALGRD, NEARS(mxnx,mxny,mxmetdom),I2DMET
!     Common block /METHR/ variables:
!           UMET(mxnx,mxny,mxnz,mxmetdom),VMET(mxnx,mxny,mxnz,mxmetdom),
!           TMET(mxnx,mxny,mxnz,mxmetdom),TEMPSS(mxss,mxmetdom),
!           TEMP2D(mxnx,mxny,mxmetdom),
!           PTG(2),IPGT(mxnx,mxny,mxmetdom), PLEXP,
!           TPRF(mxprfz),SSPRF(mxprfz),ZPRF(mxprfz),NZPRF
!     Common block /GEN/ variables:
!           METFM
!     Common block /GRID/ variables:
!           ZGPT(mxnz)
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXPRFZ, MXPRFP1, MXSS, MXMETDOM
!
! --- OUTPUT:
!
!     Common block /AMBIENT/ variables:
!           NZA,NZAP1,ZFACEA(mxprfp1),ZGPTA(mxprfz) : for METFM=4,5
!           UAMB(mxprfz),RAMB(mxprfz),DEDZ(mxprfp1),TAMB(mxprfz),
!           TAMB0,RAMB0
!
! --- NUMMET called by: AREAS2, POINTS1, POINTS2
! --- NUMMET calls:     none
!----------------------------------------------------------------------
!
! --- Include files
      include 'params.puf'
      include 'ambient.puf'
      include 'comparm.puf'
      include 'numparm.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'gen.puf'
      include 'grid.puf'
      logical ldbhr
      data zero/0.0/, half/0.5/
! --- Set default PTemp gradient (may not be used)
      kst=ipgt(ixs,iys,md)
      if(kst.LE.4) then
         ptgrad=ptgrad0
      elseif(kst.EQ.5) then
         ptgrad=ptg(1)
      else
         ptgrad=ptg(2)
      endif
! --- Impose minimum value
      if(ptgrad.LT.ptgrad0)ptgrad=ptgrad0
! --- Trap missing power-law exponent (substitute default)
      if(plexp.LT.-900.) then
! ---    Use default
         kst6=MIN(kst,6)
         plx=plx0(kst)
      else
! ---    Use it
         plx=plexp
      endif
      if(metfm.eq.1)then
!
! ---    3-D meteorological fields
! ---    Velocity (m/s) and temperature (K) profiles at grid center
         do i=1,nza
            px=umet(ixs,iys,i,md)
            py=vmet(ixs,iys,i,md)
            uamb(i)=sqrt(px*px+py*py)
         enddo
! ---    Define temperature profile from 3-D temperature grid (if
! ---    available) or from the stability-dependent lapse rate
         if(LCALGRD)then
            do i=1,nza
               tamb(i)=tmet(ixs,iys,i,md)
            enddo
! ---       Compute the surface temperature consistent with the 3-D
! ---       field
            tamb0=tamb(1)-zgpta(1)*((tamb(2)-tamb(1))/(zgpta(2)-&
             zgpta(1)))
         else
! ---       Use default potential temperature gradient if 3-D temp.
! ---       data is not available
            kst=ipgt(ixs,iys,md)
            if(kst.LE.4) then
               ptgrad=ptgrad0
            elseif(kst.EQ.5) then
               ptgrad=ptg(1)
            else
               ptgrad=ptg(2)
            endif
! ---       Impose minimum value
            if(ptgrad.LT.ptgrad0)ptgrad=ptgrad0
! ---       Construct 1-D temperature field
! frr (09/01) new calmet format (2D temp)
            if(i2dmet.EQ.1) then
               tamb0=temp2d(ixs,iys,md)
            elseif(i2dmet.EQ.0) then
               ista=nears(ixs,iys,md)
               tamb0=tempss(ista,md)
            else
               write(*,*)'Subr. WET:  Invalid I2DMET = ',i2dmet
               stop
            endif
            do i=1,nza
               tamb(i)=tamb0+zgpta(i)*(ptgrad-adia)
            enddo
! ---       Compute the surface temperature consistent with the 3-D
! ---       field
            tamb0=tamb(1)-zgpta(1)*((tamb(2)-tamb(1))/(zgpta(2)-&
             zgpta(1)))
         endif
      elseif(metfm.eq.2.or.metfm.eq.3)then
!
! ---    ISC met. or PLM met. data
         kst=ipgt(ixs,iys,md)
         if(kst.LE.4) then
            ptgrad=ptgrad0
         elseif(kst.EQ.5) then
            ptgrad=ptg(1)
         else
            ptgrad=ptg(2)
         endif
! ---    Impose minimum value
         if(ptgrad.LT.ptgrad0)ptgrad=ptgrad0
! ---    Construct 1-D temperature field
         ista=nears(ixs,iys,md)
         tamb0=tempss(ista,md)
         do i=1,nza
            tamb(i)=tamb0+zgpta(i)*(ptgrad-adia)
         enddo
! ---    Extrapolate wind speed to plume rise layers
         zref=zgpt(1)
         wsref=sqrt(umet(ixs,iys,1,md)**2+vmet(ixs,iys,1,md)**2)
         do iz=1,nza
            call powlaw(zgpta(iz),zref,wsref,plx,uamb(iz))
         enddo
      elseif(metfm.eq.4 .OR. metfm.EQ.5)then
! ---    Transfer CTDM PROFILE data to plume rise arrays
         nza=nzprf
         nzap1=nza+1
         do iz=1,nza
            zgpta(iz)=zprf(iz)
            uamb(iz)=ssprf(iz)
            tamb(iz)=tprf(iz)
         enddo
! ---    Compute the surface temperature consistent with the
! ---    temperature profile
         tamb0=tamb(1)-zgpta(1)*((tamb(2)-tamb(1))/(zgpta(2)-&
          zgpta(1)))
! ---    Compute face heights.  These are NOT equivalent to CALMET
! ---    face heights because these are midway between the grid-pt
! ---    heights, whereas CALMET grid points are midway between cell
! ---    face heights.
         zfacea(1)=zero
         do iz=2,nza
            zfacea(iz)=half*(zgpta(iz)+zgpta(iz-1))
         enddo
         zfacea(nzap1)=2*zgpta(nza)-zfacea(nza)
      else
         write(io6,*)'ERROR in Subr. NUMMET - Invalid value of ',&
          'METFM -- METFM = ',metfm
         write(*,*)
         stop 'Halted in NUMMET -- see list file.'
      endif
! --- Create potential temperature gradient at zface level
      do i=2,nza
         dedz(i)=adia+(tamb(i)-tamb(i-1))/(zgpta(i)-zgpta(i-1))
      enddo
      dedz(1)=dedz(2)
      dedz(nzap1)=dedz(nza)
! --- Create ambient density profile (kg/m^3) on grid center
! --- Ground-level air density estimated from elevation
      ramb0=1.2*exp(-gravi*elev(ixs,iys,md)/(rgas*tamb0))
      do i=1,nza
! ---    Compute average temperature in layer
         tbar=0.5*(tamb(i)+tamb0)
! ---    Compute ambient air density at height, ZGPTA(i)
         ramb(i)=ramb0*(tamb0/tamb(i))*exp(-gravi*zgpta(i)/&
          (rgas*tbar))
      enddo
!
! ----------------
! --- Debug output
! ----------------
      if(ldbhr)then
         write(io6,*)
         write(io6,*)'PTGRAD  = ',ptgrad,' (deg. K/m)'
         write(io6,*)'NZA     = ',nza
         write(io6,*)'ADIA    = ',adia,' (deg. K/m)'
         write(io6,*)'PTGRAD0 = ',ptgrad0,' (deg. K/m)'
         write(io6,*)'METFM   = ',metfm
         write(io6,*)'PLX     = ',plx
         write(io6,*)
         write(io6,*)'TAMB0   = ',tamb0,' (deg. K)'
         write(io6,*)'RAMB0   = ',ramb0,' (kg/m**3)'
         write(io6,*)
         write(io6,8)
8        format(/4x,'I',8x,'ZGPTA',7x,'UAMB',7x,'TAMB'/)
         do i=1,nza
            write(io6,10)i,zgpta(i),uamb(i),tamb(i)
10          format(i5,3x,f10.1,1x,f10.2,1x,f10.2)
         enddo
! ---    Echo back pot. temp. gradient & air density
         write(io6,18)
18       format(/4x,'I',7x,'ZFACEA',7x,'DEDZ',6x,'ZGPTA',7x,'RAMB'/)
         do i=1,nza
            write(io6,22)i,zfacea(i),dedz(i),zgpta(i),ramb(i)
22          format(i5,3x,f10.1,1x,f10.5,1x,f10.1,1x,f10.3)
         enddo
         write(io6,22)nzap1,zfacea(nzap1),dedz(nzap1)
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine rate(ua,dudz,ra,dpdz,ta,drdxa,rhs)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402              RATE
!                X. Zhang, J. Scire   EARTH TECH/Sigma Research
!
! --- PURPOSE:  Compute the right-hand side of the equations
!
! --- UPDATE
! --- V5.4-V5.7     030402  (DGS): PRIME update - add growth rate due
!                                  to ambient turbulence
!
! --- INPUTS:
!         UA - real    - Current ambient wind speed (m/s)
!       DUDZ - real    - Current wind shear (1/s)
!         RA - real    - Current ambient air density (kg/m**3)
!       DPDZ - real    - Current ambient potential temperature gradient
!                        (deg. K/m)
!         TA - real    - Current ambient air temperature (deg. K)
!      DRDXA - real    - Growth rate of plume radius due to ambient
!                        turbulence
!
!     Common block /PLU/ variables:
!           X,R,U,V,W,USC,PHI,DEN,TP
!     Common block /NUMPARM/ variables:
!           GRAVI, RP, ALPHAP(mxent), BETAP(mxent), XCAT(mxentp1),
!           NENT
!     Parameters:
!           MXENT, MXENTP1
!
! --- OUTPUT:
!        RHS(7) - real     - Right hand terms
!
! --- RATE called by:  NUMRISE
! --- RATE calls:      none
!----------------------------------------------------------------------
! --- Include files
      include 'params.puf'
      include 'numparm.puf'
      COMMON /PLU/ S,X,Y,Z,R,U,V,W,USC,PHI,DEN,TP
      DIMENSION RHS(7)
! ---   Constants:
!          GRAVI - Gravitational acceleration (m/s**2)
!          RP    - Radiation coefficient (kg/m**2/deg. K**3/s)
!          ALPHA - Entrainment coefficient (parallel direction)
!          BETA  - Entrainment coefficient (normal direction)
! --- Set default entrainment coefficients
      data alpha0/.11/, beta0/0.6/
! --- Define the entrainment coefficients
      alpha=alpha0
      beta=beta0
      if(nent.gt.0)then
! ---    Check if the plume is in the area where perturbed entrainment
! ---    coefficients apply
         if(x.lt.xcat(1))go to 99
         nentp1=nent+1
         do i=2,nentp1
            if(x.le.xcat(i))then
               alpha=alphap(i-1)
               beta=betap(i-1)
               go to 99
            endif
         enddo
! ---    Override any ambient growth rate
         drdxa=0.0
      endif
99    continue
      RHS(1)=2.0*R*ALPHA*RA*ABS(USC-UA*U/USC)+&
                     2.0*R*BETA*RA*ABS(UA*SIN(PHI))
!
! --- Condition entrainment to be .GE. growth due to ambient turb.
      rhs1a =2.0*R*RA*UA*drdxa
      rhs(1)=AMAX1(rhs(1),rhs1a)
!
      RHS(2)=-R*R*DEN*W*DUDZ
      RHS(3)=GRAVI*R*R*(RA-DEN)
      RHS(4)=-DPDZ*DEN*W*R*R-RP*R*(TP**4-TA**4)
      RHS(5)=W/USC
      RHS(6)=U/USC
! *** rhs(7)=v/usc
      RETURN
      END
!----------------------------------------------------------------------
      subroutine lump(ua,ta,f)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 950715              LUMP
!                X.(J.) Zhang, J. Scire  SRC
!
! --- PURPOSE:  Calculate the lumped variables
!
! --- INPUTS:
!         UA - real    - Current ambient wind speed (m/s)
!         TA - real    - Current ambient air temperature (K)
!
! --- OUTPUT:
!          F(7) - real     - lumped variables
!
! --- LUMP called by:  NUMRISE
! --- LUMP calls:      none
!----------------------------------------------------------------------
        COMMON /PLU/ S,X,Y,Z,R,U,V,W,USC,PHI,DEN,TP
        DIMENSION F(7)
        F(1)=DEN*USC*R*R
        F(2)=F(1)*(U-UA)
        F(3)=F(1)*W
        F(4)=F(1)*(TP-TA)
        F(5)=Z
        F(6)=X
! ***   F(7)=y
        RETURN
        END
!----------------------------------------------------------------------
      subroutine marching(fold,fnew,rhs,ds)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 950715          MARCHING
!                X.(J.) Zhang, J. Scire  SRC
!
! --- PURPOSE:  Marching S one step, either PREDICTOR or CORRECTOR
!
! --- INPUTS:
!       FOLD(7) - real     - Old values
!        RHS(7) - real     - Right hand terms
!            DS - real     - Distance (m) along plume axis
!
!
! --- OUTPUT:
!       FNEW(7) - real     - New values
!
! --- MARCHING called by:  NUMRISE
! --- MARCHING calls:      none
!----------------------------------------------------------------------
        DIMENSION FNEW(7),FOLD(7),RHS(7)
! ***   DO 10 I=1,7
        DO 10 I=1,6
           FNEW(I)=FOLD(I)+RHS(I)*DS
10      CONTINUE
        RETURN
        END
!----------------------------------------------------------------------
      subroutine unlump(ua,ta,ra,f)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 950715            UNLUMP
!                X.(J.) Zhang, J. Scire  SRC
!
! --- PURPOSE:  Calculate physical variables from lumped variables
!
! --- INPUTS:
!         UA - real    - Current ambient wind speed (m/s)
!         TA - real    - Current ambient air temperature (K)
!         RA - real    - Current ambient air density (kg/m^3)
!       F(7) - real    - Lumped variables
!
! --- OUTPUT:
!       common /PLU/:
!          U,V,W,USC,R,TP,PHI,Z,Y,X
!
! --- UNLUMP called by:  NUMRISE
! --- UNLUMP calls:      none
!----------------------------------------------------------------------
        COMMON /PLU/ S,X,Y,Z,R,U,V,W,USC,PHI,DEN,TP
        DIMENSION F(7)
        U=UA+F(2)/F(1)
        W=F(3)/F(1)
        USC=SQRT(U*U+W*W)
        TP=TA+F(4)/F(1)
! ---   Update plume density  (This short-cut could cause PROBLEMS!)
        DEN=RA*TA/TP
        R=SQRT(F(1)/USC/DEN)
        PHI=ATAN(W/U)
        Z=F(5)
        X=F(6)
! ***   Y=F(7)
        RETURN
        END
!----------------------------------------------------------------------
      subroutine zmet(z,ua,ra,ta,dudz,dpdz)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 950715              ZMET
!                X.(J.) Zhang, J. Scire  SRC
!
! --- PURPOSE:  Obtain ambient met parameters at height z
!               by interpolation of gridded values
!
! --- INPUTS:
!          Z - real    - Height (m)
!
!     Common block /AMBIENT/ variables:
!           NZA,UAMB(mxprfz),RAMB(mxprfz),TAMB(mxprfz),ZFACEA(mxprfp1),
!           ZGPTA(mxprfz),TAMB0,RAMB0,ADIA,PTGRAD0
!     Parameters:
!           MXPRFZ, MXPRFP1
!
! --- OUTPUT:
!         UA - real    - Current ambient wind speed (m/s)
!         RA - real    - Current ambient air density (kg/m**3)
!         TA - real    - Current ambient air temperature (deg. K)
!       DUDZ - real    - Current wind shear (1/s)
!       DPDZ - real    - Current ambient potential temperature gradient
!                        (deg. K/m)
!
! --- ZMET called by:  NUMRISE
! --- ZMET calls:      none
!----------------------------------------------------------------------
!     Defined at grid center: uamb,tamb,ramb
!     Defined at zface:       dedz
!----------------------------------------------------------------------
! --- Include files
      include 'params.puf'
      include 'ambient.puf'
! --- Interpolate variables defined at grid cell center
      if(z.lt.zgpta(1))then
! ---    Height is below first grid point
         zfact=(zgpta(1)-z)/zgpta(1)
         ta=tamb(1)-(tamb(1)-tamb0)*zfact
         ra=ramb(1)-(ramb(1)-ramb0)*zfact
! ---    Wind speed at z=0 is assumed to be zero
         ua=uamb(1)*(1.0-zfact)
         dudz=uamb(1)/zgpta(1)
         dpdz=adia+(tamb(1)-tamb0)/zgpta(1)
         dpdz=amax1(dpdz,ptgrad0)
      else if(z.lt.zgpta(nza))then
! ---    Find the layer containing height, Z
         do i=2,nza
            if(z.le.zgpta(i))then
               im1=i-1
               delz=zgpta(i)-zgpta(im1)
               zfact=(zgpta(i)-z)/delz
               ta=tamb(i)-(tamb(i)-tamb(im1))*zfact
               ra=ramb(i)-(ramb(i)-ramb(im1))*zfact
               ua=uamb(i)-(uamb(i)-uamb(im1))*zfact
! ---          Compute wind speed gradient & pot. temp. gradient
               dudz=(uamb(i)-uamb(im1))/delz
               dpdz=adia+(tamb(i)-tamb(im1))/delz
               dpdz=amax1(dpdz,ptgrad0)
               go to 101
            endif
         enddo
      else
! ---    Height is at or above the top grid point -- persist values
! ---    at the top grid cell
         ta=tamb(nza)
         ra=ramb(nza)
         ua=uamb(nza)
! ---    Hold wind speed and temperature constant above top layer at
! ---    values at top grid point
         dudz=0.0
         dpdz=adia
         dpdz=amax1(dpdz,ptgrad0)
      endif
101   continue
      return
      end
!----------------------------------------------------------------------
      subroutine numpr1(nz,zgpt,nzp1,zface,metfm,dsrise,trajincl)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108            NUMPR1
!                J. Scire
!
! --- PURPOSE:  Initialize the variables used by the numerical
!               plume rise algorithm
!
! --- UPDATE
! --- V5.7-V6.268   100108  (DGS): modify NSTEP calculation so that the
!                                  stepping factor remains 1 (not 2) if
!                                  the number of steps equals the
!                                  array declaration
!                   100108  (DGS): modify the levels in the met profile
!                                  to be the same as those used in ISC-
!                                  PRIME (interchange face heights with
!                                  layer heights)
! --- V5.7-V5.74    040715  (DGS): add METFM=5 (AERMET)
! --- V5.4-V5.7     030402  (DGS): add PRIME module layers for ISC met,
!                                  change DS to DS0,
!                                  compute PHICHECK from TRAJINCL
! --- V5.2-V5.4     000602  (DGS): add message to "stop"
! --- V5.0-V5.2     991104  (DGS): DS provided in control file (compute
!                                  NSTEP from DS,SLAST,MXNW)
! --- V4.0-V5.0     971107  (JSS): add logic to use METFM=2,3,4 formats
!
! --- INPUTS:
!              NZ - integer    - Number of vertical levels
!      ZGPT(mxnz) - real       - Grid point heights (m) (NZ values)
!            NZP1 - integer    - Number of vertical cell faces (NZ+1)
!   ZFACE(mxnzp1) - real       - Cell face heights (m) (NZP1 values)
!           METFM - integer    - Meteorological format flag
!                                  1=CALMET format (CALMET.DAT)
!                                  2=ISC format (ISCMET.DAT)
!                                  3=AUSPLUME format (PLMMET.DAT)
!                                  4=CTDM format (PROFILE.DAT,
!                                                 SURFACE.DAT)
!          DSRISE - real       - Along-trajectory distance step used
!                                when marching in numerical plume rise
!                                solver
!        TRAJINCL - real       - Trajectory inclination angle (deg),
!                                ignoring streamline descent, at which
!                                check is made for PRIME wake influence
!
!       Parameters:
!          MXENT, MXENTP1, MXNZ, MXNZP1, MXPRFZ, MXPRFP1, MXNW,
!          IO6
!
! --- OUTPUT:
!
!       Common block /NUMPARM/ variables:
!          GRAVI,RGAS,ZMIN,NSTEP,SLAST,RP,ALPHAP(mxent),
!          BETAP(mxent),XCAT(mxentp1),NENT,DS0,PHICHECK
!       Common block /AMBIENT/ variables:
!          ADIA,PTGRAD0,ZGPTA(mxprfz),ZFACEA(mxprfp1)
!
! --- NUMPR1 called by:  SETUP
! --- NUMPR1 calls:      none
!----------------------------------------------------------------------
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'numparm.puf'
      include 'ambient.puf'
      data deg2rad/0.017453293/
      real zgpt(nz),zface(nzp1)
! -----------------------
! --- /NUMPARM/ variables
! -----------------------
!
! --- Set the acceleration due to gravity (m/s**2)
      gravi=9.807
! --- Set the gas constant (m**2/s**2/deg. K)
      rgas=287.026
! --- Set the minimum plume centerline height (m)
      zmin=0.001
! --- Set the termination distance (m) of the plume rise calculation
      slast=5000.
! --- Pass computation step size provided in control file into DS
      ds0=dsrise
! --- Compute the internal save frequency of plume rise calculations
!     (i.e., every DS*NSTEP meters) assuring that no more than MXNW
!     values are saved over the distance SLAST
!     (NOTE: this the frequency with which the results are saved
!     internally -- not that passed back from the NUMRISE routine)
! --- DGS
!      nstep=1+(slast/(ds0*FLOAT(mxnw)))
      nstep=1+((slast-0.5*ds0)/(ds0*FLOAT(mxnw)))
! --- Set the radiation coefficient (kg/m**2/deg. K**3/s)
      rp=9.1e-11
! --- Set the perturbed entrainment coefficients
!     ALPHAP (parallel direction), BETAP (normal direction)
      nent=0
      alphap(1)=0.11
      betap(1)=0.6
      xcat(1)=-9.e9
      xcat(2)= 9.e9
! --- Compute trajectory inclination angle (radians)
      phicheck=trajincl*deg2rad
! -----------------------
! --- /AMBIENT/ variables
! -----------------------
! --- Set dry adiabatic lapse rate (deg. K/m)
      adia=.0098
! --- Set minimum potential temperature lapse rate (deg. K/m)
      ptgrad0=0.0
! --- Define plume rise layers based on selection of met. format
      if(metfm.eq.1)then
! ---    CALMET met. format
! -------------------------
! ---    Set the default number of layers same as model layers
         nza=nz
         nzap1=nza+1
         if(nza.gt.mxprfz)then
            write(io6,*)'ERROR in SUBR. NUMPR1 -- NZA is too large ',&
            'for array dimension -- NZA = ',nza,' MXPRFZ = ',mxprfz
            write(*,*)
            stop 'Halted in NUMPR1 -- see list file.'
         endif
         if(nzap1.gt.mxprfp1)then
            write(io6,*)'ERROR in SUBR. NUMPR1 -- NZAP1 is too large ',&
            'for array dimension -- NZAP1 = ',nzap1,' MXPRFP1 = ',&
            mxprfp1
            write(*,*)
            stop 'Halted in NUMPR1 -- see list file.'
         endif
! ---    Define the meteorological grid
! ---    Set plume rise arrays with met. grid point heights
         do i=1,nza
            zgpta(i)=zgpt(i)
         enddo
! ---    Set the plume rise cell face heights
         do i=1,nzap1
            zfacea(i)=zface(i)
         enddo
      else if(metfm.eq.2.or.metfm.eq.3)then
! ---    ISC met. or AUSPLUME met. format
! ---------------------------------------
! ---    Set number of layers for plume rise to 45
         nza=45
         nzap1=nza+1
         if(nza.gt.mxprfz)then
            write(io6,*)'ERROR in SUBR. NUMPR1 -- NZA is too large ',&
            'for array dimension -- NZA = ',nza,' MXPRFZ = ',mxprfz
            write(*,*)
            stop 'Halted in NUMPR1 -- see list file.'
         endif
         if(nzap1.gt.mxprfp1)then
            write(io6,*)'ERROR in SUBR. NUMPR1 -- NZAP1 is too large ',&
            'for array dimension -- NZAP1 = ',nzap1,' MXPRFP1 = ',&
            mxprfp1
            write(*,*)
            stop 'Halted in NUMPR1 -- see list file.'
         endif
! ---    Define the meteorological grid
!c ---    Set cell face heights at every 10 m from 10-200 m
!         dz=10.
!         nn=1
!         zfacea(nn)=0.0
!         do i=1,20
!            nn=nn+1
!            zfacea(nn)=zfacea(nn-1)+dz
!         enddo
!c ---    Set cell face heights every 50 m from 250-500 m
!         dz=50.
!         do i=21,26
!            nn=nn+1
!            zfacea(nn)=zfacea(nn-1)+dz
!         enddo
!c ---    Set cell face heights every 100 m from 600-2000 m
!         dz=100.
!         do i=27,41
!            nn=nn+1
!            zfacea(nn)=zfacea(nn-1)+dz
!         enddo
!c ---    Set cell face heights every 500 m from 2500-4000 m
!         dz=500.
!         do i=42,45
!            nn=nn+1
!            zfacea(nn)=zfacea(nn-1)+dz
!         enddo
!
!         if(nn.NE.nzap1) stop 'NUMPR1: nn.NE.nzap1'
!
!c ---    Compute grid point heights
!         do i=1,nza
!            zgpta(i)=0.5*(zfacea(i+1)+zfacea(i))
!         enddo
! ---    Set grid points every 10 m from 10-200 m
         dz=10.
         nn=1
         zgpta(nn)=dz
         do i=2,20
            nn=nn+1
            zgpta(nn)=zgpta(nn-1)+dz
         enddo
! ---    Set grid points every 50 m from 250-500 m
         dz=50.
         do i=21,26
            nn=nn+1
            zgpta(nn)=zgpta(nn-1)+dz
         enddo
! ---    Set grid points every 100 m from 600-2000 m
         dz=100.
         do i=27,41
            nn=nn+1
            zgpta(nn)=zgpta(nn-1)+dz
         enddo
! ---    Set grid points every 500 m from 2500-4000 m
         dz=500.
         do i=42,45
            nn=nn+1
            zgpta(nn)=zgpta(nn-1)+dz
         enddo
! --- Compute the cell face heights from the grid point values
      zfacea(1)=0.0
      do i=2,nza
         zfacea(i)=0.5*(zgpta(i)+zgpta(i-1))
      enddo
      zfacea(nzap1)=zgpta(nza)+0.5*(zgpta(nza)-zgpta(nza-1))
      else if(metfm.GT.5)then
! ---    N.B.: Layer structure varies hourly if using metfm=4,5 
! ---    so these levels are defined in NUMMET.  Any value of metfm
! ---    greater than 5 is not supported.
         write(io6,*)'ERROR in Subr. NUMPR1 -- Invalid value of METFM ',&
         '-- METFM = ',metfm
         write(*,*)
         stop 'Halted in NUMPR1 -- see list file.'
      endif
      return
      end
