! --- V5.2-V5.4 : 000602 (DGS): allow multiple PTEMARB files
!                 000602 (DGS): add header record for LC map data
!                 000602 (DGS): add message to "stop"
! --- V5.0-V5.2 : 991104 (DGS): YYYY format used for year
! --- V4.0-V5.0 : 971107 (DGS): PTEMARB.DAT either binary or ASCII
!                 971107 (DGS): Check for valid version of file
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 PTEMARB file
!                I - integer    - Index for current PTEMARB file
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!            LNPT2 - logical    - Flag indicating all sources found
!
!     Common Block /PT2/ variables:
!            MFPT2, NPT2
!     Common Block /DATASET/ variables:
!            VERPARB(mxemdat)
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!   Parameters:
!            IO6, MXSPEC, MXEMDAT, MXQSTEP, IOX
!
! --- OUTPUT:
!
!     Common Block /PT2/ variables:
!           NSE2,MFPT2(mxemdat),
!           IBSRC2(mxemdat),IESRC2(mxemdat),
!           IBDATHR2(mxemdat),IBSEC2(mxemdat),IEDATHR2(mxemdat),
!           IESEC2(mxemdat),XTZ2(mxemdat),T2BTZ2(mxemdat),
!           XMWEM2(mxspec),IXREM2(mxspec),
!           lutmpt2(mxemdat),llccpt2(mxemdat),lpspt2(mxemdat),
!           lempt2(mxemdat),llazapt2(mxemdat),lttmpt2(mxemdat),
!           iutmznpt2(mxemdat),feastpt2(mxemdat),fnorthpt2(mxemdat),
!           rnlat0pt2(mxemdat),relon0pt2(mxemdat),
!           rnlat1pt2(mxemdat),rnlat2pt2(mxemdat),NSTEP2(mxemdat),
!           NDHRQB2(mxqstep,mxemdat),NSECQB2(mxqstep,mxemdat),
!           NDHRQE2(mxqstep,mxemdat),NSECQE2(mxqstep,mxemdat),
!           CSLST2(mxspec),CID2(mxarea),
!           pmappt2(mxemdat),utmhempt2(mxemdat),datumpt2(mxemdat),
!           datenpt2(mxemdat),xyunitpt2(mxemdat)
!
! --- RDHDEM2 called by: SETUP
! --- RDHDEM2 calls:     YR4, XTRACTLL, INCR, TCLOCK, GEOFMT
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'pt2.puf'
      include 'dataset.puf'
      include 'datehr.puf'
      include 'qa.puf'
!
      LOGICAL lprt, lnpt2, lmatch
! --- Local variables
      real xmwem2x(mxspec)
      character*8 axtz2
      character*12 fname2,label2(mxemdat)
      character*12 cslst2x(mxspec),vrs2
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Set unit number
      iunit=iunit0+i-1
! --- Initialize map projection logicals
      lutmpt2(i)=.FALSE.
      llccpt2(i)=.FALSE.
      lpspt2(i)=.FALSE.
      lempt2(i)=.FALSE.
      llazapt2(i)=.FALSE.
      lttmpt2(i)=.FALSE.
! --- Initialize other variables
      datenpt2(i)='02-21-2003  '
      xyunitpt2(i)='KM  '
      ibsec2(i)=0.0
      iesec2(i)=0.0
      feastpt2(i)=0.0
      fnorthpt2(i)=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmappt2(i)='XXX     '
      iutmznpt2(i)=1
      utmhempt2(i)='N   '
      datumpt2(i)='XXX     '
      axtz2='UTC+0000'
!     -----------------------
      if(verparb(i).EQ.'1.5' .OR.&
         verparb(i).EQ.'1.54' .OR.&
         verparb(i).EQ.'1.6') then
!     -----------------------
! ---    Provide help and then stop (these formats are not supported)
         dataset='PTEMARB.DAT     '
         if(verparb(i).EQ.'1.5') then
            read(iunit,*)fname2,npt2file,nse2x,iutmznpt2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs2,label2(i)
            dataver='1.5a            '
            datamod(1:40)='Augmented 5 format with Map Projection, '
            datamod(41:64)='DATUM, Time Zone        '
         elseif(verparb(i).EQ.'1.54') then
            read(iunit,*)fname2,npt2file,nse2x,iutmznpt2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs2,label2(i)
            dataver='1.54a           '
            datamod(1:42)='Augmented 5.4 format with Map Projection, '
            datamod(43:64)='DATUM, Time Zone      '
         elseif(verparb(i).EQ.'1.6') then
            read(iunit,*)fname2,npt2file,nse2x,iuser0,iuser1,iuser2,&
                      iutmznpt2(i),ibyr,ibdy,ibhr,ibsec2(i),&
                      ieyr,iedy,iehr,iesec2(i),vrs2,label2(i)
            dataver='1.6a            '
            datamod(1:40)='Augmented 6 format with Map Projection, '
            datamod(41:64)='DATUM, Time Zone        '
         endif
         write(io6,*)
         write(io6,*)
         write(io6,*)'ERROR in PTEMARB files'
         write(io6,*)
         write(io6,*)'Old PTEMARB.DAT format cannot be used because:'
         write(io6,*)'1.  Coordinate map projection is incomplete'
         write(io6,*)'2.  The DATUM for the coordinates is missing'
         write(io6,*)'3.  The time zone is missing'
         write(io6,*)
         write(io6,*)'ADD these records to the top of the file:'
         write(io6,*)
         write(io6,'(2a16,a64)') dataset,dataver,datamod
         if(iutmznpt2(i).NE.0) then
            write(io6,'(a8)')'UTM     '
            write(io6,'(a8)')'  xxN   '
         else
            write(io6,'(a8)')'LCC     '
            if(verparb(i).NE.'1.54') then
              write(io6,'(4a16)')'yy.yyyN         ','xx.xxxW         ',&
                                 'yy.yyyN         ','yy.yyyN         '
            endif
            write(io6,*)zero,zero
         endif
         write(io6,'(a8)')'DATUM   '
         write(io6,'(a8)')'UTC-0500'
         write(io6,*)
         write(io6,*)'UPDATE these records to describe your file:'
         if(iutmznpt2(i).NE.0) then
           write(io6,*)'- Replace the xxN with your UTM zone and',&
                       ' hemisphere (S for Southern Hemisphere)'
         else
           if(verparb(i).NE.'1.54') then
             write(io6,*)'- Replace the xx.xxxW and yy.yyyN with your',&
                       ' LCC projection parameters.'
             write(io6,*)'  The order is Origin Latitude, Origin',&
                       ' Longitude, and the 2 Matching Parallels'
             write(io6,*)'  (each field is 16 characters)'
           endif
           write(io6,*)'- Replace the 0.0 0.0 with your False Easting',&
                       ' and Northing if non-zero'   
         endif
         write(io6,*)'- Replace DATUM with your datum code (enter',&
                     ' left-justified in the 8-character field)'
         write(io6,*)'- Replace UTC-0500 with your time zone code'
         write(io6,*)'  (GMT=UTC+0000, EST=UTC-0500, CET=UTC+0100)'
         write(io6,*)
         write(*,*)
         write(*,*)'ERROR in PTEMARB files -- see list file'
         write(*,*)
         stop
!     ----------------------------
      elseif(verparb(i).EQ.'1.5a' .OR.&
             verparb(i).EQ.'1.54a' .OR.&
             verparb(i).EQ.'1.6a') then
!     ----------------------------
! ---    Augmented headers
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         ncommout=ncommout+1
! ---    Map projection
         read(iunit,'(a8)') pmappt2(i)
         write(iox,'(a8)') pmappt2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmappt2(i),iutmznpt2(i),&
                     utmhempt2(i),clat0,clon0,clat1,clat2,&
                     datumpt2(i),datenpt2(i),xyunitpt2(i),axtz2)
         if(pmappt2(i).EQ.'UTM     ')  lutmpt2(i) =.TRUE.
         if(pmappt2(i).EQ.'LCC     ')  llccpt2(i) =.TRUE.
         if(pmappt2(i).EQ.'PS      ')  lpspt2(i)  =.TRUE.
         if(pmappt2(i).EQ.'EM      ')  lempt2(i)  =.TRUE.
         if(pmappt2(i).EQ.'LAZA    ')  llazapt2(i)=.TRUE.
         if(pmappt2(i).EQ.'TTM     ')  lttmpt2(i) =.TRUE.
! ---    Map projection parameters
         if(LUTMPT2(i)) then
            read(iunit,'(i4,a4)') iutmznpt2(i),utmhempt2(i)
            write(iox,'(i4,a4)') iutmznpt2(i),utmhempt2(i)
            ncommout=ncommout+1
         elseif(LLCCPT2(i) .AND. verparb(i).NE.'1.54a') then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCPT2(i)) then
            read(iunit,*) feastpt2(i),fnorthpt2(i)
            write(iox,*)  feastpt2(i),fnorthpt2(i)
            ncommout=ncommout+1
         endif
! ---    Map DATUM
         read(iunit,'(a8)') datumpt2(i)
         write(iox,'(a8)')  datumpt2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz2
         write(iox,'(a8)')  axtz2
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmappt2(i),iutmznpt2(i),&
                     utmhempt2(i),clat0,clon0,clat1,clat2,&
                     datumpt2(i),datenpt2(i),xyunitpt2(i),axtz2)
! ---    Process time zone
         read(axtz2(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM2 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz2
            stop 'Halted in RDHDEM2 -- see list file'
         endif
         xtz2(i)=-FLOAT(ibtzhh)
! ---    Old Header Record(s)
         if(verparb(i).EQ.'1.5a') then
            read(iunit,*)fname2,npt2file,nse2x,iutmznpt2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs2,label2(i)
            ibyr=ibdat/1000
            ibdy=ibdat-ibyr*1000
            ieyr=iedat/1000
            iedy=iedat-ieyr*1000
            write(iox,'(a12,9i6,2a12)')fname2,npt2file,nse2x,&
                      iutmznpt2(i),ibyr,ibdy,ibhr,ieyr,iedy,iehr,&
                      vrs2,label2(i)
            ncommout=ncommout+1
         elseif(verparb(i).EQ.'1.54a') then
            read(iunit,*)fname2,npt2file,nse2x,iutmznpt2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs2,label2(i)
            read(iunit,*) clat1,clat2,clat0,clon0
            ibyr=ibdat/1000
            ibdy=ibdat-ibyr*1000
            ieyr=iedat/1000
            iedy=iedat-ieyr*1000
            write(iox,'(a12,9i6,2a12)')fname2,npt2file,nse2x,&
                      iutmznpt2(i),ibyr,ibdy,ibhr,ieyr,iedy,iehr,&
                      vrs2,label2(i)
            write(iox,'(4a16)') clat1,clat2,clat0,clon0
            ncommout=ncommout+2
         elseif(verparb(i).EQ.'1.6a') then
            read(iunit,*)fname2,npt2file,nse2x,iuser0,iuser1,iuser2,&
                      iutmznpt2(i),ibyr,ibdy,ibhr,ibsec2(i),&
                      ieyr,iedy,iehr,iesec2(i),vrs2,label2(i)
            write(iox,'(a12,14i6,2a12)')fname2,npt2file,nse2x,&
                      iuser0,iuser1,iuser2,iutmznpt2(i),ibyr,ibdy,ibhr,&
                      ibsec2(i),ieyr,iedy,iehr,iesec2(i),vrs2,label2(i)
            ncommout=ncommout+1
         endif
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) then
            write(*,*)
            stop 'Halted in RDHDEM2'
         endif
! ---    Species list
         read(iunit,*)(cslst2x(n),n=1,nse2x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem2x(n),n=1,nse2x)
!     ---------------------------
      elseif(verparb(i).EQ.'2.1') then
!     ---------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmappt2(i)
         write(iox,'(a8)') pmappt2(i)
         ncommout=ncommout+1
! ---    Condition map projection
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmappt2(i),iutmznpt2(i),&
                     utmhempt2(i),clat0,clon0,clat1,clat2,&
                     datumpt2(i),datenpt2(i),xyunitpt2(i),axtz2)
         if(pmappt2(i).EQ.'UTM     ')  lutmpt2(i) =.TRUE.
         if(pmappt2(i).EQ.'LCC     ')  llccpt2(i) =.TRUE.
         if(pmappt2(i).EQ.'PS      ')  lpspt2(i)  =.TRUE.
         if(pmappt2(i).EQ.'EM      ')  lempt2(i)  =.TRUE.
         if(pmappt2(i).EQ.'LAZA    ')  llazapt2(i)=.TRUE.
         if(pmappt2(i).EQ.'TTM     ')  lttmpt2(i) =.TRUE.
! ---    Map projection parameters
         if(LUTMPT2(i)) then
            read(iunit,'(i4,a4)') iutmznpt2(i),utmhempt2(i)
            write(iox,'(i4,a4)') iutmznpt2(i),utmhempt2(i)
            ncommout=ncommout+1
         elseif(LLCCPT2(i)) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPSPT2(i)) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEMPT2(i).or.LLAZAPT2(i).or.LTTMPT2(i)) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCPT2(i).or.LLAZAPT2(i).or.LTTMPT2(i)) then
            read(iunit,*) feastpt2(i),fnorthpt2(i)
            write(iox,*)  feastpt2(i),fnorthpt2(i)
            ncommout=ncommout+1
         else
            feastpt2(i)=0.0
            fnorthpt2(i)=0.0
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datumpt2(i),datenpt2(i)
         write(iox,'(a8,a12)')  datumpt2(i),datenpt2(i)
         ncommout=ncommout+1
! ---    Map units
         read(iunit,'(a4)') xyunitpt2(i)
         write(iox,'(a4)')  xyunitpt2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz2
         write(iox,'(a8)')  axtz2
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmappt2(i),iutmznpt2(i),&
                     utmhempt2(i),clat0,clon0,clat1,clat2,&
                     datumpt2(i),datenpt2(i),xyunitpt2(i),axtz2)
! ---    Process Time zone
         read(axtz2(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM2 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz2
            stop 'Halted in RDHDEM2 -- see list file'
         endif
         xtz2(i)=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec2(i),&
                      ieyr,iedy,iehr,iesec2(i)
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDEM2'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec2(i),&
                           ieyr,iedy,iehr,iesec2(i)
         ncommout=ncommout+1
! ---    Number of sources and species
         read(iunit,*) npt2file,nse2x
         write(iox,*) npt2file,nse2x
         ncommout=ncommout+1
! ---    Species list
         read(iunit,*)(cslst2x(n),n=1,nse2x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem2x(n),n=1,nse2x)
!     -----
      else
!     -----
         write(*,*)'ERROR in PTEMARB File: ',i
         write(*,*)'Bad Dataset Version: ',verparb(i)
         write(*,*)'Expected 1.5, 1.5a, 1.54, 1.54a, 1.6, 1.6a, 2.1'
         stop
!     -----
      endif
!     -----
! --- Species info to scratch file
      do n=1,nse2x
         write(iox,'(a16,f12.4)')cslst2x(n),xmwem2x(n)
      enddo
      ncommout=ncommout+nse2x
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec2(i))
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec2(i))
! --- Apply time zone adjustment to date/times
      if(xtz2(i).GE.-13 .AND. xtz2(i).LE.13) then
         t2btz2(i)=xtz2(i)-xbtz
         ishift=NINT(t2btz2(i))
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in PTEMARB.DAT file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz2(i)
         stop 'Bad Time Zone in PTEMARB.DAT file -- see list file'
      endif
! --- Hourly Data:  Move the time for the first period from the end
! ---               to the start of the first hour
      if(verparb(i).EQ.'1.5a' .OR. verparb(i).EQ.'1.54a') then
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      endif
! --- Form date-hr variables for /PT2/
      ibdathr2(i)=ibyr*100000+ibdy*100+ibhr
      iedathr2(i)=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1pt2(i))
      call XTRACTLL(io6,'LAT ',clat2,rnlat2pt2(i))
      call XTRACTLL(io6,'LAT ',clat0,rnlat0pt2(i))
      call XTRACTLL(io6,'LON ',clon0,relon0pt2(i))
! --- Set source index range for this file
      if(i.EQ.1) then
         ibsrc2(i)=1
         iesrc2(i)=npt2file
      else
         ibsrc2(i)=iesrc2(i-1)+1
         iesrc2(i)=iesrc2(i-1)+npt2file
      endif
! --- Set logical to indicate if the number of sources specified in
! --- the control file (NPT2) have been found
      lnpt2=.FALSE.
      if(npt2.EQ.iesrc2(i)) lnpt2=.TRUE.
! --- Process data that must be identical in all PTEMARB.DAT files
      if(i.EQ.1) then
! ---    Pass into /pt2/ names
         nse2=nse2x
!
         if(nse2.gt.mxspec)then
            write(io6,11)nse2,mxspec
11          format(/1x,'ERROR in SUBR. RDHDEM2 -- No. emitted species',&
            ' in PTEMARB file is greater than current array',&
            ' dimension'/1x,&
            '     No. emitted species in file: ',i5/1x,&
            'Current Array dimension (MXSPEC): ',i5)
            write(*,*)
            stop 'Halted in RDHDEM2 -- see list file.'
         endif
      else
! ---    Check for consistency with first file
         if(nse2.NE.nse2x) then
            write(io6,12)nse2,nse2x
12          format(/1x,'ERROR in SUBR. RDHDEM2 -- No. emitted species',&
            ' must match in all PTEMARB files',/1x,&
            '     No. emitted species expected/found: ',2i5)
            write(*,*)
            stop 'Halted in RDHDEM2 -- see list file.'
         endif
      endif
! --- Pass into /pt2/ names or check for consistency
      if(i.EQ.1) then
         do is=1,nse2
            cslst2(is)=cslst2x(is)
            xmwem2(is)=xmwem2x(is)
         enddo
      else
         lmatch=.TRUE.
         do is=1,nse2
            if(cslst2(is).NE.cslst2x(is)) lmatch=.FALSE.
            if(xmwem2(is).NE.xmwem2x(is)) lmatch=.FALSE.
         enddo
         if(.not.LMATCH) then
            write(io6,15)
15          format(/1x,'ERROR in SUBR. RDHDEM2 -- Species names and',&
            ' molecular weights must match in all PTEMARB files',/)
            write(io6,*)
            write(io6,*)'PTEMARB.DAT File:    1'
            do is=1,nse2
               write(io6,*) cslst2(is),xmwem2(is)
            enddo
            write(io6,*)
            write(io6,*)'PTEMARB.DAT File:',i
            do is=1,nse2
               write(io6,*) cslst2x(is),xmwem2x(is)
            enddo
            write(*,*)
            stop 'Halted in RDHDEM2 -- see list file.'
         endif
      endif
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,102) i
102      format(///1x,13('----------')//1x,'Header record data from ',&
         'the PTEMARB point source file',i5)
         write(io6,103)dataset,ibsrc2(i),iesrc2(i),nse2x,axtz2,&
               ibdathr2(i),ibsec2(i),iedathr2(i),iesec2(i),pmappt2(i),&
               datumpt2(i)
103      format(/1x,'DATASET: ',a16/1x,'IBSRC2:  ',i9/&
         1x,'IESRC2:  ',i9/1x,'NSE2:    ',i6/1x,'AXTZ2:   ',a8/&
         1x,'IBDATHR2:',i9/1x,'IBSEC2:  ',i4/&
         1x,'IEDATHR2:',i9/1x,'IESEC2:  ',i4/&
         1x,'MAP2:    ',a8/1x,'DATUM:   ',a8)
         if(LUTMPT2(i)) write(io6,104) iutmznpt2(i),utmhempt2(i)
         if(LLCCPT2(i)) write(io6,105) clat0,clon0,clat1,clat2,&
                                       feastpt2(i),fnorthpt2(i)
         if(LPSPT2(i)) write(io6,106) clat0,clon0,clat1
         if(LEMPT2(i)) write(io6,107) clat0,clon0
         if(LLAZAPT2(i).or.LTTMPT2(i)) write(io6,108) clat0,clon0,&
                                       feastpt2(i),fnorthpt2(i)
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
         write(io6,109)
109      format(4x,'SPECIES',6x,'MOL. WT.'/)
         do is=1,nse2
            write(io6,110)cslst2(is),xmwem2(is)
         enddo
110      format(1x,a12,3x,f7.3)
      endif
! --- Initialize "previous" period times for /PT2/
      nstep2(i)=1
      ndhrqb2(1,i)=-000100100
      nsecqb2(1,i)=0
      ndhrqe2(1,i)=ibdathr2(i)
      nsecqe2(1,i)=ibsec2(i)
      return
      end
!----------------------------------------------------------------------
      subroutine RDHDEM3(iunit0,i,lprt,lnar2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 070921           RDHDEM3
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Read the header records from the BAEMARB buoyant area
!               source file (arbitrarily-varying location & emissions)
!
! --- UPDATE
! --- V6.1-V6.22: 070921 (DGS): Condition GEO referencing string
!                               variables and add QA
!                               Remove calls to ALLCAP
! --- V5.7-V6.1 : 050915 (DGS): Dataset version 2.1 is added,
!                               including full coordinate and time
!                               documentation with begin/end times with
!                               seconds.
!                               Header information is passed to scratch
!                               file.
! --- V5.5-V5.7 : 030402 (DGS): CALUTILS version of XTRACTLL
!                               Add list file unit to YR4 call
! --- V5.4-V5.4 : 000602_6(DGS): Change name of LCC longitude from
!                                '-ELON-' to '-WLON-'
! --- V5.2-V5.4 : 000602 (DGS): allow multiple BAEMARB files
!                 000602 (DGS): add message to "stop"
!                 000602 (DGS): add header record for LC map data
! --- V5.0-V5.2 : 991104 (DGS): YYYY format used for year
! --- V4.0-V5.0 : 971107 (DGS): Check for valid version of file
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 BAEMARB file
!                I - integer    - Index for current BAEMARB file
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!            LNAR2 - logical    - Flag indicating all sources found
!
!     Common Block /AR2/ variables:
!            NAR2
!     Common Block /DATASET/ variables:
!            VERAARB(mxemdat)
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!     Parameters:
!            IO6, MXSPEC, MXEMDAT, MXQSTEP, IOX
!
!
! --- OUTPUT:
!
!     Common Block /AR2/ variables:
!           NSE3,
!           IBSRC3(mxemdat),IESRC3(mxemdat),IUEM3(mxemdat),
!           IBDATHR3(mxemdat),IBSEC3(mxemdat),IEDATHR3(mxemdat),
!           IESEC3(mxemdat),XTZ3(mxemdat),T2BTZ3(mxemdat),
!           XMWEM3(mxspec),IXREM3(mxspec),
!           lutmar2(mxemdat),llccar2(mxemdat),lpsar2(mxemdat),
!           lemar2(mxemdat),llazaar2(mxemdat),lttmar2(mxemdat),
!           iutmznar2(mxemdat),feastar2(mxemdat),fnorthar2(mxemdat),
!           rnlat0ar2(mxemdat),relon0ar2(mxemdat),
!           rnlat1ar2(mxemdat),rnlat2ar2(mxemdat),NSTEP3(mxemdat),
!           NDHRQB3(mxqstep,mxemdat),NSECQB3(mxqstep,mxemdat),
!           NDHRQE3(mxqstep,mxemdat),NSECQE3(mxqstep,mxemdat),
!           CSLST3(mxspec),CID3(mxarea),
!           pmapar2(mxemdat),utmhemar2(mxemdat),datumar2(mxemdat),
!           datenar2(mxemdat),xyunitar2(mxemdat)
!
! --- RDHDEM3 called by: SETUP
! --- RDHDEM3 calls:     YR4, XTRACTLL, INCR, TCLOCK, GEOFMT
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'ar2.puf'
      include 'dataset.puf'
      include 'datehr.puf'
      include 'qa.puf'
!
      LOGICAL lprt, lnar2, lmatch
! --- Local variables
      real xmwem3x(mxspec)
      character*8 axtz3
      character*12 fname3,label3(mxemdat)
      character*12 cslst3x(mxspec),vrs3
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Set unit number
      iunit=iunit0+i-1
! --- Initialize map projection logicals
      lutmar2(i)=.FALSE.
      llccar2(i)=.FALSE.
      lpsar2(i)=.FALSE.
      lemar2(i)=.FALSE.
      llazaar2(i)=.FALSE.
      lttmar2(i)=.FALSE.
! --- Initialize other variables
      datenar2(i)='02-21-2003  '
      xyunitar2(i)='KM  '
      ibsec3(i)=0.0
      iesec3(i)=0.0
      feastar2(i)=0.0
      fnorthar2(i)=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmapar2(i)='XXX     '
      iutmznar2(i)=1
      utmhemar2(i)='N   '
      datumar2(i)='XXX     '
      axtz3='UTC+0000'
!     -----------------------
      if(veraarb(i).EQ.'1.5' .OR.&
         veraarb(i).EQ.'1.54' .OR.&
         veraarb(i).EQ.'1.6') then
!     -----------------------
! ---    Provide help and then stop (these formats are not supported)
         dataset='BAEMARB.DAT     '
         if(veraarb(i).EQ.'1.5') then
            read(iunit,*)fname3,nar2file,nse3x,iutmznar2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs3,label3(i)
            dataver='1.5a            '
            datamod(1:40)='Augmented 5 format with Map Projection, '
            datamod(41:64)='DATUM, Time Zone        '
         elseif(veraarb(i).EQ.'1.54') then
            read(iunit,*)fname3,nar2file,nse3x,iutmznar2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs3,label3(i)
            dataver='1.54a           '
            datamod(1:42)='Augmented 5.4 format with Map Projection, '
            datamod(43:64)='DATUM, Time Zone      '
         elseif(veraarb(i).EQ.'1.6') then
            read(iunit,*)fname3,nar2file,nse3x,iuem3(i),iuser1,iuser2,&
                      iutmznar2(i),ibyr,ibdy,ibhr,ibsec3(i),&
                      ieyr,iedy,iehr,iesec3(i),vrs3,label3(i)
            dataver='1.6a            '
            datamod(1:40)='Augmented 6 format with Map Projection, '
            datamod(41:64)='DATUM, Time Zone        '
         endif
         write(io6,*)
         write(io6,*)
         write(io6,*)'ERROR in BAEMARB files'
         write(io6,*)
         write(io6,*)'Old BAEMARB.DAT format cannot be used because:'
         write(io6,*)'1.  Coordinate map projection is incomplete'
         write(io6,*)'2.  The DATUM for the coordinates is missing'
         write(io6,*)'3.  The time zone is missing'
         write(io6,*)
         write(io6,*)'ADD these records to the top of the file:'
         write(io6,*)
         write(io6,'(2a16,a64)') dataset,dataver,datamod
         if(iutmznar2(i).NE.0) then
            write(io6,'(a8)')'UTM     '
            write(io6,'(a8)')'  xxN   '
         else
            write(io6,'(a8)')'LCC     '
            if(veraarb(i).NE.'1.54') then
              write(io6,'(4a16)')'yy.yyyN         ','xx.xxxW         ',&
                                 'yy.yyyN         ','yy.yyyN         '
            endif
            write(io6,*)zero,zero
         endif
         write(io6,'(a8)')'DATUM   '
         write(io6,'(a8)')'UTC-0500'
         write(io6,*)
         write(io6,*)'UPDATE these records to describe your file:'
         if(iutmznar2(i).NE.0) then
           write(io6,*)'- Replace the xxN with your UTM zone and',&
                       ' hemisphere (S for Southern Hemisphere)'
         else
           if(veraarb(i).NE.'1.54') then
             write(io6,*)'- Replace the xx.xxxW and yy.yyyN with your',&
                       ' LCC projection parameters.'
             write(io6,*)'  The order is Origin Latitude, Origin',&
                       ' Longitude, and the 2 Matching Parallels'
             write(io6,*)'  (each field is 16 characters)'
           endif
           write(io6,*)'- Replace the 0.0 0.0 with your False Easting',&
                       ' and Northing if non-zero'   
         endif
         write(io6,*)'- Replace DATUM with your datum code (enter',&
                     ' left-justified in the 8-character field)'
         write(io6,*)'- Replace UTC-0500 with your time zone code'
         write(io6,*)'  (GMT=UTC+0000, EST=UTC-0500, CET=UTC+0100)'
         write(io6,*)
         write(*,*)
         write(*,*)'ERROR in BAEMARB files -- see list file'
         write(*,*)
         stop
!     ----------------------------
      elseif(veraarb(i).EQ.'1.5a' .OR.&
             veraarb(i).EQ.'1.54a' .OR.&
             veraarb(i).EQ.'1.6a') then
!     ----------------------------
! ---    Augmented headers
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         ncommout=ncommout+1
! ---    Map projection
         read(iunit,'(a8)') pmapar2(i)
         write(iox,'(a8)') pmapar2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapar2(i),iutmznar2(i),&
                     utmhemar2(i),clat0,clon0,clat1,clat2,&
                     datumar2(i),datenar2(i),xyunitar2(i),axtz3)
         if(pmapar2(i).EQ.'UTM     ')  lutmar2(i) =.TRUE.
         if(pmapar2(i).EQ.'LCC     ')  llccar2(i) =.TRUE.
         if(pmapar2(i).EQ.'PS      ')  lpsar2(i)  =.TRUE.
         if(pmapar2(i).EQ.'EM      ')  lemar2(i)  =.TRUE.
         if(pmapar2(i).EQ.'LAZA    ')  llazaar2(i)=.TRUE.
         if(pmapar2(i).EQ.'TTM     ')  lttmar2(i) =.TRUE.
! ---    Map projection parameters
         if(LUTMAR2(i)) then
            read(iunit,'(i4,a4)') iutmznar2(i),utmhemar2(i)
            write(iox,'(i4,a4)') iutmznar2(i),utmhemar2(i)
            ncommout=ncommout+1
         elseif(LLCCAR2(i) .AND. veraarb(i).NE.'1.54a') then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCAR2(i)) then
            read(iunit,*) feastar2(i),fnorthar2(i)
            write(iox,*)  feastar2(i),fnorthar2(i)
            ncommout=ncommout+1
         endif
! ---    Map DATUM
         read(iunit,'(a8)') datumar2(i)
         write(iox,'(a8)')  datumar2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz3
         write(iox,'(a8)')  axtz3
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapar2(i),iutmznar2(i),&
                     utmhemar2(i),clat0,clon0,clat1,clat2,&
                     datumar2(i),datenar2(i),xyunitar2(i),axtz3)
! ---    Process Time zone
         read(axtz3(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM3 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz3
            stop 'Halted in RDHDEM3 -- see list file'
         endif
         xtz3(i)=-FLOAT(ibtzhh)
! ---    Old Header Record(s)
         if(veraarb(i).EQ.'1.5a') then
            read(iunit,*)fname3,nar2file,nse3x,iutmznar2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs3,label3(i)
            ibyr=ibdat/1000
            ibdy=ibdat-ibyr*1000
            ieyr=iedat/1000
            iedy=iedat-ieyr*1000
            write(iox,'(a12,9i6,2a12)')fname3,nar2file,nse3x,&
                      iutmznar2(i),ibyr,ibdy,ibhr,ieyr,iedy,iehr,&
                      vrs3,label3(i)
            ncommout=ncommout+1
         elseif(veraarb(i).EQ.'1.54a') then
            read(iunit,*)fname3,nar2file,nse3x,iutmznar2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs3,label3(i)
            read(iunit,*) clat1,clat2,clat0,clon0
            ibyr=ibdat/1000
            ibdy=ibdat-ibyr*1000
            ieyr=iedat/1000
            iedy=iedat-ieyr*1000
            write(iox,'(a12,9i6,2a12)')fname3,nar2file,nse3x,&
                      iutmznar2(i),ibyr,ibdy,ibhr,ieyr,iedy,iehr,&
                      vrs3,label3(i)
            write(iox,'(4a16)') clat1,clat2,clat0,clon0
            ncommout=ncommout+2
         elseif(veraarb(i).EQ.'1.6a') then
            read(iunit,*)fname3,nar2file,nse3x,iuem3(i),iuser1,iuser2,&
                      iutmznar2(i),ibyr,ibdy,ibhr,ibsec3(i),&
                      ieyr,iedy,iehr,iesec3(i),vrs3,label3(i)
            write(iox,'(a12,14i6,2a12)')fname3,nar2file,nse3x,iuem3(i),&
                      iuser1,iuser2,iutmznar2(i),ibyr,ibdy,ibhr,&
                      ibsec3(i),ieyr,iedy,iehr,iesec3(i),vrs3,label3(i)
            ncommout=ncommout+1
         endif
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) then
            write(*,*)
            stop 'Halted in RDHDEM3'
         endif
! ---    Species list
         read(iunit,*)(cslst3x(n),n=1,nse3x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem3x(n),n=1,nse3x)
!     ---------------------------
      elseif(veraarb(i).EQ.'2.1') then
!     ---------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmapar2(i)
         write(iox,'(a8)') pmapar2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapar2(i),iutmznar2(i),&
                     utmhemar2(i),clat0,clon0,clat1,clat2,&
                     datumar2(i),datenar2(i),xyunitar2(i),axtz3)
         if(pmapar2(i).EQ.'UTM     ')  lutmar2(i) =.TRUE.
         if(pmapar2(i).EQ.'LCC     ')  llccar2(i) =.TRUE.
         if(pmapar2(i).EQ.'PS      ')  lpsar2(i)  =.TRUE.
         if(pmapar2(i).EQ.'EM      ')  lemar2(i)  =.TRUE.
         if(pmapar2(i).EQ.'LAZA    ')  llazaar2(i)=.TRUE.
         if(pmapar2(i).EQ.'TTM     ')  lttmar2(i) =.TRUE.
! ---    Map projection parameters
         if(LUTMAR2(i)) then
            read(iunit,'(i4,a4)') iutmznar2(i),utmhemar2(i)
            write(iox,'(i4,a4)') iutmznar2(i),utmhemar2(i)
            ncommout=ncommout+1
         elseif(LLCCAR2(i)) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPSAR2(i)) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEMAR2(i).or.LLAZAAR2(i).or.LTTMAR2(i)) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCAR2(i).or.LLAZAAR2(i).or.LTTMAR2(i)) then
            read(iunit,*) feastar2(i),fnorthar2(i)
            write(iox,*)  feastar2(i),fnorthar2(i)
            ncommout=ncommout+1
         else
            feastar2(i)=0.0
            fnorthar2(i)=0.0
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datumar2(i),datenar2(i)
         write(iox,'(a8,a12)')  datumar2(i),datenar2(i)
         ncommout=ncommout+1
! ---    Map units
         read(iunit,'(a4)') xyunitar2(i)
         write(iox,'(a4)')  xyunitar2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz3
         write(iox,'(a8)')  axtz3
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapar2(i),iutmznar2(i),&
                     utmhemar2(i),clat0,clon0,clat1,clat2,&
                     datumar2(i),datenar2(i),xyunitar2(i),axtz3)
! ---    Process Time zone
         read(axtz3(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM3 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz3
            stop 'Halted in RDHDEM3 -- see list file'
         endif
         xtz3(i)=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec3(i),&
                      ieyr,iedy,iehr,iesec3(i)
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDEM3'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec3(i),&
                           ieyr,iedy,iehr,iesec3(i)
         ncommout=ncommout+1
! ---    Number of sources and species
         read(iunit,*) nar2file,nse3x
         write(iox,*) nar2file,nse3x
         ncommout=ncommout+1
! ---    Species list
         read(iunit,*)(cslst3x(n),n=1,nse3x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem3x(n),n=1,nse3x)
!     -----
      else
!     -----
         write(*,*)'ERROR in BAEMARB File: ',i
         write(*,*)'Bad Dataset Version: ',veraarb(i)
         write(*,*)'Expected 1.5, 1.5a, 1.54, 1.54a, 1.6, 1.6a, 2.1'
         stop
!     -----
      endif
!     -----
! --- Species info to scratch file
      do n=1,nse3x
         write(iox,'(a16,f12.4)')cslst3x(n),xmwem3x(n)
      enddo
      ncommout=ncommout+nse3x
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec3(i))
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec3(i))
! --- Apply time zone adjustment to date/times
      if(xtz3(i).GE.-13 .AND. xtz3(i).LE.13) then
         t2btz3(i)=xtz3(i)-xbtz
         ishift=NINT(t2btz3(i))
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in BAEMARB.DAT file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz3(i)
         stop 'Bad Time Zone in BAEMARB.DAT file -- see list file'
      endif
! --- Hourly Data:  Move the time for the first period from the end
! ---               to the start of the first hour
      if(veraarb(i).EQ.'1.5a' .OR. veraarb(i).EQ.'1.54a') then
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      endif
! --- Form date-hr variables for /AR2/
      ibdathr3(i)=ibyr*100000+ibdy*100+ibhr
      iedathr3(i)=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1ar2(i))
      call XTRACTLL(io6,'LAT ',clat2,rnlat2ar2(i))
      call XTRACTLL(io6,'LAT ',clat0,rnlat0ar2(i))
      call XTRACTLL(io6,'LON ',clon0,relon0ar2(i))
! --- Set source index range for this file
      if(i.EQ.1) then
         ibsrc3(i)=1
         iesrc3(i)=nar2file
      else
         ibsrc3(i)=iesrc3(i-1)+1
         iesrc3(i)=iesrc3(i-1)+nar2file
      endif
! --- Set logical to indicate if the number of sources specified in
! --- the control file (NAR2) have been found
      lnar2=.FALSE.
      if(nar2.EQ.iesrc3(i)) lnar2=.TRUE.
! --- Process data that must be identical in all BAEMARB.DAT files
      if(i.EQ.1) then
! ---    Pass into /ar2/ names
         nse3=nse3x
!
         if(nse3.gt.mxspec)then
            write(io6,11)nse3,mxspec
11          format(/1x,'ERROR in SUBR. RDHDEM3 -- No. emitted species',&
            ' in BAEMARB file is greater than current array',&
            ' dimension'/1x,&
            '     No. emitted species in file: ',i5/1x,&
            'Current Array dimension (MXSPEC): ',i5)
            write(*,*)
            stop 'Halted in RDHDEM3 -- see list file.'
         endif
      else
! ---    Check for consistency with first file
         if(nse3.NE.nse3x) then
            write(io6,12)nse3,nse3x
12          format(/1x,'ERROR in SUBR. RDHDEM3 -- No. emitted species',&
            ' must match in all BAEMARB files',/1x,&
            '     No. emitted species expected/found: ',2i5)
            write(*,*)
            stop 'Halted in RDHDEM3 -- see list file.'
         endif
      endif
! --- Pass into /ar2/ names or check for consistency
      if(i.EQ.1) then
         do is=1,nse3
            cslst3(is)=cslst3x(is)
            xmwem3(is)=xmwem3x(is)
         enddo
      else
         lmatch=.TRUE.
         do is=1,nse3
            if(cslst3(is).NE.cslst3x(is)) lmatch=.FALSE.
            if(xmwem3(is).NE.xmwem3x(is)) lmatch=.FALSE.
         enddo
         if(.not.LMATCH) then
            write(io6,15)
15          format(/1x,'ERROR in SUBR. RDHDEM3 -- Species names and',&
            ' molecular weights must match in all BAEMARB files',/)
            write(io6,*)
            write(io6,*)'BAEMARB.DAT File:    1'
            do is=1,nse3
               write(io6,*) cslst3(is),xmwem3(is)
            enddo
            write(io6,*)
            write(io6,*)'BAEMARB.DAT File:',i
            do is=1,nse3
               write(io6,*) cslst3x(is),xmwem3x(is)
            enddo
            write(*,*)
            stop 'Halted in RDHDEM3 -- see list file.'
         endif
      endif
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,102) i
102      format(///1x,13('----------')//1x,'Header record data from ',&
         'the BAEMARB area source file',i5)
         write(io6,103)dataset,ibsrc3(i),iesrc3(i),nse3x,axtz3,&
               ibdathr3(i),ibsec3(i),iedathr3(i),iesec3(i),pmapar2(i),&
               datumar2(i)
103      format(/1x,'DATASET: ',a16/1x,'IBSRC3:  ',i9/&
         1x,'IESRC3:  ',i9/1x,'NSE3:    ',i6/1x,'AXTZ3:   ',a8/&
         1x,'IBDATHR3:',i9/1x,'IBSEC3:  ',i4/&
         1x,'IEDATHR3:',i9/1x,'IETIM3:  ',i4/&
         1x,'MAP3:    ',a8/1x,'DATUM:   ',a8)
         if(LUTMAR2(i)) write(io6,104) iutmznar2(i),utmhemar2(i)
         if(LLCCAR2(i)) write(io6,105) clat0,clon0,clat1,clat2,&
                                       feastar2(i),fnorthar2(i)
         if(LPSAR2(i)) write(io6,106) clat0,clon0,clat1
         if(LEMAR2(i)) write(io6,107) clat0,clon0
         if(LLAZAAR2(i).or.LTTMAR2(i)) write(io6,108) clat0,clon0,&
                                       feastar2(i),fnorthar2(i)
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
         write(io6,109)
109      format(4x,'SPECIES',6x,'MOL. WT.'/)
         do is=1,nse3
            write(io6,110)cslst3(is),xmwem3(is)
         enddo
110      format(1x,a12,3x,f7.3)
      endif
! --- Initialize "previous" period times for /AR2/
      nstep3(i)=1
      ndhrqb3(1,i)=-000100100
      nsecqb3(1,i)=0
      ndhrqe3(1,i)=ibdathr3(i)
      nsecqe3(1,i)=ibsec3(i)
      return
      end
!----------------------------------------------------------------------
      subroutine RDHDEM4(iunit0,i,lprt,lnvl2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 070921           RDHDEM4
!                J. Scire, D. Strimaitis
!
! --- PURPOSE:  Read the header records from the VOLEMARB volume source
!               variable emissions file
!
! --- UPDATE
! --- V6.1-V6.22: 070921 (DGS): Condition GEO referencing string
!                               variables and add QA
!                               Remove calls to ALLCAP
! --- V5.7-V6.1 : 050915 (DGS): Dataset version 2.1 is added,
!                               including full coordinate and time
!                               documentation with begin/end times with
!                               seconds.
!                               Header information is passed to scratch
!                               file.
!                               VOLEMARB.DAT file must be ASCII.
! --- V5.5-V5.7 : 030402 (DGS): CALUTILS version of XTRACTLL
!                               Add list file unit to YR4 call
! --- V5.4-V5.4 : 000602_6(DGS): Change name of LCC longitude from
!                                '-ELON-' to '-WLON-'
! --- V5.2-V5.4 : 000602 (DGS): update from VOLEM.DAT to VOLEMARB.DAT
!                               with multiple files
!                 000602 (DGS): add header record for LC map data
!                 000602 (DGS): add message to "stop"
! --- V5.0-V5.2 : 991104 (DGS): YYYY format used for year
! --- V4.0-V5.0 : 980807 (DGS): VOLEM.DAT either binary or ASCII
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 VOLEMARB file
!                I - integer    - Index for current VOLEMARB file
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!            LNVL2 - logical    - Flag indicating all sources found
!
!     Common Block /VOL2/ variables:
!            NVL2
!     Common Block /DATASET/ variables:
!            VERVARB(mxemdat)
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!     Parameters:
!            IO6, MXSPEC, MXEMDAT, MXQSTEP, MXVOL,  IOX
!
!
! --- OUTPUT:
!
!     Common Block /VOL2/ variables:
!           NSE4,
!           IBSRC4(mxemdat),IESRC4(mxemdat),
!           IBDATHR4(mxemdat),IBSEC4(mxemdat),IEDATHR4(mxemdat),
!           IESEC4(mxemdat),XTZ4(mxemdat),T2BTZ4(mxemdat),
!           XMWEM4(mxspec),IXREM4(mxspec),
!           lutmvl2(mxemdat),llccvl2(mxemdat),lpsvl2(mxemdat),
!           lemvl2(mxemdat),llazavl2(mxemdat),lttmvl2(mxemdat),
!           iutmznvl2(mxemdat),feastvl2(mxemdat),fnorthvl2(mxemdat),
!           rnlat0vl2(mxemdat),relon0vl2(mxemdat),
!           rnlat1vl2(mxemdat),rnlat2vl2(mxemdat),NSTEP4(mxemdat),
!           NDHRQB4(mxqstep,mxemdat),NSECQB4(mxqstep,mxemdat),
!           NDHRQE4(mxqstep,mxemdat),NSECQE4(mxqstep,mxemdat),
!           CSLST4(mxspec),CID4(mxvol),
!           pmapvl2(mxemdat),utmhemvl2(mxemdat),datumvl2(mxemdat),
!           datenvl2(mxemdat),xyunitvl2(mxemdat)
!
! --- RDHDEM4 called by: SETUP
! --- RDHDEM4 calls:     YR4, XTRACTLL, INCR, TCLOCK, GEOFMT
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'vol2.puf'
      include 'dataset.puf'
      include 'datehr.puf'
      include 'qa.puf'
!
      LOGICAL lprt, lnvl2, lmatch
! --- Local variables
      real xmwem4x(mxspec)
      character*8 axtz4
      character*12 fname4,label4(mxemdat)
      character*12 cslst4x(mxspec),vrs4
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Set unit number
      iunit=iunit0+i-1
! --- Initialize map projection logicals
      lutmvl2(i)=.FALSE.
      llccvl2(i)=.FALSE.
      lpsvl2(i)=.FALSE.
      lemvl2(i)=.FALSE.
      llazavl2(i)=.FALSE.
      lttmvl2(i)=.FALSE.
! --- Initialize other variables
      datenvl2(i)='02-21-2003  '
      xyunitvl2(i)='KM  '
      ibsec4(i)=0.0
      iesec4(i)=0.0
      feastvl2(i)=0.0
      fnorthvl2(i)=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmapvl2(i)='XXX     '
      iutmznvl2(i)=1
      utmhemvl2(i)='N   '
      datumvl2(i)='XXX     '
      axtz4='UTC+0000'
!     -----------------------
      if(vervarb(i).EQ.'1.54' .OR.&
         vervarb(i).EQ.'1.6') then
!     -----------------------
! ---    Provide help and then stop (these formats are not supported)
         dataset='VOLEMARB.DAT    '
         if(vervarb(i).EQ.'1.54') then
            read(iunit,*)fname4,nvl2file,nse4x,iutmznvl2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs4,label4(i)
            dataver='1.54a           '
            datamod(1:42)='Augmented 5.4 format with Map Projection, '
            datamod(43:64)='DATUM, Time Zone      '
         elseif(vervarb(i).EQ.'1.6') then
            read(iunit,*)fname4,nvl2file,nse4x,iutmznvl2(i),&
                      ibyr,ibdy,ibhr,ibsec4(i),&
                      ieyr,iedy,iehr,iesec4(i),vrs4,label4(i)
            dataver='1.6a            '
            datamod(1:40)='Augmented 6 format with Map Projection, '
            datamod(41:64)='DATUM, Time Zone        '
         endif
         write(io6,*)
         write(io6,*)
         write(io6,*)'ERROR in VOLEMARB files'
         write(io6,*)
         write(io6,*)'Old VOLEMARB.DAT format cannot be used because:'
         write(io6,*)'1.  Coordinate map projection is incomplete'
         write(io6,*)'2.  The DATUM for the coordinates is missing'
         write(io6,*)'3.  The time zone is missing'
         write(io6,*)
         write(io6,*)'ADD these records to the top of the file:'
         write(io6,*)
         write(io6,'(2a16,a64)') dataset,dataver,datamod
         if(iutmznvl2(i).NE.0) then
            write(io6,'(a8)')'UTM     '
            write(io6,'(a8)')'  xxN   '
         else
            write(io6,'(a8)')'LCC     '
            if(vervarb(i).NE.'1.54') then
              write(io6,'(4a16)')'yy.yyyN         ','xx.xxxW         ',&
                                 'yy.yyyN         ','yy.yyyN         '
            endif
            write(io6,*)zero,zero
         endif
         write(io6,'(a8)')'DATUM   '
         write(io6,'(a8)')'UTC-0500'
         write(io6,*)
         write(io6,*)'UPDATE these records to describe your file:'
         if(iutmznvl2(i).NE.0) then
           write(io6,*)'- Replace the xxN with your UTM zone and',&
                       ' hemisphere (S for Southern Hemisphere)'
         else
           if(vervarb(i).NE.'1.54') then
             write(io6,*)'- Replace the xx.xxxW and yy.yyyN with your',&
                       ' LCC projection parameters.'
             write(io6,*)'  The order is Origin Latitude, Origin',&
                       ' Longitude, and the 2 Matching Parallels'
             write(io6,*)'  (each field is 16 characters)'
           endif
           write(io6,*)'- Replace the 0.0 0.0 with your False Easting',&
                       ' and Northing if non-zero'   
         endif
         write(io6,*)'- Replace DATUM with your datum code (enter',&
                     ' left-justified in the 8-character field)'
         write(io6,*)'- Replace UTC-0500 with your time zone code'
         write(io6,*)'  (GMT=UTC+0000, EST=UTC-0500, CET=UTC+0100)'
         write(io6,*)
         write(*,*)
         write(*,*)'ERROR in VOLEMARB files -- see list file'
         write(*,*)
         stop
!     ----------------------------
      elseif(vervarb(i).EQ.'1.54a' .OR.&
             vervarb(i).EQ.'1.6a') then
!     ----------------------------
! ---    Augmented headers
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         ncommout=ncommout+1
! ---    Map projection
         read(iunit,'(a8)') pmapvl2(i)
         write(iox,'(a8)') pmapvl2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapvl2(i),iutmznvl2(i),&
                     utmhemvl2(i),clat0,clon0,clat1,clat2,&
                     datumvl2(i),datenvl2(i),xyunitvl2(i),axtz4)
         if(pmapvl2(i).EQ.'UTM     ')  lutmvl2(i) =.TRUE.
         if(pmapvl2(i).EQ.'LCC     ')  llccvl2(i) =.TRUE.
         if(pmapvl2(i).EQ.'PS      ')  lpsvl2(i)  =.TRUE.
         if(pmapvl2(i).EQ.'EM      ')  lemvl2(i)  =.TRUE.
         if(pmapvl2(i).EQ.'LAZA    ')  llazavl2(i)=.TRUE.
         if(pmapvl2(i).EQ.'TTM     ')  lttmvl2(i) =.TRUE.
! ---    Map projection parameters
         if(LUTMVL2(i)) then
            read(iunit,'(i4,a4)') iutmznvl2(i),utmhemvl2(i)
            write(iox,'(i4,a4)') iutmznvl2(i),utmhemvl2(i)
            ncommout=ncommout+1
         elseif(LLCCVL2(i) .AND. vervarb(i).NE.'1.54a') then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCVL2(i)) then
            read(iunit,*) feastvl2(i),fnorthvl2(i)
            write(iox,*)  feastvl2(i),fnorthvl2(i)
            ncommout=ncommout+1
         endif
! ---    Map DATUM
         read(iunit,'(a8)') datumvl2(i)
         write(iox,'(a8)')  datumvl2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz4
         write(iox,'(a8)')  axtz4
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapvl2(i),iutmznvl2(i),&
                     utmhemvl2(i),clat0,clon0,clat1,clat2,&
                     datumvl2(i),datenvl2(i),xyunitvl2(i),axtz4)
! ---    Process Time zone
         read(axtz4(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM4 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz4
            stop 'Halted in RDHDEM4 -- see list file'
         endif
         xtz4(i)=-FLOAT(ibtzhh)
! ---    Old Header Record(s)
         if(vervarb(i).EQ.'1.54a') then
            read(iunit,*)fname4,nvl2file,nse4x,iutmznvl2(i),&
                      ibdat,ibhr,iedat,iehr,&
                      vrs4,label4(i)
            read(iunit,*) clat1,clat2,clat0,clon0
            ibyr=ibdat/1000
            ibdy=ibdat-ibyr*1000
            ieyr=iedat/1000
            iedy=iedat-ieyr*1000
            write(iox,'(a12,9i6,2a12)')fname4,nvl2file,nse4x,&
                      iutmznvl2(i),ibyr,ibdy,ibhr,ieyr,iedy,iehr,&
                      vrs4,label4(i)
            write(iox,'(4a16)') clat1,clat2,clat0,clon0
            ncommout=ncommout+2
         elseif(vervarb(i).EQ.'1.6a') then
            read(iunit,*)fname4,nvl2file,nse4x,iutmznvl2(i),&
                      ibyr,ibdy,ibhr,ibsec4(i),&
                      ieyr,iedy,iehr,iesec4(i),vrs4,label4(i)
            write(iox,'(a12,11i6,2a12)')fname4,nvl2file,nse4x,&
                      iutmznvl2(i),ibyr,ibdy,ibhr,ibsec4(i),&
                      ieyr,iedy,iehr,iesec4(i),vrs4,label4(i)
            ncommout=ncommout+1
         endif
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) then
            write(*,*)
            stop 'Halted in RDHDEM4'
         endif
! ---    Species list
         read(iunit,*)(cslst4x(n),n=1,nse4x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem4x(n),n=1,nse4x)
!     ---------------------------
      elseif(vervarb(i).EQ.'2.1') then
!     ---------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmapvl2(i)
         write(iox,'(a8)') pmapvl2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapvl2(i),iutmznvl2(i),&
                     utmhemvl2(i),clat0,clon0,clat1,clat2,&
                     datumvl2(i),datenvl2(i),xyunitvl2(i),axtz4)
         if(pmapvl2(i).EQ.'UTM     ')  lutmvl2(i) =.TRUE.
         if(pmapvl2(i).EQ.'LCC     ')  llccvl2(i) =.TRUE.
         if(pmapvl2(i).EQ.'PS      ')  lpsvl2(i)  =.TRUE.
         if(pmapvl2(i).EQ.'EM      ')  lemvl2(i)  =.TRUE.
         if(pmapvl2(i).EQ.'LAZA    ')  llazavl2(i)=.TRUE.
         if(pmapvl2(i).EQ.'TTM     ')  lttmvl2(i) =.TRUE.
! ---    Map projection parameters
         if(LUTMVL2(i)) then
            read(iunit,'(i4,a4)') iutmznvl2(i),utmhemvl2(i)
            write(iox,'(i4,a4)') iutmznvl2(i),utmhemvl2(i)
            ncommout=ncommout+1
         elseif(LLCCVL2(i)) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPSVL2(i)) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEMVL2(i).or.LLAZAVL2(i).or.LTTMVL2(i)) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCVL2(i).or.LLAZAVL2(i).or.LTTMVL2(i)) then
            read(iunit,*) feastvl2(i),fnorthvl2(i)
            write(iox,*)  feastvl2(i),fnorthvl2(i)
            ncommout=ncommout+1
         else
            feastvl2(i)=0.0
            fnorthvl2(i)=0.0
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datumvl2(i),datenvl2(i)
         write(iox,'(a8,a12)')  datumvl2(i),datenvl2(i)
         ncommout=ncommout+1
! ---    Map units
         read(iunit,'(a4)') xyunitvl2(i)
         write(iox,'(a4)')  xyunitvl2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz4
         write(iox,'(a8)')  axtz4
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapvl2(i),iutmznvl2(i),&
                     utmhemvl2(i),clat0,clon0,clat1,clat2,&
                     datumvl2(i),datenvl2(i),xyunitvl2(i),axtz4)
! ---    Process Time zone
         read(axtz4(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM4 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz4
            stop 'Halted in RDHDEM4 -- see list file'
         endif
         xtz4(i)=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec4(i),&
                      ieyr,iedy,iehr,iesec4(i)
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDEM4'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec4(i),&
                           ieyr,iedy,iehr,iesec4(i)
         ncommout=ncommout+1
! ---    Number of sources and species
         read(iunit,*) nvl2file,nse4x
         write(iox,*) nvl2file,nse4x
         ncommout=ncommout+1
! ---    Species list
         read(iunit,*)(cslst4x(n),n=1,nse4x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem4x(n),n=1,nse4x)
!     -----
      else
!     -----
         write(*,*)'ERROR in VOLEMARB File: ',i
         write(*,*)'Bad Dataset Version: ',vervarb(i)
         write(*,*)'Expected 1.54, 1.54a, 1.6, 1.6a, 2.1'
         stop
!     -----
      endif
!     -----
! --- Species info to scratch file
      do n=1,nse4x
         write(iox,'(a16,f12.4)')cslst4x(n),xmwem4x(n)
      enddo
      ncommout=ncommout+nse4x
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec4(i))
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec4(i))
! --- Apply time zone adjustment to date/times
      if(xtz4(i).GE.-13 .AND. xtz4(i).LE.13) then
         t2btz4(i)=xtz4(i)-xbtz
         ishift=NINT(t2btz4(i))
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in VOLEMARB.DAT file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz4(i)
         stop 'Bad Time Zone in VOLEMARB.DAT file -- see list file'
      endif
! --- Hourly Data:  Move the time for the first period from the end
! ---               to the start of the first hour
      if(vervarb(i).EQ.'1.54a') then
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      endif
! --- Form date-hr variables for /VOL2/
      ibdathr4(i)=ibyr*100000+ibdy*100+ibhr
      iedathr4(i)=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1vl2(i))
      call XTRACTLL(io6,'LAT ',clat2,rnlat2vl2(i))
      call XTRACTLL(io6,'LAT ',clat0,rnlat0vl2(i))
      call XTRACTLL(io6,'LON ',clon0,relon0vl2(i))
! --- Set source index range for this file
      if(i.EQ.1) then
         ibsrc4(i)=1
         iesrc4(i)=nvl2file
      else
         ibsrc4(i)=iesrc4(i-1)+1
         iesrc4(i)=iesrc4(i-1)+nvl2file
      endif
! --- Set logical to indicate if the number of sources specified in
! --- the control file (Nvl2) have been found
      lnvl2=.FALSE.
      if(nvl2.EQ.iesrc4(i)) lnvl2=.TRUE.
! --- Process data that must be identical in all VOLEMARB.DAT files
      if(i.EQ.1) then
! ---    Pass into /VOL2/ names
         nse4=nse4x
!
         if(nse4.gt.mxspec)then
            write(io6,11)nse4,mxspec
11          format(/1x,'ERROR in SUBR. RDHDEM4 -- No. emitted species',&
            ' in VOLEMARB file is greater than current array',&
            ' dimension'/1x,&
            '     No. emitted species in file: ',i5/1x,&
            'Current Array dimension (MXSPEC): ',i5)
            write(*,*)
            stop 'Halted in RDHDEM4 -- see list file.'
         endif
      else
! ---    Check for consistency with first file
         if(nse4.NE.nse4x) then
            write(io6,12)nse4,nse4x
12          format(/1x,'ERROR in SUBR. RDHDEM4 -- No. emitted species',&
            ' must match in all VOLEMARB files',/1x,&
            '     No. emitted species expected/found: ',2i5)
            write(*,*)
            stop 'Halted in RDHDEM4 -- see list file.'
         endif
      endif
! --- Pass into /VOL2/ names or check for consistency
      if(i.EQ.1) then
         do is=1,nse4
            cslst4(is)=cslst4x(is)
            xmwem4(is)=xmwem4x(is)
         enddo
      else
         lmatch=.TRUE.
         do is=1,nse4
            if(cslst4(is).NE.cslst4x(is)) lmatch=.FALSE.
            if(xmwem4(is).NE.xmwem4x(is)) lmatch=.FALSE.
         enddo
         if(.not.LMATCH) then
            write(io6,15)
15          format(/1x,'ERROR in SUBR. RDHDEM4 -- Species names and',&
            ' molecular weights must match in all VOLEMARB files',/)
            write(io6,*)
            write(io6,*)'VOLEMARB.DAT File:    1'
            do is=1,nse4
               write(io6,*) cslst4(is),xmwem4(is)
            enddo
            write(io6,*)
            write(io6,*)'VOLEMARB.DAT File:',i
            do is=1,nse4
               write(io6,*) cslst4x(is),xmwem4x(is)
            enddo
            write(*,*)
            stop 'Halted in RDHDEM4 -- see list file.'
         endif
      endif
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,102) i
102      format(///1x,13('----------')//1x,'Header record data from ',&
         'the VOLEMARB volume source file',i5)
         write(io6,103)dataset,ibsrc4(i),iesrc4(i),nse4x,axtz4,&
               ibdathr4(i),ibsec4(i),iedathr4(i),iesec4(i),pmapvl2(i),&
               datumvl2(i)
103      format(/1x,'DATASET: ',a16/1x,'IBSRC4:  ',i9/&
         1x,'IESRC4:  ',i9/1x,'NSE4:    ',i6/1x,'AXTZ4:   ',a8/&
         1x,'IBDATHR4:',i9/1x,'IBSEC4:  ',i4/&
         1x,'IEDATHR4:',i9/1x,'IESEC4:  ',i4/&
         1x,'MAP4:    ',a8/1x,'DATUM:   ',a8)
         if(LUTMVL2(i)) write(io6,104) iutmznvl2(i),utmhemvl2(i)
         if(LLCCVL2(i)) write(io6,105) clat0,clon0,clat1,clat2,&
                                       feastvl2(i),fnorthvl2(i)
         if(LPSVL2(i)) write(io6,106) clat0,clon0,clat1
         if(LEMVL2(i)) write(io6,107) clat0,clon0
         if(LLAZAVL2(i).or.LTTMVL2(i)) write(io6,108) clat0,clon0,&
                                       feastvl2(i),fnorthvl2(i)
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
         write(io6,109)
109      format(4x,'SPECIES',6x,'MOL. WT.'/)
         do is=1,nse4
            write(io6,110)cslst4(is),xmwem4(is)
         enddo
110      format(1x,a12,3x,f7.3)
      endif
! --- Initialize "previous" period times for /VOL2/
      nstep4(i)=1
      ndhrqb4(1,i)=-000100100
      nsecqb4(1,i)=0
      ndhrqe4(1,i)=ibdathr4(i)
      nsecqe4(1,i)=ibsec4(i)
      return
      end
!----------------------------------------------------------------------
      subroutine RDHDEM5(iunit,lprt)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 070921           RDHDEM5
!                J. Scire
!
! --- PURPOSE:  Read the header records from the LNEMARB line source
!               emissions file (arbitrarily-varying emissions)
!
! --- UPDATE:
! --- V6.1-V6.22: 070921 (DGS): Condition GEO referencing string
!                               variables and add QA
!                               Remove calls to ALLCAP
! --- V5.7-V6.1 : 050915 (DGS): Dataset version 2.1 is added,
!                               including full coordinate and time
!                               documentation with begin/end times with
!                               seconds.
!                               Header information is passed to scratch
!                               file.
!                               LNEMARB.DAT file must be ASCII.
! --- V5.5-V5.7 : 030402 (DGS): CALUTILS version of XTRACTLL
!                               Add list file unit to YR4 call
! --- V5.4-V5.4 : 000602_6(DGS): Change name of LCC longitude from
!                                '-ELON-' to '-WLON-'
! --- V5.2-V5.4 : 000602 (DGS): add header record for LC map data
!                 000602 (DGS): add message to "stop"
! --- V5.0-V5.2 : 991104 (DGS): YYYY format used for year
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of LNEMARB file
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!     Common Block /LN2/ variables:
!            NLN2
!     Common Block /DATASET/ variables:
!            VERLARB
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!   Parameters:
!            IO6, MXSPEC
!
!
! --- OUTPUT:
!
!     Common Block /LN2/ variables:
!            NSE5,IBDATHR5,IBSEC5,IEDATHR5,IESEC5,XTZ5,T2BTZ5,
!            lutmln2,llccln2,lpsln2,lemln2,llazaln2,lttmln2,
!            iutmznln2,feastln2,fnorthln2,rnlat0ln2,relon0ln2,
!            rnlat1ln2,rnlat2ln2,
!            XMWEM5(mxspec),IXREM5(mxspec),
!            CSLST5(mxspec),CID5(mxlines),
!            pmapln2,utmhemln2,datumln2,datenln2,xyunitln2
!
! --- RDHDEM5 called by: SETUP
! --- RDHDEM5 calls:     YR4, XTRACTLL, INCR, TCLOCK, GEOFMT
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'ln2.puf'
      include 'dataset.puf'
      include 'datehr.puf'
      include 'qa.puf'
!
      LOGICAL lprt
! --- Local variables
      character*8 axtz5
      character*12 fname5,label5
      character*12 vrs5
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Initialize map projection logicals
      lutmln2=.FALSE.
      llccln2=.FALSE.
      lpsln2=.FALSE.
      lemln2=.FALSE.
      llazaln2=.FALSE.
      lttmln2=.FALSE.
! --- Initialize other variables
      datenln2='02-21-2003  '
      xyunitln2='KM  '
      ibsec5=0.0
      iesec5=0.0
      feastln2=0.0
      fnorthln2=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmapln2='XXX     '
      iutmznln2=1
      utmhemln2='N   '
      datumln2='XXX     '
      axtz5='UTC+0000'
! --- Only previous dataset version 5.4 is recognized
!     -----------------------
      if(verlarb.EQ.'1.54') then
!     -----------------------
! ---    Provide help and then stop (this format is not supported)
         dataset='LNEMARB.DAT    '
            read(iunit,*)fname5,nsrc5,nse5,iutmznln2,&
                         ibdat,ibhr,iedat,iehr,vrs5,label5
            dataver='1.54a           '
            datamod(1:42)='Augmented 5.4 format with Map Projection, '
            datamod(43:64)='DATUM, Time Zone      '
         write(io6,*)
         write(io6,*)
         write(io6,*)'ERROR in LNEMARB files'
         write(io6,*)
         write(io6,*)'Old LNEMARB.DAT format cannot be used because:'
         write(io6,*)'1.  Coordinate map projection is incomplete'
         write(io6,*)'2.  The DATUM for the coordinates is missing'
         write(io6,*)'3.  The time zone is missing'
         write(io6,*)
         write(io6,*)'ADD these records to the top of the file:'
         write(io6,*)
         write(io6,'(2a16,a64)') dataset,dataver,datamod
         if(iutmznln2.NE.0) then
            write(io6,'(a8)')'UTM     '
            write(io6,'(a8)')'  xxN   '
         else
            write(io6,'(a8)')'LCC     '
            write(io6,'(4a16)')'yy.yyyN         ','xx.xxxW         ',&
                               'yy.yyyN         ','yy.yyyN         '
            write(io6,*)zero,zero
         endif
         write(io6,'(a8)')'DATUM   '
         write(io6,'(a8)')'UTC-0500'
         write(io6,*)
         write(io6,*)'UPDATE these records to describe your file:'
         if(iutmznln2.NE.0) then
           write(io6,*)'- Replace the xxN with your UTM zone and',&
                       ' hemisphere (S for Southern Hemisphere)'
         else
           write(io6,*)'- Replace the 0.0 0.0 with your False Easting',&
                       ' and Northing if non-zero'   
         endif
         write(io6,*)'- Replace DATUM with your datum code (enter',&
                     ' left-justified in the 8-character field)'
         write(io6,*)'- Replace UTC-0500 with your time zone code'
         write(io6,*)'  (GMT=UTC+0000, EST=UTC-0500, CET=UTC+0100)'
         write(io6,*)
         write(*,*)
         write(*,*)'ERROR in LNEMARB files -- see list file'
         write(*,*)
         stop
!     ----------------------------
      elseif(verlarb.EQ.'1.54a') then
!     ----------------------------
! ---    Augmented headers
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         ncommout=ncommout+1
! ---    Map projection
         read(iunit,'(a8)') pmapln2
         write(iox,'(a8)') pmapln2
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapln2,iutmznln2,utmhemln2,&
                     clat0,clon0,clat1,clat2,datumln2,datenln2,&
                     xyunitln2,axtz5)
         if(pmapln2.EQ.'UTM     ')  lutmln2 =.TRUE.
         if(pmapln2.EQ.'LCC     ')  llccln2 =.TRUE.
         if(pmapln2.EQ.'PS      ')  lpsln2  =.TRUE.
         if(pmapln2.EQ.'EM      ')  lemln2  =.TRUE.
         if(pmapln2.EQ.'LAZA    ')  llazaln2=.TRUE.
         if(pmapln2.EQ.'TTM     ')  lttmln2 =.TRUE.
! ---    Map projection parameters
         if(LUTMLN2) then
            read(iunit,'(i4,a4)') iutmznln2,utmhemln2
            write(iox,'(i4,a4)') iutmznln2,utmhemln2
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCLN2) then
            read(iunit,*) feastln2,fnorthln2
            write(iox,*)  feastln2,fnorthln2
            ncommout=ncommout+1
         endif
! ---    Map DATUM
         read(iunit,'(a8)') datumln2
         write(iox,'(a8)')  datumln2
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz5
         write(iox,'(a8)')  axtz5
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapln2,iutmznln2,utmhemln2,&
                     clat0,clon0,clat1,clat2,datumln2,datenln2,&
                     xyunitln2,axtz5)
! ---    Process time zone
         read(axtz5(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM5 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz5
            stop 'Halted in RDHDEM5 -- see list file'
         endif
         xtz5=-FLOAT(ibtzhh)
! ---    Old Header Record(s)
         read(iunit,*)fname5,nsrc5,nse5,iutmznln2,&
                      ibdat,ibhr,iedat,iehr,&
                      vrs5,label5
         read(iunit,*) clat1,clat2,clat0,clon0
         ibyr=ibdat/1000
         ibdy=ibdat-ibyr*1000
         ieyr=iedat/1000
         iedy=iedat-ieyr*1000
         write(iox,'(a12,9i6,2a12)')fname5,nsrc5,nse5,&
                      iutmznln2,ibyr,ibdy,ibhr,ieyr,iedy,iehr,&
                      vrs5,label5
         write(iox,'(4a16)') clat1,clat2,clat0,clon0
         ncommout=ncommout+2
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) then
            write(*,*)
            stop 'Halted in RDHDEM5'
         endif
! ---    Species list
         read(iunit,*)(cslst5(n),n=1,nse5)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem5(n),n=1,nse5)
!     ---------------------------
      elseif(verlarb.EQ.'2.1') then
!     ---------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmapln2
         write(iox,'(a8)') pmapln2
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapln2,iutmznln2,utmhemln2,&
                     clat0,clon0,clat1,clat2,datumln2,datenln2,&
                     xyunitln2,axtz5)
         if(pmapln2.EQ.'UTM     ')  lutmln2 =.TRUE.
         if(pmapln2.EQ.'LCC     ')  llccln2 =.TRUE.
         if(pmapln2.EQ.'PS      ')  lpsln2  =.TRUE.
         if(pmapln2.EQ.'EM      ')  lemln2  =.TRUE.
         if(pmapln2.EQ.'LAZA    ')  llazaln2=.TRUE.
         if(pmapln2.EQ.'TTM     ')  lttmln2 =.TRUE.
! ---    Map projection parameters
         if(LUTMLN2) then
            read(iunit,'(i4,a4)') iutmznln2,utmhemln2
            write(iox,'(i4,a4)') iutmznln2,utmhemln2
            ncommout=ncommout+1
         elseif(LLCCLN2) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPSLN2) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEMLN2.or.LLAZALN2.or.LTTMLN2) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCLN2.or.LLAZALN2.or.LTTMLN2) then
            read(iunit,*) feastln2,fnorthln2
            write(iox,*)  feastln2,fnorthln2
            ncommout=ncommout+1
         else
            feastln2=0.0
            fnorthln2=0.0
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datumln2,datenln2
         write(iox,'(a8,a12)')  datumln2,datenln2
         ncommout=ncommout+1
! ---    Map units
         read(iunit,'(a4)') xyunitln2
         write(iox,'(a4)')  xyunitln2
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz5
         write(iox,'(a8)')  axtz5
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapln2,iutmznln2,utmhemln2,&
                     clat0,clon0,clat1,clat2,datumln2,datenln2,&
                     xyunitln2,axtz5)
! ---    Process time zone
         read(axtz5(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM5 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz5
            stop 'Halted in RDHDEM5 -- see list file'
         endif
         xtz5=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec5,&
                      ieyr,iedy,iehr,iesec5
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDEM5'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec5,&
                           ieyr,iedy,iehr,iesec5
         ncommout=ncommout+1
! ---    Number of sources and species
         read(iunit,*) nsrc5,nse5
         write(iox,*) nsrc5,nse5
         ncommout=ncommout+1
! ---    Species list
         read(iunit,*)(cslst5(n),n=1,nse5)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem5(n),n=1,nse5)
!     -----
      else
!     -----
         write(*,*)'ERROR in LNEMARB File '
         write(*,*)'Bad Dataset Version: ',verlarb
         write(*,*)'Expected 1.54, 1.54a, 2.1'
         stop
!     -----
      endif
!     -----
! --- Species info to scratch file
      do n=1,nse5
         write(iox,'(a16,f12.4)')cslst5(n),xmwem5(n)
      enddo
      ncommout=ncommout+nse5
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec5)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec5)
! --- Apply time zone adjustment to date/times
      if(xtz5.GE.-13 .AND. xtz5.LE.13) then
         t2btz5=xtz5-xbtz
         ishift=NINT(t2btz5)
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in LNEMARB.DAT file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz5
         stop 'Bad Time Zone in LNEMARB.DAT file -- see list file'
      endif
! --- Hourly Data:  Move the time for the first period from the end
! ---               to the start of the first hour
      if(verlarb.EQ.'1.54a') then
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      endif
! --- Form date-hr variables for /LN2/
      ibdathr5=ibyr*100000+ibdy*100+ibhr
      iedathr5=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1ln2)
      call XTRACTLL(io6,'LAT ',clat2,rnlat2ln2)
      call XTRACTLL(io6,'LAT ',clat0,rnlat0ln2)
      call XTRACTLL(io6,'LON ',clon0,relon0ln2)
! --- Check sources and species
      if(nln2.ne.nsrc5)then
         write(io6,11)nln2,nsrc5
11       format(/1x,'ERROR in SUBR. RDHDEM5 -- number of line sources',&
         ' does not match expected value'/&
         1x,'Expected NLN2: ',i6,/&
         1x,'Found   NSRC5: ',i6)
         write(*,*)
         stop 'Halted in RDHDEM5 -- see list file.'
      endif
!
      if(nse5.gt.mxspec)then
         write(io6,12)nse5,mxspec
12       format(/1x,'ERROR in SUBR. RDHDEM5 -- No. emitted species ',&
         ' in LNEMARB file is greater than current array dimension'/1x,&
         '     No. emitted species in file: ',i5/1x,&
         'Current Array dimension (MXSPEC): ',i5)
         write(*,*)
         stop 'Halted in RDHDEM5 -- see list file.'
      endif
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,102)
102      format(///1x,13('----------')//1x,'Header record data from ',&
         'the LNEMARB line source file',i5)
         write(io6,103)dataset,nse5,axtz5,&
               ibdathr5,ibsec5,iedathr5,iesec5,pmapln2,&
               datumln2
103      format(/1x,'DATASET: ',a16/1x,'NSE5:    ',i6/&
         1x,'AXTZ5:   ',a8/&
         1x,'IBDATHR5:',i9/1x,'IBSEC5:  ',i4/&
         1x,'IEDATHR5:',i9/1x,'IESEC5:  ',i4/&
         1x,'MAP5:    ',a8/1x,'DATUM:   ',a8)
         if(LUTMLN2) write(io6,104) iutmznln2,utmhemln2
         if(LLCCLN2) write(io6,105) clat0,clon0,clat1,clat2,&
                                    feastln2,fnorthln2
         if(LPSLN2) write(io6,106) clat0,clon0,clat1
         if(LEMLN2) write(io6,107) clat0,clon0
         if(LLAZALN2.or.LTTMLN2) write(io6,108) clat0,clon0,&
                                       feastln2,fnorthln2
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
         write(io6,109)
109      format(4x,'SPECIES',6x,'MOL. WT.'/)
         do is=1,nse5
            write(io6,110)cslst5(is),xmwem5(is)
         enddo
110      format(1x,a12,3x,f7.3)
      endif
! --- Initialize "previous" period times for /LN2/
      nstep5=1
      ndhrqb5(1)=-000100100
      nsecqb5(1)=0
      ndhrqe5(1)=ibdathr5
      nsecqe5(1)=ibsec5
!
      return
      end
!----------------------------------------------------------------------
      subroutine EMQA(ldb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918              EMQA
!                J. Scire
!
! --- PURPOSE:  Perform QA checks on data from emissions files --
!               check for consistency with the control file inputs
!
!               Compute cross-referencing arrays relating species
!               ordering in the emissions files to that of the main
!               concentration array
!
! --- UPDATE
! --- v7.2.1 - v7.3.0  150918  (CDA)
!                                : Add RDEMARB.DAT (roads)
! --- TNG-7.0.0 - TNG-7.1.0  141230  (DGS)
!                                : Add RDEMARB.DAT (roads)
! --- V6.1-TNG-7.0.0  140913  (DGS)
!                                : Add FLEMARB.DAT (flares)
! --- V5.4-V6.1     050915  (DGS): Add check on begin/end times with
!                                  seconds, and report time zone shifts
! --- V5.3-V5.4     000602  (DGS): replace VOLEM.DAT with VOLEMARB.DAT
! ---               000602  (DGS): add message to "stop"
! --- V5.2-V5.3     991222  (DGS): add LEMIT to QCHECK call
! --- V4.0-V5.0     971107  (DGS): add LNEMARB.DAT data
!
! --- INPUTS:
!
!           LDB - logical - Control variable for printing debug
!                           information
!     Common Block /FILNAM/ variables:
!            nptdat,nardat,nvoldat,nfldat
!            ptdat(mxemdat),ardat(mxemdat),voldat(mxemdat)
!            fldat(mxemdat)
!     Common Block /GEN/ variables:
!            NSE, CSPEC(mxspec), XMOL(mxspec), ISPLST(5,mxspec),
!            IBDATHR, IBSEC, IEDATHR, IESEC, METRUN
!     Common Block /GRID/ variables:
!            NX, NY, DGRID, XORIG, YORIG
!     Common Block /PT2/ variables:
!            NPT2, NSE2, CSLST2(mxspec), XMWEM2(mxspec)
!     Common Block /AR2/ variables:
!            NAR2, NSE3, CSLST3(mxspec), XMWEM3(mxspec),
!            IBDATHR3(mxemdat),IBSEC3(mxemdat),IEDATHR3(mxemdat),
!            IESEC3(mxemdat),T2BTZ3(mxemdat)
!     Common Block /VOL2/ variables:
!            NVL2, NSE4, CSLST4(mxspec), XMWEM4(mxspec)
!            IBDATHR4(mxemdat),IBSEC4(mxemdat),IEDATHR4(mxemdat),
!            IESEC4(mxemdat),T2BTZ4(mxemdat)
!     Common Block /LN2/ variables:
!            NLN2, NSE5, CSLST5(mxspec), XMWEM5(mxspec)
!            IBDATHR5,IBSEC5,IEDATHR5,IESEC5,T2BTZ5
!     Common Block /FL2/ variables:
!            NFL2, NSE6, CSLST6(mxspec), XMWEM6(mxspec)
!            IBDATHR6(mxemdat),IBSEC6(mxemdat),IEDATHR6(mxemdat),
!            IESEC6(mxemdat),T2BTZ6(mxemdat)
!     Parameters:
!            MXSPEC, IO6, MXEMDAT
!
! --- OUTPUT:
!
!     Common Block /PT2/ variables:
!            IXREM2(mxspec)
!     Common Block /AR2/ variables:
!            IXREM3(mxspec)
!     Common Block /VOL2/ variables:
!            IXREM4(mxspec)
!     Common Block /LN2/ variables:
!            IXREM5(mxspec)
!     Common Block /FL2/ variables:
!            IXREM6(mxspec)
!
! --- EMQA called by: SETUP
! --- EMQA calls:     QCHECK, TCHECK
!----------------------------------------------------------------------
! --- Modules
      use mroad2
      use mspray2
! --- Include parameters
      INCLUDE 'params.puf'
! --- Include common blocks
      INCLUDE 'ar2.puf'
      INCLUDE 'filnam.puf'
      INCLUDE 'fl2.puf'
      INCLUDE 'gen.puf'
      INCLUDE 'grid.puf'
      INCLUDE 'ln2.puf'
      INCLUDE 'pt2.puf'
      INCLUDE 'vol2.puf'
      character*12 ftype(8)
      logical ldb,lemit,lzone
      data lemit/.true./
      data ftype/'not used    ','PTEMARB.DAT ','BAEMARB.DAT ',&
                 'VOLEMARB.DAT','LNEMARB.DAT ','FLEMARB.DAT ',&
                 'RDEMARB.DAT ','SPEMARB.DAT '/
      data ione/1/
!
! --- Check inputs from each emissions file
      ierr=0
      itzn=0
!
! --- PTEMARB.DAT -- point sources with arbitrarily-varying emissions
      if(npt2.gt.0) then
         call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,&
                     lemit,ftype(2),nse2,cslst2,xmwem2,ierr,ixrem2)
         call TCHECK(mxemdat,io6,ftype(2),nptdat,ibdathr,ibsec,&
                     iedathr,iesec,ibdathr2,ibsec2,iedathr2,iesec2,&
                     t2btz2,metrun,ierr,itzn)
      endif
!
! --- BAEMARB.DAT -- area sources with arbitrarily-varying emissions
      if(nar2.gt.0) then
         call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,&
                     lemit,ftype(3),nse3,cslst3,xmwem3,ierr,ixrem3)
         call TCHECK(mxemdat,io6,ftype(3),nardat,ibdathr,ibsec,&
                     iedathr,iesec,ibdathr3,ibsec3,iedathr3,iesec3,&
                     t2btz3,metrun,ierr,itzn)
      endif
!
! --- VOLEMARB.DAT -- volume sources with arbitrarily-varying emissions
      if(nvl2.gt.0) then
         call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,&
                     lemit,ftype(4),nse4,cslst4,xmwem4,ierr,ixrem4)
         call TCHECK(mxemdat,io6,ftype(4),nvoldat,ibdathr,ibsec,&
                     iedathr,iesec,ibdathr4,ibsec4,iedathr4,iesec4,&
                     t2btz4,metrun,ierr,itzn)
      endif
!
! --- LNEMARB.DAT -- line sources with arbitrarily-varying emissions
      if(nln2.gt.0) then
         call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,&
                     lemit,ftype(5),nse5,cslst5,xmwem5,ierr,ixrem5)
         call TCHECK(ione,io6,ftype(5),ione,ibdathr,ibsec,&
                     iedathr,iesec,ibdathr5,ibsec5,iedathr5,iesec5,&
                     t2btz5,metrun,ierr,itzn)
      endif
!
! --- FLEMARB.DAT -- flare sources with arbitrarily-varying emissions
      if(nfl2.gt.0) then
         call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,&
                     lemit,ftype(6),nse6,cslst6,xmwem6,ierr,ixrem6)
         call TCHECK(mxemdat,io6,ftype(6),nfldat,ibdathr,ibsec,&
                     iedathr,iesec,ibdathr6,ibsec6,iedathr6,iesec6,&
                     t2btz6,metrun,ierr,itzn)
      endif
!
! --- RDEMARB.DAT -- road sources with arbitrarily-varying emissions
      if(nrd2.gt.0) then
         call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,&
                     lemit,ftype(7),nse7,cslst7,xmwem7,ierr,ixrem7)
         call TCHECK(nrddat,io6,ftype(7),nrddat,ibdathr,ibsec,&
                     iedathr,iesec,ibdathr7,ibsec7,iedathr7,iesec7,&
                     t2btz7,metrun,ierr,itzn)
      endif
! --- SPEMARB.DAT -- road sources with arbitrarily-varying emissions
      if(nsp2.gt.0) then
         call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,&
                     lemit,ftype(8),nse,cslst8,xmwem8,ierr,ixrem8)
         call TCHECK(nspdat,io6,ftype(8),nspdat,ibdathr,ibsec,&
                     iedathr,iesec,ibdathr8,ibsec8,iedathr8,iesec8,&
                     t2btz8,metrun,ierr,itzn)
      endif
! --- Write notice to screen if any time zone shifts are done
      if(itzn.EQ.1) then
         write(*,*)
         write(*,*)'NOTICE: Times are adjusted in emissions file(s)'
         write(*,*)'(See list file for details)'
         write(*,*)
      endif
!
      if(ldb)then
         if(npt2.gt.0)write(io6,*)  'IXREM2 = ',(ixrem2(n),n=1,nse2)
         if(nar2.gt.0)write(io6,*)  'IXREM3 = ',(ixrem3(n),n=1,nse3)
         if(nvl2.gt.0)write(io6,*)  'IXREM4 = ',(ixrem4(n),n=1,nse4)
         if(nln2.gt.0)write(io6,*)  'IXREM5 = ',(ixrem5(n),n=1,nse5)
         if(nfl2.gt.0)write(io6,*)  'IXREM6 = ',(ixrem6(n),n=1,nse6)
         if(nrd2.gt.0)write(io6,*)  'IXREM7 = ',(ixrem7(n),n=1,nse7)
      endif
!
! --- Terminate execution if any errors encountered
      if(ierr.eq.1) then
         write(*,*)
         stop 'Halted in EMQA -- see list file.'
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,lemit,&
       fnamen,nsen,cslstn,xmwemn,ierr,ixremn)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 991222            QCHECK
!                J. Scire, SRC
!                R. Yamartino, SRC
!
! --- PURPOSE:  Check no. species, species names against control
!               file inputs, molecular weights -- Set up cross-
!               referencing table
!
! --- UPDATE
! --- V5.2-V5.3   991222  (DGS): add LEMIT argument to check that all
!                                emitted species (and no more) are in
!                                the CSLSTN array
! --- V5.0-V5.0   990228e (DGS): recast IXREMN array as map to FULL
!                                advected species array, not to the
!                                emitted species array
! --- V5.0-V5.0   980430  (DGS): 'use' all variables (compiler warning)
! --- V4.0-V5.0   971107  (DGS): isplst()  dimensions updated from
!                                (3,mxspec) to (4,mxspec)
!                 971107  (DGS): add treatment for special case of NSE=0
!
! --- INPUTS:
!           MXSPEC - integer       - Maximum number of species
!              IO6 - integer       - Fortran unit number for error
!                                    messages
!            NSPEC - integer       - Number of species modeled
!              NSE - integer       - Number of emitted species (control
!                                    file input)
! ISPLST(5,mxspec) - integer array - Flags indicating which species are
!                                    (1,-)=modeled,
!                                    (2,-)=emitted, (3,-)=deposited
!                                    (4,-)=droplets, (5,-)=groups
!    CSPEC(mxspec) - char*12 array - List of chemical species
!     XMOL(mxspec) - real array    - Molecular weights of each advected
!                                    species
!            LEMIT - logical       - Check that all emitted species are
!                                    present?
!                                    T: check for all NSE species
!                                    F: just check that species is one
!                                       of the NSPEC modeled species
!           FNAMEN - char*12       - Emissions file name
!             IERR - integer       - Error flag (initialized to zero in
!                                    calling routine)
!             NSEN - integer       - Number of emitted species
!                                    (emissions file input)
!   CSLSTN(mxspec) - char*12 array - List of emitted species (from
!                                    emissions file)
!   XMWEMN(mxspec) - real array    - Molecular weights of emitted
!                                    species (from emissions file)
!
! --- OUTPUT:
!             IERR - integer       - Error flag (set to one if any errors
!                                    are found)
!   IXREMN(mxspec) - integer array - Cross-referencing array of "NSEN"
!                                    values relating species ordering
!                                    in the emissions file to the
!                                    ordering in the master species list
!
! --- QCHECK called by: EMQA, RDHDBC, RDHDBC2
! --- QCHECK calls:     none
!----------------------------------------------------------------------
      real xmol(mxspec),xmwemn(mxspec)
      integer isplst(5,mxspec),ixremn(mxspec)
      logical lemit
      character*12 cspec(mxspec),cslstn(mxspec),fnamen
      if(NSE.eq.0) then
! ---    SPECIAL CASE:  First time emissions are encountered in this
! ---    application, so loop over emitted species names and set
! ---    corresponding ISPLST(2,-) in master species list
         do j=1,nsen
            do i=1,nspec
               if(cslstn(j).eq.cspec(i)) then
                  isplst(2,i)=1
                  nse=nse+1
               endif
            enddo
         enddo
      endif
!
! --- Number of emitted species should match
      if(NSEN.ne.NSE .AND. LEMIT)then
         ierr=1
         write(io6,10)fnamen
10       format(/1x,'ERROR in subr. QCHECK -- Emissions data do not ',&
         'match control file input -- FILE: ',a12)
         write(io6,12)nsen,nse
12       format(5x,'No. emitted species from emissions file: ',i6/&
                5x,'No. emitted species from control file:   ',i6)
      endif
!
! --- set up cross-referencing array & check that all emitted species
!     are in the emissions file
! --- NOTE: Pollutants in the emission files can be in any order --
!           IXREMN contains a number from 1 to NSPEC (not NSE) relating
!           the data file species ordering to that of MODELED species
!           in the master concentration array
      match=0
      LL=0
! --- Loop over all modeled species
      do 35 i=1,nspec
      if(isplst(2,i).eq.1 .OR. .not.LEMIT)then
! ---    This is an emitted species if LEMIT=T
         LL=LL+1
!
! ---    Loop over species in CSLSTN array to search for this species
         do 22 j=1,nsen
         if(cslstn(j).eq.cspec(i))then
            ixremn(j)=i
            jsave=j
            match=match+1
            go to 25
         endif
22       continue
!
! ---    species not found in emissions file
         if(LEMIT) then
            ierr=1
            write(io6,10)fnamen
            write(io6,23)cspec(i),(cslstn(n),n=1,nsen)
23          format(/5x,'Emitted species not found in emission file'/&
                 5x,'Species: ',a12/&
                 5x,'Emissions file species list: ',7(a12,1x)/&
                 10(34x,7(a12,1x)/))
         endif
         go to 35
!
25       continue
! -----------------------------------------   DISABLED !
!c ---    Check that molecular weight in emissions file matches model value
!         absdif=abs(xmol(i)-xmwemn(jsave))
!         if(absdif.gt.0.1)then
!c92         ierr=1
!            write(io6,10)fnamen
!            write(io6,26)cspec(i),xmol(i),xmwemn(jsave)
!C9226       format(/5x,'Molecular weight specified in emissions file ',
!26          format(/5x,'*** WARNING ***  ',
!     1      'Molecular weight specified in emissions file ',
!     1      'does not match value in model'/5x,'Species: ',a12/
!     2      5x,'Molecular weight (model = *.RXP) = ',f10.2/
!     3      5x,'Molecular weight (emission file) = ',f10.2/)
!         endif
! -----------------------------------------   DISABLED !
      endif
35    continue
!
! --- NSEN matches should have been found
      if(match.NE.nsen) then
         ierr=1
         write(io6,10)fnamen
         write(io6,*)'Match on all species names NOT found'
         write(io6,*)'Cross-References found:'
         do k=1,nsen
            if(ixremn(k).EQ.0) then
               write(io6,'(a16)') cslstn(k)
            else
               write(io6,'(a16,4x,a16)') cslstn(k),cspec(ixremn(k))
            endif
         enddo
      endif
! --- LL should add up to NSE
      if(LL.ne.NSE .AND. LEMIT)then
         ierr=1
         write(io6,10)fnamen
         write(io6,*)'LL ne NSE -- LL = ',LL,' NSE = ',nse
      endif
! --- 'Use' variables (compiler warning)
      jsave=jsave
      xmol(1)=xmol(1)
      xmwemn(1)=xmwemn(1)
!
      return
      end
!----------------------------------------------------------------------
      subroutine TCHECK(mxemdat,io6,fnamen,nfiles,ibdathr,ibsec,&
       iedathr,iesec,ibdathrn,ibsecn,iedathrn,iesecn,t2btzn,metrun,&
       ierr,itzn)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915            TCHECK
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Check time periods against control file inputs,
!               report any time zone adjustments
!
! --- INPUTS:
!          MXEMDAT - integer       - Maximum number of emissions files
!              IO6 - integer       - Fortran unit number for list file
!           FNAMEN - char*12       - Emissions file type
!           NFILES - integer       - Number of emissions files
!   IBDATHR, IBSEC - integer       - Starting date/time in CALPUFF
!                                    (YYYYJJJHH, SSSS)
!   IEDATHR, IESEC - integer       - Ending date/time in CALPUFF
!                                    (YYYYJJJHH, SSSS)
!IBDATHRN(mxemdat) - integer array - Starting date/time in each emission
!  IBSECN(mxemdat)                   file (YYYYJJJHH, SSSS)
!IEDATHRN(mxemdat) - integer array - Ending date/time in each emission
!  IESECN(mxemdat)                   file (YYYYJJJHH, SSSS)
!  T2BTZN(mxemdat) - real array    - Time zone adjustment for each
!                                    emission file (hr)
!           METRUN - integer       - Run all met periods flag
!
! --- OUTPUT:
!             IERR - integer       - Error flag (set to 1 if any errors
!                                    are found)
!             ITZN - integer       - Time Zone flag (set to 1 if any 
!                                    adjustments to the CALPUFF base
!                                    time zone are needed)
!
! --- TCHECK called by: EMQA
! --- TCHECK calls:     TPERIOD
!----------------------------------------------------------------------
      real t2btzn(mxemdat)
      integer ibdathrn(mxemdat),ibsecn(mxemdat)
      integer iedathrn(mxemdat),iesecn(mxemdat)
      logical lzone
      character*12 fnamen
! --- Check data time period in each file, and identify time zone
! --- shifts to screen
      lzone=.FALSE.
      do i=1,nfiles
         ishift=NINT(t2btzn(i))
         if(ishift.NE.0) lzone=.TRUE.
         call TPERIOD(0,ibdathr,ibsec,ibdathrn(i),ibsecn(i),&
                      iedathrn(i),iesecn(i),ilocb)
         call TPERIOD(0,iedathr,iesec,ibdathrn(i),ibsecn(i),&
                      iedathrn(i),iesecn(i),iloce)
! ---    Do not include end-time test when running all met periods
         if(metrun.EQ.1) iloce=0
         if(ilocb.NE.0 .OR. iloce.NE.0) then
            ierr=1
            write(io6,*)
            write(io6,*)'ERROR in emissions file -- ',fnamen,i
            write(io6,*)'Emission period does not cover simulation',&
                        ' period'
            write(io6,*)'Simulation begins: ',ibdathr,ibsec
            if(metrun.EQ.0) then
               write(io6,*)'Simulation ends  : ',iedathr,iesec
            endif
            write(io6,*)'Emission begins  : ',ibdathrn(i),ibsecn(i)
            write(io6,*)'Emission ends    : ',iedathrn(i),iesecn(i)
            write(io6,*)
         endif
      enddo
! --- Report any time zone adjustments
      if(LZONE) then
         itzn=1
         write(io6,*)
         write(io6,*)'EMQA: Times are adjusted in emissions file'
         write(io6,*)'      to obtain the CALPUFF Time Zone '
         write(io6,*)'      Emissions File Type -- ',fnamen
         write(io6,*)'      Emissions File #     Time Shift(hr)'
         do i=1,nfiles
            write(io6,'(12x,i4,18x,i4)') i,NINT(t2btzn(i))
         enddo
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine RDTIEM2(iunit0,npt2,nptdat,i,mfpt2,ibsrc2,iesrc2,&
                         avrs2,mbdw,ldb,io6,iox,tiem2,zplat2,bht2,bwd2,&
                         bln2,xbadj2,ybadj2,cid2,ncommout)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918           RDTIEM2
!                J. Scire, SRC
!
! --- PURPOSE:  Read the time-invariant records of the PTEMARB point
!               source emissions file (arbitrarily-varying emissions)
!
! --- UPGRADE
! --- TNG-7.1.0 - V7.2.2  150918 (Jelena)
!                             : Modify number of sources check to allow
!                               runs with multiple PTEMARB.DAT files
! --- V6.42_x1.0 - TNG-7.1.0  141230 (DGS)
!                             : Rename loop index in debug output that
!                               conflicted with calling argument 'i'
! --- V5.75-V6.1: 050915 (DGS): Dataset version as character.
!                               Header information is passed to scratch
!                               file.
!                               Process 1 file per call.
! --- V5.7-V5.75: 050225 (DGS): Add building downwash data for
!                               elevated structures (platforms)
! --- V5.4-V5.7 : 030402 (DGS): add PRIME building downwash data
!                               signaled by argument MBDW=2
! --- V5.0-V5.4 : 000602 (DGS): Allow multiple PTEMARB files
!                 000602 (DGS): Add vertical momentum flux factor as
!                               TIEM2(7,i)
! --- V5.0-V5.0 : 980430 (DGS): 'Use' CID (compiler warning)
! --- V4.0-V5.0 : 971107 (DGS): PTEMARB.DAT either binary or ASCII
!                 971107 (DGS): TIEM2(6,i) now holds downwash flag
!                 971107 (DGS): Building hts & widths added for downwash
!                               older files are not supported
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 PTEMARB file
!             NPT2 - integer    - Number of PTEMARB point sources
!           NPTDAT - integer    - Number of PTEMARB files
!                I - integer    - Current PTEMARB file
!    MFPT2(nptdat) - integer    - Formatted file? (0:NO, 1:YES)
!   IBSRC2(nptdat) - integer    - Source ID for first source in each
!                                 PTEMARB file
!   IESRC2(nptdat) - integer    - Source ID for last source in each
!                                 PTEMARB file
!    AVRS2(nptdat) - char*16    - PTEMARB data-set version
!             MBDW - integer    - Building downwash mode
!                                 1 = HS/SS
!                                 2 = PRIME
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!              IOX - integer    - Fortran unit number for scratch file
!         NCOMMOUT - integer    - Currrent number of header comments
!
! --- OUTPUT:
!    TIEM2(8,npt2) - real array - Time-invariant PTEMARB source data
!     ZPLAT2(npt2) - real array - Platform height (m)
!    BHT2(36,npt2) - real array - Building heights every 10 deg from N
!    BWD2(36,npt2) - real array - Building widths every 10 deg from N
!    BLN2(36,npt2) - real array - Building lengths every 10 deg from N
!  XBADJ2(36,npt2) - real array - Alongwind distance from source to
!                                 upwind face of projected building,
!                                 every 10 deg from N
!  YBADJ2(36,npt2) - real array - Crosswind distance from source to
!                                 upwind face of projected building,
!                                 every 10 deg from N
!       CID2(npt2) - char*16    - PTEMARB source identifiers
!         NCOMMOUT - integer    - Updated number of header comments
!                                 in scratch file
!
! --- RDTIEM2 called by: SETUP
! --- RDTIEM2 calls:     none
!----------------------------------------------------------------------
!
      real tiem2(8,npt2),bht2(36,npt2),bwd2(36,npt2),bln2(36,npt2)
      real xbadj2(36,npt2),ybadj2(36,npt2)
      real zplat2(npt2)
      integer mfpt2(nptdat),ibsrc2(nptdat),iesrc2(nptdat)
      character*16 cid2(npt2)
      character*16 avrs2(nptdat)
      logical ldb, problem, lfmf
      data fmfeps/0.01/
! --- Initialize PROBLEM
      data problem/.FALSE./
!
! --- Process file "i"
         iunit=iunit0+i-1
!
! ---    Set structure for older and newer formats
         nwords=8
         lfmf=.TRUE.
         if(avrs2(i).EQ.'1.5a' .OR. avrs2(i).EQ.'1.6a') then
            nwords=7
            lfmf=.FALSE.
         endif
!
! ---    Loop over sources
         if(mfpt2(i).EQ.0) then
            do k=ibsrc2(i),iesrc2(i)
               ii=MIN(k,npt2)
               read(iunit)cid2(ii),(tiem2(n,ii),n=1,nwords)
               write(iox,'(a16,20f12.3)') cid2(ii),&
                                         (tiem2(n,ii),n=1,nwords)
               ncommout=ncommout+1
               if(NINT(tiem2(6,ii)).EQ.2) then
! ---             Platform height record
                  read(iunit)zplat2(ii)
                  write(iox,'(f12.3)') zplat2(ii)
                  ncommout=ncommout+1
               endif
               if(NINT(tiem2(6,ii)).GE.1) then
! ---             Building data
                  read(iunit)(bht2(n,ii),n=1,36)
                  read(iunit)(bwd2(n,ii),n=1,36)
                  write(iox,'(6f9.3)') (bht2(n,ii),n=1,36)
                  write(iox,'(6f9.3)') (bwd2(n,ii),n=1,36)
                  ncommout=ncommout+12
                  if(mbdw.EQ.2) then
                     read(iunit)(bln2(n,ii),n=1,36)
                     read(iunit)(xbadj2(n,ii),n=1,36)
                     read(iunit)(ybadj2(n,ii),n=1,36)
                     write(iox,'(6f9.3)') (bln2(n,ii),n=1,36)
                     write(iox,'(6f9.3)') (xbadj2(n,ii),n=1,36)
                     write(iox,'(6f9.3)') (ybadj2(n,ii),n=1,36)
                     ncommout=ncommout+18
                  endif
               endif
            enddo
         else
            do k=ibsrc2(i),iesrc2(i)
               ii=MIN(k,npt2)
               read(iunit,*)cid2(ii),(tiem2(n,ii),n=1,nwords)
               write(iox,'(a16,20f12.3)') cid2(ii),&
                                         (tiem2(n,ii),n=1,nwords)
               ncommout=ncommout+1
               if(NINT(tiem2(6,ii)).EQ.2) then
! ---             Platform height record
                  read(iunit,*)zplat2(ii)
                  write(iox,'(f12.3)') zplat2(ii)
                  ncommout=ncommout+1
               endif
               if(NINT(tiem2(6,ii)).GE.1) then
! ---             Building data
                  read(iunit,*)(bht2(n,ii),n=1,36)
                  read(iunit,*)(bwd2(n,ii),n=1,36)
                  write(iox,'(6f9.3)') (bht2(n,ii),n=1,36)
                  write(iox,'(6f9.3)') (bwd2(n,ii),n=1,36)
                  ncommout=ncommout+12
                  if(mbdw.EQ.2) then
                     read(iunit,*)(bln2(n,ii),n=1,36)
                     read(iunit,*)(xbadj2(n,ii),n=1,36)
                     read(iunit,*)(ybadj2(n,ii),n=1,36)
                     write(iox,'(6f9.3)') (bln2(n,ii),n=1,36)
                     write(iox,'(6f9.3)') (xbadj2(n,ii),n=1,36)
                     write(iox,'(6f9.3)') (ybadj2(n,ii),n=1,36)
                     ncommout=ncommout+18
                  endif
               endif
            enddo
         endif
!
! ---    Check building downwash configuration
         do k=ibsrc2(i),iesrc2(i)
            ii=MIN(k,npt2)
            if(NINT(tiem2(6,ii)).EQ.2 .AND. mbdw.EQ.2) then
               write(io6,*)
               write(io6,*)'FATAL Error in subr. RDTIEM2 -- PLATFORM',&
               ' downwash is not modeled with PRIME at this time'
               write(io6,*)'    Source # = ',ii
               problem=.TRUE.
            elseif(NINT(tiem2(6,ii)).EQ.2 .AND. ZPLAT2(ii).LT.0.) then
               write(io6,*)
               write(io6,*)'FATAL Error in subr. RDTIEM2 -- PLATFORM',&
               ' height cannot be negative'
               write(io6,*)'    Source #, ZPLAT2 = ',ii,zplat2(ii)
               problem=.TRUE.
            endif
         enddo
!
! ---    Process the vertical momentum factor
         if(LFMF) then
! ---       Value from file must be 0. to 1.0
            do k=ibsrc2(i),iesrc2(i)
               ii=MIN(k,npt2)
               adel0=ABS(tiem2(7,ii))
               adel1=ABS(tiem2(7,ii)-1.0)
               if(adel0.LT.fmfeps) then
                  tiem2(7,ii)=0.0
               elseif(adel1.LT.fmfeps) then
                  tiem2(7,ii)=1.0
               else
                  write(io6,*)
                  write(io6,*)'FATAL Error in subr. RDTIEM2 -- FMFAC ',&
                  'values for sources in PTEMARB must be either 0 or 1'
                  write(io6,*)'    Source #, FMFAC = ',ii,tiem2(7,ii)
                  problem=.TRUE.
               endif
            enddo
         else
! ---       Update older format structure that uses tiem2(7,ii)
            do k=ibsrc2(i),iesrc2(i)
               ii=MIN(k,npt2)
               tiem2(8,ii)=tiem2(7,ii)
! ---          Default vertical momentum factor to 1.0 (full)
               tiem2(7,ii)=1.0
            enddo
         endif
!
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-invariant PTEMARB data'
         do 150 k=1,npt2
            write(io6,*)'Source = ',k,'CID2 = ',cid2(k)
            write(io6,*)'Source = ',k,'TIEM2 = ',(tiem2(n,k),n=1,8)
            if(NINT(tiem2(6,k)).EQ.2) then
               write(io6,*)'Source = ',k,'ZPLAT2 = ',zplat2(k)
            endif
            if(NINT(tiem2(6,k)).GE.1) then
               write(io6,*)'--- BHT2 = ',(bht2(n,k),n=1,36)
               write(io6,*)'--- BWD2 = ',(bwd2(n,k),n=1,36)
               if(mbdw.EQ.2) then
                 write(io6,*)'--- BLN2 = ',(bln2(n,k),n=1,36)
                 write(io6,*)'--- XBADJ2 = ',(xbadj2(n,k),n=1,36)
                 write(io6,*)'--- YBADJ2 = ',(ybadj2(n,k),n=1,36)
               endif
            endif
150      continue
      endif
! --- Check number of sources
      if(iesrc2(nptdat).NE.npt2.and.i.eq.nptdat) then
         write(io6,*)
         write(io6,*)'FATAL Error in RDTIEM2:  Wrong number of sources'
         write(io6,*)'          Control file NPT2 = ',npt2
         write(io6,*)' PTEMARB.DAT Time-Invariant = ',iesrc2(nptdat)
         problem=.TRUE.
      endif
      if(PROBLEM) then
         write(*,*)
         stop 'Halted in RDTIEM2 -- see list file.'
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine RDEM2(iunit,mfpt2,nsrc2,nse2,cid2,ibsrc2,iesrc2,vers,&
                     tzhr,ldb,io6,ibegh,ibegs,iendh,iends,em2dat,ieof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915             RDEM2
!                J. Scire, SRC
!
! --- PURPOSE:  Read the time-varying records of the PTEMARB point
!               source emissions file (arbitrarily-varying emissions)
!
! --- UPGRADE
! --- V5.7-V6.1 : 050915 (DGS): Dataset version 2.1 is added, and other
!                               versions are retained.
!                               Begin/end times include seconds.
! --- V5.5-V5.7   030402 (DGS): Add list file unit to YR4 call
! --- V5.2-V5.4 : 000602 (DGS): Allow multiple PTEMARB files (CID used)
!                 000602 (DGS): Add initial sigmas
! --- V5.0-V5.2 : 991104 (DGS): YYYY format for year
! --- V5.0-V5.0 : 980430 (DGS): 'Use' CID (compiler warning)
! --- V4.0-V5.0 : 971107 (DGS): PTEMARB.DAT either binary or ASCII
!                 971107 (DGS): Remove volume flux from data records
!                               read
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of PTEMARB file
!            MFPT2 - integer    - Formatted file? (0:NO, 1:YES)
!            NSRC2 - integer    - Number of sources in the PTEMARB file
!             NSE2 - integer    - Number of emitted species
!      CID2(nsrc2) - char*16    - Source identifiers
!           IBSRC2 - integer    - Index for 1st source in current file
!           IESRC2 - integer    - Index for Last source in current file
!             VERS - char*16    - Data-set version for file
!             TZHR - real       - Hours to ADD to times in file to
!                                 convert to BASE TIME ZONE
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!
! --- OUTPUT:
!      IBEGH,IBEGS - integer    - Starting time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
!      IENDH,IENDS - integer    - Ending time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
! EM2DAT(NSE2+4,nsrc2) - real array - Time-varying PTEMARB source data
!             IEOF - integer    - End-of-file flag
!
! --- RDEM2 called by: INITPUF
! --- RDEM2 calls:     YR4, INCR, TCLOCK
!----------------------------------------------------------------------
!
      real em2dat(nse2+4,nsrc2)
      character*16 cid, cid2(nsrc2), vers
      logical ldb, lproblem
! --- Initialize EOF flag
      ieof=0
! --- Read starting, ending date and time
      if(vers.EQ.'1.5a' .OR. vers.EQ.'1.54a') then
         if(mfpt2.EQ.0) then
            read(iunit,end=999)ibdat,ibhr,iedat,iehr
         else
            read(iunit,*,end=999)ibdat,ibhr,iedat,iehr
         endif
         ibegs=0.0
         iends=0.0
         ibyr=ibdat/1000
         ibdy=ibdat-ibyr*1000
         ieyr=iedat/1000
         iedy=iedat-ieyr*1000
! ---    Shift start time to beginning of hour
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      elseif(vers.EQ.'1.6a' .OR. vers.EQ.'2.1') then
         if(mfpt2.EQ.0) then
            read(iunit,end=999)ibyr,ibdy,ibhr,ibegs,&
                               ieyr,iedy,iehr,iends
         else
            read(iunit,*,end=999)ibyr,ibdy,ibhr,ibegs,&
                                 ieyr,iedy,iehr,iends
         endif
      else
         write(io6,*)
         write(io6,*)'RDEM2: Invalid Dataset Version for PTEMARB.DAT'
         write(io6,*)'Expected 1.5a, 1.54a, 1.6a, 2.1'
         write(io6,*)'Found: ',vers
         write(*,*)
         stop 'Halted in RDEM2 -- see list file.'
      endif
! --- Enforce YYYY format for year
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDEM2'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibegs)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iends)
! --- Adjust times to BASE TIME ZONE
      ishift=NINT(tzhr)
      call INCR(io6,ibyr,ibdy,ibhr,ishift)
      call INCR(io6,ieyr,iedy,iehr,ishift)
! --- Form date-hour
      ibegh=ibyr*100000+ibdy*100+ibhr
      iendh=ieyr*100000+iedy*100+iehr
! --- Set structure for older and newer formats
      nwords=nse2+4
      if(vers.EQ.'1.5a' .OR. vers.EQ.'1.6a') nwords=nse2+2
! --- Loop over sources
      lproblem = .FALSE.
      if(mfpt2.EQ.0) then
         do i=ibsrc2,iesrc2
            read(iunit)cid,(em2dat(n,i),n=1,nwords)
            if(cid.NE.cid2(i)) lproblem=.TRUE.
         enddo
      else
         do i=ibsrc2,iesrc2
            read(iunit,*)cid,(em2dat(n,i),n=1,nwords)
            if(cid.NE.cid2(i)) lproblem=.TRUE.
         enddo
      endif
! --- Check for source problem
      if(lproblem) then
         write(io6,*)
         write(io6,*)'RDEM2: Problem with source names in PTEMARB.DAT'
         write(io6,*)'Names in data record do not match header record'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(*,*)
         stop 'Halted in RDEM2 -- see list file.'
      endif
! --- Add initial sigmas to older format (sigy and sigz are inserted
! --- into positions 3,4)
      if(vers.EQ.'1.5a' .OR. vers.EQ.'1.6a') then
         do i=ibsrc2,iesrc2
! ---       Move the emission rates out 2 places in array
            do j=nwords,3,-1
               em2dat(j+2,i)=em2dat(j,i)
            enddo
! ---       Set initial sigmas to zero
            em2dat(3,i)=0.0
            em2dat(4,i)=0.0
         enddo
      endif
      if(ldb)then
! ---    Report full em2dat array
         nwords=nse2+4
         write(io6,*)
         write(io6,*)'Time-varying PTEMARB data'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(io6,*)'Source Range  : ',ibsrc2,iesrc2
         do i=ibsrc2,iesrc2
            write(io6,*)'I = ',i,'EM2DAT = ',(em2dat(n,i),n=1,nwords)
         enddo
      endif
      return
! --- Reached end of BAEMARB.DAT file
999   ieof=1
      write(*,*)'RDEM2: Data in PTEMARB.DAT file end early'
      write(io6,*)'RDEM2: Data in PTEMARB.DAT file end early'
      return
      end
!----------------------------------------------------------------------
      subroutine RDTIEM3(iunit0,nar2,nardat,i,ibsrc3,iesrc3,iuem3,&
                         avrs3,ldb,io6,iox,cid3,baemunit,ncommout)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918           RDTIEM3
!                J. Scire
!
! --- PURPOSE:  Read the time-invariant records of the BAEMARB buoyant
!               area source data file (arbitrarily-varying emissions)
!
! --- UPGRADE
! --- V6.1 - V7.2.2  150918 (Jelena)
!                             : Modify number of sources check to allow
!                               runs with multiple BAEMARB.DAT files
! --- V6.1-V6.1 : 070921 (DGS): Initialize DUMMY (compiler checks)
! --- V5.4-V6.1 : 050915 (DGS): Dataset version as character.
!                               Header information is passed to scratch
!                               file.
!                               Process 1 file per call.
! --- V5.0-V5.4 : 000602 (DGS): Allow multiple BAEMARB files
!                 000602 (DGS): Add units choice
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 BAEMARB file
!             NAR2 - integer    - Number of BAEMARB sources
!           NARDAT - integer    - Number of BAEMARB files
!                I - integer    - Current BAEMARB file
!   IBSRC3(nardat) - integer    - Source ID for first source in each
!                                 BAEMARB file
!   IESRC3(nardat) - integer    - Source ID for last source in each
!                                 BAEMARB file
!    IUEM3(nardat) - integer    - Emission units for sources in each
!                                 BAEMARB file
!    AVRS3(nardat) - char*16    - BAEMARB data-set version
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!              IOX - integer    - Fortran unit number for scratch file
!         NCOMMOUT - integer    - Currrent number of header comments
!                                 in scratch file
!
! --- OUTPUT:
!       CID3(nar2) - char*16    - BAEMARB source identifiers
!   BAEMUNIT(nar2) - char*16    - Emissions units for each source
!         NCOMMOUT - integer    - Updated number of header comments
!                                 in scratch file
!
! --- RDTIEM3 called by: SETUP
! --- RDTIEM3 calls:     none
!----------------------------------------------------------------------
!
      integer ibsrc3(nardat),iesrc3(nardat),iuem3(nardat)
      character*16 cid3(nar2),baemunit(nar2),qaunits(2)
      character*16 avrs3(nardat)
      logical ldb, lunits
! --- Set expected units
      data qaunits/'g/s','g/m2/s'/
! --- Initialize dummy variable
      dummy=0.0
! --- Set units QA logical to false
      lunits=.FALSE.
! --- Process file "i"
         iunit=iunit0+i-1
!
! ---    Loop over sources
         do k=ibsrc3(i),iesrc3(i)
            ii=MIN(k,nar2)
            if(avrs3(i).EQ.'1.5a') then
               read(iunit,*) cid3(ii)
               write(iox,'(a16)') cid3(ii)
               ncommout=ncommout+1
               baemunit(ii)=qaunits(1)
            elseif(avrs3(i).EQ.'1.54a') then
               read(iunit,*) cid3(ii),baemunit(ii),dummy,dummy
               write(iox,'(2a16)') cid3(ii),baemunit(ii)
               ncommout=ncommout+1
               if(baemunit(ii).NE.qaunits(1) .AND.&
                  baemunit(ii).NE.qaunits(2)) lunits=.TRUE.
            elseif(avrs3(i).EQ.'1.6a') then
               read(iunit,*) cid3(ii)
               write(iox,'(a16)') cid3(ii)
               ncommout=ncommout+1
               if(iuem3(i).GE.1 .AND. iuem3(i).LE.2) then
                  baemunit(ii)=qaunits(iuem3(i))
               else
                  lunits=.TRUE.
                  baemunit(ii)='                '
               endif
            elseif(avrs3(i).EQ.'2.1') then
               read(iunit,*)cid3(ii),baemunit(ii),dummy,dummy
               write(iox,'(2a16)') cid3(ii),baemunit(ii)
               ncommout=ncommout+1
               if(baemunit(ii).NE.qaunits(1) .AND.&
                  baemunit(ii).NE.qaunits(2)) lunits=.TRUE.
            else
               write(io6,*)'FATAL Error in RDTIEM3:  Unknown Version'
               write(io6,*)'avrs3(i) = ',avrs3(i)
               stop 'Halted in RDTIEM3 -- see list file.'
            endif
         enddo
! --- Check units if provided
      if(lunits) then
         write(io6,*)'FATAL Error in RDTIEM3:  Wrong units specified'
         write(io6,*)'     Allowed units = ',qaunits
         write(io6,*)'   Units Specified = '
         do k=ibsrc3(i),iesrc3(i)
            ii=MIN(k,nar2)
            write(io6,*) cid3(ii),baemunit(ii)
         enddo
         write(*,*)
         stop 'Halted in RDTIEM3 -- see list file.'
      endif
! --- Perform final QA after LAST file is processed
      if(i.NE.nardat) return
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-invariant BAEMARB data'
         do 150 k=1,nar2
            write(io6,*)'Source = ',k,'CID3 = ',cid3(k),baemunit(k)
150      continue
      endif
! --- Check number of sources
      if(iesrc3(nardat).NE.nar2.and.i.eq.nardat) then
         write(io6,*)'FATAL Error in RDTIEM3:  Wrong number of sources'
         write(io6,*)'          Control file NAR2 = ',nar2
         write(io6,*)' BAEMARB.DAT Time-Invariant = ',iesrc3(nardat)
         write(*,*)
         stop 'Halted in RDTIEM3 -- see list file.'
      endif
! --- Use dummy (satisfy compiler warning)
      dummy=dummy
!
      return
      end
