!----------------------------------------------------------------------
!
      integer ibsrc4(nvoldat),iesrc4(nvoldat)
      character*16 cid4(nvl2),avrs4(nvl2)
      logical ldb
! --- Process file "i"
         iunit=iunit0+i-1
! ---    Loop over sources
         do k=ibsrc4(i),iesrc4(i)
            ii=MIN(k,nvl2)
            if(avrs4(i).EQ.'1.54a') then
               read(iunit,*) cid4(ii),dummy
               write(iox,'(a16,f10.0)') cid4(ii),dummy
               ncommout=ncommout+1
            elseif(avrs4(i).EQ.'1.6a') then
               read(iunit,*) cid4(ii)
               write(iox,'(a16)') cid4(ii)
               ncommout=ncommout+1
            elseif(avrs4(i).EQ.'2.1') then
               read(iunit,*)cid4(ii),dummy
               write(iox,'(a16,f10.0)') cid4(ii),dummy
               ncommout=ncommout+1
            endif
         enddo
! --- Perform final QA after LAST file is processed
      if(i.NE.nvoldat) return
      if(ldb)then
         write(io6,*)
         write(io6,*)
         write(io6,*)'Time-invariant VOLEMARB data (Source IDs)'
         write(io6,*)'-----------------------------------------'
         do k=1,nvl2
            write(io6,*)'Source = ',k,'CID4 = ',cid4(k)
         enddo
      endif
! --- Check number of sources
      if(iesrc4(nvoldat).NE.nvl2.and.i.eq.nvoldat) then
         write(io6,*)'FATAL Error in RDTIEM4:  Wrong number of sources'
         write(io6,*)'          Control file NVL2 = ',nvl2
         write(io6,*)'VOLEMARB.DAT Time-Invariant = ',iesrc4(nvoldat)
         write(*,*)
         stop 'Halted in RDTIEM4 -- see list file.'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine RDEM4(iunit,nsrc4,nse4,cid4,ibsrc4,iesrc4,vers,tzhr,&
                       ldb,io6,ibegh,ibegs,iendh,iends,em4dat,ieof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915             RDEM4
!                J. Scire, D. Strimaitis,  EarthTech
!
! --- PURPOSE:  Read the time-varying records of the VOLEMARB volume
!               source emissions file (arbitrarily-varying emissions)
!
! --- UPDATE
! --- V5.7-V6.1   050915 (DGS): Dataset version 2.1 is added, and other
!                               versions are retained.
!                               Begin/end times include seconds.
! --- V5.5-V5.7   030402 (DGS): Add list file unit to YR4 call
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of VOLEMARB file
!            NSRC4 - integer    - Total number of sources in VOLEMARB
!                                 files
!             NSE4 - integer    - Number of emitted species
!      CID4(nsrc4) - char*16    - Source identifiers
!           IBSRC4 - integer    - Index for 1st source in current file
!           IESRC4 - integer    - Index for Last source in current file
!             VERS - char*16    - Dataset version for file
!             TZHR - real       - Hours to ADD to times in file to
!                                 convert to BASE TIME ZONE
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!
! --- OUTPUT:
!      IBEGH,IBEGS - integer    - Starting time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
!      IENDH,IENDS - integer    - Ending time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
! EM4DAT(NSE4+6,nsrc4) - real array - Time-varying VOLEMARB source data
!             IEOF - integer    - End-of-file flag
!
! --- RDEM4 called by: INITPUF
! --- RDEM4 calls:     YR4, INCR, TCLOCK
!----------------------------------------------------------------------
!
      real em4dat(nse4+6,nsrc4)
      character*16 cid, cid4(nsrc4), vers
      logical ldb, lproblem
! --- Initialize EOF flag
      ieof=0
! --- Read starting, ending date and time
      if(vers.EQ.'1.54a') then
         read(iunit,*,end=999)ibdat,ibhr,iedat,iehr
         ibegs=0.0
         iends=0.0
         ibyr=ibdat/1000
         ibdy=ibdat-ibyr*1000
         ieyr=iedat/1000
         iedy=iedat-ieyr*1000
! ---    Shift start time to beginning of hour
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      elseif(vers.EQ.'1.6a' .OR. vers.EQ.'2.1') then
         read(iunit,*,end=999)ibyr,ibdy,ibhr,ibegs,&
                              ieyr,iedy,iehr,iends
      else
         write(io6,*)
         write(io6,*)'RDEM4: Invalid Dataset Version for VOLEMARB.DAT'
         write(io6,*)'Expected 1.54a, 1.6a, 2.1'
         write(io6,*)'Found: ',vers
         write(*,*)
         stop 'Halted in RDEM4 -- see list file.'
      endif
! --- Enforce YYYY format for year
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDEM4'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibegs)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iends)
! --- Adjust times to BASE TIME ZONE
      ishift=NINT(tzhr)
      call INCR(io6,ibyr,ibdy,ibhr,ishift)
      call INCR(io6,ieyr,iedy,iehr,ishift)
! --- Form date-hour
      ibegh=ibyr*100000+ibdy*100+ibhr
      iendh=ieyr*100000+iedy*100+iehr
! --- Loop over sources
      lproblem = .FALSE.
      nwords=nse4+6
      do i=ibsrc4,iesrc4
         read(iunit,*)cid,(em4dat(n,i),n=1,nwords)
         if(cid.NE.cid4(i)) lproblem=.TRUE.
      enddo
! --- Check for source problem
      if(lproblem) then
         write(io6,*)
         write(io6,*)'RDEM4: Problem with source names in VOLEMARB.DAT'
         write(io6,*)'Names in data record do not match header record'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(*,*)
         stop 'Halted in RDEM4 -- see list file.'
      endif
!
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-varying VOLEMARB data'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(io6,*)'Source Range  : ',ibsrc4,iesrc4
         do i=ibsrc4,iesrc4
            write(io6,*)'I = ',i,'EM4DAT = ',(em4dat(n,i),n=1,nwords)
         enddo
      endif
      return
! --- Reached end of VOLEMARB.DAT file
999   ieof=1
      write(*,*)'RDEM4: Data in VOLEMARB.DAT file end early'
      write(io6,*)'RDEM4: Data in VOLEMARB.DAT file end early'
      return
      end
!----------------------------------------------------------------------
      subroutine RDTIEM5(iunit,mxrise,nsrc5,ldb,io6,iox,cid,ncommout,&
                         mxnseg,nlrise)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915           RDTIEM5
!                J. Scire, D. Strimaitis    EARTH TECH
!
! --- PURPOSE:  Read the time-invariant records of the LNEMARB buoyant
!               line source data file (arbitrarily-varying emissions)
!
! --- UPDATE
! --- V5.4-V6.1 : 050915  (DGS): Header information is passed to scratch
!                                file.
! --- V5.3-V5.4 : 000602  (DGS): add message to "stop"
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of LNEMARB file
!           MXRISE - integer    - Max number of distances for trans rise
!            NSRC5 - integer    - Number of sources (individual lines)
!                                 in the LNEMARB file
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!              IOX - integer    - Fortran unit number for scratch file
!         NCOMMOUT - integer    - Currrent number of header comments
!                                 in scratch file
!
! --- OUTPUT:
!              CID - char array - line source names
!         NCOMMOUT - integer    - Updated number of header comments
!                                 in scratch file
!           MXNSEG - integer    - Max number of segments for one line
!           NLRISE - integer    - Number of distances for transitional
!                                 rise
!
! --- RDTIEM5 called by: SETUP
! --- RDTIEM5 calls:     none
!----------------------------------------------------------------------
!
      character*16 cid(nsrc5)
      logical ldb
!
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-invariant LNEMARB data'
      endif
!
! --- Set number of segments and number of transitional rise distances
      read(iunit,*)mxnseg,nlrise
      write(iox,'(2i10)') mxnseg,nlrise
      ncommout=ncommout+1
      if(LDB) write(io6,*)'MXNSEG, NLRISE = ',mxnseg,nlrise
!
! --- Check number of points used to tabulate buoyant rise
      if(nlrise.gt.mxrise)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDTIEM5 -- too many points ',&
         'for rise table -- ','NLRISE = ',nlrise,' MXRISE = ',mxrise
         write(*,*)
         stop 'Halted in RDTIEM5 -- see list file.'
      endif
!
! --- Loop over line sources
      read(iunit,*)cid
      write(iox,'(a16)') cid
      ncommout=ncommout+nsrc5
      if(LDB) then
         do i=1,nsrc5
            write(io6,*)'Source = ',i,'  CID = ',cid(i)
         enddo
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine RDEM5(iunit,ngrp5,nsrc5,nse5,cid5,vers,tzhr,&
                       ldb,io6,ibegh,ibegs,iendh,iends,lngrp,idline,&
                       em5dat,em5grp,ieof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915             RDEM5
!                J. Scire, D. Strimaitis   EARTH TECH
!
! --- PURPOSE:  Read the time-varying records of the LNEMARB buoyant
!               line source data file (arbitrarily-varying emissions)
!
! --- UPDATE
! --- V5.7-V6.1   050915 (DGS): Dataset version 2.1 is added, and other
!                               versions are retained.
!                               Begin/end times include seconds.
! --- V5.5-V5.7   030402  (DGS): Add list file unit to YR4 call
! --- V5.2-V5.4 : 000602  (DGS): YYYY format for year
! --- V5.0-V5.2 : 991104  (DGS): YYYY format for year
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of LNEMARB file
!            NGRP5 - integer    - Max number of groups in LNEMARB file
!            NSRC5 - integer    - Max number of lines in  LNEMARB file
!             NSE5 - integer    - Number of emitted species
!      CID5(nsrc5) - integer ar - Line source names
!             VERS - char*16    - Dataset version for file
!             TZHR - real       - Hours to ADD to times in file to
!                                 convert to BASE TIME ZONE
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!
! --- OUTPUT:
!      IBEGH,IBEGS - integer    - Starting time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
!      IENDH,IENDS - integer    - Ending time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
!            LNGRP - integer    - Number of groups of line sources
!                                 active this period
!    IDLINE(nsrc5) - integer ar - Line source ID for each line
!                                 active this period
!  EM5DAT(NSE5+6,nsrc5)
!                  - real array - Time-varying LNEMARB line data
!  EM5GRP(7,ngrp5) - real array - Time-varying LNEMARB group data
!
! --- RDEM5 called by: INITPUF
! --- RDEM5 calls:     YR4, INCR, TCLOCK
!----------------------------------------------------------------------
!
      real em5dat(nse5+6,nsrc5)
      real em5grp(7,ngrp5)
      character*16 cid,cid5(nsrc5), vers
      integer idline(nsrc5)
      logical ldb
! --- Initialize EOF flag
      ieof=0
! --- Read starting, ending date and time
      if(vers.EQ.'1.54a') then
         read(iunit,*,end=999)ibdat,ibhr,iedat,iehr
         ibegs=0.0
         iends=0.0
         ibyr=ibdat/1000
         ibdy=ibdat-ibyr*1000
         ieyr=iedat/1000
         iedy=iedat-ieyr*1000
! ---    Shift start time to beginning of hour
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      elseif(vers.EQ.'2.1') then
         read(iunit,*,end=999)ibyr,ibdy,ibhr,ibegs,&
                              ieyr,iedy,iehr,iends
      else
         write(io6,*)
         write(io6,*)'RDEM5: Invalid Dataset Version for LNEMARB.DAT'
         write(io6,*)'Expected 1.54a, 2.1'
         write(io6,*)'Found: ',vers
         write(*,*)
         stop 'Halted in RDEM5 -- see list file.'
      endif
! --- Enforce YYYY format for year
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDEM5'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibegs)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iends)
! --- Adjust times to BASE TIME ZONE
      ishift=NINT(tzhr)
      call INCR(io6,ibyr,ibdy,ibhr,ishift)
      call INCR(io6,ieyr,iedy,iehr,ishift)
! --- Form date-hour
      ibegh=ibyr*100000+ibdy*100+ibhr
      iendh=ieyr*100000+iedy*100+iehr
! --- Read number of groups of line sources
      read(iunit,*)lngrp
      if(lngrp.GT.ngrp5) then
         write(io6,10)lngrp,ngrp5
10       format(/1x,'ERROR in SUBR. RDEM5 -- No. line groups',&
         ' in LNEMARB file exceeds current array dimension'/1x,&
         '          No. line groups in file: ',i5/1x,&
         'Current Array dimension (MXLNGRP): ',i5)
         write(*,*)
         stop 'Halted in RDEM5 -- see list file.'
      endif
!
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-varying LNEMARB data'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
      endif
!
! --- Loop over groups
      nlines=0
      do ig=1,lngrp
! ---    Get group parameters
         read(iunit,*)(em5grp(n,ig),n=1,7)
         if(ldb)then
            write(io6,*)
            write(io6,*)'Group  = ',ig
            write(io6,*)'EM5GRP = ',(em5grp(n,ig),n=1,7)
            write(io6,*)
            write(io6,*)'Line,  EM5DAT = '
         endif
!
! ---    Test total number of lines so far
         nlprev=nlines
         nlines=nlines+em5grp(1,ig)
         if(nlines.GT.nsrc5) then
            write(io6,11)nlines,nsrc5
11          format(/1x,'ERROR in SUBR. RDEM5 -- Total no. lines',&
            ' in LNEMARB file exceeds total expected'/1x,&
            '    No. lines this period in file: ',i5/1x,&
            '            Total expected (NLN2): ',i5)
            write(*,*)
            stop 'Halted in RDEM5 -- see list file.'
         endif
!
! ---    Loop over lines in group
         nwords=nse5+6
         do i=nlprev+1,nlines
            read(iunit,*)cid,(em5dat(n,i),n=1,nwords)
! ---       Check for valid source name and assign ID
            idline(i)=-1
            do isrc=1,nsrc5
               if(cid.EQ.cid5(isrc)) idline(i)=isrc
            enddo
            if(idline(i).LT.0) then
               write(io6,12)cid
12             format(/1x,'ERROR in SUBR. RDEM5 -- Invalid line source',&
               ' name found in LNEMARB file'/1x,&
               '   Line Source Name Found in file: ',a16/1x,&
               '   List of valid names     (CID5): ')
               write(io6,*)cid5
               write(*,*)
               stop 'Halted in RDEM5 -- see list file.'
            endif
         enddo
!
         if(ldb)then
            do i=nlprev+1,nlines
               write(io6,*) i,idline(i),(em5dat(n,i),n=1,nwords)
            enddo
         endif
      enddo
!
      return
! --- Reached end of LNEMARB.DAT file
999   ieof=1
      write(*,*)'RDEM5: Data in LNEMARB.DAT file end early'
      write(io6,*)'RDEM5: Data in LNEMARB.DAT file end early'
      return
      end
