                   11*2, 3, 7*2, 3, 5*2, 35*0,&
! ---             (Note:  the 35*0 must include mxspec type 2 entries)
                   3*2, 4*1, 53*0,&
                   1, 59*0,&
                   1, 59*0,&
                   60*0,&
                   60*0/
      data ivtype2/&
                   11*1, 2*2, 47*0,&
                   60*0,&
                   1*1,59*0,&
                   2,1,2*2,4*1,2,4*1,2,1,1, 44*0,&
                   60*0,&
                   1,2*2,2*1,2*2,1,2,3*1,2,11*1,2*2,3*1,3*2,8*1,&
                   2,2,4*1,2,4*1,2,2*1,2,1,2,1,1, 1*0,&
                   4*2, 56*0,&
                   4*1,4, 55*0,&
                   5*1,4, 54*0,&
                   4,2,5, 57*0,&
! ---             (Note:  the 57*0 includes mxspec entries of type 1)
                   4*2, 56*0,&
                   1,4, 58*0,&
                   2*1,4, 57*0,&
                   4,2,5, 57*0,&
! ---             (Note:  the 57*0 includes mxspec entries of type 1)
                   2, 6*1, 5*2, 48*0,&
                   1,4, 58*0,&
                   4,2,5, 57*0,&
! ---             (Note:  the 57*0 includes mxspec entries of type 1)
                   5*2, 3*1, 52*0,&
                   1,4, 58*0,&
                   4,2,5, 57*0/
! ---             (Note:  the 57*0 includes mxspec entries of type 1)
      data ivtype3/&
                   2,2,       58*0,&
                   3*2,2*1,   55*0,&
                   4,1,       58*0,&
                   5,5,4,1,5, 55*0,&
                   4,2,       58*0,&
                   1,         59*0,&
                   2,2,       58*0,&
                   2,         59*0,&
                   4,4,1,     57*0,&
                   2*2,       58*0,&
                   4,         59*0,&
                   1,         59*0/
      data ivtype4/&
                   4, 59*0,&
                   4, 59*0/
!
! --- Set array of emission rate conversion factors to convert user
! --- units to g/s (or g/s/m**2)
      data qunits/1.0,       0.2777778, 0.1260,&
                  0.0287671, 1.0,       0.0166667,&
                  0.0317098, 1.0,       31.709792/
! --- Name those factors
      data qnam1/'    g/s     ','  kg/hr     ','  lb/hr     ',&
                 'tons/yr     ','  OUV/s     ','OUV/min     ',&
                 'met. tons/yr','  Bq/s      ','GBq/yr      '/
      data qnam2/'    g/s/m^2 ','  kg/hr/m^2 ','  lb/hr/m^2 ',&
                 'tons/yr/m^2 ','  OUV/s/m^2 ','OUV/min/m^2 ',&
                 'm.tons/y/m^2','  Bq/s/m^2  ','GBq/yr/m^2  '/
!
! --- Set "problem" marker to FALSE
      data problem/.FALSE./
! --- Initialize OLD number of VOLEM volume sources to ZERO
      igrdvl=0
! --- First record of control file may contain a file version number
      read(io5,'(a80)')line_ver
! --- Read title (First 3 lines of the control file)
      if(line_ver(1:11).NE.'CALPUFF.INP') then
         title(1)=line_ver
         read(io5,'(a80/a80)')(title(n),n=2,3)
         line_ver='CALPUFF.INP     1.0             '
      else
         read(io5,'(a80/a80/a80)')title
      endif
! --- Prepare control-file version
! --------------------------------
      cfver=line_ver(17:32)
! --- Keep just number part of version string (C*16)
      do n=1,16
         ia=IACHAR(cfver(n:n))
         SELECT CASE (ia)
            CASE (46)
! ---          Keep decimal point (ASCII #46)
            CASE (48:59)
! ---          Keep numbers 0-9 (ASCII #46 through #59)
            CASE DEFAULT
! ---          Substitute a blank (anything else)
               cfver(n:n)=' '
         END SELECT
      enddo
! --- Remove blanks
      call RM_BLANK(cfver)
! --- Assign dataset version as a real
      read(cfver,'(f)') cfdataset
! --- Interpret control-file version
      if(cfdataset.LT.2.99) then
! ---    Receptor group is 17 before dataset TNG-3.0
         igroup_rec=17
      elseif(cfdataset.LT.3.09) then
! ---    Receptor group is 19 in TNG-3.0
         igroup_rec=19
      elseif(cfdataset.LT.7.01) then
! ---    Receptor group is 19 in TNG-3.0
         igroup_rec=20
      else
! ---    Receptor group is 20 in 7.01
         igroup_rec=21
      endif
! --- Branch at TNG-3.1 / 7.0
      if(cfdataset.LT.3.09) then
! ---    Control file depends on implicit temperature bins for scaled
! ---    emissions so reset TKCAT defaults to these older bins, which
! ---    are every 5C from 0C to 50C
         do i=1,11
            tkcat(i)=273.16+5.0*(i-1)
         enddo
      endif
! --- Get NIMA date for datum transformation parameters
      call NIMADATE(daten)
!
! ----------------------------------------------
! --- Read file names from control file - IG # 0
! ----------------------------------------------
      call READFN
!
! --- Open the output LIST file
      open(io6,file=puflst,status='unknown')
!
! --- write page header
      call WRIVL(io6,mmodel,ver,level)
!
! --- write clock date/time of run
      write(io6,1402)rtime,rdate
1402  format(//2x,'Clock time: ',a8/&
               2x,'      Date: ',a10)
! --- Obtain COORDS version information
      call COORDSVER(io6,verdoc)
      write(io6,*)
      write(io6,*)
      write(io6,*)'Internal Coordinate Transformations by ',verdoc
      write(io6,*)
      write(io6,*)
! --- Report control file version
      write(io6,*)'Control File Type: ',TRIM(line_ver)
! --- Report run titles
      write(io6,1410)title
1410  format(//2x,'Run Title:'/&
       3(5x,a80/))
!
! --------------------------------------------
! --- General run control parameters -- IG # 1
! --------------------------------------------
!
! --- Initialize local date/time variables to zero (not in /GEN/)
      ibmin=0
      ieyr=0
      iemo=0
      iedy=0
      iehr=0
      iemin=0
! --- Initialize the temporary array for the time zone
      do j=1,8
         cabtz(j)(1:1)=' '
      enddo
!
      call readin(cvdic(1,1),ivleng(1,1),ivtype(1,1),io5,io6,lecho,&
       IBYR,IBMO,IBDY,IBHR,IBMIN,IBSEC,IEYR,IEMO,IEDY,IEHR,IEMIN,IESEC,&
       NSECDT,IRLG,XBTZ,NSPEC,NSE,ITEST,METFM,AVET,&
       PGTIME,METRUN,MRESTART,NRESPD,MPRFFM,CABTZ,IOUTU,IOVERS,&
       idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- Special QA on starting year of simulation
      call QAYR4(io6,ibyr,metrun,ierr)
      if(ierr.NE.0) then
         write(*,*)
         stop 'CORRECT THE CONTROL FILE'
      endif
!
! --- Set runtime information
      if(metrun.EQ.1) then
! ---    Run period is defined in met file
         ibyr=0
         ibmo=0
         ibdy=0
         ibhr=0
         ibsec=0
         irlg=0
      else
! ---    Start Date-Time
         call YR4(io6,ibyr,ierr)
         if(ierr.NE.0) stop 'Halted in READCF'
         call JULDAY(io6,ibyr,ibmo,ibdy,ibjul)
! ---    Convert minutes to seconds (0-3599)
         ibsec=ibsec+ibmin*60
         if(ibsec.GE.3600) then
            nhrinc=ibsec/3600
            ibsec=ibsec-nhrinc*3600
            call INCR(io6,ibyr,ibjul,ibhr,nhrinc)
            call GRDAY(io6,ibyr,ibjul,ibmo,ibdy)
         endif
! ---    End Date-Time
         if(irlg.EQ.0) then
! ---       Mod6 control file: true starting date/time and
! ---       end date/time are provided in the control file
            call YR4(io6,ieyr,ierr)
            if(ierr.NE.0) then
               stop 'Halted in READCF'
            endif
            call JULDAY(io6,ieyr,iemo,iedy,iejul)
! ---       Convert minutes to seconds (0-3599)
            iesec=iesec+iemin*60
            if(iesec.GE.3600) then
               nhrinc=iesec/3600
               iesec=iesec-nhrinc*3600
               call INCR(io6,ieyr,iejul,iehr,nhrinc)
               call GRDAY(io6,ieyr,iejul,iemo,iedy)
            endif
            call DELTT(ibyr,ibjul,ibhr,ieyr,iejul,iehr,nrunhr)
            nsecrun=nrunhr*3600.-ibsec+iesec
            irlg=nsecrun/nsecdt
         else
! ---       Mod5 control file: end-time of first hour and
! ---       run length (hours) provided in the control file
            rhrinc=(FLOAT(nsecdt)/3600.)*FLOAT(irlg)
            nhrinc=rhrinc-1
            nsecinc=0
            ieyr=ibyr
            iejul=ibjul
            iehr=ibhr
            iesec=ibsec
            call INCR(io6,ieyr,iejul,iehr,nhrinc)
            call INCRS(io6,ieyr,iejul,iehr,iesec,nsecinc)
            call GRDAY(io6,ieyr,iejul,iemo,iedy)
! ---       Move start-time to beginning of first hour
            nhrinc=-1
            call INCR(io6,ibyr,ibjul,ibhr,nhrinc)
            call GRDAY(io6,ibyr,ibjul,ibmo,ibdy)
         endif
! ---    Compute date/time integers for simulation period
         ibdathr=ibyr*100000+ibjul*100+ibhr
         iedathr=ieyr*100000+iejul*100+iehr
      endif
! --- Transfer the char*4 data into the char*8 variable (time zone)
      do j=1,8
         if(cabtz(j)(1:1).ne.' ')abtz(j:j)=cabtz(j)(1:1)
      enddo
! --- Prepare base time zone information from Input Group 1
      if(abtz(1:2).NE.'NA') then
! ---    Character version of base time zone is provided
         call UTCBASR(abtz,xbtz)
      elseif(xbtz.GT.-900) then
! ---    Real version of base time zone is provided
         call BASRUTC(xbtz,abtz)
      endif
! --- Both ABTZ and XBTZ are either valid or invalid at this point
!
! -------------------------------
! --- Technical Options -- IG # 2
! -------------------------------
! --- Initialize RTAULY to zero
      rtauly=0.0
      call readin(cvdic(1,2),ivleng(1,2),ivtype(1,2),io5,io6,lecho,&
       MGAUSS,MCTADJ,MCTSG,MSLUG,MTRANS,MCHEM,MWET,MDRY,MEVAP,MDISP,&
       MDISP2,MTURBVW,MROUGH,MTIP,MSHEAR,MSPLIT,MPARTL,MTINV,MPDF,&
       MSGTIBL,MBCON,MSOURCE,MFOG,MAQCHEM,MBDW,MREG,MTILT,MCTURB,&
       RTAULY,MTAUADV,MPARTLBA,MRISE,MLWC,MTIP_FL,MRISE_FL,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum)
! --- Define IFOG here from MFOG selection
      if(mfog.GT.0) ifog=1
! --- Interpret RTAULY input (real)
      mtauly=NINT(rtauly)
      if(mtauly.GT.10) then
! ---    User has provided a specific value for the Lagrangian time
! ---    scale so update the value in /CSIGMA/ for Draxler Fy
         tyidr=1./(1.62*rtauly)
      endif
!
! --------------------------
! --- Species list -- IG # 3
! --------------------------
!
! --- Subgroup 3(a) - Species names
!
      do i=1,nspec
         do j=1,12
            ctemp12(j)(1:1)=' '
         enddo
         call readin(cvdic4(1,1),ivleng4(1,1),ivtype4(1,1),io5,io6,&
         lecho,&
         CTEMP12,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 species name into CHAR*12 variable
         cspec(i)='            '
         do j=1,12
            cspec(i)(j:j)=ctemp12(j)(1:1)
         enddo
      enddo
!
! --- transfer species names from master species list into variable
! --- dictionary for use in subsequent input groups
      do i=1,mxspec
! ---    Group 3
         cvdic(i,3)=cspec(i)
         ivleng(i,3)=4
         ivtype(i,3)=2
!
! ---    Group 7
         cvdic(i,9)=cspec(i)
         ivleng(i,9)=5
         ivtype(i,9)=1
!
! ---    Group 8
         cvdic(i,10)=cspec(i)
         ivleng(i,10)=2
         ivtype(i,10)=1
!
! ---    Group 10
         cvdic(i,12)=cspec(i)
         ivleng(i,12)=2
         ivtype(i,12)=1
!
! ---    Group 11b
! ---    NOTE: ipn variable needs to be adjusted if any changes in
! ---    the variables list of Input Group 11a occurs
         ipn=i+16
         cvdic(ipn,14)=cspec(i)
         ivleng(ipn,14)=2
         ivtype(ipn,14)=1
!
! ---    Group 11c
! ---    NOTE: ipn variable needs to be adjusted if any changes in
! ---    the variables list of Input Group 11a occurs
         ipn=i+0
         cvdic(ipn,15)=cspec(i)
         ivleng(ipn,15)=1
         ivtype(ipn,15)=1
!
! ---    Group 13d
! ---    NOTE: ipn variable needs to be adjusted if any changes in
! ---    the variables list of Input Group 13d occurs (before vqtemp)
         ipn=i+3
         cvdic(ipn,20)=cspec(i)
         ivleng(ipn,20)=96
         ivtype(ipn,20)=1
!
! ---    Group 14d
! ---    NOTE: ipn variable needs to be adjusted if any changes in
! ---    the variables list of Input Group 14d occurs (before vqtemp)
         ipn=i+3
         cvdic(ipn,24)=cspec(i)
         ivleng(ipn,24)=96
         ivtype(ipn,24)=1
!
! ---    Group 15c
! ---    NOTE: ipn variable needs to be adjusted if any changes in
! ---    the variables list of Input Group 15c occurs (before vqtemp)
         ipn=i+3
         cvdic(ipn,27)=cspec(i)
         ivleng(ipn,27)=96
         ivtype(ipn,27)=1
!
! ---    Group 16c
! ---    NOTE: ipn variable needs to be adjusted if any changes in
! ---    the variables list of Input Group 16c occurs (before vqtemp)
         ipn=i+3
         cvdic(ipn,30)=cspec(i)
         ivleng(ipn,30)=96
         ivtype(ipn,30)=1
      enddo
!
! --- Species list & options
! ---------------------------
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,3),ivleng(1,3),ivtype(1,3),io5,io6,lecho,&
       ISPLST(1,1),ISPLST(1,2),ISPLST(1,3),ISPLST(1,4),ISPLST(1,5),&
       ISPLST(1,6),ISPLST(1,7),ISPLST(1,8),ISPLST(1,9),ISPLST(1,10),&
       ISPLST(1,11),ISPLST(1,12),ISPLST(1,13),ISPLST(1,14),ISPLST(1,15),&
       ISPLST(1,16),ISPLST(1,17),ISPLST(1,18),ISPLST(1,19),ISPLST(1,20),&
       ISPLST(1,21),ISPLST(1,22),ISPLST(1,23),ISPLST(1,24),ISPLST(1,25),&
       ISPLST(1,26),ISPLST(1,27),ISPLST(1,28),ISPLST(1,29),ISPLST(1,30),&
       ISPLST(1,31),ISPLST(1,32),ISPLST(1,33),ISPLST(1,34),ISPLST(1,35),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
!      call readin(cvdic(1,3),ivleng(1,3),ivtype(1,3),io5,io6,lecho,
! *** 1 ISPLST(1,1),ISPLST(1,2),ISPLST(1,3),ISPLST(1,4),ISPLST(1,5),
! *** 2 ISPLST(1,6),ISPLST(1,7),ISPLST(1,8),ISPLST(1,9),ISPLST(1,10),
! *** 3 ISPLST(1,11),ISPLST(1,12),ISPLST(1,13),ISPLST(1,14),ISPLST(1,15),
! *** 4 ISPLST(1,16),ISPLST(1,17),ISPLST(1,18),ISPLST(1,19),ISPLST(1,20),
! *** 5 ISPLST(1,21),ISPLST(1,22),ISPLST(1,23),ISPLST(1,24),
! *** 6 idum,idum,idum,idum,idum,idum,idum,idum,
! *** 7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! *** 8 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! *** 9 idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=5
! *** call readin(cvdic(1,3),ivleng(1,3),ivtype(1,3),io5,io6,lecho,
! ***1 ISPLST(1,1),ISPLST(1,2),ISPLST(1,3),ISPLST(1,4),ISPLST(1,5),
! ***2 idum,idum,idum,idum,idum,
! ***3 idum,idum,idum,idum,idum,
! ***4 idum,idum,idum,idum,idum,
! ***5 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***8 idum,idum,idum,idum)
!
      if(cfdataset.LT.7.01) then     
! --- Transfer dry deposition flags and group flags into common block
! --- arrays; also set number of group names expected in next subgroup
       ngrupin=0
       do i=1,nspec
         idryflg(i)=isplst(3,i)
         ispgrp(i)=isplst(4,i)
         if(ispgrp(i).GT.ngrupin) ngrupin=ispgrp(i)
       enddo
      else
! --- Transfer dry deposition flags droplet and group flags into common block
! --- arrays; also set number of group names expected in next subgroup
       ngrupin=0
       do i=1,nspec
         idryflg(i)=isplst(3,i)
         idropflg(i)=isplst(4,i)
         ispgrp(i)=isplst(5,i)
         if(ispgrp(i).GT.ngrupin) ngrupin=ispgrp(i)
       enddo
      endif
!
! --- Subgroup 3(b) - Group names (output species list)
!
! --- First, fill group names provided for species that are in a group
      do i=1,ngrupin
         do j=1,12
            ctemp12(j)(1:1)=' '
         enddo
!
         call readin(cvdic4(1,2),ivleng4(1,2),ivtype4(1,2),io5,io6,&
         lecho,&
         CTEMP12,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 species name into CHAR*12 variable
         cgrup(i)='            '
         do j=1,12
            cgrup(i)(j:j)=ctemp12(j)(1:1)
         enddo
      enddo
!
! --- Now, fill group names for species that are not in a group
      ngrup=ngrupin
      do i=1,nspec
         if(ispgrp(i).EQ.0) then
            ngrup=ngrup+1
            cgrup(ngrup)=cspec(i)
            ispgrp(i)=ngrup
         endif
      enddo
!
! --------------------------------------------------------
! --- Map Projection and Grid control parameters -- IG # 4
! --------------------------------------------------------
! --- Initialize the temporary arrays for the character lat/lon fields
      do i=1,4
         do j=1,16
            clatlon(j,i)(1:1)=' '
         enddo
      enddo
      do j=1,16
         clat0(j:j)=' '
         clon0(j:j)=' '
         clat1(j:j)=' '
         clat2(j:j)=' '
      enddo
! --- Initialize the temporary array for the Datum-Region name and 
! --- map projection
      do j=1,8
         cpmap(j)(1:1)=' '
         cdatum(j)(1:1)=' '
      enddo
! --- Retain XLAT,XLONG,XTZ in dictionary to 'read' older control files
! --- (these variables are no longer used)
      call readin(cvdic(1,4),ivleng(1,4),ivtype(1,4),io5,io6,lecho,&
       CPMAP,CDATUM,FEAST,FNORTH,IUTMZN,UTMHEM,&
       CLATLON(1,1),CLATLON(1,2),CLATLON(1,3),CLATLON(1,4),&
       NX,NY,NZ,ZFACE,DGRIDKM,XORIGKM,YORIGKM,XLAT,XLONG,XTZ,IBCOMP,&
       JBCOMP,IECOMP,JECOMP,LSAMP,IBSAMP,JBSAMP,IESAMP,JESAMP,MESHDN,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum)
! --- Pad the char*4 UTM Hemisphere
      utmhem(2:4)='   '
! --- Transfer the char*4 data into the char*16 variables
      do j=1,16
         if(clatlon(j,1)(1:1).ne.' ')clat0(j:j)=clatlon(j,1)(1:1)
         if(clatlon(j,2)(1:1).ne.' ')clon0(j:j)=clatlon(j,2)(1:1)
         if(clatlon(j,3)(1:1).ne.' ')clat1(j:j)=clatlon(j,3)(1:1)
         if(clatlon(j,4)(1:1).ne.' ')clat2(j:j)=clatlon(j,4)(1:1)
      enddo
! --- Transfer the char*4 data into the char*8 variables
      if(cpmap(1)(1:1).ne.' ') then
         do j=1,8
            pmap(j:j)=cpmap(j)(1:1)
         enddo
      endif
      if(cdatum(1)(1:1).ne.' ') then
         do j=1,8
            datum(j:j)=cdatum(j)(1:1)
         enddo
      endif
! --- Translate character lat/lon to real NLat/ELon
      if(clat0(1:1).NE.' ') call XTRACTLL(io6,'LAT ',clat0,rnlat0)
      if(clon0(1:1).NE.' ') call XTRACTLL(io6,'LON ',clon0,relon0)
      if(clat1(1:1).NE.' ') call XTRACTLL(io6,'LAT ',clat1,xlat1)
      if(clat2(1:1).NE.' ') call XTRACTLL(io6,'LAT ',clat2,xlat2)
! --- Set logicals for map projection PMAP
      if(pmap.EQ.'UTM     ')  lutm =.TRUE.
      if(pmap.EQ.'LCC     ')  llcc =.TRUE.
      if(pmap.EQ.'PS      ')  lps  =.TRUE.
      if(pmap.EQ.'EM      ')  lem  =.TRUE.
      if(pmap.EQ.'LAZA    ')  llaza=.TRUE.
      if(pmap.EQ.'TTM     ')  lttm =.TRUE.
! --- Adjust projection information if needed
      if(LEM) then
! ---    Equatorial Mercator projection matches at 0.0N, 
! ---    and places the northing origin at 0.0N
         rnlat0=0.0
         xlat1=0.0
         xlat2=0.0
      endif
! --- Assign internal reference variables
      rlat0=rnlat0
      rlon0=-relon0
!
! --- convert horizontal coordinate variables from km to m
      dgrid = 1000. * dgridkm
      xorig = 1000. * xorigkm
      yorig = 1000. * yorigkm
! --- Set reciprocal of dgrid (common GRID)
      dgridi = 1. / dgrid
!
! --- Pass XTZ into XBTZ, ABTZ if XBTZ is not provided (needed for old
! --- control file format)
      if(xbtz.LT.-900) then
         xbtz=xtz
         call BASRUTC(xbtz,abtz)
      endif
!
! ----------------------------
! --- Output options -- IG # 5
! ----------------------------
!
! --- NOTE: ip25 variable needs to be adjusted if any changes in
! --- the variables list of Input Group 5 occurs (before ioutop)
!
! --- transfer group names from master species-group list into variable
! --- dictionary (master names were provided in Group 3)
      do i=1,mxgrup
         ip25=i+25
         cvdic(ip25,5)=cgrup(i)
         ivleng(ip25,5)=7
         ivtype(ip25,5)=2
      enddo
!
! --- The following call is set for MXGRUP=35 (check MXSPEC)
      call readin(cvdic(1,5),ivleng(1,5),ivtype(1,5),io5,io6,lecho,&
       ICON,IDRY,IWET,IVIS,ICPRT,IDPRT,IWPRT,ICFRQ,IDFRQ,IWFRQ,IMESG,&
       LDEBUG,IPFDEB,NPFDEB,NN1,NN2,IPRTU,IMFLX,IMBAL,LCOMPRS,IQAPLOT,&
       IT2D,IRHO,INRISE,IPFTRAK,&
       IOUTOP(1,1),IOUTOP(1,2),IOUTOP(1,3),IOUTOP(1,4),IOUTOP(1,5),&
       IOUTOP(1,6),IOUTOP(1,7),IOUTOP(1,8),IOUTOP(1,9),IOUTOP(1,10),&
       IOUTOP(1,11),IOUTOP(1,12),IOUTOP(1,13),IOUTOP(1,14),IOUTOP(1,15),&
       IOUTOP(1,16),IOUTOP(1,17),IOUTOP(1,18),IOUTOP(1,19),IOUTOP(1,20),&
       IOUTOP(1,21),IOUTOP(1,22),IOUTOP(1,23),IOUTOP(1,24),IOUTOP(1,25),&
       IOUTOP(1,26),IOUTOP(1,27),IOUTOP(1,28),IOUTOP(1,29),IOUTOP(1,30),&
       IOUTOP(1,31),IOUTOP(1,32),IOUTOP(1,33),IOUTOP(1,34),IOUTOP(1,35))
!
! --- The following call is set for MXGRUP=24 (check MXSPEC)
! *** call readin(cvdic(1,5),ivleng(1,5),ivtype(1,5),io5,io6,lecho,
! ***1 ICON,IDRY,IWET,IVIS,ICPRT,IDPRT,IWPRT,ICFRQ,IDFRQ,IWFRQ,IMESG,
! ***2 LDEBUG,IPFDEB,NPFDEB,NN1,NN2,IPRTU,IMFLX,IMBAL,LCOMPRS,IQAPLOT,
! ***3 IT2D,IRHO,INRISE,IPFTRAK,
! ***4 IOUTOP(1,1),IOUTOP(1,2),IOUTOP(1,3),IOUTOP(1,4),IOUTOP(1,5),
! ***5 IOUTOP(1,6),IOUTOP(1,7),IOUTOP(1,8),IOUTOP(1,9),IOUTOP(1,10),
! ***6 IOUTOP(1,11),IOUTOP(1,12),IOUTOP(1,13),IOUTOP(1,14),IOUTOP(1,15),
! ***7 IOUTOP(1,16),IOUTOP(1,17),IOUTOP(1,18),IOUTOP(1,19),IOUTOP(1,20),
! ***8 IOUTOP(1,21),IOUTOP(1,22),IOUTOP(1,23),IOUTOP(1,24),
! ***9 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- The following call is set for MXGRUP=5 (check MXSPEC)
! *** call readin(cvdic(1,5),ivleng(1,5),ivtype(1,5),io5,io6,lecho,
! ***1 ICON,IDRY,IWET,IVIS,ICPRT,IDPRT,IWPRT,ICFRQ,IDFRQ,IWFRQ,IMESG,
! ***2 LDEBUG,IPFDEB,NPFDEB,NN1,NN2,IPRTU,IMFLX,IMBAL,LCOMPRS,IQAPLOT,
! ***3 IT2D,IRHO,INRISE,IPFTRAK,
! ***4 IOUTOP(1,1),IOUTOP(1,2),IOUTOP(1,3),IOUTOP(1,4),IOUTOP(1,5),
! ***5 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum)
! --- Turn the print frequency inputs from averaging periods to
! --- timesteps by multiplying by the number of timesteps in an
! --- averaging period
      icfrq=iavg*icfrq
      idfrq=iavg*idfrq
      iwfrq=iavg*iwfrq
!
! -------------------------------------------------
! --- Subgrid scale complex terrain inputs -- IG #6
! -------------------------------------------------
!
! --- Subgroup (6a)
      call readin(cvdic(1,6),ivleng(1,6),ivtype(1,6),io5,io6,lecho,&
       NHILL,NCTREC,MHILL,XHILL2M,ZHILL2M,XCTDMKM,YCTDMKM,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum)
!
! --- Compute origin of CTDM coordinate system relative to the SW
! --- corner of cell (1,1) in the CALPUFF MET grid (meters)
      if(mhill.EQ.1) then
         xmetct0m=1000.*(xctdmkm-xorigkm)
         ymetct0m=1000.*(yctdmkm-yorigkm)
      else
         xmetct0m=0.0
         ymetct0m=0.0
      endif
!
! --- Perform certain CTSG input checks before additional data are read
      if(nctrec.gt.mxrect)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many complex ',&
         'terrain receptors -- ','NCTREC = ',nctrec,' MXRECT = ',&
         mxrect
         problem=.TRUE.
      endif
      if(nhill.gt.mxhill)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many hills -- ',&
         'NHILL = ',nhill,' MXHILL = ',mxhill
         problem=.TRUE.
      endif
      if(mhill.lt.0.or.mhill.gt.2)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- Invalid value of ',&
         'MHILL entered (MHILL must be 0, 1 or 2) -- MHILL = ',mhill
         problem=.TRUE.
      endif
      if(problem) then
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
!
! --- Subgroup (6b) -- Hill data (one subgroup per hill)
!
      if(mhill.eq.2)then
         do 14 i=1,nhill
         call readin(cvdic(1,7),ivleng(1,7),ivtype(1,7),io5,io6,lecho,&
          HILLDAT(1,i),&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
14       continue
!
! --- Convert hill coordinates from km to met. grid units
         do 15 i=1,nhill
           hilldat(1,i)=(hilldat(1,i)-xorigkm)/dgridkm
           hilldat(2,i)=(hilldat(2,i)-yorigkm)/dgridkm
15       continue
      endif
