! --- Subgroup (6c) -- Complex terrain receptor information
!
      if(mhill.eq.2)then
         do 16 i=1,nctrec
         call readin(cvdic(1,8),ivleng(1,8),ivtype(1,8),io5,io6,lecho,&
          CTRDAT,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---       Transfer Complex terrain receptor data into common blocks
            xrct(i)=(ctrdat(1)-xorigkm)/dgridkm
            yrct(i)=(ctrdat(2)-yorigkm)/dgridkm
            elrect(i)=ctrdat(3)
            ihill(i)=ctrdat(4)+0.001
16       continue
      endif
!
! -------------------------------------
! --- Dry deposition of gases -- IG # 7
! -------------------------------------
!
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,9),ivleng(1,9),ivtype(1,9),io5,io6,lecho,&
       DRYG(1,1),DRYG(1,2),DRYG(1,3),DRYG(1,4),DRYG(1,5),&
       DRYG(1,6),DRYG(1,7),DRYG(1,8),DRYG(1,9),DRYG(1,10),&
       DRYG(1,11),DRYG(1,12),DRYG(1,13),DRYG(1,14),DRYG(1,15),&
       DRYG(1,16),DRYG(1,17),DRYG(1,18),DRYG(1,19),DRYG(1,20),&
       DRYG(1,21),DRYG(1,22),DRYG(1,23),DRYG(1,24),DRYG(1,25),&
       DRYG(1,26),DRYG(1,27),DRYG(1,28),DRYG(1,29),DRYG(1,30),&
       DRYG(1,31),DRYG(1,32),DRYG(1,33),DRYG(1,34),DRYG(1,35),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
! *** call readin(cvdic(1,9),ivleng(1,9),ivtype(1,9),io5,io6,lecho,
! ***1 DRYG(1,1),DRYG(1,2),DRYG(1,3),DRYG(1,4),DRYG(1,5),
! ***2 DRYG(1,6),DRYG(1,7),DRYG(1,8),DRYG(1,9),DRYG(1,10),
! ***3 DRYG(1,11),DRYG(1,12),DRYG(1,13),DRYG(1,14),DRYG(1,15),
! ***4 DRYG(1,16),DRYG(1,17),DRYG(1,18),DRYG(1,19),DRYG(1,20),
! ***5 DRYG(1,21),DRYG(1,22),DRYG(1,23),DRYG(1,24),
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***8 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***9 idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=5
! *** call readin(cvdic(1,9),ivleng(1,9),ivtype(1,9),io5,io6,lecho,
! ***1 DRYG(1,1),DRYG(1,2),DRYG(1,3),DRYG(1,4),DRYG(1,5),
! ***2 idum,idum,idum,idum,idum,
! ***3 idum,idum,idum,idum,idum,
! ***4 idum,idum,idum,idum,idum,
! ***5 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
!*** 8 idum,idum,idum,idum)
!
! --- Transfer gas dry deposition data into common block arrays
      do 20 i=1,nspec
      pdiff(i)=dryg(1,i)
      alphas(i)=dryg(2,i)
      react(i)=dryg(3,i)
      rm(i)=dryg(4,i)
      henry(i)=dryg(5,i)
20    continue
!
! -----------------------------------------
! --- Dry deposition of particles -- IG # 8
! -----------------------------------------
!
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,10),ivleng(1,10),ivtype(1,10),io5,io6,lecho,&
       DRYP(1,1),DRYP(1,2),DRYP(1,3),DRYP(1,4),DRYP(1,5),&
       DRYP(1,6),DRYP(1,7),DRYP(1,8),DRYP(1,9),DRYP(1,10),&
       DRYP(1,11),DRYP(1,12),DRYP(1,13),DRYP(1,14),DRYP(1,15),&
       DRYP(1,16),DRYP(1,17),DRYP(1,18),DRYP(1,19),DRYP(1,20),&
       DRYP(1,21),DRYP(1,22),DRYP(1,23),DRYP(1,24),DRYP(1,25),&
       DRYP(1,26),DRYP(1,27),DRYP(1,28),DRYP(1,29),DRYP(1,30),&
       DRYP(1,31),DRYP(1,32),DRYP(1,33),DRYP(1,34),DRYP(1,35),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
! *** call readin(cvdic(1,10),ivleng(1,10),ivtype(1,10),io5,io6,lecho,
