      np=0
      do 30 i=1,nspec
      if(idryflg(i).eq.2)then
         np=np+1
         if(np.gt.mxpdep)then
            write(io6,*)'ERROR in subr. READCF -- too many particle ',&
            'species specified -- i = ',i,' np = ',np,' mxpdep = ',&
            mxpdep
            write(*,*)
            stop 'Halted in READCF -- see list file.'
         endif
!
         gdiam(np)=dryp(1,i)
         gsig(np)=dryp(2,i)
      endif
30    continue
!
! -----------------------------------------------------
! --- Miscellaneous dry deposition parameters -- IG # 9
! -----------------------------------------------------
      call readin(cvdic(1,11),ivleng(1,11),ivtype(1,11),io5,io6,lecho,&
       RCUTR,RGR,REACTR,PCONST,BMIN,BMAX,QSWMAX,DCONST1,DCONST2,&
       DCONST3,DCONST4,NPSINT,IVEG,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
