!
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,12),ivleng(1,12),ivtype(1,12),io5,io6,lecho,&
       WA(1,1),WA(1,2),WA(1,3),WA(1,4),WA(1,5),&
       WA(1,6),WA(1,7),WA(1,8),WA(1,9),WA(1,10),&
