       do j=1,nspec
        if(idryflg(j).eq.2.and.idropflg(j).eq.1) then
         WA(1,j)=SCSPRAY(1)
         WA(2,j)=SCSPRAY(2)
        endif
       enddo
      endif
! -----------------------------------
! --- Chemistry parameters -- IG # 11
! -----------------------------------
! --- First call for this group reads Subgroup 11a
! --- 6.42_x1.1 140521
! --- The following call is set for MXSPEC=5
! *** call readin(cvdic(1,14),ivleng(1,14),ivtype(1,14),io5,io6,lecho,
! ***1 MOZ,BCKO3M,MNH3,MAVGNH3,BCKNH3M,RNITE1,RNITE2,RNITE3,
! ***2 MH2O2,BCKH2O2M,BCKPMF,OFRAC,VCNX,NDECAY,
! ***& RH_ISRP,SO4_ISRP,
! ***3 dktemp(1,1),dktemp(1,2),dktemp(1,3),dktemp(1,4),dktemp(1,5),
! ***4 idum,idum,idum,
! ***5 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- The following call is set for MXSPEC=24
! *** call readin(cvdic(1,14),ivleng(1,14),ivtype(1,14),io5,io6,lecho,
! ***1 MOZ,BCKO3M,MNH3,MAVGNH3,BCKNH3M,RNITE1,RNITE2,RNITE3,
! ***2 MH2O2,BCKH2O2M,BCKPMF,OFRAC,VCNX,NDECAY,
