! ***5 dktemp(1,11),dktemp(1,12),dktemp(1,13),dktemp(1,14),dktemp(1,15),
! ***6 dktemp(1,16),dktemp(1,17),dktemp(1,18),dktemp(1,19),dktemp(1,20),
! ***7 dktemp(1,21),dktemp(1,22),dktemp(1,23),dktemp(1,24),
! ***8 idum,idum,idum,idum,idum,idum,idum,idum,
! ***9 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,14),ivleng(1,14),ivtype(1,14),io5,io6,lecho,&
       MOZ,BCKO3M,MNH3,MAVGNH3,BCKNH3M,RNITE1,RNITE2,RNITE3,&
       MH2O2,BCKH2O2M,BCKPMF,OFRAC,VCNX,NDECAY,&
       RH_ISRP,SO4_ISRP,&
       dktemp(1,1),dktemp(1,2),dktemp(1,3),dktemp(1,4),dktemp(1,5),&
       dktemp(1,6),dktemp(1,7),dktemp(1,8),dktemp(1,9),dktemp(1,10),&
       dktemp(1,11),dktemp(1,12),dktemp(1,13),dktemp(1,14),dktemp(1,15),&
       dktemp(1,16),dktemp(1,17),dktemp(1,18),dktemp(1,19),dktemp(1,20),&
       dktemp(1,21),dktemp(1,22),dktemp(1,23),dktemp(1,24),dktemp(1,25),&
       dktemp(1,26),dktemp(1,27),dktemp(1,28),dktemp(1,29),dktemp(1,30),&
       dktemp(1,31),dktemp(1,32),dktemp(1,33),dktemp(1,34),dktemp(1,35),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- Create constant vertical NH3 profile (by month)
      do k=1,12
         do iz=1,nz
            bcknh3zm(k,iz)=bcknh3m(k)
         enddo
      enddo
! --- Any subsequent calls reads Subgroup 11b entries (NDECAY times)
      do k=1,ndecay
! ---    Clear temporary decay array (all species)
         do isp=1,mxspec
            dktemp(1,isp)=-1.
            dktemp(2,isp)=-1.
         enddo
! --- The following call is set for MXSPEC=5
! *** call readin(cvdic(1,14),ivleng(1,14),ivtype(1,14),io5,io6,lecho,
! --- V6.42_x1.0 (121203)
! ***1 MOZ,BCKO3M,MNH3,MAVGNH3,BCKNH3M,RNITE1,RNITE2,RNITE3,
! ***2 MH2O2,BCKH2O2M,BCKPMF,OFRAC,VCNX,NDECAY,
! ***& RH_ISRP,SO4_ISRP,
! ***3 dktemp(1,1),dktemp(1,2),dktemp(1,3),dktemp(1,4),dktemp(1,5),
! --- V6.42_x1.0 (121203)
! ***4 idum,idum,idum,
! ***5 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    The following call is set for MXSPEC=24
! *** call readin(cvdic(1,14),ivleng(1,14),ivtype(1,14),io5,io6,lecho,
! --- V6.42_x1.0 (121203)
! ***1 MOZ,BCKO3M,MNH3,MAVGNH3,BCKNH3M,RNITE1,RNITE2,RNITE3,
! ***2 MH2O2,BCKH2O2M,BCKPMF,OFRAC,VCNX,NDECAY,
! ***& RH_ISRP,SO4_ISRP,
! ***3 dktemp(1,1),dktemp(1,2),dktemp(1,3),dktemp(1,4),dktemp(1,5),
! ***4 dktemp(1,6),dktemp(1,7),dktemp(1,8),dktemp(1,9),dktemp(1,10),
! ***5 dktemp(1,11),dktemp(1,12),dktemp(1,13),dktemp(1,14),dktemp(1,15),
! ***6 dktemp(1,16),dktemp(1,17),dktemp(1,18),dktemp(1,19),dktemp(1,20),
! ***7 dktemp(1,21),dktemp(1,22),dktemp(1,23),dktemp(1,24),
! --- V6.42_x1.0 (121203)
! ***8 idum,idum,idum,idum,idum,idum,idum,idum,
! ***9 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,14),ivleng(1,14),ivtype(1,14),io5,io6,lecho,&
! --- V6.42_x1.0 (121203)
       MOZ,BCKO3M,MNH3,MAVGNH3,BCKNH3M,RNITE1,RNITE2,RNITE3,&
       MH2O2,BCKH2O2M,BCKPMF,OFRAC,VCNX,NDECAY,&
       RH_ISRP,SO4_ISRP,&
       dktemp(1,1),dktemp(1,2),dktemp(1,3),dktemp(1,4),dktemp(1,5),&
       dktemp(1,6),dktemp(1,7),dktemp(1,8),dktemp(1,9),dktemp(1,10),&
       dktemp(1,11),dktemp(1,12),dktemp(1,13),dktemp(1,14),dktemp(1,15),&
       dktemp(1,16),dktemp(1,17),dktemp(1,18),dktemp(1,19),dktemp(1,20),&
       dktemp(1,21),dktemp(1,22),dktemp(1,23),dktemp(1,24),dktemp(1,25),&
       dktemp(1,26),dktemp(1,27),dktemp(1,28),dktemp(1,29),dktemp(1,30),&
       dktemp(1,31),dktemp(1,32),dktemp(1,33),dktemp(1,34),dktemp(1,35),&
! --- V6.42_x1.0 (121203)
       idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Process inputs only for MCHEM=5 when half-life is positive
         if(mchem.EQ.5) then
! ---       Identify the parent species ID and store decay rate
            ip=-1
            np=0
            do isp=1,nspec
               if(dktemp(1,isp).GT.0.0) then
! ---             Positive half-life found
                  ip=isp
                  np=np+1
                  dkrate(ip)=1.0/dktemp(1,isp)
               endif
            enddo
            if(ip.GT.0) then
! ---          Identify any child species IDs and store yields
               do isp=1,nspec
                  if(dktemp(2,isp).GT.0.0) then
! ---                Child found
                     dkyield(isp,ip)=dktemp(2,isp)
                  endif
               enddo
            endif
! ---       QA check
            if(np.GT.1) then
               write(io6,*)'ERROR in subr. READCF -- more than 1 ',&
               'parent species specified in a single NDECAY group'
               write(*,*)
               stop 'Halted in READCF -- see list file.'
            endif
         endif
      enddo
! --- 6.42_x1.1 140521
! --- Store SO4_ISRP as g/m3 (from ug/m3)
      so4_isrp=so4_isrp*1.0e-06
! --- Subgroup 11c
      if(cfdataset.GT.7.0) then 
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,15),ivleng(1,15),ivtype(1,15),io5,io6,lecho,&
       EV(1,1),EV(1,2),EV(1,3),EV(1,4),EV(1,5),&
       EV(1,6),EV(1,7),EV(1,8),EV(1,9),EV(1,10),&
       EV(1,11),EV(1,12),EV(1,13),EV(1,14),EV(1,15),&
       EV(1,16),EV(1,17),EV(1,18),EV(1,19),EV(1,20),&
       EV(1,21),EV(1,22),EV(1,23),EV(1,24),EV(1,25),&
       EV(1,26),EV(1,27),EV(1,28),EV(1,29),EV(1,30),&
       EV(1,31),EV(1,32),EV(1,33),EV(1,34),EV(1,35),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum) 
      endif
!
! ----------------------------------------------------------
! --- Dispersion & misc. computational parameters -- IG # 12
! ----------------------------------------------------------
! --- Initialize temporary SVMIN, SWMIN to 'missing'
      do k=1,12
         svtemp(k)=-999.
         swtemp(k)=-999.
      enddo
!
! --- Move CNSPLITH block data defaults into temporary array
! --- and replace with missing data marker
      do is=1,mxspec
         cstemp(is)=cnsplith(is)
         cnsplith(is)=-999.
      enddo
!
      call readin(cvdic(1,16),ivleng(1,16),ivtype(1,16),io5,io6,lecho,&
       SYTDEP,MHFTSZ,JSUP,CONK1,CONK2,IURB1,IURB2,XMXLEN,MXNEW,SYMIN,&
       SZMIN,XSAMLEN,MXSAM,XMINZI,XMAXZI,SL2PF,SVTEMP,SWTEMP,WSCALM,&
       WSCAT,PLX0,PTG0,PPC,TBD,NSPLIT,IRESPLIT,ZISPLIT,ROLDMAX,&
       ANEMHT,ISIGMAV,IMIXCTDM,ILANDUIN,Z0IN,XLAIIN,ELEVIN,XLATIN,&
       XLONIN,EPSSLUG,EPSAREA,CDIV,NCOUNT,NSS,XLATSS(1,1),XLONSS(1,1),&
       TIBLDIST,DSRISE,NSPLITH,SYSPLITHGRD,SHSPLITHGRD,CNSPLITH,&
       TRAJINCL,MDEPBC,HTMINBC,RSAMPBC,MXAGEHR,SZCAP_M,NLUTIBL,&
       FCLIP,TKCAT,&
       idum)
!
! --- Reset calm wind speed threshold by "small" amount
      wscalm=0.99999*wscalm
!
! --- If no inputs are read, keep all 12 default values from BLOCK DATA
! --- Otherwise, replace defaults with values that are read.
! --- Sigma-v:
      if(svtemp(1).GT.-990.) then
         if(svtemp(12).LT.-990.) then
            write(io6,*)
            write(io6,*)'*********  WARNING  ***********'
            write(io6,*)'READCF -- Missing SVMIN inputs set to default'
            write(*,*)
            write(*,*)'Warning -- Default SVMIN values are used --'
            write(*,*)'See list file.'
         endif
! ---    Replace defaults with values read
         do ist=1,6
            if(svtemp(ist).GT.-990.) then
               svmin(ist,1)=svtemp(ist)
            else
               write(io6,*)'Over LAND  stability,value = ',&
                            ist,svmin(ist,1)
            endif
            if(svtemp(ist+6).GT.-990.) then
               svmin(ist,2)=svtemp(ist+6)
            else
               write(io6,*)'Over WATER stability,value = ',&
                            ist,svmin(ist,2)
            endif
         enddo
      endif
! --- Sigma-w:
      if(swtemp(1).GT.-990.) then
         if(swtemp(12).LT.-990.) then
            write(io6,*)
            write(io6,*)'*********  WARNING  ***********'
            write(io6,*)'READCF -- Missing SWMIN inputs set to default'
            write(*,*)
            write(*,*)'Warning -- Default SWMIN values are used --'
            write(*,*)'See list file.'
         endif
! ---    Replace defaults with values read
         do ist=1,6
            if(swtemp(ist).GT.-990.) then
               swmin(ist,1)=swtemp(ist)
            else
               write(io6,*)'Over LAND  stability,value = ',&
                            ist,swmin(ist,1)
            endif
            if(swtemp(ist+6).GT.-990.) then
               swmin(ist,2)=swtemp(ist+6)
            else
               write(io6,*)'Over WATER stability,value = ',&
                            ist,swmin(ist,2)
            endif
         enddo
      endif
! --- Do this in SETNEST since there may be multiple grid domains
!c --- Reset sigma-y units from Grid Units to m (splitting)
!c --- and reset shear test units from SYSPLITH/hr to m/s
!      sysplith=sysplith*dgrid
!      shsplith=shsplith*sysplith/3600.
! --- Test for missing values of CNSPLITH
      if(cnsplith(1).LT.0.0) then
! ---    Set all values to default
         do is=1,nspec
            cnsplith(is)=cstemp(is)
         enddo
      elseif(cnsplith(2).LT.0.0) then
! ---    Use valid first value for all nspec
         do is=2,nspec
            cnsplith(is)=cnsplith(1)
         enddo
      endif
! --- Process puff age cutoff
      if(mxagehr.LE.0) then
         lage=.FALSE.
      else
         lage=.TRUE.
         tsecmax=3600.*FLOAT(mxagehr)
      endif
! --- V6.42_x1.0, Level 121203
! --- Condition FCLIP to be GE zero
      fclip=MAX(fclip,0.0)
! --- Set WSCAT and TKCAT into WQSF and TQSF arrays for scaling
! --- control-file source emissions
! --- Default values of WSCAT and TKCAT are available if none
! --- were read in the control file
      wqsf=0.0
      tqsf=0.0
! --- Point
      wqsf(:,1)=wscat(:)
      tqsf(:,1)=tkcat(:)
! --- Area
      wqsf(:,3)=wscat(:)
      tqsf(:,3)=tkcat(:)
! --- Line
      wqsf(:,5)=wscat(:)
      tqsf(:,5)=tkcat(:)
! --- Volume
      wqsf(:,7)=wscat(:)
      tqsf(:,7)=tkcat(:)
! --- BCON
      wqsf(:,9)=wscat(:)
      tqsf(:,9)=tkcat(:)
! --- Flare
      wqsf(:,10)=wscat(:)
      tqsf(:,10)=tkcat(:)
! --- Road
      wqsf(:,12)=wscat(:)
      tqsf(:,12)=tkcat(:)
! -------------------------------------------------------------------
! --- Start of control-file groups containing source information
! -------------------------------------------------------------------
! --- Initialize local counter for the master scale-factor table
! --- for each scaled source-species pair
! --- This is used only for control files that pre-date TNG-3.1 / 7.0
! --- since these do not contain a separate input group for all
! --- of the tables (NQSFTAB is provided in that group)
      nqsftab=0
! -------------------------------------------------------------------
! ---------------------------------------
! --- Point source information -- IG # 13
! ---------------------------------------
!
! --- Subgroup (13a) -- First read NPT1, NPT2, IPTU, NSPT1
!
      call readin(cvdic(1,17),ivleng(1,17),ivtype(1,17),io5,io6,lecho,&
       NPT1,NPT2,IPTU,NSPT1,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum)
!
      if(iptu.LE.0 .OR. iptu.GT.9)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- invalid units for PT1 ',&
         'sources -- ','IPTU = ',iptu,' valid = 1-9'
         iptu=1
         problem=.TRUE.
      endif
      if(npt1.gt.mxpt1)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many point ',&
         'sources -- ','NPT1 = ',npt1,' MXPT1 = ',mxpt1
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      if(npt2.gt.mxpt2)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many point ',&
         'sources -- ','NPT2 = ',npt2,' MXPT2 = ',mxpt2
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
!
! --- Change NPTDAT to 1 if single PTEMARB.DAT name is provided in
!     first section of Group 0
      if(nptdat.EQ.0 .AND. npt2.GT.0) nptdat=1
!
! --- Subgroup (13b) -- Read source data for each point source
!
      do 150 i=1,npt1
! --- Clear temporary array for source name
      do j=1,16
         ctemp16(j)(1:1)=' '
      enddo
!
! --- Initialize sigmas to zero
      pt1sigs(1)=0.0
      pt1sigs(2)=0.0
!
! --- Initialize vertical momentum flux factor to unity
      fmfac=1.0
!
! --- Initialize platform height to zero
      zpltfm=0.0
!
      call readin(cvdic(1,18),ivleng(1,18),ivtype(1,18),io5,io6,lecho,&
       PT1DATA,PT1SIGS,FMFAC,ZPLTFM,CTEMP16,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum)
!
! --- Transfer CHAR*4 source name array into CHAR*16 variable for source
      cnampt1(i)='                '
      do j=1,16
         cnampt1(i)(j:j)=ctemp16(j)(1:1)
      enddo
! --- Names must be different
      if(i.GT.1) then
         do j=1,i-1
            if(cnampt1(j).EQ.cnampt1(i)) then
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR in subr. READCF -- name duplicated ',&
               'for source -- ',i,'  name = ',cnampt1(i)
               problem=.TRUE.
            endif
         enddo
      endif
!
! --- Transfer the point source data to the common block arrays
!
! --- Compute relative grid coordinates
      xpt1grd(i)=(pt1data(1)-xorigkm)/dgridkm
      ypt1grd(i)=(pt1data(2)-yorigkm)/dgridkm
!
! --- Stack parameters
      htstak(i)=pt1data(3)
      elstak(i)=pt1data(4)
      diam(i)=pt1data(5)
      exitw(i)=pt1data(6)
      tstak(i)=pt1data(7)
!     idownw(i)=pt1data(8)+0.001
      idownw(i)=NINT(pt1data(8))
      fmfpt1(i)=fmfac
      zplatpt1(i)=zpltfm
!
! --- Emission rates (apply units conversion factor to obtain g/s, 
! --- Odour_Units*m3/s, or Bq/s)
      lqneg=.FALSE.
      do j=1,nspec
         qstak(j,i)=pt1data(8+j)*qunits(iptu)
         if(qstak(j,i).LT.0.0) lqneg=.TRUE.
      enddo
! --- Report problem if any emissions are negative
      if(lqneg) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- Invalid emission rate ',&
         ' (must NOT be negative) -- Pt. Source = ',cnampt1(i)
         problem=.TRUE.
      endif
!
! --- Initial sigmas
      syipt1(i)=AMAX1(pt1sigs(1),0.0)
      szipt1(i)=AMAX1(pt1sigs(2),0.0)
!
150   continue
!
! --- Subgroup (13c) -- Building dimension data for each source subject
!                       to building downwash effects
!
! --- Only sources with building downwash have bldg. dimensions
! --- Identify number of these sources
      ndownw=0
      do i=1,npt1
         if(idownw(i).GE.1) ndownw=ndownw+1
      enddo
!
      do 160 i=1,ndownw
! ---    Prepare temporary array for source name
         do j=1,16
            ctemp16(j)(1:1)=' '
         enddo
! ---    Zero building data in temporary arrays
         do j=1,36
            bwtemp(j)=0.0
            bhtemp(j)=0.0
            bltemp(j)=0.0
            bxtemp(j)=0.0
            bytemp(j)=0.0
         enddo
         call readin(cvdic(1,19),ivleng(1,19),ivtype(1,19),io5,io6,&
            lecho,&
         BWTEMP,BHTEMP,BLTEMP,BXTEMP,BYTEMP,CTEMP16,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 source name into CHAR*16 variable for source
         cnamsrc='                '
         do j=1,16
            cnamsrc(j:j)=ctemp16(j)(1:1)
         enddo
! ---    Match source name to index (with downwash)
         lmatch=.FALSE.
         do ip=1,npt1
            if(cnamsrc.EQ.cnampt1(ip) .AND. idownw(ip).GE.1) then
               indx=ip
               lmatch=.TRUE.
            endif
         enddo
! ---    Pass building data to common arrays
         if(lmatch) then
            do idir=1,36
               bwidth(idir,indx)=bwtemp(idir)
               bht(idir,indx)=bhtemp(idir)
               bln1(idir,indx)=bltemp(idir)
               xbadj1(idir,indx)=bxtemp(idir)
               ybadj1(idir,indx)=bytemp(idir)
            enddo
         else
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- source name for ',&
            'building data not valid:',' name = ',cnamsrc
            write(io6,*)'Valid source names for downwash are: '
            do ip=1,npt1
               if(idownw(ip).GE.1) write(io6,*) cnampt1(ip)
            enddo
            problem=.TRUE.
         endif
160   continue
! --- Were data provided for all sources with downwash?
      nfind=0
      nfind2=0
      do i=1,npt1
         test=0
         test2=0
         do j=1,36
            test=test+bwidth(j,i)
            test2=test2+bln1(j,i)
         enddo
         if(test.GT.1.) nfind=nfind+1
         if(test2.GT.1.) nfind2=nfind2+1
      enddo
      if(nfind.NE.ndownw) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- building data for ',&
         nfind,' sources processed'
         write(io6,*)'                         Expected data for ',&
         ndownw,' sources'
         problem=.TRUE.
      endif
      if(mbdw.EQ.2 .AND. (nfind.NE.nfind2)) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- building widths for ',&
         nfind,' sources processed and'
         write(io6,*)'                         building lengths for ',&
         nfind2,' sources processed.'
         write(io6,*)'Expected both width and length data for MBDW=2'
         problem=.TRUE.
      endif
!
! --- Subgroup (13d) -- Variable emission scaling factors
!
! --- Method for specifying emission scale-factors changed in
! --- CALPUFF control-file dataset version TNG-3.1, denoted as
! --- cfdataset=3.1 (also applies to dataset version 7.0)
! -------------------------------
      if(cfdataset.LT.3.01) then
! --- METHOD using IVARY index --- up through dataset TNG-3.0
! -------------------------------
! --- Loop over source-species combinations
      do i=1,nspt1
! ---    Clear temporary arrays
! ---    Note: c4_70 is not used for this dataset
         vqtemp=0.0
         do j=1,16
            ctemp16(j)(1:1)=' '
         enddo
!
! --- The following call is set for MXSPEC=35
         call readin(cvdic(1,20),ivleng(1,20),ivtype(1,20),io5,io6,&
            lecho,&
         CTEMP16,IVARY,c4_70,&
         VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),&
         VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),&
         VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),&
         VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),&
         VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),&
         VQTEMP(1,23),VQTEMP(1,24),VQTEMP(1,25),VQTEMP(1,26),&
         VQTEMP(1,27),VQTEMP(1,28),VQTEMP(1,29),VQTEMP(1,30),&
         VQTEMP(1,31),VQTEMP(1,32),VQTEMP(1,33),VQTEMP(1,34),&
         VQTEMP(1,35),                                              &
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
! ***    call readin(cvdic(1,20),ivleng(1,20),ivtype(1,20),io5,io6,
! ***1      lecho,
! ***1   CTEMP16,IVARY,c4_70,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),
! ***4   VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),
! ***5   VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),
! ***6   VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),
! ***7   VQTEMP(1,23),VQTEMP(1,24),
! ***8   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***9   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***a   idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- The following call is set for MXSPEC=5
! ***    call readin(cvdic(1,20),ivleng(1,20),ivtype(1,20),io5,io6,
! ***1      lecho,
! ***1   CTEMP16,IVARY,c4_70,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***4   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***5   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7   idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 source name into CHAR*16 variable for source
         cnamsrc='                '
         do j=1,16
            cnamsrc(j:j)=ctemp16(j)(1:1)
         enddo
! ---    Scan variable emissions factors for negatives
         lqneg=.FALSE.
         do iv=1,96
            do isp=1,mxspec
               if(vqtemp(iv,isp).LT.0.0) lqneg=.TRUE.
            enddo
         enddo
! ---    Report problem if any factors are negative
         if(lqneg) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- Invalid scaling',&
          ' factor  (must NOT be negative) -- Pt. Source = ',cnamsrc
            problem=.TRUE.
         endif
! ---    Match source name to index
         call SMATCH(io6,cnamsrc,npt1,cnampt1,isrc)
! ---    Check IVARY
         if(ivary.LT.0 .OR. ivary.GT.5) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- type of variable',&
            ' emission factor invalid:',' type = ',ivary
            write(io6,*)'                         Valid type is 0-5'
            problem=.TRUE.
         else
! ---       Map IVARY (0-5) into corresponding KVARY index
            kvary=mapivary(ivary+1)
! ---       Find species (a value of VQTEMP will be >0.0)
            iv_loop_pt: do iv=1,96
               do isp=1,mxspec
                  if(vqtemp(iv,isp).GT.0.0) EXIT iv_loop_pt
               enddo
            enddo iv_loop_pt
! ---       Make table name
            csfpt1(i)='PT_'//TRIM(cnamsrc)//'_'//TRIM(cspec(isp))
! ---       Set pointers and scale factor table information
            nqsftab=nqsftab+1
            idsfpt1(isp,isrc)=i
            ixrefpt1(i)=nqsftab
            cqsfname(nqsftab)=csfpt1(i)
            iqsftype(nqsftab)=kvary
! ---       Place factors into array
            if(kvary.NE.6) then
               do iv=1,96
                  qsftab(iv,nqsftab)=vqtemp(iv,isp)
               enddo
            else
! ---          Distribute seasonal factors (24*4) to months (24*12)
! ---          where season 1 is DEC-JAN-FEB
               iv=0
               do imonth=1,12
                  if(imonth.EQ.12 .OR. imonth.LE.2) then
! ---                Season 1
                     jv=0
                  elseif(imonth.LE.5) then
! ---                Season 2
                     jv=24
                  elseif(imonth.LE.8) then
! ---                Season 3
                     jv=48
                  else
! ---                Season 4
                     jv=72
                  endif
                  do ihour=1,24
                     iv=iv+1
                     jv=jv+1
                     qsftab(iv,nqsftab)=vqtemp(jv,isp)
                  enddo
               enddo
            endif
         endif
      enddo
! -------------------------------
      elseif(cfdataset.GT.3.01) then
! --- METHOD using Factor Table names --- Dataset TNG-3.1 / 7.0 onward
! -------------------------------
! --- Factor name is provided for each scaled source-species
! --- Loop over source-species combinations
      do i=1,nspt1
! ---    Initialize temp array for character variables
! ---    3 strings are on line, and the total length is 70
! ---    and CTEMP16,IVARY are not used here
         c4_70=' '
         call readin(cvdic(1,20),ivleng(1,20),ivtype(1,20),io5,io6,&
            lecho,&
         CTEMP16,IVARY,c4_70,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Convert from c*4 array to c*70 string with blanks at end
         ic=0
         do k=1,70
           if(c4_70(k)(1:1).NE.' ') then
              ic=ic+1
              c70temp(ic:ic)=c4_70(k)(1:1)
           endif
         enddo
         do k=ic+1,70
            c70temp(k:k)=' '
         enddo
         klast=ic
! ---    Location of first comma
         kc1=INDEX(c70temp,',')
! ---    Location of second (last) comma
         kc2=INDEX(c70temp,',',back=.true.)
! ---    Assign source, species, factor names
         c16temp='                '
         c16temp(1:kc1-1)=c70temp(1:kc1-1)
         c12temp='            '
         c12temp(1:kc2-kc1-1)=c70temp(kc1+1:kc2-1)
         c40temp='                                        '
         c40temp(1:klast-kc2)=c70temp(kc2+1:klast)
! ---    Source index
         call SMATCH(io6,c16temp,npt1,cnampt1,ksrc)
! ---    Species index
         call SMATCH(io6,c12temp,nspec,cspec,kspc)
! ---    Update source arrays (except ixrefpt1(i))
         idsfpt1(kspc,ksrc)=i
         csfpt1(i)=c40temp
      enddo
      endif
! --------------------------------------
! --- Area source information -- IG # 14
! --------------------------------------
!
! --- Subgroup (14a) -- First read NAR1, NAR2, IARU, NSAR1
!
      call readin(cvdic(1,21),ivleng(1,21),ivtype(1,21),io5,io6,lecho,&
       NAR1,NAR2,IARU,NSAR1,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum)
      if(iaru.LE.0 .OR. iaru.GT.9)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- invalid units for AR1 ',&
         'sources -- ','IARU = ',iaru,' valid = 1-9'
         iaru=1
         problem=.TRUE.
      endif
      if(nar1.gt.mxarea)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many area ',&
         'sources -- ','NAR1 = ',nar1,' MXAREA = ',mxarea
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      if(nar2.gt.mxarea)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many area ',&
         'sources -- ','NAR2 = ',nar1,' MXAREA = ',mxarea
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
!
! --- Change NARDAT to 1 if single BAEMARB.DAT name is provided in
!     first section of Group 0
      if(nardat.EQ.0 .AND. nar2.GT.0) nardat=1
!
! --- Subgroup (14b) -- Read data for each area source
!
      do 170 i=1,nar1
! --- Clear temporary array for source name
      do j=1,16
         ctemp16(j)(1:1)=' '
      enddo
!
      call readin(cvdic(1,22),ivleng(1,22),ivtype(1,22),io5,io6,lecho,&
       ARDATA,CTEMP16,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- Transfer CHAR*4 source name array into CHAR*16 variable for source
      cnamar1(i)='                '
      do j=1,16
         cnamar1(i)(j:j)=ctemp16(j)(1:1)
      enddo
! --- Names must be different
      if(i.GT.1) then
         do j=1,i-1
            if(cnamar1(j).EQ.cnamar1(i)) then
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR in subr. READCF -- name duplicated ',&
               'for source -- ',i,'  name = ',cnamar1(i)
               problem=.TRUE.
            endif
         enddo
      endif
!
! --- Transfer the area source data to the common block arrays
!
! --- Area parameters
! --- nvert1(i) is set to 4 in Block Data (hardwired).  If this is made
! --- a user input in the future, array ARDATA should be expanded.
      htar1(i)=ardata(1)
      elar1(i)=ardata(2)
      sz0ar1(i)=ardata(3)
!
! --- Emission rates (apply units conversion factor to obtain g/s/m**2)
! --- Odour_Units*m3/s/m**2, or Bq/s/m**2)
      lqneg=.FALSE.
      do j=1,nspec
         qar1(j,i)=ardata(3+j)*qunits(iaru)
         if(qar1(j,i).LT.0.0) lqneg=.TRUE.
      enddo
! --- Report problem if any emissions are negative
      if(lqneg) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- Invalid emission rate ',&
         ' (must NOT be negative) -- Area Source = ',cnamar1(i)
         problem=.TRUE.
      endif
170   continue
!
! --- Subgroup (14c) -- Read & process vertex data for each area source
!
      do i=1,nar1
!
! ---    Prepare temporary array for source name
         do j=1,16
            ctemp16(j)(1:1)=' '
         enddo
!
         call readin(cvdic(1,23),ivleng(1,23),ivtype(1,23),io5,io6,&
            lecho,&
          XVERT,YVERT,CTEMP16,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 source name into CHAR*16 variable for source
         cnamsrc='                '
         do j=1,16
            cnamsrc(j:j)=ctemp16(j)(1:1)
         enddo
! ---    Match source name to index
         lmatch=.FALSE.
         do ia=1,nar1
            if(cnamsrc.EQ.cnamar1(ia)) then
               isrc=ia
               lmatch=.TRUE.
            endif
         enddo
         if(lmatch) then
! ---       Compute and store relative vertex coordinates; accumulate
! ---       sums for computing the mean location of the area source
            sumx=0.
            sumy=0.
            do iv=1,nvert1(isrc)
               xar1grd(iv,isrc)=(xvert(iv)-xorigkm)/dgridkm
               yar1grd(iv,isrc)=(yvert(iv)-yorigkm)/dgridkm
               sumx=sumx+xar1grd(iv,isrc)
               sumy=sumy+yar1grd(iv,isrc)
            enddo
!
! ---       Mean location of source
            xar1grd(nvert1(isrc)+1,isrc)=sumx/FLOAT(nvert1(isrc))
            yar1grd(nvert1(isrc)+1,isrc)=sumy/FLOAT(nvert1(isrc))
!
! ---       Area of 4-sided area source in m^2
            call quadarea(xvert,yvert,area1(isrc))
!
! ---       Convert emission rate from (g/s/m^2) to (g/s)
            do j=1,nspec
               qar1(j,isrc)=qar1(j,isrc)*area1(isrc)
            enddo
         else
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- source name for ',&
            'AREA vertices invalid :',' name = ',cnamsrc
            write(io6,*)'Valid area source names are: '
            do ia=1,nar1
               write(io6,*) cnamar1(ia)
            enddo
            problem=.TRUE.
         endif
      enddo
!
! --- Subgroup (14d) -- Variable emission scaling factors
!
! --- Method for specifying emission scale-factors changed in
! --- CALPUFF control-file dataset version TNG-3.1, denoted as
! --- cfdataset=3.1 (also applies to dataset version 7.0)
! -------------------------------
      if(cfdataset.LT.3.01) then
! --- METHOD using IVARY index --- up through dataset TNG-3.0
! -------------------------------
! --- Loop over source-species combinations
      do i=1,nsar1
! ---    Clear temporary arrays
! ---    Note: c4_70 is not used for this dataset
         vqtemp=0.0
         do j=1,16
            ctemp16(j)(1:1)=' '
         enddo
! --- The following call is set for MXSPEC=35
         call readin(cvdic(1,24),ivleng(1,24),ivtype(1,24),io5,io6,&
            lecho,&
         CTEMP16,IVARY,c4_70,&
         VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),&
         VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),&
         VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),&
         VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),&
         VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),&
         VQTEMP(1,23),VQTEMP(1,24),VQTEMP(1,25),VQTEMP(1,26),&
         VQTEMP(1,27),VQTEMP(1,28),VQTEMP(1,29),VQTEMP(1,30),&
         VQTEMP(1,31),VQTEMP(1,32),VQTEMP(1,33),VQTEMP(1,34),&
         VQTEMP(1,35),                                              &
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
! ***    call readin(cvdic(1,24),ivleng(1,24),ivtype(1,24),io5,io6,
! ***1      lecho,
! ***1   CTEMP16,IVARY,c4_70,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),
! ***4   VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),
! ***5   VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),
! ***6   VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),
! ***7   VQTEMP(1,23),VQTEMP(1,24),
! ***8   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***9   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***a   idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- The following call is set for MXSPEC=5
! ***    call readin(cvdic(1,24),ivleng(1,24),ivtype(1,24),io5,io6,
! ***1      lecho,
! ***1   CTEMP16,IVARY,c4_70,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***4   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***5   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7   idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 source name into CHAR*16 variable for source
         cnamsrc='                '
         do j=1,16
            cnamsrc(j:j)=ctemp16(j)(1:1)
         enddo
! ---    Scan variable emissions factors for negatives
         lqneg=.FALSE.
         do iv=1,96
            do isp=1,mxspec
               if(vqtemp(iv,isp).LT.0.0) lqneg=.TRUE.
            enddo
         enddo
! ---    Report problem if any factors are negative
         if(lqneg) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- Invalid scaling',&
          ' factor  (must NOT be negative) -- Area Source = ',cnamsrc
            problem=.TRUE.
         endif
! ---    Match source name to index
         call SMATCH(io6,cnamsrc,nar1,cnamar1,isrc)
! ---    Check IVARY
         if(ivary.LT.0 .OR. ivary.GT.5) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- type of variable',&
            ' emission factor invalid:',' type = ',ivary
            write(io6,*)'                         Valid type is 0-5'
            problem=.TRUE.
         else
! ---       Map IVARY (0-5) into corresponding KVARY index
            kvary=mapivary(ivary+1)
! ---       Find species (a value of VQTEMP will be >0.0)
            iv_loop_ar: do iv=1,96
               do isp=1,mxspec
                  if(vqtemp(iv,isp).GT.0.0) EXIT iv_loop_ar
               enddo
            enddo iv_loop_ar
! ---       Make table name
            csfar1(i)='AR_'//TRIM(cnamsrc)//'_'//TRIM(cspec(isp))
! ---       Set pointers and scale factor table information
            nqsftab=nqsftab+1
            idsfar1(isp,isrc)=i
            ixrefar1(i)=nqsftab
            cqsfname(nqsftab)=csfar1(i)
            iqsftype(nqsftab)=kvary
! ---       Place factors into array
            if(kvary.NE.6) then
               do iv=1,96
                  qsftab(iv,nqsftab)=vqtemp(iv,isp)
               enddo
            else
! ---          Distribute seasonal factors (24*4) to months (24*12)
! ---          where season 1 is DEC-JAN-FEB
               iv=0
               do imonth=1,12
                  if(imonth.EQ.12 .OR. imonth.LE.2) then
! ---                Season 1
                     jv=0
                  elseif(imonth.LE.5) then
! ---                Season 2
                     jv=24
                  elseif(imonth.LE.8) then
! ---                Season 3
                     jv=48
                  else
! ---                Season 4
                     jv=72
                  endif
                  do ihour=1,24
                     iv=iv+1
                     jv=jv+1
                     qsftab(iv,nqsftab)=vqtemp(jv,isp)
                  enddo
               enddo
            endif
         endif
      enddo
! -------------------------------
      elseif(cfdataset.GT.3.01) then
! --- METHOD using Factor Table names --- Dataset TNG-3.1 /7.0 onward
! -------------------------------
! --- Factor name is provided for each scaled source-species
! --- Loop over source-species combinations
      do i=1,nsar1
! ---    Initialize temp array for character variables
! ---    3 strings are on line, and the total length is 70
! ---    and CTEMP16,IVARY are not used here
         c4_70=' '
         call readin(cvdic(1,24),ivleng(1,24),ivtype(1,24),io5,io6,&
            lecho,&
         CTEMP16,IVARY,c4_70,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Convert from c*4 array to c*70 string with blanks at end
         ic=0
         do k=1,70
           if(c4_70(k)(1:1).NE.' ') then
              ic=ic+1
              c70temp(ic:ic)=c4_70(k)(1:1)
           endif
         enddo
         do k=ic+1,70
            c70temp(k:k)=' '
         enddo
         klast=ic
! ---    Location of first comma
         kc1=INDEX(c70temp,',')
! ---    Location of second (last) comma
         kc2=INDEX(c70temp,',',back=.true.)
! ---    Assign source, species, factor names
         c16temp='                '
         c16temp(1:kc1-1)=c70temp(1:kc1-1)
         c12temp='            '
         c12temp(1:kc2-kc1-1)=c70temp(kc1+1:kc2-1)
         c40temp='                                        '
         c40temp(1:klast-kc2)=c70temp(kc2+1:klast)
! ---    Source index
         call SMATCH(io6,c16temp,nar1,cnamar1,ksrc)
! ---    Species index
         call SMATCH(io6,c12temp,nspec,cspec,kspc)
! ---    Update source arrays (except ixrefar1(i))
         idsfar1(kspc,ksrc)=i
         csfar1(i)=c40temp
      enddo
      endif
!
!
! --------------------------------------
! --- Line source information -- IG # 15
! --------------------------------------
!
! --- Subgroup (15a) -- First read NLINES, average line source
! ---                   buoyancy and layout information
!
      call readin(cvdic(1,25),ivleng(1,25),ivtype(1,25),io5,io6,lecho,&
       NLINES,XL,HBL,WBL,WML,DXL,FPRIMEL,MXNSEG,NLRISE,ILNU,NSLN1,&
       NLN2,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
      if(ilnu.LE.0 .OR. ilnu.GT.9)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- invalid units for LN1 ',&
         'sources -- ','ILNU = ',ilnu,' valid = 1-9'
         ilnu=1
         problem=.TRUE.
      endif
      if(nlines.gt.mxlines)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many line ',&
         'sources -- ','NLINES = ',nlines,' MXLINES = ',mxlines
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      if(nln2.gt.mxlines)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many line ',&
         'sources -- ','NLN2   = ',nln2,' MXLINES = ',mxlines
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      if(mxlines.GE.1000) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- model cannot ',&
         'function properly with more than 1000 line sources-- ',&
         ' MXLINES = ',mxlines
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
!
      if(nlines.GT.0) then
         if(ilnu.LE.0 .OR. ilnu.GT.7)then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- invalid units for ',&
            'LINE sources -- ','ILNU = ',ilnu,' valid = 1-7'
            ilnu=1
            problem=.TRUE.
         endif
! ---    Compute secondary information from these (/LINES/)
! ---    Separation between centers of adjacent lines (average)
         wsep=wbl+dxl
! ---    Total buoyancy flux from all line sources in block
         fptot=fprimel*nlines
! ---    Corresponding point-source buoyancy flux (fptot/pi)
         fbpt=fptot*0.3183099
      else
         nsln1=0
      endif
!
!
! --- Subgroup (15b) -- Read source data for each line source
!
      do 350 i=1,nlines
! --- Clear temporary array for source name
      do j=1,16
         ctemp16(j)(1:1)=' '
      enddo
!
      call readin(cvdic(1,26),ivleng(1,26),ivtype(1,26),io5,io6,lecho,&
       XLNDATA,CTEMP16,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- Transfer CHAR*4 source name array into CHAR*16 variable for source
      cnamln1(i)='                '
      do j=1,16
         cnamln1(i)(j:j)=ctemp16(j)(1:1)
      enddo
! --- Names must be different
      if(i.GT.1) then
         do j=1,i-1
            if(cnamln1(j).EQ.cnamln1(i)) then
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR in subr. READCF -- name duplicated ',&
               'for source -- ',i,'  name = ',cnamln1(i)
               problem=.TRUE.
            endif
         enddo
      endif
!
! --- Transfer the line source data to the common block arrays
!
! --- Compute relative grid coordinates (in grid units)
      xlbeggrd(i)=(xlndata(1)-xorigkm)/dgridkm
      ylbeggrd(i)=(xlndata(2)-yorigkm)/dgridkm
      xlendgrd(i)=(xlndata(3)-xorigkm)/dgridkm
      ylendgrd(i)=(xlndata(4)-yorigkm)/dgridkm
!
! --- Compute the vertex coordinates, assuming that the average line
! --- width (WML) applies, for corresponding area-source representation
      wmlby2=0.5*wml
! --- Reset to next larger whole meter
      wmlby2=float(int(wmlby2)+1)
      xdiffm=(xlbeggrd(i)-xlendgrd(i))*dgrid
      ydiffm=(ylbeggrd(i)-ylendgrd(i))*dgrid
! --- Let "c" denote 1/slope of line
      if(abs(ydiffm).LE.1.0e-10) then
         dx=0.0
         dy=-wmlby2
      else
         c=xdiffm/ydiffm
         dx=wmlby2/sqrt(1+c*c)
         dy=-dx*c
      endif
! --- Convert deltas to grid units
      dxgrd=dx*dgridi
      dygrd=dy*dgridi
! --- Pass vertices to arrays in /LN1/
      xvertl(1,i)=xlbeggrd(i)-dxgrd
      yvertl(1,i)=ylbeggrd(i)-dygrd
      xvertl(2,i)=xlbeggrd(i)+dxgrd
      yvertl(2,i)=ylbeggrd(i)+dygrd
      xvertl(3,i)=xlendgrd(i)+dxgrd
      yvertl(3,i)=ylendgrd(i)+dygrd
      xvertl(4,i)=xlendgrd(i)-dxgrd
      yvertl(4,i)=ylendgrd(i)-dygrd
! --- Corresponding area of line source in m^2
      arline(i)=2.0*wmlby2*sqrt(xdiffm*xdiffm+ydiffm*ydiffm)
!
! --- Stack parameters
      hsl(i)=xlndata(5)
      belevl(i)=xlndata(6)
!
! --- Emission rates (apply units conversion factor to obtain g/s)
! --- Odour_Units*m3/s, or Bq/s)
      lqneg=.FALSE.
      do j=1,nspec
         qtl(j,i)=xlndata(6+j)*qunits(ilnu)
         if(qtl(j,i).LT.0.0) lqneg=.TRUE.
      enddo
! --- Report problem if any emissions are negative
      if(lqneg) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- Invalid emission rate ',&
         ' (must NOT be negative) -- Line Source = ',cnamln1(i)
         problem=.TRUE.
      endif
350   continue
!
! --- Continue setup for line sources
      if(nlines.GT.0) then
! ---    Find center of the array of line sources, and its orientation
         xbeg=0.0
         ybeg=0.0
         xend=0.0
         yend=0.0
         do i=1,nlines
            xbeg=xbeg+xlbeggrd(i)
            ybeg=ybeg+ylbeggrd(i)
            xend=xend+xlendgrd(i)
            yend=yend+ylendgrd(i)
         enddo
         xbeg=xbeg/float(nlines)
         ybeg=ybeg/float(nlines)
         xend=xend/float(nlines)
         yend=yend/float(nlines)
!
         xlbar=0.5*(xbeg+xend)
         ylbar=0.5*(ybeg+yend)
         orientl=atan2((xbeg-xend),(ybeg-yend))
!
! ---    Check number of points used to tabulate buoyant rise
         if(nlrise.gt.mxrise)then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- too many points ',&
            'for rise table -- ','NLRISE = ',nlrise,' MXRISE = ',mxrise
            write(*,*)
            stop 'Halted in READCF -- see list file.'
         endif
      endif
!
