         VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),&
         VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),&
         VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),&
