            nqsftab=nqsftab+1
            idsfln1(isp,isrc)=i
            ixrefln1(i)=nqsftab
            cqsfname(nqsftab)=csfln1(i)
            iqsftype(nqsftab)=kvary
! ---       Place factors into array
            if(kvary.NE.6) then
               do iv=1,96
                  qsftab(iv,nqsftab)=vqtemp(iv,isp)
               enddo
