! --- Subgroup (16a) -- Read number of volume sources (etc.)
!
      call readin(cvdic(1,28),ivleng(1,28),ivtype(1,28),io5,io6,lecho,&
       NVL1,NVL2,IVLU,NSVL1,IGRDVL,VEFFHT,VSIGYI,VSIGZI,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum)
      if(ivlu.LE.0 .OR. ivlu.GT.9)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- invalid units for Vol ',&
         'sources -- ','IVLU = ',ivlu,' valid = 1-9'
         ivlu=1
         problem=.TRUE.
      endif
      if(nvl1.gt.mxvol .OR. nvl2.gt.mxvol)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many vol. ',&
         'sources -- ','NVL1 = ',nvl1,' NVL2 = ',nvl2,' MXVOL = ',mxvol
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      if(igrdvl.gt.0)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- old version of ',&
         'variable volume source file is requested.',&
         ' (Flag IGRDVL has been replaced with variale NVL2)'
         problem=.TRUE.
      endif
!
! --- Change NVOLDAT to 1 if single VOLEMARB.DAT name is provided in
!     first section of Group 0
      if(nvoldat.EQ.0 .AND. nvl2.GT.0) nvoldat=1
!
! --- Subgroup (16b) -- Read data for each volume source
!
      do i=1,nvl1
! ---    Clear temporary array for source name
         do j=1,16
            ctemp16(j)(1:1)=' '
         enddo
!
         call readin(cvdic(1,29),ivleng(1,29),ivtype(1,29),io5,io6,&
            lecho,&
          VLDATA,CTEMP16,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
          idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 source name array into CHAR*16 var. for source
         cnamvl1(i)='                '
         do j=1,16
            cnamvl1(i)(j:j)=ctemp16(j)(1:1)
         enddo
! ---    Names must be different
         if(i.GT.1) then
            do j=1,i-1
               if(cnamvl1(j).EQ.cnamvl1(i)) then
                  write(io6,*)
                  write(io6,*)'*********  FATAL  ***********'
                  write(io6,*)'ERROR in subr. READCF -- duplicate name',&
                  ' for source -- ',i,'  name = ',cnamvl1(i)
                  problem=.TRUE.
               endif
            enddo
         endif
!
! ---    Transfer the vol. source data to the common block arrays
!
! ---    Compute relative grid coordinates
         xvl1grd(i)=(vldata(1)-xorigkm)/dgridkm
         yvl1grd(i)=(vldata(2)-yorigkm)/dgridkm
!
         htvl1(i)=vldata(3)
         elvl1(i)=vldata(4)
         sy0vl1(i)=vldata(5)
         sz0vl1(i)=vldata(6)
!
! ---    Emission rates (apply units conversion factor to obtain g/s)
! ---    Odour_Units*m3/s, or Bq/s)
         lqneg=.FALSE.
         do j=1,nspec
            qvl1(j,i)=vldata(6+j)*qunits(ivlu)
            if(qvl1(j,i).LT.0.0) lqneg=.TRUE.
         enddo
! ---    Report problem if any emissions are negative
         if(lqneg) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- Invalid emission',&
            ' rate (must NOT be negative) -- Vol. Source = ',cnamvl1(i)
            problem=.TRUE.
         endif
      enddo
!
! --- Subgroup (16c) -- Variable emission scaling factors
!
! --- Method for specifying emission scale-factors changed in
! --- CALPUFF control-file dataset version TNG-3.1, denoted as
! --- cfdataset=3.1 (also applies to dataset version 7.0)
! -------------------------------
      if(cfdataset.LT.3.01) then
! --- METHOD using IVARY index --- up through dataset TNG-3.0
! -------------------------------
! --- Loop over source-species combinations
      do i=1,nsvl1
! ---    Clear temporary arrays
! ---    Note: c4_70 is not used for this dataset
         vqtemp=0.0
         do j=1,16
            ctemp16(j)(1:1)=' '
         enddo
!
! --- The following call is set for MXSPEC=35
         call readin(cvdic(1,30),ivleng(1,30),ivtype(1,30),io5,io6,&
            lecho,&
         CTEMP16,IVARY,c4_70,&
         VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),&
         VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),&
         VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),&
         VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),&
         VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),&
         VQTEMP(1,23),VQTEMP(1,24),VQTEMP(1,25),VQTEMP(1,26),&
         VQTEMP(1,27),VQTEMP(1,28),VQTEMP(1,29),VQTEMP(1,30),&
         VQTEMP(1,31),VQTEMP(1,32),VQTEMP(1,33),VQTEMP(1,34),&
         VQTEMP(1,35),&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
! ***    call readin(cvdic(1,30),ivleng(1,30),ivtype(1,30),io5,io6,
! ***1      lecho,
! ***1   CTEMP16,IVARY,c4_70,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),
! ***4   VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),
! ***5   VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),
! ***6   VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),
! ***7   VQTEMP(1,23),VQTEMP(1,24),
! ***8   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***9   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***a   idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=5
! ***    call readin(cvdic(1,30),ivleng(1,30),ivtype(1,30),io5,io6,
! ***1      lecho,
! ***1   CTEMP16,IVARY,c4_70,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***4   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***5   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7   idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 source name into CHAR*16 variable for source
         cnamsrc='                '
         do j=1,16
            cnamsrc(j:j)=ctemp16(j)(1:1)
         enddo
! ---    Scan variable emissions factors for negatives
         lqneg=.FALSE.
         do iv=1,96
            do isp=1,mxspec
               if(vqtemp(iv,isp).LT.0.0) lqneg=.TRUE.
            enddo
         enddo
! ---    Report problem if any factors are negative
         if(lqneg) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- Invalid scaling',&
          ' factor  (must NOT be negative) -- Vol. Source = ',cnamsrc
            problem=.TRUE.
         endif
! ---    Match source name to index
         call SMATCH(io6,cnamsrc,nvl1,cnamvl1,isrc)
! ---    Check IVARY
         if(ivary.LT.0 .OR. ivary.GT.5) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- type of variable',&
            ' emission factor invalid:',' type = ',ivary
            write(io6,*)'                         Valid type is 0-5'
            problem=.TRUE.
         else
! ---       Map IVARY (0-5) into corresponding KVARY index
            kvary=mapivary(ivary+1)
! ---       Find species (a value of VQTEMP will be >0.0)
            iv_loop_vl: do iv=1,96
               do isp=1,mxspec
                  if(vqtemp(iv,isp).GT.0.0) EXIT iv_loop_vl
               enddo
            enddo iv_loop_vl
! ---       Make table name
            csfvl1(i)='VL_'//TRIM(cnamsrc)//'_'//TRIM(cspec(isp))
! ---       Set pointers and scale factor table information
            nqsftab=nqsftab+1
            idsfvl1(isp,isrc)=i
            ixrefvl1(i)=nqsftab
            cqsfname(nqsftab)=csfvl1(i)
            iqsftype(nqsftab)=kvary
! ---       Place factors into array
            if(kvary.NE.6) then
               do iv=1,96
                  qsftab(iv,nqsftab)=vqtemp(iv,isp)
               enddo
            else
! ---          Distribute seasonal factors (24*4) to months (24*12)
! ---          where season 1 is DEC-JAN-FEB
               iv=0
               do imonth=1,12
                  if(imonth.EQ.12 .OR. imonth.LE.2) then
! ---                Season 1
                     jv=0
                  elseif(imonth.LE.5) then
! ---                Season 2
                     jv=24
                  elseif(imonth.LE.8) then
! ---                Season 3
                     jv=48
                  else
! ---                Season 4
                     jv=72
                  endif
                  do ihour=1,24
                     iv=iv+1
                     jv=jv+1
                     qsftab(iv,nqsftab)=vqtemp(jv,isp)
                  enddo
               enddo
            endif
         endif
      enddo
! -------------------------------
      elseif(cfdataset.GT.3.01) then
! --- METHOD using Factor Table names --- Dataset TNG-3.1 /7.0 onward
! -------------------------------
! --- Factor name is provided for each scaled source-species
! --- Loop over source-species combinations
      do i=1,nsvl1
! ---    Initialize temp array for character variables
! ---    3 strings are on line, and the total length is 70
! ---    and CTEMP16,IVARY are not used here
         c4_70=' '
         call readin(cvdic(1,30),ivleng(1,30),ivtype(1,30),io5,io6,&
            lecho,&
         CTEMP16,IVARY,c4_70,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Convert from c*4 array to c*70 string with blanks at end
         ic=0
         do k=1,70
           if(c4_70(k)(1:1).NE.' ') then
              ic=ic+1
              c70temp(ic:ic)=c4_70(k)(1:1)
           endif
         enddo
         do k=ic+1,70
            c70temp(k:k)=' '
         enddo
         klast=ic
! ---    Location of first comma
         kc1=INDEX(c70temp,',')
! ---    Location of second (last) comma
         kc2=INDEX(c70temp,',',back=.true.)
! ---    Assign source, species, factor names
         c16temp='                '
         c16temp(1:kc1-1)=c70temp(1:kc1-1)
         c12temp='            '
         c12temp(1:kc2-kc1-1)=c70temp(kc1+1:kc2-1)
         c40temp='                                        '
         c40temp(1:klast-kc2)=c70temp(kc2+1:klast)
! ---    Source index
         call SMATCH(io6,c16temp,nvl1,cnamvl1,ksrc)
! ---    Species index
         call SMATCH(io6,c12temp,nspec,cspec,kspc)
! ---    Update source arrays (except ixrefvl1(i))
         idsfvl1(kspc,ksrc)=i
         csfvl1(i)=c40temp
      enddo
      endif
! ---------------------------------------------------------------------
! --- Jump over groups if not they are not expected in
! --- older control files with receptors in group 17
      if(igroup_rec.EQ.17) goto 1017
! ---------------------------------------------------------------------
!
! ---------------------------------------
! --- Flare source information -- IG # 17
! ---------------------------------------
!
! --- Read number of flare sources
! --- (All source data currently must be provided in
! ---  arbitrarily-varying emissions file(s))
!
      call readin(cvdic(1,31),ivleng(1,31),ivtype(1,31),io5,io6,lecho,&
       NFL1,NFL2,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
      if(nfl1.GT.0)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- Control File FLARE ',&
         'source option is not available (NFL1=0) -- ',' NFL1 = ',nfl1
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      if(nfl2.GT.mxfl2)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many FLARE ',&
         'sources -- ',' NFL2 = ',nfl2,' MXFL2 = ',mxfl2
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
!
! --- Change NFLDAT to 1 if single FLEMARB.DAT name is provided in
!     first section of Group 0
      if(nfldat.EQ.0 .AND. nfl2.GT.0) nfldat=1
! --------------------------------------
! --- Road source information -- IG # 18
! --------------------------------------
!
! --- Subgroup (18a)
! ------------------
      call readin(cvdic(1,32),ivleng(1,32),ivtype(1,32),io5,io6,lecho,&
       NRD1,NRD2,NSFRDS,WQSF(1,12),TQSF(1,12),&
       idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- Verify allocations (check 1 field)
      if(nrd1.GT.0) then
        if(nrd1.GT.SIZE(srcnamrd1))then
           write(io6,*)'ERROR in subr. READCF -- too many road ',&
           'sources -- ','NRD1 = ',nrd1,' Allocated = ',SIZE(srcnamrd1)
           stop
        endif
      endif
      if(nrd2.GT.0) then
        if(nrd2.GT.SIZE(nptrd2))then
           write(io6,*)'ERROR in subr. READCF -- too many road ',&
           'sources -- ','NRD2 = ',nrd2,' Allocated = ',SIZE(nptrd2)
           stop
        endif
      else
! ---   There are no variable emission roads sources so drop any
! ---   further procesing of RDEMARB.DAT files
        nrddat=0
      endif
! --- Open scratch file for control-file road segments (conditional)
      if(NRD1.GT.0) OPEN(iordx,form='unformatted',status='scratch')
! --- Subgroup (18b) -- Read name & data for each road
! ------------------
! --- Loop over roads
      do i=1,nrd1
! ---    Initialize array for character road name
         c4_16='    '
         road_a=0.0
      call readin(cvdic(1,33),ivleng(1,33),ivtype(1,33),io5,io6,lecho,&
         C4_16,ROAD_A,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Extract string value from c*4 array
         if(c4_16(1)(1:1).NE.' ') then
! ---       Road name supplied
            srcnamrd1(i)='                '
            do j=1,16
               srcnamrd1(i)(j:j)=c4_16(j)(1:1)
            end do
         else
            write(io6,*)'ERROR in subr. READCF -- Road Name is ',&
            'missing -- Road Number = ',i
            stop
         endif
! ---    Set case
         call MK_CASE(io6,'READCF','UPPER',srcnamrd1(i))
! ---    Transfer the road data to the module arrays
         htrd1(i)=road_a(1)
         sz0rd1(i)=road_a(2)
         sy0rd1(i)=road_a(3)
! ---    Emission rates in (g/s/m) !!
         do j=1,nspec
            qrd1(j,i)=road_a(3+j)
         enddo
      enddo
! --- Subgroup (18c) -- Emissions scale-factors
! ------------------
! -------------------------------
      if(cfdataset.LT.3.01) then
! --- METHOD using TYPE string --- dataset TNG-3.0
! -------------------------------
! --- Prepare length of the source-name list and the species list
! --- variables in the data dictionary.  Assume all species may be
! --- in one assignment, and all sources.
! --- Source names are C*16, plus 1 for a comma
      mxsrcnamlen=(16+1)*nrd1
! --- Specie names are C*12, plus 1 for a comma
      mxspcnamlen=(12+1)*nspec
! --- Place into dictionary
      ivleng(1,34)=mxsrcnamlen
      ivleng(2,34)=mxspcnamlen
! --- Allocate the local transfer arrays and pointers
      ALLOCATE(c4_src(mxsrcnamlen),c4_spc(mxspcnamlen))
      ALLOCATE(ksrcnam(nrd1),kspcnam(nspec))
! --- Loop over road-species combinations
      isfrds=0
      nqsfend=nqsftab+nsfrds
      do while (isfrds < nsfrds)
! ---    Initialize temp arrays for character variables
! ---    Note: c4_70 is not used for this dataset
         c4_16='    '
         c4_src='    '
         c4_spc='    '
      call readin(cvdic(1,34),ivleng(1,34),ivtype(1,34),io5,io6,lecho,&
         c4_src,c4_spc,C4_16,qsftab(1,nqsfend),c4_70,&
         idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Scan variable emissions factors for negatives
         lqneg=.FALSE.
         do iv=1,mxqsf
            if(qsftab(iv,nqsfend).LT.0.0) lqneg=.TRUE.
         enddo
! ---    Report problem if any factors are negative
         if(lqneg) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- Invalid scaling',&
          ' factor  (must NOT be negative) -- ROAD Sources '
            problem=.TRUE.
         endif
! ---    (1) Extract pointers to list of source names provided
         ksrcnam=0
         isrc=1
         ic=0
         c16temp='                '
         do k=1,mxsrcnamlen
           if(c4_src(k)(1:1).NE.' ') then
             if(c4_src(k)(1:1).NE.',') then
! ---          Add non-blank character to current name
               ic=ic+1
               c16temp(ic:ic)=c4_src(k)(1:1)
             else
! ---          Found comma so name is complete:  match to index
               call SMATCH(io6,c16temp,nrd1,srcnamrd1,ksrcnam(isrc))
! ---          Start a new name
               isrc=isrc+1
               ic=0
               c16temp='                '
! ---          Check for too many sources
               if(isrc.GT.nrd1) then
                  write(io6,*)'ERROR in READCF:  Input Group 18c'
                  write(io6,*)'Too many sources are associated with '//&
                              'emissions scale factors'
                  write(io6,*)'Number of roads simulated:  ',nrd1
                  write(io6,*)'Check the SRCNAM list provided'
                 stop 'HALT in READCF: -- see list file'
               endif
             endif
           endif
         enddo
! ---    Last source name will not be followed by a comma
         call SMATCH(io6,c16temp,nrd1,srcnamrd1,ksrcnam(isrc))
! ---    (2) Extract pointers to list of specie names provided
         kspcnam=0
         ispc=1
         ic=0
         c12temp='            '
         do k=1,mxspcnamlen
           if(c4_spc(k)(1:1).NE.' ') then
             if(c4_spc(k)(1:1).NE.',') then
! ---          Add non-blank character to current name
               ic=ic+1
               c12temp(ic:ic)=c4_spc(k)(1:1)
             else
! ---          Found comma so name is complete:  match to index
               call SMATCH(io6,c12temp,nspec,cspec,kspcnam(ispc))
! ---          Start a new name
               ispc=ispc+1
               ic=0
               c12temp='            '
! ---          Check for too many species
               if(ispc.GT.nspec) then
                  write(io6,*)'ERROR in READCF:  Input Group 18c'
                  write(io6,*)'Too many species are associated with '//&
                              'emissions scale factors'
                  write(io6,*)'Number of species simulated:  ',nspec
                  write(io6,*)'Check the SPECIES list provided'
                 stop 'HALT in READCF: -- see list file'
               endif
             endif
           endif
         enddo
! ---    Last source name will not be followed by a comma
         call SMATCH(io6,c12temp,nspec,cspec,kspcnam(ispc))
! ---    (3) Extract and match variation type string
          do j=1,16
            c16temp(j:j)=c4_16(j)(1:1)
         enddo
         call SMATCH(io6,c16temp,nqsftype,cqsftype,kvary)
! ---    (4) Set pointers and copy current factors into source-specie
! ---    Note: factors were read into the LAST requested source-specie
         do ksrc=1,isrc
            do kspc=1,ispc
               isfrds=isfrds+1
               nqsftab=nqsftab+1
! ---          Check for too many source-species
               if(isfrds.GT.nsfrds) then
                  write(io6,*)'ERROR in READCF:  Input Group 18c'
                  write(io6,*)'Too many source-species are assigned '//&
                              'emissions scale factors'
                  write(io6,*)'Number source-species needed:  ',nsfrds
                  write(io6,*)'Check the SRCNAM & SPECIES list'
                 stop 'HALT in READCF: -- see list file'
               endif
! ---          Update table arrays
               iqsftype(nqsftab)=kvary
               qsftab(:,nqsftab)=qsftab(:,nqsfend)
! ---          Make table name
               cqsfname(nqsftab)='RD_'//&
                  TRIM(srcnamrd1(ksrcnam(ksrc)))//'_'//&
                  TRIM(cspec(kspcnam(kspc)))
! ---          Update source arrays
               idsfrds(kspcnam(kspc),ksrcnam(ksrc))=isfrds
               ixrefrds(isfrds)=nqsftab
               csfrds(isfrds)=cqsfname(nqsftab)
            enddo
         enddo
      enddo
! --- Deallocate the local transfer arrays and pointers
      DEALLOCATE(c4_src,c4_spc)
      DEALLOCATE(ksrcnam,kspcnam)
! -------------------------------
      elseif(cfdataset.GT.3.01) then
! --- METHOD using Factor Table names --- Dataset TNG-3.1 onward
! -------------------------------
! --- Factor name is provided for each scaled source-species
! --- Same dictionary entry is used here, so just list names that are
! --- not expected as idum (before the c4_70)
! --- Loop over source-species combinations
      do isfrds=1,nsfrds
! ---    Initialize temp array for character variables
! ---    3 strings are on line, and the total length is 70
         c4_70=' '
      call readin(cvdic(1,34),ivleng(1,34),ivtype(1,34),io5,io6,lecho,&
         idum,idum,idum,idum,c4_70,&
         idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Convert from c*4 array to c*70 string with blanks at end
         ic=0
         do k=1,70
           if(c4_70(k)(1:1).NE.' ') then
              ic=ic+1
              c70temp(ic:ic)=c4_70(k)(1:1)
           endif
         enddo
         do k=ic+1,70
            c70temp(k:k)=' '
         enddo
         klast=ic
! ---    Location of first comma
         kc1=INDEX(c70temp,',')
! ---    Location of second (last) comma
         kc2=INDEX(c70temp,',',back=.true.)
! ---    Assign source, species, factor names
         c16temp='                '
         c16temp(1:kc1-1)=c70temp(1:kc1-1)
         c12temp='            '
         c12temp(1:kc2-kc1-1)=c70temp(kc1+1:kc2-1)
         c40temp='                                        '
         c40temp(1:klast-kc2)=c70temp(kc2+1:klast)
! ---    Source index
         call SMATCH(io6,c16temp,nrd1,srcnamrd1,ksrc)
! ---    Species index
         call SMATCH(io6,c12temp,nspec,cspec,kspc)
! ---    Update source arrays (except ixrefrds(isfrds))
         idsfrds(kspc,ksrc)=isfrds
         csfrds(isfrds)=c40temp
      enddo
      endif
! --- Subgroup (18d) -- Read & process coordinate data for each road
! ------------------
      xmax=FLOAT(nx)
      ymax=FLOAT(ny)
      nrdseg1=0
! --- Loop over roads
      do i=1,nrd1
! ---    Initialize array for character road name again for index
         c4_16='    '
! ---    Initialize number of points for this road
         num=0
      call readin(cvdic(1,35),ivleng(1,35),ivtype(1,35),io5,io6,lecho,&
         C4_16,NUM,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Extract string value from c*4 array
         do j=1,16
            c16temp(j:j)=c4_16(j)(1:1)
         end do
! ---    Set case
         call MK_CASE(io6,'READCF','UPPER',c16temp)
! ---    Match road name to get index
         k=0
         do j=1,nrd1
            if(c16temp.EQ.srcnamrd1(j)) k=j
         enddo
         if(k.EQ.0) then
            write(io6,*)'ERROR in subr. READCF -- Road ',c16temp
            write(io6,*)'is not on the list:'
            do j=1,nrd1
               write(io6,*) srcnamrd1(j)
            enddo
            stop 'HALT in READCF:  Road Name is bad -- see list file'
         endif
         nptrd1(k)=num
! ---    Loop over points
         do j=1,nptrd1(k)
           road_b=0.0
      call readin(cvdic(1,36),ivleng(1,36),ivtype(1,36),io5,io6,lecho,&
           ROAD_B,&
           idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
           idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
           idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
           idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
           idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---      Break each road into segments no longer than 1 grid cell
! ---      and store in scratch file for ROADSEG call
           call GRIDROAD(c16temp,i,j,road_b,nrdseg1)
         enddo
      enddo
! --- Process scratch file (conditional)
      if(nrdseg1.GT.0) call ROADSEG(1)
! ---------------------------------------------------------------------
! --- Jump over groups if not they are not expected in
! --- older control files with receptors in group 19
      if(igroup_rec.EQ.20) goto 1016
! ---------------------------------------------------------------------
! ---------------------------------------
! --- Spray source information -- IG # 19
! ---------------------------------------
!
! --- Read number of spray sources
! --- (All source data currently must be provided in
! ---  arbitrarily-varying emissions file(s))
!
      call readin(cvdic(1,37),ivleng(1,37),ivtype(1,37),io5,io6,lecho,&
       NSP1,NSP2,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
      if(nsp1.GT.0)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- Control File FLARE ',&
         'source option is not available (NSP1=0) -- ',' NSP1 = ',nsp1
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
        if(nsp2.GT.SIZE(nptsp2))then
           write(io6,*)'ERROR in subr. READCF -- too many spray ',&
           'sources -- ','NSP2 = ',nsp2,' Allocated = ',SIZE(nptsp2)
           stop
        endif
!
! --- Change NSPDAT to 1 if single SPEMARB.DAT name is provided in
!     first section of Group 0
      if(nspdat.EQ.0 .AND. nsp2.GT.0) nspdat=1
! ---------------------------------------------------------------------
1016  continue
! ---------------------------------------------------------------------
! ---------------------------------------------------------------------
! --- Jump over groups if not they are not expected in
! --- older control files with receptors in group 19
      if(igroup_rec.EQ.19) goto 1017
! ---------------------------------------------------------------------
! ------------------------------------------
! --- Emission Rate Scale Factors -- IG # 20
! ------------------------------------------
! --- Subgroup (20a) -- Number of scale factor tables (NQSFTAB)
! ------------------
      call readin(cvdic(1,38),ivleng(1,38),ivtype(1,38),io5,io6,lecho,&
       nqsftab,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- Subgroup (20b) -- scale factor tables (NQSFTAB>0)
! ------------------
      if(nqsftab.GT.0) then
      do i=1,nqsftab
         c4_40='    '
         c4_16='    '
      call readin(cvdic(1,39),ivleng(1,39),ivtype(1,39),io5,io6,lecho,&
       c4_40,C4_16,qsftab(1,i),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---    (1) Extract table name string
          do j=1,40
            cqsfname(i)(j:j)=c4_40(j)(1:1)
         enddo
! ---    (2) Extract and match variation type string
          do j=1,16
            c16temp(j:j)=c4_16(j)(1:1)
         enddo
         call SMATCH(io6,c16temp,nqsftype,cqsftype,iqsftype(i))
! ---    Scan variable emissions factors for negatives
         lqneg=.FALSE.
         do j=1,mxqsf
            if(qsftab(j,i).LT.0.0) lqneg=.TRUE.
         enddo
! ---    Report problem if any factors are negative
         if(lqneg) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. READCF -- Invalid emission',&
          ' scaling factor  (must NOT be negative)'
            write(io6,*)'Scale-Factor Table:  ',i
            write(io6,*)'Table Name:  '//TRIM(cqsfname(i))
            problem=.TRUE.
         endif
      enddo
! --- Assign pointers for all source-species that are scaled
! --- Points
      do j=1,nspt1
         call SMATCH(io6,csfpt1(j),nqsftab,cqsfname,ixrefpt1(j))
      enddo
! --- Areas
      do j=1,nsar1
         call SMATCH(io6,csfar1(j),nqsftab,cqsfname,ixrefar1(j))
      enddo
! --- Lines
      do j=1,nsln1
         call SMATCH(io6,csfln1(j),nqsftab,cqsfname,ixrefln1(j))
      enddo
! --- Volumes
      do j=1,nsvl1
         call SMATCH(io6,csfvl1(j),nqsftab,cqsfname,ixrefvl1(j))
      enddo
! --- Roads
      do j=1,nsfrds
         call SMATCH(io6,csfrds(j),nqsftab,cqsfname,ixrefrds(j))
      enddo
      endif
! ---------------------------------------------------------------------
1017  continue
! ---------------------------------------------------------------------
! --------------------------------------------
! --- Discrete receptor information -- IG # 21
! --------------------------------------------
!
! --- Subgroup (21a) -- Number of discrete receptors (NREC)
! ---                   and groups (NRGRP)
      call readin(cvdic(1,40),ivleng(1,40),ivtype(1,40),io5,io6,lecho,&
       NREC,NRGRP,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
      if(nrec.gt.mxrec)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many discrete ',&
         'receptors -- NREC = ',nrec,' MXREC = ',mxrec
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      if(nrgrp.gt.mxrgrp)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many discrete ',&
         'receptor groups -- NRGRP = ',nrgrp,' MXRGRP = ',mxrgrp
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
! --- Subgroup (21b) -- Read discrete receptor group names (NRGRP>0)
      if(nrgrp.GT.0) then
         do i=1,nrgrp
            do j=1,80
               ctemp80(j)(1:1)=' '
            enddo
      call readin(cvdic(1,41),ivleng(1,41),ivtype(1,41),io5,io6,lecho,&
       ctemp80,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---       Transfer CHAR*4 species name into CHAR*80 variable
            do j=1,80
               rgrpnam(i)(j:j)=ctemp80(j)(1:1)
            enddo
         enddo
! ---    Set receptor dictionary using receptor-group names
         do k=1,nrgrp
            cvdic(k,42)=TRIM(rgrpnam(k))
            ivleng(k,42)=4
            ivtype(k,42)=1
         enddo
      else
! ---     Use 1 default group called DISCRETE
          rgrpnam(1)='DISCRETE'
      endif
! --- Subgroup (21c) -- Read data for each discrete receptor
! --- Temp array sized for MXRGRP <= 30
      if(mxrgrp.GT.30) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. READCF -- too many discrete ',&
         'receptor groups -- MXRGRP = ',mxrgrp
         write(io6,*)'Temporary array dimensioned for 30 groups'
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      do 250 i=1,nrec
! --- Initialize ht above ground as zero, and elevation as -9999.
         do k=1,30
            recwrk(3,k)=-9999.0
            recwrk(4,k)=0.0
         enddo
      call readin(cvdic(1,42),ivleng(1,42),ivtype(1,42),io5,io6,lecho,&
       RECWRK(1,1),RECWRK(1,2),RECWRK(1,3),RECWRK(1,4),RECWRK(1,5),&
       RECWRK(1,6),RECWRK(1,7),RECWRK(1,8),RECWRK(1,9),RECWRK(1,10),&
       RECWRK(1,11),RECWRK(1,12),RECWRK(1,13),RECWRK(1,14),RECWRK(1,15),&
       RECWRK(1,16),RECWRK(1,17),RECWRK(1,18),RECWRK(1,19),RECWRK(1,20),&
       RECWRK(1,21),RECWRK(1,22),RECWRK(1,23),RECWRK(1,24),RECWRK(1,25),&
       RECWRK(1,26),RECWRK(1,27),RECWRK(1,28),RECWRK(1,29),RECWRK(1,30),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum)
! ---    Identify group (valid elevation)
         if(nrgrp.GT.0) then
            do k=1,nrgrp
               if(recwrk(3,k).GT.-9000.) irgrp(i)=k
            enddo
         else
            irgrp(i)=1
         endif
!
! ---    Transfer non-gridded receptor data to the common block arrays
!
! ---    Compute relative grid coordinates
         xng(i)=(recwrk(1,irgrp(i))-xorigkm)/dgridkm
         yng(i)=(recwrk(2,irgrp(i))-yorigkm)/dgridkm
!
! ---    Receptor ground elevation
         elevng(i)=recwrk(3,irgrp(i))
!
! ---    Receptor height above ground
         zng(i)=recwrk(4,irgrp(i))
250   continue
! --- Receptor group ID is complete (if present) so reset nrgrp=1 if
! --- no groups were provided so that a default group is used
      if(nrec.GT.0 .AND. nrgrp.EQ.0) nrgrp=1
!
!
! ------------------------------------------------------
! --- Perform QA checks on selected control file inputs
! ------------------------------------------------------
      call QAINP(problem)
      if(mfog.NE.0) call QAFOG(problem)
!
! --- ALWAYS write control file confirmation to list file
!      if(LDEBUG.OR.PROBLEM)then
         write(io6,*)
         write(io6,*)'   ****  CONFIRMATION OF CONTROL DATA  ****'
         write(io6,*)
         write(io6,*)
         write(io6,*)'--------------  INPUT GROUP 1  ---------------'
         write(io6,*)
         write(io6,*)'metrun  = ',metrun
         write(io6,*)'ibyr    = ',ibyr
         write(io6,*)'ibmo    = ',ibmo
         write(io6,*)'ibdy    = ',ibdy
         write(io6,*)'ibhr    = ',ibhr
         write(io6,*)'ibsec   = ',ibsec
         write(io6,*)'ibdathr = ',ibdathr
         write(io6,*)'ieyr    = ',ieyr
         write(io6,*)'iemo    = ',iemo
         write(io6,*)'iedy    = ',iedy
         write(io6,*)'iehr    = ',iehr
         write(io6,*)'iesec   = ',iesec
         write(io6,*)'iedathr = ',iedathr
         write(io6,*)'nsecdt  = ',nsecdt
         write(io6,*)'irlg    = ',irlg
         write(io6,*)'iavg    = ',iavg
         write(io6,*)'xbtz    = ',xbtz
         write(io6,*)'abtz    = ',abtz
         write(io6,*)'nspec   = ',nspec
         write(io6,*)'nse     = ',nse
         write(io6,*)'itest   = ',itest
         write(io6,*)'metfm   = ',metfm
         write(io6,*)'mprffm  = ',mprffm
         write(io6,*)'mrestart= ',mrestart
         write(io6,*)'nrespd  = ',nrespd
         write(io6,*)'avet    = ',avet
         write(io6,*)'pgtime  = ',pgtime
         write(io6,*)'ioutu   = ',ioutu
! ---    Disable IOVERS
!        write(io6,*)'iovers  = ',iovers
!
         write(io6,*)
         write(io6,*)'--------------  INPUT GROUP 2  ---------------'
         write(io6,*)
         write(io6,*)'mgauss = ',mgauss
         write(io6,*)'mctadj = ',mctadj
         write(io6,*)'mctsg  = ',mctsg
         write(io6,*)'mslug  = ',mslug
         write(io6,*)'mtrans = ',mtrans
         write(io6,*)'mchem  = ',mchem
         write(io6,*)'maqchem = ',maqchem
         write(io6,*)'mlwc   = ',mlwc
         write(io6,*)'mwet   = ',mwet
         write(io6,*)'mdry   = ',mdry
         write(io6,*)'mevap   = ',mevap
         write(io6,*)'mtilt  = ',mtilt
         write(io6,*)'mdisp  = ',mdisp
         write(io6,*)'mdisp2 = ',mdisp2
         write(io6,*)'mturbvw = ',mturbvw
         write(io6,*)'mtauly = ',rtauly
         write(io6,*)'mtauadv= ',mtauadv
         write(io6,*)'mcturb = ',mcturb
         write(io6,*)'mrough = ',mrough
         write(io6,*)'mtip   = ',mtip
         write(io6,*)'mbdw   = ',mbdw
         write(io6,*)'mshear = ',mshear
         write(io6,*)'mrise  = ',mrise
         write(io6,*)'mrise_fl= ',mrise_fl
         write(io6,*)'mtip_fl= ',mtip_fl
         write(io6,*)'msplit = ',msplit
         write(io6,*)'mpartl = ',mpartl
         write(io6,*)'mpartlba= ',mpartlba
         write(io6,*)'mtinv  = ',mtinv
         write(io6,*)'mpdf   = ',mpdf
         write(io6,*)'msgtibl= ',msgtibl
         write(io6,*)'mbcon  = ',mbcon
         write(io6,*)'msource= ',msource
         write(io6,*)'mfog   = ',mfog
         write(io6,*)'mreg   = ',mreg
         if(mreg.EQ.1) then
           write(io6,*)'  Technical options must conform to USEPA'
           write(io6,*)'   Long Range Transport (LRT) guidance'
           write(io6,*)'        METFM    1 or 2'
           write(io6,*)'        AVET     60. (min)'
           write(io6,*)'        PGTIME   60. (min)'
           write(io6,*)'        MGAUSS   1'
           write(io6,*)'        MCTADJ   3'
           write(io6,*)'        MTRANS   1'
           write(io6,*)'        MTIP     1'
           write(io6,*)'        MCHEM    1 or 3 (if modeling SOx, NOx)'
           write(io6,*)'        MWET     1'
           write(io6,*)'        MDRY     1'
           write(io6,*)'        MDISP    2 or 3'
           write(io6,*)'        MPDF     0 if MDISP=3'
           write(io6,*)'                 1 if MDISP=2'
           write(io6,*)'        MROUGH   0'
           write(io6,*)'        MPARTL   1'
           write(io6,*)'        MPARTLBA 0'
           write(io6,*)'        SYTDEP   550. (m)'
           write(io6,*)'        MHFTSZ   0'
           write(io6,*)'        SVMIN    0.5 (m/s)'
         endif
