              write(io6,*)'line source: ',cnamln1(i),'  number: ',i
              write(io6,*)'qtl      = ',(qtl(n,i),n=1,nspec)
              write(io6,*)'arline   = ',arline(i)
              do iv=1,4
                 write(io6,*)'[x,y]vertl   = ',xvertl(iv,i),yvertl(iv,i)
              enddo
              if(nsln1.GT.0)then
                do is=1,nspec
                  k=idsfln1(is,i)
                  if(k.GT.0) then
                   kk=ixrefln1(k)
                   write(io6,*)cspec(is)//'Emission Factor Type: '//&
                               cqsftype((iqsftype(kk)))
                   ncol=nqsfcol(iqsftype(kk))
                   do nrow=1,nqsfrow(iqsftype(kk))
                     n1=(nrow-1)*ncol+1
                     n2=n1+ncol-1
                     write(io6,*)'Index ',n1,' to ',n2,&
                                 ' Emission Factor = ',&
                                 (qsftab(n,kk),n=n1,n2)
                   enddo
                  endif
                enddo
              endif
            enddo
         endif
!
         write(io6,*)
         write(io6,*)'--------------  INPUT GROUP 16  ---------------'
         write(io6,*)
         write(io6,*)'nvl1     = ',nvl1
         write(io6,*)'ivlu     = ',ivlu,'  units = ',qnam1(ivlu)
         write(io6,*)'      converted to g/s, odour_units*m3/s, or Bq/s'
         write(io6,*)'      by factor: ',qunits(ivlu)
         write(io6,*)'nsvl1    = ',nsvl1
         write(io6,*)'nvl2     = ',nvl2
         write(io6,*)
         if(nvl1.gt.0)then
            write(io6,*)'cnamvl1  = ',(cnamvl1(n),n=1,nar1)
            write(io6,*)'xvl1grd  = ',(xvl1grd(n),n=1,nvl1)
            write(io6,*)'yvl1grd  = ',(yvl1grd(n),n=1,nvl1)
            write(io6,*)'htvl1    = ',(htvl1(n),n=1,nvl1)
            write(io6,*)'elvl1    = ',(elvl1(n),n=1,nvl1)
            write(io6,*)'sy0vl1   = ',(sy0vl1(n),n=1,nvl1)
            write(io6,*)'sz0vl1   = ',(sz0vl1(n),n=1,nvl1)
         endif
         do i=1,nvl1
            write(io6,*)
            write(io6,*)'volume source: ',cnamvl1(i),'  number: ',i
            write(io6,*)'qvl1     = ',(qvl1(n,i),n=1,nspec)
            if(nsvl1.GT.0)then
              do is=1,nspec
                k=idsfvl1(is,i)
                if(k.GT.0) then
                   kk=ixrefvl1(k)
                   write(io6,*)cspec(is)//'Emission Factor Type: '//&
                               cqsftype((iqsftype(kk)))
                   ncol=nqsfcol(iqsftype(kk))
                   do nrow=1,nqsfrow(iqsftype(kk))
                     n1=(nrow-1)*ncol+1
                     n2=n1+ncol-1
                     write(io6,*)'Index ',n1,' to ',n2,&
                                 ' Emission Factor = ',&
                                 (qsftab(n,kk),n=n1,n2)
                   enddo
                endif
              enddo
            endif
         enddo
!
         write(io6,*)
         write(io6,*)'--------------  INPUT GROUP 17  ---------------'
         write(io6,*)
         write(io6,*)'nfl2     = ',nfl2
!
         write(io6,*)
         write(io6,*)'--------------  INPUT GROUP 18  ---------------'
         write(io6,*)
         write(io6,*)'nrd1     = ',nrd1
         write(io6,*)'nrd2     = ',nrd2
         write(io6,*)'nsfrds   = ',nsfrds
         if(nsfrds.GT.0)then
            write(io6,*)'wqsf     = ',(wqsf(n,12),n=1,5)
            write(io6,*)'tqsf     = ',(tqsf(n,12),n=1,11)
         endif
         write(io6,*)
         if(nrd1.GT.0)then
            write(io6,*)'srcnamrd1= ',(srcnamrd1(n),n=1,nrd1)
            write(io6,*)'htrd1    = ',(htrd1(n),n=1,nrd1)
            write(io6,*)'sz0rd1   = ',(sz0rd1(n),n=1,nrd1)
            write(io6,*)'sy0rd1   = ',(sy0rd1(n),n=1,nrd1)
            do j=1,nrd1
              write(io6,*)
              write(io6,*)'Road: ',srcnamrd1(j)
              write(io6,*)'qrd1     = ',(qrd1(n,j),n=1,nspec)
              write(io6,*)'nptrd1   = ',nptrd1(j)
              if(nsfrds.GT.0)then
                do k=1,nspec
                  kj=idsfrds(k,j)
                  if(kj.GT.0) then
                     kk=ixrefrds(kj)
                     write(io6,*)cspec(k)//'Emission Factor Type: '//&
                                 cqsftype((iqsftype(kk)))
                     ncol=nqsfcol(iqsftype(kk))
                     do nrow=1,nqsfrow(iqsftype(kk))
                       n1=(nrow-1)*ncol+1
                       n2=n1+ncol-1
                       write(io6,*)'Index ',n1,' to ',n2,&
                                   ' Emission Factor = ',&
                                   (qsftab(n,kk),n=n1,n2)
                     enddo
                  endif
                enddo
              endif
            enddo
            write(io6,*)
            write(io6,*)'Road segments for generating emissions'
            write(io6,*)'Road                x(grd)      y(grd)'//&
                 '      z(mMSL) to      x(grd)      y(grd)'//&
                 '      z(mMSL)   Length(m)'
            do i=1,nrdseg1
              write(io6,'(a16,2f12.5,f12.2,4x,2f12.5,2f12.2)')&
                          srcnamrd1(iroad1(i)),xrd1grd(1,i),&
                          yrd1grd(1,i),elrd1(1,i),xrd1grd(2,i),&
                          yrd1grd(2,i),elrd1(2,i),rdlen1(i)
           enddo
         endif
!
         write(io6,*)
         write(io6,*)'--------------  INPUT GROUP 20  ---------------'
         write(io6,*)
         write(io6,*)'nrec     = ',nrec
         write(io6,*)'nrgrp    = ',nrgrp
         if(nrec.gt.0)then
            write(io6,*)'xng          yng          zng'//&
                        '          elevng        group'
            do k=1,nrec
               write(io6,*) xng(k),yng(k),zng(k),elevng(k),&
                            TRIM(rgrpnam(irgrp(k)))
            enddo
         endif
!      endif
! --- Terminate run if QA detected a fatal flaw in control file
      if(PROBLEM) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'*******************************************'
         write(io6,*)'FATAL error(s) found in control file ---'
         write(io6,*)'Scan this file for FATAL/ERROR/WARNING '
         write(io6,*)'messages.'
         write(*,*)
         write(*,*)'*********  FATAL  ***********'
         write(*,*)'FATAL error in control file ---'
         write(*,*)
         stop 'Halted in READCF -- see list file.'
      endif
      return
      end
