!                                 screen for negative background concs
! --- V5.3-V5.4    000602  (DGS): use QAFAIL when testing MREG vars
!                  000602  (DGS): check NSPLITH input
! --- V5.3-V5.3    991222b (DGS): Check FMFPT1 range
! --- V5.2-V5.3    991222a (DGS): Check MTURBVW range
! --- V5.1-V5.2    991104  (DGS): Compare nx,ny against mxnx,mxny
! --- V5.0-V5.1    990625b (DGS): Add PGTIME to AVET check
! --- V5.0-V5.0    990228a (DGS): METRUN=0 if multiple CALMET.DAT files
!                                 are used
! --- V5.0-V5.0    990228  (DGS): Add QA check for CDIV
! --- V5.0-V5.0    990130  (JSS): Regulatory option with latest (1/99) 
!                                 recommendations
! --- V5.0-V5.0    980515  (DGS): Must select METFM=1 if MSGTIBL=1
! --- V5.0-V5.0    980304  (DGS): Must have valid Lat/Lon with MCHEM=3
! --- V4.0-V5.0    971107  (DGS): Restrict selection of strain-based
!                                 terrain treatment until finalized
!                  971107  (DGS): Restrict selection of PDF to
!                                 turbulence-based dispersion
!                  971107  (DGS): Add check for MTINV=1 when MPARTL=0
!
! --- INPUTS:
!        PROBLEM - logical    - Flag indicating if FATAL flaw is found
!                               (initialized in calling routine)
!
!     Common block /BCS/ variables:
!        HTMINBC, RSAMPBC
!     Common block /CHEMDAT/ variables:
!        MOZ, BCKO3M(12), MH2O2, BCKH2O2M(12),
!        BCKNH3M(12), MNH3, MAVGNH3, BCKNH3ZM(12,MXNZ),
!        NDECAY, DKRATE(mxspec), DKYIELD(mxspec,mxspec)
! --- 6.42_x1.1 140521
!        RH_ISRP,SO4_ISRP
!     Common block /COMPARM/ variables:
!        CDIV, NSPLITH, CNSPLITH, MXAGEHR, SVMIN(6,2), SWMIN(6,2)
!        NLUTIBL, MXSAM
!     Common block /CTSGDAT/ variables:
!        MHILL, NHILL, MCTREC
!     Common block /DATEHR/ variables:
!        XBTZ
!     Common block /DISPDAT/ variables:
!        SYTDEP, AVET, PGTIME
!     Common block /DRYDEP/ variables:
!        IDRYFLG
!     Common block /DRYPART/ variables:
!        GSIG
!     Common block /filnam/ variables:
!        NMETDATALL, NMETDOM
!     Common block /FLAGS/ variables:
!        MREG, MGAUSS, MCTADJ, MTRANS, MTIP, MCHEM, MWET, MDRY, MDISP,
!        MDISP2, MTURBVW, MROUGH, MPARTL, MPDF, MCTSG, MHFTSZ, MTINV,
!        MSGTIBL, MSPLIT, MAQCHEM, MBDW, MBCON, MDEPBC, MTILT,
!        MCTURB, MTAULY, MPARTLBA, MRISE, MLWC, MRISE_FL, MTIP_FL
!     Common block /GEN/ variables:
!        METFM, CSPEC(mxspec), METRUN, MPRFFM
!     Common block /GRID/ variables:
!        NX, NY, NZ, ZFACE(mxnzp1)
!     Common block /MAP/ variables:
!        lutm,llcc,lps,lem,llaza,lttm,
!        iutmzn,feast,fnorth,
!        rnlat0,relon0,rlat0,rlon0,xlat1,xlat2,
!        pmap,utmhem,datum,daten,clat0,clon0,clat1,clat2
!     Common block /METHD/ variables:
!        XLATIN, XLONIN
!     Common block /NONGRD/
!        NREC, XNG(mxrec), YNG(mxrec)
!     Common block /OUTPT/ variables:
!        MSOURCE, INRISE, IPFTRAK
!        IOUTU, IPRTU
!     Common block /PT1/ variables:
!        NPT1, FMFPT1(mxpt1), IDOWNW(mxpt1), ZPLATPT1(mxpt1)
!     |MROAD1| variables:
!        NRD1
!     |MROAD2| variables:
!        NRD2
!     Parameters:
!           MXSPEC, MXNZP1, MXPT1, IO6
!
! --- OUTPUT:
!        PROBLEM - logical    - Flag indicating if FATAL flaw is found
!
!     (Common block output only if a non-fatal fault is found with 
!      input data)
!     Common block /COMPARM/ variables:
!        CDIV
!     Common block /GRID/ variables:
!        NZ, ZFACE(mxnzp1)
!     Common block /CTSGDAT/ variables:
!        NHILL, MCTREC
!     Common block /FLAGS/ variables:
!        MTINV
!
! --- QAINP called by:  READCF
! --- QAINP calls:      LRSAME
!----------------------------------------------------------------------
! --- Modules
      use mroad1
      use mroad2
      use mspray2
!
! --- Include parameter statements
      include 'params.puf'
!
! --- Include common blocks
      include 'bcs.puf'
      include 'chemdat.puf'
      include 'comparm.puf'
      include 'ctsgdat.puf'
      include 'datehr.puf'
      include 'dispdat.puf'
      include 'drydep.puf'
      include 'drypart.puf'
      include 'evapdat.puf'
      include 'filnam.puf'
      include 'flags.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'map.puf'
      include 'methd.puf'
      include 'nongrd.puf'
      include 'outpt.puf'
      include 'pt1.puf'
      include 'pt2.puf'
      include 'ar1.puf'
      include 'ar2.puf'
      include 'ln1.puf'
      include 'ln2.puf'
      include 'fl1.puf'
      include 'fl2.puf'
      include 'vol1.puf'
      include 'vol2.puf'
!
      logical problem,qafail,warn
      logical lrsame, ldropflg
! --- Set warning logical
      warn=.FALSE.
! --- Check for regulatory option consistency
      if(mreg.lt.0.or.mreg.gt.1)then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Regulatory options available are currently '
         write(io6,*)'--  limited to two choices (0=not used or      '
         write(io6,*)'--  1=USEPA/IWAQM) -- Invalid value selected.   '!   
         write(io6,*)'--  MREG =  ',mreg
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --------------------------------------------------------------------
! --- Check that regulatory options for MREG=1 (USEPA/IWAQM) have been
! --- selected
!---------------------------------------------------------------------
!
      qafail=.FALSE.
      if(mreg.eq.1)then
!
! ---    Use CALMET or ISCMET meteorological data
         if(metfm.ne.1 .and. metfm.ne.2)then
            write(io6,*)
            write(io6,*)'METFM does not match regulatory default value'
            write(io6,*)'METFM = ',metfm,' Reg. default = 1 or 2'
            qafail=.true.
         endif
!
! ---    Averaging time = 60 minutes
         iavetim=nint(avet)
         ipgtim=nint(pgtime)
         if(iavetim.ne.60)then
            write(io6,*)
            write(io6,*)'AVET does not match regulatory default value'
            write(io6,*)'AVET = ',avet,' Reg. default = 60.'
            write(io6,*)'IAVETIM = ',iavetim
            qafail=.true.
         endif
         if(ipgtim.ne.60)then
            write(io6,*)
            write(io6,*)'PGTIME does not match regulatory value'
            write(io6,*)'PGTIME = ',pgtime,' Reg. default = 60.'
            write(io6,*)'IPGTIM = ',ipgtim
            qafail=.true.
         endif
!
! ---    Gaussian vertical distribution
         if(mgauss.ne.1)then
            write(io6,*)
            write(io6,*)'MGAUSS does not match regulatory default value'
            write(io6,*)'MGAUSS = ',mgauss,' Reg. default = 1'
            qafail=.true.
         endif
!
! ---    Terrain treatment
         if(mctadj.ne.3)then
            write(io6,*)
            write(io6,*)'MCTADJ does not match regulatory default value'
            write(io6,*)'MCTADJ = ',mctadj,' Reg. default = 3'
            qafail=.true.
         endif
!
! ---    Transitional plume rise
         if(mtrans.ne.1)then
            write(io6,*)
            write(io6,*)'MTRANS does not match regulatory default value'
            write(io6,*)'MTRANS = ',mtrans,' Reg. default = 1'
            qafail=.true.
         endif
!
! ---    Stack tip downwash
         if(mtip.ne.1)then
            write(io6,*)
            write(io6,*)'MTIP does not match regulatory default value'
            write(io6,*)'MTIP = ',mtip,' Reg. default = 1'
            qafail=.true.
         endif
!
! ---    Briggs plume rise
         if(mrise.ne.1)then
            write(io6,*)
            write(io6,*)'MRISE does not match regulatory default value'
            write(io6,*)'MRISE = ',mrise,' Reg. default = 1 (Briggs)'
            qafail=.true.
         endif
!
! ---    SOx and NOx chemistry -- if SO2 is being modeled, then MCHEM must 
! ---    be set to use MESOPUFF II chemistry
! ---    NOTE: species ordering (SO2, SO4, NOx, HNO3, NO3 is enforced 
! ---          elsewhere)
         if(CSPEC(1).eq.'SO2')then
            if(mchem.ne.1 .and. mchem.ne.3)then
               write(io6,*)
               write(io6,*)'MCHEM does not match regulatory default',&
               ' value'
               write(io6,*)'MCHEM = ',mchem,' Reg. default = 1 or 3'
               qafail=.true.
            endif 
         endif
!
! ---    Wet deposition
         if(mwet.ne.1)then
            write(io6,*)
            write(io6,*)'MWET does not match regulatory default value'
            write(io6,*)'MWET = ',mwet,' Reg. default = 1'
            qafail=.true.
         endif
!
! ---    Dry deposition
         if(mdry.ne.1)then
            write(io6,*)
            write(io6,*)'MDRY does not match regulatory default value'
            write(io6,*)'MDRY = ',mdry,' Reg. default = 1'
            qafail=.true.
         endif
!
! ---    Dispersion Coefficients
         if(mdisp.ne.2 .and. mdisp.ne.3)then
            write(io6,*)
            write(io6,*)'MDISP does not match regulatory default value'
            write(io6,*)'MDISP = ',mdisp,' Reg. default = 2 or 3'
            qafail=.true.
         endif
!
! ---    PDF option
         if(mdisp.eq.2 .and. mpdf.ne.1)then
            write(io6,*)
            write(io6,*)'MPDF does not match regulatory default value'
            write(io6,*)'MPDF = ',mpdf,' Reg. default = 1 when MDISP=2'
            qafail=.true.
         endif
!
! ---    Roughness adjustment
         if(mrough.ne.0)then
            write(io6,*)
            write(io6,*)'MROUGH does not match regulatory default value'
            write(io6,*)'MROUGH = ',mrough,' Reg. default = 0'
            qafail=.true.
         endif
!
! ---    Partial plume penetration
         if(mpartl.ne.1)then
            write(io6,*)
            write(io6,*)'MPARTL does not match regulatory default value'
            write(io6,*)'MPARTL = ',mpartl,' Reg. default = 1'
            qafail=.true.
         endif
! ---    No partial plume penetration on buoyant area sources
         if(mpartlba.ne.0)then
            write(io6,*)
            write(io6,*)'MPARTLBA does not match regulatory default'
            write(io6,*)'MPARTLBA = ',mpartlba,' Reg. default = 0'
            qafail=.true.
         endif
!
! ---    Heffter growth for sigma y
         idist=nint(sytdep)
         if(idist.ne.550)then
            write(io6,*)
            write(io6,*)'SYTDEP does not match regulatory default value'
            write(io6,*)'SYTDEP = ',sytdep,' Reg. default = 550.'
            write(io6,*)'IDIST  = ',idist
            qafail=.true.
         endif
!
! ---    Heffter growth for sigma z
         if(mhftsz.ne.0)then
            write(io6,*)
            write(io6,*)'MHFTSZ does not match regulatory default value'
            write(io6,*)'MHFTSZ = ',mhftsz,' Reg. default = 0'
            qafail=.true.
         endif
!
! ---    Minimum sigma-v values over water and land must be 0.5
         isv=0
         do k2=1,2
           do k=1,6
             if(.not.LRSAME(0.0001,svmin(k,k2),0.5)) isv=isv+1
           enddo
         enddo
         if(isv.GT.0) then
            write(io6,*)
            write(io6,*)'SVMIN does not match regulatory default value'
            write(io6,*)'Reg. default = 0.5 m/s'
            write(io6,*)'Over Land = ',(svmin(k,1),k=1,6)
            write(io6,*)'Overwater = ',(svmin(k,2),k=1,6)
            qafail=.true.
         endif
!
      endif
!
! --- Summary message for problems with regulatory options selected
      if(qafail) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Technical options conflict with the      --'
         write(io6,*)'--  regulatory option selected               --'
         write(io6,*)'--  MREG =  ',mreg
         write(io6,*)'                                               '
         write(io6,*)'--  Reset switches listed above to regulatory  '
         write(io6,*)'--  values and re-run model                    '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --------------------------------------------------------------------
! --- End of MREG=1 regulatory option checks
!---------------------------------------------------------------------
!
! --- Test for valid PMAP (restricted set until wind rotation
! --- and map factor codes are implemented)
      if(lutm.OR.llcc.OR.lttm) then
!        OK
      else
         write(io6,*)
         write(io6,*) 'QAINP:  Error in Input Group 4'
         write(io6,*) 'Unacceptable PMAP             = ',pmap
         write(io6,*) 'PMAP must be UTM,LCC,TTM'
         problem=.TRUE.
      endif
!c --- Test for valid PMAP
!      if(lutm.OR.llcc.OR.lps.OR.lem.OR.llaza.OR.lttm) then
!c        OK
!      else
!         write(io6,*)
!         write(io6,*) 'QAINP:  Error in Input Group 4'
!         write(io6,*) 'Unknown PMAP                  = ',pmap
!         write(io6,*) 'PMAP must be UTM,LCC,PS,EM,LAZA,TTM'
!         problem=.TRUE.
!      endif
!
! --- Test for valid IUTMZN
      if((iutmzn.LT.1 .OR. iutmzn.GT.60) .AND. LUTM) then
         write(io6,*)
         write(io6,*) 'QAINP:  Error in Input Group 4'
         write(io6,*) 'IUTMZN out of range           = ',iutmzn
         write(io6,*) 'IUTMZN should be 1 to 60'
         problem=.TRUE.
      endif
!
! --- Test for valid UTMHEM
      if((utmhem.NE.'N   '.AND.utmhem.NE.'S   ') .AND. LUTM) then
         write(io6,*)
         write(io6,*) 'QAINP:  Error in Input Group 4'
         write(io6,*) 'UTMHEM out of range           = ',utmhem
         write(io6,*) 'UTMHEM should be N or S'
         problem=.TRUE.
      endif
!
! --- Test for invalid DATUM
      if(datum.EQ.'UNKNOWN ' .OR. datum.EQ.'unknown ') then
         write(io6,*)
         write(io6,*) 'QAINP:  Error in Input Group 4'
         write(io6,*) 'DATUM must not be UNKNOWN'
         problem=.TRUE.
      endif
!
! --- Test for lat/lon of origin for LCC/PS/EM/LAZA/TTM map projection
      if(LLCC .or. LPS .or. LEM .or. LLAZA .or. LTTM) then
         if(rnlat0 .LT. -900.0 .OR. relon0 .LT. -900.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'Missing lat/lon of origin for ',pmap
            problem=.TRUE.
         elseif(rnlat0 .LT. -90.0 .OR. rnlat0 .GT. 90.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'RLAT0 (as N) out of range     = ',rnlat0
            write(io6,*) '|RLAT0| should be 0 to 90 degrees'
            problem=.TRUE.
         elseif(relon0 .LT. -180.0 .OR. relon0 .GT. 180.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'RLON0 (as E) out of range     = ',relon0
            write(io6,*) '|RLON0| should be 0 to 180 degrees'
            problem=.TRUE.
         endif
      endif
!
! --- Test for matching latitudes for LCC map projection
      if(LLCC) then
         if(xlat1 .LT. -900.0 .OR. xlat2 .LT. -900.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'Missing matching lats for ',pmap
            problem=.TRUE.
         elseif(xlat1 .LT. -90.0 .OR. xlat1 .GT. 90.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'XLAT1 (as N) out of range     = ',xlat1
            write(io6,*) '|XLAT1| should be 0 to 90 degrees'
            problem=.TRUE.
         elseif(xlat2 .LT. -90.0 .OR. xlat2 .GT. 90.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'XLAT2 (as N) out of range     = ',xlat2
            write(io6,*) '|XLAT2| should be 0 to 90 degrees'
            problem=.TRUE.
         endif
      endif
!
! --- Test for matching latitudes for PS/EM map projection
      if(LPS .or. LEM) then
         if(xlat1 .LT. -900.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'Missing matching lats for ',pmap
            problem=.TRUE.
         elseif(xlat1 .LT. -90.0 .OR. xlat1 .GT. 90.0) then
            write(io6,*)
            write(io6,*) 'QAINP:  Error in Input Group 4'
            write(io6,*) 'XLAT1 (as N) out of range     = ',xlat1
            write(io6,*) '|XLAT1| should be 0 to 90 degrees'
            problem=.TRUE.
         endif
      endif
!
! --- Check dimensions of met grid (note: mxnz already encountered as
! --- ZFACE heights are read in
      if(nx.GT.mxnx .OR. ny.GT.mxny) then
         write(io6,*)'ERROR in subr. QAINP -- Met Grid size exceeds ',&
               'size set by parameters in PARAMS.PUF (mxnx,mxny),',&
               'so revise PARAMS.PUF and recompile model.'
         write(io6,*)'    NX,NY = ',nx,ny
         write(io6,*)'MXNX,MXNY = ',mxnx,mxny
         problem=.TRUE.
      endif
! --- Check that discrete receptors are within the computational grid
      nrecbad=0
      ireclo=ibcomp-1
      jreclo=jbcomp-1
      irechi=iecomp
      jrechi=jecomp
      do k=1,nrec
         if(xng(k).LT.ireclo .OR. xng(k).GT.irechi .OR.&
            yng(k).LT.jreclo .OR. yng(k).GT.jrechi) nrecbad=nrecbad+1
      enddo
      if(nrecbad.GT.0) then
         write(io6,*)'ERROR in subr. QAINP -- discrete receptors ',&
               'lie outside of the computational grid'
         write(io6,*)'Easting grid cell range  = ',IBCOMP,IECOMP
         write(io6,*)'Northing grid cell range = ',JBCOMP,JECOMP
         write(io6,*)'Number outside range     = ',nrecbad
         problem=.TRUE.
      endif
!
! --- Check momentum adjustment factors for point sources (allow only
! --- 0.0 or 1.0 for now)
      fmfeps=0.01
      do i=1,npt1
         adel0=ABS(fmfpt1(i))
         adel1=ABS(fmfpt1(i)-1.0)
         if(adel0.LT.fmfeps) then
            fmfpt1(i)=0.0
         elseif(adel1.LT.fmfeps) then
            fmfpt1(i)=1.0
         else
            write(io6,*)'ERROR in subr. QAINP -- FMFAC values for all',&
                  ' point sources (Subgroup 13b) must be either 0 or 1'
            write(io6,*)'    Source #, FMFAC = ',i,fmfpt1(i)
            problem=.TRUE.
         endif
      enddo
!
! --- METFM is 1 - 5
      if(metfm.LT.1 .OR. metfm.GT.5) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Invalid selection for METFM option       --'
         write(io6,*)'--  Expected METFM = 1 to 5                  --'
         write(io6,*)'--  Found METFM = ',metfm
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Check for use of "1-layer" meteorological data (METMF=2,3) and
! --- impose a default 1-layer grid system if more than one layer
! --- is specified
      if(metfm.EQ.2 .OR. metfm.EQ.3) then
         if(nz.GT.1) then
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'------------------------------------------'
            write(io6,*)'           QA ALERT  !! '
            write(io6,*)'------------------------------------------'
            write(io6,*)'--  Only 1 layer is used for METFM=2,3  --'
            write(io6,*)'--  Single-layer grid system over-rides --'
            write(io6,*)'--  your choice of NZ =  ',nz
            zface(2)=zface(nz+1)
            nz=1
            write(io6,*)'    NZ    = ',nz
            n2=nz+1
            write(io6,*)'    ZFACE   = ',(zface(n),n=1,n2)
            write(io6,*)'------------------------------------------'
            write(io6,*)
            warn=.TRUE.
         endif
      endif
!
! --- CTSG data may have been provided regardless of the control MCTSG
! --- Make sure that NO CTSG calculations are done if MCTSG=0
      if(mctsg.EQ.0) then
         if(nhill.GT.0 .OR. nctrec.GT.0) then
            nhill=0
            nctrec=0
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'------------------------------------------'
            write(io6,*)'           QA ALERT  !! '
            write(io6,*)'------------------------------------------'
            write(io6,*)'--  CTSG hill information or receptor   --'
            write(io6,*)'--  information was found but MCTSG=0   --'
            write(io6,*)'--  Number of hills and CTSG receptors  --'
            write(io6,*)'--  are reset to zero                   --'
            write(io6,*)'------------------------------------------'
            write(io6,*)
            warn=.TRUE.
         endif
      else
!
! ---    CTSG is used, so make sure hills and receptors are provided!
         if((nhill.EQ.0 .OR. nctrec.EQ.0) .AND. mhill.EQ.2) then
            write(io6,*)'ERROR in subr. QAINP -- NHILL and NCTREC ',&
            'must be > 0 when CTSG is used  -- NHILL = ',nhill,&
            ' NCTREC = ',nctrec, ' MHILL = ',mhill
            problem=.TRUE.
         endif
         if(mhill.NE.1 .AND. mhill.NE.2)then
            write(io6,*)'ERROR in subr. QAINP -- MHILL must be ',&
            'specified as 1 or 2 when CTSG option is used (MCTSG=1)'
            write(io6,*)'MCTSG = ',mctsg,' MHILL = ',mhill
         problem=.TRUE.
         endif
      endif
!
! --- Make sure that METFM=1 to fill gridded terrain array when
! --- the CALPUFF terrain adjustment is selected
      if(mctadj.EQ.2 .AND. metfm.NE.1)then
         write(io6,*)'ERROR in subr. QAINP -- METFM must be ',&
         'specified as 1 when CALPUFF terrain adjustment option ',&
         'is used (MCTADJ=2)'
         write(io6,*)'MCTADJ = ',mctadj,' METFM = ',metfm
         problem=.TRUE.
      endif
!
! --- Make sure that METFM=1 to resolve land use when
! --- the sub-grid TIBL module is selected
      if(msgtibl.EQ.1 .AND. metfm.NE.1)then
         write(io6,*)'ERROR in subr. QAINP -- METFM must be ',&
         'specified as 1 (CALMET) when Sub-Grid TIBL option ',&
         'is used (MSGTIBL=1)'
         write(io6,*)'MSGTIBL = ',msgtibl,' METFM = ',metfm
         problem=.TRUE.
      endif
!
! --- Check to see that turbulence data are available for MDISP=1,5
! --- and that a valid alternate dispersion option is selected
      if(mdisp.eq.1 .OR. mdisp.EQ.5) then
         if(mdisp2.LT.2 .OR. mdisp2.GT.4) then
            write(io6,*)'ERROR in subr. QAINP -- Back-up method for ',&
            'computing sigmas when turbulence data are missing must ',&
            'not require turbulence data! ( use MDISP2=2,3,4 )'
            write(io6,*)' MDISP, MDISP2 = ',mdisp,mdisp2
            problem=.TRUE.
         endif
         if(mturbvw.LT.1 .OR. mturbvw.GT.4) then
            write(io6,*)'ERROR in subr. QAINP -- Invalid choice for ',&
            'source of turbulence data! ( use MTURBVW=1,2,3,4 )'
            write(io6,*)' MTURBVW = ',mturbvw
            problem=.TRUE.
         endif
         if(metfm.NE.4 .AND. metfm.NE.5) then
            if(mturbvw.EQ.4 .AND. metfm.NE.3) then
               write(io6,*)'ERROR in subr. QAINP -- PROFILE.DAT must ',&
               'be selected (MTURBVW=1,2,3) for turbulence data needed',&
               'for MDISP=1,5 when met option METFM=1,2 is used.'
               write(io6,*)'METFM = ',metfm,' MDISP = ',mdisp,&
               ' MTURBVW = ',mturbvw
               problem=.TRUE.
            endif
         endif
      endif
! --- MCTURB is 1 or 2
      if(mcturb.LT.1 .OR. mcturb.GT.2) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Invalid selection for MCTURB option      --'
         write(io6,*)'--  Expected MCTURB = 1 or 2                 --'
         write(io6,*)'--  Found MCTURB = ',mcturb
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
! --- MTAULY is 0,1 or 10
      if(mtauly.NE.0 .AND. mtauly.NE.1 .AND. mtauly.LE.10) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Invalid selection for MTAULY option      --'
         write(io6,*)'--  Expected MTAULY = 0,1 or >10             --'
         write(io6,*)'--  Found MTAULY = ',mtauly
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      elseif(mtauly.NE.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Diagnostic selection for MTAULY option   --'
         write(io6,*)'--  MTAULY = 0 for regulatory applications   --'
         write(io6,*)'--  Found MTAULY = ',mtauly
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         warn=.TRUE.
      endif
!
! --- MPRFFM is 1 or 2
      if(mprffm.LT.1 .OR. mprffm.GT.2) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Invalid selection for MPRFFM option      --'
         write(io6,*)'--  Expected MPRFFM = 1, or 2                --'
         write(io6,*)'--  Found MPRFFM = ',mprffm
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Check/reset type of turbulence data in PROFILE.DAT file
      if(metfm.EQ.5 .OR. mprffm.EQ.2)then
         if(isigmav.NE.0) then
         isigmav=0
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  AERMET PROFILE.DAT file always has  --'
         write(io6,*)'--  sigma-theta rather than sigma-v, so --'
         write(io6,*)'--  ISIGMAV is reset to 0 here.         --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         warn=.TRUE.
         endif
      endif
!
! --- Restrict selection of PDF Option to MDISP=1,2
      if(mpdf.EQ.1 .AND. mdisp.GT.2) then
         write(io6,*)'ERROR in subr. QAINP -- PDF option is only',&
         'available for turbulence-based dispersion (MDISP=1,2) '
         write(io6,*)'MPDF = ',mpdf,' MDISP = ',mdisp
         problem=.TRUE.
      endif
! --- Check partial penetration selections
      qafail=.FALSE.
      if(mpartl.LT.0 .OR. mpartl.GT.1)  qafail=.TRUE.
      if(mpartlba.LT.0 .OR. mpartlba.GT.1)  qafail=.TRUE.
      if(QAFAIL) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Partial penetration option must be 0 or 1--'
         write(io6,*)'--  Found MPARTL = ',mpartl
         write(io6,*)'--  and MPARTLBA = ',mpartlba
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Inversion strength above CBL is needed only when partial
! --- penetration option is selected
      if(mtinv.EQ.1 .AND. (mpartl.EQ.0 .AND. mpartlba.EQ.0)) then
         mtinv=0
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  Temperature inversion data are not  --'
         write(io6,*)'--  used if partial penetration option  --'
         write(io6,*)'--  is not selected (MPARTL=MPARTLBA=0) --'
         write(io6,*)'--  so MTINV is reset from 1 to 0 here. --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         warn=.TRUE.
      endif
!
! --- Check for valid lat/lon for met stations when MCHEM=3,6,7 (RIVAD)
! --- This test only determines if control file contains lat/lon
! --- information consistent with the choice of met data, it does
! --- not verify that data provided are correct
      qafail=.FALSE.
      if(mchem.EQ.3 .OR. mchem.EQ.6 .OR. mchem.EQ.7) then
         if(metfm.NE.1) then
! ---       Require lat/lon in Group 12 for single-pt met station
            if(xlatin.LT.-900.) qafail=.TRUE.
            if(xlonin.LT.-900.) qafail=.TRUE.
         endif
      endif
      if(QAFAIL) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Option MCHEM = 3,6,7 requires lat/lon for--'
         write(io6,*)'--  met station(s) which must be entered in  --'
         write(io6,*)'--  Group 12.  Provide XLATIN,XLONIN for     --'
         write(io6,*)'--  single-pt met station if METFM = 2,3,4   --'
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
!c --- Aqueous phase chemistry option is not active here
!      if(maqchem.NE.0) then
!         write(io6,*)
!         write(io6,*)
!         write(io6,*)
!         write(io6,*)'-----------------------------------------------'
!         write(io6,*)'          FATAL FLAW  !! '
!         write(io6,*)'-----------------------------------------------'
!         write(io6,*)'--  Option MAQCHEM is not available in this  --'
!         write(io6,*)'--  version.  Reset to MAQCHEM=0, or remove  --'
!         write(io6,*)'--  MAQCHEM from the control file.           --'
!         write(io6,*)'-----------------------------------------------'
!         write(io6,*)
!         problem=.TRUE.
!      endif
!
! --- Check for valid MCHEM selection when aqueous phase chemistry
! --- is selected
      qafail=.FALSE.
      if(maqchem.EQ.1) then
         if(mchem.NE.6 .AND. mchem.NE.7) qafail=.TRUE.
      elseif(maqchem.NE.0) then
         qafail=.TRUE.
      endif
      if(QAFAIL) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Valid Values for MAQCHEM are 0 and 1     --'
         write(io6,*)'--  Option MAQCHEM = 1 is only used when     --'
         write(io6,*)'--  chemical transformation of sulfates and  --'
         write(io6,*)'--  nitrates is modeled with MCHEM = 6 or 7  --'
         write(io6,*)'--  MAQCHEM  =  ',maqchem
         write(io6,*)'--    MCHEM  =  ',mchem
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Check for valid MLWC option
      qafail=.FALSE.
      if(maqchem.EQ.1 .AND. (mlwc.LT.0 .OR. mlwc.GT.1)) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Valid Values for MLWC are 0 and 1        --'
         write(io6,*)'--     MLWC  =  ',mlwc
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      elseif(maqchem.EQ.1 .AND. mlwc.EQ.1 .AND. metfm.GT.1) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  CALMET files (METFM=1) are needed for    --'
         write(io6,*)'--  the 3D cloud water option MLWC=1         --'
         write(io6,*)'--     MLWC  =  ',mlwc
         write(io6,*)'--    METFM  =  ',metfm
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      elseif(maqchem.EQ.0 .AND. mlwc.EQ.1) then
! ---    Reset MLWC internally without alert
         mlwc=0
      endif
!
! --- Check for valid MAVGNH3 selection
      qafail=.FALSE.
      if(mavgnh3.LT.0 .OR. mavgnh3.GT.1) then
         if(mchem.EQ.6 .OR. mchem.EQ.7) qafail=.TRUE.
      endif
      if(QAFAIL) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Valid Values for MAVGNH3 are 0 and 1     --'
         write(io6,*)'--  Option MAVGNH3 is only used when         --'
         write(io6,*)'--  chemical transformation of sulfates and  --'
         write(io6,*)'--  nitrates is modeled with MCHEM = 6 or 7  --'
         write(io6,*)'--  MAVGNH3  =  ',mavgnh3
         write(io6,*)'--    MCHEM  =  ',mchem
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Check for valid background H2O2, NH3, and Ozone (Never negative)
      qafail=.FALSE.
      if(maqchem.EQ.1) then
         do imo=1,12
            if(bckh2o2m(imo).LT.0.0) qafail=.TRUE.
         enddo
      endif
      if(mchem.NE.0 .AND. mchem.NE.2 .AND. mchem.NE.5) then
         do imo=1,12
            if(bcko3m(imo).LT.0.0) qafail=.TRUE.
            if(bcknh3m(imo).LT.0.0) qafail=.TRUE.
         enddo
      endif
      if(QAFAIL) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Background Ozone, Ammonia,               --'
         write(io6,*)'--  and H2O2 concentrations                  --'
         write(io6,*)'--  should never be negative (ppb)           --'
         write(io6,*)'--  BCKO3   =  ',bcko3m
         write(io6,*)'--  BCKNH3  =  ',bcknh3m
         write(io6,*)'--  BCKH2O2 =  ',bckh2o2m
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Check for valid RNITE1, RNITE2, RNITE3 (Never negative)
      qafail=.FALSE.
      if(mchem.EQ.1 .OR. mchem.EQ.6 .OR. mchem.EQ.7) then
         if(rnite1.LT.0.0) qafail=.TRUE.
      endif
      if(mchem.EQ.1 .AND. rnite2.LT.0.0) qafail=.TRUE.
      if(mchem.EQ.1 .AND. rnite3.LT.0.0) qafail=.TRUE.
      if(QAFAIL) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Transformation rates                     --'
         write(io6,*)'--  should never be negative (%/hr)          --'
         write(io6,*)'--  RNITE1  =  ',rnite1
         write(io6,*)'--  RNITE2  =  ',rnite2
         write(io6,*)'--  RNITE3  =  ',rnite3
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
! --- Check decay rates for MCHEM=5
      if(mchem.EQ.5) then
         qafail=.FALSE.
         ndk=0
         do isp=1,nspec
            if(dkrate(isp).GT.0.0) ndk=ndk+1
         enddo
         if(ndecay.NE.ndk) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Number of species with decay rate is     --'
         write(io6,*)'--  not equal to number expected             --'
         write(io6,*)'--  Number Found    =  ',ndk
         write(io6,*)'--  Number Expected =  ',ndecay
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
         endif
      endif
! --- Check number of puffs produced for horizontal splitting
      if(msplit.EQ.1 .AND. nsplith.LT.5) then
! ---    Number outside valid range
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Number of puffs after splitting 1 puff   --'
         write(io6,*)'--  horizontally must be at least 5          --'
         write(io6,*)'--  NSPLITH  =  ',nsplith
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
! --- Check CNSPLITH values (none should be negative)
      qafail=.FALSE.
      if(msplit.EQ.1) then
         do is=1,nspec
            if(cnsplith(is).LT.0.0) qafail=.TRUE.
         enddo
      endif
      if(QAFAIL) then
! ---    Negative concentration for horizontal puff-split criteria
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Negative value in CNSPLITH array found!  --'
         write(io6,*)'--  Enter either NSPEC values, or just enter --'
         write(io6,*)'--  a single value that will be used for ALL --'
         do is=1,nspec
            write(io6,*)'--  CNSPLITH  =  ',cnsplith(is)
         enddo
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Vertical divergence thresholds CDIV control when and how fast
! --- the convergence adjustment is applied;  make sure that
! --- CDIV(1).LE.CDIV(2) and not negative
      if(cdiv(1).LT.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  Negative CDIV value(s) are RESET    --'
         write(io6,*)'--  Values found in Control File:       --'
         write(io6,*)'--  cdiv = ',cdiv
         write(io6,*)'--  New Values:                         --'
         cdiv(1)=0.00
         if(cdiv(2).LT.cdiv(1)) cdiv(2)=cdiv(1)
         write(io6,*)'--  cdiv = ',cdiv
         write(io6,*)'------------------------------------------'
         write(io6,*)
         warn=.TRUE.
      elseif(cdiv(2).LT.cdiv(1)) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  CDIV(2) is less than CDIV(1)        --'
         write(io6,*)'--  Values found in Control File:       --'
         write(io6,*)'--  cdiv = ',cdiv
         write(io6,*)'--  New Values:                         --'
         cdiv(2)=cdiv(1)
         write(io6,*)'--  cdiv = ',cdiv
         write(io6,*)'------------------------------------------'
         warn=.TRUE.
      endif
!
! --- Run period must be explicit if using multiple CALMET.DAT files
      if(metfm.EQ.1 .AND. metrun.EQ.1 .AND. nmetdatall.GT.1) then
         write(io6,*)'ERROR in subr. QAINP -- Option to run all met.',&
         'periods cannot be used with multiple CALMET.DAT files'
         write(io6,*)'METRUN = ',metrun,' NMETDAT = ',nmetdatall
         problem=.TRUE.
      endif
! --- Check averaging times used to adjust PG sigma-y
      if(avet.GT.60. .OR. avet.LE.0.) then
! ---    Averaging time outside valid range
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Averaging time for sigma-y is not in     --'
         write(io6,*)'--  valid range: 0 < AVET <= 60 minutes      --'
         write(io6,*)'--  AVET =  ',avet
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
      if(pgtime.GT.60. .OR. pgtime.LE.0.) then
! --- 6.42_x1.1 140521
! ---    Averaging time outside valid range
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Base time for sigma-y adjustment outside --'
         write(io6,*)'--  valid range: 0 < PGTIME <= 60 minutes    --'
         write(io6,*)'--  PGTIME =  ',pgtime
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
! --- Check roughness adjustment option for PG sigmas when PG sigmas
! --- might be used
      if(MDISP.GE.3 .OR. MDISP2.GE.3) then
       if(mrough.LT.0 .OR. mrough.GT.1) then
! ---    Roughness option outside valid range
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Roughness adjustment for PG sigmas is not--'
         write(io6,*)'--  in valid range: 0 < MROUGH < 1           --'
         write(io6,*)'--  MROUGH =  ',mrough
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
       endif
!       if(mrough.EQ.1 .AND. metfm.EQ.1) then
!c ---    Roughness adjustment is not done for gridded met data
!         write(io6,*)
!         write(io6,*)
!         write(io6,*)
!         write(io6,*)'-----------------------------------------------'
!         write(io6,*)'          FATAL FLAW  !! '
!         write(io6,*)'-----------------------------------------------'
!         write(io6,*)'--  Roughness adjustment for PG sigmas uses  --'
!         write(io6,*)'--  a single (not gridded) roughness length  --'
!         write(io6,*)'--  and should not be used with CALMET data  --'
!         write(io6,*)'--  MROUGH =  ',mrough
!         write(io6,*)'--  METFM  =  ',metfm
!         write(io6,*)'-----------------------------------------------'
!         write(io6,*)
!         problem=.TRUE.
!       endif
      endif
! --- Check base time zone
      if(xbtz.GT.12.01 .OR. xbtz.LT.-12.01) then
! ---    Time zone outside valid range
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Base time zone is not in valid range:    --'
         write(io6,*)'--  -12. <= XBTZ <= 12.                      --'
         write(io6,*)'--  XBTZ  =  ',xbtz
         write(io6,*)'--  ABTZ  =  ',abtz
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- PRIME downwash option is not implemented for slugs
      if(mbdw.EQ.2 .AND. mslug.EQ.1) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Option MBDW=2 (PRIME downwash) is not    --'
         write(io6,*)'--  implemented for slugs (MSLUG=1).  Reset  --'
         write(io6,*)'--  MBDW=1 (HS/SS downwash) or MSLUG=0.      --'
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Check platform downwash configuration
!       1) Platform DW not used with PRIME at this time       (Error)
!       2) ZPLTFM > 0. not provided with platform DW          (Warn)
!       3) ZPLTFM > 0. provided but platform DW not selected  (Error)
      iplat1=0
      iplat2=0
      iplat3=0
      do i=1,npt1
         if(idownw(i).EQ.2 .AND. mbdw.EQ.2) iplat1=iplat1+1
         if(idownw(i).EQ.2 .AND. zplatpt1(i).LE.0.) iplat2=iplat2+1
         if(idownw(i).EQ.1 .AND. zplatpt1(i).GT.0.) iplat3=iplat3+1
      enddo
      if(iplat1.GT.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Option MBDW=2 (PRIME downwash) is not    --'
         write(io6,*)'--  implemented for Platform structures      --'
         write(io6,*)'--  (Group 13b Bldg Dwash = 2.).             --'
         write(io6,*)'--  Number of sources affected =   ',iplat1
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
      if(iplat2.GT.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Platform height ZPLTFM > 0 NOT provided  --'
         write(io6,*)'--  for source influenced by Platform bldgs  --'
         write(io6,*)'--  (Group 13b Bldg Dwash = 2.).             --'
         write(io6,*)'--  Number of sources affected =   ',iplat2
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         warn=.TRUE.
      endif
      if(iplat3.GT.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Platform height ZPLTFM is provided for a --'
         write(io6,*)'--  source NOT influenced by Platform bldgs  --'
         write(io6,*)'--  (Group 13b Bldg Dwash = 1.).             --'
         write(io6,*)'--  Number of sources affected =   ',iplat3
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- MBCON checks (boundary condition configuration)
      if(mbcon.LT.0 .OR. mbcon.GT.2) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Invalid selection for MBCON option       --'
         write(io6,*)'--  Expected MBCON = 0, 1, or 2              --'
         write(io6,*)'--  Found MBCON = ',mbcon
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
      dgridkm=.001*dgrid
      if(mbcon.GT.0 .AND. rsampbc.LT.dgridkm) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Search radius for sampling BC puffs may  --'
         write(io6,*)'--  be too small, compared to grid cell      --'
         write(io6,*)'--  RSAMPBC = ',rsampbc
         write(io6,*)'--  DGRIDKM = ',dgridkm
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         warn=.TRUE.
      endif
! --- Check for species BCON at end of list
      if(mbcon.GT.0 .AND. cspec(nspec).NE.'BCON'&
                    .AND. cspec(nspec).NE.'bcon') then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Last species in list must be BCON when   --'
         write(io6,*)'--  the Boundary Condition Puff otion is     --'
         write(io6,*)'--  used (MBCON > 0)                         --'
         write(io6,*)'--  Last Species Found = ',cspec(nspec)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
! --- Check source contribution option
      if(msource.LT.0 .OR. msource.GT.1) then
! ---    Invalid MSOURCE
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Source Contribution Option is invalid :  --'
         write(io6,*)'--  0 <= MSOURCE <= 1                        --'
         write(io6,*)'--  MSOURCE  =  ',MSOURCE
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
      if(msource.GT.0 .AND. MBDW.EQ.2) then
! ---    MSOURCE is not allowed with PRIME downwash
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Source Contribution Option is not     :  --'
         write(io6,*)'--  allowed with PRIME downwash              --'
         write(io6,*)'--  MSOURCE, MBDW  =  ',MSOURCE,MBDW
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
      if(mevap.GT.0) then
! ---    MEVAP is not currently implemented
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Evaporation module currently not active  --'
         write(io6,*)'--  MEVAP must = 0 to run              --'
         write(io6,*)'--  MEVAP  =  ',MEVAP
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- MTILT checks (gravitational settling option)
!       Restrictions:
!       - MDRY  = 1   
!       - NSPEC = 1  (must be particle species as well)
!       - sg    = 0  GEOMETRIC STANDARD DEVIATION in Group 8 is
!                    set to zero for a single particle diameter
!       - MCTADJ= 0,1,3  (Interaction with option 2 not supported)
      if(mtilt.LT.0 .OR. mtilt.GT.1) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Invalid selection for MTILT option       --'
         write(io6,*)'--  Expected MTILT = 0, or 1                 --'
         write(io6,*)'--  Found MTILT = ',mtilt
         write(io6,*)'-----------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      elseif(mtilt.EQ.1) then
         if(mdry.EQ.0) then
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'------------------------------------------'
            write(io6,*)'          FATAL FLAW  !! '
            write(io6,*)'------------------------------------------'
            write(io6,*)'--  Gravitational settling option(MTILT)--'
            write(io6,*)'--  is allowed only if dry deposition is--'
            write(io6,*)'--  selected (MDRY=1).                  --'
            write(io6,*)'--  Found MTILT = ',mtilt
            write(io6,*)'--  Found MDRY  = ',mdry
            write(io6,*)'------------------------------------------'
            write(io6,*)
            problem=.TRUE.
         endif
         if(mctadj.EQ.2) then
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'------------------------------------------'
            write(io6,*)'          FATAL FLAW  !! '
            write(io6,*)'------------------------------------------'
            write(io6,*)'--  Gravitational settling option(MTILT)--'
            write(io6,*)'--  is not allowed with CALPUFF terrain --'
            write(io6,*)'--  option (MCTADJ=2).                  --'
            write(io6,*)'--  Found MTILT  = ',mtilt
            write(io6,*)'--  Found MCTADJ = ',mctadj
            write(io6,*)'------------------------------------------'
            write(io6,*)
            problem=.TRUE.
         endif
         if(nspec.NE.1) then
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'------------------------------------------'
            write(io6,*)'          FATAL FLAW  !! '
            write(io6,*)'------------------------------------------'
            write(io6,*)'--  Gravitational settling option(MTILT)--'
            write(io6,*)'--  is only available for a single      --'
            write(io6,*)'--  species (NSPEC=1).                  --'
            write(io6,*)'--  Found MTILT = ',mtilt
            write(io6,*)'--  Found NSPEC = ',nspec
            write(io6,*)'------------------------------------------'
            write(io6,*)
            problem=.TRUE.
         elseif(gsig(1).GT.0.0 .OR. idryflg(1).NE.2) then
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'------------------------------------------'
            write(io6,*)'          FATAL FLAW  !! '
            write(io6,*)'------------------------------------------'
            write(io6,*)'--  Gravitational settling option(MTILT)--'
            write(io6,*)'--  requires dry deposited particle (2) --'
            write(io6,*)'--  species of one size (geo sdev=0.)   --'
            write(io6,*)'--  Found MTILT    = ',mtilt
            write(io6,*)'--  Found DRY DEP  = ',idryflg(1)
            write(io6,*)'--  Found GEO SDEV = ',gsig(1)
            write(io6,*)'------------------------------------------'
            write(io6,*)
            problem=.TRUE.
         endif
      endif
! --- MXAGEHR is typically not used, so warn if MXAGE>0.
! --- If regulatory switch is set, change warning to fatal error.
      if(mxagehr.GT.0 .AND. mreg.NE.1) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  Puff age cutoff (MXAGEHR) is active --'
         write(io6,*)'--  which may lead to underestimating   --'
         write(io6,*)'--  concentrations.                     --'
         write(io6,*)'--  Found MXAGEHR    = ',mxagehr
         write(io6,*)'------------------------------------------'
         write(io6,*)
         warn=.TRUE.
      elseif(mxagehr.GT.0 .AND. mreg.EQ.1) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  Puff age cutoff (MXAGEHR) is active --'
         write(io6,*)'--  which may lead to underestimating   --'
         write(io6,*)'--  concentrations.                     --'
         write(io6,*)'--  Found MXAGEHR    = ',mxagehr
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
! --- Check SVMIN,SWMIN values (all 24 should be valid)
      missv=0
      missw=0
      do k=1,6
         if(svmin(k,1).LE.0.0) missv=missv+1
         if(svmin(k,2).LE.0.0) missv=missv+1
         if(swmin(k,1).LE.0.0) missw=missw+1
         if(swmin(k,2).LE.0.0) missw=missw+1
      enddo
      if(missv.GT.0 .OR. missw.GT.0) then
! ---    Minimum sigv or sigw arrays are incomplete
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'          FATAL FLAW  !! '
         write(io6,*)'-----------------------------------------------'
         write(io6,*)'--  Minimum sigma-v values invalid :  ',missv
         write(io6,*)'--  Minimum sigma-w values invalid :  ',missw
         write(io6,*)'--         SVMIN over LAND                   --'
         write(io6,*)'--     Default  Stability   Found            --'
