!----------------------------------------------------------------------
      subroutine quadarea(x,y,area)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 980430          QUADAREA
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Compute the area of a quadrilateral polygon.  The
!               shape must be regular in the sense that no line that
!               is drawn through it may cross more than 2 sides.
!
! --- UPDATE
! --- V5.0-V5.0     980430  (DGS): drop constraint that vertex locations
!                                  are resolved to nearest meter
!
! --- INPUTS:
!              X - real       - X-verticies of polygon (UTM-km)
!              Y - real       - Y-verticies of polygon (UTM-km)
!
! --- OUTPUT:
!           AREA - real       - Area of polygon (m^2)
!
!
! --- QUADAREA called by:  READCF
! --- QUADAREA calls:      TRIAREA
!----------------------------------------------------------------------
!
      real x(4),y(4)
      real xm(4),ym(4)
!
! --- Convert to meters, relative to vertex 1
      xm(1)=0.0
      ym(1)=0.0
      do iv=2,4
         xm(iv)=1000.*( x(iv)-x(1) )
         ym(iv)=1000.*( y(iv)-y(1) )
      enddo
! --- Compute the 6 distances between pairs of vertices
      d12=SQRT((xm(1)-xm(2))**2 + (ym(1)-ym(2))**2)
      d13=SQRT((xm(1)-xm(3))**2 + (ym(1)-ym(3))**2)
      d14=SQRT((xm(1)-xm(4))**2 + (ym(1)-ym(4))**2)
      d23=SQRT((xm(2)-xm(3))**2 + (ym(2)-ym(3))**2)
      d24=SQRT((xm(2)-xm(4))**2 + (ym(2)-ym(4))**2)
      d34=SQRT((xm(3)-xm(4))**2 + (ym(3)-ym(4))**2)
!
! --- Sum area of 4 triangles formed by taking 3 vertices at a time
      area=triarea(d12,d13,d23)+triarea(d23,d24,d34)+&
           triarea(d13,d14,d34)+triarea(d12,d14,d24)
!
! --- Area of quadrilateral is half of this sum
      area=0.5*area
!
      return
      end
!----------------------------------------------------------------------
      function triarea(a,b,c)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 940831           TRIAREA
!                D. Strimaitis, SRC
!
! --- PURPOSE:  Compute the area of a triangle.
!
! --- INPUTS:
!          A,B,C - real       - Lengths of 3 sides (m)
!
! --- OUTPUT:
!        TRIAREA - real       - Area of triangle (m^2)
!
!
! --- TRIAREA called by:  QUADAREA
! --- TRIAREA calls:      none
!----------------------------------------------------------------------
!
      s=0.5*(a+b+c)
      triarea=SQRT(s*(s-a)*(s-b)*(s-c))
!
      return
      end
