!                                 and report errors to screen (IOMESG)
!                  971107  (DGS): add file for variable line sources
!                                 (LNEMARB.DAT)
!
! --- INPUTS:
!        Parameters: IO5, IOMESG, MXVAR
!
! --- OUTPUT:
!
! ---    Common block /FILNAM/ variables:
!           pufinp,metdat,iscdat,plmdat,puflst,condat,dfdat,wfdat,
!           visdat,t2ddat,rhodat,ozdat,vddat,
!           chemdat,hildat,rctdat,prfdat,sfcdat,rstartb,rstarte,
!           cstdat,debug,lcfiles,
!           nmetdatall,nmetdat,imetdat,metdatl,nmetdom,metdoml
!           nptdat,ptdat,nardat,ardat,nvoldat,voldat,nlndat,lndat,
!           nfldat,fldat,
!           bdydat,flxdat,baldat,bcndat,
!           fogdat,h2o2dat,risdat,trkdat,nh3zdat,auxext
! ---    Common block /GRIDNEST/ variables:
!           ngrid
! ---    Module |MROAD2| variables:
!           nrddat,rddat
! ---    Module |MRSPRAY2| variables:
!           nspdat,spdat
!
! --- READFN called by:  READCF
! --- READFN calls:      READIN, FILCASE
!----------------------------------------------------------------------
! --- Modules
      use mroad2
      use mspray2
! --- Include parameter statements
      include 'params.puf'
      include 'params.cal'
      character*4 ctemp(132,36)
      character*12 cvdic(mxvar,9)
      character*132 aux132
      integer ivleng(mxvar,9),ivtype(mxvar,9)
      integer nmet, ndom
      logical lmet, ldom
      logical lecho
! --- Include common blocks
      include 'filnam.puf'
      include 'gridnest.puf'
!
      data lecho/.false./
      data ndom/0/
      data names/36/
      data cvdic/&
       'METDAT','ISCDAT','PLMDAT','PUFLST','CONDAT','DFDAT','WFDAT',&
       'VISDAT','PTDAT','VOLDAT','ARDAT','LNDAT',&
       'OZDAT','VDDAT','CHEMDAT','HILDAT','RCTDAT','PRFDAT',&
       'SFCDAT','RSTARTB','RSTARTE','CSTDAT','BDYDAT','FLXDAT',&
       'BALDAT','BCNDAT','DEBUG','FOGDAT','H2O2DAT','T2DDAT',&
       'RHODAT','RISDAT','TRKDAT','NH3ZDAT','AUXEXT','FLDAT',&
       'LCFILES','NMETDAT','NMETDOM','NPTDAT','NARDAT','NVOLDAT',&
       'NFLDAT','NRDDAT','NLNDAT','NSPDAT',14*' ',&
       'METDAT',59*' ',&
       'PTDAT',59*' ',&
       'ARDAT',59*' ',&
       'VOLDAT',59*' ',&
       'FLDAT',59*' ',&
       'RDDAT',59*' ',&
       'SPDAT',59*' ',&
       'LNDAT',59*' '/
!
      data ivleng/&
        36*132,10*1, 14*0,&
        132, 59*0,&
        132, 59*0,&
        132, 59*0,&
        132, 59*0,&
        132, 59*0,&
        132, 59*0,&
        132, 59*0,&
        132, 59*0/
!
      data ivtype/&
        36*4,3,9*2, 14*0,&
        4, 59*0,&
        4, 59*0,&
        4, 59*0,&
        4, 59*0,&
        4, 59*0,&
        4, 59*0,&
        4, 59*0,&
        4, 59*0/
! --- Place default extension (c*16) into temp c*132
      aux132(1:16)=auxext
      do j=17,132
         aux132(j:j)=' '
      enddo
! --- Set code structure to read up to 10 nested CALMET domains
      if(mxmetdom.GT.10) then
         write(*,*)
         write(*,*)'*********  FATAL  ***********'
         write(*,*)'ERROR in subr. READFN -- configuration conflict ',&
                   'beween array-size parameter -- MXMETDOM = ',mxmetdom
         write(*,*)'      and READFN declaration for up to 10 domains.'
         write(*,*)'      READFN code needs to be modified.'
         write(*,*)
         stop 'Halted in READFN -- see list file.'
      endif
! --- Initialize number of CALMET.DAT files in each domain
      nmetdat(1:mxmetdom)=0
!
! --- Read the file names from the data file
!
! ---    Initialize the temporary arrays
         do i=1,names
            do j=1,132
               ctemp(j,i)(1:1)=' '
            enddo
         enddo
!
! ---    Read the file names for the model run
         call readin(cvdic(1,1),ivleng(1,1),ivtype(1,1),io5,iomesg,&
         lecho,&
         ctemp(1,1),ctemp(1,2),ctemp(1,3),ctemp(1,4),ctemp(1,5),&
         ctemp(1,6),ctemp(1,7),ctemp(1,8),ctemp(1,9),ctemp(1,10),&
         ctemp(1,11),ctemp(1,12),ctemp(1,13),ctemp(1,14),ctemp(1,15),&
         ctemp(1,16),ctemp(1,17),ctemp(1,18),ctemp(1,19),ctemp(1,20),&
         ctemp(1,21),ctemp(1,22),ctemp(1,23),ctemp(1,24),ctemp(1,25),&
         ctemp(1,26),ctemp(1,27),ctemp(1,28),ctemp(1,29),ctemp(1,30),&
         ctemp(1,31),ctemp(1,32),ctemp(1,33),ctemp(1,34),ctemp(1,35),&
         ctemp(1,36),lcfiles,&
         nmetdatall,nmetdom,nptdat,nardat,nvoldat,nfldat,nrddat,nlndat,&
         nspdat,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum)
! ---    Reset NMETDOM if needed:  There is at least 1 grid in all
! ---    CALPUFF runs (the CALPUFF grid)
         if(nmetdom.LT.1)then
! ---       (Do not write message to screen)
!            write(*,*)
!            write(*,*)'*********  WARNING  ***********'
!            write(*,*)'READFN -- there must be at least 1 modeling',
!     1      ' domain: RESETTING -- NMETDOM to 1'
!            write(*,*)
            nmetdom=1
         endif
! ---    Pass the number of met grids to /GRIDNEST/ variable
         ngrid=nmetdom
!
! ---    Prepare any filenames included in the I/O file by erasing
! ---    the default filename set above
         if(ctemp(1,1)(1:1).ne.' ')metdat=' '
         if(ctemp(1,2)(1:1).ne.' ')iscdat=' '
         if(ctemp(1,3)(1:1).ne.' ')plmdat=' '
         if(ctemp(1,4)(1:1).ne.' ')puflst=' '
         if(ctemp(1,5)(1:1).ne.' ')condat=' '
         if(ctemp(1,6)(1:1).ne.' ')dfdat =' '
         if(ctemp(1,7)(1:1).ne.' ')wfdat =' '
         if(ctemp(1,8)(1:1).ne.' ')visdat=' '
         if(ctemp(1,9)(1:1).ne.' ')ptdat(1)=' '
         if(ctemp(1,10)(1:1).ne.' ')voldat(1)=' '
         if(ctemp(1,11)(1:1).ne.' ')ardat(1)=' '
         if(ctemp(1,12)(1:1).ne.' ')lndat=' '
         if(ctemp(1,13)(1:1).ne.' ')ozdat=' '
         if(ctemp(1,14)(1:1).ne.' ')vddat =' '
         if(ctemp(1,15)(1:1).ne.' ')chemdat=' '
         if(ctemp(1,16)(1:1).ne.' ')hildat =' '
         if(ctemp(1,17)(1:1).ne.' ')rctdat =' '
         if(ctemp(1,18)(1:1).ne.' ')prfdat =' '
         if(ctemp(1,19)(1:1).ne.' ')sfcdat =' '
         if(ctemp(1,20)(1:1).ne.' ')rstartb=' '
         if(ctemp(1,21)(1:1).ne.' ')rstarte=' '
         if(ctemp(1,22)(1:1).ne.' ')cstdat =' '
         if(ctemp(1,23)(1:1).ne.' ')bdydat =' '
         if(ctemp(1,24)(1:1).ne.' ')flxdat =' '
         if(ctemp(1,25)(1:1).ne.' ')baldat =' '
         if(ctemp(1,26)(1:1).ne.' ')bcndat =' '
         if(ctemp(1,27)(1:1).ne.' ')debug  =' '
         if(ctemp(1,28)(1:1).ne.' ')fogdat =' '
         if(ctemp(1,29)(1:1).ne.' ')h2o2dat=' '
         if(ctemp(1,30)(1:1).ne.' ')t2ddat=' '
         if(ctemp(1,31)(1:1).ne.' ')rhodat=' '
         if(ctemp(1,32)(1:1).ne.' ')risdat=' '
         if(ctemp(1,33)(1:1).ne.' ')trkdat=' '
         if(ctemp(1,34)(1:1).ne.' ')nh3zdat=' '
         if(ctemp(1,35)(1:1).ne.' ')aux132=' '
         if(ctemp(1,36)(1:1).ne.' ')fldat(1)=' '
!
! ---    Transfer the char*4 data into the char*132 variables
         do j=1,132
            if(ctemp(j,1)(1:1).ne.' ')metdat(j:j)=ctemp(j,1)(1:1)
            if(ctemp(j,2)(1:1).ne.' ')iscdat(j:j)=ctemp(j,2)(1:1)
            if(ctemp(j,3)(1:1).ne.' ')plmdat(j:j)=ctemp(j,3)(1:1)
            if(ctemp(j,4)(1:1).ne.' ')puflst(j:j)=ctemp(j,4)(1:1)
            if(ctemp(j,5)(1:1).ne.' ')condat(j:j)=ctemp(j,5)(1:1)
            if(ctemp(j,6)(1:1).ne.' ')dfdat (j:j)=ctemp(j,6)(1:1)
            if(ctemp(j,7)(1:1).ne.' ')wfdat (j:j)=ctemp(j,7)(1:1)
            if(ctemp(j,8)(1:1).ne.' ')visdat(j:j)=ctemp(j,8)(1:1)
            if(ctemp(j,9)(1:1).ne.' ')ptdat(1)(j:j)=ctemp(j,9)(1:1)
            if(ctemp(j,10)(1:1).ne.' ')voldat(1)(j:j)=ctemp(j,10)(1:1)
            if(ctemp(j,11)(1:1).ne.' ')ardat(1)(j:j)=ctemp(j,11)(1:1)
            if(ctemp(j,12)(1:1).ne.' ')lndat (j:j)=ctemp(j,12)(1:1)
            if(ctemp(j,13)(1:1).ne.' ')ozdat (j:j)=ctemp(j,13)(1:1)
            if(ctemp(j,14)(1:1).ne.' ')vddat (j:j)=ctemp(j,14)(1:1)
            if(ctemp(j,15)(1:1).ne.' ')chemdat(j:j)=ctemp(j,15)(1:1)
            if(ctemp(j,16)(1:1).ne.' ')hildat (j:j)=ctemp(j,16)(1:1)
            if(ctemp(j,17)(1:1).ne.' ')rctdat (j:j)=ctemp(j,17)(1:1)
            if(ctemp(j,18)(1:1).ne.' ')prfdat (j:j)=ctemp(j,18)(1:1)
            if(ctemp(j,19)(1:1).ne.' ')sfcdat (j:j)=ctemp(j,19)(1:1)
            if(ctemp(j,20)(1:1).ne.' ')rstartb(j:j)=ctemp(j,20)(1:1)
            if(ctemp(j,21)(1:1).ne.' ')rstarte(j:j)=ctemp(j,21)(1:1)
            if(ctemp(j,22)(1:1).ne.' ')cstdat (j:j)=ctemp(j,22)(1:1)
            if(ctemp(j,23)(1:1).ne.' ')bdydat (j:j)=ctemp(j,23)(1:1)
            if(ctemp(j,24)(1:1).ne.' ')flxdat (j:j)=ctemp(j,24)(1:1)
            if(ctemp(j,25)(1:1).ne.' ')baldat (j:j)=ctemp(j,25)(1:1)
            if(ctemp(j,26)(1:1).ne.' ')bcndat (j:j)=ctemp(j,26)(1:1)
            if(ctemp(j,27)(1:1).ne.' ')debug  (j:j)=ctemp(j,27)(1:1)
            if(ctemp(j,28)(1:1).ne.' ')fogdat (j:j)=ctemp(j,28)(1:1)
            if(ctemp(j,29)(1:1).ne.' ')h2o2dat(j:j)=ctemp(j,29)(1:1)
            if(ctemp(j,30)(1:1).ne.' ')t2ddat (j:j)=ctemp(j,30)(1:1)
            if(ctemp(j,31)(1:1).ne.' ')rhodat (j:j)=ctemp(j,31)(1:1)
            if(ctemp(j,32)(1:1).ne.' ')risdat (j:j)=ctemp(j,32)(1:1)
            if(ctemp(j,33)(1:1).ne.' ')trkdat (j:j)=ctemp(j,33)(1:1)
            if(ctemp(j,34)(1:1).ne.' ')nh3zdat(j:j)=ctemp(j,34)(1:1)
            if(ctemp(j,35)(1:1).ne.' ')aux132 (j:j)=ctemp(j,35)(1:1)
            if(ctemp(j,36)(1:1).ne.' ')fldat(1)(j:j)=ctemp(j,36)(1:1)
         enddo
!
! ---    Convert the file names to the proper case
         call filcase(lcfiles,metdat)
         call filcase(lcfiles,iscdat)
         call filcase(lcfiles,plmdat)
         call filcase(lcfiles,puflst)
         call filcase(lcfiles,condat)
         call filcase(lcfiles,dfdat)
         call filcase(lcfiles,wfdat)
         call filcase(lcfiles,visdat)
         call filcase(lcfiles,ptdat(1))
         call filcase(lcfiles,voldat(1))
         call filcase(lcfiles,ardat(1))
         call filcase(lcfiles,lndat)
         call filcase(lcfiles,ozdat)
         call filcase(lcfiles,vddat)
         call filcase(lcfiles,chemdat)
         call filcase(lcfiles,hildat)
         call filcase(lcfiles,rctdat)
         call filcase(lcfiles,prfdat)
         call filcase(lcfiles,sfcdat)
         call filcase(lcfiles,rstartb)
         call filcase(lcfiles,rstarte)
         call filcase(lcfiles,cstdat)
         call filcase(lcfiles,bdydat)
         call filcase(lcfiles,flxdat)
         call filcase(lcfiles,baldat)
         call filcase(lcfiles,bcndat)
         call filcase(lcfiles,debug)
         call filcase(lcfiles,fogdat)
         call filcase(lcfiles,h2o2dat)
         call filcase(lcfiles,t2ddat)
         call filcase(lcfiles,rhodat)
         call filcase(lcfiles,risdat)
         call filcase(lcfiles,trkdat)
         call filcase(lcfiles,nh3zdat)
         call filcase(lcfiles,aux132)
         call filcase(lcfiles,fldat(1))
! ---    Place AUX name into c*16 string with a leading dot
         auxext(1:1)='.'
         i1=1
         if(aux132(1:1).EQ.'.') i1=2
         i2=i1+14
         auxext(2:16)=aux132(i1:i2)
!      
! --- Process group of CALMET.DAT files (conditional)
      if(nmetdom.EQ.1) metdoml(1)='MASTER'
      if(nmetdatall.LE.1 .AND. nmetdom.EQ.1) then
! ---    Place single file name into first element of array of names
         metdatl(1,1)=metdat
         nmetdat(1)=1
      elseif(nmetdom.GT.mxmetdom)then
         write(*,*)
         write(*,*)'*********  FATAL  ***********'
         write(*,*)'ERROR in subr. READFN -- too many CALMET.DAT ',&
         'grids -- NMETDOM = ',nmetdom,' MXMETDOM = ',mxmetdom
         write(*,*)
         stop 'Halted in READFN -- see list file.'
      else
! ---    Create dictionary for nested CALMET domain assignments
! ---    and keep old METDAT name in dictionary as well
         do i=1,nmetdom
! ---       METDATn assignments
            ii=i+1
            if(i.LE.9) then
               cvdic(ii,2)='METDATn'
               write(cvdic(ii,2)(7:7),'(i1)')i
            else
               cvdic(ii,2)='METDATnn'
               write(cvdic(ii,2)(7:8),'(i2)')i
            endif
            ivleng(ii,2)=132
            ivtype(ii,2)=4
! ---       DOMAINn assignments
            jj=nmetdom+i+1
            if(i.LE.9) then
               cvdic(jj,2)='DOMAINn'
               write(cvdic(jj,2)(7:7),'(i1)')i
            else
               cvdic(jj,2)='DOMAINnn'
               write(cvdic(jj,2)(7:8),'(i2)')i
            endif
            ivleng(jj,2)=132
            ivtype(jj,2)=4
         enddo
         ktot=nmetdatall
         if(nmetdom.GT.1) ktot=ktot+nmetdom
         do k=1,ktot
! ---       Initialize the temporary arrays
            do i=1,21
               do j=1,132
                  ctemp(j,i)(1:1)=' '
               enddo
            enddo
! ---       Read a CALMET.DAT file name, or a CALMET domain name
            call readin(cvdic(1,2),ivleng(1,2),ivtype(1,2),io5,iomesg,&
            lecho,&
            ctemp(1,1),ctemp(1,2),ctemp(1,3),ctemp(1,4),ctemp(1,5),&
            ctemp(1,6),ctemp(1,7),ctemp(1,8),ctemp(1,9),ctemp(1,10),&
            ctemp(1,11),ctemp(1,12),ctemp(1,13),ctemp(1,14),ctemp(1,15),&
            ctemp(1,16),ctemp(1,17),ctemp(1,18),ctemp(1,19),ctemp(1,20),&
            ctemp(1,21),&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum)
! ---       Loop over grids to find CALMET file name
            lmet=.FALSE.
            do i=1,nmetdom+1
               if(ctemp(1,i)(1:1).ne.' ') then
                  lmet=.TRUE.
! ---             Identify grid
                  igrd=MAX(1,i-1)
! ---             Update counter
                  nmetdat(igrd)=nmetdat(igrd)+1
                  nmet=MIN(nmetdat(igrd),mxmetdat)
! ---             Transfer the char*4 data into the char*132 variable
                  do j=1,132
                     metdatl(nmet,igrd)(j:j)=ctemp(j,i)(1:1)
                  enddo
               endif
            enddo
! ---       Convert the name to the proper case
            if(LMET) call filcase(lcfiles,metdatl(nmet,igrd))
! ---       Loop over grids to find CALMET domain name
            do i=1,nmetdom
               ii=nmetdom+i+1
               if(ctemp(1,ii)(1:1).ne.' ') then
! ---             Update counter
                  ndom=ndom+1
! ---             Transfer the char*4 data into the char*132 variable
                  do j=1,132
                     metdoml(i)(j:j)=ctemp(j,ii)(1:1)
                  enddo
               endif
            enddo
         enddo
      endif
      do i=1,nmetdom
         imetdat(i)=1
      enddo
! --- QA the number of CALMET.DAT filenames in control file
      do i=1,nmetdom
        if(nmetdat(i).GT.mxmetdat)then
          write(*,*)
          write(*,*)'*********  FATAL  ***********'
          write(*,*)'ERROR in subr. READFN -- too many CALMET.DAT ',&
          'files -- Most allowed is MXMETDAT = ',mxmetdat
          write(*,*)'CALMET Grid Domain    Number of Files'
          do k=1,nmetdom
             write(*,*)'        ',k,'             ',nmetdat(k)
          enddo
          write(*,*)
          stop 'Halted in READFN -- see list file.'
        endif
      enddo
      
! --- Process group of PTEMARB.DAT files (conditional)
      if(nptdat.GT.mxemdat)then
         write(*,*)
         write(*,*)'*********  FATAL  ***********'
         write(*,*)'ERROR in subr. READFN -- too many PTEMARB.DAT ',&
         'files -- NPTDAT = ',nptdat,' MXEMDAT = ',mxemdat
         write(*,*)
         stop 'Halted in READFN -- see list file.'
      else
         do k=1,nptdat
! ---       Initialize the temporary array
            do j=1,132
               ctemp(j,1)(1:1)=' '
            enddo
! ---       Read the PTEMARB.DAT file name
            call readin(cvdic(1,3),ivleng(1,3),ivtype(1,3),io5,iomesg,&
            lecho,&
            ctemp(1,1),&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---       Prepare any filenames included in the I/O file by erasing
! ---       the default filename set above
            if(ctemp(1,1)(1:1).ne.' ')ptdat(k)=' '
! ---       Transfer the char*4 data into the char*132 variable
            do j=1,132
               if(ctemp(j,1)(1:1).ne.' ')ptdat(k)(j:j)=ctemp(j,1)(1:1)
            enddo
! ---       Convert the file name to the proper case
            call filcase(lcfiles,ptdat(k))
         enddo
      endif
!      
! --- Process group of BAEMARB.DAT files (conditional)
      if(nardat.GT.mxemdat)then
         write(*,*)
         write(*,*)'*********  FATAL  ***********'
         write(*,*)'ERROR in subr. READFN -- too many BAEMARB.DAT ',&
         'files -- NARDAT = ',nardat,' MXEMDAT = ',mxemdat
         write(*,*)
         stop 'Halted in READFN -- see list file.'
      else
         do k=1,nardat
! ---       Initialize the temporary array
            do j=1,132
               ctemp(j,1)(1:1)=' '
            enddo
! ---       Read the BAEMARB.DAT file name
            call readin(cvdic(1,4),ivleng(1,4),ivtype(1,4),io5,iomesg,&
            lecho,&
            ctemp(1,1),&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
            idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---       Prepare any filenames included in the I/O file by erasing
! ---       the default filename set above
            if(ctemp(1,1)(1:1).ne.' ')ardat(k)=' '
! ---       Transfer the char*4 data into the char*132 variable
            do j=1,132
               if(ctemp(j,1)(1:1).ne.' ')ardat(k)(j:j)=ctemp(j,1)(1:1)
            enddo
! ---       Convert the file name to the proper case
            call filcase(lcfiles,ardat(k))
         enddo
      endif
!      
! --- Process group of VOLEMARB.DAT files (conditional)
      if(nvoldat.GT.mxemdat)then
         write(*,*)
         write(*,*)'*********  FATAL  ***********'
         write(*,*)'ERROR in subr. READFN -- too many VOLEMARB.DAT ',&
         'files -- NVOLDAT = ',nvoldat,' MXEMDAT = ',mxemdat
         write(*,*)
         stop 'Halted in READFN -- see list file.'
      else
         do k=1,nvoldat
! ---       Initialize the temporary array
            do j=1,132
               ctemp(j,1)(1:1)=' '
            enddo
! ---       Read the VOLEMARB.DAT file name
            call readin(cvdic(1,5),ivleng(1,5),ivtype(1,5),io5,iomesg,&
            lecho,&
            ctemp(1,1),&
