      go to 182                                                        ! isc37080
!     stability c(190)                                                  isc37090
190   xvz=sz0/c                                                        ! isc37100
      xvz=xvz * 0.001
      return                                                           ! isc37120
!     stability d,e,&f (200)                                            isc37130
200   xvz=sz0 * (d*sz0 + sqrt( (d*sz0)**2 + 4.*c**2 ) ) / (2.*c**2)
      xvz=xvz * 0.001
      return                                                           ! isc37170
      end                                                              ! isc37180
