! --- Close file and return if puff arrays are empty
      if(npuffs.EQ.0) then
         close(io4)
         return
      elseif(npuffs.LT.0) then
! ---    Fatal error
         write(io6,*)'RESTARTO:  FATAL ERROR FOUND'
         write(io6,*)'   Number of puffs after roll-down = ',npuffs
         write(*,*)
         stop 'Halted in RESTARTO -- see list file.'
