      clabel=blank8
      clabel='bidfnl'
      call wrtr1d(io4,bidfnl,npuffs,clabel,ndathrb,nsecb,ndathre,nsece)
      clabel=blank8
      clabel='fb'
      call wrtr1d(io4,fb,npuffs,clabel,ndathrb,nsecb,ndathre,nsece)
      clabel=blank8
      clabel='fm'
      call wrtr1d(io4,fm,npuffs,clabel,ndathrb,nsecb,ndathre,nsece)
      clabel=blank8
