! ----------------------------------------
! --- Loop on all puffs regardless of whether tables exist
      do ii=1,npuffs
         call ZEROTAB
         read(io3) ityp,isrc,irls,ntr,nwk,ncv
         if(ntr.GT.0) then
            call rdr1d(io3,mtver,xtr,ntr,clabel,&
                       idathrb,isecb,idathre,isece)
            call rdqa(io6,'xtr     ',ndathrb,nsecb,clabel,&
                      idathre,isece)
