      subroutine mflxhdr(title)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915           MFLXHDR
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Writes header records to the Mass Flux file
!
! --- UPDATE
! --- V5.2-V6.1     050915  (DGS): end time used with seconds
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
!
! --- INPUTS:
!         TITLE(3) - Char.*80 - Run title (3 lines of 80 char. each)
!
!     Common block /MFLUX/ variables
!         CNAMBDY(mxbndry)
!     Common block /GEN/ variables
!         NGRUP
!     Common block /OUTPT/ variables
!         IOUTOP(7,mxspec)
!
!     Parameters:
!         MXGRUP, MXBNDRY, IO36, IO6
!
! --- OUTPUT:
!             none
!
! --- MFLXHDR called by: SETUP
! --- MFLXHDR calls:     none
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      include 'mflux.puf'
      include 'gen.puf'
      include 'outpt.puf'
!
      character*80 title(3)
      character*16 datehr
      character*12 specnm
      character*24 cinout
      data cinout/'     In        Out      '/
      data datehr/'Year Day Hr Sec '/
      data specnm/'  Species   '/
! --- Identify species-groups with reported mass flux
      numgrp=0
      do i=1,ngrup
         if(ioutop(7,i).EQ.1) then
            numgrp=numgrp+1
         endif
      enddo
      if(numgrp.EQ.0) then
         write(*,*)'MFLXHDR:  Error Found in Control File Group 5'
         write(*,*)'No species selected for Mass Flux Output'
         write(io6,*)'MFLXHDR:  Error Found in Control File Group 5'
         write(io6,*)'No species selected for Mass Flux Output'
      endif
      write(io36,'(a)') title
      write(io36,*)
      write(io36,'(t30,a)')'---------------'
      write(io36,'(t30,a)')'   Mass Flux'
      write(io36,'(t30,a)')'     (g/s)'
      write(io36,'(t30,a)')'---------------'
      write(io36,*)
      write(io36,'(21x,20(8x,a16))') (cnambdy(i),i=1,nbndry)
      write(io36,'(a16,a12,20a24)') datehr,specnm,(cinout,i=1,nbndry)
      write(io36,*)
      return
!
      end
