!----------------------------------------------------------------------
      subroutine mflxgrp(ig,is)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 990228c          MFLXGRP
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Sum results for individual species into output groups
!
! --- INPUTS:
!             IG - integer - Species index used to sum current group
!             IS - integer - Species index to add to current group
!
!     Common block /MFLUX/ variables
!         NBNDRY, FLXIN(mxspec,mxbndry), FLXOUT(mxspec,mxbndry)
!
!     Parameters:
!         MXSPEC, MXBNDRY
!
! --- OUTPUT:
!     Common block /MFLUX/ variables
!         FLXIN(mxspec,mxbndry), FLXOUT(mxspec,mxbndry)
!
! --- MFLXGRP called by: OUTPUT
! --- MFLXGRP calls:     none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'mflux.puf'
! --- Loop over boundaries and sum mass fluxes of species 'is' to
! --- group total in species 'ig'
      do ib=1,nbndry
         flxin(ig,ib)=flxin(ig,ib)+flxin(is,ib)
         flxout(ig,ib)=flxout(ig,ib)+flxout(is,ib)
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine mflxout(istore)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915           MFLXOUT
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Write selected mass fluxes for hour to output file
!
! --- UPDATE
! --- V5.2-V6.1     050915  (DGS): end time used with seconds
!                           (DGS): end of day time reset to 2400
! --- V5.0-V5.2     991104  (DGS): YYYY format for year
!
! --- INPUTS:
!
!   ISTORE(mxgrup) - integer - Species index of mass flux arrays used
!                              to store summed results for group
!
!     Common block /DATEHR/ variables
!         NYRE, NJULE, NHRE, NSECE
!     Common block /GEN/ variables
!         NGRUP, CGRUP(mxgrup)
!     Common block /MFLUX/ variables
!         NBNDRY, FLXIN(mxspec,mxbndry), FLXOUT(mxspec,mxbndry)
!     Common block /OUTPT/ variables
!         IOUTOP(7,mxspec)
!
!     Parameters:
!         MXGRUP, MXSPEC, MXBNDRY, IO36
!
! --- OUTPUT:
!             none
!
! --- MFLXOUT called by: OUTPUT
! --- MFLXOUT calls:     MIDNITE
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'datehr.puf'
      include 'gen.puf'
      include 'mflux.puf'
      include 'outpt.puf'
      integer istore(mxgrup)
! --- Change hour in time label from 00 to 24 if period ends at 2400
      imo=0
      iday=0
      if(nhre.EQ.0 .AND. nsece.EQ.0) then
         call MIDNITE(io6,'TO 24h',nyre,imo,iday,njule,&
                      nyr,kmo,kday,njul)
         nhr=24
      else
         nyr=nyre
         njul=njule
         nhr=nhre
      endif
! --- Loop over groups and write those requested by user
      do i=1,ngrup
         if(ioutop(7,i).EQ.1) then
            j=istore(i)
            write(io36,99) nyr,njul,nhr,nsece,cgrup(i),&
                    (flxin(j,ib),flxout(j,ib),ib=1,nbndry)
         endif
      enddo
99    format(i4,i4,i3,i5,2x,a12,50e12.5)
      return
      end
!----------------------------------------------------------------------
      subroutine mflxcmp(ldb0,lslug,nspec,qtot)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 990228d          MFLXCMP
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Update mass flux totals for current puff, for current
!               (sub)step
!
! --- UPDATE
! --- V5.0-V5.0     990228d (DGS): remove real argument from MAX/MIN
!
! --- INPUTS:
!
!             LDB0 - logical  - Flag controlling printing of internal
!                               data (F=suppress, T=print)
!            LSLUG - logical  - Slug flag (F=puff, T=slug)
!            NSPEC - integer  - Number of species
!     QTOT(mxspec) - real     - Total average puff mass over step (g)
!
!     Common block /CURRENT/ variables
!         XB1,YB1,XE1,YE1
!         XB2,YB2,XE2,YE2
!     Common block /GRID/ variables
!         NX, NY, DGRIDI, DGRID
!     Common block /MFLUX/ variables
!         NBNDRY, LBNDRY(mxnx,mxny), YMXPBM(2,mxptbdy),
!         NPBNDRY(2,mxbndry), BNDRYGRD(2,mxptbdy),
!         FLXIN(mxspec,mxbndry), FLXOUT(mxspec,mxbndry)
!
!     Parameters:
!         MXNX, MXNY, MXNXY,
!         MXSPEC, MXBNDRY, MXPTBDY, IO6
!
! --- OUTPUT:
!     Common block /MFLUX/ variables
!         FLXIN(mxspec,mxbndry), FLXOUT(mxspec,mxbndry)
!
! --- MFLXCMP called by: COMP
! --- MFLXCMP calls:     none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'current.puf'
      include 'grid.puf'
      include 'mflux.puf'
!
      real qtot(mxspec)
      logical ldb0,lslug,ldb,lold,lnew
! --- Set local variable for a difference (MET Grid Units) small
! --- enough to be treated as ZERO
      data dsmall/0.000001/
! --- Set the factor to convert the mass accumulated over an hour
! --- to a mass/s = (1/3600)
      data persec/2.777778e-4/
! --- Introduce ldb to allow override of ldb0
      ldb=ldb0
! --- ldb=.FALSE.
! ------------------------------------------------------------
! --- Convert trajectory locations to met grid units and met
! --- cell index, and test for presence of boundary near step
! ------------------------------------------------------------
! --- Puff or Old-Slug-End
      xb1met=xb1*dgridi
      yb1met=yb1*dgridi
      xe1met=xe1*dgridi
      ye1met=ye1*dgridi
      ixb1=1+xb1met
      iyb1=1+yb1met
      ixe1=1+xe1met
      iye1=1+ye1met
      ixb1=MIN(nx,ixb1)
      iyb1=MIN(ny,iyb1)
      ixe1=MIN(nx,ixe1)
      iye1=MIN(ny,iye1)
      ixb1=MAX(1,ixb1)
      iyb1=MAX(1,iyb1)
      ixe1=MAX(1,ixe1)
      iye1=MAX(1,iye1)
! --- Check cells in step range to see if any contain a boundary
      lold=.FALSE.
      idy=1
      if(iyb1.GT.iye1) idy=-1
      idx=1
      if(ixb1.GT.ixe1) idx=-1
      do iy=iyb1,iye1,idy
         do ix=ixb1,ixe1,idx
            if(LBNDRY(ix,iy)) lold=.TRUE.
         enddo
      enddo
! --- Process new-slug-end locations if SLUG
      lnew=.FALSE.
      if(LSLUG) then
         xb2met=xb2*dgridi
         yb2met=yb2*dgridi
         xe2met=xe2*dgridi
         ye2met=ye2*dgridi
         ixb2=1+xb2met
         iyb2=1+yb2met
         ixe2=1+xe2met
         iye2=1+ye2met
         ixb2=MIN(nx,ixb2)
         iyb2=MIN(ny,iyb2)
         ixe2=MIN(nx,ixe2)
         iye2=MIN(ny,iye2)
         ixb2=MAX(1,ixb2)
         iyb2=MAX(1,iyb2)
         ixe2=MAX(1,ixe2)
         iye2=MAX(1,iye2)
         idy=1
         if(iyb2.GT.iye2) idy=-1
         idx=1
         if(ixb2.GT.ixe2) idx=-1
         do iy=iyb2,iye2,idy
            do ix=ixb2,ixe2,idx
               if(LBNDRY(ix,iy)) lnew=.TRUE.
            enddo
         enddo
      endif
! --- Assume mass of slug is divided evenly between both ends
      factor=persec
      if(LSLUG) factor=0.5*factor
      do n=1,nspec
         qtot(n)=factor*qtot(n)
      enddo
10    continue
      if(LOLD) then
! ---    Set up for a puff, or the old end of a slug
         xs=xb1met
         ys=yb1met
         xe=xe1met
         ye=ye1met
         ixs=ixb1
         iys=iyb1
         ixe=ixe1
         iye=iye1
         lold=.FALSE.
      elseif(LNEW) then
! ---    Set up for the new end of a slug
         xs=xb2met
         ys=yb2met
         xe=xe2met
         ye=ye2met
         ixs=ixb2
         iys=iyb2
         ixe=ixe2
         iye=iye2
         lnew=.FALSE.
      else
         goto 999
      endif
! ---    DEBUG Output Section
! ---------------------------
      if(LDB) then
         write(io6,*)
         write(io6,*)'MFLXCMP ---------------'
         write(io6,*)'Type of puff/slug (LOLD,LNEW): ',lold,lnew
         write(io6,*)'Mass/s contributed by this puff/slug-end:'
         write(io6,*)(qtot(n),n=1,nspec)
         write(io6,*)'Boundary segments screened for intersection in'
         write(io6,*)'(ixs,iys) to (ixe,iye): ',ixs,iys,ixe,iye
      endif
! -------------------------------------------------------------
! --- Process boundary segment(s) that puff trajectory crosses
! -------------------------------------------------------------
! --- Set sampling step vector components and length
      dxp=xe-xs
      dyp=ye-ys
      dp0=ABS(dxp)+ABS(dyp)
      if(dp0.LE.dsmall) then
! ---    This puff is not moving across any boundaries
         goto 999
      elseif(ABS(dxp).LE.dsmall) then
! ---    Trajectory is assumed to be vertical (N/S)
         slope=9.91e10
         bintr=9.91e10
      else
         slope=dyp/dxp
         bintr=ys-slope*xs
      endif
! --- Set stepping index for sampling step
      idy=1
      if(iys.GT.iye) idy=-1
      idx=1
      if(ixs.GT.ixe) idx=-1
! --- Loop over boundaries
      do ib=1,nbndry
! ---    Initialize intercept results to NULL values
         numseg=0
! ---    Loop over segments in boundary
         do is=npbndry(1,ib),npbndry(2,ib)-1
            ixc1=1+bndrygrd(1,is)
            iyc1=1+bndrygrd(2,is)
            ixc2=1+bndrygrd(1,is+1)
            iyc2=1+bndrygrd(2,is+1)
! ---       Set stepping index for boundary segments
            idyc=1
            if(iyc1.GT.iyc2) idyc=-1
            idxc=1
            if(ixc1.GT.ixc2) idxc=-1
! ---       Check for overlap of segment cells and sampling step cells
            isame=0
            do iy=iys,iye,idy
            do ix=ixs,ixe,idx
              do jy=iyc1,iyc2,idyc
              do jx=ixc1,ixc2,idxc
                if(ix.EQ.jx .AND. iy.EQ.jy) isame=isame+1
              enddo
              enddo
            enddo
            enddo
            if(isame.GT.0) then
! ---          Test this segment for an intercept
               dxc=bndrygrd(1,is+1)-bndrygrd(1,is)
               dyc=bndrygrd(2,is+1)-bndrygrd(2,is)
! ---          Boundary Vector [CROSS] Puff Vector is positive
! ---          when trajectory is from IN to OUT
               cxp=dxc*dyp-dxp*dyc
! ---          Find intercept
               if(slope.GT.9.9e10) then
                  xint=xs
                  yint=ymxpbm(1,is)*xint+ymxpbm(2,is)
               elseif(ymxpbm(1,is).GT.9.9e10) then
                  xint=bndrygrd(1,is)
                  yint=slope*xint+bintr
               else
                  xint=(bintr-ymxpbm(2,is))/(ymxpbm(1,is)-slope)
                  yint=slope*xint+bintr
               endif
! ---          Intercept must be within the line segment
               if(ymxpbm(1,is).GT.9.9e10) then
                  frac1=(yint-bndrygrd(2,is))/dyc
               else
                  frac1=(xint-bndrygrd(1,is))/dxc
               endif
! ---          Intercept must be within the sampling step
               if(slope.GT.9.9e10) then
                  frac2=(yint-ys)/dyp
               else
                  frac2=(xint-xs)/dxp
               endif
! ---    DEBUG Output Section
! ---------------------------
         if(LDB) then
         write(io6,*)'Segment,FRAC1,FRAC2:',is,frac1,frac2
         endif
               if((frac1.GE.0.0 .AND. frac1.LE.1.0) .AND.&
                  (frac2.GE.0.0 .AND. frac2.LE.1.0)) then
! ---             Path has crossed boundary at this segment
                  numseg=numseg+1
                  if(cxp.GT.0.) then
                     do n=1,nspec
                        flxout(n,ib)=flxout(n,ib)+qtot(n)
                     enddo
                  elseif(cxp.LT.0.) then
                     do n=1,nspec
                        flxin(n,ib)=flxin(n,ib)+qtot(n)
                     enddo
                  endif
               endif
            endif
         enddo
! ---    DEBUG Output Section
! ---------------------------
         if(LDB) then
         write(io6,*)'Boundary:',ib,'  Segments crossed: ',numseg
         endif
      enddo
      goto 10
999   return
      end
