!
!     Parameters:
!         IO37, IO6
!
! --- OUTPUT:
!
!     Common block /MSBAL/ variables
!         LBC
!
! --- MBALHDR called by: SETUP
! --- MBALHDR calls:     none
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      include 'msbal.puf'
!
      character*80 title(3)
      character*16 datehr
      character*12 specnm
      character*13 cline1(8),cline2(8),clinebc(8)
      data cline1/'             ','   Advected  ','             ',&
                  '     Wet     ','     Dry     ','    Domain   ',&
                  '    Domain   ','    Domain   '/
      data clinebc/'     BC      ',' BC Advected ','      BC     ',&
                  '   BC Wet    ','   BC Dry    ','  BC Domain  ',&
                  '  BC Domain  ','  BC Domain  '/
      data cline2/'   Emitted   ','     Out     ',' Transformed ',&
                  '  Depletion  ','  Depletion  ','     Total   ',&
                  '    Surface  ','     Aloft   '/
      data datehr/'Year Day Hr Sec '/
      data specnm/'  Species   '/
      write(io37,'(1x,a)') title
      write(io37,*)
      write(io37,'(t30,a)')'-----------------------'
      write(io37,'(t30,a)')'Mass Balance (Timestep)'
      write(io37,'(t30,a)')'         (g)'
      write(io37,'(t30,a)')'-----------------------'
      write(io37,*)
      if(mbcon.GT.0) then
         lbc=.TRUE.
         write(io37,'(28x,3x,16a13)') cline1,clinebc
         write(io37,'(a16,a12,3x,16a13)') datehr,specnm,cline2,cline2
      else
         lbc=.FALSE.
         write(io37,'(28x,3x,8a13)') cline1
         write(io37,'(a16,a12,3x,8a13)') datehr,specnm,cline2
      endif
      write(io37,*)
      return
!
      end
