!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'datehr.puf'
      include 'gen.puf'
      include 'msbal.puf'
! --- Change hour in time label from 00 to 24 if period ends at 2400
      imo=0
      iday=0
      if(nhre.EQ.0 .AND. nsece.EQ.0) then
         call MIDNITE(io6,'TO 24h',nyre,imo,iday,njule,&
                      nyr,kmo,kday,njul)
         nhr=24
      else
         nyr=nyre
         njul=njule
         nhr=nhre
      endif
! --- Loop over species
      do i=1,nspec
         total=baltot(i,1)+baltot(i,2)
