                           baltrnbc(i),balwetbc(i),baldrybc(i),totalbc,&
                           baltotbc(i,1),baltotbc(i,2)
         else
            write(io37,99) nyr,njul,nhr,nsece,&
                           cspec(i),balinp(i),balout(i),&
                           baltrn(i),balwet(i),baldry(i),total,&
                           baltot(i,1),baltot(i,2)
         endif
      enddo
98    format(i4,i4,i3,i5,2x,a12,16(1pe13.5))
99    format(i4,i4,i3,i5,2x,a12,8(1pe13.5))
      return
      end
!----------------------------------------------------------------------
      subroutine mbalsum(atype,qbsfc,qbupr,qesfc,qeupr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 991222           MBALSUM
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Update mass balance totals for current puff, for current
!               (sub)step
!
! --- UPDATE
! --- V5.2-V5.3     991222  (DGS): add treatment for boundary condition
!                                  mass
!
! --- INPUTS:
!
!            ATYPE - char*3   - Mass balance component name:
!                                 INP, OUT, TRN, WET, DRY, TOT
!                                 IBC, OBC, TBC, WBC, DBC, SBC
!    QBSFC(mxspec) - real     - Puff mass (g) in surface layer
!                               before gain/loss
!    QBUPR(mxspec) - real     - Puff mass (g) above surface layer
!                               before gain/loss
!    QESFC(mxspec) - real     - Puff mass (g) in surface layer
!                               after gain/loss
!    QEUPR(mxspec) - real     - Puff mass (g) above surface layer
!                               after gain/loss
!
!     Common block /GEN/ variables
!         NSPEC
!     Common block /MSBAL/ variables
!         BALINP(mxspec),BALOUT(mxspec),BALTRN(mxspec),BALWET(mxspec),
!         BALDRY(mxspec),BALTOT(mxspec,2)
!         BALINPBC(mxspec),BALOUTBC(mxspec),BALTRNBC(mxspec),
!         BALWETBC(mxspec),BALDRYBC(mxspec),BALTOTBC(mxspec,2)
!
!     Parameters:
!         MXSPEC, IO6
!
! --- OUTPUT:
!     Common block /MSBAL/ variables
!         BALINP(mxspec),BALOUT(mxspec),BALTRN(mxspec),BALWET(mxspec),
!         BALDRY(mxspec),BALTOT(mxspec,2)
!         BALINPBC(mxspec),BALOUTBC(mxspec),BALTRNBC(mxspec),
!         BALWETBC(mxspec),BALDRYBC(mxspec),BALTOTBC(mxspec,2)
!
! --- MBALSUM called by: COMP, SPLIT
! --- MBALSUM calls:     none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'gen.puf'
      include 'msbal.puf'
!
      real qbsfc(mxspec),qesfc(mxspec),qbupr(mxspec),qeupr(mxspec)
      real qdiff(mxspec)
      character*3 atype
      data zero/0.0/
! --- Loop over species to obtain change in mass
      do i=1,nspec
         qdiffs=qesfc(i)-qbsfc(i)
         qdiffu=qeupr(i)-qbupr(i)
         qdiff(i)=qdiffs+qdiffu
      enddo
! --- Loop over mass balance components
      if(atype.EQ.'INP') then
! ---    Sum emissions this period
         do i=1,nspec
            balinp(i)=balinp(i)+qesfc(i)+qeupr(i)
         enddo
      elseif(atype.EQ.'OUT') then
! ---    Sum puff mass lost from domain this period
         do i=1,nspec
            balout(i)=balout(i)+qesfc(i)+qeupr(i)
         enddo
      elseif(atype.EQ.'TRN') then
! ---    Sum mass change due to chem transformation this period
         do i=1,nspec
            if(qdiff(i).NE.zero) baltrn(i)=baltrn(i)+qdiff(i)
         enddo
      elseif(atype.EQ.'WET') then
! ---    Sum wet depletion this period (as a positive loss)
         do i=1,nspec
            if(qdiff(i).NE.zero) balwet(i)=balwet(i)-qdiff(i)
         enddo
      elseif(atype.EQ.'DRY') then
! ---    Sum dry depletion this period (as a positive loss)
         do i=1,nspec
            if(qdiff(i).NE.zero) baldry(i)=baldry(i)-qdiff(i)
         enddo
      elseif(atype.EQ.'TOT') then
! ---    Sum total puff mass in domain at end of this period
         do i=1,nspec
            baltot(i,1)=baltot(i,1)+qesfc(i)
            baltot(i,2)=baltot(i,2)+qeupr(i)
         enddo
      elseif(atype.EQ.'IBC') then
! ---    Sum emissions this period
         do i=1,nspec
            balinpbc(i)=balinpbc(i)+qesfc(i)+qeupr(i)
         enddo
      elseif(atype.EQ.'OBC') then
! ---    Sum puff mass lost from domain this period
         do i=1,nspec
            baloutbc(i)=baloutbc(i)+qesfc(i)+qeupr(i)
         enddo
      elseif(atype.EQ.'TBC') then
! ---    Sum mass change due to chem transformation this period
         do i=1,nspec
            if(qdiff(i).NE.zero) baltrnbc(i)=baltrnbc(i)+qdiff(i)
         enddo
      elseif(atype.EQ.'WBC') then
! ---    Sum wet depletion this period (as a positive loss)
         do i=1,nspec
            if(qdiff(i).NE.zero) balwetbc(i)=balwetbc(i)-qdiff(i)
         enddo
      elseif(atype.EQ.'DBC') then
! ---    Sum dry depletion this period (as a positive loss)
         do i=1,nspec
            if(qdiff(i).NE.zero) baldrybc(i)=baldrybc(i)-qdiff(i)
         enddo
      elseif(atype.EQ.'SBC') then
! ---    Sum total puff mass in domain at end of this period
         do i=1,nspec
            baltotbc(i,1)=baltotbc(i,1)+qesfc(i)
            baltotbc(i,2)=baltotbc(i,2)+qeupr(i)
         enddo
      else
! ---    PROBLEM
         write(io6,*) 'Fatal Error in MBALSUM ---'
         write(io6,*) 'Mass balance component not recognized: ',atype
         write(*,*)
         stop 'Fatal Error in MBALSUM --- see list file.'
      endif
      return
      end
