      character*12 cvdic(mxvar,mxsg0),cvdic3(mxvar,mxsg3)
      integer ivleng(mxvar,mxsg0),ivleng3(mxvar,mxsg3)
      integer ivtype(mxvar,mxsg0),ivtype3(mxvar,mxsg3)
      data lecho/.false./,lgridbc/.false./,lemit/.false./
      data nsew/'N','S','E','W'/
! --- Identify input groups with letters as continuation character;
! --- and input subgroups with numbers as continuation character.
      data cvdic/&
        'LBLBC','VRSBC','NBCX','NBCY','IBCU','NTYPEBC1','NTYPEBC2',&
        'NSPECBC','NSBC',  51*' ',&
        60*' ',&
        'AIRMASS','X',58*' ',&
        'AIRMASS','IVARY',58*' ',&
! ---  (Note: the 58*' ' above includes mxspec entries)
        'AIRMASS','SPECIES','TYPE','FACTOR', 56*' ',&
        'NORTH','WEST_EAST','SOUTH', 57*' ',&
        'AIRMASS','X',58*' ',&
        'IBDAT','IBHH','IEDAT','IEHH',56*' '/
      data cvdic3/&
        'CSPEC', 59*' '/
      data ivleng/&
                   12,8*1, 51*0,&
                   60*0,&
                   16,mxp2, 58*0,&
                   16,1, 58*0,&
! ---             (Note: the 58*0 above includes mxspec entries)
                   16,12,16,mxqsf, 56*0,&
                   mxnx,2,mxnx, 57*0,&
                   16,1, 58*0,&
                   4*1, 56*0/
      data ivleng3/&
                   12, 59*0/
! --- Variable types (ivtype) are coded as:
!          0 = null
!          1 = real
!          2 = integer
!          3 = logical
!          4 = character
!          5 = character list (csv)
      data ivtype/&
                   4,1,7*2, 51*0,&
                   60*0,&
                   4,1, 58*0,&
                   4,2, 58*0,&
! ---             (Note:  the 58*0 includes mxspec entries of type 1)
                   5,5,4,1, 56*0,&
                   3*2, 57*0,&
                   4,2, 58*0,&
                   4*2, 56*0/
      data ivtype3/&
                   4, 59*0/
!
! --- Set valid version number to 5
      data iver/5/
!
! --- Set "problem" and "warning" markers to FALSE
      data problem/.FALSE./, lwarn/.FALSE./
! --- Set fractional offset from edge of domain for BC source location
! --- to 0.1% of grid cell
      data fuzz/.001/
! --- Initialize dataset version to 0.0
      vrsbc=0.0
! --- Set dataset type
      fnamebc='BCON        '
! --- Set coordinates for corners (met grid units)
      xbcw=ibcomp-1
      ybcs=jbcomp-1
      xbce=iecomp
      ybcn=jecomp
!
!
! --------------------------------------------
! --- General run control parameters -- IG # 1
! --------------------------------------------
!
! --- Clear temporary array for reading dataset label
      do j=1,12
         ctemp(j)(1:1)=' '
      enddo
      call readin(cvdic(1,1),ivleng(1,1),ivtype(1,1),io15,io6,lecho,&
       CTEMP,VRSBC,NBCX,NBCY,IBCU,NTYPEBC1,NTYPEBC2,NSPECBC,NSBC,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum)
!
! --- Transfer CHAR*4 names into CHAR*12 dataset label
      lblbc='            '
      do j=1,12
         lblbc(j:j)=ctemp(j)(1:1)
      enddo
! --- Compute segment lengths
      nxgrid=iecomp-ibcomp+1
      nygrid=jecomp-jbcomp+1
      xmesh=FLOAT(nxgrid)/FLOAT(nbcx)
      ymesh=FLOAT(nygrid)/FLOAT(nbcy)
      xlenbc=dgrid*xmesh
      ylenbc=dgrid*ymesh
      nbc=2*(nbcx+nbcy)
      
! --- QA checks
! -------------
! --- Check version (must be version 5.0 or greater)
      ivrsbc=vrsbc
      if(ivrsbc.lt.iver)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,13)vrsbc,iver
13       format(/1x,'ERROR in SUBR. RDHDBC -- version of ',&
         ' BCON file is not acceptable to CALPUFF'/1x,&
         '        Version of BCON file: ',f5.2/1x,&
         '   Expected at least Version: ',i5)
         problem=.TRUE.
      endif
! --- Compare segments with computational grid
      if(LGRIDBC) then
! ---    Segments must align with grid cells on computational grid
         if(nbcx.NE.nxgrid .OR. nbcy.NE.nygrid) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. RDHDBC -- boundary segments',&
                        ' must align with computational grid: '
            write(io6,*)'Segment NBCX,NBCY = ',nbcx,nbcy
            write(io6,*)'Comp Grid NX,NY   = ',nxgrid,nygrid
            problem=.TRUE.
         endif
      else
! ---    Warn if segments do not align with grid cells
         if(nbcx.NE.nxgrid .OR. nbcy.NE.nygrid) then
            write(io6,*)
            write(io6,*)'********  WARNING  **********'
            write(io6,*)'RDHDBC -- boundary segments do not align',&
                        ' with computational grid (may be OK): '
            write(io6,*)'Segment NBCX,NBCY = ',nbcx,nbcy
            write(io6,*)'Comp Grid NX,NY   = ',nxgrid,nygrid
            lwarn=.TRUE.
         endif
! ---    Apply weaker constraints
! ---    (a) number of boundary segments >= number of grid cells
         if(nbcx.GT.nxgrid .OR. nbcy.GT.nygrid) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. RDHDBC -- too many boundary ',&
                        'segments -- '
            write(io6,*)'Segment NBCX,NBCY = ',nbcx,nbcy
            write(io6,*)'Comp Grid NX,NY   = ',nxgrid,nygrid
            problem=.TRUE.
         endif
! ---    (b) segment lengths along X,Y must be within 10%
          ratio=xlenbc/ylenbc
          if(ratio.GT.1.1 .OR. ratio.LT.0.9) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. RDHDBC -- length of boundary ',&
                        'segments differ more than 10% -- '
            write(io6,*)'Segment length X,Y = ',xlenbc,ylenbc
            problem=.TRUE.
          endif
       endif
! --- Check units selection (only 4 types)
      if(ibcu.LT.1 .OR. ibcu.GT.4) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- units choice must be ',&
         '1-4  -- ','IBCU = ',ibcu
         problem=.TRUE.
      endif
! --- Check number of air-mass types
      ntypebc=ntypebc1+ntypebc2
      if(ntypebc.gt.mxbc)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- too many air-mass ',&
         'types -- ','NTYPEBC = ',ntypebc,' MXBC = ',mxbc
         problem=.TRUE.
      endif
! --- Check number of species
      if(nspecbc.gt.nspec)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- number of species ',&
         'exceeds number modeled -- '
         write(io6,*)'NSPECBC = ',nspecbc,' NSPEC = ',nspec
         problem=.TRUE.
      endif
! --- Check number of species/air-mass types
      ntest=nspecbc*ntypebc
      if(nsbc.gt.ntest)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- number of species/ ',&
         'air-mass combinations exceeds number possible -- '
         write(io6,*)'NSBC = ',nsbc,' NSPECBC*NTYPEBC = ',ntest
         problem=.TRUE.
      endif
!
! --------------------------
! --- Species list -- IG # 2
! --------------------------
!
      do i=1,nspecbc
         do j=1,12
            ctemp(j)(1:1)=' '
         enddo
         call readin(cvdic3(1,1),ivleng3(1,1),ivtype3(1,1),io15,io6,&
         lecho,&
         CTEMP,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 species name into CHAR*12 variable
         cspecbc(i)='            '
         do j=1,12
            cspecbc(i)(j:j)=ctemp(j)(1:1)
         enddo
      enddo
!
! --- Transfer species names from master species list into variable
! --- dictionary for use in subsequent input groups
      do i=1,mxspec
! ---    Group 2
         cvdic(i,2)=cspecbc(i)
         ivleng(i,2)=4
         ivtype(i,2)=2
!
! ---    Group 3b
! ---    NOTE: ip2 variable needs to be adjusted if any changes in
! ---    the variables list of Input Group 3b occurs (before vqtemp)
         ip2=i+2
         cvdic(ip2,4)=cspecbc(i)
         ivleng(ip2,4)=96
         ivtype(ip2,4)=1
      enddo
!
! --- Read molecular weights (for ppm, ppb units)
! ------------------------------------------------
      if(ibcu.EQ.3 .OR. ibcu.EQ.4) then
! --- The following call is set for MXSPEC=35
      call readin(cvdic(1,2),ivleng(1,2),ivtype(1,2),io15,io6,lecho,&
       XMWTBC(1),XMWTBC(2),XMWTBC(3),XMWTBC(4),XMWTBC(5),&
       XMWTBC(6),XMWTBC(7),XMWTBC(8),XMWTBC(9),XMWTBC(10),&
       XMWTBC(11),XMWTBC(12),XMWTBC(13),XMWTBC(14),XMWTBC(15),&
       XMWTBC(16),XMWTBC(17),XMWTBC(18),XMWTBC(19),XMWTBC(20),&
       XMWTBC(21),XMWTBC(22),XMWTBC(23),XMWTBC(24),XMWTBC(25),&
       XMWTBC(26),XMWTBC(27),XMWTBC(28),XMWTBC(29),XMWTBC(30),&
       XMWTBC(31),XMWTBC(32),XMWTBC(33),XMWTBC(34),XMWTBC(35),&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
! *** call readin(cvdic(1,2),ivleng(1,2),ivtype(1,2),io15,io6,lecho,
! ***1 XMWTBC(1),XMWTBC(2),XMWTBC(3),XMWTBC(4),XMWTBC(5),
! ***2 XMWTBC(6),XMWTBC(7),XMWTBC(8),XMWTBC(9),XMWTBC(10),
! ***3 XMWTBC(11),XMWTBC(12),XMWTBC(13),XMWTBC(14),XMWTBC(15),
! ***4 XMWTBC(16),XMWTBC(17),XMWTBC(18),XMWTBC(19),XMWTBC(20),
! ***5 XMWTBC(21),XMWTBC(22),XMWTBC(23),XMWTBC(24),
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***8 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***9 idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=5
! *** call readin(cvdic(1,2),ivleng(1,2),ivtype(1,2),io15,io6,lecho,
! ***1 XMWTBC(1),XMWTBC(2),XMWTBC(3),XMWTBC(4),XMWTBC(5),
! ***2 idum,idum,idum,idum,idum,
! ***3 idum,idum,idum,idum,idum,
! ***4 idum,idum,idum,idum,idum,
! ***5 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7 idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***8 idum,idum,idum,idum)
      endif
      
! --- QA checks
! -------------
! --- V6.42_x1.0, Level 121203
      ierr=0
      call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,lemit,&
       fnamebc,nspecbc,cspecbc,xmwtbc,ierr,ixrembc)
      if(ierr.EQ.1) problem=.TRUE.
! --- Halt processing now if errors were found
      write(io6,*)
      if(PROBLEM) goto 998
!
! -------------------------------------------------------------------
! --- Air-mass information (Constant or Factored Variation) -- IG # 3
! -------------------------------------------------------------------
!
! --- Subgroup (3a) -- Set data for each air-mass type provided
! -----------------
!
      do 150 i=1,ntypebc1
! --- Clear temporary array for type name
      do j=1,16
         ctemp(j)(1:1)=' '
      enddo
!
      call readin(cvdic(1,3),ivleng(1,3),ivtype(1,3),io15,io6,lecho,&
       CTEMP,BC1DATA,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- Extract air-mass index
      iam=NINT(bc1data(1))
      if(iam.GT.ntypebc) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- air-mass type index ',&
         'exceeds number of types -- ',i,'  index = ',iam
         problem=.TRUE.
      endif
!
! --- Transfer CHAR*4 name array into CHAR*16 variable for type name
      do j=1,16
         cnamtyp(iam)(j:j)=ctemp(j)(1:1)
      enddo
! --- Current name must be new
         do j=1,ntypebc
            if(cnamtyp(j).EQ.cnamtyp(iam) .AND. j.NE.iam) then
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR in subr. RDHDBC -- name duplicated ',&
               'for types -- ',j,iam,'  name = ',cnamtyp(iam)
               problem=.TRUE.
            endif
         enddo
!
! --- Transfer the air-mass data to the common block arrays
      htbc(iam)=bc1data(2)
! --- Apply units conversion factor to obtain concentrations in g/m**3
      if(ibcu.EQ.1 .OR. ibcu.EQ.2) then
         do j=1,nspecbc
            conbc(j,iam)=bc1data(2+j)*funitsbc(ibcu)
         enddo
      else
         do j=1,nspecbc
            conbc(j,iam)=bc1data(2+j)*funitsbc(ibcu)*xmwtbc(j)
         enddo
      endif
!
150   continue
!
! --- Subgroup (3b) -- Variable concentration scaling factors
! -----------------
!
! --- Method for specifying emission scale-factors changed in
! --- CALPUFF control-file dataset version TNG-3.1 / 7.0
! --- BCON.DAT has not been updated, but adopt the storage and
! --- use structures
! --- Reset the temperature ranges to be consistent with the IVARY
! --- documentation (0,5,10,15,20,25,30,35,40,45,50 C)
! --- BCON is source type 9
      do i=1,11
         tqsf(i,9)=273.16+5.0*(i-1)
      enddo
! --- Loop over source-species combinations
      nqsfbctab=0
      do i=1,nsbc
! ---    Clear temporary arrays
         vqtemp=0.0
         do j=1,16
            ctemp(j)(1:1)=' '
         enddo
!
! --- The following call is set for MXSPEC=35
         call readin(cvdic(1,4),ivleng(1,4),ivtype(1,4),io15,io6,&
            lecho,&
         CTEMP,IVARY,&
         VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),&
         VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),&
         VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),&
         VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),&
         VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),&
         VQTEMP(1,23),VQTEMP(1,24),VQTEMP(1,25),VQTEMP(1,26),&
         VQTEMP(1,27),VQTEMP(1,28),VQTEMP(1,29),VQTEMP(1,30),&
         VQTEMP(1,31),VQTEMP(1,32),VQTEMP(1,33),VQTEMP(1,34),&
         VQTEMP(1,35),                                              &
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
         idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! --- The following call is set for MXSPEC=24
! ***    call readin(cvdic(1,4),ivleng(1,4),ivtype(1,4),io15,io6,
! ***1      lecho,
! ***1   CTEMP,IVARY,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   VQTEMP(1,6),VQTEMP(1,7),VQTEMP(1,8),VQTEMP(1,9),VQTEMP(1,10),
! ***4   VQTEMP(1,11),VQTEMP(1,12),VQTEMP(1,13),VQTEMP(1,14),
! ***5   VQTEMP(1,15),VQTEMP(1,16),VQTEMP(1,17),VQTEMP(1,18),
! ***6   VQTEMP(1,19),VQTEMP(1,20),VQTEMP(1,21),VQTEMP(1,22),
! ***7   VQTEMP(1,23),VQTEMP(1,24),
! ***8   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***9   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***a   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
! --- The following call is set for MXSPEC=5
! ***    call readin(cvdic(1,4),ivleng(1,4),ivtype(1,4),io15,io6,
! ***1      lecho,
! ***1   CTEMP,IVARY,
! ***2   VQTEMP(1,1),VQTEMP(1,2),VQTEMP(1,3),VQTEMP(1,4),VQTEMP(1,5),
! ***3   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***4   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***5   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***6   idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,
! ***7   idum,idum,idum,idum,idum)
!
! ---    Transfer CHAR*4 source name into CHAR*16 variable for type name
         cnambc='                '
         do j=1,16
            cnambc(j:j)=ctemp(j)(1:1)
         enddo
! ---    Scan variable emissions factors for negatives
         lqneg=.FALSE.
         do iv=1,96
            do isp=1,mxspec
               if(vqtemp(iv,isp).LT.0.0) lqneg=.TRUE.
            enddo
         enddo
! ---    Report problem if any factors are negative
         if(lqneg) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. RDHDBC -- Invalid scaling',&
          ' factor  (must NOT be negative) -- BCON Source = ',cnambc
            problem=.TRUE.
         endif
! ---    Match air-mass type name to index
         call SMATCH(io6,cnambc,ntypebc,cnamtyp,isrc)
! ---    Check IVARY
         if(ivary.LT.0 .OR. ivary.GT.5) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. RDHDBC -- type of variable',&
            ' concentration factor invalid:',' type = ',ivary
            write(io6,*)'                         Valid type is 1-5'
            problem=.TRUE.
         else
! ---       Map IVARY (0-5) into corresponding KVARY index
            kvary=mapivary(ivary+1)
! ---       Find species (a value of VQTEMP will be >0.0)
            iv_loop_bc: do iv=1,96
               do isp=1,mxspec
                  if(vqtemp(iv,isp).GT.0.0) EXIT iv_loop_bc
               enddo
            enddo iv_loop_bc
! ---       Make table name
            csfbc1(i)='BC_'//TRIM(cnambc)//'_'//TRIM(cspec(isp))
! ---       Set pointers and scale factor table information
            nqsfbctab=nqsfbctab+1
            idsfbc1(isp,isrc)=i
            ixrefbc1(i)=nqsfbctab
            cqsfbcname(nqsfbctab)=csfbc1(i)
            iqsfbctype(nqsfbctab)=kvary
! ---       Place factors into array
            if(kvary.NE.6) then
               do iv=1,96
                  qsfbctab(iv,i)=vqtemp(iv,isp)
               enddo
            else
! ---          Distribute seasonal factors (24*4) to months (24*12)
! ---          where season 1 is DEC-JAN-FEB
               iv=0
               do imonth=1,12
                  if(imonth.EQ.12 .OR. imonth.LE.2) then
! ---                Season 1
                     jv=0
                  elseif(imonth.LE.5) then
! ---                Season 2
                     jv=24
                  elseif(imonth.LE.8) then
! ---                Season 3
                     jv=48
                  else
! ---                Season 4
                     jv=72
                  endif
                  do ihour=1,24
                     iv=iv+1
                     jv=jv+1
                     qsfbctab(iv,i)=vqtemp(jv,isp)
                  enddo
               enddo
            endif
         endif
      enddo
! --- End Subgroup (3b) input
! --- QA checks
! -------------
! --- Check for duplication of air-mass/species combinations
      number=0
      do j=1,ntypebc
         do i=1,nspecbc
! ---       Species index in master list
            is=ixrembc(i)
            if(idsfbc1(is,j).GT.0) number=number+1
         enddo
      enddo
      if(number.NE.nsbc) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- fewer than NSBC ',&
         'air-mass/species combinations found for variation factors'
         write(io6,*)'Valid air-mass/species expected: ',nsbc
         write(io6,*)'Valid air-mass/species found   : ',number
         do j=1,ntypebc
            do i=1,nspecbc
               is=ixrembc(i)
               if(idsfbc1(is,j).GT.0) write(io6,*)&
                  cnamtyp(j),' / ',cspecbc(i)
            enddo
         enddo
         problem=.TRUE.
      endif
! --- Halt processing now if errors were found
      write(io6,*)
      if(PROBLEM) goto 998
!
! --------------------------
! --- Air-mass map -- IG # 4
! --------------------------
! --- NBCY + 2 valid 'subgroups' are expected in this group
! --- First must be NORTH, and last must be SOUTH
      j=0
      ilast=nbcy+2
      do i=1,ilast
! ---    Set initial value in each array to 0
         inorth(1)=0
         isouth(1)=0
         iw_e(1)=0
      call readin(cvdic(1,6),ivleng(1,6),ivtype(1,6),io15,io6,lecho,&
       INORTH,IW_E,ISOUTH,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum)
! ---   Process 'subgroup'
        if(i.EQ.1) then
           if(iw_e(1).GT.0 .OR. isouth(1).GT.0) then
              write(io6,*)
              write(io6,*)'*********  FATAL  ***********'
              write(io6,*)'ERROR in subr. RDHDBC -- first map record ',&
              'must be the North boundary '
              problem=.TRUE.
              goto 998
           else
              ygrd=ybcn-fuzz
              do ix=1,nbcx
                 j=j+1
                 ibc(j)=1
                 itypebc(j)=inorth(ix)
                 xbcgrd(j)=xbcw+xmesh*(FLOAT(ix)-.5)
                 ybcgrd(j)=ygrd
              enddo
           endif
        elseif(i.EQ.ilast) then
           if(iw_e(1).GT.0 .OR. inorth(1).GT.0) then
              write(io6,*)
              write(io6,*)'*********  FATAL  ***********'
              write(io6,*)'ERROR in subr. RDHDBC -- last map record ',&
              'must be the South boundary '
              problem=.TRUE.
              goto 998
           else
              ygrd=ybcs+fuzz
              do ix=1,nbcx
                 j=j+1
                 ibc(j)=2
                 itypebc(j)=isouth(ix)
                 xbcgrd(j)=xbcw+xmesh*(FLOAT(ix)-.5)
                 ybcgrd(j)=ygrd
              enddo
           endif
        else
           if(inorth(1).GT.0 .OR. isouth(1).GT.0) then
              write(io6,*)
              write(io6,*)'*********  FATAL  ***********'
              write(io6,*)'ERROR in subr. RDHDBC -- middle map records',&
              ' must be for the West and East boundaries'
              problem=.TRUE.
              goto 998
           else
              ygrd=ybcn-ymesh*(FLOAT(i-1)-.5)
              j1=j+1
              j2=j+2
              ibc(j1)=4
              ibc(j2)=3
              itypebc(j1)=iw_e(1)
              itypebc(j2)=iw_e(2)
              xbcgrd(j1)=xbcw+fuzz
              xbcgrd(j2)=xbce-fuzz
              ybcgrd(j1)=ygrd
              ybcgrd(j2)=ygrd
              j=j2
           endif
        endif
      enddo
      
! --- QA check
! ------------
! --- No air-mass type should be outside the range 1 to ntypebc
      do i=1,nbc
         if(itypebc(i).LT.1 .OR. itypebc(i).GT.ntypebc) problem=.TRUE.
      enddo
      if(PROBLEM) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- air-mass map incomplete',&
         ' No air-mass type should be outside the range 1 to NTYPEBC'
         write(io6,*)' NTYPEBC = ',ntypebc
         goto 998
      endif
!
! --------------------------------------------------------------
! --- Air-mass information (Arbitrary Time Variation) -- IG # 5
! --------------------------------------------------------------
!
! --- Subgroup (5a) -- Set data for each air-mass type provided
! -----------------
!
      do i=1,ntypebc2
! ---   Clear temporary array for type name
         do j=1,12
            ctemp(j)(1:1)=' '
         enddo
!
      call readin(cvdic(1,7),ivleng(1,7),ivtype(1,7),io15,io6,lecho,&
       CTEMP,IAM,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum)
!
! ---    QA air-mass index
         if(iam.GT.ntypebc) then
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. RDHDBC -- air-mass type index ',&
            'exceeds number of types -- ',i,'  index = ',iam
            problem=.TRUE.
         endif
!
! ---    Transfer CHAR*4 name array into CHAR*12 variable for type
         do j=1,12
            cnamtyp(iam)(j:j)=ctemp(j)(1:1)
         enddo
! ---    Current name must be new
         do j=1,ntypebc
            if(cnamtyp(j).EQ.cnamtyp(iam) .AND. j.NE.iam) then
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR in subr. RDHDBC -- name duplicated ',&
               'for types -- ',j,iam,'  name = ',cnamtyp(iam)
               problem=.TRUE.
            endif
         enddo
      enddo
!
! --- Subgroup (5b) -- Define time-period provided
! -----------------
!
      if(ntypebc2.GT.0) then
      call readin(cvdic(1,8),ivleng(1,8),ivtype(1,8),io15,io6,lecho,&
       IBYDBC,IBHBC,IEYDBC,IEHBC,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,idum,&
       idum,idum,idum,idum,idum,idum,idum,idum)
! ---    Enforce YYYY format for year
         jbyr=ibydbc/1000
         jbdy=ibydbc-jbyr*1000
         jeyr=ieydbc/1000
         jedy=ieydbc-jeyr*1000
         call YR4(io6,jbyr,ierrb)
         call YR4(io6,jeyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) then
            problem=.TRUE.
            goto 998
         endif
         ibydbc=jbyr*1000+jbdy
         ieydbc=jeyr*1000+jedy
! ---    Initialize "current" beginning/ending date-time to zero
         ibegbc=0
         iendbc=0
      endif
! --------------------------
! --- End of file header ---
! --------------------------
! --  Report configuration to the list file
! ------------------------------------------
998   write(io6,*)
      write(io6,*)
      write(io6,*)'   ****   BOUNDARY CONDITION DATA  ****'
      write(io6,*)
      write(io6,*)'dataset type                     = ',fnamebc
      write(io6,*)'dataset version                  = ',vrsbc
      write(io6,*)'dataset label                    = ',lblbc
      write(io6,*)'segments on N&S borders          = ',nbcx
      write(io6,*)'         segment length (m)      = ',xlenbc
      write(io6,*)'segments on E&W borders          = ',nbcy
      write(io6,*)'         segment length (m)      = ',ylenbc
      write(io6,*)
      write(io6,*)'concentration units              = ',cunitsbc(ibcu)
      write(io6,*)'species and units conversion factor to g/m^3:'
      do j=1,nspecbc
         factor=funitsbc(ibcu)
         if(ibcu.GT.2) factor=factor*xmwtbc(j)
         write(io6,'(20x,a12,1pe11.4)') cspecbc(j),factor
      enddo
      write(io6,*)
      write(io6,*)'Species cross-reference vector:'
      do j=1,nspecbc
         write(io6,'(20x,a12,i5)') cspecbc(j),ixrembc(j)
      enddo
      write(io6,*)
      write(io6,*)'time-variable air-mass types     = ',ntypebc2
      if(ntypebc2.GT.0) then
         write(io6,*)'    beginning date (YYYYJJJ)     = ',ibydbc
         write(io6,*)'    beginning hour (00-23 LST)   = ',ibhbc
         write(io6,*)'    ending date (YYYYJJJ)        = ',ieydbc
         write(io6,*)'    ending hour (00-23 LST)      = ',iehbc
      endif
      write(io6,*)'constant/factored air-mass types = ',ntypebc1
      write(io6,*)
      write(io6,*)'Type Air-mass  Layer(m) Concentrations (g/m^3)'
      write(io6,*)'---- --------  -------- ----------------------'
      do i=1,ntypebc
         do j=1,nspecbc
            conc=conbc(j,i)*funitsbc(ibcu)
            if(ibcu.GT.2) conc=conc*xmwtbc(j)
            if(j.EQ.1) then
              write(io6,'(i4,1x,a12,f6.0,1pe11.4,4x,a12)')i,cnamtyp(i),&
                                         htbc(i),conbc(j,i),cspecbc(j)
            else
              write(io6,'(23x,1pe11.4,4x,a12)')  conbc(j,i),cspecbc(j)
            endif
            if(nsbc.GT.0)then
              is=ixrembc(j)
              k=idsfbc1(is,i)
              if(k.GT.0) then
                 kk=ixrefbc1(k)
                 write(io6,*)cspec(is)//'Emission Factor Type: '//&
                             cqsftype((iqsfbctype(kk)))
                 ncol=nqsfcol(iqsfbctype(kk))
                 do nrow=1,nqsfrow(iqsfbctype(kk))
                    n1=(nrow-1)*ncol+1
                    n2=n1+ncol-1
                    write(io6,*)'Index ',n1,' to ',n2,&
                                ' Emission Factor = ',&
                                (qsfbctab(n,kk),n=n1,n2)
                 enddo
              endif
            endif
         enddo
      enddo
      write(io6,*)
      write(io6,*)'Air-mass type along the North boundary:'
      write(io6,'(40i3)') (itypebc(i),i=1,nbcx)
      write(io6,*)
      write(io6,*)'Air-mass type along the West & East boundaries:'
      do i=1,nbcy
         j=nbcx+2*(i-1)
         write(io6,'(i3,21x,i3)') (itypebc(j+k),k=1,2)
      enddo
      write(io6,*)
      write(io6,*)'Air-mass type along the South boundary:'
      j=nbcx+2*nbcy
      write(io6,'(40i3)') (itypebc(j+k),k=1,nbcx)
      write(io6,*)
      write(io6,*)'Characteristics of each Air-mass segment:'
      write(io6,*)'   Bndy Ht   Type  xgrd    ygrd'
      write(io6,*)
      do i=1,nbc
         write(io6,'(i4,1x,a1,f7.1,i4,2f8.2)') i,nsew(ibc(i)),&
               htbc(itypebc(i)),itypebc(i),xbcgrd(i),ybcgrd(i)
      enddo
      write(io6,*)
      write(io6,*)
! --- Terminate run if QA detected a fatal flaw in control file
999   if(PROBLEM) then
         write(*,*)
         stop 'Halted in RDHDBC -- see list file.'
      elseif(LWARN) then
         write(*,*)
         write(*,*) 'Warning issued by RDHDBC -- see list file.'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine RDEMBC(ldb,ndathr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108            RDEMBC
!                D. Strimaitis
!
! --- PURPOSE:  Read the time-varying records of the BCON boundary
!               conditions file (unit IO15) and update data in the
!               air-mass arrays for these air-mass types
!
! --- UPDATE
! --- V5.7-V6.268   100108  (DGS): Declare cstore1 using full MXCOL
! --- V5.5-V5.7     030402  (DGS): Add list file unit to YR4 call
!
! --- INPUTS:
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!           NDATHR - integer    - Current modeling date/time
!                                 (YYYYJJJHH)
!
!     Common block /BCS/:
!           HTBC(mxbc),CONBC(mxspec,mxbc),IBCU,FUNITSBC(4),
!           NTYPEBC2, NSPECBC, CSPECBC(mxspec), XMWTBC(mxspec)
!
!     Parameters:
!           MXBC, MXSPEC, IO15, IO6
!           MXCOL
!
! --- OUTPUT:
!             IBEG - integer    - Starting time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH)
!             IEND - integer    - Ending time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH)
!
!     Common block /BCS/:
!           HTBC(mxbc),CONBC(mxspec,mxbc),
!           IBEGBC, IENDBC
!
! --- RDEMBC called by: INITPUF
! --- RDEMBC calls:     YR4, ALLCAP
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'params.cal'
      include 'bcs.puf'
! --- Local variables for reading air-mass data
      real conc(mxspec)
      character*12 cid
      character*1 cstore1(mxcol)
      logical ldb,lmatch
! --- Read starting, ending date and time
101   read(io15,*)ibdat,ibtim,iedat,ietim
! --- Enforce YYYY format for year
      ibyr=ibdat/1000
      ibdy=ibdat-ibyr*1000
      ieyr=iedat/1000
      iedy=iedat-ieyr*1000
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDEMBC'
      endif
      ibdat=ibyr*1000+ibdy
      iedat=ieyr*1000+iedy
      ibegbc=ibdat*100+ibtim
      iendbc=iedat*100+ietim
! --- Test for valid time period
      if(ndathr.GT.iendbc) then
! ---    Read records for air-mass types and try next set of records
         do i=1,ntypebc2
            read(io15,*)cid,ht,(conc(k),k=1,nspecbc)
         enddo
         goto 101
      elseif(ndathr.LT.ibegbc)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR -- BCON date/time exceeds current'
         write(io6,*)'      -- current date/time :',ndathr
         write(io6,*)'      -- BCON date/time    :',ibegbc
         write(*,*)
         stop 'ERROR -- BCON date/time exceeds current --'
      endif
! --- Report valid time period
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-varying BCON data'
         write(io6,*)'IBEG = ',ibegbc,' IEND = ',iendbc
         write(io6,*)'Updated BCON data'
         write(io6,*)'Air-mass  Layer(m) Concentrations (g/m^3)'
         write(io6,*)'--------  -------- ----------------------'
      endif
! --- Loop over air-mass types and update stored data
      do i=1,ntypebc2
         read(io15,*)cid,ht,(conc(k),k=1,nspecbc)
! ---    Convert characters to upper case
         do j=1,12
            cstore1(j)=cid(j:j)
         enddo
         call ALLCAP(cstore1,12)
         do j=1,12
            cid(j:j)=cstore1(j)
         enddo
! ---    Match type name to index
         lmatch=.FALSE.
         do ip=1,(ntypebc1+ntypebc2)
            if(cid.EQ.cnamtyp(ip)) then
               ityp=ip
               lmatch=.TRUE.
            endif
         enddo
         if(LMATCH) then
! ---       Update stored data
            htbc(ityp)=ht
            do is=1,nspecbc
               conbc(is,ityp)=conc(is)*funitsbc(ibcu)
               if(ibcu.GT.2) conbc(is,ityp)=conbc(is,ityp)*xmwtbc(is)
! ---          Report new data values
               if(ldb)then
                  if(is.EQ.1) then
                    write(io6,'(a12,f6.0,1pe11.4,4x,a12)')&
                    cnamtyp(ityp),htbc(ityp),conbc(is,ityp),cspecbc(is)
                  else
                    write(io6,'(18x,1pe11.4,4x,a12)')&
                    conbc(is,ityp),cspecbc(is)
                  endif
               endif
            enddo
         else
            write(io6,*)
            write(io6,*)'*********  FATAL  ***********'
            write(io6,*)'ERROR in subr. RDEMBC -- type name for ',&
            'Air-Mass data invalid :',' name = ',cid
            write(io6,*)'Valid air-mass type names are: '
            do ip=1,(ntypebc1+ntypebc2)
               write(io6,*) cnamtyp(ip)
            enddo
            write(*,*)
            stop 'ERROR -- BCON air-mass name is invalid --'
         endif
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine bcs1(i,nspec,dthr,ldbhr,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230              BCS1
!                D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff variables to represent
!               inflow of background air consistent with boundary
!               conditions (emissions provided in BCON file)
!
! --- UPDATE
! --- V6.3 - TNG-7.1.0  141230  (DGS)
!                                : Replace VEMFAC with QFACTOR subr.
!                                  for emissions scaling
! --- V6.261-V6.3   100212  (DGS): Add nested CALMET grids
! --- V5.72-V6.261  080520  (DGS): Place rise tables in DA file
! --- V5.71-V5.72   031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                  IPUFID
! --- V5.7-V5.71    030528  (DGS): Use min/max ZI when defining current
!                                  mixing ht
!                   030528  (DGS): Place mass into species BCON so that
!                                  BC puffs are always fully active
!                                  even when influx concs are zero
! --- V5.4-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!
! --- INPUTS:
!             I - integer - Boundary segment index
!         NSPEC - real    - Number of species modeled
!          DTHR - real    - Length (seconds) of a basic time step
!         LDBHR - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           MXNEW, WSCALM, XMINZI, XMAXZI
!     Common block /GRID/ variables:
!           IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /FLAGS/ variables:
!           MBCON
!     Common block /METHD/ variables:
!           Z0(mxnx,mxny,mxmetdom), I2DMET
!     Common block /METHR/ variables:
!           HTMIX(mxnx,mxny,mxmetdom), TEMPSS(mxss,mxmetdom),TEMP2D
!     Common block /PUFF/ variables:
!           NPUFFS
!     Common block /BCS/ variables:
!           NBC,XBCGRD(mxbc),YBCGRD(mxbc),ITYPEBC(mxbc),
!           IBC(mxbc),
!           NTYPEBC1,HTBC(mxbc),CONBC(mxspec,mxbc),
!           XLENBC, YLENBC, 
!           NSPECBC,CSPECBC(mxspec),XMWTBC(mxspec),IXRBC(mxspec)
!           NSBC,IDSFBC1(mxspec,mxbc),IXREFBC1(mxspbc)
!           IQSFBCTYPE(mxspbc),QSFBCTAB(mxqsf,mxspbc)
!     Parameters:
!           MXNX, MXNY, MXMETDOM, MXNZ, MXNZP1, MXSS, MXBC, MXSPEC, IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /BCS/ variables:
!           NEWBC(mxbc)
!     Common block /PUFF/ variables:
!           all variables in common block
!
! --- BCS1 called by:  INITPUF
! --- BCS1 calls:      RLSMET, ROLLDN, SWAP, ZEROTAB, SRCTABOUT
!                      QFACTOR
!----------------------------------------------------------------------
      include 'params.puf'
      include 'bcs.puf'
      include 'comparm.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
      real q(mxspec)
      real finu(4),finv(4)
      logical ldbhr,problem,lfalse
      data rtpi/1.7724539/
      data lfalse/.false./
! --- Set conversion factor for obtaining sigma from a length scale as
! --- done in CHEM module
      data x2sig/3.467/
! --- Set minimum allowed wind speed normal to boundary for inflow puff
      data wsin0/0.1/
! --- Set local value of mfact (always a PUFF for BC)
      data mfact/0/
! --- Set MET grid domain = 1 (outermost) for extracting met data
      data md/1/
! --- Set array to establish inflow direction for boundary on N,S,E,W
! ---   finu= 0 if segment is on north or south boundary
! ---       = 1 if segment is on west boundary
! ---       =-1 if segment is on east boundary
! ---   finv= 0 if segment is on east or west boundary
! ---       = 1 if segment is on south boundary
! ---       =-1 if segment is on north boundary
      data finu/0.,0.,-1.,1./, finv/-1.,1.,0.,0./
! --- Initialize all species mass to zero (not all species may be
! --- characterized on the boundary)
      data q/mxspec*0.0/
! --- Set source type (BCON is type 9)
      itype=9
!
! --- Determine the met. grid point closest to the segment
      ixs=1.0+xbcgrd(i)
      iys=1.0+ybcgrd(i)
!
! --- Source off the computational grid -- write a FATAL message
      if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
         jecomp)then
         write(io6,20)i,ixs,iys
20       format(/1x,'FATAL -- a BC segment with constant emissions ',&
         'is off the computational grid'/1x,'segment no. (i) = ',i6,2x,&
         'nearest grid point (ixs,iys) = (',i5,',',i5,')')
         problem=.TRUE.
         return
      endif
! --- Initialize source tabulation specifications to NULL
      call ZEROTAB
! --- Identify the boundary condition type for this segment
      it=itypebc(i)
! --- Set top of puff and mean height
      zicell=AMAX1(htmix(ixs,iys,md),xminzi)
      zicell=AMIN1(zicell,xmaxzi)
      ztop=AMAX1(htbc(it),zicell)
      zbot=0.0
      heff=0.5*ztop
!
! --- Extract met. variables; and set CALM configuration if triggered
      call RLSMET(lfalse,ixs,iys,md,heff,mfact,&
                  mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                  ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,idoptz)
! frr (09/01) new calmet format (2D temp)
      if(i2dmet.EQ.1) then
          temp=temp2d(ixs,iys,md)
      elseif(i2dmet.EQ.0) then
         temp=tempss(issta,md)
      else
         write(*,*)'Subr. BCS1:  Invalid I2DMET = ',i2dmet
         stop
      endif
! --- Obtain average advection wind components
      call ADVECT(lfalse,ixs,iys,md,z0(ixs,iys,md),el,dpbl,istab,heff,&
                  zbot,ztop,uadv,vadv)
! --- Obtain the wind component normal to the segment
      wsin=uadv*finu(ibc(i))+vadv*finv(ibc(i))
! --- Return without generating new puffs if wsin < wsin0
      if(wsin.LT.wsin0) return
! --- Select length for this segment
      if(ibc(i).LE.2) then
         seglen=xlenbc
      else
         seglen=ylenbc
      endif
! --- Compute emission rate (g/s) for segment 'i' as 1D array and scale
      vflx=ztop*seglen*wsin
! --- Loop over species list as provided in BCON.DAT file
      do k=1,nspecbc
! ---    Select master species index from cross-reference array
         is=ixrembc(k)
         if(is.EQ.nspec) then
            write(*,*)'Subr. BCS1:  Invalid array of concentrations'
            write(*,*)'Expected NO influx of BCON species #',nspec
            write(*,*)'Found: ',conbc(k,it)
            write(*,*)'for Air-Mass type: ',it
            stop
         endif
         q(is)=vflx*conbc(k,it)
! ---    Apply emissions scale-factor (conditional)
         if(nsbc.GT.0) then
! ---       Pointer to scale factor tables
            id=idsfbc1(is,it)
            if(id.GT.0) then
               kid=ixrefbc1(id)
               call QFACTOR(io6,iqsfbctype(kid),&
                            itype,ws,istab,temp,iqsf)
               q(is)=q(is)*qsfbctab(iqsf,kid)
            endif
         endif
         qtot=qtot+q(is)
      enddo
! --- Now provide emission rate of marker species BCON
      q(nspec)=vflx*conbc0
! ***
! --- Report active segments in debug mode
      if(ldbhr) then
         write(io6,*)
         write(io6,*) 'BCS1:  Segment Number    ',i
      endif
! ***
! ---------------------------------------------------------
! --- Compute the number of puffs released this hour
! --- from this source and the radius
! ---------------------------------------------------------
! --- Inflow distance normal to segment
      xinflo=dthr*wsin
! --- Effective radius of puff with area equal to inflow
      reff=SQRT(xinflo*seglen)/rtpi
! --- Target radius
      rc=0.5*seglen
! --- Number of puffs 
      npnew=1+reff/rc
      npnew=min0(npnew,mxnew)
! --- Set new effective radius (preserve total area)
      reff=reff/SQRT(FLOAT(npnew))
! --- Compute initial sigma-y and sigma-z (for CHEM and reporting)
      sysrc=reff/x2sig*rtpi
      szsrc=ztop/x2sig
! --- Update puff counts
      newbc(i)=npnew
      newpuf=newpuf+npnew
! --- If puff arrays are full, remove puffs that are off the grid
! --- and "roll down" arrays to make room for new puffs
! --- (this also applies to the DA file of tabulated arrays)
      if(npuffs+newpuf.gt.mxpuff) call ROLLDN(np)
! --- Check to see if roll-down was adequate
      if(npuffs+newpuf.gt.mxpuff)then
         write(io6,*) ' ERROR in subr. BCS1'
         write(io6,*)'Too many puff on grid for array dimensions -- ',&
         'NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
         ' MXPUFF = ',mxpuff
         write(*,*)
         stop 'Halted in BCS1 -- see list file.'
      endif
!
! --- DT is the time (sec) over which each puff is emitted
      dt=dthr/float(npnew)
!
!c*****
      if(ldbhr)then
         write(io6,204)i,ilayer,ixs,iys,md,ws,istab,dpbl,issta,npnew,&
          newpuf,dt
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,2x,&
          'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'NPNEW: ',i4,2x,&
          'NEWPUF: ',i5/5x,'DT: ',f7.2)
!
         write(io6,208)np,rc,reff,ztop,uadv,vadv,wsin
208      format(5x,'NP: ',i5,2x,'RC: ',f8.2,2x,&
          'REFF: ',f8.2,2x,'ZTOP: ',f8.2,/2x,'UADV: ',f8.2,2x,&
          'VADV: ',f8.2,2x,'WSIN: ',f7.2)
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnew
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call swap(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xbcgrd(i)
         ypb(np)=ybcgrd(i)
         zitibl(np)=-1.0
         elbase(np)=elev(ixs,iys,md)
         sigyb(np)=sysrc
         sigzb(np)=szsrc
! ---    Final plume rise results
         zfinal(np)=heff
         zpb(np)=heff
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=heff
         diam0(np)=2.*reff
         temit0(np)=dt
         ws0(np)=wsin
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         el0(np)=el
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from a source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =9 for control file boundary sources)
         irlsnum(np)=j
         isrcnum(np)=i
         isrctyp(np)=itype
!
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
         zimax(np)=ztop
         ziold(np)=dpbl
         fmix=dpbl/ztop
         do ispec=1,nspec
            qtotal=q(ispec)*dt
            qm(ispec,np)=qtotal*fmix
            qu(ispec,np)=qtotal-qm(ispec,np)
         enddo
         if(ztop.GT.dpbl) then
            ipufcd(np)=6+mfact0
         else
            ipufcd(np)=2+mfact0
         endif
! ------------------------------------------------------------------
! ---    Transfer NULL tabulated arrays record to DA file (no
! ---    arrays are written, just the number of entries -- zero)
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
      return
      end
!----------------------------------------------------------------------
      subroutine setbcpuf(ii,tsamp,distm,ws,xnew,ynew,&
                          ixmet,iymet,mdmet,ldbhr,uavg)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230          SETBCPUF
!                D. Strimaitis,  Earth Tech
!
! --- PURPOSE:  Set distances/times for boundary condition puff, and
!               update stored puff data.
!
! --- UPDATES:
! --- V5.3 - TNG-7.1.0  141230  (DGS)
!                                : Pass cell location into /CURRENT/
!                                  (may not be needed)
!
! --- INPUTS:
!          II - integer   - Puff number
!       TSAMP - real      - Sampling time step (sec)
!       DISTM - real      - Advection distance (m) for time-step
!          WS - real      - Current wind speed (m/s)
!        XNEW - real      - New puff x-position (Met Grid Units)
!        YNEW - real      - New puff y-position (Met Grid Units)
!       LDBHR - logical   - Debug write logical
!
!       Common block /CURRENT/ variables:
!             IDOPTY, IDOPTZ, ICODE,
!             IPNUM, ISNUM, ISTYPE
!       Common block /GRID/ variables:
!             DGRID
!       Common block /PUFF/ variables:
!             SIGYB(mxpuff), SIGZB(mxpuff), ZPB(mxpuff),
!             TMTOTB(mxpuff), XTOTB(mxpuff), BIDFNL(mxpuff),
!             IPUFCD(mxpuff), STIPDW(mxpuff)
!       Parameters:
!             IO6
!
! --- OUTPUT:
!        UAVG - real      - Mean transport speed (m/s)
!       Common block /CURRENT/ variables:
!             xb1,yb1,zb1,syb1,szb1,
!             xe1,ye1,ze1,sye1,sze1,
!             ixmc,iymc,mdmc,
!             xttb1,xtte1,xshift,
!             sym1,szm1,zm1,fracz1,lup1
!             ipnum,iage,speedi,srat,temis
!       Common block /PUFF/ variables:
!             XPB(mxpuff), YPB(mxpuff), SIGYB(mxpuff), SIGZB(mxpuff),
!             TMTOTB(mxpuff), XTOTB(mxpuff), ZPB(mxpuff)
!
! --- SETBCPUF called by:  COMP
! --- SETBCPUF calls:      none
!
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'current.puf'
      include 'grid.puf'
      include 'puff.puf'
      logical ldbhr
      data zero/0.0/, dm2km/0.001/
! --- Initialize variables associated with terrain option MCTADJ=2
      fracz1=zero
      lup1=.FALSE.
! --- Distance travelled by puff to midpoint (km)
      dmidkm=0.5*distm*dm2km
! --- Time travelled by puff to midpoint (sec)
      tmid=0.5*tsamp
! --- Set mean transport speed for step
      uavg=ws
! --- Assign puff number
      ipnum=ii
! --- Pass cell location into /CURRENT/ variables (if needed)
      ixmc=ixmet
      iymc=iymet
      mdmc=mdmet
! -----------------------------------------
! --- Pass constant sigmas and puff height
! -----------------------------------------
      syb1=sigyb(ii)
      sym1=sigyb(ii)
      sye1=sigyb(ii)
      szb1=sigzb(ii)
      szm1=sigzb(ii)
      sze1=sigzb(ii)
      zb1=zpb(ii)
      zm1=zpb(ii)
      ze1=zpb(ii)
! ----------------------------------------
! --- Store remaining /CURRENT/ variables
! ----------------------------------------
!
! --- Puff coordinates in /CURRENT/ are relative to the origin of the
! --- meteorological grid (0.0, 0.0) = LL corner of cell (1,1) --
! --- units: meters
      xb1=xpb(ii)*dgrid
      yb1=ypb(ii)*dgrid
      xe1=xnew*dgrid
      ye1=ynew*dgrid
!
! --- Set sigma**2 at final rise due to buoyancy enhancement
      bidsq=bidfnl(ii)
!
! --- Set distance to final rise, and final rise height (delta)
      xfrise=xfinal(ii)
      zfrise=zfinal(ii)-(ht0(ii)-stipdw(ii))
!
! --- Set distance from upwind edge of line sources
      xshift=xshift0(ii)
!
! --- Wind speed variables at time of release of emissions
      speedi=ws0(ii)
      srat=srat0(ii)
!
! --- Duration (sec) of the original emissions release
      temis=temit0(ii)
!
! --- Total travel distance (m) at beginning and end of step
      xttb1=xtotb(ii)
      xtte1=xttb1+distm
!
! --- Treat all puffs as OLD
      iage=1
! ------------------------------
! --- Update /PUFF/ variables
! ------------------------------
!
! --- Update total distance (m) and time (s) to end of current step
      xtotb(ii)=xtotb(ii)+distm
      tmtotb(ii)=tmtotb(ii)+tsamp
!
! --- Update position of puff at end of step
      xpb(ii)=xnew
      ypb(ii)=ynew
      zpb(ii)=ze1
      if(ldbhr)then
         write(io6,*) 'SETBCPUF --'
         write(io6,252)dmidkm,tmid,xtotb(ii),&
                       tmtotb(ii)
252      format(10x,' DMID(km)=',f10.3,' TMID=',f10.1,&
          ' XTOTB(m)=',f10.1,' TMTOTB=',f10.1)
         write(io6,256)icode,sigyb(ii),sigzb(ii)
256      format(10x,'ICODE=',i2,' SIGYB=',f10.1,' SIGZB=',f10.1)
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine calcbc(xold,yold,xnew,ynew,qold,qnew,qoldup,qnewup,&
                        icode,hlid,hlidmax,puffd,vdpvd,nspec,&
                        q01wet,q01dry,vd,fracwet,tsampi,rbc0,ldbhr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 121203            CALCBC
!                D. Strimaitis
!
! --- PURPOSE:  For a given puff from a boundary-condition source,
!               perform the loop over all receptors.  Update the
!               concentration and wet and dry deposition fluxes at a
!               receptor if this puff comes the closest of any BC
!               puffs sampled so far this hour.
!               Time-averaged results represent the full timestep
!               (1 hour).
!
! --- UPDATE
!
! --- V6.267-6.42_x1.0  121203 : use explicit index for debug output
!                                  of local arrays
!
! --- V5.71-V6.267  090710  (DGS): restrict deposition fluxes to
!                                  receptors that are located ON the
!                                  the ground
!                           (DGS): allow elevated receptors to sample
!                                  upper layer
! --- V5.4-V5.71    030528  (DGS): impose user-defined search radius
!                                  for screening BC puff impacts, 
!                                  passed as argument
!                   030528  (DGS): apply MDEPBC flag for using vdpvd
!                                  adjustment for surface depletion
! --- V5.3-V5.4     000602  (DGS): add message to "stop"
!
! --- INPUTS:
!
!         XOLD,YOLD - real    - Starting position (m)
!         XNEW,YNEW - real    - Ending position (m)
!       QOLD(nspec) - real    - Puff mass (g) at beginning of time step
!       QNEW(nspec) - real    - Puff mass (g) at end of time step
!     QOLDUP(nspec) - real    - Puff mass (g) aloft at beginning of
!                               time step
!     QNEWUP(nspec) - real    - Puff mass (g) aloft at end of time
!                               step
!             ICODE - integer - Code for vertical layering of puff
!              HLID - real    - Height of reflection lid (m)
!           HLIDMAX - real    - Maximum lid height (m) for mass
!                               above HLID
!             PUFFD - real    - Diameter of this puff (m)
!      VDPVD(nspec) - real    - Conc. correction factor (vd'/vd) used
!                               to estimate effect of dry deposition and
!                               vertical mixing on near-surface concs.
!             NSPEC - real    - Number of species
!  Q01WET(mxspec,2) - real    - Puff mass before/after wet removal (g)
!  Q01DRY(mxspec,2) - real    - Puff mass before/after dry removal (g)
!         VD(nspec) - real    - Dry deposition velocity (m/s) for each
!                               species
!    FRACWET(nspec) - real    - Fraction of mass left in puff after wet
!                               removal
!            TSAMPI - real    - 1.0/(TSAMP)
!              RBC0 - real    - Search radius for BC puffs in grid units
!             LDBHR - logical - Debug output (T,F)
!
!     Common block /CTSGDAT/ variables:
!        NCTREC,XRCT(mxrect),YRCT(mxrect)
!     Common block /FLAGS/ variables:
!        MWET,MDRY,MBCON,MDEPBC
!     Common block /GRID/ variables:
!        NXSAM,NYSAM,LSAMP,IBSAMP,JBSAMP,MESHDN,SAM2GRID
!     Common block /NONGRID/ variables:
!        NREC,XNG(mxrec),YNG(mxrec),ZNG(mxrec)
!
!     Parameters:
!        MXNX, MXNY, NSPEC, MXNXG, MXNYG, MXREC, IO6
!
! --- OUTPUTS:
!
!     Common block /CHIFLX/ variables:
!        CBCSAM(mxnxg,mxnyg,mxspec),DBCSAM(mxnxg,mxnyg,mxspec),
!        WBCSAM(mxnxg,mxnyg,mxspec),
!        CBCREC(mxrec,mxspec),DBCREC(mxrec,mxspec),WBCREC(mxrec,mxspec),
!        CBCCT(mxrect,mxspec),
!        RBCSAM(mxnxg,mxnyg),RBCREC(mxrec),RBCCT(mxrect)
!
! --- CALCBC called by:  COMP
! --- CALCBC calls:      PFSCRN
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'chiflx.puf'
      include 'ctsgdat.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'nongrd.puf'
!
      real qold(nspec),qnew(nspec),vdpvd(nspec)
      real q01wet(mxspec,2),q01dry(mxspec,2),vd(nspec),fracwet(nspec)
      real conc(mxspec),dflx(mxspec),wflx(mxspec)
      real qoldup(nspec),qnewup(nspec)
      real concup(mxspec)
      logical ldbhr,limpact
      data pi/3.1415927/
! --- This follows the CALCPF methods with the following exceptions
!     1)  A concentration or a flux is computed at a receptor only if
!         the current BC puff passes closer to the receptor than any
!         BC puff in any previous sampling step this averaging period;
!         previous results are replaced
!     2)  A mean mass is used for the sampling step
!     3)  The horizontal coupling coefficient is the area of the BC puff
!     4)  Weighting by the fraction of the averaging period that is
!         represented in this sampling step is removed -- the impact
!         for this step is assumed to be active for the entire averaging
!         period (TFRACT is not used here)
! --- Uniform vertical distribution factor in surface layer and aloft
      f1up=0.
      if(icode.EQ.2 .OR. icode.EQ.6) then
! ---    ICODE=2,6:  within past/present mixed layer and uniform
         f1=1./hlid
         if(hlidmax.GT.hlid) f1up=1./(hlidmax-hlid)
      else
! ---    Problem here
         write(io6,*)'CALCBC: Puffs for boundary conditions should be'
         write(io6,*)'        well mixed (code 2 or 6)'
         write(io6,*)'        Found ICODE = ',icode
         write(*,*)
         stop 'Halted in CALCBC -- see list file.'
      endif
! --- Reciprocal of puff area for horizontal sampling factor
      f2=4.0/(pi*puffd**2)
! --- Concentration and fluxes for this puff in surface layer
! --- and concentration for this puff aloft
      depcorr=1.0
      do i=1,nspec
         if(mdepbc.EQ.1) depcorr=vdpvd(i)
         conc(i)=depcorr*f1*f2*0.5*(qold(i)+qnew(i))
         concup(i)=f1up*f2*0.5*(qoldup(i)+qnewup(i))
         if(mdry.EQ.1) dflx(i)=vd(i)*depcorr*f1*f2*&
                               0.5*(q01dry(i,1)+q01dry(i,2))
         if(mwet.EQ.1) wflx(i)=(1.0-fracwet(i))*tsampi*q01wet(i,1)*f2
      enddo
! ***
! --- V6.42_x1.0, Level 121203
      if(LDBHR) then
         write(io6,*) 'CALCBC: conc = ',(conc(i),i=1,nspec)
         write(io6,*) '      concup = ',(concup(i),i=1,nspec)
         if(mdry.EQ.1) write(io6,*) '        dflx = ',(dflx(i),&
                                                       i=1,nspec)
         if(mwet.EQ.1) write(io6,*) '        wflx = ',(wflx(i),&
                                                       i=1,nspec)
      endif
! ***
!
! -------------------------------------------------------------
! --- SAMPLING GRID RECEPTOR POINTS  (uses SAMPLING grid units)
! -------------------------------------------------------------
!
      if(.NOT.lsamp) goto 101
! --- Compute the start of the sampling grid in MET grid units
      xbsamp=float(ibsamp)-0.5
      ybsamp=float(jbsamp)-0.5
! --- Calculate puff center coordinates in SAMPLING grid units
      samxo=(xold-xbsamp)*meshdn+1.0
      samyo=(yold-ybsamp)*meshdn+1.0
      samxn=(xnew-xbsamp)*meshdn+1.0
      samyn=(ynew-ybsamp)*meshdn+1.0
! --- Convert search radius to SAMPLING grid units
      samr=rbc0*meshdn
      samrp1=samr+1.0
!
! --- Determine section of grid within this distance of puff
      il=amin1(samxo-samrp1,samxn-samrp1,float(nxsam))+0.001
      il=max0(1,il)
      ir=amax1(samxo+samrp1,samxn+samrp1,1.0)+0.001
      ir=min0(nxsam,ir)
      jb=amin1(samyo-samrp1,samyn-samrp1,float(nysam))+0.001
      jb=max0(1,jb)
      jt=amax1(samyo+samrp1,samyn+samrp1,1.0)+0.001
      jt=min0(nysam,jt)
!
! --- Loop over receptors in this section
      do 100 isamp=il,ir
         xr=float(isamp)
         do 100 jsamp=jb,jt
            yr=float(jsamp)
! ---       Square current minimum distance in SAMPLING grid units
            rec2=(rbcsam(isamp,jsamp)*meshdn)**2
! ---       Impose a minimum on rec2
            rec2m=AMAX1(rec2,1.0)
!
! ---       Compute nearest approach of puff to receptor and screen out
! ---       combinations that are too far away
            call PFSCRN(samxo,samyo,samxn,samyn,xr,yr,rec2m,&
                        samd2,limpact)
!
            if(.NOT.limpact) goto 100
!
! ---       Update impact of BC at this receptor
            if(samd2.LT.rec2) then
               rbcsam(isamp,jsamp)=SQRT(samd2)*sam2grid
               do ipol=1,nspec
                  cbcsam(isamp,jsamp,ipol)=conc(ipol)
                  if(mdry.EQ.1) dbcsam(isamp,jsamp,ipol)=dflx(ipol)
                  if(mwet.EQ.1) wbcsam(isamp,jsamp,ipol)=wflx(ipol)
               enddo
            endif
100   continue
101   continue
!
! ----------------------------------------------------
! --- DISCRETE RECEPTOR POINTS  (using MET grid units)
! ----------------------------------------------------
!
      if(nrec.LE.0) goto 201
!
! --- Loop over receptors
      do i=1,nrec
! ---    Compute nearest approach of puff to receptor and screen out
! ---    combinations that are too far away
! ---    Square current minimum distance for this receptor (grid units)
         rec2=rbcrec(i)**2
! ---    Impose a minimum on rec2
         rec2m=AMAX1(rec2,1.0)
         call pfscrn(xold,yold,xnew,ynew,xng(i),yng(i),rec2m,&
                     d2,limpact)
!
         if(.NOT.limpact) goto 200
!
! ---    Update impact of BC at this receptor
         if(d2.LT.rec2) then
            rbcrec(i)=SQRT(d2)
            if(zng(i).LE.0.0) then
! ---          Receptor at surface
               do ipol=1,nspec
                  cbcrec(i,ipol)=conc(ipol)
                  if(mdry.EQ.1) dbcrec(i,ipol)=dflx(ipol)
                  if(mwet.EQ.1) wbcrec(i,ipol)=wflx(ipol)
               enddo
            elseif(zng(i).LE.hlid) then
! ---          Receptor in surface layer above surface
               do ipol=1,nspec
                  cbcrec(i,ipol)=conc(ipol)
                  if(mdry.EQ.1) dbcrec(i,ipol)=0.0
                  if(mwet.EQ.1) wbcrec(i,ipol)=0.0
               enddo
            elseif(zng(i).LE.hlidmax) then
! ---          Receptor in layer aloft
               do ipol=1,nspec
                  cbcrec(i,ipol)=concup(ipol)
                  if(mdry.EQ.1) dbcrec(i,ipol)=0.0
                  if(mwet.EQ.1) wbcrec(i,ipol)=0.0
               enddo
            else
! ---          Receptor above all puff mass
               do ipol=1,nspec
                  cbcrec(i,ipol)=0.0
                  if(mdry.EQ.1) dbcrec(i,ipol)=0.0
                  if(mwet.EQ.1) wbcrec(i,ipol)=0.0
               enddo
            endif
         endif
200      continue
!
! --- End loop over discrete receptors
      enddo
!
201   continue
!
! ----------------------------------------------------
! --- CTSG RECEPTOR POINTS  (using MET grid units)
! ----------------------------------------------------
!
      if(nctrec.LE.0) return
!
! --- Loop over receptors
      do i=1,nctrec
!
! ---    Compute nearest approach of puff to receptor and screen out
! ---    receptors that are too far away
! ---    Square current minimum distance for this receptor (grid units)
         rec2=rbcct(i)**2
! ---    Impose a minimum on rec2
         rec2m=AMAX1(rec2,1.0)
         call pfscrn(xold,yold,xnew,ynew,xrct(i),yrct(i),rec2m,&
                     d2,limpact)
!
         if(.NOT.limpact) goto 300
!
! ---    Update impact of BC at this receptor
         if(d2.LT.rec2) then
            rbcct(i)=SQRT(d2)
            do ipol=1,nspec
               cbcct(i,ipol)=conc(ipol)
            enddo
         endif
!
! ---    End computation of impact at receptor
300      continue
!
! --- End loop over CTSG receptors
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine sumbc
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 991222             SUMBC
!                D. Strimaitis,  Earth Tech
!
! --- PURPOSE:  Sum the contribution of boundary-condition sources
!               this time-step to the total concentration/flux
!
! --- INPUTS:
!
!     Common block /CHIFLX/ variables:
!        CHISAM(mxnxg,mxnyg,mxspec),DFSAM(mxnxg,mxnyg,mxspec),
!        WFSAM(mxnxg,mxnyg,mxspec),CHIREC(mxrec,mxspec),
!        DFREC(mxrec,mxspec),WFREC(mxrec,mxspec),
!        CHICT(mxrect,mxspec)
!        CBCSAM(mxnxg,mxnyg,mxspec),DBCSAM(mxnxg,mxnyg,mxspec),
!        WBCSAM(mxnxg,mxnyg,mxspec),
!        CBCREC(mxrec,mxspec),DBCREC(mxrec,mxspec),WBCREC(mxrec,mxspec),
!        CBCCT(mxrect,mxspec)
!     Common block /CTSGDAT/ variables:
!        NCTREC
!     Common block /FLAGS/ variables:
!        MWET,MDRY
!     Common block /GEN/ variables:
!        NSPEC
!     Common block /GRID/ variables:
!        NXSAM,NYSAM,LSAMP
!     Common block /NONGRID/ variables:
!        NREC
!
!     Parameters:
!        MXNX, MXNY, MXSPEC, MXNXG, MXNYG, MXREC, IO6
!
! --- OUTPUTS:
!
!     Common block /CHIFLX/ variables:
!        CHISAM(mxnxg,mxnyg,mxspec),DFSAM(mxnxg,mxnyg,mxspec),
!        WFSAM(mxnxg,mxnyg,mxspec),CHIREC(mxrec,mxspec),
!        DFREC(mxrec,mxspec),WFREC(mxrec,mxspec),
!        CHICT(mxrect,mxspec)
!
! --- SUMBC called by:  COMP
! --- SUMBC calls:      none
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'chiflx.puf'
      include 'ctsgdat.puf'
      include 'flags.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'nongrd.puf'
!
! ----------------------------------
! --- SAMPLING GRID RECEPTOR POINTS
! ----------------------------------
!
      if(LSAMP) then
         do is=1,nspec
            do j=1,nysam
            do i=1,nxsam
               chisam(i,j,is)=chisam(i,j,is)+cbcsam(i,j,is)
               if(mdry.EQ.1) dfsam(i,j,is)=dfsam(i,j,is)+dbcsam(i,j,is)
               if(mwet.EQ.1) wfsam(i,j,is)=wfsam(i,j,is)+wbcsam(i,j,is)
            enddo
            enddo
         enddo
      endif
!
! -----------------------------
! --- DISCRETE RECEPTOR POINTS
! -----------------------------
!
      if(nrec.GT.0) then
         do is=1,nspec
            do i=1,nrec
               chirec(i,is)=chirec(i,is)+cbcrec(i,is)
               if(mdry.EQ.1) dfrec(i,is)=dfrec(i,is)+dbcrec(i,is)
               if(mwet.EQ.1) wfrec(i,is)=wfrec(i,is)+wbcrec(i,is)
            enddo
         enddo
      endif
!
! -------------------------
! --- CTSG RECEPTOR POINTS
! -------------------------
!
      if(nctrec.GT.0) then
         do is=1,nspec
            do i=1,nctrec
               chict(i,is)=chict(i,is)+cbcct(i,is)
            enddo
         enddo
      endif
      return
      end
!---------------------------------------------------------------------
      subroutine chemsoa(ldb,io,delt,q,ipgt,qsw,tempk,ppbo3,vloss)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 981124           CHEMSOA
!                G. Moore, Earth Tech
! ---            modified YIELD 981119 for nighttime alpha-pinene
!
! --- PURPOSE:  Compute the average rate of VOC destruction (%/hr)
!               and the average rate of secondary organic aerosol (SOA)
!               formation from VOC suitable for application in a
!               first order chemistry model, and update VOC, SOA mass
! --- UPDATE
! --- V5.3-V5.3    981124  (GEM): Add radiation to Yield call
!
! --- INPUTS:
!
!          LDB - logical - Debug T/F
!           IO - integer - Unit number for output
!         DELT - real    - Integration time interval (hours)
!         Q(5) - real    - Pollutant mass (g) in the puff before step
!         IPGT - integer - PGT stability class (1-6)
!          QSW - real    - Short-wave solar radiation (W/m**2)
!        TEMPK - real    - Air temperature (deg. K)
!        PPBO3 - real    - Ozone concentration (ppb) to use in
!                               chemistry calculations
!     Common Block /SOA/ variables:
!        LBIOG, LAROM, VC2NX, AMO
!
! --- OUTPUT:
!         Q(5) - real    - Pollutant mass (g) in the puff after step
!     VLOSS(4) - real    - VOC transformation rates (%/hour)
!                           VLOSS(1) -- TOLUENE loss rate
!                           VLOSS(2) -- XYLENE loss rate
!                           VLOSS(3) -- B-PINENE loss rate
!                           VLOSS(4) -- A-PINENE loss rate
!
! --- CHEMSOA called by:  CHEM
! --- CHEMSOA calls:      MAKEOHN, YIELD, AROMA, BIOG
!----------------------------------------------------------------------
      parameter (ms=4, nc=2)
! --- MS is the number of VOC species, NC is the number of classes
! --- 4 VOC species are used, and are taken in the following order:
!        (1) toluene - low yield aromatics
!        (2) xylene - high yield aromatics
!        (3) beta-pinene monoterpene biogenics
!        (4) alpha-pinene monoterpene biogenics
! --- The first 2 represent 2 classes of aromatics, while the second
! --- 2 represent 2 classes of biogenics.
! --- Include common blocks
      include 'soa.puf'
      real q(5)
      logical ldb
      dimension qvoc(ms),ylds(ms),vloss(ms)
      dimension arate(nc),brate(nc)
! --- Assign species mass (3 combinations of VOC species are possible)
      qsoa=0.0
      do is=1,4
         qvoc(is)=0.0
      enddo
      if(LAROM .AND. LBIOG) then
         do is=1,4
            qvoc(is)=q(is)
         enddo
         qsoa=q(5)
      elseif(LAROM) then
         do is=1,2
            qvoc(is)=q(is)
         enddo
         qsoa=q(3)
      elseif(LBIOG) then
         do is=1,2
            qvoc(is+2)=q(is)
         enddo
         qsoa=q(3)
      endif     
! --- Debug output
      if(LDB) then
         write(io,*)
         write(io,*)'CHEMSOA: Inputs -------'
         write(io,'(1x,a30,i2,4f8.2)')'  ipgt,delt,qsw,tempk,ppbo3 = ',&
                                         ipgt,delt,qsw,tempk,ppbo3
         write(io,*)'   BIOG,AROM,vc2nx,amo = ',LBIOG,LAROM,vc2nx,amo
         write(io,'(1x,a10,5e12.4)')'   Q(5) = ',q
         write(io,'(1x,a10,4e12.4)')'QVOC(4) = ',qvoc
         write(io,'(1x,a10,e12.4)') '   QSOA = ',qsoa
      endif
! --- Convert ozone conc from PPB to PPM
      ppmo3=0.001*ppbo3
! --- Estimate oh and no3
      call MAKEOHN(ipgt,qsw,tempk,vc2nx,ppmo3,ppmoh,ppmno3)
! --- Estimate the yields, and identify aromatic and biogenic parts
      call YIELD(qsw,amo,tempk,vc2nx,ylds)
! --- Estimate the destruction rate of VOCs
      do i=1,2
         arate(i)=0.0
         brate(i)=0.0
      enddo
      if(LAROM) call AROMA(ppmo3,ppmoh,ppmno3,tempk,arate)
      if(LBIOG) call BIOG(ppmo3,ppmoh,pmno3,tempk,brate)
! --- Transfer to VLOSS(mc) rate array
      do i = 1,2
         vloss(i)=arate(i)
         vloss(i+2)=brate(i)
      enddo
! --- Perform transformation/integration
! --- Include factor in timestep to remove % from rate
      dt=0.01*delt
      dsoa=0.0
      do i=1,4
! ---    VOC mass loss
         dc=qvoc(i)*(1.-EXP(-vloss(i)*dt))
! ---    SOA mass gain
         dsoa=dsoa+dc*ylds(i)
! ---    New VOC mass
         qvoc(i)=qvoc(i)-dc
      enddo
! --- New SOA mass
      qsoa=qsoa+dsoa
! --- Re-assign species mass for output
      if(LAROM .AND. LBIOG) then
         do is=1,4
            q(is)=qvoc(is)
         enddo
         q(5)=qsoa
      elseif(LAROM) then
         do is=1,2
            q(is)=qvoc(is)
         enddo
         q(3)=qsoa
      elseif(LBIOG) then
         do is=1,2
            q(is)=qvoc(is+2)
         enddo
         q(3)=qsoa
      endif     
! --- Debug output
      if(LDB) then
         write(io,*)
         write(io,*)'CHEMSOA: Results ------'
         write(io,*)'   ppmoh,ppmno3 = ',ppmoh,ppmno3
         write(io,*)'   arom rate(2) = ',arate
         write(io,*)'   biog rate(2) = ',brate
         write(io,'(1x,a10,4e12.4)')'yield(4)= ',ylds
         write(io,'(1x,a10,5e12.4)')'   Q(5) = ',q
         write(io,'(1x,a10,4e12.4)')'QVOC(4) = ',qvoc
         write(io,'(1x,a10,e12.4)') '   QSOA = ',qsoa
      endif
      return
      end
!---------------------------------------------------------------------
      subroutine makeohn(ipgt,rad,temp,vc2nx,ppmo3,ppmoh,ppmno3)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 980918           MAKEOHN
!                G. Moore, Earth Tech
!
! --- PURPOSE:  Computes the hydroxyl and the nitrate concentrations
!               using a method consistent with the MESOPUFF II
!               transformation algorithm (the RIVAD approach
!               may be implemented in the future, but is currently
!               disabled).
!
! --- INPUTS:
!
!              IPGT - integer - PGT stability class
!               RAD - real    - Short-wave solar radiation (W/m**2)
!              TEMP - real    - Air temperature (deg. K)
!             VC2NX - real    - Hydrocarbon/NOX ratio
!            RHOAIR - real    - Air density (kg/m**3)
!             PPMO3 - real    - Ozone concentration (ppm) to use in
!                               chemistry calculations
!
! --- OUTPUT:
!             PPMOH - real    - Hydroxyl concentration (ppm)
!            PPMNO3 - real    - Nitrate radical concentration (ppm)
!
! --- MAKEOHN called by:  CHEMSOA
! --- MAKEOHN calls:      none
!----------------------------------------------------------------------
! --- 1 ppm = xnd molecules/cm3 at 1 atm-289K
      data xnd/2.55e13/
! --- Set OH conc for night (used as minimum)
      data ppmnite/1.0e-11/
! --- Force use of MESOPUFF II type of calculation
      data ichem/1/
! --- Declare dummy input variables for RIVAD
      data coszn/0.0/,ppmh2o/0.0/,ppmso2/1./,ppmnox/1./
! --- Convert solar radiation from W/m**2 to kW/m**2
      srad = 0.001*rad
! --- Set PG class index to range 2-6
      xpgt = ipgt
      if(xpgt.lt.2.)xpgt = 2.0
      if(xpgt.gt.6.0)xpgt = 6.0
      xconv = xnd*60.0
! --- 3-body reaction rate at 300K at 1 atm
!     roh = xconv*3.0e-12        
! --- Use value from CB4 (reference ...)
      roh = 2.0e+03
!
! --- Estimate hydroxyl radical concentration (ppm)
! -------------------------------------------------
! --- MESOPUFF chemistry approach (ichem = 1)
      if(ichem.eq.1)then
         if(rad.gt.0.0)then
! ---       Daytime
            c1 = ppmo3**0.71
            c2 = 1.0/(xpgt**1.29)
            c3 = srad**0.55
            ppmoh = 0.36*c1*c2*c3/(60.*roh)
            ppmoh = AMAX1(ppmoh,ppmnite)
         else
! ---       Nighttime
            ppmoh = ppmnite
         endif
!
! --- RIVAD chemistry approach NOT AVAIALBLE (ichem = 2)
      elseif(ichem.eq.2)then
         x34 = 1.3e-03*coszn**2.74
         x35 = 4.45e+10
         x36 = 3.4e+05
         x37 = 2.0e+03
         x38 = 1.4e+04
         if(rad.gt.0.0)then
! ---       Daytime
            o1d = x34*ppmo3/(x35 + x36*ppmh2o)
            ppmoh = 2.0*x36*o1d*ppmh2o/(x37*ppmso2 + x38*ppmnox)
            ppmoh = AMAX1(ppmoh,ppmnite)
         else
! ---       Nighttime
            ppmoh = ppmnite
         endif
      endif
!
! --- Estimate nitrate radical concentration (ppm)
! ------------------------------------------------
      if(rad.gt.0.0)then
! ---    Photolysis severely attenuates NO3 during the day
         ppmno3 = 1.0E-10
      else
! ---    Slow buildup at night (assume N2O5 equlibrium)
! ---    Production [NO2]+[O3] - steady state with [NO3][VOC]
! ---    destruction
         c1 = 1.0e-15*xconv
         c2 = 1.760e+02*exp(-2450./temp)
         if(vc2nx.gt.0.0)then
            ppmno3 = (c2/c1)*ppmo3/vc2nx
         else
            ppmno3 = (c2/c1)*ppmo3/10.0
         endif
      endif
      return
      end
!---------------------------------------------------------------------
      subroutine yield(rad,amo,temp,vc2nx,yrates)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 981124             YIELD
!                G. Moore, Earth Tech
!
! --- PURPOSE:  Estimate the yield coefficients for a specific set
!               of chemical classes using the ambient conditions such
!               as the organic aerosol mass, the temperature, and the
!               prevailing VOC/NOx ratio.  The present form of
!               of the yield model assumes that a CB4/SAPRC90 mechanism
!               is followed with 4 classes - two aromatic and two
!               monoterpene
!
! --- UPDATE
! --- V5.3-V5.3    981124  (GEM): Add nighttime module for alpha-pinene
!
! --- INPUTS:
!
!               RAD - real    - Short-wave solar radiation (W/m**2)
!               AMO - real    - Aerosol mass load (ug/m3)
!              TEMP - real    - Air temperature (deg. K)
!             VC2NX - real    - Hydrocarbon/NOX ratio (after reaction)
!
! --- OUTPUT:
!          YRATES - real array - 1-D array of yields for 4 VOC classes:
!                                (1) toluene - low yield aromatics
!                                (2) xylene - high yield aromatics
!                                (3) beta-pinene monoterpene biogenics
!                                (4) alpha-pinene monoterpene biogenics
!
! --- YIELD called by:  CHEMSOA
! --- YIELD calls:      none
!----------------------------------------------------------------------
      parameter (nc=4)
!
      dimension yrates(nc)
      dimension scoef(2,nc),pc(2,nc)
      dimension tref(nc),vcnxr(nc)
!
      data scoef /0.071,0.138,0.038,0.167,0.35,0.0,0.038,0.326/
      data pc /0.053,0.0019,0.042,0.0014,0.11,0.0,0.171,0.004/
      data tref/308.,312.,312.,315./
      data vcnxr/nc*10.0/
!
! --- Reset nighttime parameters for alpha-pinene
      if(rad.le.0.0)then
         scoef(1,4) = 0.120
         scoef(2,4) = 0.19
         pc(1,4) = 0.20
         pc(2,4) = 0.005
      else
         scoef(1,4) = 0.038
         scoef(2,4) = 0.326
         pc(1,4) = 0.171
         pc(2,4) = 0.004
      endif
!
! --- Loop over nc classes estimating the yields
      do i = 1,nc
         yrates(i) = 0.0
! ---    Assume a two-product yield relation
         do k = 1,2
! ---       Adjustment of the ambient conditions for temp and vapor
! ---       pressure
            p1 = 1.0
            partc = pc(k,i)*(temp/tref(i))**p1
            c1 = 0.1*(vc2nx - vcnxr(i))/vcnxr(i)
            scoeff = scoef(k,i)*(1.0 + c1)
! ---       Two-product absorption isotherm yield relation
            yrates(i) = yrates(i) + amo*scoeff*partc/(1.0+partc*amo)
         enddo
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine biog(ppmo3,ppmoh,ppmno3,temp,brate)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 980918              BIOG
!                G. Moore, Earth Tech
!
! --- PURPOSE:  Estimate the destruction rate of biogenic VOC's by OH,
!               O3, and NO3.
!               This version has been designed for a 2 specie biogenic
!               profile approximating beta and alpha monoterpenes.
!
! --- INPUTS:
!
!             PPMO3 - real       - Ozone concentration (ppm)
!             PPMOH - real       - OH radical concentration (ppm)
!            PPMNO3 - real       - NO3 radical concentration (ppm)
!              TEMP - real       - Air temperature (deg. K)
!
! --- OUTPUT:
!          BRATE(2) - real       - Loss rate (%/hr)
!
! --- BIOG called by:  CHEMSOA
! --- BIOG calls:      none
!----------------------------------------------------------------------
! --- NOTES ---
!----------------------------------------------------------------------
! --- Biogenic compound estmates from BEIS and BEIS2 are put into 3
! --- broad categories - isoprene, monoterpenes, and other VOC's which
! --- include mainly low carbon olefins such as ethene, propene, etc.
! --- As a result- only monoterpenes are assumed to have the potential
! --- to form SOA. The monoterpene emissions are dominated by the emissions
! --- of pinenes, limonene, and myrcene (all are C10H16 in various stages
! --- of unsaturation) - myrcene is the most unsaturated being a triply
! --- unsaturated aliphatic hydorcabon.
!
! --- In this program we assume that monoterpenes are almost totally
! --- dominated by pinenes emissions which are split equally between alpha
! --- and beta pinene. Both species are treated seperately at the present
! --- due to slightly different lifetimes.
!
! --- If the CB4 speciation is utilized the following analogues are made
! --- (1) PAR - 8/9 monoterpene carbon mole fraction
! --- (2) OLE = 1/9 monoterpene carbon mole fraction
!----------------------------------------------------------------------
      parameter(ns=2)
      dimension brate(ns)
      dimension roh(ns),ro3(ns),rno3(ns)
! --- Convert cm3/(molec-sec) to ppm-1-min-1 (ref conditions only)
!      pwm = 154.0     pinene molecular weight g/mol
!      xn = 6.023e24   Avagadros # molecule/mole
!      1 ppm = xnd molecules/cm3 at 1 atm-289K
      xnd = 2.55e13
      xconv = xnd*60.0
! --- Inverse half life beta-pinene (min-1) - Warneck (1988) table A-4
! --- Thorsten et. al. 1997
      roh(1) = ppmoh*2.4e-11*xconv*exp(446.0/temp)
      ro3(1) = ppmo3*15.0e-18*xconv
      rno3(1) = ppmno3*2.5e-12*xconv
! --- Inverse half life alpha-pinene (min-1) - Warneck (1988) table A-4
! --- Thorsten et. al. 1997
      roh(2) = ppmoh*1.4e-11*xconv*exp(358.0/temp)
      ro3(2) = ppmo3*9.4e-16*xconv*exp(-731.0/temp)
      rno3(2) = ppmno3*6.2e-12*xconv
! --- Cnew = Cold*exp(-(roh+ro3+rno3)*dt)
! --- Express rate as %/hr rather than min-1 by factor 60.*100.
      do k = 1,ns
         brate(k)=6000.*(roh(k)+ro3(k)+rno3(k))
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine aroma(ppmo3,ppmoh,ppmno3,temp,arate)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 980918             AROMA
!                G. Moore, Earth Tech
!
! --- PURPOSE:  Estimate the destruction rate of aromatic VOC's by OH,
!               O3, and NO3.
!               This version has been designed for a 2 specie aromatic
!               profile approximating xylenes and toluenes.
!
! --- INPUTS:
!
!             PPMO3 - real       - Ozone concentration (ppm)
!             PPMOH - real       - OH radical concentration (ppm)
!            PPMNO3 - real       - NO3 radical concentration (ppm)
!              TEMP - real       - Air temperature (deg. K)
!
! --- OUTPUT:
!          ARATE(2) - real       - Loss rate (%/hr)
!
! --- AROMA called by:  CHEMSOA
! --- AROMA calls:      none
!----------------------------------------------------------------------
! --- NOTES ---
!----------------------------------------------------------------------
! --- The aromatic species are assumed to exist in terms of high 
! --- formation rate, low formation rate species. For CB4 this 
! --- translates into XYL and TOL species, and for SAPRC90 this
! --- tranlates into ARO1 and ARO2 for the COND2243 mechanism.
!----------------------------------------------------------------------
      parameter(ns=2)
      dimension arate(ns)
      dimension roh(ns),ro3(ns),rno3(ns)
! --- Convert cm3/(molec-sec) to ppm-1-min-1 (ref conditions only)
!      xn = 6.023e24   Avagadros # molecule/mole
!      1 ppm = xnd molecules/cm3 at 1 atm-289K
      xnd = 2.55e13
      xconv = xnd*60.0
! --- Inverse half life of toluene - Warneck 1988/cb4
!      roh(1) = ppmoh*6.4e-12*xconv
      roh(1) = ppmoh*3100.*exp(322./temp)
      ro3(1) = ppmo3*1.5e-22*xconv
      rno3(1) = ppmno3*1.8e-17*xconv
! --- Inverse half life of xylene - Warneck 1988/cb4
!      roh(2) = ppmoh*1.8e-11*xconv
      roh(2) = ppmoh*24530.*exp(116./temp)
      ro3(2) = ppmo3*5.0e-22*xconv
      rno3(2) = ppmno3*6.2e-17*xconv
!
! --- Cnew = Cold*exp(-(roh+ro3+rno3)*dt) 
! --- Express rate as %/hr rather than min-1 by factor 60.*100.
      do k = 1,ns
         arate(k)=6000.*(roh(k)+ro3(k)+rno3(k))
      enddo
      return
      end
!---------------------------------------------------------------------
      subroutine setsoa(ndathr)
!---------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402            SETSOA
!                G. Moore, Earth Tech
!
! --- PURPOSE:  Select aerosol mass concentration and VOC/NOX ratio
!               for current hour
!
! --- UPDATE
! --- V5.5-V5.7     030402  (DGS): Add list file unit to GRDAY
!
! --- INPUTS:
!            NDATHR - integer    - YYJJJHH date-time for hour
!
!     Common Block /CHEMDAT/ variables:
!        BCKPMF(12),OFRAC(12),VCNX(12)
!
! --- OUTPUT:
!
!     Common Block /SOA/ variables:
!        VC2NX, AMO
!
! --- SETSOA called by:  COMP
! --- SETSOA calls:      GRDAY
!----------------------------------------------------------------------
      include 'params.puf'
      include 'chemdat.puf'
      include 'soa.puf'
! --- Extract month from date-time
      iyr=ndathr/100000
      ijul=ndathr/100 - 1000*iyr
      call GRDAY(io6,iyr,ijul,imo,iday)
! --- Set background data for this month
      vc2nx=vcnx(imo)
      amo=bckpmf(imo)*ofrac(imo)
      return
      end
!----------------------------------------------------------------------
      subroutine qafog(problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 000602_2           QAFOG
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Check FOG-related input data provided in control file
!
! --- UPDATE
! --- V5.4-V5.4   000602_2 (DGS): Add MXREC >= (NPT1+NPT2)*MXRFOG for
!                                 MFOG=1 (PLUME mode)
!                 000602_2 (DGS): Add check for terrain treatment for
!                                 MFOG=1 (PLUME mode)
!                 000602_2 (DGS): Require METFM=2 (ISCMET) for MFOG=1
!                                 (PLUME mode)
!                 000602_2 (DGS): Remove 1-source limit in PLUME mode
!                 000602_2 (DGS): Remove receptor limit of 999
!
! --- INPUTS:
!        PROBLEM - logical    - Flag indicating if FATAL flaw is found
!                ( common blocks filled in READCF )
!
!     Common Block /FLAGS/ variables:
!        MSLUG, MFOG, MCTADJ
!     Common Block /GEN/ variables:
!        METFM, NSPEC, CSPEC
!     Common Block /GRID/ variables:
!        LSAMP
!     Common Block /NONGRD/ variables:
!        NREC
!     Common Block /PT1/ variables:
!        NPT1
!     Common Block /PT2/ variables:
!        NPT2
!     Common Block /AR1/ variables:
!        NAR1
!     Common Block /AR2/ variables:
!        NAR2
!     Common Block /LN1/ variables:
!        NLINES
!     Common Block /LN2/ variables:
!        NLN2
!     Common Block /VOL1/ variables:
!        NVL1
!     Common Block /VOL2/ variables:
!        NVL2
!
! --- OUTPUT:
!        PROBLEM - logical    - Flag indicating if FATAL flaw is found
!
! --- QAFOG called by:  READCF
! --- QAFOG calls:      none
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
! --- Include common blocks
      include 'flags.puf'
      include 'outpt.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'nongrd.puf'
      include 'pt1.puf'
      include 'pt2.puf'
      include 'ar1.puf'
      include 'ar2.puf'
      include 'ln1.puf'
      include 'ln2.puf'
      include 'vol1.puf'
      include 'vol2.puf'
!
      logical problem
!
! --- Check for valid non-zero MFOG selection
      if(mfog.GT.2) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--    FOG output is selected when       --'
         write(io6,*)'--            MFOG=1,2                  --'
         write(io6,*)'--  Other non-zero values are INVALID   --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Compressed output is NOT allowed in PLUME Mode
      if(MFOG.EQ.1 .AND. LCOMPRS) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--     FOG output in PLUME Mode         --'
         write(io6,*)'--        is NOT compressed             --'
         write(io6,*)'--   LCOMPRS has been reset to FALSE    --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         lcomprs=.FALSE.
      endif
!
! --- Restrict sampling method to PUFF
      if(mslug.NE.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  FOG output requires PUFF sampling   --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- PDF not allowed with FOG processing
      if(mpdf.NE.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  PDF treatment not available with    --'
         write(io6,*)'--  FOG output selected                 --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Chemical transformation not allowed with FOG processing
      if(mchem.NE.0 .OR. mwet.NE.0 .OR. mdry.NE.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'-- Transformation (MWET,MDRY,MCHEM) not --'
         write(io6,*)'--  available when FOG output selected  --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Only ISCMET is allowed with FOG processing
      if(metfm.NE.2) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  Use ISCMET met data format when     --'
         write(io6,*)'--  FOG output is selected                 --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Remove any source-types that are not allowed
      nsrc=nar1+nar2+nlines+nln2+nvl1+nvl2
      if(nsrc.NE.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  FOG processing applies to POINT     --'
         write(io6,*)'--  sources (control file & PTEMARB).   --'
         write(io6,*)'--  Other sources in control file are   --'
         write(io6,*)'--       RESET to zero.                  --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         nar1=0
         nar2=0
         nlines=0
         nln2=0
         nvl1=0
         nvl2=0
      endif
!
! --- Discrete receptors are essential in receptor mode
      if(nrec.LE.0 .AND. mfog.EQ.2) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  FOG output requires DISCRETE recs   --'
         write(io6,*)'--  when run in receptor mode           --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Ignore gridded receptors
      if(LSAMP) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  FOG processing applies to DISCRETE  --'
         write(io6,*)'--  receptors ONLY.  Gridded receptors  --'
         write(io6,*)'--  are ignored (LSAMP reset to FALSE). --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         lsamp=.FALSE.
      endif
!
! --- Species are fixed: 1=H2O, 2=TXS
      if(nspec.NE.2 .OR. cspec(1).NE.'H2O' .OR. cspec(2).NE.'TXS') then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--    MFOG option requires NSPEC=2      --'
         write(io6,*)'-- Species 1= H2O  (emitted water)      --'
         write(io6,*)'-- Species 2= TXS  (temperature excess) --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- Discrete receptor array must accept (npt1+npt2)*MXRFOG points
      nsize=(npt1+npt2)*mxrfog
      if(nsize.GT.MXREC) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'          FATAL SELECTION  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--  MFOG=1 option (PLUME mode) requires --'
         write(io6,*)'--  MXREC >= (NPT1+NPT2)* MXRFOG        --'
         write(io6,*)'--  MXREC               = ',mxrec
         write(io6,*)'--  (NPT1+NPT2)* MXRFOG = ',nsize
         write(io6,*)'--  Use fewer sources, or               --'
         write(io6,*)'--  Resize MXREC, MXRFOG and recompile  --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         problem=.TRUE.
      endif
!
! --- No terrain treatment in PLUME Mode
      if(MFOG.EQ.1 .AND. MCTADJ.NE.0) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'------------------------------------------'
         write(io6,*)'           QA ALERT  !! '
         write(io6,*)'------------------------------------------'
         write(io6,*)'--     FOG output in PLUME Mode         --'
         write(io6,*)'--  does NOT use terrain adjustments    --'
         write(io6,*)'--    MCTADJ has been reset to 0        --'
         write(io6,*)'------------------------------------------'
         write(io6,*)
         mctadj=0
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine setfog(atitle)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 101111            SETFOG
!                D. Strimaitis
!
! --- PURPOSE:  Set FOG data in commons (fixed data are assigned in
!               block data)
!
! --- UPDATE
! --- V6.3-V6.402 101111   (DGS): Update GETCELL arguments
! --- V5.4-V6.3   100212   (DGS): Add nested CALMET grids
! --- V5.4-V5.4   000602_2 (DGS): Remove the receptor index of 1000 
!                                 used in the original FOG.DAT format
!                                 to indicate compression
!                 000602_2 (DGS): Set receptor distances for PLUME mode
!
! --- INPUTS:
!        ATITLE - character    - First line of CALPUFF.INP file
!
!     Common Block /FLAGS/ variables:
!        MFOG
!     Common Block /GEN/ variables:
!        IBYR
!     Common Block /GRID/ variables:
!        NX, NY
!     Common Block /OUTPT/ variables:
!        LDEBUG, LCOMPRS
!     Common Block /NONGRD/ variables:
!        NREC
!     Common block /METHD/ variables:
!        NEARS(mxnx,mxny), NSSTA
!     Common Block /PT1/ variables:
!        XPT1GRD(mxpt1),YPT1GRD(mxpt1)   ...(Met grid)
!        ELSTAK(mxpt1)
!     Common Block /PT2/ variables:
!        TIEM2(7,mxpt2)                  ...(Met grid)
!
! --- OUTPUT:
!
!     Common Block /FOG/ variables:
!        FTITLE,NFREC,IFYR,XRFOG,ZELEVFOG,ISSTA,IXSRC,IYSRC,
!        XRFOG,LPMODE,LADTFOG,TXSMXFOG
!
! --- SETFOG called by:  SETUP
! --- SETFOG calls:      GETCELL
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
! --- Include common blocks
      include 'flags.puf'
      include 'fog.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'outpt.puf'
      include 'pt1.puf'
      include 'pt2.puf'
      include 'nongrd.puf'
      include 'methd.puf'
      character*80 atitle
      data zero/0.0/
!     Common Block /FOG/ variables:
! -----------------------------------
! --- Pass first title line to identify run
      ftitle(1)=atitle(1:20)
      ftitle(2)=atitle(21:40)
      ftitle(3)=atitle(41:60)
      ftitle(4)=atitle(61:80)
! --- Set FOG logicals based on MFOG value
! --- PLUME Mode versus RECEPTOR Mode output
      lpmode=.FALSE.
      if(mfog.EQ.1) lpmode=.TRUE.
! --- Add the temperature excesses at receptor subject to constraint
! --- that total temperature excess does not exceed DTEMP at release
      ladtfog=.TRUE.
! --- Initialize the maximum temperature excess to a LARGE value;
! --- it will be reset to this value at the end of each timestep
      txsmxfog=9999.
      if(LPMODE) then
! ---    Setup for plume mode
! ---------------------------
! ---    Set the downwind source-receptor distances (m)
! ---    Distribute using a quadratic profile x=a*(i**2) + b
         xmin=50.
         xmax=10000.
         alpha=(xmax-xmin)/(FLOAT(mxrfog**2)-1.)
         beta=xmin-alpha
         do i=1,mxrfog
            xrfog(i)=alpha*FLOAT(i**2)+beta
         enddo
         nfrec=mxrfog
      else
! ---    Setup for receptor mode
! ------------------------------
         nfrec=nrec
      endif
! --- Number of sources (points only)
      nfpts=npt1+npt2
! --- Set the year for the run equal to the starting year
      ifyr=ibyr
! --- Retain the initial zeroed days array to signal use of ALL
! --- days (and hours) in CALPUFF run (POSTPM and POSTRM modified)
! --- Set index of surface met station nearest the sources
      xsum=0.
      ysum=0.
      nsum=0
      do i=1,npt1
         xsum=xsum+xpt1grd(i)
         ysum=ysum+ypt1grd(i)
         nsum=nsum+1
      enddo
      do i=1,npt2
         xsum=xsum+tiem2(1,i)
         ysum=ysum+tiem2(2,i)
         nsum=nsum+1
      enddo
      if(nsum.LE.0) stop 'SETFOG:  No point sources are found!'
      xbar=xsum/nsum
      ybar=ysum/nsum
! --- Select met grid cell may from domains used
      call GETCELL('SETFOG      ','GRIDIJ',1,xbar,ybar,zero,zero,&
                   ixsrc,iysrc,mdsrc)
      issta=nears(ixsrc,iysrc,mdsrc)
      if(issta.gt.nssta(mdsrc))then
         write(io6,*) ' ERROR in subr. SETFOG'
         write(io6,*)'ISSTA > NSSTA -- ISSTA = ',issta,' NSSTA = ',&
                      nssta(mdsrc)
         stop
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine fogrec(ldb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            FOGREC
!                D. Strimaitis
!
! --- PURPOSE:  Compute array of discrete receptor locations (x,y)
!               based on set 'distances' fixed in BLOCK DATA.
!               These locations are set for each wind direction
!               sector.
!
! --- UPDATE
! --- V6.268-V6.3  100212 (DGS): Add nested CALMET grids
! --- V6.22-V6.268 100108 (DGS): Fix name of debug logical in ADVECT
!                                call
! --- V5.4-V6.22   070921 (DGS): Add check for ATAN2(0.0,0.0) as
!                                function will halt execution if
!                                both arguments are zero
!
! --- INPUTS:
!               LDB - logical - Debug output (T,F)
!
!     Common Block /COMPARM/ variables:
!        XMINZI, XMAXZI
!     Common Block /FOG/ variables:
!        XRFOG(mxrfog),ZRFOG(mxrfog,mxpt) ...(meters)
!        IXSRC, IYSRC, MDSRC
!     Common Block /GRID/ variables:
!        DGRIDI                          ...(1/meters)
!     Common Block /METHD/ variables:
!        Z0(mxnx,mxny,mxmetdom)
!     Common Block /METHR/ variables:
!        IPGT(mxnx,mxny,mxmetdom), HTMIX(mxnx,mxny,mxmetdom),
!        XMONIN(mxnx,mxny,mxmetdom)
!     Common Block /PUFF/ variables:
!        XPB(mxpuff), YPB(mxpuff),       ...(met grid)
!        ZPB(mxpuff), ELBASE(mxpuff)
!
! --- OUTPUT:
!
!     Common Block /NONGRD/ variables:
!        NREC, XNG(mxrec), YNG(mxrec)    ...(met grid)
!        ZNG(mxrec), ELEVNG(mxrec)
!
! --- FOGREC called by:  COMP
! --- FOGREC calls:      ADVECT
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
! --- Include common blocks
      include 'comparm.puf'
      include 'fog.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'nongrd.puf'
      include 'puff.puf'
      logical ldb
      data d2rad/0.0174533/
! --- Process puff heights to identify the mean position
      zsum=0.
      nsum=0
      do i=1,nipfog
         zsum=zsum+zpb(ipfog(i))
         nsum=nsum+1
      enddo
      if(nsum.LE.0) stop 'FOGREC:  No point sources are found!'
      zbar=zsum/nsum
! --- Find the flow direction at this height, at the mean location
      istab=ipgt(ixsrc,iysrc,mdsrc)
      istab=MIN0(istab,6)
      dpbl=AMAX1(htmix(ixsrc,iysrc,mdsrc),xminzi)
      dpbl=AMIN1(dpbl,xmaxzi)
      el=xmonin(ixsrc,iysrc,mdsrc)
      z0m=z0(ixsrc,iysrc,mdsrc)
      zbot=0.95*zbar
      ztop=1.05*zbar
      call ADVECT(ldb,ixsrc,iysrc,mdsrc,z0m,el,dpbl,istab,zbar,&
                  zbot,ztop,uadv,vadv)
! --- Convert vector components to a flow direction
      if(vadv.EQ.0.0 .AND. vadv.EQ.0.0) then
! ---    Point calms to the north
         flow0=0.0
      else
         flow0=90.-(ATAN2(vadv,uadv))/d2rad
         flow0=AMOD(flow0,360.)
      endif
! --- Shift to nearest 10-degrees
      iflow=NINT(flow0/10.)
      florad=10.*d2rad*FLOAT(iflow)
      sinf=SIN(florad)
      cosf=COS(florad)
      
! --- Initialize number of discrete receptors to zero
      nrec=0
! --- Loop over sources, and establish discrete receptors for each
      do i=1,nipfog
         do irec=1,mxrfog
            nrec=nrec+1
            xng(nrec)=xpb(ipfog(i))+xrfog(irec)*sinf*dgridi
            yng(nrec)=ypb(ipfog(i))+xrfog(irec)*cosf*dgridi
            zng(nrec)=zrfog(irec,i)
            elevng(nrec)=elbase(ipfog(i))
         enddo
      enddo
! --- Report results
      if(LDB) then
         write(io6,*)
         write(io6,*)'FOGREC:  Flow direction (deg): ',flow0
         ir=0
         do i=1,nipfog
            write(io6,*)
            write(io6,*)'Discrete receptors for source ',i
            write(io6,*)'    xng','            yng',&
                        '            zng','           elev'
            do irec=1,mxrfog
               ir=ir+1
               write(io6,*) xng(ir),yng(ir),zng(ir),elevng(ir)
            enddo
         enddo
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine ztrace(ldb,ip,ndist,xa,za)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 080520            ZTRACE
!                D. Strimaitis
!
! --- PURPOSE:  Computes puff trajectory height (m AGL) at array of
!               distances from a source, for the conditions at the
!               time of release;  terrain effects are not included
!               -- For POINT sources only --
!
! --- UPDATE
! --- V6.23-V6.261 080520 (DGS): Replace call to PRSS with GRISE
! --- V5.72-V6.23 080204  (DGS): Remove stack-tip downwash adjustment
!                                to gradual rise because adjustment
!                                is done within GRISE.
! --- V5.4-V5.72  031017  (DGS): IRLSNUM,ISRCNUM,ISRCTYP replace
!                                IPUFID
! --- V5.4-V5.4   000602_4 (DGS): Assign ZFRISE the rise rather than
!                                 the effective plume ht at final rise
!
! --- INPUTS:
!               LDB - logical - Flag for debug output
!                IP - integer - Puff index
!             NDIST - integer - Number of distances in trace
!                xa - real    - Array of distances in trace (m)
!
!     Common block /CURRENT/ variables:
!        IPNUM, ISNUM, ISTYPE, ZFRISE, XSHIFT
!     Common Block /FLAGS/ variables:
!        MTRANS, MTIP
!     Common Block /PUFF/ variables:
!        zfinal,xfinal,fb,fm,xbfin,xmfin,elbase,
!        idw0,ht0,exitw0,diam0,ws0,istab0,sqrts0,srat0,
!        hb0,hw0,heff20,iru0,sigv0,sigw0,el0,
!        plexp0,zly0,r0
!
!     Parameters:
!        IO6, MXPUFF
!
! --- OUTPUTS:
!                za - real    - Array of heights in trace (m AGL)
!
!
! --- ZTRACE called by:  COMP
! --- ZTRACE calls:      GRISE
!----------------------------------------------------------------------
!
! --- Include parameter block
      include 'params.puf'
! --- Include common blocks
      include 'current.puf'
      include 'flags.puf'
      include 'puff.puf'
      real xa(ndist),za(ndist)
      logical ldb
! --- Set variables in /CURRENT/ that may be used in GRISE call
      ipnum=ip
      zfrise=zfinal(ip)-(ht0(ip)-stipdw(ip))
      xfrise=xfinal(ip)
      xshift=xshift0(ip)
! --- Fix as source 1 from PTEMARB file (update if this subroutine is
! --- applied to other types)
      istype=2
      isnum=1
! --- Loop over distances
      do ix=1,ndist
         x=xa(ix)
! -------------------------------------------------------------------
! ---    Building downwash ---
! -------------------------------------------------------------------
!        Point sources:
!        Consider the details of effects of the Huber-Snyder downwash
!        formulation or the Schulman-Scire downwash formulation
!        within 10 HL of the source.
! -------------------------------------------------------------------
         if(idw0(ip).GT.0) then
! ---       Check for receptors in downwash zone
            hl=amin1(hb0(ip),hw0(ip))
            if(x.LT.(10.*hl)) then
               hgr=zfinal(ip)
! ---          Assess gradual rise in downwash zone
               if(x.LT.xfinal(ip)) then
! ---             Compute gradual rise
                  if(idw0(ip).EQ.2) then
! ---                Schulman-Scire building downwash
                     call grise(x,hgr,risefac)
                  elseif(idw0(ip).EQ.1) then
! ---                Huber-Snyder building downwash
                     call grise(x,hgr,risefac)
                  endif
               endif
!
! ---          Done
               goto 500
            endif
         endif
! ---    Continue on for receptors outside downwash zone
! --------------------------------------------------------------------
! ---    Compute the gradual rise if appropriate ...............
! ---      Use ISC convention that gradual rise is used to compute
! ---      buoyancy enhancement to sigmas; and is used as the "plume"
! ---      height only if MTRANS=1
! --------------------------------------------------------------------
!
         if(x.LT.xfinal(ip) .AND. x.GT.0.0) then
! ---       Compute gradual rise
            if(idw0(ip).EQ.2) then
! ---          Schulman-Scire building downwash
               call grise(x,hgr,risefac)
            elseif(idw0(ip).EQ.1) then
! ---          Huber-Snyder building downwash
               call grise(x,hgr,risefac)
            else
! ---          No building downwash
!c ---          Adjustment for stack-tip downwash (NOT for bldg downwash)
!               if(mtip.EQ.1) then
!                  call stktip(ht0(ip),diam0(ip),fm(ip),
!     &                        exitw0(ip),ws0(ip),tipdw)
!               else
!                  tipdw=0.0
!               endif
               call grise(x,hgr,risefac)
!               hgr=amax1(0.0,hgr-tipdw)
            endif
         elseif(x.LE.0.0 .AND. xfinal(ip).GT.0.0) then
! ---       Receptor upwind of source, and final rise does NOT occur at
! ---       source (wind for plume rise is not "calm")
            hgr=ht0(ip)
         else
! ---       Final rise
            hgr=zfinal(ip)
         endif
500      za(ix)=hgr
      enddo
      if(LDB) then
         write(io6,*)
         write(io6,*)'ZTRACE:  Puff IRLSNUM, ISRCNUM, ISRCTYP: '
         write(io6,*)'       ',irlsnum(ip),isrcnum(ip),isrctyp(ip)
         do i=1,ndist
            write(io6,*)'                 i,x,z = ',i,xa(i),za(i)
         enddo
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine fogwind(io,ldb,ws,u,v)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 000602_2         FOGWIND
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Change advection direction to align it with the
!               nearest 10-degree sector used for PLUME mode receptors
!
! --- INPUTS:
!             IO - integer - File unit for output
!            LDB - logical - Debug output (T,F)
!             WS - real    - Wind speed (m/s)
!            U,V - real    - Vector flow components (m/s)
!
!
! --- OUTPUT:
!            U,V - real    - Vector flow components (m/s)
!
! --- FOGWIND called by:  COMP
! --- FOGWIND calls:      none
!----------------------------------------------------------------------
!
      logical ldb
      data rad2deg/57.295778/
! --- Save initial flow components
      usave=u
      vsave=v
! --- Convert vector components to a flow direction
      flow0=90.-rad2deg*ATAN2(v,u)
      flow0=AMOD(flow0,360.)
! --- Shift to nearest 10-degrees
      iflow=NINT(flow0/10.)
      flow=10.*FLOAT(iflow)
! --- Compute revised flow components
      ang=(90.-flow)/rad2deg
      u=ws*COS(ang)
      v=ws*SIN(ang)
      if(LDB) then
         write(io,*)
         write(io,*)'FOGWIND:     input u,v = ',usave,vsave
         write(io,*)' flow dir input/output = ',flow0,flow
         write(io,*)'            output u,v = ',u,v
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine plmfog(xold,yold,jstab,&
                        iru,sigv,sigw,el,bvf,ws,wd,dpbl,nspec,&
                        qlo,qup,icode,hlid,temiti,ldbhr,lcalm)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090710            PLMFOG
!                D. Strimaitis
!
! --- PURPOSE:  Compute fog-related plume data for current source
!
! --- UPDATE
! --- V5.75-V6.267  090710  (DGS): Add maximum lid height for call to
!                                  VCOUP
! --- V5.7-V5.75    050225  (DGS): Add DPBL arg to pass on to PUFRECS,
!                                  for SETCSIG and TAULY
! --- V5.5-V5.7     030402  (FRR): Add 2D met arrays (i2dmet)
!                   030402  (DGS): Drop impact at receptors in/near
!                                  cavity zone for PRIME downwash
!
! --- INPUTS:
!         XOLD,YOLD - real    - Starting position (Met grid units)
!             JSTAB - integer - PGT stability class at puff ht
!               IRU - integer - Rural(0) or urban(1) flag
!              SIGV - real    - Sigma-v velocity (m/s)
!              SIGW - real    - Sigma-w velocity (m/s)
!                EL - real    - Monin-Obukhov length (m)
!               BVF - real    - Brunt-Vaisala frequency (1/s)
!                WS - real    - Mean wind speed (m/s)
!                WD - real    - Mean  wind direction
!             NSPEC - real    - Number of species
!        QLO(nspec) - real    - Puff mass (g) in surface layer
!        QUP(nspec) - real    - Puff mass (g) above surface layer
!             ICODE - integer - Code for vertical layering of puff
!              HLID - real    - Height of reflection lid (m)
!            TEMITI - real    - 1/Emission duration for puff mass (s-1)
!             LDBHR - logical - Debug output (T,F)
!             LCALM - logical - .TRUE. if this puff is in a calm
!
!     Common block /CHIFLX/ variables:
!           CHIREC(mxrec,mxspec)
!     Common block /FOG/ variables:
!           TXSMXFOG, ISSTA
!     Common block /GRID/ variables:
!           DGRID
!     Common block /METHR/ variables:
!     Common block /METHD/ variables:
!            I2DMET
!     Common Block /NONGRD/ variables:
!           NREC, XNG(mxrec), YNG(mxrec)
!
!     Parameters:
!        MXSPEC, MXREC, IO6, IO12
!
! --- OUTPUTS:
!     Common block /CHIFLX/ variables:
!           CHIREC(mxrec,mxspec)
!
! --- PLMFOG called by:  COMP
! --- PLMFOG calls:      INCR, VSAT, PUFRECS, VCOUP
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'chiflx.puf'
      include 'methr.puf'
      include 'fog.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'nongrd.puf'
      real qlo(nspec),qup(nspec)
      logical ldbhr,lcalm,lclip
      data rt2pi/2.5066283/, d2rad/0.0174533/
! --- No clipping for receptor-specific sigmas here
      data lclip/.FALSE./
! --- Set number of sigmas to reach "edge" of plume
      data edge/3./
! --- Calculate vertical distribution factor (uniform ONLY)
      if(icode.EQ.2 .OR. icode.EQ.6) then
! ---    ICODE=2,6:  within past/present mixed layer and uniform
         f1=1./hlid
      elseif(icode .EQ. 4) then
! ---    ICODE=4:  above mixed layer and uniform (no ground-level concs)
         f1=0.
      else
! ---    ICODE ODD: Gaussian,  set f1 to 1.0 for now
         f1=1.0
      endif
! --- Skip this puff if vertical factor is zero
      if(f1.EQ.0.) return
!
! ----------------------------------------------------
! --- DISCRETE RECEPTOR POINTS  (using MET grid units)
! --- Process plume data at receptor locations
! ----------------------------------------------------
!
      if(nrec.LE.0) then
         write(*,*)
         stop 'PLMFOG:  No FOG Receptors!'
      endif
! --- Establish coordinate rotation to place y-axis along the flow
      flow=wd+180
      flow=AMOD(flow,360.)
      florad=d2rad*flow
      sinf=SIN(florad)
      cosf=COS(florad)
! --- Loop over receptors
      do i=1,nrec
         xrec = xng(i)*dgrid
         yrec = yng(i)*dgrid
         zrec = zng(i)
         elrec = elevng(i)
! ---    Obtain receptor-specific sigmas and puff height (gradual
! ---    rise) including any terrain adjustment to height
         call PUFRECS(lclip,xrec,yrec,zrec,jstab,iru,sigv,sigw,el,bvf,&
                      ws,dpbl,elrec,ppc,lcalm,&
                      trec,frec,syrec,szrec,zgrise,zpr,zrpole,&
                      rfacsq,idrop)
! ---    PRIME downwash:  skip 'dropped' receptors in cavity zone
         if(idrop.EQ.1) goto 100
! ---    Lateral coupling
         x=xng(i)-xold
         y=yng(i)-yold
         cross=ABS(dgrid*(x*cosf-y*sinf))/syrec
         if(cross.LE.edge) then
            f2=EXP(-0.5*(cross)**2)/(rt2pi*syrec)
         else
            f2=0.0
         endif
!
         if(mod(icode,2).EQ.1) then
! ---       Tracking Gaussian plume so ignore possible 2-layer case
            hlidmax=hlid
! ---       Sample vertical distribution (GAUSSIAN)
            f1=VCOUP(icode,zrpole,zpr,szrec,hlid,hlidmax)
         endif
!
! ***
      if(LDBHR) then
      write(io6,*) 'PLMFOG: Disc rec x,y,z= ',xrec,yrec,zrec
      write(io6,*) '        Trans. Rise   = ',zgrise
      write(io6,*) '        Adj. Puff Ht. = ',zpr
      write(io6,*) '    Adj. Rec Pole Ht. = ',zrpole
      write(io6,*) '       Sigma-y @ rec. = ',syrec
      write(io6,*) '       Sigma-z @ rec. = ',szrec
      write(io6,*) '  Reflecting Lid Ht.  = ',hlid
      write(io6,*) '     Time to receptor = ',trec
      write(io6,*) ' G.Rise Adj to BID**2 = ',rfacsq
      write(io6,*) '               vcoup  = ',f1
      write(io6,*) '               hcoup  = ',f2
      endif
! ***
!
         f12=f1*f2
         if(f12.GT.0.) then
            if(LCALM) then
! ---          Compute concentration as if a single puff
               chibyq=f12/(rt2pi*syrec)
            else
! ---          Compute concentration as if a plume
               chibyq=(temiti/ws)*f12
            endif
         else
            chibyq=0.0
         endif
         do ipol=1,2
            chirec(i,ipol)=chirec(i,ipol)+(qlo(ipol)+qup(ipol))*chibyq
            if(ipol.EQ.2 .AND. LADTFOG) then
! ---          FOG: SUM the T excess (ipol=2) at receptor but
! ---          do not allow sum to exceed excess at release
               chirec(i,ipol)=AMIN1(txsmxfog,chirec(i,ipol))
            endif
         enddo
! ---    
         if(LDBHR) then
            write(io6,*)'i,chi: ',i,(chirec(i,j),j=1,2)
         endif
100      continue
! --- End loop over discrete receptors
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine fogout
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212            FOGOUT
!                D. Strimaitis
!
! --- PURPOSE:  Compute fog-related data at discrete receptors and
!               output for processing with "POSTRM" or "POSTPM"
!
! --- UPDATE
! --- V6.1-V6.3   100212   (DGS): Add nested CALMET grids
! --- V5.7-V6.1   050915   (DGS): Update date-time variables, but
!                                 retain hourly output
! --- V5.5-V5.7   030402   (DGS): Add list file unit to INCR
! --- V5.4-V5.4   000602_2 (DGS): Add LPMODE=T processing for revised
!                                 PLUME Mode
!                 000602_2 (DGS): Write compression logical instead of
!                                 adding 1000 to receptor number
!
! --- INPUTS:
!
!     Common block /CHIFLX/ variables:
!          CHIREC(mxrec,mxspec)
!     Common block /DATEHR/ variables
!          NYRE, NJULE, NHRE
!     Common block /FOG/ variables:
!          ISSTA, IXSRC, IYSRC, MDSRC, LPMODE
!     Common block /GRID/ variables:
!          NZ
!     Common block /METHR/ variables:
!          IRHSS(mxss,mxmetdom), TEMPSS(mxss,mxmetdom)
!          umet(mxnx,mxny,mxnz,mxmetdom), vmet(mxnx,mxny,mxnz,mxmetdom)
!          ipgt(mxnx,mxny,mxmetdom), htmix(mxnx,mxny,mxmetdom)
!          ptg(2)
!     Common block /NONGRD/ variables
!          NREC, ZNG
!     Common block /OUTPT/ variables
!          LCOMPRS
!     Common block /WRKSPC/ variables
!          TMP3(mxrec)
!
!     Parameters:
!        MXSPEC, MXREC, MXSS, MXNX, MXNY, MXNZ, MXMETDOM, IO6, IO12
!
! --- OUTPUTS:
!               (results to FOG.DAT file)
!
!     Common block /FOG/ variables:
!          TXSMXFOG
!
! --- FOGOUT called by:  COMP
! --- FOGOUT calls:      VSAT
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
! --- Include common blocks
      include 'chiflx.puf'
      include 'datehr.puf'
      include 'flags.puf'
      include 'fog.puf'
      include 'grid.puf'
      include 'methr.puf'
      include 'nongrd.puf'
      include 'outpt.puf'
      include 'wrkspc.puf'
! frr(09/01)
      include 'methd.puf'
!
      real qsat(mxrec),qback(mxrec),qpeak(mxrec),tplm(mxrec)
      character*15 cname,qbname,qsname
      data rtod/57.29578/
      data cname/'H2O_peak'/, qbname/'H2O_bck'/, qsname/'H2O_sat'/
! --- Set day, hour for output record (use hr=1,24)
      iyr=nyre
      ijul=njule
      ihr=nhre
      if(ihr.EQ.0) then
! ---    Back up 1 hour
         nhrinc=-1
         call INCR(io6,iyr,ijul,ihr,nhrinc)
! ---    Go forward 1 hour
         ihr=ihr+1
      endif
      nyrjulhr=iyr*100000+ijul*100+ihr
! --- Set supporting met data from cell nearest first source
      iz=1
      if(nz.EQ.1) iz=2
      ws=SQRT(umet(ixsrc,iysrc,iz,mdsrc)**2+&
              vmet(ixsrc,iysrc,iz,mdsrc)**2)
      if(ws.GT.0.0) then
         fv=90.-(ATAN2(vmet(ixsrc,iysrc,iz,mdsrc),&
                       umet(ixsrc,iysrc,iz,mdsrc)))*rtod
         if(fv.LT.0.) fv=fv+360.
      else
! ---    Absolute calm
         fv=0.0
      endif
      dpbl=htmix(ixsrc,iysrc,mdsrc)
      idpbl=NINT(dpbl)
      istab=MIN(ipgt(ixsrc,iysrc,mdsrc),6)
! --- Codes
      icd=NINT(ws*10.)*10000+istab*1000+999
      imet2=NINT(fv)*10000+MIN(9999,idpbl)
! --- Set temperature (K) and relative humidity (fraction) for
! --- representative surface met station
! frr (09/01) new calmet format (2D temp, RH)
      if(i2dmet.EQ.1) then
         tdegk=temp2d(ixsrc,iysrc,mdsrc)
         frh=0.01*FLOAT(irh2d(ixsrc,iysrc,mdsrc))
      elseif(i2dmet.EQ.0) then
         tdegk=tempss(issta,mdsrc)
         frh=0.01*FLOAT(irhss(issta,mdsrc))
      else
         write(*,*)'Subr. FOGOUT:  Invalid I2DMET = ',i2dmet
         stop
      endif
! --- Convert background RH to water vapor pressure (mb)
!     --- PBSAT   saturation vapor pressure (mb) at TDEGK
!     --- PBACK   background vapor pressure (mb) at TDEGK
!     --- QBSAT   saturation vapor mass conc (g/m^3)
!     --- QBACK0  background vapor mass conc (g/m^3)
      call VSAT(tdegk,pbsat,qbsat)
      pback=frh*pbsat
      qback0=qbsat*frh
! --- Compute QSAT(q/m^3) and QBACK(q/m^3) arrays from temperature
! --- excess array for receptors with non-zero concentration
! --- Loop over discrete receptors
      if(nrec.LE.0) then
         write(*,*)
         stop 'FOGOUT:  No FOG Receptors!'
      endif
      do i=1,nrec
! ---    Initialize in-plume temperature to the ambient near-surface
! ---    air temperature
         tplm(i)=tdegk
         if(chirec(i,1).LE.0.0) then
            chirec(i,1)=0.
            qback(i)=0.
            qsat(i)=0.
            qpeak(i)=0.
         else
! ---       Set in-plume temperature (temperature excess is species 2)
! ---       and compute local in-plume saturation vapor mass conc.
!           --- PSAT    saturation vapor pressure (mb)
!           --- QSAT    saturation vapor mass conc (g/m^3)
            tplm(i)=tplm(i)+chirec(i,2)
            call VSAT(tplm(i),psat,qsat(i))
            qpeak(i)=chirec(i,1)
! ---       Compute local in-plume ambient vapor mass conc., assuming
! ---       that the ambient mixing ratio is constant
!           --- QBACK   background vapor mass conc (g/m^3)
            qback(i)=qsat(i)*pback/psat
         endif
! --- End loop over discrete receptors
      enddo
! -------------------------------------------------
! --- Output for either PLUME mode or RECEPTOR mode
! -------------------------------------------------
      if(LPMODE) then
! ---    Plume Mode date/hr, met. data record
         write(io12)ijul,ihr,ws,fv,istab,tdegk,dpbl,qback0,qbsat,nrec
! ---    Plume Mode data records for hour
         nsrc=nrec/mxrfog
         if(mxrfog*nsrc.NE.nrec) then
            write(io6,*)'FOGOUT:  FATAL Error'
            write(io6,*)'Inconsistent number of receptors found'
            write(io6,*)'           Number of sources : ',nsrc
            write(io6,*)'  Number of receptors/source : ',mxrfog
            write(io6,*)'Number of discrete receptors : ',nrec
            stop 'Halted in FOGOUT -- see list file for details'
         endif
         do i=1,nrec
            write(io12) i,zng(i),qpeak(i),qsat(i),qback(i),tplm(i)
         enddo
      else
! ---    Receptor mode results records for hour        
         write(io12)nyrjulhr,icd,imet2,tdegk,frh,qbsat
         if(LCOMPRS) then
! ---       Write compressed data records
            call COMPRS(qpeak,nrec,tmp3,mxrec,cname,io12)
            call COMPRS(qback,nrec,tmp3,mxrec,qbname,io12)
            call COMPRS(qsat,nrec,tmp3,mxrec,qsname,io12)
         else
            call WRDAT(io12,cname,qpeak,nrec,1)
            call WRDAT(io12,qbname,qback,nrec,1)
            call WRDAT(io12,qsname,qsat,nrec,1)
         endif
      endif
! --- Reset maximum temperature excess for next step
      txsmxfog=9999.
      return
      end
!----------------------------------------------------------------------
      subroutine vsat(tdegk,psat,csat)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 000602              VSAT
!                J. Scire, Earth Tech
!
! --- PURPOSE:  Compute saturation vapor pressure and corresponding
!               saturation mass concentration for given temperature
!
! --- INPUTS:
!          tdegk - real       - Temperature (K)
!
! --- OUTPUT:
!           psat - real       - Saturation water vapor pressure (mb)
!           csat - real       - Saturation water vapor mass
!                               concentration (g h2o/m**3 air)
!
! --- VSAT called by:   PLMFOG, FOGOUT
! --- VSAT calls:       none
!----------------------------------------------------------------------
!
      data gascon/8.31372e-2/
! --- Compute saturation vapor pressure (Goff-Gratch eqns.)
      trat=273.16/tdegk
      if(tdegk.lt.273.16)then
! ---    temperature below 0. deg. C
         psat=exp(2.303*(-9.09685*(trat-1.0)+&
         0.87682*(1.0-tdegk/273.16))-3.56654*(5.6100577-alog(tdegk))+&
         1.81)
      else
! ---    temperature above 0. deg. C
         psat=exp(2.303*(10.79574*(1.0-trat)+&
         1.50475e-4*(1.0-10.**(-8.2969*(tdegk/273.16-1.0)))+&
         4.2873e-4*(10.**(4.76955*(1.0-trat))-1.0))-&
         5.028*(alog(tdegk)-5.6100577)+1.81)
      endif
! --- Compute saturation water vapor mass concentration (g h2o/m**3 air)
! --- (gascon is the gas constant -- 8.31372e-2 m**3*mb/(mole*deg k)
      csat=18.016*psat/(gascon*tdegk)
      return
      end
!----------------------------------------------------------------------
      subroutine rdhdaq(dtype,iunit,versaq,lprt,nsta,ibdathr,ibsec,&
       iedathr,iesec,t2btz,iutm,pmap,utmhem,datum,daten,xyunit,&
       feast,fnorth,rnlat0,relon0,rnlat1,rnlat2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 070921            RDHDAQ
!                J. Scire, D. Strimaitis 
!
! --- PURPOSE:  Read the header records from an air quality data file
!               (e.g. OZONE.DAT, H2O2.DAT)
!
! --- UPDATE
! --- V6.2-V6.22:  070921 (DGS):Condition GEO referencing string
!                               variables and add QA
!                               Remove calls to ALLCAP
! --- V6.114-V6.2: 070629 (DGS):Fix typo in call to YR4 for dataset
!                               version 1.5a
! --- V6.102-V6.114:060725(DGS):Expand input processing to include
!                               headers for PS,EM,LAZA,TTM maps for
!                               version 2.1 datasets
! --- V6.1-V6.102: 051130 (DGS):Drop LLORDER from header for Lat/Lon
!                               coordinates.  These files require the
!                               order to be (lat,lon), and values
!                               include N/S or E/W explicitly.
!                  051130 (DGS):Fix bug in reading the UTM zone and
!                               hemispere, and XYUNITS
! --- V5.7-V6.1 : 050915 (DGS): Dataset version 2.1 is added,
!                               including full coordinate and time
!                               documentation with begin/end times with
!                               seconds.
!                               Header information is passed to scratch
!                               file.
! --- V5.5-V5.7 : 030402 (DGS): Add list file unit to YR4 call
!
! --- INPUTS:
!            DTYPE - C*12       - Data type: e.g. ('OZONE' or 'H2O2')
!                                 [Upper Case]
!            IUNIT - integer    - Fortran unit number of data file
!           VERSAQ - C*16       - Data set version
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!        Parameters:
!            IO6, IOX, MXAQ
!
! --- OUTPUT:
!             NSTA - integer - Number of stations in the data file
!          IBDATHR - integer - Date/hour at beginning of period for
!                              the first data record in the file
!                              (YYYYJJJHH, where YYYY=year,
!                              JJJ=Julian day, HH=hour [00-23 LST])
!            IBSEC - integer - Seconds of the first data record in the
!                              file  (0000-3599)
!          IEDATHR - integer - Date/hour at end of period for
!                              the last data record in the file
!                              (YYYYJJJHH, where YYYY=year,
!                              JJJ=Julian day, HH=hour [00-23 LST])
!            IESEC - integer - Seconds of the last data record in the
!                              file  (0000-3599)
!            T2BTZ - real    - Time to ADD to dataset time to convert
!                              to the Base Time Zone
!             IUTM - integer - UTM zone in which the station
!                              coordinates are specified
!             PMAP - char    - Character code for input map projection
!                              LL  :  Latitude/longitude
!                              UTM :  Universal Transverse Mercator
!                              LCC :  Lambert Conformal Conic
!                              PS  :  Polar Stereographic
!                              EM  :  Equatorial Mercator
!                              LAZA:  Lambert Azimuthal Equal Area
!                              TTM :  Tangential Transverse Mercator
!           UTMHEM - char    - Base hemisphere for UTM projection
!                               (S=southern, N=northern)
!            DATUM - char    - Datum-Region for grid coordinates
!            DATEN - char    - NIMA date for datum parameters
!                                    (MM-DD-YYYY  )
!           XYUNIT - char    - Units for coordinates (e.g., KM)
!           FEAST  - real    - False Easting (km) at projection origin
!           FNORTH - real    - False Northing (km) at projection origin
!          RNLAT0, - real    - N. latitude & E. longitude of x=0 and y=0
!           RELON0 (deg)       of map projection (Used only if PMAP =
