!          RNLAT1, - real    - Matching N. latitude(s) for projection
!          RNLAT2 (deg)        (Used only if PMAP3= LCC, PS, or EM)
!                              LCC :  Projection cone slices through
!                                     Earth's surface at XLAT1 and XLAT2
!                              PS  :  Projection plane slices through
!                                     Earth at XLAT1
!                              EM  :  Projection cylinder slices through
!                                     Earth at [+/-] XLAT1
!     Common Block /QA/ variables:
!            NCOMMOUT
!
! --- RDHDAQ called by: CHEMI
! --- RDHDAQ calls:     YR4, INCR, XTRACTLL, TCLOCK, GEOFMT
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'datehr.puf'
      include 'qa.puf'
!
      character*12 fname,vrs,lab
      character*12 dtype
      logical lprt
      logical lgeo,lutm,llcc,lps,lem,llaza,lttm
      character*8 axtz
      character*16 dataset,dataver,versaq
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      character*4 utmhem,xyunit
      character*8 datum,pmap
      character*12 daten
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Initialize local map projection logicals
      lgeo=.FALSE.
      lutm=.FALSE.
      llcc=.FALSE.
      lps=.FALSE.
      lem=.FALSE.
      llaza=.FALSE.
      lttm=.FALSE.
! --- Initialize other variables
      daten='02-21-2003  '
      xyunit='KM  '
      ibsec=0.0
      iesec=0.0
      feast=0.0
      fnorth=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmap='XXX     '
      iutm=1
      utmhem='N   '
      datum='XXX     '
      axtz='UTC+0000'
! --- AQ routines currently configured for OZONE, H2O2
      if(dtype.EQ.'OZONE       ')then
         dataset='OZONE.DAT    '
      elseif(dtype.EQ.'H2O2        ')then
         dataset='H2O2.DAT     '
      else
         write(*,*)'ERROR in RDHDAQ '
         write(*,*)'Bad calling argument: ',dtype
         write(*,*)'Expected OZONE or H2O2'
         stop
      endif
!     -------------------------
      if(versaq.EQ.'1.5') then
!     -------------------------
! ---    Provide help and then stop (old format not supported)
         read(iunit,*)fname,nsta,iutm,ibdat,ibhr,iedat,iehr,&
                      vrs,lab
         dataver='1.5a            '
         datamod(1:40)='Augmented 5 format with Map Projection, '
         datamod(41:64)='DATUM, Time Zone        '
         write(io6,*)
         write(io6,*)
         write(io6,*)'ERROR in data file for ',dtype
         write(io6,*)
         write(io6,*)'Old file format cannot be used because:'
         write(io6,*)'1.  Coordinate map projection is incomplete'
         write(io6,*)'2.  The DATUM for the coordinates is missing'
         write(io6,*)'3.  The time zone is missing'
         write(io6,*)
         write(io6,*)'ADD these records to the top of the file:'
         write(io6,*)
         write(io6,'(2a16,a64)') dataset,dataver,datamod
         if(iutm.NE.0) then
            write(io6,'(a8)')'UTM     '
            write(io6,'(a8)')'  xxN   '
         else
            write(io6,'(a8)')'LCC     '
            write(io6,'(4a16)')'yy.yyyN         ','xx.xxxW         ',&
                               'yy.yyyN         ','yy.yyyN         '
            write(io6,*)zero,zero
         endif
         write(io6,'(a8)')'DATUM   '
         write(io6,'(a8)')'UTC-0500'
         write(io6,*)
         write(io6,*)'UPDATE these records to describe your file:'
         if(iutm.NE.0) then
           write(io6,*)'- Replace the xxN with your UTM zone and',&
                       ' hemisphere (S for Southern Hemisphere)'
         else
           write(io6,*)'- Replace the xx.xxxW and yy.yyyN with your',&
                       ' LCC projection parameters.'
           write(io6,*)'  The order is Origin Latitude, Origin',&
                       ' Longitude, and the 2 Matching Parallels'
           write(io6,*)'  (each field is 16 characters)'
           write(io6,*)'- Replace the 0.0 0.0 with your False Easting',&
                       ' and Northing if non-zero'   
         endif
         write(io6,*)'- Replace DATUM with your datum code (enter',&
                     ' left-justified in the 8-character field)'
         write(io6,*)'- Replace UTC-0500 with your time zone code'
         write(io6,*)'  (GMT=UTC+0000, EST=UTC-0500, CET=UTC+0100)'
         write(io6,*)
         write(*,*)
         write(*,*)'ERROR in ',dtype,' file -- see list file'
         write(*,*)
         stop
!     ------------------------------
      elseif(versaq.EQ.'1.5a') then
!     -----------------------------
! ---    Augmented headers
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         ncommout=ncommout+1
! ---    Map projection
         read(iunit,'(a8)') pmap
         write(iox,'(a8)') pmap
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmap,iutm,utmhem,clat0,clon0,&
                     clat1,clat2,datum,daten,xyunit,axtz)
         if(pmap.EQ.'LL      ')  lgeo =.TRUE.
         if(pmap.EQ.'UTM     ')  lutm =.TRUE.
         if(pmap.EQ.'LCC     ')  llcc =.TRUE.
         if(pmap.EQ.'PS      ')  lps  =.TRUE.
         if(pmap.EQ.'EM      ')  lem  =.TRUE.
         if(pmap.EQ.'LAZA    ')  llaza=.TRUE.
         if(pmap.EQ.'TTM     ')  lttm =.TRUE.
! ---    Map projection parameters
         if(LUTM) then
            read(iunit,'(i4,a4)') iutm,utmhem
            write(iox,'(i4,a4)')  iutm,utmhem
            ncommout=ncommout+1
         elseif(LLCC) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCC) then
            read(iunit,*) feast,fnorth
            write(iox,*)  feast,fnorth
            ncommout=ncommout+1
         endif
! ---    Map DATUM
         read(iunit,'(a8)') datum
         write(iox,'(a8)')  datum
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz
         write(iox,'(a8)')  axtz
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmap,iutm,utmhem,clat0,clon0,&
                     clat1,clat2,datum,daten,xyunit,axtz)
! ---    Process time zone
         read(axtz(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDAQ -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz
            stop 'Halted in RDHDAQ -- see list file'
         endif
         xtz=-FLOAT(ibtzhh)
! ---    Old Header Record(s)
!
! --- Header Record #1 - General data - beginning, ending dates &
!                        times, UTM zone, number of stations
!
         read(iunit,*)fname,nsta,iutmx,ibdat,ibhr,iedat,iehr,vrs,lab
         write(iox,'(a12,6i6,2a12)')fname,nsta,iutmx,ibdat,ibhr,&
                                    iedat,iehr,vrs,lab
! ---    Enforce YYYY format
         ibyr=ibdat/1000
         ibdy=ibdat-1000*ibyr
         ieyr=iedat/1000
         iedy=iedat-1000*ieyr
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) then
            write(*,*)
            stop 'Halted in RDHDAQ -- see list file.'
         endif
!     -----------------------------
      elseif(versaq.EQ.'2.1') then
!     -----------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmap
         write(iox,'(a8)') pmap
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmap,iutm,utmhem,clat0,clon0,&
                     clat1,clat2,datum,daten,xyunit,axtz)
         if(pmap.EQ.'LL      ')  lgeo =.TRUE.
         if(pmap.EQ.'UTM     ')  lutm =.TRUE.
         if(pmap.EQ.'LCC     ')  llcc =.TRUE.
         if(pmap.EQ.'PS      ')  lps  =.TRUE.
         if(pmap.EQ.'EM      ')  lem  =.TRUE.
         if(pmap.EQ.'LAZA    ')  llaza=.TRUE.
         if(pmap.EQ.'TTM     ')  lttm =.TRUE.
! ---    Map projection parameters
         if(LUTM) then
            read(iunit,'(i4,a4)') iutm,utmhem
            write(iox,'(i4,a4)')  iutm,utmhem
            ncommout=ncommout+1
         elseif(LLCC) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPS) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEM.or.LLAZA.or.LTTM) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCC.or.LLAZA.or.LTTM) then
            read(iunit,*) feast,fnorth
            write(iox,*)  feast,fnorth
            ncommout=ncommout+1
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datum,daten
         write(iox,'(a8,a12)')  datum,daten
         ncommout=ncommout+1
! ---    Units
         read(iunit,'(a4)') xyunit
         write(iox,'(a4)')  xyunit
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz
         write(iox,'(a8)')  axtz
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmap,iutm,utmhem,clat0,clon0,&
                     clat1,clat2,datum,daten,xyunit,axtz)
! ---    Process time zone
         read(axtz(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDAQ -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz
            stop 'Halted in RDHDAQ -- see list file'
         endif
         xtz=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec,&
                      ieyr,iedy,iehr,iesec
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDAQ'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec,&
                           ieyr,iedy,iehr,iesec
         ncommout=ncommout+1
! ---    Number of stations
         read(iunit,*) nsta
         write(iox,*) nsta
         ncommout=ncommout+1
!     -----
      else
!     -----
         write(*,*)'ERROR in ',dtype,' File '
         write(*,*)'Bad Dataset Version: ',versaq
         write(*,*)'Expected 1.5, 1.5a, 2.1'
         stop
!     -----
      endif
!     -----
      if(nsta.gt.mxaq)then
         write(io6,12)nsta,mxaq
12       format(/1x,'ERROR in SUBR. RDHDAQ -- No. of stations ',&
         ' in data file is greater than current array dimension'/1x,&
         '          No. stations in file: ',i5/1x,&
         'Current Array dimension (MXAQ): ',i5)
         write(*,*)
         stop 'Halted in RDHDAQ -- see list file.'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec)
! --- Apply time zone adjustment to date/times
      if(xtz.GE.-13 .AND. xtz.LE.13) then
         t2btz=xtz-xbtz
         ishift=NINT(t2btz)
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in ',dtype,' file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz
         stop 'Bad Time Zone in OZONE or H2O2 file -- see list file'
      endif
! --- Hourly Data:  Move the time for the first period from the end
! ---               to the start of the first hour
      if(versaq.NE.'2.1') then
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      endif
! --- Form date-hr variables
      ibdathr=ibyr*100000+ibdy*100+ibhr
      iedathr=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1)
      call XTRACTLL(io6,'LAT ',clat2,rnlat2)
      call XTRACTLL(io6,'LAT ',clat0,rnlat0)
      call XTRACTLL(io6,'LON ',clon0,relon0)
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,101) dtype
101      format(///1x,13('----------')//1x,'Header record data from ',&
         a12,' file')
         write(io6,102)dataset,axtz,nsta,&
               ibdathr,ibsec,iedathr,iesec,pmap,&
               datum
102      format(/1x,'DATASET: ',a16/1x,&
         'AXTZ:   ',a8/1x,'NSTA:   ',i8,/&
         1x,'IBDATHR:',i9/1x,'IBSEC:  ',i4/&
         1x,'IEDATHR:',i9/1x,'IESEC:  ',i4/&
         1x,'MAP:    ',a8/1x,'DATUM:   ',a8)
         if(LUTM) write(io6,104) iutm,utmhem
         if(LLCC) write(io6,105) clat0,clon0,clat1,clat2,&
                                       feast,fnorth
         if(LPS) write(io6,106) clat0,clon0,clat1
         if(LEM) write(io6,107) clat0,clon0
         if(LLAZA.or.LTTM) write(io6,108) clat0,clon0,&
                                       feast,fnorth
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine rdtiaq(dtype,iunit,nsta,pmap,ldb,cid,xeast,ynorth)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 051130            RDTIAQ
!                J. Scire, D. Strimaitis  Earth Tech
!
! --- PURPOSE:  Read the time-invariant records of an air quality data
!               file (e.g. OZONE, H2O2)
!
! --- UPDATE
! --- V6.1-V6.102: 051130 (DGS):Drop LLORDER for Lat/Lon coordinates.
!                               These files require the order to be
!                               (lat,lon), and values include N/S/E/W.
!                               Add PMAP to test for LL.
! --- V5.7-V6.1 : 050915 (DGS): Dataset version 2.1
!                               Header information is passed to scratch
!                               file.
!
! --- INPUTS:
!            DTYPE - C*12       - Data type: e.g. ('OZONE' or 'H2O2')
!                                 [Upper Case]
!            IUNIT - integer    - Fortran unit number of data file
!             NSTA - integer    - Number of stations in the data file
!             PMAP - char*8     - Map projection
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!     Common Block /QA/ variables:
!            NCOMMOUT
!        Parameters:
!            IO6, IOX
!
! --- OUTPUT:
!        CID(nsta) - C*16 array - Station identifiers (16 characters)
!      xeast(nsta) - real array - Coordinate 1 of the station
!                                 Easting (km) or ELongitude (deg)
!     ynorth(nsta) - real array - Coordinate 2 of the station
!                                 Northing (km) or NLatitude (deg)
!
!     Common Block /QA/ variables:
!            NCOMMOUT
!
! --- RDTIAQ called by: CHEMI
! --- RDTIAQ calls:     XTRACTLL
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'qa.puf'
!
      real xeast(nsta),ynorth(nsta)
      character*16 cid(nsta)
!
      character*8 pmap
      character*12 dtype
      character*16 alat,alon
      logical ldb
! --- Coordinates may be latitude/longitude strings
      if(pmap.EQ.'LL      ') then
! ---    Loop over data stations
         do i=1,nsta
            read(iunit,*)cid(i),alat,alon
            write(iox,'(3a20)')cid(i),alat,alon
            ncommout=ncommout+1
! ---       Conver to real NLAT and ELON and place in arrays
            call XTRACTLL(io6,'LON ',alon,xeast(i))
            call XTRACTLL(io6,'LAT ',alat,ynorth(i))
         enddo
      else
! ---    Loop over data stations
         do i=1,nsta
            read(iunit,*)cid(i),xeast(i),ynorth(i)
            write(iox,'(a16,2f12.3)')cid(i),xeast(i),ynorth(i)
            ncommout=ncommout+1
         enddo
      endif
!
      if(ldb)then
         write(io6,*) 'Time-invariant data from file ',dtype
         do i=1,nsta
            write(io6,*)'I = ',i,' CID(i) = ',cid(i),&
                        ' XEast(i) = ',xeast(i),&
                        ' YNorth(i) = ',ynorth(i)
         enddo
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine rdaq(iunit,dtype,vers,idathrm,isecm,tzhr,nsta,ldb,&
                      ibegh,ibegs,iendh,iends,aqconc,leof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915              RDAQ
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Read the time-varying records of an air quality data
!               file (e.g OZONE, H2O2)
!
! --- UPDATE
! --- V5.7-V6.1 : 050915 (DGS): Dataset version 2.1 is added, and other
!                               versions are retained.
!                               Begin/end times include seconds.
! --- V5.5-V5.7 : 030402 (DGS): Add list file unit to YR4 call
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of data file
!            DTYPE - C*12       - Data type: ('OZONE' or 'H2O2')
!             VERS - char*16    - Data-set version for file
!          IDATHRM - integer    - Mid-pt date/hour of required
!                                 data (YYYYJJJHH)
!            ISECM - integer    - Mid-pt second of required data (SSSS)
!             TZHR - real       - Hours to ADD to times in file to
!                                 convert to BASE TIME ZONE
!             NSTA - integer    - Number of stations in the data file
!              LDB - logical    - Flag controlling printing of data
!        Parameters:
!            IO6
!
! --- OUTPUT:
!      IBEGH,IBEGS - integer    - Starting date-time for which data
!                                 in current set of records is valid
!                                 (YYYYJJJHH , SSSS) in BASE TIME ZONE
!      IENDH,IENDS - integer    - Ending date-time for which data
!                                 in current set of records is valid
!                                 (YYYYJJJHH , SSSS) in BASE TIME ZONE
!     AQCONC(nsta) - real       - Concentration at each station
!                                 (NOTE: 9999. used as
!                                 a missing value indicator)
!             LEOF - logical    - End-Of-File flag
!
! --- RDAQ called by: CHEM
! --- RDAQ calls:     YR4, INCR, TPERIOD, TCLOCK
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      real aqconc(nsta)
      character*12 dtype
      character*16 vers
      logical ldb,leof
1     continue
! --- Read the data record
      if(vers.EQ.'1.5a') then
         ibegs=0.0
         iends=0.0
         read(iunit,*,end=999)ieyr,iedy,iehr,aqconc
! ---    Shift start time to beginning of hour
         ibyr=ieyr
         ibdy=iedy
         ibhr=iehr
         ishift=-1
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
      elseif(vers.EQ.'2.1') then
         read(iunit,*,end=999)ibyr,ibdy,ibhr,ibegs,&
                              ieyr,iedy,iehr,iends,aqconc
      else
         write(io6,*)
         write(io6,*)'RDAQ: Invalid Dataset Version for ',dtype
         write(io6,*)'Expected 1.5a,2.1'
         write(io6,*)'Found: ',vers
         write(*,*)
         stop 'Halted in RDAQ -- see list file.'
      endif
! --- Enforce YYYY format for year
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDAQ'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibegs)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iends)
! --- Adjust times to BASE TIME ZONE
      ishift=NINT(tzhr)
      call INCR(io6,ibyr,ibdy,ibhr,ishift)
      call INCR(io6,ieyr,iedy,iehr,ishift)
! --- Form date-hour
      ibegh=ibyr*100000+ibdy*100+ibhr
      iendh=ieyr*100000+iedy*100+iehr
! --- Check period of AQ data with mid-point of step
      call TPERIOD(0,idathrm,isecm,ibegh,ibegs,iendh,iends,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in AQ file
         go to 1
      elseif(ilocate.EQ.-1) then
         write(io6,*)'ERROR in subr. RDAQ -- current time not found',&
         ' in the data file ',dtype
         write(io6,*)' -- Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',ibegh,ibegs
         write(io6,*)'                           to ',iendh,iends
         stop 'Halted in RDAQ -- see list file.'
      endif
      if(ldb)then
         write(io6,*)
         write(io6,*)'Data File: ',dtype
         write(io6,*)'Time-varying data for period'
         write(io6,*)' -- from = ',ibegh,ibegs
         write(io6,*)' --   to = ',iendh,iends
         write(io6,*)'AQCONC = ',aqconc
      endif
!
      return
999   write(io6,*)'RDAQ: End-Of-File found in data file'
      leof=.TRUE.
      return
      end
!----------------------------------------------------------------------
      subroutine geth2o2(ixs,iys,md,ldb,chih2o2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212           GETH2O2
!                J. Scire
!
! --- PURPOSE:  Determine the appropriate value of H2O2 concentration
!               to use in the aqueous phase chemical routine
!                -- Use monthly background value if MH2O2 = 0
!                -- Read hourly values if MH2O2 = 1
!
! --- UPDATE
! --- V5.4-V6.3     100212  (DGS): add nested CALMET grids
!
! --- INPUTS:
!              IXS - integer - X index of the closest met. grid point
!                              to the puff/slug center
!              IYS - integer - Y index of the closest met. grid point
!                              to the puff/slug center
!               MD - integer - MET grid domain index
!              LDB - logical - Control variable determining if debug
!                              information is printed
!
!     Common block /CHEMDAT/ variables:
!           MH2O2, BCKH2O2, NH2O2STA, H2O2CONC(mxaq), XH2O2M(mxaq),
!           YH2O2M(mxaq), NEARH2O2(mxnx,mxny,mxmetdom)
!     Common block /METHD/ variables:
!           xgridm(mxmetdom)
!           xorigm(mxmetdom),yorigm(mxmetdom)
!     Parameters:
!           MXNX, MXNY, MXMETDOM, MXOZ, IO6, IO23
!
! --- OUTPUT:
!          CHIH2O2 - real    - H2O2 concentration (ppb)
!
! --- GETH2O2 called by:  COMP
! --- GETH2O2 calls:      FINDR
!----------------------------------------------------------------------
!
      include 'params.puf'
!
      logical ldb
!
      include 'chemdat.puf'
      include 'methd.puf'
!
! --- Missing value indicator
      data xmiss/9999./
!
      if(mh2o2.eq.0)then
         chih2o2=bckh2o2
      elseif(mh2o2.eq.1)then
!
! ---    ISTA is the H2O2 station closest to grid point (IXS,IYS)
         istah2o2=nearh2o2(ixs,iys,md)
!
! ---    Determine H2O2 concentration (ppb) at the closest station
         chih2o2=h2o2conc(istah2o2)
         if(chih2o2.ge.xmiss)then
!
! ---       Compute grid point coordinates (m) relative to met.
! ---       grid origin of outermost domain
            xgrd=(xorigm(md)-xorigm(1)) +&
                 (float(ixs)-0.5)*xgridm(md)
            ygrd=(yorigm(md)-yorigm(1)) +&
                 (float(iys)-0.5)*xgridm(md)
!
! ---       Find closest H2O2 station with non-missing data
            call findr(xh2o2m,yh2o2m,nh2o2sta,h2o2conc,xgrd,ygrd,&
            istah2o2,chih2o2,ierr)
!
! ---       If all station data missing, use default background conc.
            if(ierr.eq.1)chih2o2=bckh2o2
         endif
      else
         write(io6,*)'ERROR in subr. GETH2O2 -- Invalid MH2O2 value',&
         ' -- MH2O2 = ',mh2o2
         write(*,*)
         stop 'Halted in GETH2O2 -- see list file.'
      endif
!
!*****
      if(ldb)then
         write(io6,*)
         write(io6,*)'SUBR. GETH2O2 -- ixs = ',ixs,' iys = ',iys,&
         ' md = ',md,' istah2o2 = ',istah2o2
         write(io6,*)'h2o2conc = ',(h2o2conc(n),n=1,nh2o2sta)
         write(io6,*)'chih2o2  = ',chih2o2
      endif
!*****
      return
      end
