!----------------------------------------------------------------------
      subroutine rdemsrc(nardat,nvoldat,nfldat,em3dat,em4dat,&
                         em5dat,em5grp,em6dat)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913           RDEMSRC
!                D. Strimaitis
!
! --- PURPOSE:  Pull source location data from the first set of 
! ---           time-variable records for area, volume, flare & line 
! ---           sources and reposition file pointers (source locations
! ---           are written to output files)
!
! --- UPDATE
! --- V6.268-TNG-7.0.0 140913    : Add Flare source
! --- V6.22-V6.268  100108  (DGS): Declare logical PROBLEM
! --- V6.1-V6.22    070921  (DGS): Change LDB to LPRT in RDEM5 call
! --- 5.725-V6.1    050915  (DGS): Use begin-time/end-time (seconds)
!                                  for variable emissions files
!                                  and allow coordinate projection
!                                  transformations
! --- V5.72-V5.725  050128  (DGS): Add logical argument PROBLEM to
!                                  LN2FILL call
!
! --- INPUTS:
!
!             NARDAT - integer  - Number of area source files
!            NVOLDAT - integer  - Number of volume source files
!             NFLDAT - integer  - Number of flare source files
!             EM3DAT - real arr - Array allocated for area source data
!             EM4DAT - real arr - Array allocated for volumesource data
!             EM5DAT - real arr - Array allocated for line source data
!             EM5GRP - real arr - Array allocated for line source data
!             EM6DAT - real arr - Array allocated for flare source data
!
!     Common block /DATASET/ variables:
!           veraarb(mxemdat),verlarb,vervarb(mxemdat),
!           verflarb(mxemdat)
!
! --- OUTPUT:
!
!     Common block /AR2/
!        XAR2GRD(mxvertp1,mxqstep,mxarea),
!        YAR2GRD(mxvertp1,mxqstep,mxarea)
!     Common block /FL2/
!        XFL2GRD(mxqstep,mxfl2),YFL2GRD(mxqstep,mxfl2),
!        ELFL2(mxqstep,mxfl2)
!     Common block /VOL2/
!        XVL2GRD(mxqstep,mxvol),YVL2GRD(mxqstep,mxvol),
!        ELVL2(mxqstep,mxvol)
!     Common block /LN2/
!        XL2BEGGRD(mxqstep,mxlines),YL2BEGGRD(mxqstep,mxlines),
!        XL2ENDGRD(mxqstep,mxlines),YL2ENDGRD(mxqstep,mxlines),
!        XL2BAR(mxqstep,mxlngrp),YL2BAR(mxqstep,mxlngrp)
!
! --- Parameters used:       
!        IO6, IOPT2, IOAR2, IOVOL, IOFL2, IO19, MXLNGRP
!
! --- RDEMSRC called by: SETUP
! --- RDEMSRC calls:     RDEM3, RDEM4, RDEM5, RDEM6, LN2FILL,
!                        SKIPHDEM3, SKIPHDEM4, SKIPHDEM5, SKIPHDEM6,
!                        GLOBE, GLOBE1
!----------------------------------------------------------------------
!
      use mspray2
! --- Include parameters
      include 'params.puf'
!
! --- Set number of variables before emissions in 'EMARB' arrays
! --- as parameters
      parameter(nvarpt=4, nvarba=14, nvarln=6, nvarvl=6, nvarfl=11)
!
! --- Include common blocks
      include 'dataset.puf'
      include 'grid.puf'
      include 'ar2.puf'
      include 'fl2.puf'
      include 'ln2.puf'
      include 'map.puf'
      include 'qa.puf'
      include 'vol2.puf'
!
      logical lprt,lnar2,lnvl2,lnfl2, problem
! --- Time-variable emission arrays (declare actual dimensions here)
      real em3dat(nse3+nvarba,nar2)
      real em4dat(nse4+nvarvl,nvl2)
      real em5dat(nse5+nvarln,nln2),em5grp(7,mxlngrp)
      real em6dat(nse6+nvarfl,nfl2)
      integer idmap(mxlines),igrp(mxlngrp)
! --- For coordinate transformations
      character*8 cmapi,cmapo
      character*12 caction
      character*4 c4hem
      real*8 vecti(9),vecto(9)
! --- Scale factor for Tangential TM projection
      tmsone=1.00000
! --- Output of coord transform is to CALPUFF (x,y) - /MAP/
      iutmo=iutmzn
      if(utmhem.EQ.'S   ' .AND. iutmo.LT.900) iutmo=-iutmo
      cmapo=pmap
      if(cmapo.EQ.'TTM     ') cmapo='TM      '
      lprt=.true.
! --- Variable area sources
! -------------------------
      if(nar2.GT.0) then
! ---    Loop over BAEMARB files for all source locations
         do ia=1,nardat
            io=ioar2+ia-1
! ---       Set up map projection conversion to CALPUFF coordinates
! ---       Input is from BAEMARB.DAT header - /ar2/
            iutmi=iutmznar2(ia)
            if(utmhemar2(ia).EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
            cmapi=pmapar2(ia)
            if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---       Set conversion vectors
            call GLOBE1(cmapi,iutmi,tmsone,rnlat1ar2(ia),rnlat2ar2(ia),&
                        rnlat0ar2(ia),relon0ar2(ia),&
                        feastar2(ia),fnorthar2(ia),&
                        cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                        feast,fnorth,&
                        caction,vecti,vecto)
            call RDEM3(io,nar2,nse3,cid3,ibsrc3(ia),iesrc3(ia),&
                       veraarb(ia),t2btz3(ia),lprt,io6,ibegh,&
                       ibegs,iendh,iends,em3dat,ieof)
! ---       Swap location data into /ar2/
            do i=ibsrc3(ia),iesrc3(ia)
! ---          Compute & store relative vertex coordinates;
! ---          accumulate sums for computing the mean location of
! ---          the area source
               sumx=0.
               sumy=0.
! ---          Explicitly allow ONLY 4-sided shapes!
               nvert2(i)=4
               do iv=1,nvert2(i)
                  xvert3=em3dat(iv,i)
                  yvert3=em3dat(iv+nvert2(i),i)
! ---             Apply coordinate transformation
                  call GLOBE(io6,caction,datumar2(ia),vecti,datum,&
                             vecto,xvert3,yvert3,xvert,yvert,&
                             izone,c4hem)
                  xar2grd(iv,1,i)=(1000.*xvert-xorig)*dgridi
                  yar2grd(iv,1,i)=(1000.*yvert-yorig)*dgridi
                  sumx=sumx+xar2grd(iv,1,i)
                  sumy=sumy+yar2grd(iv,1,i)
               enddo
! ---          Mean location of source
               xar2grd(nvert2(i)+1,1,i)=sumx/FLOAT(nvert2(i))
               yar2grd(nvert2(i)+1,1,i)=sumy/FLOAT(nvert2(i))
            enddo
            REWIND(io)
         enddo
! ---    Reposition pointer to start of data records
         call SKIPHDEM3(nardat)
      endif
! --- Variable volume sources
! ----------------------------
      if(nvl2.GT.0) then
! ---    Loop over VOLEMARB files for all source locations
         do iv=1,nvoldat
            io=iovol+iv-1
! ---       Set up map projection conversion to CALPUFF coordinates
! ---       Input is from VOLEMARB.DAT header - /vol2/
            iutmi=iutmznvl2(iv)
            if(utmhemvl2(iv).EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
            cmapi=pmapvl2(iv)
            if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---       Set conversion vectors
            call GLOBE1(cmapi,iutmi,tmsone,rnlat1vl2(iv),rnlat2vl2(iv),&
                        rnlat0vl2(iv),relon0vl2(iv),&
                        feastvl2(iv),fnorthvl2(iv),&
                        cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                        feast,fnorth,&
                        caction,vecti,vecto)
            call RDEM4(io,nvl2,nse4,cid4,ibsrc4(iv),iesrc4(iv),&
                       vervarb(iv),t2btz4(iv),lprt,io6,ibegh,&
                       ibegs,iendh,iends,em4dat,ieof)
! ---       Swap location data (em4dat) into /vol2/ arrays
            do i=ibsrc4(iv),iesrc4(iv)
! ---          Apply coordinate transformation
               call GLOBE(io6,caction,datumvl2(iv),vecti,datum,&
                          vecto,em4dat(1,i),em4dat(2,i),xvol,yvol,&
                          izone,c4hem)
! ---          Compute relative grid coordinates
               xvl2grd(1,i)=(1000.*xvol-xorig)*dgridi
               yvl2grd(1,i)=(1000.*yvol-yorig)*dgridi
               elvl2(1,i)=em4dat(4,i)
            enddo
            REWIND(io)
         enddo
! ---    Reposition pointer to start of data records
         call SKIPHDEM4(nvoldat)
      endif
! --- Variable buoyant line sources
! ----------------------------------
      if(nln2.gt.0) then
         iq=1
! ---    Set up map projection conversion to CALPUFF coordinates
! ---    Input is from LNEMARB.DAT header - /LN2/
         iutmi=iutmznln2
         if(utmhemln2.EQ.'S   ' .AND. iutmi.LT.900) iutmi=-iutmi
         cmapi=pmapln2
         if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---    Set conversion vectors
         call GLOBE1(cmapi,iutmi,tmsone,rnlat1ln2,rnlat2ln2,&
                rnlat0ln2,relon0ln2,&
                feastln2,fnorthln2,&
                cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                feast,fnorth,&
                caction,vecti,vecto)
! ---    Read data from LNEMARB file for source locations
         call RDEM5(io19,mxlngrp,nln2,nse5,cid5,verlarb,t2btz5,&
                    lprt,io6,ibegh,ibegs,iendh,iends,lngrp,idmap,&
                    em5dat,em5grp,ieof)
!---     Apply projection/datum translation to positions
         iline=0
         do ig=1,lngrp
            do il=1,em5grp(1,ig)
               iline=iline+1
               x1=em5dat(1,iline)
               y1=em5dat(2,iline)
               x2=em5dat(3,iline)
               y2=em5dat(4,iline)
               call GLOBE(io6,caction,datumln2,vecti,datum,&
                    vecto,x1,y1,em5dat(1,iline),em5dat(2,iline),&
                    izone,c4hem)
               call GLOBE(io6,caction,datumln2,vecti,datum,&
                    vecto,x2,y2,em5dat(3,iline),em5dat(4,iline),&
                    izone,c4hem)
            enddo
         enddo
! ---    Swap location data (em5grp,em5dat) into /ln2/
         call LN2FILL(iq,lngrp,nln2,idmap,em5grp,em5dat,nvarln,&
                         problem)
         REWIND(io19)
! ---    Reposition pointer to start of data records
         call SKIPHDEM5
      endif
! --- Variable flare sources
! ----------------------------
      if(nfl2.GT.0) then
! ---    Loop over FLEMARB files for all source locations
         do ifl=1,nfldat
            io=iofl2+ifl-1
! ---       Set up map projection conversion to CALPUFF coordinates
! ---       Input is from FLEMARB.DAT header - /fl2/
            iutmi=iutmznfl2(ifl)
            if(utmhemfl2(ifl).EQ.'S   '.AND.iutmi.LT.900) iutmi=-iutmi
            cmapi=pmapfl2(ifl)
            if(cmapi.EQ.'TTM     ') cmapi='TM      '
! ---       Set conversion vectors
            call GLOBE1(cmapi,iutmi,tmsone,rnlat1fl2(ifl),&
                        rnlat2fl2(ifl),&
                        rnlat0fl2(ifl),relon0fl2(ifl),&
                        feastfl2(ifl),fnorthfl2(ifl),&
                        cmapo,iutmo,tmsone,xlat1,xlat2,rnlat0,relon0,&
                        feast,fnorth,&
                        caction,vecti,vecto)
            call RDEM6(io,nfl2,nse6,cid6,ibsrc6(ifl),iesrc6(ifl),&
                       verflarb(ifl),t2btz6(ifl),lprt,io6,ibegh,&
                       ibegs,iendh,iends,em6dat,ieof)
! ---       Swap location data (em6dat) into /fl2/ arrays
            do i=ibsrc6(ifl),iesrc6(ifl)
! ---          Apply coordinate transformation
               call GLOBE(io6,caction,datumfl2(ifl),vecti,datum,&
                          vecto,em6dat(1,i),em6dat(2,i),xfl,yfl,&
                          izone,c4hem)
! ---          Compute relative grid coordinates
               xfl2grd(1,i)=(1000.*xfl-xorig)*dgridi
               yfl2grd(1,i)=(1000.*yfl-yorig)*dgridi
               elfl2(1,i)=em6dat(4,i)
            enddo
            REWIND(io)
         enddo
! ---    Reposition pointer to start of data records
         call SKIPHDEM6(nfldat)
      endif
      return
      end
