      character*12 cslst(mxspec)
2     format(a1)
! --- Loop over all BAEMARB.DAT files
      do i=1,nardat
         io=ioar2+i-1
         if(veraarb(i).EQ.'1.5a' .OR.&
            veraarb(i).EQ.'1.54a' .OR.&
            veraarb(i).EQ.'1.6a') then
! ---       Augmented headers
            read(io,2) char1
            read(io,2) char1
            read(io,2) char1
            if(LLCCAR2(i)) read(io,2) char1
            read(io,2) char1
            read(io,2) char1
! ---       Old Header Record(s)
            read(io,2) char1
            if(veraarb(i).EQ.'1.54a') read(io,2) char1
         elseif(veraarb(i).EQ.'2.1') then
            read(io,2) char1
            read(io,*) ncom
            do k=1,ncom
               read(io,2) char1
            enddo
            read(io,2) char1
            read(io,2) char1
            if(LLCCAR2(i).or.LLAZAAR2(i).or.&
                             LTTMAR2(i)) read(io,2) char1
            read(io,2) char1
