            read(io,2) char1
            read(io,2) char1
            read(io,2) char1
         else
            write(*,*)'ERROR in BAEMARB File: ',i
            write(*,*)'Bad Dataset Version: ',veraarb(i)
            write(*,*)'Expected 1.5a, 1.54a, 1.6a, 2.1'
            stop
         endif
! ---    Species list
         read(io,*)(cslst(n),n=1,nse3)
! ---    Molecular weights of each emitted species
         read(io,*)(xmwem(n),n=1,nse3)
! ---    TIEM3 records
! ---    Loop over sources
         do k=ibsrc3(i),iesrc3(i)
            read(io,2) char1
         enddo
      enddo
! --- Use char1 (compilation warning)
      if(char1.EQ.'~') return
      return
      end
