         else
            write(*,*)'ERROR in LNEMARB File: '
            write(*,*)'Bad Dataset Version: ',verlarb
            write(*,*)'Expected 1.54a, 2.1'
            stop
         endif
! ---    Species list
         read(io,*)(cslst(n),n=1,nse5)
! ---    Molecular weights of each emitted species
         read(io,*)(xmwem(n),n=1,nse5)
! ---    TIEM5 records
         read(io,2) char1
! ---    Loop over sources names
         read(io,*) (cid(k),k=1,nln2)
! --- Use char1 (compilation warning)
      if(char1.EQ.'~') return
      return
      end
!----------------------------------------------------------------------
      subroutine qaplot1
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           QAPLOT1
!                D. Strimaitis
!
! --- PURPOSE:  Pass geographic setup information to files for making
!               plots to verify configuration.  Standard filenames
!               are used for each type of data, and extensions are
!               consistent with defaults used in the SURFER plotting
!               system.  All locations are in km.
!
! --- UPDATE
! --- TNG-7.0.0 - TNG-7.0.0  141230  (DGS)
!                               : Add RDEMARB.DAT Road sources
! --- V6.3 - TNG-7.0.0  140913  (DGS)
!                               : Add FLARE sources
!                               : Add ROAD sources
! --- V6.102-V6.3 100212   (DGS): Add nested CALMET grids
!                 100212   (DGS): Change elev(i,j) to elevg(i,j) for
!                                 gridded receptor elevations
! --- V6.1-V6.102 051130   (DGS): Add OZONE and H2O2 stations
! --- V5.725-V6.1 050915   (DGS): Expand arg list of variable emissions
!                                 source locations to include the 
!                                 emissions timestep
! --- V5.724-V5.725 050128 (DGS): Modify wetland color and add a RED
!                                 strip at the top of the bar in the
!                                 LUSE.CLR file.
! --- V5.723-V5.724 041013 (DGS): Use SURFER 8 image format for
!                                 QALUSE.GRD file.  Add a control for
!                                 this format, but set it locally here.
! --- V5.72-V5.723 040827  (DGS): Enlarge format for QATERR.DAT hts
! --- V5.7-V5.72  031017   (DGS): Fix gridded receptor file QARECG.DAT
!                                 [used discrete rec. elevations]
!                 031017   (DGS): Add variable area sources
! --- V5.5-V5.7   030402   (DGS): Change header format to avoid SURFER7
!                                 bug (use space-delimited form for
!                                 header of POST data files)
!
! --- INPUTS:
!
!     Common block /AR1/ variables:
!        NAR1, NVERT1(mxarea),
!        XAR1GRD(mxvertp1,mxarea),YAR1GRD(mxvertp1,mxarea),
!     Common block /AR2/ variables:
!        NAR2, NVERT2(mxarea),
!        XAR2GRD(mxvertp1,mxqstep,mxarea),
!        YAR2GRD(mxvertp1,mxqstep,mxarea),
!     Common block /CHEMDAT/ variables:
!        NOZSTA, XOZM(mxoz), YOZM(mxoz),
!        NH2O2STA, XH2O2M(mxaq), YH2O2M(mxaq),
!     Common block /CTSGDAT/ variables:
!        NCTREC, XRCT(mxrect), YRCT(mxrect), ELRECT(mxrect)
!     Common block /FL2/ variables:
!        NFL2, ELFL2(mxqstep,mxfl2),
!        XFL2GRD(mxqstep,mxfl2), YFL2GRD(mxqstep,mxfl2)
!     Common block /GEN/ variables:
!        METFM
!     Common block /GRID/ variables:
!        nx,ny,dgrid,xorig,yorig,
!        ibcomp,jbcomp,nxcmp,nycmp,
!        lsamp,ibsamp,jbsamp,nxsam,nysam,delsam
!     Common block /GRIDNEST/ variables:
!        ngrid
!     Common block /LN1/ variables:
!        NLINES, XLBEGGRD(mxlines), YLBEGGRD(mxlines),
!        XLENDGRD(mxlines), YLENDGRD(mxlines)
!     Common block /LN2/ variables:
!        NLN2, XL2BEGGRD(mxqstep,mxlines), YL2BEGGRD(mxqstep,mxlines),
!        XL2ENDGRD(mxqstep,mxlines), YL2ENDGRD(mxqstep,mxlines)
!     Common block /METHD/ variables:
!        XORIGM(mxmetdom),XORIGM(mxmetdom),XGRIDM(mxmetdom)
!        ELEV(mxnx,mxny,mxmetdom),ILANDU(mxnx,mxny,mxmetdom)
!     Common block /NONGRD/ variables:
!        NREC, XNG(mxrec), YNG(mxrec), ZNG(mxrec), ELEVNG(mxrec)
!     Common block /PT1/ variables:
!        NPT1, XPT1GRD(mxpt1), YPT1GRD(mxpt1), ELSTAK(mxpt1)
!     Common block /PT2/ variables:
!        NPT2, TIEM2(8,mxpt2)
!     Common block /VOL1/ variables:
!        NVL1, ELVL1(mxvol), XVL1GRD(mxvol), YVL1GRD(mxvol)
!     Common block /VOL2/ variables:
!        NVL2, ELVL2(mxqstep,mxvol),
!        XVL2GRD(mxqstep,mxvol), YVL2GRD(mxqstep,mxvol)
!     |mroad1| variables:
!        nrdseg1,xrd1grd(2,nrdseg1),
!        yrd1grd(2,nrdseg1),elrd1(2,nrdseg1)
!     |mroad2| variables:
!        nrdseg2,xrd2grd(2,nrdseg2),
!        yrd2grd(2,nrdseg2),elrd2(2,nrdseg2)
!     |mspray2| variables:
!        nspseg2,xsp2grd(2,nspseg2),
!        ysp2grd(2,nspseg2),elsp2(2,nspseg2)
!      
!     
!     Parameters:
!           MXNX, MXNY, MXMETDOM, MXPT1, MXPT2, MXAREA, MXVERTP1,
!           MXLINES, MXVOL, MXFL2, MXREC, MXQSTEP, MXAQ
!
! --- OUTPUT:
!
!     Files:
!        QATERR.GRD - gridded terrain elevations (mMSL) [CALMET only]
!       QATERRn.GRD - gridded terrain elevations (mMSL) [CALMET only]
!                     for CALMET nested domains, n=2,3,4...
!        QALUSE.GRD - gridded land use                  [CALMET only]
!       QALUSEn.GRD - gridded land use                  [CALMET only]
!                     for CALMET nested domains, n=2,3,4...
!        QAGRID.BNA - borders for the METEOROLOGICAL, COMPUTATIONAL,
!                     and SAMPLING grid domains
!        QARECD.DAT - location of discrete receptors
!        QARECG.DAT - location of gridded receptors
!        QARECT.DAT - location of CTSG receptors
!        QAPNTS.DAT - location of point sources
!        QAFLAR.DAT - location of flare sources
!        QAVOLS.DAT - location of volume sources
!        QAAREA.BNA - outlines for area sources
!        QALINE.BNA - segments for buoyant line sources
!        QAROAD.BNA - location of road-source segments
!        QA_OZN.DAT - location of ozone stations
!        QAH2O2.DAT - location of H2O2 stations
!
!
! --- QAPLOT1 called by:  SETUP
! --- QAPLOT1 calls:      none
!----------------------------------------------------------------------
! --- Modules
      use mroad1
      use mroad2
      use mspray2
! --- Set the io unit number for QA files (should eventually be in
! --- the params file)
      parameter (ioqa=50)
! --- Include parameter statements
      include 'params.puf'
! --- Include common blocks
      include 'ar1.puf'
      include 'ar2.puf'
      include 'chemdat.puf'
      include 'ctsgdat.puf'
      include 'fl2.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'gridnest.puf'
      include 'ln1.puf'
      include 'ln2.puf'
      include 'methd.puf'
      include 'nongrd.puf'
      include 'pt1.puf'
      include 'pt2.puf'
      include 'vol1.puf'
      include 'vol2.puf'
      real dgridkm(mxmetdom),xmetLL(mxmetdom),xmetUR(mxmetdom)
      real ymetLL(mxmetdom),ymetUR(mxmetdom)
      real xmet1(mxmetdom),ymet1(mxmetdom)
      real xmetnx(mxmetdom),ymetny(mxmetdom)
      character*40 fname,blank40
      data blank40/'                                        '/
! --- Conversion factors
      data fm2km/0.001/
! --- Image GRD file format
! ---   image=0:  SURFER 8 format with range defined by cell-centers
! ---   image=1:  SURFER 7 format with range defined by domain edges
      data image/0/
! --- Report position of variable sources from first emissions step
! --- if sources may move
      istep=1
! --- Met grid corners and corner-cell-centers (km)
! --- Loop over MET grid domains
      do md=1,ngrid
         dgridkm(md)= xgridm(md)*fm2km
         xmetLL(md) = xorigm(md)*fm2km
         xmetUR(md) = xmetLL(md) +FLOAT(nxm(md))*dgridkm(md)
         xmet1(md)  = xmetLL(md) +0.5*dgridkm(md)
         xmetnx(md) = xmet1(md)  +FLOAT(nxm(md)-1)*dgridkm(md)
         ymetLL(md) = yorigm(md)*fm2km
         ymetUR(md) = ymetLL(md) +FLOAT(nym(md))*dgridkm(md)
         ymet1(md)  = ymetLL(md) +0.5*dgridkm(md)
         ymetny(md) = ymet1(md)  +FLOAT(nym(md)-1)*dgridkm(md)
      enddo
! --- Computational grid corners (km)
      xcmpLL=xmetLL(1)+float(ibcomp-1)*dgridkm(1)
      xcmpUR=xcmpLL+FLOAT(nxcmp)*dgridkm(1)
      ycmpLL=ymetLL(1)+float(jbcomp-1)*dgridkm(1)
      ycmpUR=ycmpLL+FLOAT(nycmp)*dgridkm(1)
! --- Sampling grid corners (km)
      xsamLL=xmetLL(1)+(float(ibsamp)-0.5)*dgridkm(1)
      xsamUR=xsamLL+FLOAT(nxsam-1)*delsam*fm2km
      ysamLL=ymetLL(1)+(float(jbsamp)-0.5)*dgridkm(1)
      ysamUR=ysamLL+FLOAT(nysam-1)*delsam*fm2km
! --- Process boundary of grids [BNA format]
! ------------------------------------------
      open(ioqa,file='qagrid.bna',status='unknown')
! --- Met grids
      do md=1,ngrid
         write(ioqa,'(a15)') '" Met","grid",5'
         write(ioqa,'(f12.4,a1,f12.4)') xmetLL(md),',',ymetLL(md)
         write(ioqa,'(f12.4,a1,f12.4)') xmetLL(md),',',ymetUR(md)
         write(ioqa,'(f12.4,a1,f12.4)') xmetUR(md),',',ymetUR(md)
         write(ioqa,'(f12.4,a1,f12.4)') xmetUR(md),',',ymetLL(md)
         write(ioqa,'(f12.4,a1,f12.4)') xmetLL(md),',',ymetLL(md)
      enddo
! --- Comp grid
      write(ioqa,'(a15)') '"Comp","grid",5'
      write(ioqa,'(f12.4,a1,f12.4)') xcmpLL,',',ycmpLL
      write(ioqa,'(f12.4,a1,f12.4)') xcmpLL,',',ycmpUR
      write(ioqa,'(f12.4,a1,f12.4)') xcmpUR,',',ycmpUR
      write(ioqa,'(f12.4,a1,f12.4)') xcmpUR,',',ycmpLL
      write(ioqa,'(f12.4,a1,f12.4)') xcmpLL,',',ycmpLL
! --- Samp grid
      if(LSAMP) then
         write(ioqa,'(a15)') '"Samp","grid",5'
         write(ioqa,'(f12.4,a1,f12.4)') xsamLL,',',ysamLL
         write(ioqa,'(f12.4,a1,f12.4)') xsamLL,',',ysamUR
         write(ioqa,'(f12.4,a1,f12.4)') xsamUR,',',ysamUR
         write(ioqa,'(f12.4,a1,f12.4)') xsamUR,',',ysamLL
         write(ioqa,'(f12.4,a1,f12.4)') xsamLL,',',ysamLL
      endif
      close(ioqa)
! --- Process terrain elevations [GRD format]
! -------------------------------------------
! --- Loop over MET grid domains
      do md=1,ngrid
         fname=blank40
         if(md.EQ.1) then
            open(ioqa,file='qaterr.grd',status='unknown')
         else
            fname(1:13)='qaterr_00.grd'
            write(fname(8:9),'(i2.2)') md
            open(ioqa,file=fname,status='unknown')
         endif
         if(metfm.EQ.1) then
! ---       Obtain min/max elevations
            emin=elev(1,1,md)
            emax=elev(1,1,md)
            do j=1,nym(md)
               do i=1,nxm(md)
                  if(elev(i,j,md).GT.emax) emax=elev(i,j,md)
                  if(elev(i,j,md).LT.emin) emin=elev(i,j,md)
               enddo
            enddo
            if(emax.EQ.emin) then
               close(ioqa, status='DELETE')
            else
! ---          Header records
! ---          Place data points at cell centers for contouring
               write(ioqa,'(a4)') 'DSAA'
               write(ioqa,'(2i12)') nxm(md),nym(md)
               write(ioqa,'(2f12.4)') xmet1(md),xmetnx(md)
               write(ioqa,'(2f12.4)') ymet1(md),ymetny(md)
               write(ioqa,'(2e12.4)') emin,emax
! ---          Data, in rows of constant Y
               do j=1,nym(md)
                 write(ioqa,'(10000(1pe11.4,1x))')&
                       (elev(i,j,md),i=1,nxm(md))
               enddo
               close(ioqa)
            endif
         else
            close(ioqa, status='DELETE')
         endif
      enddo
! --- Process landuse [GRD format]
! --------------------------------
! --- Loop over MET grid domains
      do md=1,ngrid
         fname=blank40
         if(md.EQ.1) then
            open(ioqa,file='qaluse.grd',status='unknown')
         else
            fname(1:13)='qaluse_00.grd'
            write(fname(8:9),'(i2.2)') md
            open(ioqa,file=fname,status='unknown')
         endif
         if(metfm.EQ.1) then
! ---       Set min/max landuse to 10 - 100 range
            lmin=10
            lmax=100
! ---       Header records
! ---       Identify data with entire cell for image-plot
            write(ioqa,'(a4)') 'DSAA'
            write(ioqa,'(2i12)') nxm(md),nym(md)
            if(image.EQ.0) then
               write(ioqa,'(2f12.4)') xmet1(md),xmetnx(md)
               write(ioqa,'(2f12.4)') ymet1(md),ymetny(md)
            else
               write(ioqa,'(2f12.4)') xmetLL(md),xmetUR(md)
               write(ioqa,'(2f12.4)') ymetLL(md),ymetUR(md)
            endif
            write(ioqa,'(2i12)') lmin,lmax
! ---       Data, in rows of constant Y
            do j=1,nym(md)
              write(ioqa,'(10000(i3,2x))')(IABS(ilandu(i,j,md)),&
                                                i=1,nxm(md))
            enddo
            close(ioqa)
         else
            close(ioqa, status='DELETE')
         endif
      enddo
! --- Set up default land use color file in this directory
      open(ioqa,file='luse.clr',status='unknown')
      write(ioqa,'(a)') 'ColorMap 1 1'
      write(ioqa,'(a)') '    0.000000 255 255   0'
      write(ioqa,'(a)') '   11.000000 255 255   0'
      write(ioqa,'(a)') '   11.000000 204 255 102'
      write(ioqa,'(a)') '   22.100000 204 255 102'
      write(ioqa,'(a)') '   22.100000 160 255 160'
      write(ioqa,'(a)') '   33.100000 160 255 160'
      write(ioqa,'(a)') '   33.100000  80 255  80'
      write(ioqa,'(a)') '   44.200000  80 255  80'
      write(ioqa,'(a)') '   44.200000 153 255 255'
      write(ioqa,'(a)') '   50.000000 153 255 255'
      write(ioqa,'(a)') '   50.000000  60 204 255'
      write(ioqa,'(a)') '   55.400000  60 204 255'
      write(ioqa,'(a)') '   55.400000 204 153 204'
      write(ioqa,'(a)') '   66.500000 204 153 204'
      write(ioqa,'(a)') '   66.500000 255 204 153'
      write(ioqa,'(a)') '   77.600000 255 204 153'
      write(ioqa,'(a)') '   77.600000 255 255 204'
      write(ioqa,'(a)') '   88.700000 255 255 204'
      write(ioqa,'(a)') '   88.700000 255 255 255'
      write(ioqa,'(a)') '   99.030000 255 255 255'
      write(ioqa,'(a)') '   99.030000 255   0   0'
      write(ioqa,'(a)') '  100.000000 255   0   0'
      close(ioqa)
! --- Process point sources [DAT format]
! --------------------------------------
      open(ioqa,file='qapnts.dat',status='unknown')
      write(ioqa,'(a27)') '"Xkm" "Ykm" "Elev" "Source"'
      do i=1,npt1
         is=i
         x=xmetLL(1)+xpt1grd(i)*dgridkm(1)
         y=ymetLL(1)+ypt1grd(i)*dgridkm(1)
         write(ioqa,*) x,y,elstak(i),is
      enddo
      do i=1,npt2
         is=npt1+i
         x=xmetLL(1)+tiem2(1,i)*dgridkm(1)
         y=ymetLL(1)+tiem2(2,i)*dgridkm(1)
         write(ioqa,*) x,y,tiem2(5,i),is
      enddo
      npt=npt1+npt2
      if(npt.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process flare sources [DAT format]
! --------------------------------------
      open(ioqa,file='qaflar.dat',status='unknown')
      write(ioqa,'(a27)') '"Xkm" "Ykm" "Elev" "Source"'
      do i=1,nfl2
         x=xmetLL(1)+xfl2grd(istep,i)*dgridkm(1)
         y=ymetLL(1)+yfl2grd(istep,i)*dgridkm(1)
         write(ioqa,*) x,y,elfl2(istep,i),i
      enddo
      if(nfl2.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process volume sources [DAT format]
! ---------------------------------------
      open(ioqa,file='qavols.dat',status='unknown')
      write(ioqa,'(a27)') '"Xkm" "Ykm" "Elev" "Source"'
      do i=1,nvl1
         is=i
         x=xmetLL(1)+xvl1grd(i)*dgridkm(1)
         y=ymetLL(1)+yvl1grd(i)*dgridkm(1)
         write(ioqa,*) x,y,elvl1(i),is
      enddo
      do i=1,nvl2
         is=nvl1+i
         x=xmetLL(1)+xvl2grd(istep,i)*dgridkm(1)
         y=ymetLL(1)+yvl2grd(istep,i)*dgridkm(1)
         write(ioqa,*) x,y,elvl2(istep,i),is
      enddo
      nvl=nvl1+nvl2
      if(nvl.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process area sources [BNA format]
! -------------------------------------
      open(ioqa,file='qaarea.bna',status='unknown')
      do i=1,nar1
         if(nvert1(i).EQ.3) then
            write(ioqa,'(a15)') '"Area"," AR1",4'
         elseif(nvert1(i).EQ.4) then
            write(ioqa,'(a15)') '"Area"," AR1",5'
         else
            stop 'QAPLOT1:  Areas must have 3 or 4 corners'
         endif
         do j=1,nvert1(i)
            x=xmetLL(1)+xar1grd(j,i)*dgridkm(1)
            y=ymetLL(1)+yar1grd(j,i)*dgridkm(1)
            write(ioqa,'(f12.4,a1,f12.4)') x,',',y
         enddo
! ---    Repeat the first point to close the figure
         x=xmetLL(1)+xar1grd(1,i)*dgridkm(1)
         y=ymetLL(1)+yar1grd(1,i)*dgridkm(1)
         write(ioqa,'(f12.4,a1,f12.4)') x,',',y
      enddo
      do i=1,nar2
         if(nvert2(i).EQ.3) then
            write(ioqa,'(a15)') '"Area"," AR2",4'
         elseif(nvert2(i).EQ.4) then
            write(ioqa,'(a15)') '"Area"," AR2",5'
         else
            stop 'QAPLOT1:  Areas must have 3 or 4 corners'
         endif
         do j=1,nvert2(i)
            x=xmetLL(1)+xar2grd(j,istep,i)*dgridkm(1)
            y=ymetLL(1)+yar2grd(j,istep,i)*dgridkm(1)
            write(ioqa,'(f12.4,a1,f12.4)') x,',',y
         enddo
! ---    Repeat the first point to close the figure
         x=xmetLL(1)+xar2grd(1,istep,i)*dgridkm(1)
         y=ymetLL(1)+yar2grd(1,istep,i)*dgridkm(1)
         write(ioqa,'(f12.4,a1,f12.4)') x,',',y
      enddo
      nar=nar1+nar2
      if(nar.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process buoyant line sources [BNA format]
! ---------------------------------------------
      open(ioqa,file='qaline.bna',status='unknown')
      do i=1,nlines
         write(ioqa,'(a16)') '"Line"," LN1",-2'
            x=xmetLL(1)+xlbeggrd(i)*dgridkm(1)
            y=ymetLL(1)+ylbeggrd(i)*dgridkm(1)
            write(ioqa,'(f12.4,a1,f12.4)') x,',',y
            x=xmetLL(1)+xlendgrd(i)*dgridkm(1)
            y=ymetLL(1)+ylendgrd(i)*dgridkm(1)
            write(ioqa,'(f12.4,a1,f12.4)') x,',',y
      enddo
      do i=1,nln2
         write(ioqa,'(a16)') '"Line"," LN2",-2'
            x=xmetLL(1)+xl2beggrd(istep,i)*dgridkm(1)
            y=ymetLL(1)+yl2beggrd(istep,i)*dgridkm(1)
            write(ioqa,'(f12.4,a1,f12.4)') x,',',y
            x=xmetLL(1)+xl2endgrd(istep,i)*dgridkm(1)
            y=ymetLL(1)+yl2endgrd(istep,i)*dgridkm(1)
            write(ioqa,'(f12.4,a1,f12.4)') x,',',y
      enddo
      nln=nlines+nln2
      if(nln.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process road sources [BNA format]
! ---------------------------------------------
      open(ioqa,file='qaroad.bna',status='unknown')
      do i=1,nrdseg1
         write(ioqa,'(a16)') '"Road"," RD1",-2'
            do j=1,2
              x=xmetLL(1)+xrd1grd(j,i)*dgridkm(1)
              y=ymetLL(1)+yrd1grd(j,i)*dgridkm(1)
              write(ioqa,'(f12.4,a1,f12.4)') x,',',y
         enddo
      enddo
      do i=1,nrdseg2
         write(ioqa,'(a16)') '"Road"," RD2",-2'
            do j=1,2
              x=xmetLL(1)+xrd2grd(j,i)*dgridkm(1)
              y=ymetLL(1)+yrd2grd(j,i)*dgridkm(1)
              write(ioqa,'(f12.4,a1,f12.4)') x,',',y
         enddo
      enddo
      if(nrdseg1.GT.0 .OR. nrdseg2.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process discrete receptors [DAT format]
! -------------------------------------------
      open(ioqa,file='qarecd.dat',status='unknown')
      write(ioqa,'(a25)') '"Xkm" "Ykm" "Elev" "Zagl"'
      do i=1,nrec
         x=xmetLL(1)+xng(i)*dgridkm(1)
         y=ymetLL(1)+yng(i)*dgridkm(1)
         write(ioqa,*) x,y,elevng(i),zng(i)
      enddo
      if(nrec.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process gridded receptors [DAT format]
! ------------------------------------------
      open(ioqa,file='qarecg.dat',status='unknown')
      if(LSAMP) then
         write(ioqa,'(a18)') '"Xkm" "Ykm" "Elev"'
         do j=1,nysam
            do i=1,nxsam
               x=xsamLL+float(i-1)*delsam*fm2km
               y=ysamLL+float(j-1)*delsam*fm2km
               write(ioqa,*) x,y,elevg(i,j)
            enddo
         enddo
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process CTSG receptors [DAT format]
! ---------------------------------------
      open(ioqa,file='qarect.dat',status='unknown')
      write(ioqa,'(a18)') '"Xkm" "Ykm" "Elev"'
      do i=1,nctrec
         x=xmetLL(1)+xrct(i)*dgridkm(1)
         y=ymetLL(1)+yrct(i)*dgridkm(1)
         write(ioqa,*) x,y,elrect(i)
      enddo
      if(nctrec.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process OZONE stations [DAT format]
! ---------------------------------------
      open(ioqa,file='qa_ozn.dat',status='unknown')
      write(ioqa,'(a25)') '"Xkm" "Ykm" "Station"'
      do i=1,nozsta
         x=xmetLL(1)+xozm(i)*0.001
         y=ymetLL(1)+yozm(i)*0.001
         write(ioqa,*) x,y,i
      enddo
      if(nozsta.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
! --- Process H2O2 stations [DAT format]
! --------------------------------------
      open(ioqa,file='qah2o2.dat',status='unknown')
      write(ioqa,'(a25)') '"Xkm" "Ykm" "Station"'
      do i=1,nh2o2sta
         x=xmetLL(1)+xh2o2m(i)*0.001
         y=ymetLL(1)+yh2o2m(i)*0.001
         write(ioqa,*) x,y,i
      enddo
      if(nh2o2sta.GT.0) then
         close(ioqa)
      else
         close(ioqa, status='DELETE')
      endif
      return
      end
!----------------------------------------------------------------------
      logical function lrsame(r0,r1,r2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402            LRSAME
!                D. Strimaitis,    Earth Tech, Inc.
! ---            From CALPOST V5.2, L991104c
!
! --- PURPOSE:  Compare 2 real numbers (r1,r2) to determine if their
!               fractional difference exceeds r0
!
! --- INPUTS:
!            r0 - real       - Fractional difference allowed
!            r1 - real       - Real value 1
!            r2 - real       - Real value 2
!
!
! --- OUTPUT:
!        lrsame - logical    - Key indicating result of test
!                              .TRUE.  -- values are 'same'
!                              .FALSE. -- values are NOT 'same'
!
!
! --- LRSAME called by:   (utility)
! --- LRSAME calls:       none
!----------------------------------------------------------------------
!
      data half/0.5/
      lrsame=.TRUE.
! --- Direct comparison
      if(r1.EQ.r2) return
      rdif=ABS(r1-r2)
      ravg=half*ABS(r1+r2)
      if(rdif.GE.ravg) then
! ---    Fractional difference greater than one!
         lrsame=.FALSE.
      else
         ftest=rdif/ravg
         if(ftest.GT.r0) lrsame=.FALSE.
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine tfercf
!----------------------------------------------------------------------
!
! --- CALPUFF  Version: 7.3.1        Level: 090612              TFERCF
! ---          D. Strimaitis
!
! --- PURPOSE: Transfer image of control file to scratch file
!
! --- UPDATES:
! --- V5.7 (030402) to V6.265 (090612) (DGS)
!     - Reset length of line in control file to 200
!       (store image as 132, splitting lines if needed)
!
! --- INPUTS:
!
!        Common block /QA/
!           ncommout
!        Parameters: mxcol, iox, io5
!
! --- OUTPUT:
!
!        Common block /QA/
!           ncommout
!
! --- TFERCF called by:  SETUP
! --- TFERCF calls:      none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'params.cal'
      include 'qa.puf'
      character*132 aline, blank
      character*200 aline200, blank200
      character*33 blank33
! --- Set blanks
      data blank33/'                                 '/
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
      blank200(1:100)=blank(1:100)
      blank200(101:200)=blank(1:100)
! --- Confirm max size of control file record
      if(mxcol.GT.200) then
         write(*,*)'ERROR in TFERCF!'
         write(*,*)'Control file records are assumed to be <= 200'
         write(*,*)'Actual limit is MXCOL = ',mxcol
         write(*,*)'CALPUFF code needs to be modified'
         stop
      endif      
! --- Reposition input control file
      REWIND(io5)
! --- Open scratch file
      OPEN(iox,status='scratch')
! --- Initialize number of records written to scratch file
      ncommout=0
! --- Transfer control file records
10    aline=blank
      aline200=blank200
      read(io5,'(a200)',end=999) aline200
      write(iox,'(a132)') aline200(1:132)
      ncommout=ncommout+1
      klen=LEN_TRIM(aline200)
      if(klen.GT.132) then
! ---    Line is split
         aline(1:68)=aline200(133:200)
         write(iox,'(a132)') aline
         ncommout=ncommout+1
      endif
      goto 10
! --- Transfer completed
999   close(io5)
      return
      end
!----------------------------------------------------------------------
      subroutine met1doc
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           MET1DOC
!                D. Strimaitis
!
! --- PURPOSE:  Read header comment records of the CALMET meteorological
!               data files, and place in scratch file.  Replicate full
!               image from file #1, but just the title lines from any
!               subsequent files.
!
! --- UPDATE
! --- V6.301 - TNG-7.1.0  141230  (DGS)
!                                : Trap file-open error
! --- V6.3-V6.301   100827  (DGS): NMETDAT changed to array
! --- V6.1-V6.3     100212  (DGS): Add nested grid CALMET files
! --- V5.7-V6.1     050915  (DGS): Add FILVERS call for dataset version
!
! --- INPUTS:
!     Common block /FILNAM/ variables:
!           NMETDAT(mxmetdom), METDATL(mxmetdat,mxmetdom), NMETDOM
!     Common block /QA/ variables:
!           NCOMMOUT
!
!     Parameters:
!           MXMETDAT, MXMETDOM, IO7, IOX
!
! --- OUTPUT:
!     Common block /QA/ variables:
!           NCOMMOUT
!     Common block /DATASET/ variables:
!           vermet
!
! --- MET1DOC called by:  SETUP
! --- MET1DOC calls:      OPEN_ERR
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'dataset.puf'
      include 'filnam.puf'
      include 'qa.puf'
!
! --- Local Variables
      character*16 dataset,dataver
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Loop over CALMET.DAT files
      do kk=1,nmetdom
       io=io7+(kk-1)
       do k=1,nmetdat(kk)
         call FILVERS(io,'BIN',metdatl(k,kk),'CALMET          ',&
                      vercmet(kk))
         open(io,file=metdatl(k,kk),status='old',form='unformatted',&
              iostat=ierr)
         if(ierr.NE.0) call OPEN_ERR(io6,'MET1DOC','CALMET File',&
                                     metdatl(k,kk),io)
! ---    Read and test first record to check header format
! ---    Record #1 - File Declaration -- 24 words
         read(io) dataset,dataver,datamod
         ifilver=0
         if(dataset.EQ.'CALMET.DAT') ifilver=1
         if(ifilver.EQ.0) then
! ---       No comment records
            close(io)
         elseif(ifilver.EQ.1) then
! ---       To scratch file
            write(iox,'(a132)') blank
            write(iox,'(a132)') break
            write(iox,'(a132)') blank
            write(iox,'(2a16,a64)') dataset,dataver,datamod
            ncommout=ncommout+4
! ---       Number of comment records
            read(io) ncom
            if(k.EQ.1) then
! ---          Keep entire comment image
               ncommout=ncommout+ncom
               do i=1,ncom
                  comment1=blank
                  read(io) comment1
                  write(iox,'(a132)') comment1
               enddo
            else
! ---          Keep only title information
               n=MIN(ncom,4)
               do i=1,n
                  comment1=blank
                  read(io) comment1
                  write(iox,'(a132)') comment1
                  ncommout=ncommout+1
               enddo
            endif
            close(io)
         endif
       enddo
      enddo
      return
      end
!-----------------------------------------------------------------------
      subroutine cav_conc(ldb,nspec,q,qcav,xsgrd,ysgrd,ntr,xtr,ztr,htr,&
                          flow,dpbl,hlidmax,xcgrd,ycgrd,x115,x115c)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108           CAV_CONC
!                D. Strimaitis
!
! --- PURPOSE:  Process concentrations at receptors located between
!               source and the end of the cavity transition zone
!               (PRIME building downwash) for current source
!
! --- UPDATES:
!                    
! --- V6.267-V6.268 100108(DGS):Add HTR array to arg list and pass to
!                               CAV_SAMP
! --- V6.2-V6.267 090710 (DGS): Add maximum lid height for mass above
!                               Zi to arguments and pass to CAV_SAMP
!                               call for later use in VCOUP
! --- V5.7-V6.2   070629 (DGS): Include receptors upwind of source for
!                               cavity concentrations
!                 070629 (DGS): Place cavity impacts from all PRIME
!                               sources into the TOTAL arrays since the
!                               regular arrays are zeroed in the loop
!                               over sources.
!
! --- INPUTS:
!
!           LDB - logical - Debug output flag
!         NSPEC - integer - Number of species
!     Q(mxspec) - real    - Emission rate of each species (g/s)
!                           NOT captured/released from cavity
!  QCAV(mxspec) - real    - Emission rate of each species (g/s)
!                           captured/released from cavity
!         XSGRD - real    - Source x-coordinate (met grid)
!         YSGRD - real    - Source y-coordinate (met grid)
!           NTR - integer - Number of entries in rise table
!   XTR(mxrise) - real    - Distance from source (m) in rise table
!   ZTR(mxrise) - real    - Height above ground (m) in rise table
!   HTR(mxrise) - real    - Rise (m) without streamline effects
!          FLOW - real    - Flow vector (radians CW from N)
!          DPBL - real    - Boundary layer depth (m)
!       HLIDMAX - real    - Maximum lid height (m) for mass above HLID
!
!     Common block /CTSGDAT/ variables:
!           NCTREC,XRCT(mxrect),YRCT(mxrect)
!     Common block /GRID/ variables:
!           NXSAM,NYSAM,LSAMP,IBSAMP,JBSAMP,DELSAM,
!           DGRID,DGRIDI
!     Common block /NONGRD/ variables:
!           NREC,XNG(mxrec),YNG(mxrec),ZNG(mxrec)
!     Common block /WAKEDAT/ variables:
!           Hb, Wb, xLb, Rb, xLC, xLR, HR, XBADJ, YBADJ,
!           CBYQCAV, XCAV(mxrise), SYCAV(mxrise), SZCAV(mxrise)
!
! --- OUTPUT:
!
!         XCGRD - real    - Cavity source x-coordinate (met grid)
!         YCGRD - real    - Cavity source y-coordinate (met grid)
!          X115 - real    - Distance (m) from primary source to the
!                           end of cavity transition zone
!         X115C - real    - Distance (m) from cavity source to the
!                           end of cavity transition zone
!
!     Common block /CHIFLX/ variables:
!           TCHISAM(mxnxg,mxnyg,mxspec), TCHIREC(mxrec,mxspec),
!           TCHICT(mxrect,mxspec)
!
!
! --- CAV_CONC called by:  POINTS1, POINTS2
! --- CAV_CONC calls    :  CAV_SAMP
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'chiflx.puf'
      include 'ctsgdat.puf'
      include 'grid.puf'
      include 'nongrd.puf'
      include 'wakedat.puf'
      real q(mxspec),qcav(mxspec)
      real xtr(mxrise),ztr(mxrise),htr(mxrise)
      logical ldb
      data piby2/1.5707963/, rt2pi/2.5066283/
! --- Set trig. factors for flow direction (phi is CCW rotation that
! --- places x-axis along the flow)
      phi=piby2-flow
      sinf=sin(phi)
      cosf=cos(phi)
! --- Convert source location to meters
      xsm=xsgrd*dgrid
      ysm=ysgrd*dgrid
! --- Set centerline of cavity plume relative to center of bldg
! --- Top-hat equivalent width of cavity sigma-y
      wtop=sycav(1)*rt2pi
! --- Max distance from bldg center to centerline of cavity plume
      ybmax=0.5*(Wb-wtop)
      if(ybmax.LE.0.0) then
! ---    Plume spread exceeds bldg width so cavity source is
! ---    centered on bldg
         ycb=0.0
      else
! ---    Source location relative to center of bldg
         ysb=-ybadj
         if(ysb.LT.0.0) then
            ycb=AMAX1(ysb,-ybmax)
         else
            ycb=AMIN1(ysb,ybmax)
         endif
      endif
! --- Identify location of effective cavity source on the met grid
! --- Along-wind coordinate (relative to primary source location)
      xc=xcav(1)      
! --- Cross-wind coordinate (relative to primary source location)
      yc=ycb+ybadj
! --- Rotate from along-wind system to original system
      xco=xc*cosf-yc*sinf
      yco=xc*sinf+yc*cosf
! --- Change to met grid units
      xcgrd=xsgrd+xco*dgridi
      ycgrd=ysgrd+yco*dgridi
! --- Set limits of transition zone at end of cavity
      x115b=xLb+1.15*xLR
      x85b=xLb+0.85*xLR
! --- End of zone relative to primary source
      x115=x115b+xbadj
! --- End of zone relative to cavity source
      x115c=x115-xc
! --- Set denomenator for cavity-end scaling factor
      fcavi=1./(x115b-x85b)
! --- DEBUG output
      if(LDB) then
         write(io6,*)
         write(io6,*)'CAV_CONC:  Flow (rad CW from N) = ',flow
         write(io6,*)'Primary Source (met grid)       = ',xsgrd,ysgrd
         write(io6,*)'Cavity Source (met grid)        = ',xcgrd,ycgrd
         write(io6,*)'  ycb(m) bldg center to cav src = ',ycb
      endif
! --- Loop over gridded receptors
! --------------------------------
      if(LSAMP) then
! ---    All gridded receptors are on the ground
         zr = 0.0
! ---    Compute the start of the sampling grid in MET grid units
         xbsamp=float(ibsamp)-0.5
         ybsamp=float(jbsamp)-0.5
         do 100 isam=1,nxsam
         do 100 jsam=1,nysam
! ---       Get location of the receptor (m) (on met. grid)
            xm = xbsamp*dgrid+float(isam-1)*delsam
            ym = ybsamp*dgrid+float(jsam-1)*delsam
! ---       Locate receptor (m) relative to source (xr is along-flow)
            dx=xm-xsm
            dy=ym-ysm
            xr=dx*cosf+dy*sinf
            yr=dy*cosf-dx*sinf
! ---       Locate receptor relative to center of upwind bldg face
            xrb=xr-xbadj
            yrb=yr-ybadj
! ---       Process receptors downwind of source, in/near cavity
            xtest=AMAX1(xr,xrb)
            if(xtest.GT.0.0 .AND. xrb.LE.x115b) then
! ---          Set cavity/wake transition factors
               fcav=1.0
               fwak=0.0
               if(xrb.GT.x85b) then
! ---             Transition zone at far end of cavity
                  fcav=(x115b-xrb)*fcavi
                  fwak=1.0-fcav
               elseif(xrb.LT.xLb) then
! ---             Transition zone at building
                  fcav=AMAX1(0.0,xrb/xLb)
               endif
! ---          Get coupling
               call CAV_SAMP(ldb,xr,yr,zr,xrb,yrb,ycb,fcav,fwak,&
                             ntr,xtr,ztr,htr,dpbl,hlidmax,cbyq0,cbyqc)
! ---          Screen out upwind receptor impacts (if any)
               if(xr.LE.0.0) cbyq0=0.0
               if(xrb.LE.0.0) cbyqc=0.0
! ---          Compute concentration for each species
               do is=1,nspec
                  tchisam(isam,jsam,is)=tchisam(isam,jsam,is)+&
                                        qcav(is)*cbyqc+q(is)*cbyq0
               enddo
! ---          DEBUG output:  report non-zero species 1 concentration
               if(LDB) then
                  write(io6,*)'  gridded i,j,chi(1) = ',isam,jsam,&
                                               tchisam(isam,jsam,1)
               endif
            endif
100      continue
      endif
! --- Loop over discrete receptors
! --------------------------------
      do ir=1,nrec
         zr=zng(ir)
! ---    Locate receptor (m) relative to source (xr is along-flow)
         dx=xng(ir)*dgrid-xsm
         dy=yng(ir)*dgrid-ysm
         xr=dx*cosf+dy*sinf
         yr=dy*cosf-dx*sinf
! ---    Locate receptor relative to center of upwind bldg face
         xrb=xr-xbadj
         yrb=yr-ybadj
! ---    Process receptors downwind of source, in/near cavity
         xtest=AMAX1(xr,xrb)
         if(xtest.GT.0.0 .AND. xrb.LE.x115b) then
! ---       Set cavity/wake transition factors
            fcav=1.0
            fwak=0.0
            if(xrb.GT.x85b) then
! ---          Transition zone at far end of cavity
               fcav=(x115b-xrb)*fcavi
               fwak=1.0-fcav
            elseif(xrb.LT.xLb) then
! ---          Transition zone at building
               fcav=AMAX1(0.0,xrb/xLb)
            endif
! ---       Get coupling
            call CAV_SAMP(ldb,xr,yr,zr,xrb,yrb,ycb,fcav,fwak,&
                          ntr,xtr,ztr,htr,dpbl,hlidmax,cbyq0,cbyqc)
! ---       Screen out upwind receptor impacts (if any)
            if(xr.LE.0.0) cbyq0=0.0
            if(xrb.LE.0.0) cbyqc=0.0
! ---       Compute concentration for each species
            do is=1,nspec
               tchirec(ir,is)=tchirec(ir,is)+&
                              qcav(is)*cbyqc+q(is)*cbyq0
            enddo
! ---       DEBUG output:  report non-zero species 1 concentration
            if(LDB) then
               write(io6,*)'   discrete i,chi(1) = ',ir,tchirec(ir,1)
            endif
         endif
      enddo
! --- Loop over CTSG receptors
! ----------------------------
! --- All CTSG receptors are on the ground
      zr = 0.0
      do ir=1,nctrec
! ---    Locate receptor (m) relative to source (xr is along-flow)
         dx=xrct(ir)*dgrid-xsm
         dy=yrct(ir)*dgrid-ysm
         xr=dx*cosf+dy*sinf
         yr=dy*cosf-dx*sinf
! ---    Locate receptor relative to center of upwind bldg face
         xrb=xr-xbadj
         yrb=yr-ybadj
! ---    Process receptors downwind of source, in/near cavity
         xtest=AMAX1(xr,xrb)
         if(xtest.GT.0.0 .AND. xrb.LE.x115b) then
! ---       Set cavity/wake transition factors
            fcav=1.0
            fwak=0.0
            if(xrb.GT.x85b) then
! ---          Transition zone at far end of cavity
               fcav=(x115b-xrb)*fcavi
               fwak=1.0-fcav
            elseif(xrb.LT.xLb) then
! ---          Transition zone at building
               fcav=AMAX1(0.0,xrb/xLb)
            endif
! ---       Get coupling
            call CAV_SAMP(ldb,xr,yr,zr,xrb,yrb,ycb,fcav,fwak,&
                          ntr,xtr,ztr,htr,dpbl,hlidmax,cbyq0,cbyqc)
! ---       Screen out upwind receptor impacts (if any)
            if(xr.LE.0.0) cbyq0=0.0
            if(xrb.LE.0.0) cbyqc=0.0
! ---       Compute concentration for each species
            do is=1,nspec
               tchict(ir,is)=tchict(ir,is)+&
                             qcav(is)*cbyqc+q(is)*cbyq0
            enddo
! ---       DEBUG output:  report non-zero species 1 concentration
            if(LDB) then
               write(io6,*)'   discrete i,chi(1) = ',ir,tchict(ir,1)
            endif
         endif
      enddo
      return
      end
!-----------------------------------------------------------------------
      subroutine cav_samp(ldb,xr,yr,zr,xrb,yrb,ycb,fcav,fwak,&
                          ntr,xtr,ztr,htr,hlid,hlidmax,cbyq0,cbyqc)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108           CAV_SAMP
!                D. Strimaitis
!
! --- PURPOSE:  Compute concentration/emission rate at a position
!               between the source and the end of the cavity
!               transition zone (PRIME building downwash)
!
!               Wind speed at the release height is used for the
!               'primary' source, while the speed at the building ht
!               is used for the 'cavity' source
!
! --- UPDATE
! --- V6.267-V6.268 100108 (DGS):Revise "inside" cavity concentration
!                                to be consistent with ISC-PRIME
!                                implementation:  reflection term was
!                                missing, as was contribution beyond
!                                the end of the cavity (to the end of
!                                the transition region)
!                  100108 (DGS): Drop nobidcav because BID is not
!                                used with ground-level cavity source
!                  100108 (DGS): Add rise table HTR for BID rise
! --- V6.22-V6.267 090710 (DGS): Pass maximum lid height of puff to
!                                VCOUP call
! --- V6.2-V6.22   070921 (DGS): Restrict primary source calculations
!                                to receptors downwind of primary source
!                                and add screen for receptors located
!                                far to the side (no impact)
! --- V5.7-V6.2    070629 (DGS): Initialize local receptor ht for
!                                cavity calcs ZRC to ZR
!
! --- INPUTS:
!
!           LDB - logical - Debug output flag
!         XR,YR - real    - Coordinates (m) of receptor relative to
!                           primary source location (x is alongwind)
!            ZR - real    - Receptor height (m) above ground
!       XRB,YRB - real    - Coordinates (m) of receptor relative to
!                           center of windward face of building
!                           (x is alongwind)
!           YCB - real    - Crosswind coordinate (m) of effective
!                           cavity source relative to the
!                           center of windward face of building
!                           (x is alongwind)
!          FCAV - real    - Transition factor for 'internal' cavity
!                           source
!          FWAK - real    - Transition factor for 'external' cavity
!                           source
!           NTR - integer - Number of entries in rise table
!   XTR(mxrise) - real    - Distance from source (m) in rise table
!   ZTR(mxrise) - real    - Height above ground (m) in rise table
!   HTR(mxrise) - real    - Rise table (m) without streamline effects
!          HLID - real    - Boundary layer depth (m)
!       HLIDMAX - real    - Maximum lid height (m) for mass above HLID
!
!     Common block /WAKEDAT/ variables:
!           Hb, Wb, xLb, Rb, xLC, xLR, HR, Ub, Urh,
!           CBYQCAV, SYCAV(mxrise), SZCAV(mxrise)
!
! --- OUTPUT:
!
!         CBYQ0 - real    - Coupling for primary source (s/m3)
!         CBYQC - real    - Coupling for cavity source (s/m3)
!
!
! --- CAV_SAMP called by:  CAV_CONC
! --- CAV_SAMP calls    :  VCOUP, WAKE_XSIG, POSITION, INTERTAB,
!                          CAVITY_HT
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedat.puf'
      real xtr(mxrise),ztr(mxrise),htr(mxrise)
      logical ldb, nobidprm
      data rt2pi/2.5066283/, zero/0.0/, pi/3.1415927/
      data sigcut/12.0/
! --- Call VCOUP with a fixed puff code of 1 (Gaussian, below lid)
      data icode/1/
! --- Add BID to sigmas for the primary source
! --- (if not already in tabulated sigmas)
      data nobidprm/.FALSE./
! --- Set travel time to receptor, using wind at top of building for
! --- the cavity source, and at release ht. for the primary source
      timec=xr/Ub
      timep=xr/Urh
! --- Set primary source height and rise
      call INTERTAB(xr,ntr,xtr,i1,i2,factor)
      ht=ztr(i1)+factor*(ztr(i2)-ztr(i1))
      rise=htr(i1)+factor*(htr(i2)-htr(i1))
! --- Set sigmas for primary (sy,sz) and cavity (syc,szc) sources
      call WAKE_XSIG(xr,timep,rise,nobidprm,sz,sy,szcdum,sycdum)
      call WAKE_XSIG(xr,timec,zero,nobidprm,szdum,sydum,szc,syc)
! --- Initialize coupling to zero
      cbyq0=zero
      cbyqc=zero
! --- Process source contributions according to receptor location
! --- relative to wake structures
      call POSITION(xrb,yrb,zr,ipositn)
! --- Primary source
! ------------------
! --- Primary source does not impact upwind
      if(ipositn.GE.3 .AND. xr.GT.0.0) then
! ---    Drop calculation for receptors too far to the side
         if(ABS(yr) .LE. (sigcut*sy)) then
! ---       Primary source contributes outside of cavity
            fy0=(EXP(-0.5*(yr/sy)**2))/(rt2pi*sy)
            cbyq0=fy0*VCOUP(icode,zr,ht,sz,hlid,hlidmax)/Urh
         endif
      endif
! --- Cavity source
! -----------------
      if(ipositn.LT.4 .AND. syc.GT.zero) then
! ---    Receptor in projected building, cavity, or wake
! ---    Compute concentration due to cavity material
! ---    Initialize receptor ht to value passed into subroutine
         zrc=zr
         if(ipositn.EQ.3) then
! ---       Sample vertical distribution when outside cavity
            if(zr.GT.zero) then
! ---          Set pole height to height above cavity boundary
               if(xrb.LT.(xLb+xLR)) then
                  call CAVITY_HT(Hb,Wb,xLb,Rb,xLC,xLR,HR,xrb,&
                                 zcav,wcav)
                  zrc=AMAX1(zero,zr-zcav)
               endif
            endif
         else
! ---       Position 1 is within structure, and 2 is within cavity
! ---       Place receptor on the 'ground' to get inside cavity conc.
            zrc=zero
         endif
! ---    'Inside' cavity cbyq before transition factor
         fycn=(EXP(-0.5*((yrb-ycb)/sycav(1))**2))/(rt2pi*sycav(1))
         cavnear=fycn*VCOUP(icode,zrc,zero,szcav(1),hlid,hlidmax)/Ub
! ---    'Outside' cavity cbyq before transition factor
         fycf=(EXP(-0.5*((yrb-ycb)/syc)**2))/(rt2pi*syc)
         cavfar=fycf*VCOUP(icode,zrc,zero,szc,hlid,hlidmax)/Ub
! ---    Weighted average
         cbyqc=fcav*cavnear+fwak*cavfar
      endif
! --- DEBUG output
      if(LDB) then
         write(io6,*)'                ------------------'
         write(io6,*)'CAV_SAMP:  xr,yr(m from primary) = ',xr,yr
         write(io6,*)'     xrb,yrb(m from upwind face) = ',xrb,yrb
         write(io6,*)'        [transition]   fcav,fwak = ',fcav,fwak
         write(io6,*)'          receptor position flag = ',ipositn
         write(io6,*)'                 [primary] sy,sz = ',sy,sz
         write(io6,*)'                  [cavity] sy,sz = ',syc,szc
         write(io6,*)'                     cbyq0,cbyqc = ',cbyq0,cbyqc
         write(io6,*)'                ------------------'
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_cav0(sycapt,szcav0,sycav0)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402          WAKE_CAV0
!
! --- Adapted from
! --- PRIME      Version:  1.0     Level:  970812             WAKE_CAV0
!                D. Strimaitis, L. Schulman,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Compute the sigmas for a source placed on the floor
!               of the cavity, which produce the target cavity
!               concentration
!
! --- INPUTS:
!
!        SYCAPT - real    - Sigma-y (m) of plume at point where
!                           mass is captured in cavity
!
!     Common block /WAKEDAT/ variables:
!           Hb, Wb, xLR, xLC, HR, Ub, Urh
!
! --- OUTPUT:
!
!        SZCAV0 - real    - Initial sigma-z (m) of cavity source
!        SYCAV0 - real    - Initial sigma-y (m) of cavity source
!
!                 Note    - These sigmas reproduce the cavity
!                           concentration when the used in:
!                           C = Qc / (pi * ub * szcav0 * sycav0)
!                           where ub is the wind speed at the ht of the
!                           building (i.e., for the cavity source),
!                           and Qc is the mass flux captured by
!                           and released from the cavity.
!
! --- WAKE_CAV0 called by:  WAKE_DFSN
! --- WAKE_CAV0 calls    :  none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedat.puf'
      data rt2pi/2.5066283/, rt2bypi/.7978846/, third/0.3333333/
! --- Interpret plume sigma-y at cavity entry point as equivalent
! --- top-hat;  limit to full width of bldg
      wcapt=AMIN1(rt2pi*sycapt,Wb)
! --- Set width scale for lateral distribution in cavity
      wscale=AMIN1(Wb,3.*Hb)
      wscale=AMAX1(wscale,third*Hb)
      wscale=AMAX1(wscale,wcapt)
! --- Sigma-y for equivalent top-hat distribution
      sycav0=wscale/rt2pi
! --- Set height of cavity behind the bldg
      if(xLC .LT. xLb) then
! ---    Reattachment
         hcav=Hb
      else
! ---    No Reattachment
         hcav=HR
      endif
! --- Set sigma-z that results in centerline concentration equal to
! --- cavity concentration (using wind speed at building height)
! --- Wilson & Britter 1982 approach to cavity concentration
      szcav0=rt2bypi*hcav*third
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_dbg(io,ntr,xtr,ztr,rtr,htr,nobid,hstack)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108           WAKE_DBG
!
! --- Adapted from
! --- PRIME      Version:  1.0     Level:  970812              WAKE_DBG
!                D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- UPDATE
! --- V5.7-V6.268 100108(DGS): Initialize dbduw=0.0 everywhere that
!                              dbuw is initialized
! --- V5.7-V6.2  070629 (DGS): Initialize dbduw=0.0 for output
!
! --- PURPOSE: Reports salient features of PRIME results to
!              file for DEBUG purposes
!
! --- INPUTS:
!               io - integer     - unit for output file
!         XTR(ntr) - real        - Downwind distance (m)
!         ZTR(ntr) - real        - Plume centerline height (m)
!         RTR(ntr) - real        - Plume radius (m)
!         HTR(ntr) - real        - Plume rise (m) without streamline
!                                  modifications (used for BID)
!            NOBID - logical     - flag for BID
!           HSTACK - real        - height (m) of release
!
!     Common block /PARAMS/ variables:
!           MXRISE, MXNW
!     Common block /WAKEDAT/ variables:
!           XBADJ, Hb, Wb, xLb, Rb, xLR, xLC, HR,
!           XCAV, SZCAV, SYCAV, Ub
!
! --- OUTPUT: (written to file)
!
!          DBXB - real    - Distance (m) from upwind bldg face
!           DBX - real    - Distance (m) from source along wind
!           DBZ - real    - Plume centerline height above ground (m)
!          DBHC - real    - Cavity height above ground (m)
!          DBHW - real    - Wake height above ground (m)
!          DBSZ - real    - Sigma-z (m)
!          DBSY - real    - Sigma-y (m)
!          DBUW - real    - Wind speed factor at DBZ  (u/Ua)
!         DBRSZ - real    - Sigma-z (m) inferred from plume radius
!       IPOSITN - integer - 1: in bldg
!                           2: in cavity
!                           3: in far wake
!                           4: outside bldg influence
!         DBSZC - real    - Sigma-z (m) for cavity source
!         DBSYC - real    - Sigma-y (m) for cavity source
!
! --- WAKE_DBG called by:  POINTS1
! --- WAKE_DBG calls    :  WAKE_XSIG, WAKE_DIM, CAVITY_HT,
!                          POSITION, WAKE_U
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedat.puf'
      REAL XTR(ntr),ZTR(ntr),RTR(ntr),HTR(ntr)
      logical nobid,ldb
      ldb=.FALSE.
      data rt2bypi/0.797885/
! --- Write section header to file
      write(io,*)
      write(io,*)'------------------------------------------------'
      write(io,*)'PRIME Module Results for Current Source and Hour'
      write(io,*)'          (all lengths in meters)'
      write(io,*)'------------------------------------------------'
      write(io,*)
      write(io,100)
      write(io,*)
! --- Report start of cavity as first point if it lies upwind of source
      if(xcav(1).LT.0.0) then
! ---    Set plume coordinates
         dbx=xcav(1)
         dbz=0.0
! ---    Set initial values
         dbsz=0.0
         dbsy=0.0
         dbhw=0.0
         dbhc=0.0
         dbrsz=0.0
         dbuw=1.0
         ipositn=4
         dbduw=0.0
! ---    Compute related data
         rise=0.0
         xb=dbx-xbadj
         yb=-ybadj
         zb=dbz
         dbxb=xb
! ---    Set sigmas
         dbsz=0.0
         dbsy=0.0
         dbszc=szcav(1)
         dbsyc=sycav(1)
! ---    Set dr/dx of plume radius within wake region
         dbdrdx=0.0
         if(xb.GE.0.0) then
! ---       Set wake dimension along center plane from bldg
            call WAKE_DIM(xb,Hb,Wb,Rb,dbhw,dbww)
! ---       Set cavity dimension along centerplane from bldg
            call CAVITY_HT(Hb,Wb,xLb,Rb,xLC,xLR,HR,xb,dbhc,dbwc)
! ---       Set speed factor
            call POSITION(xb,yb,zb,ipositn)
            dbuw=1.0
            dbduw=0.0
            if(ipositn.LT.4) call WAKE_U(ldb,xb,yb,zb,dbuw,dbduw)
         endif
! ---    Report values
         write(io,101) dbxb,dbx,dbz,dbhw,dbhc,dbsz,dbsy,dbuw,dbduw,&
                       dbrsz,dbdrdx,ipositn,dbszc,dbsyc
      endif
! --- Process point of release
! --- Set plume coordinates
      dbx=0.0
      dbz=hstack
! --- Set initial values
      dbsz=0.0
      dbsy=0.0
      dbhw=0.0
      dbhc=0.0
      dbrsz=0.0
      dbuw=1.0
      ipositn=4
      dbduw=0.0
! --- Compute related data
      rise=0.0
      xb=dbx-xbadj
      yb=-ybadj
      zb=dbz
      dbxb=xb
! --- Set sigmas just downwind of source
      xzero=0.001
      tzero=0.001
      call WAKE_XSIG(xzero,tzero,rise,nobid,dbsz,dbsy,dbszc,dbsyc)
! --- Set dr/dx of plume radius within wake region
      call WAKE_DRDX(dbx,dbdrdx)
      if(xb.GE.0.0) then
! ---    Set wake dimension along center plane from bldg
         call WAKE_DIM(xb,Hb,Wb,Rb,dbhw,dbww)
! ---    Set cavity dimension along centerplane from bldg
         call CAVITY_HT(Hb,Wb,xLb,Rb,xLC,xLR,HR,xb,dbhc,dbwc)
! ---    Set speed factor
         call POSITION(xb,yb,zb,ipositn)
         dbuw=1.0
         dbduw=0.0
         if(ipositn.LT.4) call WAKE_U(ldb,xb,yb,zb,dbuw,dbduw)
      endif
! --- Report values
      write(io,101) dbxb,dbx,dbz,dbhw,dbhc,dbsz,dbsy,dbuw,dbduw,dbrsz,&
                    dbdrdx,ipositn,dbszc,dbsyc
! --- Now loop over entries in plume rise array
      do it=1,ntr
! ---    Set plume coordinates
         dbx=xtr(it)
         dbz=ztr(it)
         dbrsz=rtr(it)*rt2bypi
         rise=htr(it)
! ---    Use advection wind at top of building to set travel time
         dbt=dbx/Ub
! ---    Set initial values
         dbhw=0.0
         dbhc=0.0
         dbuw=1.0
         ipositn=4
! ---    Compute related data
         xb=dbx-xbadj
         yb=-ybadj
         zb=dbz
         dbxb=xb
! ---    Set sigmas
         call WAKE_XSIG(dbx,dbt,rise,nobid,dbsz,dbsy,dbszc,dbsyc)
! ---    Set dr/dx of plume radius within wake region
         call WAKE_DRDX(dbx,dbdrdx)
         if(xb.GE.0.0) then
! ---       Set wake dimension along center plane from bldg
            call WAKE_DIM(xb,Hb,Wb,Rb,dbhw,dbww)
! ---       Set cavity dimension along centerplane from bldg
            call CAVITY_HT(Hb,Wb,xLb,Rb,xLC,xLR,HR,xb,dbhc,dbwc)
! ---       Set speed factor
            call POSITION(xb,yb,zb,ipositn)
            dbuw=1.0
            dbduw=0.0
            if(ipositn.LT.4) call WAKE_U(ldb,xb,yb,zb,dbuw,dbduw)
         endif
! ---    Report values
         write(io,101) dbxb,dbx,dbz,dbhw,dbhc,dbsz,dbsy,dbuw,dbduw,&
                       dbrsz,dbdrdx,ipositn,dbszc,dbsyc
      enddo
      write(io,*)
100   format('     XB      X      Z   Hwake   Hcav    Sz     S',&
             'y   Ufac  dUfac  R->Sz   dRdx  Pos  Szcav  Sycav')
101   format(1x,7f7.1,2f7.3,f7.1,f7.3,i4,2f7.1)
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_dfsn(ldbhr,xi,bidsq,sz0,sy0)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100430          WAKE_DFSN
!
! --- Adapted from
! --- PRIME      Version:  1.0     Level:  970812             WAKE_DFSN
!                L. Schulman, D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE: Tabulates sigmas and rate of plume growth as function
!              of location within the wake from modified Weil (1996)
!              analytical expressions
!
! --- UPDATE
! --- V6.268-V6.269  100430  (DGS): Creat XWAKISC() and DRWAKISC()
!                                   arrays to align calculation of
!                                   plume drdx in wake region with
!                                   ISC-PRIME.
! --- V6.2-V6.268    100308  (DGS): Include BID in sigmas at xi and
!                                   allow these to grow linearly within
!                                   the wake PDF region.  Tabulated
!                                   sigmas for PRIME sources will now
!                                   include the BID.  Sigmas upwind of
!                                   of xi do not have BID at this point
!                                   and are flagged as negative.
!                    100308  (DGS): Fix bug in defining the entries in
!                                   the sigma arrays to conform to those
!                                   used in ISC-PRIME.  Change trap for
!                                   source located downwind of entry
!                                   wake.
!                    100308  (DGS): Add control to skip comparing wake
!                                   sigmas with ambient-growth sigmas to
!                                   conform to ISC-PRIME.
! --- V5.712-V6.2    070629  (DGS): Initialize dummy variables passed
!                                   but not used
!                                   SZDUM, ZKDUM, SYDUM, YKDUM
! --- V5.711-V5.712  030822  (DGS): Constrain virtual distance increment
!                                   XVWAK to be positive
!
! --- INPUTS:
!            ldbhr - logical     - Flag for debug write statements
!                                  to upwind bldg wall
!               xi - real        - distance (m) from upwind bldg wall
!                                  to point where plume intersects wake
!            bidsq - real        - square of buoyancy-enhanced sigma
!                                  (m^2) at point of entry into wake
!              sz0 - real        - sigma-z (m) at the source
!              sy0 - real        - sigma-y (m) at the source
!
!     Common block /PARAMS/ variables:
!           MXRISE, MXNW
!     Common block /WAKEDAT/ variables:
!           XBADJ, Hb, Wb, xLb, Rb, xLR, UBKPS
!
! --- OUTPUT:
!
!     Common block /WAKEDAT/ variables:
!           NWAK, XWAK(mxrise), SZWAK(mxrise), SYWAK(mxrise),
!           DRWAK(mxrise), XZVWAK, XYVWAK,
!           NCAV, XCAV(mxrise), SZCAV(mxrise), SYCAV(mxrise),
!           XZVCAV, XYVCAV, LRURL, ISTAB
!
! --- WAKE_DFSN called by:  NUMRISE
! --- WAKE_DFSN calls    :  SIGTZ, SIGTY,
!                           WAKE_XA, WAKE_CAV0, WAKE_TURB,
!                           WAKE_SIG, WAKE_SIGA
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedat.puf'
! --- Define local variable arrays for fine-steps
      real dist(mxnw),asigz(mxnw),asigy(mxnw),dsz(mxnw)
      real csigz(mxnw),csigy(mxnw)
      logical ldbhr,lcav,lwak,lrevcav
! --- Local control for imposing ISC-PRIME constructs
      logical L_ISCPRIME
! --- Misc. constants
      data zero/0.0/, half/0.5/, one/1.0/
      data fm2km/0.001/, fkm2m/1000.0/
      data rtpiby2/1.253314/
! --- Target stepping length (m)
      data dx0/2.0/
! --- ISC-PRIME mode
      data L_ISCPRIME/.TRUE./
! --- Initialize all dummy vars (passed but not used)
      szdum=0.0
      zkdum=0.0
      sydum=0.0
      ykdum=0.0
! --- Compute xa, where turbulent growth in the wake transitions
! --- to ambient growth rate, measured from upwind face of bldg
      call WAKE_XA(xLb,Rb,xaz,xay)
      xamx=AMAX1(xaz,xay)
      xamn=AMIN1(xaz,xay)
! --- Compute the virtual source offset for sz0,sy0 (km)
      call SIGTZ(sz0,zero,zero,Hb,szdum,tz0,xz0km)
      call SIGTY(sy0,zero,zero,sydum,ty0,xy0km)
! --- Compute the ambient-growth sigmas at point of entry (xi)
      xikm=(xi+xbadj)*fm2km
      xkm=xikm+xz0km
      tsec=xkm/ubkps
      call SIGTZ(zero,xkm,tsec,Hb,szi,tzi,xzikm)
      xkm=xikm+xy0km
      tsec=xkm/ubkps
      call SIGTY(zero,xkm,tsec,syi,tyi,xyikm)
! --- Add BID to entry sigmas
      szibid=SQRT(szi*szi+bidsq)
      syibid=SQRT(syi*syi+bidsq)
! --- Recompute virtuals for these sigmas (km)
      call SIGTZ(szibid,zero,zero,Hb,szdum,tzi,xzikm)
      call SIGTY(syibid,zero,zero,sydum,tyi,xyikm)
      if(LDBHR) then
         write(io6,*)
         write(io6,*)'----- WAKE_DFSN:  Wake Entry Sigmas'
         write(io6,*)'xikm,szi_amb,syi_amb = ',xikm,szi,syi
         write(io6,*)'     szi_bid,syi_bid = ',szibid,syibid
      endif
! --- Initialize CAVITY parameters
! --------------------------------
! --- Set distance from upwind face of bldg to END of cavity
      xcave=xLb+xLR
! --- Set distance from source to start of cavity
      distc=xLb+xbadj
! --- Set downwind distance to effective cavity source (when present),
! --- from the upwind face of bldg
      xbc=AMAX1(xi,xLb)
      xbc=AMIN1(xbc,xcave)
! --- Location of downwind edge of PDF region from effective cavity
! --- source
      xdc=xbc+xLR
! --- Set initial sigmas for cavity source using sigma-y at xi,
! --- including BID
      call WAKE_CAV0(syibid,szcav0,sycav0)
! --- The cavity sigma-y will need to be revised if xi lies upwind of
! --- the downwind face of the bldg.
      if(xi.LT.xLb) then
         lrevcav=.TRUE.
      else
         lrevcav=.FALSE.
      endif
! --- Determine if any plume material in cavity may be modeled
! ------------------------------------------------------------
      if(xi.GE.xcave) then
         lcav=.FALSE.
         lrevcav=.FALSE.
      else
         lcav=.TRUE.
      endif
! --- Initialize output arrays for a cavity source
      ncav=1
      xcav(1)=xbc+xbadj
      szcav(1)=szcav0
      sycav(1)=sycav0
! --- Compute corresponding virtual distances
! --- Note: this virtual distance is added to distance from the
! --- cavity source, not the primary source
      call SIGTZ(szcav0,zero,zero,Hb,szdum,tzvcav,xzvcav)
      call SIGTY(sycav0,zero,zero,sydum,tyvcav,xyvcav)
! --- Convert distance from km to m
      xzvcav=xzvcav*fkm2m
      xyvcav=xyvcav*fkm2m
      if(LDBHR) then
         write(io6,*)
         write(io6,*)'----- WAKE_DFSN:  Cavity Initialization'
         write(io6,*)'xcav1=xbc+xbadj (m)  = ',xcav(1)
         write(io6,*)'xzvcav,szcav0 (m)    = ',xzvcav,szcav0
         write(io6,*)'xyvcav,sycav0 (m)    = ',xyvcav,sycav0
         write(io6,*)'      lrevcav        = ',lrevcav
         write(io6,*)'Note:  virtuals added to distance from CAV src'
      endif
! --- Is plume altered by wake turbulence?
! ----------------------------------------
! --- Initialize output arrays
      nwak=1
      if(xi.GE.xamx) then
         lwak=.FALSE.
         xwak(1)=zero
         szwak(1)=sz0
         sywak(1)=sy0
         drwak(1)=zero
         xzvwak=xz0km*fkm2m
         xyvwak=xy0km*fkm2m
         if(ldbhr) then
            write(io6,*)
            write(io6,*)'----- WAKE_DFSN:        NWAK = ',nwak
            write(io6,*)'Z-dispersion reaches ambient at: ',(xaz+xbadj)
            write(io6,*)'Y-dispersion reaches ambient at: ',(xay+xbadj)
            write(io6,*)'z,y virtual distances (m)    = ',xzvwak,xyvwak
            write(io6,*)'xadj, yadj, xi        (m)    = ',xbadj,ybadj,xi
            write(io6,*)'Plume NOT altered by wake turbulence!'
         endif
      else
         lwak=.TRUE.
         xwak(1)=xi+xbadj
         szwak(1)=szibid
         sywak(1)=syibid
         drwak(1)=zero
! ---    Set corresponding virtual distances
         xasrc=xwak(1)
! ---    Convert virtual distance from km to m
         xzvwak=xzikm*fkm2m
         xyvwak=xyikm*fkm2m
! ---    Set virtual distance as increment
         xzvwak=xzvwak-xasrc
         xyvwak=xyvwak-xasrc
! ---    Constrain virtual distance to be positive
! ---    (Error if more negative than -1m)
         if(xzvwak.LE.-1. .OR. xyvwak.LE.-1.) then
            write(io6,*) 'WAKE_DFSN ERROR:  Negative virtual distance'
            write(io6,*) 'xzvwak = ',xzvwak
            write(io6,*) 'xyvwak = ',xyvwak
            stop 'ERROR in WAKE_DFSN --- see list file'
         else
            xzvwak=AMAX1(zero,xzvwak)
            xyvwak=AMAX1(zero,xyvwak)
         endif
      endif
! --- Return now if sigmas in wake do not need to be tabulated
      if(.NOT.lwak .AND. .NOT.lcav) return
! --- Compute location of downwind edge of PDF region from xi
      xd=xi+xLR
! --- Set stepping parameters
      dx=dx0
! --- Range of table is from point of entry into wake (xi), to the point
! --- at which ambient growth rate resumes (xa), plus one "ds" so that
! --- both sigmas reach ambient, and virtual distances are computed.
! --- When cavity sigmas are also computed, range may start at the
! --- downwind bldg face, and extend to end of cavity.
      xlow=xi
      xhi=xamx
      if(lcav) then
         xlow=AMIN1(xi,xbc)
         xhi=AMAX1(xamx,xcave)
      endif
      xrange=xhi-xlow+dx
      np=NINT(xrange/dx)+1
      np=MIN(np,mxnw-1)
      dx=xrange/(FLOAT(np)-one)
      dxi=one/dx
      nws=0
      ncs=0
! --- Set step increment for calls to ambient sigma subroutines
      dxkm=dx*fm2km
      dts=dxkm/ubkps
! --- Fill first element of marching arrays using values at xlow
! --- (dist array measures distance downwind from primary source)
      dist(1)=xlow+xbadj
      if(lwak) then
         asigz(1)=szibid
         asigy(1)=syibid
! ---    Set inital plume growth rate in wake to zero
         dsz(1)=zero
      endif
      if(lcav) then
         csigz(1)=szcav0
         csigy(1)=sycav0
      endif
! --- Initialize distance (from upwind face of bldg)
      x=xlow
! --- Loop over steps in wake region
! -----------------------------------
      do n=2,np
         xold=x
         x=x+dx
         dist(n)=dist(n-1)+dx
! ---    Check to see if cavity data should be revised based on
! ---    wake sigma from previous step, with BID
         if(lrevcav .AND. xold.GE.xLb) then
            call WAKE_CAV0(asigy(n-1),szcav0,sycav0r)
            if(sycav0r.GT.sycav0) then
               sycav0=sycav0r
               sycav(1)=sycav0
! ---          Compute corresponding virtual distance
! ---          Note: this virtual distance is added to distance from
! ---          the cavity source, not the primary source
               call SIGTY(sycav0,zero,zero,sydum,tyvcav,xyvcav)
! ---          Convert distance from km to m
               xyvcav=xyvcav*fkm2m
! ---          Replace sigma-y values in stepping arrays
               do ir=1,n-1
                  csigy(ir)=AMAX1(csigy(ir),sycav0)
               enddo
      if(LDBHR) then
         write(io6,*)
         write(io6,*)'----- WAKE_DFSN:  Cavity Revised'
         write(io6,*)'xcav1=xbc+xbadj (m)  = ',xcav(1)
         write(io6,*)'xzvcav,szcav0 (m)    = ',xzvcav,szcav0
         write(io6,*)'xyvcav,sycav0 (m)    = ',xyvcav,sycav0
         write(io6,*)'Note:  virtuals added to distance from CAV src'
      endif
            endif
            lrevcav=.FALSE.
         endif
! ---    Obtain sigmas for this step
! ---    First, persist initial values if upwind of starting point
         if(lwak .AND. (xi.GE.x)) then
            asigz(n)=asigz(n-1)
            asigy(n)=asigy(n-1)
            dsz(n)=dsz(n-1)
! ---       Set index for skipping entry when filling wake arrays
            nws=n
         endif
         if(lcav .AND. (xbc.GE.x)) then
            csigz(n)=szcav0
            csigy(n)=sycav0
! ---       Set index for skipping entry when filling cav arrays
            ncs=n
         endif
! ---    Now test again and apply full treatment when needed
         if(xold.GT.xamx) then
! ---       Ambient growth region in far wake:  use virtual source
! ---       Primary source ---
            if(lwak .AND. (xi.LT.x)) then
               xzkm=(dist(n)+xzvwak)*fm2km
               xykm=(dist(n)+xyvwak)*fm2km
               tz=xzkm/ubkps
               ty=xykm/ubkps
               call SIGTZ(zero,xzkm,tz,Hb,asigz(n),dtdum,dxdum)
               call SIGTY(zero,xykm,ty,asigy(n),dtdum,dxdum)
               dsz(n)=(asigz(n)-asigz(n-1))*dxi
            endif
! ---       Cavity source ---
            if(lcav .AND. (xbc.LT.x)) then
               distcav=x-xbc
               xzkm=(distcav+xzvcav)*fm2km
               xykm=(distcav+xyvcav)*fm2km
               tz=xzkm/ubkps
               ty=xykm/ubkps
               call SIGTZ(zero,xzkm,tz,Hb,csigz(n),dtdum,dxdum)
               call SIGTY(zero,xykm,ty,csigy(n),dtdum,dxdum)
            endif
         else
            if(x.LT.xamn) then
! ---          Wake growth for both sigz and sigy
! ---          Set x at mid-point of step
               xmid=half*(x+xold)
! ---          Compute turbulence intensities at midpoint
               call WAKE_TURB(xmid,xLb,Rb,wakiz,wakiy)
               if(lwak .AND. (xi.LE.x)) then
! ---             Compute sigmas in wake
                  call WAKE_SIG(x,xd,xold,wakiz,wakiy,asigz(n-1),&
                                asigy(n-1),Hb,Wb,Rb,zk,yk,&
                                asigz(n),asigy(n),dsz(n))
! ---             Check for larger ambient sigma
! ---             ISC-PRIME does not do this test, so restrict it with
! ---             a control variable
                  if(.NOT.L_ISCPRIME) then
                     xzkm=(dist(n)+xzvwak)*fm2km
                     xykm=(dist(n)+xyvwak)*fm2km
                     tz=xzkm/ubkps
                     ty=xykm/ubkps
                     call WAKE_SIGA('z',asigz(n-1),dxkm,dts,Hb,&
                                    xzkm,tz,asigza,zdxdum,zdtdum)
                     call WAKE_SIGA('y',asigy(n-1),dxkm,dts,zero,&
                                    xykm,ty,asigya,ydxdum,ydtdum)
                     asigz(n)=AMAX1(asigz(n),asigza)
                     asigy(n)=AMAX1(asigy(n),asigya)
                  endif
               endif
! ---          Cavity source ---
               if(lcav .AND. (xbc.LE.x)) then
                  call WAKE_SIG(x,xdc,xold,wakiz,wakiy,csigz(n-1),&
                                csigy(n-1),Hb,Wb,Rb,zkc,ykc,&
                                csigz(n),csigy(n),dzrate)
! ---             Check for larger ambient growth rate
! ---             ISC-PRIME does not do this test, so restrict it with
! ---             a control variable
                  if(.NOT.L_ISCPRIME) then
                     distcav=x-xbc
                     xzkm=(distcav+xzvcav)*fm2km
                     xykm=(distcav+xyvcav)*fm2km
                     tz=xzkm/ubkps
                     ty=xykm/ubkps
                     call WAKE_SIGA('z',csigz(n-1),dxkm,dts,Hb,&
                                    xzkm,tz,csigza,zdxdum,zdtdum)
                     call WAKE_SIGA('y',csigy(n-1),dxkm,dts,zero,&
                                    xykm,ty,csigya,ydxdum,ydtdum)
                     csigz(n)=AMAX1(csigz(n),csigza)
                     csigy(n)=AMAX1(csigy(n),csigya)
                  endif
               endif
            else
! ---          At least one of the sigmas reaches ambient growth
! ---          Process SIGMA-Z
! ----------------------------
               if(xold.GE.xaz) then
! ---             Ambient growth region in wake: use virtual source
! ---             Primary source ---
                  if(lwak .AND. (xi.LE.x)) then
                     xzkm=(dist(n)+xzvwak)*fm2km
                     tz=xzkm/ubkps
                     call SIGTZ(zero,xzkm,tz,Hb,asigz(n),dtdum,dxdum)
                     dsz(n)=(asigz(n)-asigz(n-1))*dxi
                  endif
! ---             Cavity source ---
                  if(lcav .AND. (xbc.LE.x)) then
                     distcav=x-xbc
                     xzkm=(distcav+xzvcav)*fm2km
                     tz=xzkm/ubkps
                     call SIGTZ(zero,xzkm,tz,Hb,csigz(n),dtdum,dxdum)
                  endif
               elseif(x.GE.xaz) then
! ---             Transition from wake to ambient in current step
                  dxakm=(xaz-xold)*fm2km
                  dtas=dxakm/ubkps
! ---             Compute turbulence intensities at midpoint
                  xmid=half*(xaz+xold)
                  call WAKE_TURB(xmid,xLb,Rb,wakiz,wakiy)
! ---             Primary source ---
                  if(lwak .AND. (xi.LE.xaz)) then
! ---                Compute sigma at xaz
                     xasrc=xaz+xbadj
                     call WAKE_SIG(xaz,xd,xold,wakiz,wakiy,asigz(n-1),&
                                   asigy(n-1),Hb,Wb,Rb,zk,ykdum,&
                                   sigzxa,sydum,dzrate)
                     call SIGTZ(sigzxa,zero,zero,Hb,szdum,&
                                tzvwak,xzvwakkm)
! ---                Check for larger ambient sigma
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        xzkm=(xasrc+xzvwak)*fm2km
                        tz=xzkm/ubkps
                        call WAKE_SIGA('z',asigz(n-1),dxakm,dtas,Hb,&
                                       xzkm,tz,asigzxa,dxdum,dtdum)
! ---                   Test wake result
                        if(asigzxa.GE.sigzxa) then
                           sigzxa=asigzxa
                           xzvwakkm=dxdum
                           tzvwak=dtdum
                        endif
                     endif
! ---                Set virtual distance increment in m
                     xzvwak=xzvwakkm*fkm2m-xasrc
! ---                Compute sigma at x
                     xzkm=(dist(n)+xzvwak)*fm2km
                     tz=xzkm/ubkps
                     call SIGTZ(zero,xzkm,tz,Hb,asigz(n),dtdum,dxdum)
                     dsz(n)=(asigz(n)-asigz(n-1))*dxi
                  endif
! ---             Cavity source ---
                  if(lcav .AND. (xbc.LE.xaz)) then
! ---                Compute sigma at xaz
                     xasrc=xaz-xbc
                     call WAKE_SIG(xaz,xdc,xold,wakiz,wakiy,csigz(n-1),&
                                   csigy(n-1),Hb,Wb,Rb,zkc,ykdum,&
                                   sigzxa,sydum,dzrate)
                     call SIGTZ(sigzxa,zero,zero,Hb,szdum,&
                                tzvcav,xzvcavkm)
! ---                Check for larger ambient growth rate
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        xzkm=(xasrc+xzvcav)*fm2km
                        tz=xzkm/ubkps
                        call WAKE_SIGA('z',csigz(n-1),dxakm,dtas,Hb,&
                                       xzkm,tz,csigzxa,dxdum,dtdum)
                        if(csigzxa.GE.sigzxa) then
                           sigzxa=csigzxa
                           xzvcavkm=dxdum
                           tzvcav=dtdum
                        endif
                     endif
! ---                Set virtual distance increment in m
                     xzvcav=xzvcavkm*fkm2m-xasrc
! ---                Compute sigma at x
                     distcav=x-xbc
                     xzkm=(distcav+xzvcav)*fm2km
                     tz=xzkm/ubkps
                     call SIGTZ(zero,xzkm,tz,Hb,csigz(n),dtdum,dxdum)
                  endif
               else
! ---             Wake growth for sigz
! ---             Set x at mid-point of step
                  xmid=half*(x+xold)
! ---             Compute turbulence intensities at midpoint
                  call WAKE_TURB(xmid,xLb,Rb,wakiz,wakiy)
! ---             Primary source ---
                  if(lwak .AND. (xi.LE.x)) then
! ---                Compute sigmaz
                     call WAKE_SIG(x,xd,xold,wakiz,wakiy,asigz(n-1),&
                                   asigy(n-1),Hb,Wb,Rb,zk,ykdum,&
                                   asigz(n),sydum,dsz(n))
! ---                Check for larger ambient sigma
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        xzkm=(dist(n)+xzvwak)*fm2km
                        tz=xzkm/ubkps
                        call WAKE_SIGA('z',asigz(n-1),dxkm,dts,Hb,&
                                       xzkm,tz,asigza,dxdum,dtdum)
                        asigz(n)=AMAX1(asigz(n),asigza)
                     endif
                  endif
! ---             Cavity source ---
                  if(lcav .AND. (xbc.LE.x)) then
                     call WAKE_SIG(x,xdc,xold,wakiz,wakiy,csigz(n-1),&
                                   csigy(n-1),Hb,Wb,Rb,zkc,ykdum,&
                                   csigz(n),sydum,dzrate)
! ---                Check for larger ambient growth rate
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        distcav=x-xbc
                        xzkm=(distcav+xzvcav)*fm2km
                        tz=xzkm/ubkps
                        call WAKE_SIGA('z',csigz(n-1),dxkm,dts,Hb,&
                                       xzkm,tz,csigza,zdxdum,zdtdum)
                        csigz(n)=AMAX1(csigz(n),csigza)
                     endif
                  endif
               endif
! ---          Process SIGMA-Y
! ----------------------------
               if(xold.GE.xay) then
! ---             Ambient growth region in wake: use virtual source
! ---             Primary source ---
                  if(lwak .AND. (xi.LE.x)) then
                     xykm=(dist(n)+xyvwak)*fm2km
                     ty=xykm/ubkps
                     call SIGTY(zero,xykm,ty,asigy(n),dtdum,dxdum)
                  endif
! ---             Cavity source ---
                  if(lcav .AND. (xbc.LE.x)) then
                     distcav=x-xbc
                     xykm=(distcav+xyvcav)*fm2km
                     ty=xykm/ubkps
                     call SIGTY(zero,xykm,ty,csigy(n),dtdum,dxdum)
                  endif
               elseif(x.GE.xay) then
! ---             Transition from wake to ambient
                  dxakm=(xay-xold)*fm2km
                  dtas=dxakm/ubkps
! ---             Compute turbulence intensities at midpoint
                  xmid=half*(xay+xold)
                  call WAKE_TURB(xmid,xLb,Rb,wakiz,wakiy)
! ---             Primary source ---
                  if(lwak .AND. (xi.LE.xay)) then
! ---                Compute sigma at xay
                     xasrc=xay+xbadj
                     call WAKE_SIG(xay,xd,xold,wakiz,wakiy,asigz(n-1),&
                                   asigy(n-1),Hb,Wb,Rb,zkdum,yk,&
                                   szdum,sigyxa,dzrate)
                     call SIGTY(sigyxa,zero,zero,sydum,&
                                tyvwak,xyvwakkm)
! ---                Check for larger ambient sigma
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        xykm=(xasrc+xyvwak)*fm2km
                        ty=xykm/ubkps
                        call WAKE_SIGA('y',asigy(n-1),dxakm,dtas,zero,&
                                       xykm,ty,asigyxa,dxdum,dtdum)
                        if(asigyxa.GE.sigyxa) then
                           sigyxa=asigyxa
                           xyvwakkm=dxdum
                           tyvwak=dtdum
                        endif
                     endif
! ---                Set virtual distance increment in m
                     xyvwak=xyvwakkm*fkm2m-xasrc
! ---                Compute sigma at x
                     xykm=(dist(n)+xyvwak)*fm2km
                     ty=xykm/ubkps
                     call SIGTY(zero,xykm,ty,asigy(n),dtdum,dxdum)
                  endif
! ---             Cavity source ---
                  if(lcav .AND. (xbc.LE.xay)) then
! ---                Compute sigma at xay
                     xasrc=xay-xbc
                     call WAKE_SIG(xay,xdc,xold,wakiz,wakiy,csigz(n-1),&
                                   csigy(n-1),Hb,Wb,Rb,zkdum,ykc,&
                                   szdum,sigyxa,dzrate)
                     call SIGTY(sigyxa,zero,zero,sydum,&
                                tyvcav,xyvcavkm)
! ---                Check for larger ambient growth rate
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        xykm=(xasrc+xyvcav)*fm2km
                        ty=xykm/ubkps
                        call WAKE_SIGA('y',csigy(n-1),dxakm,dtas,zero,&
                                       xykm,ty,csigyxa,dxdum,dtdum)
                        if(csigyxa.GE.sigyxa) then
                           sigyxa=csigyxa
                           xyvcavkm=dxdum
                           tyvcav=dtdum
                        endif
                     endif
! ---                Set virtual distance increment in m
                     xyvcav=xyvcavkm*fkm2m-xasrc
! ---                Compute sigma at x
                     distcav=x-xbc
                     xykm=(distcav+xyvcav)*fm2km
                     ty=xykm/ubkps
                     call SIGTY(zero,xykm,ty,csigy(n),dtdum,dxdum)
                  endif
               else
! ---             Wake growth for sigy
! ---             Set x at mid-point of step
                  xmid=half*(x+xold)
! ---             Compute turbulence intensities at midpoint
                  call WAKE_TURB(xmid,xLb,Rb,wakiz,wakiy)
! ---             Primary source ---
                  if(lwak .AND. (xi.LE.x)) then
! ---                Compute sigmay
                     call WAKE_SIG(x,xd,xold,wakiz,wakiy,asigz(n-1),&
                                   asigy(n-1),Hb,Wb,Rb,zkdum,yk,&
                                   szdum,asigy(n),dzrate)
! ---                Check for larger ambient sigma
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        xykm=(dist(n)+xyvwak)*fm2km
                        ty=xykm/ubkps
                        call WAKE_SIGA('y',asigy(n-1),dxkm,dts,zero,&
                                       xykm,ty,asigya,dxdum,dtdum)
                        asigy(n)=AMAX1(asigy(n),asigya)
                     endif
                  endif
! ---             Cavity source
                  if(lcav .AND. (xbc.LE.x)) then
                     call WAKE_SIG(x,xdc,xold,wakiz,wakiy,csigz(n-1),&
                                   csigy(n-1),Hb,Wb,Rb,zkdum,ykc,&
                                   szdum,csigy(n),dzrate)
! ---                Check for larger ambient growth rate
! ---                ISC-PRIME does not do this test, so restrict it
! ---                with a control variable
                     if(.NOT.L_ISCPRIME) then
                        distcav=x-xbc
                        xykm=(distcav+xyvcav)*fm2km
                        ty=xykm/ubkps
                        call WAKE_SIGA('y',csigy(n-1),dxkm,dts,zero,&
                                       xykm,ty,csigya,dxdum,dtdum)
                        csigy(n)=AMAX1(csigy(n),csigya)
                     endif
                  endif
               endif
            endif
         endif
! --- Next distance
      enddo
! --- Construct arrays for /WAKEDAT/
! ----------------------------------
      if(lwak) then
! ---    WAK arrays:
         npw=np-nws
! ---    Add points for portion of trajectory that lies upwind of xi,
! ---    using the same step interval, including the source location
         xwak1=xi+xbadj
! !!!    npup=xwak1*dxi+1
         npup=xwak1*dxi
         if(xwak1.LT.0.0) then
! ---       This may need more care to avoid a false halt caused by
! ---       precision
            write(io6,*)' ERROR:  plume enters wake UPWIND of source'
            write(io6,*)' Distance from source (m) = ',xwak1
            stop
         elseif(xwak1.GT.0.0) then
! ---       Keep npup=0 case only for xwak1 really = 0.0
            npup=MAX(1,npup)
         endif
         if(npup.GE.1) then
! ---       Upwind of xi-entry so make sigmas negative to indicate
! ---       that BID needs to be added in WAKE_ADDBID call
            nptot=npw+npup
            xwak(1)=zero
            szwak(1)=-sz0
            sywak(1)=-sy0
            drwak(1)=zero
         endif
         if(nptot.GE.mxrise) then
! ---       Sample a subset of the nptot points
            nwak=mxrise
            xwak(nwak)=dist(np)
            szwak(nwak)=asigz(np)
            sywak(nwak)=asigy(np)
            drwak(nwak)=rtpiby2*dsz(np)
            if(nptot.LE.2*mxrise) then
! ---          Fill elements with nearest values
               deln=FLOAT(nptot)/FLOAT(nwak)
               do in=2,nwak-1
! !!!             jn=in*deln
                  jn=(in-1)*deln
                  if(jn.LE.npup) then
! ---                Compute ambient sigmas
                     xtraj=jn*dx
                     xkm=xtraj*fm2km+xz0km
                     ts=xkm/ubkps
                     call SIGTZ(zero,xkm,ts,Hb,szwak(in),dtdum,dxdum)
                     xkm=xtraj*fm2km+xy0km
                     ts=xkm/ubkps
                     call SIGTY(zero,xkm,ts,sywak(in),dtdum,dxdum)
                     xwak(in)=xtraj
                     drwak(in)=zero
! ---                Upwind of xi-entry so make sigmas negative to
! ---                indicate that BID needs to be added in WAKE_ADDBID
                     szwak(in)=-szwak(in)
                     sywak(in)=-sywak(in)
                  else
! ---                Pass wake array values
                     jn=jn-npup+nws
                     xwak(in)=dist(jn)
                     szwak(in)=asigz(jn)
                     sywak(in)=asigy(jn)
                     drwak(in)=rtpiby2*dsz(jn)
                  endif
               enddo
            else
! ---          Use sliding step-size to sample nearfield more frequently
               deln=2.*FLOAT(nptot-mxrise)/FLOAT(mxrise*(mxrise-1))
               rn=one
               do in=2,nwak-1
                  rn=rn+one+(in-1)*deln
                  if(INT(rn).LE.npup) then
! ---                Compute ambient sigmas
                     xtraj=rn*dx
                     xkm=xtraj*fm2km+xz0km
                     ts=xkm/ubkps
                     call SIGTZ(zero,xkm,ts,Hb,szwak(in),dtdum,dxdum)
                     xkm=xtraj*fm2km+xy0km
                     ts=xkm/ubkps
                     call SIGTY(zero,xkm,ts,sywak(in),dtdum,dxdum)
                     xwak(in)=xtraj
                     drwak(in)=zero
! ---                Upwind of xi-entry so make sigmas negative to
! ---                indicate that BID needs to be added in WAKE_ADDBID
                     szwak(in)=-szwak(in)
                     sywak(in)=-sywak(in)
                  else
! ---                Pass wake array values
                     jn=rn-npup+nws
                     xwak(in)=dist(jn)
                     szwak(in)=asigz(jn)
                     sywak(in)=asigy(jn)
                     drwak(in)=rtpiby2*dsz(jn)
                  endif
               enddo
            endif
         else
! ---       Fill only those elements used
            nwak=nptot
            if(npup.GT.0) delta_x=xwak1/FLOAT(npup)
            do in=2,nptot
               if(in.LE.npup) then
! ---             Compute ambient sigmas
! !!!             xtraj=in*dx
                  xtraj=(in-1)*delta_x
                  xkm=xtraj*fm2km+xz0km
                  ts=xkm/ubkps
                  call SIGTZ(zero,xkm,ts,Hb,szwak(in),dtdum,dxdum)
                  xkm=xtraj*fm2km+xy0km
                  ts=xkm/ubkps
                  call SIGTY(zero,xkm,ts,sywak(in),dtdum,dxdum)
                  xwak(in)=xtraj
                  drwak(in)=zero
! ---             Upwind of xi-entry so make sigmas negative to
! ---             indicate that BID needs to be added in WAKE_ADDBID
                  szwak(in)=-szwak(in)
                  sywak(in)=-sywak(in)
               else
! ---             Pass wake array values
                  jn=in-npup+nws
                  xwak(in)=dist(jn)
                  szwak(in)=asigz(jn)
                  sywak(in)=asigy(jn)
                  drwak(in)=rtpiby2*dsz(jn)
               endif
            enddo
         endif
! ---    Repeat procedure to store DRWAKISC data (as in ISC-PRIME)
! ---    starting at xi
! ---    Place initial values into first element
         xwakisc(1)=xi+xbadj
         drwakisc(1)=zero
         if(npw.GE.mxrise) then
! ---       Sample a subset of the nptot points
            nwakisc=mxrise
            xwakisc(nwakisc)=dist(np)
            drwakisc(nwakisc)=rtpiby2*dsz(np)
            if(npw.LE.2*mxrise) then
! ---          Fill elements with nearest values
               deln=FLOAT(npw)/FLOAT(nwakisc)
               do in=2,nwakisc-1
                  jn=in*deln+nws
                  xwakisc(in)=dist(jn)
                  drwakisc(in)=rtpiby2*dsz(jn)
               enddo
            else
! ---          Use sliding step-size to sample nearfield more frequently
               deln=2.*FLOAT(npw-mxrise)/FLOAT(mxrise*(mxrise-1))
               rn=one
               do in=2,nwakisc-1
                  rn=rn+one+(in-1)*deln
                  jn=rn+nws
                  xwakisc(in)=dist(jn)
                  drwakisc(in)=rtpiby2*dsz(jn)
               enddo
            endif
         else
! ---       Fill only those elements used
            nwakisc=npw
            do in=2,npw
               inp=in+nws
               xwakisc(in)=dist(inp)
               drwakisc(in)=rtpiby2*dsz(inp)
            enddo
         endif
      endif
      if(lcav) then
! ---    CAV arrays:
         npc=np-ncs
! ---    Place initial values into first element, using distance 
! ---    downwind of primary source
         xcav(1)=xbc+xbadj
         szcav(1)=szcav0
         sycav(1)=sycav0
         if(npc.GE.mxrise) then
! ---       Sample a subset of the npc points
            ncav=mxrise
            xcav(ncav)=dist(np)
            szcav(ncav)=csigz(np)
            sycav(ncav)=csigy(np)
            if(npc.LE.2*mxrise) then
! ---          Fill elements with nearest values
               deln=FLOAT(npc)/FLOAT(ncav)
               do in=2,ncav-1
                  jn=in*deln+ncs
                  xcav(in)=dist(jn)
                  szcav(in)=csigz(jn)
                  sycav(in)=csigy(jn)
               enddo
            else
! ---          Use sliding step-size to sample nearfield more frequently
               deln=2.*FLOAT(npc-mxrise)/FLOAT(mxrise*(mxrise-1))
               rn=one
               do in=2,ncav-1
                  rn=rn+one+(in-1)*deln
                  jn=rn+ncs
                  xcav(in)=dist(jn)
                  szcav(in)=csigz(jn)
                  sycav(in)=csigy(jn)
               enddo
            endif
         else
! ---       Fill only those elements used
            ncav=npc
            do in=2,npc
               inp=in+ncs
               xcav(in)=dist(inp)
               szcav(in)=csigz(inp)
               sycav(in)=csigy(inp)
            enddo
         endif
      endif
      if(ldbhr) then
         write(io6,*)
         write(io6,*)'----- WAKE_DFSN:        NWAK = ',nwak
         write(io6,*)'Z-dispersion reaches ambient at: ',(xaz+xbadj)
         write(io6,*)'Y-dispersion reaches ambient at: ',(xay+xbadj)
         write(io6,*)'z,y virtual dist (m) - WAKE  = ',xzvwak,xyvwak
         write(io6,*)'z,y virtual dist (m) - CAV   = ',xzvcav,xyvcav
         write(io6,*)'xadj, yadj, xi        (m)    = ',xbadj,ybadj,xi
         write(io6,*)'xbc,distc,xdc         (m)    = ',xbc,distc,xdc
         write(io6,*)'lwak, nws, npw               = ',lwak,nws,npw
         write(io6,*)'      npup, nptot            = ',npup,nptot
         write(io6,*)'lcav, ncs, npc               = ',lcav,ncs,npc
         write(io6,*)
!
! ---    Write the arrays passed back to the calling routine
         write(io6,28)
28       format(/4x,'I',9x,'XWAK',6x,'SZWAK',6x,'SYWAK',6x,'DRWAK',/)
         write(io6,*)' Note:  sigmas before entry are without BID and'
         write(io6,*)'        are negative to flag this'
         write(io6,*)
         do i=1,nwak
            write(io6,32)i,xwak(i),szwak(i),sywak(i),drwak(i)
32          format(i5,3x,4(f10.4,1x))
         enddo
         write(io6,*)
         write(io6,29)
29       format(/4x,'I',9x,'XCAV',6x,'SZCAV',6x,'SYCAV',/)
         do i=1,ncav
            write(io6,33)i,xcav(i),szcav(i),sycav(i)
33          format(i5,3x,3(f10.4,1x))
         enddo
         write(io6,*)
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_siga(cyz,sigold,dxkm,dts,ht,xkm,ts,&
                           sigma,xvkm,tvs)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402          WAKE_SIGA
!
!
! --- PURPOSE: Computes a sigma for ambient growth over an incremental
!              step, and for ambient growth from the source, and
!              returns the larger of the two.
!
! --- INPUTS:
!              cyz - char*1      - Flag for either sigma-y or sigma-z
!                                  'y' = sigma-y
!                                  'z' = sigma-z
!           sigold - real        - Sigma (m) at start of increment
!             dxkm - real        - Incremental distance (km)
!              dts - real        - Incremental time (s)
!               ht - real        - Height (m) above ground (for sigma-z)
!              xkm - real        - Effective travel distance (km) from
!                                  source, including virtual-source
!               ts - real        - Effective travel time (s) from
!                                  source, including virtual-source
!
!     Common block /PARAMS/ variables:
!           MXRISE, MXNW
!
! --- OUTPUT:
!            sigma - real        - Ambient-growth sigma (m)
!             xvkm - real        - Effective travel distance (km) from
!                                  source, including virtual-source
!              tvs - real        - Effective travel time (s) from
!                                  source, including virtual-source
!
!
! --- WAKE_SIGA called by:  WAKE_DFSN
! --- WAKE_SIGA calls    :  SIGTZ, SIGTY,
!----------------------------------------------------------------------
      character*1 cyz
      logical lz
      data zero/0.0/
! --- Define logical for either sigma-z or sigma-y
      if(cyz.EQ.'z') then
         lz=.TRUE.
      elseif(cyz.EQ.'y') then
         lz=.FALSE.
      else
         write(*,*)
         write(*,*)'WAKE_SIGA:  Error!'
         write(*,*)'First argument must be y or z'
         write(*,*)'Found:                ',cyz
         stop
      endif
! --- Compute sigma for an incremental step
      if(LZ) then
         call SIGTZ(sigold,dxkm,dts,ht,sig1,dt1,dx1)
      else
         call SIGTY(sigold,dxkm,dts,sig1,dt1,dx1)
      endif
! --- Compute sigma for total effective transport from source
      if(LZ) then
         call SIGTZ(zero,xkm,ts,ht,sig2,dt2,dx2)
      else
         call SIGTY(zero,xkm,ts,sig2,dt2,dx2)
      endif
! --- Select the larger of the two sigmas
      if(sig1.GT.sig2) then
         sigma=sig1
         xvkm=dx1
         tvs=dt1
      else
         sigma=sig2
         xvkm=dx2
         tvs=dt2
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_dim(x,H,W,R,hwake,wwake)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402           WAKE_DIM
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812              WAKE_DIM
!                D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE: Calculates the vertical height and lateral half-width
!              of a building wake at a distance x from the upwind
!              face of the bldg
!
! --- INPUTS:
!                x - real        - dist (m) from upwind bldg face
!                H - real        - building height (m)
!                W - real        - building width (m)
!                R - real        - wake scaling length (m)
!
! --- OUTPUT:
!
!            hwake - real        - wake height (m)
!            wwake - real        - wake half-width (m)
!
! --- WAKE_DIM called by:  POSITION, WAKE_SIG
! --- WAKE_DIM calls    :  none
!----------------------------------------------------------------------
! --- Wake height from combination of Wilson (1979) and Weil (1996)
! --- limits for uniform approach wind
! --- Set const. used in wake height formula
      data cwkht/1.2/
! --- Misc. constants
      data half/0.5/, third/0.3333333/, zero/0.0/
! --- Scale distance by R
      xbyr=x/R
      xbyr3rd=xbyr**third
! --- Compute match to bdlg height at x=0
      xpbyr=-(H/(cwkht*R))**3
      dxbyr=xbyr-xpbyr
! --- Wake height
      hwake=zero
      if(xbyr.GT.zero) hwake =cwkht*R*dxbyr**third
! --- Wake half-width from empirical fit to Snyder wind tunnel data
      wwake=zero
      if(xbyr.GT.zero) wwake=half*W+(third*R)*xbyR3rd
      return
      end
!----------------------------------------------------------------------
      subroutine wake_drdx(x,drdx)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100430         WAKE_DRDX
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812             WAKE_DRDX
!                J. Scire, D. Strimaitis,  EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Compute the plume radius growth rate in the wake
!               by interpolating among the stored values.
!
! --- UPDATE
! --- V5.7-V6.269   100430  (DGS): Extract drdx from ISC-type arrays
!
! --- INPUTS:
!                X - real       - Downwind distance (m) from source
!
!     Common block /PARAMS/ variables:
!           MXRISE
!     Common block /WAKEDAT/ variables:
!           NWAKISC, XWAKISC(mxrise), DRWAKISC(mxrise)
!
! --- OUTPUT:
!             DRDX - real       - Rate of growth of plume radius at
!                                 downwind distance X from source
!
! --- WAKE_DRDX called by:  NUMRISE
! --- WAKE_DRDX calls:      none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedat.puf'
!
! --- Set growth rate to zero outside interpolation region
! --- (all x outside wake)
      if(x.gt.xwakisc(nwakisc) .OR. x.lt.xwakisc(1))then
         drdx=0.0
      elseif(nwakisc.le.1) then
! ---    Wake turbulence does not alter this plume
         drdx=0.0
      else
         nwkm1=nwakisc-1
         drdx=drwakisc(1)
         do i=nwkm1,1,-1
            if(x.ge.xwakisc(i))then
               ip1=i+1
               drdx=drwakisc(ip1)-(xwakisc(ip1)-x)*&
                 (drwakisc(ip1)-drwakisc(i))/(xwakisc(ip1)-xwakisc(i))
               return
            endif
         enddo
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_fqc(ldb,xbi,xtr,ztr,ntr)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 070629           WAKE_FQC
!
! --- Adapted from
! --- PRIME      Version:  1.0     Level:  970812              WAKE_FQC
!                D. Strimaitis, L. Schulman,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE: Computes the maximum plume mass captured by cavity.
! ---          Plume centerline enters wake boundary before clearing
! ---          downwind end of cavity, so WAKE_FQC is used to find
! ---          point where mass in cavity is greatest.  Note that
! ---          distances are measured from center of upwind face of bldg
!
! --- UPDATE
! --- V5.71-V6.2    070629  (DGS): Initialize xmax, fzmax to 0.0 since
!                                  these are in debug writes, but may 
!                                  not be set
! --- V5.7-V5.71    030528  (DGS): Recast MAX as MAX1 with real args
!
! --- INPUTS:
!              ldb - logical     - Debug output switch
!              xbi - real        - Downwind distance (m) from upwind
!                                  face of bldg to point where plume
!                                  centerline enters wake
!         XTR(ntr) - real        - Downwind distance from source (m)
!         ZTR(ntr) - real        - Plume centerline height (m)
!              NTR - integer     - Number of entries in arrays
!
!     Common block /PARAMS/ variables:
!           MXRISE, MXNW
!     Common block /WAKEDAT/ variables:
!           Hb, Wb, xLb, Rb, xLC, xLR, HR, XBADJ, YBADJ, Ub
!
! --- OUTPUT:
!
!     Common block /WAKEDAT/ variables:
!           FQCAV
!
! --- WAKE_FQC called by:  NUMRISE
! --- WAKE_FQC calls    :  NUMGRAD, WAKE_XSIG, CAVITY_HT, FRGAUSS
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedat.puf'
      REAL XTR(ntr),ZTR(ntr)
      logical ldb, nobid
! --- BID already in wake sigma tables, but might need to be added if
! --- plume is not in a wake (want BID so NOBID is FALSE)
      data nobid/.FALSE./
      fqcav=0.0
      xmax=0.0
      fzmax=0.0
! --- Define range of distances from upwind face of bldg at which to
! --- evaluate plume mass fraction within cavity
      xbstart=AMAX1(xbi,xLb)
      xbend=xLb+xLR
      xrange=0.99*(xbend-xbstart)
      yba=ABS(ybadj)
! --- Distance from source to end of cavity
      xend=xbend+xbadj
! --- Travel time (use transport at top of building)
      tend=xend/Ub
! --- Use at least 5 steps, with a maximum length of 10 m
      nstep=MAX1(5.,(1.+xrange/10.))
      xstep=xrange/FLOAT(nstep)
! --- For vertical plane, compute mass fraction below Hb at the
! --- downwind end of the cavity.  This allows the influence of plume
! --- rise to continue lifting plume mass out of the influence of
! --- of the cavity structure for strongly buoyant releases.
! --- Use this value as a cap to fractz.
      call NUMGRAD(xend,xtr,ztr,ntr,zplm)
      call WAKE_XSIG(xend,tend,0.0,nobid,sz,sy,szc,syc)
      call FRGAUSS(zplm,sz,Hb,-Hb,fractz0)
      do is=0,nstep
         xb=xbstart+is*xstep
         x=xb+xbadj
         time=x/Ub
         call NUMGRAD(x,xtr,ztr,ntr,zplm)
         call CAVITY_HT(Hb,Wb,xLb,Rb,xLC,xLR,HR,xb,zcav,ycav)
         call WAKE_XSIG(x,time,0.0,nobid,sz,sy,szc,syc)
         call FRGAUSS(zplm,sz,zcav,-zcav,fractz)
         call FRGAUSS(yba,sy,ycav,-ycav,fracty)
      if(ldb) then
         write(io6,*)'WAKE_FQC:'
         write(io6,*)'x,nobid,sz,sy,szc,syc = ',x,nobid,sz,sy,szc,syc
         write(io6,*)'zplm,sz,zcav,fractz   = ',zplm,sz,zcav,fractz
      endif
         fz=AMIN1(fractz,fractz0)
         fract=fz*fracty
         if(fract.GT.fqcav) then
            fqcav=fract
            xmax=x
            fzmax=fz
         endif
      enddo
! --- Additional constraint:  account for fluctuations in cavity
! --- boundary on capturing low-level plumes by imposing a maximum
! --- capture fraction that linearly drops from 1.0 at 85% of the
! --- cavity length, to 0% at the end of the cavity.
      xb85=xLb+0.85*xLR
      if(xbstart.GT.xb85) then
         fq85=AMAX1( 0.0, 1.-(xbstart-xb85)/(xbend-xb85) )
         fqcav=AMIN1(fqcav,fq85)
      endif
      if(ldb) then
         write(io6,*)
         write(io6,*)'WAKE_FQC:'
         write(io6,*)'xbi,xbstart,xbend  = ',xbi, xbstart, xbend
         write(io6,*)'xstep,nstep,fractz0= ',xstep, nstep, fractz0
         write(io6,*)'xb85,xmax          = ',xb85,xmax
         write(io6,*)'fqcav,fzmax        = ',fqcav,fzmax
         write(io6,*)
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine wake_fin(ldbhr,nw,xw,szw,syw,drw,szw0,syw0,&
                          nc,xc,szc,syc,szc0,syc0,fqcav0)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100430          WAKE_FIN
!                D. Strimaitis
!
! --- PURPOSE:  Places wake and cavity data for current source into
!               source arrays
!
! --- UPDATE
! --- V6.268-V6.269 100430 (DGS): Trap negative virtual for initial
!                                 sigma in wake and reset to zero --
!                                 ISC-PRIME logic creates a negative
!                                 virtual when the sigma at the end
!                                 of the wake is less than ambient
! --- V5.7-V6.268  100308  (DGS): Sigma upwind of xi is negative to
!                                 signal check that it is > BID, so
!                                 take ABS() before printing
!
! --- INPUTS:
!
!      LDBHR - logical - Debug output written when .TRUE.
!
!     Common block /WAKEDAT/ variables:
!           NWAK, XWAK(mxrise), SZWAK(mxrise), SYWAK(mxrise),
!           DRWAK(mxrise), XZVWAK, XYVWAK,
!           NCAV, XCAV(mxrise), SZCAV(mxrise), SYCAV(mxrise),
!           XZVCAV, XYVCAV, FQCAV, Ub, ubkps
!
!     Parameters:
!           MXRISE
!
! --- OUTPUT:
!
!          NW - integer - Number of entries in wake arrays
!  XW(mxrise) - real    - Downwind distance (m) from primary source
! SZW(mxrise) - real    - Wake sigma-z (m) at position XW
! SYW(mxrise) - real    - Wake sigma-y (m) at position XW
! DRW(mxrise) - real    - Plume growth rate at position XW expressed
!                         as d/dx(plume radius) for equivalent top-hat
!        SZW0 - real    - Initial sigma-z (m) that produces the wake
!                         SZW(NW) using current ambient diffusion
!        SYW0 - real    - Initial sigma-y (m) that produces the wake
!                         SYW(NW) using current ambient diffusion
!          NC - integer - Number of entries in cavity arrays
!  XC(mxrise) - real    - Downwind distance (m) from cavity source
! SZC(mxrise) - real    - Cavity sigma-z (m) at position XC
! SYC(mxrise) - real    - Cavity sigma-y (m) at position XC
!        SZC0 - real    - Initial sigma-z (m) that produces the cavity
!                         SZC(NC) using current ambient diffusion
!        SYC0 - real    - Initial sigma-y (m) that produces the cavity
!                         SYC(NC) using current ambient diffusion
!      FQCAV0 - real    - Fraction of plume mass captured by cavity
!
!     Common block /WAKEDAT/ variables:
!           CBYQCAV
!
! --- WAKE_FIN called by:  POINTS1, POINTS2
! --- WAKE_FIN calls:      SIGTZ, SIGTY
!----------------------------------------------------------------------
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'wakedat.puf'
! --- Declare output arrays
      real xw(mxrise),szw(mxrise),syw(mxrise),drw(mxrise)
      real xc(mxrise),szc(mxrise),syc(mxrise)
      logical ldbhr
      data zero/0.0/
      data pi/3.1415927/
      data fm2km/0.001/
      data fsmall/0.000001/
! --- Pass contents of sigma arrays for wake region
      nw=nwak
      do i=1,nw
         xw(i)=xwak(i)
         szw(i)=szwak(i)
         syw(i)=sywak(i)
         drw(i)=drwak(i)
      enddo
      nwp1=nw+1
      do i=nwp1,mxrise
         xw(i)=zero
         szw(i)=zero
         syw(i)=zero
         drw(i)=zero
      enddo
! --- Pass contents of sigma arrays for cavity region, with distance
! --- referenced to the cavity source location rather than the primary
      nc=ncav
      do i=1,nc
         xc(i)=xcav(i)-xcav(1)
         szc(i)=szcav(i)
         syc(i)=sycav(i)
      enddo
      ncp1=nc+1
      do i=ncp1,mxrise
         xc(i)=zero
         szc(i)=zero
         syc(i)=zero
      enddo
! --- Pass fraction of mass captured in cavity
      fqcav0=fqcav
! --- Pass centerline Chi/Q in cavity into /WAKEDAT/
      if(fqcav0.LT.fsmall) then
         cbyqcav=zero
      else
         cbyqcav=fqcav0/(pi*Ub*szc(1)*syc(1))
      endif
! !!! DEBUG
!      write(*,*)'WAKE_FIN: Primary Source xzvwak,xyvwak= ',xzvwak,xyvwak
!      write(*,*)'WAKE_FIN:  Cavity Source xzvcav,xyvcav= ',xzvcav,xyvcav
! --- Assign effective sigmas at the source which reproduce the
! --- sigmas at the end of the wake region for the dispersion
! --- conditions at the time of release (clip negative at zero)
! --- Primary source
! ------------------
      szw0=0.0
      syw0=0.0
      if(xzvwak.GE.0.0) then
         xzvkm=xzvwak*fm2km
         tzv=xzvkm/ubkps
         call SIGTZ(zero,xzvkm,tzv,Hb,szw0,tdum,xdum)
      endif
      if(xyvwak.GE.0.0) then
         xyvkm=xyvwak*fm2km
         tyv=xyvkm/ubkps
         call SIGTY(zero,xyvkm,tyv,syw0,tdum,xdum)
      endif
! --- Cavity source
! -----------------
      xzvkm=xzvcav*fm2km
      tzv=xzvkm/ubkps
      call SIGTZ(zero,xzvkm,tzv,Hb,szc0,tdum,xdum)
      xyvkm=xyvcav*fm2km
      tyv=xyvkm/ubkps
      call SIGTY(zero,xyvkm,tyv,syc0,tdum,xdum)
! --- Debug output
! --- Take ABS of tabulated sigmas because they are negative
! --- for x<xi to identify prewake values
      if(ldbhr) then
         write(io6,*)
         write(io6,*) 'WAKE_FIN: transition sy,sz = ',syw(nw),szw(nw)
         write(io6,*) '          xw,xyvwak,xzvwak = ',xw(nw),xyvwak,&
                                                      xzvwak
         write(io6,*)
         write(io6,*) 'Compare with ambient sigmas'
         write(io6,*) '    x (m)   szwak   szamb   sywak   syamb'
         do i=1,nw
            xwkm=xw(i)*0.001
            tw=xwkm/ubkps
            call SIGTZ(zero,xwkm,tw,Hb,szamb,tdum,xdum)
            call SIGTY(zero,xwkm,tw,syamb,tdum,xdum)
            write(io6,'(5f8.2)') xw(i),ABS(szw(i)),szamb,&
                                 ABS(syw(i)),syamb
         enddo
         write(io6,*)
         write(io6,*)'WAKE_FIN:  nwak,ncav =',nw,nc
         write(io6,*)'Initial (primary) syw0,szw0 = ',syw0,szw0
         write(io6,*)'Initial (cavity)  syc0,szc0 = ',syc0,szc0
         write(io6,*)'cbyqcav = fqcav0/(pi*Ub*szc(1)*syc(1))'
         write(io6,*)'cbyqcav,fqcav0,Ub = ',cbyqcav,fqcav0,Ub
         write(io6,*)
         write(io6,*)'Wake X,SigY,SigZ  ---  Cavity X,SigY,SigZ'
         do ii=1,nw
            write(io6,'(i6,6f10.2)') ii,xw(ii),ABS(syw(ii)),&
                                  ABS(szw(ii)),xc(ii),syc(ii),szc(ii)
         enddo
         write(io6,*)
         write(io6,*)
      endif
      return
      end
