      subroutine wake_ini(ldbhr,kst,irural,dsbh,dsbw,dsbl,&
                          xadj,yadj,ubldg,ustack,&
                          svbldg,swbldg,idopty,idoptz)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402          WAKE_INI
!
! --- Adapted from
! --- PRIME      Version:  1.0     Level:  970812              WAKE_INI
!                D. Strimaitis, EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Refreshes variables in /wakedat/ and /wakedfsn/ commons
!
! --- UPDATE
! --- V5.7          030402  (DGS): assign current values of ambient
!                                    turbulence intensity
!                                  pass CALPUFF integer for RURAL/URBAN
!                                  initialize virtuals & cavity fraction
!
! --- INPUTS:
!
!      LDBHR - logical - Debug output written when .TRUE.
!        KST - integer - PG stability class
!     IRURAL - integer - Rural(0) or urban(1) flag
!       DSBH - real    - Effective building height (m)
!       DSBW - real    - Effective building width (m) across flow
!       DSBL - real    - Effective building length (m) along flow
!       XADJ - real    - Distance (m) from source to upwind face of bldg
!                        along flow
!       XADJ - real    - Distance (m) from source to center of upwind
!                        face of bldg across flow
!      UBLDG - real    - Wind speed (m/s) at top of building
!     USTACK - real    - Wind speed (m/s) at release height
!     SVBLDG - real    - Turbulence sigma-v (m/s) at top of building
!     SWBLDG - real    - Turbulence sigma-w (m/s) at top of building
!     IDOPTY - real    - CALPUFF dispersion option for sigma-y
!     IDOPTZ - real    - CALPUFF dispersion option for sigma-z
!
!     Common block /WAKEDFSN/ variables:
!           rurliz,rurliy,urbniz,urbniy
!
!     Parameters:
!           IO6, MXRISE
!
! --- OUTPUT:
!
!     Common block /WAKEDFSN/ variables:
!           AMBIZ, AMBIY
!     Common block /WAKEDAT/ variables:
!           HB,WB,XLB,RB,HR,XLR,XLC,XBADJ,YBADJ,
!           NWAK, XWAK, SZWAK, SYWAK, DRWAK, NCAV, XCAV, SZCAV, SYCAV,
!           UB, UBKPS, URH, LRURL, ISTAB
!           XZVWAK,XYVWAK,XZVCAV,XYVCAV,FQCAV,CBYQCAV
!
! --- WAKE_INI called by:  POINTS1, POINTS2
! --- WAKE_INI calls:      WAKE_SCALES
!----------------------------------------------------------------------
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'wakedfsn.puf'
      include 'wakedat.puf'
      logical ldbhr
      data zero/0.0/, fm2km/0.001/
! --- Transfer arguments to /wakedat/ variables
      istab =kst
      Hb    =dsbh
      Wb    =dsbw
      xLb   =dsbl
      xbadj =xadj
      ybadj =yadj
      Ub    =ubldg
      ubkps =ubldg*fm2km
      Urh   =ustack
      if(irural.EQ.0) then
         lrurl=.TRUE.
      elseif(irural.EQ.1) then
         lrurl=.FALSE.
      else
         write(io6,*) 'WAKE_INI:  invalid value for rural/urban flag:'
         write(io6,*) '           IRURAL (0 or 1) = ',irural
         write(*,*)
         stop 'Halted in WAKE_INI -- see list file'
      endif
! --- Compute wake dimensions and related parameters
      call WAKE_SCALES(ldbhr)
! --- Reset contents of sigma arrays for wake region (primary and
! --- cavity sources)
      nwak=1
      xwak(1)=zero
      szwak(1)=zero
      sywak(1)=zero
      drwak(1)=zero
      ncav=1
      xcav(1)=zero
      szcav(1)=zero
      sycav(1)=zero
! --- Reset virtuals and cavity fraction
      xzvwak=zero
      xyvwak=zero
      xzvcav=zero
      xyvcav=zero
      fqcav=zero
      cbyqcav=zero
! --- Set ambient turb. intensities (at building height) in /WAKEDFSN/
      if(idopty.LT.3 .OR. idopty.GT.4) then
! ---    Compute from turbulence velocities at building-height
         ambiy=svbldg/ubldg
      else
! ---    Use defaults for distance-based dispersion curves
         if(lrurl) then
            ambiy=rurliy(istab)
         else
            ambiy=urbniy(istab)
         endif
      endif
      if(idoptz.LT.3 .OR. idoptz.GT.4) then
! ---    Compute from turbulence velocities at building-height
         ambiz=swbldg/ubldg
      else
! ---    Use defaults for distance-based dispersion curves
         if(lrurl) then
            ambiz=rurliz(istab)
         else
            ambiz=urbniz(istab)
         endif
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_scales(ldbhr)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402        WAKE_SCALES
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812           WAKE_SCALES
!                L. Schulman, D. Strimaitis,  J. Scire,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Calculates length scale and wake dimensions
!
! --- INPUTS:
!            LDBHR - logical           - Control variable for debug
!                                        write statements
!
!     Common block /WAKEDAT/ variables:
!           Hb,Wb,xLb
!     Parameters:  IO6
!
! --- OUTPUT:
!
!     Common block /WAKEDAT/ variables:
!           Rb,HR,xLR,xLC
!
! --- WAKE_SCALES called by:  WAKE_INI
! --- WAKE_SCALES calls:      none
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include commons
      include 'wakedat.puf'
      logical ldbhr
! --- Set misc. constants
      data third/0.3333333/, twoby3/0.6666667/
      if(HB.le.0.) then
! ---    No building
         Rb=0.0
         Hb=0.0
         xLR=0.0
         xLC=0.0
      else
!
! ---    Set ratios
         rw = Wb/Hb
         rl = xLb/Hb
! ---    Fackrell limits on aspect ratio L/H
         if(rl .lt. 0.3) rl=0.3
         if(rl .gt. 3.0) rl=3.0
!
! ---    Length scale R --- Wilson
! ---    Wilson limits for length scale R
! ---    H/W or W/H not greater than 8 --  already behaves as 2-D
         HH=Hb                    ! only modify H to calculate R
         WW=Wb                    ! only modify W to calculate R
         if(HH.gt.8.0*WW)HH=8.0*WW
         if(WW.gt.8.0*HH)WW=8.0*HH
         Rb= (amin1(HH,WW)**twoby3) * (amax1(HH,WW)**third)
!
! ---    Reattachment for LC < L
         xLC = 0.9*Rb
!
! ---    Recirculation cavity length---Fackrell
! ---    Modify Fackrell for W/H less than 1 by weakening dependence
! ---    on L/H.  Snyder found that cavity did not increase in length
! ---    as W/H = L/H decreased from 1 to 0.33.
! ---    Let L/H dependence decrease from Fackrell dependence at W/H=1
! ---    to no dependence at W/H=0.33.
         explh = 0.3
         if(rw .lt. 1.) explh=AMAX1(0.0,0.3*(rw-0.33)/0.67)
         xLR = 1.8*Wb/(rl**explh*(1.+0.24*rw))
!
! ---    Maximum cavity height  (Wilson,ASHRAE):
         HR = Hb+0.22*Rb
      endif
! --- Write the results
      if(ldbhr)then
         write(io6,*)
         write(io6,*)'WAKE_SCALES inputs: '
         write(io6,*)'   HB    = ',Hb,' (m)'
         write(io6,*)'   WB    = ',Wb,' (m)'
         write(io6,*)'   LB    = ',xLb,' (m)'
         write(io6,*)
         write(io6,*)'WAKE_SCALES output: '
         write(io6,*)'   Scale length (R)               = ',Rb
         write(io6,*)'   Max. cavity height (HR)        = ',HR
         write(io6,*)'   Length of downwind cavity (LR) = ',xLR
         write(io6,*)'   Length of roof cavity (LC)     = ',xLC
         write(io6,*)
      endif
!
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_sig(x,xd,xold,turbz,turby,szold,syold,&
                          H,W,R,zk,yk,sz,sy,dsz)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402           WAKE_SIG
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812              WAKE_SIG
!                D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE: Calculates sigmas and d(sigma)/dx within the wake
!              at a distance x from the upwind face of the bldg,
!              prior to the ambient growth regime, for a "small"
!              distance increment
!
! --- INPUTS:
!                x - real        - dist (m) from upwind bldg face
!               xd - real        - dist (m) at which PDF growth ends
!             xold - real        - starting x of this step (m)
!     turbz, turby - real        - current turbulence intensities
!     szold, syold - real        - sigmas (m) at start of step
!   htwake, hwwake - real        - height and half-width (m) of wake
!           zk, yk - real        - matching constants for PDF transition
!
! --- OUTPUT:
!
!           zk, yk - real        - matching constants for PDF transition
!           sz, sy - real        - sigmas (m) at end of step
!              dsz - real        - d(sigmaz)/dx over step
!
! --- WAKE_SIG called by:  WAKE_DFSN
! --- WAKE_SIG calls    :  WAKE_DIM
!----------------------------------------------------------------------
! --- Wake height from combination of Wilson (1979) and Weil (1996)
! --- limits for uniform approach wind
! --- Misc. constants
      data two/2.0/
! --- Get wake dimensions
      call WAKE_DIM(x,H,W,R,htwake,hwwake)
! --- Use full width of the wake to scale lateral diffusivity
      fwwake=two*hwwake
      delx=x-xold
      xstepi=1./delx
      if(x.LT.xd) then
! ---    Pure PDF Form
         dsz=turbz
         sz=szold + delx*turbz
         sy=syold + delx*turby
      elseif(xold.GT.xd) then
! ---    Pure Wake Diffusivity Form
         dsz2=zk*turbz*htwake
         dsy2=yk*turby*fwwake
         sz=SQRT(szold**2+delx*dsz2)
         sy=SQRT(syold**2+delx*dsy2)
         dsz=(sz-szold)*xstepi
      else
! ---    Transition from PDF to Diffusivity Form
! ---    To end of PDF:
         delx=xd-xold
         sigzd=szold + delx*turbz
         sigyd=syold + delx*turby
         zk=two*sigzd/htwake
         yk=two*sigyd/fwwake
! ---    Beyond end of PDF:
         delx=x-xd
         dsz2=zk*turbz*htwake
         dsy2=yk*turby*fwwake
         sz=SQRT(sigzd**2+delx*dsz2)
         sy=SQRT(sigyd**2+delx*dsy2)
         dsz=(sz-szold)*xstepi
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_turb(x,L,R,tiz,tiy)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402          WAKE_TURB
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812             WAKE_TURB
!                L. Schulman, D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE: Calculates turbulence intensity as a function of
!              location within the wake from modified Weil (1996)
!              analytical expressions
!
! --- UPDATE
! --- V5.7          030402  (DGS): ambient turbulence intensity set in
!                                  /WAKEDFSN/
!
! --- INPUTS:
!                x - real        - distance (m) from upwind bldg wall
!                L - real        - dist (m) of downwind bldg wall from
!                                  upwind bldg wall
!                R - real        - wake scaling length (m)
!
!     Common block /WAKEDFSN/ variables:
!           wiz0,wiy0,wfz,wfy,
!           dua_ua,xdecay,xdecayi,
!           ambiy,ambiz
!
! --- OUTPUT:
!
!              tiz - real        - turbulence intensity sigw/u
!              tiy - real        - turbulence intensity sigv/u
!
! --- WAKE_TURB called by:  WAKE_DFSN
! --- WAKE_TURB calls    :  none
!----------------------------------------------------------------------
!
      include 'wakedfsn.puf'
      real L
! --- Misc. constants
      data one/1.0/, zero/0.0/
! --- Compute asymptotic turbulence intensity in far wake
      fariz=AMIN1(wiz0,ambiz)
      fariy=AMIN1(wiy0,ambiy)
! --- Compute turbulence intensity at position downwind of bldg
      xmL=AMAX1(zero,x-L)
      xfac=one/(((xmL+R)/R)**xdecay-dua_ua)
      tiz=fariz*(one+((wfz*wiz0/fariz-one)+dua_ua)*xfac)
      tiy=fariy*(one+((wfy*wiy0/fariy-one)+dua_ua)*xfac)
! --- Interpolate turbulence intensity if over roof of bldg
      if(x.LT.L) then
         xfrac=x/L
         tiz=ambiz+xfrac*(tiz-ambiz)
         tiy=ambiy+xfrac*(tiy-ambiy)
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_u(ldb,x,y,z,ubyua,dufac)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402             WAKE_U
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  990726 (99207)        WAKE_U
!                D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE: Calculates speed ratio u(wake)/u(ambient) as a function
!              of location within the wake
!
!              Modified by B. de Foy, 26th July 1999,
!              To set fmin as a minimum value for ubyua
!
! --- INPUTS:
!              ldb - logical     - flag for debug output
!                x - real        - downwind distance (m) from upwind
!                                  bldg wall
!                y - real        - crosswind distance (m) from center of
!                                  upwind bldg wall
!                z - real        - height (m) above ground
!
!     Common block /PARAMS/ variables:
!           MXRISE, MXNW
!     Common block /WAKEDAT/ variables:
!           Hb, Wb, xLb, Rb, xLR
!     Common block /WAKEDFSN/ variables:
!           dua_ua,xdecay,xdecayi
!
! --- OUTPUT:
!
!            ubyua - real        - U(x,z)/Ua speed in wake scaled by
!                                  ambient speed
!            dufac - real        - Gradient in speed factor above
!                                  Zcav
!
! --- WAKE_U called by:  NUMRISE, WAKE_DBG
! --- WAKE_U calls    :  CAVITY_HT, WAKE_DIM
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedfsn.puf'
      include 'wakedat.puf'
      logical ldb
! --- Misc. constants
      data two/2.0/, one/1.0/, zero/0.0/
      data fmin/0.01/
! --- Compute cavity height above ground, and width
      call CAVITY_HT(Hb,Wb,xLb,Rb,xLC,xLR,HR,x,zcav,ycav)
! --- Compute far wake height above ground, and width
      call WAKE_DIM(x,Hb,Wb,Rb,hwake,wwake)
! --- Return "null" values if point is outside wake
      yabs=ABS(y)
      ubyua=one
      dufac=zero
      if(z.GE.hwake .OR. yabs.GE.wwake) return
! --- Adjust "base" speed deficit dua_ua if lateral position is
! --- beyond bldg width projection, but within the wake
      ymin=AMAX1(0.5*Wb,wwake-Rb/3.)
      du_ua=dua_ua
      ydiff=wwake-ymin
      if(yabs.GT.ymin .AND. ydiff.GT.zero) then
         du_ua=dua_ua*(one-(yabs-ymin)/ydiff)
      endif
! --- Scale speed deficit (Ua-U)/Ua =  du_ua in wake for
! --- position x downwind of bldg face
      xmL=AMAX1(zero,x-xLb)
      du_ua=du_ua*((xmL+Rb)/Rb)**(-xdecay)
! --- Interpolate factor if over roof of bldg (linear)
      if(x.LT.xLb) then
         xfrac=x/xLb
         du_ua=xfrac*du_ua
      endif
! --- Compute speed factor Ucav/Ua at top of cavity
! --- Assume that speed is constant below ZCAV, and increases linearly
! --- with height to ambient speed at HWAKE
      ucbyua=AMAX1(zero,(one-two*hwake*du_ua/(hwake+zcav)))
! --- Compute gradient in speed factor (zero below Zcav)
      dufac=zero
      if(z.GT.zcav) then
         dufac=(one-ucbyua)/(hwake-zcav)
      endif
! --- Compute speed factor U/Ua at height z
      zz=AMIN1(z,hwake)
! --- Ensure fmin as lower bound for ubyua
      ubyua=AMAX1(fmin,(ucbyua+dufac*(zz-zcav)))
      if(ldb) then
        write(io6,*)'WAKE_U         '
        write(io6,*)'       x,y,z = ',x,y,z
        write(io6,*)'hwake, zcav  = ',hwake, zcav
        write(io6,*)'wwake, ymin  = ',wwake, ymin
        write(io6,*)'du_ua, ucbyua= ',du_ua, ucbyua
        write(io6,*)'ubyua, dufac = ',ubyua,dufac
        write(io6,*)
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine wake_xa(L,R,xaz,xay)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402            WAKE_XA
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  980310               WAKE_XA
!                D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE: Calculates the distance from the upwind face of the
!              building to the point at which the turbulence intensity
!              in the wake approaches that in the ambient flow.
!
!              Final distances are limited to "xbyrmax" scale-lengths
!              (R) set in prime1, measured from downwind bldg face
!
! --- UPDATE
! --- V5.7          030402  (DGS): ambient turbulence intensity set in
!                                  /WAKEDFSN/
!
! --- INPUTS:
!                L - real        - dist (m) of downwind bldg wall from
!                                  upwind bldg wall
!                R - real        - wake scaling length (m)
!
!     Common block /WAKEDFSN/ variables:
!           afac,xbyrmax,wiz0,wiy0,wfz,wfy,
!           dua_ua,xdecay,xdecayi,
!           ambiy,ambiz
!
! --- OUTPUT:
!
!              xaz - real        - distance (m) from upwind bldg wall
!                                  at which wake turbulence Iz = ambient
!              xay - real        - distance (m) from upwind bldg wall
!                                  at which wake turbulence Iy = ambient
!
! --- WAKE_XA called by:  WAKE_DFSN
! --- WAKE_XA calls    :  none
!----------------------------------------------------------------------
!
      include 'wakedfsn.puf'
      real L
! --- Misc. constants
      data one/1.0/
! --- Compute asymptotic turbulence intensity in far wake
      fariz=AMIN1(wiz0,ambiz)
      fariy=AMIN1(wiy0,ambiy)
! --- Define the turbulence intensity at the transition point
      farizt=AMAX1(ambiz,afac*fariz)
      fariyt=AMAX1(ambiy,afac*fariy)
! --- Compute leading term
      x0byr=L/R-one
! --- Compute scaled distance at which Iz equals transition Iz
      xaz=x0byr+(dua_ua+(wfz*wiz0-fariz*(one-dua_ua))/&
             (farizt-fariz))**xdecayi
! --- Compute distance at which Iy equals transition Iy
      xay=x0byr+(dua_ua+(wfy*wiy0-fariy*(one-dua_ua))/&
             (fariyt-fariy))**xdecayi
! --- Cap distances
      xbyr=L/R+xbyrmax
      xaz=R*AMIN1(xbyr,xaz)
      xay=R*AMIN1(xbyr,xay)
      return
      end
!----------------------------------------------------------------------
      subroutine wake_xsig(x,t,rise,NOBID,sz,sy,szc,syc)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230         WAKE_XSIG
!
! --- Adapted from
! --- PRIME      Version:  1.0     Level:  970812             WAKE_XSIG
!                D. Strimaitis,  EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Extract sigmas in the wake by interpolating among the
!               stored /WAKEDAT/ values; compute sigmas outside
!               tabulated range using HOST sigma curves with BID or
!               virtual source adjustments
!
! --- UPDATE
! --- V6.42_x1.1 - TNG-7.1.0  141230  (DGS)
!                               : Allow roughness adjustment (MROUGH)
!                                 of RURAL PG sigmas to be used with
!                                 CALMET gridded fields
! --- V6.268-V6.42_x1.1  140521 : Compute primary source sigmas beyond
!                                 tabulated range using last sigma in
!                                 table and release-ht dispersion data
!                                 Replaces using /WAKEDAT/ virtuals
!                                 that used building-top data.
! --- V5.7-V6.268  100108  (DGS): BID is compared to interpolated sigmas
!                                 in tabulated region if the tabulated
!                                 value is negative.  The BID sigma is
!                                 used if it is larger than ABS(table
!                                 value).  BID is already added to
!                                 tabulated sigmas and it is implicit
!                                 in the virtuals used beyond the
!                                 tabulated region.
!
! --- INPUTS:
!                X - real       - Downwind distance (m) from primary
!                                 source
!                T - real       - Travel time (s) from primary source
!             RISE - real       - Gradual plume rise (m)
!            NOBID - logical    - Directs use of buoyancy enhancement
!
!     Common block /PARAMS/ variables:
!           MXRISE
!     Common block /WAKEDAT/ variables:
!           UBKPS,
!           NWAK, XWAK(mxrise), SZWAK(mxrise), SYWAK(mxrise),
!           XZVWAK, XYVWAK
!           NCAV, XCAV(mxrise), SZCAV(mxrise), SYCAV(mxrise),
!           XZVCAV, XYVCAV
!
!      common/WAKECSIG/idopty_bt,idoptz_bt,iru_bt,uavg_bt,kst_bt,
!     &                el_bt,bvf_bt,tsigv_bt,tsigw_bt,symin_bt,
!     &                szmin_bt,zht_bt,zmix_bt,
!     &                idopty_rh,idoptz_rh,iru_rh,uavg_rh,kst_rh,
!     &                el_rh,bvf_rh,tsigv_rh,tsigw_rh,symin_rh,
!     &                szmin_rh,zht_rh,zmix_rh,
!     &                ix_btrh,iy_btrh,md_btrh
!
! --- OUTPUT:
!             SZ - real       - Sigma-z (m) at downwind distance X
!                               due to primary source
!             SY - real       - Sigma-y (m) at downwind distance X
!                               due to primary source
!            SZC - real       - Sigma-z (m) of cavity source at
!                               downwind distance X from primary source
!            SYC - real       - Sigma-y (m) of cavity source at
!                               downwind distance X from primary source
!
! --- WAKE_XSIG called by:  WAKE_DBG, WAKE_FQC, CAV_SRC
! --- WAKE_XSIG calls:      SIGTZ, SIGTY, INTERTAB
!                           SETCSIG
!----------------------------------------------------------------------
! --- Note:  sigmas tabulated in /WAKEDAT/ use distance measured from
! --- the primary source, and start from either the primary source or
! --- the cavity source, depending on which lies further upwind.  The
! --- virtual source distances in /WAKEDAT/ produce the corresponding
! --- sigma at the end of the tabulation, when added to the actual
! --- distance from the corresponding source.
      include 'params.puf'
      include 'wakedat.puf'
      logical NOBID
! --- 6.42_x1.1 140521
      logical LDB
      LDB=.FALSE.
      bid=rise/3.5
      bidsq=bid*bid
! --- Misc. constants
      data zero/0.0/
      data fm2km/0.001/
! --- Convert distance to kilometers
      xkm=x*fm2km
! --- Primary source:
! -------------------
! --- Set virtuals
      xzvkm=xzvwak*fm2km
      xyvkm=xyvwak*fm2km
      tzv=xzvkm/ubkps
      tyv=xyvkm/ubkps
! --- Set total effective distance(km), time(s)
      xzkm=xkm+xzvkm
      xykm=xkm+xyvkm
      tz=t+tzv
      ty=t+tyv
! --- 6.42_x1.1 140521
! --- These will be replaced below with virtuals from
! --- last sigmas and release-ht data if needed ---
! --- Downwind distance from last tabulated value
      delx=x-xwak(nwak)
      delxkm=x*fm2km
! --- Time Increment
      delt=delx/uavg_rh
      if(x.LE.0.0) then
! ---    Report null values (these should never get used!)
         sz=0.0
         sy=0.0
      elseif(nwak.LE.1) then
! ---    Plume never altered by wake turbulence:
! ---    use HOST curves with BID adjustment
! --- 6.42_x1.1 140521
! ---    Set selected data in /CSIGMA/ for RELEASE-HT sigma calls
         if(LDB) then
            write(io6,*)'WAKE_XSIG: nwak.LE.1, dx,dt = ',delx,delt
         endif
         call SETCSIG(idopty_rh,idoptz_rh,iru_rh,uavg_rh,kst_rh,&
                      el_rh,bvf_rh,tsigv_rh,tsigw_rh,symin_rh,&
                      szmin_rh,zht_rh,zmix_rh,ix_btrh,iy_btrh,md_btrh)
! ---    Grow sigmas from end of table with release-ht data
         call SIGTZ(szwak(nwak),delxkm,delt,zht_rh,sz,tzvdum,xzvdum)
         call SIGTY(sywak(nwak),delxkm,delt,sy,tyvdum,xyvdum)
! ---    Restore selected data in /CSIGMA/ for BUILDING-TOP sigma calls
         call SETCSIG(idopty_bt,idoptz_bt,iru_bt,uavg_bt,kst_bt,&
                      el_bt,bvf_bt,tsigv_bt,tsigw_bt,symin_bt,&
                      szmin_bt,zht_bt,zmix_bt,ix_btrh,iy_btrh,md_btrh)
         if(.not.NOBID) then
            sz=SQRT(sz**2+bidsq)
            sy=SQRT(sy**2+bidsq)
         endif
      elseif(x.gt.xwak(nwak)) then
! ---    Point lies downwind of transition to ambient growth:
! --- 6.42_x1.1 140521
! ---    Set selected data in /CSIGMA/ for RELEASE-HT sigma calls
         if(LDB) then
            write(io6,*)'WAKE_XSIG: x>xwak(nwak), dx,dt = ',delx,delt
         endif
         call SETCSIG(idopty_rh,idoptz_rh,iru_rh,uavg_rh,kst_rh,&
                      el_rh,bvf_rh,tsigv_rh,tsigw_rh,symin_rh,&
                      szmin_rh,zht_rh,zmix_rh,ix_btrh,iy_btrh,md_btrh)
! ---    Grow sigmas from end of table with release-ht data
! ---    Tabulated sigmas include BID
         call SIGTZ(szwak(nwak),delxkm,delt,zht_rh,sz,tzvdum,xzvdum)
         call SIGTY(sywak(nwak),delxkm,delt,sy,tyvdum,xyvdum)
! ---    Restore selected data in /CSIGMA/ for BUILDING-TOP sigma calls
         call SETCSIG(idopty_bt,idoptz_bt,iru_bt,uavg_bt,kst_bt,&
                      el_bt,bvf_bt,tsigv_bt,tsigw_bt,symin_bt,&
                      szmin_bt,zht_bt,zmix_bt,ix_btrh,iy_btrh,md_btrh)
      else
! ---    Point lies within range of tabulated values
         call INTERTAB(x,nwak,xwak,i1,i2,factor)
         sz=ABS(szwak(i1))+factor*(ABS(szwak(i2))-ABS(szwak(i1)))
         sy=ABS(sywak(i1))+factor*(ABS(sywak(i2))-ABS(sywak(i1)))
         if(.not.NOBID) then
! ---       Compare with BID since rise is not linear near source
! ---       and interpolation may be inadequate
            if(szwak(i1).LE.0.) sz=AMAX1(sz,bid)
            if(sywak(i1).LE.0.) sy=AMAX1(sy,bid)
         endif
      endif
! --- Cavity source:
! -------------------
! --- Set virtuals
      xzvkm=xzvcav*fm2km
      xyvkm=xyvcav*fm2km
      tzv=xzvkm/ubkps
      tyv=xyvkm/ubkps
! --- Set total effective distance(km), time(s)
      xzkm=xkm+xzvkm
      xykm=xkm+xyvkm
      tz=t+tzv
      ty=t+tyv
      if(ncav.LE.1) then
! ---    No contribution from cavity source (report initial values)
         szc=szcav(1)
         syc=sycav(1)
      elseif(x.lt.xcav(1)) then
! ---    Point lies upwind of cavity region (report initial values)
         szc=szcav(1)
         syc=sycav(1)
      elseif(x.gt.xcav(ncav)) then
! ---    Point lies downwind of transition to ambient growth:
! ---    use HOST curves with the virtual offset
! ---    Compute sigmas
         call SIGTZ(zero,xzkm,tz,Hb,szc,dtdum,dxdum)
         call SIGTY(zero,xykm,ty,syc,dtdum,dxdum)
      else
! ---    Point lies within range of tabulated values
         call INTERTAB(x,ncav,xcav,i1,i2,factor)
         szc=szcav(i1)+factor*(szcav(i2)-szcav(i1))
         syc=sycav(i1)+factor*(sycav(i2)-sycav(i1))
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine cavity_ht(H,W,L,R,LC,LR,HR,x,zcav,ycav)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402          CAVITY_HT
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812             CAVITY_HT
!                L. Schulman, EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Calculates height of cavity envelope as function of x
!
! --- INPUTS:
!                H - real              - Building height above ground
!                W - real              - Projected building width
!                L - real              - Along-wind building length
!                                        building face
!                R - real              - Scale length from H and W
!               LC - real              - Length of roof cavity
!               LR - real              - Length of downwind cavity from
!                                         lee face
!               HR - real              - Maximum cavity height above
!                                         ground
!                x - real              - downwind distances
!
! --- OUTPUT:
!
!          zcav    - real              - cavity height as function of x
!
!          ycav    - real              - cavity half-width as f(x)
! --- CAVITY_HT called by:  PRIME
! --- CAVITY_HT calls:      none
!----------------------------------------------------------------------
!
!
!
      real H,W,L,R,HR,LR,LC
      real x
! --- Initialize
      zcav=0.0
      ycav=0.0
! --- Cavity is not present upwind of bldg or at/beyond L+LR
      if(x.GE.(L+LR)) then
         return
      elseif(x.LT.0.0) then
         return
      endif
!
!     calculate x-y near wake boundary
!
      if(x.ge.0. .and. x.le.R) then
        ycav=(W/2.+R/3.)-(x-R)**2/(3.*R)
      elseif(x.gt.R .and. x.le.(L+LR)) then
        ycav=(W/2.+R/3.)*(1.-((x-R)/(L+LR-R))**2)**0.5
      endif
!     calculate x-z near wake boundary
!
      if(LC .lt. L)then       ! reattachment
!
        if(x.ge.0. .and. x.le.L) then
          zcav=H
        elseif(x.ge.L .and. x.le.(L+LR)) then
          zcav=H*(1.-((x-L)/LR)**2)**0.5
        endif
!
      else                    ! nonreattachment
        if(x.ge.0. .and. x.le.0.5*R) then
        zcav=HR+4.*(x-0.5*R)**2*(H-HR)/(R**2)
        elseif(x.gt.0.5*R .and. x.le.(L+LR)) then
          zcav=HR*(1.-((x-0.5*R)/(L+LR-0.5*R))**2)**0.5
        endif
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine FRGAUSS(hcntr,sigma,h1,h2,fract)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402           FRGAUSS
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812               FRGAUSS
!                J. Scire, D. Strimaitis,  EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Compute the fraction of a Gaussian distribution between
!               two limits
!
! --- INPUTS:
!            HCNTR - real    - Center of Gaussian distribution (m)
!            SIGMA - real    - Standard deviation (m) of the
!                              distribution
!           H1, H2 - real    - Limits between which the distribution
!                              is to be integrated
!
! --- OUTPUT:
!            FRACT - real    - Fraction of the Gaussian distribution
!                              between H1 and H2
!
! --- FRGAUSS called by: WAKE_FQC
! --- FRGAUSS calls:     ERFDIF
!----------------------------------------------------------------------
!
      data sqrt2/1.4142136/,small/1.e-5/
!
! --- Prevent numerical problems with very small sigmas
      s=sqrt2*AMAX1(sigma,small)
!
      z1=(h1-hcntr)/s
      z2=(h2-hcntr)/s
!
      fract=0.5*ABS(ERFDIF(z1,z2))
!
      return
      end
!----------------------------------------------------------------------
      subroutine numgrad(x,xtr,ztr,ntr,zeff)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402           NUMGRAD
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812               NUMGRAD
!                J. Scire,  EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Compute the effective gradual plume height by
!               interpolation of the stored values.  Effective
!               plume height is the stack height + plume rise.
!
! --- INPUTS:
!                X - real       - Downwind distance (m)
!         XTR(ntr) - real array - Array of stored downwind distances (m)
!         ZTR(ntr) - real array - Array of stored effective plume height
!                                 at each downwind distance
!              NTR - integer    - Number of stored values in XTR, ZTR
!
! --- OUTPUT:
!             ZEFF - real       - Effective plume height (m) at
!                                 downwind distance X
!
! --- NUMGRAD called by:  PHEFF
! --- NUMGRAD calls:      none
!----------------------------------------------------------------------
!
      real xtr(ntr),ztr(ntr)
!
      if(x.ge.xtr(ntr))then
         zeff=ztr(ntr)
      else
         ntrm1=ntr-1
         zeff=ztr(1)
         do i=ntrm1,1,-1
            if(x.ge.xtr(i))then
               ip1=i+1
               zeff=ztr(ip1)-(ztr(ip1)-ztr(i))*(xtr(ip1)-x)/&
                (xtr(ip1)-xtr(i))
               return
            endif
         enddo
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine position(x,y,z,ipositn)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402           POSITION
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812              POSITION
!                L. Schulman, J. Scire, D. Strimaitis,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Identifies if (x,y,z) location is in the building,
!               in the near wake, in the far wake, or outside.
!               IPOSITN is set to: 1 if within the bldg.
!                                  2 if within the near wake
!                                  3 if within the far wake
!                                  4 if outside wake region
!
! --- INPUTS:
!                x - real              - downwind distance from center
!                                        of upwind face of bldg
!                y - real              - crosswind distance from center
!                                        of upwind face of bldg
!                z - real              - height above ground
!
!     Common block /WAKEDAT/ variables:
!           Hb,Wb,xLb,Rb,HR,xLR,xLC
!
! --- OUTPUT:
!
!          ipositn - integer           - 1 if (x,y,z) within building
!                                        2 if location within near wake
!                                        3 if location within far wake
!                                        4 if location outside
!
! --- POSITION called by:  NUMRISE, PCALC (HOST subroutine)
! --- POSITION calls:      WAKE_DIM
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
! --- Include commons
      include 'wakedat.puf'
      data zero/0.0/, third/0.333333/, half/0.5/
! --- Define a fractional boundary just inside building
      data skin/0.99998/
! --- Initialize
      iposy=4
      iposz=4
      ipositn=4
! --- Screen out any cases without building
      if(Hb.le.zero) return
! --- Screen out positions upwind of building (and on windward face)
      if(x.le.zero) return
! --- Set y positive for calculations
      ypos=ABS(y)
! --- Set selected length scale products
      rby2=half*Rb
      rby3=third*Rb
      wby2=half*Wb
! --- Set ipositn to 1 if location within building
      xtest=x/xLB
      ytest=ypos/wby2
      ztest=z/Hb
      if(xtest.lt.skin .and. ztest.lt.skin .and. ytest.lt.skin) then
         ipositn=1
         return
      endif
! --- Calculate if location below height of near wake boundary
      if(xLC .lt. xLb)then
! ---    Reattachment
         if(x.lt.xLb) then
! ---       Cavity height equal to building height
            zcav=Hb
            if(z .le. zcav) iposz=2
         elseif(x.lt.(xLb+xLR)) then
! ---       Cavity height is ellipse with a=LR and b=H
            zcav=Hb*SQRT(1.-((x-xLb)/xLR)**2)
            if(z .le. zcav) iposz=2
         endif
      else
! ---    No reattachment
         if(x.le.rby2) then
! ---       Cavity height is parabola with vertex at height MAX(0.5R,HR)
! ---       and passing thru upwind building edge (0,H)
            zcav=HR+4.*(x-rby2)**2*(Hb-HR)/(Rb**2)
            if(z .le. zcav) iposz=2
         elseif(x.lt.(xLb+xLR)) then
! ---       Cavity height is ellipse with a=LR+L-0.5R and b=HR
            zcav=HR*SQRT(1.-((x-rby2)/(xLb+xLR-rby2))**2)
            if(z .le. zcav) iposz=2
         endif
      endif
! --- Calculate x-y near wake boundary
      if(x.le.Rb) then
! ---    Cavity width is parabola with vertex @ width MAX(R,W/2+R/3)
! ---    and passing thru upwind building edge (0,W/2)
         ycav=(wby2+rby3)-(x-Rb)**2/(3.*Rb)
         if(ypos .le. ycav) iposy=2
      elseif(x.lt.(xLb+xLR)) then
! ---    Cavity width is ellipse with a=W/2+R/3 and b=LR+L-R
         ycav=(wby2+rby3)*SQRT(1.-((x-Rb)/(xLb+xLR-Rb))**2)
         if(ypos .le. ycav) iposy=2
      endif
! --- Set ipositn to 2 if (x,y,z) location within near wake
      if( iposz .eq. 2 .and. iposy .eq. 2) ipositn=2
! --- Test for position in far wake if still 4
      if(ipositn.EQ.4) then
         call WAKE_DIM(x,Hb,Wb,Rb,zwake,ywake)
         if(z.le.zwake .AND. ypos.le.ywake) ipositn=3
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine zstream(H,W,L,R,LR,HR,x,y,z,dzdx)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402           ZSTREAM
!
! --- Adopted from
! --- PRIME      Version:  1.0     Level:  970812               ZSTREAM
!                L. Schulman, J. Scire,   EARTH TECH
!                Prepared for EPRI under contract WO3527-01
!
! --- PURPOSE:  Estimates the local mean slope of streamlines in the
!               vicinity of buildings.  The local slope is equal to
!               w/u, where w is the local mean vertical velocity and
!               u the local mean longitudinal velocity.  For modeling
!               plume rise, the streamline deflection effect is modeled
!               as (w/u)(dx).
!
! --- INPUTS:
!                H - real              - Building height above ground
!                W - real              - Projected building width
!                L - real              - Along-wind building length
!                R - real              - Scale length from H and W
!               LR - real              - Length of downwind cavity from
!                                         lee face
!               HR - real              - Maximum cavity height above
!                                         ground
!                x - real              - downwind distances
!                y - real              - crosswind distances
!                z - real              - heights above ground
!
! --- OUTPUT:
!             dzdx - real              - Vertical streamline slope
!
! --- ZSTREAM called by:  NUMRISE
! --- ZSTREAM calls:      none
!----------------------------------------------------------------------
!
      real H,W,L,R,HR,LR,expz1,expz2,expzg,expx
      real x,y,z,dzdx2,zslope,dzdx,ypos
      data expx/1./,expz1/3./,expz2/1./
!
! --- Check for a building
      if(h.le.0.0)then
         dzdx=0.0
         go to 900
      endif
! --- Set a few constants
      hbyw=H/W
      ypos=abs(y)
      onebyr=1.0/R
      wby2=0.5*W
      rby2=0.5*R
! --- Power law exponent for slope approaching zero at ground
! --- Exponent modified for tall, narrow buidings
! --- zg is level below which slope is zero in far wake
      zg=0.
      expzg=0.3
      if(hbyw .ge. 2.) expzg=expzg*(0.5*hbyw)**2
!
! --- Local streamline slope (zslope) at z=H
! --- Local two-dimensional streamline slope (dzdx2)
! --- Local three-dimensional streamline slope (dzdx)
! --- (x,y,z)=(0,0,0) at ground of center of upwind building face
!
      if(x .lt. -R) then
! ---    Upwind of building influence
         zslope = 0.
         dzdx2  = 0.
      elseif(x .lt. 0.) then
! ---    Ascent upwind of building:
! ---    parobolic fit to slope=0 at (-R,0) with increasing slope
! ---    to (0,(HR-H))
! ---    vertical decay above building using expz1
! ---    below building nonzero slope above 2/3 H for R<H reducing
! ---    to ground as R approaches 2H
         zslope = 2.*(HR-H)*(x+R)*onebyr**2.
         if(z .gt. H) then
            dzdx2 = zslope/((z-H+R)*onebyr)**expz1
         elseif(R .le. H .and. z .le. 0.67*H) then
            dzdx2 = 0.
         elseif(R .le. H .and. z .gt. 0.67*H) then
            dzdx2 = zslope
         elseif(R .gt. H .and. z .le. 0.67*(2*H-R)) then
            dzdx2 = 0.
         elseif(R .gt. H .and. z .gt. 0.67*(2*H-R)) then
            dzdx2 = zslope
         else
            print *,'z out of bounds      ',x,z
         endif
      elseif(x .le. rby2) then
! ---    Ascent over building
! ---    parobolic fit from (0,0) with decreasing slope to
! ---    to (0.5R,(HR-H))
! ---    vertical decay above building using expz1
         zslope = (-(HR-H)*4.*onebyr)*(2.*x*onebyr-1.)
         if(z .le. H) then
            dzdx2 = zslope
         else
            dzdx2 = zslope/((z-H+R)*onebyr)**expz1
         endif
      elseif(x .le. L+LR) then
! ---    Descent over building to end of near wake
! ---    parobolic fit from (.5R,(HR-H)) with increasing slope to
! ---    to (L+LR,-H/2)
! ---    vertical decay above z=H using expz2
! ---    vertical decay below z=H using expzg
         zslope = (HR-H)*(R-2.*x)/((L-rby2+LR)**2)
         if(z .gt. H) then
            dzdx2 = zslope/((z-H+R)*onebyr)**expz2
         elseif(z .le. zg) then
            dzdx2 = 0.
         else
            dzdx2 = zslope*((z-zg)/(H-zg))**expzg
         endif
      else
! ---    Descent beyond near wake (far wake)
! ---    horizontal decay beyond L+LR using expx
! ---    vertical decay above z=H using expz2
! ---    vertical decay below z=H using expzg
         zslopeLR  = -2.*(HR-H)/(L-rby2+LR)
         zslope = zslopeLR/((x-(L+LR-R))*onebyr)**expx
         if(z .gt. H) then
            dzdx2 = zslope/((z-H+R)*onebyr)**expz2
         elseif(z .le. zg) then
            dzdx2 = 0.
         else
            dzdx2 = zslope*((z-zg)/(H-zg))**expzg
         endif
      endif
! --- Calculate 3-D slopes,: dzdx : from 2-d centerplane slope,: dzdx2
      if(ypos .gt. (wby2+R/3.))then
         dzdx=0.
      elseif(ypos .le. wby2)then
         dzdx=dzdx2
      else
         yscale=1.+(3.*onebyr)*(wby2-ypos)
         dzdx=dzdx2*yscale
      endif
900   continue
      return
      end
!----------------------------------------------------------------------
      subroutine intertab(val,nt,tab,i,j,fac)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030402          INTERTAB
! ---            D. Strimaitis, EarthTech
!
! --- PURPOSE:  Finds pointers in an array to bracket a value, and
!               returns the interpolation factor.
!               Tabulation is assumed to be monotonic.
!               Use:  val = tab(i) + fac * (tab(j) - tab(i))
!
! --- INPUTS:
!           VAL - real     - Value to interpolate in table
!            NT - integer  - Number of points in table
!       TAB(nt) - real     - Tabulated values
!
! --- OUTPUT:
!             I - integer  - Index 1 bracketing value
!             J - integer  - Index 2 bracketing value
!           FAC - real     - Interpolation factor
!                            fac=0.0 at tab(i1)
!                            fac=1.0 at tab(i2)
!
! --- INTERTAB called by:  
! --- INTERTAB calls:      none
!----------------------------------------------------------------------
      real tab(nt)
      data zero/0.0/
! --- Check for increasing values in table, and treat special cases
      if(tab(nt).GT.tab(1)) then
         increase=1
         if(val.LE.tab(1)) then
            i=1
            j=1
            fac=zero
            return
         elseif(val.GE.tab(nt)) then
            i=nt
            j=nt
            fac=zero
            return
         endif
      elseif(tab(1).GT.tab(nt)) then
         increase=-1
         if(val.GE.tab(1)) then
            i=1
            j=1
            fac=zero
            return
         elseif(val.LE.tab(nt)) then
            i=nt
            j=nt
            fac=zero
            return
         endif
      elseif(tab(1).EQ.tab(nt)) then
! ---    Single value in table (should not happen)
         i=1
         j=1
         fac=zero
         return
      endif
! --- Set starting point by assuming a linear trend
      iguess=1+(nt-1)*(val-tab(1))/(tab(nt)-tab(1))
! --- Set do loop range
      i1=iguess
      if(val.GE.tab(iguess)) then
         if(increase.EQ.1) then
            istep=1
            i2=nt-istep
         else
            istep=-1
            i2=1-istep
         endif
      elseif(val.LE.tab(iguess)) then
         if(increase.EQ.1) then
            istep=-1
            i2=1-istep
         else
            istep=1
            i2=nt-istep
         endif
      endif
! --- Set factor for 'GT' tests
      ifac=increase*istep
! --- Step through loop until val is in the range i to i+istep
      do i=i1,i2,istep
         j=i+istep
         if(ifac*tab(j).GT.ifac*val) then
! ---       Compute interpolation factor
            fac=(val-tab(i))/(tab(j)-tab(i))
            return
         endif
      enddo
! --- Problem if this point is reached
      write(*,*)
      write(*,*) 'INTERTAB:  val,nt   = ',val,nt
      write(*,*) '   tab(1),tab(nt)   = ',tab(1),tab(nt)
      write(*,*) ' increase, iguess   = ',increase,iguess
      write(*,*) '      i1,i2,istep   = ',i1,i2,istep
      write(*,*)
      stop 'Halted in INTERTAB -- interpolator failed!'
      end
!----------------------------------------------------------------------
      subroutine wake_tab(istype,is,it,idw,x,sy,sz,z,rise,xlast,idone)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108          WAKE_TAB
! ---            D. Strimaitis
!
! --- PURPOSE:  Interpolates sigmas for the distance X from a source
!               and does same for the height (gradual rise), using
!               arrays of tabulated values.  Distance is measured
!               in the x-y plane along the puff trajectory.
!
! --- UPDATE
! --- V6.261-V6.268 100108  (DGS): Add NOBID logic as in WAKE_XSIG to
!                                  process sigmas that are negative,
!                                  requiring a test with the BID
!                                  value for the current rise.
! --- V6.1-V6.261   080520  (DGS): Replace individual PT1,PT2 rise
!                                  tables with /SRCTAB/ arrays from
!                                  Direct Access file
! --- V5.7-V6.1     050915  (DGS): Add emission timestep to inputs to
!                                  avoid including /CURRENT/
!                           (DGS): Add emission timestep arg to PT2
!                                  arrays
!
! --- INPUTS:
!        ISTYPE - integer  - Source type         (not needed 080520)
!            IS - integer  - Source index        (not needed 080520)
!            IT - integer  - Emission step index (not needed 080520)
!           IDW - integer  - PRIME Downwash flag
!                            3 = Primary source
!                            4 = Cavity source
!             X - real     - Distance (m) from source to point for
!                            obtaining sigmas
!     Parameters:
!           MXRISE, IO6
!
!     Common block /SRCTAB/ variables:
!           NTR, NWK, NCV,
!           XTR(mxrise),ZTR(mxrise),RTR(mxrise),HTR(mxrise),
!           XWK(mxrise),SYWK(mxrise),SZWK(mxrise),DRWK(mxrise),
!           XCV(mxrise),SYCV(mxrise),SZCV(mxrise)
!
! --- OUTPUT:
!            SY - real     - Sigma-y (m) without BID
!            SZ - real     - Sigma-z (m) without BID
!             Z - real     - Height (m) above ground
!          RISE - real     - Plume rise (m) without streamline
!                            deflection near buildings
!         XLAST - real     - Distance (m) from source to the last
!                            entry in the wake array
!         IDONE - integer  - Completion flag
!                            0:  No interpolation
!                            1:  Interpolation completed
!
! --- Note:  If idone=0, sigmas at the end of the 
!
! --- WAKE_TAB called by:  SETPUF, PUFRECS
! --- WAKE_TAB calls:      INTERTAB, WAKE_XSIG
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'srctab.puf'
      logical nobid
! --- Initialize NULL return condition
      idone=0
      z=0.0
      rise=0.0
      sz=0.0
      sy=0.0
      xlast=0.0
! --- Configure wake table variables
      timedum=0.0
      nobid=.FALSE.
      if(idw.EQ.3) then
! ---    Primary source (PRIME downwash)
         xlast=xwk(nwk)
         sz=szwk(nwk)
         sy=sywk(nwk)
         if(x.LE.xwk(nwk)) then
! ---       Extract height and rise without streamline modifications
            call INTERTAB(x,ntr,xtr,i1,i2,fac)
            z=ztr(i1)+fac*(ztr(i2)-ztr(i1))
            rise=htr(i1)+fac*(htr(i2)-htr(i1))
            bid=rise/3.5
! ---       Extract sigmas
            call INTERTAB(x,nwk,xwk,i1,i2,fac)
            sz=ABS(szwk(i1))+fac*(ABS(szwk(i2))-ABS(szwk(i1)))
            sy=ABS(sywk(i1))+fac*(ABS(sywk(i2))-ABS(sywk(i1)))
! ---       Test sigmas against BID values for current rise
            if(szwk(i1).LE.0.) then
               if(NOBID) then
                  write(io6,*)'WAKE_TAB: unexpected negative sigma-z:',&
                               szwk(i1)
                  write(*,*)'WAKE_TAB: unexpected negative sigma-z:',&
                               szwk(i1)
                  stop
               else
                  sz=AMAX1(sz,bid)
               endif
            endif
            if(sywk(i1).LE.0.) then
               if(NOBID) then
                  write(io6,*)'WAKE_TAB: unexpected negative sigma-y:',&
                               sywk(i1)
                  write(*,*)'WAKE_TAB: unexpected negative sigma-y:',&
                               sywk(i1)
                  stop
               else
                  sy=AMAX1(sy,bid)
               endif
            endif
            idone=1
         endif
      elseif(idw.EQ.4) then
! ---    Cavity source (PRIME downwash)
         xlast=xcv(ncv)
         sz=szcv(ncv)
         sy=sycv(ncv)
         if(x.LE.xcv(ncv)) then
! ---       Cavity source is on the ground
            z=0.0
            rise=0.0
! ---       Extract sigmas
            call INTERTAB(x,ncv,xcv,i1,i2,fac)
            sz=szcv(i1)+fac*(szcv(i2)-szcv(i1))
            sy=sycv(i1)+fac*(sycv(i2)-sycv(i1))
            idone=1
         endif
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine rdhdbc2(io)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108            RDHDBC2
! ---            D. Strimaitis
!
!  PURPOSE:     Reads header information from concentration file
!               used as a boundary condition file and passes information
!               to main program through common /headbc2/.  Process
!               header information into /BCS/ variables.
!
! --- UPDATE
! --- V5.721(040503) to V6.268 (100108)(DGS)
!        Fix typo FNAMEBC (not FNAMBC) in call to QCHECK
! --- V5.72(030528) to V5.721(040503)  (DGS)
!        File unit passed to subroutine YR4 for listfile output was
!        not defined so that error reports attempted to write to 
!        unit 0.  Changed io1 to io6 in YR4 call argument.
!
!  ARGUMENTS:
!     PASSED:   io      Unit number for accessing input file         [i]
!
!
! --- INPUTS:
!     Common block /BCS/:
!           HTMINBC
!     Common block /GEN/:
!           NSPEC,NSE,ISPLST(4,mxspec),CSPEC(mxspec),XMOL(mxspec)
!     Common block /GRID/:
!           DGRID, DGRIDI, IBCOMP, JBCOMP, IECOMP, JECOMP
!     Parameters:
!           MXNX, MXSG, MXVAR, MXSPEC, IO15, IO6,
!           MXP2, MXBC
!
! --- OUTPUT:
!
!     Common block /BCS/:
!           XLENBC, YLENBC, IBCU, FUNITSBC(4), CUNITSBC(4),
!           NBC,XBCGRD(mxbc),YBCGRD(mxbc),ITYPEBC(mxbc),
!           IBC(mxbc),NEWBC(mxbc),
!           NTYPEBC1,HTBC(mxbc),CONBC(mxspec,mxbc),NSBC,
!           CNAMTYP(mxbc),
!           FNAMEBC, NTYPEBC2, NSPECBC, IBYDBC, IBHBC,
!           IEYDBC, IEHBC, VRSBC, LBLBC, CSPECBC(mxspec),
!           XMWTBC(mxspec), IBEGBC, IENDBC
!     Common block /HEADBC2/:
!           all
!
!
!  CALLING ROUTINES:    SETUP
!
!  EXTERNAL ROUTINES:   INCR, YR4, BC2DOC, QCHECK
!-----------------------------------------------------------------------
      INCLUDE 'params.puf'
      INCLUDE 'headbc2.puf'
! -- Note: all declaration statements for data read in by RDHDBC2
!          are contained in this common
      include 'bcs.puf'
      include 'chiflx.puf'
      include 'flags.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'qa.puf'
      real dxrecbc(mxbc),dyrecbc(mxbc)
      character*1 nsew(4)
      character*48 msg(8),msg0
      logical lfatal, lemit
      data nsew/'N','S','E','W'/
      data msg0  /'  **********************************************'/
      data msg(1)/'  *      FATAL Problem Found in RDHDBC2!       *'/
      data msg(2)/'  * ------------------------------------------ *'/
      data msg(3)/'  *  Size of Array(s) in BCON.DAT too Large    *'/
      data msg(4)/'  *  Re-size Array Parameter(s) in PARAMS.PUF  *'/
      data msg(5)/'  *         and Recompile CALPUFF              *'/
      data msg(6)/'  * ------------------------------------------ *'/
      data msg(7)/'  * Parameter       PARAMS.PUF       BCON.DAT  *'/
      data msg(8)/'  * ---------       ----------       --------  *'/
      data lfatal/.FALSE./, lemit/.false./
! --- Process first section of CALPUFF output file (including comment
! --- records), and determine dataset version
      call BC2DOC(io,idtype)
!
!  Read control variables
      if(idtype.EQ.0) then
         i2drhBC=0
         read(io) amodelBC,averBC,alevBC,msyrBC,mjsdayBC,mshrBC,&
               mnhrsBC,mavgBC,ielmetBC,jelmetBC,delxBC,delyBC,nzBC,&
               xorigkmBC,yorigkmBC,nsdumBC,&
               iastarBC,iastopBC,jastarBC,jastopBC,&
               isastrBC,jsastrBC,isastpBC,jsastpBC,&
               meshdnBC,nptsBC,nareasBC,nlinesBC,nvolsBC,&
               ndrecBC,nctrecBC,LSGRIDBC,nszoutBC,lcomprsBC
      else
! ---    Dataset version 2.0 format
         read(io) amodelBC,averBC,alevBC,msyrBC,mjsdayBC,mshrBC,&
               mnhrsBC,mavgBC,ielmetBC,jelmetBC,delxBC,delyBC,nzBC,&
               xorigkmBC,yorigkmBC,nsdumBC,&
               iastarBC,iastopBC,jastarBC,jastopBC,&
               isastrBC,jsastrBC,isastpBC,jsastpBC,&
               meshdnBC,nptsBC,nareasBC,nlinesBC,nvolsBC,&
               ndrecBC,nctrecBC,LSGRIDBC,nszoutBC,lcomprsBC,i2drhBC,&
               iutmznBC,feastBC,fnorthBC,rnlat0BC,relon0BC,&
               xlat1BC,xlat2BC,pmapBC,utmhemBC,datumBC,datenBC,&
               clat0BC,clon0BC,clat1BC,clat2BC
      endif
! --- Set logicals for discrete and CTSG receptors
      ldiscbc=.FALSE.
      if(ndrecbc.GT.0) ldiscbc=.TRUE.
      lctsgbc=.FALSE.
      if(nctrecbc.GT.0) lctsgbc=.TRUE.
! --- Set flag indicating CALPUFF data version is 'MOD5' or older
      ipver=1
      call YR4(io6,msyrBC,ierry)
      if(ierry.NE.0) stop 'Halted in RDHDBC2'
! --- Move starting time back 1 hour to identify the start of the
! --- first hour
      call INCR(io6,msyrBC,mjsdayBC,mshrBC,-1)
! --- Set period step to 1 hour
      nsecdtBC=3600
      mssecBC=0
! --- Rename averaging period variables
      mnrunBC=mnhrsBC
      mavgpdBC=mavgBC
!
!  Use nsdumBC to satisfy compiler checks
      if(nsdumBC .EQ. 1) nsdumBC=1
!
!  Check size of array dimensions (compute gridded rec. array dims.)
!  with limits contained in PARAMS.PUF file
      ngxBC=(isastpBC-isastrBC)*meshdnBC+1
      ngyBC=(jsastpBC-jsastrBC)*meshdnBC+1
      ngrecBC=ngxBC*ngyBC
      if(ngxBC .GT. mxnxg) lfatal=.TRUE.
      if(ngyBC .GT. mxnyg) lfatal=.TRUE.
      if(ndrecBC .GT. mxrec) lfatal=.TRUE.
      if(nctrecBC .GT. mxrect) lfatal=.TRUE.
      if(nszoutBC .GT. mxspec) lfatal=.TRUE.
      if(LFATAL) then
! ---    Report problem information and quit
! ---    List File:
         write(io6,*)
         write(io6,100) msg0
         do i=1,8
            write(io6,100) msg(i)
         enddo
         write(io6,101)'  *    MXNXG',mxnxg,ngxBC
         write(io6,101)'  *    MXNYG',mxnyg,ngyBC
         write(io6,101)'  *    MXREC',mxrec,ndrecBC
         write(io6,101)'  *   MXRECT',mxrect,nctrecBC
         write(io6,101)'  *   MXSPEC',mxspec,nszoutBC
         write(io6,100) msg0
! ---    Screen:
         write(*,*)
         write(*,100) msg0
         do i=1,8
            write(*,100) msg(i)
         enddo
         write(*,101)'  *    MXNXG',mxnxg,ngxBC
         write(*,101)'  *    MXNYG',mxnyg,ngyBC
         write(*,101)'  *    MXREC',mxrec,ndrecBC
         write(*,101)'  *   MXRECT',mxrect,nctrecBC
         write(*,101)'  *   MXSPEC',mxspec,nszoutBC
         write(*,100) msg0
         stop
      endif
!
!  Read title of run
      read(io) atitleBC
!
!  Read specie/level list
      read(io) (asplstBC(i),i=1,nszoutBC)
!
!  Read coordinates of non-gridded receptors if any are used
      if(ndrecBC .NE. 0) read(io) (xrecBC(i),i=1,ndrecBC),&
                                (yrecBC(i),i=1,ndrecBC),&
                                (zrecBC(i),i=1,ndrecBC)
!
!  Read coordinates of complex terrain receptors if any are used
      if(nctrecBC .NE. 0) read(io) (xctrBC(i),i=1,nctrecBC),&
                                 (yctrBC(i),i=1,nctrecBC),&
                                 (zctrBC(i),i=1,nctrecBC),&
                                 (ihillBC(i),i=1,nctrecBC)
!
!  Generate coordinates of gridded receptors if any are used
      if(LSGRIDBC .AND. ngrecBC .NE. 0) then
!  Compute coord. for lower-left point in sampling grid, and spacing
         gx0=xorigkmBC+delxBC*(isastrBC-0.5)
         gy0=yorigkmBC+delyBC*(jsastrBC-0.5)
         delgx=delxBC/meshdnBC
         delgy=delyBC/meshdnBC
!  Fill out arrays
         do i=1,ngxBC
            do j=1,ngyBC
               xgrdBC(i,j)=gx0+(i-1)*delgx
               ygrdBC(i,j)=gy0+(j-1)*delgy
            enddo
         enddo
      endif
100   format(1x,a48)
101   format(1x,a12,2(8x,i8),'   *')
! --- Configure information for /BCS/ variables
! ---------------------------------------------
! --- Set air-mass/species with variation factors to zero
      nsbc=0
! --- Set fractional offset from edge of domain for BC source location
! --- to 0.1% of grid cell
      fuzz=.001
! --- Set coordinates for corners if comp grid (met grid units)
      xbcw=ibcomp-1
      ybcs=jbcomp-1
      xbce=iecomp
      ybcn=jecomp
! --- Set boundary segments equal to the computational grid cell sides
      nbcx=iecomp-ibcomp+1
      nbcy=jecomp-jbcomp+1
      xlenbc=dgrid
      ylenbc=dgrid
      nbc=2*(nbcx+nbcy)
! --- Set concentration units (g/m3)
      ibcu=1
! --- Air-mass species; use approx MWT of air (29)
      nspecbc=nszoutBC
      do i=1,nspecbc
         cspecbc(i)=asplstBC(i)
         xmwtbc(i)=29.0
      enddo
! --- Number of air-mass types (each segment is an air-mass type)
      ntypebc1=0
      ntypebc2=nbc
! --- Set layer height of each air-mass to HTMINBC;
! --- assign airmass type name
      do i=1,nbc
         htbc(i)=htminbc
         write(cnamtyp(i),'(i6)') i
      enddo
      
! --- QA check
      call QCHECK(mxspec,io6,nspec,nse,isplst,cspec,xmol,lemit,&
       fnamebc,nspecbc,cspecbc,xmwtbc,ierr,ixrembc)
      if(ierr.EQ.1) lfatal=.TRUE.
! --- Map air-mass segments along comp grid boundary
      j=0
! --- Set NORTH boundary (ibc=1) from west to east
      ygrd=ybcn-fuzz
      do i=1,nbcx
         j=j+1
         ibc(j)=1
         itypebc(j)=j
         xbcgrd(j)=xbcw+(FLOAT(i)-.5)
         ybcgrd(j)=ygrd
      enddo
! --- Set SOUTH boundary (ibc=2) from west to east
      ygrd=ybcs+fuzz
      do i=1,nbcx
         j=j+1
         ibc(j)=2
         itypebc(j)=j
         xbcgrd(j)=xbcw+(FLOAT(i)-.5)
         ybcgrd(j)=ygrd
      enddo
! --- Set EAST boundary (ibc=3) from north to south
      xgrd=xbce-fuzz
      do i=1,nbcy
         j=j+1
         ibc(j)=3
         itypebc(j)=j
         xbcgrd(j)=xgrd
         ybcgrd(j)=ybcn-(FLOAT(i)-.5)
      enddo
! --- Set WEST boundary (ibc=4) from north to south
      xgrd=xbcw+fuzz
      do i=1,nbcy
         j=j+1
         ibc(j)=4
         itypebc(j)=j
         xbcgrd(j)=xgrd
         ybcgrd(j)=ybcn-(FLOAT(i)-.5)
      enddo
      if(j.NE.nbc) then
         write(*,*)'RDHDBC2:  boundary segment assignment is BAD!'
         write(*,*)'Number Set      = ',j
         write(*,*)'Number Expected = ',nbc
         lfatal=.true.
      endif
! --- Set date-time information
! --- End-time of first period in file
      jbyr=msyrBC
      jbdy=mjsdayBC
      ibhbc=mshrBC
      call INCR(io6,jbyr,jbdy,ibhbc,1)
      ibydbc=jbyr*1000+jbdy
! --- End-time of last period in file
      jeyr=jbyr
      jedy=jbdy
      iehbc=ibhbc
      call INCR(io6,jeyr,jedy,iehbc,mnhrsbc)
      ieydbc=jeyr*1000+jedy
! --- Initialize "current" beginning/ending date-time to zero
      ibegbc=0
      iendbc=0
! --- Assign nearest receptor to each air-mass segment
! ----------------------------------------------------
! --- Initialize arrays
      do k=1,mxbc
         irecbc(k)=0
         jrecbc(k)=0
         d2recbc(k)=100.
      enddo
! --- Loop over segments
      do k=1,nbc
! ---    Loop over discrete receptors
         do i=1,ndrecbc
! ---       Compute receptor location in grid units
            xg=(xrecbc(i)*1000.-xorig)*dgridi
            yg=(yrecbc(i)*1000.-yorig)*dgridi
            dxg=ABS(xg-xbcgrd(k))
            dyg=ABS(yg-ybcgrd(k))
! ---       Screen for receptor 'in' the correct boundary
            if((ibc(k).LE.2 .AND. dyg.LT.1.0) .OR.&
               (ibc(k).GE.3 .AND. dxg.LT.1.0)) then
! ---          Check distance for minimum
               dsq=dxg**2+dyg**2
               if(dsq.LT.d2recbc(k)) then
! ---             Update receptor for this segment
                  irecbc(k)=i
                  d2recbc(k)=dsq
                  dxrecbc(k)=dxg
                  dyrecbc(k)=dyg
               endif
            endif
         enddo
! ---    Loop over gridded receptors
         if(LSGRIDBC) then
           do i=1,ngxBC
           do j=1,ngyBC
! ---        Compute receptor location in grid units
             xg=(xgrdbc(i,j)*1000.-xorig)*dgridi
             yg=(ygrdbc(i,j)*1000.-yorig)*dgridi
             dxg=ABS(xg-xbcgrd(k))
             dyg=ABS(yg-ybcgrd(k))
! ---        Screen for receptor 'in' the correct boundary
             if((ibc(k).LE.2 .AND. dyg.LT.1.0) .OR.&
                (ibc(k).GE.3 .AND. dxg.LT.1.0)) then
! ---           Check distance for minimum
                dsq=dxg**2+dyg**2
                if(dsq.LT.d2recbc(k)) then
! ---              Update receptor for this segment
                   irecbc(k)=i
                   jrecbc(k)=j
                   d2recbc(k)=dsq
                   dxrecbc(k)=dxg
                   dyrecbc(k)=dyg
                endif
             endif
           enddo
           enddo
         endif
      enddo
! --- QA checks
! -------------
! --- Check units selection (only 4 types)
      if(ibcu.LT.1 .OR. ibcu.GT.4) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC2 -- units choice must be ',&
         '1-4  -- ','IBCU = ',ibcu
         lfatal=.TRUE.
      endif
! --- Check number of air-mass types
      ntypebc=ntypebc1+ntypebc2
      if(ntypebc.gt.mxbc)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC2 -- too many air-mass ',&
         'types -- ','NTYPEBC = ',ntypebc,' MXBC = ',mxbc
         lfatal=.TRUE.
      endif
! --- Check number of species
      if(nspecbc.gt.nspec)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC2 -- number of species ',&
         'exceeds number modeled -- '
         write(io6,*)'NSPECBC = ',nspecbc,' NSPEC = ',nspec
         lfatal=.TRUE.
      endif
! --- Check number of species/air-mass types
      ntest=nspecbc*ntypebc
      if(nsbc.gt.ntest)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC -- number of species/ ',&
         'air-mass combinations exceeds number possible -- '
         write(io6,*)'NSBC = ',nsbc,' NSPECBC*NTYPEBC = ',ntest
         lfatal=.TRUE.
      endif
! --- All segments must have a valid receptor index
      nzero=0
      do k=1,nbc
         if(irecbc(k).EQ.0) nzero=nzero+1
      enddo
      if(nzero.GT.0) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC2 -- a segment mid-pt is',&
         ' not assigned a valid receptor index'
         lfatal=.TRUE.
      endif
! --- Check max distance from segment midpoint to nearest receptor
      d2max=0.0
      do k=1,nbc
         if(d2max.LT.d2recbc(k)) d2max=d2recbc(k)
      enddo
      if(d2max.GT.25.) then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR in subr. RDHDBC2 -- segment mid-pt is ',&
         'more than 5 grid cells from nearest receptor'
         lfatal=.TRUE.
      endif
! --  Report configuration to the list file
! ------------------------------------------
      write(io6,*)
      write(io6,*)
      write(io6,*)'   ****   BOUNDARY CONDITION DATA  ****'
      write(io6,*)
      write(io6,*)'dataset type                     = ','CONC.DAT'
      write(io6,*)'segments on N&S borders          = ',nbcx
      write(io6,*)'         segment length (m)      = ',xlenbc
      write(io6,*)'segments on E&W borders          = ',nbcy
      write(io6,*)'         segment length (m)      = ',ylenbc
      write(io6,*)
      write(io6,*)'concentration units              = ',cunitsbc(ibcu)
      write(io6,*)'species and units conversion factor to g/m^3:'
      do j=1,nspecbc
         factor=funitsbc(ibcu)
         if(ibcu.GT.2) factor=factor*xmwtbc(j)
         write(io6,'(20x,a12,1pe11.4)') cspecbc(j),factor
      enddo
      write(io6,*)
      write(io6,*)'Species cross-reference vector:'
      do j=1,nspecbc
         write(io6,'(20x,a12,i5)') cspecbc(j),ixrembc(j)
      enddo
      write(io6,*)
      write(io6,*)'time-variable air-mass types     = ',ntypebc2
      if(ntypebc2.GT.0) then
         write(io6,*)'    beginning date (YYYYJJJ)     = ',ibydbc
         write(io6,*)'    beginning hour (00-23 LST)   = ',ibhbc
         write(io6,*)'    ending date (YYYYJJJ)        = ',ieydbc
         write(io6,*)'    ending hour (00-23 LST)      = ',iehbc
      endif
      write(io6,*)
      write(io6,*)'Characteristics of each Air-mass segment:'
      write(io6,*)'   Bndy Ht   Type  xgrd    ygrd irec jrec dxg  dyg'
      write(io6,*)
      do i=1,nbc
         write(io6,'(i4,1x,a1,f7.1,i4,2f8.2,2i4,2f5.1)') i,nsew(ibc(i)),&
               htbc(i),itypebc(i),xbcgrd(i),ybcgrd(i),&
               irecbc(i),jrecbc(i),dxrecbc(i),dyrecbc(i)
      enddo
      write(io6,*)
      write(io6,*)
! --- Terminate run if QA detected a fatal flaw
999   if(LFATAL) then
         write(*,*)
         stop 'Halted in RDHDBC2 -- see list file.'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine bc2doc(io,ifilver)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030528            BC2DOC
!                D. Strimaitis, Earth Tech, Inc.
!
! --- PURPOSE:  Read header comment records of the CALPUFF output
!               data file used for boundary conditions, and set file
!               type.
!
! --- INPUTS:
!            io - integer    - Unit number for CALPUFF file
!     Common block /QA/ variables:
!           NCOMMOUT
!
!     Parameters:
!           IO6, IOX
!
! --- OUTPUT:
!       ifilver - integer    - Dataset version flag
!                              0: Before 2.0
!                              1: 2.0 or later
!     Common block /QA/ variables:
!           NCOMMOUT
!
! --- BC2DOC called by:  RDHDBC2
! --- BC2DOC calls:      none
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common blocks
      include 'filnam.puf'
      include 'qa.puf'
!
! --- Local Variables
      character*16 dataset,dataver
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Read and test first record to check header format
! --- Record #1 - File Declaration -- 24 words
      read(io) dataset,dataver,datamod
      ifilver=0
      if(dataset.EQ.'CONC.DAT') then
         ifilver=1
      endif
      if(ifilver.EQ.0) then
! ---    Old file format with no comment records
         REWIND(io)
      elseif(ifilver.EQ.1) then
! ---    To scratch file
         write(iox,'(a132)') blank
         write(iox,'(a132)') break
         write(iox,'(a132)') blank
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         ncommout=ncommout+4
! ---    Number of comment records
         read(io) ncom
! ---    Keep only title information in scratch file
         do i=1,ncom
            comment1=blank
            read(io) comment1
            if(i.LE.4) then
               write(iox,'(a132)') comment1
               ncommout=ncommout+1
            endif
         enddo
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine RDEMBC2(ldb,ndathr)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 030528           RDEMBC2
!                D. Strimaitis   Earth Tech
!
! --- PURPOSE:  Read the time-varying records of the BCON boundary
!               conditions file (unit IO15) and update data in the
!               air-mass arrays for these air-mass types
!               MBCON=2:  process a CONC.DAT file
!
! --- INPUTS:
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!           NDATHR - integer    - Current modeling date/time
!                                 (YYYYJJJHH)
!
!     Common block /BCS/:
!           HTBC(mxbc),CONBC(mxspec,mxbc),IBCU,FUNITSBC(4),
!           NTYPEBC2, NSPECBC, CSPECBC(mxspec), XMWTBC(mxspec)
!
!     Parameters:
!           MXBC, MXSPEC, IO15, IO6
!
! --- OUTPUT:
!
!     Common block /BCS/:
!           CONBC(mxspec,mxbc),
!           IBEGBC, IENDBC
!
! --- RDEMBC2 called by: INITPUF
! --- RDEMBC2 calls:     GETRCRD, YR4
!----------------------------------------------------------------------
!
      include 'params.puf'
      INCLUDE 'headbc2.puf'
      include 'bcs.puf'
! --- Local variables for reading air-mass data
      real tcd(mxrec),tcg(mxnxg,mxnyg),tct(mxrect)
      logical ldb
! --- Set the array size parameters for GETRCRD call (not zero)
      if(lsgridbc) then
         nxdum=ngxBC
         nydum=ngyBC
      else
         nxdum=mxnxg
         nydum=mxnyg
      endif
      if(ldiscbc) then
         nddum=ndrecbc
      else
         nddum=mxrec
      endif
      if(lctsgbc) then
         ntdum=nctrecbc
      else
         ntdum=mxrect
      endif
! --- Read data for the next hour in the file
101   call GETRCRD(io15,tcg,tcd,tct,nxdum,nydum,nddum,ntdum,ierr)
      if(ierr.GT.1) then
         write(*,*)
         stop 'Halted in RDEMBC2'
      elseif(ierr.EQ.1) then
         write(*,*)
         write(*,*)'Reached end of BCON.DAT while searching for'
         write(*,*)'time period = ',ndathr
         write(*,*)'Last period = ',ibegbc
         stop 'Halted in RDEMBC2'
      endif
! --- Test for valid time period
      if(ndathr.GT.iendbc) then
! ---    Try next set of records
         goto 101
      elseif(ndathr.LT.ibegbc)then
         write(io6,*)
         write(io6,*)'*********  FATAL  ***********'
         write(io6,*)'ERROR -- BCON date/time exceeds current'
         write(io6,*)'      -- current date/time :',ndathr
         write(io6,*)'      -- BCON date/time    :',ibegbc
         write(*,*)
         stop 'ERROR -- BCON date/time exceeds current --'
      endif
! --- Report valid time period
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-varying BCON data'
         write(io6,*)'IBEG = ',ibegbc,' IEND = ',iendbc
         write(io6,*)'Updated BCON data'
         write(io6,*)'Air-mass  Layer(m) Concentrations (g/m^3)'
         write(io6,*)'--------  -------- ----------------------'
      endif
! --- Loop over air-mass types and scale data
      do ityp=1,nbc
         do is=1,nspecbc
            conbc(is,ityp)=conbc(is,ityp)*funitsbc(ibcu)
            if(ibcu.GT.2) conbc(is,ityp)=conbc(is,ityp)*xmwtbc(is)
! ---       Report new data values
            if(ldb)then
               if(is.EQ.1) then
                 write(io6,'(a12,f6.0,1pe11.4,4x,a12)')&
                 cnamtyp(ityp),htbc(ityp),conbc(is,ityp),cspecbc(is)
               else
                 write(io6,'(18x,1pe11.4,4x,a12)')&
                 conbc(is,ityp),cspecbc(is)
               endif
            endif
         enddo
      enddo
      return
      end
!-----------------------------------------------------------------------
      subroutine getrcrd(io,tcg,tcd,tct,ix,iy,idrec,itrec,ierr)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521            GETRCRD
! ---            D. Strimaitis, Earth Tech, Inc.
!
!  PURPOSE:     Reads hourly data records from "CONC.DAT" file
!
!
! --- UPDATE
!
! --- V5.721(040503) to V6.42_x1.1(140521)
!        Label either 12 or 15 characters from UNCOMPRS
! --- V5.72(030528) to V5.721(040503)  (DGS)
!        File unit passed to subroutine YR4 for listfile output was
!        not defined so that error reports attempted to write to 
!        unit 0.  Changed io1 to io6 in YR4 call argument.
!
!  ARGUMENTS:
!     PASSED:   io        Unit number for accessing input file       [i]
!               tcg(i,j)  Temporary gridded receptor array          [ra]
!               tcd(i)    Temporary discrete receptor array         [ra]
!               tct(i)    Temporary complex terrain receptor array  [ra]
!               ix,iy     Dimensions for tcg array                   [i]
!               idrec     Dimension for tcd array                    [i]
!               itrec     Dimension for tct array                    [i]
!               ierr      Error flag:                                [i]
!                          0=No error
!                          1=EOF encountered
!                          2=Species name does not match
!
!  
! --- OUTPUT:
!     Common block /BCS/:
!           CONBC(mxspec,mxbc),
!           IBEGBC, IENDBC
!
!  CALLING ROUTINES:    RDEMBC2
!
!  EXTERNAL ROUTINES:   YR4
!-----------------------------------------------------------------------
!
      INCLUDE 'params.puf'
      INCLUDE 'headbc2.puf'
      include 'bcs.puf'
!
! --- Declare temporary variables using actual array dimensions
      character*15 tsplv
      character*12 csptest
! --- 6.42_x1.1 140521
      character*12 clab12
      real tcg(ix,iy),tcd(idrec),tct(itrec)
      real xwork1(mxnxg,mxnyg),xwork2(mxrec),xwork3(mxrect)
      ierr=0
! --- 6.42_x1.1 140521
      nchar=15
! --- Read time record
      read(io,end=999) myre,mjdaye,mhre
! --- Enforce YYYY format for year
      call YR4(io6,myre,ierr)
      if(ierr.NE.0) stop 'Halted in GETRCRD'
! --- Set date-time
      ibegbc=myre*100000+mjdaye*100+mhre
      iendbc=ibegbc
! --- Read data records
! --- Loop over chemical species/levels
      do isl=1,nszoutBC
!
! ---    Read concentration data
         if(LSGRIDBC) then
            if(lcomprsBC)then
               nwords=ix*iy
               read(io)ii
! --- 6.42_x1.1 140521
               call uncomprs(xwork1,ii,io,nwords,nchar,&
                             clab12,tsplv,tcg)
            else
               read(io) tsplv,tcg
            endif
            csptest=tsplv(1:12)
            if(csptest.NE.cspecbc(isl)) then
               ierr=2
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR -- BCON species does not match'
               write(io6,*)'      -- Expected species  :',cspecbc(isl)
               write(io6,*)'      -- BCON species      :',csptest
               write(*,*)
               write(*,*)'ERROR -- BCON species does not match --'
               return
            endif
         endif
!
         if(ndrecBC .GT. 0) then
            if(lcomprsBC)then
               read(io)ii
! --- 6.42_x1.1 140521
               call uncomprs(xwork2,ii,io,idrec,nchar,&
                             clab12,tsplv,tcd)
            else
               read(io) tsplv,tcd
            endif
            csptest=tsplv(1:12)
            if(csptest.NE.cspecbc(isl)) then
               ierr=2
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR -- BCON species does not match'
               write(io6,*)'      -- Expected species  :',cspecbc(isl)
               write(io6,*)'      -- BCON species      :',csptest
               write(*,*)
               write(*,*)'ERROR -- BCON species does not match --'
               return
            endif
         endif
!
         if(nctrecBC .GT. 0) then
            if(lcomprsBC)then
               read(io)ii
! --- 6.42_x1.1 140521
               call uncomprs(xwork3,ii,io,itrec,nchar,&
                             clab12,tsplv,tct)
            else
               read(io) tsplv,tct
            endif
            csptest=tsplv(1:12)
            if(csptest.NE.cspecbc(isl)) then
               ierr=2
               write(io6,*)
               write(io6,*)'*********  FATAL  ***********'
               write(io6,*)'ERROR -- BCON species does not match'
               write(io6,*)'      -- Expected species  :',cspecbc(isl)
               write(io6,*)'      -- BCON species      :',csptest
               write(*,*)
               write(*,*)'ERROR -- BCON species does not match --'
               return
            endif
         endif
! ---    Update boundary concentrations
         do k=1,nbc
            if(jrecbc(k).EQ.0) then
               conbc(isl,k)=tcd(irecbc(k))
            else
               conbc(isl,k)=tcg(irecbc(k),jrecbc(k))
            endif
         enddo
      enddo
!
      return
!
!  EOF encountered in date-time record
999   ierr=1
      return
!
      end
!----------------------------------------------------------------------
      subroutine uncomprs(xwork,ii,io,nwords,nchar,clab12,clab15,xdat)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521          UNCOMPRS
!                J. Scire, EARTH TECH
!
! --- PURPOSE:  Read a compressed data records and uncompress
!               the data
!
! --- UPDATE
!
! --- V5.(960422) to V6.42_x1.1(140521)
!               Label may be either 12 or 15 characters
!
! --- INPUTS:
!        XWORK(nwork) - real array - Work array to temporarily store
!                                    compressed array
!                  II - integer    - Number of words in compressed
!                                    data record
!                  IO - integer    - Unit number of input file
!              NWORDS - integer    - Number of values in data array
!                                    after uncompression
! --- 6.42_x1.1 140521
!               NCHAR - integer    - Number of characters in label
!
!      PARAMETERS: IO6
!
! --- OUTPUTS:
!
! --- 6.42_x1.1 140521
!              CLAB12 - char*12    - Character record header
!              CLAB15 - char*15    - Character record header
!
!        XDAT(nwords) - real array - Array of uncompressed data to be
!                                    output
!
! --- UNCOMPRS called by: GETRCRD
! --- UNCOMPRS calls:     none
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      real xwork(ii),xdat(nwords)
! --- 6.42_x1.1 140521
      character*12 clab12
      character*15 clab15
! --- Read the compressed data record
      if(nchar.EQ.12) then
         read(io)clab12,xwork
      elseif(nchar.EQ.15) then
         read(io)clab15,xwork
      else
         write(io6,*)'ERROR in Subr. UNCOMPRS -- label must be ',&
         'either 12 or 15 --  NCHAR = ',nchar
         stop
      endif
! --- Uncompress the data
      jj=0
      do 100 i=1,ii
      if(xwork(i).gt.0.0)then
         jj=jj+1
         xdat(jj)=xwork(i)
      else
         nzero=-xwork(i)
         do j=1,nzero
            jj=jj+1
            xdat(jj)=0.0
         enddo
      endif
100   continue
!
! --- QA check that expanded array is correct size
      if(jj.ne.nwords)then
         write(io6,*)'ERROR in Subr. UNCOMPRS -- Expanded array ',&
         'is not the correct size -- JJ = ',jj,' NWORDS = ',nwords
         stop
      endif
!
      return
      end
!----------------------------------------------------------------------
      subroutine tchiflx(imode)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 031017           TCHIFLX
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Manage concentration, dry & wet deposition flux
!               fields for totals and source contributions
!
! --- INPUTS:
!                 IMODE - integer    - Update mode
!                    1: Add source contribution to Total CHIFLX arrays
!                    2: Swap Total CHIFLX arrays into standard arrays
!
!     Common block /CHIFLX/ variables
!          CHISAM(mxnxg,mxnyg,mxspec), DFSAM(mxnxg,mxnyg,mxspec),
!          WFSAM(mxnxg,mxnyg,mxspec), CHIREC(mxrec,mxspec),
!          DFREC(mxrec,mxspec), WFREC(mxrec,mxspec),
!          CHICT(mxrect,mxspec)
!          TCHISAM(mxnxg,mxnyg,mxspec), TDFSAM(mxnxg,mxnyg,mxspec),
!          TWFSAM(mxnxg,mxnyg,mxspec), TCHIREC(mxrec,mxspec),
!          TDFREC(mxrec,mxspec), TWFREC(mxrec,mxspec),
!          TCHICT(mxrect,mxspec)
!     Common block /CTSGDAT/ variables
!          NCTREC
!     Common block /GEN/ variables
!          NSPEC
!     Common block /GRID/ variables
!          NXSAM, NYSAM, LSAMP
!     Common block /NONGRD/ variables
!          NREC
!     Parameters: MXNXG, MXNYG, MXSPEC, MXREC, MXRECT,
!                 IO6
!
! --- OUTPUT:  none
!
! --- TCHIFLX called by: COMP
! --- TCHIFLX calls:     none
!----------------------------------------------------------------------
!
! --- Include parameter statements
      include 'params.puf'
!
      include 'chiflx.puf'
      include 'ctsgdat.puf'
      include 'gen.puf'
      include 'grid.puf'
      include 'nongrd.puf'
!
      if(imode.EQ.1) then
! ---    Add source contribution to Total CHIFLX arrays
! ---    Gridded receptor concentrations
         if(lsamp) then
           do k=1,nspec
             do j=1,nysam
               do i=1,nxsam
                 tchisam(i,j,k)=tchisam(i,j,k)+chisam(i,j,k)
                 tdfsam(i,j,k) =tdfsam(i,j,k)+dfsam(i,j,k)
                 twfsam(i,j,k) =twfsam(i,j,k)+wfsam(i,j,k)
               enddo
             enddo
           enddo
         endif
! ---    Discrete receptor concentrations
         if(nrec.GT.0) then
           do k=1,nspec
             do i=1,nrec
               tchirec(i,k)=tchirec(i,k)+chirec(i,k)
               tdfrec(i,k) =tdfrec(i,k)+dfrec(i,k)
               twfrec(i,k) =twfrec(i,k)+wfrec(i,k)
             enddo
           enddo
         endif
! ---    Discrete CTSG receptor concentrations
         if(nctrec.GT.0) then
           do k=1,nspec
             do i=1,nctrec
               tchict(i,k)=tchict(i,k)+chict(i,k)
             enddo
           enddo
         endif
      elseif(imode.EQ.2) then
! ---    Swap from Total CHIFLX arrays into standard CHIFLX arrays
! ---    Gridded receptor concentrations
         if(lsamp) then
           do k=1,nspec
             do j=1,nysam
               do i=1,nxsam
                 chisam(i,j,k)=tchisam(i,j,k)
                 dfsam(i,j,k) =tdfsam(i,j,k)
                 wfsam(i,j,k) =twfsam(i,j,k)
               enddo
             enddo
           enddo
         endif
! ---    Discrete receptor concentrations
         if(nrec.GT.0) then
           do k=1,nspec
             do i=1,nrec
               chirec(i,k)=tchirec(i,k)
               dfrec(i,k) =tdfrec(i,k)
               wfrec(i,k) =twfrec(i,k)
             enddo
           enddo
         endif
! ---    Discrete CTSG receptor concentrations
         if(nctrec.GT.0) then
           do k=1,nspec
             do i=1,nctrec
               chict(i,k)=tchict(i,k)
             enddo
           enddo
         endif
      endif
      return
      end
!-----------------------------------------------------------------------
      subroutine warn(astring,value,zeroval)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108               WARN
!                D. Strimaitis
!
! --- PURPOSE:  Screens data associated with potential problems and
!               reports outcome to list file (diagnostic)
!
! --- UPDATES:
! --- V5.721 (040503) to V6.268(100108)  (DGS)
!        Add logic for processing SIGTZ as well as SIGTY
!        Add minimum reporting values to argument list
!
! --- INPUTS:
!       ASTRING - char*12 - subroutine/action string
!         VALUE - real    - value to process
!       ZEROVAL - real    - threshold travel time (sec) or distance (km)
!                           for counting (calling this routine)
!
! --- Parameters: IO6
!
! --- OUTPUT:
!             (List File)
!
! --- WARN called by: SIGTY, FIN
! --- WARN calls:     none
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
      logical LWARNY,LWARNZ
      character*12 astring
! --- Initialize via data statements
      data lwarny/.FALSE./,lwarnz/.FALSE./
      data sigtysec/0.0/, ntsigty/0/
      data sigtykm/0.0/, nxsigty/0/
      data sigtzsec/0.0/, ntsigtz/0/
      data sigtzkm/0.0/, nxsigtz/0/
      if(astring.EQ.'SIGTY-t     ') then
! ---    Process negative delta-t from SIGTY
         if(value.LT.sigtysec) sigtysec=value
         ntsigty=ntsigty+1
         ntsigty=MIN(ntsigty,1000001)
         lwarny=.TRUE.
         zerosecy=zeroval
      elseif(astring.EQ.'SIGTY-x     ') then
! ---    Process negative delta-x from SIGTY
         if(value.LT.sigtykm) sigtykm=value
         nxsigty=nxsigty+1
         nxsigty=MIN(nxsigty,1000001)
         lwarny=.TRUE.
         zeromy=zeroval*1000.
      elseif(astring.EQ.'SIGTZ-t     ') then
! ---    Process negative delta-t from SIGTZ
         if(value.LT.sigtzsec) sigtzsec=value
         ntsigtz=ntsigtz+1
         ntsigtz=MIN(ntsigtz,1000001)
         lwarnz=.TRUE.
         zerosecz=zeroval
      elseif(astring.EQ.'SIGTZ-x     ') then
! ---    Process negative delta-x from SIGTZ
         if(value.LT.sigtzkm) sigtzkm=value
         nxsigtz=nxsigtz+1
         nxsigtz=MIN(nxsigtz,1000001)
         lwarnz=.TRUE.
         zeromz=zeroval*1000.
      elseif(astring.EQ.'FIN         ') then
! ---    Report diagnostic
         if(LWARNY) then
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'Diagnostic for SIGTY:'
            write(io6,'(a25,f8.3)')' Most Negative dt(sec) = ',&
                                     sigtysec
            if(ntsigty.GT.100000) then
               write(io6,3)'  Number less than ',zerosecy,'s > ',&
                              ntsigty
            else
               write(io6,3)'  Number less than ',zerosecy,'s = ',&
                              ntsigty
            endif
            write(io6,'(a25,f8.3)')' Most Negative dx(m)   = ',&
                                     (1000.*sigtykm)
            if(nxsigty.GT.100000) then
               write(io6,3)'  Number less than ',zeromy,'m > ',&
                              nxsigty
            else
               write(io6,3)'  Number less than ',zeromy,'m = ',&
                              nxsigty
            endif
            write(io6,*)
            write(io6,*)
         endif
         if(LWARNZ) then
            write(io6,*)
            write(io6,*)
            write(io6,*)
            write(io6,*)'Diagnostic for SIGTZ:'
            write(io6,'(a25,f8.3)')' Most Negative dt(sec) = ',&
                                     sigtzsec
            if(ntsigtz.GT.100000) then
               write(io6,3)'  Number less than ',zerosecz,'s > ',&
                              ntsigtz
            else
               write(io6,3)'  Number less than ',zerosecz,'s = ',&
                              ntsigtz
            endif
            write(io6,'(a25,f8.3)')' Most Negative dx(m)   = ',&
                                     (1000.*sigtzkm)
            if(nxsigtz.GT.100000) then
               write(io6,3)'  Number less than ',zeromz,'m > ',&
                              nxsigtz
            else
               write(io6,3)'  Number less than ',zeromz,'m = ',&
                              nxsigtz
            endif
            write(io6,*)
            write(io6,*)
         endif
      else
! ---    Problem here
         write(*,*)
         write(*,*)'ERROR in subroutine WARN!'
         write(*,*)'Unexpected arguments:'
         write(*,*)'    arg1 = ',astring
         write(*,*)'    arg2 = ',value
         write(*,*)
         stop
      endif
      return
3     format(a19,f8.3,a4,i20)
      end
!----------------------------------------------------------------------
      subroutine aerswv(io6,ldbhr,ustar,xl,wstar,zi,ziconv,zimech,&
                        uatzi,z,sigw,sigv)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050225            AERSWV
!                D. Strimaitis   Earth Tech, Inc.
!
! --- PURPOSE:  Compute the standard deviation of vertical velocity
!               (sigw) and the horizontal crosswind velocity (sigv)
!               based on micrometeorological parameters, using
!               AERMOD formulation.
!
! --- INPUTS:
!
!               IO6 - integer - Unit number for list file
!             LDBHR - logical - Debug output (T,F)
!             USTAR - real    - Friction velocity (m/s)
!             WSTAR - real    - Convective velocity scale (m/s)
!                XL - real    - Monin-Obukhov length (m)
!                ZI - real    - Mixing height (m)
!            ZICONV - real    - Convective Mixing height (m)
!            ZIMECH - real    - Mechanical Mixing height (m)
!             UATZI - real    - Wind speed at ZI (m/s)
!                 Z - real    - Plume height (m)
!
! --- OUTPUTS:
!
!              SIGW - real    - Standard deviation of vertical
!                               velocity (m/s)
!              SIGV - real    - Standard deviation of horizontal
!                               crosswind velocity (m/s)
!
! --- AERSWV called by:  TURBSET
! --- AERSWV calls:      REFSV, REFSW
!
!----------------------------------------------------------------------
!
      logical ldbhr
      data xlmn/1.e-2/
!
      sigv=0.0
      sigw=0.0
!
! --- Enforce minimum M-O length to prevent numerical problems
      xmon=AMIN1(xlmn,ABS(xl))
      if(xl.LT.0.0) xmon=-xmon
! --- Set the residual sigma-w at the top of the boundary layer
      swrmax = 0.02*uatzi
! --- Obtain sigmas using AERMOD subroutine
      call REFSV ( z,ustar,wstar,xmon,ziconv,zimech, sigv )
      call REFSW ( z,ustar,wstar,xmon,zi,ziconv,swrmax, sigw )
!
      if(LDBHR) then
         write(io6,*)'AERSWV:  (MDISP = 2)'
         write(io6,*)'       el,ustar,wstar= ',xl,ustar,wstar
         write(io6,*)'       uatzi,z,zi    = ',uatzi,z,zi
         write(io6,*)'       ziconv,zimech = ',ziconv,zimech
         write(io6,*)'       sigv,sigw     = ',sigv,sigw
         write(io6,*)'       (before minimum values imposed)'
      endif
!
      return
      end
!----------------------------------------------------------------------
      SUBROUTINE REFSV ( HEIGHT,ustar,wstar,xmon,ziconv,zimech,&
                         VALUE )
! dgs SUBROUTINE REFSV ( HEIGHT, VALUE )
!=======================================================================
!                REFSV Module of the AERMOD Dispersion Model
!
!   Purpose:     To compute the value of sigma-V, the horizontal
!                (lateral) dispersion parameter from the surface
!                friction velocity and convective scaling velocity
!                alone (i.e., no observations of sigma-V)
!
!   Input:       Mixing height (ZI)
!                Friction velocity (USTAR)
!                Convective scaling velocity (WSTAR)
!                Height at which sigma-V is needed (HEIGHT)
!
!   Output:      sigmaV at the required level (VALUE)
!
!   Assumptions: Mixing height, friction and convective scaling
!                velocities are nonmissing
!
!   Programmer:  Jim Paumier                        30 September 1993
!                Pacific Environmental Services
!
!   Revision history:
!                <none>
!
!   Reference(s):
!
!-----------------------------------------------------------------------
!
!---- Variable declarations
!
! dgs USE MAIN1
! dgs IMPLICIT NONE
! dgs CHARACTER MODNAM*12
! dgs SAVE
      REAL      HEIGHT, VALUE, SVC, SVM
!
!---- Data dictionary
!
!---- Data initializations
!
! dgs MODNAM = 'REFSV '
!
!.......................................................................
!
! dgs IF( UNSTAB )THEN
      if(xmon.LT.0.0) then
!        Compute the convective component of sigma-v, SVC
! dgs    CALL REFSVC( HEIGHT, SVC )
         CALL REFSVC( HEIGHT,wstar,ziconv, SVC )
!        Compute the mechanical component of sigma-v, SVM
! dgs    CALL REFSVM( HEIGHT, SVM )
         CALL REFSVM( HEIGHT,ustar,zimech, SVM )
         VALUE = SQRT( SVC*SVC + SVM*SVM )
! dgs ELSEIF( STABLE )THEN
      ELSE
!        Compute the mechanical component of sigma-v, SVM
! dgs    CALL REFSVM( HEIGHT, SVM )
         CALL REFSVM( HEIGHT,ustar,zimech, SVM )
         VALUE = SVM
      ENDIF
!
      RETURN
      END
!----------------------------------------------------------------------
      SUBROUTINE REFSVC ( HEIGHT,wstar,ziconv, VALUE )
! dgs SUBROUTINE REFSVC ( HEIGHT, VALUE )
!=======================================================================
!                REFSVC Module of the AERMOD Dispersion Model
!
!   Purpose:     To compute the value of sigma-V, the horizontal
!                (lateral) dispersion parameter from the surface
!                friction velocity and convective scaling velocity
!                alone (i.e., no observations of sigma-V)
!
!   Input:       Mixing height (ZI)
!                Friction velocity (USTAR)
!                Convective scaling velocity (WSTAR)
!                Height at which sigma-V is needed (HEIGHT)
!
!   Output:      sigmaV at the required level (VALUE)
!
!   Assumptions: Mixing height, friction and convective scaling
!                velocities are nonmissing
!
!   Programmer:  Jim Paumier                        30 September 1993
!                Pacific Environmental Services
!
!   Revision history:
!                <none>
!
!   Reference(s):
!
!-----------------------------------------------------------------------
!
!---- Variable declarations
!
! dgs USE MAIN1
! dgs IMPLICIT NONE
! dgs CHARACTER MODNAM*12
! dgs SAVE
      REAL      HEIGHT, VALUE, SV2, ZDCRS, ATZI, SV2DCR, VAL2
!
!---- Data dictionary
!     SV2    = variance of the horizontal component of the wind
!     ZDCRS  = value of 1.2 * ZICONV used in profiling SV2
!     ATZI   = value of SV at ZICONV
!     AT1PT2 = the fraction of and above the mixing height
!              through which the parameter is changing
!
!     AT1PT2 is defined in a PARAMETER statement in MAIN1.INC
!
      PARAMETER (AT1PT2 = 1.2)
!---- Data initializations
!
! dgs MODNAM = 'REFSVC'
      ZDCRS  =  AT1PT2 * ZICONV
!
!.......................................................................
!
      SV2 = 0.35 * WSTAR**2
!
      IF( HEIGHT  .LE.  ZICONV )THEN
         VALUE = SQRT( SV2 )
      ELSEIF( HEIGHT .GT. ZICONV  .AND.  HEIGHT .LE. ZDCRS )THEN
!        COMPUTE sigmaV at 1.2*ZI
         SV2DCR = AMIN1( SV2, 0.25 )
!        INTERPOLATE between value of SV2 at ZI and at 1.2*ZI
         CALL GINTRP ( ZICONV, SV2, ZDCRS, SV2DCR, HEIGHT, VAL2 )
         VALUE = SQRT( VAL2 )
      ELSE   ! requested height is above 1.2*mixing height
         ATZI  = SQRT( SV2 )
         VALUE = AMIN1( ATZI, 0.5 )
      ENDIF
!
      RETURN
      END
!----------------------------------------------------------------------
      SUBROUTINE REFSVM ( HEIGHT,ustar,zimech, VALUE )
! dgs SUBROUTINE REFSVM ( HEIGHT, VALUE )
!=======================================================================
!                REFSVM Module of the AERMOD Dispersion Model
!
!   Purpose:     To compute the value of sigma-V, the horizontal
!                (lateral) dispersion parameter from the surface
!                friction velocity and convective scaling velocity
!                alone (i.e., no observations of sigma-V)
!
!   Input:       Mixing height (ZI)
!                Friction velocity (USTAR)
!                Convective scaling velocity (WSTAR)
!                Height at which sigma-V is needed (HEIGHT)
!
!   Output:      sigmaV at the required level (VALUE)
!
!   Assumptions: Mixing height, friction and convective scaling
!                velocities are nonmissing
!
!   Programmer:  Jim Paumier                        30 September 1993
!                Pacific Environmental Services
!
!   Revision history:
!                <none>
!
!   Reference(s):
!
!-----------------------------------------------------------------------
!
!---- Variable declarations
!
! dgs USE MAIN1
! dgs IMPLICIT NONE
! dgs CHARACTER MODNAM*12
! dgs SAVE
      REAL      HEIGHT, VALUE, SV02, SV2ZI, VARV
!
!---- Data dictionary
!     SV02   = variance of the horizontal component of the wind at the
!              surface.
!
!---- Data initializations
!
! dgs MODNAM = 'REFSVM'
!
!.......................................................................
!
!     Compute SV2 at the surface
      SV02 = 3.6 * USTAR**2
!
!     Compute SV2 at ZI;
!     Do not let SV2 at ZI exceed the surface value.
      SV2ZI = AMIN1( SV02, 0.25 )
      IF( HEIGHT  .LE.  ZIMECH )THEN
!        INTERPOLATE between these two values of the variance if HEIGHT is
!        below ZIMECH
         CALL GINTRP ( 0., SV02, ZIMECH, SV2ZI, HEIGHT, VARV )
         VALUE = SQRT(VARV)
      ELSE
!        Persist value at ZIMECH upward.
         VALUE = SQRT(SV2ZI)
      ENDIF
!
      RETURN
      END
!----------------------------------------------------------------------
      SUBROUTINE REFSW ( HEIGHT,ustar,wstar,xmon,zi,ziconv,swrmax,&
                         VALUE )
!     SUBROUTINE REFSW ( HEIGHT, VALUE )
!=======================================================================
!                REFSW Module of the AERMOD Dispersion Model
!
!   Purpose:     To compute the value of sigma-W, the vertical
!                dispersion parameter from the surface friction
!                velocity and convective scaling velocity alone
!                (i.e., no observations of sigma-W)
!
!   Input:
!
!
!
!
!   Output:      Value of the parameter at the required level
!
!   Assumptions: Mixing height, friction and convective scaling
!                velocities are nonmissing
!
!   Programmer:  Jim Paumier                        30 September 1993
!                Pacific Environmental Services
!
!   Revision history:
!                Jim Paumier, PES                    5 July 1995
!                  - modified the coefficient on the u* term for CBL;
!                  - made the value 1.2 a parameter in MAIN1;
!                  - modified the form of sigma-w at all height for SBL;
!
!   Reference(s):
!
!-----------------------------------------------------------------------
!
!---- Variable declarations
!
! dgs USE MAIN1
! dgs IMPLICIT NONE
! dgs CHARACTER MODNAM*12
! dgs SAVE
      REAL      HEIGHT, VALUE, SWC, SWM
!
!---- Data dictionary
!
!     SWC    = convective component of the variance of the vertical velocity
!     SWM    = mechanical component of the variance of the vertical velocity
!
!---- Data initializations
!
! dgs MODNAM = 'REFSW '
!
!.......................................................................
!
! dgs IF (UNSTAB )THEN
      if(xmon.LT.0.0) then
!        Compute the convective component of sigma-w, SWC
! dgs    CALL REFSWC( HEIGHT, SWC )
         CALL REFSWC( HEIGHT,wstar,ziconv, SWC )
!        Compute the mechanical component of sigma-w, SWM
! dgs    CALL REFSWM( HEIGHT, SWM )
         CALL REFSWM( HEIGHT,ustar,zi,swrmax, SWM )
!        Apply lower limit to sigma-w to avoid potential math errors
         SWC = AMAX1( SWC, 0.00001 )
         SWM = AMAX1( SWM, 0.00001 )
         VALUE = SQRT( SWC*SWC + SWM*SWM )
!rwb     Output convective and mechanical components of profile.
!rwb         if (METEOR) then
!rwb            if (height .eq. 0.0) then
!rwb               write(dbmunt,909) kurdat, ziconv, zimech, zi
!rwb909            format(1x,'DATE= ',i8,2x,'ZIc= ',f8.2,2x,'ZIm= ',f8.2,2x,
!rwb     &                'ZI = ',f8.2,/,
!rwb     &          '   GRIDHT    SIGW_COVN      SIGW_MECH       SIGW_TOT')
!rwb            end if
!rwb            write(dbmunt,919) height, swc, swm, value
!rwb919         format(1x,f8.2,3(3x,g13.5))
!rwb         end if
! dgs ELSE IF (STABLE) THEN
      ELSE
!        Compute the mechanical component of sigma-w, SWM
! dgs    CALL REFSWM( HEIGHT, SWM )
         CALL REFSWM( HEIGHT,ustar,zi,swrmax, SWM )
         VALUE = AMAX1( SWM, 0.00001 )
      END IF
      RETURN
      END
!----------------------------------------------------------------------
      SUBROUTINE REFSWC ( HEIGHT,wstar,ziconv, VALUE )
! dgs SUBROUTINE REFSWC ( HEIGHT, VALUE )
!=======================================================================
!                REFSWC Module of the AERMOD Dispersion Model
!
!   Purpose:     To compute the value of sigma-W, the vertical
!                dispersion parameter from the surface friction
!                velocity and convective scaling velocity alone
!                for the convective boundary layer.
!                (i.e., no observations of sigma-W)
!
!   Input:
!
!
!
!
!   Output:      Value of the parameter at the required level
!
!   Assumptions: Mixing height, friction and convective scaling
!                velocities are nonmissing
!
!   Programmer:  Jim Paumier                        30 September 1993
!                Pacific Environmental Services
!
!   Revision history:
!                Jim Paumier, PES                    5 July 1995
!                  - modified the coefficient on the u* term for CBL;
!                  - made the value 1.2 a parameter in MAIN1;
!                  - modified the form of sigma-w at all height for SBL;
!
!   Reference(s):
!
!-----------------------------------------------------------------------
!
!---- Variable declarations
!
! dgs USE MAIN1
! dgs IMPLICIT NONE
! dgs CHARACTER MODNAM*12
! dgs SAVE
      REAL      HEIGHT, VALUE, SW2
!
!---- Data dictionary
!
!     SW2    = variance of the vertical velocity
!
!---- Data initializations
!
! dgs MODNAM = 'REFSWC'
!
!.......................................................................
!
      IF( HEIGHT  .LE.  0.1*ZICONV )THEN
         SW2 = 1.6 * ( HEIGHT / ZICONV )**0.667 * WSTAR**2
         VALUE  = SQRT( SW2 )
      ELSEIF( HEIGHT .GT. 0.1*ZICONV  .AND.  HEIGHT .LE. ZICONV )THEN
         VALUE = SQRT( 0.35 * WSTAR**2 )
      ELSEIF( HEIGHT .GT. ZICONV )THEN
         SW2 = 0.35 * WSTAR**2 * EXP( -(6.*(HEIGHT - ZICONV)/ZICONV) )
         VALUE = SQRT( SW2 )
      ENDIF
!
      RETURN
      END
!----------------------------------------------------------------------
      SUBROUTINE REFSWM ( HEIGHT,ustar,zi,swrmax, VALUE )
! dgs SUBROUTINE REFSWM ( HEIGHT, VALUE )
!=======================================================================
!                REFSWM Module of the AERMOD Dispersion Model
!
!   Purpose:     To compute the value of sigma-W, the vertical
!                dispersion parameter from the surface friction
!                velocity and convective scaling velocity alone
!                for the mechanical boundary layer.
!                (i.e., no observations of sigma-W)
!
!   Input:
!
!
!
!
!   Output:      Value of the parameter at the required level
!
!   Assumptions: Mixing height, friction and convective scaling
!                velocities are nonmissing
!
!   Programmer:  Jim Paumier                        30 September 1993
!                Pacific Environmental Services
!
!   Revision history:
!                Jim Paumier, PES                    5 July 1995
!                  - modified the coefficient on the u* term for CBL;
!                  - made the value 1.2 a parameter in MAIN1;
!                  - modified the form of sigma-w at all height for SBL;
!
!   Reference(s):
!
!-----------------------------------------------------------------------
!
!---- Variable declarations
!
! dgs USE MAIN1
! dgs IMPLICIT NONE
! dgs CHARACTER MODNAM*12
! dgs SAVE
      REAL      HEIGHT, VALUE, SWR, SWBL
!
!---- Data dictionary
!
!     SWR    = variance of the vertical velocity due to residual turbulence
!     SWBL   = variance of the vertical velocity in the boundary layer
!
!---- Data initializations
!
! dgs MODNAM = 'REFSWM'
!
!.......................................................................
!
!
      SWR  = SWRMAX * MIN( 1.0, HEIGHT/ZI)
      IF (HEIGHT .LT. ZI) THEN
         SWBL = 1.3 * USTAR * SQRT( 1.0 - HEIGHT/ZI)
      ELSE
         SWBL = 0.0
      END IF
      VALUE = SQRT ( SWR*SWR + SWBL*SWBL )
      RETURN
      END
!----------------------------------------------------------------------
      SUBROUTINE GINTRP ( HTBELO,VBELOW, HTABOV,VABOVE, REQDHT,VALUE )
!***********************************************************************
!             GINTRP Module of the AMS/EPA Regulatory Model - AERMOD
!
!   Purpose:     A generalized interpolation routine
!
!   Input:       Height below the required height (HTBELO)
!                Value below the required height (VBELOW)
!                Height above the required height (HTBELO)
!                Value above the required height (VBELOW)
!                Height at which a value is required (REQDHT)
!
!   Output:      Value of the parameter at the required level (VALUE)
!
!   Called by:   Utility routine called by many modules
!
!   Assumptions:
!
!   Programmer:  Jim Paumier, PES, Inc.
!
!   Date:        September 30, 1993
!
!   Revision history:
!                <none>
!
!   Reference(s):
!
!***********************************************************************
!
!---- Variable declarations
!
      REAL   VALUE, HTBELO, VBELOW, HTABOV, VABOVE, REQDHT
!
!---- Data dictionary
!
!---- Data initializations
!
!.......................................................................
!
!---- Interpolate
      VALUE = VBELOW + ( (REQDHT - HTBELO) / (HTABOV - HTBELO) ) *&
                         (VABOVE - VBELOW)
      RETURN
      END
!-----------------------------------------------------------------------
      subroutine tauly(ldb,sigv,sigw,el,zi,zht,tau)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050225              TAULY
!                D. Strimaitis   Earth Tech, Inc.
!
! --- PURPOSE:  Estimates the Lagrangian timescale using elements from
!               SCIPUFF
!
! --- INPUTS:
!             LDB - logical  - Flag controlling printing of debug data
!            sigv - real     - Sigma-v (m/s)
!            sigw - real     - Sigma-w (m/s)
!              el - real     - Monin-Obukhov length (m)
!              zi - real     - Surface boundary layer height (m)
!             zht - real     - Height above ground (m)
!
! --- OUTPUT:
!             tau - real     - Lagrangian timescale (s) for lateral
!                              wind fluctuations
!
!
! --- TAULY called by: SETCSIG
! --- TAULY calls:     none
!----------------------------------------------------------------------
      include 'params.puf'
      logical LDB
! --- Set lateral length scale above boundary layer (m)
      data ymeso/1000./
! --- Do not evaluate timescale nearer the surface than zmin (m)
      data zmin/5./
      data vonk/0.4/
! --- Zi should not be zero (e.g., < 1.)
      if(zi.LT.1.) then
         write(io6,*)
         write(io6,*)'Fatal ERROR in subroutine TAULY'
         write(io6,*)'Mixing height is too small: ',zi
         stop 'Halted in TAULY -- see list file for details.'
      endif
      z=AMAX1(zht,zmin)
! --- Total variance (assume sigma-u = sigma-v)
      q=SQRT(2.*sigv*sigv+sigw*sigw)
      if(q.LE.0.) then
         write(io6,*)
         write(io6,*)'Fatal ERROR in subroutine TAULY'
         write(io6,*)'Turbulence is zero'
         stop 'Halted in TAULY -- see list file for details.'
      endif
! --- Estimate length scale
      if(z.GT.zi) then
! ---    Length scale is fixed above zi
         yl=ymeso
      else
! ---    Length scale is a weighted average of shear and buoyancy
! ---    scales
         ylshear=1./(SQRT(1./(0.3*zi)**2 + 1./(0.65*z)**2))
         ylbuoy=0.3*zi
         if(el.GE.0.) then
! ---       Stable - neutral
            wtshear=1.0
            wtbuoy=0.0
         else
! ---       Convective - neutral
            zbyzi=z/zi
            a=1.-zbyzi
            b=0.052*(-zi/(vonk*el))**0.66667
            wtshear=a/(a+b*(1+1.5*EXP(-zbyzi)))
         endif
         wtbuoy=1.-wtshear
         yl=wtshear*ylshear+wtbuoy*ylbuoy
      endif
! --- Compute time scale
      tau=yl/(0.75*q)
      if(LDB) then
         write(io6,*)'TAULY:  (Lagragian timescale - horizontal)'
         write(io6,*)'       el,sigv,sigw  = ',el,sigv,sigw
         write(io6,*)'       zht,zi        = ',zht,zi
         write(io6,*)'     ylshear,ylbuoy  = ',ylshear,ylbuoy
         write(io6,*)'     wtshear,wtbuoy  = ',wtshear,wtbuoy
         write(io6,*)'            TAU, YL  = ',tau, yl
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine tperiod(ibe,ndhr,nsec,ndhrb,nsecb,ndhre,nsece,ilocate)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 990709           TPERIOD
!                D. Strimaitis,  Earth Tech
!
! --- PURPOSE:  Tests year/day/hour and seconds variables at beginning
!               and end of period against target date-time to see if
!               target lies within period
!
! --- INPUTS:
!           IBE - integer - Treatment of Beginning and End of period
!                           -1: Beginning time within period (not end)
!                            0: Beginning & End are within period
!                            1: Ending time within period (not begin)
!          NDHR - integer - YYYYJJJHH of target (year-day-hour)
!          NSEC - integer - SSSS of target (seconds)
!         NDHRB - integer - YYYYJJJHH at beginning of period
!         NSECB - integer - SSSS at beginning of period
!         NDHRE - integer - YYYYJJJHH at end of period
!         NSECE - integer - SSSS at end of period
!
!
! --- OUTPUT:
!       ILOCATE - integer - Location of target relative to period
!                           -1: target preceeds period
!                            0: target within period
!                            1: target lies beyond period
!
! --- TPERIOD called by:  COMP, METQA, EMQA, SIGSET
!                         RDMET, RDSIG, RDISC,RDPLM, RDMET4
! --- TPERIOD calls:      TCLOCK
!----------------------------------------------------------------------
      ilocate=0
      if(ndhr.LT.ndhrb) then
! ---    Target date-hr before start of period
         ilocate=-1
      elseif(ndhr.GT.ndhre) then
! ---    Target date-hr after end of period
         ilocate=1
      elseif(ndhr.EQ.ndhrb) then
         if(nsec.LT.nsecb) then
! ---       Target time before start of period
            ilocate=-1
         elseif(ibe.EQ.1 .AND. nsec.EQ.nsecb) then
! ---       Target time before start of period
            ilocate=-1
         elseif(ndhrb.EQ.ndhre) then
            if(nsec.GT.nsece) then
! ---          Target time after end of period
               ilocate=1
            elseif(ibe.EQ.-1 .AND. nsec.EQ.nsece) then
! ---          Target time after end of period
               ilocate=1
            endif
         endif
      elseif(ndhr.EQ.ndhre) then
         if(nsec.GT.nsece) then
! ---       Target time after end of period
            ilocate=1
         elseif(ibe.EQ.-1 .AND. nsec.EQ.nsece) then
! ---       Target time after end of period
            ilocate=1
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine tclock(io,ndhr,nyr,njul,nhr,nsec)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915            TCLOCK
!                D. Strimaitis,  Earth Tech
!
! --- PURPOSE:  Tests year/day/hour and seconds variables and changes
!               them if needed to fall within standard clock times
!               (HH:SSSS) between 00:0000 and 23:3599
!
! --- INPUTS:
!            IO - integer - List file unit number
!          NDHR - integer - YYYYJJJHH (year-Julian day-hour)
!           NYR - integer - YYYY (year)
!          NJUL - integer - JJJ (Julian day)
!           NHR - integer - HH (hour)
!          NSEC - integer - SSSS (seconds)
!
!
! --- OUTPUT:
!          NDHR - integer - YYYYJJJHH of target (year-day-hour)
!           NYR - integer - YYYY (year)
!          NJUL - integer - JJJ (Julian day)
!           NHR - integer - HH (hour)
!          NSEC - integer - SSSS of target (seconds)
!
! --- TCLOCK called by:  (utility)
! --- TCLOCK calls:      INCR
!----------------------------------------------------------------------
      if(ndhr.GT.0) then
! ---    Extract date/hour elements
         iyr=ndhr/100000
         jjjhh=ndhr-iyr*100000
         jul=jjjhh/100
         ihr=jjjhh-jul*100
      else
! ---    Assign input values
         iyr=nyr
         jul=njul
         ihr=nhr
      endif
      if(nsec.LT.3600 .AND. ihr.LT.24) return
! --- Condition times (HH:SSSS) to be between 00:0000 and 23:3599
      khr=nsec/3600
      nsec=nsec-khr*3600
      ihr=ihr+khr
      if(ihr.GT.23) then
         khr=ihr-23
         ihr=23
         call INCR(io,iyr,jul,ihr,khr)
      endif
      if(ndhr.GT.0) then
         ndhr=iyr*100000+jul*100+ihr
      else
         nyr=iyr
         njul=jul
         nhr=ihr
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine timeset
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915           TIMESET
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Set date/time variables for the start of the run
!
! --- INPUT:
!
!     Common block /GEN/
!        IBYR, IBMO, IBDAY, IBHR, IBSEC, NSECDT
!
! --- OUTPUT:
!
!     Common block /DATEHR/
!        nyrb,nmob,ndayb,njulb,nhrb,nsecb,ndathrb,
!        nyre,nmoe,ndaye,njule,nhre,nsece,ndathre,
!        nyrm,njulm,nhrm,nsecm,ndathrm,
!        nhrind
!        
!
! --- TIMESET called by: SETUP
! --- TIMESET calls:     JULDAY,  GRDAY,  INCRS
!----------------------------------------------------------------------
      include 'params.puf'
!
! --- Include common blocks
      include 'datehr.puf'
      include 'gen.puf'
! --- Mark periods with both the beginning and ending time
! --- Length of period is NSECDT in seconds
! --- Half of period is NSECDT2 seconds
      nsecdt2=nsecdt/2
!
! --- Set beginning time of first period
      nyrb=ibyr
      nmob=ibmo
      ndayb=ibdy
      nhrb=ibhr
      nsecb=ibsec
      call JULDAY(io6,nyrb,nmob,ndayb,njulb)
      ndathrb=nyrb*100000+njulb*100+nhrb
!
! --- Set time at the middle of first period
      nyrm=nyrb
      njulm=njulb
      nhrm=nhrb
      nsecm=nsecb
      call INCRS(io6,nyrm,njulm,nhrm,nsecm,nsecdt2)
      ndathrm=nyrm*100000+njulm*100+nhrm
!
! --- Set ending time of first period
      nyre=nyrb
      njule=njulb
      nhre=nhrb
      nsece=nsecb
      call INCRS(io6,nyre,njule,nhre,nsece,nsecdt)
      call GRDAY(io6,nyre,njule,nmoe,ndaye)
      ndathre=nyre*100000+njule*100+nhre
!
! --- Use midpoint of period to assign an hour index (1-24)
      nhrind=nhrm+1
      return
      end
!----------------------------------------------------------------------
      subroutine timeup(i)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 050915            TIMEUP
!                D. Strimaitis, Earth Tech
!
! --- PURPOSE:  Update date/time variables by 1 timestep
!
! --- INPUT:
!               I - integer      - Direction of update
!                                  i=+1  Forward 1 step
!                                  i=-1  Backward 1 step
!
!     Common block /DATEHR/
!        nyrb,nmob,ndayb,njulb,nhrb,nsecb,ndathrb,
!        nyre,nmoe,ndaye,njule,nhre,nsece,ndathre,
!        nyrm,njulm,nhrm,nsecm,ndathrm,
!     Common block /GEN/
!        NSECDT
!
! --- OUTPUT:
!
!     Common block /DATEHR/
!        nyrb,nmob,ndayb,njulb,nhrb,nsecb,ndathrb,
!        nyre,nmoe,ndaye,njule,nhre,nsece,ndathre,
!        nyrm,njulm,nhrm,nsecm,ndathrm,
!        nhrind
!        
!
! --- TIMEUP called by: COMP
! --- TIMEUP calls:     JULDAY,  GRDAY,  INCRS
!----------------------------------------------------------------------
      include 'params.puf'
!
! --- Include common blocks
      include 'datehr.puf'
      include 'gen.puf'
! --- Set the update step (seconds)
      nstep=nsecdt*i
      if(i.EQ.1) then
! ---    Forward 1 step
! ---    Beginning
         nyrb=nyre
         nmob=nmoe
         ndayb=ndaye
         njulb=njule
         nhrb=nhre
         nsecb=nsece
         ndathrb=ndathre
! ---    Mid-point
         call INCRS(io6,nyrm,njulm,nhrm,nsecm,nstep)
         ndathrm=nyrm*100000+njulm*100+nhrm
! ---    Ending
         call INCRS(io6,nyre,njule,nhre,nsece,nstep)
         call GRDAY(io6,nyre,njule,nmoe,ndaye)
         ndathre=nyre*100000+njule*100+nhre
      elseif(i.EQ.-1) then
! ---    Backward 1 step
! ---    Ending
         nyre=nyrb
         nmoe=nmob
         ndaye=ndayb
         njule=njulb
         nhre=nhrb
         nsece=nsecb
         ndathre=ndathrb
! ---    Mid-point
         call INCRS(io6,nyrm,njulm,nhrm,nsecm,nstep)
         ndathrm=nyrm*100000+njulm*100+nhrm
! ---    Beginning
         call INCRS(io6,nyrb,njulb,nhrb,nsecb,nstep)
         call GRDAY(io6,nyrb,njulb,nmob,ndayb)
         ndathre=nyre*100000+njule*100+nhre
      else
! ---    Argument must be either -1 or 1
         write(*,*)
         write(*,*)'FATAL ERROR in TIMEUP---'
         write(*,*)'The update step is not -1 or 1: ',i
         stop
      endif
! --- Use midpoint of period to assign an hour index (1-24)
      nhrind=nhrm+1
      return
      end
!-----------------------------------------------------------------------
      subroutine filvers(io,ab,fname,ftype,dataver)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918            FILVERS
!                D. Strimaitis
!
! --- PURPOSE:  Opens file of input data for the application and sets
!               the file version format based on information from one
!               or more header records.  (File is closed on exit)
!
! --- UPDATE
! --- v7.2.1 - v7.3.0  150918  (CDA)
!                               : add SPEMARB.DAT (Sprays)
! --- TNG-7.0.0 - TNG-7.1.0  141230  (DGS)
!                               : Trap file-open error
!                               : add RDEMARB.DAT (Roads)
! --- V6.116 - TNG-7.0.0  140913 (DGS)
!                               : add FLEMARB.DAT (Flares)
! --- V6.11-V6.116 070404  (DGS): change read for first record of the
!                                 restart file to access just the first
!                                 variable expected (avoids possibility
!                                 of attempting to read past end of
!                                 record)
! --- V6.1-V6.11   060309  (DGS): filename c*70 to c*132
!                                 remove LTYPE2 declaration (not used)
!
! --- INPUTS:
!            io - integer      - file unit number
!            ab - character*3  - file form (ASC or BIN)
!         fname - character*132- full filename
!         ftype - character*12 - type of file (e.g., 'BAEMARB')
!
!
! --- OUTPUT:
!       dataver - character*16 - file version identifier (e.g., '2.0')
!
! --- FILVERS called by: SETUP
! --- FILVERS calls:     OPEN_ERR
!----------------------------------------------------------------------
!
      parameter(mxftype=18)
      integer iquote(4)
      character*132 aline
      character*80 title(3)
      character*132 fname
      character*64 adoc
      character*16 anames(2,mxftype),blank16,var16
      character*16 dataset,dataver
      character*12 ftype
      character*12 ver0, var12
      character*8 vermet,levmet
      character*3 ab
      character*1 aquote
      logical lascii
! --- Set fixed characters
      data aquote/''''/
      data blank16/'                '/
! --- Set file type names
      data anames/'CALMET          ','CALMET.DAT      ',&
                  'ISCMET          ','ISCMET.DAT      ',&
                  'PLMMET          ','PLMMET.DAT      ',&
                  'PROFILE         ','PROFILE.DAT     ',&
                  'SURFACE         ','SURFACE.DAT     ',&
                  'PTEMARB         ','PTEMARB.DAT     ',&
                  'BAEMARB         ','BAEMARB.DAT     ',&
                  'LNEMARB         ','LNEMARB.DAT     ',&
                  'VOLEMARB        ','VOLEMARB.DAT    ',&
                  'OZONE           ','OZONE.DAT       ',&
                  'H2O2            ','H2O2.DAT        ',&
                  'COASTLN         ','COASTLN.DAT     ',&
                  'FLUXBDY         ','FLUXBDY.DAT     ',&
                  'BCON            ','BCON.DAT        ',&
                  'RESTART         ','RESTART.DAT     ',&
                  'FLEMARB         ','FLEMARB.DAT     ',&
                  'RDEMARB         ','RDEMARB.DAT     ',&
                  'SPEMARB         ','SPEMARB.DAT     '/
! --- Initialize dataset version
      dataver=blank16
! --- Identify file type
      itype=0
      do k=1,mxftype
         if(ftype.EQ.anames(1,k)) itype=k
      enddo
      if(itype.EQ.0) then
         write(*,*)'FATAL Error in FILVERS - unknown file type: ',&
                    ftype
         write(*,*)'Expected one of the following: '
         do k=1,mxftype
            write(*,*) anames(1,k)
         enddo
         stop
      endif
! --- Open file
      if(ab.EQ.'BIN') then
         lascii=.FALSE.
         open(io,file=fname,status='old',form='unformatted',&
              iostat=ierr)
         if(ierr.NE.0) call OPEN_ERR(io6,'FILVERS','Input File',&
                                     fname,io)
      elseif(ab.EQ.'ASC') then
         lascii=.TRUE.
         open(io,file=fname,status='old',iostat=ierr)
         if(ierr.NE.0) call OPEN_ERR(io6,'FILVERS','Input File',&
                                     fname,io)
      else
         write(*,*)'FATAL Error in FILVERS - unknown file format: ',ab
         write(*,*)'Expected BIN or ASC'
         write(*,*)'File Type input: ',ftype
         stop
      endif
      if(LASCII) then
! ---    Read first record into string
         read(io,'(a132)') aline
! ---    Assume type 2 file first
         if(aline(1:16).EQ.anames(2,itype)) then
            dataver=blank16
            j=0
            do i=17,32
               if(aline(i:i).NE.' ') then
                  j=j+1
                  dataver(j:j)=aline(i:i)
               endif
            enddo
         else
! ---       Process file formats by file type
            if(itype.EQ.2) then
! ---          ISC met file
! ---          ------------
               if(aline(1:3) .EQ. 'BEG') then
! ---             CALPUFF MOD-6.0 ISC format conventions
                  dataver(1:3)='1.6'
               else
! ---             Standard ISC format conventions
                  dataver(1:3)='1.0'
               endif
            elseif(itype.EQ.3) then
! ---          PLM met file
! ---          ------------
! ---          Standard PLM format conventions
               dataver(1:3)='1.0'
            elseif(itype.EQ.4) then
! ---          CTDM or AERMET PROFILE met file
! ---          -------------------------------
               if(aline(1:3) .EQ. 'BEG') then
! ---             CALPUFF MOD-6.0 PROFILE format conventions
                  dataver(1:3)='1.6'
               else
! ---             Standard PROFILE format conventions
                  dataver(1:3)='1.0'
               endif
            elseif(itype.EQ.5) then
! ---          CTDM or AERMET SURFACE met file
! ---          -------------------------------
               if(aline(1:3) .EQ. 'BEG') then
! ---             CALPUFF MOD-6.0 SURFACE format conventions
                  dataver(1:3)='1.6'
               else
! ---             Standard SURFACE format conventions
                  dataver(1:3)='1.0'
               endif
            elseif(itype.GE.6 .AND. itype.LE.9) then
! ---          Variable emissions files
! ---          ------------------------
! ---          Free-format with quoted character variables
! ---          Find position of first 4 quotes
               iq=0
               do k=1,132
                  if(aline(k:k).EQ.aquote .AND. iq.LT.4) then
                     iq=iq+1
                     iquote(iq)=k
                  endif
               enddo
               if(iq.NE.4) then
                  write(*,*)'FATAL Error in FILVERS - unknown header'
                  write(*,*)'Expected at least 2 quoted strings'
                  write(*,*)'Number of quotes found: ',iq
                  write(*,*) aline
                  stop
               endif
! ---          Check name
               i1=iquote(1)+1
               i2=iquote(2)-1
               i3=i2-i1+1
               k1=iquote(3)+1
               k2=iquote(4)-1
               k3=k2-k1+1
               if(aline(i1:i2).EQ.anames(1,itype)(1:i3)) then
                  dataver(1:k3)=aline(k1:k2)
               else
                  write(*,*)'FATAL Error in FILVERS - unknown header'
                  write(*,*)'Expected: ',anames(1,itype)(1:i3)
                  write(*,*)'   Found: ',aline(i1:i2)
                  write(*,*) aline
                  stop
               endif
! ---          Construct uniform dataset version
               read(dataver,'(f16.0)') dver
               iver10=NINT(10.*dver)
               if(iver10.GE.60) then
                  dataver='1.6             '
               elseif(iver10.GE.54) then
                  dataver='1.54            '
               elseif(iver10.GE.50) then
                  dataver='1.5             '
               else
                  write(*,*)'FATAL Error in FILVERS - bad version'
                  write(*,*)'Expected: 5.0. 5.4, or 6.0'
                  write(*,*)'   Found: ',dataver
                  write(*,*) aline
                  stop
               endif
            elseif(itype.GE.10 .AND. itype.LE.14) then
! ---          Remaining ASCII files
! ---          ---------------------
! ---          These have only 1 dataset version before 2.0
               dataver(1:3)='1.5'
            endif
         endif
      else
         if(itype.EQ.1) then
! ---       CALMET file
! ---       -----------
            read(io) dataset,dataver,adoc
            if(dataset.NE.anames(2,itype)) then
! ---          Older dataset 
               REWIND(io)
! ---          record #1 - run title -- 60 words
               read(io)title
! ---          record #2 - run control parameters
               read(io)vermet,levmet
               read(levmet(1:6),'(i6)') ilevmet
               read(vermet(1:6),'(f6.0)') rvermet
               if(ilevmet.GE.980304 .OR. rvermet.GT.5.1) then
                  dataver='1.5'
               else
                  dataver='1.0'
               endif
            endif
         elseif(itype.EQ.15) then
! ---       RESTART file
! ---       ------------
! ---       Determine major difference in format from first variable
            var16=anames(2,itype)
            var12=blank16(1:12)
            read(io) var12
            var16(1:12)=var12
            REWIND(io)
            if(var16.EQ.anames(2,itype)) then
               read(io) dataset,dataver,adoc
            else
! ---          Older dataset 
               read(io)ver0
               read(ver0(1:6),'(f6.0)') rver
               if(rver.LT.6.0) then
                  dataver='1.0             '
               else
                  dataver='1.6             '
               endif
            endif
         endif
      endif
! --- Close file
      close(io)
      return
999   write(*,*)
      write(*,*)'FATAL Error in FILVERS - bad filename:'
      write(*,*)fname
      stop
      end
!----------------------------------------------------------------------
      subroutine varsam(ldb,temit,tleft,secdt,smxi,mxnew,ws,&
                        nsstr,nsam,tsamp2,tsampf)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 080227            VARSAM
!                D. Strimaitis
!
! --- PURPOSE:  Computes sampling step information in first timestep
!               for puffs/slugs released from sources with variable
!               release parameters.  The first sampling step is the
!               emitting step for this puff, with duration TEMIT.
!               Subsequent steps are consistent with parameters XMXLEN
!               and MXNEW until the last step, which is the residual
!               needed to close out the timestep.  
!
! --- UPDATE
! --- V6.24-V6.25    080227 (DGS): Replace MXSAM cap with MXNEW cap
!                                  Revise subroutine to include 3-part
!                                  sampling step structure
! --- V6.22-V6.24    080208 (DGS): Condition tsamp2 output to be equal
!                                  to temit when difference is less
!                                  than precision
! --- V6.1-V6.22     070921 (DGS): Impose MXSAM limit on NSAM result
! --- V6.0-V6.1      050915 (DGS): Remove emission step index (stored
!                                  in /PUFF/ now)
!
! --- INPUTS:
!           LDB - logical   - Output debug writes when .TRUE.
!         TEMIT - real      - Duration of emission for puff/slug (s)
!         TLEFT - real      - Time remaining in this timestep after
!                             puff/slug is emitted, expressed as
!                             fraction of timestep
!         SECDT - real      - Length of timestep (s)
!          SMXI - real      - Reciprocal of speed required to travel
!                             max allowed distance in 1 timestep (s/m)
!         MXNEW - integer   - Maximum number of new puffs allowed for
!                             one emission period
!            WS - real      - Wind speed (m/s)
!
!
! --- OUTPUT:
!         NSSTR - integer   - Starting sampling step for current puff
!          NSAM - integer   - Total number of sampling steps for
!                             this timestep
!        TSAMP2 - real      - Sampling step duration for period after
!                             emission step
!        TSAMPF - real      - Sampling step duration for final step
!
! --- VARSAM called by:  COMP
! --- VARSAM calls:      LRSAME
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
      logical ldb
      logical lrsame
      data zero/0.0/
! --- Set minimum time (sec) that is treated as significant enough to
! --- use as the last sampling step (residual time difference may not
! --- be zero due to precision)
      data tzero/0.01/
! --- Number of emitting steps allowed in full timestep (for constant Q)
      nsam=ws*smxi+1
      nsam=MIN(nsam,mxnew)
! --- Time for each step
      tsamp2=secdt/FLOAT(nsam)
! --- Avoid meaningless difference between temit and tsamp2 that
! --- may arise from single precision resolution
      if(LRSAME(1.0e-05,temit,tsamp2)) tsamp2=temit
      if(tleft.LE.zero) then
! ---    Emission of current puff/slug ends at end of this timestep,
! ---    so there is only one sampling step
         nsstr=nsam
         tsamp2=zero
         tsampf=temit
      else
! ---    Configure sampling step properties after emitting step
         secleft=tleft*secdt
! ---    Number of full sampling steps after puff is emitted
         nfull=secleft/tsamp2
! ---    Partial step needed to complete timestep period
         tsampf=secleft-(nfull*tsamp2)
         if(tsampf.LE.tzero) then
            nlast=0
            tsampf=tsamp2
         else
            nlast=1
! ---       Avoid meaningless difference between tsampf and tsamp2 that
! ---       may arise from single precision resolution
            if(LRSAME(1.0e-05,tsampf,tsamp2)) tsampf=tsamp2
         endif
! ---    Finish sampling step configuration
         nsstr=nsam-(nfull+nlast)
         if(nsstr.LE.0) then
            nsam=nsam+1-nsstr
            nsstr=1
         endif
      endif
! --- Debug output
      if(LDB) then
         write(io6,*)'VARSAM -- ws,temit,tleft: ',ws,temit,tleft
         write(io6,*)'        secdt,mxnew,smxi: ',secdt,mxnew,smxi
         write(io6,*)'              nsstr,nsam: ',nsstr,nsam
         write(io6,*)'           tsamp2,tsampf: ',tsamp2,tsampf
      endif
! --- Reset TLEFT to zero (release use of tmtotb array)
      tleft=zero
      return
      end
!----------------------------------------------------------------------
      subroutine geofmt(dataset,dataver,pmap,iutm,utmhem,&
                        clat0,clon0,clat1,clat2,&
                        datum,daten,xyunit,tzone)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 080430            GEOFMT
!                D. Strimaitis
!
! --- PURPOSE:  Condition character strings of geo-referencing data
!               before use, and apply first level QA
!
! --- UPDATE
! --- V6.22-V6.26    080430 (DGS): Dimension CSTOR1,CSTOR2 arrays to
!                                  MXCOL, as defined in PARAMS.CAL,
!                                  to be consistent with CALUTIL subs
!
! --- INPUT:
!          DATASET - char*16 - Name of dataset (file type) containing
!                              the geo data
!          DATAVER - char*16 - Version of dataset
!             PMAP - char*8  - Character code for input map projection
!                              LL  :  Latitude/longitude
!                              UTM :  Universal Transverse Mercator
!                              LCC :  Lambert Conformal Conic
!                              PS  :  Polar Stereographic
!                              EM  :  Equatorial Mercator
!                              LAZA:  Lambert Azimuthal Equal Area
!                              TTM :  Tangential Transverse Mercator
!             IUTM - integer - UTM zone in which the station
!                              coordinates are specified
!           UTMHEM - char*4  - Base hemisphere for UTM projection
!                               (S=southern, N=northern)
!      CLAT0,CLON0 - char*16 - Latitude & longitude of x=0 and y=0
!           (deg)              of map projection (Used only if PMAP =
!                              LCC, PS, EM, TTM or LAZA), and
!      CLAT1,CLAT2 - char*16 - Matching latitude(s) for projection
!           (deg)             (Used only if PMAP3= LCC, PS, or EM)
!                              LCC :  Projection cone slices through
!                                     Earth's surface at XLAT1 and XLAT2
!                              PS  :  Projection plane slices through
!                                     Earth at XLAT1
!                              EM  :  Projection cylinder slices through
!                                     Earth at [+/-] XLAT1
!            DATUM - char*8  - Datum-Region for grid coordinates
!            DATEN - char*8  - NIMA date for datum parameters
!                                    (MM-DD-YYYY  )
!           XYUNIT - char*4  - Units for coordinates (e.g., KM)
!            TZONE - char*8  - Time zone (UTC+nnnn)
!                             (GMT=UTC+0000, EST=UTC-0500, CET=UTC+0100)
!
! --- OUTPUT:
!             (all inputs returned)
!
!
! --- GEOFMT called by: 
! --- GEOFMT calls:     ALLCAP, TLEFT
!----------------------------------------------------------------------
      include 'params.puf'
      include 'params.cal'
      character*4 utmhem, xyunit
      character*8 pmap, datum, tzone
      character*12 daten
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2,clatlon4(4)
      character*1 cstor1(mxcol), cstor2(mxcol)
      logical lfatal
      lfatal=.FALSE.
! --- Swap lat/lon variables into array
      clatlon4(1)=clat0
      clatlon4(2)=clon0
      clatlon4(3)=clat1
      clatlon4(4)=clat2
! --- Convert characters to upper case and left-justify all strings
! -----------------------------------------------------------------
! --- PMAP
      do j=1,8
         cstor1(j)=pmap(j:j)
      enddo
      call ALLCAP(cstor1,8)
      call TLEFT(cstor1,1,8,cstor2,nlim)
      do j=1,8
         pmap(j:j)=cstor2(j)
      enddo
! --- UTMHEM
      do j=1,4
         cstor1(j)=utmhem(j:j)
      enddo
      call ALLCAP(cstor1,4)
      call TLEFT(cstor1,1,4,cstor2,nlim)
      do j=1,4
         utmhem(j:j)=cstor2(j)
      enddo
! --- DATUM
      do j=1,8
         cstor1(j)=datum(j:j)
      enddo
      call ALLCAP(cstor1,8)
      call TLEFT(cstor1,1,8,cstor2,nlim)
      do j=1,8
         datum(j:j)=cstor2(j)
      enddo
! --- DATEN
      do j=1,12
         cstor1(j)=daten(j:j)
      enddo
      call ALLCAP(cstor1,12)
      call TLEFT(cstor1,1,12,cstor2,nlim)
      do j=1,12
         daten(j:j)=cstor2(j)
      enddo
! --- XYUNIT
      do j=1,4
         cstor1(j)=xyunit(j:j)
      enddo
      call ALLCAP(cstor1,4)
      call TLEFT(cstor1,1,4,cstor2,nlim)
      do j=1,4
         xyunit(j:j)=cstor2(j)
      enddo
! --- TZONE
      do j=1,8
         cstor1(j)=tzone(j:j)
      enddo
      call ALLCAP(cstor1,8)
      call TLEFT(cstor1,1,8,cstor2,nlim)
      do j=1,8
         tzone(j:j)=cstor2(j)
      enddo
! --- Lat/Lon specs
      do k=1,4
         do j=1,16
            cstor1(j)=clatlon4(k)(j:j)
         enddo
         call ALLCAP(cstor1,16)
         call TLEFT(cstor1,1,16,cstor2,nlim)
         do j=1,16
            clatlon4(k)(j:j)=cstor2(j)
         enddo
      enddo
! --- QA
! -------
! --- Screen for valid projection strings
      ifound=0
      if(pmap.EQ.'LL      ')  ifound=1
      if(pmap.EQ.'UTM     ')  ifound=1
      if(pmap.EQ.'LCC     ')  ifound=1
      if(pmap.EQ.'PS      ')  ifound=1
      if(pmap.EQ.'EM      ')  ifound=1
      if(pmap.EQ.'LAZA    ')  ifound=1
      if(pmap.EQ.'TTM     ')  ifound=1
      if(ifound .EQ. 0) then
         write(io6,*)
         write(io6,*)'FATAL Error:  Invalid Map Projection'
         write(io6,*)'Expected PMAP = LL,UTM,LCC,PS,EM,LAZA,TTM'
         write(io6,*)'Found    PMAP = ',pmap
         lfatal=.TRUE.
      endif
      if(pmap(1:3).EQ.'UTM') then
! ---    UTMHEM is N or S
         if(utmhem.NE.'N   ' .AND. utmhem.NE.'S   ') then
            write(io6,*)
            write(io6,*)'FATAL Error:  UTMHEM must be N or S'
            write(io6,*)'Found UTMHEM = ',utmhem
            lfatal=.TRUE.
         endif
! ---    IUTM is 1 to 60
         if(iutm.LT.1 .OR. iutm.GT.60) then
            write(io6,*)
            write(io6,*)'FATAL Error:  IUTM must be 1 to 60'
            write(io6,*)'Found IUTM = ',iutm
            lfatal=.TRUE.
         endif
      else
! ---    Screen lat/lon strings for blanks before right-most character
         do k=1,4
            ilast=16
            do i=1,16
               if(clatlon4(k)(i:i).NE.' ') ilast=i
            enddo
            nblank=0
            do i=1,ilast
               if(clatlon4(k)(i:i).EQ.' ') nblank=nblank+1
            enddo
! ---       Require at least 2 characters, and allow 1 blank between
! ---       characters
            if(nblank.GT.1 .OR. ilast.LT.3) then
               write(io6,*)
               write(io6,*)'FATAL Error:  invalid lat/lon data'
               write(io6,*)'Expected numbers with 1 character in each'
               write(io6,*)'Found:  ',clatlon4(k)
               lfatal=.TRUE.
            endif
         enddo
      endif
      if(pmap(1:3).NE.'LL ') then
! ---    Distance must be KM
         if(xyunit.NE.'KM  ') then
            write(io6,*)
            write(io6,*)'FATAL Error:  XYUNIT must be KM'
            write(io6,*)'Found XYUNIT = ',xyunit
            lfatal=.TRUE.
         endif
      endif
! --- Time zone must be UTC+hhmm in whole hours
         if(tzone(7:8).NE.'00') then
            write(io6,*)'FATAL Error: Time Zone format'
            write(io6,*)'Expected whole hour, Found: ',tzone
            lfatal=.TRUE.
         endif
         if(tzone(1:3).NE.'UTC') then
            write(io6,*)'FATAL Error: Time Zone format'
            write(io6,*)'Expected UTC+hh00, Found: ',tzone
            lfatal=.TRUE.
         endif
      if(LFATAL) then
         write(io6,*)
         write(io6,*)'Troubled file is type: ',dataset
         write(io6,*)'              version: ',dataver
         write(*,*)
         stop 'Halted in subr. GEOFMT -- see list file'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine NRISEHD(itype,i,ndhrb,nsecb,ndhre,nsece,&
                         ndhrqb,nsecqb,ndhrqe,nsecqe,zelev,zht,&
                         tdegk,reff,weff,xgrd,ygrd)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 080430           NRISEHD
!                D. Strimaitis
!
! --- PURPOSE:  Prepare data header record for RISE.DAT file and 
!               place in scratch file
!
! --- INPUTS:
!         ITYPE - integer - Source type
!             I - integer - Source index
!         NDHRB - integer - Julian day, hour beginning of timestep
!         NSECB - integer - Seconds at beginning of timestep
!         NDHRE - integer - Julian day, hour at end of timestep
!         NSECE - integer - Seconds at end of timestep
!        NDHRQB - integer - Julian day, hour beginning current data
!        NSECQB - integer - Seconds at beginning of current data
!        NDHRQE - integer - Julian day, hour at end of current data
!        NSECQE - integer - Seconds at end of current data
!
!     Common block /OUTPT/ variables:
!           INRISE
!     Common block /GRID/ variables:
!           DGRID, XORIG, YORIG
!     Parameters:
!           IO6, IOX
!
! --- OUTPUT:
!
! --- NRISEHD called by:  AREAS2
! --- NRISEHD calls:      (none)
!----------------------------------------------------------------------
      include 'params.puf'
      include 'grid.puf'
      include 'outpt.puf'
! --- Check output directive
      if(inrise.EQ.0) then
         return
      elseif(inrise.NE.1) then
         write(io6,*)
         write(io6,*)'Error in NRISEHD:  Bad INRISE value = ',inrise
         write(io6,*)'Expected either 0 or 1'
         stop 'Halted in NRISEHD:  See list file'
      endif
! --- Convert source location from grid units to KM
      xkm=0.001*(xgrd*dgrid+xorig)
      ykm=0.001*(ygrd*dgrid+yorig)
! --- Rewind temporary file for intermediate rise information
      REWIND(iox)
! --- Write time period of the model timestep
      write(iox,*) ndhrb,nsecb,ndhre,nsece
! --- Write source information
      write(iox,*) itype,i,xkm,ykm,zelev,zht,tdegk,reff,weff
! --- Write time period of the emissions data for this source
      write(iox,*) ndhrqb,nsecqb,ndhrqe,nsecqe
      return
      end
!----------------------------------------------------------------------
      subroutine NRISEOUT(krise,mprime)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 080430          NRISEOUT
!                D. Strimaitis
!
! --- PURPOSE:  Prepare data header record for RISE.DAT file and open
!               scratch file
!
! --- INPUTS:
!         KRISE - integer - Number of data records
!        MPRIME - integer - Flag indicating NUMRISE configuration
!                           0:  Standard plume rise
!                           1:  Modified rise with PRIME building
!                               downwash
!
!     Parameters:
!           IO38, IOX
!
! --- OUTPUT:
!
! --- NRISEOUT called by:  NUMRISE
! --- NRISEOUT calls:      (none)
!----------------------------------------------------------------------
      include 'params.puf'
      character*50 adoc
      data adoc/'s,x,y,z,rad,us,u,v,w,denp,tp,ua,dena,ta,dudz,dpdz '/
! --- Reset scratch file position
      REWIND(iox)
! --- Time period of the time step
      read(iox,*) ndhrb,nsecb,ndhre,nsece
      write(io38,*) ndhrb,nsecb,ndhre,nsece
! --- Source information (with MPRIME added)
      read(iox,*) itype,i,xkm,ykm,zelev,zht,tdegk,reff,weff
      write(io38,*) itype,i,xkm,ykm,zelev,zht,tdegk,reff,weff,mprime
! --- Emissions time period (with number of records added)
      read(iox,*) ndhrb,nsecb,ndhre,nsece
      write(io38,*) ndhrb,nsecb,ndhre,nsece,krise
! --- Document data each time
      write(io38,*) adoc
! --- Transfer rise records
      do k=1,krise
         read(iox,*)s,x,y,zc,r,usc,u,v,w,den,tp,ua,ra,ta,dudz,dpdz
         write(io38,*)s,x,y,zc,r,usc,u,v,w,den,tp,ua,ra,ta,dudz,dpdz
      enddo
! --- Done with file (rewind, do not close)
      REWIND(iox)
      return
      end
