!----------------------------------------------------------------------
      subroutine chemdk(delt,q,r)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 090619            CHEMDK
!                D. Strimaitis
!
! --- PURPOSE:  Adjust the pollutant mass to account for chemical
!               transformations prescribed by user's half-life
!               values and yields
!
! --- UPDATES:
! --- V6.264 (090326) to V6.266 (090619) (DGS)
!          -  Allow multiple child species
!
! --- INPUTS:
!         DELT - real    - integration time interval (seconds)
!    Q(mxspec) - real    - Pollutant mass (g) in the puff 
!                          (all species)
!     Common block /GEN/ variables:
!           NSPEC
!     Common block /CHEMDAT/ variables:
!           DKRATE(mxspec),DKYIELD(mxspec,mxspec)
!
! --- OUTPUT:
!    R(mxspec) - real    - Transformation rates (%change/step)
!
! --- CHEMDK called by: CHEM
! --- CHEMDK calls:     none
!----------------------------------------------------------------------
      include 'params.puf'
      include 'chemdat.puf'
      include 'gen.puf'
      real q(mxspec),qloss(mxspec),qgain(mxspec),r(mxspec)
! --- Set ln(2)
      data rln2/0.6931472/
      data zero/0.0/
! --- Set numerator of EXP
      enum=rln2*delt
! --- Initialize local variables
      do is=1,nspec
         qloss(is)=zero
         qgain(is)=zero
      enddo
! --- Loop over species
      do is=1,nspec
! ---    Define loss as positive
         qloss(is)=q(is)*(1.-EXP(-enum*dkrate(is)))
! ---    Process yield for any child species
         do ksp=1,nspec
            if(dkyield(ksp,is).GT.zero) then
! ---          Map to gain in another species
               qgain(ksp)=qgain(ksp)+qloss(is)*dkyield(ksp,is)
            endif
         enddo
      enddo
! --- DEBUG
!     write(io6,*)'Species 1: q,gain,loss= ',q(1),qgain(1),qloss(1)
! --- Compute effective transformation rates (%change/step)
      do is=1,nspec
         qdif=qgain(is)-qloss(is)
         qnet=AMAX1(zero,q(is)+qdif)
         qdif=qnet-q(is)
         if(ABS(qdif).GT.1000.*q(is)) then
! ---       Report saturated %change
            r(is)=99999.
            if(qdif.LT.zero) r(is)=-r(is)
         elseif(qdif.EQ.zero) then
            r(is)=zero
         else
            r(is)=100.*qdif/q(is)
         endif
! ---    Set new mass for output
         q(is)=qnet
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine traksam(ii,istep,kdathr,ksec)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521           TRAKSAM
!                D. Strimaitis
!
! --- PURPOSE:  Assign puff-tracking arrays at end of current sampling
!               step
!
! --- UPDATES:
! --- V6.267 (090710) to v6.42_x1.1 (140521)
!          -  Add snap-shot timestamp
!
! --- INPUTS:
!
!               II - integer  - Puff array index
!            ISTEP - integer  - Current sampling step index
!           KDATHR - integer  - Snap-shot date-hour (YYYYJJJHH)
!             KSEC - integer  - Snap-shot second (SSSS)
!
!     Common block /GRID/ variables
!                 dgrid,xorig,yorig
!     Common block /PUFF/ variables
!                 xpb(mxpuff),ypb(mxpuff),zpb(mxpuff),
!                 zimax(mxpuff),ziold(mxpuff),
!                 sigyb(mxpuff),sigzb(mxpuff),xtotb(mxpuff),
!                 tmtotb(mxpuff),bidfnl(mxpuff),
!                 irlsnum(mxpuff),isrcnum(mxpuff),isrctyp(mxpuff),
!                 ipufcd(mxpuff)
!
!     Parameters:
!         MXPUFF, MXTRAK
!
! --- OUTPUT:
!     Common block /TRAK/ variables
!                 idathrtrak(mxtrak),isectrak(mxtrak),
!                 xkmtrak(mxtrak),ykmtrak(mxtrak),hmtrak(mxtrak),
!                 sytrak(mxtrak),sztrak(mxtrak),zitrak(mxtrak),
!                 zimaxtrak(mxtrak),tsectrak(mxtrak),dmtrak(mxtrak),
!                 icodetrak(mxtrak),isrctrak,ityptrak,ntrk
!
! --- TRAKSAM called by: COMP
! --- TRAKSAM calls:     none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'grid.puf'
      include 'puff.puf'
      include 'trak.puf'
      data zero/0.0/
      ntrk=istep
! --- Timestamp
      idathrtrak(istep)=kdathr
      isectrak(istep)=ksec
! --- Convert grid units to km for coordinates
      xkmtrak(istep)=.001*(xorig+xpb(ii)*dgrid)
      ykmtrak(istep)=.001*(yorig+ypb(ii)*dgrid)
! --- Direct pass from PUFF arrays
      hmtrak(istep)=zpb(ii)
      sytrak(istep)=sigyb(ii)
      sztrak(istep)=sigzb(ii)
      zitrak(istep)=ziold(ii)
      zimaxtrak(istep)=zimax(ii)
      tsectrak(istep)=tmtotb(ii)
      dmtrak(istep)=xtotb(ii)
      icodetrak(istep)=ipufcd(ii)
      isrctrak=isrcnum(ii)
      ityptrak=isrctyp(ii)
! --- BID might be added to sigmas in quadrature
      if(bidfnl(ii).GT.zero) then
         sytrak(istep)=SQRT(sytrak(istep)**2+bidfnl(ii))
         sztrak(istep)=SQRT(sztrak(istep)**2+bidfnl(ii))
      endif
      return
      end
