!-----------------------------------------------------------------------
      subroutine wake_addbid(ldb,ntr,xtr,htr)
!-----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100108        WAKE_ADDBID
!
! --- PURPOSE: Adds the BID sigma to the wake sigmas upwind of the
! ---          entry to the wake.  WAKE_DFSN includes the BID in the
! ---          starting sigmas so this is carried in the resulting
! ---          wake tabulation from xi forward, but it is not in the
! ---          sigmas tabulated upwind of the entry to the wake.
! ---          These sigmas are tabulated as negatives.  WAKE_ADDBID
! ---          replaces all negative sigmas with the corresponding BID
! ---          enhanced sigmas.  Negative sign is retained and
! ---          is treated later in WAKE_XSIG and WAKE_TAB to add
! ---          constraint that linearly interpolated sigmas during rise
! ---          phase are at least as large as the BID sigmas.
!
! --- INPUTS:
!              ldb - logical     - Debug output switch
!              ntr - integer     - Number of entries in rise arrays
!              xtr - real        - distance(m) in rise arrays
!              htr - real        - rise(m) without streamline deflection
!
!     Common block /WAKEDAT/ variables:
!           NWAK, XWAK(mxrise), SZWAK(mxrise), SYWAK(mxrise)
!
! --- OUTPUT:
!
!     Common block /WAKEDAT/ variables:
!           SYWAK(mxrise),SZWAK(mxrise)
!
! --- WAKE_ADDBID called by:  NUMRISE
! --- WAKE_ADDBID calls    :  NUMGRAD
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'wakedat.puf'
      real xtr(mxrise),htr(mxrise)
      real zero
      logical ldb
      data zero/0.0/
! --- Loop over wake table entries
      do n=1,nwak
         x=xwak(n)
! ---    Get BID-modified sigmas (start as negatives)
         if(sywak(n).LT.zero .OR. szwak(n).LT.zero) then
! ---       Get rise for BID
            call NUMGRAD(x,xtr,htr,ntr,rise)
            bidsq=(rise/3.5)**2
            sz=SQRT(szwak(n)**2+bidsq)
            sy=SQRT(sywak(n)**2+bidsq)
      if(ldb) then
         write(io6,*)'WAKE_ADDBID: x,rise,sz,sy= ',x,rise,sz,sy
      endif
! ---       Do QA check and replace array sigmas
            if(sywak(n).LT.zero .AND. szwak(n).LT.zero) then
               sywak(n)=-sy
               szwak(n)=-sz
            else
               write(io6,*)'WAKE_ADDBID: Error'
               write(io6,*)'Wake sigmas requiring BID must be < 0'
               write(io6,*)'n,sywak(n),szwak(n) = ',n,sywak(n),szwak(n)
               stop 'Halted in WAKE_ADDBID -- See List File'
            endif
         endif
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine setnest
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           SETNEST
!                D. Strimaitis
!
! --- PURPOSE:  Read header data records of the CALMET meteorological
!               data files to obtain grid information for all CALMET
!               nests (conditional);
!               First QA test for nested grids;
!               Create nested grid information for all met data types
!               Note:  CALMET files are closed after processing header
!                      Assumes MET1DOC already called
!
! --- UPDATE
! --- V6.301 - TNG-7.1.0  141230  (DGS)
!                                : Trap file-open error
! --- V6.3-V6.301    100827 (DGS): Allow nested grid domains that do not
!                                  align with cells in parent domains
!                    100827 (DGS): Remove code that identifies cells in
!                                  parent domain covered by cells in
!                                  nested grid
!                    100827 (DGS): Create horizontal splitting arrays
!                                  that vary by domain nesting factor
!                    100827 (DGS): Allow nested CALMET files to have
!                                  different time-periods
!
! --- INPUTS:
!
!     Common block /FILNAM/ variables:
!           METDATL(mxmetdat,mxmetdom), NMETDOM
!     Common block /DATASET/ variables:
!           VERCMET(mxmetdom)
!     Common block /COMPARM/ variables:
!           sysplithgrd, shsplithgrd
!     Common block /GRID/ variables:
!           NX, NY, DGRID
!
!     Parameters:
!           MXMETDAT, MXMETDOM, IO7
!
! --- OUTPUT:
!     Common block /COMPARM/ variables:
!           sysplith(mxmetdom), shsplith(mxmetdom)
!     Common block /GRIDNEST/ variables:
!           metcell(mxnx,mxny,mxmetdom)
!           nestfac(mxmetdom),nestfacmx,ipargrd(mxmetdom)
!           rnxnest0(mxmetdom),rnynest0(mxmetdom)
!           cgridll(2,mxmetdom),cgridur(2,mxmetdom)
!
! --- SETNEST called by:  SETUP
! --- SETNEST calls:      LRSAME, OPEN_ERR
!----------------------------------------------------------------------
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'comparm.puf'
      include 'dataset.puf'
      include 'filnam.puf'
      include 'grid.puf'
      include 'gridnest.puf'
      include 'methd.puf'
! --- Local Variables
      real area(mxmetdom)
      integer kused(mxmetdom), nsame(mxmetdom)
      logical LRSAME, lproc, ldb, lbad
      character*80 title(3)
      character*8 vermet
      character*8 axbtzm
      character*16 dataset,dataver
      character*64 datamod
      character*132 char132
! --- Set small FB for threshold to accept differences as zero
      data eps/1.0e-07/
! --- Print results to list file?
      data ldb/.TRUE./
! --- Initialize Arrays
      area(1:mxmetdom)=0.0
      kused(1:mxmetdom)=0
      nsame(1:mxmetdom)=0
      metcell(1:mxnx,1:mxny,1:mxmetdom)=0
      nestfac(1:mxmetdom)=0
      ipargrd(1:mxmetdom)=0
      rnxnest0(1:mxmetdom)=0.0
      rnynest0(1:mxmetdom)=0.0
      if(nmetdom.EQ.1) then
! ---    Create single grid information for all met data types
! ---    from control file grid specification and exit
         nestfac(1)=1
         nestfacmx=1
         cgridll(1,1)=0.0
         cgridll(2,1)=0.0
         cgridur(1,1)=nx
         cgridur(2,1)=ny
         nsame(1)=0
         do j=1,ny
            do i=1,nx
               metcell(i,j,1)=1
            enddo
         enddo
! ---    Horizontal splitting parameters for grid domains
! ---    Reset minimum sigma-y units from Grid Units to m 
! ---    and reset shear test units from SYSPLITH/hr to m/s
         sysplith(1)=sysplithgrd*dgrid
         shsplith(1)=shsplithgrd*sysplith(1)/3600.
         goto 101
      endif
! --- Loop over CALMET.DAT files for first time period
      do kg=1,nmetdom
         io=io7+(kg-1)
         k=1
         open(io,file=metdatl(k,kg),status='old',form='unformatted',&
              iostat=ierr)
         if(ierr.NE.0) call OPEN_ERR(io6,'SETNEST','CALMET File',&
                                     metdatl(k,kg),io)
         if(vercmet(kg).EQ.'2.1') then
! ---      Dataset 2.1 with comment records and begin/end times
! -------------------------------------------------------------
! ---      Record #1 - File Declaration -- 24 words
           read(io) dataset,dataver,datamod
! ---      Record #2 - Number of comment lines -- 1 word
           read(io) ncom
! ---      Skip comment records
           do i=1,ncom
             read(io) char132
           enddo
! ---      record #NCOM+3 - run control parameters -- 39 words
           read(io)&
           ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg),ibsmet(kg),&
           ieymet(kg),iemmet(kg),iedmet(kg),iehmet(kg),iesmet(kg),&
           axbtzm,irlgmet,irtype,&
           nxm(kg),nym(kg),nzm,xgridm(kg),xorigm(kg),yorigm(kg),iwfcod,&
           nssta(kg),nusta(kg),npsta(kg),nowsta(kg),nlu,iwat1,iwat2,&
           lcalgrd,pmapm,datumm,datenm,feastm,fnorthm,utmhemm,iutmznm,&
           rnlat0m,relon0m,xlat1m,xlat2m
         elseif(vercmet(kg).EQ.'2.0') then
! ---      Dataset 2.0 with comment records
! -----------------------------------------
! ---      Record #1 - File Declaration -- 24 words
           read(io) dataset,dataver,datamod
! ---      Record #2 - Number of comment lines -- 1 word
           read(io) ncom
! ---      Skip comment records
           do i=1,ncom
             read(io) char132
           enddo
! ---      record #NCOM+3 - run control parameters -- 37 words
           read(io) ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg),&
           ibtzm,irlgmet,irtype,&
           nxm(kg),nym(kg),nzm,xgridm(kg),xorigm(kg),yorigm(kg),iwfcod,&
           nssta(kg),nusta(kg),npsta(kg),nowsta(kg),nlu,iwat1,iwat2,&
           lcalgrd,pmapm,datumm,datenm,feastm,fnorthm,utmhemm,iutmznm,&
           rnlat0m,relon0m,xlat1m,xlat2m
         else
! ---      Read older CALMET header records
! -----------------------------------------
! ---      record #1 - run title -- 60 words
           read(io)title
! ---      record #2 - run control parameters -- 26 words
! ---      (vermet, levmet are both 8 bytes)
           read(io)vermet,levmet,&
           ibymet(kg),ibmmet(kg),ibdmet(kg),ibhmet(kg),ibtzm,&
           irlgmet,irtype,nxm(kg),nym(kg),nzm,xgridm(kg),xorigm(kg),&
           yorigm(kg),iutmznm,iwfcod,nssta(kg),nusta(kg),npsta(kg),&
           nowsta(kg),nlu,iwat1,iwat2,lcalgrd
         endif
         close(io)
      enddo
! --- Set up information for mapping nested grid cells
! ----------------------------------------------------
! --- Set nesting factor relative to grid-1 (assumed outermost);
! --- Calculate the area of each grid in grid-1 cell units;
! --- Set offset of origin of each grid relative to the origin of the
! --- outermost grid, using nested cell units
      nestfacmx=0
      do kg=1,nmetdom
         xgridmi(kg)=1./xgridm(kg)
         nestfac(kg)=NINT(xgridm(1)*xgridmi(kg))
         if(nestfac(kg).GT.nestfacmx) nestfacmx=nestfac(kg)
         area(kg)=FLOAT(nxm(kg)*nym(kg))/FLOAT(nestfac(kg)**2)
         rnxnest0(kg)=(xorigm(kg)-xorigm(1))*xgridmi(kg)
         rnynest0(kg)=(yorigm(kg)-yorigm(1))*xgridmi(kg)
! ---    Horizontal splitting parameters for grid domains
! ---    Reset minimum sigma-y units from Grid Units to m 
! ---    and reset shear test units from SYSPLITH/hr to m/s
         sysplith(kg)=sysplithgrd*xgridm(kg)
         shsplith(kg)=shsplithgrd*sysplith(kg)/3600.
! ---    Identify previous domains in list with same resolution
         do ng=1,kg-1
            if(nestfac(ng).EQ.nestfac(kg)) nsame(ng)=nsame(ng)+1
         enddo
      enddo
! --- Find max
      iareamax=1
      do kg=2,nmetdom
         if(area(kg).GT.area(iareamax)) iareamax=kg
      enddo
! --- Make sure that first grid is outermost
      if(iareamax.NE.1) then
         write(io6,*)
         write(io6,*)'SETNEST: Error - outermost domain must be first'
         write(io6,*)'  Use METDAT1 as assignment name for the'
         write(io6,*)'  outermost domain; then METDAT2, METDAT3, etc.'
         stop 'HALTED in SETNEST -- see list file'
      endif
! --- Set coordinates of corner of each MET grid domain in grid-cell
! --- units of the outermost grid
      do kg=1,nmetdom
         cgridll(1,kg)=rnxnest0(kg)/FLOAT(nestfac(kg))
         cgridll(2,kg)=rnynest0(kg)/FLOAT(nestfac(kg))
         cgridur(1,kg)=cgridll(1,kg)+FLOAT(nxm(kg))/FLOAT(nestfac(kg))
         cgridur(2,kg)=cgridll(2,kg)+FLOAT(nym(kg))/FLOAT(nestfac(kg))
      enddo
! --- Identify parent grid for each nested grid
! --- First grid is outermost and has no parent
      do k=2,nmetdom
! ---    Loop over all grids
         do kp=1,nmetdom
! ---       Exclude current grid
            if(kp.NE.k) then
! ---          Area of parent grid must not be smaller
               if(area(kp).GE.area(k)) then
! ---             Nesting factor of parent grid must be smaller
                  if(nestfac(kp).LT.nestfac(k)) then
! ---                Nested grid must lie within parent
                     rp=xorigm(kp)
                     r=xorigm(k)
                     dx1=r-rp
                     if(LRSAME(eps,r,rp)) dx1=0.
                     rp=yorigm(kp)
                     r=yorigm(k)
                     dy1=r-rp
                     if(LRSAME(eps,r,rp)) dy1=0.
                     rp=xorigm(kp)+xgridm(kp)*nxm(kp)
                     r=xorigm(k)+xgridm(k)*nxm(k)
                     dx2=rp-r
                     if(LRSAME(eps,rp,r)) dx2=0.
                     rp=yorigm(kp)+xgridm(kp)*nym(kp)
                     r=yorigm(k)+xgridm(k)*nym(k)
                     dy2=rp-r
                     if(LRSAME(eps,rp,r)) dy2=0.
                     if(dx1.GE.0. .AND. dx2.GE.0. .AND.&
                        dy1.GE.0. .AND. dy2.GE.0.) ipargrd(k)=kp
                  endif
               endif
            endif
         enddo
      enddo
! --- QA:  all grids except outermost grid must have a parent
      kmin=nmetdom
      do k=2,nmetdom
         if(ipargrd(k).LT.kmin) kmin=ipargrd(k)
      enddo
      if(kmin.EQ.0) then
         write(io6,*)
         write(io6,*)'SETNEST: Error - a nested domain has no parent'
         do k=2,nmetdom
            write(io6,*)'  Domain = ',k
            write(io6,*)'  Parent Domain = ',ipargrd(k)
         enddo
         stop 'HALTED in SETNEST -- see list file'
      endif
! --- QA: nesting factor must be exact integer
! --- Counter for number of problems
      nbad=0
      do k=2,nmetdom
         celldim=FLOAT(nestfac(k))*xgridm(k)
         if(.NOT.LRSAME(eps,xgridm(1),celldim)) then
            nbad=nbad+1
            write(io6,*)
            write(io6,*)'SETNEST: an integer number of nested grid'
            write(io6,*)'cells must fit in one cell of the outermost'
            write(io6,*)'domain.  Problem domain: '//TRIM(metdoml(k))
            write(io6,*)'Problem domain: '//TRIM(metdoml(k)),xgridm(k)
            write(io6,*)'Outer   domain: '//TRIM(metdoml(1)),xgridm(1)
         endif
      enddo
      if(nbad.GT.0) then
         stop 'HALTED in SETNEST -- see list file'
      endif
! --- Mark cells that are used in each grid
      do k=1,nmetdom
! ---    Map all cells in this grid
         do j=1,nym(k)
         do i=1,nxm(k)
            metcell(i,j,k)=1
         enddo
         enddo
      enddo
101   if(LDB) then
         write(io6,*)
         write(io6,*)
         write(io6,*)'SETNEST: Setup results for nested CALMET grids'
         write(io6,*)'----------------------------------------------'
         write(io6,*)
         write(io6,*)'Properties of each CALMET domain grid'
         do k=1,nmetdom
            write(io6,*)
            if(nmetdom.EQ.1) then
               write(io6,*)'  Domain      = ',k
               write(io6,*)'  Origin(m)   = ',xorig,yorig
               write(io6,*)'nx,ny,cell(m) = ',nx,ny,dgrid
            else
               write(io6,*)'  Domain      = ',k,&
                           ' ('//TRIM(metdoml(k))//')'
               write(io6,*)'  Origin(m)   = ',xorigm(k),yorigm(k)
               write(io6,*)'nx,ny,cell(m) = ',nxm(k),nym(k),xgridm(k)
               write(io6,*)'    1/cell    = ',xgridmi(k)
            endif
            write(io6,*)'  Nest Factor = ',nestfac(k)
            write(io6,*)'Offset nx0,ny0= ',rnxnest0(k),rnynest0(k)
            write(io6,*)'Corner coordinates in outermost grid units:'
            write(io6,*)'  LL Corner   = ',cgridll(1,k),cgridll(2,k)
            write(io6,*)'  UR Corner   = ',cgridur(1,k),cgridur(2,k)
            write(io6,*)'Horizontal splitting parameters for domain:'
            write(io6,*)'  SYSPLITH(m) = ',sysplith(k)
            write(io6,*)'SHSPLITH(m/s) = ',shsplith(k)
         enddo
         if(nmetdom.GT.1) then
            write(io6,*)
            write(io6,*)'Parent MET Domain for each nest'
            write(io6,*)
            do k=2,nmetdom
               kp=ipargrd(k)
               write(io6,*)'  Domain        = ',k,&
                           ' ('//TRIM(metdoml(k))//')'
               write(io6,*)'  Parent Domain = ',kp,&
                           ' ('//TRIM(metdoml(kp))//')'
            enddo
! ---       Report order in which overlapping domains of equal
! ---       resolution are used
            write(io6,*)
            write(io6,*)'Points within overlapping domains always use'
            write(io6,*)'data from the domain with the smallest grid'
            write(io6,*)'cells.  When 2 or more overlapping domains'
            write(io6,*)'have the same cell size, the domain used is '
            write(io6,*)'the FIRST of these domains assigned in the'
            write(io6,*)'control file.'
            num=0
            do k=1,nmetdom
               if(nsame(k).GT.0) then
                  num=num+1
                  write(io6,*)
                  write(io6,*)'   Domain '//TRIM(metdoml(k))//&
                              ' has priority over:'
                  do k2=k+1,nmetdom
                     if(nestfac(k).EQ.nestfac(k2)) then
                        write(io6,*)'   --Domain '//TRIM(metdoml(k2))
                     endif
                  enddo
               endif
            enddo
            if(num.EQ.0) then
               write(io6,*)
               write(io6,*)'No domains have the same cell size'
               write(io6,*)'in this application'
            endif
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine getcell(acall,axy,idom,x,y,dx,dy,icell,jcell,kdom)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 101111           GETCELL
!                D. Strimaitis
!
! --- PURPOSE:  Return the met grid cell and domain for a given
!               location
!
! --- UPDATE
! --- V6.401-V6.402  101111 (DGS): Allow input position to be sum of 2
!                                  parts, and retain precision of
!                                  in-line calculations
! --- V6.301-V6.401  101108 (DGS): Revise logic for nudging location to
!                                  keep it within the outermost domain
! --- V6.3-V6.301    100827 (DGS): Add logic to allow overlapping domain
!                                  grids of the same resolution.  The
!                                  finest grid cell domain is always
!                                  selected, but when several are
!                                  present the choice uses the FIRST
!                                  grid in the list that has the
!                                  controlling resolution
!                                     (the list is determined by the
!                                     order of the DOMAIN1, DOMAIN2,
!                                     DOMAIN3, etc. names in the
!                                     control file)
!                    100827 (DGS): Allow nested grid domains that do not
!                                  align with cells in parent domains
!
! --- INPUTS:
!            acall - character  - Name of routine that called subr.
!              axy - character  - Type of coordinates provided
!                                 MAPKM:  KM in map projection
!                                 GRIDKM: KM from Grid-k origin
!                                 GRID_M: M from Grid-k origin
!                                 GRIDIJ: Number of grid-k cell lengths
!                                         from Grid-k origin
!                                         (as real, e.g. 5.32)
!             idom - integer    - Met grid domain of input I,J or X,Y
!                                 for types GRID_M, GRIDKM, GRIDIJ 
!              x,y - real       - Location coordinates
!            dx,dy - real       - Location coordinate increment
!                                (usually zero)
!
!     Common block /GRIDNEST/ variables:
!           ngrid,metcell(mxnx,mxny,mxmetdom)
!           nestfac(mxmetdom), nestfacmx
!           rnxnest0(mxmetdom),rnynest0(mxmetdom)
!     Common block /METHD/ variables:
!           nxm(mxmetdom),nym(mxmetdom)
!           xgridmi(mxmetdom),xorigm(mxmetdom),yorigm(mxmetdom)
!
!     Parameters:
!           MXNX, MXNY, MXMETDOM, IO6
!
! --- OUTPUT:
!             icell - integer   - Met cell x-index
!             jcell - integer   - Met cell y-index
!              kdom - integer   - Met grid domain of icell,jcell
!
! --- GETCELL called by:  PUFRECS, SLGRECS, COMP, SPLIT, AREAS1, AREAS2,
!                         VOLS, POINTS1, POINTS2, LINES1, LINES2,
!                         CTADJ2, GETELEV, WALLTRAP, GETPRFM, TURBSET,
!                         TIBLSET, TIBLON, TIBLGRO, SETFOG
! --- GETCELL calls:      none
!----------------------------------------------------------------------
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'gridnest.puf'
      include 'methd.puf'
      integer ik(mxmetdom),jk(mxmetdom),mdk(mxmetdom)
      character*12 acall
      character*6 axy
      data cm2km/.001/, ckm2m/1000./
! --- Condition location that is beyond the edge of the outer domain
! --- to the range  0.0 <= x1 < nxm and 0.0 <= y1 < nym.  A minimum
! --- of zero is OK as this is part row 1 or col 1.  But nxm or nym
! --- is part of row nym+1 or col nxm+1, so the location must be inside
! --- this border.  Set margin used to move a location just inside 
! --- the border to 0.5 times the smallest grid cell.
      delta1=0.5/FLOAT(nestfacmx)
! --- Initialize
      ik(1:mxmetdom)=0
      jk(1:mxmetdom)=0
      mdk(1:mxmetdom)=0
! --- Save input domain into local variable (allows it to change)
      idom0=idom
      if(axy(1:5).EQ.'MAPKM') idom0=0
! --- Convert input coordinate to met grid 1 grid-cell coordinates
! --- (Distance from grid-1 origin in grid-1 cell increments)
      if(axy(1:6).EQ.'GRIDIJ') then
         if(idom.EQ.1) then
! ---       Input is distance from grid 1 origin in grid 1 cells
            x1=x
            y1=y
            dx1=dx
            dy1=dy
         else
! ---       Input is distance from grid IDOM origin in grid IDOM cells
            x1=(x+rnxnest0(idom))/FLOAT(nestfac(idom))
            y1=(y+rnynest0(idom))/FLOAT(nestfac(idom))
            dx1=dx/FLOAT(nestfac(idom))
            dy1=dy/FLOAT(nestfac(idom))
         endif
      elseif(axy(1:5).EQ.'MAPKM') then
! ---    Input is map coordinate in km
         x1=(ckm2m*x-xorigm(1))*xgridmi(1)
         y1=(ckm2m*y-yorigm(1))*xgridmi(1)
         dx1=(ckm2m*dx)*xgridmi(1)
         dy1=(ckm2m*dy)*xgridmi(1)
      elseif(axy(1:6).EQ.'GRID_M' .AND. idom.GT.0) then
! ---    Input is distance from grid IDOM origin in m
         x1=(x+rnxnest0(idom)*xgridm(idom))*xgridmi(1)
         y1=(y+rnynest0(idom)*xgridm(idom))*xgridmi(1)
         dx1=dx*xgridmi(1)
         dy1=dy*xgridmi(1)
      elseif(axy(1:6).EQ.'GRIDKM' .AND. idom.GT.0) then
! ---    Input is distance from grid IDOM origin in km
         x1=(ckm2m*x+rnxnest0(idom)*xgridm(idom))*xgridmi(1)
         y1=(ckm2m*y+rnynest0(idom)*xgridm(idom))*xgridmi(1)
         dx1=ckm2m*dx*xgridmi(1)
         dy1=ckm2m*dy*xgridmi(1)
      else
         write(io6,*)
         write(io6,*)'GETCELL:  Error in coordinate type = ',axy
         write(io6,*)'Expected MAPKM or '
         write(io6,*)'GRIDIJ, GRIDKM, GRID_M with Met Domain > 0'
         write(io6,*)'Met Domain = ',idom
         write(io6,*)'Called from '//TRIM(acall)
         stop 'HALTED in GETCELL -- see list file'
      endif
! --- Do not request cell location for a point outside the outermost
! --- MET grid domain (may be part of puff step as it leaves domain)
      x1=AMIN1(x1,(FLOAT(nxm(1))-delta1-dx1))
      x1=AMAX1(x1,(0.0-dx1))
      y1=AMIN1(y1,(FLOAT(nym(1))-delta1-dy1))
      y1=AMAX1(y1,(0.0-dy1))
! --- Loop over met grids to find cell for this location
      nfinest=0
      nestmax=nestfac(1)
      do k=1,ngrid
         if(k.EQ.1) then
            xk=x1
            yk=y1
            dxk=dx1
            dyk=dy1
         else
! ---       Convert grid-cell units from grid-1 to grid-k
            xk=x1*nestfac(k)-rnxnest0(k)
            yk=y1*nestfac(k)-rnynest0(k)
            dxk=dx1*nestfac(k)
            dyk=dy1*nestfac(k)
         endif
         if((xk+dxk).GE.0. .AND. (yk+dyk).GE.0.) then
! ---       Get grid-k (i,j) index
            i=(xk+dxk)+1.0
            j=(yk+dyk)+1.0
! ---       Process if this cell is used
            if(i.LE.mxnx .AND. j.LE.mxny) then
               if(metcell(i,j,k).EQ.1) then
! ---             Valid cell found for this location in met domain k
                  if(nestfac(k) .EQ. nestmax) then
! ---                Same resolution as smallest cell found so far
                     nfinest=nfinest+1
                     mdk(nfinest)=k
                     ik(nfinest)=i
                     jk(nfinest)=j
                  elseif(nestfac(k).GT.nestmax) then
! ---                First cell with finest resolution found so far
                     nfinest=1
                     nestmax=nestfac(k)
                     mdk(nfinest)=k
                     ik(nfinest)=i
                     jk(nfinest)=j
                   endif
               endif
            endif
         endif
      enddo
! --- There may be more than 1 valid (>0) cell (overlapping domains)
      if(nfinest .LT. 1) then
! ---    ERROR
         write(io6,*)
         write(io6,*)'GETCELL:  Error in cells found'
         write(io6,*)'No valid cells found'
         write(io6,*)'        Coord,domain = ',axy,idom
         write(io6,*)'           x,y,dx,dy = ',x,y,dx,dy
         write(io6,*)'       outer nxm,nym = ',nxm(1),nym(1)
         write(io6,*)'delta1,x1,y1,dx1,dy1 = ',delta1,x1,y1,dx1,dy1
         do k=1,ngrid
            write(io6,*)'Domain, Cell I,J = ',k,ik(k),jk(k)
         enddo
         write(io6,*)'Called from '//TRIM(acall)
         stop 'HALTED in GETCELL -- see list file'
      else
! ---    Select the first of the available finest resolution grids
         kdom=mdk(1)
         icell=ik(1)
         jcell=jk(1)
      endif
! --- Final adjustment:  compute cell index in the selected domain
! --- with full precision of in-line code from input data
      if(idom0.GT.0) then
         if(idom0.EQ.kdom) then
! ---       Selected domain is same as input domain
            if(axy(1:6).EQ.'GRIDIJ') then
               ix0=(x+dx)+1.0
               iy0=(y+dy)+1.0
            elseif(axy(1:6).EQ.'GRID_M') then
               ix0=(x+dx)*xgridmi(idom0)+1.0
               iy0=(y+dy)*xgridmi(idom0)+1.0
            elseif(axy(1:6).EQ.'GRIDKM') then
               ix0=ckm2m*(x+dx)*xgridmi(idom0)+1.0
               iy0=ckm2m*(y+dy)*xgridmi(idom0)+1.0
            endif
         else
! ---       Selected domain is different from input domain
            fac_k=FLOAT(nestfac(kdom))
            fac_0=FLOAT(nestfac(idom0))
            if(axy(1:6).EQ.'GRIDIJ') then
               ix0=1.0&
                  +((x+dx)*(fac_k/fac_0))&
                  +(rnxnest0(idom0)*(fac_k/fac_0)-rnxnest0(kdom))
               iy0=1.0&
                  +((y+dy)*(fac_k/fac_0))&
                  +(rnynest0(idom0)*(fac_k/fac_0)-rnynest0(kdom))
            elseif(axy(1:6).EQ.'GRID_M') then
               ix0=1.0&
                  +((x+dx)*xgridmi(idom0)*(fac_k/fac_0))&
                  +(rnxnest0(idom0)*(fac_k/fac_0)-rnxnest0(kdom))
               iy0=1.0&
                  +((y+dy)*xgridmi(idom0)*(fac_k/fac_0))&
                  +(rnynest0(idom0)*(fac_k/fac_0)-rnynest0(kdom))
            elseif(axy(1:6).EQ.'GRIDKM') then
               ix0=1.0&
                  +(ckm2m*(x+dx)*xgridmi(idom0)*(fac_k/fac_0))&
                  +(rnxnest0(idom0)*(fac_k/fac_0)-rnxnest0(kdom))
               iy0=1.0&
                  +(ckm2m*(y+dy)*xgridmi(idom0)*(fac_k/fac_0))&
                  +(rnynest0(idom0)*(fac_k/fac_0)-rnynest0(kdom))
            endif
         endif
         ix0=MAX(1,ix0)
         icell=MIN(ix0,nxm(kdom))
         iy0=MAX(1,iy0)
         jcell=MIN(iy0,nym(kdom))
      endif
      return
      end
