         write(io6,*)'iwat2= ',iwat2x,iwat2
         do k=1,mxnzp1
            write(io6,*)'zfacem= ',zfacemx(k),zfacem(k)
         enddo
         write(io6,*)'lcalgrd= ',lcalgrdx,lcalgrd
