!----------------------------------------------------------------------
      subroutine mdomcross(ldbin,x1,y1,x2,y2,kdom)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100212         MDOMCROSS
!                D. Strimaitis
!
! --- PURPOSE:  Return the met grid domain ID with the smallest cell
!               size that is touched by a line segment
!
! --- INPUTS:
!            LDBIN - logical    - Debug output flag
!            x1,y1 - real       - End 1 of line segment in grid-cell
!                                 coordinates of outermost domain
!            x2,y2 - real       - End 2 of line segment in grid-cell
!                                 coordinates of outermost domain
!
!     Common block /GRIDNEST/ variables:
!           ngrid,cgridll(2,mxmetdom),cgridur(2,mxmetdom),
!           nestfac(mxmetdom)
!
!     Parameters:
!           MXNX, MXNY, MXMETDOM, IO6
!
! --- OUTPUT:
!              kdom - integer   - Met grid domain ID of smallest cell
!                                 touched by line segment
!
! --- MDOMCROSS called by:  COMP
! --- MDOMCROSS calls:      none
!----------------------------------------------------------------------
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'gridnest.puf'
      logical lproc, lcross, ldb, ldbin
! --- Assign local debug output logical
!      ldb=ldbin
      ldb=.FALSE.
! --- Initialize to outermost domain
      kdom=1
! --- Characterize line segment
! --- Range
      xmin=AMIN1(x1,x2)
      xmax=AMAX1(x1,x2)
      ymin=AMIN1(y1,y2)
      ymax=AMAX1(y1,y2)
! --- Slope and intercept
      xdif=x2-x1
      ydif=y2-y1
      axdif=ABS(xdif)
      aydif=ABS(ydif)
      if(axdif.GT.1.0e-20) then
         slope=ydif/xdif
         bintr=y1-x1*slope
      else
! ---    Vertical, b-intercept not used
         slope=1.0e+20
         bintr=0.
      endif
      if(aydif.GT.1.0e-20) then
         slopei=xdif/ydif
      else
         slopei=1.0e+20
      endif
      if(LDB) then
         write(io6,*)
         write(io6,*)'MDOMCROSS: '
         write(io6,*)'    Step Xmin,Xmax = ',xmin,xmax
         write(io6,*)'    Step Ymin,Ymax = ',ymin,ymax
         write(io6,*)'  Slope, Intercept = ',slope,bintr
      endif
! --- Loop over met grids
      do kg=2,ngrid
         if(LDB) then
            write(io6,*)
            write(io6,*)'  Domain = ',kg
         endif
! ---    Test for possible crossing
         lproc=.TRUE.
         if(xmin.GT.cgridur(1,kg)) then
            lproc=.FALSE.
         elseif(xmax.LT.cgridll(1,kg)) then
            lproc=.FALSE.
         elseif(ymin.GT.cgridur(2,kg)) then
            lproc=.FALSE.
         elseif(ymax.LT.cgridll(2,kg)) then
            lproc=.FALSE.
         endif
         lcross=.FALSE.
         if(LPROC) then
! ---       Intersections
            if(axdif.LE.1.0e-20 .OR. aydif.LE.1.0e-20) then
               lcross=.TRUE.
            else
! ---          Test top side
               yi=cgridur(2,kg)
               if(yi.GE.ymin .AND. yi.LE.ymax) then
                  xi=slopei*(yi-bintr)
                  if(ldb) write(io6,*)'TOP: xi,yi =',xi,yi
                  if(xi.GE.xmin .AND.&
                     xi.LE.xmax .AND.&
                     xi.GE.cgridll(1,kg) .AND.&
                     xi.LE.cgridur(1,kg)) lcross=.TRUE.
               endif
! ---          Test bottom side
               yi=cgridll(2,kg)
               if(yi.GE.ymin .AND. yi.LE.ymax) then
                  xi=slopei*(yi-bintr)
                  if(ldb) write(io6,*)'BOTTOM: xi,yi =',xi,yi
                  if(xi.GE.xmin .AND.&
                     xi.LE.xmax .AND.&
                     xi.GE.cgridll(1,kg) .AND.&
                     xi.LE.cgridur(1,kg)) lcross=.TRUE.
               endif
! ---          Test left side
               xi=cgridll(1,kg)
               if(xi.GE.xmin .AND. xi.LE.xmax) then
                  yi=slope*xi+bintr
                  if(ldb) write(io6,*)'LEFT: xi,yi =',xi,yi
                  if(yi.GE.ymin .AND.&
                     yi.LE.ymax .AND.&
                     yi.GE.cgridll(2,kg) .AND.&
                     yi.LE.cgridur(2,kg)) lcross=.TRUE.
               endif
! ---          Test right side
               xi=cgridur(1,kg)
               if(xi.GE.xmin .AND. xi.LE.xmax) then
                  yi=slope*xi+bintr
                  if(ldb) write(io6,*)'Right: xi,yi =',xi,yi
                  if(yi.GE.ymin .AND.&
                     yi.LE.ymax .AND.&
                     yi.GE.cgridll(2,kg) .AND.&
                     yi.LE.cgridur(2,kg)) lcross=.TRUE.
               endif
            endif
         endif
! ---    Check grid cell size if this domain touched by line segment to
! ---    identify domain with smallest cell size (largest nest factor)
         if(LCROSS) then
            if(nestfac(kg).GT.nestfac(kdom)) kdom=kg
         endif
         if(LDB) write(io6,*)'  LPROC, LCROSS = ',lproc,lcross
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine rdnh3zm(iunit,nz)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 100917           RDNH3ZM
!                J. Scire, D. Strimaitis 
!
! --- PURPOSE:  Read monthly vertical profiles of NH3 concentrations
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of data file
!               NZ - integer    - Number of layers expected
!
!     Common Block /QA/ variables:
!            NCOMMOUT
!        Parameters:
!            IO6, IOX
!
! --- OUTPUT:
!
!     Common Block /CHEMDAT/ variables:
!            BCKNH3ZM(12,mxnz)
!     Common Block /QA/ variables:
!            NCOMMOUT
!
! --- RDNH3ZM called by: CHEMI
! --- RDNH3ZM calls:     none
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'chemdat.puf'
      include 'qa.puf'
      integer nmonth(12)
      character*10 month(12)
      character*16 cunits,cformat
      character*33 blank33,break33
      character*132 comment1,blank,break
      character*264 line264,blank264
      data month/'JANUARY   ','FEBRUARY  ','MARCH     ',&
                 'APRIL     ','MAY       ','JUNE      ',&
                 'JULY      ','AUGUST    ','SEPTEMBER ',&
                 'OCTOBER   ','NOVEMBER  ','DECEMBER  '/
      data blank33/'                                 '/
      data break33/'.................................'/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
      blank264(1:132)=blank
      blank264(133:264)=blank
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Initialize month counter
      nmonth(1:12)=0
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- First record and comments
      read(iunit,'(2a16,a64)') dataset,dataver,datamod
      write(iox,'(2a16,a64)') dataset,dataver,datamod
      read(iunit,*) ncom
      write(iox,*) ncom
      do k=1,ncom
         comment1=blank
         read(iunit,'(a132)') comment1
         write(iox,'(a132)') comment1
      enddo
      ncommout=ncommout+2+ncom
! --- Units
      read(iunit,'(a16)') cunits
      write(iox,'(a16)') cunits
      ncommout=ncommout+1
! --- Only concentration unit currently recognized is 'PPB'
      itest=INDEX(cunits,'PPB')
      if(itest.EQ.0) then
         write(io6,*)
         write(io6,*)'ERROR in NH3Z.DAT File'
         write(io6,*)'Expected concentration unit: PPB'
         write(io6,*)'   Found concentration unit: '//cunits
         write(*,*)'ERROR in NH3Z.DAT File -- See List File'
         stop
      endif
! --- File format
      read(iunit,'(a16)') cformat
      write(iox,'(a16)') cformat
      ncommout=ncommout+1
! --- Only format currently recognized is 'MONTH_LAYER'
      itest=INDEX(cformat,'MONTH_LAYER')
      if(itest.EQ.0) then
         write(io6,*)
         write(io6,*)'ERROR in NH3Z.DAT File'
         write(io6,*)'Expected format type: MONTH_LAYER'
         write(io6,*)'   Found format type: '//cformat
         write(*,*)'ERROR in NH3Z.DAT File -- See List File'
         stop
      endif
! --- Number of layers
      read(iunit,*) nzfile
      write(iox,*) nzfile
      ncommout=ncommout+1
! --- Number of layers must match model run
      if(nz.NE.nzfile) then
         write(io6,*)
         write(io6,*)'ERROR in NH3Z.DAT File'
         write(io6,*)'Number of layers must match model simulation'
         write(io6,*)'   File NZ: ',nzfile
         write(io6,*)'  Model NZ: ',nz
         write(*,*)'ERROR in NH3Z.DAT File -- See List File'
         stop
      endif
! --- Read data into array
      do k=1,12
! ---    Image of current line
         line264=blank264
         read(iunit,'(a264)',end=999) line264
         write(iox,'(a)') TRIM(line264)
         ncommout=ncommout+1
! ---    Identify month
         imo=0
         do kk=1,12
            itest=INDEX(line264,TRIM(month(kk)))
            if(itest.GT.0) imo=kk
         enddo
         if(imo.EQ.0) then
            write(io6,*)
            write(io6,*)'ERROR in NH3Z.DAT File'
            write(io6,*)'Month not found on data record:'
            write(io6,*)TRIM(line264)
            write(io6,*)'  Expected: '
            do i=1,12
               write(io6,*) month(i)
            enddo
            write(*,*)'ERROR in NH3Z.DAT File -- See List File'
            stop
         endif
! ---    Increment counter
         nmonth(imo)=nmonth(imo)+1
! ---    Identify start of data on record
         itest=INDEX(line264,':')
         if(itest.GT.0) then
            j1=itest+1
            j2=LEN_TRIM(line264)
            read(line264(j1:j2),*) (bcknh3zm(imo,iz),iz=1,nz)
         else
            write(io6,*)
            write(io6,*)'ERROR in NH3Z.DAT File'
            write(io6,*)'Delimiter (:) not found on data record:'
            write(io6,*)TRIM(line264)
            write(*,*)'ERROR in NH3Z.DAT File -- See List File'
            stop
         endif
      enddo
! --- QA on months provided
      do i=1,12
         if(nmonth(i).EQ.0) goto 999
      enddo
! --- Report data values to list file
      write(io6,*)
      write(io6,*)
      write(io6,*)'RDNH3ZM: Monthly Ammonia Profiles Provided (ppb)'
      write(io6,*)'------------------------------------------------'
      do iz=1,nz
         write(io6,*)'Layer:     ',iz
         write(io6,*)'bcknh3zm = ',(bcknh3zm(ih,iz),ih=1,4)
         write(io6,*)'         = ',(bcknh3zm(ih,iz),ih=5,8)
         write(io6,*)'         = ',(bcknh3zm(ih,iz),ih=9,12)
      enddo
      write(io6,*)
! --- QA on values provided
      do iz=1,nz
         do imo=1,12
            if(bcknh3zm(imo,iz).LT.0.0) then
               write(io6,*)
               write(io6,*)'ERROR in NH3Z.DAT File'
               write(io6,*)'Background Ammonia Profile Concentrations'
               write(io6,*)'should never be negative'
               write(io6,*)
               write(*,*)'ERROR in NH3Z.DAT File -- See List File'
               stop
            endif
         enddo
      enddo
      return
999   write(io6,*)
      write(io6,*)'ERROR in NH3Z.DAT File'
      write(io6,*)
      write(io6,*)'12 Months are not found in the file'
      write(io6,*)'Month   Number of entries'
      do i=1,12
         write(io6,*) month(i),nmonth(i)
      enddo
      write(*,*)'ERROR in NH3Z.DAT File -- See List File'
      stop
      end
