!                   rnlat0ma,relon0ma,xlat1ma,xlat2ma,
!                   zfacema(mxnzp1),itimesa,
!                   ibymeta(mxmetdom),ibmmeta(mxmetdom),
!                   ibdmeta(mxmetdom),ibjdmeta(mxmetdom),
!                   ibhmeta(mxmetdom),ibsmeta(mxmetdom),
!                   ieymeta(mxmetdom),iemmeta(mxmetdom),
!                   iedmeta(mxmetdom),iejdmeta(mxmetdom),
!                   iehmeta(mxmetdom),iesmeta(mxmetdom),
!                   iutmznma,feastma,fnorthma,pmapma,
!                   datumma,datenma,utmhemma,
!                   naux2d,naux3d,auxnam2d,auxnam3d,
!                   auxunit2d,auxunit3d,auxtyp2d,auxtyp3d
!     Common block /AUXDAT/ variables:
! --- 6.42_x1.1 140521
!                   icldmr2D(3,mxmetdom),icldmr3D(mxmetdom)
!
! --- AUX1 called by:  SETUP, NEXTFIL
! --- AUX1 calls:      RDR1D, YR4, JULDAY, UTCBASR
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
!
! --- Include common block /AUXHD/ -- CALMET auxiliary data
      include 'auxhd.puf'
! --- Include common block /AUXDAT/ -- CALMET auxiliary data
      include 'auxdat.puf'
! --- Include common block /GRIDNEST/ -- Nest data
      include 'gridnest.puf'
! --- Include common block /WRKSPC/  -- Working arrays
      include 'wrkspc.puf'
      character*80 title(3)
      character*70 messag
      character*8 vermet,clab1
      character*8 clabexp
      logical ldate,lecho
! --- Control for printing entire CALMET control file
      logical lmetcf
! --- Local Variables
      character*16 dataset,dataver
      character*33 blank33
      character*64 datamod
      character*80 doc1
      character*132 comment1,blank
      data ldate/.false./
      data lmetcf/.false./
      data blank33/'                                 '/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
      if(lecho) then
         write(io6,*)
         write(io6,*)
         write(io6,*)
         write(io6,*)'CALMET Auxiliary file information: Grid ',kg
         write(io6,*)'--------------------------------------------'
         write(io6,*)
      endif
! --- File unit for nested grid kg
      io=io7+(kg-1)+ngrid
! --- Read records
! -------------------
!
! ---   Dataset 1.0 with comment records and begin/end times
! ----------------------------------------------------------
        itimesa=1
! ---   Record #1 - File Declaration -- 24 words
        read(io) dataset,dataver,datamod
        if(lecho) write(io6,'(2a16,a64)') dataset,dataver,datamod
! --- 6.42_x1.1 140521
! ---   Expect dataset 1.1 with comment records and begin/end times
! ---               (Arrays use compression to pack zeroes)
! ---   Stop if dataset is version 1.0 (not supported)
        ktest=INDEX(dataver,'1.0')
        if(ktest.NE.0) then
           write(io6,*)
           write(io6,*)'ERROR in subr. AUX1 -- unsupported '
           write(io6,*)' Auxiliary file dataset version: ',dataver
           stop 'Halted in AUX1 -- see list file.'
        endif
! ---   Record #2 - Number of comment lines -- 1 word
        read(io) ncom
! ---   Loop over comment records
        do i=1,ncom
          comment1=blank
          read(io) comment1
          if(i.EQ.1) then
! ---       Save model version line
            doc1=comment1(1:80)
          elseif(i.LE.4) then
! ---       Save 3 title lines
            title(i-1)=comment1(1:80)
          endif
          if(lmetcf) write(io6,*) comment1
        enddo
        if(lecho .AND. .NOT.lmetcf) then
           write(io6,*)
           write(io6,*) doc1
           write(io6,*) title(1)
           write(io6,*) title(2)
           write(io6,*) title(3)
           write(io6,*)
        endif
! ---   record #NCOM+3 - run control parameters -- 36 words
        read(io)&
         ibymeta(kg),ibmmeta(kg),ibdmeta(kg),ibhmeta(kg),ibsmeta(kg),&
         ieymeta(kg),iemmeta(kg),iedmeta(kg),iehmeta(kg),iesmeta(kg),&
         axbtzma,irlgmeta,nxma(kg),nyma(kg),nzma,&
         xgridma(kg),xorigma(kg),yorigma(kg),&
         pmapma,datumma,datenma,&
         feastma,fnorthma,utmhemma,iutmznma,&
         rnlat0ma,relon0ma,xlat1ma,xlat2ma,naux2d(kg),naux3d(kg)
        call YR4(io6,ibymeta(kg),ierrb)
        call YR4(io6,ieymeta(kg),ierre)
        if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in AUX1'
        call JULDAY(io6,ibymeta(kg),ibmmeta(kg),ibdmeta(kg),&
                    ibjdmeta(kg))
        call JULDAY(io6,ieymeta(kg),iemmeta(kg),iedmeta(kg),&
                    iejdmeta(kg))
! ---   Process character AXBTZM into real XBTZM
        call UTCBASR(axbtzma,xbtzma)
      if(LECHO)then
         write(io6,*)
         write(io6,*)
         write(io6,*)'-- Data begin at:'
         write(io6,*)' IBYR    = ',ibymeta(kg)
         write(io6,*)' IBMO    = ',ibmmeta(kg)
         write(io6,*)' IBDY    = ',ibdmeta(kg)
         write(io6,*)' IBHR    = ',ibhmeta(kg)
         write(io6,*)' IBSEC   = ',ibsmeta(kg)
         write(io6,*)'-- Data end at:'
         write(io6,*)' IEYR    = ',ieymeta(kg)
         write(io6,*)' IEMO    = ',iemmeta(kg)
         write(io6,*)' IEDY    = ',iedmeta(kg)
         write(io6,*)' IEHR    = ',iehmeta(kg)
         write(io6,*)' IESEC   = ',iesmeta(kg)
         write(io6,*)
         write(io6,*)' AXBTZM  = ',axbtzma
         write(io6,*)' XBTZM   = ',xbtzma
         write(io6,*)' IRLG    = ',irlgmeta
         write(io6,*)' PMAP    = ',pmapma
         write(io6,*)' DATUM   = ',datumma
         write(io6,*)' NIMADATE= ',datenma
         write(io6,*)' FEASTM  = ',feastma
         write(io6,*)' FNORTHM = ',fnorthma
         if(PMAPMA.EQ.'UTM     ') then
            write(io6,*)' IUTMZN  = ',iutmznma
            write(io6,*)' UTMHEM  = ',utmhemma
         else
            write(io6,*)' XLAT1M  = ',xlat1ma
            write(io6,*)' XLAT2M  = ',xlat2ma
            write(io6,*)' RNLAT0M = ',rnlat0ma
            write(io6,*)' RELON0M = ',relon0ma
         endif
         write(io6,*)' NXM     = ',nxma(kg)
         write(io6,*)' NYM     = ',nyma(kg)
         write(io6,*)' NZM     = ',nzma
         write(io6,*)' XGRIDM  = ',xgridma(kg)
         write(io6,*)' XORIGM  = ',xorigma(kg)
         write(io6,*)' YORIGM  = ',yorigma(kg)
      endif
! --- Check that enough workspace has been allocated
      nec=nxma(kg)*nyma(kg)
      if(nw1.lt.nec)then
         write(io6,*)'ERROR in subr. AUX1 -- insufficient workspace ',&
         'allocated to work arrays -- Allocated (NW1) = ',nw1,&
         '   Required (NEC) = ',nec
         write(*,*)
         stop 'Halted in AUX1 -- see list file.'
      endif
      nzmp1=nzma+1
      if(mxnzp1.lt.nzmp1)then
         write(io6,*)'ERROR in subr. AUX1 -- dimension of ZFACEM ',&
         'array is too small for no. layers in MET file'
         write(io6,*)'MXNZP1 = ',mxnzp1,' NZMP1 = ',nzmp1
         write(*,*)
         stop 'Halted in AUX1 -- see list file.'
      endif
! ---   Check MXAUX array size
        mxsize=MAX(naux2d(kg),naux3d(kg))
        if(mxsize.GT.mxaux) then
         write(io6,*)'ERROR in subr. AUX1 -- insufficient size ',&
         'allocated to auxiliary data arrays --'
         write(io6,*)'MXAUX set in PARAMS.PUF = ',mxaux
         write(io6,*)'     Need NAUX2D,NAUX3D = ',naux2d(kg),naux3d(kg)
         write(*,*)
         stop 'Halted in AUX1 -- see list file.'
        endif
! --- record #NCOM+4 - cell face heights (NZ + 1 words)
      call rdr1d(io,itimesa,zfacema,nzmp1,clab1,idum,idum,idum,idum)
! --- Check that record label matches expected label
      if(clab1.NE.'ZFACE')then
         clabexp='ZFACE'
         go to 999
      endif
! --- record #NCOM+5 - Names, units and types of 2D variables
! ---                                (2+naux2d*5 words)
      read(io) clab1,(auxnam2d(k,kg),k=1,naux2d(kg)),&
                     (auxunit2d(k,kg),k=1,naux2d(kg)),&
                     (auxtyp2d(k,kg),k=1,naux2d(kg))
! --- Check that record label matches expected label
      if(clab1.NE.'2D_VARS')then
         clabexp='2D_VARS'
         go to 999
      endif
! --- record #NCOM+6 - Names, units and types of 3D variables
! ---                                (2+naux3d*5 words)
      read(io) clab1,(auxnam3d(k,kg),k=1,naux3d(kg)),&
                     (auxunit3d(k,kg),k=1,naux3d(kg)),&
                     (auxtyp3d(k,kg),k=1,naux3d(kg))
! --- Check that record label matches expected label
      if(clab1.NE.'3D_VARS')then
         clabexp='3D_VARS'
         go to 999
      endif
      call SETAUX(kg)
! -------------------------------------
      if(LECHO)then
         write(io6,203)(zfacema(n),n=1,nzmp1)
         write(io6,*)
         write(io6,*)' NAUX2D  = ',naux2d(kg)
         write(io6,204)(auxnam2d(k,kg),k=1,naux2d(kg))
         write(io6,204)(auxunit2d(k,kg),k=1,naux2d(kg))
         write(io6,205)(auxtyp2d(k,kg),k=1,naux2d(kg))
         write(io6,*)' NAUX3D  = ',naux3d(kg)
         write(io6,204)(auxnam3d(k,kg),k=1,naux3d(kg))
         write(io6,204)(auxunit3d(k,kg),k=1,naux3d(kg))
         write(io6,205)(auxtyp3d(k,kg),k=1,naux3d(kg))
         write(io6,*)
! --- 6.42_x1.1 140521
         write(io6,*)' Cloud Data Indexes Assigned:'
         write(io6,*)' 2D Arrays ---'
         write(io6,*)'      LCW Mix Ratio Aloft =',icldmr2d(1,kg)
         write(io6,*)'        Base of LCW Aloft =',icldmr2d(2,kg)
         write(io6,*)'        Top of  LCW Aloft =',icldmr2d(3,kg)
         write(io6,*)' 3D Arrays ---'
         write(io6,*)'         3D LCW Mix Ratio =',icldmr3d(kg)
203      format(1x,'ZFACEM  = ',10(f9.3,', ')/)
204      format(1x,10(a8,2x))
205      format(1x,10(a4,6x))
      endif
! -------------------------------------
!
      return
!
! --- Write error messages -- incorrect record label read
999   continue
      write(io6,1001)clab1,clabexp
1001  format(/1x,'ERROR in subr. AUX1 -- incorrect record label ',&
       'read from MET data file'//1x,'Label read     = ',a8/&
                                     1x,'Label expected = ',a8)
      write(*,*)
      stop 'Halted in AUX1 -- see list file.'
      end
!----------------------------------------------------------------------
      subroutine setaux(kg)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521            SETAUX
!                D. Strimaitis
!
! --- PURPOSE:  Process header records of auxiliary CALMET
!               meteorological data file
!
! --- UPDATES:
!
! --- v6.302 (100917) to 6.42_x1.1 (140521)
!        - AUX dataset v1.1 and 4 cloud variables (3 are 2D, 1 is 3D)
!
! --- INPUTS:
!            KG - integer       - Current CALMET grid (e.g., nesting)
!
!     Common block /AUXHD/ variables:
!                   naux2d,naux3d,auxnam2d,auxnam3d,
!                   auxunit2d,auxunit3d,auxtyp2d,auxtyp3d
!
!     Parameters:
!           MXAUX, MXMETDOM, IO6
!
! --- OUTPUT:
!
!     Common block /AUXDAT/ variables:
! --- 6.42_x1.1 140521
!           icldmr2D(3,mxmetdom),icldmr3D(mxmetdom)
!
! --- SETAUX called by:  AUX1
! --- SETAUX calls:      none
!----------------------------------------------------------------------
! --- Include parameters
      include 'params.puf'
! --- Include common block /AUXHD/ -- CALMET auxiliary header
      include 'auxhd.puf'
! --- Include common block /AUXDAT/ -- CALMET auxiliary data
      include 'auxdat.puf'
      logical problem
      character*8 cldvar2d(3), cldvar3d
      character*8 cldunit2d(3), cldunit3d
      data cldvar2d/'CLDMRUP ','ZCLDBOT ','ZCLDTOP '/
      data cldvar3d/'CLDMR3D '/
      data cldunit2d/'G/KG    ','MAGL    ','MAGL    '/
      data cldunit3d/'G/KG    '/
      data ncldvar2d/3/
      problem=.FALSE.
! --- 3D cloud variables
! ----------------------
! --- Select indexes for 3D cloud variables in AUX data arrays
      icldmr3d(kg)=0
      do k=1,naux3d(kg)
         if(auxnam3d(k,kg).EQ.cldvar3d) icldmr3d(kg)=k
      enddo
! --- Test for needed variables
      if(icldmr3d(kg).EQ.0) then
         write(io6,*)
         write(io6,*)'ERROR in SETAUX -- 3D cloud data not found'
         write(io6,*)'for Met Grid Domain ',kg
         write(io6,*)'Expected: '//cldvar3d
         write(io6,*)'Found:'
         do k=1,naux3d(kg)
            write(io6,*)'      ',auxnam3d(k,kg)
         enddo
         problem=.TRUE.
         write(*,*) 'ERROR in SETAUX -- missing 3D variable '
      endif
! --- Test units
      if(auxunit3d(icldmr3d(kg),kg).NE.cldunit3d) then
         write(io6,*)
         write(io6,*)'ERROR in SETAUX -- Wrong 3D Cloud Variable'
         write(io6,*)'Units for Met Grid Domain ',kg
         write(io6,*)'Expected: '//cldunit3d
         write(io6,*)'Found: ',auxunit3d(icldmr3d(kg),kg)
         problem=.TRUE.
         write(*,*) 'ERROR in SETAUX -- wrong 3D data units'
      endif
! --- Test type (all must be RC4)
      if(auxtyp3d(icldmr3d(kg),kg).NE.'RC4 ') then
         write(io6,*)
         write(io6,*)'ERROR in SETAUX -- Wrong 3D Cloud Variable'
         write(io6,*)'Data Type for Met Grid Domain ',kg
         write(io6,*)'Expected RC4'
         write(io6,*)'Found: ',auxtyp3d(icldmr3d(kg),kg)
         problem=.TRUE.
         write(*,*) 'ERROR in SETAUX -- wrong 3D data type'
      endif
! --- 2D cloud variables
! ----------------------
! --- Select indexes for 2D cloud variables in AUX data arrays
      do n=1,ncldvar2d
         icldmr2d(n,kg)=0
      enddo
      do k=1,naux2d(kg)
         do n=1,ncldvar2d
            if(auxnam2d(k,kg).EQ.cldvar2d(n)) icldmr2d(n,kg)=k
         enddo
      enddo
! --- Test for needed variables
      itest=0
      do n=1,ncldvar2d
         if(icldmr2d(n,kg).EQ.0) then
            write(io6,*)
            write(io6,*)'ERROR in SETAUX -- 2D cloud variable not '//&
                        'found for Met Grid Domain ',kg
            write(io6,*)'Expected variable name '//cldvar2d(n)
            itest=itest+1
         endif
      enddo
      if(itest.NE.0) then
         write(*,*) 'ERROR in SETAUX -- # missing 2D variables: ',itest
         problem=.TRUE.
      endif
! --- Test units
      itest=0
      do n=1,ncldvar2d
         if(auxunit2d(icldmr2d(n,kg),kg).NE.cldunit2d(n)) then
            write(io6,*)
            write(io6,*)'ERROR in SETAUX -- Wrong Cloud Units '//&
                        'for Met Grid Domain ',kg
            write(io6,*)'Expected: '//cldunit2d(n)
            write(io6,*)'Found: ',auxunit2d(icldmr2d(n,kg),kg)
            itest=itest+1
         endif
      enddo
      if(itest.NE.0) then
         write(*,*) 'ERROR in SETAUX -- # wrong 2D units: ',itest
         problem=.TRUE.
      endif
! --- Test type (all must be RC4)
      itest=0
      do n=1,ncldvar2d
         if(auxtyp2d(icldmr2d(n,kg),kg).NE.'RC4 ') then
            write(io6,*)
            write(io6,*)'ERROR in SETAUX -- Wrong Cloud Data '//&
                        'Type for Met Grid Domain ',kg
            write(io6,*)'Expected RC4'
            write(io6,*)'Found: ',auxtyp2d(icldmr2d(n,kg),kg)
            itest=itest+1
         endif
      enddo
      if(itest.NE.0) then
         write(*,*) 'ERROR in SETAUX -- # wrong 2D data types: ',itest
         problem=.TRUE.
      endif
      if(problem) stop 'Halted in SETAUX -- see list file.'
      return
      end
!----------------------------------------------------------------------
      subroutine rdaux(idathrm,isecm,wrk1,nwork,lecho,&
                       icldmr2d,icldmr3d,qcup,zupbot,zuptop,qc3d,&
                       kdathre,ksece,kdathrb,ksecb)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140521             RDAUX
!                D. Strimaitis
!
! --- PURPOSE:  Read auxiliary CALMET meteorological data for one
!               period, keeping cloud water
!
! --- UPDATES:
!
! --- v6.302 (100917) to 6.42_x1.1 (140521)
!        - Revised to obtain more detailed LWC data from Auxiliary
!          data file
!
! --- INPUTS:
!       IDATHRM - integer     - Mid-pt date/hour of required
!                               data (YYYYJJJHH)
!         ISECM - integer     - Mid-pt second of required data (SSSS)
!   WRK1(nwork) - real array  - Work array of length "NWORK" words
!         NWORK - integer     - Dimension of work array -- NOTE:
!                               NWORK must be at least as large as
!                               the max. of (NXM * NYM)
!         LECHO - logical     - Flag controlling printing of input
!                               meteorological data fields
!   ICLDMR2D(3,mxmetdom) - integer - Position of 2D cloud water data
!                                    in CALMET AUXiliary files
!                                    (1,_): CLDMRUP
!                                    (2,_): ZCLDBOT
!                                    (3,_): ZCLDTOP
!     ICLDMR3D(mxmetdom) - integer - Position of 3D cloud water data
!                                    in CALMET AUXiliary files
!                                    (_): CLDMR3D
!
!    Common block /AUXHD/ variables:
!         NXMA, NYMA, NZMA, ITIMESA
!    Common block /GRIDNEST/ variables:
!         ngrid
!
!    Parameters:
!         MXNX, MXNY, MXAUX, MXMETDOM, MXNZMP1, IO6, IO7
!
! --- OUTPUT:
!
!        QCUP(mxnx,mxny,mxmetdom)
!                       - real    - CLDMRUP:  Liquid cloud water
!                                   mixing ratio averaged over all
!                                   cloud layers above CALMET domain
!                                   top (g/kg)
!      ZUPBOT(mxnx,mxny,mxmetdom)
!                       - real    - ZCLDBOT:  Bottom of liquid cloud
!                                   water layers above CALMET domain
!                                   top (mAGL)
!                                   (Zero if no water)
!      ZUPTOP(mxnx,mxny,mxmetdom)
!                       - real    - ZCLDTOP:  Top of liquid cloud
!                                   water layers above CALMET domain
!                                   top (mAGL)
!                                   (Zero if no water)
!   QC3D(mxnx,mxny,mxnz,mxmetdom)
!                       - real    - CLDMR3D:  Liquid cloud water
!                                   mixing ratio for 3D CALMET grid
!                KDATHR - integer - Date of hour of data (YYYYJJJHH)
!                KDATHR - integer - Date of hour of data (YYYYJJJHH)
!               KDATHRE - integer - End date/hour of data (YYYYJJJHH)
!                 KSECE - integer - End second of data (SSSS)
!               KDATHRB - integer - Begin date/hour of data (YYYYJJJHH)
!                 KSECB - integer - Begin second of data (SSSS)
!
! --- RDAUX called by:  COMP
! --- RDAUX calls:      RDR2DAUX, RDI2DAUX, INCR, OUT,
!                       NEXTFIL, TPERIOD, YR4
!----------------------------------------------------------------------
! --- Include parameter statements
      include 'params.puf'
      include 'gridnest.puf'
      include 'auxhd.puf'
      real wrk1(nwork)
      real qcup(mxnx,mxny,mxmetdom)
      real zupbot(mxnx,mxny,mxmetdom)
      real zuptop(mxnx,mxny,mxmetdom)
      real qc3d(mxnx,mxny,mxnz,mxmetdom)
      integer icldmr2d(3,mxmetdom),icldmr3d(mxmetdom)
      logical lcmprs
! --- Dummy arrays
      real rskip(mxnx,mxny)
      integer iskip(mxnx,mxny)
! --- Declare dummy arrays as scalars since they are only used as
! --- placeholders in OUT argument list for input arrays that are 
! --- not used (this may provoke a compiler error)
      real    rdum
      integer idum
      character*70 messag
      character*12 clabel
      character*12 clabexp
      logical lecho,ldate
      data ldate/.false./
! --- Process each CALMET grid in turn to fill arrays
! -------------------------------------------------------
      do kg=1,ngrid
! --- CALMET.DAT.AUX file unit for current grid domain
      io=io7+(kg-1)+ngrid
! --- Check that enough workspace has been allocated
      nec=nxma(kg)*nyma(kg)
      if(nwork.lt.nec)then
         write(io6,*)'ERROR in subr. RDAUX -- insufficient workspace ',&
         'allocated to work arrays -- Allocated (NWORK) = ',nwork,&
         '   Required (NEC) = ',nec
         write(*,*)
         stop 'Halted in RDAUX -- see list file.'
      endif
! --- Begin reading data records for one time period
1     continue
! --- Initialize flag for this file
      ifirst=1
! --- Read the 2D variables
      ieof=0
      do k=1,naux2d(kg)
! ---    Read timestamp (EOF logic applies to timestamp record)
         ieof=1
         if(ifirst.EQ.1) then
            read(io,end=99) kdathrb,ksecb,kdathre,ksece
            ifirst=0
         else
            read(io,end=99) ndathrb,nsecb,ndathre,nsece
! ---       Must match
            idiff=ABS(ndathre-kdathre)+ABS(nsece-ksece)
            if(idiff.NE.0) goto 3999
         endif
         ieof=0
! ---    Set compression flag
         if(auxtyp2d(k,kg).EQ.'RC4 ') then
            lcmprs=.TRUE.
         else
            lcmprs=.FALSE.
         endif
! ---    Set expected label
         clabexp(1:8)=auxnam2d(k,kg)
         clabexp(9:12)='    '
! ---    Identify cloud variables (presently there are 3)
         iuse=0
         do i=1,3
            if(k.EQ.icldmr2d(i,kg)) then
               iuse=i
! ---          This variable is needed for cloud LWC
               if(lecho) write(io6,*)'2D Cloud variable, k = ',&
                                    auxnam2d(k,kg),k
               if(lecho) write(io6,*)
            endif
         enddo
         if(iuse.EQ.1) then
! ---       CLDMRUP
            call RDR2DAUX(io,qcup(1,1,kg),wrk1,mxnx,mxny,&
                          nxma(kg),nyma(kg),lcmprs,clabel)
! ---       Check that record label matches expected label
            if(clabel.NE.clabexp)go to 999
         elseif(iuse.EQ.2) then
! ---       ZCLDBOT
            call RDR2DAUX(io,zupbot(1,1,kg),wrk1,mxnx,mxny,&
                          nxma(kg),nyma(kg),lcmprs,clabel)
! ---       Check that record label matches expected label
            if(clabel.NE.clabexp)go to 999
         elseif(iuse.EQ.3) then
! ---       ZCLDTOP
            call RDR2DAUX(io,zuptop(1,1,kg),wrk1,mxnx,mxny,&
                          nxma(kg),nyma(kg),lcmprs,clabel)
! ---       Check that record label matches expected label
            if(clabel.NE.clabexp)go to 999
         else
! ---       Not used
            if(auxtyp2d(k,kg).EQ.'I_4 ') then
               call RDI2DAUX(io,iskip,wrk1,mxnx,mxny,&
                             nxma(kg),nyma(kg),lcmprs,clabel)
            else
               call RDR2DAUX(io,rskip,wrk1,mxnx,mxny,&
                             nxma(kg),nyma(kg),lcmprs,clabel)
            endif
         endif
      enddo
! --- Read the 3D variables
      ieof=0
      do k=1,naux3d(kg)
! ---    Read timestamp (EOF logic applies to timestamp record)
         ieof=1
         if(ifirst.EQ.1) then
            read(io,end=99) kdathrb,ksecb,kdathre,ksece
            ifirst=0
         else
            read(io,end=99) ndathrb,nsecb,ndathre,nsece
! ---       Must match
            idiff=ABS(ndathre-kdathre)+ABS(nsece-ksece)
            if(idiff.NE.0) goto 3999
         endif
         ieof=0
! ---    Set compression flag
         if(auxtyp3d(k,kg).EQ.'RC4 ') then
            lcmprs=.TRUE.
         else
            lcmprs=.FALSE.
         endif
! ---    Set expected label
         clabexp(1:8)=auxnam3d(k,kg)
         clabexp(9:12)='    '
         if(k.EQ.icldmr3d(kg)) then
! ---       This variable is needed for cloud LWC
            if(lecho) write(io6,*)'3D Cloud variable, k = ',&
                                 auxnam3d(k,kg),k
            if(lecho) write(io6,*)
! ---       3D Cloud Mixing ratio
            do iz=1,nzma
               call RDR2DAUX(io,qc3d(1,1,iz,kg),wrk1,mxnx,mxny,&
                             nxma(kg),nyma(kg),lcmprs,clabel)
! ---          Check that record label matches expected label
               write(clabexp(9:12),'(i4.4)')iz
               if(clabel.NE.clabexp)go to 999
            enddo
         elseif(auxtyp3d(k,kg).EQ.'I_4 ') then
! ---       Not needed: integer
            do iz=1,nzma
               call RDI2DAUX(io,iskip,wrk1,mxnx,mxny,&
                             nxma(kg),nyma(kg),lcmprs,clabel)
            enddo
         else
! ---       Not needed: real
            do iz=1,nzma
               call RDR2DAUX(io,rskip,wrk1,mxnx,mxny,&
                             nxma(kg),nyma(kg),lcmprs,clabel)
            enddo
         endif
      enddo
99    continue
      if(ieof.EQ.1) then
! ---    Reached end of CALMET file; check for another file
! ---    and start over
         call NEXTFIL(kg,ifound)
         if(ifound.EQ.1) then
            goto 1
         else
            write(*,*)
            write(*,*)'RDAUX: Unexpected EOF in CALMET records'
            write(*,*)'       for CALMET Grid Domain ',kg
            write(*,*)'Target  Date-time: ',idathrm,isecm
            write(*,*)'Reading Date-time: ',ndathrb,nsecb
            write(*,*)'               to: ',ndathre,nsece
            stop 
         endif
      endif
! --- Set date-time for period
! ---------------------------------
! --- Enforce YYYY format for year
      ibyr=kdathrb/100000
      ibdyhr=kdathrb-ibyr*100000
      ieyr=kdathre/100000
      iedyhr=kdathre-ieyr*100000
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDAUX'
      kdathrb=ibyr*100000+ibdyhr
      kdathre=ieyr*100000+iedyhr
! --- Check to see if required date-time has been read
      call TPERIOD(0,idathrm,isecm,kdathrb,ksecb,kdathre,&
                   ksece,ilocate)
      if(ilocate.EQ.1) then
! ---    Obtain next time period in MET file
         go to 1
      elseif(ilocate.EQ.-1) then
         write(io6,*)'ERROR in subr. RDAUX -- current time not found',&
         ' in the MET data file'
         write(io6,*)' -- Current date/hour,sec = ',idathrm,isecm
         write(io6,*)' -- Last date/hour,sec read = ',kdathrb,ksecb
         write(io6,*)'                           to ',kdathre,ksece
         stop 'ERROR in subr. RDAUX -- current time not found'
      endif
! --- Next CALMET grid
      enddo
! ----------------------------------------------------
!
! --- Write the input fields, if requested
      if(LECHO)then
!
! ---    Date and time
         write(io6,*)
         write(io6,*)'------ AUX. FIELDS BEGINNING: ',kdathrb,ksecb
         write(io6,*)'------            AND ENDING: ',kdathre,ksece
! ---    Provide each CALMET grid in turn
         do kg=1,ngrid
           write(io6,*)
! ---      Just the cloud data fields
           messag='2D Avg. Cloud MixR Aloft (g/kg) -- Grid: '
           write(messag(41:42),'(i2)')kg
           call OUT(qcup(1,1,kg),idum,1,5,ldate,messag,&
                       nxma(kg),nyma(kg))
           messag='2D Base Cloud MixR Aloft (mAGL) -- Grid: '
           write(messag(41:42),'(i2)')kg
           call OUT(zupbot(1,1,kg),idum,1,5,ldate,messag,&
                       nxma(kg),nyma(kg))
           messag='2D Top Cloud MixR Aloft (mAGL) -- Grid: '
           write(messag(40:41),'(i2)')kg
           call OUT(zuptop(1,1,kg),idum,1,5,ldate,messag,&
                       nxma(kg),nyma(kg))
           do iz=1,nzma
             messag='3D Cloud MixR (g/kg) -- Level:     -- Grid: '
             write(messag(44:45),'(i2)')kg
             write(messag(31:33),'(i3)')iz
             call OUT(qc3d(1,1,iz,kg),idum,1,5,ldate,messag,&
                      nxma(kg),nyma(kg))
           enddo
         enddo
      endif
      return
! --- Write error messages -- incorrect record label read
999   continue
      write(io6,1001)clabel,clabexp
1001  format(/1x,'ERROR in subr. RDAUX -- incorrect record label ',&
       'read from MET data file'//1x,'Label read     = ',a12/&
                                     1x,'Label expected = ',a12)
      write(*,*)
      stop 'Halted in RDAUX -- see list file.'
3999  continue
      write(io6,*)'ERROR in subr. RDAUX -- date/time variables ',&
       'do not match -- '
      write(io6,*)'   NDATHRE ,NSECE = ',ndathre,nsece
      write(io6,*)'   KDATHRE ,KSECE = ',kdathre,ksece
      stop 'Halted in RDAUX -- see list file.'
      end
!----------------------------------------------------------------------
      subroutine wake_csig(ity,itz,irur,utrans,kstab,elmo,bvfreq,&
                           sv,sw,symin,szmin,z,zi,ix,iy,md,amode)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230        WAKE_CSIG
!
! --- PURPOSE:  Save all arguments needed in SETCSIG call for release
!               height dispersion or for building-top dispersion
!
! --- UPDATES:
! --- V6.42_x1.1 - TNG-7.1.0  141230  (DGS)
!                                : Allow roughness adjustment (MROUGH)
!                                  of RURAL PG sigmas to be used with
!                                  CALMET gridded fields
!
! --- INPUTS:
!          ITY - integer - Sigma-y method
!          ITZ - integer - Sigma-z method
!         IRUR - integer - Rural flag (rural=0 ; urban=1)
!        UTRANS - real    - Transport wind speed (m/s)
!         KSTAB - integer - PGT stability class
!          ELMO - real    - Monin-Obukhov length (m)
!        BVFREQ - real    - Brunt-Vaisala freq (1/s)
!            SV - real    - Sigma-v (m/s)
!            SW - real    - Sigma-w (m/s)
!         SYMIN - real    - Sigma-y floor (m)
!         SZMIN - real    - Sigma-z floor (m)
!             Z - real    - Height (m AGL)
!            ZI - real    - Mixing height (m)
!      IX,IY,MD - integer - grid cell location
!         AMODE - char*8  - 'RELEASE ' for release-height data
!                           'BUILDING' for building-top data
!
! --- OUTPUT:
!      common/WAKECSIG/idopty_bt,idoptz_bt,iru_bt,uavg_bt,kst_bt,
!     &                el_bt,bvf_bt,tsigv_bt,tsigw_bt,symin_bt,
!     &                szmin_bt,zht_bt,zmix_bt,
!     &                idopty_rh,idoptz_rh,iru_rh,uavg_rh,kst_rh,
!     &                el_rh,bvf_rh,tsigv_rh,tsigw_rh,symin_rh,
!     &                szmin_rh,zht_rh,zmix_rh,
!     &                ix_btrh,iy_btrh,md_btrh
!
! --- WAKE_CSIG called by:  POINTS1, POINTS2
! --- WAKE_CSIG calls:      (none)
!----------------------------------------------------------------------
!
! --- Include common blocks
      include 'params.puf'
      include 'wakedat.puf'
      character*8 amode
! --- Assign grid location (same for both modes)
      ix_btrh=ix
      iy_btrh=iy
      md_btrh=md
      if(amode.EQ.'RELEASE ') then
! ---    Assign input variables to release-ht names in /WAKECSIG/
         idopty_rh=ity
         idoptz_rh=itz
         iru_rh   =irur
         uavg_rh  =utrans
         kst_rh   =kstab
         el_rh    =elmo
         bvf_rh   =bvfreq
         tsigv_rh =sv
         tsigw_rh =sw
         symin_rh =symin
         szmin_rh =szmin
         zht_rh   =z
         zmix_rh  =zi
      elseif(amode.EQ.'BUILDING') then
! ---    Assign input variables to building-top names in /WAKECSIG/
         idopty_bt=ity
         idoptz_bt=itz
         iru_bt   =irur
         uavg_bt  =utrans
         kst_bt   =kstab
         el_bt    =elmo
         bvf_bt   =bvfreq
         tsigv_bt =sv
         tsigw_bt =sw
         symin_bt =symin
         szmin_bt =szmin
         zht_bt   =z
         zmix_bt  =zi
      else
         write(*,*)'ERROR in WAKE_CSIG:  invalid mode'
         write(*,*)'   Expected RELEASE or BUILDING'
         write(*,*)'   Found ',amode
         stop
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine flares(mflare,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                        dtsec,smxi1,ldb,mfact,metfm,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230            FLARES
!                D. Strimaitis
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               FLARE SOURCES prescribed in the control-file, or
!               FLARE SOURCES prescribed in FLEMARB.DAT
!               (Control-file implementation is not active)
!
! --- UPDATES:
!
! --- TNG-7.0.0 - TNG-7.1.0  141230  (DGS)
!                                : Allow roughness adjustment (MROUGH)
!                                  of RURAL PG sigmas to be used with
!                                  CALMET gridded fields
!                                : Revise emission scale-factor storage
!
! --- INPUTS:
!        MFLARE - integer - Flag for origin of flare source data
!                            1 = Control file (/fl1/) -- NA
!                            2 = FLEMARB.DAT file (/fl2/)
!            IV - integer - FLEMARB.DAT file index
!             I - integer - Flare source index
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         DTSEC - real    - Length (seconds) of a basic time step
! SMXI1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!            NP - integer - Index of last puff/slug initialized
!         METFM - integer - Meteorological data format
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     Common block /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, PTG0(2), WSCALM, PLX0
!     Common block /DISPDAT/ variables:
!           JSUP
!     Common block /FLAGS/ variables:
!           MGAUSS, MSLUG, MSHEAR, MPARTL, MHFTSZ,
!           MTIP_FL, MRISE_FL
!     Common block /GRID/ variables:
!           IBCOMP, IECOMP, JBCOMP, JECOMP
!     Common block /METHD/ variables:
!           LCALGRD, I2DMET
!     Common block /METHR/ variables:
!           TEMPSS(mxss,,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP, PTG(2), DPTINVO
!     Common block /PUFF/ variables:
!           NPUFFS
!     |MQSCALE| variables:
!           NQSFTAB,IQSFTYPE(nqsftab),QSFTAB(mxqsf,nqsftab)
!     Common block /FL1/ variables:
!           NSFL1,
!           XFL1GRD(mxfl1),YFL1GRD(mxfl1),
!           HTFL1(mxfl1),ELFL1(mxfl1),
!           DFL1(mxfl1),TFL1(mxfl1),
!           UFL1(mxfl1),VFL1(mxfl1),WFL1(mxfl1),
!           SY0FL1(mxfl1),SZ0FL1(mxfl1),
!           QFL1(mxspec,mxfl1),IDSFFL1(mxspec,mxfl1),
!           IXREFFL1(mxspfl),
!           QFL1(mxspec,mxfl1),NEWFL1(mxfl1)
!     Common block /FL2/ variables:
!           NSTEP6(mxemdat),NDHRQB6(mxqstep,mxemdat),
!           NSECQB6(mxqstep,mxemdat),NDHRQE6(mxqstep,mxemdat),
!           NSECQE6(mxqstep,mxemdat),
!           XFL2GRD(mxqstep,mxfl2),YFL2GRD(mxqstep,mxfl2),
!           HTFL2(mxqstep,mxfl2),ELFL2(mxqstep,mxfl2),
!           DFL2(mxqstep,mxfl2),TFL2(mxqstep,mxfl2),
!           UFL2(mxqstep,mxfl2),VFL2(mxqstep,mxfl2),
!           WFL2(mxqstep,mxfl2),
!           SY0FL2(mxqstep,mxfl2),SZ0FL2(mxqstep,mxfl2),
!           QFL2(mxspec,mxqstep,mxfl2),NEWFL2(mxfl2)
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXSPEC, MXFL2, MXQSTEP, MXEMDAT,
!           MXMETDOM, IO6
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     Common block /FL1/ variables:
!           NEWFL1(mxfl1)
!     Common block /FL2/ variables:
!           NEWFL2(mxfl2)
!     Common block /PUFF/ variables:
!           all variables in common block
!     Common block /SLUG/ variables:
!           all variables in common block
!
! --- FLARES called by:  INITPUF
! --- FLARES calls:      RLSMET, ROLLDN, PTLAPS, SETCSIG, SIGTY,
!                        SIGTZ, STKTIP, PRSS, PRFIN, PRM,
!                        PRFINSH, PRFPP, NUMMET, NUMRISE, NUMCLIP,
!                        NRISEHD, SWAP, INJECT, DELTSEC,
!                        ZEROTAB, SRCTABOUT, GETCELL, QFACTOR
!----------------------------------------------------------------------
! --- Modules
      use mqscale
      include 'params.puf'
      include 'comparm.puf'
      include 'csigma.puf'
      include 'dispdat.puf'
      include 'flags.puf'
      include 'fl2.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
      include 'slug.puf'
      include 'srctab.puf'
!
      real smxi1(mxmetdom)
      real q(mxspec)
      logical ldb,problem
      logical LBRIGGS
      character*16 fltype
!
      data rt2/1.4142136/, rt2pi/2.5066283/, piby4/0.7853982/
! --- Set minimum allowed potential temperature gradient PTGRAD0,
      data ptgrad0/.005/
! --- Set minimum allowed wind speed for building downwash
      data wsdw0/1.0/
      data zero/0.0/
! --- Initialize number of new puffs from this source, this timestep
      npnew=0
! --- Set source emission period range, and screen MFLARE
! --- Set source type (10 or 11 for FLARES) using MFLARE
      if(mflare.EQ.1) then
         nt=1
         itype=10
         write(io6,*)
         write(io6,*)'FLARES:  Unavailable flare source type: ',mflare
         write(io6,*)'         Control file selection not implemented'
         write(*,*)
         stop 'Halted in FLARES -- see list file.'
      elseif(mflare.EQ.2) then
         nt=nstep6(iv)
         itype=11
      else
         write(io6,*)
         write(io6,*)'FLARES:  Invalid flare source type: ',mflare
         write(io6,*)'          Valid types are 1 (control file)'
         write(io6,*)'                          2 (FLEMARB.DAT)'
         write(*,*)
         stop 'Halted in FLARES -- see list file.'
      endif
! --- Extract flux flag for source from time-invariant data
      iflux=NINT(tiem6(1,i))
! --- Loop over emission periods within this timestep
      do 100 it=1,nt
! ---    Initialize source tabulation specifications to zero
         call ZEROTAB
! ---    Set specific plume rise variables for Briggs and Numerical
! ---    rise to clarify treatment
! ---    Briggs
         xfinm_B=0.0
         xfinb_B=0.0
         zfinm_B=0.0
         zfinb_B=0.0
         xf_B=0.0
         rise_B=0.0
         fmix_B=1.0
         hmax_B=0.0
         bidsq_B=0.0
         heff_B=0.0
! ---    Numerical
         xfinm_N=0.0
         xfinb_N=0.0
         zfinm_N=0.0
         zfinb_N=0.0
         xf_N=0.0
         rise_N=0.0
         fmix_N=1.0
         hmax_N=0.0
         bidsq_N=0.0
         heff_N=0.0
! ---    Assign variables for current source
!         if(mflare.EQ.1) then
!            fltype=' Control File '
!            xflgrd=xfl1grd(i)
!            yflgrd=yfl1grd(i)
!            htstak=htfl1(i)
!            elstak=elfl1(i)
!            diam=dfl1(i)
!            tstak=tfl1(i)
!            exitw=wfl1(i)
!            exitu=ufl1(i)
!            exitv=vfl1(i)
!            szsrc=amax1(szmin,sz0fl1(i))
!            sysrc=amax1(symin,sy0fl1(i))
!            do is=1,nspec
!               q(is)=qfl1(is,i)
!            enddo
!         elseif(mflare.EQ.2) then
         if(mflare.EQ.2) then
            fltype=' FLEMARB.DAT '
            xflgrd=xfl2grd(it,i)
            yflgrd=yfl2grd(it,i)
            htstak=htfl2(it,i)
            elstak=elfl2(it,i)
            diam=dfl2(it,i)
            tstak=tfl2(it,i)
            exitw=wfl2(it,i)
            exitu=ufl2(it,i)
            exitv=vfl2(it,i)
            szsrc=amax1(szmin,sz0fl2(it,i))
            sysrc=amax1(symin,sy0fl2(it,i))
            do is=1,nspec
               q(is)=qfl2(is,it,i)
            enddo
         else
            write(*,*)'Subr. FLARES:  Invalid MFLARE = ',mflare
            stop
         endif
!
! ***
      if(ldb) then
         write(io6,*)
         write(io6,*) 'FLARES: Source Number ',i,&
                      '  Source Type ',fltype
         if(mflare.EQ.2) write(io6,*) '   Emissions period ',it
      endif
! ***
!
! ---    Determine MET grid 1 (outermost) point closest to the source
         ixs=1.0+xflgrd
         iys=1.0+yflgrd
!
! ---    Source off the computational grid -- write a FATAL message
         if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
            jecomp)then
            write(io6,20) fltype,i,ixs,iys
20          format(/1x,'FATAL -- a flare source of Type ',a16,&
            'is off the computational grid'/1x,'source no. (i) = ',&
            i6,2x,'nearest grid point (ixs,iys) = (',i5,',',i5,')')
            problem=.TRUE.
            goto 100
         endif
! ---    Now determine the met. grid point closest to the source
         call GETCELL('FLARES      ','GRIDIJ',1,xflgrd,yflgrd,&
                      zero,zero,ixs,iys,md)
!
! ---    Extract stack height wind speed & other met. variables
! ---    and set CALM configuration if triggered
         call RLSMET(ldb,ixs,iys,md,htstak,mfact,&
                     mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                     ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,&
                     idoptz)
!
! frr (09/01) new calmet format (2D temp)
         if(i2dmet.EQ.1) then
            temp=temp2d(ixs,iys,md)
         elseif(i2dmet.EQ.0) then
            temp=tempss(issta,md)
         else
            write(*,*)'Subr. FLARES:  Invalid I2DMET = ',i2dmet
            stop
         endif
! ---    Trap missing power-law exponent (substitute default)
         if(plexp.LT.-900.) then
! ---       Use default
            plx=plx0(istab)
         else
! ---       Use it
            plx=plexp
         endif
! --------------------------------------------------------------------
! ---    Do not generate puff for this step if all emissions are ZERO!
! --------------------------------------------------------------------
! ---    Adjust TYPE-1 emissions for source 'i' and obtain total
         qtot=0.0
         do is=1,nspec
!c ---       Apply emissions scale-factor (conditional)
!            if(mflare.EQ.1 .AND. nsfl1.GT.0) then
!c ---       Pointer to scale factor tables
!            k=idsffl1(is,i)
!            if(k.GT.0) then
!               kk=ixreffl1(k)
!               call QFACTOR(io6,iqsftype(kk),itype,ws,istab,temp,iqsf)
!               q(is)=q(is)*qsftab(iqsf,kk)
!            endif
            qtot=qtot+q(is)
         enddo
         if(qtot.EQ.0.0) then
! ***
            if(ldb) then
               write(io6,*)
               write(io6,*) 'No emissions this step from Source ',i
               write(io6,*)
            endif
! ***
            goto 100
         endif
! -----------------------------------------------------------------
! ---    Calculate fluxes
! ---    Buoyancy flux:  2.4516625 = g/4., where g = 9.80665 m/s**2
! ---         fluxb=(2.4516625*diam**2*exitw/tstak)*dtemp
! ---    Momentum flux
! ---         fluxm=0.25*(exitw*diam)**2*temp/tstak
! -----------------------------------------------------------------
         dsq=diam**2
         if(iflux.EQ.1) then
! ---       T, Velocity are input
! ---       Use vertical velocity for momentum flux, but total velocity
! ---       for buoyancy flux
            exits=SQRT(exitu**2+exitv**2+exitw**2)
            dtemp=tstak-temp
            dtemp=amax1(dtemp,0.0)
! ---       Buoyancy flux:
            fluxb=(2.4516625*dsq*exits/tstak)*dtemp
! ---       Vertical Momentum flux
            fluxm=0.25*(dsq*exitw**2)*temp/tstak
         elseif(iflux.EQ.2) then
! ---       Fbs, Fmx,Fmy,Fmz are input in slots for T, Velocity u,v,w
            fbs=tstak
            fmx=exitu
            fmy=exitv
            fmz=exitw
            fluxm=fmz
            fluxb=fbs
! ---       Compute corresponding T, Velocity
            fms=fmx+fmy+fmz
            exits=(2.0*fbs/(9.80665*dsq))*&
                  (1.+SQRT(1.+fms*(9.80665*diam/fbs)**2))
            tstak=temp/(1.-(4.*fbs/(9.80665*dsq*exits)))
            fac=(2./diam)*SQRT(tstak/temp)
            exitu=fac*SQRT(fmx)
            exitv=fac*SQRT(fmy)
            exitw=fac*SQRT(fmz)
            dtemp=tstak-temp
            dtemp=amax1(dtemp,0.0)
         else
            write(io6,*)
            write(io6,*)'FLARES:  Invalid flare source flux: ',iflux
            write(io6,*)'          Valid types are 1 (T,u,v,w)'
            write(io6,*)'                          2 (Fb,Fmx,Fmy,Fmz)'
            write(*,*)
            stop 'Halted in FLARES -- see list file.'
         endif      
! -------------------------------------------------------------
! --- Compute the final plume rise and distance to final rise
! -------------------------------------------------------------
!
! ---    Adjustment for stack-tip downwash (NOT for bldg downwash!)
         if(mtip_fl.EQ.1) then
            call STKTIP(htstak,diam,fluxm,exitw,ws,tipdw)
         else
            tipdw=0.0
         endif
! --- Briggs Rise (always computed)
! ---------------------------------
! ---    Final plume rise (no downwash considered)
         call prfin(exitw,diam,fluxm,fluxb,ws,istab,sqrts,&
                    xfinm_B,xfinb_B,xf_B,zfinm_B,zfinb_B,rise_B)
!
! ---    Compute vertical wind shear effects
         if(mshear.EQ.1 .AND. xfinb_B.GT.0.0)then
            call prfinsh(fluxb,ws,plx,htstak,xfinb_B,istab,sqrts,&
             zfinsh)
! ---        Final buoyant rise is the LOWER of the Briggs & shear hts.
             zfinb_B=amin1(zfinb_B,zfinsh)
! ---        Final plume rise is the HIGHER of momentum & buoyant hts.
             rise_B=amax1(zfinb_B,zfinm_B)
         endif
!
! ---    Adjust for partial penetration of elevated stable layer
!        if(mpartl.EQ.1 .and. istab.LE.3 .and. htstak.LT.dpbl) then
         if(mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0) then
! ---       Set thickness (m) of transition region above mixing height
            dzinv=30.
! ---       Estimate potential temperature gradient above mixed layer
            if(LCALGRD) then
! ---          Use temperatures provided by met model
               htinv=amax1(dpbl,htstak)
               call ptlaps(ixs,iys,md,htinv,ptgrad0,dzinv,ptginv,tmix)
            else
! ---          Use default potential temperature lapse rate
               if(jsup.LE.4) then
                  ptginv=ptgrad0
               elseif(jsup.EQ.5) then
                  ptginv=amax1(ptg0(1),ptgrad0)
               else
                  ptginv=amax1(ptg0(2),ptgrad0)
               endif
            endif
! ---       Estimate potential temperature change across inversion
            if(dptinvo.GT.0.0) then
! ---          Use observed value provided in met file
               dptinv=dptinvo
            else
               dptinv=ptginv*dzinv
            endif
            call prfpp(rise_B,fluxb,ws,htstak,dpbl,temp,ptginv,&
                       dptinv,fmix_B,hmax_B)
! ---       Reset component final rise heights for use in gradual rise
            if(zfinb_B.GT.rise_B) zfinb_B=rise_B
            if(zfinm_B.GT.rise_B) zfinm_B=rise_B
!
         else
! ---       Set partial penetration variables to null values
            fmix_B=1.0
            hmax_B=dpbl
         endif
!
! ---    Set variance associated with buoyancy-enhanced growth at
! ---    final rise
         bidsq_B=(rise_B/3.5)**2
!
! ---    Set final plume height
         heff_B=htstak-tipdw+rise_B
!
! ---    Set selected data in /CSIGMA/ for sigma calls
         call SETCSIG(idopty,idoptz,iru,ws,istab,el,sqrts,&
                      tsigv,tsigw,symin,szmin,heff_B,dpbl,ixs,iys,md)
! ---    Assign Briggs adjustments to numerical rise variables as
! ---    defaults
         xfinm_N=xfinm_B
         xfinb_N=xfinb_B
         zfinm_N=zfinm_B
         zfinb_N=zfinb_B
         xf_N=xf_B
         rise_N=rise_B
         fmix_N=fmix_B
         hmax_N=hmax_B
         bidsq_N=bidsq_B
         heff_N=heff_B
! ---    Numerical Rise (conditional)
! -------------------------------------
         if(mrise_fl.EQ.2) then
! ---       Construct met. profiles at this grid cell
            call NUMMET(ixs,iys,md,ldb)
! ---       Set stack radius
            reff=0.5*diam
! ---       Set effective release height
            hteff=AMAX1(0.0,htstak-tipdw)
! ---       Prepare data header record for RISE.DAT file
! ---       (control for output to scratch file is within subroutine)
            call NRISEHD(itype,i,ndhrb,nsecb,ndhre,nsece,&
                         ndhrqb6(it,iv),nsecqb6(it,iv),ndhrqe6(it,iv),&
                         nsecqe6(it,iv),elstak,hteff,tstak,reff,exitw,&
                         xflgrd,yflgrd)
! ---       Compute plume rise
            mprime=0
! ---       Use full MXRISE points in rise table
            ntr=mxrise
            call NUMRISE(mprime,ldb,hteff,tstak,reff,exitw,&
                         sy0fl2(it,i),sz0fl2(it,i),&
                         ntr,xtr,ztr,rtr,htr)
            rise_N=htr(ntr)
! ---       Adjust for partial penetration of elevated stable layer
            if(mpartl.EQ.1 .and. istab.LE.4 .and. fluxb.GT.0.0) then
! ---          (ptginv, dptinv computed in Briggs Rise section)
               call PRFPP(rise_N,fluxb,ws,htstak,dpbl,temp,ptginv,&
                          dptinv,fmix_N,hmax_N)
! ---          Clip rise table at new final height
               call NUMCLIP(mxrise,hteff,rise_N,ntr,xtr,ztr,rtr,htr)
            else
! ---          Set partial penetration variables to null values
               fmix_N=1.0
               hmax_N=dpbl
            endif
! ---       Assign final plume height and distance
            heff_N=ztr(ntr)
            xf_N=xtr(ntr)
! ---       Variance associated with buoyancy-enhanced growth
! ---       @ final rise
            bidsq_N=(rise_N/3.5)**2
         endif
! ---    Select appropriate rise results (simple without downwash)
! --------------------------------------
         if(mrise_fl.EQ.1) then
! ---       Briggs rise is selected
            LBRIGGS=.true.
         elseif(mrise_fl.EQ.2) then
! ---       Numerical rise is selected
            LBRIGGS=.false.
         else
            write(io6,*) ' ERROR in subr. FLARES'
            write(io6,*)'Invalid source treatment found -- ',&
            ' MRISE_FL(1 or 2) = ',mrise_fl
            write(*,*)
            stop 'Halted in FLARES -- see list file.'
         endif
         if(LBRIGGS) then
            xfinm=xfinm_B
            xfinb=xfinb_B
            zfinm=zfinm_B
            zfinb=zfinb_B
            xf=xf_B
            rise=rise_B
            fmix=fmix_B
            hmax=hmax_B
            bidsq=bidsq_B
            heff=heff_B
         else
            xfinm=xfinm_N
            xfinb=xfinb_N
            zfinm=zfinm_N
            zfinb=zfinb_N
            xf=xf_N
            rise=rise_N
            fmix=fmix_N
            hmax=hmax_N
            bidsq=bidsq_N
            heff=heff_N
         endif
! ---------------------------------------------------------
! ---    Compute number of puffs for this emissions period
! ---------------------------------------------------------
! ---    Use CALMET/PROFILE winds at final rise to set release/sampling
! ---    data and to check MFACT0 determination (slug changed to puff
! ---    only if CALM is detected at both heights)
         if(metfm.EQ.1 .OR. metfm.EQ.4 .OR. metfm.EQ.5) then
            call RLSMET(ldb,ixs,iys,md,heff,mfact,&
                     mfact0fr,ilayerfr,wsfr,flowfr,ivecfr,istab,dpbl,&
                     el,ustr,wstr,tsigvfr,tsigwfr,iru,issta,sqrtsfr,&
                     idoptyfr,idoptzfr)
            mfact0=MAX0(mfact0,mfact0fr)
            wsnew=AMAX1(ws,wsfr)
         else
            wsnew=ws
         endif
! ---    Establish emitting time
!         if(mflare.EQ.1) then
!            ndhr1=ndhrb
!            nsec1=nsecb
!            ndhr2=ndhre
!            nsec2=nsece
!         elseif(mflare.EQ.2) then
         if(mflare.EQ.2) then
! ---       Start-time for emission period during timestep
            if(ndhrqb6(it,iv).LT.ndhrb) then
               ndhr1=ndhrb
               nsec1=nsecb
            elseif(ndhrqb6(it,iv).EQ.ndhrb .AND.&
                   nsecqb6(it,iv).LT.nsecb) then
               ndhr1=ndhrb
               nsec1=nsecb
            else
               ndhr1=ndhrqb6(it,iv)
               nsec1=nsecqb6(it,iv)
            endif
! ---       End-time for emission period during timestep
            if(ndhrqe6(it,iv).GT.ndhre) then
               ndhr2=ndhre
               nsec2=nsece
            elseif(ndhrqe6(it,iv).EQ.ndhre .AND.&
                   nsecqe6(it,iv).GT.nsece) then
               ndhr2=ndhre
               nsec2=nsece
            else
               ndhr2=ndhrqe6(it,iv)
               nsec2=nsecqe6(it,iv)
            endif
         else
            write(*,*)'Subr. FLARES:  Invalid MFLARE = ',mflare
            stop
         endif
! ---    Duration of emission period within timestep (seconds)
         call DELTSEC(ndhr1,nsec1,ndhr2,nsec2,ntemit)
         temit=FLOAT(ntemit)
! ---    Apply transport constraints
         npnewt=wsnew*smxi1(md)*temit/dtsec+1
         npnewt=MIN(npnewt,mxnew)
         temit=temit/FLOAT(npnewt)
! ---    Update puff numbers
         npnew=npnew+npnewt
         newpuf=newpuf+npnewt
!         if(mflare.EQ.1) then
!            newfl1(i)=npnew
!         elseif(mflare.EQ.2) then
         if(mflare.EQ.2) then
            newfl2(i)=npnew
         else
            write(*,*)'Subr. FLARES:  Invalid MFLARE = ',mflare
            stop
         endif
! ---    If puff arrays are full, remove puffs that are off the grid
! ---    and "roll down" arrays to make room for new puffs
! ---    (this also applies to the DA file of tabulated arrays)
         if(npuffs+newpuf.gt.mxpuff) call ROLLDN(np)
! ---    Check to see if roll-down was adequate
         if(npuffs+newpuf.gt.mxpuff)then
            write(io6,*) ' ERROR in subr. FLARES'
            write(io6,*)'Too many puffs on grid for array dimensions',&
            ' -- NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
            ' MXPUFF = ',mxpuff
            write(*,*)
            stop 'Halted in FLARES -- see list file.'
         endif
! ---    Set downwash flag to zero (no building downwash)
         idw=0
!******
      if(ldb)then
         write(io6,*)
         write(io6,*)'Variable emissions step: ',it
         write(io6,204)i,ilayer,ixs,iys,md,ws,istab,dpbl,&
          issta,npnewt,newpuf,temit
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'WS: ',f5.1,2x,'ISTAB: ',i1,&
          2x,'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,'NPNEWT: ',i4,2x,&
          'NEWPUF: ',i5/5x,'DT: ',f7.2)
!
         write(io6,206)htstak,diam,exitw,tstak,&
          temp,dtemp,fluxb,fluxm,xf,heff,tipdw
206      format(5x,'HTSTAK: ',f6.1,2x,'DIAM: ',f6.3,2x,'EXITW: ',&
          f6.3,2x,'TSTAK: ',f8.2,2x,'TEMPSS: ',f6.2,2x,'DTEMP: ',f8.3,&
          2x,'FLUXB : ',f8.3,2x,'FLUXM: ',f6.1/5x,'XF: ',f6.1,2x,&
          'HEFF: ',f6.1,2x,'TIPDW: ',f6.1)
!
         write(io6,210)idw,rise
210      format(5x,'IDW: ',i5,2x,'RISE : ',f8.2)
!
         write(io6,208)i,np,iru,sysrc,szsrc,temit,istab
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,&
          'IRU: ',i2,2x,'SIGYB: ',f8.2,2x,'SIGZB: ',f8.2,2x/&
          5x,'DT: ',f7.2,2x,'ISTAB: ',i1)
!
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnewt
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call SWAP(np,0)
!
! ---    Fill non-zero values
         xpb(np)=xflgrd
         ypb(np)=yflgrd
         zitibl(np)=-1.0
         elbase(np)=elstak
         sigyb(np)=sysrc
         sigzb(np)=szsrc
! ---    Final plume rise results
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xf
         bidfnl(np)=bidsq
! ---    Intermediate results for calc. gradual rise
         fb(np)=fluxb
         fm(np)=fluxm
         xbfin(np)=xfinb
         xmfin(np)=xfinm
         zbfin(np)=zfinb
         zmfin(np)=zfinm
         stipdw(np)=tipdw
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=htstak
         exitw0(np)=exitw
         diam0(np)=diam
         temit0(np)=temit
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations (downwash)
         idw0(np)=idw
         heff20(np)=heff
         sy0(np)=sysrc
         sz0(np)=szsrc
         if(mflare.EQ.2) then
! ---       Place end-time of emission period for this puff into
! ---       TMTOTB(mxpuff) array, expressed as fraction of the timestep
! ---       This will be used in COMP when computing the sampling steps
! ---       used to advect the freshly emitted puff during remainder
! ---       of the timestep, and then reset.
! ---       Seconds from start of emissions period to end of timestep
            call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
            tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
         endif
! ---    Emission period index for this puff
         iemstep(np)=it
! ---    Set puff identity where IRLSNUM is the puff number released
! ---    from a source this time step, ISRCNUM is the source number
! ---    of type ISRCTYP from which the puff was released.
! ---    (Type =10 for control file flare sources)
! ---    (Type =11 for FLEMARB.DAT flare sources)
         jj=npnew-npnewt+j
         irlsnum(np)=jj
         isrcnum(np)=i
         isrctyp(np)=itype
!
! ----------------------------------------------------------
! ---    Do same for additional SLUG parameters
! ----------------------------------------------------------
! ---    Note that "oldest" end of slug is at the source, as is the
! ---    "newest end; oldest end moves away and grows during the step,
! ---    while newest end remains fixed in size at the source.
!
         if(mslug.EQ.1 .AND. mfact0.GT.0) then
! ---       Set coordinates of oldest end of slug (in met. grid units)
            xpe(np)=xpb(np)
            ype(np)=ypb(np)
            xtote(np)=xtotb(np)
            tmtote(np)=tmtotb(np)
! ---       Set sigmas for oldest end of slug
            sigye(np)=sigyb(np)
            sigze(np)=sigzb(np)
         endif
!
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
         call inject(np,nspec,temit,mfact0,q,heff,hmax,dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---       Transfer tabulated arrays record to DA file using full
! ---       MXRISE dimension
! ------------------------------------------------------------------
            call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
! --- End loop over emission periods within this timestep
100   continue
!
      return
      end
!----------------------------------------------------------------------
      subroutine RDHDEM6(iunit0,i,lprt,lnfl2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913           RDHDEM6
!                D. Strimaitis
!
! --- PURPOSE:  Read the header records from the FLEMARB flare source
!               variable emissions file
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 FLEMARB file
!                I - integer    - Index for current FLEMARB file
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!            LNFL2 - logical    - Flag indicating all sources found
!
!     Common Block /FL2/ variables:
!            NFL2
!     Common Block /DATASET/ variables:
!            VERFLARB(mxemdat)
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!     Parameters:
!            IO6, MXSPEC, MXEMDAT, MXQSTEP, MXFL2,  IOX
!
!
! --- OUTPUT:
!
!     Common Block /FL2/ variables:
!           NSE6,
!           IBSRC6(mxemdat),IESRC6(mxemdat),
!           IBDATHR6(mxemdat),IBSEC6(mxemdat),IEDATHR6(mxemdat),
!           IESEC6(mxemdat),XTZ6(mxemdat),T2BTZ6(mxemdat),
!           XMWEM6(mxspec),IXREM6(mxspec),
!           lutmfl2(mxemdat),llccfl2(mxemdat),lpsfl2(mxemdat),
!           lemfl2(mxemdat),llazafl2(mxemdat),lttmfl2(mxemdat),
!           iutmznfl2(mxemdat),feastfl2(mxemdat),fnorthfl2(mxemdat),
!           rnlat0fl2(mxemdat),relon0fl2(mxemdat),
!           rnlat1fl2(mxemdat),rnlat2fl2(mxemdat),NSTEP6(mxemdat),
!           NDHRQB6(mxqstep,mxemdat),NSECQB6(mxqstep,mxemdat),
!           NDHRQE6(mxqstep,mxemdat),NSECQE6(mxqstep,mxemdat),
!           CSLST6(mxspec),CID6(mxfl2),
!           pmapfl2(mxemdat),utmhemfl2(mxemdat),datumfl2(mxemdat),
!           datenfl2(mxemdat),xyunitfl2(mxemdat)
!
! --- RDHDEM6 called by: SETUP
! --- RDHDEM6 calls:     YR4, XTRACTLL, INCR, TCLOCK, GEOFMT
!----------------------------------------------------------------------
!
      include 'params.puf'
      include 'fl2.puf'
      include 'dataset.puf'
      include 'datehr.puf'
      include 'qa.puf'
!
      LOGICAL lprt, lnfl2, lmatch
! --- Local variables
      real xmwem6x(mxspec)
      character*8 axtz6
      character*12 fname6,label6(mxemdat)
      character*12 cslst6x(mxspec),vrs6
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Set unit number
      iunit=iunit0+i-1
! --- Initialize map projection logicals
      lutmfl2(i)=.FALSE.
      llccfl2(i)=.FALSE.
      lpsfl2(i)=.FALSE.
      lemfl2(i)=.FALSE.
      llazafl2(i)=.FALSE.
      lttmfl2(i)=.FALSE.
! --- Initialize other variables
      datenfl2(i)='02-21-2003  '
      xyunitfl2(i)='KM  '
      ibsec6(i)=0.0
      iesec6(i)=0.0
      feastfl2(i)=0.0
      fnorthfl2(i)=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmapfl2(i)='XXX     '
      iutmznfl2(i)=1
      utmhemfl2(i)='N   '
      datumfl2(i)='XXX     '
      axtz6='UTC+0000'
!     -------------------------------
      if(verflarb(i).EQ.'1.0') then
!     -------------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmapfl2(i)
         write(iox,'(a8)') pmapfl2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapfl2(i),iutmznfl2(i),&
                     utmhemfl2(i),clat0,clon0,clat1,clat2,&
                     datumfl2(i),datenfl2(i),xyunitfl2(i),axtz6)
         if(pmapfl2(i).EQ.'UTM     ')  lutmfl2(i) =.TRUE.
         if(pmapfl2(i).EQ.'LCC     ')  llccfl2(i) =.TRUE.
         if(pmapfl2(i).EQ.'PS      ')  lpsfl2(i)  =.TRUE.
         if(pmapfl2(i).EQ.'EM      ')  lemfl2(i)  =.TRUE.
         if(pmapfl2(i).EQ.'LAZA    ')  llazafl2(i)=.TRUE.
         if(pmapfl2(i).EQ.'TTM     ')  lttmfl2(i) =.TRUE.
! ---    Map projection parameters
         if(LUTMFL2(i)) then
            read(iunit,'(i4,a4)') iutmznfl2(i),utmhemfl2(i)
            write(iox,'(i4,a4)') iutmznfl2(i),utmhemfl2(i)
            ncommout=ncommout+1
         elseif(LLCCFL2(i)) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPSFL2(i)) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEMFL2(i).or.LLAZAFL2(i).or.LTTMFL2(i)) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCFL2(i).or.LLAZAFL2(i).or.LTTMFL2(i)) then
            read(iunit,*) feastfl2(i),fnorthfl2(i)
            write(iox,*)  feastfl2(i),fnorthfl2(i)
            ncommout=ncommout+1
         else
            feastfl2(i)=0.0
            fnorthfl2(i)=0.0
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datumfl2(i),datenfl2(i)
         write(iox,'(a8,a12)')  datumfl2(i),datenfl2(i)
         ncommout=ncommout+1
! ---    Map units
         read(iunit,'(a4)') xyunitfl2(i)
         write(iox,'(a4)')  xyunitfl2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz6
         write(iox,'(a8)')  axtz6
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapfl2(i),iutmznfl2(i),&
                     utmhemfl2(i),clat0,clon0,clat1,clat2,&
                     datumfl2(i),datenfl2(i),xyunitfl2(i),axtz6)
! ---    Process Time zone
         read(axtz6(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM6 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz6
            stop 'Halted in RDHDEM6 -- see list file'
         endif
         xtz6(i)=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec6(i),&
                      ieyr,iedy,iehr,iesec6(i)
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDEM6'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec6(i),&
                           ieyr,iedy,iehr,iesec6(i)
         ncommout=ncommout+1
! ---    Number of sources and species
         read(iunit,*) nfl2file,nse6x
         write(iox,*) nfl2file,nse6x
         ncommout=ncommout+1
! ---    Species list
         read(iunit,*)(cslst6x(n),n=1,nse6x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem6x(n),n=1,nse6x)
!     -----
      else
!     -----
         write(*,*)'ERROR in FLEMARB File: ',i
         write(*,*)'Bad Dataset Version: ',verflarb(i)
         write(*,*)'Expected 1.0'
         stop
!     -----
      endif
!     -----
! --- Species info to scratch file
      do n=1,nse6x
         write(iox,'(a16,f12.4)')cslst6x(n),xmwem6x(n)
      enddo
      ncommout=ncommout+nse6x
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec6(i))
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec6(i))
! --- Apply time zone adjustment to date/times
      if(xtz6(i).GE.-13 .AND. xtz6(i).LE.13) then
         t2btz6(i)=xtz6(i)-xbtz
         ishift=NINT(t2btz6(i))
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in FLEMARB.DAT file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz6(i)
         stop 'Bad Time Zone in FLEMARB.DAT file -- see list file'
      endif
! --- Form date-hr variables for /FL2/
      ibdathr6(i)=ibyr*100000+ibdy*100+ibhr
      iedathr6(i)=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1fl2(i))
      call XTRACTLL(io6,'LAT ',clat2,rnlat2fl2(i))
      call XTRACTLL(io6,'LAT ',clat0,rnlat0fl2(i))
      call XTRACTLL(io6,'LON ',clon0,relon0fl2(i))
! --- Set source index range for this file
      if(i.EQ.1) then
         ibsrc6(i)=1
         iesrc6(i)=nfl2file
      else
         ibsrc6(i)=iesrc6(i-1)+1
         iesrc6(i)=iesrc6(i-1)+nfl2file
      endif
! --- Set logical to indicate if the number of sources specified in
! --- the control file (NFL2) have been found
      lnfl2=.FALSE.
      if(nfl2.EQ.iesrc6(i)) lnfl2=.TRUE.
! --- Process data that must be identical in all FLEMARB.DAT files
      if(i.EQ.1) then
! ---    Pass into /FL2/ names
         nse6=nse6x
!
         if(nse6.gt.mxspec)then
            write(io6,11)nse6,mxspec
11          format(/1x,'ERROR in SUBR. RDHDEM6 -- No. emitted species',&
            ' in FLEMARB file is greater than current array',&
            ' dimension'/1x,&
            '     No. emitted species in file: ',i5/1x,&
            'Current Array dimension (MXSPEC): ',i5)
            write(*,*)
            stop 'Halted in RDHDEM6 -- see list file.'
         endif
      else
! ---    Check for consistency with first file
         if(nse6.NE.nse6x) then
            write(io6,12)nse6,nse6x
12          format(/1x,'ERROR in SUBR. RDHDEM6 -- No. emitted species',&
            ' must match in all FLEMARB files',/1x,&
            '     No. emitted species expected/found: ',2i5)
            write(*,*)
            stop 'Halted in RDHDEM6 -- see list file.'
         endif
      endif
! --- Pass into /FL2/ names or check for consistency
      if(i.EQ.1) then
         do is=1,nse6
            cslst6(is)=cslst6x(is)
            xmwem6(is)=xmwem6x(is)
         enddo
      else
         lmatch=.TRUE.
         do is=1,nse6
            if(cslst6(is).NE.cslst6x(is)) lmatch=.FALSE.
            if(xmwem6(is).NE.xmwem6x(is)) lmatch=.FALSE.
         enddo
         if(.not.LMATCH) then
            write(io6,15)
15          format(/1x,'ERROR in SUBR. RDHDEM6 -- Species names and',&
            ' molecular weights must match in all FLEMARB files',/)
            write(io6,*)
            write(io6,*)'FLEMARB.DAT File:    1'
            do is=1,nse6
               write(io6,*) cslst6(is),xmwem6(is)
            enddo
            write(io6,*)
            write(io6,*)'FLEMARB.DAT File:',i
            do is=1,nse6
               write(io6,*) cslst6x(is),xmwem6x(is)
            enddo
            write(*,*)
            stop 'Halted in RDHDEM6 -- see list file.'
         endif
      endif
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,102) i
102      format(///1x,13('----------')//1x,'Header record data from ',&
         'the FLEMARB flare source file',i5)
         write(io6,103)dataset,ibsrc6(i),iesrc6(i),nse6x,axtz6,&
               ibdathr6(i),ibsec6(i),iedathr6(i),iesec6(i),pmapfl2(i),&
               datumfl2(i)
103      format(/1x,'DATASET: ',a16/1x,'IBSRC6:  ',i9/&
         1x,'IESRC6:  ',i9/1x,'NSE6:    ',i6/1x,'AXTZ6:   ',a8/&
         1x,'IBDATHR6:',i9/1x,'IBSEC6:  ',i4/&
         1x,'IEDATHR6:',i9/1x,'IESEC6:  ',i4/&
         1x,'MAP6:    ',a8/1x,'DATUM:   ',a8)
         if(LUTMFL2(i)) write(io6,104) iutmznfl2(i),utmhemfl2(i)
         if(LLCCFL2(i)) write(io6,105) clat0,clon0,clat1,clat2,&
                                       feastfl2(i),fnorthfl2(i)
         if(LPSFL2(i)) write(io6,106) clat0,clon0,clat1
         if(LEMFL2(i)) write(io6,107) clat0,clon0
         if(LLAZAFL2(i).or.LTTMFL2(i)) write(io6,108) clat0,clon0,&
                                       feastfl2(i),fnorthfl2(i)
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
         write(io6,109)
109      format(4x,'SPECIES',6x,'MOL. WT.'/)
         do is=1,nse6
            write(io6,110)cslst6(is),xmwem6(is)
         enddo
110      format(1x,a12,3x,f7.3)
      endif
! --- Initialize "previous" period times for /FL2/
      nstep6(i)=1
      ndhrqb6(1,i)=-000100100
      nsecqb6(1,i)=0
      ndhrqe6(1,i)=ibdathr6(i)
      nsecqe6(1,i)=ibsec6(i)
      return
      end
!----------------------------------------------------------------------
      subroutine RDTIEM6(iunit0,nfl2,nfldat,i,ibsrc6,iesrc6,avrs6,&
                         ldb,io6,iox,tiem6,cid6,ncommout)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913           RDTIEM6
!                D. Strimaitis
!
! --- PURPOSE:  Read the time-invariant records of a FLEMARB flare
!               source file (arbitrarily-varying properties)
!
!               Initial Implementation:  do not allow buoyancy and
!               momentum fluxes in place of exit temperature and
!               velocity.
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 FLEMARB file
!             NFL2 - integer    - Number of FLEMARB flare sources
!           NFLDAT - integer    - Number of FLEMARB files
!                I - integer    - Current FLEMARB file
!   IBSRC6(nfldat) - integer    - Source ID for first source in each
!                                 FLEMARB file
!   IESRC6(nfldat) - integer    - Source ID for last source in each
!                                 FLEMARB file
!    AVRS6(nfldat) - char*16    - FLEMARB data-set version
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!              IOX - integer    - Fortran unit number for scratch file
!         NCOMMOUT - integer    - Currrent number of header comments
!                                 in scratch file
!
! --- OUTPUT:
!    TIEM6(8,nfl2) - real array - Time-invariant FLEMARB source data
!       CID6(nfl2) - char*16    - FLEMARB source identifiers
!         NCOMMOUT - integer    - Updated number of header comments
!                                 in scratch file
!
! --- RDTIEM6 called by: SETUP
! --- RDTIEM6 calls:     none
!----------------------------------------------------------------------
!
      real tiem6(8,nfl2)
      integer ibsrc6(nfldat),iesrc6(nfldat)
      character*16 cid6(nfl2),avrs6(nfl2)
      logical ldb
! --- Process file "i"
         iunit=iunit0+i-1
! ---    Loop over sources
         do k=ibsrc6(i),iesrc6(i)
            ii=MIN(k,nfl2)
            if(avrs6(i).EQ.'1.0') then
               read(iunit,*)cid6(ii),(tiem6(n,ii),n=1,8)
               write(iox,'(a16,f10.0)') cid6(ii),(tiem6(n,ii),n=1,8)
               ncommout=ncommout+1
            else
               write(io6,*)'RDTIEM6:  Bad FLEMARB.DAT dataset version'
               write(io6,*)'          Expected 1.0'
               write(io6,*)'          Found    ',avrs6(i)
               stop 'Halted in RDTIEM6 -- see list file'
            endif
         enddo
! --- Perform final QA after LAST file is processed
      if(i.NE.nfldat) return
      if(ldb)then
         write(io6,*)
         write(io6,*)
         write(io6,*)'Time-invariant FLEMARB data'
         write(io6,*)'-----------------------------'
         do k=1,nfl2
            write(io6,*)'Source = ',k,'CID6  = ',cid6(k)
            write(io6,*)'Source = ',k,'TIEM6 = ',(tiem6(n,k),n=1,8)
         enddo
      endif
! --- Check number of sources
      if(iesrc6(nfldat).NE.nfl2) then
         write(io6,*)'FATAL Error in RDTIEM6:  Wrong number of sources'
         write(io6,*)'         Control file NFL2 = ',nfl2
         write(io6,*)'FLEMARB.DAT Time-Invariant = ',iesrc6(nfldat)
         write(*,*)
         stop 'Halted in RDTIEM6 -- see list file.'
      endif
! --- Do not allow exit flux option (not fully supported)
! --- Loop over sources
      nbad=0
      do k=1,nfl2
         if(tiem6(1,k).NE.1) nbad=nbad+1
      enddo
      if(nbad.GT.0) then
         write(io6,*)'FATAL Error in RDTIEM6:  Unsupported Option'
         write(io6,*)'  Ability to use buoyancy and momentum fluxes'
         write(io6,*)'  is not fully implemented'
         write(*,*)
         stop 'Halted in RDTIEM6 -- see list file.'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine RDEM6(iunit,nsrc6,nse6,cid6,ibsrc6,iesrc6,vers,tzhr,&
                       ldb,io6,ibegh,ibegs,iendh,iends,em6dat,ieof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913             RDEM6
!                D. Strimaitis
!
! --- PURPOSE:  Read the time-varying records of the FLEMARB flare
!               source emissions file (arbitrarily-varying emissions)
!
!               Initial Implementation:  do not allow non-vertical
!               exit velocity.
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of FLEMARB file
!            NSRC6 - integer    - Total number of sources in FLEMARB
!                                 files
!             NSE6 - integer    - Number of emitted species
!      CID6(nsrc6) - char*16    - Source identifiers
!           IBSRC6 - integer    - Index for 1st source in current file
!           IESRC6 - integer    - Index for Last source in current file
!             VERS - char*16    - Dataset version for file
!             TZHR - real       - Hours to ADD to times in file to
!                                 convert to BASE TIME ZONE
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!
! --- OUTPUT:
!      IBEGH,IBEGS - integer    - Starting time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
!      IENDH,IENDS - integer    - Ending time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
! EM6DAT(NSE6+11,nsrc6)- real array - Time-varying FLEMARB source data
!             IEOF - integer    - End-of-file flag
!
! --- RDEM6 called by: INITPUF
! --- RDEM6 calls:     YR4, INCR, TCLOCK
!----------------------------------------------------------------------
!
      real em6dat(nse6+11,nsrc6)
      character*16 cid, cid6(nsrc6), vers
      logical ldb, lproblem
! --- Small value to screen for non-zero horizontal exit velocity
      data small/0.0001/
! --- Initialize EOF flag
      ieof=0
! --- Read starting, ending date and time
      if(vers.EQ.'1.0') then
         read(iunit,*,end=999)ibyr,ibdy,ibhr,ibegs,&
                              ieyr,iedy,iehr,iends
      else
         write(io6,*)
         write(io6,*)'RDEM6: Invalid Dataset Version for FLEMARB.DAT'
         write(io6,*)'Expected 1.0'
         write(io6,*)'Found: ',vers
         write(*,*)
         stop 'Halted in RDEM6 -- see list file.'
      endif
! --- Enforce YYYY format for year
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDEM6'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibegs)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iends)
! --- Adjust times to BASE TIME ZONE
      ishift=NINT(tzhr)
      call INCR(io6,ibyr,ibdy,ibhr,ishift)
      call INCR(io6,ieyr,iedy,iehr,ishift)
! --- Form date-hour
      ibegh=ibyr*100000+ibdy*100+ibhr
      iendh=ieyr*100000+iedy*100+iehr
! --- Loop over sources
      lproblem = .FALSE.
      nwords=nse6+11
      do i=ibsrc6,iesrc6
         read(iunit,*)cid,(em6dat(n,i),n=1,nwords)
         if(cid.NE.cid6(i)) lproblem=.TRUE.
! ---    Screen out non-vertical exit velocity
         absu=ABS(em6dat(7,i))
         absv=ABS(em6dat(8,i))
         if(absu.GT.small .OR. absv.GT.small) then
            write(io6,*)
            write(io6,*)'RDEM6: Problem with exit vel. in FLEMARB.DAT'
            write(io6,*)'       Source ID = ',cid
            write(io6,*)'       Exit velocity must be vertical (U,V=0)'
            write(io6,*)'       Found U,V = ',em6dat(7,i),em6dat(8,i)
            write(*,*)
            stop 'Halted in RDEM6 -- see list file.'
         endif
      enddo
! --- Check for source problem
      if(lproblem) then
         write(io6,*)
         write(io6,*)'RDEM6: Problem with source names in FLEMARB.DAT'
         write(io6,*)'Names in data record do not match header record'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(*,*)
         stop 'Halted in RDEM6 -- see list file.'
      endif
!
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-varying FLEMARB data'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(io6,*)'Source Range  : ',ibsrc6,iesrc6
         do i=ibsrc6,iesrc6
            write(io6,*)'I = ',i,'EM6DAT = ',(em6dat(n,i),n=1,nwords)
         enddo
      endif
      return
! --- Reached end of FLEMARB.DAT file
999   ieof=1
      write(*,*)'RDEM6: Data in FLEMARB.DAT file end early'
      write(io6,*)'RDEM6: Data in FLEMARB.DAT file end early'
      return
      end
!----------------------------------------------------------------------
      subroutine SKIPHDEM6(nfldat)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913         SKIPHDEM6
!                D. Strimaitis
!
! --- PURPOSE:  Skip over header records from the FLEMARB flare
!               source file (arbitrarily-varying location & emissions)
!               to reach the start of the data records
!
! --- INPUTS:
!           NFLDAT - integer    - Number of FLEMARB files
!
! --- OUTPUT:
!
! --- SKIPHDEM6 called by: RDEMSRC
! --- SKIPHDEM6 calls:     none
!----------------------------------------------------------------------
!
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'fl2.puf'
      include 'dataset.puf'
! --- Local variables
      character*1 char1
      real xmwem(mxspec)
      character*12 cslst(mxspec)
2     format(a1)
! --- Loop over all FLEMARB.DAT files
      do i=1,nfldat
         io=iofl2+i-1
         if(verflarb(i).EQ.'1.0') then
            read(io,2) char1
            read(io,*) ncom
            do k=1,ncom
               read(io,2) char1
            enddo
            read(io,2) char1
            read(io,2) char1
            if(LLCCFL2(i).or.LLAZAFL2(i).or.&
                             LTTMFL2(i)) read(io,2) char1
            read(io,2) char1
            read(io,2) char1
            read(io,2) char1
            read(io,2) char1
            read(io,2) char1
         else
            write(*,*)'ERROR in FLEMARB File: ',i
            write(*,*)'Bad Dataset Version: ',verflarb(i)
            write(*,*)'Expected 1.0'
            stop
         endif
! ---    Species list
         read(io,*)(cslst(n),n=1,nse6)
! ---    Molecular weights of each emitted species
         read(io,*)(xmwem(n),n=1,nse6)
! ---    TIEM6 records
! ---    Loop over sources
         do k=ibsrc6(i),iesrc6(i)
            read(io,2) char1
         enddo
      enddo
! --- Use char1 (compilation warning)
      if(char1.EQ.'~') return
      return
      end
!----------------------------------------------------------------------
      subroutine RDHDEM7(iunit0,i,lprt,lnrd2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           RDHDEM7
!                D. Strimaitis
!
! --- PURPOSE:  Read the header records from the RDEMARB road source
!               variable emissions file
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 RDEMARB file
!                I - integer    - Index for current RDEMARB file
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!            LNRD2 - logical    - Flag indicating all sources found
!
!     |MROAD2| variables:
!            NRD2
!     Common Block /DATASET/ variables:
!            VERRDARB(mxemdat)
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!     Parameters:
!            IO6, MXSPEC, MXEMDAT, MXQSTEP, IOX
!
! --- OUTPUT:
!
!     |MROAD2| variables:
!           NSE7,NRD2,
!           IBSRC7(mxemdat),IESRC7(mxemdat),
!           IBDATHR7(mxemdat),IBSEC7(mxemdat),IEDATHR7(mxemdat),
!           IESEC7(mxemdat),XTZ7(mxemdat),T2BTZ7(mxemdat),
!           XMWEM7(mxspec),IXREM7(mxspec),
!           iutmznrd2(mxemdat),feastrd2(mxemdat),fnorthrd2(mxemdat),
!           rnlat0rd2(mxemdat),relon0rd2(mxemdat),
!           rnlat1rd2(mxemdat),rnlat2rd2(mxemdat),NSTEP7(mxemdat),
!           NDHRQB7(mxqstep,mxemdat),NSECQB7(mxqstep,mxemdat),
!           NDHRQE7(mxqstep,mxemdat),NSECQE7(mxqstep,mxemdat),
!           CSLST7(mxspec),
!           pmaprd2(mxemdat),utmhemrd2(mxemdat),datumrd2(mxemdat),
!           datenrd2(mxemdat),xyunitrd2(mxemdat)
!
! --- RDHDEM7 called by: SETUP
! --- RDHDEM7 calls:     YR4, XTRACTLL, INCR, TCLOCK, GEOFMT
!----------------------------------------------------------------------
! --- Modules
      use mroad2
!      implicit none
      include 'params.puf'
      include 'dataset.puf'
      include 'datehr.puf'
      include 'qa.puf'
! --- Arguments
      LOGICAL :: lprt, lnrd2
      integer :: iunit0,i
! --- Locals
      LOGICAL :: lmatch
      LOGICAL :: lutmrd2,llccrd2,lpsrd2,lemrd2,llazard2,lttmrd2
      integer :: nlim,iunit,ncom,ncommout
      integer :: k,n,is
      integer :: ibtzhh,nrd2file
      integer :: ibyr,ibdy,ibhr,ieyr,iedy,iehr,idum,ishift
      integer :: ierrb,ierre
      real    :: zero
      real xmwem7x(mxspec)
      character*8 axtz7
      character*12 fname7,label7(mxemdat)
      character*12 cslst7x(mxspec),vrs7
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Set unit number
      iunit=iunit0+i-1
! --- Initialize map projection logicals
      lutmrd2=.FALSE.
      llccrd2=.FALSE.
      lpsrd2=.FALSE.
      lemrd2=.FALSE.
      llazard2=.FALSE.
      lttmrd2=.FALSE.
! --- Initialize other variables
      datenrd2(i)='02-21-2003  '
      xyunitrd2(i)='KM  '
      ibsec7(i)=0.0
      iesec7(i)=0.0
      feastrd2(i)=0.0
      fnorthrd2(i)=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmaprd2(i)='XXX     '
      iutmznrd2(i)=1
      utmhemrd2(i)='N   '
      datumrd2(i)='XXX     '
      axtz7='UTC+0000'
!     -------------------------------
      if(verrdarb(i).EQ.'1.0') then
!     -------------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmaprd2(i)
         write(iox,'(a8)') pmaprd2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmaprd2(i),iutmznrd2(i),&
                     utmhemrd2(i),clat0,clon0,clat1,clat2,&
                     datumrd2(i),datenrd2(i),xyunitrd2(i),axtz7)
         if(pmaprd2(i).EQ.'UTM     ')  lutmrd2 =.TRUE.
         if(pmaprd2(i).EQ.'LCC     ')  llccrd2 =.TRUE.
         if(pmaprd2(i).EQ.'PS      ')  lpsrd2  =.TRUE.
         if(pmaprd2(i).EQ.'EM      ')  lemrd2  =.TRUE.
         if(pmaprd2(i).EQ.'LAZA    ')  llazard2=.TRUE.
         if(pmaprd2(i).EQ.'TTM     ')  lttmrd2 =.TRUE.
! ---    Map projection parameters
         if(LUTMRD2) then
            read(iunit,'(i4,a4)') iutmznrd2(i),utmhemrd2(i)
            write(iox,'(i4,a4)') iutmznrd2(i),utmhemrd2(i)
            ncommout=ncommout+1
         elseif(LLCCRD2) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPSRD2) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEMRD2.or.LLAZARD2.or.LTTMRD2) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCRD2.or.LLAZARD2.or.LTTMRD2) then
            read(iunit,*) feastrd2(i),fnorthrd2(i)
            write(iox,*)  feastrd2(i),fnorthrd2(i)
            ncommout=ncommout+1
         else
            feastrd2(i)=0.0
            fnorthrd2(i)=0.0
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datumrd2(i),datenrd2(i)
         write(iox,'(a8,a12)')  datumrd2(i),datenrd2(i)
         ncommout=ncommout+1
! ---    Map units
         read(iunit,'(a4)') xyunitrd2(i)
         write(iox,'(a4)')  xyunitrd2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz7
         write(iox,'(a8)')  axtz7
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmaprd2(i),iutmznrd2(i),&
                     utmhemrd2(i),clat0,clon0,clat1,clat2,&
                     datumrd2(i),datenrd2(i),xyunitrd2(i),axtz7)
! ---    Process Time zone
         read(axtz7(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM7 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz7
            stop 'Halted in RDHDEM7 -- see list file'
         endif
         xtz7(i)=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec7(i),&
                      ieyr,iedy,iehr,iesec7(i)
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDEM7'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec7(i),&
                           ieyr,iedy,iehr,iesec7(i)
         ncommout=ncommout+1
! ---    Number of sources and species
         read(iunit,*) nrd2file,nse7x
         write(iox,*) nrd2file,nse7x
         ncommout=ncommout+1
! ---    Species list
         read(iunit,*)(cslst7x(n),n=1,nse7x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem7x(n),n=1,nse7x)
!     -----
      else
!     -----
         write(*,*)'ERROR in RDEMARB File: ',i
         write(*,*)'Bad Dataset Version: ',verrdarb(i)
         write(*,*)'Expected 1.0'
         stop
!     -----
      endif
!     -----
! --- Species info to scratch file
      do n=1,nse7x
         write(iox,'(a16,f12.4)')cslst7x(n),xmwem7x(n)
      enddo
      ncommout=ncommout+nse7x
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec7(i))
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec7(i))
! --- Apply time zone adjustment to date/times
      if(xtz7(i).GE.-13 .AND. xtz7(i).LE.13) then
         t2btz7(i)=xtz7(i)-xbtz
         ishift=NINT(t2btz7(i))
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in RDEMARB.DAT file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz7(i)
         stop 'Bad Time Zone in RDEMARB.DAT file -- see list file'
      endif
! --- Form date-hr variables for |MROAD2|
      ibdathr7(i)=ibyr*100000+ibdy*100+ibhr
      iedathr7(i)=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1rd2(i))
      call XTRACTLL(io6,'LAT ',clat2,rnlat2rd2(i))
      call XTRACTLL(io6,'LAT ',clat0,rnlat0rd2(i))
      call XTRACTLL(io6,'LON ',clon0,relon0rd2(i))
! --- Set source index range for this file
      if(i.EQ.1) then
         ibsrc7(i)=1
         iesrc7(i)=nrd2file
      else
         ibsrc7(i)=iesrc7(i-1)+1
         iesrc7(i)=iesrc7(i-1)+nrd2file
      endif
! --- Set logical to indicate if the number of sources specified in
! --- the control file (NRD2) have been found
      lnrd2=.FALSE.
      if(nrd2.EQ.iesrc7(i)) lnrd2=.TRUE.
! --- Process data that must be identical in all RDEMARB.DAT files
      if(i.EQ.1) then
! ---    Pass into |MROAD2| names
         nse7=nse7x
!
         if(nse7.gt.mxspec)then
            write(io6,11)nse7,mxspec
11          format(/1x,'ERROR in SUBR. RDHDEM7 -- No. emitted species',&
            ' in RDEMARB file is greater than current array',&
            ' dimension'/1x,&
            '     No. emitted species in file: ',i5/1x,&
            'Current Array dimension (MXSPEC): ',i5)
            write(*,*)
            stop 'Halted in RDHDEM7 -- see list file.'
         endif
      else
! ---    Check for consistency with first file
         if(nse7.NE.nse7x) then
            write(io6,12)nse7,nse7x
12          format(/1x,'ERROR in SUBR. RDHDEM7 -- No. emitted species',&
            ' must match in all RDEMARB files',/1x,&
            '     No. emitted species expected/found: ',2i5)
            write(*,*)
            stop 'Halted in RDHDEM7 -- see list file.'
         endif
      endif
! --- Pass into |MROAD2| names or check for consistency
      if(i.EQ.1) then
         do is=1,nse7
            cslst7(is)=cslst7x(is)
            xmwem7(is)=xmwem7x(is)
         enddo
      else
         lmatch=.TRUE.
         do is=1,nse7
            if(cslst7(is).NE.cslst7x(is)) lmatch=.FALSE.
            if(xmwem7(is).NE.xmwem7x(is)) lmatch=.FALSE.
         enddo
         if(.not.LMATCH) then
            write(io6,15)
15          format(/1x,'ERROR in SUBR. RDHDEM7 -- Species names and',&
            ' molecular weights must match in all RDEMARB files',/)
            write(io6,*)
            write(io6,*)'RDEMARB.DAT File:    1'
            do is=1,nse7
               write(io6,*) cslst7(is),xmwem7(is)
            enddo
            write(io6,*)
            write(io6,*)'RDEMARB.DAT File:',i
            do is=1,nse7
               write(io6,*) cslst7x(is),xmwem7x(is)
            enddo
            write(*,*)
            stop 'Halted in RDHDEM7 -- see list file.'
         endif
      endif
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,102) i
102      format(///1x,13('----------')//1x,'Header record data from ',&
         'the RDEMARB road source file',i5)
         write(io6,103)dataset,ibsrc7(i),iesrc7(i),nse7x,axtz7,&
               ibdathr7(i),ibsec7(i),iedathr7(i),iesec7(i),pmaprd2(i),&
               datumrd2(i)
103      format(/1x,'DATASET: ',a16/1x,'IBSRC7:  ',i9/&
         1x,'IESRC7:  ',i9/1x,'NSE7:    ',i6/1x,'AXTZ7:   ',a8/&
         1x,'IBDATHR7:',i9/1x,'IBSEC7:  ',i4/&
         1x,'IEDATHR7:',i9/1x,'IESEC7:  ',i4/&
         1x,'MAP7:    ',a8/1x,'DATUM:   ',a8)
         if(LUTMRD2) write(io6,104) iutmznrd2(i),utmhemrd2(i)
         if(LLCCRD2) write(io6,105) clat0,clon0,clat1,clat2,&
                                    feastrd2(i),fnorthrd2(i)
         if(LPSRD2) write(io6,106) clat0,clon0,clat1
         if(LEMRD2) write(io6,107) clat0,clon0
         if(LLAZARD2.or.LTTMRD2) write(io6,108) clat0,clon0,&
                                 feastrd2(i),fnorthrd2(i)
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
         write(io6,109)
109      format(4x,'SPECIES',6x,'MOL. WT.'/)
         do is=1,nse7
            write(io6,110)cslst7(is),xmwem7(is)
         enddo
110      format(1x,a12,3x,f7.3)
      endif
! --- Initialize "previous" period times for |MROAD2|
      nstep7(i)=1
      ndhrqb7(1,i)=-000100100
      nsecqb7(1,i)=0
      ndhrqe7(1,i)=ibdathr7(i)
      nsecqe7(1,i)=ibsec7(i)
      return
      end
!----------------------------------------------------------------------
      subroutine RDTIEM7(iunit0,nrd2,nrddat,i,ibsrc7,iesrc7,avrs7,&
                         ldb,io6,iox,caction,datumi,vecti,datumo,vecto,&
                         nrdseg2,cid7,ncommout)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           RDTIEM7
!                D. Strimaitis
!
! --- PURPOSE:  Read the time-invariant records of a RDEMARB road
!               source file (arbitrarily-varying properties)
!               These provide the points along each line, which are
!               transformed into the modeling map projection and
!               stored in a scratch file.
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 RDEMARB file
!             NRD2 - integer    - Number of RDEMARB road sources
!           NRDDAT - integer    - Number of RDEMARB files
!                I - integer    - Current RDEMARB file
!   IBSRC7(nrddat) - integer    - Source ID for first source in each
!                                 RDEMARB file
!   IESRC7(nrddat) - integer    - Source ID for last source in each
!                                 RDEMARB file
!    AVRS7(nrddat) - char*16    - RDEMARB data-set version
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!              IOX - integer    - Fortran unit number for scratch file
!    [For coordinate transformations]
!          CACTION - char*12    - Coordinate projection transformation
!           DATUMI - char*8     - DATUM of the input coordinates
!         VECTI(9) - real*8     - Input coordinate definition
!           DATUMO - char*8     - DATUM of the CALPUFF coordinates
!         VECTO(9) - real*8     - CALPUFF coordinate definition
!          NRDSEG2 - integer    - Number of road segments
!                                 in scratch file
!         NCOMMOUT - integer    - Currrent number of header comments
!                                 in scratch file
!
! --- OUTPUT:
!          NRDSEG2 - integer    - Updated number of road segments
!                                 in scratch file
!       CID7(nrd2) - char*16    - RDEMARB source identifiers
!         NCOMMOUT - integer    - Updated number of header comments
!                                 in scratch file
!
! --- RDTIEM7 called by: SETUP
! --- RDTIEM7 calls:     GLOBE, GRIDROAD
!----------------------------------------------------------------------
      implicit none
! --- Arguments
      logical :: ldb
      integer :: iunit0,nrd2,nrddat,i,io6,iox,nrdseg2,ncommout
      integer :: ibsrc7(nrddat),iesrc7(nrddat)
      real*8  :: vecti(9),vecto(9)
      character(len=16) :: cid7(nrd2),avrs7(nrd2)
      character(len=12) :: caction
      character(len=8)  :: datumi,datumo
! --- Locals
      integer :: n,k,ii,kk,numpoints
      integer :: iutmz, iunit
      character(len=4) :: c4hem
      real    :: tiem7(8), xyz(3)
! --- Process file "i"
      iunit=iunit0+i-1
! --- Loop over sources
      do k=ibsrc7(i),iesrc7(i)
         ii=MIN(k,nrd2)
         if(avrs7(i).EQ.'1.0') then
            read(iunit,*)cid7(ii),numpoints
            write(iox,'(a16,i15)') cid7(ii),numpoints
            ncommout=ncommout+1
! ---       Read and process the NUMPOINTS coordinates along this road
            do kk=1,numpoints
               read(iunit,*) tiem7
               write(iox,*)  tiem7
! ---          Check the order
               if(NINT(tiem7(1)).NE.kk) then
                  write(io6,*)'RDTIEM7:  Road coordinates out of order'
                  write(io6,*)'        Expected point ',kk
                  write(io6,*)'        Found    point ',NINT(tiem7(1))
                  stop 'Halted in RDTIEM7 -- see list file'
               endif
! ---          Transfer point to modeling coordinates
               call GLOBE(io6,caction,datumi,vecti,datumo,vecto,&
                          tiem7(2),tiem7(3),xyz(1),xyz(2),iutmz,c4hem)
! ---          Break each road into segments no longer than 1 grid cell
! ---          and store in scratch file for ROADSEG call
               xyz(3)=tiem7(4)
               call GRIDROAD(cid7(ii),ii,kk,xyz,nrdseg2)
! !!!
!            write(*,*)'RDTIEM7 After GRIDROAD:  k,ii,xyz,nrdseg2 = ',
!     &                   k,ii,xyz,nrdseg2
! !!!
            enddo
         else
            write(io6,*)'RDTIEM7:  Bad RDEMARB.DAT dataset version'
            write(io6,*)'          Expected 1.0'
            write(io6,*)'          Found    ',avrs7(i)
            stop 'Halted in RDTIEM7 -- see list file'
         endif
      enddo
! --- Perform final QA after LAST file is processed
      if(i.NE.nrddat) return
      if(ldb)then
         write(io6,*)
         write(io6,*)
         write(io6,*)'Time-invariant RDEMARB data'
         write(io6,*)'-----------------------------'
         do k=1,nrd2
            write(io6,*)'Source = ',k,'CID7  = ',cid7(k)
         enddo
      endif
! --- Check number of sources
      if(iesrc7(nrddat).NE.nrd2) then
         write(io6,*)'FATAL Error in RDTIEM7:  Wrong number of sources'
         write(io6,*)'         Control file NRD2 = ',nrd2
         write(io6,*)'RDEMARB.DAT Time-Invariant = ',iesrc7(nrddat)
         write(*,*)
         stop 'Halted in RDTIEM7 -- see list file.'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine RDEM7(iunit,nsrc7,nse7,cid7,ibsrc7,iesrc7,vers,tzhr,&
                       ldb,io6,ibegh,ibegs,iendh,iends,em7dat,ieof)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230             RDEM7
!                D. Strimaitis
!
! --- PURPOSE:  Read the time-varying records of one RDEMARB road
!               source emissions file (arbitrarily-varying emissions)
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of RDEMARB file
!            NSRC7 - integer    - Total number of sources in RDEMARB
!                                 files
!             NSE7 - integer    - Number of emitted species
!      CID7(nsrc7) - char*16    - Source identifiers
!           IBSRC7 - integer    - Index for 1st source in current file
!           IESRC7 - integer    - Index for Last source in current file
!             VERS - char*16    - Dataset version for file
!             TZHR - real       - Hours to ADD to times in file to
!                                 convert to BASE TIME ZONE
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!
! --- OUTPUT:
!      IBEGH,IBEGS - integer    - Starting time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
!      IENDH,IENDS - integer    - Ending time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
! EM7DAT(NSE7+3,nsrc7)- real array - Time-varying RDEMARB source data
!             IEOF - integer    - End-of-file flag
!
! --- RDEM7 called by: INITPUF
! --- RDEM7 calls:     YR4, INCR, TCLOCK
!----------------------------------------------------------------------
!
      real em7dat(nse7+3,nsrc7)
      character*16 cid, cid7(nsrc7), vers
      logical ldb, lproblem
! --- Initialize EOF flag
      ieof=0
! --- Read starting, ending date and time
      if(vers.EQ.'1.0') then
         read(iunit,*,end=999)ibyr,ibdy,ibhr,ibegs,&
                              ieyr,iedy,iehr,iends
      else
         write(io6,*)
         write(io6,*)'RDEM7: Invalid Dataset Version for RDEMARB.DAT'
         write(io6,*)'Expected 1.0'
         write(io6,*)'Found: ',vers
         write(*,*)
         stop 'Halted in RDEM7 -- see list file.'
      endif
! --- Enforce YYYY format for year
      call YR4(io6,ibyr,ierrb)
      call YR4(io6,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDEM7'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibegs)
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iends)
! --- Adjust times to BASE TIME ZONE
      ishift=NINT(tzhr)
      call INCR(io6,ibyr,ibdy,ibhr,ishift)
      call INCR(io6,ieyr,iedy,iehr,ishift)
! --- Form date-hour
      ibegh=ibyr*100000+ibdy*100+ibhr
      iendh=ieyr*100000+iedy*100+iehr
! --- Loop over sources
      lproblem = .FALSE.
      nwords=nse7+3
      do i=ibsrc7,iesrc7
         read(iunit,*)cid,(em7dat(n,i),n=1,nwords)
         if(cid.NE.cid7(i)) lproblem=.TRUE.
      enddo
! --- Check for source problem
      if(lproblem) then
         write(io6,*)
         write(io6,*)'RDEM7: Problem with source names in RDEMARB.DAT'
         write(io6,*)'Names in data record do not match header record'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(*,*)
         stop 'Halted in RDEM7 -- see list file.'
      endif
!
      if(ldb)then
         write(io6,*)
         write(io6,*)'Time-varying RDEMARB data'
         write(io6,*)'Beginning time: ',ibegh,ibegs
         write(io6,*)'Ending time   : ',iendh,iends
         write(io6,*)'Source Range  : ',ibsrc7,iesrc7
         do i=ibsrc7,iesrc7
            write(io6,*)'I = ',i,'EM7DAT = ',(em7dat(n,i),n=1,nwords)
         enddo
      endif
      return
! --- Reached end of RDEMARB.DAT file
999   ieof=1
      write(*,*)'RDEM7: Data in RDEMARB.DAT file end early'
      write(io6,*)'RDEM7: Data in RDEMARB.DAT file end early'
      return
      end
!----------------------------------------------------------------------
      subroutine gridroad(c16road,idrd,n,xyz,nrdseg)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230          GRIDROAD
!                D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Place road segments into scratch file, breaking as
!               needed to make each no longer than a grid cell
!
! --- INPUTS:
!          C16ROAD - character  - Current road name
!             IDRD - integer    - Current road index
!                N - integer    - Current point along this road
!           XYZ(3) - real       - x(km), y(km), z(m) of point in
!                                 modeling map projection
!           NRDSEG - integer    - Number of segments stored at start
!
!     Common /GRID/ nx, ny, xorig, yorig, dgrid, dgridi
!
! --- OUTPUT:
!           NRDSEG - integer    - Number of segments stored at end
!
!           Scratch File:  contains nrdseg records of segment lengths
!                          and coordinates
!
! --- GRIDROAD called by: READCF, RDTIEM7
! --- GRIDROAD calls:     FGSTEP
!----------------------------------------------------------------------
      include 'params.puf'
      include 'grid.puf'
! --- Arguments
      character(len=16) :: c16road
      integer  :: idrd, n, nrdseg
      real     :: xyz(3)
! --- Locals
      logical  :: loop
      real     :: x1,y1,z1,x2,y2,z2,xn,yn,zn,dx,dy,dz,eps_cell
      real     :: sm,slen_m,fx,fy,fac
! --- Set marginal epsilon for locating cell-boundaries
      data eps_cell/0.01/
! --- Convert location to grid-cell units
      x2=(1000.*xyz(1)-xorig)*dgridi
      y2=(1000.*xyz(2)-yorig)*dgridi
      z2= xyz(3)
! --- QA
      if(x2.LT.0.0 .OR. y2.LT.0.0 .OR.&
         x2.GT.nx .OR. y2.GT.ny) then
         write(io6,*)'ERROR in subr. GRIDROAD -- Road is off the'//&
                     ' grid on Road ',c16road
         write(io6,*)'   (x,y)km = ', xyz(1),xyz(2)
         stop 'HALT in GRIDROAD -- see list file'
      endif
      if(n.EQ.1) then
! ---    Store first point
         x1=x2
         y1=y2
         z1=z2
      else
! ---    Make segments
         loop=.true.
         do while (loop)
            dx=x2-x1
            dy=y2-y1
            dz=z2-z1
            slen_m=SQRT(dx*dx+dy*dy)*dgrid
! ---       Next cell boundary along road in x
            fx=FGSTEP(x1,dx,eps_cell)
! ---       Next cell boundary along road in y
            fy=FGSTEP(y1,dy,eps_cell)
! ---       Fraction of step for this segment is minimum of these
            fac=AMIN1(fx,fy)
            if(fac.GE.1.0) then
! ---          Segment finishes at the end-pt
               loop=.false.
               xn=x2
               yn=y2
               zn=z2
               sm=slen_m
            else
               xn=x1+fac*dx
               yn=y1+fac*dy
               zn=z1+fac*dz
               sm=fac*slen_m
            endif
! ---       Create source segment (record to scratch file)
            nrdseg=nrdseg+1
            write(iordx) idrd,sm,x1,xn,y1,yn,z1,zn
            x1=xn
            y1=yn
            z1=zn
         enddo
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine gridspray(c16spray,idsp,iqt,n,xyz,nspseg)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918          GRIDSPRAY
!                C. DesAutels
!
! --- PURPOSE:  Place spray segments into scratch file, breaking as
!               needed to make each no longer than a grid cell
!
! --- INPUTS:
!          C16SPRAY - character - Current spray line name
!             IDSP - integer    - Current spray index
!                N - integer    - Current point along this spray line
!           XYZ(3) - real       - x(km), y(km), z(m) of point in
!                                 modeling map projection
!           NSPSEG - integer    - Number of segments stored at start
!
!     Common /GRID/ nx, ny, xorig, yorig, dgrid, dgridi
!
! --- OUTPUT:
!           NSPSEG - integer    - Number of segments stored at end
!
!           Scratch File:  contains nrdseg records of segment lengths
!                          and coordinates
!
! --- GRIDSPRAY called by: INITPUF
! --- GRIDSPRAY calls:     FGSTEP
!----------------------------------------------------------------------
      include 'params.puf'
      include 'grid.puf'
! --- Arguments
      character(len=16) :: c16spray
      integer  :: idsp, n, nspseg, iqt
      real     :: xyz(3)
! --- Locals
      logical  :: loop
      real     :: x1,y1,z1,x2,y2,z2,xn,yn,zn,dx,dy,dz,eps_cell
      real     :: sm,slen_m,fx,fy,fac,qt
! --- Set marginal epsilon for locating cell-boundaries
      data eps_cell/0.01/
! --- Convert location to grid-cell units
      x2=(1000.*xyz(1)-xorig)*dgridi
      y2=(1000.*xyz(2)-yorig)*dgridi
      z2= xyz(3)
! --- QA
      if(x2.LT.0.0 .OR. y2.LT.0.0 .OR.&
         x2.GT.nx .OR. y2.GT.ny) then
         write(io6,*)'ERROR in subr. GRIDSPRAY -- Road is off the'//&
                     ' grid on Spray ',c16spray
         write(io6,*)'   (x,y)km = ', xyz(1),xyz(2)
         stop 'HALT in GRIDSPRAY -- see list file'
      endif
      if(n.EQ.1) then
! ---    Store first point
         x1=x2
         y1=y2
         z1=z2
      else
! ---    Make segments
         loop=.true.
         do while (loop)
            dx=x2-x1
            dy=y2-y1
            dz=z2-z1
            slen_m=SQRT(dx*dx+dy*dy)*dgrid
! ---       Next cell boundary along road in x
            fx=FGSTEP(x1,dx,eps_cell)
! ---       Next cell boundary along road in y
            fy=FGSTEP(y1,dy,eps_cell)
! ---       Fraction of step for this segment is minimum of these
            fac=AMIN1(fx,fy)
            if(fac.GE.1.0) then
! ---          Segment finishes at the end-pt
               loop=.false.
               xn=x2
               yn=y2
               zn=z2
               sm=slen_m
            else
               xn=x1+fac*dx
               yn=y1+fac*dy
               zn=z1+fac*dz
               sm=fac*slen_m
            endif
! ---       Create source segment (record to scratch file)
            nspseg=nspseg+1
            write(iospx) idsp,iqt,sm,x1,xn,y1,yn,z1,zn
!            write(*,*)'GRIDSPRAY idsp,iqt,sm,x1,xn,y1,yn,z1,zn',
!     &                  idsp,iqt,sm,x1,xn,y1,yn,z1,zn
            x1=xn
            y1=yn
            z1=zn
         enddo
      endif
      return
      end
