!      integer, allocatable :: idsfrds(:,:)                              ! (mxspec,nrd1)
!      integer, allocatable :: ixrefrds(:)                               ! (nsfrds)
!      real, allocatable    :: htrd1(:),sz0rd1(:),sy0rd1(:)              ! (nrd1)
