!
!     Module |mroad2|
!      integer, allocatable :: ixrem7(:)                                 ! (mxspec)
!      integer, allocatable :: nptrd2(:)                                 ! (nrd2)
!      character(len=12), allocatable :: cslst7(:)                       ! (mxspec)
!      character(len=16), allocatable :: cid7(:)                         ! (nrd2)
