!      integer, allocatable :: ndhrqb7(:,:),nsecqb7(:,:)                 ! (mxqstep,nrddat)
!      integer, allocatable :: ndhrqe7(:,:),nsecqe7(:,:)                 ! (mxqstep,nrddat)
!      real,    allocatable :: xtz7(:),t2btz7(:)                         ! (nrddat)
!      real,    allocatable :: feastrd2(:),fnorthrd2(:)                  ! (nrddat)
!      real,    allocatable :: rnlat0rd2(:),relon0rd2(:)                 ! (nrddat)
!      real,    allocatable :: rnlat1rd2(:),rnlat2rd2(:)                 ! (nrddat)
!      character(len=8),  allocatable :: pmaprd2(:),datumrd2(:)          ! (nrddat)
!      character(len=4),  allocatable :: utmhemrd2(:),xyunitrd2(:)       ! (nrddat)
!      character(len=12), allocatable :: datenrd2(:)                     ! (nrddat)
!      character(len=16), allocatable :: verrdarb(:)                     ! (nrddat)
!      character(len=132),allocatable :: rddat(:)                        ! (nrddat)
!
!     Module |mspray2|
!      integer, allocatable :: ixrem8(:)                                 ! (mxspec)
!      integer, allocatable :: nptsp2(:)                                 ! (nsp2)
!      character(len=12), allocatable :: cslst8(:)                       ! (mxspec)
!      character(len=16), allocatable :: cid8(:)                         ! (nsp2)
!      real,    allocatable :: xmwem8(:)                                 ! (mxspec)
!      real,    allocatable :: htsp2(:,:),sz0sp2(:,:),sy0sp2(:,:)        ! (mxqstep,nsp2)
!      real,    allocatable :: diasp2(:,:),dcutsp2(:,:)                  ! (mxqstep,nsp2)
!      real,    allocatable :: qsp2(:,:,:)                               ! (mxspec,mxqstep,nsp2)
! --- Arrays for data stored for each SPEMARB.DAT file (nspdat files)
!      integer, allocatable :: ibsrc8(:),iesrc8(:),ibdathr8(:),ibsec8(:) ! (nspdat)
!      integer, allocatable :: iedathr8(:),iesec8(:)                     ! (nspdat)
!      integer, allocatable :: iutmznsp2(:)                              ! (nspdat)
!      integer, allocatable :: nstep8(:),mfsp2(:)                        ! (nspdat)
!      integer, allocatable :: ndhrqb8(:,:),nsecqb8(:,:)                 ! (mxqstep,nspdat)
!      integer, allocatable :: ndhrqe8(:,:),nsecqe8(:,:)                 ! (mxqstep,nspdat)
!      real,    allocatable :: xtz8(:),t2btz8(:)                         ! (nspdat)
!      real,    allocatable :: feastsp2(:),fnorthsp2(:)                  ! (nspdat)
!      real,    allocatable :: rnlat0sp2(:),relon0sp2(:)                 ! (nspdat)
!      real,    allocatable :: rnlat1sp2(:),rnlat2sp2(:)                 ! (nspdat)
!      character(len=8),  allocatable :: pmapsp2(:),datumsp2(:)          ! (nspdat)
!      character(len=4),  allocatable :: utmhemsp2(:),xyunitsp2(:)       ! (nspdat)
!      character(len=12), allocatable :: datensp2(:)                     ! (nspdat)
!      character(len=16), allocatable :: versparb(:)                     ! (nspdat)
!      character(len=132),allocatable :: spdat(:)                        ! (nspdat)
!
!     Module |mqscale|
!      integer              :: nqsftab
!      integer, allocatable :: iqsftype(:)                                ! (nqsftab)
!      real, allocatable    :: qsftab(:,:)                                ! (mxqsf,nqsftab)
!      character(len=40), allocatable :: cqsfname(:)                      ! (nqsftab)
!
!
! --- RUNSIZE called by: SETUP
! --- RUNSIZE calls:     DELIM, MK_CASE, RM_BLANK
!----------------------------------------------------------------------
! --- Access module data
      use mroad1
      use mroad2
      use mspray2
      use mqscale
      implicit none
! --- Arguments
      integer :: io5,io6,mxspec,mxqsf,mxqstep
! --- Locals
      logical :: lprocess, lsamp, ldbg
      integer :: i,ieof, ictrl,iolst
      integer :: id1,id2,ieq
      integer :: l1,l2,nl,r1,r2
      integer :: ibsamp,iesamp,jbsamp,jesamp, mesh
      integer :: ntemp, nspt1,nsar1,nsln1,nsvl1
      character(len=320)  ::  blank320,aline320
      character(len=1)    ::  dlim
      real    :: zero
      data dlim/'!'/
      data ldbg/.FALSE./
      data zero/0.0/
! --- Establish initial values
      do i=1,320
         blank320(i:i)=' '
      enddo
      ictrl=io5
      iolst=io6
      nrd1=0
      nrddat=0
      nrd2=0
      nspdat=0
      nsp2=0
      nsfrds=0
      nqsftab=0
      nspt1=0
      nsar1=0
      nsln1=0
      nsvl1=0
! --- Scan control file for array-size parameters
! -----------------------------------------------------------
      ieof=0
      do while (ieof.EQ.0)
         aline320=blank320
         read(ictrl,'(a320)',iostat=ieof) aline320
! ---    Clear all blanks
         call RM_BLANK(aline320)
! ---    Get delimiter information
         call DELIM(dlim,aline320,id1,id2,ieq,lprocess)
         if(lprocess) then
! ---       Set range for variable name on left and value on right
            l1=id1+1
            l2=ieq-1
            nl=l2-l1+1
            r1=ieq+1
            r2=id2-1
! ---       Convert to upper case
            call MK_CASE(iolst,'RUNSIZE','UPPER',aline320)
! ---       Interpret subset of inputs
            if(INDEX(aline320(l1:l2),'NRDDAT').GT.0 .AND.&
                   nl.EQ.6) then
               read(aline320(r1:r2),'(i)') nrddat
            elseif(INDEX(aline320(l1:l2),'NRD1').GT.0 .AND.&
                   nl.EQ.4) then
               read(aline320(r1:r2),'(i)') nrd1
            elseif(INDEX(aline320(l1:l2),'NSFRDS').GT.0 .AND.&
                   nl.EQ.6) then
               read(aline320(r1:r2),'(i)') nsfrds
            elseif(INDEX(aline320(l1:l2),'NRD2').GT.0 .AND.&
                   nl.EQ.4) then
               read(aline320(r1:r2),'(i)') nrd2
            elseif(INDEX(aline320(l1:l2),'NSPDAT').GT.0 .AND.&
                   nl.EQ.6) then
               read(aline320(r1:r2),'(i)') nspdat
            elseif(INDEX(aline320(l1:l2),'NSP2').GT.0 .AND.&
                   nl.EQ.4) then
               read(aline320(r1:r2),'(i)') nsp2
            elseif(INDEX(aline320(l1:l2),'NSFTAB').GT.0 .AND.&
                   nl.EQ.6) then
               read(aline320(r1:r2),'(i)') nqsftab
! ---       Scan for other scale-factor source-species tables
            elseif(INDEX(aline320(l1:l2),'NSPT1').GT.0 .AND.&
                   nl.EQ.5) then
               read(aline320(r1:r2),'(i)') nspt1
            elseif(INDEX(aline320(l1:l2),'NSAR1').GT.0 .AND.&
                   nl.EQ.5) then
               read(aline320(r1:r2),'(i)') nsar1
            elseif(INDEX(aline320(l1:l2),'NSLN1').GT.0 .AND.&
                   nl.EQ.5) then
               read(aline320(r1:r2),'(i)') nsln1
            elseif(INDEX(aline320(l1:l2),'NSVL1').GT.0 .AND.&
                   nl.EQ.5) then
               read(aline320(r1:r2),'(i)') nsvl1
            endif
         endif
      enddo
! --- Rewind control file
      REWIND(io5)
! --- Emissions scale-factor tables
! --- Number of tables is either total number of source-species scaled
! --- (for old files with IVARY) or nqsftab (newer format with tables
! --- in Group after sources)
      if(nqsftab.EQ.0) nqsftab=nspt1+nsar1+nsln1+nsvl1+nsfrds
! --- Allocate arrays
! --- Some arrays need to be read in even when not used
      if(nrddat.GT.0) ALLOCATE(rddat(nrddat))
      if(nspdat.GT.0) ALLOCATE(spdat(nspdat))
      if(nqsftab.GT.0) then
! ---    Module mqscale
! ---    Emissions scale factor tables
         ALLOCATE(iqsftype(nqsftab),cqsfname(nqsftab))
         ALLOCATE(qsftab(mxqsf,nqsftab))
         iqsftype=0
         cqsfname='                                        '
         qsftab=zero
      endif
      if(nrd1.GT.0) then
! ---    Module mroad1
         ALLOCATE(srcnamrd1(nrd1))
         ALLOCATE(nptrd1(nrd1))
         ALLOCATE(htrd1(nrd1),sz0rd1(nrd1),sy0rd1(nrd1))
         ALLOCATE(qrd1(mxspec,nrd1))
         if(nsfrds.GT.0) then
            ALLOCATE(ixrefrds(nsfrds),csfrds(nsfrds))
            ALLOCATE(idsfrds(mxspec,nrd1))
            ixrefrds=0
            csfrds='                                        '
            idsfrds=0
         endif
         srcnamrd1='                '
         nrd1=0
         nptrd1=0
         htrd1=zero
         sz0rd1=zero
         sy0rd1=zero
         qrd1=zero
      endif
      if(nrd2.GT.0) then
! ---    Module mroad2
! ---    Number of species (max)
         ALLOCATE(cslst7(mxspec),ixrem7(mxspec),xmwem7(mxspec))
! ---    Number of roads
         ALLOCATE(cid7(nrd2),nptrd2(nrd2))
         ALLOCATE(sz0rd2(mxqstep,nrd2),sy0rd2(mxqstep,nrd2))
         ALLOCATE(htrd2(mxqstep,nrd2),qrd2(mxspec,mxqstep,nrd2))
! ---    Number of files
         ALLOCATE(ibsrc7(nrddat),iesrc7(nrddat),ibdathr7(nrddat))
         ALLOCATE(ibsec7(nrddat),iedathr7(nrddat),iesec7(nrddat))
         ALLOCATE(iutmznrd2(nrddat),nstep7(nrddat))
         ALLOCATE(ndhrqb7(mxqstep,nrddat),nsecqb7(mxqstep,nrddat))
         ALLOCATE(ndhrqe7(mxqstep,nrddat),nsecqe7(mxqstep,nrddat))
         ALLOCATE(xtz7(nrddat),t2btz7(nrddat))
         ALLOCATE(feastrd2(nrddat),fnorthrd2(nrddat))
         ALLOCATE(rnlat0rd2(nrddat),relon0rd2(nrddat))
         ALLOCATE(rnlat1rd2(nrddat),rnlat2rd2(nrddat))
         ALLOCATE(pmaprd2(nrddat),datumrd2(nrddat))
         ALLOCATE(utmhemrd2(nrddat),xyunitrd2(nrddat))
         ALLOCATE(datenrd2(nrddat))
         ALLOCATE(mfrd2(nrddat),verrdarb(nrddat))
         cslst7='            '
         ixrem7=0
         xmwem7=zero
         nrd2=0
         cid7='                '
         nptrd2=0
         htrd2=zero
         sz0rd2=zero
         sy0rd2=zero
         qrd2=zero
         ibsrc7=0
         iesrc7=0
         ibdathr7=0
         ibsec7=0
         iedathr7=0
         iesec7=0
         iutmznrd2=0
         nstep7=0
         ndhrqb7=0
         nsecqb7=0
         ndhrqe7=0
         nsecqe7=0
         xtz7=zero
         t2btz7=zero
         feastrd2=zero
         fnorthrd2=zero
         rnlat0rd2=zero
         relon0rd2=zero
         rnlat1rd2=zero
         rnlat2rd2=zero
         pmaprd2='        '
         datumrd2='        '
         utmhemrd2='    '
         xyunitrd2='    '
         datenrd2='            '
      endif
      if(nsp2.GT.0) then
! ---    Module mspray2
! ---    Number of species (max)
         ALLOCATE(cslst8(mxspec),ixrem8(mxspec),xmwem8(mxspec))
! ---    Number of sprays
         ALLOCATE(cid8(nsp2),nptsp2(nsp2))
         ALLOCATE(sz0sp2(mxqstep,nsp2),sy0sp2(mxqstep,nsp2))
         ALLOCATE(diasp2(mxqstep,nsp2),dcutsp2(mxqstep,nsp2))
         ALLOCATE(htsp2(mxqstep,nsp2),qsp2(mxspec,mxqstep,nsp2))
! ---    Number of files
         ALLOCATE(ibsrc8(nspdat),iesrc8(nspdat),ibdathr8(nspdat))
         ALLOCATE(ibsec8(nspdat),iedathr8(nspdat),iesec8(nspdat))
         ALLOCATE(iutmznsp2(nspdat),nstep8(nspdat))
         ALLOCATE(ndhrqb8(mxqstep,nspdat),nsecqb8(mxqstep,nspdat))
         ALLOCATE(ndhrqe8(mxqstep,nspdat),nsecqe8(mxqstep,nspdat))
         ALLOCATE(xtz8(nspdat),t2btz8(nspdat))
         ALLOCATE(feastsp2(nspdat),fnorthsp2(nspdat))
         ALLOCATE(rnlat0sp2(nspdat),relon0sp2(nspdat))
         ALLOCATE(rnlat1sp2(nspdat),rnlat2sp2(nspdat))
         ALLOCATE(pmapsp2(nspdat),datumsp2(nspdat))
         ALLOCATE(utmhemsp2(nspdat),xyunitsp2(nspdat))
         ALLOCATE(datensp2(nspdat))
         ALLOCATE(mfsp2(nspdat),versparb(nspdat))
         cslst8='            '
         ixrem8=0
         xmwem8=zero
         nsp2=0
         cid8='                '
         nptsp2=0
         htsp2=zero
         sz0sp2=zero
         sy0sp2=zero
         qsp2=zero
         ibsrc8=0
         iesrc8=0
         ibdathr8=0
         ibsec8=0
         iedathr8=0
         iesec8=0
         iutmznsp2=0
         nstep8=0
         ndhrqb8=0
         nsecqb8=0
         ndhrqe8=0
         nsecqe8=0
         xtz8=zero
         t2btz8=zero
         feastsp2=zero
         fnorthsp2=zero
         rnlat0sp2=zero
         relon0sp2=zero
         rnlat1sp2=zero
         rnlat2sp2=zero
         pmapsp2='        '
         datumsp2='        '
         utmhemsp2='    '
         xyunitsp2='    '
         datensp2='            '
      endif
! --- Report info (debug)
      if(ldbg) then
         write(io6,*)
         write(io6,*)'RUNSIZE:  Array-Size parameters'
         write(io6,*)'nrd1, nsfrds        = ',nrd1,nsfrds
         write(io6,*)'nrd2, nrddat        = ',nrd2,nrddat
         write(io6,*)'nsp2, nspdat        = ',nsp2,nspdat
         write(io6,*)'nqsftab             = ',nqsftab
         write(io6,*)
      endif
      return
      end
