!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913            FGSTEP
!                D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Return fraction of the step DR to reach the next
!               cell boundary
!               Grid-cell units are used so a boundary lies at each
!               integer value
!
! --- INPUTS:
!             R - real    - Starting coordinate (cell units)
!            DR - real    - Step increment (cell units)
!           EPS - real    - Resolution margin (cell units)
!
! --- OUTPUT:
!        FGSTEP - real    - Fraction of a step to reach the next
!                           grid-cell boundary 
!
! --- FGSTEP called by: READCF
! --- FGSTEP calls:     none
!----------------------------------------------------------------------
      implicit none
! --- Arguments
      real       :: r,dr,eps
! --- Locals
      integer    :: ir
      real       :: rn
! --- Next cell boundary along dr (may be + or -)
      fgstep=999.
      ir=NINT(r)
      rn=FLOAT(ir)
      if(dr.GT.0.0) then
         if(ABS(rn-r).LE.eps) then
! ---       Go to the next grid-line
            rn=rn+1.0
         elseif(rn.LE.r) then
! ---       Go to the next grid-line
            rn=rn+1.0
         endif
         fgstep=(rn-r)/dr
      elseif(dr.LT.0.0) then
         if(ABS(rn-r).LE.eps) then
! ---       Go to the next grid-line
            rn=rn-1.0
         elseif(rn.GE.r) then
! ---       Go to the next grid-line
            rn=rn-1.0
         endif
         fgstep=(rn-r)/dr
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine roadseg(itype)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230           ROADSEG
!                D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Place road segments that were saved to scratch file
!               into allocated storage array
!
! --- UPDATES:
!     TNG-7.0.0(140913) - TNG-7.1.0  141230  (DGS)
!               : Process either control-file roads or variable road
!                 source data (RDEMARB.DAT)
!
! --- INPUTS:
!            ITYPE - integer    - Processing flag
!                                 1: Control File road data
!                                 2: RDEMARB.DAT file road data
!     /params/ variables:
!           iordx, io6
!     |mroad1| variables:
!           nrdseg1
!     |mroad2| variables:
!           nrdseg2
!
! --- OUTPUT:
!     |mroad1| variables:
!           nrdseg1
!           iroad1(nrdseg1),newrd1(nrdseg1),rdlen1(nrdseg1)
!           xrd1grd(2,nrdseg1),yrd1grd(2,nrdseg1),elrd1(2,nrdseg1)
!     |mroad2| variables:
!           nrdseg2
!           iroad2(nrdseg2),newrd2(nrdseg2),rdlen2(nrdseg2)
!           xrd2grd(2,nrdseg2),yrd2grd(2,nrdseg2),elrd2(2,nrdseg2)
!
! --- ROADSEG called by: READCF, SETUP
! --- ROADSEG calls:     none
!----------------------------------------------------------------------
! --- Modules
      use mroad1
      use mroad2
!      implicit none
! --- Include parameters
      include 'params.puf'
! --- Arguments
      integer    :: itype
! --- Locals
      integer    :: i
! --- Process by type
! -------------------
      if(itype.EQ.1) then
! ---    Allocate the arrays for the road segments from control file
         ALLOCATE(iroad1(nrdseg1),newrd1(nrdseg1),rdlen1(nrdseg1))
         ALLOCATE(xrd1grd(2,nrdseg1),yrd1grd(2,nrdseg1))
         ALLOCATE(elrd1(2,nrdseg1))
         iroad1=0
         newrd1=0
         rdlen1=0.0
         xrd1grd=0.0
         yrd1grd=0.0
         elrd1=0.0
! ---    Access records in the scratch file
         REWIND(iordx)
         do i=1,nrdseg1
            read(iordx) iroad1(i),rdlen1(i),&
                        xrd1grd(1,i),xrd1grd(2,i),&
                        yrd1grd(1,i),yrd1grd(2,i),&
                        elrd1(1,i),elrd1(2,i)
         enddo
      elseif(itype.EQ.2) then
! ---    Allocate the arrays for the road segments from RDEMARB.DAT
         ALLOCATE(iroad2(nrdseg2),newrd2(nrdseg2),rdlen2(nrdseg2))
         ALLOCATE(xrd2grd(2,nrdseg2),yrd2grd(2,nrdseg2))
         ALLOCATE(elrd2(2,nrdseg2))
         iroad2=0
         newrd2=0
         rdlen2=0.0
         xrd2grd=0.0
         yrd2grd=0.0
         elrd2=0.0
         
! ---    Access records in the scratch file
         REWIND(iordx)
         do i=1,nrdseg2
            read(iordx) iroad2(i),rdlen2(i),&
                        xrd2grd(1,i),xrd2grd(2,i),&
                        yrd2grd(1,i),yrd2grd(2,i),&
                        elrd2(1,i),elrd2(2,i)
         enddo
      else
         write(io6,*)'ERROR in ROADSEG:  invalid data type'
         write(io6,*)'   Expected 1 or 2'
         write(io6,*)'   Found ',itype
         stop 'HALT in ROADSEG -- see list file'
      endif
! --- Done with scratch file
      CLOSE(iordx)
      return
      end
!----------------------------------------------------------------------
      subroutine roads(mroad,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                       dtsec,smxi1,ldb,mfact,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 141230             ROADS
!                D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               discrete ROAD SOURCES with constant emissions.
!               This source type always emits slugs in the way that
!               puffs are emitted:  They do not extend continuously
!               from a point during the release step, with a new and
!               old end.  Rather, they are emitted as 'old' slugs at
!               the start of the release step, and both ends are the
!               same age and have the same properties.
!
! --- UPDATES:
!     TNG-7.0.0(140913) - TNG-7.1.0  141230  (DGS)
!              : Process either control-file roads or variable road
!                source data (RDEMARB.DAT);  name of subroutine
!                changed from ROADS1 to ROADS
!              : Revise use of stored scale factor arrays
!              : Place scale-factor after met variables are
!                extracted (bug -- affects only wind, stability,
!                temperature scale factors)
!
! --- INPUTS:
!         MROAD - integer - Flag for origin of road source data
!                            1 = Control file (|mroad1|)
!                            2 = RDEMARB.DAT file (|mroad2|)
!            IV - integer - RDEMARB.DAT file index
!             I - integer - Road-segment source index
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         DTSEC - real    - Length (seconds) of a basic time step
! SMXI1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!                           Should be SLUG for these roads
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, WSCALM, PLX0
!     /FLAGS/ variables:
!           MGAUSS, MSLUG, MHFTSZ
!     /GRID/ variables:
!           IBCOMP, IECOMP, JBCOMP, JECOMP
!     /METHR/ variables:
!           TEMPSS(mxss,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP
!     /METHD/ variables:
!           I2DMET
!     /PUFF/ variables:
!           NPUFFS
!     |MQSCALE| variables:
!           NQSFTAB,IQSFTYPE(nqsftab),QSFTAB(mxqsf,nqsftab)
!     |MROAD1| variables:
!           NRDSEG1, NRD1
!           HTRD1(nrd1),SZ0RD1(nrd1),SY0RD1(nrd1)
!           QRD1(mxspec,nrd1),
!           IROAD1(nrdseg1),RDLEN1(nrdseg1),ELRD1(2,nrdseg1)
!           XRD1GRD(2,nrdseg1),YRD1GRD(2,nrdseg1),
!           IDSFRDS(mxspec,nrd1),SRCNAMRD1(nrd1),
!           NSFRDS,IXREFRDS(nsfrds)
!     |MROAD2| variables:
!           NRDSEG2, NRD2
!           HTRD2(mxqstep,nrd2),SZ0RD2(mxqstep,nrd2),
!           SY0RD2(mxqstep,nrd2),QRD2(mxspec,mxqstep,nrd2),
!           IROAD2(nrdseg2),RDLEN2(nrdseg2),ELRD2(2,nrdseg2)
!           XRD2GRD(2,nrdseg2),YRD2GRD(2,nrdseg2),
!           CID7(nrd2),
!           NSTEP7(nrddat),NDHRQB7(mxqstep,nrddat),
!           NSECQB7(mxqstep,nrddat),NDHRQE7(mxqstep,nrddat),
!           NSECQE7(mxqstep,nrddat)
!
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXSPEC, IO6
!           MXQSF, MXMETDOM, MXQSTEP
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     |MROAD1| variables:
!           NEWRD1(nrdseg1)
!     |MROAD2| variables:
!           NEWRD2(nrdseg2)
!     /PUFF/ variables:
!           all variables in module
!     /SLUG/ variables:
!           all variables in module
!
! --- ROADS called by:  INITPUF
! --- ROADS calls:      RLSMET, ROLLDN, SWAP, INJECT, QFACTOR
!                       ZEROTAB, SRCTABOUT, GETCELL, RLSMET
!                       DELTSEC
!----------------------------------------------------------------------
! --- Modules
      use mroad1
      use mroad2
      use mqscale
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'comparm.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
      include 'slug.puf'
      real xrdgrd(2),yrdgrd(2),elrd(2),qrd(mxspec)
      real smxi1(mxmetdom)
      logical ldb,problem
      integer isourcetype(2)
      data zero/0.0/
! --- Road source type (12=control-file, 13=RDEMARB.DAT)
      data isourcetype/12,13/
! --- All road emissions go into puff-like slugs
      data mfact_rd/10/
! --- Expect MFACT in this call to be for slugs
      if(mfact.NE.mfact_rd) then
         write(*,*)
         write(*,*)'ROADS:  WARNING -- Roads always use an along'//&
                   '-road SLUG release to represent the emitted cloud'
         write(*,*)'Ignoring MFACT that is passed;  Using MFACT_RD'
         write(*,*)'MFACT, MFACT_RD = ',mfact,mfact_rd
      endif
! --- Set source emission period range, and screen MROAD
! --- Road source type is 12 (control-file) or 13 (RDEMARB.DAT)
      if(mroad.EQ.1) then
         nt=1
         itype=12
         if(ldb) write(io6,*)&
            'ROADS:  Control-File Source (Road Segment) Number    ',i,&
            '    Road = '//srcnamrd1(iroad1(i))
      elseif(mroad.EQ.2) then
         nt=nstep7(iv)
         itype=13
         if(ldb) write(io6,*)&
            'ROADS:  RDEMARB.DAT Source (Road Segment) Number    ',i,&
            '    Road = '//CID7(iroad2(i))
      else
         write(io6,*)
         write(io6,*)'ROADS:  Invalid road-source type: ',mroad
         write(io6,*)'        Valid types are 1 (control file)'
         write(io6,*)'                        2 (RDEMARB.DAT)'
         write(*,*)
         stop 'Halted in ROADS -- see list file.'
      endif
! --- Initialize number of new puffs from this source, this timestep
      npnew=0
! --- Loop over emission periods within this timestep
      do 100 it=1,nt
! ---    Initialize source tabulation specifications to zero
         call ZEROTAB
! ---    Assign variables for current source
         if(mroad.EQ.1) then
! ---       Control File
! ---       Variables stored for each segment
            rdlen=rdlen1(i)
            do k=1,2
               xrdgrd(k)=xrd1grd(k,i)
               yrdgrd(k)=yrd1grd(k,i)
               elrd(k)=elrd1(k,i)
            enddo
! ---       Road variables associated with segment i
            iroad=iroad1(i)
            htrd=htrd1(iroad)
            sz0rd=sz0rd1(iroad)
            sy0rd=sy0rd1(iroad)
! ---       Select emission rate and convert to g/s
            do is=1,nspec
               qrd(is)=qrd1(is,iroad)*rdlen
            enddo
         elseif(mroad.EQ.2) then
! ---       RDEMARB.DAT
            if(ldb) write(io6,*) '   Emissions period ',it
! ---       Variables stored for each segment
            rdlen=rdlen2(i)
            do k=1,2
               xrdgrd(k)=xrd2grd(k,i)
               yrdgrd(k)=yrd2grd(k,i)
               elrd(k)=elrd2(k,i)
            enddo
! ---       Road variables associated with segment i
            iroad=iroad2(i)
            htrd=htrd2(it,iroad)
            sz0rd=sz0rd2(it,iroad)
            sy0rd=sy0rd2(it,iroad)
! ---       Select emission rate and convert to g/s
            do is=1,nspec
               qrd(is)=qrd2(is,it,iroad)*rdlen
! !!!
            if(ldb) write(io6,*)'   Road,Species, Q(g/s) = ',&
                                 iroad,is,qrd(is)
! !!!
            enddo
         else
            write(*,*)'Subr. ROADS:  Invalid MROAD = ',mroad
            stop
         endif
! ---    Road center in MET grid 1 (outermost) units
         xcentgrd=0.5*(xrdgrd(1)+xrdgrd(2))
         ycentgrd=0.5*(yrdgrd(1)+yrdgrd(2))
! ---    Determine MET grid 1 (outermost) point closest to road center
         ixs=1.0+xcentgrd
         iys=1.0+ycentgrd
! ---    Source off the computational grid -- write a WARNING
         if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
            jecomp)then
            write(io6,20)i,ixs,iys
20          format(/1x,'Warning -- a road-link segment is ',&
            'off the computational grid'/1x,'segment no. (i) = ',i6,&
            '  nearest grid point (ixs,iys) = (',i5,',',i5,')')
            problem=.TRUE.
            goto 100
         endif
! ---    Now determine the met. grid point closest to the source
         call GETCELL('ROADS       ','GRIDIJ',1,xcentgrd,ycentgrd,&
                      zero,zero,ixs,iys,md)
! ---    Extract release height wind speed & other met. variables
! ---    Note: mfact0 is not used for road-slugs and so remain as
! ---    slugs when released in calms
         call RLSMET(ldb,ixs,iys,md,htrd,mfact,&
                     mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                     ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,&
                     idoptz)
         if(i2dmet.EQ.1) then
            tdk=temp2d(ixs,iys,md)
         elseif(i2dmet.EQ.0) then
            tdk=tempss(issta,md)
         else
            write(*,*)'Subr. ROADS:  Invalid I2DMET = ',i2dmet
            stop
         endif
! ---    Trap missing power-law exponent (substitute default)
         if(plexp.LT.-900.) then
! ---       Use default
            plx=plx0(istab)
         else
! ---       Use it
            plx=plexp
         endif
! --------------------------------------------------------------------
! --- Do not generate puff for this step if all emissions are ZERO!
! --------------------------------------------------------------------
         qtot=0.0
         do is=1,nspec
! ---       Apply emissions scale-factor for scaled source-species
            if(mroad.EQ.1 .AND. nsfrds.GT.0) then
! ---          Pointer to scale factor tables
               k=idsfrds(is,iroad)
               if(k.GT.0) then
                  kk=ixrefrds(k)
                  call QFACTOR(io6,iqsftype(kk),isourcetype(mroad),&
                               ws,istab,tdk,iqsf)
                  qrd(is)=qrd(is)*qsftab(iqsf,kk)
               endif
            endif
            qtot=qtot+qrd(is)
         enddo
         if(qtot.EQ.0.0) then
            if(ldb) then
               write(io6,*)
               write(io6,*) 'No emissions this step from Source ',i
               write(io6,*)
            endif
            goto 100
         endif
! ---    Establish emitting time
         if(mroad.EQ.1) then
            ndhr1=ndhrb
            nsec1=nsecb
            ndhr2=ndhre
            nsec2=nsece
         elseif(mroad.EQ.2) then
! ---       Start-time for emission period during timestep
            if(ndhrqb7(it,iv).LT.ndhrb) then
               ndhr1=ndhrb
               nsec1=nsecb
            elseif(ndhrqb7(it,iv).EQ.ndhrb .AND.&
                   nsecqb7(it,iv).LT.nsecb) then
               ndhr1=ndhrb
               nsec1=nsecb
            else
               ndhr1=ndhrqb7(it,iv)
               nsec1=nsecqb7(it,iv)
            endif
! ---       End-time for emission period during timestep
            if(ndhrqe7(it,iv).GT.ndhre) then
               ndhr2=ndhre
               nsec2=nsece
            elseif(ndhrqe7(it,iv).EQ.ndhre .AND.&
                   nsecqe7(it,iv).GT.nsece) then
               ndhr2=ndhre
               nsec2=nsece
            else
               ndhr2=ndhrqe7(it,iv)
               nsec2=nsecqe7(it,iv)
            endif
         else
            write(*,*)'Subr. ROADS:  Invalid MROAD = ',mroad
            stop
         endif
! ---    Duration of emission period within timestep (seconds)
         call DELTSEC(ndhr1,nsec1,ndhr2,nsec2,ntemit)
         temit=FLOAT(ntemit)
! ---    Apply transport constraints
         npnewt=ws*smxi1(md)*temit/dtsec+1
         npnewt=MIN(npnewt,mxnew)
         temit=temit/FLOAT(npnewt)
! ---    Update puff numbers
         npnew=npnew+npnewt
         newpuf=newpuf+npnewt
         if(mroad.EQ.1) then
            newrd1(i)=npnew
         elseif(mroad.EQ.2) then
            newrd2(i)=npnew
         else
            write(*,*)'Subr. ROADS:  Invalid MROAD = ',mroad
            stop
         endif
! ---    If puff arrays are full, remove puffs that are off the grid
! ---    and "roll down" arrays to make room for new puffs
! ---    (this also applies to the DA file of tabulated arrays)
         if(npuffs+newpuf.GT.mxpuff) call ROLLDN(np)
! ---    Check to see if roll-down was adequate
         if(npuffs+newpuf.GT.mxpuff)then
            write(io6,*) ' ERROR in subr. ROADS'
            write(io6,*)'Too many puffs on grid for array dimensions',&
            ' -- NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
            ' MXPUFF = ',mxpuff
            write(*,*)
            stop 'Halted in ROADS -- see list file.'
         endif
! ---    Assign final plume height
         heff=htrd
! ---    Assign sigma y, sigma z at the source
         szsrc=amax1(szmin,sz0rd)
         sysrc=amax1(symin,sy0rd)
! ---    Set variance associated with buoyancy-enhanced growth @ final
! ---    rise
         xf=0.0
         rise=0.0
         bidsq=0.0
! ---    Set downwash flag to zero (no building downwash)
         idw=0
!******
      if(ldb)then
         write(io6,*)
         write(io6,*)'Variable emissions step: ',it
         write(io6,204) i,ilayer,ixs,iys,md,tdk,ws,istab,dpbl,issta,&
          npnewt,newpuf,temit
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'TDK: ',f5.1,2x,'WS: ',f5.1,2x,&
          'ISTAB: ',i1,2x,'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,&
          'NPNEWT: ',i4,2x,'NEWPUF: ',i5/5x,'DT: ',f7.2)
         write(io6,210)idw,heff,rise,xf,sysrc,szsrc
210      format(5x,'IDW: ',i5,2x,'HEFF: ',f8.2,2x,'RISE : ',f8.2,2x,&
          'XF : ',f8.2,2x,'SYSRC: ',f8.2,2x,'SZSRC: ',f8.2)
         write(io6,208)i,np,iru,temit,sqrts
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,&
          'IRU: ',i2,2x,'DT: ',f7.2,2x,'SQRTS: ',f7.3)
! ---    Scaling information
         if(mroad.EQ.1 .AND. nsfrds.GT.0) then
           do is=1,nspec
             if(idsfrds(is,iroad).GT.0) write(io6,*)&
             '    Species#, Scaled Q(g/s): ',is,qrd(is)
           enddo
         endif
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnewt
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call SWAP(np,0)
! ---    Fill non-zero values
         xpb(np)=xrdgrd(1)
         ypb(np)=yrdgrd(1)
         xpe(np)=xrdgrd(2)
         ype(np)=yrdgrd(2)
         zitibl(np)=-1.0
         elbase(np)=0.5*(elrd(1)+elrd(2))
         sigyb(np)=sysrc
         sigzb(np)=szsrc
         sigye(np)=sysrc
         sigze(np)=szsrc
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xf
         bidfnl(np)=bidsq
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=heff
         temit0(np)=temit
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Set effective slug elongation speed to match length and
! ---    emitting time
         speed0(np)=rdlen/temit
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations (downwash)
         idw0(np)=idw
         heff20(np)=heff
         sy0(np)=sysrc
         sz0(np)=szsrc
         if(mroad.EQ.2) then
! ---       Place end-time of emission period for this puff into
! ---       TMTOTB(mxpuff) array, expressed as fraction of the timestep
! ---       This will be used in COMP when computing the sampling steps
! ---       used to advect the freshly emitted puff during remainder
! ---       of the timestep, and then reset.
! ---       Seconds from start of emissions period to end of timestep
            call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
            tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
         endif
! ---    Emission period index for this puff
         iemstep(np)=it
! ---    Puff is identified by its sequence number as it is released
! ---    from a road-segment this time step, the source number from
! ---    which the puff was released, and the source type
         jj=npnew-npnewt+j
         irlsnum(np)=jj
         isrcnum(np)=i
         isrctyp(np)=isourcetype(mroad)
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
! ---    Set partial penetration variables to null values
         fmix=1.0
         hmax=dpbl
         call INJECT(np,nspec,temit,mfact_rd,qrd,heff,hmax,&
                     dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer NULL tabulated arrays record to DA file
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
! --- End loop over emission periods within this timestep
100   continue
      return
      end
!----------------------------------------------------------------------
      subroutine sprayseg(itype)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918           SPRAYSEG
!                C. DesAutels
!
! --- PURPOSE:  Place spray segments that were saved to scratch file
!               into allocated storage array
!
!
! --- INPUTS:
!            ITYPE - integer    - Processing flag
!                                 1: Control File road data (not active)
!                                 2: SPEMARB.DAT file road data
!     /params/ variables:
!           iospx, io6
!     |mspray2| variables:
!           nspseg2
!
! --- OUTPUT:
!     |mspray2| variables:
!           nspseg2
!           ispray2(nspseg2),newsp2(nspseg2),splen2(nspseg2)
!           xsp2grd(2,nspseg2),ysp2grd(2,nspseg2),elsp2(2,nspseg2)
!
! --- SPRAYSEG called by: INITPUF
! --- SPRAYSEG calls:     none
!----------------------------------------------------------------------
! --- Modules
      use mspray2
!      implicit none
! --- Include parameters
      include 'params.puf'
! --- Arguments
      integer    :: itype
! --- Locals
      integer    :: i
! --- Process by type
! -------------------
      if(itype.EQ.2) then
! ---    Allocate the arrays for the spray segments from SPEMARB.DAT
         IF (ALLOCATED (ispray2)) DEALLOCATE (ispray2)
         IF (ALLOCATED (newsp2)) DEALLOCATE (newsp2)
         IF (ALLOCATED (splen2)) DEALLOCATE (splen2)
         IF (ALLOCATED (xsp2grd)) DEALLOCATE (xsp2grd)
         IF (ALLOCATED (ysp2grd)) DEALLOCATE (ysp2grd)
         IF (ALLOCATED (elsp2)) DEALLOCATE (elsp2)
         IF (ALLOCATED (iqtspray2)) DEALLOCATE (iqtspray2)
         
         ALLOCATE(ispray2(nspseg2),newsp2(nspseg2),splen2(nspseg2))
         ALLOCATE(xsp2grd(2,nspseg2),ysp2grd(2,nspseg2))
         ALLOCATE(elsp2(2,nspseg2),iqtspray2(nspseg2))
         ispray2=0
         newsp2=0
         splen2=0.0
         xsp2grd=0.0
         ysp2grd=0.0
         elsp2=0.0
         
! ---    Access records in the scratch file
         REWIND(iospx)
         do i=1,nspseg2
            read(iospx) ispray2(i),iqtspray2(i),splen2(i),&
                        xsp2grd(1,i),xsp2grd(2,i),&
                        ysp2grd(1,i),ysp2grd(2,i),&
                        elsp2(1,i),elsp2(2,i)
!            write(*,*) 'SPRAYSEG: i,ispray2(i),iqtspray2(i),splen2(i),
!     &                  xsp2grd(1,i),xsp2grd(2,i),
!     &                  ysp2grd(1,i),ysp2grd(2,i),
!     &                  elsp2(1,i),elsp2(2,i)'
!            write(*,*) i,ispray2(i),iqtspray2(i),splen2(i),
!     &                  xsp2grd(1,i),xsp2grd(2,i),
!     &                  ysp2grd(1,i),ysp2grd(2,i),
!     &                  elsp2(1,i),elsp2(2,i)
         enddo
      else
         write(io6,*)'ERROR in SPRAYSEG:  invalid data type'
         write(io6,*)'   Expected 2'
         write(io6,*)'   Found ',itype
         stop 'HALT in SPRAYSEG -- see list file'
      endif
! --- Done with scratch file
      CLOSE(iospx)
      return
      end
!----------------------------------------------------------------------
      subroutine sprays(mspray,iv,i,nspec,ndhrb,nsecb,ndhre,nsece,&
                       dtsec,smxi1,ldb,mfact,np,newpuf,problem)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918            SPRAYS
!                C. DesAutels
!
! --- PURPOSE:  Initialize newly released puff/slug variables for
!               discrete SPRAY SOURCES with constant emissions.
!               This source type always emits slugs in the way that
!               puffs are emitted:  They do not extend continuously
!               from a point during the release step, with a new and
!               old end.  Rather, they are emitted as 'old' slugs at
!               the start of the release step, and both ends are the
!               same age and have the same properties.
!
! --- UPDATES:
!
! --- INPUTS:
!         MSPRAY - integer - Flag for origin of spray source data
!                            1 = Control file (Not Active)
!                            2 = SPEMARB.DAT file (|mspray2|)
!            IV - integer - SPEMARB.DAT file index
!             I - integer - Spray-segment source index
!         NSPEC - real    - Number of species modeled
!         NDHRB - integer - Julian day, hour beginning current step
!         NSECB - integer - Seconds at beginning of current step
!         NDHRE - integer - Julian day, hour at end of current step
!         NSECE - integer - Seconds at end of current step
!         DTSEC - real    - Length (seconds) of a basic time step
! SMXI1(mxmetdom) - real  - Reciprocal of speed required to travel max
!                           allowed distance in 1 timestep (each grid)
!           LDB - logical - Control variable for debug write statements
!                           (T = write, F = do not write)
!         MFACT - integer - Stepping index to identify slugs vs puffs
!                           Should be SLUG for these roads
!            NP - integer - Index of last puff/slug initialized
!        NEWPUF - integer - Number of new puffs/slugs released
!                           during the current time step
!
!     /COMPARM/ variables:
!           XMXLEN, MXNEW, SYMIN, SZMIN, WSCALM, PLX0
!     /FLAGS/ variables:
!           MGAUSS, MSLUG, MHFTSZ
!     /GRID/ variables:
!           IBCOMP, IECOMP, JBCOMP, JECOMP
!     /METHR/ variables:
!           TEMPSS(mxss,mxmetdom), TEMP2D(mxnx,mxny,mxmetdom)
!           PLEXP
!     /METHD/ variables:
!           I2DMET
!     /PUFF/ variables:
!           NPUFFS
!     |MQSCALE| variables:
!           NQSFTAB,IQSFTYPE(nqsftab),QSFTAB(mxqsf,nqsftab)
!     |MSPRAY2| variables:
!           NSPSEG2, NSP2
!           HTSP2(mxqstep,nsp2),SZ0SP2(mxqstep,nsp2),
!           SY0SP2(mxqstep,nsp2),QSP2(mxspec,mxqstep,nsp2),
!           ISPRAY2(nspseg2),SPLEN2(nspseg2),ELSP2(2,nspseg2)
!           XSP2GRD(2,nspseg2),YSP2GRD(2,nspseg2),
!           CID8(nsp2),
!           NSTEP8(nspdat),NDHRQB8(mxqstep,nspdat),
!           NSECQB8(mxqstep,nspdat),NDHRQE8(mxqstep,nspdat),
!           NSECQE8(mxqstep,nspdat)
!
!     Parameters:
!           MXNX, MXNY, MXNZ, MXNZP1, MXSPEC, IO6
!           MXQSF, MXMETDOM, MXQSTEP
!
! --- OUTPUT:
!            NP - integer - UPDATED index of last puff/slug initialized
!        NEWPUF - integer - UPDATED number of new puffs/slugs released
!                           during the current time step
!       PROBLEM - logical - Set true if fatal problem encountered
!
!     |MSPRAY2| variables:
!           NEWSP2(nspseg2)
!     /PUFF/ variables:
!           all variables in module
!     /SLUG/ variables:
!           all variables in module
!
! --- SPRAYS called by:  INITPUF
! --- SPRAYS calls:      RLSMET, ROLLDN, SWAP, INJECT
!                       ZEROTAB, SRCTABOUT, GETCELL, RLSMET
!                       DELTSEC
!----------------------------------------------------------------------
! --- Modules
      use mspray2
      use mqscale
! --- Include parameters
      include 'params.puf'
! --- Include common blocks
      include 'comparm.puf'
      include 'flags.puf'
      include 'grid.puf'
      include 'methd.puf'
      include 'methr.puf'
      include 'puff.puf'
      include 'slug.puf'
      real xspgrd(2),yspgrd(2),elsp(2),qsp(mxspec)
      real smxi1(mxmetdom)
      logical ldb,problem
      integer isourcetype(2)
      data zero/0.0/
! --- Spray source type (14=control-file, 15=SPEMARB.DAT)
      data isourcetype/14,15/
! --- All spray emissions go into puff-like slugs
      data mfact_sp/10/
! --- Expect MFACT in this call to be for slugs
      if(mfact.NE.mfact_sp) then
         write(*,*)
         write(*,*)'SPRAY:  WARNING -- Spray sources always use a'//&
                   'SLUG release to represent the emitted cloud'
         write(*,*)'Ignoring MFACT that is passed;  Using MFACT_SP'
         write(*,*)'MFACT, MFACT_SP = ',mfact,mfact_sp
      endif
! --- Set source emission period range, and screen MSPRAY
! --- Spray source type is 14 (control-file) or 15 (SPEMARB.DAT)
! --- Control file inputs are not active.
      if(mspray.EQ.1) then
         nt=1
         itype=14
         write(io6,*)
         write(io6,*)'SPRAYS:  Invalid spray-source type: ',mspray
         write(io6,*)'        Valid types are 2 (SPEMARB.DAT)'
         write(io6,*)'     (control file inputs not implemented)'
         write(*,*)
         stop 'Halted in SPRAYS -- see list file.'
      elseif(mspray.EQ.2) then
         nt=nstep8(iv)
         itype=15
         if(ldb) write(io6,*)&
            'SPRAYS:  SPEMARB.DAT Source (Spray Segment) Number    ',i,&
            '    Spray = '//CID8(ispray2(i))
      else
         write(io6,*)
         write(io6,*)'SPRAYS: Invalid spray-source type: ',mspray
         write(io6,*)'        Valid types are 2 (SPEMARB.DAT)'
         write(io6,*)'     (control file inputs not implemented)'
         write(*,*)
         stop 'Halted in SPRAYS -- see list file.'
      endif
! --- Initialize number of new puffs from this source, this timestep
      npnew=0
! --- Loop over emission periods within this timestep
      do 100 it=1,nt
! ---    Initialize source tabulation specifications to zero
         call ZEROTAB
! ---    Assign variables for current source
         if(mspray.EQ.1) then
! ---       Control File
! ---       Not implemented
            write(io6,*) ' ERROR in subr. SPRAYS'
            write(io6,*)'Control File inputs not implemented'
            write(io6,*)'All inputs must use variable emissions'
            write(io6,*)'SPEMARB.DAT files'
            write(*,*)
            stop 'Halted in SPRAYS -- see list file.'
         elseif(mspray.EQ.2) then
! ---       SPEMARB.DAT
            if(ldb) write(io6,*) '   Emissions period ',it
            if (it.ne.iqtspray2(i)) then
              goto 100
            endif
! ---       Variables stored for each segment
            splen=splen2(i)
            do k=1,2
               xspgrd(k)=xsp2grd(k,i)
               yspgrd(k)=ysp2grd(k,i)
               elsp(k)=elsp2(k,i)
            enddo
! ---       Road variables associated with segment i
            ispray=ispray2(i)
            htsp=htsp2(it,ispray)
            sz0sp=sz0sp2(it,ispray)
            sy0sp=sy0sp2(it,ispray)
            dcutsp=dcutsp2(it,ispray)
            diasp=diasp2(it,ispray)
! ---       Select emission rate and convert to g/s
            do is=1,nspec
               qsp(is)=qsp2(is,it,ispray)*splen
! 
            if(ldb) write(io6,*)'   Spray,Species, Q(g/s) = ',&
                                 ispray,is,qsp(is)
! 
            enddo
         else
            write(*,*)'Subr. SPRAYS:  Invalid MSPRAY = ',mspray
            stop
         endif
! ---    Road center in MET grid 1 (outermost) units
         xcentgrd=0.5*(xspgrd(1)+xspgrd(2))
         ycentgrd=0.5*(yspgrd(1)+yspgrd(2))
! ---    Determine MET grid 1 (outermost) point closest to road center
         ixs=1.0+xcentgrd
         iys=1.0+ycentgrd
! ---    Source off the computational grid -- write a WARNING
         if(ixs.lt.ibcomp.or.ixs.gt.iecomp.or.iys.lt.jbcomp.or.iys.gt.&
            jecomp)then
            write(io6,20)i,ixs,iys
20          format(/1x,'Warning -- a spray-link segment is ',&
            'off the computational grid'/1x,'segment no. (i) = ',i6,&
            '  nearest grid point (ixs,iys) = (',i5,',',i5,')')
            problem=.TRUE.
            goto 100
         endif
! ---    Now determine the met. grid point closest to the source
         call GETCELL('SPRAYSS       ','GRIDIJ',1,xcentgrd,ycentgrd,&
                      zero,zero,ixs,iys,md)
! ---    Extract release height wind speed & other met. variables
! ---    Note: mfact0 is not used for spray-slugs and so remain as
! ---    slugs when released in calms
         call RLSMET(ldb,ixs,iys,md,htsp,mfact,&
                     mfact0,ilayer,ws,flow,ivec,istab,dpbl,el,&
                     ustr,wstr,tsigv,tsigw,iru,issta,sqrts,idopty,&
                     idoptz)
         if(i2dmet.EQ.1) then
            tdk=temp2d(ixs,iys,md)
         elseif(i2dmet.EQ.0) then
            tdk=tempss(issta,md)
         else
            write(*,*)'Subr. SPRAYS:  Invalid I2DMET = ',i2dmet
            stop
         endif
! ---    Trap missing power-law exponent (substitute default)
         if(plexp.LT.-900.) then
! ---       Use default
            plx=plx0(istab)
         else
! ---       Use it
            plx=plexp
         endif
! --------------------------------------------------------------------
! --- Do not generate puff for this step if all emissions are ZERO!
! --------------------------------------------------------------------
         qtot=0.0
         do is=1,nspec
            qtot=qtot+qsp(is)
         enddo
         if(qtot.EQ.0.0) then
            if(ldb) then
               write(io6,*)
               write(io6,*) 'No emissions this step from Source ',i
               write(io6,*)
            endif
            goto 100
         endif
! ---    Establish emitting time
         if(mspray.EQ.1) then
! --- INACTIVE - CONTROL FILE INPUTS NOT ACCPETED IN THIS VERISON
           write(io6,*)
           write(io6,*)'ERROR in Control file :'
           write(io6,*)'Control File Spray Emissions not Implemented'
           stop 'Error in Control file -- see list file'
         elseif(mspray.EQ.2) then
! ---       Start-time for emission period during timestep
            if(ndhrqb8(it,iv).LT.ndhrb) then
               ndhr1=ndhrb
               nsec1=nsecb
            elseif(ndhrqb8(it,iv).EQ.ndhrb .AND.&
                   nsecqb8(it,iv).LT.nsecb) then
               ndhr1=ndhrb
               nsec1=nsecb
            else
               ndhr1=ndhrqb8(it,iv)
               nsec1=nsecqb8(it,iv)
            endif
! ---       End-time for emission period during timestep
            if(ndhrqe8(it,iv).GT.ndhre) then
               ndhr2=ndhre
               nsec2=nsece
            elseif(ndhrqe8(it,iv).EQ.ndhre .AND.&
                   nsecqe8(it,iv).GT.nsece) then
               ndhr2=ndhre
               nsec2=nsece
            else
               ndhr2=ndhrqe8(it,iv)
               nsec2=nsecqe8(it,iv)
            endif
         else
            write(*,*)'Subr. SPRAYS:  Invalid MSPRAY = ',mspray
            stop
         endif
! ---    Duration of emission period within timestep (seconds)
         call DELTSEC(ndhr1,nsec1,ndhr2,nsec2,ntemit)
         temit=FLOAT(ntemit)
! ---    Apply transport constraints
         npnewt=ws*smxi1(md)*temit/dtsec+1
         npnewt=MIN(npnewt,mxnew)
         temit=temit/FLOAT(npnewt)
! ---    Update puff numbers
         npnew=npnew+npnewt
         newpuf=newpuf+npnewt
         if(mspray.EQ.2) then
            newsp2(i)=npnew
         else
            write(*,*)'Subr. SPRAYS:  Invalid MSPRAY = ',mspray
            stop
         endif
! ---    If puff arrays are full, remove puffs that are off the grid
! ---    and "roll down" arrays to make room for new puffs
! ---    (this also applies to the DA file of tabulated arrays)
         if(npuffs+newpuf.GT.mxpuff) call ROLLDN(np)
! ---    Check to see if roll-down was adequate
         if(npuffs+newpuf.GT.mxpuff)then
            write(io6,*) ' ERROR in subr. SPRAYS'
            write(io6,*)'Too many puffs on grid for array dimensions',&
            ' -- NPUFFS (old puffs) = ',npuffs,' NEWPUF = ',newpuf,&
            ' MXPUFF = ',mxpuff
            write(*,*)
            stop 'Halted in SPRAYS -- see list file.'
         endif
! ---    Assign final plume height
         heff=htsp
! ---    Assign sigma y, sigma z at the source
         szsrc=amax1(szmin,sz0sp)
         sysrc=amax1(symin,sy0sp)
! ---    Set variance associated with buoyancy-enhanced growth @ final
! ---    rise
         xf=0.0
         rise=0.0
         bidsq=0.0
! ---    Set downwash flag to zero (no building downwash)
         idw=0
!******
      if(ldb)then
         write(io6,*)
         write(io6,*)'Variable emissions step: ',it
         write(io6,204) i,ilayer,ixs,iys,md,tdk,ws,istab,dpbl,issta,&
          npnewt,newpuf,temit
204      format(5x,'IS: ',i5,2x,'ILAYER: ',i3,2x,'IXS: ',i4,2x,&
          'IYS: ',i4,2x,'MD: ',i3,2x,'TDK: ',f5.1,2x,'WS: ',f5.1,2x,&
          'ISTAB: ',i1,2x,'DPBL: ',f7.1,2x,'ISSTA: ',i4,2x,&
          'NPNEWT: ',i4,2x,'NEWPUF: ',i5/5x,'DT: ',f7.2)
         write(io6,210)idw,heff,rise,xf,sysrc,szsrc
210      format(5x,'IDW: ',i5,2x,'HEFF: ',f8.2,2x,'RISE : ',f8.2,2x,&
          'XF : ',f8.2,2x,'SYSRC: ',f8.2,2x,'SZSRC: ',f8.2)
         write(io6,208)i,np,iru,temit,sqrts
208      format(5x,'I: ',i5,2x,'NP: ',i5,2x,&
          'IRU: ',i2,2x,'DT: ',f7.2,2x,'SQRTS: ',f7.3)
      endif
!*****
! ----------------------------------------------------------
! ---    Initialize current hour's new puffs for this source
! ----------------------------------------------------------
         do 50 j=1,npnewt
         np=np+1
!
! ---    Use SWAP to start with all puff/slug variables equal to zero
         call SWAP(np,0)
! ---    Fill non-zero values
         xpb(np)=xspgrd(1)
         ypb(np)=yspgrd(1)
         xpe(np)=xspgrd(2)
         ype(np)=yspgrd(2)
         zitibl(np)=-1.0
         elbase(np)=0.5*(elsp(1)+elsp(2))
         sigyb(np)=sysrc
         sigzb(np)=szsrc
         sigye(np)=sysrc
         sigze(np)=szsrc
         dropdia(np)=diasp
         dropdcut(np)=dcutsp
         zfinal(np)=heff
         zpb(np)=heff
         zpe(np)=heff
         xfinal(np)=xf
         bidfnl(np)=bidsq
         isplit(np)=1
! ---    Time-of-release data
         ht0(np)=heff
         temit0(np)=temit
         ws0(np)=ws
         istab0(np)=istab
         sqrts0(np)=sqrts
         iru0(np)=iru
         sigv0(np)=tsigv
         sigw0(np)=tsigw
         el0(np)=el
         plexp0(np)=plx
! ---    Set vector/scalar ws ratio to 1.0
         srat0(np)=1.0
! ---    Set effective slug elongation speed to match length and
! ---    emitting time
         speed0(np)=splen/temit
! ---    Additional time-of-release parameters needed for
! ---    receptor-specific calculations (downwash)
         idw0(np)=idw
         heff20(np)=heff
         sy0(np)=sysrc
         sz0(np)=szsrc
         if(mspray.EQ.2) then
! ---       Place end-time of emission period for this puff into
! ---       TMTOTB(mxpuff) array, expressed as fraction of the timestep
! ---       This will be used in COMP when computing the sampling steps
! ---       used to advect the freshly emitted puff during remainder
! ---       of the timestep, and then reset.
! ---       Seconds from start of emissions period to end of timestep
            call DELTSEC(ndhr1,nsec1,ndhre,nsece,ntot)
            tmtotb(np)=(FLOAT(ntot)-temit*j)/dtsec
         endif
! ---    Emission period index for this puff
         iemstep(np)=it
! ---    Puff is identified by its sequence number as it is released
! ---    from a spray-segment this time step, the source number from
! ---    which the puff was released, and the source type
         jj=npnew-npnewt+j
         irlsnum(np)=jj
         isrcnum(np)=i
         isrctyp(np)=isourcetype(mspray)
! ------------------------------------------------------------------
! ---    Inject emissions into proper layer (above/below mixing ht.)
! ------------------------------------------------------------------
! ---    Set partial penetration variables to null values
         fmix=1.0
         hmax=dpbl
         call INJECT(np,nspec,temit,mfact_sp,qsp,heff,hmax,&
                     dpbl,fmix,istab)
! ------------------------------------------------------------------
! ---    Transfer NULL tabulated arrays record to DA file
! ------------------------------------------------------------------
         call SRCTABOUT(np,isrctyp(np),isrcnum(np),irlsnum(np))
50       continue
! --- End loop over emission periods within this timestep
100   continue
      return
      end
!----------------------------------------------------------------------
      subroutine RDHDEM8(iunit0,i,lprt,lnsp2)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918           RDHDEM8
!                C.DesAutels
!
! --- PURPOSE:  Read the header records from the SPEMARB spray source
!               variable emissions file
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 SPEMARB file
!                I - integer    - Index for current SPEMARB file
!             LPRT - logical    - Flag controlling printing of header
!                                 record data (F=suppress, T=print)
!            LNSP2 - logical    - Flag indicating all sources found
!
!     |MSPRAY2| variables:
!            NSP2
!     Common Block /DATASET/ variables:
!            VERSPARB(mxemdat)
!     Common Block /DATEHR/ variables:
!            XBTZ
!     Common Block /QA/ variables:
!            NCOMMOUT
!     Parameters:
!            IO6, MXSPEC, MXEMDAT, MXQSTEP, IOX
!
! --- OUTPUT:
!
!     |MSPRAY2| variables:
!           NSE8,NSP2,
!           IBSRC8(mxemdat),IESRC8(mxemdat),
!           IBDATHR8(mxemdat),IBSEC8(mxemdat),IEDATHR8(mxemdat),
!           IESEC8(mxemdat),XTZ8(mxemdat),T2BTZ8(mxemdat),
!           XMWEM8(mxspec),IXREM8(mxspec),
!           iutmznsp2(mxemdat),feastsp2(mxemdat),fnorthsp2(mxemdat),
!           rnlat0sp2(mxemdat),relon0sp2(mxemdat),
!           rnlat1sp2(mxemdat),rnlat2sp2(mxemdat),NSTEP8(mxemdat),
!           NDHRQB8(mxqstep,mxemdat),NSECQB8(mxqstep,mxemdat),
!           NDHRQE8(mxqstep,mxemdat),NSECQE8(mxqstep,mxemdat),
!           CSLST8(mxspec),
!           pmapsp2(mxemdat),utmhemsp2(mxemdat),datumsp2(mxemdat),
!           datensp2(mxemdat),xyunitsp2(mxemdat)
!
! --- RDHDEM8 called by: SETUP
! --- RDHDEM8 calls:     YR4, XTRACTLL, INCR, TCLOCK, GEOFMT
!----------------------------------------------------------------------
! --- Modules
      use mspray2
!      implicit none
      include 'params.puf'
      include 'dataset.puf'
      include 'datehr.puf'
      include 'qa.puf'
! --- Arguments
      LOGICAL :: lprt, lnsp2
      integer :: iunit0,i
! --- Locals
      LOGICAL :: lmatch
      LOGICAL :: lutmsp2,llccsp2,lpssp2,lemsp2,llazasp2,lttmsp2
      integer :: nlim,iunit,ncom,ncommout
      integer :: k,n,is
      integer :: ibtzhh,nsp2file
      integer :: ibyr,ibdy,ibhr,ieyr,iedy,iehr,idum,ishift
      integer :: ierrb,ierre
      real    :: zero
      real xmwem8x(mxspec)
      character*8 axtz8
      character*12 fname8,label8(mxemdat)
      character*12 cslst8x(mxspec),vrs8
      character*16 dataset,dataver
      character*16 clat0,clon0,clat1,clat2
      character*33 blank33,break33
      character*64 datamod
      character*132 comment1,blank,break
      data blank33/'                                 '/
      data break33/'.................................'/
      data nlim/1/
      data zero/0.0/
! --- Set blank (132 characters)
      blank(1:33)=blank33
      blank(34:66)=blank33
      blank(67:99)=blank33
      blank(100:132)=blank33
! --- Set break (132 characters)
      break(1:33)=break33
      break(34:66)=break33
      break(67:99)=break33
      break(100:132)=break33
! --- Begin scratch file image
      write(iox,'(a132)') blank
      write(iox,'(a132)') break
      write(iox,'(a132)') blank
      ncommout=ncommout+3
! --- Set unit number
      iunit=iunit0+i-1
! --- Initialize map projection logicals
      lutmsp2=.FALSE.
      llccsp2=.FALSE.
      lpssp2=.FALSE.
      lemsp2=.FALSE.
      llazasp2=.FALSE.
      lttmsp2=.FALSE.
! --- Initialize other variables
      datensp2(i)='02-21-2003  '
      xyunitsp2(i)='KM  '
      ibsec8(i)=0.0
      iesec8(i)=0.0
      feastsp2(i)=0.0
      fnorthsp2(i)=0.0
      clat0='0.0N            '
      clon0='0.0W            '
      clat1='0.0N            '
      clat2='0.0N            '
      dataset='                '
      dataver='                '
      pmapsp2(i)='XXX     '
      iutmznsp2(i)=1
      utmhemsp2(i)='N   '
      datumsp2(i)='XXX     '
      axtz8='UTC+0000'
!     -------------------------------
      if(versparb(i).EQ.'1.0') then
!     -------------------------------
! ---    First record and comments
         read(iunit,'(2a16,a64)') dataset,dataver,datamod
         write(iox,'(2a16,a64)') dataset,dataver,datamod
         read(iunit,*) ncom
         write(iox,*) ncom
         do k=1,ncom
            comment1=blank
            read(iunit,'(a132)') comment1
            write(iox,'(a132)') comment1
         enddo
         ncommout=ncommout+2+ncom
! ---    Map projection
         read(iunit,'(a8)') pmapsp2(i)
         write(iox,'(a8)') pmapsp2(i)
         ncommout=ncommout+1
! ---    Condition map projection (rest are dummies)
         call GEOFMT(dataset,dataver,pmapsp2(i),iutmznsp2(i),&
                     utmhemsp2(i),clat0,clon0,clat1,clat2,&
                     datumsp2(i),datensp2(i),xyunitsp2(i),axtz8)
         if(pmapsp2(i).EQ.'UTM     ')  lutmsp2 =.TRUE.
         if(pmapsp2(i).EQ.'LCC     ')  llccsp2 =.TRUE.
         if(pmapsp2(i).EQ.'PS      ')  lpssp2  =.TRUE.
         if(pmapsp2(i).EQ.'EM      ')  lemsp2  =.TRUE.
         if(pmapsp2(i).EQ.'LAZA    ')  llazasp2=.TRUE.
         if(pmapsp2(i).EQ.'TTM     ')  lttmsp2 =.TRUE.
! ---    Map projection parameters
         if(LUTMSP2) then
            read(iunit,'(i4,a4)') iutmznsp2(i),utmhemsp2(i)
            write(iox,'(i4,a4)') iutmznsp2(i),utmhemsp2(i)
            ncommout=ncommout+1
         elseif(LLCCSP2) then
            read(iunit,'(4a16)') clat0,clon0,clat1,clat2
            write(iox,'(4a16)')  clat0,clon0,clat1,clat2
            ncommout=ncommout+1
         elseif(LPSSP2) then
            read(iunit,'(3a16)') clat0,clon0,clat1
            write(iox,'(3a16)')  clat0,clon0,clat1
            ncommout=ncommout+1
         elseif(LEMSP2.or.LLAZASP2.or.LTTMSP2) then
            read(iunit,'(2a16)') clat0,clon0
            write(iox,'(2a16)')  clat0,clon0
            ncommout=ncommout+1
         endif
! ---    Map false Easting/Northing
         if(LLCCSP2.or.LLAZASP2.or.LTTMSP2) then
            read(iunit,*) feastsp2(i),fnorthsp2(i)
            write(iox,*)  feastsp2(i),fnorthsp2(i)
            ncommout=ncommout+1
         else
            feastsp2(i)=0.0
            fnorthsp2(i)=0.0
         endif
! ---    Map DATUM
         read(iunit,'(a8,a12)') datumsp2(i),datensp2(i)
         write(iox,'(a8,a12)')  datumsp2(i),datensp2(i)
         ncommout=ncommout+1
! ---    Map units
         read(iunit,'(a4)') xyunitsp2(i)
         write(iox,'(a4)')  xyunitsp2(i)
         ncommout=ncommout+1
! ---    Time zone
         read(iunit,'(a8)') axtz8
         write(iox,'(a8)')  axtz8
         ncommout=ncommout+1
! ---    Condition remaining variables
         call GEOFMT(dataset,dataver,pmapsp2(i),iutmznsp2(i),&
                     utmhemsp2(i),clat0,clon0,clat1,clat2,&
                     datumsp2(i),datensp2(i),xyunitsp2(i),axtz8)
! ---    Process Time zone
         read(axtz8(4:6),'(i3)') ibtzhh
         if(ibtzhh.LT.-13 .OR. ibtzhh.GT.13) then
            write(io6,*)'FATAL Error in RDHDEM8 -- Time Zone'
            write(io6,*)'Expected UTC-1300 to UTC+1300'
            write(io6,*)'Found: ',axtz8
            stop 'Halted in RDHDEM8 -- see list file'
         endif
         xtz8(i)=-FLOAT(ibtzhh)
! ---    Time period
         read(iunit,*)ibyr,ibdy,ibhr,ibsec8(i),&
                      ieyr,iedy,iehr,iesec8(i)
! ---    Enforce YYYY format for year
         call YR4(io6,ibyr,ierrb)
         call YR4(io6,ieyr,ierre)
         if(ierrb.NE.0 .OR. ierre.NE.0) stop 'Halted in RDHDEM8'
         write(iox,'(9i6)')ibyr,ibdy,ibhr,ibsec8(i),&
                           ieyr,iedy,iehr,iesec8(i)
         ncommout=ncommout+1
! ---    Number of sources and species
         read(iunit,*) nsp2file,nse8x
         write(iox,*) nsp2file,nse8x
         ncommout=ncommout+1
! ---    Species list
         read(iunit,*)(cslst8x(n),n=1,nse8x)
! ---    Molecular weights of each emitted species
         read(iunit,*)(xmwem8x(n),n=1,nse8x)
!     -----
      else
!     -----
         write(*,*)'ERROR in SPEMARB File: ',i
         write(*,*)'Bad Dataset Version: ',versparb(i)
         write(*,*)'Expected 1.0'
         stop
!     -----
      endif
!     -----
! --- Species info to scratch file
      do n=1,nse8x
         write(iox,'(a16,f12.4)')cslst8x(n),xmwem8x(n)
      enddo
      ncommout=ncommout+nse8x
! --- Condition times
      idum=-999
      call TCLOCK(io6,idum,ibyr,ibdy,ibhr,ibsec8(i))
      call TCLOCK(io6,idum,ieyr,iedy,iehr,iesec8(i))
! --- Apply time zone adjustment to date/times
      if(xtz8(i).GE.-13 .AND. xtz8(i).LE.13) then
         t2btz8(i)=xtz8(i)-xbtz
         ishift=NINT(t2btz8(i))
         call INCR(io6,ibyr,ibdy,ibhr,ishift)
         call INCR(io6,ieyr,iedy,iehr,ishift)
      else
         write(io6,*)
         write(io6,*)'ERROR in SPEMARB.DAT file : Bad Time Zone'
         write(io6,*)'Expected zone between -13 and 13'
         write(io6,*)'Found zone = ',xtz8(i)
         stop 'Bad Time Zone in SPEMARB.DAT file -- see list file'
      endif
! --- Form date-hr variables for |MROAD2|
      ibdathr8(i)=ibyr*100000+ibdy*100+ibhr
      iedathr8(i)=ieyr*100000+iedy*100+iehr
! --- Process lat/lon character variables to extract content
      call XTRACTLL(io6,'LAT ',clat1,rnlat1sp2(i))
      call XTRACTLL(io6,'LAT ',clat2,rnlat2sp2(i))
      call XTRACTLL(io6,'LAT ',clat0,rnlat0sp2(i))
      call XTRACTLL(io6,'LON ',clon0,relon0sp2(i))
! --- Set source index range for this file
      if(i.EQ.1) then
         ibsrc8(i)=1
         iesrc8(i)=nsp2file
      else
         ibsrc8(i)=iesrc8(i-1)+1
         iesrc8(i)=iesrc8(i-1)+nsp2file
      endif
! --- Set logical to indicate if the number of sources specified in
! --- the control file (NSP2) have been found
      lnsp2=.FALSE.
      if(nsp2.EQ.iesrc8(i)) lnsp2=.TRUE.
! --- Process data that must be identical in all SPEMARB.DAT files
      if(i.EQ.1) then
! ---    Pass into |MSPRAY2| names
         nse8=nse8x
!
         if(nse8.gt.mxspec)then
            write(io6,11)nse8,mxspec
11          format(/1x,'ERROR in SUBR. RDHDEM8 -- No. emitted species',&
            ' in SPEMARB file is greater than current array',&
            ' dimension'/1x,&
            '     No. emitted species in file: ',i5/1x,&
            'Current Array dimension (MXSPEC): ',i5)
            write(*,*)
            stop 'Halted in RDHDEM8 -- see list file.'
         endif
      else
! ---    Check for consistency with first file
         if(nse8.NE.nse8x) then
            write(io6,12)nse8,nse8x
12          format(/1x,'ERROR in SUBR. RDHDEM8 -- No. emitted species',&
            ' must match in all SPEMARB files',/1x,&
            '     No. emitted species expected/found: ',2i5)
            write(*,*)
            stop 'Halted in RDHDEM8 -- see list file.'
         endif
      endif
! --- Pass into |MSPRAY2| names or check for consistency
      if(i.EQ.1) then
         do is=1,nse8
            cslst8(is)=cslst8x(is)
            xmwem8(is)=xmwem8x(is)
         enddo
      else
         lmatch=.TRUE.
         do is=1,nse8
            if(cslst8(is).NE.cslst8x(is)) lmatch=.FALSE.
            if(xmwem8(is).NE.xmwem8x(is)) lmatch=.FALSE.
         enddo
         if(.not.LMATCH) then
            write(io6,15)
15          format(/1x,'ERROR in SUBR. RDHDEM8 -- Species names and',&
            ' molecular weights must match in all SPEMARB files',/)
            write(io6,*)
            write(io6,*)'SPEMARB.DAT File:    1'
            do is=1,nse8
               write(io6,*) cslst8(is),xmwem8(is)
            enddo
            write(io6,*)
            write(io6,*)'SPEMARB.DAT File:',i
            do is=1,nse8
               write(io6,*) cslst8x(is),xmwem8x(is)
            enddo
            write(*,*)
            stop 'Halted in RDHDEM8 -- see list file.'
         endif
      endif
! --- WRITE CONTENTS OF HEADER RECORDS (if requested)
      if(lprt)then
         write(io6,102) i
102      format(///1x,13('----------')//1x,'Header record data from ',&
         'the SPEMARB road source file',i5)
         write(io6,103)dataset,ibsrc8(i),iesrc8(i),nse8x,axtz8,&
               ibdathr8(i),ibsec8(i),iedathr8(i),iesec8(i),pmapsp2(i),&
               datumsp2(i)
103      format(/1x,'DATASET: ',a16/1x,'IBSRC8:  ',i9/&
         1x,'IESRC8:  ',i9/1x,'NSE78    ',i6/1x,'AXTZ8:   ',a8/&
         1x,'IBDATHR8:',i9/1x,'IBSEC8:  ',i4/&
         1x,'IEDATHR8:',i9/1x,'IESEC8:  ',i4/&
         1x,'MAP8:    ',a8/1x,'DATUM:   ',a8)
         if(LUTMSP2) write(io6,104) iutmznsp2(i),utmhemsp2(i)
         if(LLCCSP2) write(io6,105) clat0,clon0,clat1,clat2,&
                                    feastsp2(i),fnorthsp2(i)
         if(LPSSP2) write(io6,106) clat0,clon0,clat1
         if(LEMSP2) write(io6,107) clat0,clon0
         if(LLAZASP2.or.LTTMSP2) write(io6,108) clat0,clon0,&
                                 feastsp2(i),fnorthsp2(i)
104      format(/1x,'UTMZN:   ',i4/1x,'UTMHEM:  ',a4)
105      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16/1x,'Lat2:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
106      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'Lat1:    ',a16)
107      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16)
108      format(/1x,'Lat0:    ',a16/1x,'Lon0:    ',a16/&
                 1x,'False_E: ',f15.3/1x,'False_N: ',f15.3)
         write(io6,109)
109      format(4x,'SPECIES',6x,'MOL. WT.'/)
         do is=1,nse8
            write(io6,110)cslst8(is),xmwem8(is)
         enddo
110      format(1x,a12,3x,f7.3)
      endif
! --- Initialize "previous" period times for |MSPRAY2|
      nstep8(i)=1
      ndhrqb8(1,i)=-000100100
      nsecqb8(1,i)=0
      ndhrqe8(1,i)=ibdathr8(i)
      nsecqe8(1,i)=ibsec8(i)
      return
      end
!----------------------------------------------------------------------
      subroutine RDTIEM8(iunit0,nsp2,nspdat,i,ibsrc8,iesrc8,avrs8,&
                         ldb,io6,iox,nspseg2,cid8,ncommout)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 150918           RDTIEM8
!                C. DesAutels
!
! --- PURPOSE:  Read the time-invariant records of a SPEMARB spray
!               source file (arbitrarily-varying properties)
!
! --- INPUTS:
!           IUNIT0 - integer    - Fortran unit number for the FIRST
!                                 SPEMARB file
!             NSP2 - integer    - Number of SPEMARB sources
!           NSPDAT - integer    - Number of SPEMARB files
!                I - integer    - Current SPEMARB file
!   IBSRC8(nspdat) - integer    - Source ID for first source in each
!                                 SPEMARB file
!   IESRC8(nspdat) - integer    - Source ID for last source in each
!                                 SPEMARB file
!    AVRS8(nspdat) - char*16    - SPEMARB data-set version
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!         NCOMMOUT - integer    - Currrent number of header comments
!                                 in scratch file
!
! --- OUTPUT:
!       CID8(nsp2) - char*16    - SPEMARB source identifiers
!
! --- RDTIEM8 called by: SETUP
! --- RDTIEM8 calls:     GLOBE, GRIDROAD
!----------------------------------------------------------------------
      implicit none
! --- Arguments
      logical :: ldb
      integer :: iunit0,nsp2,nspdat,i,io6,iox,nspseg2,ncommout
      integer :: ibsrc8(nspdat),iesrc8(nspdat)
      character(len=16) :: cid8(nsp2),avrs8(nsp2)
! --- Locals
      integer :: n,k,ii,kk,numpoints
      integer :: iutmz, iunit
      character(len=4) :: c4hem
      real    :: tiem8(8)
! --- Process file "i"
      iunit=iunit0+i-1
! --- Loop over sources
      do k=ibsrc8(i),iesrc8(i)
         ii=MIN(k,nsp2)
         if(avrs8(i).EQ.'1.0') then
            read(iunit,*)cid8(ii)
            write(iox,'(a16,i15)') cid8(ii)
            ncommout=ncommout+1
         else
            write(io6,*)'RDTIEM8:  Bad SPEMARB.DAT dataset version'
            write(io6,*)'          Expected 1.0'
            write(io6,*)'          Found    ',avrs8(i)
            stop 'Halted in RDTIEM8 -- see list file'
         endif
      enddo
! --- Perform final QA after LAST file is processed
      if(i.NE.nspdat) return
      if(ldb)then
         write(io6,*)
         write(io6,*)
         write(io6,*)'Time-invariant SPEMARB data'
         write(io6,*)'-----------------------------'
         do k=1,nsp2
            write(io6,*)'Source = ',k,'CID8  = ',cid8(k)
         enddo
      endif
! --- Check number of sources
      if(iesrc8(nspdat).NE.nsp2) then
         write(io6,*)'FATAL Error in RDTIEM8:  Wrong number of sources'
         write(io6,*)'         Control file NSP2 = ',nsp2
         write(io6,*)'SPEMARB.DAT Time-Invariant = ',iesrc8(nspdat)
         write(*,*)
         stop 'Halted in RDTIEM8 -- see list file.'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine RDEM8(iunit,nsrc8,nse8,cid8,ibsrc8,iesrc8,vers,&
                       tzhr,ldb,iolst,ibegh,ibegs,iendh,iends,em8dat,&
                       ieof)
!----------------------------------------------------------------------
!
 
! --- CALPUFF    Version: 7.3.1        Level: 150918             RDEM8
!                C. DesAutels
!
! --- PURPOSE:  Read the time-varying records of one SPEMARB spray
!               source emissions file (arbitrarily-varying emissions)
!
! --- INPUTS:
!            IUNIT - integer    - Fortran unit number of SPEMARB file
!            NSRC8 - integer    - Total number of sources in SPEMARB
!                                 files
!             NSE8 - integer    - Number of emitted species
!      CID8(nsrc8) - char*16    - Source identifiers
!           IBSRC8 - integer    - Index for 1st source in current file
!           IESRC8 - integer    - Index for Last source in current file
!             VERS - char*16    - Dataset version for file
!             TZHR - real       - Hours to ADD to times in file to
!                                 convert to BASE TIME ZONE
!              LDB - logical    - Flag controlling printing of debug
!                                 data
!              IO6 - integer    - Fortran unit number for debug output
!
! --- OUTPUT:
!      IBEGH,IBEGS - integer    - Starting time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
!      IENDH,IENDS - integer    - Ending time for which emissions
!                                 data in current set of
!                                 records is valid (YYYYJJJHH & SSSS)
!                                 in BASE TIME ZONE
! EM8DAT(NSE8+3,nsrc8)- real array - Time-varying SPEMARB source data
!             IEOF - integer    - End-of-file flag
!
! --- RDEM8 called by: INITPUF
! --- RDEM8 calls:     YR4, INCR, TCLOCK
!----------------------------------------------------------------------
!
      include 'params.puf'
      real em8dat(nse8+14,nsrc8)
      character*16 cid, cid8(nsrc8), vers
      logical ldb, lproblem
      integer :: n,k,ii,kk,numpoints
      integer :: iutmz, iunit
      character(len=4) :: c4hem
! --- Initialize EOF flag
      ieof=0
! --- Read starting, ending date and time
      if(vers.EQ.'1.0') then
         read(iunit,*,end=999)ibyr,ibdy,ibhr,ibegs,&
                              ieyr,iedy,iehr,iends
      else
         write(iolst,*)
         write(iolst,*)'RDEM8: Invalid Dataset Version for SPEMARB.DAT'
         write(iolst,*)'Expected 1.0'
         write(iolst,*)'Found: ',vers
         write(*,*)
         stop 'Halted in RDEM8 -- see list file.'
      endif
! --- Enforce YYYY format for year
      call YR4(iolst,ibyr,ierrb)
      call YR4(iolst,ieyr,ierre)
      if(ierrb.NE.0 .OR. ierre.NE.0) then
         write(*,*)
         stop 'Halted in RDEM8'
      endif
! --- Condition times
      idum=-999
      call TCLOCK(iolst,idum,ibyr,ibdy,ibhr,ibegs)
      call TCLOCK(iolst,idum,ieyr,iedy,iehr,iends)
! --- Adjust times to BASE TIME ZONE
      ishift=NINT(tzhr)
      call INCR(iolst,ibyr,ibdy,ibhr,ishift)
      call INCR(iolst,ieyr,iedy,iehr,ishift)
! --- Form date-hour
      ibegh=ibyr*100000+ibdy*100+ibhr
      iendh=ieyr*100000+iedy*100+iehr
! --- Loop over sources
      lproblem = .FALSE.
      nwords=nse8+14
      do i=ibsrc8,iesrc8
         read(iunit,*)cid,(em8dat(n,i),n=1,nwords)
         if(cid.NE.cid8(i)) lproblem=.TRUE.
      enddo
! --- Check for source problem
      if(lproblem) then
         write(iolst,*)
         write(iolst,*)'RDEM8: Problem with source names in SPEMARB.DAT'
         write(iolst,*)'Names in data record do not match header record'
         write(iolst,*)'Beginning time: ',ibegh,ibegs
         write(iolst,*)'Ending time   : ',iendh,iends
         write(*,*)
         stop 'Halted in RDEM8 -- see list file.'
      endif
!
      if(ldb)then
         write(iolst,*)
         write(iolst,*)'Time-varying SPEMARB data'
         write(iolst,*)'Beginning time: ',ibegh,ibegs
         write(iolst,*)'Ending time   : ',iendh,iends
         write(iolst,*)'Source Range  : ',ibsrc8,iesrc8
         do i=ibsrc8,iesrc8
            write(iolst,*)'I = ',i,'EM8DAT = ',(em8dat(n,i),n=1,nwords)
         enddo
      endif
      return
! --- Reached end of SPEMARB.DAT file
999   ieof=1
      write(*,*)'RDEM8: Data in SPEMARB.DAT file end early'
      write(iolst,*)'RDEM8: Data in SPEMARB.DAT file end early'
      return
      end
!----------------------------------------------------------------------
      subroutine qfactor(iolst,itsf,itsrc, ws,kst,tdk, isf)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913           QFACTOR
!                D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Select emission rate factor index for current
!               conditions
!
! --- INPUTS:
!         IOLST - integer   - List-file output unit
!          ITSF - integer   - Type of scaling factor
!         ITSRC - integer   - Type of source (used for WS and T bins)
!            WS - real      - Wind speed (m/s)
!           KST - integer   - PG stability class (1-6)
!           TDK - real      - Temperature (K)
!
!     |mqscale| variables:
!           cqsftype(9), nqsftype
!           wqsf(5,13), tqsf(11,13)
!     common block /datehr/ variables:
!           nhrind,nyrb,nmob,ndayb,njulb
!
! --- OUTPUT:
!           ISF - integer - Index for scale-factor
!
! --- QFACTOR called by: ROADS1,POINTS1,AREAS,LINES1,VOLS,FLARES
! --- QFACTOR calls:     WEEK_DAY
!----------------------------------------------------------------------
! --- Modules
      use mqscale
      implicit none
! --- Include common blocks
      include 'datehr.puf'
! --- Arguments
      integer         :: iolst,itsf,itsrc,kst,isf
      real            :: ws,tdk
! --- Locals
      integer    :: iwsp,itdk,imon,iday,ihr24
      integer    :: i
      character(len=9) :: cday
! --- Check array index values from argument list
      if(itsf.GT.nqsftype .OR. itsf.LE.0) then
         write(iolst,*)'ERROR in QFACTOR:  invalid scale-factor type'
         write(iolst,*)'  Expected types 1 - ',nqsftype
         write(iolst,*)'  Found type ',itsf
         stop 'HALT in QFACTOR -- see list file'
      endif
      if(itsrc.GT.13 .OR. itsrc.LE.0) then
         write(iolst,*)'ERROR in QFACTOR:  invalid source type'
         write(iolst,*)'  Expected types 1 - 13'
         write(iolst,*)'  Found type ',itsrc
         stop 'HALT in QFACTOR -- see list file'
      endif
! --- Initialize index values
      iwsp=0
      itdk=0
      iday=0
      imon=nmob
      ihr24=nhrind
! --- Prepare date (conditional)
      if(INDEX(cqsftype(itsf),'DAY').GT.0) then
         call WEEK_DAY(iolst,'GREGOR',nyrb,nmob,ndayb,njulb,iday,cday)
      endif
! --- Prepare wind speed class (conditional)
! --- 6 classes are defined from 5 class boundaries
! --- (boundary is upper limit of class)
      if(INDEX(cqsftype(itsf),'WSP').GT.0) then
         iwsp=1
         do i=1,5
            if(ws.GT.wqsf(i,itsrc)) iwsp=i+1
         enddo
      endif
! --- Prepare temperature class (conditional)
! --- 12 classes are defined from 11 class boundaries
! --- (boundary is upper limit of class)
      if(INDEX(cqsftype(itsf),'TEMPERATURE').GT.0) then
         itdk=1
         do i=1,11
            if(tdk.GT.tqsf(i,itsrc)) itdk=i+1
         enddo
      endif
      SELECT CASE (TRIM(cqsftype(itsf)))
         CASE ("CONSTANT1")
         isf=1
         CASE ("MONTH12")
         isf=imon
         CASE ("DAY7")
         isf=iday
         CASE ("HOUR24")
         isf=ihr24
         CASE ("HOUR24_DAY7")
         isf=(iday-1)*24+ihr24
         CASE ("HOUR24_MONTH12")
         isf=(imon-1)*24+ihr24
         CASE ("WSP6")
         isf=iwsp
         CASE ("WSP6_PGCLASS6")
         isf=(MIN(6,kst)-1)*6+iwsp
         CASE ("TEMPERATURE12")
         isf=itdk
         CASE DEFAULT
! ---    Code is incomplete
         write(iolst,*)
         write(iolst,*)'ERROR in QFACTOR:  Scale-Factor ',cqsftype(itsf)
         write(iolst,*)'has not been coded (requires update!)'
         stop 'HALTED in QFACTOR -- see list file'
      END SELECT
! --- QA
      if(isf.LE.0) then
         write(iolst,*)
         write(iolst,*)'ERROR in QFACTOR:  Scale-Factor ',cqsftype(itsf)
         write(iolst,*)'returns invalid category = ', isf
         write(iolst,*)'ws,kst,tdk = ',ws,kst,tdk
         write(iolst,*)'nhrind,nyrb,nmob,ndayb = ',&
                        nhrind,nyrb,nmob,ndayb
         stop 'HALTED in QFACTOR -- see list file'
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine smatch(iolst,ctarget,num,carray,imatch)
!----------------------------------------------------------------------
!
! --- CALPUFF    Version: 7.3.1        Level: 140913            SMATCH
!                D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Identify index of member in array of strings that
!               matches the target string
!
! --- INPUTS:
!         IOLST - integer   - List-file output unit
!       CTARGET - char*     - Target string
!           NUM - integer   - Number of members in CARRAY
!   CARRAY(num) - char*     - Array of string values
!
! --- OUTPUT:
!       IMATCH - integer - Index for array member that matches target
!
! --- SMATCH called by: READCF
! --- SMATCH calls:     MK_CASE
!----------------------------------------------------------------------
      implicit none
! --- Arguments
      integer          :: iolst,num,imatch
      character(len=*) :: ctarget,carray(num)
! --- Locals
      integer    :: i
! --- Force target to be upper case
      call MK_CASE(iolst,'SMATCH','UPPER',ctarget)
! --- Loop over members
! --- Match string to get index
      imatch=0
      do i=1,num
         if(ctarget.EQ.carray(i)) imatch=i
      enddo
      if(imatch.EQ.0) then
         write(iolst,*)'ERROR in SMATCH -- Name ',ctarget,&
                       ' is not on the list:'
         do i=1,num
            write(iolst,*) carray(i)
         enddo
         stop 'HALT in SMATCH:  Bad Name -- see list file'
      endif
      return
      end
