      subroutine mk_case(iolst,cfrom,action,cvar)
!----------------------------------------------------------------------
!
! --- UTILITY   Version: 2.1.1          Level: 140219          MK_CASE
!               D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Imposes uniform case on a string
!
! --- UPDATES:
! --- v1.21 (130605) to v2.0 (140219)
!        1. Remove IERR argument and issue STOP within subroutine, and
!           add new string CFROM to argument to report calling-path
!           to list file.
! --- v1.0 (120518) to v1.21 (130605)
!        1. Revise local integer names to start with implicit range
!
! --- INPUT:
!               IOLST - integer   - Unit number of output list file
!               CFROM - char*     - Called-From string to report error
!              ACTION - char*     - Action to perform
!                                   (UPPER or LOWER)
!
! --- OUTPUT:
!
! --- INPUT/OUTPUT:
!                CVAR - char*     - String
!
! --- MK_CASE called by:  ()
! --- MK_CASE calls:      none
!----------------------------------------------------------------------
      implicit none
! --- Declare arguments
      character(len=*) :: cvar,action,cfrom
      integer :: iolst
! --- Declare locals
      integer :: iul,ilo(2),ihi(2),ishift(2)
      integer :: nc,ia,ic
! --- Define ASCII table pointers for conversion to UPPER and LOWER
! ---         (UPPER,LOWER)
      data ilo   / 97,65/
      data ihi   /122,90/
      data ishift/-32,32/
! --- Set conversion
      if(TRIM(action).EQ.'UPPER') then
         iul=1
      elseif(TRIM(action).EQ.'LOWER') then
         iul=2
      else
         write(iolst,*)'ERROR in MK_CASE -- Invalid ACTION'
         write(iolst,*)'   ACTION expected: UPPER or LOWER'
         write(iolst,*)'   ACTION found   : ',action
         write(iolst,*)'Called from  '//cfrom
         stop 'HALT in MK_CASE -- see list file'
      endif
! --- String length
      nc=LEN(cvar)
! --- Process string
      do ic=1,nc
         ia=IACHAR(cvar(ic:ic))
         if(ia.GE.ilo(iul) .AND. ia.LE.ihi(iul)) then
            ia=ia+ishift(iul)
            cvar(ic:ic)=ACHAR(ia)
         endif
      enddo
      return
      end
!----------------------------------------------------------------------
      subroutine rm_blank(cvar)
!----------------------------------------------------------------------
!
! --- UTILITY   Version: 2.1.1          Level: 120518         RM_BLANK
!               D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Moves all blanks to end of string
!
! --- INPUT:
!
! --- OUTPUT:
!
! --- INPUT/OUTPUT:
!                CVAR - char*     - String
!
! --- RM_BLANK called by:  ()
! --- RM_BLANK calls:      none
!----------------------------------------------------------------------
      implicit none
! --- Declare arguments
      character(len=*) :: cvar
! --- Declare locals
      integer :: nc,ic,ix
! --- Intrinsic fcn clears leading blanks
      cvar=ADJUSTL(cvar)
! --- March along string, overwriting any blanks
      nc=LEN_TRIM(cvar)
      ix=1
      do ic=1,nc
         if(cvar(ic:ic).NE.' ') then
            cvar(ix:ix)=cvar(ic:ic)
            ix=ix+1
         endif
      enddo
! --- Clear to end of string if needed
      if(ix.LE.nc) then
         do ic=ix,nc
            cvar(ic:ic)=' '
         enddo
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine delim(cdlim,cvar,id1,id2,ieq,lprocess)
!----------------------------------------------------------------------
!
! --- UTILITY   Version: 2.1.1          Level: 131218            DELIM
!               D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Determines if CVAR is a line containing a control-file
!               assignment between delimiters and if so, it returns
!               the position of the 2 delimiters and the equals sign.
!               Only the first assignment on a line is processed.
!
! --- UPDATES:
! --- v1.21 (130605) to v1.22 (131218)
!        1. If more than 2 delimiters are in string, accept just
!           the first 2 (do not halt)
!        2. If single delimiter is on the line, skip it (no halt)
! --- v1.0 (120518) to v1.21 (130605)
!        1. Revise delimiter character name to start with 'c'
!
! --- INPUT:
!               CDLIM - char*     - Delimiter (should be 1 character)
!                CVAR - char*     - String
!
! --- OUTPUT:
!                 ID1 - integer   - Position of 1st delimiter in CVAR
!                 ID2 - integer   - Position of next delimiter in CVAR
!                 IEQ - integer   - Position of '=' in CVAR
!            LPROCESS - logical   - True if a good assignment
!
! --- DELIM called by:  ()
! --- DELIM calls:      none
!----------------------------------------------------------------------
      implicit none
! --- Declare arguments
      logical          :: lprocess
      character(len=*) :: cdlim,cvar
      integer          :: id1,id2,ieq
! --- Locals
      integer          :: idx
      lprocess=.TRUE.
      ieq=0
      id1=0
      id2=0
! --- Find position of markers
      id1=INDEX(cvar,cdlim)
      if(id1.EQ.0) then
         lprocess=.FALSE.
         return
      endif
      id2=INDEX(cvar,cdlim,back=.TRUE.)
      if(id1.GT.0 .AND. id2.GT.id1) then
! ---    There are at least 2 delimiters
! ---    Check for another delimiter between these
! ---    and take the first if another is found
         idx =INDEX(cvar(id1+1:id2-1),cdlim)
         if(idx.GT.0) id2=id1+idx
! ---    Equals sign between these?
         ieq=INDEX(cvar(id1:id2),'=')
         if(ieq.GT.0) ieq=ieq+id1-1
      else
! ---    One delimiter found
         lprocess=.FALSE.
!         write(*,*)'ERROR in Control File'
!         write(*,*)'Line contains only 1 delimiter'
!         write(*,*)'Assignment between delimiters is expected'
!         write(*,*)TRIM(cvar)
!         stop 'HALTED in DELIM'
      endif
! --- Must have an equals sign to process
      if(ieq.EQ.0) then
         lprocess=.FALSE.
         return
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine cal_day(iolst,cfrom,action,iyear,month,iday,julday)
!----------------------------------------------------------------------
! --- UTILITY   Version: 2.1.1          Level: 140219          CAL_DAY
!               J. Scire, Exponent Inc.
!               D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Convert between Julian day and Gregorian date
!
! --- UPDATES:
! --- v1.21 (130605) to v2.0 (140219)
!        1. Remove IERR argument and issue STOP within subroutine, and
!           add new string CFROM to argument to report calling-path
!           to list file.
! --- v1.0 (120518) to v1.21 (130605)
!        1. Revise integer names to start with: i,j,k,l,m,n
!
! --- INPUTS:
!               IOLST - integer   - Unit number of output list file
!               CFROM - char*     - Called-From string to report error
!              ACTION - char*     - Action to perform
!                                   TOJULIAN returns Julian day
!                                   TOGREGOR returns Gregorian date
!
! --- OUTPUT:
!
! --- INPUT/OUTPUT:
!               IYEAR - integer   - Year (YYYY)
!               MONTH - integer   - Month (MM)
!                IDAY - integer   - Day (DD)
!              JULDAY - integer   - Julian day (JJJ)
!
! --- CAL_DAY called by:  ()
! --- CAL_DAY calls:      none
!----------------------------------------------------------------------
      implicit none
! --- Declare arguments
      character(len=*) :: action, cfrom
      integer          :: iolst,iyear,month,iday,julday
! --- Locals
      logical  :: ljulian
      integer  :: i,ndpmo(12,2),ncumday(12,2),leap,lastday(2),ierr
! --- Set number of days in each month (regular,leap)
      data ndpmo/31,28,31,30,31,30,31,31,30,31,30,31,&
                 31,29,31,30,31,30,31,31,30,31,30,31/
! --- Set number of days in year (regular,leap)
      data lastday/365,366/
! --- Set cumulative number of days to start of each
! --- month (regular,leap)
      data ncumday/0,31,59,90,120,151,181,212,243,273,304,334,&
                   0,31,60,91,121,152,182,213,244,274,305,335/
! --- Initializations
      ierr=0
! --- Set leap year index (1=regular, 2=leap)
      leap=1
      if(MOD(iyear,4).EQ.0) leap=2
! --- Set conversion
      if(action.EQ.'TOJULIAN') then
         ljulian=.TRUE.
! ---    QA inputs
         if(month.GT.12 .OR. month.LT.1) ierr=1
         if(ierr.EQ.0) then
            if(iday.LT.1 .OR. iday.GT.ndpmo(month,leap)) ierr=1
         endif
         if(ierr.NE.0) then
            write(iolst,*)'ERROR in CAL_DAY -- Invalid inputs'
            write(iolst,*)'      YEAR =',iyear
            write(iolst,*)'     MONTH =',month
            write(iolst,*)'       DAY =',iday
            write(iolst,*)'Called from  '//cfrom
            stop 'HALT in CAL_DAY -- see list file'
         endif
      elseif(action.EQ.'TOGREGOR') then
         ljulian=.FALSE.
! ---    QA inputs
         if(julday.GT.lastday(leap) .OR. julday.LT.1) ierr=1
         if(ierr.NE.0) then
            write(iolst,*)'ERROR in CAL_DAY -- Invalid inputs'
            write(iolst,*)'      YEAR =',iyear
            write(iolst,*)'JULIAN DAY =',julday
            write(iolst,*)'Called from  '//cfrom
            stop 'HALT in CAL_DAY -- see list file'
         endif
      else
         write(iolst,*)'ERROR in CAL_DAY -- Invalid ACTION'
         write(iolst,*)'   ACTION expected: TOJULIAN or TOGREGOR'
         write(iolst,*)'   ACTION found   : ',action
         write(iolst,*)'Called from  '//cfrom
         stop 'HALT in CAL_DAY -- see list file'
      endif
      if(ljulian) then
! ---    Convert date to the Julian day
         julday=ncumday(month,leap)+iday
      else
! ---    Convert Julian day to the date
         if(julday.LE.ncumday(7,leap)) then
            do i=6,1,-1
               if(julday.GT.ncumday(i,leap)) then
                  month=i
                  iday=julday-ncumday(i,leap)
                  return
               endif
            enddo
         else
            do i=12,7,-1
               if(julday.GT.ncumday(i,leap)) then
                  month=i
                  iday=julday-ncumday(i,leap)
                  return
               endif
            enddo
         endif
      endif
      return
      end
!----------------------------------------------------------------------
      subroutine week_day(iolst,action,iyear,month,iday,julday,&
                          iwkday,cwkday)
!----------------------------------------------------------------------
! --- UTILITY   Version: 2.1.1          Level: 140913         WEEK_DAY
!               D. Strimaitis, Exponent Inc.
!
! --- PURPOSE:  Return day of week from Zeller's Congruence
!
! --- UPDATES:
! --- v2.0 (140219) to v2.1.1 (140913)
!        1. Set 'cfrom' string (not passed in arg list)
! --- v1.23 (140106) to v2.0 (140219)
!        1. Add new string argument CFROM to calls in place of ierr
!
! --- INPUTS:
!               IOLST - integer   - Unit number of output list file
!              ACTION - char*     - Action to perform (type of date)
!                                   JULIAN uses Julian day (day-of-year)
!                                   GREGOR uses Gregorian date
!               IYEAR - integer   - Year (YYYY)
!               MONTH - integer   - Month (MM)
!                IDAY - integer   - Day (DD)
!              JULDAY - integer   - Julian day (JJJ)
!
! --- OUTPUT:
!
!              IWKDAY - integer   - Day of week (1=Sunday, 7=Saturday)
!              CWKDAY - character - Day of week
!
! --- WEEK_DAY called by:  ()
! --- WEEK_DAY calls:      CAL_DAY
!----------------------------------------------------------------------
      implicit none
! --- Declare arguments
      character(len=*) :: action
      character(len=9) :: cwkday
      integer          :: iwkday
      integer          :: iolst,iyear,month,iday,julday
! --- Locals
      character(len=9) :: cweek(7)
      character(len=8) :: cfrom
      integer          :: i,j,k,jk,iq,m
      data cweek/'SUNDAY   ','MONDAY   ','TUESDAY  ','WEDNESDAY',&
                 'THURSDAY ','FRIDAY   ','SATURDAY '/
      data cfrom/'WEEK_DAY'/
      SELECT CASE (action)
         CASE ("JULIAN")
            call CAL_DAY(iolst,cfrom,'TOGREGOR',jk,m,iq,julday)
         CASE ("GREGOR")
            jk=iyear
            m=month
            iq=iday
         CASE DEFAULT
            write(iolst,*)'ERROR in WEEK_DAY -- Invalid ACTION'
            write(iolst,*)'  Expected: JULIAN or GREGOR'
            write(iolst,*)'     Found: '//action
            stop 'HALT in WEEK_DAY -- see list file'
      END SELECT
! --- Treat JAN and FEB as months 13, 14 of previous year
      if(m.LE.2) then
         jk=jk-1
         m=m+12
      endif
! --- Separate the year into century and year of century
      j=jk/100
      k=MOD(jk,100)
! --- Using integer arithmetic, Zeller's formula is
! --- i(0-6) = (iq + ((m+1)*26)/10 + k +k/4 + j/4 +5j), MOD 7
      i=iq + ((m+1)*26)/10 + k +k/4 + j/4 +5*j
      iwkday=MOD(i,7)
! --- Saturday is 0 or 7
      if(iwkday.EQ.0) iwkday=7
! --- Set character result
      cwkday=cweek(iwkday)
      return
      end
